INSERT INTO `inner_policy` (`id`, `department`, `file`, `chapter`, `article`, `content`) VALUES (NULL, '三农业务部', '《代收代付业务管理办法》', '第一章  总则', '第一条', '为加强代收代付业务管理，防范业务风险，维护我行、委托人及其他当事人的合法权益，依据《商业银行法》、《商业银行服务价格管理暂行办法》等的有关规定，结合我行实际情况，特制定本办法。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第一章  总则', '第二条', '本办法所称代收代付业务是指我行接受客户（法人或自然人）的委托，按照委托代理协议约定，利用我行在网络、网点和资金清算等方面的资源，代为办理委托人指定项目的收付款业务。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第一章  总则', '第三条', '代收代付业务应遵循按照委托代理协议约定办理收付业务，银行不垫付资金，不负责处理收付双方任何经济纠纷的原则，在互惠互利、共同发展的基础上加强与客户的业务合作，推动金融业务的不断创新。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第四条', '代收代付业务是一项综合性较强的业务品种，各有关职能部门应明确分工、密切协作，做好代收代付业务的指导、管理、推广和培训工作。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第五条', '三农业务部负责全行中间业务公用事业行业客户营销，签订委托代理协议；负责全行代收代付业务的发展规划、指导、协调、管理；制定相关业务管理办法、业务操作办法；负责代收代付业务培训和代收代付业务自律监管等工作。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第六条', '计划财务部和运营管理部负责制定全行代收代付业务的会计核算办法，负责全行代收代付业务会计核算的管理、检查和辅导工作。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第七条', '科技信息部负责代收代付业务的软件开发和日常维护工作，保证系统的安全运行。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第八条', '审计部负责对代收代付业务开展情况进行稽核、检查和监督。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第九条', '合规管理部负责全行代收代付业务委托代理协议的法律审查工作。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第十条', '各支行负责处理代收代付业务咨询、客户投诉工作；负责代收代付业务办理，代发工资推荐客户优先使用企业网上银行。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第二章  组织管理', '第十一条', '各项代收代付业务的主办行是指委托人代收代付款项账户的开户行。主办行负责委托人的日常维护工作，负责委托人的资金清算、账务核对和账户管理工作；负责与委托人结算代收代付手续费，并划转到各协办行；负责办理代收代付业务的柜台服务及其他相关工作。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第三章  业务管理', '第十二条', '代收代付协议（一）所有代收代付业务开办前，都应根据授权管理有关规定与委托人签订代收代付协议书，协议书由主办行或总行管理部门与委托人签订。协议书需经签定行或合规管理部审核认可，代收代付协议书至少包括以下内容：1．协议各方的名称；2．业务名称、业务合作范围及实施方式；3．协议各方应承担的权利和义务；4．手续费的收取标准和支付方式、时间；5．数据传输方式、资金清算和对账方式；6．违约责任和争议解决方案；7．协议签订日期，协议期限和生效方式；8．双方需要约定的其他事项；9．各方有权签字人签字并加盖公章。（二）采用代扣方式办理的代收代付业务，必须取得被代扣客户的书面授权，同意我行根据委托人提供的数据，从其指定的结算账户中划扣相应的款项，缴纳给委托人。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第三章  业务管理', '第十三条', '代收代付业务的报备我行推出的代收代付业务新产品，需在推出前向当地人民银行、银监局报备，报备材料包括：开办申请、开办业务的管理办法或操作办法及人民银行、银监局要求提供的其他文件。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第三章  业务管理', '第十四条', '代收代付业务管理（一）必须严格按照与委托人签订的代收代付协议约定的内容开展代收代付业务，不得办理协议约定内容以外项目的代收代付业务。（二）各经办网点必须按照总行统一安排，在规定时间受理各项代收代付业务，不得推诿、拒办。（三）各经办网点必须按照总行制定的各项代收代付业务管理办法、操作办法、会计核算办法规范操作，严禁违规操作。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第三章  业务管理', '第十五条', '代收代付业务数据管理（一）需要委托人提供数据文件的代收代付业务，必须要求委托人在提供数据文件的同时提供纸质汇总清单，纸质汇总清单一式两份，内容包括：业务提交日期、业务提交总笔数、业务提交总金额等信息，同时加盖委托人财务专用章及经办人印章。双方指定专人办理交接，核对无误后双方签章认可。（二）需要向委托人提供处理结果数据文件的代收代付业务，必须在向委托人提供数据文件的同时提供处理结果纸质清单，处理结果纸质清单一式两份，内容包括：业务办理日期、业务成功总笔数、业务成功总金额、业务失败总笔数、业务失败总金额等信息，双方指定专人办理交接，核对无误后双方签章认可。（三）代收代付业务数据（数据文件、纸质清单等）保存期限与会计传票保存期限相同。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第三章  业务管理', '第十六条', '代收代付业务结算管理（一）各行在办理代收代付业务时，应严格按照《人民银行支付结算管理办法》办理相关支付结算业务。（二）各行在办理代收业务时，必须坚持先向付款客户收款或扣账、后给委托人进账的原则；进行代付业务时，必须坚持先向委托人收妥款项、后给收款客户进账的原则。（三）代收代付业务发生时，主办行应产生相关的明细、汇总、成功与不成功等业务报表，并由专人详细核对业务报表。按成功部分向委托人进行资金结算。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第三章  业务管理', '第十七条', '代收代付业务手续费管理（一）开办代收代付业务时，原则上应按代收代付金额或笔数向委托人收取一定比例的手续费，具体执行的手续费标准按照主办行或总行与委托人签订的代收代付协议约定为准。（二）代收代付手续费由主办行按协议约定时间向委托人收取；主办行同时负责根据各协办行代理业务量（金额或笔数），将手续费划转到各协办行。代收代付业务发生的所有手续费收入，必须全额转账计入中间业务收入科目——代收代付手续费收入科目。（三）开办代收代付业务必须坚持谁委托，谁付费的原则，不得向委托人以外的其他客户收取代收代付手续费。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第三章  业务管理', '第十八条', '代收代付业务凭证管理。代收代付业务凭证可分为一般单证和重要单证，凭证分类按照与委托人签定的代收代付协议约定为准。一般单证视同银行一般凭证进行管理，重要单证视同银行重要空白凭证进行管理。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第四章  风险管理', '第十九条', '加强内部约束制度，严格操作程序，加强业务培训，提高员工素质；各业务环节操作员要互相制约，提高差错或纠纷的处理效率，对内部违规违章等操作行为必须进行严格处罚，及时发现问题，堵塞漏洞，有效防止风险。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第四章  风险管理', '第二十条', '加强系统风险防范，对营业网点的日常业务进行监控和跟踪，及时发现业务操作过程中存在的问题并分析解决，不断完善各项代收代付业务。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第四章  风险管理', '第二十一条', '加强制度建设，各部门应根据代收代付业务的业务特点，制定相应实施细则、操作规范、会计核算办法、数据维护等管理制度。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第五章  附则', '第二十二条', '本办法由本行三农业务部负责解释、修订。'),
(NULL, '三农业务部', '《代收代付业务管理办法》', '第五章  附则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第一章  总则', '第一条', '为了规范本行代理保险业务的经营行为，保护保险消费者的合法权益，促进商业银行代理保险业务持续健康发展，根据《》、《》、《》、《保险兼业代理管理暂行办法》、保监发〔2014〕3号《中国保监会中国银监会关于进一步规范商业银行代理保险业务销售行为的通知》、银监发〔2016〕24号《中国银监会关于规范商业银行代理销售业务的通知》等法律、行政法规及规章，制定本办法。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第一章  总则', '第二条', '本办法所称本行代理保险业务是指本行接受保险公司委托，在保险公司授权的范围内，代理保险公司销售保险产品及提供相关服务，并依法向保险公司收取代理费用的经营活动。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第一章  总则', '第三条', '本行开展代理保险业务，应当遵守法律、行政法规和中国保监会、中国银监会的有关规定，遵循自愿、诚实信用和公平竞争的原则，实现双方优势互补，互利共赢，为保险消费者创造价值。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第一条', '代理保险合作对象'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第四条', '本行和保险公司应当结合自身及对方的资本状况、资产规模、管控能力等因素审慎选择合作对象，合理确定合作对象的范围和数量。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第五条', '本行选择合作保险公司时，应当充分考虑其偿付能力状况、风险管控能力、业务和财务、近两年受监管部门处罚情况等。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第六条', '本行和保险公司选择和评价合作对象，应当参考监管部门、外部审计、评级机构以及行业协会披露的信息，确保获取的有关信息真实可靠。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第七条', '本行和保险公司应当保持合作关系和客户服务的稳定性。本行与每家保险公司的连续合作期限不得少于一年。合作期间内，如果一方出现对双方合作关系有实质影响的不利情形，另一方可以提前中止合作。对保险公司与本行网点已经中止合作的情况，本行应配合保险公司做好满期给付、退保、投诉处理等保单后续服务。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第八条', '本行不得将保险代理业务转委托给其他机构或个人。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第二条', '代理资格'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第九条', '本行网点代理保险业务实施资格管理。（一）本行代理保险业务的，应当取得中国保监会颁发的经营保险代理业务许可证，支行应在总行业务代理许可范围内开展代理业务。（二）本行网点经营保险代理业务许可证的使用和管理，应当按照中国保监会《》有关规定办理。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第十条', '本行代理保险业务销售人员（简称：销售人员）实施资格管理。（一）本行和保险公司应当按照监管部门有关规定，对本行代理保险业务销售人员进行法律法规、业务知识培训和职业道德教育。（二）本行从事代理保险业务的销售人员，应当符合中国保监会规定的保险销售从业资格条件，取得中国保监会颁发的《保险销售从业人员资格证书》。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第三条', '代理协议'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第十一条', '本行代理保险公司保险业务，原则上应当与保险公司的分支机构签订代理协议，并事先取得总公司书面授权。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第十二条', '本行和保险公司签订的代理协议应当包括但不限于以下主要条款：代理产品种类，代理费用标准及支付方式，单证及宣传资料管理，客户账户及身份信息核对，反洗钱，客户信息保密，双方权利责任划分，争议的解决，危机应对及客户投诉处理机制，合作期限，协议生效、变更和终止，违约责任等。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第四条', '代理行为'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第十三条', '本行只能在其主业营业场所内代理保险业务，不得在营业场所外另设代理网点。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第二章  代理关系', '第十四条', '本行从事保险代理业务，不得有下列行为：（一）擅自变更保险条款，提高或降低保险费率；（二）利用行政权力、职务或职业便利强迫、引诱投保人购买指定的保单；（三）使用不正当手段强迫、引诱或者限制投保人、被保险人投保或转换保险人；（四）串通投保人、被保险人或受益人欺骗保险人；（五）对其他保险机构、保险代理机构做出不正确的或误导性的宣传；（六）代理再保险业务；（七）挪用或侵占保险费；（八）兼做保险经纪业务；（九）中国保监会认定的其他损害保险人、投保人和被保险人利益的行为。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第一条', '保险产品'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第十五条', '本行代理保险公司销售的保险产品，应当是按照中国保监会保险产品审批备案管理的有关规定，经过中国保监会审批或备案的保险产品。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第十六条', '本行应当充分发挥销售渠道优势，在本行代理保险业务中大力发展风险保障型保险产品。所代理销售意外伤害保险、健康保险、定期寿险、终身寿险、保险期间不短于10年的年金保险、保险期间不短于10年的两全保险、财产保险（不包括财产保险公司投资型保险）、保证保险、信用保险的保费收入之和不得低于代理保险业务总保费收入的20%。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第十七条', '本行和保险公司在合作过程中，应当加大产品创新力度，以消费者需求为导向，推进本行代理保险产品的多样化和差异化，不断满足客户日益增长的保险保障和金融资产管理需求。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第十八条', '本行代理保险公司销售的保险产品，保单封面主体部分应当以显著的印有“保险单”或“保险合同”字样、保险公司名称等内容，保险合同中应当包含保险条款及其他合同要件。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第十九条', '保险公司应合理设计保险单册样式，保险单册封套在颜色、样式、材料等方面应与银行单证材料有明显区别。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十条', '保险公司应在保险单册标明“保险单”字样，并标明保险公司名称。（一）保险公司应在保险单册封面应标明风险提示语及犹豫期提示语。（二）其他产品类型的风险提示语，由公司自行确定。（三）保险单证应载明保险犹豫期提示语。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二条', '代理费用'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十一条', '本行和保险公司协商代理费用时，应当本着互利共赢、共同发展、保护消费者利益的原则，共同促进本行代理保险市场的持续健康发展。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十二条', '保险公司向本行支付代理费用，应当由保险公司一级分支机构向本行转账支付。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十三条', '本行应当加强代理费用集中管理，从代理费用中列支代理保险业务销售人员的业务计价费用。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十四条', '本行及其工作人员不得以任何方式向保险公司及其工作人员收取、索要合作协议约定以外的任何利益。并加强对员工的教育管理，防范商业贿赂风险。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三条', '销售模式'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十五条', '保险公司应为银行提供培训、单证交换等服务，并协助本行做好保险产品销售后的满期给付、续期缴费等相关客户服务。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十六条', '营业网点应当根据总行代理的险种进行规范操作，不得与合作保险公司以外的公司合作，不得代理合作保险公司合作险种以外的产品。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十七条', '本行和保险公司加强战略性合作，在依法合规、风险可控的前提下，通过合法合规的渠道开展业务。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四条', '销售行为'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十八条', '销售人员应请投保人本人填写投保单。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第二十九条', '本行及其销售人员不得篡改客户投保信息，不得以银行网点电话、销售及相关人员电话冒充客户联系电话。需要投保人、被保险人确认的，应确保本人亲自签字或盖章确认。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十条', '本行及其销售人员不得截留客户投保信息，应将完整、真实的客户投保信息提供给保险公司。保险公司应将客户退保、满期给付等信息完整、真实地提供给本行。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十一条', '本行应在保险单、业务系统和保险代理业务账簿中完整、真实地记录本行网点名称及网点销售人员姓名或工号。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十二条', '本行应向投保人提供完整合同材料，包括投保提示书、投保单、保险单、保险条款、产品说明书、现金价值表等。对合同材料不得进行删减或截取内容。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十三条', '本行及其销售人员不得擅自印制代销产品的宣传资料或变更产品宣传资料的内容。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十四条', '客户应及时填写保险办理申请单，销售人员应当引导投保人在投保单上填写真实完整的客户信息及其他要素信息，并由客户本人签字确认，任何人不得代抄录有关声明或代投保人或被保险人签名。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十五条', '销售人员负责在销售过程中全面客观介绍保险产品，应当按保险条款将保险责任、责任免除、退保费用、、缴费期限、犹豫期等重要事项明确告知客户。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十六条', '销售人员不得进行误导销售或错误销售。在销售过程中不得将保险产品与储蓄存款、等混淆，不得使用“银行和保险公司联合推出”、“银行推出”、“银行理财新业务”等不当用语，不得套用“本金”、“利息”、“存入”等概念，不得将保险产品的利益与银行存款收益、国债收益等进行片面类比，不得夸大或变相夸大保险合同的收益，不得承诺固定分红收益。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十七条', '本行网点及其销售人员不得以中奖、抽奖、送实物、送保险、产品停售等方式进行误导或诱导销售。保险公司不得支持或鼓励本行采取上述行为。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十八条', '本行网点应当向保险公司提供全面、完整、真实的客户投保信息，确保保险公司承保业务和客户回访工作顺利开展。保险公司应当建立投保单信息审查机制，对投保单信息不全、捏造变更客户信息的保险业务不得承保。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第三十九条', '保险公司应当对通过本行网点渠道销售保险产品投保人进行回访。对于到各营业网点申请退保、满期给付、续期缴费业务的，各营业网点和保险公司应当相互配合，及时做好相应工作。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第五条', '财务核算'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十条', '本行在销售时通过银行收取保费的，应当就缴款账户、金额、时间等内容与投保人达成协议，并有独立于投保单等其他单证和资料的银行自动转账授权书，授权书应包括转出账户、每期转账金额、转账期限、转账频率等信息。划款时应向投保人出具保费发票或保费划扣收据。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十一条', '本行应按照保险兼业代理合同的规定，与保险公司按时结算保费和交接有关单证。保费结算时间最长不得超过一个月，不得用保费抵扣代理手续费。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十二条', '本行从事代理保险业务时,收取保费后须在中间业务模块操作并打印代理业务缴款凭条,保险费收入上划到为各保险公司在我行设立的保费收入帐户上。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十三条', '本行网点应建立业务台帐，台帐应逐笔列明保单流水号、代理险种、保险金额、保险费、代理手续费等内容。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十四条', '本行按照各营业网点代理保险费总金额的一定比例折算代理手续费收入,在年终考核时作支行认账利润处理。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十五条', '本行对代理保险业务取得的代理费用应当如实入账，对不同保险公司的代收保费、代理费用进行独立核算。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第六条', '风险控制与应急处理'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十六条', '投保人出险后须由投保人本人或家人向保险公司报案。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十七条', '销售人员违反本办法的规定办理代理保险业务,由总行根据具体情况按照有关规定作出处罚,对由此造成的经济纠纷,由责任人自行承担。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十八条', '本行网点和保险公司应当在客户投诉、退保等事件发生的第一时间积极处理，实行首问负责制度，不得相互推诿，避免产生负面影响使事态扩大。按照双方共同制定的重大事件处理办法规定，及时采取措施，妥善解决。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第三章  经营规则', '第四十九条', '对于出现的重大事件，本行网点和保险公司应当及时向事件发生地的分支机构，应当及时向当地中国保监会、中国银监会派出机构报告。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第四章  附则', '第五十条', '本办法由本行三农业务部负责解释和修订。'),
(NULL, '三农业务部', '《代理保险业务暂行管理办法》', '第四章  附则', '第五十一条', '本暂行办法自印发之日起实施。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第一章  总则', '第一条', '为加强本行代理实物贵金属业务管理，规范业务运作，防范和控制风险，根据《上海黄金交易所交易规则》、《上海黄金交易所个人贵金属交易试行办法》、《上海黄金交易所黄金交割管理办法》以及监管部门有关规定，结合本行实际，制定本办法。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第一章  总则', '第二条', '本办法所称代理实物贵金属业务，是指本行与业务合作单位签订“代理实物贵金属合作协议”，接受客户的委托，进行实物贵金属的购买、资金清算和实物交割等交易行为。本行代理实物贵金属业务主要包括：接受客户的申购，代其购买符合行业标准的标准金条、金币、银币等实物贵金属。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第一章  总则', '第三条', '本行开展代理实物贵金属业务遵循优势互补、合作共赢、依法合规和防范风险的原则。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第二章  交易品种、时间与渠道', '第四条', '本行代理贵金属的种类目前分为：标准金条（含金量99.99%）、金币、金章、银条、银章、银币等贵金属货品。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第二章  交易品种、时间与渠道', '第五条', '贵金属报价方式为人民币“元/克”，保留两位小数，根据上海黄金交易所报价实时变化。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第二章  交易品种、时间与渠道', '第六条', '代理实物贵金属业务在上海黄金交易所白天开市时间内进行交易。具体为：每周一至周五（国家法定节假日除外）上午：09：30----11：30下午：13：30----15：30'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第二章  交易品种、时间与渠道', '第七条', '代理实物贵金属业务，客户通过本行各营业网点申购，交易时客户填写《代理实物贵金属业务委托购买凭条》，打印《代理销售实物贵金属客户委托购买凭证》（按重要空白凭证管理规定保管、使用），该凭证同时是客户提金的凭证。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第二章  交易品种、时间与渠道', '第八条', '客户提出购买申请、提金预约，均由销售网点受理并办理，但提取贵金属实物由营业部统一办理。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第九条', '业务开展前，本行与合作单位签订业务合作协议，明确双方的权利与义务。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十条', '三农业务部是代理实物贵金属业务的管理部门，主要职责是：（一）负责业务洽谈、合作协议、业务培训、制订相关的操作规程及业务管理办法，印制宣传资料。（二）负责与业务合作单位日常业务的衔接以及业务方面有关事宜的沟通协调。（三）对业务的经营情况进行分析、考评、监督。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十一条', '信息科技部是代理实物贵金属业务的技术支持部门，负责进行业务系统的日常维护及升级等工作。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十二条', '合规管理部是代理实物贵金属业务的合规性管理部门，负责相关管理办法、操作流程、协议、合同等法律文本的审核确认。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十三条', '风险管理部是代理实物贵金属业务的风险管理部门，负责对实物贵金属代售业务实行风险评估。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十四条', '审计稽核部是代理实物贵金属业务的监督部门，负责对实物贵金属代售业务实行业务监督。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十五条', '运营管理部负责为代理实物贵金属业务设立会计核算科目、开设内部账户及设置相关登记簿等管理；负责实物贵金属保证金的汇划、清算。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十六条', '计划财务部负责代理中间业务手续费的结算等。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十七条', '营业部是代理实物贵金属业务的资金清算和提货、保管部门，负责对账户资金进行清算、汇划以及手续费收入计算复核等工作；负责为全行的客户提供实物贵金属。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十八条', '销售网点负责落实各项操作规程和管理规定；负责对本辖区代理实物贵金属业务的宣传、咨询服务，进行市场拓展和客户开发，为客户提供贵金属购买服务；负责核对当日交易情况，并进行账务核对。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第十九条', '办理代理实物贵金属业务的网点，必须具备下列条件：（一）认真执行各项规章制度，业务操作规范，管理制度健全，建立完善的事后监督和内部控制制度；（二）分工合理，岗位责任明确；（三）最近三年内无重大结算事故和经济责任案件。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第三章  业务管理', '第二十条', '经办人员的基本要求：（一）思想品质好、业务素质高；（二）参加代理实物贵金属业务培训，并达到上岗要求；（三）三年内未受到警告以上纪律处分。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第四章  账户管理、交易费用和实物交割', '第二十一条', '运营管理部开立“其他应付款-代理实物贵金属”账户，用于核算客户购买贵金属时存入的资金（含收取的手续费和本行支付给贵金属业务合作单位的全部费用）。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第四章  账户管理、交易费用和实物交割', '第二十二条', '收益入账和分成。代理手续费按代理协议规定执行：金条类贵金属价格由实时行情价格和手续费构成（向客户报价时应为含手续费的价格），纪念币（章）类等其它贵金属价格根据每个批次的零售价格执行，手续费为零售价减去供货价的差额。从内部应付款账户中划转销售款清算时结算手续费，本行将收入计入“手续费收入”科目。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第四章  账户管理、交易费用和实物交割', '第二十三条', '实物贵金属代理销售产生的销售收入，可在销售收入中扣除本行应得手续费后将资金实时划至合作机构在本行开立的账户，或全额划入合作机构账户后由其返还本行手续费。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第四章  账户管理、交易费用和实物交割', '第二十四条', '客户如对所提实物贵金属的质量存有异议，应在提货后2个工作日内向营业部提出，同时提供提货确认凭证、金条质量证明书等，并由本行联系合作单位协调解决。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第四章  账户管理、交易费用和实物交割', '第二十五条', '查询查复、差错处理严格按照“有疑速查、有查速复、复必详尽、切实处理”的原则办理查询查复业务。发生业务差错时，通过传真等形式及时发送业务查询书，根据实际情况，进行相关账务调整。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第四章  账户管理、交易费用和实物交割', '第二十六条', '风险管理（一）三农业务部负责制定管理办法和操作规程，负责对拟办理该业务的网点进行培训，确保经办网点业务操作人员在业务开展前熟悉有关业务知识和操作流程。（二）代理实物贵金属业务必须设立专用账户用于进行资金清算。（三）总行应结合业务发展情况，每季度不少于一次对辖内经办网点进行业务检查，发现问题，及时整改。（四）为配合《中华人民共和国反洗钱》的实施，在我行以现金购买实物贵金属的客户，须出示身份证件并保留证件复印件；或在我行开立个人存款账户，通过转账方式购买。（五）营业部须每日汇总实物贵金属库存情况，并上报总行运营管理部。科技信息部每日查询系统库存，并与总行运营管理部做好核对工作。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第五章   附则', '第二十七条', '本暂行办法由本行三农业务部解释和修订。'),
(NULL, '三农业务部', '《代理实物贵金属业务管理办法（暂行）》', '第五章   附则', '第二十八条', '本办法自发布之日起正式实施。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第一章  总则', '第一条', '为加强代理实物贵金属业务管理，规范操作行为，根据《商业银行内部控制指引》、《代理实物贵金属业务管理办法（暂行）》规定，制定本操作规程。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第一章  总则', '第二条', '本操作规程适用于本行开办代理实物贵金属业务的各营业网点及其管理部门。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第一章  总则', '第三条', '代理实物贵金属业务实行“统一管理、分级负责、岗位分离、相互制约”的管理原则。统一管理是指网络通讯、岗位设置、操作规程的制定等事项由本行统一管理。分级负责是指本行相关部门、营业部、支行营业网点分别在权限范围内履行相应职责，并根据职责承担相应的责任。岗位分离是指业务控制岗、资金清算岗，实物管理岗、销售业务岗等岗位相互分离，各司其职。相互制约是指各岗位之间按照各自分工操作、相互监督和制约，有效防范操作风险。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第二章  岗位设置与职责', '第四条', '根据业务需要和管理原则，设立业务控制岗、资金清算岗、实物管理岗和销售业务岗。具体岗位职责为：（一）业务控制岗。三农业务部设业务处理员两名，负责联系本行所代销单位（以下简称“合作单位”）和确定当日挂牌价；负责在系统内每个交易日的贵金属挂牌价输入及交易控制（录入和复核），负责向合作单位办理客户订货单下单等工作。（二）资金清算岗。营业部设立资金清算员两名，分别负责全行代理实物贵金属业务的资金清算、汇划及手续费分成的计算清分等工作的经办、复核工作。（三）实物管理岗。营业部设立一名实物贵金属保管出纳岗，负责实物贵金属出库、入库、保管、验收及交付;设立一名实物贵金属保管复核岗，负责登记《实物贵金属交接登记簿》、《实物贵金属库存登记簿》、《客户实物贵金属交接登记簿》及《客户实物贵金属发票交接登记簿》，并且负责实物贵金属出库、入库、验收及交付的复核以及贵金属实物的检查、核对工作，并按月向三农业务部上报销售报表信息，填报《实物贵金属产品进货\销售\库存表》、实物贵金属代理业务开票明细表。（四）销售业务岗。销售网点指定一名柜员专门负责实物贵金属客户的申购和提取预约工作。根据客户委托办理申购手续并收取资金；负责保管空白“代理销售实物贵金属客户委托购买凭证”（以下简称“购金凭证”，按照本行重要空白凭证管理）。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第三章  操作流程', '第五条', '交易流程代理实物贵金属业务操作流程包括：贵金属挂牌价及交易控制、客户申购和交易确认、贵金属提货（收货）、贵金属入库和出库、资金清算、收益入账和分成、客户提金（领取）、开具发票八项。具体交易流程（一）贵金属挂牌价及交易控制每个正常交易日，三农业务部业务控制岗人员负责在系统中录入贵金属品牌当前时段对应的挂牌价、交易开放或关闭控制。（二）客户申购和交易确认各支行（部）设立受理专柜，在规定的时间（周一至周五，上午9：30－11：30，下午13：30－15：30，国家法定节假日除外）内受理客户购买申请，本行代理贵金属的种类目前分为：标准金条（含金量99.99%）、金币、金章、银条、银章、银币等贵金属货品。购金价格由实时行情价格和手续费构成（向客户报价时应为含手续费的价格）。客户申购时填写代理实物贵金属业务委托购买凭条，全额支付购金款后，支行网点柜员在系统中按照客户申购要求录入相关要素，打印购金凭证，经办人应将购金凭证加盖印章后交客户。收取客户的购金款通过系统自动全额记入在营业部开立的“其他应付款”科目—“代理实物贵金属对应合作单位”账户中。作废的购金凭证作当日传票附件。（三）贵金属收货合作单位负责送货到本行营业部，本行实物管理岗负责验收，必须对贵金属实物的规格、数量等逐件进行核对、验收，发现产品存在以下质量问题的，应当拒收。（1）实物贵金属产品缺损、变形、表面有气泡，尺寸、重量不符合约定的；（2）塑膜包装破损、有污渍；（3）配套的鉴定证书、防伪标签不齐全；（4）收货后对交割的贵金属质量持有异议，须在交割日后２个工作日内向合作单位书面提出。贵金属交接过程须在录像监控环境下完成，交接完成后双方按照验收结果填写《实物贵金属交接确认单》并签章确定，双方各执一联留存。（四）贵金属入库和出库贵金属交接完毕后，实物管理岗人员要做好入库交接手续，交接过程须在录像监控环境下进行，并将对应贵金属重量录入系统，并且保管复核岗在贵金属入库时应登记《实物贵金属交接登记簿》、《实物贵金属库存登记簿》。客户提取实物贵金属后，应在系统实物贵金属保管员总库中对应减去（注：销售的实物贵金属只能在营业部办理实物贵金属的提取）。实物贵金属营业期间应存放专门保险箱，营业终了装箱入库保管。（五）资金清算客户申购对应的实物贵金属送达本行后，本行应将相应的销售款（不含我行手续费）划转至合作单位在本行开立的账户中。（六）收益入账和分成代理手续费按代理协议规定执行，从内部应付款账户中划转销售款清算时结算手续费，本行将收入计入“手续费收入”科目。（七）客户提金（领取）客户提金应向销售网点提出，通过电话或其他方式告知营业部进行预约，营业部对提金具体时间予以答复。提金时客户需持客户本人有效身份证件、购金凭证到营业部办理提金，支行员工可予陪同。营业部保管复核岗负责提金受理，核实身份证件、购金凭证后，在系统中录入并在购金凭证打印“已兑付”字样，同时打印《客户提取实物贵金属信息凭证》，登记《实物贵金属库存登记簿》，然后将贵金属实物交于客户当面验收，并让客户在《客户提取实物贵金属信息凭证》和《客户实物贵金属交接登记簿》上签字确认。（八）开具发票销售发票均由合作单位最终提供。客户需要发票的，提金时一并在本行营业部柜面换取购金发票，实物管理岗将发票交于客户的同时必须进行《客户实物贵金属发票交接登记簿》签收并加盖“发票已领”字样印章。客户对发票如有特殊要求的，销售网点应以电话形式通知本行业务控制岗人员，业务控制岗将客户发票的相关要求在提供给合作单位的定货单上加以注明。本行负责定期向合作单位开具本行手续费收入发票。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第四章  异常处理', '第六条', '异常交易是指进行交易确认过程中，由于系统异常、网络故障等原因导致的交易流程中断。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第四章  异常处理', '第七条', '因网络故障导致的业务处理异常中断的，销售业务岗等岗位人员向客户做好解释，并立即上报科技部门，检查故障、尽快恢复。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第四章  异常处理', '第八条', '客户提金凭证丢失的，按照存单挂失规定办理，须客户本人办理。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第五章  附则', '第九条', '本操作规程由本行三农业务部负责解释和修订。'),
(NULL, '三农业务部', '《代理贵金属业务操作流程》', '第五章  附则', '第十条', '本操作规程自下发之日起执行。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第一章  总则', '第一条', '为进一步加强本行代理贵金属业务风险管理，根据《商业银行法》、《商业银行内部控制指引》、《合规管理办法》、《代理实物贵金属业务管理办法（暂行）》，制定代理贵金属业务风险内控制度。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第一章  总则', '第二条', '代理贵金属业务是指银行与委托人签订代理协议，利用本行的网点和结算为其代理贵金属产品而取得代理手续费的业务。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第一章  总则', '第三条', '代理贵金属业务主要面临的风险有操作风险、声誉风险等风险。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第二章  风险类别', '第四条', '操作风险是指在代理贵金属业务过程中由于不完善或有问题的内部程序、员工、信息科技系统以及外部事件所造成的风险。具体包括：（一）贵金属在实物运输交接环节存在的安全风险；（二）贵金属的出入库管理不规范，贵金属的保管不当，造成贵金属遗失或者贵金属品相受损影响销售；（三）贵金属和现金没有进行实时交割，造成账实不符；（四）交付给客户的贵金属复核错误，造成账实不符；（五）网点安全措施存在漏洞导致不法分子利用漏洞盗取贵金属实物风险；（六）贵金属实物的保管未能做到实物的有效隔离，内部员工未经授权接触贵金属；（七）没有建立台账，账务核对不及时，造成账实、账账不符；（八）每日贵金属实物盘点时流于形式，未能真正做到实物盘点清查。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第二章  风险类别', '第五条', '声誉风险是指在代理贵金属业务中所带来的对本行负面评价的风险。具体包括：（一）客户在购买贵金属时，经办人员未尽到对产品的告知义务导致客户对产品存在误解，最终引起客户投诉；（二）经办人员未经过专业培训，在产品销售过程有误导客户的现象；（三）机构未经授权擅自代理销售贵金属，从而引发的客户投诉及造成不良的社会影响。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第三章   风险控制', '第六条', '操作风险的控制措施：（一）贵金属的交接及运输环节，必须“四人八目”，所有交接程序的完成必须在监控摄像头的监控范围内进行；（二）贵金属实物及相关重要空白凭证的保管视同现金管理，贵金属证明文件（如鉴定证书）要随贵金属实物一并保管，贵金属的外包装不得破损，贵金属的出入库实行双人同进同出；（三）贵金属出售当日必须进行实物的交割及现金的清算，不得出现单边挂账、账实不符的现象；（四）贵金属销售实行一人操作，一人复核模式；（五）定期对网点安全措施进行检查，及时发现安全漏洞，对安全问题坚持早发现，早处置；（六）对贵金属的隔离状况实施实时监控，防止出现单人或未经授权接触贵金属的现象；（七）建立贵金属交易台账，逐笔登记贵金属的交易明细，每日营业终了，核对贵金属库存余额、交易明细台账、交易传票，确保账实相符；（八）贵金属实物盘点必须在监控之下进行，贵金属管理部门不定期对监控进行调阅，防止出现盘点流于形式现象；贵金属管理部门每月不定期查库至少一次。（九）贵金属产品的销售、资金划转交易传票纳入事后监督常规业务监督范围。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第三章   风险控制', '第七条', '声誉风险的控制措施：（一）客户在购买贵金属时，经办人员应尽到对产品的告知义务，并将合同进行详细讲解后，与客户签订已完全了解合同双方义务等字样，防止事后客户投诉；（二）加强对贵金属业务经办人员的专业培训，准确把握产品的特点及风险等各项内容，做好营销宣传；（三）代理销售贵金属业务必须得总行授权批准；（四）出现苗头性的声誉风险时，第一时间要进行舆情控制，并及时向总行相关部门汇报，防止事态进一步扩散。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第四章   附则', '第八条', '本办法由本行三农业务部负责解释和修订。'),
(NULL, '三农业务部', '《代理贵金属业务风险内控制度》', '第四章   附则', '第九条', '本制度自发文之日起实施。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第一章  总则', '第一条', '为进一步加强本行保管箱业务的管理，促进保管箱业务发展，规范操作流程，根据《中华人民共和国商业银行法》及其他有关法律、法规，制定本办法。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第一章  总则', '第二条', '本行保管箱业务是以出租保管箱的形式代客户保管贵重物品、重要文件、有价单证等财物的中间业务。本行开办保管箱业务，其宗旨是完善服务功能，满足社会需求，促进业务发展。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第一章  总则', '第三条', '本行保管箱业务管理的主要目标是：按照“服务、保密、效益、安全”的原则，统一保管箱业务的开办条件和岗位设置，规范租箱、开箱、续租、退租等业务处理手续，确保保管箱业务有序开展。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第二章  组织管理体系', '第四条', '本行运营管理部负责具体业务操作的指导工作，每半年至少组织一次对保管箱业务的全面检查，并向高管层提交相关检查报告；三农业务部负责保管箱业务的营销管理，制度、流程制定；营业部负责具体办理保管箱业务和管理保管箱库房；	各支行、中心负责保管箱业务的拓展工作；安全保卫部负责保管箱业务设施的安全性和库区安全的日常保卫管理工作；风险管理部负责保管箱业务风险的监督、识别、评估、控制，并及时向经营管理层报告风险状况；合规管理部负责保管箱业务各项管理办法及操作流程的审核，保证相关制度的合规性和有效性，不定期做出保管箱业务经营合规性评价；审计部负责对保管箱业务进行审计稽核和监督检查，不定期向董事会提交相关审计报告；其他相关部门予以配合。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第三章  人员配备及岗位职责', '第五条', '办理保管箱业务须配备业务主管、业务经办、业务复核（兼主管库员）、副管库员和安全保卫人员。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第三章  人员配备及岗位职责', '第六条', '办理保管箱业务人员必须具备以下条件（一）爱岗敬业，具有良好的职业道德；（二）诚实守信，严守客户秘密；（三）熟悉保管箱业务章程、管理办法、实施细则及协议条款，遵守有关规章制度；（四）能熟练办理本岗位保管箱业务。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第三章  人员配备及岗位职责', '第七条', '办理保管箱业务人员岗位职责如下：（一）业务主管人员职责1．负责检查、监督保管箱库房管理情况；2．负责授权办理优惠减免、信息更改以及破箱、查封、纠纷处理等特殊业务；3．负责掌握库房门应急密码锁，在应急情况下开关库门；4．负责定期或不定期抽查未出租的保管箱钥匙及各类账务。（二）业务经办人员职责1．负责办理租箱、开箱、续箱、退租、挂失等业务；2．负责办理现金收付业务，并记录有关账目；3．负责每日营业终了时将业务登记簿、收付日结单分别与相关业务记录和会计相关科目进行核对。(三)业务复核员兼主管库员1．负责复核经办人员所办业务；2．负责复核每日业务发生、现金收付记录与实物、款项是否相符及日终账务核算；3．负责建立保管箱钥匙保管和领用登记簿；4．掌握库房外门钥匙和上密码锁，会同副管库员开关库房门；5．负责装订和保管各类资料，建立出入库房登记簿。(四)副管库员职责1．掌握库房内库门钥匙和库房门下密码锁，会同主管库员开关库房门；2．负责保管保管箱主钥匙并陪同客户共同开启或关闭保管箱；3．负责做好出租保管箱期限管理，按约定方式通知租用户办理到期续租手续(到期前15天内通知)；4.协助主管库员做好保管箱相关业务；5．负责库房的日常管理。(五)安全保卫人员职责负责营业区域和库房的安全保卫工作。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第四章  保管箱业务的开办', '第八条', '凡具有完全民事行为能力和合法有效身份证件的中国公民、港澳台同胞和外籍人员，以及依法成立并在中国境内从事经营活动的国内外公司、企业、社会团体及其他经济组织，均可在本行申请办理保管箱租用业务。本行可视情况制订免收租金条款，符合条件的免收租金客户应提供相关证明，由营业部工作人员予以确认，填写《保管箱免收租金审批表》，经营业部负责人审核，并报请营业部分管行长及本行行长批准后方可享受相关待遇。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第四章  保管箱业务的开办', '第九条', '办理保管箱业务的营业部应当与客户签定《保管箱租用协议》，明确双方的权利和义务，并严格遵照保管箱业务管理办法和协议条款为客户提供服务。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第五章  保管箱的租用', '第十条', '个人申请租用保管箱，须持本人合法有效证件，与本行签定《保管箱租用协议》，在登记处签章（签名或签名加盖章，下同），填写《个人租用保管箱申请表》，提交有关资料。个人授权委托他人代理保管箱业务，应在租箱申请表中写明，并填制《授权委托书》确定授权代理人，明确授权范围和授权期限。租箱日授权代理人须同时到场出示合法有效证件，并预留个人资料。两人联名租用保管箱，双方都须持合法有效身份证件，填写租箱申请表，预留个人资料，所有手续均须双方同时到场办理。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第五章  保管箱的租用', '第十一条', '单位申请租箱，须持合法有效证明文件，与本行签定《保管箱租用协议》，同时填写《单位租用保管箱申请表》，分别在申请表上加盖公章，由法人代表签名、盖章，并在申请表上指定代理人，填制《授权委托书》，指定代理人须出示合法有效证件，并预留个人资料。如法人代表变更，应重新填写租箱申请表(原协议租期可不变)，确定授权代理人，如授权代理人变更，应重新填制《授权委托书》确定代理人。其他手续按照个人申请租箱程序办理。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第五章  保管箱的租用', '第十二条', '本行业务经办人员应认真审验个人或单位租用人(含单位法人、代理人)填写的租箱申请表、《授权委托书》及其提交的身份证、单位公函等证明资料，符合规定后，在申请表上签名、盖章，并换人复核。复核员核实无误后在申请表上签名、盖章，并记录客户资料、建立客户档案。《个人(单位)租用保管箱申请表》归档永久保管。如单位租用人中途决定撤销对原代理人的授权，须到本行办理手续，填写《撤销授权委托书》并签名、盖章。本行业务主管审核无误后，记录有关资料《撤销授权通知书》归档永久保管。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第五章  保管箱的租用', '第十三条', '业务经办人员根据出租保管箱的规格、租期，向租用人收取租金、押金（具体收费标准由运营管理部制订并适时公布），并开具收费凭证一式三联。款项收妥后，在收费凭证上加盖“收讫”章及收费人名章确认，一联交租用人，一联为客户账传票，记入相关客户账、一联为保管箱业务收入传票，记入客户科目保管箱业务收入。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第五章  保管箱的租用', '第十四条', '业务复核人员再次检查租用人填写的租箱申请表，复查租用人及其代理人证件、预留印鉴(或签名)及客户资料录入情况等，确认无误后将出租保管箱的客钥匙(两把)交付租用人，主钥匙由本行保管。租用人领取钥匙后在《重要物品登记簿》上签章确认。租用人出入库房应在《出入库房登记簿》上做好记录，主、客钥匙共同使用方可打开保管箱。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第五章  保管箱的租用', '第十五条', '办理保管箱业务须按箱数登记表外科目，按假定价1元计账。会计分录为：收：代保管有价值品。客户租用后，会计分录为：付：代保管有价值品。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第六章  租金、押金及手续费等的收取', '第十六条', '保管箱租期内，租金(收费标准以运营管理部公布为准)一般按年计算，可按年续租。租金一律预交，第一年租金应在租箱时交纳，每年到期前15天内交纳下一年租金。如按日、月开展短期租箱业务（按日租箱的，不足一月则按月收费)，可按日、月续租，租箱时交纳第一期租金。租金业务收费如有调整，按交费时标准收取。多期租金也可一次性交纳，且不受期间收费标准调整的影响。保管箱租用人缴存押金是遵守协议的保证，押金收取标准以运营管理部公布为准。如停止租用，在办妥退租手续后，本行凭客户提交的押金收费凭证，将押金退还租用人，如租用人有租金或其他费用未付清，本行可在押金中予以扣除。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第六章  租金、押金及手续费等的收取', '第十七条', '租用期满，租用人未按规定交纳租金的，本行按逾期天数和日租金标准收取逾期租金，同时按逾期天数和年租金的1‰向其收取滞纳金。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第六章  租金、押金及手续费等的收取', '第十八条', '租用人按年或半年租箱，每月开箱超过4次(每月4次内免费)，本行按次收取开箱手续费。（收费标准以运营管理部公布为准）'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第六章  租金、押金及手续费等的收取', '第十九条', '租用人申请挂失或破箱，本行按规定收取手续费。(收费标准以运营管理部公布为准)'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第六章  租金、押金及手续费等的收取', '第二十条', '保管箱租用收费的会计核算l. 收取租金借：现金（或：对公活期对私活期）贷：保管箱手续费收入2. 收取押金借：现金（或：对公活期对私活期）贷：其他应付款----保管箱押金退还押金相反。3. 收取开箱、挂失、破箱等手续费及工本费借：现金（或：对公活期对私活期）贷：保管箱手续费收入4. 收取滞纳金借：其他应付款----保管箱押金（或：现金对公活期对私活期）贷：营业外收入'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第六章  租金、押金及手续费等的收取', '第二十一条', '收取租金、押金、手续费、滞纳金，应做到一笔一清，账实相符，账平表对，当日并账，日清日结。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第六章  租金、押金及手续费等的收取', '第二十二条', '在租约期内，租用人如违反保管箱管理办法及租用协议，本行应终止租约，并联系租用人当面说明情况，本行只退押金，不退租金。在租约解除后直至租用人交还钥匙、将箱内物品提清前，本行照收租金。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第七章  保管箱的开启', '第二十三条', '租用人在本行营业时间内申请开箱，由业务经办人员鉴定其开箱资格，确认无误后出具开箱通知单(标明所开箱号)交租用人。如租用人超次开箱，经办人员应向其收取开箱手续费，确认款项收妥后，出具开箱通知单交租用人。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第七章  保管箱的开启', '第二十四条', '主管库员审核租用人所持开箱通知单及相关证件，待客户填写《保管箱客户指纹信息录入申请书》后，录入客户指纹等信息，登记《出入库房登记簿》。主、副管库员应采用一定的安全检查手段，确保保存的物品为非违禁物品。在确认客户所有相关信息准确无误后，由主、副管库员共同打开保管箱库房，并由副管库员陪同租用人进入保管箱库房。副管库员和租用人分别用主客钥匙打开保管箱锁。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第七章  保管箱的开启', '第二十五条', '有下列情况之一者，本行应拒绝开箱：（一）申请开箱人身份证件、身份认证资料（如指纹、影像、印鉴、签名、合法有效证明文件等）与预留本行的资料不符；（二）代理人申请开箱未经授权；（三）联名租用人现场发生争执、纠纷；（四）未按规定缴纳租金、押金、手续费及滞纳金等；（五）接到租用人死亡通知后尚未确定合法继承人；（六）保管箱被公安司法部门认定有做非法用途嫌疑或依法查封。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第七章  保管箱的开启', '第二十六条', '每日营业结束，业务经办人员打印业务登记簿，与实物及相关业务记录核对，并将有关账务并入该营业机构当日总账。业务复核人员（兼主管库员）负责复核并统计所租保管箱即将到期的客户，形成清单，交由副管库员按照约定方式(电话或邮寄，如邮寄则发送到期通知单)通知租用人。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第二十七条', '租用人如需续箱，应于租箱到期前填写《续租申请书》交本行审核，本行业务经办人员鉴定客户资格无误后鉴章，办理续租手续，并按规定收取下期租金。为保护租用人利益，必须由租用人或其授权人办理续租。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第二十八条', '租用人可以自由退租。租箱期满，租用人持客钥匙(两把)和押金等收费凭证，到本行申请退租。业务经办人员鉴定客户资格无误后，办理退租手续。租用人清理完箱内物品，本行经核实后收回客钥匙(如所退箱为可变锁，须及时将客户锁孔变为零钥匙状态)和押金收费凭证。所有费用结清后，经办人员开具现金付出凭证，将押金退还租用人，并加盖“付讫”章及经办人员名章。经业务复核员复核后，释放所退箱号，将该客户资料移档保存。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第二十九条', '租用人(不含代理人)中途退租，填写《中途退租申请表》经本行审验无误后办理。已交本期租金不予退还；押金和已预交的下一租期租金，本行凭租用人提交的收费凭证全额退还。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第三十条', '租用人亡故，本行在接到死亡证明文件前，其授权代理人有权在授权期限和授权范围内办理手续。本行在接到死亡证明文件后，根据有效法律文件（境外的法律文件必须经过有权部门公证证明其真实性、有效性和合法性)，准许其合法继承人或授权代理人自行清理箱内物品(本行不必参与)，并办理退租手续。如合法继承人需继续租用保管箱，应重新办理租箱手续。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第三十一条', '继承人开箱应按照中国银行业监督管理委员会的有关规定执行；无明确规定的，可参照存款人死亡后存款过户或支付手续的有关规定执行。合法继承人无须全部到场，但未到的继承人应有经公证的书面声明或书面委托。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第三十二条', '租用人或代理人所持客钥匙遗失，应办理挂失手续。如遗失一把钥匙，本行应予以换锁；如遗失两把钥匙，租用人须同时填写《破箱申请书》，本行予以破箱，破箱时租用人或代理人必须在场，并在破箱记录上签章确认。由此产生的费用及损失由租用人或代理人承担。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第三十三条', '租用人或代理人身份证件遗失或更换，应及时通知本行登记备案。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第三十四条', '租用人或代理人逾期3个月未办理续租、退租手续，本行有权扣留押金并停止开箱，同时由业务复核人员通知客户解除租用协议。如逾期6个月仍未办理续租、退租手续的，本行有权向司法机关申请公证破箱，箱内物品由公证部门登记、开列清单，本行移存保管。由此形成的费用和损失，由租用人或代理人承担。经公证破箱满6个月，本行再次限期催告客户，如仍未提取，本行将依业务管理办法和租用协议处理存放物品以偿付一切费用及损失，如有盈余暂列“其他应付款”科目。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第八章  续租、退租、挂失和查封', '第三十五条', '如公安司法部门依法查询、冻结或扣收保管箱内物品，符合法律程序并出具有关证明的，银行应予协助执行，并妥善保存有关法律证明文件。已查封的保管箱必须在禁令解除后方可使用。箱内物品的处理依照有关法律规定办理，或移至他箱与公安司法部门或公证部门共同封存冻结，或由公安司法部门出具有效的物证转移书进行扣收。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第九章  附则', '第三十六条', '租用人或代理人不得在保管箱内存放易燃、易爆、易变质、易腐蚀和易挥发等危险品及液体，不得存放枪支、弹药和毒品等违禁物品以及放射性物品，不得利用保管箱进行窝藏赃物等非法活动，本行有权要求租用人开启保管箱，以便检查，如违反本条规定，本行有权终止开箱并提请公安、司法部门处理。如因租用人或代理人违反本规定而造成本行及他人财产损失，由其承担全部责任。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第九章  附则', '第三十七条', '本行及其工作人员应严格执行国家法律的有关规定，保护租用人的合法权益，并为租用人严守秘密。除法律另有规定外，本行拒绝任何单位或个人以任何形式查询，冻结租用人保管箱。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第九章  附则', '第三十八条', '本议事规则由本行三农业务部负责解释和修订。'),
(NULL, '三农业务部', '《保管箱业务管理办法及操作流程》', '第九章  附则', '第三十九条', '本办法及流程自印发之日起执行。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第一章  总则', '第一条', '为进一步规范××××银行股份有限公司（以下简称“本行”）的机关部室及团队员工管理，全面、客观、准确地考评机关部室及团队员工的政治素质、业务素质、服务水平和岗位职责履行情况，建立科学、合理的考核和分配机制，制订本办法。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第一章  总则', '第二条', '本办法所指机关部室及团队员工是指与本行签订劳动合同，机关部室从事业务拓展、经营管理、支持保障等工作的人员，以及其他由部室直接管理的集中作业的所有人员。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第一章  总则', '第三条', '本行成立员工等级考评领导小组，组长由分管人力资源管理工作的行长担任，组员为机关各部室负责人；员工等级考评按照“统一领导、分级负责、客观公正”的原则，根据部室、团队、员工本人的岗位职责对机关部室及团队员工进行等级考评经部室初评、分管行长审核后交人力资源部汇总，报员工等级考评领导小组审定；年初各部室须明确各岗位工作职责、目标任务。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第二章  考评项目与评定条件', '第四条', '机关部室及团队员工等级考核评定工作每年组织一次，原则上在每年员工业务技能测试后进行。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第二章  考评项目与评定条件', '第五条', '考评从职业素养、业务素质、业务技能、工作质量、工作业绩、服务效能等六个方面综合评价。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第二章  考评项目与评定条件', '第六条', '参加评级人员应当具备的条件：1.具有良好的职业道德，较好地履行岗位职责，文明服务，秉公守法，廉洁奉公。2.熟悉和遵守国家有关法律、法规和规章制度，认真贯彻执行行业和本行规章制度。3.熟练掌握商业银行基本业务知识和岗位专业知识，熟练掌握岗位业务技能及办公业务系统操作技能。4.取得职业资格和上岗资格证书。5.取得岗位需要的业务技能级别。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第三章  等级评定标准和内容', '第七条', '等级评定标准和内容：等级考评指标采取百分制。（一）职业素养考核（10 分）1.拥护党的路线、方针、政策，具有较高的政治素质和思想觉悟，积极支持和参与本行中心工作及重点工作，从正当途径反映工作问题或工作建议，得 2 分；2.熟悉相关法律法规和管理规定，通过总行组织的合规及法律法规知识测试，得 1 分；3.具有较强的事业心和责任感，作风正派，身心健康，遵纪守法，有良好的职业素养和从业记录，得 3 分；受到警告、记过、记大过等纪律处分的分别扣 1 分、2 分、5 分（多次受到处分的以最高等级计算，受到党纪处分的按对应等级扣分）；4.近三年年度考核均为称职以上，得 1 分；5.自愿服从组织调配，得 1 分；不服从部门工作安排的每次扣 1 分，不服从总行工作调配的每次扣 3 分；6.自觉遵守本行管理制度，得 1 分；发生不遵守本行管理制度行为未造成重大影响的每次扣 2 分，造成重大影响的每次扣 5 分；7.遵守廉洁从业规定，得 1 分；发生违背廉洁从业规定行为，经查实，情节较轻，未造成影响的扣 5 分，情节较重，造成社会影响的，扣 10 分。（二）业务素质考核（20 分）业务素质考核包括学历、相关职称、职业资格、学习能力四个方面，学历 5 分、职称 7 分、职业资格 8 分，学习能力为加、减分项。1.学历考核，取得本科学历的得 4 分，取得研究生及以上学历得 5 分，大专学历扣 1 分，中专及高中学历扣 2 分，高中以下学历扣 3 分。2.职称考核，分为高级职称、中级职称和初级职称三个等级，分别计 7 分、3 分和 1 分，无职称的扣 2 分。职称必须与本人工作岗位相近，取得多个的按最高级别计算，不重复计算得分。3.职业资格考核，包括银行业专业人员职业资格和从业资格，银行专业人员职业资格占 5 分，从业资格占 3 分。其中，银行业专业人员职业初级资格（《银行业法律法规与综合能力》加任一专业科目）得 2 分，另通过一个专业科目加 0.5 分；取得银行业专业人员职业中级资格（《银行业法律法规与综合能力》加任一专业科目）的得 5 分，另通过一个科目加 1 分；未取得初级资格的扣 2 分。取得与岗位相匹配的从业资格，得２分，另取得一个加 0.5 分；应取得未取得的扣 2 分（无要求的不扣）。4.学习能力考核，总行组织的培训测试及其他考试实行积分制，低于 60 分（或不合格）每次扣 1 分，高于 80 分（或合格）每次加 0.5 分，最高加分不超过 5 分。（三）业务技能考核（20 分）业务技能考核包括综合业务知识、会出业务实务、信贷业务实务、电子银行实务、数字录入、汉字录入、手工点钞等 7 个项目；通过（即全部达到 D 级）五项以上的每项得 1 分，每项每提高 1 个等级加 0.5 分；通过四项以下的不得分；五个以上项目为 A 级的本项目满分。（四）工作质量考核（20 分，由员工所在部门和工作牵头部门负责）1.报表、材料报送。迟报、错报、漏报，每次扣 5 分，被主管部门通报批评的，每次扣 10 分，累计两次以上的该项不得分；因配合部门员工配合不力造成的，对配合部门员工加倍扣分，最高扣 20 分（由牵头部门负责人提请分管领导审定）。2.工作质量。被主管领导批评的，有一次扣 2 分，受到主要领导批评的，有一次扣 4 分。3.主管或经办工作。出现一般性差错的有一次扣 1 分；在各项检查中发现重大问题的，至少扣 8 分，直至扣完为止；主管、经办的工作出现案件或重大事故，负有直接责任的，本项不得分，负有间接责任的，至少扣 15 分。4.岗位工作。不按时按质完成的，每次视情至少扣 3 分；突击性工作不能按时按质完成的，加倍扣分。（五）工作业绩考核（20 分，由员工所在部门负责）1.本岗位基本工作任务。完成的得 10 分；未完成的，按未完成的比例和数量扣分，完成率低于 80％的不得分或每有一项不完成的扣 3 分，完成率在 80％以上的按比例得分。2.总行下达的中心工作任务。完成的得 8 分；未完成的，按未完成的比例和数量扣分，完成率低于 80％的不得分或每有一项不完成的扣 3 分，完成率在 80％以上的按比例得分，最高得 12 分。3.突击性工作任务。完成的得 2 分，有一项不完成的扣 1 分。（六）服务效能考核（10 分）按服务测评结果折算得分，发生一起服务对象有效投诉扣 2 分，扣完为止。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第四章  等级确定', '第八条', '机关部室及团队员工等级按年评定。由高到低一、二、三、四、五、六、七等七个等级。综合得分 95 分（含）以上的为一级，90 分（含）－95 分的为二级，85 分（含）－90 分的为三级，80 分（含）－85 分为四级，75 分（含）－80 分为五级，70 分（含）－75 分为六级，70 分以下为见习级。其中：一级为专家级行员，二级为资深级行员，三级为高级行员，四级、五级为中级行员，六级为初级行员、七级为见习行员。业务技能测试及银行业专业人员职业资格考试未全部通过、业务技能等级达不到五个 A 级以上者不得评定为三级以上等级。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第四章  等级确定', '第九条', '45 周岁以上的女职工和 50 周岁以上的男职工参加业务技能测试理论考试合格后，按实际评定等级上调一级（一级除外）。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第四章  等级确定', '第十条', '有下列情形的，在考评时给予加分。1.本人受到本级或××××市属部门表彰的，加计 2 分；受到××××市级（含）或××××市属部门表彰的加计 5 分，受到××××市级或省属部门表彰的加计 8 分，受到省部级表彰的加计 20 分。2.代表本行参加××××级别（包括行管组）业务技能比赛或知识竞赛获得团体或单项前 1－3 名（或一、二、三等奖）的，分别加计3 分、2 分、1 分和 5 分、3 分、2 分；××××级别业务技能比赛获得团体或单项前 1－3 名（或一、二、三等奖），分别加计 5 分、3 分、分和 10 分、8 分、5 分；省级农商行系统业务技能比赛获得团体或单项前 1－3 名，分别加计 15 分、12 分、10 分，个人获得前 10名的，分别加计 20 分、18 分、17 分、15 分、14 分、13 分、12 分、11 分、10 分、9 分。3.发生本行认为可以加分的情形，经评级小组集体认定后予以加分。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第四章  等级确定', '第十一条', '有下列情形的，取消评级、降级或缓评级。1.受到记大过及以上处分的，在处分期内取消原等级，降为见习级。2.受到服务对象投诉或其他因素引发重大舆情风险，对本行造成声誉影响的，已评级的降为见习级一年，未评级的缓评级一年。3.合规员工考核不达标或违规记分一年内累计记分 100 分以上的，按原评级下降一个等级评定，累计记分 150 分以上的，已评级的取消等级一年，未评级的缓评级一年。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第五章  等级管理', '第十二条', '岗位变动员工等级的调整1.因工作需要调整为部室及团队岗位的，在下一个评定期前，运营主管按机关员工平均等级确定，客户经理、柜员均下调一个等级。2.跨部室或团队间岗位平行调整的，在下一个评定期前，原级别不变，从二级团队调整到部室或一级团队的下调一个等级，具体团队级别按照组织架构设置相关文件为准。3.部室及团队岗位调整到经营单位的，在下一个评定期前，正常调整的原级别不变，特殊情况由总行员工等级评定领导小组确定。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第五章  等级管理', '第十三条', '等级评定结果与薪酬待遇挂钩，挂钩比例按照相关办法执行。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第五章  等级管理', '第十四条', '实行员工等级公示、复议制度。员工对公示的等级评定结果有异议的，可向总行员工等级评定领导小组申请。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第五章  等级管理', '第十五条', '对违反考评工作纪律的，按照本行《员工违规行为处理办法》从重处理。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第五章  等级管理', '第十六条', '公司业务部门、小微业务部门、资产保全部团队人员等级按客户经理等级评定。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第六章  附则', '第十七条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '(2017版待发文后发最新稿定稿)《机关部室及团队员工等级管理办法》', '第六章  附则', '第十八条', '本办法经职工代表大会审议通过后，自 ×××× 年×月 ×日起执行。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第一条', '基本原则（一）党管干部；（二）德才兼备，以德为先；（三）年轻化、知识化、专业化；（四）群众公认，注重实绩；（五）个人意愿与组织挑选相结合；（六）风险控制，动态管理。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第二条', '任用条件（一）基本条件1.拥护党的路线、方针、政策，具有较高的政治素质和思想觉悟；2.具有合格的业务技能，工作业绩突出，协调管理能力、风险管控能力和问题处理能力强，富有开拓创新精神，市场竞争意识好；3.具有履行岗位职责所需的专业知识，熟知相关法律法规和管理规定，通过总行统一组织的竞岗测试；4.具有较强的事业心和责任感，作风正派，身心健康，廉洁从业，遵纪守法，有良好的职业素养和从业记录；5.近三年年度考核均为称职以上；6.自愿服从组织调配；7.符合亲属回避规定。（二）资格条件1.部门总经理（1）首任年龄男性 40 周岁（含）以下，女性 35 周岁（含）以下，在任或曾担任部门负责人的年龄适当放宽，其中男性不超过 54 周岁，女性不超过 50 周岁；（2）本科及以上学历；（3）本科学历人员须金融从业 8 年以上，且从事管理岗位 2 年（含）以上；硕士研究生学历人员须金融从业 5 年以上，且从事管理岗位 1 年（含）以上。（4）银行业专业人员职业资格（中级）专业科目通过 3 门以上或具备劳动人事部门认可的与岗位相关的中级及以上专业技术职称；（5）熟悉金融法律法规、监管规定、行业规章，熟悉银行业务及管理流程，具备较强的风险管控能力、业务拓展能力、协调能力；   （6）具备一定的战略规划水平和较强的语言文字表达能力，能熟练使用办公软件；（7）具有较强的执行力；（8）具有较强的敬业精神和团队合作精神；（9）符合银行业监管部门要求的其他任职资格条件。2.支行行行长（1）首任年龄男性 45 周岁（含）以下，女性 35 周岁（含），在任或曾担任经营单位、部门负责人的年龄适当放宽，其中男性不超过 56 周岁，女性不超过 50 周岁；（2）本科及以上学历；（3）本科以上学历须金融从业 4 年以上，且曾担任信贷主管、会计主管、运营主管或在机关工作 2 年以上。在任人员为高中（中专）学历的，金融从业须 10 年以上；（4）银行业专业人员职业资格（中级）专业科目通过 3 门以上或具备劳动人事部门认可的与岗位相关的中级及以上专业技术职称；（5）熟悉金融法律法规、监管规定、行业规章，熟悉银行业务及管理流程，具备较强的风险管控能力；（6）具备较强的业务拓展能力和一定的公关能力；（7）具有较强的执行力；（8）具有较强的敬业精神和团队合作精神；（9）符合银行业监管部门要求的其他任职资格条件。3.团队负责人（1）首任年龄原则上不超过 35 周岁，在任或曾经担任副股级以上人员的年龄适当放宽，其中男性不超过 52 周岁，女性不超过 48 周岁；（2）大学本科及以上学历，已在部室工作的人员放宽至大专及以上学历；（3）本科学历人员须金融从业 5 年以上，且具有 3 年以上总行部室工作经历或从事信贷主管、会计主管、运营主管等以上管理岗位 2 年以上；硕士研究生学历人员须金融从业 3 年以上，具有 2 年以上总行部室工作经历或从事信贷主管、会计主管、运营主管等以上管理岗位 1 年以上；（4）通过银行业专业人员职业资格（中级）专业科目通过 3 门以上或具备劳动人事部门认可的与岗位相关的中级及以上专业技术职称；（5）熟悉金融法律法规、监管规定、行业规章，熟悉银行业务及管理流程，具备较强的风险管控能力、业务拓展能力；（6）熟悉熟练运用办公软件，具有较强的文字表达能力；（7）具有较强的执行力；（8）具有较强的敬业精神和团队合作精神。（三）特别条件1.已到轮岗期限的在任人员不得竞聘原职位。2.所有岗位试用期均为一年，六个月初考核一次。3.鼓励条件优秀的人员竞报规模较大的支行、总行业务发展部门、业务管理部门。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第三条', '竞岗流程（一）公布方案总行发布公开竞聘方案，公布竞争岗位、报名基本条件及资格、竞岗流程等。各支行（部）、机关各部门做好宣传发动工作。（二）报名及资格审查实行自愿报名，经所在单位（部门）初审后，总行资格审查、政审后提交总行党委审定。（三）组织竞聘1.竞聘演讲（20 分）。由竞聘者根据拟竞岗位基本情况、部门职能、岗位职责，阐述未来任期的工作思路、工作目标、工作措施等，由评委现场评分。评委由总行领导班子成员、经民主推荐的部门负责人或支行行长代表组成，其中总行领导班子成员打分权重为60%。2.综合测评（15 分）。在审阅竞聘者个人述职材料（1500 字左右）的基础上，分两个层面进行，一是组织竞聘人员所在单位（机关为一个单位）全员对其进行测评，分值占比 40%；二是组织部门负责人对基层竞聘人员进行测评，支行行长对机关竞聘人员进行测评，分值占比 60%。测评实行“实名制”。3.前三年奖惩加减分（加减分均不超过 15 分）。各单位（部门）近三年获得省级表彰（含机关部门在省联社专项考核中获得先进表彰或排名前 15 名)的，对单位负责人一次加 5 分，其他人员一次加 3 分；获得××××市级表彰的，对单位负责人一次加 3 分，其他人员一次加 2 分；获得××××市级表彰（含总行表彰的“综合先进集体”）的，对单位负责人一次加 2 分，其他人员一次加 1 分；基层经营单位前三年在本行经营目标综合考核中排名前 10 名的，对负责人一次加 5 分，其他人员一次加 3 分。前三年本人获得的荣誉按同级别部门负责人加分标准量分。同一年度按最高奖励分计算。机关部门近三年在省联社专项考核中排名后 15 名的，部门负责人一次减 5 分，其他人员一次减 3 分；近三年本行机关履职测评和服务测评中，每年年终折算综合排名后三名的部门负责人一次减 5 分，其他人员一次减 3 分；基层经营单位近三年在本行经营目标综合考核中排名后 10 名的，负责人一次减 5 分，其他人员一次减 3 分。4.学习积分（10 分）。标准如下：5.党委民主集中决策（35 分）。6.其他加分项（不超过 5 分）。（1）年龄 35 周岁（含）以下，加 2 分。（2）第一学历为硕士研究生的，加 2 分。（3）曾经担任总行机关部门副职、副股级办事员、总经理助理的加 1 分，现任或曾经担任总行机关部门负责人的，任职年限满三年及以上的加 5 分，不足的加 2 分；现任或曾经担任基层经营单位信贷主管、会计主管、运营主管或副行长的加 0.5 分，现任或曾经担任基层经营单位负责人的，任职年限满三年以上的加 3 分，不足的加 1 分。（4）近三年理论调研文章在××××市级行业报刊发表的，有一次加 1 分，在省级以上行业报刊发表的，有一次加 2 分；在省联社工作经验介绍或工作成果在全省行业推广的，有一次加 3 分。（四）组织考察。根据竞岗情况，由总行党委确定各岗位 1-2名考察人选，考察情况提交党委会。（五）党委讨论。总行党委集体讨论，确定各岗位拟聘人选。如出现空缺现象，由总行党委视情况决定。（六）公示及聘任。对拟聘人员在全行公示，公示结果不影响任职的，按照相关规定聘任。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第四条', '组织领导中层人员竞聘工作在总行党委领导下进行，总行纪委全程监督。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第五条', '任职管理（一）现任中层管理人员中未取得银行业专业人员中级职业资格的，两年内须通过《银行业法律法规与综合能力》及 1 个专业科目，三年内须达到任职条件。对上岗第二年未通过的人员当年降薪5%；三年内未达到第二年条件的予以解聘，同时按未通过的科目，当年每门降薪 5%，对虽满足第二年条件但不满足三年条件的亦按此标准降薪。每年度超额完成上述学习考试任务的，总行将给予适当奖励。（二）任期内，当年在全行经营目标综合考核排名最后 1 名的，予以淘汰；连续两年后 3 名，或连续三年后 5 名的，予以淘汰。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第六条', '退出现职管理（一）对退出现职人员实行弹性管理，体现人文关怀。（二）原则上，基层经营单位男 56 周岁、女 52 周岁退居二线。任期内经营目标综合考核排名全行前 10 名的，在本人自愿的前提下，经总行党委研究同意的，可适当延长任职年限，推迟退二线年龄；男年满 52 周岁，女年满 48 周岁，前三年经营目标综合考核排名全行后 10 名的，须退居二线；男年满 50 周岁，女年满 45 周岁的在任支行行长主动申请退居二线人员，须经组织批准并接受组织安排。（三）退二线人员，自愿接受组织安排，能够完成所从事岗位工作任务的，其薪酬标准按照以下标准执行：1.工作年限满 30 周年，且担任中层正职（或副职主持工作）满15 周年的，自批准次月起连续 24 个月享受原岗位职能工资标准；自第 25 个月起，按隶属关系，连续 24 个月享受机关团队负责人或基层运营主管平均岗位职能工资；自第 49 个月起，不再享受岗位职能工资，连续 12 个月享受本单位（部门）一般员工最高工资标准，但最高不超过所在支行的客户经理平均工资或所在团队的团队负责人工资；自第 61 个月起按其所从事岗位重新确认。2.工作年限满 25 周年，且担任中层正职（或中层副职主持）满 10 周年的，自批准次月起连续 24 个月继续享受原岗位职能工资标准；自第 25 个月起，不再享受岗位职能工资，按隶属关系，连续12 个月享受本单位（部门）一般员工最高工资标准，但不得超过所在支行的客户经理或所在团队的团队负责人工资；自第 37 个月起按其所从事岗位重新确认。3.工作年限满 20 周年，且担任中层正职（或中层副职主持）满5 周年的，自批准次月起连续 12 个月继续享受原岗位职能工资标准；自第 13 个月起按其所从事岗位重新确认。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第七条', '工作纪律参加竞聘人员要正确对待竞聘工作，不得找关系打招呼、拉选票；相关工作人员要严格遵守人事工作纪律，不得弄虚作假，随意泄露信息。如出现违规违纪行为，除取消竞聘资格外，总行党委将视情况追究相关责任人责任。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第八条', '本办法由××××银行股份有限公司人力资源部解释和修订。'),
(NULL, '人力资源部', '《中层管理人员任用办法》', '', '第九条', '本办法自发文之日起施行。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第一章　总  则', '第一条', '为适应××××银行股份有限公司（以下简称“本行”）改革发展的要求，建立健全人力资源管理机制，更好地为全行发提供人力资源保障，依据国家劳动人事法律法规、政策制度与现代公司治理要求，结合《中华人民共和国劳动合同法》、《中华人民共和国档案法》、《××××银行股份有限公司章程》（以下简称《章程》）以及本行实际，制定本办法。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第一章　总  则', '第二条', '本行人力资源管理，遵循“以人为本、依法运行、统一领导、集中管理”的原则，依据“科学配置、提高效能、合规操作、不断创新”的要求，对人力资源进行合理利用和开发管理。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第一章　总  则', '第三条', '本办法主要内容包括：机构设立、员工招录、劳动用工、岗位管理、考核奖惩、干部管理、薪酬福利、教育培训、异动管理、员工退出、考勤管理、回避制度、人事档案等。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第二章　机构设立', '第四条', '本行为股份制公司，各级分支机构和职能部门的设立与调整须经本行董事会审议同意，按照银行业监督管理行政许可程序办理，并合理配备相应人力资源团队。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第二章　机构设立', '第五条', '本行董事会、监事会组成人员，按本行《章程》规定由股东大会、职工代表大会依法选举产生；董事会、监事会内设职能机构分别由董事会、监事会各自会议决议产生。本行经营管理按照业务发展需要建立“总行—支行”组织架构，实行董事会领导下的行长负责制，其中：（一）总行。下设“办公室、人力资源部、运营管理部、计划财务部、信贷管理部、授信评审部、风险管理部、合规管理部、电子银行部、科技信息部、市场营销部、审计部、纪检监察部 、安全保卫部”等职能部门。根据需要设置相应的管理中心，分别挂靠有关职能部门。职能部门及中心根据管理需要，配备总经理（副总经理）、中心主任（副主任）和符合业务发展需要的若干办事员等人力资源。（二）总行营业部。依照总行授权在营业部总经理领导下开展业务经营。（三）分支机构。依照总行授权在其所属支行行长（主持工作副行长）领导下开展业务经营。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第二章　机构设立', '第六条', '本行高级管理人员的任职本行董事长、执行董事、行长、监事长、副行长、行长助理及董事会秘书，总行运营管理、计划财务、合规管理、审计稽核等部门负责人，总行营业部总经理，支行行长等任职资格，应报经银行业监督管理机构审查核准。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第二章　机构设立', '第七条', '总行及分支机构、内设职能部门的人力资源由本行人力资源部归口管理，并按相应许可政策办理。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第二章　机构设立', '第八条', '机构岗位设置坚持“合规有序、精简高效”的原则，做到职能明确，责任清晰，权限到位，管理有效。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第三章　员工招录', '第九条', '本行招聘录用新员工遵循“按需定编、公开招聘、择优录用、双向选择”的原则。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第三章　员工招录', '第十条', '根据本行业务发展状况、当年自然减员情况以及网点机构的增减情况，在“三定”基础上平衡测算，按照增员需求与岗位要求，由人力资源部拟定年度员工招聘方案，经总行党委研究同意，报省联社审批后实施。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第三章　员工招录', '第十一条', '招聘工作坚持“公开、平等、择优”的原则，笔试、面试、考核和体检合格者，提交总行党委会议研究，报经省联社审批同意后方可正式录用。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第三章　员工招录', '第十二条', '招录人员需参加本行组织的职业资格培训，培训费和食宿费自理。培训合格，业务技能全部达标后由本行按规定程序办理聘用手续。（一）新招录员工需将本人人事档案调入本行管理。 （二）为保护本行的商业秘密，维护企业与员工共同的长远利益，重要岗位人员须签订《竞业限制协议》。（三）试用期原则上为6个月，特殊情况另定。试用期间，如发现不符合招聘条件的，立即停止试用。试用期考核不合格者不予以正式录用。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第三章　员工招录', '第十三条', '本行招聘录用新员工执行人事回避制度。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第三章　员工招录', '第十四条', '本行员工招录实施细则另行规定。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第四章　劳动用工', '第十五条', '本行员工管理, 遵循“合法公平、平等自愿、协商一致、诚实信用”的原则，正确处理好国家、企业、股东和员工之间的利益关系，调动各方面的积极性。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第四章　劳动用工', '第十六条', '根据《中华人民共和国劳动合同法》的有关规定，本行在编人员实行全员劳动合同制。在平等自愿、协商一致的基础上，按照法律法规和劳动合同，确定、调整本行与员工的权利义务，通过签订劳动合同形式建立劳动关系。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第四章　劳动用工', '第十七条', '劳动合同制适用下列劳动法律法规：（一）《中华人民共和国劳动法》；（二）《关于贯彻<中华人民共和国劳动法>若干问题的意见》；（三）《中华人民共和国劳动合同法》；（四）地方相关法律法规。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第四章　劳动用工', '第十八条', '劳动合同文本由本行根据国家与地方政府的政策要求，结合当地劳动部门规定及本行实际,印制《××××银行股份有限公司劳动合同书》。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第四章　劳动用工', '第十九条', '劳动合同由总行与员工签订。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第四章　劳动用工', '第二十条', '根据需要，本行炊事员等岗位实行非全日制用工的用工制度，按法律相应规定确定劳动报酬。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第四章　劳动用工', '第二十一条', '为维护劳动者和用人单位合法权益，按照政策规定与政府部门要求，本行与工会签订集体劳动合同，共同促进全行劳动关系和谐稳定。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十二条', '与本行签订劳动合同的员工，通过竞聘或双向选择取得相应工作岗位，并与本行签订岗位聘用（任）协议及其相关责任书，在劳动合同的基础上进一步明确各自权利义务。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十三条', '岗位聘用（任）协议的内容包括岗位职责和要求、聘用（任）期限、与受聘岗位相应的待遇及其他协商条款等。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十四条', '根据工作岗位的性质和职责，相关重要岗位聘用（任）人员还应签订竞业限制协议和保密协议。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十五条', '聘用（任）方式（一）高中层管理人员（总行高级管理层、中级管理层、基层管理层等人员）的选聘，按照《章程》及高级管理人员资格管理规定执行。（二）一般员工按岗位需要，通过考试、考核等方式竞争上岗，双向选择，择优聘用。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十六条', '聘用（任）期限应小于或等于劳动合同期限，一般为1—3年，聘期届满或劳动合同终止后，聘用关系终止。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十七条', '在聘用（任）期内，因工作岗位变动，须重新签订岗位聘用（任）协议。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十八条', '受聘人员不愿继续从事本岗位工作的，或不能胜任与适应现工作岗位、不认真履行岗位职责的，可以解除聘用（任）关系。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第五章　岗位管理', '第二十九条', '由于员工自身原因未达到岗位聘用（任）条件或要求的，实行待（转）岗培训，经考试、考核合格者，再参与竞聘上岗。本人拒绝待（转）岗培训或竞聘上岗未成的，由人力资源部报经总行党委会议研究决定后，按照有关规定程序解除劳动合同。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十条', '本行考核遵循“客观公正、民主公开、注重实绩”的原则，考核内容包括德、能、勤、绩、廉等五方面，重点考核其在本职岗位上履职情况以及个人综合表现。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十一条', '对员工的考核分为常规考核和年度考核。（一）常规考核作为年度考核的基础，总行各部门、分支机构负责人对下属日常工作情况应有详细观察、记录、分析。（二）年度考核每年一次，考核结果分为优秀、称职、基本称职、不称职四个等级。（三）考核务必做到公平、公正，以事实为依据，不受被考核对象性别、年龄、学历、工龄、地位等因素干扰，不受个人偏见与喜好左右。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十二条', '按照管理权限的不同，总行高级管理人员（领导班子成员）的考核由省联社负责；中级管理层人员、基层管理层人员的考核由行长室负责；总行各部门、分支机构一般员工的考核分别由各部门、分支机构负责。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十三条', '考核对象、内容、标准、方法和程序根据本行员工考核的有关规定实施。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十四条', '年度考核结果作为对员工聘用（任）、续订或解除劳动合同、奖惩及享受有关待遇的主要依据。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十五条', '员工有下列行为之一的，给予嘉奖或授予荣誉称号：（一）为本行创造显著经济效益的；（二）维护本行利益和荣誉，敢于制止、揭发损害本行利益的行为，保护公共财产、防止事故发生，为本行挽回重大经济损失的；（三）为本行获得重大社会荣誉的；（四）改进管理成效显著的；（五）有其他突出成绩的。受表彰、嘉奖和授予荣誉称号的员工，本行给予精神鼓励和物质奖励。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十六条', '员工犯有错误或违反本行的规章制度，视情节轻重，按有关规定和程序，分别给予通报批评、警告、记过、记大过处分直至解除劳动合同。受处分的员工，同时给予相应的经济处罚。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第六章　考核奖惩', '第三十七条', '本行实行全员风险责任金制度。本行根据员工工作岗位的责任大小、风险程度等因素由员工缴纳相应的风险责任金，设立员工风险责任金账户，用于员工因责任事故、经济案件、违约过失等需赔偿或罚没款项等支出。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第七章　干部管理', '第三十八条', '本行按照现代企业制度对人力资源管理的要求，实施人才分类指导与集中管理。建立公开平等、竞争择优、权责对等的企业领导管理体制。本行所称“干部”，是指从事管理岗位的人员，按职务序列分为：高级管理层，包括董事长、行长、监事长、副行长、行长助理、工会主席等管理人员；中级管理层，包括总行职能部门（营业部）总经理（主任）、副总经理（主持工作）、副总经理（副主任）、总经理助理、副股级办事员等管理人员；基层管理层，包括支行行长、副行长等管理人员。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第七章　干部管理', '第三十九条', '干部管理的基本原则。坚持党管干部的原则；坚持统一管理的原则；坚持德才兼备的原则；坚持企业特点的原则。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第七章　干部管理', '第四十条', '干部选拔任用（一）干部选拔，采用组织配备和市场配置相结合的方式，推行组织提议、群众推选、个人自荐、公开招聘、竞争上岗等方式，并采取适当形式，在一定范围内进行干部任前公示。（二）干部考察，依据选拔任用条件和不同职务的要求，全面考察其德、能、勤、绩、廉。考察中必须进行民主测评，广泛听取群众意见，客观、准确地了解被考察对象的基本情况，形成书面考察报告。（三）干部考核，除常规考核和年度考核外，还应进行任期考核。任期考核重点是任期经营管理目标完成情况和经济责任审计结果。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第七章　干部管理', '第四十一条', '干部交流本行干部按照政策规定与工作要求实行交流制度。本行干部交流以总行高级管理层副职、总行部门与分支机构负责人等人员为重点，按照一定的比例，在总行党委领导下有计划地推进。总行高级管理层副职任期超过3年，原则上应进行分工调整。其他干部应进行岗位交流。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第七章　干部管理', '第四十二条', '后备干部储备本行实行以后备干部储备为核心的人才战略，确保管理人员的梯次化与可持续性。后备干部的培养采取考察、轮岗、挂职、见习、培训等多种形式。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第七章　干部管理', '第四十三条', '干部聘任（解聘）权限本行干部实施聘任制，引入竞争机制，推行职务“能上能下”机制。全行干部聘任（解聘）权限实施既统一管理又分级负责的制度。（一）本行董事、监事由股东大会、职工代表大会选举产生（罢免）,董事长、监事长分别由董事会、监事会选举产生；（二）本行行长、董事会秘书由董事长提名（提议），董事会决议聘任（解聘）；（三）本行副行长，行长助理及运营管理、计划财务、合规管理、审计稽核等部门和营业部负责人由行长提名（提议），经董事会审议后聘任（解聘）；（四）总行其他职能部门总经理（含副职）、支行行长（含副职）由行长室提名（提议），总行行长聘任（解聘）；（五）总行部门内设中心主管与委派分支机构业务管理人员以及风险员、合规员等，由总行考察后聘任（解聘）；（六）凡须经银行业监督管理部门资格核准的管理人员，均须经相应资格核准后，按相应程序聘任；（七）经银行业监督管理部门资格核准的管理人员，按相应程序解聘后，须报银行业监督管理部门备案。（八）凡经本行聘任的干部违反党员干部纪律和本行制度，犯有错误，视情节轻重，应给予降职、解聘职务直至解除劳动合同等处分。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第七章　干部管理', '第四十四条', '干部退出本行实行有序的干部退出机制，干部退出包括：任期届满、解聘（免职）、辞职和提前离岗。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第八章　薪酬福利', '第四十五条', '本行依据国家法规政策与行业特点，遵循公正、公平的原则，依据员工的价值创造能力（才能）、在价值创造过程中的位置（责任与风险承诺）与表现（工作态度）以及价值创造的结果（贡献），给予员工合理的薪酬。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第八章　薪酬福利', '第四十六条', '员工的薪酬包括固定工资和绩效考核工资两部分。具体参照本行相关考核办法执行。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第八章　薪酬福利', '第四十七条', '本行员工依法参加社会保险，按时足额为在编员工缴纳社会保险费。同时，建立企业年金制度。     第四十八条   本行员工享有劳动保护和保障等国家规定的福利待遇。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第八章　薪酬福利', '第四十九条', '按国家有关规定，本行员工享有法定休假。因工作需要员工法定假不休息的，按规定发放加班费。员工按规定享受的婚假、产假、探亲假、丧假等期间的工资福利待遇，按国家相应规定享受。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第九章　教育培训', '第五十条', '本行根据发展战略，将教育培训与本行发展目标、人力资源开发紧密结合，实行全员长效培训制度。教育培训工作由人力资源部统一规划、具体实施、集中管理。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第九章　教育培训', '第五十一条', '员工教育培训主要包括时事政治、经济政策、金融理论、法律法规、规章制度、专业知识、工作技能、业务操作以及企业文化等与本行改革和发展相关联的内容。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第九章　教育培训', '第五十二条', '员工教育培训的形式包括新员工的岗前入职培训、在职员工的岗位培训、待（转）岗人员的上岗培训、管理人员的素质培训、专业技术人员的技能培训和员工的学历教育等多种形式。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第九章　教育培训', '第五十三条', '员工教育培训考试成绩作为转正、升（降）级、晋（降）职、转（上）岗等的重要依据之一。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第九章　教育培训', '第五十四条', '本行教育培训费用实行年度预算制，年初由人力资源部依据教育培训规划与需求编制年度费用计划，列入年度财务预算，经董事会、股东大会审议通过后组织实施。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第九章　教育培训', '第五十五条', '本行对员工进行专业技术培训、提供专项培训费用的，应事先与员工订立服务期协议。在服务期内员工无论以任何形式离职的，均按协议条款退还培训费。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十章　异动管理', '第五十六条', '调动因家庭实际困难、工作需要或其他合理原因，员工可申请调动。（一）内部调动程序个人申请→单位或部门审核并签署意见→人力资源部复核并提交总行党委→总行党委研究审批→人力资源部备案（二）外部调动程序个人申请→单位或部门审核并签署意见→人力资源部复核并提交总行党委→总行党委研究初审→报送省联社→省联社研究审批→总行党委通知人力资源部备案'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十章　异动管理', '第五十七条', '人员借调（一）由于经营管理需要，需从分支机构或总行部门临时借调人员使用的，须由人力资源部统一扎口，经与调出和调入单位（部门）协商一致，经行长同意后办理借调手续。（二）借调程序：由调入部门提出借调申请，并填写《借调审批表》，调入和调出部门签署意见后，交人力资源部初审后提交行长审批。中层管理人员借调须经董事长审批。（三）借调期间，借用人员计价工资由调入单位或部门支付，借调时间一般不超过3个月。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十章　异动管理', '第五十八条', '岗位交流和轮换为培养高素质和复合型的人才队伍，同时加强内控管理，本行实行工作岗位定期交流和轮换制度。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十一章　员工退出', '第五十九条', '根据现代企业制度对人力资源管理的要求及本行的发展战略，本行建立并不断完善员工退出机制，真正实现员工“能进能出”。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十一章　员工退出', '第六十条', '劳动合同的解除与终止（一）员工与单位因单方或双方原因，劳动合同关系可以解除和终止。（二）员工有下列情形之一的，本行可以随时与员工解除劳动合同：1.在试用期内被证明不符合录用条件的；2.严重违反本行规章制度、劳动纪律的；3.严重失职，营私舞弊，对本行利益造成重大损害的；4.被依法追究刑事责任的；5.同时与其他用人单位建立劳动关系，对完成本行工作任务造成严重影响，或经本行提出，拒不改正的；    6.法律、行政法规规定的其他情形。（三）有下列情形之一的，劳动合同终止：1.劳动合同期满；2.员工开始依法享受基本养老保险待遇的；3.员工死亡，或者被法院宣告死亡或者宣告失踪的；4.法律、行政法规规定的其他情形。（四）员工无论何种原因解除或终止劳动合同前应当办理工作交接手续，必要时接受离任审计。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十一章　员工退出', '第六十一条', '员工辞职  员工辞职原则上须提前1个月递交书面报告，正式离职前须办理好离任工作交接表；中层以上管理人员、财务人员、营销人员及敏感岗位员工须经离任审计后方可离职。（一）辞职后15日内应将基本养老保险关系和档案从本行转出。（二）员工办理辞职手续时，应归还本行有关资料、文件、办公用品及其他公物。（三）工作服工作牌必须交回，由各单位（部门）负责收缴（签字）后交总行办公室。未交回工作服工作牌的人员，必须向总行办公室缴纳折算费用。（四）员工辞职时所掌握的科技档案、合同文本等保密资料，由所属单位或部门负责人负责收回，否则由单位或部门负责人承担责任。（五）员工辞职审批流程：个人申请→单位或部门审核并签署意见→人力资源部复核并提交总行党委→总行党委研究审批→人力资源部备案并通知本人及其原单位办理相关手续。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十一章　员工退出', '第六十二条', '员工退休员工达到法定退休年龄，即男性员工年满60周岁，女性员工年满55周岁（法律法规另有规定的除外），应办理退休手续。本条款自2013年1月1日起执行，退休手续办理以省社保部门政策认可为准。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十二章　考勤管理', '第六十三条', '考勤是指对所属员工的出勤、迟到、早退、旷工、事假、病假、加班等情况的记录，也是对员工进行考核、奖惩的重要依据之一。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十二章　考勤管理', '第六十四条', '员工必须严格遵守劳动纪律，按时上下班，不得无故迟到或早退，工作时间内不得外出办私事，上班时间不得带与工作无关的人员进入单位，也不得从事与工作无关的活动。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十二章　考勤管理', '第六十五条', '工作日作息时间安排上午：8：00－11：30　　下午：14：00－17：30（夏季高温作息时间：上午：7：30－11：30　　下午：15：00－18：00，执行时间按××××市人民政府通知）。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十二章　考勤管理', '第六十六条', '休假项目包括：公休假、法定休假、年休假、婚假、丧假、生育假、病假、事假等，具体参照《××××银行股份有限公司假期管理暂行办法》执行 第六十七条  请假程序员工休假前，必须办理休假手续，经审批同意且办理好工作交接后，方可休假。非突发情况，本人应在3天前提出请假申请，休年休假的应在5天前提出申请。员工休假结束后，必须及时向人力资源部销假，否则视同旷工。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十二章　考勤管理', '第六十八条', '员工续假的手续和程序与请假相同，天数累计计算后按权限报批。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十二章　考勤管理', '第六十九条', '凡属下列情况之一者，按旷工论：（一）未请假或请假未获批准而擅自离开工作岗位的；（二）假期已满，未续假或续假未获批准而逾期不归的；（三）不服从总行调动和工作分配，不按时到工作单位或岗位工作的；（四）利用病假、事假在外兼职的；（五）旷工3天以上者由各单位（部门）报告人力资源部，否则由各单位（部门）负责人承担责任。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十二章　考勤管理', '第七十条', '员工出差前必须按以下程序办理审批：（一）中层以上管理人员由总行行长审批。（二）其他员工由单位（部门）负责人审批。（三）出差人员办理差旅费借支、报销手续须按有关规定执行。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十三章　回避制度', '第七十一条', '本行员工遇有下述情况时应实行回避：(一)本行员工之间有亲属关系（夫妻关系、直系血亲关系、三代以内旁系血亲及其配偶关系、近姻亲关系及其他可能影响履职公正的亲属关系）的，不得在同一部门、同一机构工作；（二）总行高级管理层的亲属不应有上下级直接领导关系，也不得在总行机关从事人事、纪检、审计、财务等重要岗位；（三）在办理招聘、任免、考核、调配、职称、奖惩等工作时，凡涉及本人或亲属的，必须主动申请回避，并不得以任何形式施加影响；（四）员工有义务如实向主管领导和人力资源部申报应回避的亲属情况，由人力资源部调查核实，提出回避方案，经总行党委研究决定，人力资源部负责具体落实。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十二条', '人事档案管理工作实行集中统一和分级负责的管理体制，并接受省联社的监督、指导。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十三条', '员工的人事档案由总行人力资源部按人事档案管理办法的规定实行归口管理。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十四条', '人事档案管理，必须贯彻执行党和国家有关档案保密的法规和制度，严密保管，确保人事档案的完整与安全。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十五条', '员工与本行解除劳动关系后，未就业的，其档案转至当地人才流动服务中心保管；就业的，其档案转至新就业单位。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十六条', '员工退休、出国定居、失踪、逃亡以后，其档案按规定转至当地劳动保障部门或人才流动服务中心保管。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十七条', '员工因工作调动等原因需办理档案转递时，应严格执行转递制度，严密转递手续。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十八条', '因工作需要需查（借）阅人事档案时，应严格执行查（借）阅制度。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十四章　人事档案', '第七十九条', '人力资源部负责人、人事档案管理人员及其在本行工作的直系亲属的档案，指定专人管理。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十五章　附　则', '第八十条', '本办法适用于本行全体员工。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十五章　附　则', '第八十一条', '本办法未尽事宜，国家有明确规定的，按国家有关规定执行；国家没有规定的，通过双方平等协商解决。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十五章　附　则', '第八十二条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《人力资源管理办法》', '第十五章　附　则', '第八十三条', '本办法经本行职工代表大会审议通过后执行。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第一章  总则', '第一条', '为了保证××××银行人力资源管理系统（简称“系统”）在××××银行股份有限公司（简称“本行”）的有效应用，确保系统运行和信息安全，实现信息录入的正确性、及时性和一致性，明确系统维护和使用的规范、责任，依据《××××银行人力资源管理系统暂行管理办法》（××××号），特制定本办法。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第一章  总则', '第二条', '人力资源管理系统是应用软件系统中的基础系统，实现人员管理、人才管理、档案管理、合同管理、考勤管理、培训管理等人力资源管理功能。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第一章  总则', '第三条', '人力资源管理系统使用原则（一）权责对等原则。谁使用，谁负责，按照授予的权限进行系统操作，并承担相应责任。（二）严格保密原则。系统各项人事信息、薪酬数据必须严格保密，未经授权，不得泄漏。（三）准确完整原则。系统各项信息必须准确、完整，并及时进行维护。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第一章  总则', '第四条', '本办法适用本行全体员工，本行人力资源部和科技信息部按照管理权限共同负责本办法的组织实施。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第二章  职 责', '第五条', '人力资源部作为人力资源管理系统主要用户，负责牵头抓总和总体协调，科技信息部负责技术支撑，总行各部室和各支行负责管理权限内的员工信息维护。（一）人力资源部1.负责人力资源管理系统中本行用户权限管理，机构、岗位、员工信息维护，及时准确地录入、审核责任范围内的信息，保守系统信息秘密，落实岗位制约机制，防范操作风险；2.负责人力资源管理系统在本行的推广使用，并对相关人员进行培训；3.负责及时向省联社反馈人力资源管理系统存在的问题及需求建议。（二）科技信息部为人力资源管理系统在本行正常运行提供网络与设备维护等科技支持和技术保障。（三）各部门及支行负责人1.作为本单位系统管理负责人，具体负责本办法的有效实施，保证系统的有效使用；2.负责本单位人力资源信息完整性、规范性和及时性、准确性。（四）本行员工1.在权限范围内进行自助操作，所提供的信息应真实有效，并积极支持和配合系统维护工作；2.及时向本行人力资源部反馈个人变动信息、使用人力资源管理系统中的问题和需求建议。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第二章  职 责', '第六条', '所有用户要自觉遵守系统管理相关规定，按照授予的权限，认真履行本人在系统中所承担的职责。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第三章  系统使用管理', '第七条', '在本行内网状态下登陆系统登陆网址为：××××'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第三章  系统使用管理', '第八条', '信息录入与维护（一）用户系统登陆账号为18位身份证号码，初始密码为6个0。员工应保证个人账号密码的安全性，不得将密码透露给他人。用户取得初始密码后应立即修改，若因账号、密码被他人使用造成不良影响，一切后果由用户本人承担。（二）用户忘记密码，应向系统管理员进行反馈，提出修改密码申请，经系统管理员核实后重设员工密码。（三）任何用户不得索取他人账号密码，未经授权，不得用其他用户账号进行系统操作。（四）本行人力资源部须明确两名系统管理员（A、B角），负责总行人力资源管理系统的日常管理。系统管理员必须责任心强，具有良好的职业操守，有一定的人力资源管理知识和应用软件操作知识。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第三章  系统使用管理', '第九条', '信息查阅（一）总行领导、各单位主要负责人：可以查询到下属部门每个人员的基本信息、工作简历、教育简历等基础信息，能够直接调阅本单位目前人力资源统计状态和各项静态的反映现状的人力资源指标。（二）本行人力资源部人员：总行范围内本人工作所需信息。（三）各单位员工：本单位范围内工作所需信息。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第三章  系统使用管理', '第十条', '人力资源数据的开放规定（一）原则上每季末25日至月底由系统管理员开放给用户自助登记或修改系统个人信息，保证系统中相关信息的完整准确。（二）人力资源基本信息向其他系统开放需经过本行人力资源部同意，且此信息系统须得到本行科技信息部认可。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第三章  系统使用管理', '第十一条', '在系统使用中遇到问题或需求建议，可及时向本行人力资源部门人员反映，由本行人力资源部门筛选后填写《系统问题反馈单》，提交省联社人力资源部系统管理员。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第三章  系统使用管理', '第十二条', '为提高系统的稳定性、可靠性、安全性和运行速度，如因系统维护或升级的需要而暂停服务，本行将根据省联社的通知事先进行通告。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第三章  系统使用管理', '第十三条', '人力资源信息系统使用、维护和管理（一）人力资源管理系统是人力资源数据的统计、查询来源。各项人力资源管理系统操作应合理分工，操作职责落实到人，确保各项数据及时、完整和准确。（二）系统维护人员须严格按照规范进行操作，认真、及时维护各项人力资源数据。（三）新入职员工的基本信息须在录用（报到后）7日内录入系统，人员信息中必填项须按要求填写完整、准确，非必填项信息也应尽量完整准确。（四）员工个人信息发生变动的，要及时按系统规定流程进行修改完善，保证系统中人员信息的完整准确。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第四章  系统安全管理', '第十四条', '用户须遵守保密纪律规定，并对权限范围内的人力资源数据的保密性、准确性、完整性、安全性负责，不得危害系统的安全运行。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第四章  系统安全管理', '第十五条', '系统管理员应定期检查人力资源管理系统，发现问题及时解决，无法解决的要立即向省联社系统管理员反馈，确保系统正常运行。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第四章  系统安全管理', '第十六条', '人力资源管理系统的数据应当有完善的备份制度，重要的人员数据库信息应定期进行备份。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第四章  系统安全管理', '第十七条', '人力资源部负责人作为人力资源管理系统管理的第一责任人，系统管理员作为第二责任人，须认真落实管理责任，确保人力资源管理系统各项数据和操作的准确、及时和完整。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第四章  系统安全管理', '第十八条', '违反办法规定，有下列行为之一的，应视情节轻重予以处罚：（一）违反办法有关规定，危害系统安全的；（二）擅自使用他人账号进入系统操作造成不良影响的；（三）不按系统操作手册使用系统造成不良影响的；（四）未及时录入或录入虚假人员信息的；（五）操作失误造成人员历史信息错误的；（六）擅自泄露系统中有关员工个人信息的；（七）其它违反系统管理和安全的行为。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第五章  附  则', '第十九条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《人力资源管理系统使用管理办法》', '第五章  附  则', '第二十条', '本办法自发文之日起施行。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第一章  总则', '第一条', '为建立多支柱的养老保障体系，保障和提高××××银行股份有限公司(以下简称“本行”)职工的养老保障待遇，调动职工的劳动积极性，建立薪酬的长效激励机制，增强企业的凝聚力，根据《中华人民共和国劳动法》、《企业年金试行办法》（劳社部第 20 号令）、《企业年金基金管理办法》（人社部、银监会、证监会、保监会第 11 号令）、《企业年金基金管理机构资格认定暂行办法》（劳社部第 24 号令）、《企业年金基金管理运作流程》、《企业年金基金账户管理信息系统规范》和《企业年金基金管理机构资格认定专家评审规则》（劳社部发〔2004〕32 号）、《国有金融企业年金管理办法》（财金〔2012〕159 号）等有关法律法规规定，结合本行实际，制定本办法（以下简称“本办法”）。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第一章  总则', '第二条', '本办法所称企业年金，是指在国家政策指导下，金融企业及其职工在依法参加基本养老保险的基础上，自愿建立的补充养老保险制度。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第一章  总则', '第三条', '本行企业年金制度应遵循下列原则：（一）保障与激励相结合。建立企业年金制度，在为本行退休职工提供基本生活保障的基础上，发挥企业年金的激励作用，将其作为健全薪酬福利制度和激励机制的重要方式，实现保障性与激励性的有机结合。（二）公平与效率相结合。建立企业年金制度，按照“公开、公平、公正”的原则，并根据发展战略、收入分配制度、人力资源策略、职工贡献程度等因素，探索建立多层次的企业年金保障体系，促进本行经济效益增长。（三）出资人、企业和职工利益相结合。建立企业年金制度，应当统筹兼顾出资人、企业和职工的利益，结合本行实际情况，正确处理当期与长远的关系，依法保障职工权益，切实维护出资人利益，推动本行持续健康发展，促进资产保值增值。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第二章  对象、权利与义务', '第四条', '具备下列条件的人员方可成为本行企业年金的对象：（一）列入省联社批准编制，与本行有全日制劳动合同关系，并全面履行劳动合同中所规定义务的正式在岗职工；（二）参加本行工作满一年以上；（三）已经参加基本养老保险并按时履行缴费义务。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第二章  对象、权利与义务', '第五条', '本行企业年金对象具有下列权利：（一）依法享有领取企业年金待遇的权利；（二）有对其个人账户信息的知情权；（三）法律、法规赋予的其他权利。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第二章  对象、权利与义务', '第六条', '本行企业年金对象须履行下列义务：（一）如实向总行或基金管理机构提供建立年金计划所需的个人信息资料；（二）根据企业年金方案的规定授权总行作为委托人委托基金管理机构对其个人账户中的资产进行管理；（三）法律、法规规定的其他义务。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第三章  年金筹集和计发', '第七条', '本行企业年金的组成：（一）本行按规定提取的部分，员工个人暂不缴纳；（二）企业年金基金投资运营收益。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第三章  年金筹集和计发', '第八条', '本行每年按最高不超过本行上年度职工工资总额的十二分之一的标准提取企业年金，企业和职工个人缴费合计一般不超过本企业上年度职工工资总额的1/6，其中 5％在成本中列支。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第三章  年金筹集和计发', '第九条', '每年年初，本行将筹集到的企业年金基金个人部分分配给应享受上年企业年金计划的职工，并计入个人账户。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第三章  年金筹集和计发', '第十条', '企业年金基金投资运营收益，按净收益分别计入个人企业年金账户和公共账户。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第四章  账户管理', '第十一条', '账户的开立。本行企业年金设立个人账户和公共账户。个人账户记录由本行缴纳按规定计算所得的部分；公共账户用于归集不属于个人部分和个人账户中因故被扣回的部分。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第四章  账户管理', '第十二条', '个人账户的转移（一）职工调入本行的，其年金待遇由本行从取薪之月起按本行标准重新核定并重新开户，经省联社等组织安排调出本行的，本行协助将其原个人账户的全部储存额转入调入单位的个人账户。（二）因被开除、除名、辞退、判刑、劳动教养或违反本行有关规章制度被解除劳动合同或解除劳动关系的，不享受年金待遇，其个人账户中的储存额全部并入本行企业年金公共账户。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第四章  账户管理', '第十三条', '账户的封存。如服兵役等响应国家号召新单位未建立企业年金的，职工可以向本行申请将其个人账户转入保留账户，继续投资运营，待具备条件时予以转移或达到领取条件时一次性领取。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第四章  账户管理', '第十四条', '个人账户的注销。职工领取个人账户所有余额后，个人账户自动注销。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第五章  支付方式', '第十五条', '符合下列条件之一的，可以支付企业年金个人账户全部余额：（一）符合退休条件，按月领取基本养老保险金（包括病退）的，或未达到退休年龄，因病或非因工完全丧失劳动能力的或是规定的医疗期满后不能从事原工作，也不能从事由本行安排的其他工作并终止劳动关系的；（二）出境定居的；（三）在职死亡的；（四）其他规定情形。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第五章  支付方式', '第十六条', '申请程序：由符合本办法第十五条规定的职工或其指定受益人（法定继承人）向本行经办部门提交《企业年金支付申请表》（此表由账户管理人员提供），经本行审查后向账户管理人提出申请，由账户管理人发放年金。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第五章  支付方式', '第十七条', '支付方式：一次性领取。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第五章  支付方式', '第十八条', '受益人的指定和修改。企业年金的受益人是职工本人。但当职工死亡时，可由职工在申请加入企业年金计划时指定的受益人领取其个人账户余额，如无指定受益人的，由法定继承人领取。在年金计划执行中参加人可更改指定受益人，但需向本行经办部门递交书面申请并经公证机关公证。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第十九条', '出现下列情形之一的，应修改企业年金方案：（一）某一会计年度亏损或者连续两个会计年度未满足建立企业年金条件，按照规定修改企业年金方案，适当降低企业缴费标准。（二）根据本行经营效益情况需要适时调整本行缴费比例。（三）本行半数以上职工提议进行企业年金方案修改。（四）年金方案的某些条款被劳动保障部门或司法机关判定为无效。（五）法律、法规规定要求修改的。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十条', '出现下列情形之一，应中止企业年金方案：（一）连续两个会计年度亏损或者连续三个会计年度未满足建立企业年金条件，按照规定中止企业年金方案。（二）本行半数以上职工提议中止企业年金方案。（三）年金方案被劳动保障部门或司法机关判定为无效。（四）法律、法规规定要求中止的。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十一条', '职工受到留用察看及以上纪律处分或撤销党内职务及以上党纪处分的，在处分期内中止缴费。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十二条', '出现下列情况之一，终止执行企业年金方案：（一）破产、兼并或重组。（二）半数以上职工反对继续实施企业年金方案。（三）企业年金方案被劳动保障部门或司法机关判定为无效。（四）法律、行政法规规定的其他情形。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十三条', '职工个人出现下列情况之一的，应终止缴费：（一）劳动合同期限未满就解除劳动合同的。（二）职工自愿提出申请不参加企业年金制度的。（三）员工退休、死亡等开始领取年金或其他情形。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十四条', '修改、中止企业年金方案的程序。（一）本行与职工通过集体协商；（二）提交职工代表大会讨论通过；（三）报劳动保障部门备案；（四）书面通知受托人，由受托人通知托管人、账户管理人和投资管理人。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十五条', '第二十条、第二十一条、第二十二条情形消除后，如再次满足建立企业年金制度的条件，则恢复企业年金方案。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十六条', '出现不可抗力情形。根据国家相关规定由本行决定企业年金方案是否继续实行。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十七条', '中止缴费后企业年金的处理。（一）账户继续留存运作；（二）符合领取条件或转移的，个人账户存储额可以领取或转移；（三）本行恢复缴费的可继续计入原账户；（四）个人中止缴费情况解除后可继续缴费。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十八条', '终止企业年金方案，需报劳动保障部门备案，书面通知受托人，由受托人通知托管人、账户管理人和投资管理人。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第六章  修改、中止与终止处理', '第二十九条', '终止缴费后企业年金个人账户按照相关的法律法规处理。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十条', '本行成立由相关部门和职工代表组成的企业年金管理委员会，加强对建立企业年金制度的组织指导。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十一条', '企业年金方案由本行与职工通过集体协商确定，经职工代表大会讨论通过形成企业年金方案，报劳动保障部门备案通过后实施。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十二条', '选择符合国家规定的法人受托机构作为企业年金基金的受托人。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十三条', '监督受托人按照“公开、公平、公正”的原则，择优选择经国家有关部门认定的机构管理运营企业年金，明确与企业年金基金管理运营主体的职责及运作规则。通过受托人建立对管理运营机构的动态考核评价机制，根据评估情况调整管理运营机构。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十四条', '按国家相关规定支付管理费用。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十五条', '企业年金基金受托人应当于年度结束后 60 日内，向职工大会或者职工代表大会报告企业年金管理、运营情况。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十六条', '建立信息披露制度。参加人有权对下列事项进行查询：（一）个人账户余额；（二）个人信息的基本情况；（三）本企业年金基金财产的投资收益情况；（四）法律、法规所允许的其他信息。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第七章  组织管理和监督', '第三十七条', '因订立或履行企业年金方案发生争议的，由本行与职工协商解决；协商不成的，依法提请仲裁或者诉讼。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第八章  附则', '第三十八条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《企业年金管理办法》', '第八章  附则', '第三十九条', '本办法经本行职工代表大会审议通过后实施。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第一章  总  则', '第一条', '为加强劳动管理，建立××××银行股份有限公司（以下简称“本行”）正常的工作秩序，根据国家有关法规和政策，结合本行实际情况，特制定本办法。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第一章  总  则', '第二条', '休假项目包括：公休假、法定休假、年休假、婚假、丧假、生育假、病假、事假等。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第二章  休假及其待遇', '第四条', '年休假（一）本行员工累计工作已满1年不满10年的，年休假5天；已满10年不满20年的，年休假10天；已满20年的，年休假15天。（二）员工休年休假，以不影响正常工作为前提，须服从单位统一安排。（三）有下列情形之一的，不享受当年的年休假，已休的年休假，按事假处理。1.累计工作满1年不满10年的职工，请病假累计2个月以上的;累计工作满10年不满20年的职工，请病假累计3个月以上的;累计工作满20年以上的职工，请病假累计4个月以上。2.当年请事假累计15天以上且按照规定不扣工资的；3.受党内严重警告或行政记大过（含）以上处分的（含处分期）。（四）休假期间遇有国家规定的法定节假日，休假期可以按本行节假日休息天数顺延。当年未休假的，不与下一年度的休假期合并使用。（五）员工休假期间享受工资及其它福利待遇。（六）员工已享受当年的年休假，年度内又出现上述第（三）款1.2.3.4项规定情形之一的，不享受下一年度的年休假。（七）本行员工休年休假由总行根据经营情况统筹安排，一般安排在每年的4-9月份，如果遇有突击性工作，本行有权停止审批员工的年休假。（八）试用（见习）期员工，在上半年转正的，下半年可以享受年休假；下半年转正的，从下一年度起开始享受年休假；未转正的不享受。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第二章  休假及其待遇', '第五条', '婚假凡达到国家法定的结婚年龄，依法办理结婚登记的员工，经本人提出申请，经批准，可享受婚假13天。员工休假期间享受工资及其他福利待遇。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第二章  休假及其待遇', '第六条', '生育假符合《中华人民共和国人口与计划生育法》、《××××省人口与计划生育条例》等有关规定的，女员工产假为128天（包含国家规定产假98天），其中产前可休假15天（预产期前15天的休假），休产假不能提前或推后。男员工因配偶符合《中华人民共和国人口与计划生育法》、《××××省人口与计划生育条例》等规定生育（不含流产）的，可休护理假15天。符合计划生育规定的女员工怀孕不满4个月流产，经医院证明，可休假15天；满4个月以上流产，经医院证明，可休产假42天。已婚已育女员工进行节育手术的，经医院证明，按照国家和地方政府的有关规定享受假期(结扎术7天，其他1－2天)。有不满一周岁婴儿的女员工，每天给1小时哺乳假，每多哺乳1个婴儿，每天哺乳时间增加1小时。女员工保胎、怀孕反应、葡萄胎、宫外孕等，经医院证明，可休病假。违反人口与计划生育法规、条例的，不享受女职工劳动保护等规定的权利。员工休假期间享受工资及其他福利待遇（不含上述第（六）款）。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第二章  休假及其待遇', '第七条', '丧假员工的亲属（指父母、配偶、子女、岳父母、爷爷奶奶或外公外婆）死亡时，可休丧假3天，本人料理的，可以增加1-2天；如亲属在外地死亡的，可根据路程远近，另给予路假1-2天。员工休假期间享受工资和其他福利待遇。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第二章  休假及其待遇', '第八条', '病假员工因病或因工负伤必须治疗、休养的，持有县级以上医院病假证明，可申请病假。病假的医疗期限及计算方法以国家和地方相关劳动法规为准。病假3天以内的不扣发薪酬，4－7天的每个工作日扣发100元，8天以上的每工作日扣发150元；超过1个月，第2个月起按工龄发给病假工资。工龄不满3年的，发给本人基本薪酬的80%，工龄满3年不满6年的发给本人基本薪酬的85%，工龄满6年（含）不满8年的发给本人基本薪酬的90%，满8年（含）及以上的发给本人基本薪酬的100％；病假超过6个月，从第7个月起，工龄不满3年的发给本人基本薪酬的75%，工龄满3年不满6年的发给本人基本薪酬的80%，工龄满6年及以上的，发给本人基本薪酬的85%。病假超过15天的不计为当月考核人数；超过6个月的不计为当年考核人数。病假的累计方法为每个月的1日往前推12个月，凡是在这12个月内病假累计达2个月者，即按本人的工龄发给病假工资。病愈者恢复工作后又患病累计休息15天以上者继续发给病假工资。一个年度内病假时间超过6个月的不评定员工等级，不享受岗位工资。试用（见习）期间病假超过1个月，相应延长试用（见习）期。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第二章  休假及其待遇', '第九条', '事假员工因私事必须由本人处理的，可申请事假，事假每天扣发薪酬150元。事假可抵算年休假。事假抵算年休假后，超出部分按天扣发薪酬。（四）事假累计超过15天的扣减一个月考核人数（已计算的在下月扣回）；达到6个月的不计为当年考核人数。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第三章 休假的审批程序', '第十条', '员工休假前，必须办理休假手续，经审批同意且办理好工作交接后，方可休假（审批表附后）。非突发情况，本人应在3天前提出请假申请，休年休假的应在5天前提出申请。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第三章 休假的审批程序', '第十一条', '员工休假2天以内的，由所有单位负责人批准，报人力资源部备案；5天以内的，由单位签署意见后报人力资源部审批；超过5天的，由单位签署意见、人力资源部审核后，报分管人力资源的行领导批准。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第三章 休假的审批程序', '第十二条', '员工休假结束后，必须及时向人力资源部销假，否则视同旷工。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第三章 休假的审批程序', '第十三条', '员工续假的手续和程序与请假相同，天数累计计算后按权限报批。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第四章 附　则', '第十四条', '本办法由××××银行股份有限公司人力资源部负责解释与修订。'),
(NULL, '人力资源部', '《假期管理暂行办法》', '第四章 附　则', '第十五条', '本办法自发文之日起施行。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第一条', '根据《中华人民共和国劳动合同法》、《国务院关于职工工作时间的规定》和××××省银行业协会、××××市银行业协业关于节假日营业网点休业等有关规定，特制定本办法。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第二条', '本办法旨在依法妥善处理满足社会公众节假日期间金融服务需求与维护本行员工合法权益的关系，引导更多客户使用银行自助设备，促进金融行业实现绿色“低碳”运营。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第三条', '工作日作息时间安排上午：8：00－11：30　　下午：14：00－17：30（夏季高温作息时间：上午：7：30－11：30　、下午：15：00－18：00，执行时间按××××市人民政府通知）。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第四条', '本行所有营业网点按以下要求安排休业：（一）每年1月1日至3月31日：（1）因该期间为本行业务旺季，所有网点每天均需开门营业。（2）各营业网点营业时间为：上午8:00正式对外营业，中午不关门，下午关门时间在17：00－17：30，具体根据运钞路线时间安排确定。（二）每年4月1日至12月31日：（1）法定假日：清明节、五一节、端午节、国庆节、中秋节当天，所有营业网点统一休业。（2）周六周日双休日：本行所有营业网点（不含营业部）周六正常对外营业，周日休业。（3）法定假日中逢属周日调休，按上述条款执行。    （4）营业时间为：上午8:00－11：30对外营业，中午关门休息，下午14：00对外营业，关门时间为17：00－17：30，具体根据运钞路线时间安排确定。（夏季高温作息时间：上午：7：30－11：30、下午：15：00－18：00，中午关门休息。执行时间按总行通知）。营业部双休日、节假日均对外营业，营业时间按上述时间执行。总行可视金融同业营业部休业情况适时调整营业部休业时间。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第五条', '各营业网点在不影响对外营业并保证服务质量的前提下合理安排员工轮休。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第六条', '各营业网点要充分发挥自助网点、自助机具等服务功能，大力推广手机银行、网络银行等新型终端，以方便客户办理业务；要加强对客户使用电子设备的宣传，鼓励和引导客户尽可能使用电子设备，引导社会公众使用电子设备，减少对银行柜面服务的过度依赖。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第七条', '节假日营业网点休业前要做好设备正常运行的维护工作，保持设备的正常使用，并做好休业期间钞币的补充和设备的维护，以方便客户办理业务。业务量较大、自助设备较多的营业网点要配备专人进行值班引导和辅导客户做好存取款、自助终端等自助设备的使用操作。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第八条', '各营业网点要将法定节假日和双休日休业公告提前告知客户，告示中应包含休业期间特殊情况联系电话及处理方式等要素。营业网点休业和作息时间应在其营业时间牌中明确列示，区分工作日和节假日、对公业务和对私业务营业时间。建立值班制度，制定应急预案，保持通讯畅通；总行部室在双休日、法定节假日要安排一名以上人员值班，相关部门、中心要确保营业网点业务正常开展；各营业网点在休业期间，要配备专业人员解决客户咨询和处置应急业务，避免客户投诉。农副产品收购等业务集中期以及特殊情况需调整营业时间的由总行视实际情况确定。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第十一条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《办公营业时间及节假日管理暂行办法》', '', '第十二条', '本办法自发文之日起施行。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第一章 总则', '第二条', '后备管理人员队伍建设坚持以下原则；（一）坚持德才兼备、以德为先的原则； （二）坚持群众公认、注重实绩的原则；（三）坚持公开、平等、竞争、择优的原则； （四）坚持注重发展潜力、重视培养提高的原则；（五）坚持备用结合、动态管理的原则；（六）坚持服从大局、统一调配使用的原则。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第一章 总则', '第三条', '后备管理人员队伍建设的目标是：建设一支数量充足，结构合理，素质优良，既能满足近期管理人员调整的需要，又能适应中、远期管理人员队伍建设需要的后备队伍。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第一章 总则', '第四条', '后备管理人员应当具备后备管理人员选拔方案中规定的基本条件和资格；且年轻、优秀、具有培养潜力。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第二章  职责与权限', '第五条', '人力资源部（一）负责后备管理人员管理考核方案的制定；（二）负责后备管理人员的考核与使用；'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第二章  职责与权限', '第六条', '本行各部门、支行负责后备管理人员的推荐上报。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第二章  职责与权限', '第七条', '总行党委负责后备干部人员的认定工作。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第三章  内容与要求', '第八条', '后备管理人员应具备的基本条件：（一）思想政治素质好。在思想上、政治上与党中央保持高度一致，在重大问题和政治斗争中经得起考验，具有良好的思想意识和道德品质，坚持原则，廉洁自律。（二）知识层次高。具有大专以上学历，具备与履行岗位职责相适应的专业知识和管理知识，善于学习，能够把所学知识运用到工作实践。（三）有一定的组织协调能力，发展潜力较大。善于研究解决工作中的新情况、新问题，创造性地开展工作，具有担任领导职务的发展潜力。（四）工作作风深入扎实，实绩突出。具有强烈的革命事业心和责任感，勤奋工作，注重实效，不飘浮，不做表面文章。（五）群众公认。在个别谈话、民主推荐和民主测评中得到多数群众的认可和拥护。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第三章  内容与要求', '第九条', '选拔后备管理人员，实行多种形式相结合。（一）民主推荐或组织考试等。通过民主推荐或组织笔试、面试等确定考察对象。（二）组织考察。在确定考察对象的基础上，总行对考察对象进行深入考察。（三）集体研究决定。在考察的基础上，经总行党委集体研究确定人选。     （四）进行公示。在本行范围内，对集体研究的人选进行公示。     （五）确定人选。公示无异议，即确定为正式的后备管理人员。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第三章  内容与要求', '第十条', '后备管理人员培养应当根据管理人员队伍建设的实际需要，针对后备管理人员的具体情况，制定具体的培养计划，包括培养目的、方向、方式和时间等，采取切实有效的措施，使后备管理人员得到全面锻炼、健康成长。（一）后备培养长效机制后备管理人员培养工作应本着立足长远，着手当前；全面培养，突出重点；因人制宜，形式多样的原则，逐步实现后备干部培养工作的制度化、经常化。 （二）理论培训加强对后备管理人员的培训力度，着重提高后备管理人员的政治理论修养和思想素质。同时要努力为后备管理人员提高业务素质，扩大知识面创造条件。 （三）专业培训对基础好，有发展潜力，是本行重点培养对象的，可选派到高等院校进修深造，以提高专业素质与管理水平。（四）实践锻炼实践锻炼应当根据后备管理人员的实际情况。要有重点的选派后备管理人员到基层任职或挂职。主要方法：1．实行岗位轮换；2．安排分管综合性工作或与培养方向相关的重要职务；3．分配急、难、险、重的工作任务；4．选调到本行机关、部门任职、挂职、交流任职。挂职以平挂为主，对有发展潜力，需要提高全局工作能力和宏观决策能力的干部，可采用下派高挂的形式，交任务，压担子。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第三章  内容与要求', '第十一条', '后备管理人员的考察（一）考察主要内容：政治立场、政治观点、思想品德和廉洁自律情况；政治理论、社会主义市场经济理论、现代科技、法律、历史知识和相关业务知识的水平和学习情况；掌握政策的水平、敬业精神和组织领导能力情况；工作业绩与培养目标的距离以及原有缺点、不足的改正情况以及生活、社交等方面的情况，要特别注重考察后备管理人员的思想政治素质、工作实绩、廉政情况和发展潜力。考察结果作为后备管理人员培养、使用、调整的依据。（二）应定期或不定期地对后备管理人员进行考察。　　（三）考察时必须坚持走群众路线，充分发扬民主。要根据不同岗位的职责要求，全面准确地了解考察对象的表现。并实事求是地写出反映考察对象情况的综合材料。（四）对后备管理人员除进行经常性考察外，还要坚持定期考察，全面考察其德、能、勤、绩、廉，注意考察工作实绩，考察后，要形成书面考察材料。党委成员要经常性同后备管理人员谈话，在肯定其工作成绩的同时，发现其缺点、不足时应及时进行教育谈话，帮助他们进一步明确努力方向。　第十二条  后备管理人员的任用（一）提拔任用干部，原则上应从后备干部中挑选。（二）未经理论培训的后备管理人员原则上不提拔使用。（三）总行党委应根据工作需要，对后备管理人员统一调配使用。　第十三条  后备管理人员的管理 （一）后备管理人员的管理，要同现职干部的培养、考察、使用、管理有效地结合起来。 （二）后备管理人员队伍应相对稳定，对后备管理人员实行动态管理，优进劣出，滚动管理。要结合领导班子的建设，对新发现的优秀年轻管理人员，应及时补为后备管理人员。始终保持素质优良，数量充足，结构合理。本行每年组织对后备管理人员进行全面考核和定期考察。（三）对虽已列入后备管理人员名单，但业绩差，群众不满意的应当及时淘汰；对那些年龄、专业、学历已不适合将来领导班子结构需要的后备管理人员也应及时调整。  下列情形之一者应调整出后备人才库。　1.政治思想、道德品质、廉洁自律等方面出现问题的；　2．年度考核政绩平平，得不到群众信任和拥护的；　3．思想滑坡，工作不求进取，发展潜力不大的；　4．工作失职，造成较大损失或不良影响的；　5．健康状况不佳，不宜担负繁重任务的；　6．年龄偏大的；  7．其他不适合作为后备管理人员的。（四）建立健全后备管理人员档案。后备管理人员档案由人力资源部负责管理。档案管理内容为：　　1．党委研究后备干部情况的材料；　　2．后备管理人员花名册；　　3．后备管理人员个人基本情况登记表；　　4．民主推荐或笔试等材料；　　5．考察材料；　　6．公示情况；　　7．年度考核材料；　　8．培训和奖惩材料；　　9．后备管理人员个人年度工作总结；（五）坚持实行管理人员交流、岗位轮换制度，帮助后备管理人员开阔视野，拓宽知识面；试行“一带一”的导师制，丰富后备管理人员工作经验，提高实际工作水平和领导能力。（六）对长期在机关工作的后备管理人员，将有计划地安排他们到基层单位任职或挂职，使其增长基层工作经验。（七）对长期在基层工作的后备管理人员，将有目的地选派他们到机关工作，使其增长领导机关的工作经验。（八）对长期从事某一专业工作的，可调整到综合部门或其他主要业务部门任职，使其取得全面的领导经验。（九）对于基本素质好、确有发展潜力的年轻后备管理人员，将多压担子、压重担子，把他们放到艰苦地区和重要岗位去任职锻炼，促其较快地增长实际工作经验和全面领导能力。（十）本行党委将把对后备管理人员的管理工作列入全年重点工作，每年至少研究一次后备管理人员工作。建立和健全后备管理人员工作责任制，班子成员为后备管理人员管理小组成员，主要领导亲自抓，其他成员分工负责，人力资源部负责具体相关事务工作。（十一）对已经具备条件的后备管理人员，将及时提拔使用。各支行（部门）管理人员职位出现空缺时，优先从后备管理人员中择优选拔。后备管理人员调整充实工作，一般在年度考核和定期考察结束后进行。（十二）建立防腐预警制度。对后备管理人员经常进行拒腐防变的警示教育、无私奉献的典型教育，帮助后备管理人员不断强化正确的世界观、人生观、价值观。后备管理人员思想上、工作上存在的苗头性问题，要通过诫勉谈话等形式及时提醒，防微杜渐。认真受理举报后备管理人员问题的人民来信来访，督促后备管理人员自警自律，严格要求自己。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第三章  内容与要求', '第十四条', '后备管理人员的有关材料，包括名册、档案、培养意图、使用方向等，由人力资源部内部掌握，必须严格保密。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第四章 附 则', '第十五条', '本制度未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第四章 附 则', '第十六条', '本制度由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《后备管理人员管理制度》', '第四章 附 则', '第十七条', '本制度自发文之日起施行。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第一章  总则', '第一条', '为进一步强化员工队伍建设和内控管理，有效防范和化解操作风险，完善员工管理制度，提高经营管理水平，根据《中国银监会办公厅关于落实案件防控工作有关要求的通知》〔银监办（2012）127号〕、《商业银行内部控制指引》银监发〔2014〕40号、及有关规定，结合我行实际，制定本制度。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第一章  总则', '第二条', '员工交流及岗位轮换是指按照管理权限，根据规定的条件、程序，通过跨岗位、跨机构（网点）、跨部门、跨服务对象范围等方式，定期对部门（机构）负责人实行岗位交流，对重要岗位业务人员实行岗位轮换，以达到加强内部监督，防范道德风险和操作风险的目的。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第一章  总则', '第三条', '本制度适用于××××银行股份有限公司在岗员工。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第二章  员工交流及岗位轮换对象及要求', '第四条', '员工交流及岗位轮换工作应坚持经常性、强制性和统一管理的原则，坚持亲属回避原则。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第二章  员工交流及岗位轮换对象及要求', '第五条', '开展员工交流及岗位轮换，应当处理好定期岗位轮换与提高工作效率的关系、优化人才组合与保证工作质量的关系、培养多岗位复合型人才与加强内部控制的关系。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第二章  员工交流及岗位轮换对象及要求', '第六条', '岗位交流对象及要求（一）各部门负责人在同一职位任职 3 年以上的，应实行岗位交流；满 5 年的，必须进行交流；各支行负责人在同一职位任职达 3 年的，必须进行交流。（二）信贷（授信）人员、客户经理等，在同一单位（部门）同一岗位工作满 3 年的，必须进行轮岗。（三）运营主管在同一单位同一岗位达 2 年的，必须进行轮岗。（四）会计事后监督、业务系统授权、电子联行、管库员、印、押、机、重要空白凭证管理人员、网上支付系统操作人员、资金业务交易以及其他认定为重要岗位的人员，在同一单位（部门）同一岗位工作满 3 年的，必须进行轮岗。（五）柜面业务操作人员从事同一工种满 1 年的，应安排轮岗。确因工作需要或其他特殊原因暂时无法实施的，可采取不限于强制休假、收紧授权管理和权限、加强后台或会计系统监督等一系列措施，切实加强柜员操作事中监督。（六）其他岗位轮换期限由各单位根据岗位职责、工作需要及风险防控要求合理确定。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第二章  员工交流及岗位轮换对象及要求', '第七条', '认为有必要交流或轮岗的人员，可随时进行交流和轮岗。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第二章  员工交流及岗位轮换对象及要求', '第八条', '有下列情形之一的，可不交流、轮岗或暂缓交流、轮岗：（一）距退休年龄不足五年的（属于必须交流或轮岗的对象，可区别不同情况对其工作进行调整）；（二）因健康问题不宜轮岗或交流的；（三）涉嫌违法违纪正在接受司法机关或纪检监察部门审查尚未作出结论的；（四）其他原因不适合轮岗、交流的。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第三章  员工交流及岗位轮换的组织实施', '第九条', '员工交流及岗位轮换工作由总行按照员工管理权限统一领导、整体规划、分步实施。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第三章  员工交流及岗位轮换的组织实施', '第十条', '员工岗位轮换可采取横向岗位轮换和纵向岗位调动两种方式。横向岗位轮换主要在同一单位（部门）进行，包括调整工种和交换业务权限、服务对象、服务区域；纵向岗位调动主要在全辖进行。具体方式为：（一）总行与支行、支行与支行、本部门或不同部门之间的岗位轮换；（二）本专业内或不同专业之间的岗位轮换；（三）对所经办账户或服务的客户范围进行重新划分；（四）其他方式。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第三章  员工交流及岗位轮换的组织实施', '第十一条', '员工交流及岗位轮换按照以下程序进行：（一）人力资源部每年年初根据单位（部门）负责人及员工工作年限、年度考核情况和工作需要，拟定员工交流及岗位轮换初步方案。（二）各单位根据管理权限研究审定员工交流及岗位轮换方案。（三）人力资源部按照有关规定及程序，以书面函告的方式宣布有关人员交流及岗位轮换的决定并根据方案组织实施。（四）轮岗、交流人员必须在相关部门派员监交情况下进行书面工作交接，并在规定的工作日内完成交接，移交情况及有关资料存档备查。部室总经理、副总经理、支行行长交流轮岗由审计部门监交和离任审计，形成书面审计结论；团队经理交流轮岗由所在单位负责检查、监交；信贷人员、客户经理交流轮岗由信贷管理部、支行行长负责检查、监交，并对其负责；运营主管交流轮岗由运营管理部负责检查、监交；柜员岗位轮换由支行行长和运营主管负责检查、监交，并对其负责。检查中若发现违规违纪违法问题，按照有关规定做出相应处理。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第四章  纪律要求和管理监督', '第十二条', '总行负责对员工交流及轮岗工作进行指导、检查和监督。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第四章  纪律要求和管理监督', '第十三条', '各单位（部门）应规范交流及岗位轮换过程中工作交接、离岗审计（检查）及相关档案资料的保管工作。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第四章  纪律要求和管理监督', '第十四条', '属于交流、轮岗范围的人员，不得拒绝调动或有意拖延时间。无正当理由拒不执行交流、轮岗决定的，视情节按照有关规定给予相应处理。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第四章  纪律要求和管理监督', '第十五条', '对现任岗位人员交流、轮岗过程中发现前任岗位人员有问题隐匿不报的，按有关规定从严处理。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第四章  纪律要求和管理监督', '第十六条', '审计人员对交流、轮岗人员工作进行离岗审计（业务检查）时不按操作程序、走过场或不客观、不公正反映问题的，按有关规定给予处理。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第四章  纪律要求和管理监督', '第十七条', '人力资源部、纪检监察部、审计部建立工作台账，密切关注并掌握员工工作情况和思想动态，通过定期分析，查找薄弱环节，研究工作对策。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第四章  纪律要求和管理监督', '第十八条', '对执行制度不力，导致重要岗位人员发生严重违规违纪违法问题或引发案件的，按照中国银行业监督管理委员会提出的“一案四问”原则，追究相关责任人的责任。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第五章  附则', '第十九条', '主管部门、监管部门对员工交流和岗位轮换有新的规定的，按主管部门、监管部门规定执行。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第五章  附则', '第二十条', '本制度由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《员工交流及岗位轮换制度》', '第五章  附则', '第二十一条', '本制度自发文之日起施行。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '', '第一条', '为建立健全内部监督机制，加强相互制约，相互监督，防范和减少案件发生，强化内部管理，结合××××银行股份有限公司(以下简称“本行”）实际情况，特制定本制度。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '', '第二条', '本制度适用于本行对所有在岗人员亲属的管理。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '', '第三条', '本制度所指应回避的亲属关系包括：夫妻关系、直系血亲关系、三代以内旁系血亲关系、近姻亲关系。直系血亲关系，即本人、父母、子女。三代以内旁系血亲及其配偶关系，包括兄弟姐妹、堂兄弟姐妹、表兄弟姐妹、叔、伯、姑、舅、姨、侄子女、甥子女以及他们的配偶。近姻亲关系。包括配偶的父母、配偶的兄弟姐妹。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第二章  职责与权限', '第四条', '本行人力资源部负责对亲属关系人员进行登记；负责全行亲属回避、员工的统一调配工作；负责全行亲属回避工作的监督与实施。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第二章  职责与权限', '第五条', '本行纪检监察部、审计部负责对全行亲属回避工作的审计监督。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第二章  职责与权限', '第六条', '总行各部室、各支行负责亲属回避工作的执行与上报。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第三章 亲属回避内容', '第七条', '员工有义务向主管领导和人力资源部申报应回避的亲属情况并提出调配要求。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第三章 亲属回避内容', '第八条', '任职回避（一）本行现有在岗员工之间有亲属连带关系的，不得在同一部门、同一支行工作，不得在一个业务系统内不同的岗位任职，不得从事不相容的岗位。（二）因特殊原因回避有困难的，有亲属关系的员工不得在同一机构担任双方直接隶属于同一负责人的职位或直接上下级领导关系的职位，也不得在其中一方担任领导职位的机构从事监察、审计、人力资源管理、财务工作；关系人不得在同一单位担任有相互制约关系的部门的领导工作。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第三章 亲属回避内容', '第九条', '履职回避员工在工作中，如遇有涉及本人或者涉及与本人有本规定所列亲属关系人员的利害关系时必须回避。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第三章 亲属回避内容', '第十条', '对同一单位已形成亲属关系的或因联姻等原因新形成的亲属关系，应及时调整解决，有关人员必须服从统一调配。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第三章 亲属回避内容', '第十一条', '在岗的外聘人员须执行亲属回避制度的。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第四章  实施流程及纪律', '第十二条', '回避申请（一）本行员工自报亲属关系，填写《员工亲属关系登记表》，上报人力资源部，由人力资源部调查核实后进行汇总。（二）本行人力资源部根据已汇总全行亲属关系，按照亲属回避的要求和内容，拟订回避方案，经总行党委会集体研究决定，人力资源部负责具体落实。（三）亲属关系根据回避方案，提出回避申请，本行人力资源部负责回避人员工作的协调与调配。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第四章  实施流程及纪律', '第十三条', '工作交接（一）回避对象在回避前要按规定办理交接手续，如交接时发现涉及违章违纪贷款责任，应暂停回避，待收回贷款后，再行回避。回避对象要在规定时间内到岗，对不服从分配的，视情节给予必要的处分。（二）亲属回避工作要公开、公正、公平地进行，各级领导和员工不得以任何形式阻碍亲属回避工作，也不得利用职权和工作关系授意、指使、暗示和托请他人进行干预。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第四章  实施流程及纪律', '第十四条', '离岗检查或稽核（一）部室正、副总经理（正、副主任）、支行行长离岗由审计部组织离任审计；运营主管交流由总行运营管理部门负责监交；客户经理离岗由支行行长负责检查、监交；各单位内勤岗位离岗由支行行长和运营主管负责监交，并对其负责。有问题的，根据有关规定及时予以处理。对有疑点问题的人员要立即进行离岗检查或稽核。（二）各级领导及员工要对此项工作进行监督，发现问题及时向纪检监察部门举报'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第四章  实施流程及纪律', '第十五条', '资料归档本行人力资源部负责保管亲属回避的有关资料。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第四章  实施流程及纪律', '第十六条', '回避纪律（一）总行领导、各支行、部室负责人要做好亲属回避员工的思想工作，不得以任何借口干预、阻挠回避工作。 （二）全行员工要认真落实回避制度。如不认真执行亲属回避制度，导致违规、违纪、违法案件的发生，要追究直接责任人和相关责任人的责任，从严查处，绝不姑息迁就。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第五章 附则', '第十七条', '本制度依据国家法律、法规和有关规定制定，由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《员工亲属回避管理制度》', '第五章 附则', '第十八条', '本制度自发文之日起施行。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第一条', '员工内部退养的原则实施员工内部退养，是要让业务素质、身体素质不适应工作要求的人员让出岗位，把综合素质好、年轻的员工充实到员工队伍，以优化员工队伍结构，提升工作和服务质效，提升本行形象。员工内部退养实行本人申请、组织审批的原则。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第二条', '内部退养的对象长期病假、年龄偏大、业务素质等因素造成的不适应岗位要求的人员，以及其他原因造成的不适应、不适宜在岗工作的人员。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第三条', '内部退养的条件员工男满 52 周岁，女满 47 周岁，且工龄满 20 年，或男工龄满30 年，女工龄满 25 年，确因身体原因或能力因素不适应岗位要求，又不适宜安排其他工作的。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第四条', '内部退养的待遇（一）退养人员在内退期间改发退养生活费。退养生活费原则上按员工的保障工资和津贴之和计发。（二）退养人员的养老保险金、失业保险金、医疗保险金、住房公积金等，以退养生活费为基数，退养员工个人和总行按各自承担的比例缴纳。（三）退养期间工龄连续计算。（四）退养员工达到或符合国家规定的退休、退职条件的，总行为其办理正式退休、退职手续。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第五条', '审批及管理（一）员工内部退养必须由本人提出申请，所在单位审查，总行审批。（二）内部退养人员在办理内部退养手续时，应填写《内部退养审批表》，并存入本人档案。（三）内退员工按在册人员另册统计，不列入所在单位业务和费用考核的人员基数。（四）对履行劳动合同期间被批准内部退养的员工，其劳动合同中不适应的条款应进行变更，并签订《内部退养协议》。（五）内部退养员工被司法机关依法追究刑事责任的，或有严重违纪违规行为的，按在职员工有关规定处理。（六）内部退养员工由退养前所在单位管理。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第六条', '其他有其他特殊原因需内部退养的，由总行研究决定。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第七条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《员工内部退养管理办法》', '', '第八条', '本办法经职工代表大会通过后施行。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第一章  总则', '第一条', '为规范员工培训管理，提高培训质量，打造适应××××银行股份有限公司（以下简称“本行”）发展的员工队伍，依据《中华人民共和国劳动法》等相关法律、法规，针对形势发展要求，结合本行实际，特制定本办法。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第一章  总则', '第二条', '本办法适用于本行的各类培训。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第一章  总则', '第三条', '本办法所称的岗前培训，是指由人力资源部组织的对新入职员工进行的集中培训。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第一章  总则', '第四条', '本办法所称的岗位培训，是指员工在工作期间，由各业务主管部门根据业务需要和员工专业技能水平组织实施的有关培训。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第一章  总则', '第五条', '本办法所称的持续发展型培训，是对部分高级管理人员、技术人员、业务骨干人员, 以培养战略性人才为目的的培训。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第二章  职责和权限', '第六条', '人力资源部负责制定中远期培训规划；汇总总行各部室的培训需求，制定年度培训计划；负责全行培训工作的组织、协调、实施；建立和保存员工培训档案。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第二章  职责和权限', '第七条', '总行各部、室负责根据各支行、部的培训需求，拟定年度专业培训计划；负责本专业、本部室人员的培训计划落实，进行相关业务指导，并组织考试验收。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第二章  职责和权限', '第八条', '各支行、部负责征求员工意见，提出各项业务的培训需求；依据总行培训计划，制定本单位的年度培训计划；负责人员组织，配合总行做好培训管理工作。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第三章  培训方式、内容', '第九条', '培训的方式。（一）采取灵活多样的形式，组织员工有针对性地进行政治理论、政策法规学习，形成正确的世界观、人生观、价值观。（二）根据省联社有关规定和安排，组织相应的人员参加各类进修、培训学习。（三）有计划地选派员工到院校、培训机构进修或培训，并组织有关人员到先进单位考察学习等。（四）积极鼓励和支持在职人员接受继续教育，培养高层次的经营管理人才。（五）根据经营管理的需要，组织员工进行监管政策、行业管理、本行规章制度、产品营销知识和能力的培训。（六）组织开展业务技能培训，提升员工岗位操作能力。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第三章  培训方式、内容', '第十条', '培训形式。（一）内部培训。主要采取分散自学、集中办班、专题讲座、理论研讨、经验交流、以考代训、视频会议等方式。（二）外出培训。主要采取交流考察、委托办班、高等院校进修、专业机构培训等方式。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第三章  培训方式、内容', '第十一条', '培训内容。（一）岗前培训主要内容1.讲解本行发展历程、经营范围、业务品种、企业文化和发展规划；2.讲解本行组织机构设置，介绍工作环境和工作条件；3.讲解本行各项业务流程，组织学习各项规章制度、合规风险常识；4.讲解本行员工行为规范，提出道德、情操和礼仪要求；5.组织新员工撰写岗前培训心得体会；6.参加拓展训练或其他形式的活动，培养新员工的团队意识。（二）岗位培训主要内容1.业务知识和业务操作技能培训；2.思想素质、职业道德、行为管理培训；3.文化知识培训；4.产品营销能力培训；5.内控管理、业务流程培训。（三）持续发展型培训主要内容1.前沿理论、理念；2.先进管理模式；3.业务营销与品牌开发；4.先进技术。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第三章  培训方式、内容', '第十二条', '参训要求。（一）岗前培训。新招聘的高等院校毕业生及其他新入行员工必须参加岗前培训。（二）岗位培训。各岗位人员每年必须参加总行组织的专业知识、业务技能培训。（三）持续发展型培训。1.本行高级管理人员、后备干部、管理人员、业务骨干；2.除高级管理人员外，其他人员须签订服务期协议，违反服务期约定的，应当按照约定向本行支付违约金。违约金的数额不得超过本行提供的培训费用，且不得超过服务期尚未履行部分所应分摊的培训费用，具体额度以协议约定为准。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十三条', '计划制定。（一）各支行、部、总行部室根据当年员工培训情况，在广泛征求意见的基础上，提出次年的培训需求，交人力资源部。（二）人力资源部汇总全行培训需求后，分类反馈给总行各部室。（三）总行各部室在充分分析、评价的基础上，根据业务发展、内控管理的需要，提出本部室年度培训需求，于每年年末填写《专业培训计划表》交人力资源部。（四）人力资源部结合全行的实际情况及本年度的人力资源规划，编制年度员工教育培训计划，经批准后，由相关部门组织实施。（五）各支行、部、总行部室要结合实际，有针对性的制定本单位的年度培训计划，并抓好落实。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十三条', '培训流程。（一）内部培训流程1.制定方案。各培训负责部门根据年初培训计划和业务发展、内控管理的需要，制定培训方案，确定培训内容、培训形式和方法，明确参训人员，落实授课人员。授课人员原则上由各支行、部室的管理人员和业务骨干担任。对确需外聘师资时，由主办部门负责落实，人力资源部协助。2.下发通知。每次培训前下发培训通知，明确培训的目的、时间、地点、人员、内容和要求。3.参加培训。各单位接到通知后，根据要求组织人员参加培训，配合总行做好培训的管理。4.组织考试。各培训负责部门在培训结束后，根据培训的目的和要求，组织考试，检验培训效果。（二）外出培训流程1.团体性外出学习（考察）（1）制定方案。根据年度培训计划的要求，由各责任部门拟订培训内容、机构、地点和参训人员范围。（2）审批备案。将培训方案经领导审批后，送人力资源部备案。（3）工作交接。外出培训超过一周以上的，要做好工作移交工作，可根据需要进行稽核审计。（4）参加培训。参训人员要严格遵守培训纪律，认真听课并做好学习笔记。（5）认真总结。培训结束后参培人员每人须提交学习总结。2.业务骨干外出学习（1）报告。由相关部门将培训内容、机构、地点、参训人员范围和要求，向分管领导或主要领导报告。（2）审批。审批后送人力资源部备案。（3）工作交接。培训超过一周以上的，要做好工作移交工作。（4）提交学习感想和收获，对学习成果组织交流和分享。3.持续发展型学习（1）制定方案。根据年度培训计划，由人力资源部拟订培训内容、机构、地点、参训人员范围。（2）个人申请或组织推荐。符合条件的提出人员申请或组织对符合条件的人员进行推荐。（3）审核审批。人力资源部审核后，按权限进行审批。（4）签订协议。申请人交纳学习保证金，与本行签订个人外出培训协议。（5）工作交接。移交工作，接受稽核审计。如出境学习，按规定办理审批、出入境手续。（6）参加培训。参训人员要严格遵守培训纪律，如出国学习，要遵守当地的法律法规，不得做有损我行声誉的行为。（7）成效评估。根据学习内容，针对我行的实际情况提出改良方案，由总行领导班子成员和相关专业人员进行评估，评估结果即为成绩。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十四条', '培训考核。（一）正常培训必须考核，由组织培训的部门负责，属特殊原因不需进行考试或考核，需经领导同意。（二）主办部门填制《培训考试考核登记表》，于培训结束后5 个工作日内报人力资源部。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十五条', '培训效果的评估。（一）培训结束后，人力资源部组织向参培人员发放《培训效果调查表》，了解参训学员的培训效果。（二）人力资源部根据《培训效果调查表》、《培训班考试考核登记表》及培训过程中检查所反映的信息，对培训的有效性作出评估，对培训过程中存在的不足，分析产生的影响因素，作为下年度持续改进要素。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十六条', '培训档案。人力资源部建立《员工培训经历档案》，内容包括：（一）《培训班考试考核登记表》；（二）《外部培训登记表》；（三）学历、学位教育结业材料。1.学前备案在报名参加入学考试前，员工填写《员工在职参加学历、学位教育审核备案表》，报人力资源部备案。备案材料包括招生简章及其他相关材料，由人力资源部审查、登记并验证。2.学后审查员工学习结束后，将学校寄发的全部学籍档案材料，包括报考登记表、入学登记表、学籍成绩册、毕业登记表、毕业证书、学位证书及其他有关材料原件移交人力资源部审验。3.记录经审核、确认，相关教育培训经历记入培训档案，同时录入“人力资源信息管理系统”。4.控制要求学前未经备案的，不予审核认定。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十七条', '培训费用。（一）培训费用按财务制度列支。（二）聘请授课人员的费用按照协议约定支付。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十八条', '培训纪律。（一）参加培训人员不得旷课、迟到、早退。旷课按旷工处理，迟到、早退半小时内者处以罚款 80 元，半小时以上者处以罚款 100 元。（二）遵守课堂纪律，不准大声喧哗，不准打瞌睡，不准随意走动，不得看与上课内容无关的书籍，做与上课无关的事情，关闭通信工具。严重违反课堂纪律，不服从管理的给予全行通报批评，并视情节处以不低于 500 元罚款。（三）有特殊情况不能参加培训，须在培训前一天请假，并有请假条，由单位负责人签字，报人力资源部审批。非本人请假，无单位审批者作旷工处理。（四）参加培训人员由本人签到，明知他人未到，代他人签到欺骗考勤人员的处以 100 元罚款。（五）团体性外出培训要严格落实考勤制度，如有违反上述规定者，给予双倍处罚，情节严重者给予纪律处分。（六）个人外出学习，有下列行为之一者，按个人外出学习协议，学习费用由其个人承担，并追究违约责任，情节严重的，按相关规定处理：1.不按规定时间报到，不及时返行的；2.私自改变学习科目或培训地点、机构的；3.利用学习时间进行旅游的；4.从事个人劳务活动的；5.严重违反培训机构规章制度，情节恶劣，给我行声誉造成严重影响的；6.违反培训机构所在地法律制度的。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第四章  培训管理流程', '第十九条', '资料归档。有关培训的记录要分类归档，妥善保管。参训人员的考试、考核成绩记入员工培训档案，作为上岗资格的基本条件、定职定级和晋职晋级的主要参考依据，个人年度综合考评、评选先进的内容之一。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十条', '人力资源部是内部培训讲师（以下简称“ 内训师”）的归口管理部门，负责内训师的评聘及相关管理工作。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十一条', '内训师工作职责。（一）根据人力资源部的安排，展开相关内训课程；（二）负责参与培训效果工作总结，对培训方法、课程内容等提出改进建议，协助人力资源部完善培训体系；（三）负责培训学员的考勤和考核；（四）负责编写或提供教材教案；（五）负责制作培训学员测试试卷及考后阅卷工作。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十二条', '内训师由所在领域操作技能和业务水平突出者担任，除了可以获得一定的授课薪酬之外，还可以优先参加相关培训。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十三条', '内训师评选条件。（一）具有良好的工作态度和敬业精神，积极配合培训工作的开展；（二）在某一岗位、某一领域、某一业务上具有较高的理论知识和实际工作经验或技能；（三）形象良好，有较强的语言表达能力；（四）具备编写讲义、教材、测试题的能力。能够列出授课内容的基础知识、基本技能，明确重点、难点；（五）能够熟练运用办公自动化软件制作课件。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十四条', '内训师评聘程序如下：（一）单位推荐或个人自荐—－主管部门审核――人力资源部审批。（二）人力资源部会同相关部门进行资格能力评审，对评审合格的讲师，由人力资源部给予其资格确认。（三）人力资源部组织对授课效果进行抽查，对连续二次抽查得分低于 60 分的，不予聘用。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十五条', '本行鼓励广大员工积极参与内训师评定，内训师业绩作为其工作绩效考核的参考依据之一。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十六条', '年终考核。人力资源部每年年末对内训师进行一次综合评定，考核优秀的给予一定物质奖励和精神奖励；对考核不合格或者受到学员两次以上重大投诉的，取消资格。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第五章  内部培训讲师', '第二十七条', '内训师的报酬按 80 元－100 元/小时计算，按测评结果确定，由人力资源部负责报酬的申请与支付。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第六章  附则', '第二十八条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《员工培训管理办法》', '第六章  附则', '第二十九条', '本办法自发文之日起施行。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第一章  总则', '第一条', '为进一步强化员工管理，加强内部控制，防范和控制风险，根据《商业银行内部控制指引》（银监发〔2014〕40号）、《商业银行内部控制评价试行办法》（2004年第9号）、《中华人民共和国商业银行法》（主席令第34号）、《商业银行操作风险管理指引》银监发[2007]42号及有关规定，结合××××银行股份有限公司（以下简称“本行”）实际，制定本制度。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第一章  总则', '第二条', '强制休假，是指在事前不告知相关员工及所在单位的情况下，由上级管理部门在某一时间委派人员临时接管或代理其工作，对相关员工实施强制带薪休假。休假期间由相关部门对其经办的业务实施审计或检查。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第一章  总则', '第三条', '本制度适用于××××银行股份有限公司在岗员工。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第二章  强制休假原则', '第四条', '强制休假应坚持以下原则：（一）统一管理、分步实施。强制休假由总行统一安排，在不影响正常业务经营活动的基础上，有计划、分步骤组织实施。根据风险控制需要和实际情况，确保每年强制休假人员达到本单位在岗员工的一定比例。（二）统筹兼顾、突出重点。实行强制休假的目的是促进员工身心健康，提高工作效率，及时发现和有效预防各类案件。总行将根据岗位特点、任职期限和风险排查需要，科学制定强制休假计划，在兼顾全局的同时合理确定当年强制休假的重点。（三）规范操作、注重实效。强制休假是防范风险、加强员工监督管理的重要手段，在组织实施中必须规范操作程序、严格保密，充分体现强制休假的严肃性，确保离岗审计效果。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第三章  强制休假程序及基本要求', '第五条', '强制休假基本操作程序为：（一）制定方案。每年初，人力资源部在广泛听取合规、风险管理、监察、审计等相关部门意见的基础上，拟定本年度强制休假方案。（二）研究审定。按照管理权限审定强制休假方案，确定具体强制休假对象。（三）下达通知。人力资源部向强制休假人员、代理人员及其所在单位下达《强制休假通知书》（附件 1），并同时送达监察、审计部门。（四）离岗交接。办理强制休假人员离岗交接手续时，必须由强制休假人员所在单位负责人和监察、审计部门指定工作人员共同监交。必要时监察、审计部门可对强制休假人员的经办业务或钱账实行先封存后交接。休假人员岗位业务由所在单位指定有关人员代理，本单位确因人员紧张无人代理的，由总行抽调人员代理。（五）审计与业务检查。强制休假必须坚持先移交后离岗，先审计后上岗的原则。强制休假期间，审计部门应及时派出审计稽核人员按有关规定对强制休假人员经办的工作进行责任审计或业务检查。（六）反馈结果。审计或业务检查结束后，应向休假人员出具审计或检查事实确认书，通报审计情况，由休假人员签字确认，形成《离岗审计（业务检查）结论书》（附件 2），并将审计结果报相关部门。监察、审计部门应向强制休假人员及其所在单位针对存在问题提出整改要求。经查实有违规违纪违法行为的，按有关规定处理。（七）通知返岗。离岗审计未发现违规违纪违法问题的，休假期满由人力资源部通知强制休假人员办理销假手续。同时按规定做好返岗工作交接和监交工作。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第三章  强制休假程序及基本要求', '第六条', '除正常确定强制休假对象外，员工有下列情形之一的，必须及时采取强制休假措施：（一）经检查或举报发现业务操作中存在重大违规隐患的；（二）思想情绪波动较大，神情恍惚，工作心不在焉，行为异常的；（三）家庭、婚姻遭受重大变故或直系亲属投资经商出现巨额亏损、倒闭破产等情形；（四）相关部门（单位）认为其他需要强制休假的情况。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第三章  强制休假程序及基本要求', '第七条', '存在第六条所述情形时采取的强制休假，由发起单位（部门）及时向人力资源部提出书面建议，人力资源部会同相关部门组织实施。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第三章  强制休假程序及基本要求', '第八条', '强制休假时间根据岗位特点和离岗审计需要视实际情况而定，原则上一次不少于连续 5 个工作日。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第三章  强制休假程序及基本要求', '第九条', '强制休假时间冲抵休假人员当年的年休假，年休假不足冲抵的，冲抵其他公休假时间。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十条', '对强制休假人员审计或业务检查过程中如发现有重大问题，审计人员应及时向上级部门汇报并提交审计报告，报告内容应包括：审计（检查）中发现的问题，审计对象应负的责任，处理意见及建议等。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十一条', '员工不得以任何理由拒绝执行强制休假安排。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十二条', '员工在被要求强制性休假时，必须在休假通知书规定的时间内办理全部移交手续，不得无故拖延时间。对违反强制休假决定的当事人或责任人，按照有关规定给予处理。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十三条', '员工在强制休假期间如需外出（指离开××××市），应向所在单位、人力资源部报备并获许后方可出行，同时确保通讯联系畅通。强制休假人员在休假期内不得回单位代班。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十四条', '员工强制休假期间，如遇特殊情况需休假人员回单位说明情况或办理有关事宜，需经监察、审计部门批准，由所在单位或部门通知。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十五条', '对强制休假人员离岗审计或业务检查必须遵循真实、准确、严谨的原则，从实际出发，不得推断臆测、弄虚作假，更不得借机打击报复、隐瞒欺报。一经发现，应对有关人员做出严肃处理。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十六条', '强制休假所安排的岗位代理人员，必须按照规定和代理岗位的操作流程进行工作，认真履行所代岗位职责，对代理期间所发生的属职责范围内的问题承担相应责任。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十七条', '代理人员在代理过程中对发现的重大问题应及时按规定向上级部门报告，隐瞒不报者视情节追究相应责任。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十八条', '人力资源部会同监察、审计部门负责员工强制休假的组织实施，对强制休假、离岗审计的落实情况和效果进行检查和评价。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第四章  强制休假纪律及责任追究', '第十九条', '对执行本制度不力，导致重要岗位人员发生严重违规违纪违法问题或引发案件的，按照中国银行业监督管理委员会“一案四问责”原则，追究相关责任人的责任。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第五章  附则', '第二十条', '本制度由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《员工强制休假制度》', '第五章  附则', '第二十一条', '本制度自发文之日起施行。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第一条', '基本目标紧紧抓住人力资源管理、培养、配置三大环节，结合本行业务实际需求和未来发展需要，制定明确的招聘原则、详细的招聘流程、完善的后续管理，进一步健全培训、考核、奖惩机制，招聘（含引进）适应现代金融企业需求的高层次复合型人才、核心人才以及符合现实业务需求的急需人才，建立一支结构合理、素质优良、运转高效的员工队伍，为建立现代金融企业，提高本行市场竞争力与可持续发展能力提供人才支撑。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第二条', '基本原则（一）科学性与人文性相结合原则。遵循员工招聘内在规律，在充分调研的基础上，把握内外部人才市场供求情况，提高招聘工作针对性和时效性。既要立足于实际需求，注重招聘对象的社会经历经验，定向招聘大学生村官、事业单位工作人员以及具有专长的特殊人才，满足业务发展的现实需求，又要着眼于未来发展需要，注重学历层次、学习能力，通过全省信用社系统统招统考，招聘应届本科生、研究生，为员工队伍注入新鲜血液，同时还要建立健全教育培训体系，满足员工合理的物质需要和精神追求，尊重其个性，激发其主动性，加强教育与培训，为员工成长成才提供良好的环境和条件。（二）现实性与先进性相结合原则。员工招聘要立足人力资源数量、质量和结构的实际，既要适时组织阶段性招聘，满足当前业务运作需要，又要以改革的思路、创新的精神、开放的眼光、务实的态度，以现代人力资源管理理论为依据，科学规划长远性招聘，引进符合现代金融企业经营管理需要的高层次复合型人才和能够担当改革发展重任的核心人才，满足未来经营管理需求，进一步加强人力资源梯队建设，使本行员工招聘工作具有先进性和前瞻性。（三）公开、公平、公正、择优录用原则。员工招聘工作涉及面广，社会影响大，要严明招聘纪律，过程公开，标准公平，结果公正，录用综合素质较高、满足岗位需要的员工。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第三条', '统筹规划，提高员工招聘工作的科学化水平按照“控制总量、调整结构、规范管理、提高素质”的要求，统筹规划员工招聘工作，进一步创新员工招聘方式、方法，不断提高员工招聘工作科学化水平。（一）科学分析招聘需求。在对本行人力资源现状进行全面分析的基础上，结合未来组织结构设计、业务发展战略、员工分流情况、人力资源市场环境等因素，在定岗定员、优化资源配置前提下，就招聘人员的数量、质量、岗位、资格条件、方式方法等，广泛调研，科学分析，提高招聘工作的科学性、针对性、有效性。（二）合理制定招聘计划。既需“外引”，更要“内培”，首先，注重挖掘现有人力资源潜力，节约人力资源成本，提高人力资源利用效率；其次，注重结合实际需求。本着“缺什么招什么”的原则，合理控制进人节奏，制定员工招聘计划，使招聘计划更具实效性；第三，注重长远规划，不仅要解决员工数量不足、业务骨干紧缺等短期性需求问题，还要围绕优化人力资源结构的目标，合理规划好长远性员工招聘工作，不断优化员工队伍结构，加强人才储备。（三）按需设置招聘条件。招聘条件设置要具有先进性、前瞻性和科学性。设置的招聘条件既要能满足本行现在和未来一段时期经营管理对员工素质的需求，也要与业务运行、管理要求、薪酬水平、企业文化和岗位设置等相匹配，同时还要考虑招聘岗位对知识技能的要求和对员工未来的培养方向。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第四条', '规范运作，严格员工招聘工作的程序和要求员工招聘工作政策性强，要求高，必须执行省联社有关招聘工作的规章制度，规范招聘工作程序和要求。（一）严格执行招聘计划。每年第三季度，本行在合理规划人力资源的基础上，向省联社申报下年度员工招聘计划（包括普通高校毕业生、引进专业人才等），经省联社核定后，坚持总量从严控制的原则，实施招聘计划。（二）科学制定招聘方案。在省联社批准的招聘计划内，制定相应的招聘方案。招聘方案包括招聘岗位、对象、条件、基本程序和方法等内容。招聘方案经本行领导班子集体研究、报省联社备案后，发布招聘信息，实施招聘程序。（三）广泛开展招聘宣传。根据不同的招聘对象，采取不同的招聘宣传方式，提高宣传的针对性和有效性。一般情况下通过报刊、网络、电视等新闻媒体发布和对外显示屏进行公告；招聘应届大学毕业生可通过举办专场招聘会、校园招聘会等方式发布招聘信息；人才引进和定向招聘宣传可通过专业网络媒体、中高层人才市场、猎头公司等方式发布招聘信息。不断拓宽招聘宣传的渠道，提高招聘宣传的深度与广度，扩大招聘宣传的社会覆盖面。（四）认真审核应聘资格。要对应聘人员进行资格审查，重点审核年龄、学历、工作经验、奖惩情况等，必要时可约请应聘人员面谈以全面掌握其情况。积极创造条件，引进智力、性格等相关测试工具，加强对应聘人员的情商、智商、能力等方面的测试，初步审核筛选和确定考试人员。 （五）精心组织招聘考试。员工招聘考试一般为笔试和面试两种形式。普通高校毕业生由省联社统一组织招聘笔试，笔试人数应达到省联社规定的招聘职位开考比例。根据省联社确定的面试范围，对面试入围应聘人员的资格进行验证，及时有效地组织面试工作。对于人才引进和定向招聘等个性化招聘考试，原则上聘请专业机构、专业人员，组织招考工作，确保招聘工作效果最大化。（六）认真组织招聘考察。本行根据笔试和面试综合成绩，从高到低依次确定录用人选，并考察其背景、在校表现、从业记录等，从源头上防范员工道德风险，严把“准入关”。对拟录用的应届普通高校毕业生，应尽可能安排岗前实习，了解其岗位适应能力和工作态度。对引进的专业人才，要防止出现“重能力轻品德”的考察倾向。（七）规范办理招聘手续。本行按照“规范、客观、完整、真实、准确”的要求，及时将拟录用人员名单及相关资料按规定格式和要求报省联社审核备案。经审核同意后，及时规范办理新员工入职手续。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第五条', '高度重视，加强员工招聘工作的后续管理招聘仅是员工管理工作的开始，要重视和加强员工素质的提升、队伍结构的优化等后续管理工作。（一）依法签订劳动合同。新聘员工要按照《中华人民共和国劳动法》、《中华人民共和国劳动合同法》、《中华人民共和国社会保险法》等法律法规签订劳动合同，办理相关保险，明确试用期限，并告知劳动纪律和注意事项。首次签订劳动合同期限一般不超过5年，并载明试用期限。（二）开展适岗培训。对无银行从业经历的员工，要安排足够的时间进行岗前培训，培训合格后方可安排上岗，原则上安排到一线业务岗位工作。试用期结束后，要对新录用人员进行考核，达不到相关要求的按合同规定解除劳动关系。（三）科学规划员工职业生涯。建立和完善人才培养、开发机制和职位晋升通道，加强员工职业生涯规划管理，关心员工成长，引导员工进步。（四）完善人力资源管理体系。进一步深化劳动用工制度改革，科学定岗定员，建立健全持证上岗、竞争上岗和新员工培养制度，完善绩效考核办法，逐步建立与员工招聘相配套的人力资源管理体系。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第六条', '严肃纪律，加强员工招聘工作的组织领导（一）坚持重大事项集体研究。本行分管负责人和人力资源部要按照主要负责人的要求，稳妥实施招聘工作。对招聘计划、招聘方案、拟录用人选确定以及招聘工作中遇到的其他重大问题，应集体研究决定，重要情况应及时请示报告，并做好有关资料的收集归档。要密切关注网络、媒体等舆论动态，加强对招聘工作突发性事件的舆情研判，制定预警方案，畅通信息渠道，稳妥有效处置。（二）严格遵守招聘纪律。招聘工作人员及相关负责人，应自觉遵守相关人事管理制度和《××××省农村信用社员工亲属回避制度》。对在招聘工作中徇私舞弊、弄虚作假等违反招聘工作纪律和要求的，按照相关规章给予当事人严肃处理。（三）严把人才引进关口。引进专业人才，要从经营管理需要和长远发展角度制定引进人才标准，严格执行招聘管理程序，确保引进人才质量。原则上引进的专业人才应具备相关专业国民教育本科及以上学历和相应的专业技术职称，除特殊专业人才外，年龄不超过35周岁。同时，要建立内部人才培养机制，加快人才培养步伐，形成“内培为主，外引为辅”的人才引进管理模式。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第七条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《员工招聘管理办法》', '', '第八条', '本办法自发文之日起施行。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第一章   总  则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）员工离职管理，维护员工权益，依据《中华人民共和国劳动法》、《中华人民共和国劳动合同法》及国家相关政策规定，现结合本行实际，制定本细则。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第一章   总  则', '第二条', '本细则所称“离职”，是指劳动合同制员工与本行劳动关系的结束，离开本行的情形。主要有以下方式：（一）员工辞职；（二）辞退员工；（三）协商解除劳动合同。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第一章   总  则', '第三条', '员工离职管理工作应遵循以下基本原则：（一）依法合规，注重程序和时效，细致操作；（二）统一规范，分工负责，配合协调；（三）积极应对，妥善处理，防范风险。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第一章   总  则', '第四条', '为积极维护本行和员工本人的合法权益，明确各方责任，减少争议，在对员工离职管理的重要操作环节中，均应形成书面材料和相关记录，并建立完整的员工离职管理文档。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第五条', '本细则所称“员工辞职”，是指员工主动提出与本行解除劳动关系的情形。如员工具有下列情况之一，原则上不得批准辞职申请：在涉及本行资金安全和重要机密等特殊职位或重要岗位上工作，且暂不宜辞职的；（二）经办的重要业务尚未办理完毕，且不能由他人替代的；（三）相关司法事务尚未了结的；（四）正在接受本行审计或有关部门检查的；（五）其他不适宜辞职的情况。    第六条  本行员工辞职统一由总行负责受理。对重要岗位的辞职人员需由本行审计部进行离任(岗)审计，并出具相关审计报告。    第七条  为确保员工辞职表示的真实性，员工辞职必须以书面方式提出，并在人力资源部工作人员前进行面签确认，妥善处理好劳动合同约定的有关事项。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第八条', '员工辞职必须提前以书面形式通知所在单位和人力资源部，试用期内员工的辞职提前通知期为三日；一般人员为三十日；行级领导、部门总经理、副总经理、总经理助理、享受（正）副股级待遇人员、支行行长、支行副行长、支行行长助理、会计主管、信贷主管、客户经理为三个月。对于本行核心岗位的关键员工，根据国家或地方规定在劳动合同或其他相关协议中另行约定了较长提前通知期（最长不超过6个月）的，还应遵守该提前通知期约定。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第九条', '员工提出辞职后，在提前通知期内应按照规定正常出勤，服从所在单位管理，按质按量完成原岗位工作或按要求进行相关工作交接，不得擅自离岗。本细则中所称的“所在单位”均指离职时，人员所属的支行（部）及中心。办理员工辞职时，应告知员工遵守国家法律规定和劳动合同或其他相关协议中约定的提前通知期。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十条', '员工在提前通知期内发生违规违纪行为的，应按照员工违规处理的相关规定及时进行处理。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十一条', '除有法定理由外，员工未通知或提前通知期未满即擅自离职的，属于严重违纪并构成旷工行为，本行有权按照员工违规处理的相关规定，对该员工进行处理（包括以该员工严重违纪而解除劳动关系），给本行造成损失的，应当要求该员工予以赔偿：（一）本行为招收录用该员工所直接支付的费用；（二）本行为该员工支付的培训费用；（三）对本行经营造成的直接经济损失；（四）劳动合同或相关协议约定的其他赔偿费用。其他机关、企事业单位、学校等因拟招聘、雇佣、招考该员工而向本行询问、调查该员工相关情况的，应将上述事实向其如实反映，并在个人档案中载明。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十二条', '本行核准的辞职员工离职日期和解除劳动合同日期原则上应为提前通知期届满之日。如员工书面同意的，也可以为提前通知期届满之日以后的日期。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十三条', '辞职是国家法律赋予劳动者的权利，本行设定的内部批准、核准、审批、备案等程序，均不影响员工提前通知期届满即可离职并要求出具解除劳动合同证明的权利。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十四条', '员工辞职违反与本行签订的培训协议中服务期约定的，应承担违约责任，向本行支付违约金。违约金的数额不超过服务期尚未履行部分所应分摊的培训费用。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十五条', '员工因个人原因要求调往其他机关、企事业单位的，应要求其本人以书面方式提出辞职，不得仅凭对方单位商调函件办理劳动关系解除手续。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十六条', '对于已提出辞职或有辞职意向的员工，所在单位负责人应在2个工作日内组织员工谈话，并填写《××××银行员工辞职谈话记录表》（附件1），了解分析员工提出辞职的原因，征求员工的意见，听取员工的建议，对表现优秀的员工应予以挽留。对于员工辞职谈话中所反映的本行经营或管理问题，应予以重视。未经人力资源部负责人批准，不得擅自查阅员工辞职谈话记录内容。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十七条', '在确认员工离职意向并经本行党委会研究同意后，在1个工作日内向员工发《××××银行员工离职审批表》（附件2，以下简称《离职审批表》），并向员工所在单位下发《××××银行员工离职自查、核查通知单》（附件3，以下简称《自查、核查通知单》）。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十八条', '员工应在5个工作日内填妥《离职审批表》上的个人离职信息并经所在单位签署意见后交回人力资源部。员工所在单位也应在5个工作日内填妥《自查、核查通知单》并经主管部门核查后交回人力资源部。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第二章  员工辞职', '第十九条', '根据员工辞职的审批权限，人力资源部在5个工作日内完成对员工的辞职审批。经审批同意后，通知员工按照本办法第五章“离职手续”的要求，办妥各项离职手续。未妥善办理离职手续且未经人力资源部负责人批准同意，不得在提前通知期届满前准予员工离职并出具解除劳动合同证明。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十条', '本细则所称“辞退”是指由本行单方依法作出解除劳动合同决定或不予续签劳动合同决定，结束与员工之间劳动关系的情形。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十一条', '根据《劳动法》、《劳动合同法》中单位提出解除或终止员工劳动合同的相关规定，辞退具体原因类型可分为：（一）员工在试用期内被证明不符合录用条件的；（二）员工严重违反本行规章制度的；（三）员工严重失职、营私舞弊，给本行利益造成重大损害的；（四）员工与其他单位建立劳动关系，对完成本行工作任务造成严重影响，或者经本行提出，拒不改正的；（五）员工被依法追究刑事责任的；（六）员工医疗期届满不能从事原工作，也不能从事另行安排的工作的；（七）员工不能胜任工作，经培训或调整岗位，仍不能胜任工作的；（八）订立劳动合同时所依据的客观情况发生重大变化，致使劳动合同无法履行的；（九）符合国家规定的裁员条件而裁员的；（十）员工劳动合同到期本行决定不予续签的；（十一）其他合法的辞退原因。以上（一）至（五）均与员工本人存在过失相关，称为过失性辞退；（六）至（十）与员工本人是否存在过失无关，称为非过失性辞退。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十二条', '辞退员工，必须严格按照国家《劳动法》、《劳动合同法》以及其他相关政策法规中规定的解除或终止劳动合同条件和程序进行。非过失性辞退，还应按照国家规定支付经济补偿金。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十三条', '员工因不符合录用条件而解除劳动合同的，应注意遵守以下要求：（一）必须在劳动合同中规定的试用期内作出解除决定；（二）应有相应的证据材料证明员工不符合录用条件；（三）试用期满，不得再以员工不符合录用条件为由解除。所称录用条件包括国家法律法规中规定的基本任职条件和本行在招聘时规定具备的知识文化、技能、身体状况、思想品质等条件。证据材料包括但不限于载有明确招聘录用条件的招聘公告或招聘简章；入职前向员工申明的岗位资格条件、记录或评价员工不符合招聘录用条件的文字材料等。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十四条', '员工严重违反本行规章制度解除劳动合同的，应注意遵守以下要求：（一）本行规章制度中已明确规定可据此解除员工劳动合同；（二）员工违规事实已有充分的证据材料予以证实；（三）应在员工违规后及时做出处理决定。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十五条', '员工被依法追究刑事责任而解除劳动合同的，除国家另有规定外，应注意遵守以下要求：（一）员工被依法追究刑事责任，限于被人民法院判处各种刑罚（包括管制、拘役、有期徒刑、无期徒刑、死刑；罚金、剥夺政治权利、没收财产等）及被人民法院免予刑事处分的情形；（二）员工被人民法院判处拘役、有期徒刑但宣告缓刑的，仍属于被追究刑事责任，可予以辞退；（三）人民法院追究刑事责任的法律文书已生效；（四）人民检察院作出的不起诉决定，不属于追究刑事责任，不能以此作为解除劳动合同的依据。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十六条', '员工因涉嫌违法犯罪被国家有关机关拘留、逮捕或被采取其他限制人身自由的强制措施的，劳动合同暂时停止履行，不应直接据此解除劳动合同。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十七条', '员工因涉嫌违法犯罪被国家有关机关拘留、逮捕或被采取其他限制人身自由的强制措施，后虽未被追究刑事责任，但已符合本行内部规章制度规定的解除劳动合同条件的，应按照本行员工违规处理规定及时解除劳动合同。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十八条', '员工患病或者非因工负伤的医疗期，按照员工实际参加工作年限和在本行工作的年限确定。患有癌症、精神病、瘫痪等特殊疾病的员工，在二十四个月内尚不能痊愈，可适当延长停工医疗期。员工医疗期满经劳动能力鉴定为一至四级，符合退休、退职条件的，办理退休、退职手续，员工享受退休、退职待遇。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第二十九条', '应建立公正、严谨的考核认定程序，确定员工是否胜任岗位工作，是否遵守规章制度和劳动纪律，其中，考核既包括按年、季、月等进行的定期考核、常规考核，也包括不定期考核、专项考核。不能胜任是指员工不能按时、按质完成岗位常规工作任务，或距期初双方设定的岗位绩效目标有一定甚至很大差距等情形。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十条', '劳动合同订立时所依据的客观情况发生重大变化，致使原劳动合同无法履行，经员工与本行协商不能就变更劳动合同达成一致的，经提前30日通知或额外支付一个月工资后可予以解除劳动合同。“客观情况发生重大变化”的情形按国家或地方有关政策予以认定。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十一条', '员工在本行连续工作满十五年，且距法定退休年龄不足五年、处于“四期”（即医疗期、孕期、产期、哺乳期）以内或者有其他法定情形的，不得依据本办法第二十一条第（六）、（七）、（八）、（九）项解除劳动合同。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十二条', '员工存在本办法第二十一条第（一）、（二）、（三）、（四）、（五）项情形之一的，解除与员工的劳动合同不受第三十一条限制。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十三条', '员工与本行签订固定期限劳动合同，本行决定不予续签劳动合同的，劳动合同到期即行终止，劳动关系结束。员工在“四期”内劳动合同到期或有其他法定情形的，劳动合同顺延至相应的期限届满或情形消失时终止。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十四条', '本行可根据业务发展战略、人员配置状况、机构网点布局、业务流程改造等情况，确定员工续签劳动合同的具体资格和条件。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十五条', '对于工作能力较差、业务水平较低、有违规违纪或严重不当行为，以及按绩效考核规定要求连续两年距离绩效目标有一定差距或一年距离绩效目标有较大差距的，不再与其续签劳动合同。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十六条', '本行决定辞退员工，按照合同期限管理情况，分为以下方式：（一）不予续签劳动合同的，应在员工劳动合同到期前30天向员工送达《关于不予续签劳动合同的通知》（附件4），合同到期即行终止劳动合同，双方劳动关系结束；（二）解除劳动合同的，应提前30天向员工送达《关于解除劳动合同的通知》（附件5），或者以支付1个月的补偿金方式，向员工送达《关于解除劳动合同的通知》后，可及时解除劳动合同。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十七条', '辞退员工，应填写《××××银行辞退员工审批表》（见附件6），履行内部审核、批准、备案等程序，用人部门、机构应签署明确意见，并由负责人签字后盖章。本细则第三十六条提及的两份通知的送达必须严格按照以下程序办理：（一）以书面形式直接送达本人签收；（二）本人不在的，交其同住成年亲属签收；（三）采用上述直接送达有困难的，可以邮寄送达，以挂号查询回执上注明的收件日期为送达日期；（四）在受送达员工下落不明或采取上述送达方式无法送达的，可以公告形式送达，通过报纸等新闻媒介刊登通知。自发出公告之日起，30日之后，即视为送达。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十八条', '自送达之日起一个月届满（或者以支付1个月的补偿金方式），应按照本办法第五章“离职手续”的要求，办妥各项离职手续。未妥善办理离职手续且未经人力资源部负责人批准同意，不得在提前通知期届满前准予员工离职，不得提前出具解除劳动合同证明。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第三章   辞退员工', '第三十九条', '未经总行批准同意，其他机构或部门、单位不得擅自作出辞退员工决定。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第四章  协商解除劳动合同', '第四十条', '协商解除劳动合同是指本行与员工通过协商一致的方式结束双方劳动合同。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第四章  协商解除劳动合同', '第四十一条', '采用协商方式解除劳动合同的，双方要签订书面《协商解除劳动合同协议书》（附件7），明确以下基本内容：（一）双方同意解除劳动关系的明确意愿，说明协商解除劳动合同后员工与各单位的关系；（二）劳动关系解除的生效时间；（三）是否支付经济补偿金、生活补助费和其他补助费用，以及支付的标准、金额和方式；（四）离职工作交接；（五）离职后的保密等义务；（六）提示员工在就业、养老、社会保险缴费等方面的风险；（七）其他双方经协商确定的内容。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第四章  协商解除劳动合同', '第四十二条', '按照《××××银行协商解除劳动合同审批表》（附件8）的要求，履行内部审核批准程序，其中，提交核准的内容应包括《××××银行协商解除劳动合同审批表》和《协商解除劳动合同协议书》。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第四章  协商解除劳动合同', '第四十三条', '本行与员工采用协商方式解除劳动合同的，应与员工签订《协商解除劳动合同协议书》，并按照本办法第五章“离职手续”的要求，办妥各项离职手续。未妥善办理离职手续且未经人力资源部负责人批准同意的，所在单位不得在提前通知期届满前准予员工离职，不得出具解除劳动合同证明。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第四十四条', '员工无论因何种原因离职，均应按照本办法的规定办理离职手续。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第四十五条', '人力资源部为离职员工出具《××××银行员工离职手续办理表》（附件10，以下简称《离职手续办理表》），并将员工离职信息告知相关单位。员工应在10个工作日内办妥《离职手续办理表》的所有手续，主要包括：（一）工作移交；（二）所占用本行公用财物（如房屋、办公设备等）返还与清理；（三）取消内部权限（如业务系统、内部办公网等）和相关内部证件；（四）转移员工党团组织关系； （五）转移员工档案及社会保险、住房公积金关系；（六）结算工资、违约金、补偿金、赔偿金等； （七）出具解除或终止劳动关系的证明；（八）其他必要的手续环节。 第四十六条  机关部门或支行负责人离职的，应按本行稽核制度及时安排离任稽核。未列入离任稽核范围，但负责业务经营管理或管钱、管物、管账的人员离职的，由所在单位对其进行离职经济检查。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第四十七条', '不得采用以下非法手段限制员工离职：（一）扣押员工档案、身份证件；（二）不出具或未按规定期限出具解除/终止劳动关系证明；（三）不办理或未按照规定时限办理党团组织关系转移；（四）不办理或未按规定时限办理社会保险、住房公积金关系转移；（五）限制员工人身自由等。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第四十八条', '离职员工应在劳动合同解除（终止）15天内，凭相关手续办理个人档案及各项保险的转移手续。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第四十九条', '对于员工欠缴违约金、赔偿金或未能退还本行住房、物资等，本行应通过以应付工资薪酬合法抵销或直接提起仲裁、法院诉讼等手段进行追索。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十条', '员工办妥《离职手续办理表》有关手续后，由人力资源部开具《解除（终止）劳动合同证明书》(附件11)。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十一条', '员工在《解除（终止）劳动合同证明书》、《离职人员声明书》（附件12）上签字确认，并将《离职手续办理表》、《解除（终止）劳动合同证明书》（单位留存联）、《离职人员声明书》交回人力资源部，方可正式离职。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十二条', '员工离职后，应将其离职情况通过内部发文形式通报全行。对于享有对外业务签字权限或其他代表、代理权限的员工，还应由业务办理部门将员工离职及取消权限的情况向外部客户、机构和政府有关部门予以通知和说明。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十三条', '员工离职以后，发现其仍以本行名义在外洽谈客户、联系业务、吸收存款或以本行名义从事其他活动的，应及时向客户和社会公众澄清，并向有关机关举报。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十四条', '对业务部门或支行负责人、信贷主管、客户经理等有经办贷款责任的员工，应按照本行相关管理规定，由新接管客户经理对其经办贷款在三个月内予以确认，对确认有问题的贷款依据责任贷款追究管理办法给予界定，对界定有问题的予以赔偿，赔偿金不足的直接抵销上岗抵押金、贷款风险金、当年度绩效考核工资及转让本行员工股权。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十五条', '员工在劳动合同期限内辞职并解除劳动合同，本行有权对其当年绩效薪酬及延期支付部分予以全部扣除。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十六条', '员工离职后，被发现在本行工作期间严重违规且情节严重的，仍应按本行违规违纪处理相关规定追究责任。涉嫌犯罪的移送或提请司法机关处理，应给予处分的，向有关监管机关移送材料。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第五章  离职手续', '第五十七条', '已经解除或终止的劳动合同的文本及相关材料，应作长期档案保存。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第六章  离职脱密管理的专门规定', '第五十八条', '对于掌握本行商业秘密的管理人员或其他重要岗位员工，应在其离职前采取必要的脱密措施。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第六章  离职脱密管理的专门规定', '第五十九条', '可采取的脱密措施包括：（一）回收相关秘密信息载体（书面或电子）；（二）不再安排参加重要业务会议；（三）限制或禁止查阅、复制、接触涉密材料；（四）限制或禁止与客户、客户信息直接接触；（五）要求员工离岗休假或不履行全部或部分职责；（六）其他必要的脱密管理措施。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第六章  离职脱密管理的专门规定', '第六十条', '员工离职后，仍负有保守本行商业秘密的持续义务。应要求员工签署《××××银行员工保密承诺书》（见附件13）。对于泄露本行商业秘密的离职员工，本行保留追索赔偿经济损失及追究其他责任的权利。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第六章  离职脱密管理的专门规定', '第六十一条', '与本行签订有竞业限制协议的员工，离职后还应遵守竞业限制协议规定的义务。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第六章  离职脱密管理的专门规定', '第六十二条', '离职员工未遵守保密协议或竞业限制协议规定义务的，应及时依法追究员工的违约责任。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第六十三条', '本细则由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第六十四条', '由于特殊原因，与本行已形成用工关系但未签订书面劳动合同的员工，也应按照本细则执行。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第六十五条', '本细则之条款遇国家法律、地方法规，以及本行政策或规定调整，从其规定执行。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第六十六条', '本细则经第九届职工代表大会表决通过之日起施行。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第一条', '甲、乙双方劳动关系解除的生效时间为    年    月    日。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第二条', '甲方同意以           （支付/不支付）经济补偿金方式,支付标准为：          元/年，乙方累计在甲方工作      年（大写），共应支付补偿金额合计：人民币    元（大写），支付方式为直接发放至乙方本人。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第三条', '甲、乙劳动关系解除生效后，乙方配合甲方办理离职工作交接，以及甲方规定的其他各项离职手续。因乙方过错导致离职手续出现瑕疵，由此引发的一切责任由乙方承担。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第四条', '乙方确认：甲将已经就双方劳动合同解除后可能存在的有关就业、养老、社会保险缴费等方面的风险已充分阐述，且乙方已经明白并且愿意承担在与甲方劳动关系解除后，在就业、养老、社会保险缴费等方面的风险。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第五条', '乙方离职后，仍负有对甲、乙双方所签《补充协议》、《服务协议》约定的保密义务和竞业限制义务。乙方违反上述义务及其他违规行为的，甲方保留追索赔偿经济损失以及追究其他责任的权利。'),
(NULL, '人力资源部', '《员工离职管理办法实施细则》', '第七章  附  则', '第六条', '本协议书一式两份，甲乙双方各执一份，具有同等法律效力。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '', '第一条', '根据银监会《商业银行稳健薪酬监管指引》（银监发〔2010〕14号）、《××××省农村信用社行业薪酬管理办法》（××××）精神，结合××××银行股份有限公司（以下简称“本行”）实际，制定本办法。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '', '第二条', '为加强薪酬延期支付工作的领导，本行成立薪酬延期支付管理领导小组。组长由总行行长担任，副组长由总行副行长担任，成员由人力资源部、计划财务部、运营管理部、信贷管理部、合规管理部、风险管理部、审计部负责人担任。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第二章 实施对象', '第三条', '实行薪酬延期支付管理的岗位：（一）总行领导：董事长、行长、监事长、副行长；（二）董事会：董事会秘书、办公室主任、办事员；（三）监事会：办公室主任、办事员；（四）业务管理、支持保障部门及团队：总经理（主任）、副总经理、风险经理、团队负责人、运营主管、办事员；（五）业务经营部门及团队：总经理、副总经理、团队负责人、客户经理、产品经理、办事员等；（六）支行、分理处：支行行长、分理处负责人、柜员等；（七）其他需要实行薪酬延期支付的人员。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第三章 延期支付比例', '第四条', '根据岗位风险状况，确定一定的薪酬延期支付比例，具体标准为：（一）董事长、行长绩效工资不少于 50％用于延期支付；（二）监事长、总行副行长, 支行行长, 从事公司业务、小微业务、三农业务、金融市场业务、资产保全、授信评审、信贷管理、支行运营主管及清算、科技信息等人员，其绩效工资不少于 40％用于延期支付；（三）从事风险管理、合规管理、计划财务、运营管理（运营管理部门、事后监督、授权、对账、放款等团队）、电子银行等人员，其绩效工资不少于 20％用于延期支付；（四）从事董事会、监事会、经营层办公室、人力资源、审计、发展规划、运营管理（清分、会出辅导、自助银行管理）、安全保卫等人员，柜员、产品经理等，其绩效工资不少于 10％用于延期支付；（五）其他岗位的薪酬延期支付比例由总行视其风险程度确定，但不得低于其绩效工资的 10％。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第四章 延期支付的管理', '第五条', '延期支付提取和支付的方式延期支付实行按月提取、全年统筹的方式，当出现当月员工实得薪酬低于 3000 元的情形时，则可暂缓提取，待满足提取条件时再行补提，年终对全年应提取额进行补差。延期支付期限与风险持续期相一致，支付期限为三年，实行等分原则，不得前重后轻。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第四章 延期支付的管理', '第六条', '延期支付使用规则延期支付主要用于员工因责任事故、经济案件、违约或其他过失需赔偿或罚没款项等的支出。在延期支付期限内，对于产生风险损失赔偿、不良资产责任赔偿，或其他应由员工承担的经济损失，应优先从延期支付中支付，本行有权确定从当期薪酬与延期支付薪酬中支付比例。在延期支付期限内，对于发生符合本行有关员工问责处罚事项，经规定程序确定需扣发绩效薪酬的，符合延期支付扣减情况的，先从延期支付账户中扣减，再扣减当月薪酬（留足最低生活保障金），当月不足扣减的从下月薪酬中扣减，直到满足为止。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第四章 延期支付的管理', '第七条', '延期支付的 3 年期限内，相关领导班子成员和员工职责内风险损失超常暴露，本行有权将相应期限内已发放的薪酬全部追回，并止付所有未止付部分。绩效薪酬延期追索、扣回规定同样适用于离职人员。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第四章 延期支付的管理', '第八条', '延期支付的管理本行延期支付实行统一管理。人力资源部负责薪酬延期支付办法的制定，计划财务部负责建立全行薪酬延期支付账户；审计部负责延期支付期满后风险责任的认定，牵头相关部门进行问责调查和责任认定。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第四章 延期支付的管理', '第九条', '因公工作调动、退休等人员，在延期支付期满后支付相应薪酬；离职人员延期支付薪酬按《××××银行股份制员工离职管理办法实施细则》执行。 第十条  本行审计部须定期对延期支付薪酬的缴存、调整、扣减、退回等管理情况进行专项审计。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第五章   附则', '第十一条', '本办法由××××银行股份有限公司人力资源部负责解释和修订，如与国家或监管部门有关规定不一致的，按国家和监管部门规定执行。'),
(NULL, '人力资源部', '《员工薪酬延期支付管理办法》', '第五章   附则', '第十二条', '本制度自发文之日起施行。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第一章 总则', '第一条', '为建立多层次的医疗保障体系，提高××××银行股份有限公司（以下简称“本行”）职工的医疗保险待遇，减轻职工因病住院导致个人医疗费的过重负担，根据《国务院关于建立城镇员工基本医疗保险制度的决定》[国发（1984）44号]、《关于企业补充医疗保险有关问题的通知》［财社（2002）18号］、《××××市职工医疗保险办法》等规定，结合实际，特制定本办法。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第一章 总则', '第二条', '补充医疗基金是对基本医疗基金的补充，在本行经营效益许可的情况下，按照国家有关规定本行建立的一种补充医疗保险形式。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第一章 总则', '第三条', '建立职工补充医疗基金的原则：自愿参加原则、个人出资和单位资助相结合原则、单位资助与单位发展水平相适应原则、互助与受益相结合原则。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第二章 实施职工补充医疗基金的对象和范围', '第四条', '凡本行正式在编职工，均可自愿参加职工补充医疗基金。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第二章 实施职工补充医疗基金的对象和范围', '第五条', '参保员工的权利（一）有权选择参加或不参加企业医疗基金计划；（二）有权了解本行为其缴纳的额度和规定；（三）有权向本行和医疗基金管理机构查询与个人账户有关的账面以及其他文件；（四）在符合相关法规、医疗基金支付规则的条件下，有权申请领取、处分其个人账户上的资金；（五）法律、法规规定和本合同约定的其他义务。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第二章 实施职工补充医疗基金的对象和范围', '第六条', '参保员工的义务（一）享受员工需如实向本行或医疗基金管理机构提供建立医疗基金计划所需的信息材料；（二）如果发生与医疗基金计划管理有关的信息变更，需及时通知本行或医疗基金管理机构；（三）授权本行作为委托人委托受托人对其个人账户中的企业医疗基金进行运营管理；（四）承诺按照规定的条件领取补充医疗基金；（五）法律、法规规定和本合同约定的其他义务。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第二章 实施职工补充医疗基金的对象和范围', '第七条', '参保员工退出计划的条件和程序本方案实施后，参保员工无故不得退出本方案，符合以下条件，方可退出本方案：（一）达到医疗基金规定领取条件；（二）由参保员工直接向企业医疗基金管理机构申请领取，申请时需填写相关申请书和提取领取的所证明；（三）参保员工与本行解除劳动合同关系。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第三章　职工补充医疗基金的管理', '第八条', '职工补充医疗基金设置专门账户，专款专用，也可以由专业公司集中托管。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第三章　职工补充医疗基金的管理', '第九条', '总行成立相关部门组成的××××银行职工补充医疗基金管理工作小组，负责管理和制订相关制度。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第四章　职工补充医疗基金的筹集', '第十条', '职工补充医疗基金提取及标准。（一）职工补充医疗基金由单位出资，按本行上一年度工资总额的5％提取，从营业及管理费用中列支，个人不缴纳。（二）如果需要调整缴费比例，在国家规定范围内由本行职工代表大会协商确定。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第四章　职工补充医疗基金的筹集', '第十一条', '员工上年度有下列情况之一的，不享受当年的企业医疗基金个人账户分配。（一）当年事假累计满30天；（二）旷工累计满3天的；（三）行政记大过或党内严重警告及以上处分，行政拘留的；（四）年度考核不称职的；（五）其他按国家政策规定应取消当年医疗基金的。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第五章  职工补充医疗基金的使用', '第十二条', '凡参加职工补充医疗基金的职工，以个人帐户余额的上限为报销最高额。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第五章  职工补充医疗基金的使用', '第十三条', '经确诊为重症病人或情况特殊的，经××××银行职工补充医疗基金管理工作小组集体讨论确定补助比例。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第五章  职工补充医疗基金的使用', '第十四条', '职工申请职工补充医疗基金补助需提供申请补助报告、诊疗证明、发票原件或结账单。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第五章  职工补充医疗基金的使用', '第十五条', '下列情形不属于职工补充医疗基金的补助范畴：（一）职工在其他保险和其他赔付责任范围支付的医药费用；（二）与检查、治疗无直接关系的费用，如美容、矫形等费用；（三）违法犯罪、酗酒、自杀、自残发生的医药费用；（四）无证及酒后驾驶所致车祸所发生的医疗费用；（五）经管理工作小组认定为不属于补助范畴的其他费用。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第五章  职工补充医疗基金的使用', '第十六条', '职工已经交纳的补充医疗基金不得退出；停交的补充医疗基金不得补交，停交期间不享受本基金的补助；职工离开本单位后，不再享受本基金的补助。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第六章　操作流程', '第十七条', '支行（营业部）：由运营主管受理、审核申请人的申请补助报告、诊疗证明、发票原件或结账单；运营主管审核后在报告上签署审核意见；并经支行行长（营业部总经理）签署审查意见后报总行确定的管理部室。'),
(NULL, '人力资源部', '《员工补充医疗基金管理办法》', '第六章　操作流程', '第十八条', '总行职能部门：由部门指定人员受理、审核申请人的申请补助报告、诊疗证明、发票原件或结账单；指定人员审核后在报告上签署审核意见；并经部门总经理（主任）签署审查意见后报总行确定的管理部室。'),
(NULL, '人力资源部', '《总行部室、团队、台城支行员工岗位管理要求》', '', '第一条', '岗位基本要求今后凡申请调入部室、团队、台城机构的，均须符合以下条件：（一）部室员工岗位基本要求1.学历：本科及以上学历（第一学历）；2.工龄：本科学历人员须在基层支行工作3年及以上，硕士研究生学历人员须在基层支行工作1年及以上；3.学识水平：取得与岗位相近的中级职称，或取得银行业专业人员中级职业资格，取得省联社认定的从业资格，其岗位有其他从业要求的须取得从业资格，或在省级及以上行业主管部门内部刊物上发表1篇及以上理论文章和调研成果（不含经验材料），或在市级行业主管部门内部刊物上发表3篇及以上理论文章和调研成果（不含经验材料）；4.业务能力：（1）能够熟练运用OFFICE 等办公软件，具有一定的文字表达能力；（2）业务技能。30周岁（含）以下人员：综合业务知识A级，会出业务实务A级，信贷业务实务A级，电子银行业务A级，数字录入B级，汉字录入B级，手工点钞C级；30周岁至男50周岁、女45周岁（含）人员：综合业务知识A级，会出业务实务B级，信贷业务实务B级，电子银行业务B级，数字录入C级，汉字录入C级，手工点钞C级；男满50周岁、女45周岁以上人员：综合业务知识B级，会出业务实务C级，信贷业务实务C级，电子银行业务C级，数字录入C级，汉字录入C级，手工点钞C级；（3）或在各级业务技能比赛中获得三等（或相当于）以上奖项（团体或个人）；（4）工作业绩在本单位处于中上水平；5.个人品行（1）具有较强的敬业精神、团队合作意识，执行力强；（2）无严重违章违纪行为，无记大过及以上纪律处分。（二）团队员工岗位基本要求1.学历：本科及以上学历（第一学历）；2.工龄：本科学历人员须在基层支行工作3年及以上（工作表现优秀的适用人才放宽为2年及以上），硕士研究生学历人员须在基层支行工作1年及以上；3.学识水平：取得与岗位相近的中级职称，或取得银行业专业人员初级职业资格，取得省联社认定的从业资格，其岗位有其他从业要求的须取得从业资格；4.业务能力：（1）业务技能。30周岁（含）以下人员：综合业务知识A级，会出业务实务A级，信贷业务实务A级，电子银行业务A级，数字录入B级，汉字录入B级，手工点钞C级；30周岁至男50周岁、女45周岁（含）人员：综合业务知识A级，会出业务实务B级，信贷业务实务B级，电子银行业务B级，数字录入C级，汉字录入C级，手工点钞C级；男满50周岁、女45周岁以上人员：综合业务知识B级，会出业务实务C级，信贷业务实务C级，电子银行业务C级，数字录入D级，汉字录入D级，手工点钞D级；（2）或在各级业务技能比赛中获得优秀（或相当于）以上奖项（团体或个人）；（3）工作业绩在本单位处于中上水平；5、个人品行（1）具有较强的敬业精神、团队合作意识，执行力强；（2）无严重违章违纪行为，无记大过及以上纪律处分。（三）台城支行员工岗位基本要求1.学历：本科及以上学历（第一学历）；2.工龄：本科学历人员须在台城地域外支行工作3年及以上（工作表现优秀的放宽为2年及以上），硕士研究生学历人员须在台城地域外支行工作1年及以上；3.学识水平：取得与岗位相近的中级职称，或取得银行业专业人员初级职业资格，取得省联社认定的从业资格，其岗位有其他从业要求的须取得从业资格；4.业务能力：（1）业务技能。30周岁（含）以下人员：综合业务知识B级，会出业务实务B级，信贷业务实务B级，电子银行业务A级，数字录入B级，汉字录入B级，手工点钞C级；30周岁至男50周岁、女45周岁（含）人员：综合业务知识B级，会出业务实务B级，信贷业务实务B级，电子银行业务B级，数字录入C级，汉字录入C级，手工点钞C级；男满50周岁、女45周岁以上人员：综合业务知识B级，会出业务实务C级，信贷业务实务C级，电子银行业务C级，数字录入D级，汉字录入D级，手工点钞D级；（2）或在各级业务技能比赛中获得优秀（或相当于）以上奖项（团体或个人）；（3）工作业绩在本单位处于中上水平；5.个人品行（1）具有较强的敬业精神、团队合作意识，执行力强；（2）无严重违章违纪行为，无记大过及以上纪律处分。6.特殊条件（1）在台城有优质的工作资源；（2）家庭确有较难以克服的困难。'),
(NULL, '人力资源部', '《总行部室、团队、台城支行员工岗位管理要求》', '', '第二条', '现任岗位不满足条件的处置已进入部室、团队、台城支行的员工，除男满50周岁、女满45周岁人员外，均须作出以下承诺：（一）部室、团队中基层工龄不足的人员，每年到基层代班不少于2个月，直至满足条件；（二）1年内取得相应的职称或银行业专业人员资格，取得省联社认定的从业资格，其岗位有其他从业要求的须取得从业资格；（三）1年内技能达标。对未兑现承诺的，实行降薪处理。第一年未取得岗位资格的、技能不达标的各降薪5%，第二年未取得岗位资格的、技能不达标的各降薪10%，第三年内未兑现承诺的调离现岗位。'),
(NULL, '人力资源部', '《总行部室、团队、台城支行员工岗位管理要求》', '', '第三条', '部室、团队、台城支行人员须无条件服从总行上下交流、岗位轮换以及其他工作安排。'),
(NULL, '人力资源部', '《总行部室、团队、台城支行员工岗位管理要求》', '', '第四条', '本管理要求与总行人力资源管理制度具有相同效力。'),
(NULL, '人力资源部', '《总行部室、团队、台城支行员工岗位管理要求》', '', '第五条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《总行部室、团队、台城支行员工岗位管理要求》', '', '第六条', '本管理要求经本行职工代表大会审议通过后施行。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）薪酬管理，进一步深化改革，充分发挥绩效考核的激励与约束作用，调动全体员工工作的积极性、主动性，努力提高经营管理水平和经济效益，促进各项工作目标的实现，根据《商业银行稳健薪酬监管指引》、《中华人民共和国劳动法》、《中国银监会关于建立健全农村合作金融机构激励约束机制的指导意见》和省联社有关薪酬管理的文件精神，结合本行实际，特制定本制度。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第一章  总则', '第二条', '本制度明确了全行员工薪酬体系的管理原则及薪酬构成内容与要求。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第一章  总则', '第三条', '本制度所称的薪酬，是指商业银行为获得员工提供的服务和贡献而给予的报酬及其相关支出，包括基本薪酬、绩效薪酬、福利性收入等项下的货币和非现金的各种权益性支出。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第一章  总则', '第四条', '本制度所称的员工，是指本行所有在编人员（含劳务派遣人员）。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第一章  总则', '第五条', '本制度适用于本行薪酬的管理。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第二章   职责和权限', '第六条', '在本行提名与薪酬委员会领导下成立劳动用工和薪酬管理领导组，由本行主要领导、人事和相关业务人员组成，具体负责劳动用工和薪酬管理工作的组织领导和实施。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第二章   职责和权限', '第七条', '人力资源部负责全行薪酬管理的日常工作。负责员工评定和晋升的组织工作。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第二章   职责和权限', '第八条', '营业部、支行以及其他经营性单位负责本单位绩效收入的考核和分配。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第九条', '薪酬管理的原则坚持建章在先，依章管理的原则；坚持以人为本，优胜劣汰的原则；坚持精干高效，公平公正的原则；坚持按劳分配，工效挂钩的原则；坚持因地制宜，分步实施的原则；坚持循序渐进，逐步完善的原则。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第十条', '薪酬构成的内容薪酬管理要坚持效率优先、兼顾公平，既要提倡奉献精神，又要落实分配政策，既要反对平均主义，又要防止收入悬殊。本行实行结构工资制，员工的工资结构分为基础工资、岗位工资、津贴和绩效薪酬、福利性收入几部分，其中前三项为基本薪酬。（一）基础工资。该项工资是保障员工基本生活的工资。以省劳动和社会保障部门规定的当地最低工资标准为保障工资标准。该项工资标准随当地最低工资标准的变动而调整。（二）岗位工资。岗位工资根据对员工的综合素质、工作业绩、服务技能、服务质量进行综合考核后评定的行员等级以及岗位责任风险大小等确定。（三）津贴。津贴包括工龄津贴、学历津贴、职称津贴、职业资格津贴、从业资格津贴、技能津贴等，此项工资是对员工的学历、资历、专业技术水平等方面给予的补偿性工资。津贴的项目和标准视本行经营管理情况和员工队伍状况适时调整。（四）绩效薪酬。绩效薪酬是根据员工从事业务经营、管理和业务操作创造业绩大小而结算的薪酬，包括月度绩效、季度绩效、年度绩效。总行领导班子成员绩效薪酬根据省联社核定领导班子工资总额及相关考核结果、董事会对经营层考核结果计发。（五）福利性收入。福利性收入是总行根据国家有关规定以及为保障和提高员工生活水平而提供的相关福利，包括社会基本保险、住房公积金、企业年金、补充医疗保险、其他社会保险以及按照规定发放的职工福利费等。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第十一条', '薪酬分配原则（一）本行要按照多贡献，多创利，多收入的分配原则，加大对员工的考核力度，把工资增长和效益增长紧密挂钩，根据岗位责任的大小、工作质量的高低、经营业绩的好坏、所做贡献的多少拉开工资收入差距。（二）本行支付给员工的年度薪酬总额要综合考虑当年人员总量、结构以及银行财务状况、经营成果、风险控制等多种因素，参考上年薪酬总额占上年业务管理费的比例确定。  第四章 考核管理'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第十二条', '员工薪酬分配及考核管理实行行长室领导下的分管领导负责制，坚持职能部门与其它业务部门相协调，形成上下结合、齐抓共管的考核管理格局。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第十三条', '本行领导班子薪酬总额由省联社核准；其他员工的薪酬变动由人力资源部审批。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第十四条', '薪酬分配以绩效考核系统、职能部门检查考核和基层反馈信息等为基本依据，经本行人力资源部汇总，并报领导核批后交财务会计部门执行。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第十五条', '薪酬管理以调动员工积极因素、促进业务经营健康发展为宗旨，确保及时准确、操作规范、公正合理。对违反政策、弄虚作假、擅增支出的责任人和经办人，视情节轻重和责任大小严肃处理。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第三章  内容与要求', '第十六条', '审计稽核部每年对薪酬管理办法的合理性和执行情况进行专项审计，审计报告报提名和薪酬委员会，审计结果作为下一年度改进薪酬管理工作的依据。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第五章  其 他', '第十七条', '员工福利。员工的各项福利待遇经本行提名和薪酬委员会讨论通过后确定。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第五章  其 他', '第十八条', '企业年金。企业年金参照本行企业年金管理办法执行。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第五章  其 他', '第十九条', '绩效薪酬延期支付。本行建立绩效薪酬延期支付办法，根据员工的岗位、职务和等级来确定差别化的延期支付比例，延期支付期限与风险持续期相一致，支付期限一般不少于3年。具体情况参照本行《××××银行股份有限公司员工薪酬延期支付管理办法》执行。总行机关职能部室及其下属团队员工基本薪酬按本办法执行，绩效薪酬考核办法另行制定。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第六章  附 则', '第二十条', '本制度未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第六章  附 则', '第二十一条', '本制度由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《薪酬管理制度》', '第六章  附 则', '第二是二条', '本制度自发文之日起施行。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第一条', '为加强对××××银行股份有限公司(以下简称”本行”)金融许可证的管理，根据《金融许可证管理办法》（中国银行业监督管理委员会令××××年第××××号）、《中国银行业监督管理委员会关于公示金融许可证等有关事项的通知》（银监办通××××号）、《××××银监局金融许可证管理工作实施细则》（××××号）文件精神，制定本办法。 第二条  金融许可证是指中国银行业监督管理委员会（以下简称“银监会”）依法颁发的特许金融机构经营金融业务的法律文件。金融许可证的颁发、更换、扣押、吊销等由银监会依法行使，其他任何单位和个人不得行使上述职权。 金融许可证适用于银监会监管的、经批准经营金融业务的金融机构。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第四条', '本行人力资源部作为金融许可证的归口管理部门，负责本行总部及全辖内各分支机构的金融许可证管理及监督检查等工作,各分支机构负责人为本机构金融许可证管理的第一责任人，由本机构运营主管负责具体保管。    第五条  本行人力资源部应当在收到银监会或其派出机构批准文件60日内，持下列材料到银监会或其他派出机构领取或换领金融许可证：（一）银监会或其派出机构的批准文件；（二）金融机构介绍信；（三）领取许可证人员的合法有效身份证明；（四）银监会或其派出机构要求的其他资料。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第六条', '银监会或其派出机构颁发许可证时间期限为收到上述有效文件之日起五个工作日内。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第七条', '金融许可证载明下列内容：（一）机构编码；（二）机构名称（农村合作银行机构以括号注明法人机构或分支机构）；（三）依据的法律法规；（四）机构批准成立日期；（五）营业地址；（六）颁发许可证日期；（七）银监会或其派出机构的公章。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第八条', '发生下列情形，由本行人力资源部向银监会或其派出机构申请换发金融许可证：（一）机构更名；（二）营业地址（仅限于清算代码）变更；（三）许可证破损；（四）许可证遗失；（五）银监会或其派出机构认为其他需要更换许可证的情形。机构更名和营业地址变更应当将旧证缴回银监会或其派出机构，并持本办法第四条规定的材料换领金融许可证。许可证破损应在重新申领许可证时缴回原证。许可证遗失，应当在银监会或其派出机构指定的报纸上声明原许可证作废，重新申领许可证。并对保管人、责任人、机构负责人按照有关规定给予经济处罚和纪律处分。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第九条', '金融许可证实行机构编码终身制原则。除发生更名、营业地址（仅限于清算代码）变更、被撤销等原因外，机构编码一旦确定不再改变。金融许可证如遗失或破损，再申请换领许可证时，原机构编码继续沿用。金融许可证如被吊销，该机构编码自动作废，不再使用。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十条', '金融许可证颁发或更换时，应在银监会或其派出机构指定的全国公开发行的报纸上进行公告。金融许可证被吊销或注销时，也应在银监会或其派出机构指定的报纸上进行公告。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十一条', '公告的具体内容应当包括：机构名称、营业地址、金融机构编码、邮政编码、联系电话。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十二条', '金融许可证应当在机构营业场所的显著位置公示。本行总部及各分支机构应按要求在营业场所的显著位置公示其业务范围、主要负责人。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十三条', '任何机构和个人不得伪造、变造金融许可证。金融机构不得出租、出借、转让金融许可证。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十四条', '本行各机构违反《金融许可证管理办法》，有下列行为之一的，将责令限期改正，给予警告；逾期不改正的，可以处以3万元以下罚款；情节严重的，可以取消其直接负责的高级管理人员的任职资格：（一）不按规定换领金融许可证；（二）损坏金融许可证；（三）遗失金融许可证且不向银监会报告；（四）未在营业场所公示金融许可证；（五）伪造、变造、出租、出借、转让金融许可证。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十五条', '金融许可证由中国银行业监督委员会统一印制和管理。银监会按照金融许可证编码方法打印金融许可证，颁发时加盖中国银行业监督管理委员会或其派出机构的单位印章方具有效。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十六条', '金融许可证交接应实行第三人监交管理,并做好相关登记台帐。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十七条', '本办法由××××银行股份有限公司人力资源部负责解释和修订。'),
(NULL, '人力资源部', '《金融许可证管理办法》', '', '第十八条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第一条', '为加强xxx银行(以下简称“本行”) 信息安全管理工作，规范信息安全管理过程，建立、健全安全控制措施，保证信息的机密性、完整性、可用性，根据《中华人共和国计算机信息系统安全保护条例》、《金融机构计算机信息系统安全保护工作暂行规定》及相关法律法规特制定本办法。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第二条', '信息安全是指保证信息的保密性，完整性，可用性；另外也可包括诸如真实性，可核查性，不可否认性和可靠性等特性。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第三条', '信息安全事件是指系统、服务或网络的一种可识别的状态的发生，它可能是对信息安全策略的违反或防护措施的失效，或是和安全关联的一个先前未知的状态。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第四条', '一个信息安全事故由单个的或一系列的有害或意外信息安全事件组成，它们具有损害业务运作和威胁信息安全的极大的可能性。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第五条', '风险是指在信息系统安全领域，信息资产遭受损坏并给企业带来负面影响的潜在可能性；风险管理是指以可接受的代价，识别、控制、减少或消除可能影响信息系统的安全风险的过程。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第六条', '安全措施是指通过防范威胁、减少弱点、限制意外事件造成的影响等途径来削减风险的方法，包括策略、程序、指南、实践或组织结构，它们具备可管理性、技术性、可操作性和合法性。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第七条', '访问控制是指为防止对资源的未授权使用而采取的安全措施。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第一章    总   则', '第八条', '主机及系统是指小型机、开放平台服务器的操作系统、数据库系统等应用支撑平台及在其上运行的业务应用系统。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第二章   职责与权限', '第九条', '信息安全管理工作领导小组职责：按《xxx银行信息科技管理委员会工作制度》（xxx银行行发[2016]84 号）执行。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第二章   职责与权限', '第十条', '信息科技部门的主要职责：1.负责管理和指导本行信息安全工作；2.负责牵头制订本行信息安全管理制度、规范、标准和安全方案；3.负责牵头组织实施本行信息安全方案；4.负责牵头组织本行信息安全风险防范和安全监督等工作；5.负责牵头组织本行信息安全知识培训；6.负责本行安全技术产品的选型、购置等。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第二章   职责与权限', '第十一条', '风险管理部门主要职责：1.负责信息科技风险管理工作；2.负责协调制定有关信息科技风险管理策略；3.负责实施持续性信息科技风险评估，跟踪整改意见的落实；4.负责监控信息安全威胁的发生。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第二章   职责与权限', '第十二条', '合规管理部门主要职责：1.负责为业务部门和科技部门提供建议及相关合规性信息；2.负责监控不合规事件的发生。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第二章   职责与权限', '第十三条', '内部审计部门主要职责：1.设立专门的信息科技风险审计岗位；2.负责信息科技审计制度和流程的实施；3.负责制订和执行信息科技审计计划；4.负责对信息科技整个生命周期和重大事件等进行审计。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第二章   职责与权限', '第十四条', '各支行、部门的主要职责：1.按照安全体系规范的要求，组织落实本单位、本部门承担的各专业安全管理任务，制定本单位信息安全实施方案；2.按专业实施各类安全措施，并对实施结果负责；3.负责各类安全措施的日常维护和管理。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第三章   信息风险管理', '第十五条', '信息科技部门应在信息系统的建设、运行、变更管理的过程中引入风险管理，对风险进行评估和控制。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第三章   信息风险管理', '第十六条', '信息科技部门应组织各相关部门根据信息资产的重要等级对实施风险管理的信息资产进行识别，并有效识别信息资产的脆弱性、安全威胁，根据及安全事件发生的可能性和产生的影响，定期组织对风险的识别，确定风险等级。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第三章   信息风险管理', '第十七条', '各相关部门应根据风险的等级，从技术和管理方面选择和实施安全控制措施，削减、转移或接受风险。选择控制措施时应综合考虑实现成本、技术成熟性、可操作性、实用性等因素。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第三章   信息风险管理', '第十八条', '信息资产出现变更后，各相关部门应重新识别风险、确定风险等级并选择和实施安全控制措施。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第三章   信息风险管理', '第十九条', '各相关部门应建立相应的管理制度，保证风险管理的落实及维护。应建立和完善安全防范措施，降低安全威胁因素发生的可能性。应建立和完善安全监控措施，提高安全问题和事件的检测、响应和处理效率。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十条', '员工录用前必须进行严格的审查，并应与新员工签署保密协议，明确规定员工的信息安全责任、保密要求及违约的法律责任。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十一条', '员工任职期间应进行相关安全培训，增强安全意识，提高安全技能，明确安全责任：1.应进行上岗前的培训，掌握基本技能，培训内容包括：法律、法规、技术、业务、各种管理办法和操作规程、信息系统紧急事件的处理等；2.要求信息安全管理人员具有较高的政治素质和职业道德，各类安全工作人员必须经过相关的培训，严格考核，持证上岗；3.应组织员工进行信息安全知识培训；4.应组织员工进行法律、法规培训；5.应定期（或不定期）组织员工进行安全教育、安全技能培训，以增强员工遵守信息安全策略和信息安全规范的安全意识，提高安全技能水平；6.应组织员工进行安全职责、安全程序、安全知识、安全管理规章制度及应急处理程序等方面的培训。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十二条', '安全管理岗应定期对各部门和员工进行安全责任落实情况考核，考核内容包括安全意识、责任事故、责任问题、问题整改、制度执行等。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十三条', '安全管理、敏感信息处理等高风险操作岗位必须遵循权力分散、相互制约、权限最小化的原则，并定期轮换。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十四条', '必须制定员工的安全守则，明确员工在信息系统安全方面的职责、违反安全规定时的惩罚措施、聘用关系结束后的有效期。以适当形式的文件给出工作说明、岗位职责，阐明安全任务和职责，并备案；互斥、不兼容的工作角色必须进行角色分离。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十五条', '制定并落实信息安全奖惩机制，明确员工奖惩使用情况、证据提供、奖惩手段、审批程序等；奖惩手段包括行政警告或处分、经济奖励或处罚、调离岗位，根据合同予以辞退等方式，对于触犯刑律者，应该交于司法机关处理。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十六条', '凡涉及本行计算机业务系统和网络安全等的内容，未经批准，任何单位和个人不得在新闻媒体、技术刊物、非保密的外部会议上擅自公开发表，涉密人员不得利用公开通信工具谈论有关机密资源的内容。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十七条', '员工雇佣合同或条款发生变动时，特别是员工要离开单位或其合同到期时，必须根据保密协议对其工作进行审查；信息系统安全管理人员在调离岗位时，要求履行保密协议，承诺保密事项，并上交有关资料、证件。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十八条', '信息系统安全管理人员、敏感信息处理人员在转职、离职时，必须对其任职期间工作情况严格审查并执行脱敏期制度。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第四章   人力资源安全', '第二十九条', '员工在转职、离职之后，应及时撤销其访问权限，并收回相关信息资料。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第五章   信息资产管理', '第三十条', '信息资产识别和分类作为信息安全管理工作的基础，安全管理岗应清晰识别本行所有信息资产，并对组成信息系统的系统、设备数据等信息资产进行全面识别和统计，形成资产文档并统一归档管理，建立并维护信息资产清单。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第五章   信息资产管理', '第三十一条', '信息资产出现变更后，应对资产清单进行相应的变更处理，并定期对资产文档进行回顾。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第五章   信息资产管理', '第三十二条', '信息资产应依据其重要程度，如影响业务的类型和范围、敏感性、关键性等，按照机密性、完整性和可用性进行级别划分，并确定保护需求。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第五章   信息资产管理', '第三十三条', '信息科技部门应根据信息资产分级机制，建立信息标记和处理程序。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第五章   信息资产管理', '第三十四条', '信息科技部门应明确不同级别的信息资产责任人和访问控制原则，建立信息资产访问的授权机制。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第三十五条', '信息科技部门应以信息资产的安全等级、设备对环境要求、易管理性及物理空间分布为主要依据，划分物理安全区域，并根据物理区域安全等级，为其提供相应的安全环境保护。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第三十六条', '生产环境、测试环境、开发环境相互之间必须有明确的物理安全边界，在安全边界采取设置门禁、有人管理的接待台等控制措施。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第三十七条', '物理安全区域必须有明确的标志，根据物理安全区域等级，制定不同的保护措施和严格的访问控制措施。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第三十八条', '信息系统环境的安全控制措施及管理要求按照《xxx银行机房管理办法》执行。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第三十九条', '设备管理岗应对本行所有设备建立设备清单，定期对设备清单进行复查。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十条', '设备管理岗应保护好设备，以减少未授权访问的几率。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十一条', '对信息系统的相关计算机和数据通信设备及其连接关系，要编制与实际相符的物理连接图和逻辑结构图，并归档保存。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十二条', '合理规划传输数据或支持信息服务的电源布缆和通信布缆，防止被窃听或破坏。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十三条', '生产运行系统和网络的关键设备应有备份策略。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十四条', '信息科技部门应建立设备维护规范，确保维护人员能正确有效的对设备进行维护。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十五条', '信息科技部门应制定明确的管理制度，来管理办公设备和介质在办公区域以外的使用，保护其免遭破坏、丢失和非法使用。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十六条', '各单位对存放有信息的磁盘、光盘、磁带、纸张等存储介质必须建立清单并定期复核检查，不同安全级别的存储介质应采用不同的使用和存储要求。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十七条', '对长期存放的存储介质，必须在介质有效期内对信息进行转存，同时应确保转存所需的物理环境、各种类型介质所对应的转存期限。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十八条', '设备（包括存储介质）的处置或再利用：1.必须制定设备在销毁和重用过程中的安全流程；2.对于存储敏感信息的设备，在将其销毁时，应该确保存储在其上的所有敏感数据被不可恢复地彻底销毁；3.对于存储敏感信息的设备，在停止使用后或重用前，应确保所有敏感数据已被彻底删除或覆盖；4.对于已毁坏的包含敏感数据的设备，应对其进行风险评估，以确定是否应该销毁、修理或弃置。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第四十九条', '放置在桌面上暂时不用的物理文档，应该及时采用适当的形式进行安全保管。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第五十条', '个人计算机、终端在无人使用时，应保持注销状态，并采用锁屏、口令等措施保护起来。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第六章   物理与环境安全', '第五十一条', '白板、计算机等物品使用后，应及时隐藏或消除其上的信息。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十二条', '必须对第三方和外包服务单位应进行严格的资格审查，与第三方或外包服务单位签订合同，且合同中应该明确外包方的安全责任和必须遵守的安全要求。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十三条', '当第三方与外包服务单位需要对敏感的信息资产进行访问时，应与其签订安全协议书，明确安全责任和义务。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十四条', '根据信息资产等级及访问形式进行风险评估，确定其安全风险，根据风险等级，采取适当的控制方法对第三方访问进行安全控制，保护信息处理设施和信息资产的安全。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十五条', '各单位必须做好外来人员的出入管理、访问控制和陪同工作。对需要长期进入本单位工作的第三方和外包服务单位和人员，必须做好其工作区域的隔离和访问控制，并告知工作期间的安全要求。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十六条', '对第三方的逻辑访问必须严格控制访问权限，并对访问过程进行全程监控、详细记录和及时审计。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十七条', '对于外包软件开发，必须与软件开发商明确代码所有权和知识产权等条件，并签订保密协议，保证软件相关的文档、源程序、数据结构在软件的整个生命周期内不被非法访问和利用。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十八条', '本行应建立合理的软件外包验收机制，确保外包方能准确实现软件产品的功能和安全要求，并把整个外包过程纳入检查机制。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第五十九条', '信息科技部门应对外部审计所要访问的信息资产进行风险评估并采取有效控制措施，确保审计过程不影响信息资产的安全。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第六十条', '信息科技部门应对外包活动履行情况进行检查与评审，以确保外包方履行其安全责任和遵守安全要求。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第六十一条', '信息科技部门应对第三方或外包服务商提供的服务变更进行管理，包括保持和改进现有的信息安全方针策略、程序和控制措施，对涉及的业务系统、关键程序再次进行风险评估。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第七章   第三方与外包安全', '第六十二条', '应急事件发生时，应及时通知各相关合作伙伴、供应商协助参与应急处理。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第六十三条', '网络管理岗应将全行整个网络系统划分为相对独立的安全域，通过安全域的边界防护和安全域内的统一安全管理实现安全域内的网络安全。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第六十四条', '网络管理岗应根据网络安全域的安全级别和风险分析确定的安全需求来设计、实施网络安全方案，安全管理岗应定期组织对网络安全方案进行回顾，检查与实际系统的符合性。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第六十五条', '网络系统设计中应采取适当措施，尽可能减少网络崩溃和拥塞发生的几率，提高出现崩溃和拥塞后的系统恢复能力。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第六十六条', '网络系统应拥有完善的网管系统提供网络系统状态的实时监控和故障报警。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第六十七条', '网络管理岗必须对网络设备配置命令和信息的完整性、合理性及保密性进行验证。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第六十八条', '网络管理岗必须对所有当前使用和备用的网络设备建立清单并定期检查。对访问生产系统的外联网络设备应集中管理，应明确外联网络设备的使用方式并对其使用情况进行详细记录。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第六十九条', '必须进行规范化网络安全管理，管理内容详见《xxx银行网络安全管理办法》。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十条', '新建网络、网络改造或变更在投入使用前，网络管理岗应制定相应的网络安全防范措施。安全管理岗负责组织对新建网络或改造后网络实施安全检验，未通过检验不允许投产使用。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十一条', '严禁擅自将本行内网与外网直接连接，本行内网与外网连接的方案必须经信息科技部门负责人审批，在确保网络安全的前提下实施。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十二条', '办公网与生产网必须采取严格的隔离措施。对于目前尚无法做到彻底隔离的单位，须采取控制措施。外来人员严禁通过办公网访问生产网。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十三条', '严格管理 TELNET、PC ANYWHERE 和 WINDOWS 终端服务、X-WINDOWS 等远程访问生产系统方式的使用，使用前应经过安全管理岗审批，限制在只可以从安全的网络进行访问并对系统进行安全配置和记录日志。安全管理岗应定期进行检查。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十四条', '对路由器、交换机等通讯设备必须采取严格的管理措施，未经批准不得随意移动和接入。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十五条', '各单位的无线网络建设、无线网络可访问的资源和适用人员范围必须通过信息科技部门审批，无线网络必须采用认证和加密传输等安全技术，并严格按照相关网络技术规范进行建设。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十六条', '各单位不得建设  Internet  移动办公系统。Internet 移动办公系统（VPN）可访问的资源和适用人员范围必须通过信息科技部门审批，各单位 VPN 系统的访问范围和权限如需变更，必须对存在的风险进行评估，并将风险评估报告和详细变更方案报信息科技管理委员会审批，未经批准不得擅自变更。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十七条', '网络设备用户访问网络资源须经过安全认证、合理授权和日志记录。网络管理岗应定期对网络设备用户进行检查。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十八条', '防火墙策略的制定要遵循最小授权原则和除非明确允许禁止所有其他访问的原则，只开放必须的地址、协议和端口。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第七十九条', '利用防火墙、防病毒、安全漏洞扫描、网络非法外联、网络入侵监测软件和工具对获得的信息进行分析时，如发现安全事件，必须按照规定及时处理和报告，并对其日志进行保存和审计。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十条', '对于外单位需临时从外部网络拨入本行网络的情况，必须遵循以下管理要求：1.安全管理岗与网络管理岗必须对拨入的必要性、可行性和拨入存在的风险进行评估。2.拨入前，必须采取必要的隔离和安全控制措施，确保与生产环境的完全隔离，并遵循访问权限和范围的最小化原则。远程拨号接入方式要采取安全方式，确保所有活动的可监控和可审计。3.拨入过程中，要采取严密监控措施，进行实时监控，要按照既定变更计划控制访问范围和使用时间，防范非法操作。4.在远程工作完成后，要及时采取物理和逻辑方式断开临行性连接，修改必要的用户权限和配置，防止出现未经授权的再次访问。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十一条', '严禁将含有涉及本行商业秘密和有关重要业务信息数据的计算机与互联网连接，严禁用于上网的计算机保留涉及本行秘密和有关业务信息的数据。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十二条', '对网络设备和通信线路应进行定期的检测和维护，确保随时处于可用状态。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十三条', '必须建立、健全电子邮件、电话、传真、视频会议等办公系统的安全使用制度，并实施必要的安全控制措施，确保办公系统的安全。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十四条', '不得在本行信息交流群、网络邮件系统中发布、传递与业务无关的信息，及时删除垃圾邮件，不得利用网络系统从事危害本行信息系统安全的活动。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十五条', '重要专用邮箱的使用应遵循以下安全控制要求：1.必须安排专人管理和维护重要专用邮箱，限制无关人员对其进行物理访问。2.发送重要邮件必须经过审批，并进行记录。3.发送重要邮件的网关/服务器必须配置防止非授权进入内网的机制。其服务器只能发送或接收邮件，不能提供访问网络的途径。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十六条', '必须明确员工在使用电话等语音设备时的流程及职责，保护交换机设备不被非法使用和重新配置。必须保证 IP 电话的安全，防止被窃听。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十七条', '视频会议设备必须安排专人负责管理和维护；在公网使用视频会议必须采用加密传输保证视频会议的安全性；视频会议应采用集中控制方式，禁止终端主动呼叫参会。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第八章   网络与通信安全', '第八十八条', '应对机要传真的使用进行安全控制。配备专门的机要传真机管理和维护人员，发送机要传真需要经过审批，机要传真网关/服务器必须配置防止非授权进入内网的机制，在没有经过安全控制下，传真不能携带高/中安全要求的信息，制定保护机要传真数据的机密性和完整性的措施，进/出的业务传真应该进行记录。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第八十九条', '本行应建立适当的移动设备安全管理程序, 以防止文件、计算机媒体（如磁带、磁盘）、输入/输出数据和系统文件遭受未授权泄露、修改、删除和破坏。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第九十条', '对移动计算机、手机、通讯系统等远程设备设计保护手段，防止移动设备丢失时造成数据丢失或非法接入。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第九十一条', '防止外部移动设备非法接入内网，需经过严格审批后方可接入。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第九十二条', '信息科技部门应建立移动设备处置机制，防止移动设备在送修、维护、废止时，敏感信息泄露给未授权人员。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第九十三条', '在移动设备送修时，对其上的数据进行删除覆盖处理，防止信息泄露。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第九十四条', '信息科技部门应建立信息处理及存储程序，以防止信息未授权泄露。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第九十五条', '信息科技部门应建立系统文件访问权限管理，以保护系统文件安全。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第九章   移动设备安全', '第九十六条', '移动工作终端应该设置超时保护机制。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第一条', '项目与工程安全'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第九十七条', '在项目立项前，根据项目所涉及的信息资产的安全等级、项目范围及项目类别，定义项目的安全目标。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第九十八条', '根据项目的安全目标，从安全风险管理的角度对项目进行安全风险分析，识别项目所面临的威胁、评估项目的脆弱性、确定发生风险的可能性、明确风险的影响程度，确定风险等级，形成风险分析报告，以确定项目的安全需求。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第九十九条', '应根据项目范围、目标、类别，确定项目实施部门、人员，明确其职责；根据安全需求分析，综合考虑实现成本、技术成熟性、可操作性、实用性等，制定安全设计方案，明确安全控制措施及所采取的安全技术。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第一百条', '根据设计方案的要求，对使用的软硬件产品进行选型，并对产品进行功能、性能、压力、支持环境等方面的测试，最终确定具体采用的产品。第一百零一条 在编制实施方案时，方案必须考虑到实施过程中的风险，必须包含详细的实施步骤、应急方案、测试方案和项目实施计划。第一百零二条 在项目实施过程中如需变更，必须制定明确的变更方案提交相关部门审批后，严格按照变更方案实施变更，确保项目变更的安全。变更方案包括变更的原因、变更设计、变更的风险，变更后的剩余风险、变更对项目的影响，变更的测试内容，变更实施步骤、资源要求，变更的时间安排及回退方案等内容。第一百零三条 在项目实施过程中必须进行安全管理，明确实施过程中的各种活动和操作的程序及职责，并形成文件。第一百零四条 项目实施过程中必须由独立部门根据工程安全要求监控项目建设和实施过程，降低实施风险，保证项目安全有效的完成。第一百零五条 项目实施完成后，必须根据项目验收流程，对项目进行必要的测试和评定并形成验收报告。第一百零六条 在项目进行过程中，当项目工程的需求不再存在或被新的方案、产品所替代时，经相关部门审批，在确保不对本行的安全生产和业务持续性产生影响的条件下可废止项目。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第二条', '应用系统开发与支持第一百零七条 应用系统开发必须符合项目与工程安全控制规范，应用系统开发整个过程都必须在严密的监督下进行，保证应用系统的安全。所采用的技术和方案，必须保护其信息资产的安全。第一百零八条 在应用需求调研阶段，应依据风险分析的结果明确所有的安全需求并形成文档，已确定的安全需求应体现在应用系统设计中。安全需求应包括身份鉴别、访问控制、数据安全性、交易安全性、日志生成和分析、容量需求等方面。第一百零九条  在应用系统安全设计阶段必须对安全需求进行详细的分析，并将已确定的安全需求体现于应用系统设计中。第一百一十条 在应用系统设计中必须建立访问控制机制，确保可以对系统信息和系统功能的访问进行授权，并且保证只有经过授权的访问才能进行。第一百一十一条 在应用系统编码阶段，应规范编码，并加强代码安全检查，以减少由于编码不规范给系统带来安全隐患。第一百一十二条 必须对完成的应用系统进行安全测试，检查所有的安全设计是否得到实现以及应用系统所存在的其它脆弱性。第一百一十三条 应用系统必须通过安全测试之后才能上线。第一百一十四条  必须建立代码安全管理机制，对代码进行统一管理，做好代码的登记、备份、存放、访问、变更、作废等整个生命周期的安全管理。第一百一十五条 对投入运营的应用系统，要具备开发和测试环境，同时开发部门要成立专门技术团队，保持对应用系统代码的升级维护能力和对使用部门的及时响应能力。第一百一十六条 在对投产后应用系统技术支持时，如涉及变更和升级需求，必须严格按照相关管理规定实施。第一百一十七条 投入运营的应用系统，开发部门必须对故障记录和维护记录建立规范的档案。第一百一十八条 必须为应用系统编写管理文档和使用文档，管理文档和使用文档中必须包含正确管理和使用应用系统的方法，并帮助应用系统的管理和使用人员正确理解应用系统提供的安全功能，及紧急情况下所应采取的措施。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第三条', '主机及系统开发与支持第一百一十九条 主机及系统的安全设计必须符合应用系统的安全要求以及主机及系统的安全技术标准，运行关键业务的主机及系统还应考虑性能、冗余、灾备、系统容量、资源分配等。第一百二十条 系统安全实施部门应按照主机及系统安全规范和标准对所负责的主机及系统进行安全配置，完成后应将配置信息文档化，配置的修改必须严格遵循审批流程。第一百二十一条 安全管理岗应定期组织进行安全标准的符合性检查。第一百二十二条 安全管理岗应组织制定主机及系统的命名规范并定期检查落实情况，系统安全实施部门负责命名规范的执行。第一百二十三条 主机及系统必须在合理的访问控制机制下运行，用户对主机及系统资源的访问，必须进行身份认证和授权，用户的权限分配应该遵循最小授权原则并做到角色分离。第一百二十四条 对于运行关键业务的主机及系统只允许通过安全的客户端登录程序从安全的网络节点进行访问，用户口令不能采用明文方式在认证请求端和验证端之间传输。第一百二十五条 用户登录主机及系统没有完全成功时，终端不能显示任何系统标识的信息；在执行操作之前，主机及系统应该有明显的警示信息。第一百二十六条 用户登录过程中，主机及系统不能提供任何帮助消息，以免被非法用户利用；只有在用户输入所有登录数据后，主机及系统才验证登录信息；主机及系统只显示验证结果，不对登录数据进行任何提示性输出；口令或其它认证方式的变更应当在系统认证成功之后才能进行；登录成功后提示用户上次登录日期。第一百二十七条 主机及系统应具备警示功能：在执行操作前，所有对主机及系统的访问都必须向用户提供示警信息。第一百二十八条 安全管理岗应开启主机及系统的用户密码复杂度功能，设置安全的用户密码；严禁多人同时具有同一用户的使用权限；限制登录程序所允许的时限，如超时系统将终止登录过程；应定期对用户进行核对，清理冗余用户。第一百二十九条 安全管理岗应严格管理生产主机及系统超级用户的使用，使用前应经过审批，安全管理岗应定期对超级用户的使用进行检查。第一百三十条 主机及系统软件安装时应采用最小安装方式，只安装必须的授权软件，只开启必须的系统服务和功能。第一百三十一条 各单位和部门必须使用本行统一的防病毒软件，严禁自行安装其他防病毒软件。及时进行防病毒软件的升级工作。总行安全管理岗及网点信息安全员要及时开展防病毒管理工作的监督和检查，对病毒感染事件，及时处理并上报。第一百三十二条 安全管理岗应及时进行操作系统补丁程序的测试、发布和安装工作。第一百三十三条 安全管理岗应定期进行主机及系统的漏洞扫描工作，及时发现安全漏洞并督促相关部门整改。第一百三十四条 安全管理岗应对关键业务的主机及系统采取监控措施，监控主机及系统状态，记录异常状态，处理主机及系统报警，且对监控活动结果进行定期检查。第一百三十五条 必须对主机及系统的日志进行安全有效的管理。必须创建记录异常事件和安全相关事件的日志，并按照规定的保留期限进行保留。有证据收集方面需求的日志或其它重要日志必须记录归档，必须安全地存储在介质中并定期备份。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第四条', '软件安全管理第一百三十六条	必须对软件进行清单登记管理，并进行复查。登记内容主要包括：来源、使用范围、软件版本、版权、使用情况等。第一百三十七条 各单位的计算机必须使用本行授权的操作系统、数据库、应用软件和工具软件。第一百三十八条 各单位的生产、开发、测试及办公计算机必须安装、使用正版软件产品，禁止各类非授权软件在本行的使用。第一百三十九条 必须对软件（商业软件、自主开发软件、下载软件）获得途径进行严格的管理。第一百四十条 软件在安装前必须经过严格的测试，安装时必须通过安全控制，安装后必须进行安全性检查。第一百四十一条 对于本行下发的各类应用软件和工具软件（包括注册码、用户帐户和密码等信息），严禁向软件产品发布范围以外的单位或个人提供。第一百四十二条 各单位要建立辖内外部软件使用情况监督检查机制，加强日常例行化检查工作，切实杜绝各类非授权软件在本行的使用。第一百四十三条 对因使用非授权软件给本行带来不良影响的，将追究相关单位和人员的责任。第一百四十四条 原则上不能安装任何个人软件，如果有特殊需求必须经过严格的审批程序。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第五条', '安全技术及使用第一百四十五条	安全技术包括防病毒、漏洞扫描、入侵监测、拨号外联监测、集中用户管理等。第一百四十六条	各单位必须根据信息科技部门的部署，安装统一的防火墙、防病毒、拨号外联监测、入侵监测、安全漏洞扫描等安全产品。第一百四十七条	使用各类安全技术前应报送本行安全管理岗审批，严禁擅自使用。第一百四十八条	安全技术的使用和管理应制定相应的使用手册、工作流程和工作计划。第一百四十九条	对生产系统可能造成影响的安全技术，在使用前应经过严格的审批，并与相关部门共同确定影响最小的时间段和安全使用的方法。第一百五十条 严格保管安全技术的硬件设备、软件介质、使用记录和问题报告，未经批准严禁扩散使用范围和泄露敏感信息。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十章   信息系统管理', '第六条', '密码技术与管理第一百五十一条	信息系统安全设计和安全管理过程中，应该充分使用现代密码技术提供的安全功能来保护信息资产安全并降低信息系统风险；密码技术的选择和使用必须符合国家密码技术相关的规定和技术标准。第一百五十二条	密码技术的确定应有相应的专家或组织来评审确定。第一百五十三条	确定加密使用标准，以指导行内密码技术的使用。加密使用标准应该包含以下内容：1.加密技术所使用的环境和场合；2.允许使用的加密技术种类和密钥长度；3.密钥的管理。第一百五十四条	选择加密技术应遵循以下要求：1.根据信息系统风险评估结果，选择密码技术（如数字签名、抗抵赖、数字证书、加密等）和恰当的密码算法；2.选用密码算法或机制应该遵循国家及本行相关的法律、法规和标准；3.在项目需求分析及总体方案确定阶段，需根据保护的信息类型、信息的使用方式及保护的时间段来选定相应强度的加密机制；4.密码算法的选择和强度的确定应该维持“风险－安全－投资”的平衡关系；5.选用的加密硬件必须符合相关的法律、法规，并达到相应的国际、国家或行业标准。第一百五十五条	使用加密技术应遵循以下要求：1.当敏感的或重要信息的完整性与真实性受到威胁时，应该使用数字签名机制；2.当敏感的或重要信息的机密性受到威胁时，应该使用加密机制；3.当对事件或行为的发生或未发生可能产生纠纷时，应该使用抗抵赖技术；4.当使用数字签名的时候，应当考虑相关的法规，使数字签名具有法律约束力；5.用于数字签名的密钥应当区别于用于加密的密钥。'),
(NULL, '信息科技部', '《信息安全管理办法》', '第十五章	监督检查', '第二百条', '信息安全管理岗应配合各级监管机构及相关部门的检查。第二百零一条 安全管理岗应明确监督检查的对象、内容、周期、实施岗位、配合部门和交付件模板，形成监督实施方案，并以此为依据，具体实施监督检查工作。第二百零二条 安全管理岗在实施监督检查工作过程中，要保留必要的工作记录，整理监督检查报告，并落实整改。第二百零三条 监督检查应选择对信息系统运行影响最小的方式和时间进行，相关部门应做好配合。第二百零四条 必须对监督检查工具的使用进行控制，防止滥用。第二百零五条 对于安全性要求高的信息系统应由独立的第三方安全服务机构进行专业安全测试，促进信息系统安全体系进一步完善。第二百零六条 安全管理岗应定期组织进行信息安全内部自查活动。第二百零七条 安全管理岗应从问题发现、问题分类、问题报告、整改实施、追踪反馈等方面建立健全安全问题整改工作机制，督促责任部门采取整改和防范措施，消除问题直接和潜在风险隐患。第二百零八条 各类安全问题的整改，必须在确认整改方案有效后才能实施。第二百零九条 通过定期组织生产、测试、研发等环节事故、问题的责任分析和鉴定，以查找安全管理中的漏洞和薄弱环节，不断提高各级部门和人员的安全控制意识，防范问题的再次发生。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第一章  总 则', '第一条', '为规范xxx银行（以下简称“本行”）信息科技外包活动，加强信息科技风险管理，依据银监会颁布的《商业银行信息科技风险管理指引》、《银行业金融机构信息科技外包风险监管指引》及省联社相关制度，特制定本办法。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第一章  总 则', '第二条', '信息科技外包是指农商行将原本由自身负责处理的信息科技活动委托给服务提供商进行处理的行为，包含项目外包、人力资源外包等形式。本办法适用于以下信息科技外包：1.基础设施类外包，指外包服务商提供机房、线路及配套等基础设施服务；2.系统服务类外包，指外包服务商提供信息系统日常运行和维护等服务工作；3.软件开发类外包，指外包服务商提供专业技术人员协助本行完成指定功能应用或管理系统的开发工作；4.电子设备服务类外包，指外包服务商提供 ATM 等自助设备的布放和运维、监控设备和终端设备运维等服务工作；5.其他信息科技外包。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第一章  总 则', '第三条', '外包应以满足需求、保证质量、提高效率、风险可控、成本可控为基本原则。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第一章  总 则', '第四条', '本行信息科技的战略管理、核心管理以及内部审计等职能不宜外包。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第二章  组织架构及职责', '第五条', '本行外包管理的组织架构包括信息科技管理委员会及下设信息科技外包管理责任小组，其中信息科技外包管理责任小组组成成员主要包括信息科技部、风险管理部、合规管理部、审计部、办公室等部门。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第二章  组织架构及职责', '第六条', '信息科技管理委员会以及下设信息科技外包管理责任小组的职责按《关于成立xxx银行信息科技外包管理责任小组的通知》（xxx银行行办[2016]128 号）执行。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第二章  组织架构及职责', '第七条', '信息科技部的职责主要包括以下方面：1.信息科技部门建立外包执行团队，执行团队包括内控管理岗、信息科技外包项目经理、信息安全管理岗。2.根据本行发展规划或外包服务需求，结合本行的建设情况，确立外包项目的范围和内容，制定外包年度计划及外包阶段性计划；3.配合完成外包活动的合同签署以及外包服务技术指标的制定等工作；4.负责外包服务商在本部门工作的日常管理，包括现场管理、合同履约、合同验收及风险防控，定期提交外包管理工作报告；5.负责对外包服务商履约情况及遵守现场工作秩序情况进行监督检查，对发现的问题提出风险警示；6.根据本行要求提出外包管理的优化和改进方案；7.在发现外包服务商的业务活动存在缺陷时，采取及时有效的措施；8.信息科技管理委员会确定的其他职责。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第二章  组织架构及职责', '第八条', '风险管理部的职责主要包括以下方面：1.负责外包风险状况的监督及风险管理；2.向信息科技管理委员会提出有关外包活动发展和风险管控的意见和建议；3.根据信息科技部提交的外包项目情况报告，及时发现信息科技外包风险，并进行风险提示；4.定期向信息科技管理委员会提交外包风险评估报告；5.信息科技管理委员会确定的其他职责。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第二章  组织架构及职责', '第九条', '合规管理部的职责主要包括以下方面：1.负责外包合同签署前的审查与核对；2.信息科技管理委员会确定的其他职责。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第二章  组织架构及职责', '第十条', '审计部的职责主要包括以下方面：1.负责对外包服务商的资质进行尽职调查，定期安排内部审计，确保审计范围涵盖所有外包安排；2.信息科技管理委员会确定的其他职责。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第二章  组织架构及职责', '第十一条', '办公室的职责主要包括以下方面：负责做好外包人员的食宿安排及行为管理；信息科技管理委员会确定的其他职责。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第三章  外包服务商资质评审', '第十二条', '外包服务商的资质评审由本行信息科技管理委员会负责统一组织，原则上每年评审一次，特殊情况可根据实际需要安排评审。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第三章  外包服务商资质评审', '第十三条', '提供外包服务的外包服务商必须符合资质要求，不符合资质要求的外包服务商不准参与外包服务。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第三章  外包服务商资质评审', '第十四条', '开展外包服务商资质评审前必须对外包服务商进行尽职调查，并形成尽职调查报告。对外包服务商的尽职调查主要包括以下内容：1.管理能力和行业地位；2.财务稳健性；3.经营声誉和企业文化；4.技术实力和服务质量；5.突发事件应对能力；6.对银行业的熟悉程度；7.对其他银行业金融机构提供服务的情况；8.本行认为重要的其他事项。如果外包活动涉及多个外包服务商时，应当对这些外包服务商进行关联关系的调查。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第三章  外包服务商资质评审', '第十五条', '外包服务商资质评审活动由信息科技部牵头实施，风险管理部、合规管理部、审计部配合落实。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第四章  外包合同', '第十六条', '本行信息科技外包项目立项前，必须实施风险评估，明确双方的权利义务。评估结果为风险可控后方可签订合同或协议，应当包括但不限于以下内容：1.外包服务的范围和标准；2.外包服务的保密性和安全性的安排；3.外包服务的业务连续性的安排以及外包服务商提供专属资源的承诺；4.外包服务的监督、检查和考核；5.外包争端的解决机制；6.合同或协议变更或终止的过渡安排；7.担保和损失赔偿以及违约责任。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第四章  外包合同', '第十七条', '签订外包合同时外包服务商须承诺以下事项：1.定期通报外包活动的有关事项；2.及时通报外包活动的突发性事件；3.配合本行接受银行业监督管理机构的检查；4.保障客户信息的安全性，当客户信息不安全或客户权利受到影响时，本行有权随时终止外包合同；5.遵守本行有关信息科技风险管理制度和流程；6.第三方供应商出现问题时，保证软硬件持续可用的相关措施；7.不得以本行的名义开展活动；8.本行认为应当承诺的其他事项。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第四章  外包合同', '第十八条', '本行外包合同由信息科技部上报合规管理部法务审查岗审核，合规管理部总经理核对，分管行长审查，行长批准，办公室用印。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第五章  风险管理', '第十九条', '信息科技外包管理责任小组要切实加强信息科技相关外包管理工作，确保本行客户资料等敏感信息的安全，应采取包括但不限于以下风险管控措施：1.本行应按“必需知道”和“最小授权”原则对外包服务商相关人员的系统登陆权限、操作权限、文件读写权限等进行严格控制，授权管理工作要求及流程要制定成文，授权的书面审批或电子审批过程应当保持原始记载，并由专人负责收集、归档、管理；2.应当采取技术手段实现研发数据（含客户资料，下同）与外包服务商自带数据的有效隔离，外包人员未经批准不得将 U 盘、移动硬盘等带有存储功能的介质接入开发测试环境，严禁外包服务商下载、复制或截取本行的相关数据资料；3.本行在批准外包服务商使用本行客户资料等数据时，应当先对数据进行技术处理和过滤，严禁将“裸数据”交由外包服务商进行开发测试；4.外包服务商应保证其相关人员遵守本行信息科技风险的相关管理制度和保密规定；5.将涉及本行客户资料的外包作为重要外包；6.严格禁止外包服务商对外转包，采取足够措施确保相关信息的安全；7.信息科技外包项目文档全面详实，并由外包执行团队负责审查后归档；8.信息科技外包项目文档由专人规范管理，调阅有审批记录；9.信息科技部应对外包开发商提供的软件源代码进行安全检查。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第五章  风险管理', '第二十条', '关注外包服务商分包风险，若外包活动存在分包情况的，须在合同中明确以下事项：1.外包服务商分包的规则；2.分包服务提供商应当严格遵守主服务提供商与本行确定的外包合同或协议中的相关条款；3.主服务提供商应当确认在业务分包后继续保证对服务水平和系统控制负总责；4.不得将外包活动的主要业务分包；5.不得将外包活动转包或变相转包。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第五章  风险管理', '第二十一条', '信息科技部负责制定和建立外包突发事件应急预案和机制，以应对外包服务商在服务中可能出现的重大缺失。尤其是需要考虑外包服务商的重大资源损失、重大财物损失和重要人员的变动，以及外包合同或协议的意外终止等情况。通过采取替代方案、寻求合同项下的保险安排等措施，确保业务活动的正常经营。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第五章  风险管理', '第二十二条', '风险管理部负责对外包活动进行全面风险评估，审计部负责对外包活动进行审计。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第五章  风险管理', '第二十三条', '本行在开展外包活动时如遇到对本行业务经营、客户信息安全、声誉等产生重大影响事件，应及时向监管部门报告。本行在实施重要外包(如数据中心和信息科技基础设施等）应格外谨慎，在准备实施重要外包时应以书面材料正式报告省联社及监管部门。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第二十四条', '外包服务商需要明确一名项目经理（或项目负责人）负责协调项目相关重要事项。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第二十五条', '本行对于外包人员的管理方式以管理外包服务商项目经理为主，也可以根据实际需要直接管理和调配外包人员。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第二十六条', '外包人员在本行服务期间，应严格遵守本行作息考勤制度以及其他工作规范。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第二十七条', '信息科技部负责对外包人员使用环境和权限配置管理，对使用环境中的系统权限、文件读写权限必须严格控制，以满足工作需要为前提，遵循权限最小化原则，不得授予与工作无关的权限。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第二十八条', '对于向外包人员提供本行内部资料必须严格审核，严禁未经授权提供。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第二十九条', '外包单位人员应经信息科技部向办公室申请登记后，在正常工作时间访问办公区域及办公电脑。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第三十条', '外包单位人员访问受控区域或设备须提出书面申请，批准后由信息科技部专人全程陪同，并登记备案，受控区域为本行机房及其它重要区域。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第六章  外包人员管理', '第三十一条', '除已发生事故外，外包人员严禁在生产时间进行可能影响对外服务的变更维护操作。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第七章  外包服务的交付', '第三十二条', '外包人员应按外包合同要求交付服务工作成果。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第七章  外包服务的交付', '第三十三条', '开发类外包人员的交付物必须经过在测试环境下的严格测试，验收合格后才可以投产试用。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第七章  外包服务的交付', '第三十四条', '对于外包交付物验收不合格的，应要求外包服务商重新提供产品，并重新组织验收，直到验收通过，并纳入外包服务商考核评价指标。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第七章  外包服务的交付', '第三十五条', '由于外包服务商原因导致未按计划完成或完成项目质量不高，并造成一定影响的，作为不良合作记录登记，对未完成服务由外包服务商继续完成，并不再追加任何费用。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第八章  外包交付物管理', '第三十六条', '对于外包人员提交的源代码，须经信息科技部人员审核编译。所产生的执行程序，须经本行相关人员测试通过后，按规定流程投入生产系统。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第八章  外包交付物管理', '第三十七条', '对于外包开发人员提交的关键代码（涉及业务系统核心处理流程、记账或有关安全加密、认证的代码等），信息科技部须对源代码进行重点抽查，并记录抽查结果，存档保留。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第八章  外包交付物管理', '第三十八条', '对于外包测试人员提交的测试方案和测试案例，信息科技部须组织人员进行复核确认；外包测试人员编写的测试脚本和测试用程序必须满足项目测试性能要求。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第八章  外包交付物管理', '第三十九条', '外包咨询人员应及时对本行的咨询要求做出响应，按时协助解决问题，或提供符合要求的咨询建议或报告。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第八章  外包交付物管理', '第四十条', '外包维护人员必须及时响应业务需求，并按本行要求提供服务。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第九章  外包评价与审计', '第四十一条', '信息科技部负责组织制定对外包服务商考核评价的量化指标，根据量化的考核指标，每半年进行一次对外包服务提供商进行安全检查和服务评价，获取服务提供商自评估或行方评估报告，由信息科技部及审计部评审外包方提供的服务报告，对外包方履职情况进行审计。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第九章  外包评价与审计', '第四十二条', '考核评价结果将作为外包服务商资质评审的重要依据。外包服务商如存在考核不合格的情况，未来三年内不得参与本行外包服务。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第九章  外包评价与审计', '第四十三条', '审计部负责定期对信息科技外包活动进行审计。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第九章  外包评价与审计', '第四十四条', '在外包服务过程中，依据服务水平设定的目标，对外包服务提供商的服务水平进行持续监控，监控中发现问题，外包服务提供商能及时进行整改，若不能及时整改则扣减相应考核指标分数。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第十章  外包商准入', '第四十五条', '外包商的准入条件：1.外包方公司管理完善，财务、科技实力雄厚，诚信良好，有稳定的售后服务；2.网络、服务器等硬件系统外包方须持有相关设备的金牌代理或相应级别资质；3.外包方及相关项目经理须实施过银行系统内同等规模不少于3 个成功项目，杜绝借壳投标情况，通过合同明确特定的项目经理在项目施工时的驻场时间；4.外包方需具备较强的发展能力，在行业内有一定的市场份额；5.外包方需具备较强的故障、意外事件和突发事件的担当能力，对因外包没有履行职责造成的损失具有经济承担能力；6.外包方所开发的软件应符合相关法律、法规和软件开发规范，未侵犯他人的著作权，应提供相关专属资源的承诺；7.外包方提供的软件中涉密部分必须有国家要求的相关资质。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第十章  外包商准入', '第四十六条', '外包商需提交相关的资质资料，所有资料需加盖单位公章并保证真实有效。资质资料包括以下几项：1.营业执照副本；2.企业法人代表或委托授权人有效证件；3.外包商的专业资质、技术实力及其从业人员的履历和专业技能资料；4.其他能证明外包商实力的资信文件。以上资料审核通过后，由本行信息科技外包管理责任小组进行综合评定，选择经营状况、财务实力、诚信历史、安全资质、技术服务能力和责任承担能力比较强的外包商，进入合格外包商名单'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第十一章	外包商退出', '第四十七条', '根据外包商的外包服务水平、竞争力及供需双方的依赖程度,将外包商分为:A 级外包商、B 级外包商、C 级外包商。1.A 级外包商：能够长期稳定的提供安全的信息技术服务，对需求方的工作具有全局性或局部战略需求的影响，生产资源优势明显。2.B 级外包商：在信息技术服务方面能够达到基本满足。3.C 级外包商：能够满足合同约定的当前运作要求，在信息技术服务方面偶尔会出现小的波动或不稳定的情况。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第十一章	外包商退出', '第四十八条', '根据外包商 ABC 三级管理。凡出现以下情况考虑退出：1．连续两年被评定为 C 级外包商的，信息科技外包管理责任小组应积极寻找更好的外包商逐一替代。对确因资源紧张不得不使用的Ｃ级外包商，要通过制定纠正预防措施，改进信息技术服务质量；2．信息技术服务中出现问题,整改措施屡次得不到实施,且信息技术服务质量得不到提高,不能满足本行技术和安全要求的；3．凡发生欺诈、违约、失信等不法行为，造成影响恶劣，且行为得不到纠正的。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第十二章	附 则', '第四十九条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《信息科技外包管理办法》', '第十二章	附 则', '第五十条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第一章	总	则', '第一条', '为预防或最大程度地减少信息科技突发事件给xxx银行（以下简称“本行”）带来的经济损失，提高本行信息科技水平和应急处理能力，防范信息科技风险，根据《中华人民共和国计算机信息系统安全保护条例》、《国家信息化领导小组关于加强网络与信息安全保障工作的意见》以及省联社、银监部门的要求，并结合《xxx银行计算机信息系统应急处置管理办法》，特制定本预案。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第一章	总	则', '第二条', '信息科技突发事件应急预案是针对本行科技信息系统运行期间可能出现的故障而建立的快速反应机制，确保系统出现故障后迅速恢复。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第一章	总	则', '第三条', '本预案所指的网络与信息安全突发事件，是指重要网络与信息系统突然遭受不可预知外力的破坏、毁损、故障，发生对社会、公众造成或者可能造成重大损失的紧急事件。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第二章 突发事件分类和分级', '第四条', '根据网络与信息安全突发公共事件的发生过程、性质和特征，网络安全突发事件是指自然灾害、事故灾难和人为破坏引起的网络与信息系统的损坏。具体的分类为：（一）自然灾害是指地震、台风、雷电、火灾、洪水等。（二）事故灾难是指电力中断、网络损坏或者是软件、硬件设备故障等。（三）人为破坏是指人为破坏网络线路、通信设施、黑客攻击、病毒攻击、恐怖袭击等事件。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第二章 突发事件分类和分级', '第五条', '根据信息科技突发事件的可控性、严重程度和影响范围，将信息科技突发事件分为四级：一级：最严重的级别。具有以下两个重要标志：一是全局性，二是特别紧急性。全局性指涉及到辖内所有网点或者大多数网点，全部或者大多数交易不正常且短时间内难以恢复。特别紧急性是指可能影响到人身安全或资金安全的情况。二级：较严重的级别。涉及部分主要设备、部分网点、所有网点的部分交易，以及虽然严重但短时间内能够处理的情况。同时还包括网点工作正常但主要设备出现严重问题的情况。三级：一般性的技术故障。部分网点的部分交易、个别网点的故障错误以及软、硬件的非严重性问题。四级：轻微的技术故障。个别的、非主要设备或者个别的数据、程序错误。信息科技突发事件分级对照表具体如下：信息科技突发事件分级对照表'),
(NULL, '信息科技部', '《信息科技应急预案》', '第三章 组织架构体系及其职责', '第六条', '建立应急处置团队，在发生灾难时，能够做到及时实施专项应急处置工作。应急处置团队包括应急处置领导小组、执行小组和保障小组。（一）应急处置领导小组领导小组成员：由组长、若干人员组成，组长由分管科技的副行长担任，成员由信息科技部、行政办公室、计划财务部、运营管理部、信贷管理部、授信管理部、合规管理部、风险管理部、审计部、安全保卫部等部门负责人组成。领导小组工作职责：1.制订应急管理制度，统一组织、协调、指导、检查本行信息系统突发事件应急管理工作。2.负责信息系统突发事件的应急指挥、组织协调和过程控制。3.审核并批准应急恢复预案的测试方案和应急演练方案。4.组织对应急演练预案结果进行评审。5.负责及时向董事会及高级管理层汇报本行信息系统应急及备份状况分析、应急恢复预案的实施情况以及应急演练的结果。6.灾难发生后，对应急恢复执行小组提交的解决方案及应急备份预警评估报告进行评估并发出灾难宣告。7.组织协调有关工作，并与银监部门、人民银行、其他金融机构就信息系统突发事件的处置进行信息交流和沟通。8．做好秩序维护、安全、咨询和后勤保障等工作，与电力、通讯、公安和消防等相关外部机构建立应急协调机制和应急联动机制。9.及时向省联社、yy银监分局汇报处置进程及应急处置的相关工作。10.负责其他信息系统应急备份及恢复的管理。（二）应急处置执行小组执行小组成员：由组长、若干人员组成，组长由分管信息科技的副行长担任，成员由信息科技部、行政办公室、计划财务部、运营管理部、审计部、安全保卫部等机关各部室负责人及信息科技部、外包服务公司技术人员组成。执行小组工作职责：1.信息系统应急恢复的需求分析。2.结合本行信息系统应急备份管理实际情况，制定、修改各信息系统应急子预案。3.应急备份系统运行维护、技术支持、检查、变更等。4.应急恢复预案的培训和应急。5.应急备份系统灾难预警、评估、汇报和解决方案的制定。6.灾难发生后信息系统和业务功能的恢复。7.灾难发生后风险评估报告的撰写。8.灾难发生后的外部协作。9.信息系统应急恢复的实施。（三）应急处置支持保障小组支持保障小组成员：由组长、若干人员组成，组长由分管信息科技的副行长担任，成员由行政办公室、计划财务部、合规管理部、人力资源部、安全保卫部等机关各部室负责人组成。支持保障小组工作职责：1.信息系统应急保障的需求分析。2.结合本行信息系统应急备份管理实际情况，负责联系涉及基础保障方面的第三方机构。3.参与应急恢复预案的培训和应急演练。4.参与应急备份系统灾难预警、评估、汇报和解决方案的制定。5.参与灾难发生后信息系统和业务功能的恢复。6.参与灾难发生后风险评估报告的撰写。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第七条', '相关部门人员要熟练掌握本部门的业务范围、软硬件故障检查、修复技能，每季度进行一次应急演练。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第八条', '信息科技部负责对每次安装的程序进行详细记录，对配置、参数调整前后的数据进行详细登记，对于配置、参数调整出现的问题须及时恢复。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第九条', '信息科技部负责对全部软件的安装介质、说明文档妥善保管，对系统每次更新、安装新程序之后要及时做好备份工作，同时妥善保管备份介质。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第十条', '信息科技部每日记录系统运行情况，在业务高峰期随时监控主机运行状况（如内存、交换空间、页面调度等），发现异常情况，及时向部门领导及分管行长反映和建议。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第十一条', '信息科技部负责备份全部网络设备的配置参数，对配置、参数调整前后的数据进行详细登记，对于配置、参数调整出现的问题须及时恢复。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第十二条', '信息科技部在调整参数配置、调整网络配置、安装程序时，应充分考虑到备份机的安装、调整，并在安装、调整日志上进行记载。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第十三条', '信息科技部在软件安装前应进行充分测试，每季度对数据库、日志等备份数据进行恢复测试。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第四章 预防和预警机制', '第十四条', '信息科技部负责严格执行业务数据备份管理办法，保证业务数据备份的完整性、可靠性。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第五章 应急程序和技术保障措施', '第十五条', '突发事件发生时，涉事部门和相关人员在领导小组组长组织下启动预案自救，同时上报领导小组，情况特别严重的，将有关情况上报上级管理部门和当地政府。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第五章 应急程序和技术保障措施', '第十六条', '应急处置团队对突发事件应急处置做好组织、协调工作，帮助各营业网点恢复正常营业秩序，并做好正面宣传报道工作。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第五章 应急程序和技术保障措施', '第十七条', '信息科技部应确保系统的计算机设备及网络系统有足够的软硬件技术保证，有关重要信息有完整备份。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第五章 应急程序和技术保障措施', '第十八条', '核心账务数据应实现异地备份，建立数据备份中心。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第五章 应急程序和技术保障措施', '第十九条', '加强应急处置人员的队伍建设,确保在任何情况下，都不会因人员的缺岗而影响整个系统的正常运行。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第五章 应急程序和技术保障措施', '第二十条', '保持和系统集成商、软件供应商、硬件供应商的紧密联系，确保其在突发事件后能在第一时间内能提供技术支持或赶赴现场。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第六章 责任追究及处罚', '第二十一条', '突发事件处置结束后，总行组织有关部门人员对事件的原因及全过程进行调查，根据对有关人员的责任调查结果，视情节轻重程度，追究相关人员的责任。可分别采取经济处罚、行政处分乃至移送司法机关处理等处罚措施。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第六章 责任追究及处罚', '第二十二条', '小组成员未按照相关规定履行相关职责的，依法给予行政处分；构成犯罪的，移交司法机关、依法追究刑事责任。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第六章 责任追究及处罚', '第二十三条', '对突发事件应急管理工作中作出突出贡献的成员，应予以表彰和奖励。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第七章	附	则', '第二十四条', '本预案由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第七章	附	则', '第二十五条', '本预案和《xxx银行计算机信息系统应急处置管理办法》互为补充，如与应急处置管理办法相冲突，以应急处置管理办法为准。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第七章	附	则', '第二十六条', '信息科技部制订计算机网络系统应急预案、外包服务管理应急预案、基础设施应急预案、业务系统应急预案、主机系统应急预案等相关子预案，作为对本预案的补充和细化。'),
(NULL, '信息科技部', '《信息科技应急预案》', '第七章	附	则', '第二十七条', '本预案自发文之日起施行。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第一章    总    则', '第一条', '为进一步加强xxx银行（以下简称“本行”）信息科技管理，提高信息科技风险防范能力，促进业务系统安全、持续、稳健运行，增强本行核心竞争力和可持续发展能力，根据《商业银行信息科技风险管理指引》等规定，制定本规则。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第一章    总    则', '第二条', '信息科技管理委员会是信息科技管理的研究、审议机构，受经营层委托，审议全行信息科技管理政策、制度流程和工作规划，检查监督信息科技风险管理和内部控制执行情况，研究决定有关信息科技风险管理和内部控制等重大事项。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第一章    总    则', '第三条', '概念释义（一）本规则所称信息科技是指计算机、通信、微电子和软件工程等现代信息技术，在商业银行业务交易处理、经营管理和内部控制等方面的应用，并包括进行信息科技治理，建立完整的管理组织架构，制订完善的管理制度和流程。（二）本规则所称信息科技风险，是指信息科技在商业银行运用过程中，由于自然因素、人为因素、技术漏洞和管理缺陷产生的操作、法律和声誉等风险。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第二章    组织机构', '第四条', '信息科技管理委员会由主任委员、副主任委员和委员若干人员组成，主任委员由本行行长担任，副主任委员由分管信息科技的副行长担任，委员由合规管理部、风险管理部、审计部、信贷管理部、运营管理部、电子银行部、三农业务部、信息科技部等部门负责人组成，同时邀请董事会办公室、监事会办公室部门负责人列席会议。信息科技管理委员会下设领导小组和办公室：领导小组包含信息安全管理工作领导小组、计算机信息系统应急处置领导小组、金融创新领导小组、业务需求管理责任小组、项目开发管理责任小组、信息科技外包管理责任小组；办公室作为信息科技管理委员会的办事机构设在信息科技部，负责信息科技管理委员会日常具体工作。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第三章    职责权限', '第五条', '信息科技管理委员会工作职责（一）负责审议信息科技工作年度计划和预算；（二）负责审议信息科技风险管理工作报告（风险管理部门牵头编制），报告中要包括风险管理、安全管理、业务连续性管理、外包管理等内容；（三）负责审议信息科技战略规划（信息科技部门牵头编制），确保与本单位经营管理规划一致，有效支撑业务和管理工作；（四）负责审议信息科技重大项目（三重一大）工作重要节点报告（立项、变更、投产等）（信息科技部门牵头编制）；（五）上述审议内容，要提交高级管理层集体研究后报告董事会决策；（六）负责审议批准信息科技工作建设原则、技术架构、主要制度；以及重要系统（对外、账务、实时系统）的运行维护、系统开发的重要节点报告（信息科技部门提供）；（七）负责审议批准全行（社）各层面（高管、中层、骨干、全行）人员的信息科技道德、意识、技能培训教育报告（计划、预算、成果等）（人力资源牵头、信息科技部门配合编制）；（八）负责审议批准启动重大信息安全事件应急处置预案；（九）上述审议批准内容，要报告高级管理层及董事会；（十）经营层授权的其他事项。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第三章    职责权限', '第六条', '信息科技管理委员会下设领导小组工作职责（一）信息安全管理工作领导小组工作职责1.负责贯彻、落实国家和银监部门以及省联社关于信息安全工作的方针、政策。2.负责协调本行信息安全管理工作，决策本行信息安全管理的重大事项。3.负责规划和设计本行计算机信息系统安全建设方案，做好信息安全规划，从安全管理、制度防范、技术防范三个方面，建立全行的安全体系。4.负责制定本行信息安全策略和原则，编列和修订信息安全管理办法。5.负责组织开展本行信息安全普及教育，提高全行人员的信息安全意识。6.负责监督检查各部门（支行）执行信息安全管理制度情况、按省联社和银监部门要求及时汇总和上报本行计算机信息系统安全报告。7.委员会交办的其他事项。（二）计算机信息系统应急处置领导小组工作职责1.负责组织制定全辖计算机信息系统应急处置预案和实施细则，并报董事会和高级管理层审定，统一组织、协调、指导、检查全辖应急处置预案的演练和管理。2.负责全辖计算机信息系统应急处置日常检查和管理。3.负责定期分析风险状况和总结应急处置管理成效，履行向董事会和高级管理层的报告职责。4.负责全辖信息系统突发事件的应急指挥、组织协调和过程控制。5.负责应急处置过程中的对外信息发布。6.负责宣布重大应急响应状态的降级或解除。7.负责向董事会和高级管理层报告应急处置进展情况和总结报告。8.负责履行向监管当局报告的职责。9.委员会交办的其他事项。（三）金融创新领导小组工作职责1.负责做好本行金融科技产品调研及创新工作。2.负责与省联社进行金融科技新产品的交流与沟通工作。3.负责向省联社提交本行金融科技新产品的详细需求工作。4.负责基于本行自主开发系统的金融科技新产品的开发工作。5.负责做好金融科技新产品的上线培训及推广工作。6.切实加强做好本行金融科技产品创新工作，提高信息科技运用水平。7.委员会交办的其他事项。（四）业务需求管理责任小组工作职责1.在需求的提出者与开发者之间建立对需求的共同理解，维护需求和最终系统的一致性，并控制需求的变更。2.召开需求确认会，对前期需求提出部门提出的需求与开发者进行确认，为某些有分歧的问题找到解决方案。3.需求变更要依据“变更申请-审批-更改-重新确认”的流程处理需求变更，防止需求变更失去控制而导致项目发生混乱。4.进行需求跟踪，对照后续开发系统和需求文档，确认系统开发完全依据需求文档开发。5.负责对需求提出部门提交的业务需求进行分析、整合、规范，并向开发者提交《业务需求书》、《业务需求变更文件》等相关文档。6.委员会交办的其他事项。（五）项目开发管理责任小组工作职责1.负责制定全面的项目开发管理制度、流程和指引，包括但不限于：项目的开发流程和组织管理、参与部门的职责划分、时间进度和财务预算管理、质量检测和风险评估等。2.负责明确项目实施管理的责任部门，对项目需求进行细化，组织项目开发、测试和验收。3.负责明确项目质量管理的责任部门，对项目建设过程中的质量进行管理的控制。4.负责明确项目经理岗位，以确保项目目标的实现，保持与业务部门良好的沟通，协调项目组成员之间的合作。5.负责明确项目试运行及项目推广的责任部门，解决项目试运行期间暴露的系统问题，根据试运行情况做好项目推广和上线有关工作。6. 负责明确项目后评价管理的责任部门，在项目上线 3 个月后，牵头组织负责对上线项目后评价工作，包括：产品对原先需求的实现程序、产品对用户体验友好性、产品市场效益等。7.委员会交办的其他事项。（六）信息科技外包管理责任小组工作职责信息科技外包管理责任小组下设外包执行团队，执行团队包括内控管理岗、信息科技外包项目经理、信息安全管理岗。 一、信息科技外包管理责任小组工作职责： （一）负责审核信息科技外包风险管理规章、制度； （二）负责审核信息科技外包预算； （三）负责开展外包服务商的选择评审和定期评审； （四）负责审核信息科技外包合同条款； （五）负责外包项目的审核； （六）负责特定情况下需要转包的审核。 二、内控管理岗工作职责： （一）负责对外包服务商的审计和监督； （二）负责监督外包商服务工作的流程、范围、内容和质量；（三）负责外包服务结束的管理和控制； （四）负责监督外包人员的岗位制约。 三、信息安全管理岗的工作职责： （一）负责将信息系统外包风险纳入总体安全策略和风险控制中； （二）负责监督外包服务过程中信息资产所面临的威胁和脆弱点的重大变化，对外包活动中出现的信息安全隐患及时提出控制措施。四、信息科技外包项目经理工作职责： （一）负责根据业务的发展和各职能部门的实际需要对外包项目进行可行性分析； （二）负责提出全行信息系统软件开发外包项目的申请； （三）负责组织审查外包商的经营状况、财务实力、诚信历史、安全资质、技术服务能力和责任承担能力； （四）负责审查外包商承包信息技术服务案例的真实性； （五）负责外包项目完成后的项目验收、检验、后评价工作； （六）负责在软件开发外包过程中配合外包商完成实施项目； （七）负责软件开发外包过程中及开发后，对外包商进行监督； （八）负责其他信息科技外包相关管理工作。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第三章    职责权限', '第七条', '信息科技管理委员会办公室的职责1.负责组织委员会会议的有关材料和文件，负责会议的记录，整理会议纪要。2.负责编制年度信息科技管理工作规划和风险报告，提交委员会审议，并根据委员会的决议负责委员会闭会期间日常的政策解释、管理指导等工作。3.贯彻执行信息科技管理委员会的各项决定和工作部署，组织、协调、推动全行的信息科技日常工作。4.加强信息科技管理调研工作，充分发挥参谋助手作用，为领导层提供有参考价值的信息科技管理信息，提高信息科技工作的预见性，为领导决策提供服务。5.组织开展信息科技委员会决定的各项检查活动。6.研究制订信息科技风险管理目标评价体系，确立正确的发展导向，引导全行在加快业务发展的同时加强信息科技风险管理。7.加强信息的沟通和反馈，指导各业务部门和分支机构开展信息科技工作。8.委员会交办的其他事项。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第四章   委员权利与义务', '第八条', '信息科技管理委员会委员权利（一）获取与信息科技业务发展及风险管理相关的信息。（二）向信息科技管理委员会提出议案。（三）参加信息科技管理委员会会议并对会议审议的事项独立发表意见。（四）对信息科技管理委员会讨论、审议的事项行使表决权。（五）其他权利。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第四章   委员权利与义务', '第九条', '信息科技管理委员会委员义务（一）按时出席会议。（二）按时提交对书面征询意见的反馈材料。（三）认真审议办公室提交的各项议案和相关材料。（四）在本机构范围内督促落实委员会审议通过的各项规范、办法、标准及其它事项。（五）严格遵守保密制度，对会议审议事项的审议情况及结果不得泄露。对于连续两次或累计三次无故缺席会议的委员，视为不适宜担任委员工作，由行长解聘后另行聘任。（六）其他义务。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第五章   议事规则与决策程序', '第十条', '议事规则（一）委员会会议根据需要召开，在会议召开前 3 天由委员会下设办公室通知全体委员。会议由主任委员主持。（二）委员会会议应由三分之二以上的委员出席方可举行；每一名委员有一票的表决权；会议做出的决议，必须经全体委员过半数通过。（三）委员会会议表决方式为举手表决或投票表决。（四）必要时亦可邀请本行其他领导和支行（中心）负责人参加委员会会议。（五）如有必要，委员会可以聘请有关机构为其决策提供专业意见。（六）委员会会议的召开程序、表决方式和会议通过的议案必须遵循有关法律、法规以及本行和上级管理部门相关规定。（七）委员会召开会议应当有会议记录，出席会议的委员应当在会议记录上签名，会议记录按照档案管理规定保存。（八）委员会会议通过的方案及表决结果，应以书面形式报本行经营层。（九）出席会议的人员均对会议所议事项有保密义务，不得擅自披露有关信息。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第五章   议事规则与决策程序', '第十一条', '决策程序（一）信息科技管理委员会办公室负责做好委员会决策前的准备工作，根据工作需要，提供有关方面的书面资料：信息科技战略规划报告；电子化建设重大项目立项和可行性分析报告；信息科技、风险和审计部门有关信息科技方面的工作报告；有关信息科技方面的领导小组的工作报告；其他需要准备工作事宜。（二）信息科技管理委员会在对相关报告进行评议后，应将书面决议材料呈报经营层讨论。（三）经营层讨论后，会办结果由总行有权审批人审批后执行；经营层权限内决定事项应同时报董事长、监事长备案。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第六章    附	   则', '第十二条', '本规则由xxx银行信息科技管理委员会办公室负责解释和修订。'),
(NULL, '信息科技部', '《信息科技管理委员会议事规则》', '第六章    附	   则', '第十三条', '本规则自发文之日起施行。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第一章    总    则', '第一条', '为保障xxx银行(以下简称“本行”)信息系统为客户提供稳定、高质量的服务，建立并完善信息科技问题管理流程。最小化信息科技问题造成的不利影响，防范类似问题的重复发生，寻求解决问题的措施，《中华人民共和国计算机信息系统安全保护条例》、《商业银行信息科技风险管理指引》结合本行实际，特制订本办法。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第一章    总    则', '第二条', '问题管理流程具有主动和被动两个方面。被动问题管理关注于解决问题以响应一个或多个事故。主动问题管理关注于在事故首次出现前就能识别和解决问题以及错误。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第一章    总    则', '第三条', '问题：一个或多个信息科技事故未知的底层原因。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第一章    总    则', '第四条', '已知错误：已经成功诊断出来的问题，即找到了事故的根本原因，并且为之定义了临时措施。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第一章    总    则', '第五条', '临时措施：避免事故或问题的方法，这种方法或者是暂时修复以恢复服务，或者是使客户避免使用已知有问题的服务。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第二章    职责与权限', '第六条', 'IT 服务台工作人员职责：（一）负责设计和维护问题管理数据库；（二）负责识别和记录问题；（三）负责跟踪问题的解决；（四）负责将已解决的问题添加至问题管理数据库。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第二章    职责与权限', '第七条', '运行保障人员职责：（一）负责提供（或推荐）事故/问题适合的临时措施；（二）负责确定问题的根本原因，提供解决方案；（三）负责排查和分析新问题产生原因；（四）负责提出新问题解决办法；（五） 新问题的临时处理措施向上级领导汇报，并申请批示。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第二章    职责与权限', '第八条', '软件开发人员职责：（一）负责处理运行保障人员无法解决的问题；（二）负责提供（或推荐）事故/问题适合的临时措施；（三）负责问题的根本原因，提供解决方案；（四）负责排查和分析新问题产生原因；（五）负责提出新问题解决办法。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第二章    职责与权限', '第九条', '联合小组联合小组由运行保障人员、软件开发人员和外协单位人员组成，负责协作解决问题。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第三章    基本规定', '第十条', '问题识别（一）事故管理流程将原因不明的事故（包括已经关闭但原因不明的事故）提交 IT 服务台工作人员；（二）IT 服务台工作人员将事故症状与问题管理数据库中的已知错误相匹配；（三）如果发现是由一个已知错误造成的，将该已知错误的临时措施提供给事故管理流程，以便迅速恢复服务；将事故与该已知错误关联；如果该已知错误存在解决方案，IT 服务台工作人员执行解决方案，消除事故；（四）如果发现是由一个已经存在的问题造成的，首先将事故与问题关联；如果该问题存在临时措施，向事故管理流程提供临时措施，以便迅速恢复服务。（五）产生新问题记录，写入问题管理数据库，并对问题分类，以确定优先级；将事故与问题关联；将新产生问题分配给相应的运行保障人员解决。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第三章    基本规定', '第十一条', '问题控制（一）运行保障人员对接收到的问题记录，借助于事故症状描述、服务视图首先进行初步诊断，确定有无临时措施可采用，如果有则先推荐可用的临时措施以恢复服务；（二）运行保障人员查找可能的故障组件，确定根本原因，必要时寻求供应商的支持；（三）在确定根本原因后，软件开发人员首先考虑开发快速而成本较低的临时措施以解决事故、恢复服务；IT 服务台工作人员负责将问题变为已知错误，并将临时措施提供给事故管理流程；（四）运行保障人员评估错误，确定最终解决方案；（五）如果执行解决方案需要实施变更，则由软件开发人员制作变更申请单，通过变更管理流程完成解决方案；（六）IT 服务台工作人员将相关的配置项、解决方案以及防止措施与已知错误关联，关闭问题；（七）通知事故管理流程，关闭未解决的相关事故，或修改使用临时措施关闭的事故状态。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第四章    操作流程', '第十二条', '问题管理操作流程（一）运行保障人员将事件、事故管理（未知原因）递交给 IT服务台工作人员；（二）IT 服务台工作人员接收事件、事故，并在问题管理库中对已知事故、事件进行匹配；（三）IT 服务台工作人员查看是否有存在与接收的事故、事件相匹配的解决方案：1．如果存在，按照方案解决相关问题，关闭问题流程；2．如果不存在，则将接收的事故、事件交由运行保障人员解决。（四）运行保障人员判断是否能够解决问题：1．若问题解决，则由 IT 服务台工作人员将该问题解决方案导入问题管理数据库；2．若问题无法解决，则交由软件开发人员进行问题解决。（五）软件开发人员判断是否能够解决问题：1．若问题解决，则由 IT 服务台工作人员将该问题解决方案导入问题管理数据库；2．若问题无法解决，则交由协作单位进行问题解决。（六）协作单位支持判断是否能够解决问题：1．若问题解决，则由 IT 服务台工作人员将该问题解决方案导入问题管理数据库；2．若问题无法解决，则交由联合小组进行问题解决，并由 IT服务台工作人员将该问题解决方案导入问题管理数据库。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第五章    附    则', '第十三条', '本办法由xxx银行信息科技部负责解释、修订。'),
(NULL, '信息科技部', '《信息科技问题管理办法》', '第五章    附    则', '第十四条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第一章  总  则', '第一条', '为加强xxx银行（以下简称“本行”）信息科技风险管理，实现风险识别、计量、监测和控制，依据《银行业金融机构全面风险管理指引》、《商业银行信息科技风险管理指引》（银监发〔2009〕19号）、《江苏省农村商业银行信息科技风险管理制度（试行）》及相关规定，制定本办法。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第一章  总  则', '第二条', '本办法所称的信息科技风险是指本行在信息科技运用过程中，由于自然因素、人为因素、技术漏洞和管理缺陷等原因，产生的操作、法律和声誉等风险。信息科技风险包括但不限于：（一）决策风险。具体体现为IT战略不能保持与业务战略的一致性，选择了不适合的信息科技路线。（二）安全风险。具体体现为信息安全事件造成信息资源或系统等机密性、完整性、可用性受损。（三）外包风险。具体体现为外包方选择失误，对外包方服务水平控制不力，缺乏有效监督及外包应急机制等。（四）业务连续性风险。具体体现为环境灾难事件、自然灾难事件、人为事件等导致系统故障或资产受损，造成服务中断或业务无法持续。（五）质量风险。具体体现为项目建设管理不规范，人为疏忽、不尽责导致信息系统设计、实现、部署、测试过程存在缺陷。（六）运行风险。具体体现为运行监控不到位、不规范，事件应急处置机制不成熟，运行事件造成服务、数据可用性受损。（七）合规风险。具体体现为不符合国家和行业的信息科技管理要求。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第一章  总  则', '第三条', '本办法适用于总行及各营业网点的信息科技风险管理。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第二章  组织及职责', '第四条', '董事会是信息科技风险管理最高决策机构，法定代表人是信息科技风险管理的第一责任人。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第二章  组织及职责', '第五条', '风险管理部门牵头开展信息科技风险管理工作。组织制定信息科技风险管理工作报告，经业务连续性管理委员会、高级管理层审议后，提交董事会决策。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第二章  组织及职责', '第六条', '内部审计部门进行独立有效的信息科技风险管理审计，对审计报告进行确认并落实整改。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第三章  制度制订及维护', '第七条', '风险管理部门牵头组织制订本单位信息科技风险管理制度体系，包括策略、制度、流程及表单。该体系应通过业务连续性管理委员会审议，经高级管理层集体研究后报送董事会决策批准。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第三章  制度制订及维护', '第八条', '信息科技风险管理制度体系包括但不限于以下内容：（一）科技治理。（二）风险管理。（三）安全管理。（四）项目管理。（五）运行管理。（六）业务连续性管理。（七）外包管理。（八）审计。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第三章  制度制订及维护', '第九条', '本行每年对信息科技风险管理制度体系进行修订维护。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第四章  科技风险评估及处置', '第十条', '评估工作（一）本行风险管理部门组织建立针对外部风险提示、重大项目的立项和投产环节的风险评估方法，并牵头实施。（二）建立重大项目在设计和运维环节的风险评估方法，并牵头实施。（三）建立信息科技风险管理体系的风险评估方法，并每年执行评估工作。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第四章  科技风险评估及处置', '第十一条', '处置工作（一）风险管理部门基于风险评估及信息科技审计结果，牵头组织制订风险处置管理要求，实施处置工作。（二）每年对信息科技风险管理体系评估所发现问题进行风险处置。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第五章  科技风险监测', '第十二条', '风险管理部门组织建立本行信息科技风险管理专项检查机制，并牵头实施检查工作。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第五章  科技风险监测', '第十三条', '风险管理部门组织建立信息科技风险监测数据的采集和报送机制，以及信息科技风险监测指标体系和分析方法。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第五章  科技风险监测', '第十四条', '定期实施信息科技风险数据的采集和分析。条件允许情况下可运用信息系统定期实施。监测范围至少包括：（一）信息科技项目（含科技外包）实施情况。（二）信息系统日常运行指标及状况。（三）运行环境下的操作风险。（四）信息安全事件。（五）信息科技服务投诉和事件形成的风险点。（六）内部审计、外部审计和监管发现问题及风险处置情况。（七）外包服务商服务水平协议完成情况。（八）新技术发展可能造成的影响和已使用软件面临的新威胁。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第六章  报告及培训', '第十五条', '风险管理部门牵头组织制订编写信息科技风险管理年度报告，提交业务连续性管理委员会和高级管理层审议后并入风险管理年度报告，提交董事会决策。根据管理要求，将年度报告报送行业管理机构。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第六章  报告及培训', '第十六条', '风险管理部门制订信息科技风险意识培训年度计划，每年组织实施面向不同层面的信息科技风险意识培训。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第七章  附  则', '第十七条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《信息科技风险管理办法（试行）》', '第七章  附  则', '第十八条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第一章	总	则', '第一条', '为规范xxx银行（以下简称“本行”）信息系统上线投产流程，控制信息系统投产变更风险，根据银监会《银行业金融机构重要信息系统投产及变更管理办法》等法律、法规、规章及其他规范性文件的规定，特制定本办法。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第一章	总	则', '第二条', '本办法适用于本行业务信息系统开发测试完成后的上线投产管理过程。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第二章	职责与权限', '第三条', '对上线投产的信息系统的运行维护人员、软件开发人员、安全管理人员进行职责与权限的分配：（一）运行维护人员1.负责基础环境的部署；2.负责与软件开发人员共同编写上线文档；3.负责信息系统上线投产的操作；4.负责信息系统上线投产后的监控、备份、巡检。（二）软件开发人员1.负责收集整理上线投产所需文档，并提交至项目开发管理责任小组审批；2.负责与运行维护人员共同编写上线文档；3.负责信息系统上线的技术支持。（三）安全管理人员1.负责上线前生产环境的检查；2.负责信息系统用户权限的分配；3.负责评估信息系统整体安全架构，配置系统安全策略。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第三章    管理内容', '第四条', '因上线信息系统而新增设备的，应在计划投产日期之前一个月到货，由设备管理岗负责设备的到货验收，并协调初始安装，具体工作包括：（一）设备到货。设备管理岗在设备供货商和设备原厂商的配合下，按照设备采购商务合同中的规定，对到货设备的型号、数量、配件等具体配置进行核对和查验，确认到货设备与采购要求相符。（二）设备安装。设备管理岗和机房管理岗应按照机房管理的规划，为设备选定部署区域和机柜位置，监督设备供货商和设备原厂商，完成设备上架、安装、加电等操作。（三）设备验收。完成设备到货查验和安装加电操作后，可进行设备验收。验收通过的，设备管理岗、设备供货商人员应共同签署设备到货验收单；验收未通过的，设备管理岗应与设备供货商、设备原厂商共同协调解决。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第三章    管理内容', '第五条', '上线投产评审会由信息科技部的运行维护人员、软件开发人员、安全管理人员和业务部门相关人员组成。上线投产评审会应就信息系统上线的范围、影响、风险、应急、回退、业务部门以及第三方支持和联系方式、系统验证、业务验证、上线准备材料、上线准备工作、上线后续跟踪等方面进行评审，并确定上线时间。具体包括：（一）安装部署说明是否清晰完备。（二）生产系统的环境配置部署是否完成。（三）上线投产时间节点是否合理。（四）上线实施和业务验证人员安排是否合理。（五）备份、验证和回退方案是否可行。（六）与关联信息系统的关系设定是否恰当。（七）上线投产操作的脚本化程度。（八）涉及重要信息系统上线的监管报备是否履行。（九）对应用维护人员的培训是否完成。（十）操作风险评估及其它影响上线投产的因素。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第四章    管理程序', '第六条', '基础环境准备（一）项目经理应根据项目进度安排，向运行维护人员提出系统上线环境部署申请，落实相关设备到货时间、设备部署方案、网络部署方案、系统部署方案等准备工作。（二）网络管理岗在设备管理岗、设备供应商和设备原厂商的配合下，按照机房管理规定和网络部署方案，完成网络布线工作和网络设备、网络安全设备的配置部署。（三）系统管理岗、数据库管理岗在设备供应商和设备原厂商的配合下，按照系统部署方案，完成服务器分区配置，以及系统软件的初始安装和配置。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第四章    管理程序', '第七条', '上线准备（一）软件开发人员应编写数据备份、监控策略等上线文档。（二）软件开发人员与运行维护人员应制定上线实施方案，方案中应明确列出各切换步骤的时间点、操作内容、负责人员，同时应包括操作后的验证方案，以及切换失败后的回退方案。（三）网络环境配置部署完成后，对相关设备的维护管理应通过堡垒机进行操作。对主机系统、应用程序、业务数据的配置部署应通过堡垒机进行，不得越过堡垒机进行操作。（四）软件开发人员应将业务应用最终版本（含编译后的文件、可执行程序、配置脚本、存储过程等）移交至运行维护应用系统管理岗人员。（五）基础环境准备完毕后，安全管理人员应按照安全基线对安全配置进行核查，网络管理岗、系统管理岗、数据库管理岗应协助安全管理人员完成核查，对于未完成的安全配置应及时整改。（六）需风险评估的重要信息系统，在上线投产前应进行系统上线评估，并出具《系统上线评估报告》。（七）法律、法规规定需备案的重要信息系统上线投产前至少 20个工作日，应及时向当地银监会派出机构报送材料并备案。（八）软件开发人员应认真填写上线投产相关文件和表格，并签字确认。所有上线准备工作完毕后，软件开发人员应将该表移交给运行维护人员。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第四章    管理程序', '第八条', '上线申请、评审和批准（一）业务需求部门应填写软件项目投产申请表，软件开发人员及运行维护人员应准备上线文档，并提交给项目开发管理责任小组。（二）项目开发管理责任小组收到软件项目投产申请表后，应对上线文档是否齐备进行审查，并提交上线投产评审会评审。评审结果分为评审通过、评审通过但需补充材料、评审不通过三种。评审通过的，应将软件项目投产申请表按本行软件开发项目管理规定的要求报送审批；评审通过但需补充材料的，相关人员应根据评审意见补充上线文档并重新申请评审；评审不通过的，说明项目不具备上线条件，待具备上线条件之后再重新申请评审。（三）在重要信息系统投产前至少20个工作日、变更前至少10个工作日向中国银监会或其派出机构报告。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第四章    管理程序', '第九条', '上线投产（一）信息科技部应在批准的上线投产时间内安排上线投产，遇特殊情况无法在规定时间内上线投产的，应向项目开发管理责任小组提出申请，并由最终审批人批准。（二）系统上线投产应由运行维护人员和软件开发人员共同实施，并进行验证和记录。投产失败的，执行回退方案，待解决问题后重新安排投产。（三）系统上线成功后，应用系统管理员应编写系统上线报告。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第四章    管理程序', '第十条', '上线后管理（一）软件开发人员应配合运行维护人员实施系统运行集中监控。（二）系统需求部门为系统试运行和上线期间的责任部门，配合软件开发人员和运行维护人员解决系统试运行期间的系统问题，及时把出现的问题反馈给开发人员。（三）系统需求部门在系统上线后负责进行系统评价，在项目上线后 3 个月后，牵头组织负责对上线项目后评价工作，包括原先需求实现和满足程度、系统用户体验友好度、系统实际产生的市场效益、系统用户意见和建议、系统下一步改进措施等。（四）运行维护人员负责在巡检手册中增加与投产系统有关的运行监控内容。（五）重大软件项目生产环境运行稳定后，软件开发人员应配合运行维护人员按季度向信息科技管理委员会提交重大项目进度报告，内容包括重大项目名录、重大项目分项项目报告、重大项目生产运行问题总结和应对措施等。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第五章    附   则', '第十一条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《信息系统上线投产管理办法》', '第五章    附   则', '第十二条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第一章  总 则', '第一条', '为规范xxx银行(以下简称 “本行”) 信息系统事件管理工作，明确各单位在信息系统事件管理工作中的职能，有效应对信息系统各种紧急事件，保障信息系统安全、稳定、持续运行，依据《中华人民共和国计算机信息系统安全保护条例》、《商业银行信息科技风险管理指引》及省联社相关规定中关于事件管理的相关要求，特制订本办法。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第一章  总 则', '第二条', '信息系统事件是指影响或可能影响业务应用、系统环境、网络通信、机器设备、机房设施正常有效运行的事件。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第一章  总 则', '第三条', '信息系统事件可分为一般事件和紧急事件： （一）一般事件是指可能导致较小影响或破坏的信息系统事件。轻微的技术故障，指个别的、非主要的设备或者个别的数据、程序错误。比如：办理业务的设备发生故障等。 （二）紧急事件是指可能导致较严重影响或破坏的信息系统事件。一般性的技术故障，指部分网点的部分交易、个别网点的故障错误以及软、硬件的非严重性问题。比如：通信设备问题、电信线路问题、电信线路问题、网点所用的UPS故障或UPS电池用电完结，造成网点不能正常办理业务等。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第一章  总 则', '第四条', '事故是指已发生的事件，对业务系统运行已经造成了一定影响，例如：主机系统或中心主交换机故障,导致全辖所有网点业务不能办理；电信线路问题,与大部分网点不能连通；总行后备电源不能正常供电,而造成主机、中心前置服务器、网络设备掉电停机；中心多台中间业务前置服务器发生故障等。事故可分为：较小影响事故，较大影响事故，重大影响事故。 （一）较小影响事故，导致单个用户的业务系统服务不可用（或服务质量下降），对整个业务系统的运行影响很小。 （二）较大影响事故，导致某个部门或某个区域用户的业务系统服务不可用（或服务质量下降）的事故。 （三）重大影响事故，导致一项或多项重要业务系统服务对其所有用户不可用（或服务质量下降）的事故。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第二章  职责与权限', '第五条', '发现事件人员职责： 本行全体员工都有责任和义务将已发现的或可疑的事件、故障和薄弱点及时报告给相关部门或人员。其职责为负责发现事件，报告事件，确认事件进展情况。在事件受理人到来处理之前，发现事件人员尽量不要改变现状。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第二章  职责与权限', '第六条', '信息科技部门IT服务台工作人员职责： （一）负责对事件发生报告的接收； （二）负责对事件进行记录、分析、分发； （三）负责生产环境事故报告的接收； （四）负责生产环境事故症状的记录和初步诊断； （五）负责生产环境事故的初始分类，根据影响范围向不同层级报告； （六）负责生产环境事故的一线处理（有标准解决方案的事故）； （七）负责对被分配到其它部门的事故处理进展的跟踪； （八）负责向客户/用户报告事故处理的进展情况；（九）事故解决关闭后，负责事故的归档和整理，并对事故进行跟踪观察。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第二章  职责与权限', '第七条', '信息科技部门技术支持人员职责： （一）负责对发现事件进行分析确定其紧急程度及影响级别； （二）制定合理的事件解决方案并上报信息科技部门负责人； （三）对发现事件依照事件解决方案实施解决； （四）负责对已解决的安全事件进行复核； （五）负责较小影响事故的处理； （六）负责建立系统运维事件/事故知识库； （七）负责生产环境事故的诊断；  （八）负责向IT服务台工作人员提供关于事故处理的标准解决方案和培训； （九）负责向信息科技部门负责人报告较大事故及重大事故。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第二章  职责与权限', '第八条', '信息系统应急处置领导小组职责： 按《xxx银行计算机信息系统应急处置管理办法》执行。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第二章  职责与权限', '第九条', '信息科技部门负责人职责： （一）对发现事件的紧急程度及业务影响分析结果进行审核； （二）对制定的事件解决方案进行审核； （三）依据事件的紧急及影响程度汇报高级管理层；  （四）负责协调本部门管理人员对已经上升至事故的事件进行事故处理； （五）负责协调本部门管理人员对需要进行紧急变更的事故进行变更处理；（六）对事件解决方案的实施过程进行管理与监督，保证事件及时、有效解决； （七）负责对较大事故处理方案进行审批； （八）负责通知事故相关人员、应急领导小组响应事故，启动应急预案； （九）负责将重大事故上报高级管理层。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第二章  职责与权限', '第十条', '安全管理岗职责： （一）负责接受事件报告，组织进行事件调查，分析原因、涉及范围； （二）评估信息系统事件的严重程度，提出信息系统事件防范措施； （三）负责事件的跟踪，判断事件是否真正解决； （四）及时向信息科技部门负责人报告发生的重大信息系统突发事件，按应急预案快速响应，并协助相关部门及时进行处理。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第三章  基本规定', '第十一条', 'IT 服务台工作人员应具有对上报事件或事故的初步分析能力，信息科技部门负责人定期组织对IT服务台工作人员的培训。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第三章  基本规定', '第十二条', '信息科技部门应建立知识库，知识库将完整记录事件及事故的名称、详细描述、解决方案等内容。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第三章  基本规定', '第十三条', '信息系统事件 （一）信息系统事件 1．信息系统事件发生后，事件发现人员必须通过有效途径及时向 IT 服务台工作人员报告，IT 服务台工作人员进行事件记录并在自身拥有的资源范围内解答用户技术上的问题，若有解决不了的问题，服务台会向内部技术人员或外部集成商寻求解决方案； 2．支行发生紧急事件时，除向IT服务台工作人员报告外，还需通知本单位主管领导以及受影响的单位； 3．信息系统事件要素包括：事件人、事件发生单位、事件发生时间、事件类别、事件紧急程度、事件影响范围、事件描述等。 4．系统管理人员应在日常工作中定期检查硬件设备的系统日志和硬件运行状态，发现并分析问题，及时进行故障申报和处理；并对处理结果进行归档分类。 （二）信息系统事件受理 1．IT服务台工作人员负责7×24小时受理事件； 2．IT 服务台工作人员对事件受理后，首先对事件内容进行记录、分析，然后报告技术支持人员，由其判定事件级别，并根据事件级别及时组织、指定事件处理人员，并协调事件处理人员立即解决事件； 3．如果事件无法解决，事件处理人员应负责将事件提交信息科技部门负责人。 （三）信息系统事件处理 1．事件处理人员需在接到IT服务台提交的事件单后20分钟内到达处理现场，及时处理各类事件； 2．事件处理人员接到IT服务台工作人员提交的事件后，依据事件级别及紧急程度，落实解决方案，及时解决或规避解决事件； 3．对于需要跨单位提供解决方案的事件，事件处理人员接到事件后，对事件进行判断分析，确定影响程度，及时报告信息科技部门负责人，由信息科技部门负责人组织内部事件处理人员提供解决方案； 4．对于需要技术支持单位现场提供技术支持的事件，事件处理人员提出申请，经信息科技部门负责人批准后统一向支持单位事件管理负责人提出申请，事件管理负责人要组织协调有关工作； 5．对于需要协调本行业务部门解决的事件，本行业务部门负责人需配合信息科技部门协调工作，直到事件解决； 6．对于需要通过变更解决的事件，由本行业务部门负责提出变更申请，依据《xxx银行信息系统变更管理办法》相关规定实施； 7．对于在操作、维护或应急手册中有明确操作方法的情况可以直接参照处理，无需进行变更审批；对于无明确操作方法的情况，要对解决方案组织进行必要的风险评估，并按照相关制度实施； 8．对于必须通过远程登陆方式解决的突发性紧急事件，要通过部门领导同意后进行远程技术支持。远程登陆后，支持单位事件处理人员必须双人操作； 9．发生紧急事件并需要启动应急处理时，要按照《xxx银行应急预案--业务系统》相关规定启动应急流程。 （四）信息系统事件反馈 1．对于一般事件，技术支持人员应在2天内（遇节假日在节后第一工作日内）向 IT 服务台工作人员答复事件解决情况或进展情况； 2．对于紧急事件，技术支持人员应在4小时内向IT服务台工作人员明确答复事件解决情况或进展情况；3．信息系统事件关闭时，应反馈以下信息： （1）事件基本信息: a)涉及主要系统； b)影响范围（包括业务种类、下属分支行和网点数量等，下属分支行、网点、用户）； c)事件级别（一般事件、紧急事件）。 （2）事件处理情况包括：事件处理人员；故障实际开始时间；实际恢复时间；解决方案、实施计划及跟踪结果；最终解决程度（完全解决/临时规避解决/未解决）； （3）事件原因；按事件原因分类（管理操作事件/产品事件/ 版本事件/第三方原因导致事件/其他事件）。 （五）信息系统事件汇总、统计、分析 1．IT 服务台工作人员要收集、跟踪事件处理情况，及时更新知识库； 2．事件负责人应编制事件简报，上报信息科技部门负责人。事件简报内容应包括评价内容。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第三章  基本规定', '第十四条', '信息系统事故 （一）信息系统事故 1．信息系统事件或薄弱点对业务系统运行造成了一定影响后，信息系统事件处理应立即升级到信息系统事故处理； 2．支行发生事故时，事故发现人员将辖内事故通过有效途径通知本单位主管领导、IT服务台工作人员，同时通知受影响的单位； 3．信息系统事故要素包括：事故人、事故发生单位、事故发生时间、事故类别、事件影响程度、事故影响范围、事故描述等。（二）信息系统事故受理 1．IT服务台工作人员需要7×24小时受理事故报告； 2．IT 服务台工作人员对受理的内容进行记录、初步诊断，并上报信息科技部门负责人，由其判断事故影响程度，并及时组织、指定人员处理； 3．信息科技技术人员需制定事故解决方案。 （三）信息系统事故处理 1．事故处理人员需要在IT服务台工作人员提交事故报告单后30分钟内到达处理现场，及时处理各类事故； 2．处理人员依据事故级别，落实解决方案，及时解决事故； 3．对于需要现场提供技术支持的事故，事故处理人员提出申请，经信息科技部门负责人批准后统一向事故发生单位提出申请； 4．对于需要协调本行业务部门解决的情况 ，本行信息科技部门负责人需配合事故处理人员落实本行业务部门的协调工作，直到事故解决； 5．对于事故处理过程中需要进行变更操作的，由我行业务部门负责提出变更申请，依据《xxx银行信息系统变更管理办法》相关规定实施； 6．对于在操作、维护或应急手册中有明确操作方法的情况可以直接参照处理，无需进行变更审批；对于无明确操作方法的情况，要对解决方案组织进行必要的风险评估，并按照相关制度实施。 （四）信息系统事故反馈 1．事故处理人员应根据事故影响程度定时向IT服务台工作人员汇报进展情况： 1)较小影响事故汇报周期至少为2小时； 2)较大影响或重大影响事故回报周期至少为1小时。 2．信息系统事故关闭时，应反馈以下信息： 1)基本信息： a)涉及主要系统； b)影响范围（包括业务种类、网点数量、网点、用户）； c)事故级别（较小影响、较大影响、重大影响）。 2)处理情况包括：处理人员；故障实际开始时间；实际恢复时间；解决方案、实施计划及跟踪结果；最终解决程度（完全解决/ 临时规避解决）； 3)原因；按原因分类（管理操作/产品/版本/第三方/其他）。 （五）汇总统计分析 1．IT服务台工作人员要根据反馈结果及时更新知识库； 2．信息科技技术人员要收集、跟踪事故处理情况，编制简报，上报本行信息科技部门负责人。简报内容应包括评价内容。 （六）跟踪与复核 为确定变更的影响度和紧急度，以及监控事故解决进展情况和方案实施情况，同时监控变更请求执行情况，安全管理岗需要在事故整个生命周期内对其进行监控，并对关闭状态的事件或事故不定期进行复核，避免事件或事故再次发生。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第四章  操作流程', '第十五条', '信息系统事件处理流程（一）事件发现人员发现事件之后，将发现事件及时向IT服务台工作人员报告； （二）IT服务台工作人员负责记录/报告发现人员报告的事件； （三）技术支持人员负责及时判断所接受的事件是否为紧急事件，并采取相应措施。 1.若为一般事件： 1)技术支持人员启动一般事件管理流程； 2)技术支持人员判断一般事件是否演变成紧急事件，若演变成紧急事件，则需转入紧急流程管理；若没有演变成紧急事件，则匹配知识库； 3)技术支持人员将该事件与知识库进行匹配。 2.若为紧急事件： 1)信息科技部门负责人启动紧急事件管理流程； 2)技术支持人员判断事件是否升级为事故，若升级为事故，则需转入事故管理流程；若没有升级为事故，则匹配知识库； 3)技术支持人员将该紧急事件与知识库进行匹配。 （四）技术支持人员查看知识库中是否存在该事件解决记录： 1．若存在解决记录，则执行变更管理操作； 2．若不存在解决记录，则由信息科技部门负责人判断是否紧急变更。 3.若需要启动紧急变更，则由信息科技部门负责人启动紧急变更； 4.若不需要启动紧急变更，则由技术支持人员启动一般变更。（五）IT服务台工作人员负责变更后的问题管理； （六）安全管理岗负责问题管理后续跟踪； （七）IT服务台工作人员进行配置知识库管理、将解决问题录入问题知识库。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第五章  附 则', '第十六条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《信息系统事件管理办法》', '第五章  附 则', '第十七条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第一章    总	   则', '第一条', '为加强xxx银行（以下简称“本行”）各类信息系统的变更管理，规范系统变更流程，明确变更责任，确保系统的可靠性、完整性和可维护性，根据《中华人民共和国计算机信息系统安全保护条例》、《商业银行信息科技风险管理指引》结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第一章    总	   则', '第二条', '本办法所称系统变更是指根据本行业务发展或管理需要，对全行信息系统的相关系统软件、应用软件、数据及网络所做的相应变动。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第二章    定义、缩写和分类', '第三条', '变更首要目标是保证组织能以一种标准的方法和步骤，高效、快速地处理所有变更，从而将变更对服务质量和业务连续性的影响降到最低，并对变更影响、资源需求和变更批准进行控制和管理。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第二章    定义、缩写和分类', '第四条', '变更控制：是指建立一套正规的程序对变更进行有效的控制，从而更好地实现变更的目标。控制因变更而带来相应的风险，把风险降到最小化。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第二章    定义、缩写和分类', '第五条', '变更范围：生产系统投产运行后，所有进行的修改操作都为变更操作，变更指没有对系统造成影响前进行的系统修改操作，对系统发生影响的管理活动属于事故管理及应急响应管理，变更的前期管理活动是问题管理，变更的后期管理活动是配置管理。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第二章    定义、缩写和分类', '第六条', '一般变更：指不对系统造成重大影响，按照工作计划进行的变更。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第二章    定义、缩写和分类', '第七条', '紧急变更：指可能对系统造成重大影响，不得不紧急进行的变更，紧急变更不得超过系统变更的 20%。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第三章    职责与权限', '第八条', '变更申请人职责：（一）负责对需要变更的程序系统提出申请；（二）变更申请人可以是网络管理岗、设备管理岗、系统管理岗等管理岗；'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第三章    职责与权限', '第九条', '信息科技部门技术人员职责：（一）负责检查变更程序上线要素是否齐全；（二）负责根据指标体系对操作管理过程中的各项指标进行评价；（三）负责对要变更的系统进行上线前的缺陷测试，或当有新业务提出需求时进行业务功能和性能方面的测试；（四）负责按照变更操作手册实施操作；（五）负责监控系统的状态，发现事件及时处理或报告；（六）负责按照记录要求记录操作日志。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第三章    职责与权限', '第十条', '信息科技部门负责人职责：（一）负责对变更程序上线进行初步审核。（二）负责对变更结果进行验证，以确定其正确性、有效性；'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第三章    职责与权限', '第十一条', '分管信息科技副行长职责：负责对重要变更程序上线进行最终审批。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第三章    职责与权限', '第十二条', 'IT 服务台工作人员职责：负责知识库的维护、补充完善工作。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第四章    基本规定', '第十三条', '变更操作准备（一）信息科技部门工作人员应根据本行生产系统实际运行情况编制变更操作手册；（二）信息科技部门工作人员应根据生产系统运行的实际情况，及时调整操作手册和操作日志，确保其真实性和有效性；（三）对操作日志的调整必须经过测试环境验证并交信息科技部门领导审批。任何人不得擅自变更操作手册和操作日志。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第四章    基本规定', '第十四条', '操作变更培训（一）变更操作人员实施变更前必须经过培训；（二）要定期组织对操作人员进行培训和考核；（三）当生产系统结构或操作流程发生重大变化时，要及时组织对变更操作人员进行培训。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第四章    基本规定', '第十五条', '变更操作实施（一）变更操作人员必须严格按照规定的权限、操作实施方案中的步骤进行操作；（二）任何人不得越权操作系统，并且必须阻止未经授权的人员对生产系统进行操作；（三）操作人员在离开终端前应退出用户。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第四章    基本规定', '第十六条', '操作值班（一）在对外服务时间内实行变更操作，系统运维人员必须现场监督。要设置固定的值班电话、传真和电子信箱；（二） 系统运维人员根据职责应对系统变更期间系统运行情况进行监控；（三）系统运维人员应及时查看各类业务交接通知、邮件和传真，按规定处理各类事件；（四）变更期间系统运维人员值班记录必须包括变更运行记录。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第四章    基本规定', '第十七条', '操作记录（一）变更日志上真实记录的所有操作过程需由本人签字；（二）所有变更日志一经签字不得更改。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第四章    基本规定', '第十八条', '持续改进（一）信息科技部门应当根据管理水平指标发展趋势，以及评估报告，制定变更操作管理的改进计划；（二）根据改进计划，对变更操作管理的实施过程进行持续改进，对管理的关键部分进行控制，对影响变更操作管理水平提高的部门进行调整和优化；（三）信息科技部门在对指标体系进行全面评估的同时，根据实际情况可对其进行修订，对相应的管理指标进行调整。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第四章    基本规定', '第十九条', '检查监督（一）应以定期检查、随即抽查的形式检查操作规定中的具体实施情况，并抽查变更操作值班人员到岗情况、操作登记完成情况、操作手册及日志更新情况等；（二）应定期更新操作手册、不定期进行操作培训等；定期检查操作手册实行及操作准确情况。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第五章	操作流程', '第二十条', '变更处理流程——一般变更（一）变更申请人负责对要变更的系统提出申请，并填写《变更方案与申请表》；（二）信息科技部门负责人负责对提出的申请进行审核，以确定是否需要变更；（三）信息科技部门技术人员负责对要变更的系统进行上线前的缺陷测试，或当有新业务提出需求时进行业务功能和性能方面的测试，并提供测试报告；（四） 信息科技部门技术人员负责检查变更程序上线要素是否齐全；（五）信息科技部门负责人对变更程序上线进行初步审批，若审批通过，则涉及到重要信息系统的变更由分管信息科技副行长进行审批，否则结束变更；（六）分管信息科技副行长负责对变更程序上线进行最终审批，若通过审批，由信息科技部门负责人组织实施小组实施变更，否则结束变更；（七）信息科技部门负责人负责对变更结果进行验证，以确定其正确性、有效性；（八）信息科技部门技术人员判断变更验证是否成功：1．如果验证成功，则转入问题管理；2．如果验证不成功，由实施小组实施回退措施，变更申请人重新修订变更实施方案，并交由需求测试人员再次进行测试。（九）IT 服务台人员负责问题库的维护、补充完善工作。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第五章	操作流程', '第二十一条', '变更处理流程——紧急变更（一）变更申请人员以口头形式提出变更申请；（二） 信息科技部门技术人员提供简要的变更实施方案和回退方案；（三）需求测试人员进行测试，确保变更实施方案和回退方案的可用性及可靠性；（四）信息科技部门技术人员进行变更要素准备；（五）信息科技部门负责人对变更实施方案提出初审意见：1．若意见通过，则由分管信息科技副行长做最终审批；2．若不通过，则由运行保障中心/软件开发中心负责人重新准备变更实施方案和回退措施；（六）分管信息科技副行长对变更实施方案做最终审批；（七）信息科技部门负责人组织人员为紧急变更做充分准备；（八）分管信息科技副行长发布紧急变更实施命令；（九）实施小组根据变更实施方案实施变更；（十）信息科技部门技术人员验证变更，并判断变更是否成功：1．如果验证成功，则由信息科技部门技术人员补充书面变更记录，并转入问题管理；2．如果验证不成功，信息科技部门技术人员需要重新准备简要变更实施方案和回退措施；（十一）信息科技部门技术人员补充书面变更记录，记录相关变更文档；'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第五章	操作流程', '第二十二条', '系统操作手册变更流程（一）技术人员根据系统情况提出修改操作手册申请交管理人员审核；（二）操作管理人员根据系统情况审核由技术人员提出的修改操作手册申请；（三）技术人员根据系统变更情况修改操作手册；（四）操作管理人员审核技术人员修改后的操作手册，若审核通过，则按新操作手册执行；若审核不通过，则需技术人员继续修改操作手册；（五）值班操作人员熟悉更新后的操作手册，以后参照新手册操作系统跑批及备份。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第六章    附    则', '第二十三条', '本办法由xxx银行信息科技部负责解释、修订。'),
(NULL, '信息科技部', '《信息系统变更管理办法》', '第六章    附    则', '第二十四条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第一条', '为维护xxx银行（以下简称“本行”）信息系统安全，避免重要区域遭受未授权访问，确保重要区域信息安全，依据《中华人民共和国计算机信息系统安全保护条例》及省联社相关规定中关于外部人员对重要区域访问控制管理的要求，特制订本办法。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第二条', '非本单位机房工作人员（以下简称“外部人员”）进出中心机房、设备库房、网点机房等重要区域，须按本办法进行管理。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第三条', '外部人员因工作需要进出重要区域时，需向信息科技部门提出访问申请，经信息科技部门审批同意后，由相关区域负责人员根据操作内容，确定进入人员，选择进出时间，填写《外部人员重要区域访问登记簿》，重大操作原则上应放在业务数据录入或备份之后进行，整个工作过程需有信息科技部门相关岗位的工作人员陪同进行。如遇审批领导不在，而又必须紧急处理的，需向审批领导电话报告，并准予进入，待审批领导回来后补办相关手续。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第四条', '信息科技部门相关人员需明确外部人员允许访问的区域、设备的范围，规定允许访问的时间，并做好访问登记工作。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第五条', '外部人员严禁单独进入中心机房等重要区域,确需进入的,机房管理人员必须全程陪同，严禁从事非业务范围内的其它任何操作。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第六条', '外部参观人员出入中心机房等重要区域，须由单位领导或信息科技部门负责人陪同进行参观，相关区域工作人员须做好登记备案工作。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第七条', '进入机房等重要区域，相关人员应保持机房清洁、卫生，严禁在机房内进食、吸烟。严禁携带任何易燃、易爆、腐蚀性、强电磁、辐射性、流体物质等进入，避免对机房设备构成威胁。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第八条', '相关业务人员进入机房等重要区域，原则上须两人同时进出，并按规定进行相关业务操作，严禁随意对设备进行操作，严禁接触与业务无关的设备，如违规造成网络和业务系统事故的，追究操作人员的责任。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第九条', '进入机房等重要区域的外部人员在机房内完成相关工作时，须按机房相关规定认真填写机房内工作内容后方可退出。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第十条', '除已发生事故外，严禁外部人员在数据生产时间进行可能影响对外服务的变更维护操作。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第十一条', '中心机房等重要区域配备监控设施，未按规定进入者，如造成损失和泄密等不安全后果的，应对相关重要区域工作人员，给予严肃处理；损失后果情节严重的，由本行依照有关规定进行处理；如触犯国家有关法律、法规，移交司法机关处理。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第十二条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《外部人员重要区域访问控制管理办法》', '', '第十三条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《容量管理办法》', '', '第一条', '容量管理的定义容量管理指对本行机房支撑信息系统运行所依托的各类环境容量，如电力、网络带宽、存储空间、制冷量等参数进行规划、分析、调整和优化。主要是针对已确定的服务级别目标和业务需求来设计、维持相应的 IT 服务能力，从而确保实际的 IT 服务能够满足业务的要求。'),
(NULL, '信息科技部', '《容量管理办法》', '', '第二条', '容量管理的目的对机房的运行设备和系统进行容量规划、分析、调整和优化，目的是消除运行隐患，及时发现资源配置瓶颈，有效提高机房设备和系统资源的利用率。容量管理可以全面展示机房的总体容量利用情况和剩余率，并对各设备和系统的使用趋势进行客观分析，为将来的数据中心规划、机房搬迁提供基础数据支持，提高本行对机房管理和规划的能力。'),
(NULL, '信息科技部', '《容量管理办法》', '', '第三条', '容量管理的内容（一）制定和维护设备或系统的容量监控阈值，并定期通过命令查看语句、监控系统或通过其它查看方式记录容量和冗余情况。（二）定期将容量数据进行汇总对比和分析。完成相关统计、对比、分析后形成主机系统、应用系统、网络系统、UPS 系统、空调系统等软硬件的使用趋势分析结论。'),
(NULL, '信息科技部', '《容量管理办法》', '', '第四条', '容量管理的方法（一）系统 CPU 占用。当服务对 CPU 占用率大于 80%时，采取有效措施降低 CPU 占用率，包括重启设备、停止占用 CPU 资源的进程、重启操作系统和数据库、硬件故障联系服务商等手段。（二）系统硬盘空间。当服务需要使用的硬盘分区空间占用大于75%或少于 10 天的运行存储量时，采取有效的措施释放硬盘空间，包括清理临时文件、删除不使用的软件、部分数据迁移到其他设备、申请增加存储器等手段。（三）系统内存。当服务内存占用高于 70%时采取有效的措施释放内存，包括重启设备、停止占用内存资源的进程、停止部分程序运行、重启操作系统和数据库、重新安装操作系统、硬件故障联系服务商等手段。（四）网络带宽。通过交换机或上网行为管理系统对网路带宽进行检测，当网络带宽占用超出阈值（80%），通知网络管理员采取有效的措施降低网络带宽占用率，包括停止部分网络服务、停止部分联网设备、重启网络设备、查找计算机病毒等手段。（五）UPS 负荷。每日巡检时监测 UPS 的负荷情况，当 UPS 的单相负荷达到 45%以上时产生告警，督促机房管理员检查用电情况，采取有效措施降低 UPS 负荷，包括停用部分设备、申请增加 UPS 容量等手段。（六）制冷容量。每日巡检时监测中心机房和辅助机房的温、湿度，当空调满负荷制冷但温度持续上升，达到 25 摄氏度以上时产生告警，并在相应的容量登记簿上登记，督促机房管理员检查空调是否正常运行，采取停用部分设备、启动备用空调等措施降低环境温度。'),
(NULL, '信息科技部', '《容量管理办法》', '', '第五条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《容量管理办法》', '', '第六条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第一条', '为加强数据信息处理全过程安全管理，在发生人为或自然灾难的情况下,保证关键应用的数据不丢失。满足监管和内部控制要求，对xxx银行（以下简称“本行”）数据备份及安全管理工作进行规范管理，根据《中华人民共和国计算机信息系统安全保护条例》、《商业银行信息科技风险管理指引》结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二条', '本办法适用于本行生产机房的数据备份安全处理，具体包括核心业务数据备份、前置业务数据备份、基于 B/S 架构的应用系统数据备份。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第三条', '信息科技部负责对提供生产的信息系统数据进行备份与恢复操作，确保生产数据的安全及备份介质的安全。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第四条', '数据管理员应根据备份数据的性质、重要等级制定数据备份策略，备份策略应包括：备份方式、备份时间、备份周期、备份介质、保存环境、保存期限、数据清理周期等。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第五条', '备份策略必须提交信息科技部负责人审批，审批通过后，由数据管理人员执行数据备份策略。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第六条', '数据管理人员每日例行查看备份日志，若发现备份异常，应及时向信息科技部负责人汇报。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第七条', '数据管理人员应定期对备份进行恢复，检查备份情况，若发现异常应及时处理，并留下备份恢复检测记录。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第八条', '数据管理人员应定期检查备份系统，确保备份系统能够满足业务增长需求。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第九条', '应定期检查备份介质的可用性，若发现介质已经不能使用，应及时更换新介质并对重要数据进行转存处理。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十条', '在服务器上保留各个应用系统版本，如果新版本的应用发生错误，能及时切换到旧版本，以确保业务的连续性。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十一条', '小型机操作系统和应用系统只要发生变更，就应重做备份。备份介质的使用、数据备份、数据恢复应经过信息科技部负责人的审批。数据恢复后要立即检查数据的完整性，同时填写维护记录。数据备份时，执行人员必须仔细检查备份作业或备份程序的执行结果，核实备份介质与备份内容的一致性，确保备份数据的正确性和完整性。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十二条', '在生产系统进行数据恢复前，必须对生产系统进行必要的备份，切实保护当前生产数据的安全。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十三条', '存放备份数据的介质必须具有明确的标识。标识必须使用统一的命名规范，注明介质编号、备份内容、备份日期、备份时间和保留期限等重要信息。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十四条', '数据管理人员每三个月对数据备份介质进行一次抽检，以确认介质能否继续使用、备份内容是否正确。需要长期保存的数据，应在介质有效期内进行转存，防止存储介质过期失效。对于不可用的存储介质按照规定进行转存或销毁。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十五条', '数据管理人员必须对数据备份介质信息建立档案，档案信息包括：介质标识、存储备份信息、存储备份信息的记录时间、有效期、访问记录、维护人员等。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十六条', '为了提高生产系统性能，降低运行成本，必须定期对数据量庞大和增长速度较快的数据库、表、文件进行数据清理，回收利用率极低的存储空间。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十七条', '生产数据的保留期限和清理周期应根据系统性能、存储容量、数据量增长速度等因素分别制定，并尽可能满足联机业务处理的需要。生产数据的清理属于重大变更，必须严格履行变更审批手续。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十八条', '数据清理前必须对数据进行备份，在确认备份正确后方可进行清理操作。历次清理前的备份数据应按不同的数据类型进行定期保存或永久保存。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第十九条', '备份数据应仅用于明确规定的目的，未经批准不得它用。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十条', '涉及加密环节的重要生产数据（例如各类密码和密钥、各类校验算法、加/解密算法和参数、终端设备识别算法和参数、身份识别算法和参数等）及其存放介质和技术资料等，必须同时按照本行有关的保密条款规定进行严格管理。保密数据备份时不得以明码形式传输和存储。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十一条', '备份数据资料保管地点必须满足防火、防热、防潮、防尘、防盗等条件，并指定专人保存。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十二条', '因可控的人为原因造成重要数据遗失的，因根据损失情况对责任人进行严厉处罚，情节严重的，移交司法机关处理。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十三条', '严禁外泄备份的数据，否则以故意泄露商业机密论处。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十四条', '员工离职时，必须将重要数据与本部门相关人员交接清楚。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十五条', '外请计算机维护人员进行系统维护时，本行信息科技部门人员必须全程陪同并监督，严禁维护人员以任何形式拷贝任何资料。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十六条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《数据备份实施管理办法》', '', '第二十七条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第一章	总 则', '第一条', '为保证xxx银行（以下简称“本行”）数据在申请、提取、使用以及借阅过程中的数据安全管理，防范数据信息泄露，根据《中华人民共和国计算机信息系统安全保护条例》、《中国人民银行计算机系统安全管理暂行规定》，以及中国银监会、江苏省农村信用社联合社对数据安全管理的相关要求，制订本办法。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第一章	总 则', '第二条', '不通过综合业务系统、信贷管理系统等正常途径导出数据，而是通过信息科技部后台取得的信息和数据均适用于本办法。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第一章	总 则', '第三条', '业务部门、基层网点、系统开发项目组、查询数据信息的有权机关、客户、客户相关关系人等对数据申请查询、提取、使用、借阅、保管、新增、变更以及销毁等均须遵循本办法。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第一章	总 则', '第四条', '查询数据信息的有权机关是指依照法律、行政法规的明确规定，有权查询、冻结、扣划单位或个人在金融机构存款的司法机关、行政机关、军事机关及行使行政职能的事业单位。具体包括：人民法院、税务机关、海关、人民检察院、公安机关、国家安全机关、军队保卫部门、监狱、走私犯罪侦查机关、监察机关（包括军队监察机关）、审计机关、工商行政管理机关、证券监管管理机关等。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第二章 分工与职责', '第五条', '本办法涉及到的对象包括：业务部门、基层网点、信息科技部、系统开发项目组、公安机关、检察院、法院以及有权在本行进行数据调阅的其他机关或个人等。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第二章 分工与职责', '第六条', '信息科技部主要负责对数据的提取、保管、变更等操作；对数据提出需求进行技术审核；对项目组数据提取、使用、管理进行控制。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第二章 分工与职责', '第七条', '业务部门、基层网点根据自身的业务需要在数据权限范围内对数据申请、提取、借阅以及使用，并对取得数据准确性进行核对与确认；另外业务部门还需对自身牵头的项目组提出的数据需求和使用负责。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第二章 分工与职责', '第八条', '项目组在申请数据时必须向牵头或相关的业务部门提出书面申请，并经信息科技部同意后方可取得和使用数据。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第三章 行内数据使用申请', '第九条', '行内数据申请使用是指基层网点、部室以及行内员工根据本单位或个人自身的业务工作需要，向信息科技部提出的数据申请需求。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第三章 行内数据使用申请', '第十条', '无特殊情况下，各单位或员工提出数据申请需要经业务部门主管或支行行长签字同意；如果涉及到核心机密数据，必须经过分管领导签字同意；对于需求的申请涉及到费用支出的也必须经分管领导签字确认。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第三章 行内数据使用申请', '第十一条', '各单位申请的数据范围必须在自身的权限范围内。业务部室申请的数据必须是与该部室业务相关的数据，支行仅能申请自身范围内的数据。对查询超出本单位权限范围内的数据，必须经过分管领导的签字确认。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第三章 行内数据使用申请', '第十二条', '各单位内部成员查询数据，可以根据需要设置相应的权限等级。具体的权限等级由各单位自行设定。超出自身权限范围内的，需要经单位负责人签字确认后方可查询申请。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第三章 行内数据使用申请', '第十三条', '各单位对本单位申请的数据安全负责。对于每次申请的数据需要做好登记工作。数据的传输与使用过程中要防止数据的泄露和失密。需要保存留档的必须专人对数据进行管理，防止数据丢失泄露。临时调阅查看的数据，使用完成后立即删除、销毁。处理的过程必须双人进行并做好登记。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第三章 行内数据使用申请', '第十四条', '所有数据的传输必须在本行内部网或经过桌面管理软件认证和杀毒的 U 盘上进行，所有的数据查看必须在桌面安全管理软件管理的电脑上进行操作。需要在未经认证的电脑和设备上操作时，必须经分管领导签字同意并在进行病毒查杀后的电脑上使用。任何人不得在未经允许的情况下，擅自将数据带离本行或泄露给他人。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第四章 有权机关数据查询', '第十五条', '有权机关在办理协助查询业务时，经办人员应当核实执法人员的工作证件，以及有权机关县市级以上机构签发的协助查询存款通知书。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第四章 有权机关数据查询', '第十六条', '有权机关在办理协助查询业务时必须有两人或两人以上同时在场，并出具相关证件、通知书或其他有效证明。协助查询存款通知书必须详细写明编号、原因、需要查询的数据内容、日期以及有权机关的印章。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第四章 有权机关数据查询', '第十七条', '协助查询的内容必须依据协助查询存款通知书上的内容给予查询，不得超出通知书上的范围。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第四章 有权机关数据查询', '第十八条', '办理协助查询业务时，应当按照内控制度的规定建立和完善协助查询工作的登记制度。在协助有权机关办理查询手续时，应对有权机关名称、执法人员姓名和执法证件号码、经办人员姓名、被查询单位或个人的名称、协助查询的时间和金额、协助查询书文的名称和编号，协助查询结果等进行登记。登记表应当在协助办理查询手续时填写，并由有权机关执法人员和经办人员共同签字确认。经办人员应当妥善保存登记表，并严格执行保密原则。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第四章 有权机关数据查询', '第十九条', '协助有权机关查询的资料包括电子数据和纸质档案资料，经办人员应当如实提供，有权机关根据需要可以抄录、复制、照相，但不得带走纸质档案的原件。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十条', '项目组必须与本行进行签署《xxx银行信息安全保密协议》后方可进入本行开展需求调研、项目开发、测试上线等工作。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十一条', '项目组作为数据提供方时，需要对信息科技部进行负责，即所有的数据提供必须经过信息科技部的签字同意后方可提供；当作为数据需求方时，需要对牵头的业务部门进行负责，即所有的数据需求必须由牵头部门进行签字确认后方可申请数据资源。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十二条', '在向项目组进行数据申请时，必须先提供数据需求申请单，申请单中必须注明数据申请内容、范围以及相关业务口径，并由业务部门签字盖章确认；需求申请单必须经信息科技部门进行业务技术评估、数据权限审核并签字确认。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十三条', '项目组在提供数据时必须检查需求单内容、业务口径要求、申请单位数据权限等事项，不得提供额外的数据信息'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十四条', '项目组提供数据时必须经过内部网或通过桌面安全管理系统认证的 U 盘进行数据传输，不得通过外部网或未经注册的 U 盘进行传输。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十五条', '项目组必须在本行指定的机器上对数据进行使用和操作；未经允许不得将数据擅自拷贝的个人电脑、移动硬盘等非指定设备上使用与存储；未经允许不得对本行客户数据进行抄录、复制与拍照等；禁止内外网互通，不得将数据放在互联网中进行传输。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十六条', '项目开发过程中，需要对数据的关键信息进行脱敏处理。开发测试的过程中如必须采用真实数据，需要向信息科技部申请后方可使用，数据使用完毕后必须在本行信息科技部监督下及时销毁。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十七条', '项目开发过程中，需要对系统生产环境数据进行变更、删除等操作时，项目组必须向信息科技部进行书面申请，并经信息科技部签字盖章确认后方可进行操作。在未经同意的情况下，禁止对生产环境数据进行变更、删除等操作。操作过程中，必须双人操作，双人复合，并做好相应的变更、删除记录。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十八条', '对系统采用数据备份机制，指定并明确相关责任人定期的检查数据备份文件，以确保数据能够正常顺利的进行恢复工作。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第五章 项目组数据安全管理', '第二十九条', '在项目组撤离本行前，必须收回各类开发设备，做好对数据回收、删除、销毁工作；对放在项目组的个人电脑中的数据必须进行删除和销毁，以防止数据泄露。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第六章 其他数据安全', '第三十条', '重要数据(介质)应设专人负责登记保管，未经批准，不得随意挪用。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第六章 其他数据安全', '第三十一条', '在使用重要数据(介质)期间，应严格按保密规定控制转借或复制，需要使用或复制的须经分管领导批准。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第六章 其他数据安全', '第三十二条', '对所有重要数据(介质)应定期检查，要考虑介质的安全保存期限，及时更新复制。损坏、废弃或过时的重要数据(介质)应由专人负责消磁处理，机密级以上的重要数据(介质)在过保密期或废弃不用时，要及时销毁载体。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第六章 其他数据安全', '第三十三条', '机密数据处理作业结束时，应及时清除存储器、联机磁带、磁盘及其它介质上有关作业的程序和数据。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第六章 其他数据安全', '第三十四条', '机密级及其以上的信息存储设备不得并入互联网。重要数据不得外泄，输入及修改信息科技部由专人完成。重要数据的打印输出及外存介质应存放在安全的地方，打印介质及时销毁。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第七章	附 则', '第三十五条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《数据安全管理办法》', '第七章	附 则', '第三十六条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第一章　总　则', '第一条', '为实现xxx银行（以下简称“本行”）科技应用规划目标，科学合理配置电子设备资源，提高本行计算机管理水平，依据《中华人民共和国计算机信息系统安全保护条例》、《江苏省农村信用社电子设备采购管理暂行办法》等规定，结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第一章　总　则', '第二条', '电子设备指各类计算机及外围设备、网络通信设备、计算机及网络通信软件、安全保密设备、计算机机房场地设施等。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第一章　总　则', '第三条', '本办法所指的电子设备管理主要是指除价值管理和账务管理以外的设备管理，信息科技部是电子设备归口管理部门。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第四条', '对到货即须安装的设备，经信息科技部负责人同意后，先安装再办理入、出库登记手续；对大批量的设备无法逐件验收可与供货商讨论验收方式。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第五条', '对所有购置（领用）的电子设备应通过我行的资产管理系统进行登记管理。设备管理员负责信息登记与数据维护；库房保管员负责出入库设备的实物管理与库房管理。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第六条', '设备在入库时应填写完整的电子设备入库清单，要求真实、完整、规范地记录入库日期、设备名称、数量、金额等要素，并做到账实相符。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第七条', '对于验收不合格的电子设备，信息科技部应妥善处理并及时会同相关部门根据采购合同办理相关手续。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第八条', '设备使用部门领用电子设备及其耗材时必须严密手续，填写领用清单，详细记载领用单位、种类、型号、数量、领用日期等要素，领用人、设备维护员和设备保管员应签字确认。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第九条', '原则上设备不可外借，若确因特殊情况，必须经信息科技部负责人签字同意后，方可外借设备。1. 必须认真填写借用清单，需注明名称，型号，规格，数量及归还日期。借用人应如期归还，到期不归还者需说明情况，并在借用清单上注明设备续借延长期限。2. 借用设备到期后由设备保管员提请催收，并检查设备的情况，如发现外借设备有短少、损坏等，由借用人赔偿后再办理入库登记。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第十条', '设备保管员应负责定期检查库存情况，确保账、卡、物相符。若发现库存数量与账面不符，必须查明原因，因失职造成的损失须承担相应责任。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第十一条', '存放库房内的设备，应按用途、项目分类堆放整齐，妥善保管便于查找使用，同时库房要具备防火、防水、防盗等设施。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第二章　实物管理', '第十二条', '设备使用部门领用的设备应做到专人管理、专人使用，不经本人许可，严禁他人操作使用。1. 严禁非专业管理人员私自随意拆装、移动电子设备，对因此而导致电子设备丢失、损坏、重要数据受损，影响正常工作，追究相关人员责任。2. 保持电子设备周围环境整洁，做好防潮、防尘保护，并定期或不定期的进行保养。3．所有电子设备必须在显眼处粘贴标签，标签内容按资产管理系统的要求及省联社科技工作规范对设备管理的要求进行填写。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第三章　维护管理', '第十三条', '信息科技部每年初根据电子设备类型、分布区域和使用情况制定年度设备维护与维修方案。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第三章　维护管理', '第十四条', '信息科技部设立专门的电子设备维护人员，负责设备的维护与维修工作。由外包公司维护和维修的设备，信息科技部须做好相关管理工作。1. 保证生产设备的正常运行，及时发现并排除设备故障，如遇到无法排除的故障，应及时上报，或与维保商联系，落实维修事宜，排除故障。2. 每季度将设备损坏、易耗品需求及辅助设备情况上报信息科技部负责人，并做好添置计划。3. 保证备用电子设备完好无损，定期检查确认备用设备处于良好的工作状态。4. 对易损部件要定期进行更换；对机械传动部件较多的电子设备要定期进行清洁、注油、防锈处理；对精密设备和重要设备要做好重点维护工作。5. 严格按照操作规程和步骤进行操作，如需更换零部件，应办理电子设备备件领用手续。6. 实行电子设备维修报告制度，分析故障原因、类型及责任，对非正常性或周期性故障要采取有效措施，避免再次发生。7. 负责或配合有关部门定期对计算机机房和电子设备的接地系统、避雷系统、供配电系统、空调系统、消防系统及运行情况进行维护，并做好详细记录。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第三章　维护管理', '第十五条', '各种设备应按规定位置摆放，严禁带电搬动，造成对设备强烈震动。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第三章　维护管理', '第十六条', '对接入系统设备必须有专用线路供电，不得与空调等涌流设备混用线路。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第三章　维护管理', '第十七条', 'UPS用于专用设备的供电，须定期进行充放电。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第三章　维护管理', '第十八条', '重要的电子设备必须购买维保。电子设备出现故障无法使用时，不得自行拆卸，应向信息科技部提出维修申请，提交电子设备维修申请表，内容包括日期、设备型号、故障原因、联系人、使用部门等，由信息科技部确定维修方案。1. 保修期内的电子设备发生故障时，严格按照电子设备购置合同的有关保修服务条款执行。2. 当电子设备需外送修理时，应经分管领导和信息科技部负责人批准，并在设备台账中作相应的登记。3. 电子设备外送修理前，需将存贮介质内的应用程序、业务数据等与金融业务相关信息备份后删除，修复后的电子设备维护人员应进行验收，并对存贮介质中的信息进行安全检查。4. 外来维修人员现场进行维修时，信息科技部须登记外来人员信息，对涉及信息和系统安全的电子设备维修，在维修的整个过程中必须有信息科技部的维护人员在场，并详细记录维修过程，以保证电子设备和信息的安全。5. 信息科技部须按照购置合同或保修合同对厂商的产品质量、保修质量、巡检情况进行监督、检查和评价，评价结果将作为设备购置和续保的重要依据。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第三章　维护管理', '第十九条', '部分设备由外包公司维护和维修，参照《xxx银行信息科技外包管理办法》。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第四章　清查与报废', '第二十条', '在资产牵头管理部门的组织下，信息科技部每年对其管理的电子设备进行全面清查、盘点，以落实设备资产的使用责任、管理责任。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第四章　清查与报废', '第二十一条', '信息科技部负责电子设备的报废鉴定工作。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第四章　清查与报废', '第二十二条', '报废以使用功能丧失为界定原则，有下列情形之一的电子设备由信息科技部提出报废申请：1. 折旧年限已到并已提足折旧，并且其主要功能丧失、退化、零部件损耗或一次性恢复其原有功能的再投入超过其重置价值的1/3以上的。2. 因超强度使用、自然损耗等原由造成其性能老化、事故频发（有运行记录和相关技术鉴定）而无法正常使用，恢复其原有功能或达到目标功能需要的投入占其重置价值的1/3以上的。3. 因不可抗力（如自然灾害、城市规划）或人为事故等因素导致的设备毁损、拆迁，恢复其原有功能的再投入超出同类资产重置价值1/2以上的。4. 长期闲置不用，在可预见的未来不会再使用，且已无转让价值的。 5. 由于技术进步等原因，已不可使用的。 6. 其他实质上已经不能再带来经济利益的。7. 国家规定必须报废的以及信息科技部认定其它可以报废的等。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第四章　清查与报废', '第二十三条', '信息科技部对提交的报废申请要及时进行技术鉴定和审核，具体要求如下：1. 对申请报废的电子设备的有关账、卡与实物进行逐台逐件核实，做到账、卡、物相符。2. 对申请报废的电子设备进行运行测试和故障损坏情况分析，确认可否进行修复、改装，估算修复或改装费用。3. 对折旧年限未到、因人为事故造成严重损坏，或功能过低、技术落后的电子设备要做认真分析、慎重处理。4. 按照有关规定，合理确定报废电子设备的残值。5. 对报废电子设备中存有的程序、数据等资料进行转移后清除，防止泄密。6. 填制固定资产报废审批表，内容包括：使用单位、开始使用日期、卡片号码、型号、原值、净值等。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第四章　清查与报废', '第二十四条', '对涉密的报废设备，按有关保密要求妥善处理。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第五章　监督与检查', '第二十五条', '信息科技部应加强对电子设备管理工作的监督和检查，及时发现和解决管理过程中存在的问题，总结经验教训，不断改进电子设备管理工作。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第五章　监督与检查', '第二十六条', '信息科技部应定期或不定期组织有关人员采取自查、互查、抽查和专项检查的方式检查辖内电子设备管理工作情况。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第五章　监督与检查', '第二十七条', '电子设备检查的主要内容包括：1. 电子设备购租、验收、归档、消耗品与备品备件管理。2. 电子设备维护、报废情况。3. 电子设备台账账实相符和信息规范化情况。4. 电子设备的原值、完好率、利用率、闲置率、报废率和使用部门满意率等内容。5. 电子设备申请采购用途与到货使用情况是否一致、是否被挪用、购置合同中有关售后服务条款执行情况。6. 有关规章制度的制定与执行情况。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第五章　监督与检查', '第二十八条', '检查人员要如实反映检查中发现的成绩和问题，全面、客观地写出检查报告。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第五章　监督与检查', '第二十九条', '检查中如发现不按本办法规定办理、管理混乱、违规操作、危害信息系统安全、造成电子设备重大损坏、积压、丢失或浪费的应追究有关支行（部门）负责人和相关人员的责任，并依照计分办法给予处分。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第六章　附　则', '第三十条', '本制度未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第六章　附　则', '第三十一条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《电子设备管理办法》', '第六章　附　则', '第三十二条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第一章    总	  则', '第一条', '为保障xxx银行（以下简称“本行”）的日常生产经营活动正常进行，保障本行生产系统日志（以下简称“系统日志”）安全、高效管理，根据银监会《商业银行信息科技风险管理指引》的要求，制定本办法。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第一章    总	  则', '第二条', '本办法所称系统日志是指各类信息系统生产环境运行过程的运行情况记录信息，主要分为三类：（一）设备运行日志：各类服务器、网络设备、安全设备的运行情况记录等；（二）基础软件运行日志：各类操作系统、数据库、中间件的运行情况记录等；（三）应用系统运行日志：各类业务、应用、信息及监控管理软件的运行情况记录等。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第一章    总	  则', '第三条', '总行信息科技部为本行系统日志日常管理的职能部门，具体负责系统日志的记录设置、保存、备份、恢复、使用、分析处理等工作。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第二章    系统日志记录设置', '第四条', '根据“谁主管，谁负责”的原则，由总行信息科技部专业维护人员依据生产维护的具体需求对各自负责的软硬件进行日志记录规则的设置。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第二章    系统日志记录设置', '第五条', '系统日志存放要统筹考虑信息保存的必要性与存储空间的合理使用。有追溯价值的信息要重点保存，不能遗漏；重要程度不高的信息可不进行记录，减少对存储空间的浪费。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第二章    系统日志记录设置', '第六条', '应用系统运行日志的记录设置应当紧贴应用的使用情况，在系统开发阶段予以充分考虑。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第二章    系统日志记录设置', '第七条', '承载各类应用系统、基础软件环境的设备应正确设置时区与时间，与网络时间服务器进行同步配置，保证系统日志记录时间戳的准确性。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第三章   系统日志保存', '第八条', '系统日志应当严格按照监管要求，结合行内业务使用的具体要求及相关技术规范，合理设置系统日志保存的位置与方式，妥善保存便于备查。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第三章   系统日志保存', '第九条', '重要信息系统的运行日志应当保存在专业存储设备上，保证数据不丢失。其它日志一般采取设备本地保存方式。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第四章    系统日志备份与恢复', '第十条', '重要信息系统运行日志实行定期与不定期相结合的备份方式，由指定人员检查运行情况，并登记在案。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第四章    系统日志备份与恢复', '第十一条', '系统重大变更前，应当对系统日志进行必要的备份，必要时进行系统级备份。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第四章    系统日志备份与恢复', '第十二条', '根据日志的重要级别，确定备份日志的保存期限，应根据备份介质使用寿命至少每年进行一次恢复性测试。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第四章    系统日志备份与恢复', '第十三条', '备份介质的存放处必须符合防火、防水、防磁、防盗的安全要求。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第四章    系统日志备份与恢复', '第十四条', '重要系统的运行日志要定期异地备份。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第五章    系统日志使用', '第十五条', '备份日志的使用与恢复，必须通过相关审批流程后操作，需要使用备份日志时，使用部门应填写《备份日志使用申请表》，交由需求部门及信息科技部负责人审批后方可使用。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第五章    系统日志使用', '第十六条', '本行信息安全人员应定期对各类安全设备的系统日志进行检查，分析各类安全事件，保证系统安全运行。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第六章    系统日志的分析处理', '第十七条', '借助集中统一的日志管理平台，实现多种网络设备、安全设备、主机设备以及其他应用系统的日志集中搜集、分析与展现，实现对整个网络平台和面向应用的事前管理、事中监控和事后分析。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第六章    系统日志的分析处理', '第十八条', '各类专业维护人员应当定期对系统日志进行检查与分析，排查系统运行的隐患，调整优化系统日志的记录设置策略。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第六章    系统日志的分析处理', '第十九条', '信息科技部应制定相应的备份日志审核计划，计划中应将日志按等级分类，保证按照优先级对备份日志审核。需要备份日志审核时，需求部门应填写《备份日志审核表》，由需求部门及信息科技部负责人审批。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第七章    附   则', '第二十条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《系统日志管理规定》', '第七章    附   则', '第二十一条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第一章　总　则', '第一条', '为规范xxx银行（以下简称“本行”）中心机房管理，确保本行计算机核心业务系统和其它各类计算机系统安全、高效运行，防范和控制操作风险，根据《银行计算机机房及柜面设备安全防护暂行规定》等法律法规及相关规定，结合本行实际，特制定本制度。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第一章　总　则', '第二条', '计算机中心机房是指本行的数据、网络、核心设备集中存放的场所。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第一章　总　则', '第三条', '本制度明确了计算机中心机房设立标准、环境要求、系统管理要求、值班制度、出入管理等内容和要求，适用于本行计算机中心机房的管理。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第二章　职责与权限', '第四条', '硬件管理人员、系统管理人员及当日值班人员负责计算机机房的日常管理。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第二章　职责与权限', '第五条', '信息科技部负责人负责机房管理的检查、监督。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第六条', '设立的标准和环境要求（一）机房的建设应先委托有资质的单位根据国家相关标准进行设计，设计方案应满足国家计算机机房A级标准，设计完成并通过审核后再严格按照标准进行建设。（二）机房应按相应级别合理分区，保障不同运行环境有效的安全空间隔离。（三）安置重要计算机信息系统设备环境应当符合下列基本安全要求：1. 中心机房应安装门禁系统、防雷系统、接地系统、监视系统、消防系统、报警系统。2. 中心主机房应建立必要的应急备份机制，确保紧急状态下备份设备切换运作的能力，做好备份设备的维护保养。中心运行模式采用双设备冗余，包括：双主机、双数据存储通道及双路由器和不同电信运营商线路备份。同时，根据业务流程，应制定紧急状态下完整的应急方案，并加强平时对应急方案的演练，确保紧急状态下业务持续。3. 中心业务数据备份必须遵循安全可靠的原则，严格按照相关的数据备份计划执行，采用磁盘和光盘双重备份。业务数据备份实行异地保存。对备份数据应定期恢复，检查其有效性，数据恢复操作必须经信息科技部负责人授权，由系统管理员进行数据恢复，并做好记录。4. 计算机电源系统应符合技术规范，要求设专用的供电系统，配备必要的 UPS和发电机。UPS电源只能供计算机系统设备使用，严禁乱拉、乱接电源、严禁使用带电感性负载和可控硅负载及其他设备（如电热器件、风扇、空调、照明、卷闸门等设备）。5. 机房内不得使用电热器、充电器、热水器、电饭煲等与运行无关的用电电器。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第七条', '消防系统安全管理要求（一）中心机房严禁带入易燃、易爆、易污染、强磁性物质及各种杂物，严禁吸烟和使用大功率电热器具。（二）机房必须安装气体灭火系统，同时指定消防责任人，定期对消防器材进行检查维护。（三）应定期对机房电器设备、线路、开关等进行检查，及时发现隐患，避免因电器、线路老化，造成短路，引发火灾。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第八条', '机房电源系统管理要求（一）中心机房电源主要由机房中心配电柜和UPS主机、UPS备机和电池柜组成。（二）中心机房 UPS不间断电源为计算机专用电源，中心机房主机、服务器、所有通讯设备、微机和终端都必须接入UPS。由于该UPS容量是经过对必保设备用电高峰测算后确定的，任何人不得擅自接入非计算机设备及各类大功率电器，以免损坏UPS并由此威胁计算机中心安全。（三）对UPS要及时签订维保协议，定期检查，确保UPS的稳定工作。（四）未经授权，中心各配电柜及UPS主控台不得随意开关，更严禁擅自维修和调整线路。（五）中心值班人员须注意观察市电输入及UPS输出的电压、电流及频率。供电电压要求三相额定线电压为380伏，单相额定电压为220伏，频率为50赫兹。（六）当中心突然停电或其他紧急事故，应密切注意供电 UPS消防等系统的运行情况，如有异常，应立即通知相关负责人员，情况严重时，应通知信息科技部负责人。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第九条', '机房专用空调管理要求（一）中心机房空调系统由机房专用空调组成。（二）机房专用空调指定专人管理，其它人员对空调主控台上各按钮不得随意操作。（三）当机房停电重新供电时，必须先检查三相电的相位是否正确，只有在确认无误的情况下，方可开启空调。（四）中心值班人员须注意观察机房的室内温度是否保持在22-24摄氏度范围内，及相对湿度是否保持在40%-55%之间。若超限，应及时通知有关人员，对空调参数进行调整。（五）应定期对空调滤网进行清洗和更换，保持良好的空气过滤作用，提高制冷、制热效果。对空调加湿罐要及时清洗沉淀的锈渍，防止堵塞，影响加湿效果。当风机皮带发出摩擦声时，应及时调整皮带的松紧程度，磨损严重时应及时更换。（六）空调设备应及时过保，定期巡检。当空调发生故障时，必须尽快通知维修单位进行修理，以确保主机系统的安全运行。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十条', '值班人员及时间安排（一）计算机机房值班由信息科技部组织实施。（二）值班时间：正常营业工作时间。节假日照常值班。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十一条', '工作职责（一）值班人员要牢固树立高度的工作责任心和强烈的安全防范意识，认真细致地履行值班职责，充分利用本部门所拥有技术、信息资源，热情、妥善地处理值班期间所接受的技术咨询。机房管理人员为要害岗位人员，须经安全培训后方可上岗工作。（二）负责接听各支行（部）报障电话并进行登记，负责常见问题处理，对不能处理的问题及时通知相关人员。（三）值班人员要定时对机房内电源、空调、门禁等检查，并作好记录，杜绝安全隐患。（四）值班人员要检查计算机设备的运行情况，确保机房计算机系统的正常使用，若发现异常或死机必须及时处理、恢复正常，如问题重大，应向信息科技部负责人汇报。（五）定时检查各系统的运行情况，如有异常及时按照要求进行处理，不能处理的要及时通知相关系统责任人。（六）负责处理领导交办的其他工作。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十二条', '工作制度（一）值班人员必须严格遵守本行规章制度，严格按照要求进行操作。（二）值班期间保持精神集中，不得随意离开工作岗位, 每天必须完成规定的操作流程。（三）值班人员接听电话时要用礼貌用语，如有问题必须进行登记，记录下营业网点或业务部门的电话、联系人姓名，并按照首问责任制要求及时回答咨询或解决问题，不得推诿拖拉，如需其他人员处理的，应立即联系有关人员，并跟踪处理情况，如有特殊原因不能及时处理，必须做好解释工作。（四）值班人员必须保持工作环境的清洁、整齐，一律不准带茶杯或水具进入机房。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十三条', '交接班制度值班人员必须按时到岗进行值班交接，交接时必须做好交接登记，上一值班人员要将上日未能处理或需注意事项向下一值班人移交。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十四条', '请示报告制度（一）值班人员在处理、答复一些重要问题或超越本值班权限范围的事情，须通知相关部门，紧急情况可直接向信息科技部负责人报告。（二）值班人员接听客户业务问题咨询,可指导客户拨打客户服务中心电话，不得简单回答不能处理。（三）值班期间发生的系统问题，应立即通知相关系统责任人。（四）除特殊情况外，重大问题必须坚持先请示，不得擅自处理。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十五条', '责任处理值班人员由于态度差，有推诿、拖延现象，或没有履行职责，贻误工作，造成不良后果的，将根据本行有关规章制度追究值班人员的责任，并进行相应处罚。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十六条', '机房的出入管理（一）机房过道应设有录像监控，监控无死角，录像视频文件存储不少于1个月。 （二）中心值班人员及系统管理员在非当班时间，无故不得进入中心机房的主机房。特殊情况进入主机房，实行双人进入、双人操作、双人管理制度。（三）信息科技部其他人员无故不得进入中心主机房，确需进入主机房进行维护工作的，必须在《机房进出人员登记簿》进行出入登记。（四）非信息科技部工作人员严禁进入中心机房。若需进入机房参观或进行系统维护时须经信息科技部负责人批准，必要时须经信息科技部分管领导批准。进入机房时必须由本部门工作人员全程陪同，并登记机房出入登记簿，不得随意触摸各种设备，随意上机操作和采集数据。（五）严禁易燃易爆和强磁物品及其它与机房工作无关的物品进入机房。（六）凡涉及机密事项、文件资料、重要数据不准带出机房。（七）出入主机房需进行登记，记录出入人员姓名，证件号码，出入日期、时间、事由，信息科技部陪同人员等要素，并由出入人员在离开机房之前签字确认。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十七条', '机房人员的操作管理（一）外来人员在无本行人员陪同情况下不得在机房擅自操作电脑。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十八条', '机房纪律及检查（一）任何人员一律不准在机房内会客、吸烟、吃零食，乱扔杂物、大声喧哗。（二）机房人员只能进入允许的工作区域内，使用指定的电脑完成相应的工作。（三）加强安全防范意识，定期检查与更换机房消防专用器材，保证防火、防盗等防范设施的完好无损。（四）注意做好中心机房内各种重要的媒体数据安全保护工作，防止数据的泄漏、被毁、被盗及非法拷贝，实施专人管理。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第三章　内容与要求', '第十九条', '严禁下列各种违反计算机安全管理的行为（一）因工作责任心不强,造成计算机重要信息泄密的行为。（二）非法窃取各种计算机重要信息的行为，如重要的文件资料、密码、数据盘、网络IP地址、网络配置参数、通信电话号码等。（三）未经许可非法登录业务主机进行操作的行为。（四）将来历不明的病毒盘装入业务主机中使用的行为。（五）故意编制病毒软件或其它破坏性软件的行为。（六）有意传播计算机病毒的行为。（七）对违反本办法人员，应追究有关责任人的责任。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第四章　附　则', '第二十条', '本制度未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第四章　附　则', '第二十一条', '本制度由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机中心机房管理制度》', '第四章　附　则', '第二十二条', '本制度自发文之日起施行。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第一章	总	则', '第一条', '为确保xxx银行（以下简称“本行”）计算机业务系统安全运行，根据《中华人共和国计算机信息系统安全保护条例》和《金融机构计算机信息系统安全保护工作暂行规定》等有关法律、法规，制定本办法。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第一章	总	则', '第二条', '计算机信息系统应急处置管理办法是针对系统运行期间可能出现的故障而建立的快速反应机制，确保系统出现故障后能迅速恢复。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第一章	总	则', '第三条', '本行信息科技部定期对辖内各种相关业务系统、数据进行备份。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第一章	总	则', '第四条', '本行成立由分管科技副行长为组长的应急处置领导小组，负责辖内计算机业务系统应急预案的制定、实施和管理。应急组织信息应及时向省联社科技管理部、yy银监分局报备。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第二章	预案制定', '第五条', '应急预案的制定（一）涉及业务系统安全运行的每个环节必须制定应急预案，预案的制定遵循确保基本业务不间断的原则。（二）应急预案应包括业务应急措施、技术应急措施和组织机构管理等内容，并对应急预案的启动和具体实施步骤做出明确规定。（三）应急措施主要由应急处置领导小组责成相关部门负责落实。应急措施针对计算机应用系统运行的关键环节，分析可能出现的影响业务运转的各种突发事件，对不同的突发事件，采取适用的应急措施。（四）应急措施包括对硬件设备、软件、数据备份和技术支持方式的具体措施和方案，明确各种突发事件的处理流程和职责分工。（五）应急措施中提出的应急处理必备的技术要求由科技信息部门组织落实。（六）应急处置领导小组依据应急预案内容，责成相关部门对应急处置执行人员进行培训；并在保证系统正常运行的前提下，预案制定单位每年至少组织一次应急预案的模拟演练。（七）应急措施须根据运行系统和有关情况的变化，不断进行完善。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第三章	故障级别和分类', '第六条', '故障情况按其对业务的影响程度进行分级：一级：最严重的级别，它具有以下两个重要标志之一：一是全局性；二是特别紧急性。全局性指涉及到辖内所有网点或者大多数网点，全部或者大多数交易不正常且短时间内难以恢复。特别紧急是指可能影响到人身安全或资金安全的情况。二级：较严重的级别，它涉及部分主要设备、部分网点、所有网点的部分交易，以及虽然严重但短时间内能够处理的情况。它同时还包括虽然网点工作正常但主要设备出现严重问题的情况。三级：一般性的技术故障是指部分网点的部分交易、个别网点的故障错误以及软、硬件的非严重性问题。四级：轻微的技术故障，指个别的、非主要的设备或者个别的数据、程序错误。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第三章	故障级别和分类', '第七条', '故障技术分类（一）主机系统方面的故障：⒈主机系统硬件部分故障，包括存储、CPU、主板、内存等;⒉主机系统故障是指操作系统方面的故障，包括系统资源、参数配置等方面;⒊主机数据库故障;⒋主机中间件故障;⒌主机安全方面故障。（二）网络方面的故障：⒈网络设备故障;⒉网络线路故障;⒊网络配置故障;⒋网络安全故障。（三）软件方面的故障：⒈后台系统和程序故障，包括后台系统和程序安装、更新等;⒉前台系统和程序故障，包括前台系统和程序安装、更新等。（四）其他方面的故障：⒈火灾；⒉水灾；⒊电力系统方面的故障，包括电力线路、UPS、发电机等。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第八条', '当发生系统故障时，与应急处置有关的所有人员服从修复故障的需要，全力以赴解决问题、协调处理相关事宜。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第九条', '故障发现人员根据故障的情况，判断故障的级别和技术分类，向应急处置领导小组建议启用相应的应急机制。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第十条', '所有人员须按本单位应急预案制定的岗位职责及操作要求，力争在最短的时间内排除故障。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第十一条', '预案参与人员冷静处理，及时通报实际情况和遇到的问题，必要时要求合作公司远程或现场支持。同时通知各联网网点对客户做好解释工作。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第十二条', '根据不同的故障技术分类启用相应的应急响应机制：一级故障：（一）须在第一时间通知应急处置领导小组全体成员。若在 2小时内未能排除故障，向所在地银行业监督管理局汇报。（二）分管领导责成应急处置领导小组立即召集相关技术部门负责人制定故障排除方案，并联系省联社协助完成故障，努力把影响降低到最低程度。二级故障：（一）对于二级故障：立即根据技术分类，在第一时间内解决，30 分钟之内未能排除故障，及时报告分管领导，请示处理意见；30 分钟之内排除故障，则由相关部门负责人于事后向分管领导汇报具体情况。（二）主机系统故障，立即通知科技信息部负责人，由科技信息部负责人组织技术人员进行维修，具体方案：⒈技术人员查明故障原因为程序问题，则立即进行程序更新。如果仍然不能解决问题，立即切换到备份机；⒉生产系统出现硬件致命损坏，立即启用备份机；⒊如果经以上处理故障仍然不能排除，则立即请求省联社科技创新处或相关软硬件、集成公司进行远程或现场支持。（三）通讯故障的恢复，具体方案为：⒈与电信、联通等相关网络服务部门保持一定的联动应急机制；⒉确保备用网络设备随时能够启用，故障设备自身不能恢复的及时与设备维保商取得联系，在规定的时间内到达现场技术支持，解决相关问题；⒊通讯线路中断立即启用备份线路并及时联系相关公司予以解决。（四）软件问题，业务系统故障立即与科技信息部联系，必要时，请求省联社帮助解决问题。三级故障：（一）根据技术分类，在第一时间通知相关技术维护人员。（二）排除故障后，及时向部门负责人汇报。四级故障：值班人员自行解决，或者通知相关人员解决，并向部门负责人汇报。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第十三条', '火灾、水灾、供电、空调系统等故障实施以下步骤：（一）发生火灾、水灾时关闭机房外的供电总闸，必要时关闭科技信息部所有机器设备和电源。（二）启用灭火设备进行灭火，并打火警电话 119。（三）全力保护重要的数据备份，并转移到安全地点。（四）尽量保护好火灾现场，协助有关方面勘察分析火灾发生的原因、造成的损失、提出防范和整改措施。（五）供电故障按照以下步骤操作：⒈突发性停电时，关闭不影响业务正常运行的所有用电设备以减轻 UPS 载荷。⒉有发电机的必须立即使用发电机供电以防 UPS 电能耗尽。⒊供电线路出现问题，及时联系电力部门尽快恢复供电。（六）对发生突发性事件造成的损失、原因等做好记录，经部门负责人审阅后，向行领导全面汇报。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第十四条', '科技信息部人员对出现的问题作认真记录，填写《日常业务系统故障处理记录单》。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第四章	故障处理', '第十五条', '所有问题解决之后形成的文档，均在本部门内传阅，或在本部门的会议上传达，建立故障恢复数据库，共同学习故障的解决方法和预防措施。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第五章	故障预防', '第十六条', '应急预案只针对于各种突发事件的处理，各部门的工作重点是积极预防各种可能发生的问题，力争把问题消灭在萌芽状态，同时，对于本规定中提及的各种问题定时进行演练，确保在出现问题时有条不紊地排除故障，具体要求如下：(一)计算机系统应急领导小组召集相关人员熟悉本办法中提及的各种应急方案，做到熟练掌握本部门范围内的软硬件故障检查、修复技能，每年进行一次预案演练。(二)科技信息部对每次软件安装的程序进行详细记录，对配参数调整前后的数据进行详细登记，对于系统配置、参数调整出现的问题可予以及时恢复。(三)科技信息部对全部软件的安装介质、说明文档妥善保管，在系统每次更新、安装新程序之后及时做好备份工作，同时妥善保管备份介质。        (四)总行应在各基层网点设立专职计算机信息系统安全员，定期邀请公安机关人员对计算机信息系统安全员进行安全知识培训。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第六章	附	则', '第十七条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机信息系统应急处置管理办法》', '第六章	附	则', '第十八条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第一章　总　则', '第一条', '为规范xxx银行（以下简称“本行”）计算机档案管理，根据《中华人民共和国档案法》、国家档案局《电子文件归档及电子档案管理办法》，结合本行实际，制定本办法。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第一章　总　则', '第二条', '本办法明确了计算机档案归档、保管、调阅与销毁等方面的职责、程序等内容和要求。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第一章　总　则', '第三条', '本办法适用于本行计算机档案的管理。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第一章　总　则', '第四条', '本办法所称的计算机档案，包括计算机软、硬件的随机资料、介质、文档，以及各种技术规范、标准、制度、方案、计划、技术报告等。计算机档案的管理是对计算机档案的形成、保管、借阅、销毁等环节的控制。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第二章　职责与权限', '第五条', '信息科技部档案管理员负责以下内容：（一）各种文件、资料的收集、整理、归档、调阅、销毁等工作。（二）不同载体的计算机档案的集中管理。（三）编制各类档案检索工具、主动提供档案利用条件，为各项工作服务。（四）做好档案的保密工作。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第二章　职责与权限', '第六条', '信息科技部负责人负责权限内档案借阅的审批。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第二章　职责与权限', '第七条', '信息科技部负责计算机档案的管理与检查工作。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第二章　职责与权限', '第八条', '审计部负责监督计算机档案销毁的实施。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第二章　职责与权限', '第九条', '本行分管信息科技的副行长负责重要档案借阅的审批。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第三章　内容与要求', '第十条', '计算机档案分为以下四类：（一）文字资料类：各类科技文件、规范、会议纪要、资料、计算机专业书籍、计算机及附属设备的随机资料。（二）系统运行类：信息系统应用过程中形成的业务数据、各种密码档案、运行维护日志、工作日志、管理日志等技术档案。（三）软件档案类：各种系统软件、项目开发技术档案、应用软件源代码、目标程序、开发和运行环境的介质及其相关的文档资料等。（四）综合管理类：申请报告、信息科技项目合同等信息科技日常工作产生的电子档案。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第三章　内容与要求', '第十一条', '计算机档案的收集范围，按国家关于文件归档的现行有关规定执行。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第四章　管理流程', '第十二条', '计算机档案的收集（一）计算机档案的收集要求1．文字处理技术形成的计算机档案，收集时应注明文件存储格式和属性。2．用扫描仪等设备获得的图像计算机档案，如果采用非标准压缩法，则应将相关软件一并收集。3．用计算机辅助设计或绘图等获得的图形计算机档案，收集时应注意其对设备的依赖性，以及易修改性等问题，不可遗漏相关软件和各种数据。4．用视频设备获得的动态图像计算机档案，收集时应注意收集其压缩法和软件。5．用音频设备获得的计算机档案，收集时应注意收集其属性标识和相关软件。6．由计算机多媒体技术制作的计算机档案，其中包含前面所示的两种以上的信息形式，收集时应注意参数准确、数据完整。7．通用软件产生的计算机档案，收集时应注意收集其软件型号和相关参数，专用软件产生的计算机档案，收集时必须连同专用软件一并收集。8．计算机系统运行和信息处理等过程中涉及的各类参数、管理数据等应与计算机档案一同收集。（二）归档计算机档案同时存在纸介质和磁介质或其他载体形式的文件时，则应在内容、相关说明及描述上保持一致。（三）计算机档案的收集方法1．及时按要求制作计算机档案备份。2．每份计算机档案均须在计算机档案登记表中登记。3．计算机档案登记表如果制成电子表格，应与备份文件一同保存，并附有纸张打印件。4．计算机档案性质代码：T—文本文件；I--图像文件；G--图形文件；V--影像文件；A--声音文件；M--多媒体文件；P--计算机文件；D--数据文件。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第四章　管理流程', '第十三条', '档案的整理及归档（一）计算机档案的整理，应按内容、保管期限、密级等因素相对集中。（二）计算机档案应按《档案著录规则》著录，并制成机读目录。（三）归档计算机档案应填写登记表。（四）应定期将符合归档条件的计算机档案，按档案管理要求的格式存储到可长期保存的脱机载体。（五）计算机档案归档范围、保管期限的划分，参照国家的现行有关规定执行。（六）计算机档案的归档时间：逻辑归档应实时进行，物理归档应定期完成。（七）计算机档案采用的载体按优先顺序分别是：原版光盘、一次写光盘、可擦写光盘、磁带等。禁用软磁盘作为归档计算机档案长期保存的载体。（八）存储计算机档案的载体或包装盒上应贴有标签，标签上填写编号、名称、密级、保管期限、硬件及软件环境等。（九）档案保管部门应配备相应的处理设备，以保证完成计算机档案的检验工作。归档的每套载体均应接受检验，合格率应达到100%。检验项目包括：载体是否可读，是否清洁；有无病毒；核实电子档案的完整性和有效性审核手续；核实软件、说明资料等是否齐全。检验有误的应退回形成单位重新制作。（十）计算机档案实行统一编目、集中分类、归档管理。目录清单应有完整的控制信息，包含媒体类别、信息类别、文件所有者、卷序列号、文件名称及其主要内容、项目编号、技术环境、相关软件、版本、数据类型、格式、被操作数据、检测数据、重要性等级、密级、建立日期等以保证电子档案的质量。（十一）归档前应由档案形成部门对电子文件的有效性和完整性进行审核，并由负责人签署意见。如果档案形成单位采用了某些技术方法保证档案的有效性和完整性则应把其技术方法和相关软件一同移交给档案管理部门。（十二）因文件量太大而需要存储在磁盘阵列上的档案资料应实现双机热备份或异地备份，并定期检查，确保数据安全。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第四章　管理流程', '第十四条', '档案的保管（一）计算机档案室必须符合防火、防潮、防尘、防腐蚀、防虫蛀、防静电、防电磁场及防盗等安全要求。密级档案和特别重要的档案资料、软件、数据应有保护措施，进行多重备份并异地存放。（二）磁介质档案保管除应具备纸介质档案保管的要求外，还应符合下列条件：1．归档载体应作防写处理。不得擦、划、触摸记录涂层。载体应直立存放，做到防尘、防变形。2．环境温度选定范围: 22℃— 24℃；相对湿度选定范围：40%—55%。3．存放时应注意远离强磁场，并与有害气体隔离。（三）计算机档案的有效性保证1．每年对计算机档案涉及的形成部门和档案保管部门的设备更新情况进行一次检查登记。2．磁性载体上的计算机档案，每4年转存一次，并检验有效性。原载体同时保留时间不少于4年。3．设备环境更新时应确认库存载体与新设备的兼容性，如不兼容，应进行计算机档案的载体转换工作，原载体同时保留时间不少于3年。（四）计算机档案按保存年限可分为：永久保存、十五年、五年和三年。1.永久保存的计算机档案主要有：信息系统应用过程中形成的业务数据、各种密码档案；各种系统软件、项目开发技术档案、应用软件源代码、目标程序、开发和运行环境的介质及其相关的文档资料等。2.存放十五年的计算机档案主要有：各类科技文件、规范、会议纪要、资料、计算机专业书籍、计算机及附属设备的随机资料。3.存放五年的计算机档案主要有：申请报告、信息科技项目合同等信息科技日常工作产生的电子档案。4.存放三年的计算机档案主要有：系统运行维护日志、工作日志、管理日志。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第四章　管理流程', '第十五条', '档案的借阅（一）信息科技部应严格限定技术档案的借阅范围，各种计算机档案的借阅必须履行申请、审批、登记手续。（二）档案的封存载体不得外借，使用时使用拷贝件并遵守保密规定，使用者对电子档案的使用应在权限规定范围之内。（三）具有保密要求的电子档案上网时必须符合国家或部门有关保密的规定，要有稳妥的安全保密措施。（四）涉及技术档案中的保密内容，未经信息科技部门负责人批准，任何人不得以介绍、复印、拷贝、发表文稿等方式外泄。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第四章　管理流程', '第十六条', '档案的销毁（一）对过期和报废的计算机档案及介质的销毁鉴定，按国家现行有关规定执行。（二）计算机档案的销毁，应在办理审批手续后，在审计部的监督下方可实施。（三）非保密计算机档案可进行逻辑删除。属于保密范围的计算机档案被销毁时，如存储在不可擦除载体上，须连同存储载体一起销毁并在网络中彻底清除。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第五章　附　则', '第十七条', '本办法未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第五章　附　则', '第十八条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机档案管理办法》', '第五章　附　则', '第十九条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第一章　总　则', '第一条', '为规范xxx银行（以下简称“本行”）计算机用电管理，确保用电安全和电力正常供给，根据《中华人民共和国计算机信息系统安全保护条例》结合本行实际，特制定本制度。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第一章　总　则', '第二条', '本制度适用于本行各部室及所属团队，各支行（含营业部，下同）。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第三条', '供电系统要注意防潮、防尘、绝缘、通风，要定期检查。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第四条', '供电系统的维护工作由本行指定专业人员进行维护。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第五条', '严禁在UPS输出端连接非业务类的电器设备。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第六条', '各单位要做到安全用电、节约用电。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第七条', '信息科技部每月应对供电设备及供电线路的运行状态进行一次检查。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第八条', '信息科技部要定期对供电系统的接地情况进行测量。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第九条', '信息科技部每季度应对配电电路的微机供电电压、辅助电路供电电压、UPS供电电压、线路各连接部件的牢靠程度以及各类参数进行一次测量。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第二章　用电管理', '第十条', '信息科技部每年应对配电柜的仪表进行一次校对。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第三章　UPS管理', '第十一条', 'UPS系统使用环境的温度应保持在15℃-25℃之间；辅助区温度控制在18-28℃，湿度控制在35-75%；UPS周围环境要保持清洁，以减少有害灰尘对UPS内部线路的腐蚀。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第三章　UPS管理', '第十二条', 'UPS安放空间要充裕，方便维护人员进行维护。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第三章　UPS管理', '第十三条', 'UPS必须有良好的接地系统，接地电阻应该小于5Ω。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第三章　UPS管理', '第十四条', 'UPS开、关机要求：（一） 应按照正常的开机顺序开机。先送市电给UPS，使其处于旁路工作，再逐个打开负载，先开冲击电流较大的负载，再开冲击电流较小的负载，然后UPS面板开机，使其处于逆变工作状态。（二） 应按照正常的关机顺序关机。先逐个关闭负载，再将UPS面板关机，使UPS完全关闭，然后再将输入市电断开即可。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第四章　电池管理', '第十五条', '电池应安装在清洁、阴凉、通风、干燥的地方，要避免受到阳光、暖气片或其他辐射热源的影响。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第四章　电池管理', '第十六条', '电池应正立放置，不可倾斜，每个电池间端子连接要牢固，接触要充分。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第四章　电池管理', '第十七条', '电池环境温度应在25℃左右。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第四章　电池管理', '第十八条', '定期对电池进行放电养护。放电实验三个月进行一次，放电方法：UPS带载（负载50%以上），然后断开市电，使UPS处于电池放电状态，放电持续时间视电池容量而定，一般电量指示灯剩下两格即可（电池不可以过度放电），放电后恢复市电供电，继续对电池充电。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第四章　电池管理', '第十九条', '对长期放置不用的电池，要定期进行充放电。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第五章　附　则', '第二十条', '本制度未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第五章　附　则', '第二十一条', '本制度由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机用电管理制度》', '第五章　附　则', '第二十二条', '本制度自发文之日起施行。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第一章　总　则', '第一条', '为规范xxx银行（以下简称“本行”）计算机硬件设备的运行管理，满足本行不断发展的业务需要，根据《中华人民共和国计算机信息系统安全保护条例》结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第一章　总　则', '第二条', '本办法所指计算机硬件设备包括各支行、部室所使用的服务器、计算机、存贮设备、终端、打印机、UPS、网络通讯设备、刷卡设备、自助机具、复印传真一体机等；本行中心机房的小型机、服务器、存储设备、通讯设备、网络设备、UPS、精密空调、开发、测试用机、中间业务前置机、安全设备等。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第一章　总　则', '第三条', '本办法适用于本行硬件设备的运行管理。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第二章　职责与权限', '第四条', '信息科技部负责本行计算机系统硬件设备的检查与维护，负责与相关设备厂商联系、沟通，负责报废设备的审批工作。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第二章　职责与权限', '第五条', '各支行（部）合规员为设备管理员，负责设备管理的日常事务，对设备的正常、安全使用负有督导责任。设备使用人为第一责任人。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第六条', '使用人员必须严格按照计算机设备操作规程操作，爱护机器设备，开、关设备应有时间间隔，不得凭主观意识随意操作，避免人为损坏。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第七条', '计算机及网络设备使用环境应具备温度在22～24摄氏度之间，湿度在40%～55%，配备具有良好接地的电源等条件，以保证设备正常运行，延长设备的使用寿命。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第八条', '重要计算机设备必须在配备UPS电源或稳压电源的电网环境下运行。为确保人身及设备安全，严禁在加电状态下拔插电源插头；设备运行过程中不得振动、搬运或碰撞。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第九条', '连接设备电源时，应首先查看清楚供电电压的类型和范围是否符合设备的输入电压要求，不符合要求时，不得连接和开启电源。不得在UPS输出端使用电炉、取暖器、吸尘器、荧光台灯等其他非业务类电子设备。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十条', '设备使用人应及时在设备管理系统中登记设备信息，并有义务妥善保管好随机资料和软件介质，以备将来设备保修和维修之用。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十一条', '计算机设备的键盘、鼠标、驱动器和显示器等设备是易损件，使用过程中要轻拿轻放，保持清洁，驱动器在读\写时，禁止移动介质。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十二条', '采取必要的防尘措施，设备使用人应保持设备周边环境的清洁，及时使用专用清洁剂擦拭屏幕及外壳，去除灰尘及污染物。保持设备工作台整洁，不得将无关物品堆放于设备及工作台上，防止机器损坏或发生短路故障。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十三条', '各类服务器设备由管理员统一管理，管理员凭口令进行操作和管理，其他人员未授权不得以管理员身份登录，更不能不经管理员同意关闭服务器。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十四条', '计算机设备出现故障时，设备使用人应及时与信息科技部联系，信息科技部统一负责维护及维修管理，严禁非专业技术人员擅自拆卸设备，更换零（部）件。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十五条', '对长期不使用的设备，使用人应定期进行加电检查；对UPS应每季进行充放电维护。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十六条', '计算机管理（一）每台计算机管理的第一责任人是使用者本人。（二）非专业技术人员严禁拆卸计算机部件，未经设备管理员允许不得随意更换使用人。（三）使用人要爱护计算机设备，计算机在工作时严禁拍打、摇晃、移动，以免损坏设备。（四）计算机的使用者，应该对计算机的性能指标、硬件配置、操作系统、售后服务有全面的了解，以便在今后的使用过程中遇到问题时采取有效的应对措施。（五）能固定的设备必须固定在机柜内，不得随意摆放在机柜外。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十七条', '打印设备维护管理（一）个人使用的打印设备由个人负责管理，共享的打印设备各支行（部）应指定专人负责管理。（二）使用人注意保护打印机的数据线，不得硬拉，硬扯，挤压数据线，不得与强电磁线靠近放置。（三）使用人应轻按打印机控制面板按键，不要随意用力击打按键。    （四）本着厉行节约的原则，使用人对打印耗材要做到物尽其用。（五）各支行（部）要更换或移动打印设备时，必须关闭打印设备，以防止烧毁打印机数据端口或计算机打印端口。（六）打印机应保持整洁、安放稳固，使用人要经常清洁机身，及时更换色带。（七）在长时间没有打印任务时，使用人应关闭打印机电源。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十八条', '其他电子外设或辅设，如指纹仪、身份证识别器等应按上述管理要求进行管理。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第十九条', '由于人员调动或调岗，需要设备移交的，设备使用人应在设备管理系统进行设备转让。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第三章　管理内容', '第二十条', '信息科技部对中心机房和各支行、部室的计算机硬件设备的运行情况定期进行检查与维护，以保证其正常运行。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第四章　附　则', '第二十一条', '本办法未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第四章　附　则', '第二十二条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机硬件设备运行管理办法》', '第四章　附　则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第一章　总　则', '第一条', '为规范xxx银行（以下简称“本行”）计算机系统故障的处理，及时恢复业务运作，保证业务的连续性，根据《中华人民共和国计算机信息系统安全保护条例》结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第一章　总　则', '第二条', '计算机系统故障指保证系统正常运行的软件、硬件在日常运行过程中出现的技术类问题。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第一章　总　则', '第三条', '信息科技部实行业务期间值班制度，设立专用值班电话并明确专人负责，值班电话应向辖内各支行和上级科技管理部门公布，确保24小时畅通，便于及时协调解决系统运行中出现的问题。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第四条', '计算机系统发生故障后，支行（部）人员应及时在省联社“在线平台系统”将故障发生前系统故障现象、故障特征详细上报给信息科技部，技术人员作出基本判断后应按不同故障类型执行相应的处理方法。    第五条　计算机系统故障处理主要包括三种方式：网络远程维护、电话指导、技术人员现场维护。（一）网络远程维护。指系统检查、程序应用、文件配置、超级用户权限方可进行的操作等特别故障处理，技术人员通过网络远程登录进行处理。（二）电话指导。指综合业务系统、信贷管理系统、WIN终端设置、打印机设置等常见性操作故障，技术人员根据故障特征，结合实际操作以电话方式进行指导。（三）技术人员现场维护。指计算机网络故障、硬件设备故障，经技术人员和电信部门联合测试确认支行（部）为故障点所在地，须专人现场维护的，由技术人员到现场维护。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第六条', '外单位维保技术人员进入支行（部）机房须由支行（部）行长或运营主管、信息科技部工作人员陪同。因维修需要更换支行（部）机房的硬件设备，须经信息科技部认可。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第七条', '信息科技部值班人员和各支行（部）运营主管应及时登记计算机运行日志或运营主管坐班日记，详细记录故障及修复情况。网络设备故障、系统恢复等重要故障性操作，信息科技部技术人员在次日实行回访制度。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第八条', '信息科技部根据支行（部）故障发生频率合理确定一定数量的终端、打印机等日常使用设备作为备份设备。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第九条', '信息科技部针对易出现故障的终端安装一键还原等备份工具，并定期下发杀毒软件和升级包，保证系统运行的稳定性。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第十条', '因人为原因、恶意破坏等行为造成计算机系统故障的，将追究当事人责任。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第十一条', '严禁各支行（部）使用业务主机、终端连接互联网，严禁内外网互联。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第十二条', '严禁在业务用机上安装游戏或其它非业务软件，包括媒体播放器、手机控件、网络游戏、单机游戏和其它可能对系统造成危害的软件。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第十三条', '外来U盘等存储设备接入内网时，必须全盘杀毒后方可使用。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第十四条', '未经信息科技部允许，任何单位或个人不得擅自删除、卸载业务主机中的系统软件、文件或程序。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第二章　故障处理', '第十五条', '计算机系统故障响应和处理时间。（一）网络故障，支行（部）电话报故障后，信息科技部应在半小时内确定网络故障类型，路由交换机发生故障的，必须专人专车将需更换的设备送达支行（部）或由信息科技部派人上门检修；线路供应商的设备或线路故障，信息科技部应督促线路供应商一小时内派人上门处理，线路较远的支行可适当延长在途时间。（二）软件故障，发生一般性故障时，信息科技部应在半小时内处理完毕（不含杀毒时间）；发生较严重的故障时，信息科技部应在两小时内处理完毕（不含杀毒时间）；发生严重故障时，信息科技部必须在半天内处理完毕，同时说明原因。所有问题解决后要跟踪支行（部）的运行状况，确保问题真正解决到位，并向分管行长汇报处理情况。（三）硬件故障，信息科技部统一时间组织送修，不超过半个工作日；UPS、WIN终端本地维修时间两个工作日，异地送修不超过两周，且必须有备件代替；主机、打印机等信息科技部有备用设备的，一至三小时内上门更换。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第三章　附　则', '第十六条', '本办法未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第三章　附　则', '第十七条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机系统故障处理管理办法》', '第三章　附　则', '第十八条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第一章　总　则', '第一条', '为规范xxx银行（以下简称“本行”）计算机系统的运行管理，规范操作，依据《中华人民共和国计算机信息系统安全保护条例》、省联社《信息科技运行管理制度》等规定，制定本办法。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第一章　总　则', '第二条', '概念释义（一）本办法中计算机系统运行管理包括：中心机房、设备运行、主机系统、网络系统、操作权限与口令管理等。（二）中心机房是指容纳并集中运行计算机业务处理、管理信息或网络通信系统等的专用物理场所（三）设备运行管理是指对运行中的主机设备、网络通信设备及外围设备的管理。（四）主机系统运行管理是指对机房运行的主机、操作系统、数据库和中间件等操作、维护过程的管理。（五）网络系统管理是指对所辖网络通信系统运行的管理。（六）操作权限与口令管理是指对主机系统、网络通信运行实行操作权限与口令的管理。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第二章　职责与权限', '第三条', '信息科技部负责人职责：（一）负责主机系统参数修改的批准。（二）负责内部网络规划与开通的审核、审批。（三）负责内部网络变更应用的审批等。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第二章　职责与权限', '第四条', '信息科技部职责：（一）负责实施与监督计算机机房运行环境。（二）负责内部网络的规划、实施、安全管理及维护工作。（三）负责对业务网络接入方案做出安全性评估及审定等。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第三章　内容与要求', '第五条', '计算机系统运行应遵循“严密制度、控制风险、规范操作、严格管理”的原则。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第三章　内容与要求', '第六条', '机房运行管理（一）机房建设应符合省联社机房建设相关规范。（二）机房为重要设备运行管理场所，除机房工作人员外，其他人员未经批准不得进入。确因工作需要必须进入的，需填写“机房出入登记簿”，经批准后由机房工作人员陪同进出。（三）机房管理实行责任制。信息科技部应根据规范要求实施管理、操作、监控和维护保养。机房工作人员应具备相应的机房管理知识。（四）机房工作人员应每天定时巡检设备运行情况，及时维护保养，发现问题立即报告并处理，必要时可实行全时监控。（五）机房设备的操作规程、制度应当明示上墙，安全防护措施齐备，各项操作必须在确保安全的前提下依照规程进行，严禁违规操作。（六）机房的温度、湿度、粉尘度等各项指标必须严格控制在规定范围之内。（七）机房应保持卫生清洁，严禁吸烟、会客、聚餐和娱乐等与机房管理无关的行为。（八）机房值班人员要认真做好机房出入登记、设备巡检登记、操作记录登记等各项记录，登记资料内容必须完整，并定期整理归档。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第三章　内容与要求', '第七条', '设备运行管理（一）设备安装时，相关技术人员必须按规定的步骤操作，其中关键设备的安装必须由原厂商（供货商）技术人员现场支持。（二）设备在投入使用前，应依据供货厂商提供的项目和相关指标，经测试合格后才能使用。（三）设备升档要经过充分的技术论证和审批，升档过程中应保证现有生产系统的正常运行。（四）设备管理员应做好核心设备的日常维护工作：1．做好设备的日常监测、检查、记录，及时掌握设备的运行状况；2．设备发生故障时应及时维修，必要时，通知供货商或维保单位的技术人员到场解决。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第三章　内容与要求', '第八条', '主机系统运行管理（一）维护人员须按照规定的步骤执行，并详细记录维护工作情况。 （二）系统维护人员应密切监控系统运行状况，及时处理系统运行中出现的故障，并详细记录系统故障的处理过程。对影响较大且影响业务正常办理的故障，须及时报告信息科技部负责人。对应用系统的故障，及时反馈给项目开发人员，并对故障产生原因进行认真的分析总结。（三）系统维护人员每日须对系统日志进行检查，具体分析系统可能存在的安全隐患和漏洞，及时研究并提出补救性措施，经批准后实施，确保系统运行的安全。（四）系统维护人员修改系统参数，须经负责人批准且双人临岗，修改前须进行系统备份或参数备份，详细记录操作过程并整理归档。（五）严格控制对业务数据库的非应用性操作。特殊原因必须修改业务数据库的，由需要修改的部门提出数据修改申请，报单位分管领导批准后，双人上机操作并做好修改前的数据备份。（六）系统维护人员必须制定备份计划，对备份的时间、内容、级别、人员、保管期限、异地存取和销毁手续等进行明确规定，需用备份介质对运行系统进行故障恢复时，视同重要事件进行管理控制。（七）主机系统、应用软件升级或更新、新旧系统切换、应用系统年终结转、应用系统存贷结息等重要操作，系统维护人员应与相关业务部门密切配合，共同制定详细的工作计划和方案。1．应用系统增加新业务功能的，系统开发人员须提供业务功能说明书、操作手册、用户手册、软件包，系统维护人员在指定FTP服务器存储，并提前2个工作日向应用单位发出书面的安装新业务程序通知，各应用单位须在规定时间内做好新业务程序安装工作。2．主机系统硬件、操作系统升级，由相关公司提供升级方案，系统维护人员协助相关公司技术人员进行操作维护。3．数据库、中间件升级的，由相关公司提供升级方案和技术支持，系统维护人员根据升级方案，负责实施。4．应用软件后台服务程序升级，由应用软件开发人员提供升级方案、模块功能说明书，并通知应用网点。在业务周期结束后，由系统维护人员负责实施。5．应用软件前台服务程序升级，由应用软件开发人员提供升级方案、模块功能说明书、操作手册、用户手册、升级程序`包等，由系统维护人员在指定FTP服务器存储，并提前2个工作日发出前台服务程序升级通知，各单位在规定时间做好升级工作。（八）计算机病毒的防范工作应明确专人负责，并建立本行的计算机病毒防范管理制度，定期进行计算机病毒的检查、清除。（九）建立计算机系统运行日志，具体记载系统运行情况，运行日志作为机密档案进行管理。（十）系统维护人员应定期对系统运行情况进行检查，分析系统资源应用及运行情况，并提出系统资源的优化报告。（十一）运行系统的配置、技术方案、软件和业务测试方案的修改由信息科技部门提出，报分管行长审批后实施。（十二）系统运行与系统开发和维护要严格分离，确保信息科技部内部的岗位相互制约。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第三章　内容与要求', '第九条', '网络通信管理（一）区域性网络系统改造按省联社网络设计相关规范执行，网络系统改造前应将改造方案形成规范性文档，报省联社科技管理部批准后实施。（二）网络改造必须在信息科技部统一规划下进行，各支行不得擅自增加、删除网络节点及修改网络参数，确需增加、删除网络节点或修改网络参数的，应严格履行审批手续。并报省联社计算机中心备案。 （三）严格控制远程登录，建立远程登录登记簿，详细记录登录原因、登录人员、起止时间、批准人等内容。登录结束后须及时更改相关的密码、口令。（四）网络拓扑结构图、网络通信设备的配置参数、网络地址（如IP地址等）等网络通信的技术性资料应及时归档保管，并建立严格的保密制度。（五）本行内部网络与外单位的联网必须按省联社关于外联业务接入网络的相关要求执行。（六）网络维护人员应做好网络通信系统的日常运行维护工作： 1．密切监控网络的运行，及时排除网络运行中出现的故障，并做好网络运行和维护的记录。 2．定期分析网络运行状况，形成网络性能优化方案，优化方案报信息科技部负责人批准后实施。 3．严格控制内部网络各节点的通信，防止各种非法访问。4．网络的通信线路应有备份，对长期处于休眠状态的备份线路应按月进行检测。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第三章　内容与要求', '第十条', '权限与口令管理（一）信息科技部须对主机系统、网络通信运行实行操作权限与口令的管理，并建立操作权限与口令管理登记簿。（二）操作口令的设置应具备安全性、保密性，密码长度不少于6位，由字母与数字混合组成。（三）操作口令应定期修改，使用时间一般不得超过一个月，如发现或怀疑泄露应立即更改，修改后须在口令管理登记簿上登记用户名、修改时间、修改人等内容，以备查考。（四）主机、路由器等重要设备的超级用户口令由信息科技部负责人指定专人设置与管理，并由口令设置人员将口令装入密码信封，在骑缝处加盖个人名章或签字后登记存档。特殊情况需启用封存口令时，必须经过部门负责人书面批准。使用完毕后，须立即更改并封存，同时在口令管理登记簿中登记备查。（五）普通用户的建立必须由用户使用者提出申请，报信息科技部负责人审批后，由系统、网络维护人员遵循有限授权原则建立。（六）相关被授权工作人员调离岗位，信息科技部负责人须指定专人接替并对口令立即修改或将用户删除，同时在口令管理登记簿中登记备查。（七）掌握系统或设备最高用户权限的人员须严格控制该系统或设备的多余用户，一经发现，立即删除，并记录有关信息。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第四章　管理流程', '第十一条', '网络规划（一）全辖内部网络系统的规划、设计及设备安装，统一由信息科技部网络管理员负责，并要求严格遵守保密制度。（二）生产业务网络的规划，原则上必须严格将网络和办公网实行完全的物理隔离。（三）由于网上银行、远程办公等特殊应用，不能做到完全物理隔离的互联接入应用规划，必须采用国家级计算机信息安全和保密机构，以及中国人民银行计算机信息安全主管机构认可的计算机网络安全架构、设备进行隔离和规划，并应按照上级主管部门相关要求执行或备案。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第四章　管理流程', '第十二条', '开通流程（一）由具体应用项目部门填写《内部网络开通、变更审批表》，并提交本部门负责人审批后上报信息科技部。（二）信息科技部负责人对《内部网络开通、变更审批表》进行审核后，交网络管理人员拟定网络接入方案。（三）由信息科技部对该应用网络接入的安全性作出安全性评估，信息科技部负责人审批，最后由网络管理人员实施。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第四章　管理流程', '第十三条', '变更流程（一）由应用变更需求部门或个人填写《内部网络开通、变更审批表》，并提交本部门负责人审批后上报信息科技部。（二）信息科技部负责人对《内部网络开通、变更审批表》进行审批后，交网络管理人员实施。（三）对涉及接入方式和安全架构发生重大变化的应用变更，应按项目开通流程执行。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第四章　管理流程', '第十四条', '安全管理（一）网络管理人员负责本行网络（包含局域网、广域网）设备的安装、调试和维护工作，改变网络路由配置和通信地址等参数的操作，必须具有包括时间、目的、内容及维护人员等要素的书面记录，认真填写《网络日志检查登记薄》。（二）网络系统专用主机设备的用户口令及超级用户口令仅限于信息科技部网络管理人员使用，其口令使用“双人保管，各保管一半”的方式进行管理。口令要求每月进行不定期的更换两次以上，设定口令后要求密封存放在口令保险柜中，以作备份应急时使用。（三）信息科技部网络管理人员应经常巡查网络系统是否有可疑设备及非法电缆的接入，定期检查网络日志，随时监测和定期检查网络运行状况，对获得的信息应进行分析，发现安全隐患应即时通知安全隐患所辖区负责人和系统管理员并报告信息科技部负责人，提前制止可能的网络犯罪，保护网络系统的安全。（四）网络管理人员应实时注意观察系统的运行情况，检查登录日志文件，若发现有可疑人员侵入，应立即采取网络应急措施，报告信息科技部负责人。（五）网络系统实行物理隔离、访问控制、防火墙、网络监控、定期杀毒等安全手段，防止非法用户侵入系统。（六）实行生产业务网络与办公网络隔离、办公网与互联网隔离的方式进行管理。（七）任何内部网络终端用户，未经信息科技部许可不得擅自采用任何网络互连设备或其它方式接入网络或其它用途的计算机设备，实行专机专用，专人负责。（八）网络系统技术文档资料、应用软件及参数设置文件，应交档案管理人员统一管理。（九）严禁任何人向非授权机构和个人泄露内部网络配置参数和信息资料，擅自更改内部网络的接入方式和架构，以及相关网络和计算机设备的配置参数。（十）严禁任何人将未经信息科技部许可的计算机及通信设备接入本行内部网络、挪用或移动内部网络通信设备和线路。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第四章　管理流程', '第十五条', '网络维护（一）严禁非网络管理人员对网络系统主机进行任何操作。（二）重要网络通信硬件设施、应用软件设施及网络参数配置应有备份。（三）支行系统管理人员应协助信息科技部管理好本支行的网络系统设备，不定期地对设备进行检查，若发现设备有异常情况或有可疑设备、非法电缆的接入等，应立即向信息科技部或有关部门反映，及时处理。（四）各使用部门按照要求加强维护，认真执行有关规定。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第四章　管理流程', '第十六条', '检查（一）检查内容：是否有未允许接入内部网的设备，内部网生产业务用机是否为单应用内部生产业务网络，是否做到“单一应用，专机专用”。（二）检查时间信息科技部网络管理人员对内部网用户的连接和使用实行不定期检查。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第四章　管理流程', '第十七条', '处置发现问题，信息科技部网络管理人员可立即切断问题设备与内部网的连接，通知应用部门负责人，上报信息科技部负责人，责令其立即整改，并可视情况的严重程度，按本行员工违规违章行为管理规定对违规人员进行处罚。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第五章　附　则', '第十八条', '本办法未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第五章　附　则', '第十九条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机系统运行管理办法》', '第五章　附　则', '第二十条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第一章　总　则', '第一条', '为加强xxx银行（以下简称“本行”）的网络管理，保障系统安全运行，建立良好的网络故障处理反应机制，根据《中华人民共和国计算机信息系统安全保护条例》结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第一章　总　则', '第二条', '本办法适用于本行计算机网络运行的管理。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第二章　人员管理及职责划分', '第三条', '信息科技部是本行网络的管理部门，负责安排和实施本行的网络管理工作。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第二章　人员管理及职责划分', '第四条', '信息科技部应配备专职网络管理员，负责全辖的网络管理工作。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第三章　网络设备管理', '第五条', '本行所有网络设备，包括路由器，交换机，防火墙，光纤收发器、终端路由器、DTU等，均由信息科技部统一配置和管理。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第三章　网络设备管理', '第六条', '生产环境中所有设备的接入和移除都必须经过信息科技部审批，并建立登记簿进行审批登记，未经审批同意，任何部门和人员不得私自在生产环境网络中接入和移除设备。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第三章　网络设备管理', '第七条', '网络中连接的重要网络设备必须有备份设备，以便在该设备出现故障时能及时更换，确保业务的正常运行。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第三章　网络设备管理', '第八条', '所有网络设备配置修改，都必须经过信息科技部审批，并由网络管理员负责实施，未经信息科技部批准的，任何部门、任何人员不得随意修改网络设备的配置。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第三章　网络设备管理', '第九条', '在修改网络设备配置前，信息科技部应组织讨论配置修改的可行性，并检测备用设备是否正常，以防备配置出现问题时及时更换。对于测试需要修改的配置在测试结束后应该快速恢复原配置。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第三章　网络设备管理', '第十条', '网络管理员更改网络设备的配置后，要及时更新相关网络技术档案（包括网络拓扑图、配置备份等）并交信息科技部档案管理员保管。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第三章　网络设备管理', '第十一条', '为保证网络设备使用可靠安全，信息科技部网络管理员必须定期与网络设备厂家联系，及时做好网络设备版本的升级和技术支持。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十二条', '信息科技部应为全辖的网络绘制网络拓扑图，并在网络拓扑图中标注网络设备、线路及管理系统性能与用途等主要特征。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十三条', '本行网络线路包括：总行至各支行、总行至黄海农商行、互联网、机关楼层与中心机房之间等；网络管理系统包括：域名（DNS）、网管等系统。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十四条', '网络管理员的职责如下：（一）及时查看网管系统及路由器、交换机、防火墙等主要网络设备指示灯的显示情况。（二）及时查看DNS、防病毒、网络管理等服务器的运行情况。（三）及时查看总行至支行、总行至黄海农商行、互联网等各类线路主要端口的输入、输出数据流量。（四）定期对总行至支行、总行至黄海农商行、互联网等各类线路质量进行检测。测试参数不少于1000个包，每个包不小于1024B，对每条线路的测试结果（丢包率、延迟时间等）要记录存档。（五）定期备份本行网络中的重要系统、参数与数据。（六）每月应对网络的稳定性、完整性和安全性进行认真评估，评价网络的运行状况，归纳网络故障，分析故障产生的原因，总结排除故障的经验。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十五条', '严禁使用本行网络散布反动、淫秽、惑众的言论或图片，严禁恶意传播计算机病毒。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十六条', '网络管理员对具有扫描、监视、控制等功能的网络管理软件要严格管理，谨慎使用，非网络管理人员不得使用。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十七条', '未经信息科技部批准，任何人不得对网络系统中的主要设备、线路及管理系统进行设置或更改等操作。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十八条', '确实需要对网络进行维护或试验时，信息科技部要在充分调研的基础上，制定技术策略，拟定维护或试验方案，备份网络系统或参数，选择维护或试验时间（尽量避开白天工作时间，必要时要发出警告通知）。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第十九条', '对于出现的网络事故，信息科技部将根据具体情况下达整改通知书，并限期整改。对人为原因造成的网络事故或没有按照整改要求限期整改的，在经过本行分管领导同意后，信息科技部可采取：警告、通报、罚款等措施予以处理。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第四章　网络管理', '第二十条', '互联网的使用管理包括：（一）计算机连接互联网必须履行审批手续；（二）互联网计算机必须按批准的方式接入互联网，不得私自接入路由器和无线网卡；（三）禁止在互联网计算机上处理内部敏感信息；（四）凡向互联网上提供或者发布信息，必须分管行长审批同意，严格执行信息上网发布审核制定；（五）禁止一台计算机同时接入内外网；（六）互联网计算机应安装防病毒软件和漏洞修补工具，并及时更新；（七）互联网计算机不得从事工作以外的事情，如游戏，占用带宽下载音乐、电影文件，炒股，聊天等。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第五章　附　则', '第二十一条', '本办法未尽事宜，按国家有关法律、法规的规定执行；本办法如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第五章　附　则', '第二十二条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《计算机网络管理办法》', '第五章　附　则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第一条', '为了保证xxx银行（以下简称“本行”）所有计算机设备的安全、持续、稳定地运行，根据《商业银行信息科技风险管理指引》，以及省联社、银监局相关的计算机管理制度，配合本行桌面安全管理系统的运行，特制定本办法。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二条', '本办法适用于辖内所有正式员工，以及需要使用本行电脑的非本行人员。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第三条', '本办法所指的计算机设备包括用于办公的台式机、笔记本、终端、平板电脑和配套的 U 盘、移动硬盘、打印机、扫描仪等。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第四条', '按照“谁使用，谁负责”的原则，使用人对配给个人的计算机设备负有保管和安全使用的责任，要做好防尘、防潮、防火、防盗工作，并按照规范使用计算机设备。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第五条', '总行信息科技部对所有接入本行网络的计算机设备安装桌面管理系统客户端，用于对所有的计算机设备进行合规检查和准入控制，任何人不得擅自对桌面管理系统进行修改、破解、删除。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第六条', '任何使用人在未经他人许可的情况下，不能使用他人或其它未经授权的计算机设备，也不得将所使用的计算机设备任意转借他人。确因工作需要，使用人可以允许非本行人员在受监控的情况下操作其负责的计算机设备，但该使用人应承担使用过程中的全部责任。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第七条', '注意预防病毒、木马、恶意代码、黑客程序等破坏系统和数据、影响计算机设备的运行。严禁使用来历不明的软盘、光盘、U 盘等设备，不轻易接收或打开来历不明的邮件，不轻易打开不明链接，不登录色情、暴力和反动网站。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第八条', '计算机设备统一由信息科技部管理人员进行管理，自己严禁私自拆开机箱、改变硬件设备的配置。若发现计算机设备硬件故障或需安装软件，可向信息科技部或外包服务人员反映，由信息科技部安排技术人员或外包服务人员到现场解决问题。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第九条', '办公用计算机的计算机名和 IP 地址等网络配置信息由信息科技部统一分配、统一安装桌面管理系统客户端进行管理，严禁随意更改、删除，以免影响网络功能的使用。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十条', '使用人是维护计算机设备的第一责任人，负责计算机设备的使用与维护。机关大楼各办公室中的网线、交换机等公共设备由信息科技部负责管理维护，网点的网线、交换机等公共设备由该网点负责日常管理，由外包服务人员负责维护。在遵守本管理办法的前提下，出现本人无法解决的故障时，应向信息科技部提出申请，由信息科技部派遣技术人员维护维修。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十一条', 'U 盘、移动硬盘等便携式存储设备在接入内网时必须经过信息科技部同意并进行注册后方可接入。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十二条', '在使用计算机设备时不得擅自改变预安装的软件，不得安装与工作无关的、非法使用的其他软件，如聊天软件、游戏软件、代理服务软件、娱乐软件等；如确因工作需要安装其他软件的，必须得到信息科技部技术人员的确认。需要信息科技部技术人员协助安装的，可以向信息科技部申请协助。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十三条', '不得在办公使用的计算机设备上使用黑客、系统破解、端口扫描或口令破解等软件。不得使用计算机设备制造和散布各种恶意电脑程序，包括电脑病毒、电脑木马程序、电脑蠕虫程序等。不得使用计算机设备执行网络或主机扫描、网络监听、网络拒绝服务攻击。不得擅自监控网络流量，不得针对网络上的设备和系统运行安全测试工具和软件。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十四条', '办公用计算机及系统软件必须设定用户密码。办公电脑必须开启屏幕保护功能并设立屏保密码，长时间离开计算机，必须锁定计算机。密码要求使用字母、数字和非字母字符的组合并保持口令的保密性。禁止将密码共享给其他人，且至少每三个月更换一次密码。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十五条', '所有的计算机设备不得通过无线网卡和无线路由器等无线设备接入内部网络。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十六条', '所有接入内部网络的计算机设备不得通过无线网卡接入互联网使用。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十七条', '未经信息科技部允许，不得将个人的移动设备、便携设备如手机、笔记本、平板等，接入内部网络。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十八条', '员工下班、出差，必须彻底关闭计算机设备的电源。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第十九条', '员工必须定期从办公用电脑和平板电脑等计算机设备中备份重要的工作文档、数据。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十条', '接入内部网络的计算机设备的备份形式采用本行注册 U 盘拷出后再进行转拷贝的形式，不得直接使用外来 U 盘或移动硬盘等存储设备进行拷贝。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十一条', '连接互联网或未连接网络的计算机设备可以采用接入 U 盘、移动硬盘拷贝的方式进行备份，但在接入前必须进行病毒检测。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十二条', '打开非本机的文件(如软盘、光盘、U 盘、邮件附件和下载软件等)之前，应先用软件杀毒。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十三条', '由于未备份、误操作等导致计算机设备中的重要的工作文档、数据丢失，可要求信息科技部给予协助恢复，但信息科技部不对数据的丢失负责。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十四条', '未经信息科技部批准不得擅自安装非指定的防病毒软件，不得私自删除防病毒软件及改变防病毒软件的设置。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十五条', '对刻意不执行本办法、漠视计算机安全工作和存在安全隐患而没有及时整改造成重大安全事故和事件的，将追究部门主要负责人和直接责任者的责任，涉嫌犯罪的移送国家司法机关处理。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十六条', '本办法由xxx银行信息科技部负责解释、修订，并作为本行计算机安全管理方面制度的细化和补充，如和制度有冲突，以制度为准。'),
(NULL, '信息科技部', '《计算机设备桌面安全管理办法》', '', '第二十七条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第一章  总 则', '第一条', '为规范xxx银行（以下简称“本行”）软件开发项目管理，完善软件开发流程，指导相关部门做好软件开发各个环节的工作，明确工作职责，不断提高开发管理水平，促进软件开发有序开展。根据《中华人民共和国计算机信息系统安全保护条例》、《商业银行信息科技风险管理指引》结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第一章  总 则', '第二条', '软件开发项目管理是指系统整个生命周期的管理，包括制定需求、可行性分析、立项、方案设计、程序开发、系统测试、使用培训、实施操作、维护等。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第一章  总 则', '第三条', '软件开发方式可划分为： （一）独立开发，是指由行内人员独立进行设计、开发和推广； （二）合作开发，是指由行内人员与合作公司人员共同进行设计、开发和推广； （三）外包开发，包括项目整体外包、部分外包。 1．项目整体外包是指完全由合作公司进行整个项目的设计、开发和推广，行内人员负责监理和验收； 2．部分外包是指项目的某些部分采用外包方式。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第二章 组织管理及人员角色', '第四条', '项目需求提出部门（以下简称“需求部门”）的主要职责如下： （一）负责提出项目需求或需求变更，并编写《业务需求说明书》； （二）负责参与业务需求管理小组组织的会议，与开发者进行《业务需求说明书》的确认； （三）负责解答项目组提出的业务需求的疑问，协助项目组进行需求分析。 （四）需求部门为项目试运行及项目推广的责任部门，解决项目试运行期间暴露的系统问题，根据试运行情况做好项目推广和上线有关工作，科技信息部协助需求提出部门做好相关工作。 （五）需求部门为项目后评价管理的责任部门，在项目上线后3 个月后，牵头组织对上线项目的后评价工作，包括：产品对原先需求的实现程序、产品对用户体验友好性、产品市场效益等。 （六）组织编写业务测试案例和业务培训教材，做好业务培训工作。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第二章 组织管理及人员角色', '第五条', '科技信息部的主要职责如下： （一）负责制定项目方案和项目计划； （二）负责承担具体项目研发、实施和研究等工作；（三）负责及时向项目开发管理责任小组上报项目风险、问题和项目变更等情况； （四）负责对项目状况进行跟踪和控制，定期向项目开发管理责任小组上报项目的进度，确保项目按时、保质完成。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第二章 组织管理及人员角色', '第六条', '信息科技管理委员会拥有重大信息系统项目开发的审批权和项目争议的裁量权，确保本行的 IT 战略规划与业务发展目标 一致，并加强安全管理。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第二章 组织管理及人员角色', '第七条', '对于重大项目，应建立项目报告制度，按年度进行重大 项目进度报告，内容包括重大项目名录、分项项目进行报告、内容总结和应对措施等。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第二章 组织管理及人员角色', '第八条', '系统分析人员主要工作职责： （一）负责开发项目的技术可行性分析工作； （二）配合做好开发项目的立项审批及上线评审工作； （三）参与开发项目实施过程中相关技术方案的设计，对于在开发过程中出现的重大技术难题参与讨论并提出可行性方案。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第二章 组织管理及人员角色', '第九条', '项目经理项目开发实行项目经理负责制，由项目经理负责项目开发的全过程，对开发项目的质量、进度全面负责，主要工作职责为： （一）组织编写项目开发及其以后各阶段中形成的相关技术文 档； （二）组织编写项目实施计划，掌握项目开发进度，沟通协调项目组内部技术和业务人员的工作； （三）定期组织召开项目例会，并按时向项目开发管理责任小组提交项目周报或月报； （四）负责开发项目详细设计方案的制定和编写，并细分软件设计工作，合理安排软件开发人员的工作； （五）控制项目开发过程中的软件质量； （六）对程序设计过程中出现的重大问题，及时沟通解决；（七） 负责做好项目单元测试、集成测试和压力测试的支持工作；（八）负责组织部门内部资源配合业务需求人员相关工作；（九）做好领导交办的其他工作。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第二章 组织管理及人员角色', '第十条', '软件开发人员软件开发人员一般由程序开发人员担任，主要作用于项目实施 过程中的开发及其以后的阶段，主要工作职责为： （一）负责项目开发过程中的软件设计，定期向项目经理提交工作进度，对在软件设计过程中出现的问题及时向项目经理汇报；（二）做好软件设计类文档的编写工作； （三）做好系统的功能性测试工作，在单元测试、集成测试和压力测试阶段提供技术支持，并对出现的问题及时进行纠正； （四）做好项目经理交办的其他工作。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第三章 制定需求', '第十一条', '需求部门组织人员完成信息系统相关资料收集和需求详细调查工作，出具《业务需求说明书》，并报送业务需求管理小组审批确认，作为实施开发工作的依据。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第三章 制定需求', '第十二条', '业务需求管理小组对提交的业务需求进行分析、整合、规范，并向信息科技部提交补充、完善后的《业务需求说明书》、《需求变更申请》等相关文档。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第三章 制定需求', '第十三条', '对于合作开发的项目，当业务需求发生变更时，需求提出部门应向业务需求管理小组提交《需求变更申请》。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第十四条', '开发项目立项前应进行充分的可行性分析，具体由需求部门负责制订计划并组织可行性分析工作。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第十五条', '需求部门明确项目目标及基本要求后，应向项目开发管理责任小组提出进行技术可行性分析的申请。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第十六条', '项目开发管理责任小组指定系统分析人员进行技术 可行性分析。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第十七条', '系统分析人员应具体负责项目的技术可行性分析，主要内容包括： （一）分析现有系统，阐述项目开发的必要性； （二）分析项目开发的技术可行性，评估项目实施风险，涉及多个技术方案的，需进行技术方案比较，并确定最终方案；（三）明确项目可利用资源，提出项目开发方式和实施进度的建议； （四）完成《技术可行性分析报告》。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第十八条', '技术可行性分析应对项目开发方式进行评估，提出行内自行开发、合作开发或外包建议，有下列情况之一的可采取合作开发或外包方式： （一）外部公司具有明显的技术优势或项目开发经验，而本行不具备的； （二）能引进先进管理模式和产品的； （三）可显著降低成本，或赢得竞争优势的； （四）行内开发力量不足的。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第十九条', '对技术方案进行可行性分析时，至少应从以下几个方面考虑： （一）开发的风险：在现有的约束条件下，设计系统并实现必需的功能和性能的可能性； （二）资源的有效性：人力资源及技术环境的支持能力； （三）技术的成熟度：相关技术的发展对系统支持的充分性； （四）系统的安全性：系统采用的安全技术及其可靠性分析。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第二十条', '项目的可利用资源包括已有的或可以获得的人力资 源等，通过对可利用资源的分析，确定技术环境的来源、渠道，提出项目的开发方式和项目实施进度的建议。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第二十一条', '测算项目投入应按照项目各阶段分别测算并提供测 算方法和依据，跨年度项目需列出年度支出计划，涉及软硬件购置的应列出配置和价格。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第四章 可行性分析', '第二十二条', '小型软件优化项目经项目开发管理责任小组批准，可不进行技术可行性分析。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第五章 立项管理', '第二十三条', '项目经过技术可行性分析，技术上可行后，由项目需求部门提出立项申请提交信息科技管理委员会审议，并负责将审议结果告知需求部门。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第五章 立项管理', '第二十四条', '开发项目立项的必要前提是由需求部门提出完整 的项目需求，并明确业务需求负责人。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第六章 方案设计', '第二十五条', '项目经理组织人员编写《系统设计》文档，文档内容一般包括概要设计、详细设计、主要系统功能流程设计、系统外部接口说明和定义等，并提交需求部门确认。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第六章 方案设计', '第二十六条', '在方案设计阶段中，用户应充分参与，确保方案设 计能满足系统需求。设计评审均以《业务需求说明书》和《系统设计规格说明书》为依据，确保系统设计满足全部需求。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第六章 方案设计', '第二十七条', '方案设计的修改文档须由文档管理人员进行归档 管理。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第二十八条', '在项目实施过程中，项目经理应严格履行好项目经理的职责，确保开发安全。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第二十九条', '对于外包项目，项目开发管理责任小组应委派专门人员担任项目经理，参加项目设计、开发全过程，保证项目目标的实现，以及交付使用后运行维护工作的顺利交接。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第三十条', '根据需求部门、科技信息部共同确认后的《详细需求说明书》，项目经理进行软件设计，形成初步的《详细设计说明书》。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第三十一条', '项目经理根据工作任务进行分解，协同软件开发人员进行任务估算。工作任务包括模块开发任务、其它任务（如操作系统、数据库、中间件安装等），其中模块开发任务主要包括设计、编码和单元测试。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第三十二条', '任务估算完成后，软件开发人员向项目经理提交个人进度安排（以里程碑或甘特图的形式表示），项目经理根据进度安排细化《项目实施计划》，提交业务需求人员确认。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第三十三条', '业务需求人员确认后，项目开发团队根据《项目实施计划》实施。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第三十四条', '在开发期间，软件开发人员每周需上交一份工作日志给项目经理，汇报工作任务进度。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第三十五条', '在开发期间，项目经理负责定期填写项目进度报 告提交项目开发管理责任小组，并定期组织召开例会。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第七章 程序开发', '第三十六条', '项目经理应根据实际的进度情况，及时调整项目计划，并将调整后的计划及时上报项目开发管理责任小组。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第八章 系统测试', '第三十七条', '项目经理根据实际的进度情况，安排软件开发人员进行功能模块测试，并完善《详细设计说明书》。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第八章 系统测试', '第三十八条', '功能测试完成后，由业务需求人员组织相关人员进行测试，完成测试后，需提交《测试报告》交项目经理审核。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第八章 系统测试', '第三十九条', '项目经理提供《测试环境应用安装文档》交由版本控制人员同步测试环境。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第九章 系统验收', '第四十条', '信息系统建设完成后，项目经理编写项目验收申请报告，并提交需求部门申请验收。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第九章 系统验收', '第四十一条', '系统主要使用部门及信息科技部门联合组成独立系 统验收小组。验收小组从功能需求及技术需求层面对系统进行综合 评估。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第九章 系统验收', '第四十二条', '验收小组应根据验收情况整理形成《系统验收报告》提交系统主要使用部门和科技信息部审阅。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第九章 系统验收', '第四十三条', '系统主要使用部门和信息科技部门负责人根据系 统测试、试运行情况签署验收意见。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十章 系统试运行', '第四十四条', '项目通过初步验收后进入系统试运行环节，项目负 责人和项目需求单位应当确定试运行范围、设定试运行目标，制定各方协调机制和试运行计划，组织相关的业务人员开展试运行。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十章 系统试运行', '第四十五条', '科技信息部应当进行系统试运行环境准备，部署信 息系统，在试运行期间提供技术支持并跟踪系统试运行情况。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十一章 系统上线与培训', '第四十六条', '项目经理和业务需求人员编写《项目上线计划》，与最终定版的《详细需求说明书》、《详细设计说明书》等相关文档一并提交项目需求管理小组做上线评审，评审不通过的回退至集成测试阶段。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十一章 系统上线与培训', '第四十七条', '评审通过后，科技信息部生产版本控制人员根据事先编制的《生产环境应用安装文档》同步生产环境。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十一章 系统上线与培训', '第四十八条', '业务需求人员组织生产验证。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十一章 系统上线与培训', '第四十九条', '项目经理组织召开项目总结会，交流项目实施过程中的心得体会，并指定后期技术支持人员，由项目经理形成《项目总结》。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十一章 系统上线与培训', '第五十条', '项目经理组织小组所有成员对项目过程中形成的 文档、源程序等资料进行整理、归档，与科技信息部文档管理人员进行项目文档的交接。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十一章 系统上线与培训', '第五十一条', '开发单位应当制定系统培训计划和培训教材，组织使用人员进行系统使用培训。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十二章 实施操作和维护', '第五十二条', '进行信息系统的日常运行和维护管理，实时监控系 统运行状态，保证系统各类运行指标符合相关规定。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十二章 实施操作和维护', '第五十三条', '迅速而准确地定位和排除各类故障，保证信息系统 正常运行，确保所承载的各类应用和业务正常。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十二章 实施操作和维护', '第五十四条', '在保证系统运行质量的情况下，提高维护效率，降低维护成本。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十三章 附 则', '第五十五条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《软件开发项目管理实施办法》', '第十三章 附 则', '第五十六条', '本办法自发文之日起施行。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第一章  总	则', '第一条', '为加强对xxx银行（以下简称“本行”）软件版本的管理，规范软件版本管理工作流程，提高信息系统运行维护质量，保障信息系统安全稳定高效地运行，根据《中华人民共和国计算机信息系统安全保护条例》、《商业银行信息科技风险管理指引》结合本行实际，特制定本办法。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第一章  总	则', '第二条', '软件版本管理是信息系统开发管理和运行维护管理工作的重要组成部分，本办法作为软件版本管理的重要依据，软件版本管理的归口管理部门、业务支撑部门、风险管理部门、内部审计部门及软件供应商应认真履行各自职责，严格执行软件版本管理的各项规定和流程，保障信息系统的安全稳定运行。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第一章  总	则', '第三条', '本办法涉及的软件包括已投产运行的软件和拟投产运行的软件。软件版本管理的对象包括应用软件版本以及相关操作系统、数据库、中间件等基础软件的版本。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第一章  总	则', '第四条', '任何未经归口管理部门许可的软件版本不允许在生产环境使用。在商务合同中若涉及信息系统软件版本，应确认为归口管理部门允许使用的软件版本。因使用未经许可的软件版本而造成系统故障影响正常业务交易，相关使用部门及软件供应商须承担相应的责任。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第二章  组织与职责', '第五条', '软件版本管理实行总行集中管理体系，信息科技部是软件版本管理的归口管理部门，前台业务部门是软件版本管理的业务支撑部门，风险管理部是软件版本管理的风险控制部门，审计部是软件版本管理的内部审计部门。软件版本管理工作还涉及软件供应商，软件供应商包括软件最终提供商、代理商和维保服务商（以下简称“厂商”）。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第二章  组织与职责', '第六条', '归口管理部门职责：1.负责制定和完善软件版本管理办法。2.负责制定软件版本管理工作的工作计划、工作要求和技术规范，并组织实施。3.负责审批业务支撑部门上报的版本变更申请，组织进行资料审核和上线测试，安排试运行工作及全行推广实施。4.负责建立软件版本信息库，发布软件版本管理各类信息；建立版本预警体系，发布软件版本缺陷信息和版本预警信息。5.负责与业务支撑部门、风险管理部门、内部审计部门、厂商协调软件版本管理的相关工作。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第二章  组织与职责', '第七条', '业务支撑部门职责1.负责业务类需求的日常收集和集中收集。2.负责发起新版本的试运行申请。3.负责协助归口管理部门审核新版本发布资料（包括申请、厂家及仿真环境测试报告、版本说明文档、升级方案、测试方案等），并协助开展新版本试运行测试工作。4.负责自查并督促其下属机构履行职责，严格执行版本管理相关制度和流程。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第二章  组织与职责', '第八条', '风险管理部门职责负责重大版本发布前的风险评估。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第二章  组织与职责', '第九条', '内部审计部门职责负责监督和检查版本管理归口管理部门、业务支撑部门、风险管理部门和厂商是否严格执行版本管理的相关制度与流程。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第二章  组织与职责', '第十条', '厂商义务1.严格遵守软件版本管理的规章制度、技术规范。2.根据业务发展及运行维护的需要及时更新版本，保证在线运行的软件版本是允许使用的版本。3.配合软件版本归口管理部门进行软件仿真测试，及时提供各类运行维护及仿真测试所需的文件资料和技术咨询，并对这些材料的真实性、可靠性和实时性负责4.配合软件试运行工作。厂商根据版本变更情况选择能够测试所有升级功能点的分支机构，并结合用户量、安全性等的要求向提出试验点建议。5.配合做好软件版本管理工作，建立本厂商软件版本管理资料库信息，协助软件版本归口管理部门做好版本预警信息的发布与管理，提供必要的技术资料和技术支持。6.指定专门的版本管理联系人与软件版本归口管理部门衔接，以便配合进行软件的升级实施和及时跟踪处理升级过程中或者升级后出现的各种故障。7.在升级过程中按照要求配合完成各项工作，包括协助软件版本归口管理部门模拟重现升级或试运行期间出现的和软件版本相关的故障。8.在工程招标书中，承诺按照版本管理相关制度和流程履行投标方的义务。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第三章  软件版本管理的内容', '第十一条', '软件版本分为版本和补丁。版本是指软件系统中的核心部分发生结构性变化、应用部分新增若干功能而生成的软件版本。补丁是指软件系统中不涉及核心部分的变化，只是应用部分的故障修复或功能完善而生成的软件版本。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第三章  软件版本管理的内容', '第十二条', '软件版本管理的各项工作必须按照规定的操作流程执行，相关部门应认真履行本部门的职责，做好部门之间的衔接和协调。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第三章  软件版本管理的内容', '第十三条', '软件版本管理工作内容主要包括需求管理、认证管理、变更管理、评估管理和信息管理。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第三章  软件版本管理的内容', '第十四条', '需求管理是通过收集、整理和分析版本的新特性需求或未修复缺陷，引导厂商进行新版本开发，确定待认证的版本；认证管理是依据技术规范，对厂商待认证版本的符合性和可用性进行认证，并对已认证版本进行更新或废止管理；变更管理是对生产运行版本变更的技术审核和流程管控；评估管理是对生产运行版本的版本能力、缺陷等方面的评价和管理；信息管理是对全行软件版本信息及版本管理工作各环节输出信息的动态管理，主要包括信息的收集、整合、关联、更新、价值挖掘和全行共享，是版本管理各项工作的基础。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第四章  监督与检查', '第十五条', '内部审计部门适时组织检查信息系统的软件版本管理工作情况，及时通报检查结果；对于因版本管理不善或使用未经许可的软件版本而造成的业务中断故障、用户投诉及经济损失等，内部审计部门视具体情况对相关部门、负责人或直接责任人给予处罚。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第四章  监督与检查', '第十六条', '归口管理部门在进行“外包商服务质量评估”时，应将厂商软件版本运行评估情况及对版本管理工作的支撑情况作为评估内容之一，并将评估结果作为采购评标的重要考虑因素；在维保合同中应增加版本管理工作相关要求的条款，并进行相关考核；对于因厂家原因造成的业务中断故障及由此产生的损失，将视具体情况对相关厂家给予处罚并追究相关责任。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第五章  附	则', '第十七条', '本办法由xxx银行信息科技部负责解释和修订。'),
(NULL, '信息科技部', '《软件版本管理办法》', '第五章  附	则', '第十八条', '本办法自发文之日起施行。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第一章  总则', '第一条', '为有效规范和推动本行”信贷业务的健康开展，依据《个人贷款管理暂行办法》，和本行《信贷管理制度》、《信贷业务操作流程》、《贷款业务尽职管理办法》等，制定本办法。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称“易拍贷”是本行向符合贷款条件的、具有完全民事行为能力的自然人发放的，用于购买本地法院依法进行拍卖的房地产所需的临时性人民币贷款。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所称“贷款人”是指本行所辖支行、小微业务部、营业部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第四条', '经办行负责对“易拍贷”贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '授信评审部负责对“易拍贷”贷款业务的授信管理。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定“易拍贷”贷款管理办法，对“易拍贷”业务的用信及贷后检查进行管理监督。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对“易拍贷”贷款用款进行审查。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '风险管理部负责对“易拍贷”贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核“易拍贷”贷款办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '审计部负责对“易拍贷”贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十二条', '借款人应符合下列贷款条件：1.借款人户口在本辖区，或户口虽不在本辖区，但固定经营场所在本行服务范围内；2.借款人信用状况良好，无重大不良信用记录；3.借款人具备还款意愿和还款能力；4.本行要求的其他条件。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十三条', '借款人应提供下列申请资料：1.申请人及其配偶身份证、户口簿、结婚证、实际居住地证明资料以及家庭主要财产、收入明细等，单身借款人应提供单身承诺，并由所在街道、居委会（村委会）或所在单位出具证明，并提供共同借款人。2.提供拍卖标的物权属裁定书或法院出具的拍卖成交确认书；3.本行要求的其他资料。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十四条', '贷款额度。根据拍卖标的物类别和本行规定，合理确定贷款额度，其中，购买个人住房（含商住用地）、生产性厂房（工业、仓储用地）的，贷款额度最高不超过成交价格的60％；购买门面房（商业用地）的，最高不超过成交价的70%。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十五条', '贷款期限。自竞价成功取得法院裁定书或法院出具的拍卖成交确认书之日起至办理产权抵押登记手续、取得《不动产登记证明》之前，一般最长不超过3个月。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十六条', '贷款利率。按照本行利率定价办法执行。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十七条', '还款方式。实行利随本清的还款方式。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第四章  业务流程', '第十八条', '“易拍贷”的授（用）信按照本行《个人贷款业务操作规程》办理。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第四章  业务流程', '第十九条', '合同签订。借款人取得拍卖标的物权属裁定书或法院出具的拍卖成交确认书后，经办行根据借款人实际情况，应要求借款人提供具备代偿能力的保证人（含其家庭其他主要成员）承担过渡性保证担保，并与借款人、保证人签订《个人保证担保借款合同》。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第四章  业务流程', '第二十条', '贷款支付。无论金额大小，一律实行受托支付方式。经办行将贷款资金受托支付到法院指定的账户，并书面告知法院，对入账贷款资金按本行与法院签订的《监管委托协议》进行监管，明确在本行未取得《不动产登记证明》或借款人未结清“易拍贷”贷款前，所监管的贷款资金不得动用。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第五章  其他事项', '第二十一条', '根据“易拍贷”贷款实际发放情况，经办行应建立“易拍贷”贷款台账，并及时抄送法院指定账户的开户机构协助监管，开户机构应落实专人定期与法院对账，确保法院指定账户存款余额不低于本行“易拍贷”贷款本金总额。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第五章  其他事项', '第二十二条', '借款人应根据法院裁定书，在规定期限内办理房地产过户手续，并申办房产抵押贷款或二手房按揭贷款，及时至产权登记部门办理抵押登记，经办行在取得《不动产登记证明》后立据发放，并要求借款人将“易拍贷”贷款本息结清。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第五章  其他事项', '第二十三条', '借款人将“易拍贷”贷款本息结清后，本行出具书面通知，告知法院指定账户内对应的资金监管取消，法院作为执行款处置。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第五章  其他事项', '第二十四条', '当出现下列情况时，经办行应及时告知法院，请求退回已入账的监管贷款资金，用于归还“易拍贷”贷款本金，所产生的贷款利息由借款人承担。（一）产权登记部门不予办理拍卖房地产过户登记；（二）其他原因导致拍卖房地产过户不成；（三）借款人无正当理由不及时办理竞拍所得房产的抵押手续；（四）“易拍贷”贷款本息逾期。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第六章  附则', '第二十五条', '信贷业务经办人员在办理“易拍贷”贷款时必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第六章  附则', '第二十六条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第六章  附则', '第二十七条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《“易拍贷”贷款管理办法及流程》', '第六章  附则', '第二十七条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第一章  总则', '第一条', '为进一步丰富小微企业产品体系，促进与政府的合作，不断提升小微企业金融服务能力，有效规范和推动本行信贷业务的健康开展，根据本行《信贷管理制度》、《信贷业务操作流程》等制定本办法。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称“税企通”是本行根据不同的小微企业客户税种、客户纳税信用等级、年度纳税额变化以及在本行开立结算账户等情况，向符合贷款条件，按时足额纳税的小微企业发放的用于短期生产经营周转的人民币贷款业务。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所称“贷款人”是指本行所辖有公司贷款业务经办权的支行、营业部、公司业务部、小微业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第四条', '经办行负责对“税企通”贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '授信评审部负责对“税企通”贷款业务的授信管理。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定“税企通”贷款管理办法，对“税企通”业务的用信及贷后检查进行管理监督。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对“税企通”贷款用款进行审查。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '计划财务部负责制定“税企通”贷款利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '风险管理部负责对“税企通”贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核“税企通”贷款办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '审计部负责对“税企通”贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第三章  贷款对象及条件', '第十二条', '“税企通”贷款的对象为本行服务辖区内经依法履行工商行政管理职能的机构(或者其他主管机关)依法核准登记的小微企业。“税企通”贷款客户的获取主要有两种途径：1.与当地税务等政府有关部门合作，批量获取小微企业纳税信息，从中筛选出的符合条件的客户；2.税务等政府有关部门出具《小微企业“税企通”客户推荐函》（附件1），向本行推荐的依法纳税的小微企业优质客户。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第三章  贷款对象及条件', '第十三条', '借款人除应符合《流动资金贷款管理办法》规定的借款人应具备条件外，还需全部满足以下准入条件：（一）企业成立且实际经营2年（含）以上，主营业务销售收入呈上升态势，且未出现亏损情形；（二）企业主（实际控制人）从事本行业3年（含）以上；（三）企业近2年按时足额缴税，无不良纳税记录，上一年度纳税总额在5万元（含）以上，其中，纳税总额是指小微企业在国税、地税部门缴纳的增值税、营业税和企业所得税等税种的总和（含减免税额）；（四）纳税信用等级B级及以上，且在本行内部信用等级评定A级及以上；（五）企业、企业主（实际控制人）、主要股东及其配偶无参与高利贷、涉黑、洗钱、地下钱庄等违法行为，无投资股票、期货、期权等高风险经营行为；（六）企业主（实际控制人）、主要股东及其配偶同意提供连带责任保；（七）企业在本行无授信额度，且企业主（实际控制人）及其家庭成员在本行无个人经营性借款，企业已有贷款合作银行不超过1家。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第四章  贷款额度、贷款担保、期限、利率及还款方式', '第十四条', '贷款额度。贷款额度综合考虑企业资产负债情况、经营状况、纳税情况、企业主的经营能力等因素确定，非有限责任公司借款主体可将企业主家庭财产并入考虑。单户贷款授信额度最高不超过300万元，对于授信额度超过100万元的，授信额度一般不得超出企业净资产。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第四章  贷款额度、贷款担保、期限、利率及还款方式', '第十五条', '贷款担保。“税企通”贷款，借款人一般需提供可靠的担保。对信誉及经营状况良好，盈利能力强，纳税信用等级A级的小微企业，经审批后可采用信用方式放款。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第四章  贷款额度、贷款担保、期限、利率及还款方式', '第十六条', '贷款期限。贷款期限最长为1年（含）。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第四章  贷款额度、贷款担保、期限、利率及还款方式', '第十七条', '贷款利率。按照本行利率定价办法执行。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第四章  贷款额度、贷款担保、期限、利率及还款方式', '第十八条', '还款方式。“税企通”贷款采用按月结息，到期还本的还款方式。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第五章  贷款操作程序', '第十九条', '“税企通”贷款操作程序为借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第五章  贷款操作程序', '第二十条', '借款人申请。申请人除需按《信贷业务操作规程》提交材料外，还需提供以下资料：（一）税务部门出具的《小微企业“税企通”业务纳税证明》（附件2）或税务部门开具的近2年增值税、营业税、企业所得税的完税证明（盖章）；（二）近2年缴税付款凭证、纳税账户的银行对账单（银行盖章）；（三）网上纳税申报系统截图；（四）对于税务部门推荐的依法纳税的小微企业客户，还需提供税务等政府有关部门出具《小微企业“税企通”客户推荐函》。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '贷款受理与调查。“税企通”贷款的申请，由经办行负责受理、调查。调查结束，调查人要形成详实的调查材料，并形成综合调查意见。调查的基本内容主要包括：（一）企业。企业的资产负债情况、经营项目、产品市场行情、盈利能力、贷款用途、担保情况、信用状况、偿还能力、近两年纳税情况、纳税信用等级等；（二）企业主或实际控制人。企业主或实际控制人的综合素质、家庭财产状况、信用情况、年收入、对外负债等情况。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等环节，经办行应严格按本行《信贷业务操作规程》等制度要求办理。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第六章  客户退出', '第二十四条', '借款人符合以下条件之一的，应及时退出：（一）企业或企业主被人民银行或税务部门列入黑名单的；（二）企业出现恶意偷税、逃税、骗税、抗税、虚开发票等涉税犯罪行为的；（三）企业纳税信用等级被评定为C级以下（含）的；（四）贷款存续期间未全部落实授信持续条件的；（五）企业经营情况恶化，偿债能力严重下降的；（六）贷款期间内，企业或企业主出现逾期、欠息或未履行担保责任情形的；（七）企业或企业主存在直接或间接参与高利贷违法行为的；（八）企业列入本行退出客户名单的；（九）出现重大交叉违约事件的；（十）未履行诚实告知（或报告）义务的；（十一）本行认定的其它退出情形。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第七章  附则', '第二十五条', '本办法及流程由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《“税企通”贷款管理办法及流程》', '第七章  附则', '第二十六条', '本办法自公布之日起施行。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第一章  总则', '第一条', '为推动全市精神文明建设，培育诚信金融生态环境，建立金融服务“道德模范”和“好人”（以下简称“道德模范”）的长效机制，帮助道德模范赢得发展机遇，改善生产生活，本行根据监管部门《个人贷款管理暂行办法》，以及本行《信贷管理制度》、《贷款业务尽职管理办法》、《个人贷款管理实施细则》等相关的信贷管理制度，制定本管理暂行办法。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第一章  总则', '第二条', '本行开展道德贷款业务，以及为道德模范提供金融服务和技能培训，遵循“依法合规、审慎经营、平等自愿、公平诚信”的原则。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第二章  服务对象和服务条件', '第三条', '服务对象。服务对象是指获得市级以上表彰的道德模范。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第二章  服务对象和服务条件', '第四条', '服务条件。借款人申请道德贷款和被提供金融服务，除具备本行相关的信贷管理制度规定的基本条件外，还应具备下列限制性条款规定的条件：（一）借款人应提供已获得的道德模范荣誉证书；（二）借款人应提供在本市有固定住所、常住户口或有效居住证明；（三）本行规定的其他条件。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第三章  服务种类及形式', '第五条', '个人消费贷款。向道德模范最高授信20万元，一次授信、循环使用，贷款主要用于个人合理的消费支出。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第三章  服务种类及形式', '第六条', '惠农致富贷款。向从事农村生产经营的道德模范最高授信30万元，贷款主要用于生产经营等流动资金需要。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第三章  服务种类及形式', '第七条', '创业助业贷款。对已获得道德模范的小微企业主最高授信100万元，贷款主要用于生产经营过程中正常资金需求。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第三章  服务种类及形式', '第八条', '贷款期限、利率、担保方式。贷款期限一般不超过1年（含），贷款利率按基准利率上浮30%执行，担保方式为信用。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第四章  服务流程', '第九条', '道德贷款流程参照本行相关的信贷管理制度执行，本行建立道德模范档案，落实专人定期上门了解金融服务需求，并提供金融服务。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第五章  风险防范', '第十条', '贷款发放后，当借款人出现不良行为时，应根据具体情况予以考虑调减或取消授信额度，坚持做到授信、用信与还款能力相匹配。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第五章  风险防范', '第十一条', '发放道德贷款，不得因其身份关系而放松对其资格的审查，凡有下列情形之一的,不得向其提供用信或增加用信:（一）已出现不良信用记录的；（二）已被取消“道德模范”荣誉称号的；（三）对本行工作不支持、不配合或有其他间接的不良行为，已给本行的工作开展造成影响的。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第五章  风险防范', '第十二条', '对于道德贷款发生逾期（含展期后到期）的，除进行必要的正常催收，还可告知市宣传部、文明办，通过道义、纪律等其他方式进行催收。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第六章  附则', '第十三条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第六章  附则', '第十四条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《“道德贷”金融服务管理办法》', '第六章  附则', '第十五条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第一章  总则', '第一条', '为进一步加大对社会主义新农村建设的金融支持，解决青年创业过程中的资金瓶颈问题，完善青年创业金融服务体系，促进广大城乡青年创业、增收致富，农商行、共青团委员会（以下简称“团市委”）根据共青团中央、中国银监会《关于实施农村青年创业小额贷款的指导意见》、《个人贷款管理暂行办法》等有关法律、法规，及本行《信贷管理制度》、《个人贷款管理实施细则》、《贷款业务尽职管理办法》等制度，特制定本办法。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称的“青年易贷”创业贷款，是指农商行向经服务区域内团组织审查推荐，具有创业意向和能力且符合贷款条件的青年发放的贷款业务。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第一章  总则', '第三条', '“青年易贷”创业贷款采用“先授信、后用信”的管理办法，做到“一次授信、循环使用、随用随贷”。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第一章  总则', '第四条', '“青年易贷”创业贷款办理遵循“资金优先、利率优惠、手续简便、规范操作、风险可控”的原则。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第二章  贷款对象、条件及用途', '第五条', '本办法的贷款对象系指年龄在18周岁（含）-40周岁（含），具有完全民事行为能力，遵纪守法，诚实守信，无不良信用记录，有创业意愿和能力的，在农商行服务区域内有固定住所或生产经营场所的青年。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第二章  贷款对象、条件及用途', '第六条', '“青年易贷”创业贷款应具备下列条件：1.借款人系初次创业，且在农商行未发生过贷款业务；2.从事的生产、经营活动合法合规，并符合国家的行业、产业政策和社会发展规划要求；3.有具体创业项目、经营场所，经营项目状况良好；4.能提供有效的身份证明；5.经营的实体已在工商部门登记，且合法存续；6.具有一定比例的自有资金，有较好的信誉，偿债能力和意愿较强，能提供有效担保；7.在农商行开立存款账户；8.农商行规定的其他条件。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第二章  贷款对象、条件及用途', '第七条', '贷款用途：1.种植业、养殖业等农业生产费用贷款；2.加工、手工、商业、运输等个体经营贷款。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第三章  贷款方式、额度、利率及期限', '第八条', '贷款方式。可采用保证、联保、抵押、质押等方式，并可采取多种方式组合担保。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第三章  贷款方式、额度、利率及期限', '第九条', '贷款额度。对资金实力、创业领域、信用状况等综合因素较好并有志创业的青年个人提供“青年易贷”创业贷款，最高授信额度为20万元；对具有固定生产经营地址、一定生产经营规模、一定社会影响、有产业特色和经营效益、有良好信誉、需继续扩大规模的青年创业企业，最高授信额度不超过200万元。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第三章  贷款方式、额度、利率及期限', '第十条', '贷款利率。实行利率优惠，按农商行《贷款利率定价办法》中的相应档次下浮10个百分点。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第三章  贷款方式、额度、利率及期限', '第十一条', '贷款期限。根据借款人的生产经营周期、贷款用途、还款能力，由借贷双方共同商议确定。贷款期限一般不超过1年，最长不超过2年。借款人提出展期且担保人同意继续担保的，可按规定展期一次。其他贷款方式期限及展期规定按相关信贷政策执行。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第四章  贷款程序', '第十二条', '贷款申请。符合条件的城乡创业青年向农商行当地支行、镇、街道团（工）委或者直接向团市委领取《市“青年易贷”创业贷款申请表》，《申请表》一式三份，借款人、团市委、农商行当地支行各执一份。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第四章  贷款程序', '第十三条', '贷款初审及推荐。各镇、街道团（工）委接到申请人申请后，应及时对申请人的基本条件、创业项目等内容进行审查，并及时汇总上报至团市委，由团市委向农商行授信评审部推荐初审合格者。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第四章  贷款程序', '第十四条', '贷款调查、审查和审批。本行各支行负责“青年易贷”创业贷款业务的受理以及贷款“三查”的具体实施。授信评审部负责对“青年易贷”创业贷款业务的授信管理。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第四章  贷款程序', '第十五条', '贷款的发放和支付管理。各支行与经审批同意的借款人签订借款合同、保证（抵、质押）合同，抵（质）押贷款须办理相关抵（质）押登记，按规定程序发放贷款，并严格执行支付管理要求。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第四章  贷款程序', '第十六条', '在授信期限和授信额度范围内，可分次发放，循环使用。贷款到期后，申请人应按借款合同的约定，按时归还贷款。创业青年仍有资金需求的，可按程序重新提出申请。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第五章  贷款管理', '第十七条', '贷后管理。农商行各支行须按信贷管理要求对创业贷款进行贷后检查，监督贷款资金的使用，跟踪贷款项目的经营，严格控制贷款风险。发现贷款风险应及时反映并采取相应保全措施。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第五章  贷款管理', '第十八条', '档案管理。要按照农商行《信贷档案管理制度》做好客户信贷档案的建立保管工作，确保信贷资料的合法、完整、有效。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第五章  贷款管理', '第十九条', '统计管理。由授信评审部建立“青年创业”贷款台账，按月将“青年创业”贷款的授信额度、实际发放笔数、发放金额等情况编制成报表上报相关部门，同时报送团市委。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第六章  附则', '第二十条', '本办法未尽事宜按照农商行贷款业务相关规定执行。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第六章  附则', '第二十一条', '本办法及流程所指“青年易贷”创业贷款与妇女创业贷款不可同时发放；享受“阳光信贷”信用村、信用户优惠的创业青年，不再降息。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第六章  附则', '第二十二条', '本办法及流程由信贷管理部负责解释和修订。'),
(NULL, '信贷管理部', '《“青年易贷”创业贷款管理办法及流程》', '第六章  附则', '第二十三条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第一章  总则', '第一条', '为更好地满足居民住房消费的需求，规范本行二手住房信贷业务，根据《中华人民共和国物权法》、《中华人民共和国担保法》、《个人贷款管理暂行办法》及本行《信贷管理制度》、《信贷业务操作规程》、《个人楼宇按揭贷款管理办法》等有关制度，特制定本办法。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第一章  总则', '第二条', '个人二手房按揭贷款，是指本行向借款人发放的用于购买自用二手房的贷款。二手房特指已经取得房地产产权，可以进入房地产二级市场流通交易的个人住房、商住房或商业用房，是相对一手房（即开发商未售出的商品房）而言的。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第一章  总则', '第三条', '借贷双方应当遵守国家法律法规，遵循自愿、互利、公平和诚实信用的原则。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第一章  总则', '第四条', '个人二手房贷款适用于本行辖区内各支行、营业部、公司业务部、小微业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '经办行负责个人二手房按揭贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '授信评审部负责个人二手房按揭贷款业务的授信管理。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '三农业务部负责个人二手房按揭贷款业务的宣传策划、营销推动及日常督查管理。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定个人二手房按揭贷款管理办法及流程，对个人二手房按揭贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '运营管理部根据权限负责对个人二手房按揭贷款用款进行审查。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '资产保全部负责对不良贷款的专业清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对个人二手房按揭贷款风险的监督、识别、评估、控制，并向经营管理层提交风险报告。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核个人二手房按揭贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '审计部负责对个人二手房按揭贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第三章  贷款的对象及条件', '第十四条', '贷款对象是服务辖区内18周岁以上，且借款人年龄加借款期限不超过65周岁，具有完全民事行为能力的自然人。其中：借款人为公职人员的，申请个人住房按揭贷款，可根据其身体状况和未来还款能力（综合考虑退休前后）适当放宽至70周岁。公职人员指公务员或参照公务员管理的其他企事业单位、人民团体等组织内从事公共事务管理部门的正式在编工作人员。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第三章  贷款的对象及条件', '第十五条', '借款人申请二手房按揭贷款，应当具备以下条件：（一）一般应具有本地常住户籍或有效居留身份，或有固定居所、营业场所；（二）有稳定的收入，信用良好，能够按期偿还贷款本息；（三）借款人同意以所购的房地产作抵押；（四）已支付不低于本行规定的首期付款，首期付款的支付须通过买卖方事先约定在本行开立的账户进行，同时借款人应提供相应的支付凭证或单据；（五）房屋产权明晰，符合国家规定的上市交易条件，可进入房地产市场流通，房龄一般不超过20年；（六）所购房屋未列入当地城市改造拆迁规划，并有房产、土地管理部门核发的房产证和土地证；（七）所购房屋必须是现房且能够办妥房屋、土地产权过户手续、抵押登记等手续；（八）本行规定的其他条件。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第四章  贷款限额、期限、利率、与偿还方式', '第十六条', '贷款限额。借款人所购的二手房，房龄在10年（含）以下的，贷款额度最高不超过房产交易价格的70％；房龄在10年以上的，贷款额度一般不超过房产交易价格的60％。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第四章  贷款限额、期限、利率、与偿还方式', '第十七条', '房产交易价格的确定以房产交易契税发票上载明的房产价格为依据，明显偏离公允价格的除外。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第四章  贷款限额、期限、利率、与偿还方式', '第十八条', '个人二手住房贷款期限最长不超过20年，且房龄与贷款期限之和不超过30年，最长不超过房屋土地剩余使用年限；个人二手商住、商业用房贷款期限最长不超过10年，且房龄与贷款期限之和最长不超过房屋土地剩余使用年限。个人二手住房、商住两用、商业用房的贷款期限加上借款人年龄不超过本制度第十四条的规定。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第四章  贷款限额、期限、利率、与偿还方式', '第十九条', '首付比例。对贷款购买首套自住房，首付款比例不得低于 30%。对贷款购买第二套住房的家庭，首付款比例不得低于50%。对贷款购买第三套住房的家庭，首付款比例不得低于70%。商业用房购房贷款首付款比例不得低于50%；对以“商住两用房”名义申请贷款的，首付款比例不得低于45%。暂停发放居民家庭购买第三套以上住房贷款。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第四章  贷款限额、期限、利率、与偿还方式', '第二十条', '贷款利率根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第四章  贷款限额、期限、利率、与偿还方式', '第二十一条', '贷款偿还方式按《个人楼宇按揭贷款管理办法》的有关规定执行。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '个人二手房按揭贷款操作程序为借款人申请、受理与调查、授信审查审批、用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '借款人申请二手房按揭贷款应提交下列资料：（一）借款人合法的身份证件；（二）借款人经济收入证明或职业证明；（三）有配偶借款人需提供夫妻关系证明，单身借款人应提供单身承诺，单身借款人有工作的，须由单位出具相关证明；无工作的，须由所在街道、居委会或村委会出具相关证明。若贷款申请金额超过50万元（含），除出具相关证明外，客户经理还应双人到相关部门核实确认。（四）有共同借款人的，需提供借款人各方签订的明确共同还款责任的书面承诺；（五）在本行交付的首付款单据证明。为保证首付款真实、足额，借款人应将首付款存入买卖方事先约定在本行开立的账户；（六）借款人应提供契税缴纳证明，购房人与售房人签订《房地产买卖契约》和买卖双方达成的购房协议；（七）所购二手房的房产权利证明复印件；（八）借款人与售房人签订的《房屋买卖合同》，及售房人提供的划款账号；（九）本行要求提供的其他文件或资料。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '经办行收到申请后，及时调查借款人是否符合贷款条件和所提供资料的真实性。并由买卖双方作出对首付款止付承诺，符合贷款条件的由客户经理出具止付书并通知柜面经办人员办理。借款人书面同意售房人使用首付款的，可不受此限，客户经理应告知借款人可能存在的交易风险。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '贷款授、用信审批同意后，借款人与售房人办理过户手续，客户经理与借款人签订个人二手房按揭借款合同、划扣款授权书，并办理抵押登记。办理抵押登记手续，经办行客户经理须全程参与。房地产评估价值可以由本行和借款人双方协议确认，也可委托本行认可的资产评估机构进行评估，评估费用由借款人承担。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '借款人应办理抵押物保险，保险期不得短于借款期限，投保金额不得低于贷款本金和利息之和，保险单应载明本行为第一受益人，保险所需一切费用由借款人负担。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '办妥上述手续后，本行按信贷管理规定发放贷款。最后按借款合同约定，将止付的首付款加贷款资金一次性划入售房人提供的划款账号，或约定本行、售房人和借款人三方同时在场进行资金交割。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '贷款发放后，应按照本行信贷管理相关制度办法进行贷后管理。客户经理要不定期检查贷款偿还情况，检查贷款抵押品价值等有无变化，检查借款人的财务经济状况等。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '档案管理。经办行应按本行信贷档案管理有关规定收集、保管相关资料。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第六章  附则', '第三十一条', '贷款业务经办人员必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第六章  附则', '第三十二条', '本办法及流程未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第六章  附则', '第三十三条', '本办法及流程由信贷管理部负责制定、解释和修改。'),
(NULL, '信贷管理部', '《个人二手房按揭贷款管理办法及流程》', '第六章  附则', '第三十四条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第一章  总则', '第一条', '为实现本行客户分类营销和管理，更好地服务优质客户，促进地方经济发展，根据《个人贷款管理暂行办法》等相关法律、法规以及本行《信贷管理制度》、《个人贷款管理实施细则》、《贷款业务尽职管理办法》规定，特制定本办法。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第一章  总则', '第二条', '本办法所称个人信用贷款是指本行向资信良好及以上的借款人发放的，用于短期融资需要、无需提供担保的人民币贷款。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第一章  总则', '第三条', '本办法所称贷款人是指本行所辖支行、营业部、小微业务部、公司业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第四条', '经办行负责对个人信用贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第五条', '授信评审部负责对个人信用贷款业务的授信管理。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第六条', '三农业务部负责个人信用贷款业务的宣传策划、营销推动及日常督查管理。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定个人信用贷款管理办法，对个人信用贷款业务的用信管理和贷后检查管理的监督。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对个人信用贷款放款进行审查。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第九条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第十条', '资产保全部负责对不良贷款的清收保全及相关管理。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第十一条', '风险管理部负责对个人信用贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核个人信用贷款办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第二章  组织管理体系', '第十三条', '审计部负责对个人信用贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十四条', '申请个人信用贷款的客户包括但不限于以下客户：（一）公职人员1.市、镇党委和政府机关及其组成部门的工作人员，人大、政协机关及其组成部门的工作人员；公安、检察院、法院的工作人员；2.经批准设立的妇联、共青团、残联、侨联等由财政全额拨付经费的工作人员；3.教育、卫生等公立单位工作人员；4.实行垂直化管理的机关、事业单位的驻本市机构工作人员;5.供电、烟草、盐业等垄断行业以及金融、邮政、电信行业的在编工作人员;6.央企正式员工；7.其他参照公务员管理的工作人员。（二）非公职人员客户1.阳光信贷授信客户；2.本行优质存款客户;3.具有固定经营场所、盈利模式稳定的个体工商户、私民营企业主以及新型农业经营主体（种养殖大户、家庭农场主、农民经济人）。（三）先进人物县级（含）以上人大代表或获得县级（含）以上表彰的道德模范、劳动模范、优秀共产党员、先进工作者等。（四）其他客户1.具有执业认证资格且在特定领域具有专业技能的优秀人士，包括律师、会计师、工程师、设计师、评估师等；2.信用良好、收入稳定，具有按期偿还贷款本息能力的其他客户群体。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十五条', '以上客户除具备本行《个人贷款管理实施细则》所规定的个人贷款基本条件外，还必须同时具备以下特定准入条件：（一）借款人资信等级评估为良好以上；（二）近5年内无借款、担保以及经济纠纷未解决等重大不良信用记录，且无借户借贷、转移用途等行为；（三）持有本行借记卡或易贷通卡，并注册开通本行手机银行；（四）本行规定的其他条件。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十六条', '个人信用贷款额度确定。公职人员个人信用贷款额度按行政级别或持有的专业技术职称综合确定，一般办事员或初级职称人员限额为10万元（含）、股级或中级职称人员限额为20万元（含）、科级或高级职称人员限额为30万元、副处级（含）及以上限额为50万元（含），除公职人员的其他三类客户，信用贷款额度单户最高限额一般不超过10万元。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十七条', '个人信用贷款授信期限一般不超过1年，期限内可周转使用。个人信用贷款采取按季（月）结息，到期利随本清的还款方式。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十八条', '个人信用贷款利率按本行利率定价办法执行。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第四章  其他规定', '第十九条', '申请人向本行申请个人信用贷款时，应填写个人贷款申请表，并提供以下资料（或其中相应资料）的原件和已核原件的复印件：1.借款人及其配偶有效身份证、居住证明，婚姻状况证明等。单身借款人需提供单身承诺，其中，单身借款人有工作的，由单位出具相关证明；无工作的，由所在街道、居委会或村委会出具相关证明。2.客户所在单位出具的工作关系证明或收入佐证证明（包括但不限于公积金、社保、工资清单）等。3.客户所在单位有权部门出具的职务或专业技术级别的书面证明、专业技术职称或执业认证资格证书。4.有经营实体的借款人，需提供工商营业执照等资格（质）证书等。5.获得县级（含）以上表彰的相关文件或证书。6.客户在本行存款证明资料或借款人拥有的各类金融资产凭证，如银行存款、债券和基金等；借款人及其配偶名下动产或不动产所有权证，如房产证、土地证和行驶证等。7.客户信用资料及其它需审查的资料。8.本行要求提供的其他资料。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第四章  其他规定', '第二十条', '个人信用贷款借款人必须明确一名家庭主要成员（配偶或有代偿能力的父母、成年子女）为共同借款人，共同承担贷款本息按期偿还责任，并在借款合同上签章（签字盖章或签字加指纹）。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第四章  其他规定', '第二十一条', '个人信用贷款借款人须投保意外伤害保险，且明确本行为第一受益人，保险金额一般须覆盖贷款本金及预计利息。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第四章  其他规定', '第二十二条', '个人信用贷款的授信、用信、合同签订以及贷后管理参照本行《个人贷款管理实施细则》执行。采用本行电子渠道自助放款（用信）的，按自助用信服务协议相关要求执行。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第四章  其他规定', '第二十三条', '个人信用贷款原则上不办理展期，确因不可抗力或意外事故而不能按期还贷的，需经总行审批同意后方可展期。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第五章  附则', '第二十四条', '贷款经办人员必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第五章  附则', '第二十五条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第五章  附则', '第二十六条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《个人信用贷款管理办法》', '第五章  附则', '第二十七条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行个人定期存单质押贷款业务，防范贷款风险，提高信贷资产质量，根据《中华人民共和国担保法》、《中华人民共和国物权法》、《个人贷款管理暂行办法》、《个人定期存单质押贷款办法》和本行《信贷管理制度》、《贷款业务尽职管理办法》等规定，制定本办法。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称个人定期存单质押贷款，是指借款人以本行所辖支行、营业部（以下称“存款行”）签发的个人未到期定期存单作质押，及与我行有合作协议的其他银行（以下简称“他行”）签发的未到期的定期存单作质押，从本行所辖支行、营业部、小微业务部、公司业务部（以下简称“贷款人”）获得人民币贷款的一种贷款方式。出质人可以是借款人本人，也可以是自愿出质的第三人。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第一章  总则', '第三条', '定期存单包括未到期的整存整取定期储蓄存款存单等具有定期存款性质的存单凭证。所有权有争议、已作担保、挂失、失效或被依法止付的定期存单不得作为质押品。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第一章  总则', '第四条', '本办法所指的借款人是经有权部门核准登记的企(事)业法人、其他经济组织、个体工商户或具有合法身份证件、在本行辖区内有有效居住证明（含暂住证）的具有完全民事行为能力的自然人。本办法所称单位包括企业、事业单位、社会团体以及其他组织。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第一章  总则', '第五条', '本行存单以定期存单签发行为质押贷款办理行，非本行存单应在总行批准的支行（部）办理。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第一章  总则', '第六条', '权利质押贷款的用途必须是借款人依法从事生产、经营和消费等方面的资金需求。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '贷款人承担个人定期存单质押贷款的受理、“三查”实施及相关资料的保管。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '授信评审部承担个人定期存单质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '信贷管理部负责管理办法和流程的制定，承担用信的审查、审批及贷后检查的管理监督。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '运营管理部负责对个人定期存单质押贷款用款进行审查。第十一条 风险管理部负责个人定期存单质押贷款业务风险的识别、评估、计量、监控，并向经营管理层提交风险报告。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核管理办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '审计部负责对个人定期存单质押贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第三章  客户对象和基本条件', '第十四条', '客户应当是经有权部门核准登记的企(事)业法人、其他经济组织、个体工商户或具有合法身份证件、在本行辖区内有有效居住证明（含暂住证）的具有完全民事行为能力的自然人。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第三章  客户对象和基本条件', '第十五条', '客户申请个人定期存单质押贷款应当具备下列基本条件：（一）依法从事生产、经营和消费等方面的资金需求；（二）借款用途明确、合法；（三）有合法稳定的收入或收入来源，具有按期还本付息能力；（四）在本行开立结算账户，自愿接受本行的信贷监督和结算监督；（五）除自然人外，企业法人应当在有权部门有效登记，如实向本行提供有关财务报表、税务证明等相关资料。特殊行业须持有有权机构颁发的特殊行业许可证；事业法人依照《事业单位登记管理暂行条例》的规定向事业单位登记管理机关办理了登记或备案手续。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十六条', '贷款金额。期限一年内，贷款额度一般不超过质押物的90%，期限一年以上，贷款额度一般不超过质押物的80%。存单质押担保的范围包括贷款本金和利息、罚息、损害赔偿金、违约金和实现质权的费用。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十七条', '贷款期限。由借款人、出质人、贷款人协商确定，最长不超过三年，但贷款到期日不得超过质物的到期日。若质物为多张、到期日间隔时间较长的，以距离到期日时间最近者确定贷款期限，或质押贷款采取分笔发放，确保贷款到期日与质物到期日符合前款规定。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十八条', '贷款利率。按本行贷款利率定价管理办法执行。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十九条', '在贷款到期日10天前，借款人可申请展期。贷款人办理展期应按审慎管理原则，合理确定贷款展期期限，并按本行信贷管理制度有关规定执行，同时，由借款人、出质人、贷款人共同办理相关展期手续。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第二十条', '还款方式。公司客户实行按月结息，到期还本方式；个人客户实行利随本清或期间内按季结息，到期利随本清的方式。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '个人定期存单质押贷款操作程序：借款人申请，授信审查、审批，用信审查、审批，合同文件的签订，贷款发放与支付，贷后管理，贷款收回和档案管理。非本行个人定期存单质押贷款操作按《合作协议》进行操作。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '借款人申请个人定期存单质押贷款，应向贷款人提供贷款金额、用途、期限、还款来源、质押物等基本情况，并提供下列资料：（一）借款人及出质人基本情况；（二）借款人及出质人的身份证明；（三）用于质押的个人定期存单凭证；（四）凭印鉴、密码支取的章印、密；（五）贷款人要求提供的其他资料。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '贷款人客户经理接到借款人的申请后，应进行尽职调查。调查的主要内容包括但不限于：（一）借款人提供的定期存单是否真实有效，有无挂失、冻结、止付，是否存在争议或法律纠纷；（二）借款人的身份证件是否真实有效，是否具有完全民事行为能力；（三）借款人以他人的定期存单办理质押贷款，借款人、出质人是否提供了有效身份证件，出质人是否具有完全民事行为能力；（四）还款来源是否落实等。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '贷款人应对贷款调查内容的合法性、合理性、准确性进行全面审查，重点关注借款人的偿还能力、诚信状况、质押比率、风险程度等。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '贷款授、用信审批，按本行《转授权管理办法》、《信贷业务操作规程》规定执行。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '客户经理办理贷款手续，主要包括以下内容：（一）严格执行贷款面谈、面签制度。（二）出质承诺及背书。出质人在《出质承诺书》的出质人承诺栏作出“如贷款不能按期归还，同意质权人将质押的权利凭证兑现，并以所得价款偿还贷款”的承诺；在出质人背书栏填写身份证件名称和号码并签字加盖指模。（三）凭预留印鉴或密码支取的存单作为质押时，出质人须向存款行提供印鉴或密码并经柜员修改为凭单支取；以凭有效身份证明支取的存单作为质押时，出质人应转为凭单支取，否则贷款人有权拒绝发放贷款。他行签发的个人存单还必须双人到存单签发行进行查询，办理冻结止付，并且要求出质人出具贷款发生违约的扣划授权书。（四）签订质押担保借款合同。客户经理对《出质承诺书》审查无误后，与借款人、出质人签订质押担保借款合同，由借款人、出质人同时到场在合同上签字和加盖指模或章印。出质人和贷款人应该在质押合同中约定，当借款人没有依法履行合同的，贷款人可直接将存单兑付以实现质权。存单到期日后于借款到期日的，贷款人也可继续保管质押存单，在存单到期日兑付以实现质权。兑付清偿主债权后，多余部分应退还给出质人，不足以清偿主债权的，应继续向借款人追偿。质押担保借款合同及相关材料应当载明下列内容：1.借款人、贷款人和出质人住址或营业场所；2.被担保的贷款的种类、数额、期限、利率、结息方式、贷款用途以及贷款合同号；3.定期存单号码及所载存款的种类、户名、开立机构、数额、期限、利率；4.质押担保的范围；5.定期存单确认情况；6.定期存单的保管责任；7.质权的实现方式；8.违约责任；9.争议的解决方式；10.当事人认为需要约定的其他事项。（五）客户经理进入信贷业务系统录入登记，打印《放贷通知书》，并按有关规定做好支付管理工作。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '质押存单处理。（一）柜员接到质押存单和《押品出账通知书》后，应认真检查质押存单是否收集齐全，同时填制《抵、质押品代保管凭证》由客户签字。在《抵、质押品代保管凭证》上加盖业务章，收据联交客户，质押存单和《抵、质押品代保管凭证》（管理卡联）交后台管理并登记表外登记簿。（二）根据《放贷通知书》，柜员应对质押贷款手续认真进行审查，审查要素是否齐全、利率执行是否符合规定、大小写是否一致、签字审批是否齐全等，审核无误后，打印借据交由客户签字加盖指模或章印，并由客户经理和审批人在借据上签字，进行贷款发放并按支付管理要求进行支付。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '贷款收回。贷款期满借款人履行债务的，或者借款人提前偿还质押贷款的，柜员进行还本还息处理，收妥款项后，收回出质人提交的《抵、质押品保管凭证》（收据联），进行质押品注销处理，贷款人应当及时将质押的定期存单退还出质人，并及时办理登记注销手续。若还款时出质人未提交《抵、质押品保管凭证》（收据联），则不得进行“质押品注销”的操作，质押存单也不取出，日后出质人可凭《抵、质押品保管凭证》（收据联）办理注销、领取手续。如出质人将《抵、质押品保管凭证》（收据联）遗失，由出质人、借款人共同出具书面证明，并凭合法身份证明按照储蓄挂失的要求和手续办理取回质押存单。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '以他行签发的个人定期存单质押，发生违约贷款行应按照出质人授权书等相关规定解除止付并且扣划还贷;按期归还结清贷款的，贷款行应通知存单签发行解除止付。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '客户经理是权利质押贷款发生后管理的责任人，负责客户贷款业务发生后的日常管理。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十一条', '贷款人应妥善管理质押存单。因保管不善造成丢失、损坏，由贷款人承担责任。质押存续期间，除法律另有规定外，任何人不得擅自动用质押存单。用于质押的定期存单在质押期间丢失、毁损的，贷款人应立即通知借款人和出质人，并与出质人共同向存款行申请挂失、补办。补办的存单仍应继续作为质物。质押期间，未经贷款人同意，任何单位不得受理存款人提出的挂失申请。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十二条', '短期质押贷款到期应提前15天、中期质押贷款到期应提前30天通知客户。质押贷款逾期或发生欠息等违约行为，经向借款人、出质人催收仍不还款的，按合同约定或出质人承诺（授权书），贷款人可兑付存单归还贷款本息。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十三条', '质押存续期间如出质人死亡，其合法继承人依法办理存款过户和继承手续，并继续履行原出质人签订的质押合同。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十四条', '贷款发生下列情形之一的，贷款人可依第二十六条第四款的约定方式或其他法定方式处分质押的定期存单：（一）质押贷款合同期满，借款人未按期归还贷款本金和利息的；（二）借款人或出质人违约，贷款人需依法提前收回贷款的；（三）借款人或出质人被宣告破产的；（四）借款人或出质人死亡而无继承人履行合同的。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十五条', '资料管理。办理质押贷款涉及的《出质承诺书》、《抵、质押品保管凭证》等应按本办法的规定和注明的用途使用，并作为相应业务凭证的附件订入当日传票。《质押担保借款合同》和《贷款到期通知书》、《逾期贷款催收通知书》、《担保人履行责任通知书》的回执等文本资料应作为重要信贷档案由客户经理按月整理装订归档，按档案管理规定的期限保管。以他行存单质押的贷款，相关资料应一户一档，专夹保管。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第六章  附则', '第三十六条', '本办法及流程由信贷管理部制订、修改和解释。'),
(NULL, '信贷管理部', '《个人定期存单质押贷款管理办法及流程》', '第六章  附则', '第三十七条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行个人楼宇贷款行为，促进信贷业务健康发展，根据《中华人民共和国民法通则》、《中华人民共和国合同法》、《中华人民共和国担保法》、《中华人民共和国物权法》、《个人贷款管理暂行办法》，结合本行《信贷管理制度》、《贷款业务尽职管理办法》等相关法律法规及有关制度，特制定本办法。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称个人楼宇是指购房人在房屋一级市场上购买的个人住房、商用房或商住用房，所购房产可以是现房也可以是期房。期房是指开发商从取得商品房预售许可证开始至取得房地产权证止这一期间的商品房。现房是指开发商已办妥所售房屋的大产证的商品房，与消费者签订商品房买卖合同后，即可以办理产权证的商品房。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第一章  总则', '第三条', '个人楼宇按揭贷款（以下简称“贷款”）是指购房人（借款人）以从房屋一级市场购买的房屋作抵押担保或提供保证担保，向本行申请的用于支付部分购房款的贷款。以楼宇按揭贷款的，房地产开发商与本行须签订贷款合作协议，并由房地产开发商提供阶段性保证担保。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第一章  总则', '第四条', '本办法不适用个人在房屋二级市场购买的各类型再交易房产（俗称二手房）。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第一章  总则', '第五条', '本办法适用于本行辖区内支行、营业部、公司业务部、小微业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '经办行负责个人楼宇按揭贷款业务的受理、贷款“三查”的具体实施，以及对商品房预售专用账户资金的管理。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '授信评审部负责对个人楼宇按揭贷款业务的授信管理。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '三农业务部负责个人楼宇按揭贷款业务的宣传策划、营销推动及日常督查管理。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '信贷管理部负责拟定个人楼宇按揭贷款管理办法及流程，对个人楼宇按揭贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '运营管理部根据权限负责对个人楼宇按揭贷款用款进行审查。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '资产保全部负责对不良贷款的专业清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '风险管理部负责对个人楼宇按揭贷款风险的监督、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '合规管理部负责审核个人楼宇按揭贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十四条', '审计部负责对个人楼宇按揭贷款进行审计稽核。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第三章  贷款对象和条件', '第十五条', '贷款对象服务辖区内18周岁以上，且借款人年龄加借款期限不超过65周岁，具有完全民事行为能力的自然人。其中：借款人为公职人员的，申请个人住房按揭贷款，可根据其身体状况和未来还款能力（综合考虑退休前后）适当放宽至70周岁。公职人员指公务员或参照公务员管理的其他企事业单位、人民团体等组织内从事公共事务管理部门的正式在编工作人员。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第三章  贷款对象和条件', '第十六条', '贷款条件（一）一般应具有本地常住户籍或有效居留身份，或有固定居所、营业场所；（二）具有稳定的职业和收入，信用良好，有按期偿还贷款本息的意愿和能力；（三）能够支付不低于本行规定的首期付款；（四）申请现房按揭贷款的，须持有与房地产开发商签订的《商品房销售合同》及所购房屋竣工验收证明；申请期房按揭贷款的，须持有与房地产开发商签订的《商品房预售合同》及所购房屋预售许可证；（五）用于申请个人住房贷款的必须是主体结构已封顶住房，用于申请商业用房贷款的房屋必须已办理竣工验收手续；（六）同意用所购房屋作抵押，并办理抵押登记，或提供本行认可的其他担保方式；（七）本行规定的其他条件。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第四章  贷款额度、期限、利率、还款方式', '第十七条', '贷款额度（一）对购买首套自住房的，贷款额度最高不得超过购房价的80％；（二）对居民申请购买第二套住房且需申请个人住房按揭贷款的，贷款额度最高不得超过购房价的60％；（三）对居民申请购买第三套住房且需申请个人住房按揭贷款的，贷款额度最高不得超过购房价的40％；（四）商业用房购房贷款额度最高不得超过购房价的50％；（五）以“商住两用房”名义申请贷款的，贷款额度最高不得超过购房价的55％。（六）对购买别墅（指独栋别墅、双拼别墅、联排别墅等高档住宅）的，贷款额度最高不得超过购房价的50%。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第四章  贷款额度、期限、利率、还款方式', '第十八条', '对购买首套自住房的贷款首付款比例不得低于20%；对居民申请购买第二套住房且需申请个人住房按揭贷款的，贷款首付款比例不得低于40%；对居民申请购买第三套住房且需申请个人住房按揭贷款的，贷款首付款比例不得低于60%；商业用房购房贷款首付款比例不得低于50%；对以“商住两用房”名义申请贷款的，首付款比例不得低于45%；对拥有一套住房并已结清相应购房贷款的家庭，为改善居住条件再次申请贷款购买普通商品住房的，可以执行首套房贷款政策。首套房标准认定根据监管部门政策适时调整，暂停发放居民家庭购买第三套（不含）以上住房贷款；居民家庭住房套数，应依据购房家庭（包括借款人、配偶及共同生活的父母和未成年子女，下同）成员名下实际拥有的成套住房数量进行认定。核实家庭住房成套数，可借助本地房屋登记信息系统、个人征信系统等系统查询。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第四章  贷款额度、期限、利率、还款方式', '第十九条', '贷款期限。根据购房人经济状况、还款能力等实际情况确定，期限最长不超过30年，商业用房购房贷款和以“商住两用房”名义申请贷款的，期限最长不超过10年，并且借款人年龄加上贷款期限不超过65岁。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第四章  贷款额度、期限、利率、还款方式', '第二十条', '贷款利率。根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第四章  贷款额度、期限、利率、还款方式', '第二十一条', '还款方式。采用委托扣款方式偿还，借款人在经办行开立个人结算账户，提前将还款资金存入本人约定账户并与经办行签订《划款扣款授权书》，委托经办行在其账户中直接扣划还款。贷款偿还分为一次还款和分期还款两种方式。贷款期限在1年（含）以内的，实行按月结息，贷款本金结清时利随本清；贷款期限在1年以上的，借款人自行选择“等额本息还款法”或“等额本金还款法”。1.等额本息还款法。指在贷款期内每月以相等的金额平均偿还贷款本金和利息。计算公式为：每月还款额＝贷款本金×月利率×（1＋月利率）还款月数（1＋月利率）还款月数－12.等额本金还款法。指在贷款期内每月等额偿还贷款本金，贷款利息随本金逐月递减。计算公式为：每月还款额＝贷款本金/还款月数＋（贷款本金－累计已还本金）×月利率贷款还款日的确定。自取得贷款后次月起进入还款期，实行按月还款，一般确定每月20日为还款日。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第四章  贷款额度、期限、利率、还款方式', '第二十二条', '客户经理可根据借款人的贷款期限以及实际收支状况，与借款人协商确定还款方式。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第四章  贷款额度、期限、利率、还款方式', '第二十三条', '借款人住房贷款的月房产支出与收入比必须控制在50%（含）以下，月所有债务支出与收入比必须控制在55％（含）以下。房产支出与收入比的计算公式为：（本次贷款的月还款额+月物业管理费）/月均收入；所有债务与收入比的计算公式为：（本次贷款的月还款额+月物业管理费+其他债务月均偿付额）/月均收入；上述计算公式中提到的收入指申请人自身的可支配收入，即单一申请为申请人本人可支配收入，共同申请为主申请人和共同申请人的可支配收入。但对于单一申请的贷款，如将申请人配偶的收入计算在内，应该先予以调查核实，同时对于已将配偶收入计算在内的贷款也应相应的将配偶的债务一并计入。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '个人楼宇按揭贷款分为开发商第三方授信申请、审查、审批，借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '与开发商确立合作关系（一）开发商申请第三方授信应向经办行提出书面申请，并提供下列资料：1.公司资料：企业法人营业执照（履行年报公示手续的）、企业代码证书、税务登记证复印件或者新版营业执照；法定代表人证明书；公司章程、成立批文及信用等级证明；验资报告；年度审计报告及近期财务报表（上年末至本期）；房地产开发企业资质等级证书；公司按章程规定出具的为借款人承担阶段性连带保证责任的承诺；2.按揭项目资料：项目立项批文及可行性研究报告；建设用地规划许可证；建设工程施工许可证；建设工程规划许可证；国有土地使用权证；3.项目总投资，资金来源及落实情况；4.商品房预售许可证、预售方案及项目施工进度表，预售方案包括商品房位置、交付使用日期、预售总面积、交付使用后的物业管理等内容并附商品房预售总平面图。（二）经办行受理开发商申请后，要对开发商提供的有关资料进行调查，重点调查开发商资质、项目资金来源及销售情况。1.调查开发商的法人资格、经济实力、开发能力和信用等级；2.调查项目申报程序是否合规合法、手续是否完备；3.调查项目概算、投资来源、资本金及自筹资金到位落实情况；4.现场调查开发项目的地段、周边环境、户型设计、市政配套、工程进度等；分析同类、同档房屋的价格和客户购买意向，预测房屋销售价格和市场销售前景；5.调查开发商在本行的开户和贷款情况。（三）开发商按揭贷款第三方授信按本行《转授权管理办法》执行。（四）经审查批准后，本行与开发商签订个人楼宇按揭贷款业务合作协议，明确在借款人取得房屋产权并办妥抵押登记手续以前，开发商必须为借款人提供阶段性连带保证责任担保，在阶段性担保期间，借款人不能清偿债务时，由开发商承担连带保证责任。开发商必须在经办行开立销售商品房保证金专户，且该专户上保持一定的存款余额。对与本行有信贷关系的房地产开发企业必须承诺按照按揭贷款的发放比例、进度，分阶段归还房地产开发贷款。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '借款人申请（一）借款人向经办行提出贷款书面申请，填写《个人楼宇按揭借款申请表》，并如实提供下列资料：1.借款人有效身份证件（指居民身份证、户口簿或其他有效居留证件）及其复印件，已婚借款人需提供结婚证复印件，单身借款人应提供单身承诺，单身借款人有工作的，须由单位出具相关证明；无工作的，须由所在街道、居委会或村委会出具相关证明。若贷款申请金额超过50万元（含），除出具相关证明外，客户经理还应双人到相关部门核实确认。2.借款人已向开发商支付本行规定的首付款收据（由开发商开具，并有开发商的签章）；3.借款人与开发商签订的《商品房销售合同》或《商品房预售合同》及其复印件；4.借款人和家庭成员稳定合法的经济收入证明（包括由工作单位出具的个人收入证明或纳税凭证、储蓄存单、不动产证明、有价证券等）；自雇人士（即自行成立法人机构或其他经济组织，或在上述机构内持有超过 10％股份，或其个人收入的主要来源为上述机构的经营收入者）营业执照、投资证明、纳税证明等；借款人是公司的股东、董事的，应要求借款人提供公司验资报告或董事会成员名单；5.房产管理机构出示的借款人家庭拥有房产套数的证明；6.在本行开立个人结算账户；7.本行要求提供的其他资料。（二）担保人需向经办行提供下列资料：1.以现房作抵押担保的，抵押人需提供房屋产权证和国有土地使用证的原件及复印件。本行不接受空置 3 年以上的商品房作为贷款的抵押物。2.以法人保证担保的，保证人需提供企业营业执照复印件、法人代码证复印件、上一年度及近期的财务报表等。3.以自然人保证担保的，保证人需提供有效身份证件（指居民身份证、户口簿或其他有效居留证件）以及复印件、稳定合法的经济收入证明（包括由工作单位出具的个人收入证明或纳税凭证、储蓄存单、不动产证明、有价证券等）、担保承诺书等。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '贷款调查经办行在受理借款人提交贷款申请后，应重点对借款人的资格和还贷能力进行调查和核实，调查人应对调查内容的真实性负责。通过对包括借款人的工作单位、税务部门、工商管理部门以及征信机构等独立的第三方进行调查，审核贷款申请的真实性及借款人的信用情况，以了解其本人及家庭的资产、负债情况、信用记录等。调查内容主要包括：（一）借款人所提供资料的真实性、合法性；（二）借款人及主要家庭成员职业、年龄、学历、品行及专业技能，有无影响还款能力的不良品行和不良信用记录；（三）借款人家庭主要财产和收入状况；（四）借款人申请借款金额及期限是否与其家庭收入、还款能力相符等；（五）家庭已有住房套数。应通过借款人的年龄、学历、工作年限、职业、在职年限等信息判断借款人目前收入的合理性及未来行业发展对收入水平的影响；应通过借款人的收入水平、财务情况和负债情况判断其贷款偿付能力；应通过了解借款人目前居住情况及此次购房的首付支出判断其所购房产的目的及拥有意愿等因素，并据此对贷款申请做整体分析。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '借款合同签订（一）贷款经审批同意后，经办行与借款人签订《个人购房借款/担保合同》和《划款扣款授权书》。借款人必须在经办行开立个人结算存款账户，便于还本付息；（二）以现房按揭的，经办行应与房地产开发商签订《个人购房借款/担保合同》，保证期间自经办行与借款人签订借款合同起至借款人所购房屋作为抵押物并办妥抵押手续止；（三）以期房按揭的，经办行应协助抵押人持依法生效的商品房销（预）售合同到房地产登记机关办理预告登记，并确保自能够进行不动产登记之日起三个月内办理正式登记手续，借款人必须将所购房产价值全额用于贷款抵押。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '以所购房屋作抵押担保的，借款人应办理抵押财产保险，保险单中应注明本行为出险第一受益人，保险期限不得短于借款期限，投保金额不得低于贷款本息，保险费用由借款人承担。抵押期间，保险单正本由本行保管。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十一条', '贷款发放。办妥上述手续后，本行按信贷管理规定发放贷款。最后按合同约定，将贷款以转账方式直接汇入约定的房地产开发商在银行开立的监管专户。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十二条', '凡预售房许可证上明确监管账户为本行的，房地产开发企业应当向监管账户开立行提交根据项目建设方案及施工进度编制的预售项目用款计划。监管账户开立行必须根据用款计划进行账户资金的使用。用款计划应按照地下结构完成、结构封顶、竣工验收、完成初始登记并达到购房人可单方办理转移登记的条件等四个环节设置资金使用节点。商品房预售项目用款计划表经客户经理调查，符合条件的须报信贷管理部审核审批，审核通过后方可进行用款。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十三条', '凡预售房许可证上明确监管账户为本行的，房地产开发企业申请使用监管资金，应填写商品房预售资金拨付申请表，监管账户开立行结合本行审批的用款计划。审核申请材料，符合资金使用条件的在申请表“预售资金监管银行资金监管情况确认意见”一栏中签署意见，并加盖业务专用章，交柜面经办；不符合资金使用条件的应及时在申请表中出具不予拨付的说明，并注明原因交房地产开发企业，一联留存。资料需专人专夹保管。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十四条', '贷后检查贷款发放后，要加强对开发商、借款人和担保人的贷后检查与监督，发现风险应及时采取相应措施。（一）对开发商的贷后检查1.是否配合借款人及时办理“两证”；2.是否有影响履行担保责任的其他情况。（二）对借款人的贷后检查1.每期还款是否正常；2.收入是否依然稳定，还款能力是否有变化；3.抵押物保存是否完好，未经本行同意，借款人是否有将抵押物再次抵押、出租、转让、变卖、赠予等现象。（三）对担保人的贷后检查1.收入是否依然稳定，担保能力是否有变化；2.是否有影响履行担保责任的其他情况。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十五条', '预告登记自能进行不动产登记之日起 3 个月内必须进行登记申请。经办行要明确专人跟踪负责，每月必须与开发商了解办证进度，形成书面记录入档保管。对确认已办证的，必须在 15 个工作日内持《房屋预告登记证明》前往房管局换取他项权证并上缴总行档案室保管。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十六条', '借款人在每月 20 日前如不能筹齐当月还款额，可给予宽限期，宽限期不超过当月月底。如借款人月底仍不能归还当月贷款本息，则于下月初进行逾期处理，逾期部分从上月21日起按规定计收逾期息和罚息。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十七条', '借款人可以提前全部或部分偿还贷款本金。提前还款的当期仍计收贷款利息。借款人在征得本行同意的前提下，可一次性提前归还所余的贷款本息或部分提前归还贷款本息。提前偿还贷款本息的计算。提前还款分两种情况：一种是缩短整个贷款期限，增加每期或某期的还款金额；另一种是整个还款期限不变，增加某期还款金额，减少其他期还款金额。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十八条', '借款人在第一期和最后一期还款时，根据贷款余额和合同约定期限的利率按实际使用的天数计算贷款利息。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十九条', '借款人按合同约定偿还全部贷款本息后，借款担保合同终止。经办行应当将保管的抵押物权属证明交还借款人，并与借款人向原登记机关办理注销登记。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第四十条', '经办行应按信贷管理制度规定对客户进行检查，并实行分析制度和重大事项分析报告制度，及时采取措施，防范贷款风险。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第四十一条', '借款人死亡、失踪或丧失民事行为能力的，由其财产合法继承人继续履行借款人所签订的借款合同义务。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第六章  违约处置', '第四十二条', '有下列情况之一的，本行有权解除合同，并有权实现抵押权。（一）借款合同期内，借款人未按合同约定的还款计划按时、足额偿还贷款本息；（二）借款人在合同履行期内死亡、失踪或丧失民事行为能力后无继承人或受遗赠人；（三）借款人在合同履行期内死亡、失踪或丧失民事行为能力后，其遗产或财产继承人、受遗赠人、监护人、财产代管人拒绝继续履行原借款合同。违反上述第（一）条款的，本行有权根据人民银行相关规定，对贷款余额、未收取的利息以及利息复利计收罚息。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第六章  违约处置', '第四十三条', '有下列情况之一的，本行有权停止发放贷款，解除贷款合同，提前收回贷款本息，并有权实现抵押权。（一）借款人提供虚假文件或资料，已经或很可能造成贷款损失；（二）借款人拒绝或阻挠本行对其贷款用途进行监督检查；（三）保证人违反保证担保合同或丧失承担连带保证责任能力，抵押物因意外损毁不足以清偿贷款本息，而借款人未按要求重新落实新保证人或新抵押物；（四）借款人擅自将抵押物拆除、转让、变卖、赠予或重复抵押等；（五）借款人与他人签订有损本行权益的合同或协议；（六）借款人在合同履行期内擅自中断或撤销保险；（七）违反本办法和借款合同的其他行为。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第六章  违约处置', '第四十四条', '本行处分抵押物所得款项按以下顺序分配：（一）支付处分抵押物的费用；（二）扣除有关税款；（三）清偿借款人所有借款本息。处分抵押物如有结余的，退还借款人或其继承人、受遗赠人，不足偿还贷款本息及相关费用部分，本行有权继续向借款人或其继承人等追索。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第七章  附则', '第四十五条', '贷款业务经办人员必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第七章  附则', '第四十六条', '本办法及流程未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第七章  附则', '第四十七条', '本办法及流程由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《个人楼宇按揭贷款管理办法及流程》', '第七章  附则', '第四十八条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第一章  总则', '第一条', '为有效规范和推动本行各类个人消费信贷业务的健康开展，根据《个人贷款管理暂行办法》、《关于开展个人消费信贷的指导意见》等法律法规以及本行《个人贷款管理实施细则》、《贷款业务尽职管理办法》等相关的信贷管理制度，制定本办法。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称个人消费贷款是本行向服务辖区内自然人发放的，用于购买非营运汽车或其它大宗耐用消费品、房屋装修、旅游消费、教育消费以及其它具有合理用途的消费品或服务的人民币贷款。（一）非营运汽车贷款是指向借款人发放用于购买符合注册条件的非营运汽车贷款。（二）大宗耐用消费品贷款是指向借款人发放的用于购买家庭耐用消费品的贷款，包括家用电器、电脑、家具、健身器材等。（三）房屋装修贷款是指向借款人发放的用于本人及家庭房屋装修的贷款。（四）旅游消费贷款是指向借款人发放的用于本人及家庭成员旅游消费的贷款。（五）教育消费贷款是指向借款人发放的满足本人及其子女就学资金需求的贷款。（六）其它具有合理用途的消费贷款。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第一章  总则', '第三条', '个人消费贷款应当遵循“严格程序，额度控制，专款专用，按期偿还”的原则。严格程序。严格按照有关规定办理个人消费贷款业务，不得简化或省略程序，不得逆程序操作。额度控制。要按照借款人的信用状况、消费总额、家庭收入水平和综合还款能力，核定每个借款人的具体贷款额度，不得突破本办法规定的最高限额。专款专用。个人消费贷款只能按申请时用途购买消费品或服务，不得挪作他用。按期偿还。贷款到期前，管户客户经理要及时通知借款人按期偿还贷款本息。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第四条', '本行所辖的支行、营业部、小微业务部、公司业务部（以下简称“经办行”）负责对个人消费贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '授信评审部负责对个人消费贷款业务的授信管理。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '三农业务部负责个人消费贷款业务的宣传策划、营销推动及日常督查管理。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定个人消费贷款管理办法，对个人消费贷款业务的用信管理和贷后检查管理的监督。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对个人消费贷款用款进行审查。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '资产保全部负责对不良贷款的清收保全及相关管理。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对个人消费贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核个人消费贷款办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '审计部负责对个人消费贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十四条', '借款人应具备以下条件：（一）年龄在18周岁（含）以上，具有完全民事行为能力的自然人；借款人年龄加贷款期限不得超过65周岁，其中：借款人为公职人员的，申请以个人住房抵押担保申请消费贷款的，可根据其身体状况和未来还款能力（综合考虑退休前后）适当放宽至70周岁。公职人员指公务员或参照公务员管理的其他企事业单位、人民团体等组织内从事公共事务管理部门的正式在编工作人员。存单质押贷款借款人年龄不受限制。（二）有固定的住所和合法、稳定的经济收入，具备履约还款能力；（三）诚实守信，无不良生活嗜好，无重大不良信用记录；（四）自有资金或首付款比例不得低于消费总价款的 20%；（五）除信用借款外，能够提供本行认可的财产抵押、质押或保证；（六）在本行开立账户，自愿接受信贷监督和结算监督；（七）家庭其他成员在本行无贷款（住房按揭贷款、质押贷款除外）；（八）借款人需提供与借款用途相符的，真实合法的消费证明；（九）本行规定的其他条件。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十五条', '借款人条件的特殊规定：申请个人消费贷款用于留学的，借款人为拟留学人员或其直系亲属（父母及配偶），另须具备以下条件：（一）借款人为出国留学人员本人的，在出国留学前应具有贷款人所在地的常住户口或其他有效居住身份；（二）借款人为出国留学人员的直系亲属的，应具有贷款人服务区域内的常住户口或其他有效居住身份，有固定的住所，有稳定的职业和收入来源，具备按期还本付息的能力；（三）借款人应持有拟留学人员的国外留学学校出具的入学通知书或其他有效入学证明，并在贷款提款前须取得拟留学国家的入境签证。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十六条', '贷款额度。根据借款人的消费总价款、信用状况、家庭总收入、家庭净资产和综合还款能力等，合理确定贷款额度，其中，汽车消费贷款额度按《汽车消费贷款管理办法及流程》规定执行。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十七条', '贷款期限。在确定贷款期限时，应在综合考虑借款人的借款用途、经济收入状况、还款能力、担保情况、健康状况及年龄等因素的基础上合理确定，最长不超过5年。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十八条', '贷款利率。个人消费贷款利率根据借款人提供的不同的担保方式和贷款品种等，按照本行利率定价办法执行。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第三章  贷款条件、额度、期限、利率及还款方式', '第十九条', '还款方式。对于贷款期限在一年内（含）的，实行按季（月）结息，到期还本的还款方式；贷款期限在一年以上的，实行分期还款，一般实行等额本息或等额本金的还款方式。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第四章  贷款担保', '第二十条', '借款人申请个人消费贷款，除符合条件的信用借款外，借款人应按要求提供本行认可的担保。各经办行需按照本行《个人贷款管理实施细则》等相关制度办法的要求，对担保人的资格、担保意愿、担保能力或抵（质）押物价值及变现能力等方面进行调查、审查。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第四章  贷款担保', '第二十一条', '采取保证方式担保的，保证人应具有完全民事行为能力，收入稳定，信用良好，具备保证资格和担保能力，有较好的代偿意愿。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第四章  贷款担保', '第二十二条', '以抵（质）押方式申请个人消费贷款的，抵（质）押物必须权属清晰、价值稳定、易于变现，需要登记的，要按规定办理抵押登记。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第四章  贷款担保', '第二十三条', '采取抵（质）押方式担保的，抵（质）押率应结合贷款期限、抵(质)押物种类及本行规定等合理确定。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第四章  贷款担保', '第二十四条', '采取信用免担保方式申请个人消费贷款的，按本行《个人信用贷款管理办法》执行。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第五章  业务流程', '第二十五条', '个人消费贷款的授信、用信的调查、审查、审批，合同签订、贷款发放、贷款支付及贷后管理等业务流程参照本行《个人贷款业务操作规程》办理。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第六章  附则', '第二十六条', '贷款经办人员必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第六章  附则', '第二十七条', '本办法及流程未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第六章  附则', '第二十八条', '本办法及流程所称个人消费贷款不包括个人住房贷款,个人住房贷款按《个人楼宇按揭贷款管理办法》执行。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第六章  附则', '第二十九条', '本办法及流程由商业银行股份有限公司负责制订、修改和解释。'),
(NULL, '信贷管理部', '《个人消费贷款管理办法及流程》', '第六章  附则', '第三十条', '本办法及流程自发文之日起施行。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第一章  总则', '第一条', '为提高本行信贷服务水平，满足辖区内同一区域的农户、个体工商户、小企业主在生产、经营等方面的信贷资金需求，根据《中华人民共和国担保法》、《农村信用合作社农户联保贷款指引》、《个人贷款管理暂行办法》和本行信贷管理相关制度的规定，制定本办法。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第一章  总则', '第二条', '本行所辖的支行、小微业务部（以下简称“支行”）办理个人联保业务。本办法所称借款人是指依照本办法规定参加联保小组的自然人。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所称个人联保贷款是指本行服务辖内同一区域范围内的农户、个体工商户、小企业主分别依照本办法组成联保小组，本行对联保小组成员发放的，并由联保小组成员共同承担连带保证责任的贷款。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第一章  总则', '第四条', '个人联保贷款实行“自愿组合、共同申请、多户联保、周转使用、责任连带、到期还款”的原则。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '支行负责个人联保贷款业务的受理、贷款“三查”和联保风险保证金的核算和管理。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '授信评审部负责对个人联保贷款业务的授信管理。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定个人联保贷款管理办法及流程，对个人联保贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对个人联保贷款用款进行审查。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '风险管理部负责对个人联保贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核个人联保贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '审计部负责对个人联保贷款进行审计稽核。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十二条', '具备下列条件的借款人可以自愿组成联保小组：1.具有完全民事行为能力；2.经营项目符合国家的政策、法令，产品有市场；3.生产经营有效益，能按时偿还贷款本息，诚实守信；4.有固定住所和相对稳定的生产经营场地、必要的设施及有合法、稳定的收入来源；5.联保小组各成员在本行授信额度应相同；6.在同一区域内经营；7.在本行开立存款结算账户。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十三条', '联保小组由本行服务区域内的借款人组成，小组成员一般不少于5人。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十四条', '联保小组成员一次只能参加一个联保小组，不得同时参加两个或两个以上联保小组；未经贷款人同意，不得为本联保小组外的借款人提供担保。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十五条', '设立联保小组，联保小组成员应填写设立联保小组申请书，由各支行受理后，客户经理对联保小组的基本情况进行调查，并形成书面材料，报经本行授信评审部审查确认后，由经办行与小组所有成员签订联保协议。联保小组自联保协议签署之日起成立。联保有效期由借贷双方协商议定，但最长不得超过2年。联保协议期满，经借贷双方同意后方可续签。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十六条', '联保小组所有成员应当遵循“自愿组合、诚实守信、风险共担”的原则，履行下列义务：1.按照借款合同约定偿付贷款本息；2.联保小组成员自愿缴纳联保小组风险保证金，风险保证金不低于授信金额的20%，并且总额须覆盖最大一户授信额；3.督促联保小组其他成员履行联保合同和联保协议，当其他借款人发生贷款挪用或其他影响贷款偿还的情况时，及时报告本行；4.在贷款本息未还清前，联保小组成员不得随意转让、毁损用贷款购买的物资和财产；5.对联保小组其他成员在本行的联保协议下的借款债务承担连带保证责任，在借款人不能按期归还贷款本息时，小组其他成员代为偿还贷款本息；6.联保小组应推选一名小组成员为联保小组组长，负责联保小组有关事项的协调；7.联保小组每季召开一次例会，支行应派人列席，成员之间相互通报经营和贷款使用情况；8.共同决定联保小组的变更和解散事宜。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十七条', '联保小组成员的变更。联保借款合同签订之前允许联保小组成员变更，但联保借款合同签订后，联保小组成员不得变更。1.联保小组成员的退出。联保协议签订后，小组任一成员退出联保小组，须征得联保小组全体成员和支行同意，且其履行清偿义务后，方可退出联保小组。 2.联保小组成员的增补。对于符合参加联保小组条件的，经联保小组全体成员同意和支行审查同意后，可加入联保小组，成为联保小组新成员。3.联保小组成员变更后，必须与支行重新签署联保协议或签订补充协议。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十八条', '出现下列情况之一的，联保小组解散：1.联保小组成员少于本行规定的最低户数；2.根据联保协议约定或经联保小组成员共同协商决定解散;3.联保小组半数以上成员无力承担连带担保责任；4.联保小组严重违反联保协议。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第三章  联保小组的成立、变更和解散', '第十九条', '联保协议到期内，联保小组解散，联保小组成员应按照联保协议履行偿还贷款本息和保证义务，联保协议至联保小组全体成员付清所欠本行贷款本息后终止。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第四章  贷款用途、金额、期限及利率', '第二十条', '贷款用途：（一）种植业、养殖业等农业生产费用贷款；（二）加工、手工、商业、运输等个体经营贷款；（三）消费性贷款；（四）本行同意的其他用途。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第四章  贷款用途、金额、期限及利率', '第二十一条', '联保贷款实行“双限”管理，即核定联保小组成员的最高授信限额和联保小组总的授信限额，联保小组总的授信限额一般不超过小组所有成员的净资产总额60%，对小组任一成员的授信不得超过联保小组授信额度总和的50%。具体额度由本行与联保小组成员根据实际情况共同协商确定。本行每年可根据借款人的信用记录及经营状况适当调整贷款的授信额度。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第四章  贷款用途、金额、期限及利率', '第二十二条', '联保贷款期限由本行根据借款人生产经营活动的周期确定，但最长不得超过联保协议的期限。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第四章  贷款用途、金额、期限及利率', '第二十三条', '联保贷款利率参照本行利率定价办法执行。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '联保贷款的操作流程包括联保小组要件审查和发放贷款两部分。（一）联保小组要件审查流程：借款人申请、客户经理调查、审查审批、签订联保协议。（二）联保贷款操作流程：客户申请、受理调查、授信审查审批、用信审查审批、设立联保小组、签订合同、缴纳风险保证金、发放与支付、贷后管理、贷款归还、档案管理。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '联保小组要件审查：（一）申请人提出申请。申请人向支行书面提出设立联保小组申请并提供下列资料：1.书面申请。内容包括经营情况、联保小组成员情况、拟申请联保贷款额度等；2.联保小组所有成员身份证件原件及复印件；3.联保小组所有成员的经营资料，包括但不限于营业执照、税务登记证、经营许可证、购销合同等；4.联保小组所有成员的资产证明、收入证明等。（二）客户经理调查。支行受理申请后，客户经理对申请人提供的有关资料进行调查，除按本行信贷管理制度规定调查情况外，重点调查以下情况：1.联保小组成员关系，是否是直系亲属，是否存在相互间的关联关系等；2.联保小组成员的合作意愿是否真实有效；3.联保小组成员经营情况、经济实力、信用记录和还款能力等，是否具备代偿能力。（三）审查和审批。支行客户经理将调查结论意见，报支行贷款会办小组会办后，上报授信评审部审批。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '客户贷款申请。借款人向支行提出书面申请，填写个人借款申请书，并提供下列资料：1.借款人的合法身份证件（居民户口簿、身份证或其他有效居留证件、婚姻证明等）；2.营业执照；3.借款人家庭财产和经济收入的证明；4.能够证明授信合理用途的资料，如明确的生产经营计划、纳税凭证、采购合同等；5.本行要求提供的其他资料。组建联保小组已有的资料可不再重复提供。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '受理调查。对借款人提交的申请，支行对符合条件的应及时受理，落实双人实地调查，除按照本行授信管理办法要求进行调查外，还应重点调查以下内容：1.核实借款人递交的所有材料是否真实、合法；2.借款用途是否用于自身经营；3.申请金额是否适当，贷款期限是否合理，还款来源是否稳定，是否有足够的现金流或经营收入；4.借款人信用情况，在我行或他行是否有不良记录，是否有欠缴税款等情况。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '设立联保小组。设立的联保小组必须符合第三章相关规定，签订《联保小组合作协议》，联保小组自联保协议签署之日设立。1.明确联保小组成员的权利和义务，以及联保小组设立、变更和终止情况；2.按授信额度设立风险保证金。风险保证金存入支行专户，归小组成员所有。小组成员全部还清贷款本息后，小组成员可自主决定处理风险保证金，联保协议终止的，风险保证金应退还缴纳的小组各成员。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '签订合同。联保协议签订后，联保小组借款人共同与本行签订借款合同。客户经理（两人以上）必须当场监督联保小组所有成员共同在合同文本上签字，确保签订的合同真实、有效。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第三十一条', '缴纳风险保证金。联保小组成员须在支行开立保证金专户，存入联保风险保证金，用于因借款人不能履行到期贷款本息时履行责任的担保金。因履行担保责任，风险保证金扣划后，保证金专户余额低于规定的保证金额度时，小组成员应协商立即补足。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第三十二条', '发放与支付。借款人办妥上述手续后，支行按合同约定发放贷款，将资金划入借款人本人的结算账户，并严格监督借款人按合同约定支付方式支付贷款资金，不符合自主支付条件的应实行受托支付。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第三十三条', '贷后管理。1.贷款发放后，联保小组组长及其他成员应负责协助本行管理贷款，及时了解借款人的生产经营情况和贷款使用情况，并告知本行。2.支行要以联保小组为单位设立贷款台账，及时记载借款人生产经营及贷款使用情况。3.贷款发放后，客户经理按照本行信贷管理制度的要求做好贷后管理工作，了解和掌握借款人的生产经营情况和贷款使用情况，加强对贷款的跟踪检查和监督管理。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第三十四条', '联保小组任何成员不得以任何方式，将贷款转让、转借给他人或集中使用支行贷给联保小组其他成员的贷款。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第五章  贷款操作程序', '第三十五条', '联保小组成员在联保协议以外借款或提供担保，应取得联保小组成员的书面同意。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第六章  责任追究', '第三十六条', '业务经办人员必须严格按照本办法规定的贷款条件，对联保小组成员进行认真的调查与审查，严格按贷款流程发放贷款，同一组联保小组成员的贷款必须由同一名客户经理负责发放和管理，该客户经理即为该小组所有成员贷款的清收责任人。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第六章  责任追究', '第三十七条', '联保贷款产生风险的，按照本行信贷管理有关规定认定和追究相关人员的责任。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第七章  附则', '第三十八条', '本办法及流程由信贷管理部负责制定、修改和解释。'),
(NULL, '信贷管理部', '《个人联保贷款管理办法及流程》', '第七章  附则', '第三十九条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第一章  总则', '第一条', '为规范本行个人贷款业务经营行为，加强个人贷款审慎经营管理，促进个人贷款业务健康发展，依据《中华人民共和国商业银行法》、《贷款通则》、《个人贷款管理暂行办法》和本行《个人贷款管理实施细则》制定本办法。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第一章  总则', '第二条', '本行所辖支行、营业部、公司业务部、小微业务部（以下简称“经办行”）以及个人贷款业务审查、审批和管理机构，必须遵守本规程。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第一章  总则', '第三条', '本行开展个人贷款业务应当遵循“依法合规、审慎经营、平等自愿、公平诚信”的原则。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第一章  总则', '第四条', '按照办理贷款的先后顺序，个人贷款业务流程分为：客户申请、受理与调查、授信审查与审批、用信审查与审批、签订合同、发放与支付、贷后管理、收回贷款、档案管理等环节。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第二章  客户申请、受理与调查', '第五条', '客户可以通过本行营业网点、服务热线电话、网上银行、手机银行等渠道向经办行提出授信申请，客户经理受理后，应及时联系并告知客户提供如下资料： ㈠申请人及其配偶身份证、户口簿、结婚证、实际居住地证明资料以及家庭主要财产、收入明细等，单身借款人应提供单身承诺，并由所在街道、居委会（村委会）或所在单位出具证明，并提供共同借款人。㈡申请人所从事的生产经营项目概况和资产负债、收入支出等情况证明材料。㈢申请贷款金额、用途、期限。㈣还款资金来源、还款方式及还款计划。㈤保证担保的，需按照《信贷业务操作规程》提供相应资料。㈥权利质押的，须提供拟质押权利凭证金额、期限、出票行等信息，申请人同时须向经办行提交质押的权利凭证原件。㈦抵押担保的，须提供抵押人、抵押物基本情况以及拟抵押物的相关权属证书原件。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第二章  客户申请、受理与调查', '第六条', '对申请人资格审查。申请人应符合下列基本条件：（一）借款人户口在本辖区，或户口虽不在本辖区，但固定经营场所在本辖区服务范围内；（二）借款人信用状况良好，无重大不良信用记录；（三）贷款用途明确、合法；（四）贷款申请数额、期限合理；（五）借款人具备还款意愿和还款能力；（六）能提供本行认可的担保，信用贷款除外；（七）家庭其他成员在本行无贷款（住房按揭贷款、质押贷款除外）；（八）本行要求的其他条件。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第二章  客户申请、受理与调查', '第七条', '客户面谈。客户经理应向申请人了解包括但不限于以下基本情况：㈠ 家庭成员构成；㈡家庭成员从事行业及收入、支出情况；㈢生产经营项目、规模及盈利情况；㈣资金来源及资产负债情况；㈤拟提供担保人基本情况。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第二章  客户申请、受理与调查', '第八条', '对以上面谈了解事项及时做好记录。对明显不符合受理条件的，要向申请人讲明不能受理的原因，退回授信申请；符合受理条件的，进行授信调查。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第二章  客户申请、受理与调查', '第九条', '经办行应采取现场与非现场相结合的形式履行尽职调查，根据调查情况形成书面报告，并对其内容的真实性、完整性和有效性负责。尽职调查包括但不限于以下内容：㈠实地调查。对申请人居住地和经营场所进行调查， 并做好书面记录，主要内容：1.申请人基本情况；2.申请人信用记录；3.申请人资产负债、收入、支出情况；4.借款用途；5.申请人还款来源、还款能力及还款方式；6.保证人信用记录、担保意愿、担保能力或抵（质）押物价值及变现能力。㈡外部调查。通过申请人、担保人的邻居、同事、居委会、村组干部、同行业客户进行外部调查，并做好书面记录，主要包括：1.品行。申请人和保证人是否有不良嗜好、违法行为。2.信誉。申请人和保证人是否有不良履约记录或未终结的诉讼纠纷。3.实力。申请人和保证人资产负债和盈利状况是否真实。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第二章  客户申请、受理与调查', '第十条', '客户经理应按照“双人四眼”原则进行实地调查和外围调查，就自有资金形成过程、还款来源、经营能力、生产技术、市场前景、经营效益及利润率与同行业比较，在此基础上对客户进行信用评级。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第二章  客户申请、受理与调查', '第十一条', '客户经理根据面谈和调查情况撰写调查报告，报告包括但不限于以下内容：㈠申请人基本情况；㈡申请人生产经营情况、资产负债、收入、支出情况；㈢申请贷款原因及用途；㈣申请人经营效益分析；㈤借款人还款意愿、还款能力及还款方式；㈥担保人担保意愿、担保能力、代偿能力、或抵（质）押物价值及变现能力；㈦贷款风险点及防范措施；㈧结论。客户经理应对是否同意授信作出明确表示，对建议授信的金额、期限、利率、还款方式、担保方式和限制性条款等提出明确的调查意见并签名。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第三章  授信审查与审批', '第十二条', '经办行审查岗和授信评审部审查人负责对借款人基本条件审查。贷款授信审查与风险评价要点包括但不限于：㈠基本条件审查要点：1.送审的材料是否齐全；2.申请人、担保人是否符合本行有关规定的条件；3.申请人经营项目、授信用途是否合法、合规；4.申请人生产经营、财务状况、信誉状况是否良好；5.授信金额、期限、利率、还款方式、担保方式和限制性条款等贷款要素是否合理、合规；6.第一、第二还款来源是否充足、可靠、合法、有效；7.申请人经营中存在的主要问题及对贷款安全的潜在影响；8.授信尽职调查报告是否基本真实并完整；9.其他需要审查的内容。㈡风险审查要点：1.申请人、担保人的资格、资信情况是否符合法律法规、符合监管制度的规定；2.贷款尽职调查报告是否对风险点进行了充分揭露，是否制定了有效的风险防范措施；3.账户监管、支付委托等相关手续的合规性、有效性；4.其他需要审查的内容。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第三章  授信审查与审批', '第十三条', '审查。经办行会办通过的授信申请，应及时按要求完善资料，上报授信评审部审查，授信评审部必要时可进行现场复查，需报下列资料的原件或复印件：㈠个人客户授信申请调查表；㈡申请人和拟提供担保人的征信记录、本行信息平台查询记录；㈢拟用于担保的抵、质押物权属、数量等证明文件；㈣借款人及其配偶、保证人身份证复印件；㈤调查报告；㈥内部会办记录；㈦个人贷款客户面谈记录；㈧其他资料。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第三章  授信审查与审批', '第十四条', '审批。权限内的，由授信评审部有权审批人审批后下发授信批复。权限外的，由授信评审部提交贷款审查委员会审议，报总行行长备案后下发授信批复。重大关联交易经贷审会审议后，报董事会风险管理与关联交易控制委员会审查后，交董事会审议批准。一般关联交易经贷审会审议后，向董事会风险管理与关联交易控制委员会实施事后备案。重大关联交易应当在批准之日起十个工作日内报告监事会，同时报告中国银行业监督管理委员会。与本银行董事、总行高级管理人员有关联关系的关联交易应当在批准之日起十个工作日内报告监事会。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第四章  用信审查与审批', '第十五条', '经办行权限内的用信申请，由经办行有权人审查、审批；超出经办行审批权限的用信申请，经办行会办后报送信贷管理部审核，由信贷管理部下发用信批复。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第四章  用信审查与审批', '第十六条', '用信申请时应提供下列资料：㈠授信审批文件及授信限制性条款落实情况的证明材料；㈡用信需求的证明文件(包括购销合同、协议等)；㈢50万元以上的大额自然人贷款或借新还旧、展期等贷款用信时，还须提供《用信审批表》；㈣其他需要审核的材料。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第四章  用信审查与审批', '第十七条', '用信审查的主要内容包括：㈠借款人、担保人的资信情况是否符合制度的规定；㈡对客户提供的用信资料完整性、真实性、有效性进行审查核实，并分析其是否符合法律法规和本行的相关信贷政策；㈢查验授信文件的有效性，客户用信条件是否满足授信条件；㈣查验授信时是否提出限制性条款，有无落实到位；㈤其他需要审核的内容。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第五章  签订合同', '第十八条', '贷款用信审批同意后，客户经理应及时通知借款人、担保人进行借款合同、相关协议（以下简称“合同”）商谈。商谈应在经办行视频监控录像状态下进行。如因特殊情况，无法在视频监控下操作时，经办行应指定两名以上（含）工作人员与客户商谈并拍照或摄像，影像资料应及时打印并签字确认，作为重要资料归档保管。合同商谈的主要内容：㈠各方当事人的权利、义务及违约责任，避免对重要事项未约定、约定不明或约定无效；㈡具体的贷款金额、期限、利率、用途、还款保障以及风险处置等要素和有关细节；㈢约定贷款支付的相关事项，包括但不限于以下内容：1.贷款资金的支付方式和本行受托支付的金额标准；2.支付方式变更及触发变更条件；3.借款资金支付的限制、禁止行为；4.借款人应及时提供贷款资金使用记录和资料。㈣与借款人约定的提款条件以及贷款资金支付接受本行管理和控制等与贷款使用相关的条款；㈤对借款人相关账户实施监控，必要时可约定专门的发放账户；㈥要求借款人对与贷款相关的重要内容做出承诺，承诺内容应包括：1.及时向本行提供完整、真实、有效的材料；2.配合经办行进行贷款支付管理、贷后管理及相关检查；3.发生影响其偿债能力的重大不利事项时及时通知本行；4.经办行有权根据借款人经营情况提前收回贷款；5.其他需要借款人做出承诺的事项。㈦借款人出现以下情形之一时，借款人应承担的违约责任和本行可采取的措施：1.未按约定用途使用贷款的；2.未按约定方式进行贷款资金支付的；3.未遵守承诺事项的；4.发生重大交叉违约事件的；5.违反借款合同约定其他情形的。㈧其他需要商谈的事项。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第五章  签订合同', '第十九条', '合同商谈完成后，根据相关法律法规、监管制度及本行相关信贷管理规定的要求，结合合同商谈的结果，由经办行和借款人及其他相关当事人签订相关合同及协议。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第五章  签订合同', '第二十条', '合同签订的具体规定须按本行《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第五章  签订合同', '第二十一条', '一般借款人应购买借款人意外伤害保险。对授信总额300万元以上、以厂房、设备、船舶等运输工具抵押担保的，应同时购买企业财产保险，保险期不得短于贷款存续期限，保险金额不得小于贷款本息，保险合同及保险单中应当注明出险时本行为保险赔偿金的第一受益人，保单正本经本行信贷管理部核验后交档案中心集中保管。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十二条', '经办客户经理根据审批意见，在信贷管理系统中录入相关信息；非手机银行自助放款客户，由客户本人携带本人身份证和易贷通卡，按用款流程办理。柜面受理后，经办行权限内的，由有权人审查发放；权限外的，由运营管理部审查发放。对采用手机银行自助放款的客户，按相关规定和协议执行。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十三条', '经办行柜面人员应根据合同约定的受托支付或借款人自主支付方式支付贷款资金，监督贷款资金按约定用途使用。受托支付是指经办行根据借款人书面的提款申请和支付委托，将贷款通过借款人账户支付给符合合同约定用途的借款人交易对象。借款人自主支付是指经办行根据借款人的提款申请将贷款资金发放至借款人账户后，由借款人自主支付给符合合同约定用途的借款人交易对象。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十四条', '采用受托支付的，经办行应根据借款人申请约定的贷款用途，审核借款人提供的支付申请所列支付对象、支付金额等相关交易资料和凭证是否符合合同约定条件。审核同意后，经办行按借款人授权将贷款资金通过借款人账户支付给借款人交易对象。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十五条', '采用借款人自主支付的，经办行应按借款合同约定要求借款人定期报告贷款资金支付情况，并通过账户分析、凭证查验或现场检查等方式核查贷款支付是否符合约定用途。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十六条', '签订最高额借款合同实行循环放款的，可以确定支付方式的，在合同中约定具体的支付方式；不能确定具体支付方式的，应根据核定额度和期限内的每笔贷款的实际用途，在借款人《提款支付审查意见表》上予以明确。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十七条', '经办行可以通过借款合同的约定，要求借款人指定专门的贷款发放账户，贷款发放和支付应通过该账户办理。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十八条', '在贷款发放和支付过程中，借款人出现下列情形的，经办行应与借款人协商补充贷款发放和支付的条件，或根据合同约定变更贷款支付方式，停止贷款资金的发放和支付：㈠信用状况下降；㈡不按合同约定支付贷款资金，或以化整为零方式规避经办行受托支付；㈢借款人收入出现异常变化；㈣贷款资金使用出现异常。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第六章  发放与支付', '第二十九条', '信贷管理业务系统流程审批。客户经理要在客户信息健全的基础上，在信贷管理系统中按照客户信用评级、授信申请、授信项下业务申请、合同登记、放贷申请、打印放贷通知书等实际业务流程进行操作。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第七章  贷后管理', '第三十条', '贷款业务发生后，贷款发放责任人应在15日内进行首次跟踪检查（贷款存续期不足15天的除外），主要检查客户是否按合同约定用途使用贷款，对未按借款合同规定用途使用贷款的，应查明原因并提出处置的意见和建议。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第七章  贷后管理', '第三十一条', '经办行对所有个人贷款的贷后检查的方式、检查的时间与频率按规定执行。贷后检查的重点：㈠检查借款人、担保人的资产和生产经营、财务状况、资金回笼是否正常，按月或按季结息是否正常付息。㈡了解掌握借款人、担保人的家庭和经营出现的诉讼等重大事项，分析这些变动是否影响或将要影响借款人的生产经营和贷款安全。㈢检查抵（质）押物的完整性和安全性，抵押物的价值是否受到损失，抵押权是否受到侵害，质押物的保管是否符合规定。㈣其他需要检查的内容。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第七章  贷后管理', '第三十二条', '检查完毕后，客户经理应及时填制《贷后检查报告表》，经有权人复核后归档备查；对检查过程中发现影响贷款安全的重大事项，应及时揭示，提出防范和化解信贷风险的措施，报送经办行负责人和本行相关职能部门。所有贷后检查资料应及时上传、录入信贷V6系统。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第八章  收回贷款', '第三十三条', '贷款催收。短期贷款到期一周之前、中长期贷款到期一个月之前，客户经理应向借款人通过书面、短信、QQ、微信等途径发送到期履行债务通知，并取得有效回执。当月新增不良贷款至月末未有效处置的，应于次月3号前以书面形式向信贷管理部报告，说明形成原因和采取的清收措施。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第八章  收回贷款', '第三十四条', '个人贷款需要展期的，借款人应在贷款到期前5日内向经办行提出书面申请，经办行应对贷款展期原因进行审查，决定是否展期受理，并合理确定贷款展期期限，按规定程序报批，并加强对展期的后续管理。一年（含）以内的个人贷款，展期期限累计不得超过原贷款期限；一年以上的个人贷款，展期期限累计与原贷款期限相加，不得超过该贷款品种规定的最长贷款期限。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第八章  收回贷款', '第三十五条', '借款人出现违反合同约定情形的，经办行应及时采取有效清收或盘活措施，包括但不限于以下方式：㈠对借款人确因暂时经营困难不能按期归还贷款本息的，经办行可与借款人协商进行贷款重组、降本还息等。㈡对借款人拒不履行的，依法诉讼清收。㈢对确实无法收回的，符合呆账核销标准的，按规定及时申报核销。核销后，应继续向借款人、担保人追索，直至债务本息清偿完毕。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第八章  收回贷款', '第三十六条', '责任追究。对确实无法收回的不良贷款，根据最终清收结果，对照本行管理制度进行责任认定和追究。若触犯刑律的，依法移交司法机关，追究刑事责任。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第九章  档案管理', '第三十七条', '按本行信贷档案管理的要求收集、整理和保管个人贷款客户的档案。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第十章  附则', '第三十八条', '本规程未尽事宜参照本行信贷管理制度及信贷业务操作规程执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第十章  附则', '第三十九条', '本规程由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《个人贷款业务操作规程》', '第十章  附则', '第四十条', '本规程自公布之日起执行。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第一章  总则', '第一条', '为规范本行个人贷款业务经营行为，加强个人贷款业务审慎经营管理，促进个人贷款业务健康发展，依据《中华人民共和国商业银行法》、《贷款通则》、《个人贷款管理暂行办法》、《江苏省农村信用社个人贷款管理实施办法（试行）》，和本行《信贷管理制度》、《贷款业务尽职管理办法》制定本实施细则。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第一章  总则', '第二条', '本细则所称个人贷款，是指本行所辖支行、营业部、小微业务部、公司业务部（以下简称“经办行”）向符合条件的自然人发放的人民币贷款。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第一章  总则', '第三条', '本行开展个人贷款业务应当遵循依法合规、审慎经营、平等自愿、公平诚信的原则。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第一章  总则', '第四条', '本行应按区域、品种、客户群等维度建立个人贷款风险限额管理机制。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第一章  总则', '第五条', '本行应加强个人贷款用途与支付方式的管理。贷款用途应符合法律法规和国家有关政策，不得发放无指定用途的个人贷款。经办行应在借款合同中与借款人约定贷款发放条件、支付方式等事项，并按约定的支付方式和受托事项办理支付手续，有效防范个人贷款业务风险。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第一章  总则', '第六条', '应根据借款人经营情况、偿债能力，结合借款人收入、支出、资产负债、贷款用途、担保情况等因素，合理确定贷款金额和期限。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第一章  总则', '第七条', '应对借款人进行内部个人评级，采用科学合理的授信方法，做好综合授信，建立客户资信记录档案。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第八条', '经办行负责个人贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第九条', '授信评审部负责个人贷款业务的授信管理。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第十条', '信贷管理部负责拟定个人贷款管理实施细则及个人贷款业务操作规程，对个人贷款业务进行用信管理和贷后检查监督管理。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第十一条', '运营管理部根据权限负责对个人贷款用款进行审查。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第十二条', '计划财务部负责制定利率定价办法，并且对利率执行监督检查。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第十三条', '资产保全部负责对不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第十四条', '风险管理部负责个人贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第十五条', '合规管理部负责审核个人贷款管理实施细则的合规性和有效性。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第二章  组织管理体系', '第十六条', '审计部负责对个人贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第三章  贷款对象与条件', '第十七条', '个人贷款对象为国家规定可以作为借款人的、具有完全民事行为能力的自然人，且借款人年龄加上贷款期限不超过65周岁，其中：借款人为公职人员的，申请个人住房按揭贷款或以个人住房抵押担保申请消费贷款的，可根据其身体状况和未来还款能力（综合考虑退休前后）适当放宽至70周岁。公职人员指公务员或参照公务员管理的其他企事业单位、人民团体等组织内从事公共事务管理部门的正式在编工作人员。存单质押贷款借款人年龄不受限制。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第三章  贷款对象与条件', '第十八条', '个人贷款借款人应具备以下条件：（一）借款人户口在本辖区，或户口虽不在本辖区，但固定经营场所在本辖区服务范围内；（二）借款人信用状况良好，无重大不良信用记录；（三）贷款用途明确、合法；（四）贷款申请数额、期限合理；（五）借款人具备还款意愿和还款能力；（六）能提供本行认可的担保，信用贷款除外；（七）家庭其他成员在本行无贷款（住房按揭贷款、质押贷款除外）；（八）本行要求的其他条件。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第四章  贷款的种类、期限与利率', '第十九条', '个人贷款种类个人贷款按用途分为个人住房贷款、个人消费贷款和个人经营类贷款。（一）个人住房贷款是指向借款人发放的用于购买自用普通住房的贷款。（二）个人消费贷款是指向申请购买合理用途的消费品或服务的借款人发放的个人贷款，包括：购买非营运汽车或其它大宗耐用消费品、房屋装修、旅游消费、教育消费以及其它具有合理用途的消费品或服务的人民币贷款。（三）个人经营类贷款是指向具有合法生产、经营用途的自然人发放的贷款，包括以业主或实际控制人个人名义申请的实际用于其经营实体的贷款。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第四章  贷款的种类、期限与利率', '第二十条', '个人贷款期限贷款期限主要根据借款人的生产经营周期、未来现金流、还款能力和本行的资金供给能力等因素，并由借贷双方协商确定。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第四章  贷款的种类、期限与利率', '第二十一条', '个人贷款的利率按本行利率定价办法执行。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第五章  授信申请、受理与调查', '第二十二条', '客户申请。客户可到本行营业网点、或通过服务热线电话、网上银行、手机银行等渠道申请，客户经理受理后，应告知客户提供如下资料：（一）申请人及家庭基本情况资料主要包括夫妻双方身份证、户口簿、结婚证、实际居住地证明资料以及家庭主要财产、收入明细等，单身借款人需提供单身承诺并提供共同借款人，其中：单身借款人有工作的，由单位出具相关证明；无工作的，由所在街道、居委会或村委会出具相关证明。（二）贷款用途证明材料主要包括各类合同、协议以及自有资金筹集的渠道与存储情况等。（三）各类授权文件主要包括查询申请人本人、配偶或共同借款人以及拟提供保证自然人的个人信用报告的书面授权，拟向借款人提供担保的公司类客户应出具查询企业征信的书面授权。（四）拟提供担保的相关资料个人提供担保的，主要提供担保人有效身份证明、家庭主要财产、收入情况等；法人提供担保的须按本行《信贷业务操作规程》规定，提供相应的资料。（五）本行要求提供的借款人账户流水等其他资料。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第五章  授信申请、受理与调查', '第二十三条', '经办行应对申请人提供的授信申请材料的方式和具体内容提出要求，要求申请人恪守诚实守信原则，承诺所提供材料真实、完整、有效。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第五章  授信申请、受理与调查', '第二十四条', '经办行接受借款人申请必须坚持面谈制度。个人贷款面谈可与贷款调查同步进行，主要了解借款申请人的基本情况、家庭收入、资产负债情况、家庭成员构成、贷款用途、贷款期限、还款来源、担保方式、法律纠纷等内容，并做好面谈记录（见附件），共同签字确认。面谈记录作为信贷档案资料，统一规范保管。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第五章  授信申请、受理与调查', '第二十五条', '尽职调查经办行受理授信申请后，客户经理应按照“双人四眼”原则履行尽职调查，形成书面报告，就贷款同意与否、贷款金额、利率、期限、担保方式、还款方式和限制性条款等等提出明确意见，并对其内容的真实性、完整性和有效性负责。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第五章  授信申请、受理与调查', '第二十六条', '在不损害申请人合法权益和风险可控的前提下，可将贷款调查中的部分特定事项委托第三方代为办理，但必须明确第三方的资质条件与代理责任。不得将贷款调查的全部事项委托第三方完成。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第六章  授信审查与审批', '第二十七条', '授信审查经办行客户经理负责对个人贷款的受理、调查，审查人员负责对调查资料的真实性、有效性进行审查。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第六章  授信审查与审批', '第二十八条', '风险评价贷款风险评价应以分析借款人现金收入为基础，采取定量和定性分析方法，全面、动态地进行资信等级评定、贷款审查和风险评估。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第六章  授信审查与审批', '第二十九条', '本行授信评审部负责对经办行授信申请事项的审查，并按本行转授权管理办法提交有权审批人审批。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第六章  授信审查与审批', '第三十条', '对未获批准的授信申请，经办行应及时告知客户并做好解释工作。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第六章  授信审查与审批', '第三十一条', '本行将根据重大经济形势变化、违约率明显上升等异常情况，对经办行贷款审查、审批环节进行评价分析，及时、有针对性地调整审批政策，加强相关贷款的管理。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第七章  用信审查与审批', '第三十二条', '用信审查时应提供下列资料：（一）授信审批文件及授信限制性条款落实情况的证明材料；（二）用信需求的证明文件(包括购销合同、协议等)；（三）50万元以上的大额自然人贷款或借新还旧、展期等贷款用信时，还须提供《用信审批表》；（四）其他需要审核的材料。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第七章  用信审查与审批', '第三十三条', '用信审查的主要内容包括：（一）借款人、担保人的资信情况是否符合制度的规定；（二）对客户提供的用信资料完整性、真实性、有效性进行审查核实，并分析其是否符合法律法规和本行的相关信贷政策；（三）查验授信文件的有效性，客户用信条件是否满足授信条件；（四）查验授信时是否提出限制性条款，有无落实到位;（五）其他需要审核的内容。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第七章  用信审查与审批', '第三十四条', '经办行权限内的用信申请由经办行有权人审查、审批；超出经办行审批权限的用信申请，经办行用信小组会办后提交本行信贷管理部审核，并按本行转授权管理办法提交有权审批人审批。对使用特殊文本的用信须交合规管理部进行审查。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第三十五条', '客户经理根据本行信贷管理要求，与借款人、担保人及相关当事人签订合同、协议（以下简称合同）时，应在签约台视频监控录像状态下进行。如因上门服务等特殊情况，未在视频监控下签约的，必须由经办行指定两名及以上工作人员到客户所在地现场办理，并对签约环节进行拍照，图片打印后现场服务工作人员应及时签字确认，作为重要信贷资料留存。对需抵押的应按合同约定办理抵押物登记。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第三十六条', '未经复核、加盖“贷款合同专用章”的合同，不得发放贷款。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第三十七条', '经办行应在借款合同中与借款人明确约定贷款的金额、期限、利率、用途、支付、还款、结息方式、限制性条件等条款。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第三十八条', '上条所指“支付”条款，包括但不限于以下内容：（一）贷款资金的支付方式和本行受托支付的金额标准；（二）支付方式变更及触发变更条件；（三）借款资金支付的限制、禁止行为；（四）借款人应及时提供的贷款资金使用记录和资料。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第三十九条', '借款合同采用格式条款应向社会进行公示。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第四十条', '签订合同时，应提请客户仔细阅读合同条款，对客户提出的问题或疑问，经办行应予以解答。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第四十一条', '保险相关规定。借款人一般应办理借款人意外伤害保险，但从事运输、建筑、化工等危险行业的借款人必须全部办理。对贷款授信金额达到或高于300万元的个人，其拥有的生产性用房须办理财产保险，保险期不得短于贷款主合同履行期限，保险合同及保险单中应当注明出险时本行为保险赔偿金的第一受益人。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第八章  合同签订与发放', '第四十二条', '合同变更相关规定合同变更包括合同当事人变更、贷款额度调整、合同期限调整等。合同一般不允许变更，合同如有变更事项，必须由客户经理受理、审核，并按授信、用信审批权限逐级上报审批。合同中格式条款的变更必须经本行合规管理部或本行法律顾问审核。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第九章  贷款支付管理', '第四十三条', '经办客户经理根据审批意见，在相关信贷管理系统中录入相关信息，按流程审批发放。柜面受理后，经办行权限范围内的，由有权人审查；权限外的，按转授权办法执行。客户申请手机银行等电子渠道自助放款的，必须签订自助用信服务协议。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第九章  贷款支付管理', '第四十四条', '经办行柜面人员应根据合同约定的受托支付或借款人自主支付方式支付贷款资金，监督贷款资金按约定用途使用。受托支付是指经办行根据借款人书面的提款申请和支付委托，将贷款资金支付给符合合同约定用途的借款人交易对象。借款人自主支付是指经办行根据借款人的提款申请将贷款资金直接发放至借款人账户，并由借款人自主支付给符合合同约定用途的借款人交易对象。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第九章  贷款支付管理', '第四十五条', '下列个人贷款，贷款资金应当采用经办行受托支付方式向借款人交易对象支付，其余贷款除授信批复上有明确限制性条款，经办行审查同意后可以采取借款人自主支付方式。（一）个人住房贷款；（二）借款人交易对象可以事先确定，具备使用非现金结算方式且金额超过30万元的个人消费贷款；（三）借款人交易对象可以事先确定，具备使用非现金结算方式且金额超过50万元的个人生产经营类贷款；（四）法律法规规定的其他情形的。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第九章  贷款支付管理', '第四十六条', '采用受托支付的，经办行应根据借款人申请约定的贷款用途，审核借款人提供的支付申请所列支付对象、支付金额等相关交易资料和凭证是否符合合同约定条件。审核同意后，经办行按借款人授权将贷款资金通过借款人账户支付给借款人交易对象。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第九章  贷款支付管理', '第四十七条', '采用借款人自主支付的，经办行应按借款合同约定要求借款人定期报告贷款资金支付情况，并通过账户分析、凭证查验或现场检查等方式核查贷款支付是否符合约定用途。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第九章  贷款支付管理', '第四十八条', '签订最高额借款合同实行循环放款的，可以确定支付条件、方式的，在合同中约定具体条件以及相应的支付方式；不能确定支付方式的，应根据核定额度和期限内的每笔贷款的实际用途，在借款人《提款支付审查意见表》上予以明确。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第九章  贷款支付管理', '第四十九条', '在贷款发放和支付过程中，借款人出现下列情形之一的，经办行应与借款人协商补充贷款发放和支付条件，或根据合同约定变更贷款支付方式，停止贷款资金的发放和支付：（一）信用状况下降；（二）不按合同约定支付贷款资金，或以化整为零方式规避经办行受托支付；（三）交易资料不完备或者真实；（四）贷款资金使用出现异常；（五）本行认定的其他情形。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十条', '贷款发放责任人应在15日内进行首次跟踪检查（贷款存续期不足15天的除外），主要检查客户是否按合同约定用途使用贷款，对未按借款合同规定用途使用贷款的，应查明原因并提出处置的意见和建议。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十一条', '经办行对所有个人贷款的贷后检查方式、检查时间与频率按贷后检查相关规定执行。检查后要填制贷后检查报告表，经有权人复核后及时归档备查；对检查过程中发现影响贷款安全的重大事项，应在检查表上填列并提出防范和化解信贷风险的措施，报送经办行负责人和信贷、风险管理部门。所有贷后检查应按规定流程，及时录入信贷管理系统。贷后检查的重点为：（一）检查借款人、担保人的资产和生产经营、财务状况是否正常，按月或按季结息的是否正常付息，分期还款是否正常。（二）了解掌握借款人、担保人的家庭和经营出现的重大事项，分析这些变动是否影响或将要影响借款人的生产经营和贷款安全。（三）检查抵（质）押物的完整性和安全性，抵押物的价值是否受到损失，抵押权是否受到侵害，质押物的保管是否符合规定。（四）其他需要检查的内容。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十二条', '短期贷款到期一周之前、中长期贷款到期一个月之前，客户经理应向借款人通过书面、短信、QQ、微信等途径发送到期履行债务通知，并取得有效回执，有效回执包含但不限于客户书面签字、图片截屏等。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十三条', '经办行应定期跟踪分析评估借款人履行借款合同约定内容的情况，并作为与借款人后续合作的信用评价基础。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十四条', '经办行应当按照法律法规规定和借款合同的约定，对借款人未按合同承诺提供真实、完整信息和未按合同约定用途使用、支付贷款等行为追究违约责任。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十五条', '个人贷款需要展期的，借款人应在贷款到期日5日前向经办行提出书面申请，经办行应对贷款展期原因进行审查，决定是否受理展期，并合理确定贷款展期期限，按规定程序报批，加强对展期的后续管理。一年以内（含）的个人贷款，展期期限累计不得超过原贷款期限；一年以上的个人贷款，展期期限累计与原贷款期限相加，不得超过该贷款品种规定的最长贷款期限。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十六条', '借款人出现违反合同约定情形的，经办行应及时采取有效措施，必要时应依法追究借款人的违约责任。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十七条', '形成不良贷款的，经办行应对其进行专门管理，并及时制定清收或盘活措施。对借款人确因暂时经营困难不能按期归还贷款本息的，经办行可与借款人协商进行贷款重组。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十章  贷后管理', '第五十八条', '对追偿后确实无法收回的个人不良贷款，经办行按照相关规定，申报核销。贷款核销后，应做到账销案存，并且继续向债务人追索，直至债务本息清偿完毕。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十一章  责任追究', '第五十九条', '贷款形成不良后，本行对照有关制度追究相应责任人的责任。若触犯刑律，依法移交司法机关，追究刑事责任。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十二章  附则', '第六十条', '确因本行债权保全或降低弱化风险等原因，需要办理续贷的，不受本办法第十八条条件限制。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十二章  附则', '第六十一条', '本细则未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十二章  附则', '第六十二条', '本细则由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《个人贷款管理实施细则》', '第十二章  附则', '第六十三条', '本细则自公布之日起执行。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行中小企业联保贷款管理，优化客户结构，支持中小企业的发展，提高信贷资产质量，根据《中华人民共和国担保法》等法律法规，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关信贷管理制度的规定，制定本办法。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称中小企业联保贷款是指本行辖区内中小企业依照本办法组成联保小组，由本行对联保小组成员发放，并由联保小组成员共同承担连带责任保证的贷款。本办法所称中小企业是指在本市注册，有固定营业场所并在市内从事生产经营活动的中小企业法人和非法人企业客户。以下企业不包括在本办法所称中小企业范围内：1.房地产开发及从事房地产建筑安装的企业；2.集团成员企业；3.学校、医院、出版社、电视台等非生产流通企事业单位；4.大型企业由于生产经营恶化，规模收缩成中小企业标准的。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第一章  总则', '第三条', '本行所辖的公司业务部办理中小企业联保业务，本办法所称借款人是指依照本办法规定参加联保小组的中小企业。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第一章  总则', '第四条', '中小企业联保贷款实行“自愿组合、共同申请、多户联保、周转使用、责任连带、到期还款”的原则。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '公司业务部负责中小企业联保贷款业务的受理及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '授信评审部负责对中小企业联保贷款业务的授信管理。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定中小企业联保贷款管理办法及流程，对中小企业联保贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对中小企业联保贷款用款进行审查。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '风险管理部负责对中小企业联保贷款的风险监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核中小企业联保贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '审计部负责对中小企业联保贷款进行审计稽核。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第三章  贷款条件、期限和利率', '第十二条', '参加联保的中小企业应当符合下列基本条件：1.具有工商行政管理机关核发的营业执照及其他有效证件，且在有效期内，有固定的经营场所；2.从事符合国家政策、经营活动正常、成长性较好、现金流及利润稳定增长的中小企业；3.在本行开立基本存款账户或一般存款账户，货款归行率在70%以上；4.按本行信用评级标准评定信用等级为A级（含）以上；5.无虚假注册或抽逃资本现象，资产负债率70%以下；6.无不良信用记录，具有按期偿还联保贷款本息的能力；7.企业成立2年以上；8.遵守联保协议，自愿缴纳联保风险保证金；9.符合中小企业贷款的其它有关规定。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第三章  贷款条件、期限和利率', '第十三条', '联保小组的条件：1.在同一服务区内有借款需求的3户以上中小企业自愿组成联保小组；2.受同一自然人（含配偶）或法人实际控制的多个中小企业，只能有一个参加联保小组，不得分别参加多个联保小组。3.联保小组成员之间无关联关系；4.建组时小组采取自愿形式推选确定组长；5.联保小组各成员自愿缴纳联保小组风险保证金，风险保证金交纳不低于授信金额的20%，并且总额须覆盖最大一户授信额；6.联保小组成员一年内不得退出并对小组成员所借贷款本息承担连带保证责任；7.联保小组成员中最大一户授信金额不得高于联保小组总授信额度的1/2。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第三章  贷款条件、期限和利率', '第十四条', '中小企业联保贷款期限由本行根据借款企业生产经营活动的周期确定，但不得超过联保协议的期限。小组解散时，小组成员贷款本息必须全部还清。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第三章  贷款条件、期限和利率', '第十五条', '中小企业联保贷款利率参照本行利率定价办法执行。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第十六条', '中小企业联保贷款操作流程包括联保小组要件审查和贷款发放两部分。联保小组审查流程：借款人申请、客户经理调查、审查审批。联保贷款发放操作流程为：客户申请、受理调查、授信审查审批、用信审查审批、设立联保小组、签订合同、缴纳风险保证金、发放与支付、贷后管理、贷款归还、档案管理。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第十七条', '联保小组要件审查（一）申请人提出申请。申请人向公司业务部书面提出联保贷款申请并提供下列资料：1.书面申请，内容包括经营情况、联保小组成员情况、拟申请联保贷款额度等；2．拟参加联保小组成员的营业执照、税务登记证、经营许可证、组织机构代码证书、公司《章程》，股本金或出资人明细表；3．法定代表人或负责人身份证及身份证件复印件；4．公司应出具董事会或股东会按章程规定同意参与联保的决议；5．前两年末及申请日前一个月的资产负债表、损益表；6．开户许可证、银行账号及存款余额情况；7．客户各类资质证明和近二年获得的各类荣誉证明；8．本行认为需要提交的其他资料。（二）客户经理调查。公司业务部受理申请人申请后，客户经理应对申请人提供的有关资料进行调查，除按照本行信贷管理制度规定调查情况外，还应重点调查以下内容：1.联保小组和小组成员是否符合联保小组设立条件和借款人准入条件；2.联保小组成员经营实力是否相当；3.借款用途是否用于自身经营；4.申请借款金额是否适当，贷款期限是否合理，还款来源是否稳定，是否有足够的现金流或经营收入；5.借款人信用情况，是否有不良记录，是否有欠缴税款等情况。（三）审查与审批。支行客户经理将调查结论意见，报支行贷款会办小组会办后，上报授信评审部审批。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第十八条', '客户申请。借款人向支行提出书面申请，并提供相关资料。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第十九条', '受理调查。对借款人提交的申请，支行应及时受理和审查，审查的主要内容包括：1.申请人是否符合中小企业标准；2.申请人的偿债能力；3.经营项目是否可行及其盈利能力等；4.中小企业的财务收支及现金流情况是否正常；5.申请人信用等级是否达到授信标准； 6.贷款金额是否超过办法规定；7.小组成员间的关联关系是否符合本办法的规定；8.判定调查人对申请人提出的贷款金额、期限和利率等意见的合理性和准确性；9.中小企业借据及担保决议书是否有效；10.各项申报材料是否齐全。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十一条', '设立联保小组与管理1.明确联保小组成员的权利和义务，以及联保小组设立、变更和终止情况；联保小组自联保协议签署之日设立。2.按授信额度设立风险保证金。风险保证金存入支行专户，归小组成员所有。小组成员全部还清贷款本息后，小组成员可自主决定处理风险保证金，联保协议终止的，风险保证金应退还交纳的小组各成员。3.联保成员退出小组时，须提前两个月向本行申请，并征得小组其他成员和支行同意，但退出成员对经支行同意前的其他成员的存量贷款须承担连带责任。4.当联保小组有成员不能归还贷款本息时，小组其他成员应同意由本行直接扣收风险保证金代偿，代偿不足的，由小组其他成员或任一成员承担连带代偿责任。代偿后，小组其他成员还应及时补足风险保证金。5.小组成员发生以下情况，本行可取消对其的授信、停止发放贷款和提前收回已发放的贷款：(1)发现有欺诈行为，未按要求向支行和其他成员提供真实的资产和经营情况；(2)未按约定用途使用贷款，特别是将贷款借给其他成员使用的；(3)经营项目出现重大风险并危及到贷款安全和小组联保实力的；(4)通过各种形式逃废债务或不主动履行联保协议或不协助本行维护债权的；(5)被依法追究刑事责任，使其无法履行职责；(6)发生其他危及自身和小组其他成员贷款安全事项的。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十二条', '签订合同联保协议签订后，联保小组成员共同与本行签定借款及担保合同。客户经理（两人以上）必须当场监督联保小组所有成员共同在合同文本上签字，确保签订的合同真实、有效。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十三条', '缴纳风险保证金。联保小组成员须在支行开立保证金专户，存入联保风险保证金，用于代偿借款人无法偿还的逾期贷款本息。风险保证金扣划后，保证金专户余额低于规定的保证金额度时，联保小组成员应立即补足。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十四条', '发放与支付借款人办妥上述手续后，支行按合同约定发放贷款，将资金划入借款人的结算账户，并严格监督借款人按合同约定支付方式支付贷款资金，不符合自主支付条件的应实行受托支付。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十五条', '贷后管理1.贷款发放后，联保小组组长及其他成员应负责协助本行管理贷款，及时了解借款人的生产经营情况和贷款使用情况，并告知支行。2．支行要以联保小组为单位设立贷款台账，及时记载借款人生产经营及贷款使用情况。3．贷款发放后，客户经理应按照本行信贷管理制度的要求做好贷后管理工作，了解和掌握借款人的生产经营情况和贷款使用情况，加强对贷款的跟踪检查和监督管理。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十六条', '借款人必须按规定用途使用贷款。联保小组任何成员不得以任何方式，将贷款转让、转借给他人或集中使用联保小组其他成员的贷款。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十七条', '联保小组每季召开一次例会，支行应派人列席，成员之间相互通报经营和贷款使用情况。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第四章  贷款操作程序', '第二十八条', '本行建立联保企业信用档案，对联保小组实行动态管理，及时处置发现的问题。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第五章  责任追究', '第二十九条', '业务经办人员须严格按照本办法规定的贷款条件，对联保小组成员进行认真的调查与审查，严格按贷款流程发放贷款，同一组联保成员的贷款须由同一名客户经理负责发放和管理，该客户经理即为该小组所有成员贷款的责任人。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第五章  责任追究', '第三十条', '联保贷款产生风险的，按照本行信贷管理有关规定认定和追究相关人员的责任。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第六章  附则', '第三十一条', '中小企业联保贷款办法未明确的事项，适用本行《流动资金贷款管理办法》等相关管理规定。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第六章  附则', '第三十二条', '本办法及流程由信贷管理部负责制定、修改和解释。'),
(NULL, '信贷管理部', '《中小企业联保贷款管理办法及流程》', '第六章  附则', '第三十三条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第一章  总则', '第一条', '为切实加强贷款的管理，确保信贷资产安全，达到防范信贷风险的目的，根据《中华人民共和国物权法》、《中华人民共和国担保法》、《流动资金贷款管理暂行办法》，以及本行《信贷管理制度》、《贷款业务尽职管理办法》的有关规定，特制定本办法。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第一章  总则', '第二条', '本办法中仓单质押贷款是指借款人以仓储公司签发的借款人或第三人自有的存货仓单作为质押物，向本行申请办理贷款的一种信贷业务。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第一章  总则', '第三条', '本办法中的仓单是指仓储公司签发给存货人或货物所有权人的记载仓储货物所有权的唯一合法的物权凭证。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第一章  总则', '第四条', '仓单质押贷款在“第一还款来源充分和贷款用途合法”的前提下，坚持“以货定贷、货物质押、到期归还、逾期扣收”的原则。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第一章  总则', '第五条', '仓单质押贷款坚持集中统一办理，由公司业务部集中受理、调查、发放和管理，按《转授权管理办法》进行报批。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '公司业务部负责仓单质押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '授信评审部负责对仓单质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定仓单质押贷款管理办法，对仓单质押贷款业务的用信管理和贷后检查的监督。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '风险管理部负责对仓单质押贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核仓单质押贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '审计部负责对仓单质押贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第三章  贷款对象及条件', '第十二条', '借款人应为本地客户，具有一定的自有资金，是独立核算的企业法人、其他经济组织或个体工商户，持有有效的营业执照和组织机构代码证（或新版营业执照），特殊行业还必须具备特种行业经营资质或资格。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第三章  贷款对象及条件', '第十三条', '借款人自愿承担仓储保管费用、保险费用和质检费用，对仓储物资进行保险，且在保险合同中约定本行为第一受益人，自愿承担保管过程中发生的非人为因素损耗造成的损失。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第三章  贷款对象及条件', '第十四条', '出质人对仓储物必须拥有无争议的所有权或处分权，须提供增值税发票原件或其他证明文件；且出质人具有经营质押仓单项下仓储物的资格或资质，非经营范围的仓储物不得用于出质。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第三章  贷款对象及条件', '第十五条', '下列公司或个人不得作为借款人或者出质人（除原客户逐步压降外）：1．仓储公司；2．仓储公司的母公司或实际控制人；3．仓储公司或其母公司的关联方；4．异地客户。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第三章  贷款对象及条件', '第十六条', '质押仓单项下的货物，必须是所有权明确且无法律纠纷的产品、半成品、原材料等动产，范围包括棉花、蚕茧、铜、铝材、钢材、锌锭等，但不得为原材料、半成品、产成品的混合物。仓储物必须同时具备下列条件：1．必须易计量、易保管、易运输，不易耗损和变质的物资，在本地区用量大、有销路，易变现；2．可通过公开的实体市场或网络市场进行实时客观询价，且价格较稳定，波动较小；3．规格、等级明确，仓储物资符合国家有关标准或通过相关部门的质量检验。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第三章  贷款对象及条件', '第十七条', '有下列情形之一，在规定期限内仍未改正的，不得发放贷款：1．转移贷款用途；2．未按规定经营范围开展经营；3．没有规定比例的自筹资金；4．盲目超价收购或质次价高的；5．存在欺诈行为的；6．违反国家宏观政策，从事其他非法经营活动的。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第四章  仓储公司', '第十八条', '与仓储公司开展合作须经本行同意，签订合作协议书，约定存储物资的种类等内容，明确双方权利义务，并由仓储公司对保管责任提供充足的连带担保。仓储公司应满足以下条件：1．具有从事仓储业务的资格，从事专业仓储业务两年以上，行业信誉好；2．会计核算健全，具有承担违约责任赔偿的能力，净资产不低于1000万元；3．有专门仓库，仓储设施齐全（含电子化的仓储管理系统）；4．具有健全的货物监管、进出库制度、规范的业务操作规程；5、本行规定的其他条件。6、仓储公司应符合本行公司客户授信条件。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第五章  贷款利率、期限、额度和方式', '第十九条', '仓单质押贷款利率按利率定价办法执行。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第五章  贷款利率、期限、额度和方式', '第二十条', '仓单质押贷款期限应与借款人实际经营活动和销售周期相匹配，一般不超过6个月，最长不得超过9个月,并不得超过仓储物储存期限，仓储物有保质期的贷款到期日必须先于保质期期满前6个月。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第五章  贷款利率、期限、额度和方式', '第二十一条', '结合质押物的性质和借款期限，审慎确定贷款仓储物的质押率，取两者较低值确定质押率，且单户仓单质押贷款余额不超过3000万元。棉花、蚕茧、铜、铝材、钢材、锌锭等工业通用的原材料，贷款额度不得超过仓单项下仓储物资现时价值的70%；非上述通用的原材料以及半成品、产成品贷款额度不得超过仓单项下仓储物资产品购入价或成本价的60%至70%。贷款期限在6个月以内的贷款额度不得超过仓单项下仓储物资现时价值的70%，贷款期限超过6个月的贷款额度不得超过仓单项下仓储物资现时价值的60%。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第五章  贷款利率、期限、额度和方式', '第二十二条', '仓单质押贷款借款人到期不能归还的，应按合同约定对仓储物平仓处理，符合条件的可申请展期。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第五章  贷款利率、期限、额度和方式', '第二十三条', '贷款方式采用借款人或第三人将自有货物移交至贷款人指定合作的仓储公司，凭仓单办理质押手续。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第二十四条', '申请贷款。对符合条件的对象，公司业务部客户经理负责受理借款人的准入申请，并要求借款人提供下列材料，同时对所提供资料的真实性和合法性负完全责任：1.《仓单质押贷款申请书》；2.借款人近三年度及申请日上个月度的资产负债表、损益表；3生产经营、收购、加工或销售资格证明文件(营业执照或收购、加工许可证)；4.购销合同及供货单位开出的增值税发票原件，不开增值税专用发票的商品交易，申请人可提交其他能够证明质押物的所有权、质量、数量和价格的证明；5.《公司董事会（股东大会）同意借款决议》、《公司董事会（股东大会）同意质押意见书》和《公司股东保证承诺书》；6.出质人与保管人签订的《仓储合同》(附件1)，保管人资质证明文件（营业执照经营许可证）；7.需要提供的其他资料。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第二十五条', '贷款调查。客户经理接收申请后，应及时对申请人的有关情况进行调查，并在调查的基础上提出拟贷金额、利率和期限等，调查的主要内容有：l.核实借款人提供的资料是否齐全、真实、有效；借款人法定代表人的品行、敬业精神、行业地位、诚信情况；2.核实借款人资产、负债和经营效益的真实性，第一还款来源是否充足；3.核实出质人提供的仓储物资的合法性、真实性，保管人的保管设施和管理能力以及其保证人的担保能力；4.自筹资金到位情况和购销合同的有效性；5.仓储货物的需求及价格走势。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第二十六条', '贷款审查和审批。授信评审部对公司业务部上报的授信资料进行审查，主要包括：1.审查资料是否齐全；2.是否符合贷款条件，贷款手续是否完备；3.测算借款人的还款能力及来源，审查质押率是否合理，确定贷款期限和金额；贷款审批。对符合条件的仓单质押贷款授信审批按本行《转授权管理办法》规定执行。信贷管理部对公司业务部上报的已获批授信的必要性、准确性以及合规性进行审查，以及对用信资料完整性、真实性、有效性进行审查核实，提出审查意见，按本行转授权办法进行审批。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第二十七条', '同意准入。贷款审批同意后将仓单质押贷款协议书提供给申请人审阅，在无异议的前提下，由借款人（出质人）、贷款人（质权人）、保管人、保证人签订“四方”《协议书》（附件2）。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第二十八条', '提供仓储货物。借款申请人按照本行规定向指定仓储公司存入货物，本行委派人员根据《货物代保管入库通知单》（附件3）注明的入库时间、品种、规格型号、数量（件数、磅码单），对拟提供的质押物进行全程监督，货物入库后由出质人与仓储公司签订《仓储合同》。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第二十九条', '仓储物资质量检测。仓单物资必须经有权部门的检测，检测报告必须涵盖全部抽样检测物，且报告结果必须明确。由出质人、仓储公司、驻点人员三方共同抽检，同时客户经理有权参与，抽样既要保证样品的合理性，又要保证样品的真实性，一同送有权检验部门检验，一同拿取检验报告，检验费用由借款人承担。除要求出具检验报告外，现场入库时还要进行必要和充分的检测（如金属材料、产品均需深度打磨或剪切后由出质人、仓储公司、驻点人员三方共同在场用光谱仪再次大批量检验；其他产品如棉花破包率不少于3%），确保表里如一，质量一致。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第三十条', '质押物保险。仓单项下所有仓储物资均必须参加财产综合保险，贵重物资、特殊物资必须增加盗抢险等必要的保险品种，保险期限必须不小于贷款期限，且保险单上的第一受益人必须是本行，保险单正本由本行保管。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第三十一条', '签发仓单。核实检验报告与实物质量相符后，由仓储公司签发《代保管物资仓储单》（附件4），同时出质人、驻点人员在仓储单上应签字确认。借款人取得仓单后，向公司业务部提出质押贷款发放申请。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第三十二条', '客户经理核实《代保管物资仓储单》后，确定实际入库的物资质量、数量、价格不低（少）于申请贷款约定的，要求出质人对《代保管物资仓储单》出质背书。客户经理必须通知借款人、出质人、仓储公司办理贷款借款手续，由借款人（出质人）、贷款人（质权人）共同签订《仓单质押担保借款合同》（附件5）。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第三十三条', '贷款发放和使用。由风险员审查仓单质押贷款后提交公司业务部有权审批人员审批发放，对所借贷款按“三个办法一个指引”要求进行面谈面签、用途核实、立据发放及支付管理。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第六章  贷款程序', '第三十四条', '贷款归还。借款人归还贷款后，驻点人员凭公司业务部客户经理、有权审批人员签署同意出库的《仓单质押商品出库通知单》（附件6）及还款凭证原件（客户经理联）办理，按出库清单进行出库，结清仓单质押贷款出仓的还应提供《代保管物资仓储单》。出库清单根据贷款归还比例确定，驻点人员、客户经理、公司业务部有权审批人员要严格把关。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第七章  仓储监管', '第三十五条', '质押仓储物的仓库应单独设置和管理，实行双人管库，仓储公司保管为主，本行委派人员应保管其中一把钥匙或通过密码管理实现有效的牵制或控制。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第七章  仓储监管', '第三十六条', '加强仓储管理的监督。白天由本行委派人员值守，晚间由仓储公司监管，但仓储公司不得出、入库。本行监控中心实行24小时不间断监控，其中每天18时至次日8时监控检查不少于两次，并做好检查书面记录。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第七章  仓储监管', '第三十七条', '贷后检查工作。建立多层的贷后检查体系，客户经理对质押仓储物资的价格跟踪每周至少一次，做好记录。客户经理对仓储物全面检查每个月不得少于一次；公司业务部有权人员每月检查、抽检不得少于一次；信贷管理部按季对客户经理、公司业务部有权人检查情况进行督查，实行检查面不少于50%；分管前、中台行长实际检查面每半年不少于50%，行长实际检查每年不少于一次。检查要做好记录，发现问题及时采取措施并报告本行。因监管不力、检查不及时，驻点人员、客户经理以及各级管理部门的相关人员承担相应责任。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第八章  附则', '第三十八条', '本办法及流程由信贷管理部制定、解释和修改。'),
(NULL, '信贷管理部', '《仓单质押贷款管理办法及流程》', '第八章  附则', '第三十九条', '本办法及流程自公布之日起施行。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第一章  总则', '第一条', '为规范本行贷款业务操作，防范信贷风险，提高信贷资产质量和信贷资金使用效益，为客户提供高效便捷的金融服务，根据《中华人民共和国商业银行法》、《中华人民共和国担保法》、《贷款通则》、《商业银行授信工作尽职指引》等国家有关法律法规和本行《信贷管理制度》，制定本办法。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第一章  总则', '第二条', '本办法所称信贷业务是指本行所辖支行、营业部、小微业务部、公司业务部、金融市场部、资产保全部（以下统简称“经办”）为客户提供的各类贷款业务。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第一章  总则', '第三条', '本办法是本行办理信贷业务必须遵循的基本规则，是规范单项信贷业务运作程序的基本依据。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第二章  基本操作流程', '第四条', '办理信贷业务的基本流程：客户申请授信→授信受理→授信审查、审批→客户申请用信→用信审查、审批→合同文件的签订→贷款发放与支付审核→柜面支付→贷款业务发生后的管理→贷款收回。客户采用手机银行等电子渠道自助放款的，在合同签订后须签订《手机银行自助用信服务协议》，自助放款时不需要进行支付审核，其它环节同上述流程。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第二章  基本操作流程', '第五条', '授信阶段的流程授信评审部权限：客户申请→经办行受理、面谈、调查→授信评审部初审→有权人审批→下发批复。信贷审查委员会（以下简称“贷审会”）权限：客户申请→经办行受理、面谈、调查→授信评审部初审→提交贷审会审议→报总行行长备案→下发批复。一般关联交易：客户申请→经办行受理、面谈、调查→授信评审部初审→提交贷审会审议→报总行行长备案→交董事会风险管理与关联交易控制委员会事后备案。重大关联交易：客户申请→经办行受理、面谈、调查→授信评审部初审→提交贷审会审议→报总行行长备案→交董事会风险管理与关联交易控制委员会审查→董事会审议→下发批复→报备。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第二章  基本操作流程', '第六条', '用信阶段的流程采取手机银行等电子渠道自助放款的，按协议执行；采取柜面放款方式的，按下列流程运作。（一）用信流程经办行权限：客户用信申请→经办行提出用信意见→有权人审查审批→合同文件面签。信贷管理部权限：客户用信申请→经办行提出用信意见→经办行有权人审查→信贷管理部审查审批→签署审批意见或下发批复→合同文件面签。（二）放款流程权限内：客户申请→经办行有权人审查→柜员发放→资料集中保管。权限外：客户申请→经办行有权人初审→运营管理部审查、审批→柜员发放→资料集中保管。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第一条', '贷款授信的申请'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第七条', '客户申请。客户通过本行营业网点、服务热线电话、网上银行、手机银行等渠道提出授信申请，经办行受理后，应及时指定客户经理与客户取得联系，并告知客户需要提供的相关申请资料，其内容主要包括申请原因、授信金额、授信期限、授信用途、担保方式、有关承诺等。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第八条', '法人客户申请信贷业务授信用于经营的，须按具体用途提供以下资料（或其中对应资料）的原件及复印件（复印件需加盖公章,下同）：（一）营业执照、组织机构代码证和税务登记证； （二）公司章程；（三）资信等级证书；（四）开户许可证；（五）按章程规定由公司董事会或股东会（按章程规定）同意借款的决议书；（六）法定代表人、主要股东身份证明及其个人和配偶名下的固定资产明细、权证；（七）经办人身份证及法定代表人的授权委托书；（八）前两年年报和近3个月的财务报表、纳税申报表；（九）验资报告、企业资质证明、公司成立批文、生产经营许可证、进出口批文、进出口许可证、生产经营计划、购销合同（加工、承建、承包、委托等合同、协议）等；（十）抵押物权属证明、购置发票等；（十一）新客户需提供印鉴卡；（十二）属于房地产企业的，须提供对应项目的《建设用地许可证》、《国有土地使用权证》、《建设工程规划许可证》、《建设工程施工许可证》等“四证”原件及复印件；商品房已经预（销）售的，还应提供《商品房预售许可证》或《商品房销售许可证》；如属特种行业，须持有特种行业经营许可证；（十三）中、长期项目贷款还应提供以下资料：1.有权部门批准的项目建议书、项目可行性研究报告、环境影响报告书；2.实施项目必须有相关部门的有效批文；3.项目开工前期准备工作完成情况；4.在开户行存入规定比例资本金的证明；5.项目投资来源及资本金到位情况；6.根据信贷业务品种、信用方式需提供的其他资料。（十四）其他资料（如消防合格证、安全合格证、环境评价报告等）。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第九条', '自然人客户申请贷款授信用于经营的，需提供下列资料原件及复印件：（一）本人及配偶的身份证件、结婚证；单身借款人应提供单身承诺，并由所在街道、居委会（村委会）或所在单位出具证明;（二）借款人及其家庭资产权属证明、所在单位出具的工作关系证明或收入佐证证明（包括但不限于公积金、社保、工资清单等其中一项或多项）；（三）借款人持有的本行借记卡（含易贷通卡）及复印件；（四）用于企业经营的，除按上述要求外还需按第八条规定提供相对应资料。（五）其他证明资料。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十条', '自然人客户申请贷款授信用于住房或消费的，除按第九条明确的1-3项资料外，还需按具体用途提供以下资料（或其中对应资）的原件及复印件：（一）购置合同（房、车等）、首付款发票；（二）建设许可证、承建协议；（三）装修、装饰、装潢协议；（四）自筹资金来源证明资料；（五）近3个月个人或家庭账户流水；（六）其他证明资料。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十一条', '担保人是企（事）业法人的，需提供如下资料（或其中对应资料）的原件及复印件：（一）营业执照（事业单位登记证）、组织机构代码证和税务登记证；或者新版营业执照；（二）按章程规定由公司董事会或股东会同意担保的决议书；（三）资信等级证书；（四）开户许可证；（五）公司章程；（六）法定代表人、主要股东身份证明；（七）经办人身份证及法定代表人的授权委托书；（八）前两年度年报和近3个月的的财务报表、纳税申报表；（九）本行需要的其他足以证明代偿能力的资料。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十二条', '担保人是自然人的，需提供下列资料的原件及复印件：（一）身份证复印件；（二）收入佐证证明（包括但不限于公积金、社保、工资清单等其中一项或多项）；（三）本行需要的其他足以证明代偿能力的资料。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二条', '贷款授信的受理与调查'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十三条', '受理经办行负责受理服务区域内客户的贷款授信申请。受理申请时经办行应履行下列义务：（一）公布所经营的贷款种类、期限和利率，并向客户提供咨询；（二）公开授信的条件和授信审查的内容；（三）认真审议客户的申请，在规定的时间内给予明确答复；（四）应当对客户的债务、财产等相关情况保密。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十四条', '调查授信调查必须坚持面谈制度和“双人四眼”原则。经办行负责人可根据内部分工，及时安排客户经理对客户基本情况及项目可行性进行面谈和调查，认定客户是否具备发放贷款的基本条件,资料是否完整、齐全。调查人对客户申请事项进行现场核实后出具书面调查报告。授信评审部根据风险控制需要可派员复查并提出评审意见。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十五条', '调查的主要内容包括但不限于以下几个方面：（一）对客户提供的资料完整性、真实性、有效性等进行调查核实，对提供的复印件与原件应核对相符。1.查验客户提供的相关证明是否真实、有效，企（事）业法人营业执照或居民身份证是否真实、有效，法人营业执照是否有效；查询法人营业执照是否被吊销、注销、申明作废，内容是否发生变更等；2.查验客户法定代表人和授权委托人的签章是否真实、有效；3.查验客户填制的信贷业务申请书的内容是否齐全、完整，客户的住所地址和联系电话是否详细真实等。（二）调查客户信用及有关人员品行状况。1.查询人民银行信贷征信系统及本行企业信息平台借款人综合信息系统，了解客户目前贷款、其他负债和提供担保情况，是否有不良信用记录，对外提供的担保是否超出客户的承受能力，是否涉及诉讼等。2.调查了解客户核心团队负责人的品行、经营管理能力和业绩，是否有个人不良记录等。（三）对企（事）业法人、其他经济组织及担保人的资产状况、生产经营状况和市场情况进行调查，分析信贷需求和还款方案。1.深入了解客户及其担保人单位，查阅其资产负债表、损益表、现金流量表等账表，对客户的资产、负债、所有者权益、收入、成本、利润等情况进行分析，并进行“账账、账表、账实”等核对；2.调查客户及其担保人生产经营是否合法、正常，是否超出规定的经营范围，核实企业“三表一税”情况，重点调查分析生产经营主要产品的技术含量、市场占有率及市场趋势等情况；3.调查分析贷款需求的原因；4.调查分析贷款用途的合法性。客户申请授信不得用于财政性收支、镇、村、组公益性支出；不得用于归还贷款利息、租金或其他债务性支出；不得用于股票、有价证券、期货、期指、权证等投资以及其他不符合本行信贷制度规定的用途。5.调查关联企业的情况，分析关联交易的真实性和合法性；6.查验商品交易的真实性，分析商品交易的必要性；7.调查抵（质）押物的变现能力、稳定性及足值性；8.调查分析还款来源和还款时间；9.调查分析贷款的需求量；10.核实借款单位法定代表人、主要股东个人及其配偶名下的固定资产明细、权证及相应抵押情况；11.核实借款单位或法定代表人、主要股东个人及其配偶持有本行股金情况等。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十六条', '对自然人贷款业务，客户经理应调查分析客户及其家庭的经济收入是否真实，各项收入来源是否稳定，是否具有持续偿还贷款本息的能力；提供个人担保的，要对担保人的经济收入是否真实，各项收入来源是否稳定等情况进行调查；提供抵（质）押担保的，要对抵（质）押物价值的稳定性、变现能力进行调查。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十七条', '客户经理依据有关规定，对客户提供的担保资料进行分析，确定客户提供的担保是否符合条件，并核实其担保能力。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十八条', '调查分析结束后，客户经理明确调查结论意见，并按本行制度规定出具调查报告。客户年度基本授信时，必须详细撰写调查报告。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第十九条', '调查报告的主要内容包括但不限于：（一）法人客户授信调查报告的内容：1.客户基本情况及主体资格；2.财务状况、经营效益及市场分析；3.担保情况和贷款风险评价；4.本次贷款授信的综合效益分析；5.本次贷款授信的具体用途及合法、合规性分析；6.已用信贷款按约定使用情况分析；7.还款资金来源的落实情况；8.对贷款资金需求及额度大小的分析、测算。9.信贷业务的综合效益分析；10.结论。调查人对是否同意办理本次贷款授信以及对授信金额、用途、期限、利率、还款方式、担保方式和限制性条款等提出明确意见。（二）自然人客户授信调查报告的内容：1.授信申请人的基本情况；2.授信申请贷款的用途和已用信贷款按约定使用情况分析；3.担保情况分析；4.收入来源分析；5.还款来源分析；6.自然人申请授信如为本公司使用的，应详细分析该公司的经营状况、财务状况等；7.结论。调查人对是否同意办理此项贷款授信，及对本次授信金额、用途、期限、利率、还款方式、担保方式和限制性条款等提出明确意见。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十条', '客户经理应根据调查情况对客户信用等级进行评定，同时，签注调查意见并落实好责任人，将上述全部贷款调查资料移交审查岗审查。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第三条', '贷款授信的审查'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十一条', '贷款授信的审查实行分级审查。经办行有权人负责对客户的基础资料和客户经理的调查资料（以下称贷款资料）进行审查，经贷款会办小组会办形成授信意见后报授信评审部复审。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十二条', '信贷业务授信审查的要点和主要内容包括：（一）基本要素审查1.客户及担保人有关资料是否齐备；2.贷款授信的流程资料及手续是否齐全。（二）主体资格审查1.客户及担保人主体资格、法定代表人有关证明材料是否符合规定；2.客户及担保人产权关系是否明晰；3.客户及担保人的法定代表人、主要部门负责人有无不良记录。（三）信贷政策审查1.贷款用途是否符合国家有关政策及本行规定；2.贷款期限、方式、利率等是否符合本行有关规定。（四）贷款风险审查1.是否分析、揭示客户的财务风险、经营管理风险、市场风险等风险要素；2.是否提出风险防范措施。（五）提出审查结论和有关限制性条款。（六）其他需要审查的要点。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十三条', '审查结束后，审查人员应在审批表中签注审查意见。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十四条', '审查人员对调查人员提交的信贷业务授信资料不全、调查内容不完整、不清晰的授信业务，应要求补充完善。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第四条', '授信的审议与审批'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十五条', '授信评审部对经办行上报的授信意见复查，初步确定授信金额。权限内的，由授信评审部有权审批人审批；权限外的，由授信评审部提交贷审会审议，按审议结果报总行行长备案。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十六条', '一般关联交易经贷审会审议后，向董事会风险管理与关联交易控制委员会实施事后备案。重大关联交易经贷审会审议，报董事会风险管理与关联交易控制委员会审查后，交董事会审议。重大关联交易应当在批准之日起十个工作日内报告监事会，同时报告中国银行业监督管理委员会。与本银行董事、总行高级管理人员有关联关系的关联交易应当在批准之日起十个工作日内报告监事会。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十七条', '本行授信评审部认为经办行调查资料不完善的，退回补充资料，或指定专人进行现场复查。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十八条', '贷款授信审议的主要内容包括（但不限于）：（一）贷款业务是否合法合规，是否符合国家产业政策、信贷政策；（二）贷款业务的风险和防范措施；（三）其他需要审议的内容。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第二十九条', '经办行贷款会办小组、授信评审部会办小组、贷审会须明确专人对信贷业务授信审议过程进行记录，并确定审议结论，报有权审批人审批或按转授权权限审批。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第三章  授信管理', '第三十条', '需向监事会、省联社、监管部门报备的贷款授信，由授信评审部按相关规定的要求准备资料，统一上报。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第一条', '贷款用信的申请'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十一条', '本行信贷业务审批实行“先授信、后用信”的原则。已授信客户的用信，由客户提交用信申请，超出经办行用信审批权限的申请，经审查后向信贷管理部申报。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第二条', '贷款用信的受理与调查'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十二条', '已授信客户的贷款用信申请由客户经理负责受理。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十三条', '客户已授信未用信且期限超过6个月的用信申请，客户经理受理后应进行必要的调查、核实，内容主要包括：1.客户的基本情况、经营现状、风险分析及防范措施；2.客户本次申请贷款用信的用途及还款来源；3.客户授信条件的变化情况；4.对于是否同意用信提出明确结论，如同意用信，按用信程序办理；如不同意用信，应及时记载否决的具体原因，经会办小组会办后，及时答复客户。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三条', '贷款用信的审查'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十四条', '贷款用信的审查按权限实行分级审查。经办行权限内的用信申请，由经办行有权人审查；超出经办行审批权限的用信申请，经办行有权人审查并经用信小组会办同意后报送信贷管理部审核。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十五条', '用信审查的主要内容包括：（一）对客户提供的用信资料完整性、有效性进行审查核实，并分析其是否符合法律法规和本行的相关信贷政策；（二）查验授信文件的有效性，客户用信条件是否满足授信条件；（三）查验授信时是否提出限制性条款；如有，查验是否落实到位。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十六条', '用信申请时应提供下列资料：（一）授信审批文件及授信限制性条款落实情况的证明材料；（二）用信需求的证明文件(包括购销合同、协议等)；（三）所有公司贷款、50 万元以上的大额自然人贷款或借新还旧、展期等贷款用信时，还须提供《用信审批表》；（四）以房地产抵押的，每次用信前应查询抵押物登记状态，看有无被法院查封、冻结等；（五）其他需要审核的材料。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十七条', '信贷管理部审查人员对经办行提交的资料不全、调查内容不完整、不清晰的贷款用信申请，必须要求补充完整。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十八条', '用信审查人员审查完毕后，按权限报有权审批人审批。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第四条', '贷款用信的审批'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第四章  用信管理', '第三十九条', '贷款用信的审批按权限实行分级审批。经办行权限内的用信申请，由经办行有权人审批，权限外的用信申请，经用信小组会办后提交信贷管理部审查，信贷管理部及时签署用信意见或下发用信批复，对使用特殊文本的用信需先提交合规管理部进行审查。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十条', '客户经理是对外签订借款合同的经办人。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十一条', '办理所有信贷业务，客户经理须当面与借款人签订书面合同，内容包括贷款种类、币种、用途、金额、期限、利率、支付方式、还款方式、担保方式、合同双方的权利、义务、承诺等。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十二条', '合同必须按规定使用统一制式合同文本。经办行可在不违背制式合同文本基本要求的基础上，为增加保全措施或减少风险，合同双方可以在协商一致的前提下签订补充协议，补充协议内容须事先经信贷管理部、合规管理部同意，必要时须由本行法律顾问审核把关。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十三条', '经办行发放担保贷款时必须与客户签订担保合同，担保合同要件必须与主合同相互衔接。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十四条', '借款合同和担保合同的填写和签章应符合下列要求：（一）合同必须使用由本行提供的黑色或蓝黑墨水钢笔、黑色水笔书写或电脑打印，内容填制必须完整，正副文本的内容必须一致，不得涂改；合同签章时必须使用由本行提供的印泥。严禁使用客户自备的钢笔、水笔、印泥等签署合同、协议类法律文书；（二）借款合同的贷款业务种类、金额、用途、期限、利率、还款方式和担保合同中的相应内容，应与审批的内容一致；（三）担保主债权是否全部覆盖预计贷款本息以及实现债权的相关费用；（四）客户经理按相关贷款品种支付管理要求，在合同中明确贷款资金使用是采用受托支付的方式或由借款人自主支付；（五）客户经理必须在视频监控录像状态下，监督借款人、担保人（保证人、抵押人、质押人）或授权代理人在合同文本上签字、盖章、核对预留印鉴，确保合同签订的真实、有效。需要加按指模的，应要求借款人、担保人加按指模。如因特殊情况无法在视频监控下操作时，经办行应指定两名以上（含）工作人员与客户签约并拍照或摄像，影像资料应及时打印并签字确认，作为重要资料归档保管。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十五条', '客户经理应区别不同担保方式，与借款人、抵押人或质押人共同办理以下事宜：（一）以抵押、质押担保的要到相关的房地产、车辆、海关、市场监督管理等有权登记的职能部门办理抵押、质押登记，并将他项权证按规定转交本行档案中心保管。（二）以存单、国债、债券、保单等质押的权利凭证应办理止付等相关手续；（三）质押担保的质物交接应填制《权利质押清单》，并办理质物交接手续。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十六条', '合同审核包括但不限于以下内容：（一）合同文本的使用是否恰当；（二）合同是否按编号规则统一编号，是否按顺序依次登记《信贷档案登记簿》；（三）合同填制的内容是否规范、符合要求；（四）合同的补充条款是否合法合规，是否符合制式合同文本的基本条款；（五）客户、保证人（抵押人、质押人）的法定代表人或授权委托人是否在合同文本等签字栏签字盖章，提示语录是否为当事人亲笔抄录；（六）中长期贷款还款是否明确每年两次以上（含）；（七）合同签订过程是否合规。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第五章  贷款合同的签订', '第四十七条', '所有借款合同须经审查人复核，无误后加盖贷款合同专用章并及时记载《贷款合同专用章使用登记簿》。未经复核或未加盖“贷款合同专用章”的合同不得发放贷款。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第六章  贷款的发放、支付审核与柜面支付', '第四十八条', '客户经理依据贷款用信审批意见，在信贷管理系统中进行贷款发放操作，进行相关信息、数据维护。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第六章  贷款的发放、支付审核与柜面支付', '第四十九条', '经办行权限内的，须经有权人审查、审批；权限外的，须按转授权办法执行。根据审批意见，打印《放款通知单》（易贷通除外），交客户至柜面办理放款。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第六章  贷款的发放、支付审核与柜面支付', '第五十条', '柜面接到客户提交的《放款通知单》（易贷通除外），经办行权限内的，由有权人进行审查、发放；权限外的，经运营管理部审查、审批后发放。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第六章  贷款的发放、支付审核与柜面支付', '第五十一条', '经办行应根据《担保法》、《物权法》规定的合同生效时间办理贷款发放手续。（一）保证担保的贷款业务自签订借款合同和担保合同后发放，按借款合同约定用款计划的日期使用贷款；（二）抵押担保的贷款业务自设立抵押权登记后发放，按借款合同约定用款计划的日期使用贷款；（三）质押担保的贷款业务自签订借款合同和质押合同并办妥质物移交后发放（质权需登记的自办理登记后发放），按借款合同约定用款计划的日期使用贷款。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第六章  贷款的发放、支付审核与柜面支付', '第五十二条', '客户经理依据借款合同约定的用款计划或约定，一次或分次发放，客户应在借款借据上签字并盖章。法人客户借款人在借款借据上的签章应与其预留印鉴一致，并加盖行政公章。借款借据的基本要求：（一）借款借据中的借款人名称、金额、用途、还款日期、利率等内容不得与借款合同内容冲突，借据必须打印借款合同合同号，一份借据只能对应一份借款合同，借款日期不得在借款合同生效日期之前，对流动资金循环贷款和最高额周转使用贷款的借据到期日不得超过合同到期时间后6个月；特殊情况须经总行批准；（二）借款借据的大、小写金额必须一致，分笔发放的，借款借据的合计金额不得超过相应借款合同的金额；（三）借款人在借款借据上签章（或签名加指模）。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第六章  贷款的发放、支付审核与柜面支付', '第五十三条', '会计审查岗应审查贷款业务是否经有权审批人审批同意，借款借据要素是否齐全，填制内容是否符合要求，是否借款人（或授权委托人）本人办理借款业务等。审查无误后，办理信贷业务账务核算手续进行贷款业务柜面支付，将资金划入借款人本人的结算账户，并严格监督借款人按合同约定支付方式支付贷款资金。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第六章  贷款的发放、支付审核与柜面支付', '第五十四条', '柜面人员应在贷款发放前对照本行授权权限、授信客户的授信额度等文件进行监督、审查。会计审查岗对因审查不严、监督不力造成的后果承担相应责任。贷款相关资料有下列任一项情形的，会计审查岗不得受理和进行柜面支付：（一）没有调查人、审查人、审批人同时签注意见并签名的（手机自助放款除外），超权限贷款没有本行用信批复的；（二）非借款人本人办理或代理人不能提供有效授权委托书的；（三）借款资料内容要素不一致、不全的；（四）资料要素涂改未经有效确认的；（五）抵押贷款没有取得他项权利证书的；（六）质押贷款没有对存款进行止付处理并取得止付处理回执的；（七）未提供客户提款申请书和审查意见书的；（八）其他不符合贷款条件的情形。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第五十五条', '客户经理是贷款业务发生后管理的责任人，负责贷款业务发生后的日常管理。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第五十六条', '客户经理应对贷款从申请至收回全过程形成的资料、文件、合同、协议等按照本行信贷档案管理制度的要求，进行信贷档案资料收集、建档和管理，并按规定做好所有资料的电子扫描备份，实行“一户一档，分类保管”，以保证信贷资料的完整、安全和有效利用。（一）.以客户为单位，按一户一档要求建立信贷档案，其中，所有公司信贷客户、50万元以上大额自然人实行一户一个档案盒，50万元（含）及以下的自然人实行一户一个文件夹；（二）信贷档案中合同、协议、授（用）信资料及批复等重要资料原件应定期上交档案保管人集中保管，条件具备时由本行集中保管，客户经理以上述重要资料的复印件建档，日常查阅、使用以电子扫描件备查；（三）实行档案资料查询、调阅登记制度；（四）实行信贷档案和贷款质量交接制度。客户经理和档案保管人工作变动时应按规定办理档案资料的交接手续，并由本行指定专人进行监交，明确档案资料管理责任，确保档案资料管理的延续性，信贷资料的交接应在七天内完成。同时，对贷款质量给予接受人3个月的确认期，超过确认期或在确认期内未提出异议的，贷款的清收、管理责任均由接受人承担。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第五十七条', '建立多层级、常态化的贷后检查体系。应根据客户类别、贷款金额、品种明确的检查频率、检查方式等进行贷后检查。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第五十八条', '贷款业务发生后，贷款发放责任人应在15日内进行首次跟踪检查（贷款存续期不足15天的除外），检查内容主要包括：（一）检查客户是否按照借款合同规定的用途使用贷款，如未按借款合同规定用途使用的，应查明原因并提出处置的意见和建议；（二）贷后检查应填制贷后首次跟踪检查表，发现影响到期还本付息的不利因素，应及时报告。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第五十九条', '经办行对辖内所有贷款的日常检查，按照“发放和检查不为同一人”的原则实行，贷后检查资料须及时归档。客户经理须对分工片内的贷款进行检查，阳光信贷客户贷款，每年至少检查一遍；非阳光信贷客户，每半年须检查一遍；特殊性的贷款检查，按相关信贷业务产品管理办法执行；公司类客户，每季须检查一遍。另外，公司类客户及50万元以上大额自然人客户的贷后检查，经办行负责人应独立或配合客户经理做好日常检查，确保每年对上述客户至少检查一遍。检查内容主要包括：（一）检查借款人、担保人的资产和生产经营、财务状况是否正常，货款回笼是否正常，主要产品的市场变化是否影响产品的销售和经济效；（二）了解掌握借款人、担保人的机构、体制及高层管理人员人事变动以及诉讼情况等重大事项，分析这些变动是否影响或将要影响借款人生产经营；（三）检查抵（质）押物的完整性和安全性，抵押物的价值是否受到损失，抵押权是否受到侵害，质押物的保管是否符合规定；（四）检查固定资产建设项目进展情况。根据固定资产项目管理规定，逐项检查项目资金是否按期到位，是否按招投标计划进行，项目贷款是否被挤占挪用，项目工程进展是否正常，项目是否能按期竣工，项目竣工投产能否达产等内容；（五）检查后要填制贷后检查报告表，经有权人复核后及时归档备查；（六）检查过程中，发现影响贷款安全重大事项的，应在检查表上填列，必要时进行专题汇报，提出防范和化解信贷风险的措施，报送单位负责人和相关职能部门；（七）所有贷后检查报告、图片等资料应及时录入信贷系统。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第六十条', '风险分类。贷款发放后，经办行应按本行风险分类制度规定及时分类。根据检查结果及时提出调整资产风险分类意见，报信贷管理部初审、风险管理部认定批准后实施。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第六十一条', '信贷管理部每年至少组织开展1次信贷综合检查（或分项目实施）；每季至少组织开展2个经办行的贷款业务专项检查；每月必须对上月新增（含新拓户或存量授信调增）的公司客户和100万元以上的个人客户贷款进行检查。检查各项信贷管理制度的执行情况，信贷管理责任制是否落实，信贷业务操作是否合法，有权审批人提出的限制性条款是否落实，督导、检查贷款业务发生后的跟踪检查和日常检查，检查发现的问题是否及时报告，是否采取了相应措施，已发现问题是否整改到位，是否存在重大风险隐患，是否存在屡查屡犯现象等。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第六十二条', '风险管理部每年至少开展一次关联交易检查，每半年至少组织一次风险分类、重点贷款检查。检查制度执行合规性、操作规范性及整体风险控制到位情况等。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第六十三条', '合规管理部每年组织开展信贷制度执行有效性的检查，评估制度执行的效果及存在的问题等。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第六十四条', '检查中发现问题的，检查人员应交被检查单位负责人确认，被检查单位如有异议的，应说明理由；无异议的，被检查单位应将整改意见报相关检查部门。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第七章  贷后管理', '第六十五条', '强化日常信贷风险预警。经办行通过贷后管理，对客户财务和非财务等因素，包括管理人员、银企关系、债权债务关系、财务状况等进行监控，运用定量和定性分析相结合的方法，尽早识别风险的类别、程度、原因及其发展变化趋势，确定不同预警等级。建立主动退出机制，限制、淘汰高风险客户。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第八章  贷款业务的到期处理', '第六十六条', '短期贷款到期一周之前、中长期贷款到期一个月之前，客户经理应向借款人通过书面、短信、QQ、微信等途径发送到期履行债务通知，并取得有效回执。有效回执包含但不限于从客户处取得书面签收回执、图片截屏等。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第八章  贷款业务的到期处理', '第六十七条', '借款人还清全部贷款后，经办行应将抵押、质押的权利凭证交还借款人并作签收登记，设定抵押、质押登记的应按借款人要求向登记部门办理登记注销手续。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第九章  特殊业务的处理', '第六十八条', '符合条件展期的，可由借款人申请，按以下要求办理贷款展期手续：（一）客户应提交书面展期申请。客户应在贷款到期日前向经办行提交《贷款展期申请书》，原贷款保证人、抵押人或质押人应在《贷款展期申请书》上签署“同意展期”的意见并签章；存单质押贷款展期后的到期日不得迟于最近一张存单的到期日。（二）贷款展期的调查、审查、审批。客户经理应对客户申请展期的原因、金额、期限、还款措施和还款资金来源进行调查，签注调查意见，按照审批权限审批。（三）签订贷款展期协议。贷款展期批准后，客户经理与客户、担保人（保证人、抵押人或质押人）签订《贷款展期协议书》。（四）贷款展期账务处理。客户经理将展期信息输入信贷V7系统。展期批复一联由会计部门作借款借据附件，一联作为信贷档案留存入档。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第九章  特殊业务的处理', '第六十九条', '借新还旧贷款（转据）的管理。经办行权限内，由经办行有权人审批；超经办行权限的，按转授权管理办法执行。（一）办理借新还旧贷款必须遵循以下标准：1.借款人生产经营正常；2.能够按时支付到期利息；3.重新办理贷款手续，责任人明确；4.风险程度有所降低，如办理了有效担保手续等。（二）符合下列情形，可以办理贷款借新还旧：1.通过贷款借新还旧，可收回部分贷款本金或利息，且贷款担保效力不低于原担保；2.贷款借新还旧后有利于贷款安全和借款人落实还款计划通过贷款借新还旧能使原借款合同或担保合同存在的法律缺陷得到完善，或使信用贷款转化为担保贷款，或进一步增强担保的可靠性；3.贷款借新还旧后担保、抵质押权不会丧失或削弱，而通过其他方式处置将导致贷款担保或优先受偿权丧失；4.变更借款人后贷款风险明显降低；贷款重组而合理分割原企业债务，需要办理借新还旧，重新落实债权。5.其他通过贷款借新还旧可以降低贷款风险的情形。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第九章  特殊业务的处理', '第七十条', '办理借新还旧贷款时，若前一份保证合同没有保证人，或者前一份保证合同的保证人与后一份保证合同的保证人不同，为确保转据后贷款担保手续的合法有效，应注意以下事项：（一）贷款借新还旧必须重新签订借款及担保合同，实行单笔合同管理，贷款用途注明“借新还旧”。（二）特殊情况签订最高额合同时，应在借款合同的“其他事项”一栏填明“依据本合同，XX 年 XX 月 XX 日发放贷款用于偿还×××（原合同编号）合同项下借款人所欠债务”。（三）借新还旧后的贷款为担保贷款的，应重新签订担保合同，并在担保合同“其他事项”中必须增加但不限于下列内容的保护性条款：本担保人知悉，借款人×××（借款人全称）与贵行签订编号为×××（新合同编号）借款合同项下贷款用于归还×××（原合同编号）合同项下所欠债务。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十章  贷款业务的风险监管', '第七十一条', '贷款到期之日营业终了尚未归还的贷款，转入逾期科目核算。客户经理应填制逾期贷款催收通知书，分别发送至借款人和担保人并取得回执，逾期一年内的贷款每季至少催收一次；逾期一年以上未诉讼的贷款，每半年应催收一次，已诉讼未履行的，应及时申请强制执行，确保债权落实。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十章  贷款业务的风险监管', '第七十二条', '贷款逾期后，经办行要按合同约定和有关规定采取计收罚息、停止提供新授信、催收借款人、担保人等清收措施收回贷款。与借款人（担保人）协商并订立还款计划的，必须经本行批准。经催收、协商，未有效落实还款计划或拒不偿还贷款的，应采取诉讼等方式进行强制清收。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十章  贷款业务的风险监管', '第七十三条', '建立客户重大经营事项报告制度。经办行对客户的重大事项，应按规定逐级报告，及时调整应对策略。当月新增公司和50万元以上大额自然人不良贷款的，客户经理应及时以书面形式向信贷管理部、风险管理部报告，说明形成原因和采取的清收措施。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十章  贷款业务的风险监管', '第七十四条', '本行的不良贷款实行“分线管理、分类清收”。对新发生的不良贷款，在责任认定和追究到位的基础上，风险管理部负责牵头、监督，分批划转至资产保全部，由资产保全部负责保全和清收。对未划转的不良贷款由原贷款发放机构负责日常管理、保全和清收。因管理松弛、玩忽职守、催收措施不力等主观原因导致贷款诉讼（执行）时效丧失造成损失的，将追究相关责任人员的责任。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十章  贷款业务的风险监管', '第七十五条', '按照本行《信贷资产风险分类实施细则》的具体要求，实施贷款风险认定，按季调整。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十章  贷款业务的风险监管', '第七十六条', '对列入呆账的不良贷款，符合呆账核销条件的，由经办行填制《核销贷款呆账损失申请表》，并附有关申报资料，报资产保全部审核后，按规定程序逐级申报，经批准后办理呆账核销手续。核销后，应继续向借款人、担保人追索或进行市场化处置，直至债务本息清偿完毕。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十一章  附则', '第七十七条', '本规程由信贷管理部负责制定、解释和修改。'),
(NULL, '信贷管理部', '《信贷业务操作规程》', '第十一章  附则', '第七十八条', '本规程自印发之日起执行。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第一章  总则', '第一条', '为切实加强本行信贷档案工作的科学管理，促使信贷档案管理规范化、制度化，有效防范信贷风险和保障本行债权，根据《中华人民共和国档案法》等国家法律法规、金融规章，以及本行《信贷管理制度》，特制定本办法。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第一章  总则', '第二条', '本办法所称信贷档案是指本行所辖支行、营业部、小微业务部、公司业务部（以下简称“经办行”）在经办和管理贷款过程中形成的，用以记录和反映信贷业务全过程和信贷关系的合同凭据、重要文件、报表、图片、影像以及电子资料等。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第一章  总则', '第三条', '信贷档案实行“统一领导，规范管理，责任到人，加强监督”的管理原则。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第一章  总则', '第四条', '经办行要切实重视和加强信贷档案管理工作，做到科学管理、有效利用，严防损毁散失，确保信贷档案资料的完整和安全。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第二章  组织管理体系', '第五条', '经办行负责信贷档案资料的搜集、建立、保管及管理工作。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定信贷档案管理办法，负责本行信贷档案管理的检查、指导和监督工作。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第二章  组织管理体系', '第七条', '合规管理部负责审核信贷档案管理办法的合规性和有效性。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第二章  组织管理体系', '第八条', '审计部负责对信贷档案管理进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第九条', '信贷档案资料按其取得来源分为从贷款客户处获取的资料和经办行内部信贷管理形成的资料。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十条', '从贷款客户获取的资料是指借款人、担保人的基本情况资料，是用于反映和记录借款人、担保人主体资格、资信状况、资金、财务和经营情况的档案资料。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十一条', '从贷款客户处获取的资料，客户经理应在受理后及时告知客户按要求提供，也可在客户面谈、贷前调查以及管理过程中补充提供，确保客户基础资料搜集齐全、完整。获取的资料复印件，客户经理均应认真与原件进行核对，并加盖“与原件核对一致”戳记后签名，对资料真实性负责。其中，借款人、担保人为企业法人客户的，留存的复印件应加盖单位公章。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十二条', '借款人为企（事）业法人的，应按本行《信贷业务操作规程》规定的借款人需提供的资料收集，包含但不限于下列资料：（一）基础资料1.借款企业依法成立的文件和有效的《营业执照》（或《事业单位法人登记证》）、组织机构代码证、税务登记证等复印件；2.企业章程（含修正案）复印件；3.验资报告复印件；4.法定代表人证明书原件、身份证复印件；5.法定代表人、实际控制人及高管简历；6.基本账户开户许可证复印件及金融机构开户情况（开户行名称、账户性质、账号）；7.企业房产、土地使用权证明（租赁合同）及法定代表人、实际控制人个人有效资产证明等；8.企业预留印鉴及董事（股东）签字样本；9.企业、法定代表人及配偶、主要股东的信用报告查询授权书及信用报告；10.前两年度年报和近3个月的财务报表、纳税申报表；11．质量体系等认证证书、资质证书、信用等级证书、消防验收意见书复印件（非必有项）；12.生产经营或投资项目环保部门的许可证明，若医药、卫生、化工等特殊行业需持有权部门颁发的生产、经营等许可证明，自营出口许可证复印件（非必有项）；13.关联企业基本情况（包括关联企业名称、关联关系、出资金额）及关联企业信用报告；14.如属于房地产企业的，须提供对应项目的《建设用地规划许可证》、《国有土地使用权证》、《建设工程规划许可证》、《建设工程施工许可证》复印件，商品房已经预（销）售的，还应提供《商品房预售许可证》或《商品房销售许可证》复印件；15.中国执行信息公开网失信被执行人信息查询结果；16.贷款人需要的其他资料。（二）发生贷款业务的文件资料：1.公司流动资金借款申请（审批）书；2.借款企业董事（股东）会同意借款决议；3.借款企业提供的购销等商务合同复印件；4.贷款担保资料，按不同担保方式搜集对应资料；①保证担保。保证人是企业法人的，除按借款人基础资料（1）-（12）项执行外，如保证人为有限责任公司、股份有限公司的，还应提供公司董事会或股东(大)会依公司章程作出的同意提供保证的决议。保证人是自然人的，应搜集保证人有效身份证件复印件；收入佐证证明（包括但不限于公积金、社保、工资清单、个人所得税纳税证明、经办行客户经理调查测算的收入证明及经办行认可的其他证明材料等其中一项或多项）；保证人个人信用报告查询授权书、信用报告和综合信息查询报告等。②抵押担保。抵押财产为借款人自有的，需提供抵押物产权证明文件、发票单据等复印件；抵押物清单；有权部门办理抵押登记核发的《他项权利证》、《不动产登记证明》或《抵押物登记证》复印件；如为有限责任公司、股份有限公司的，还应提供公司董事会或股东(大)会依公司章程作出的同意提供抵押的决议；国有资产抵押，需有国有资产管理部门批准同意抵押或行政主管部门出具书面同意抵押的证明文件；在建工程抵押或新竣工房产需提供施工单位放弃工程款优先受偿权的书面承诺。以第三人财产抵押，如为企业法人的，除上述资料外，还需按借款人基础资料（1）-（12）项资料进行搜集；如为自然人财产抵押的，除上述资料外，还需搜集抵押人有效身份证件复印件、个人信用报告查询授权书及信用报告等资料；③质押担保。借款人提供质物清单和有处分权人同意质押的《出质承诺书》，质押物的产权证明文件复印件；存单质押贷款在相应的营业机构办理止付登记手续的文件；非本行签发存单质押的，还应提供本行与他行签订的协议。（三）中长期贷款还应具备以下资料：1.有权部门批准的项目建议书、项目可行性研究报告、项目设计或实施方案、纳入年度固定资产投资计划等有关批文；2.项目开工前期准备工作完成情况，若为续建项目需提供前期投资情况、资金来源及使用情况、工程进度等资料；3.中长期贷款使用情况检查报告、项目竣工验收报告、项目后评估报告；4.在开户行存入规定比例资本金的证明；5.资本金和其他建设资金筹措方案及来源落实的证明材料。6.涉及环境保护、消防、供电供水、卫生防疫、劳动保护、税收优惠，生产产品或商品购销属专营、土地征用、城市规划、生产工艺及新产品技术鉴定等方面的项目，需提供有关方面的批准文件或证明材料和审核意见。（四）.其他资料：1.借款人自贷款日至归还日的财务报表（包括年报、半年报、季报和月报）；2.为保证信贷资产安全，需要客户提供的其它资料。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十三条', '借款人为自然人的，应包含但不限于下列资料：（一）.借款人基础资料：1.本人及配偶的有效身份证件、户口簿和结婚证；单身借款人应提供单身承诺，并由所在街道、居委会（村委会）或所在单位出具证明;2.借款人夫妻个人信用报告查询授权书及信用报告；3.借款人夫妻综合信用查询报告（通过本行“企业信息平台”查询）；4.借款人及其家庭资产权属证明、所在单位出具的工作关系证明或收入佐证证明（包括但不限于公积金、社保、工资清单、个人所得税纳税证明、经办行客户经理调查测算的收入证明及经办行认可的其他证明材料等其中一项或多项）；5.借款人持有的本行借记卡（含易贷通卡）及复印件；6.用于企业经营的，除按上述要求外，还需按企（事）业法人贷款中明确的“借款人基础资料”进行搜集；7.用于住房或消费的，还需提供购置合同（房、车等）、首付款发票；建设许可证、承建协议；装修、装饰、装潢协议；自筹资金来源证明资料；近3个月个人或家庭账户流水等资料；8.中国执行信息公开网失信被执行人信息查询结果；9.贷款人需要的其他资料。（二）.发生贷款业务的资料：1.个人贷款申请、调查、审批表；2.担保贷款资料，按不同的担保方式搜集相关材料，具体要求参照第十二条相关条款执行。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十四条', '自然人贷款采取“阳光信贷”批量授信方式的，按《“阳光信贷”业务管理办法》的要求搜集行政村户籍资料、评议资料、经营单位授信会办资料及总行授信批复等，进行单独管理。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十五条', '经办行内部形成的档案资料包括记录和反映客户贷款申请、调查、审查、发放、检查、保全、收回等全过程运行中管理情况的资料，包含但不限于下列资料：（一）授信阶段资料1.授信申请书、申请报告；2.面谈记录；3.授信调查表或授信调查报告；4.授信内部会办资料；5.授信批复。（二）用信阶段资料1.用信审批表（所有公司贷款、50万元以上的大额自然人贷款或借新还旧、展期等贷款用信时提供）；2.用信批复或审批意见；3.授信审批文件及授信限制性条款落实情况的证明材料。（三）签约支付阶段1.借款合同、担保合同及上门签约图片资料；2.抵（质）押物清单；抵押评估报告及抵押价值确认审批表；《抵押他项权证》、《不动产登记证明》或《抵押物登记证》原件及复印件；3.自助用信服务协议、圆鼎易贷通卡正面复印件及客户本人签字（非必有项）；4.提款支付审查意见表、提款通知书、委托支付协议、购销合同；5.人身意外伤害保险单复印件（非必有项）；财产保险单复印件（非必有项）6.借款借据（信贷管理联）、结算业务申请书。（四）贷后管理阶段1.首次贷后检查报告、常规贷后检查报告及检查现场图片；2.五级分类资料，包括五级分类工作底稿、分类认定表、分类认定报告、台账、形态调整审批表、会办意见等；3.收贷收息证明单；4.《贷款到（逾）期催收通知书》、《担保人履行责任通知书》、《贷款提前到期通知书》或短信、QQ、微信等途径通知，并取得有效回执（非必有项）；5.贷款展期资料，包括《借款展期申请书》、审批书或批复、展期协议书；保证人、抵押人、出质人同意担保延期的书面证明；6.贷款形成不良后，次月起管户客户经理应按户建立《不良贷款诊断档案》，并将基础资料、贷款发放资料（授信、用信、签约支付、贷后管理阶段所形成的资料）全部纳入不良贷款诊断档案中，同时需及时增加和补充下列追偿资料：⑴公司贷款及50万元以上大额自然人风险预警报告；⑵对不良贷款借款人（担保人）进行书面催收的有效回执、公证催收公证文书、律师催收证明、信函催收邮寄回执等；⑶处置协议，还款计划，总行相关批复或回复（非必有项）；⑷对借款人、担保人依法诉讼、仲裁的有关法律文书，包含诉状、法院受理通知书、判决（调解）书、申请执行书、裁定书、解协议及诉讼费、执行费发票复印件等；⑸法院宣告借款人、担保人进入破产还债程序裁定书，借款人、担保人死亡证明，法院宣告借款人、担保人失踪证明，工商行政部门吊销（注销、歇业）企业营业执照文件，借款人、担保人财产评估、清算、分配报告；⑹贷款核销资料，包含但不限于：呆账核销申请报告；呆账核销明细表；呆账核销认定意见书；对责任人的责任追究意见；呆账核销申请审批表；借款申请书、借款合同、抵押合同（含抵押物清单）、质押合同（含质押物清单）、保证合同、借款借据（包括申请书）等；追偿证明资料、还款记录凭证复印件；本行同意呆账核销会办表及其他资料等。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十六条', '贷款形成不良后，采取以产抵债方式处置的，应按户建立《抵债资产管理档案》，除客户基础资料和贷款发放资料外，还应增加和补充下列资料：（一）收产时的资料1.收产企业评估报告复印件；2.收产企业会办审批表；3.收产抵债协议书；4.抵债资产移交清单；5.抵债资产房地产、设备等权属变更证书复印件；6.人民法院民事裁定书复印件；7.抵债企业主从合同文本、借据复印件，本金、利息清单；8.以产抵贷划转审批表；9.待处理抵债资产登记簿。（二）处置时的资料1.资产租赁资料①资产租赁协议书；②租赁资产清单；③租赁会办审批表。2.资产变现租赁资料①资产变现时评估报告；②变现资产清单；③资产处置前公告文书；④资产拍卖书；⑤资产转让协议书；⑥资产移交清单；⑦变现资产会办审批表。（三）日常管理资料1.抵债资产检查监督台账；2.抵债资产检查报告表；（四）其它相关管理资料'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第三章  信贷资料搜集要求', '第十七条', '日常信贷管理过程中形成的各类资料，应分门别类搜集，定期整理装订归档，包含但不限于下列资料：1.《贷款会办登记簿》、《贷款否决登记簿》；2.《信贷档案交接登记簿》或《信贷档案移交清册》，《信贷档案调（查）阅登记簿》；3.各种信贷月报、季报、年报和突击性报表及相关管理台账；4.本行信贷管理制度和信贷管理相关文件及会议材料；5.总行、银监、人行等部门对贷款运作问题出具的稽核意见书、通知书和整改意见书；6.其它需要归档资料。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第四章  信贷资料建档', '第十八条', '自发生信贷业务起，客户经理应按客户类别和“一户一档”的要求及时建档。基本要求为：（一）个人贷款1.采取“阳光信贷”批量授信方式的，授信资料以村、组立卷建档；2.存单质押贷款以时间发放先后顺序，按月（季）立卷建档;3.个人住房按揭贷款资料按户立卷建档；4.其他个人贷款档案按授信金额建档，其中，授信金额50万元以上的个人客户为大额自然人，实行一户一个档案盒的管理模式；授信金额小于或等于50万元的个人客户为小额自然人，实行一户一个文件夹的档案管理模式，所有信贷资料须按目录和业务发生时间的先后顺序整理。（二）公司贷款：信贷档案按户分类立卷建档，按贷款运作程序及取得的时间排序，一笔一卷，一户一档。（三）抵债资产、已核销（置换）贷款：按户分类建档。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第四章  信贷资料建档', '第十九条', '每户档案均应设置卷内目录，一式一份，附在某一客户卷内首页；卷内资料应按照目录顺序整理，档外标明客户名称、立档时间。其中，涉及贷款合同、协议及授（用）信批复等重要资料，档案中一律使用复印件，不得放置原件。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第四章  信贷资料建档', '第二十条', '客户经理须按年对在用的客户信贷档案进行整理，将过期、丧失时效资料、已结清贷款的用信资料或全部归还且无续借需求的客户档案进行清理，按客户、按发生时间排序整理、装订。对已装订成册的信贷档案，卷内材料编写页码，页码必须连续，并在卷内目录上注明内容和页码，同时登记《信贷档案登记簿》，做好档案移交、归档前的准备工作。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第四章  信贷资料建档', '第二十一条', '客户信贷档案实行电子化影像存储和管理。客户经理在取得纸质资料的同时，须逐份扫描并按资料名称、日期等命名建立电子档案。其中，公司客户和大额自然人客户按户建立文件夹并以客户名称命名后，再分别建立基础资料、授信资料、合同资料、用信资料、贷后管理资料等5个子文件夹，并将影像扫描件存入相应文件夹中，以便日常查阅；小额自然人客户按户建立文件夹并以客户名称命名后，可根据影像资料数量统一存放于一个文件夹内。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第五章  信贷档案管理要求', '第二十二条', '信贷档案资料的管理职责。客户经理负责信贷档案资料的收集、签订、整理、更新和装订。同时，经办行应设档案管理员1人，由运营主管兼任，负责重要档案及归档资料的保管和管理。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第五章  信贷档案管理要求', '第二十三条', '档案管理员基本职责为：（一）涉及信贷档案中合同、协议、授（用）信资料及批复等重要资料原件，应督促客户经理按月将上述资料与贷款发生明细逐一勾对、核实，确保资料完整，无误后按业务发生时间顺序整理，单独按月（季）立卷建档和装订。（二）档案管理员应按月对客户电子档案建立情况进行检查、督促，按季拷贝备份、存档，严防电脑损坏造成资料丢失。（三）负责客户经理已装订档案资料的移交接受和管理工作。对客户经理移交归档的各类信贷档案，应及时审查、复核，确保档案资料与目录一致，无误后双方在《信贷档案交接登记簿》或《档案移交清册》上签字确认，实行集中保管。条件具备时由总行统一上收、集中保管。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第五章  信贷档案管理要求', '第二十四条', '客户经理因工作变动离岗的，所有信贷档案资料（含纸质档案、电子档案）需全部移交指定人员，并及时登记《信贷档案交接登记簿》或抄列《档案移交清册》，由运营主管负责监交。交接到位后，移交人、接收人、监交人必须在《信贷档案交接登记簿》或《档案移交清册》上签字确认，无误后客户经理方可办理有关变动、离岗等手续。信贷档案资料的交接须在5个工作日内完成。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第五章  信贷档案管理要求', '第二十五条', '对已实行集中保管的按揭类信贷档案，需于次月10号前将上月发放的客户信贷档案资料整理装订、抄列清单后移交本行档案管理中心集中保管，并按规定办理档案移交手续，同时须将移交清单报一份信贷管理部备查。对非按揭类贷款的信贷档案，集中保管的相关规定和要求，由本行另行制定。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第六章  归档后的信贷档案管理', '第二十六条', '信贷档案的调阅。档案管理员建立《信贷档案调（查）阅登记簿》，及时记载信贷档案调（查）阅情况。（一）内部人员调阅信贷档案。已归档保管的信贷档案，不准借出。因工作需要调阅的，由调阅人出具调阅手续，经经办行负责人签字同意后方可调阅。档案管理员应负责登记调阅人员名单、时间和调阅内容；调阅的信贷档案不得涂改、圈划、复制、拆散原卷册，用后必须及时归还。（二）外部人员调阅信贷档案。信贷档案不对外调阅借出。当发生借款纠纷或依法起诉、处理案件等情况时，下列情况可以查阅：1.司法、法院等部门需查阅、复印时，必须持有正式介绍信，经经办行负责人签字批准后方可查阅；2.本行顾问律师经总行指令后有需要可以查阅；3.非本行顾问律师不可以查阅，若其通过法院来查阅,法院须持有正式介绍信，经经办行负责人签字批准后方可查阅。查阅时，经办行应有专人在场陪同，查阅人需要索取复印有关证据和资料时，须经经办行负责人同意后，方可抄录和复印，但严禁将原件抽出借出。（三）档案管理员对调阅归还的信贷档案，要检查其完整性和原始性。对发现档案不完整或有改动的，可以拒绝接受，并立即向经办行负责人报告。必要时可向总行报告。（四）对已移交总行保管的档案资料，不得对外借出。确因工作等原因需要临时借出使用或办理换发新证的，管户客户经理须出具书面报告，由所在单位负责人签字证明、经信贷管理部审核后报分管行长审批同意方可借出。管户客户经理对借出档案资料的使用、保管、登记等负完全责任，使用完毕后应及时归还。总行档案管理员应及时登记借出、归还情况，并对归还的信贷档案，认真检查其完整性、有效性。对发现档案不完整或有改动的，可以拒绝接受，并立即向信贷管理部反馈，以便及时处理。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第六章  归档后的信贷档案管理', '第二十七条', '信贷档案的存放。客户经理和档案管理员应将各自管理的信贷档案资料放置在坚固的箱、柜内，并加强防盗、防火、防潮湿、防虫蛀、防腐蚀等方面的管理。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第六章  归档后的信贷档案管理', '第二十八条', '信贷档案保管期限。信贷档案保管期限分为短期保存和永久保存。（一）短期保存的信贷档案为5年。贷款本息已收回的信贷档案，再保管期限为5年。（二）永久保存的档案。短期和中长期贷款到期、逾期至今仍未收回和呆账核销（置换）贷款档案、抵债资产档案应持续永久保存。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第六章  归档后的信贷档案管理', '第二十九条', '信贷档案的销毁。对超过保存期、需要销毁的信贷档案编制销毁清册，并经过档案管理员提出和慎重鉴定，经办行负责人审核，经本行信贷管理部审查批准后，集中到指定地点进行销毁，并由监销人员监督销毁。档案管理员、经办行负责人和监销人员同时在留存销毁清册上签字备查。'),
(NULL, '信贷管理部', '《信贷档案管理实施办法》', '第七章  附则', '第三十条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。第三十一  条本办法由信贷管理部制定、修改和解释。第三十二  条本办法自印发之日起施行。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第一章  总则', '第一条', '为加强信贷管理，规范信贷行为，防范和化解信贷风险，优化客户结构，提高信贷资产质量，更好地为农民、农业和农村经济服务，根据《中华人民共和国商业银行法》、《中华人民共和国担保法》、《贷款通则》、《商业银行授信工作尽职指引》、《商业银行小企业授信工作尽职指引（试行）》、《固定资产贷款暂行办法》、《项目融资业务指引》、《个人贷款管理暂行办法》、《流动资金贷款管理暂行办法》等有关法律法规，结合实际，特制定本制度。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第一章  总则', '第二条', '本制度是本行及下属支行、营业部、小微业务部、公司业务部、金融市场部、电子银行部、资产保全部（以下统简称“经办行”）信贷经营和管理必须遵循的基本准则，是制定各类信贷管理办法的基本依据。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第一章  总则', '第三条', '信贷经营和管理必须坚持安全性、流动性和效益性相统一的原则。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第一章  总则', '第四条', '本行的信贷经营和管理应当遵循国家法律法规，执行国家经济政策和产业政策，以服务“三农”为宗旨，积极拓展城乡信贷业务，支持区域经济的发展。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第一章  总则', '第五条', '本制度所指“信贷工作人员”是指经本行批准，代表本行拓展和维护客户关系，为客户提供资产、负债、中间业务等金融服务和管理的本行在职人员。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第一章  总则', '第六条', '本制度中所称“信贷业务”是指本行对客户提供的各类信用的总称，包括本外币贷款、贴现、银行承兑、保函、贷记卡等表内外资产业务。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第二章  信贷管理组织体系', '第七条', '实行审贷分离、分级审批制度。在办理信贷业务过程中，将调查、审查、审批、经营管理等环节的工作职责分解，由不同经营层次和不同部门承担，实现其相互制约和支持。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第二章  信贷管理组织体系', '第八条', '按照“管理垂直化，机构扁平化”原则，设立前台营销部门、中后台管理部门和决策层三个层级。前台营销部门为经办行。承担信贷业务的受理、调查、评估和审批后信贷业务的经营管理。中后台管理部门为授信评审部、信贷管理部、风险管理部、合规管理部、运营管理部、计划财务部、资产保全部等部门。授信评审部承担信贷业务授信审查、审批，开展客户信用等级评审，实施客户准入、退出策略；信贷管理部负责队伍建设与管理，承担用信的审查、审批，对重点行业、区域、客户和信贷品种进行监控、预警和防范。风险管理部组织对风险管理的政策、制度和流程的执行效果检查评估，负责全辖风险的识别、评估、计量、监控和对风险资产的分类、认定和上报。合规管理部负责审核信贷各项规章制度和操作规程，组织收集、识别、汇总、更新适用的法律法规；运营管理部负责放款的审查、审批。计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。资产保全部负责对不良贷款的清收保全及相关管理。决策层为资产负债管理委员会、信贷审查委员会、风险与内控管理委员会（以下分别简称资管会、贷审会、内控会，下同）。资管会、贷审会、内控会是本行信贷业务决策的议事机构，在各自的授权范围内负责信贷业务的审议和决策，对有权审批人进行制约以及智力支持。人员分别由本行相关部门负责人和具有评审能力的人员组成，分管的行长分别任资管会、贷审会、内控会主任委员。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第二章  信贷管理组织体系', '第九条', '实行信贷业务授权管理制度。本行董事会对行长进行授权，行长对资管会、贷审会、内控会、授信评审部、信贷管理部、经办行进行转授权。对超过资管会、贷审会、内控会权限的关联人贷款、大额贷款、大额收产抵债、资产处置、其他重大事项需报董事会风险管理与关联交易控制委员会审议。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第二章  信贷管理组织体系', '第十条', '实行贷款责任追究制度。本行每笔贷款都必须确定责任人，同时明确其他各岗位的责任，不得发放无责任人的贷款。责任人对贷款风险承担主要责任，其他岗位人员承担相应责任。贷款出现风险或发生不良后，经本行有权部门界定，根据各岗位责任大小，按制度规定予以经济赔偿或行政处分。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第三章  客户对象和基本条件', '第十一条', '客户应当是经依法履行工商行政管理职能的机构核准登记的企(事)业法人、其他经济组织、个体工商户或具有合法身份证件、在本行辖区内有有效居住证明（含暂住证）具有完全民事行为能力的自然人（除按揭类贷款外）。借款人为自然人的，借款时年龄与借款期限相加不超过65周岁，其中：借款人为公职人员的，申请个人住房按揭贷款或以个人住房抵押担保申请消费贷款的，可根据其身体状况和未来还款能力（综合考虑退休前后）适当放宽至70周岁。公职人员指公务员或参照公务员管理的其他企事业单位、人民团体等组织内从事公共事务管理部门的正式在编工作人员。存单质押贷款借款人年龄不受限制。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第三章  客户对象和基本条件', '第十二条', '客户申请信贷业务应当具备下列基本条件：㈠从事符合国家产业政策和社会发展规划要求的建设和生产经营活动，借款用途明确、合法。㈡采用担保方式发放的，近3年内无借款、对外担保等重大不良信用记录；采用信用方式发放的，近5年内无借款、对外担保等不良信用记录。㈢有合法稳定的收入来源，具有按期还本付息能力；原应付贷款利息和到期贷款已清偿或落实了经本行认可的还款计划。㈣在本行开立结算账户，自愿接受本行的信贷监督和结算监督，如实向本行提供有关财务报表、税务证明等相关资料。㈤除自然人外，企业法人须持有履行工商行政管理职能的机构核准登记的证件，特殊行业须持有有权机构颁发的特殊行业许可证；事业法人依照《事业单位登记管理暂行条例》的规定向事业单位登记管理机关办理了登记或备案手续。㈥申请短期贷款，申请客户的资产负债率一般应低于70%。申请固定资产项目贷款的客户，应有经国家有权机关批准的项目立项批文，并按照相关管理办法规定提供相应比例的资本金。㈦申请流动资金贷款，客户经理需根据企业财务数据或实际需求进行流动资金贷款需求量测算。㈧申请票据贴现的，必须持有合法有效的票据并提供具有真实商品交易背景的相关资料。㈨按照中国人民银行的有关规定，企业法人在发生信贷业务前，必须在征信业务系统中申请中征码。㈩新拓客户，已在两家金融机构有贷款授信的，在充分考虑其企业状况、总体信贷规模后采取审慎进入原则；对已在三家及以上金融机构有贷款授信的，不得新增保证贷款，以核心资产抵押的审慎进入，经总行批准的除外。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第三章  客户对象和基本条件', '第十三条', '不得对未履行借款合同、担保合同约定义务的借款人、担保人或重大经济纠纷未解决的企（事）业法人或自然人发放贷款，确因本行债权保全或降低弱化风险等原因需要办理的，必须经本行批准。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第四章  主要信贷业务', '第十四条', '贷款是指本行根据客户申请，对其提供的并按约定的利率和期限还本付息的货币资金。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第四章  主要信贷业务', '第十五条', '贷款按期限分为短期贷款、中期贷款和长期贷款。㈠短期贷款是指期限在1年(含1年)以内的贷款。㈡中期贷款是指期限在1年以上5年(含5年)以下的贷款。㈢长期贷款是指期限在5年以上的贷款。长期贷款期限最长不得超过10年（住房按揭贷款除外）；特殊情况需超过10年的，应报本行批准。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第四章  主要信贷业务', '第十六条', '贷款按方式分为信用贷款、担保贷款和票据贴现。㈠信用贷款是指以借款人的信誉发放的贷款。信用贷款发放需符合规定条件。㈡担保贷款分为保证贷款、抵押贷款和质押贷款。1.保证贷款是指按《担保法》规定的保证方式以第三人承诺，在借款人不能偿还贷款时，按约定承担一般保证责任或连带保证责任而发放的贷款。本行只发放连带保证责任的保证贷款。办理保证贷款，应当对保证人保证资格、资信状况及其还款记录进行审查，并签订保证合同。2.抵押贷款是指按《担保法》规定的抵押方式以借款人或第三人的财产作为抵押物发放的贷款。办理抵押贷款应当对抵押物的权属、有效性和变现能力以及所设定抵押的合法性进行审查，签订抵押合同并办理抵押物的有关登记手续。根据抵押物的不同情况，合理确定贷款抵押比例，严格执行本行关于抵押贷款抵押率的规定，抵押贷款抵押率一般最高不超过抵押物评估价值或协议约定价值的60％。商业用地、门面房抵押以及三线（含）以上城市个人住宅，抵押率可适当提高，但最高不得超过抵押物评估值的70%，三线（含）以上城市名录以《第一财经周刊》等权威网站公布为准；在建工程抵押率应当控制在50%以下；存货抵押率控制在70%以内；机械设备抵押率控制在40％以内；机械设备、运输工具抵押的，应考虑设备的通用性、使用年限及成新率等因素，按一定比例确认抵押值。3.质押贷款是指按《担保法》规定的质押方式以借款人或第三人的动产或权利作为质物发放的贷款。办理质押贷款，应当对质物的权属和价值以及设定质押的合法性进行审查，与出质人签订质押合同，并办理相关的登记或移交手续。存单、银票质押的，不得超过其面值的90%；仓单质押按期限长短，结合质押物不得超过产品购入价或成本价的60%至70%。4.所有借款不论是保证、抵押都必须追加企业股东联保。对法人代表及其配偶名下的个人核心资产，须一并抵押给本行（总行批准除外）。㈢票据贴现是指持票人为了取得资金将未到期的银行承兑汇票转让给本行的票据行为，是本行向持票人融通资金的一种方式。票据贴现期限自贴现之日起到票据到期日止，最长不超过6个月。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第四章  主要信贷业务', '第十七条', '承兑是指承兑人（银行）应承兑申请人的请求，依据票据法和本行的相关规定，对其签发的银行承兑汇票，承诺在该汇票到期日无条件支付汇票金额的票据行为。银行承兑汇票承兑期限不得超过6个月。若因业务需要办理敞口承兑业务，按转授权管理办法执行。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第四章  主要信贷业务', '第十八条', '银团贷款指多家金融机构依据同一贷款协议，按商定的期限和条件向同一客户提供资金的贷款方式。银团贷款的发放应依据本行《银团贷款管理办法》的有关规定执行。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第四章  主要信贷业务', '第十九条', '其他资产信用品种。本行新开发的信用品种按有关规定报备或报批后实施。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第五章  贷款期限和利率管理', '第二十条', '期限管理。1.贷款期限应由本行根据借款人生产经营周期、综合还款能力和自身资金供给能力审慎确定。2.中长期贷款期限由本行根据具体的贷款品种审议决定，但每年还款期不得少于两次。对于流动资金贷款授信期限最长可达3年，但每次借款期限不得超过1年（含）。3.贷款到期催收。短期贷款到期一周之前、中长期贷款到期一个月之前，客户经理应向借款人通过书面、短信、QQ、微信等途径发送到期履行债务通知，并取得有效回执。4.逾期贷款管理。贷款形成逾期后，客户经理应及时取得借款人、担保人签章确认的催收通知书回执，确保债权有效。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第五章  贷款期限和利率管理', '第二十一条', '本行贷款利率按照中国人民银行的利率政策，在允许的浮动幅度范围内，按借款方式、用途、期限、客户贡献度等因素，综合确定每笔贷款利率，并在借款合同和借款凭证上载明。银行承兑汇票等或有资产业务按规定收取手续费。贷款展期期限加上原期限达到新的利率期限档次时，从展期之日起，贷款利息按新的期限档次利率计收。逾期贷款和挤占挪用贷款按规定计收利息。贴息贷款，根据利息补贴方法，按规定计收利息。提前归还贷款，应当按实际借款的期限计收利息；如果合同另有约定的，按合同约定要求客户支付违约赔偿金。停息、减息、缓息和免息，严格按有关规定办理。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第六章  授信管理', '第二十二条', '实行客户统一授信管理。客户统一授信管理是本行对客户实施集中统一控制其信用风险的管理制度。客户授信的条件由本行统一确定，做到授信主体统一，标准统一，内容统一，对象统一。自然人贷款授信实行归口管理，同一家庭仅能以一名成员作为借款人，且不得在本行下属两个或两个以上的经办行申办贷款（按揭贷款、存单质押以及总行批准 特定用途贷款除外）。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第六章  授信管理', '第二十三条', '本行办理授信业务时坚持“先授信、后用信”，“谁受信、谁用信”的原则。实施有条件授信时应遵循“先落实条件，后实施授信”的原则。授信条件未落实或条件发生变更未重新决策的，不得使用授信。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第六章  授信管理', '第二十四条', '客户信用等级管理。客户信用等级评定是本行客户授信管理的基础工作。公司客户信用等级分为：AAA、AA、A、BBB、BB、B、CCC、CC、C 级，评定指标包括基本信息、偿债能力、发展能力、规模指标、经营能力、履约情况、企业素质和盈利能力等。自然人客户信用等级分为：优秀、良好、一般、不佳、恶化等五级，评定指标包括基本信息、保险情况、财务情况、个人概况、家庭情况、经营情况、合作关系和职业情况等。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第六章  授信管理', '第二十五条', '评定信用等级实行内部评定和外部评定相结合。内部评定是由本行对照标准自我评定,是贷款的必备条件；外部评定是在客户自愿的基础上，由具备资质的评估机构进行，评估结果按“就低不就高”的原则确认。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第六章  授信管理', '第二十六条', '实行客户分类管理。对非自然人客户按优良客户、一般客户、限制客户、退出客户实行客户分类管理。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第六章  授信管理', '第二十七条', '根据授信客户信用等级评定结果、资产负债率和其他要素确定客户最高综合授信额度，使本行对其提供的本币贷款、贴现、承兑敞口等资产信用余额之和不超过最高综合授信额度。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第六章  授信管理', '第二十八条', '客户统一授信管理分为内部统一授信和公开统一授信两种方式。内部统一授信指本行核定客户最高综合授信额度，作为本行内部控制客户信用风险的最高限额，由本行内部掌握。公开统一授信指本行根据客户申请，在对单一法人客户或企业集团(关联)的风险和财务状况进行综合评价的基础上，就核定的最高综合授信额度，与客户签订授信协议，使客户在一定时期和核定额度内，能够便捷使用银行信用。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第二十九条', '客户申请。客户可以通过本行营业网点、服务热线电话、网上银行、手机银行等多种渠道提出授信申请。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十条', '授信受理。经办行应根据客户申请，及时指定客户经理开展授信前调查的各项准备工作。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十一条', '授信调查。授信调查必须坚持面谈制度和 “双人四眼”原则。客户经理的授信调查应以实地调查为主，间接调查为辅，对客户的基本情况、财务状况、非财务信息等进行综合调查，形成书面调查报告（意见）。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十二条', '授信审查审批。本行实行部门会办与有权审批人相结合的流程审查、审批制度，按权限、程序运作，严禁超授权、逆程序、越程序操作。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十三条', '用信申请。客户获得授信后，用信时应向经办行提出申请，贷款经办人负责受理。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十四条', '用信审查审批。贷款经办人根据客户提出的用信申请，对客户授信文件有效性、限制性条款落实、担保措施及用信资料完整性、有效性等进行审查核实，其中，对客户已授信未用信且期限超过6个月的，申请用信时客户经理应对客户进行必要的调查和核实，提出用信意见，对符合用信条件的，按权限审查、审批。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十五条', '合同签约。本行使用统一制式的合同文本，经办行发放的每笔贷款须与相关合同匹配，严禁发放无合同、合同无效或与合同不对应的贷款。客户经理与客户签订贷款合同时，应在监控录像状态下进行并做好登记，特殊情况按相关规定办理。签订后的合同须及时提交审查人复核，内容、要素齐全、准确的，及时加盖“贷款合同专用章”并做好登记。未经复核或未加盖“贷款合同专用章”的合同不得发放贷款。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十六条', '贷款发放与支付审批。经办行权限内的，由有权人审查、审批；经办行权限外的，提交本行信贷管理部及有权人审查、审批。贷款经办人根据审批意见，打印《放款通知单》（易贷通除外）交客户至柜面办理放款。客户申请使用手机银行等电子渠道自助放款的，除按规定签订贷款合同外，客户经理应与客户签订《手机银行自助用信服务协议》，同时复印圆鼎易贷通卡正面并经客户签字确认，与贷款合同、服务协议等一并保存。客户通过手机银行自助放款成功后，经办行应及时补打借款借据，并按规定妥善保管。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十七条', '柜面支付。接到客户提交的《放款通知单》（易贷通除外），权限内的，由有权人审查、审批；权限外的，经运营管理部有权人审查、审批。柜面经办人员根据审查、审批意见发放贷款，并严格按约定的支付方式，办理自主支付或受托支付。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十八条', '贷后管理。主要包括贷后检查，信贷风险预警、客户重大经营事项报告、违约处理、贷款档案管理、贷款收回环节。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第七章  信贷业务基本流程', '第三十九条', '特殊业务处理1.对符合条件的借新还旧（以贷还贷）贷款，不得发生大于原贷款本金的贷款发放业务，不得以贷收息。2.贷款展期。不能按期归还，但符合展期条件的贷款，借款人应在贷款到期前向经办行申请贷款展期，经办行审核后按权限进行审批。担保贷款申请展期，还应出具担保人同意展期并继续担保的书面证明。展期贷款条件不得低于原贷款条件。公司类短期贷款展期累计不超过原期限，中期贷款展期不超过原期限的一半，长期贷款展期最长不超过3年。一年（含）以内的个人贷款，展期期限累计不得超过原贷款期限；一年以上的个人贷款，展期期限累计与原贷款期限相加，不得超过该贷款品种规定的最长贷款期限。客户未申请或申请展期未获批准，其贷款从到期日次日起转入逾期贷款科目核算。有资产业务到期不能履约的款项，从到期次日起转入相应的逾期贷款（或垫款）科目。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十条', '严格执行贷款责任追究制度，凡新发生的五级不良贷款，按照尽职免责、失职追责的原则进行责任追究。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十一条', '实行贷款风险分类管理制度。经办行应根据贷款风险状况确定风险类别。贷款风险分类应遵循以下原则：(一)风险原则。风险分类以信贷资产内在风险为主要依据，逾期情况只作为重要参考因素。(二)真实原则。以借款人的财务状况、经营成果、现金流量、信用记录为主要依据，对各类信贷资产准确分类，真实反映其风险价值。(三)审慎原则。通过对影响债务人偿还债务可能性的诸多因素进行定性与定量分析评估，合理划分风险类别。介于相邻类别之间的信贷资产应归入低级档次。(四)灵活原则。贷款应逐笔分类，同一借款人有多笔贷款，且条件基本相同，在不影响总的分类结果的前提下，可将多笔贷款合并分类。对明确界定可能偿还比例的单笔贷款，可按偿还可能性拆分成多笔后进行分类。(五)动态原则。在定期进行风险分类的基础上，及时、动态地掌握影响信贷资产回收相关因素的变化情况，借款人风险状况如有明显变化应及时进行重新认定。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十二条', '贷款的监测实行期限分类法和风险分类法。1.按期限分类，贷款分为正常贷款、逾期贷款、呆滞贷款和呆账贷款。其中，逾期贷款、呆滞贷款和呆账贷款为不良贷款。2.按风险分类法，企事业贷款风险分为正常1—3级、关注1—3级、次级1—2级、可疑、损失十个级别。自然人贷款风险分为正常、关注、次级、可疑、损失五个级别。其中，次级贷款、可疑贷款和损失贷款为不良贷款。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十三条', '实行不良贷款风险认定和监测制度，严格标准，真实反映信贷资产质量。新发生的不良贷款要坚持逐笔（户）审查、明确责任、分级审批、规范运作的原则，按规定权限和程序认定。对不良贷款实行直接监管和重点监管，严格责任考核。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十四条', '建立不良贷款诊断书制度。客户经理对不良贷款按户建立诊断档案，动态收集信贷资料和法律文书，确保诊断资料的完整性、连续性、有效性。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十五条', '不良贷款的催收。客户经理对分工区域内的不良贷款，要定期对借款人、保证人进行催收，取得书面回执归档保管，防止因主观失误而形成贷款时效丧失。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十六条', '不良贷款清收。本行实行“分线管理、分类清收”原则。对新发生的不良贷款，在责任认定和追究到位的基础上，按内部管理流程分批划转至资产保全部，由资产保全部负责日常清收、管理。对未划转的不良贷款仍由原经办行负责保全和清收。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十七条', '不良贷款保全。对债务人不能按合同约定归还贷款本息或不能落实还本付息事宜的，应采取包括诉讼等方式的债权保全措施。对接受借款人、保证人、抵押人或出质人以非货币资产作价偿还贷款，应当遵循合法取得、妥善保管、及时处置、准确核算、确保本行利益的原则，按照本行《抵债资产管理办法》的有关规定执行。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十八条', '不良贷款的考核。本行根据业务经营需要，下达经办行不良贷款清收和保全任务，按月、按季或按年度考核兑现；经办行对本行下达的目标细化分解到客户经理，与客户经理业务能力挂钩考核。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第八章  信贷资产风险监管', '第四十九条', '呆账贷款的核销。本行按规定提取呆账准备金，对符合呆账核销条件的，按国家有关标准和程序进行核销。核销后，应继续向借款人、担保人追索，直至债务本息清偿完毕。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第九章  信贷业务系统管理', '第五十条', '本行统一使用省联社制定并组织开发的信贷业务管理系统。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第九章  信贷业务系统管理', '第五十一条', '信贷数据登录。客户经理负责按信贷管理系统要求将客户基本信息、担保人信息、调查分析资料和用信资料进行登录，并坚持“必填项准确、选填项充分”的原则，登录完成后，提交审查人员审核。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第九章  信贷业务系统管理', '第五十二条', '系统数据审核。审查人依据已审核贷款的纸质资料，在本人操作界面中对客户经理登记的信息进行逐项审查。对登记不完整、不准确的，退回上一环节补充；对登记符合要求的，提交下一环节审批。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第九章  信贷业务系统管理', '第五十三条', '信贷系统数据审批。有权审批人按照系统设置权限审批贷款。对信息登记不全面、不准确的，退回补充；对登记符合要求的，按权限完成信贷系统的审批流程；对超过本人权限的，提交下一环节审批。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第九章  信贷业务系统管理', '第五十四条', '完成各级审批流程后，客户到柜面承借的，贷款经办人应及时打印《放贷通知书》（易贷通除外）交客户，由客户凭《放贷通知书》、身份证等资料到经办行柜面办理放款手续。客户采用手机银行等电子渠道自助放款的，按前款第三十六条规定执行。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十章  信贷信息管理', '第五十五条', '本行相关部门和经办行应严守信贷业务的有关机密，不得将电脑操作密码告知无关人员或泄露借款人的商业机密。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十章  信贷信息管理', '第五十六条', '申请信贷业务，公司类客户必须由借款人、保证人授权并提供中征码、密码，方可查询其资信；个人类客户必须由借款人、保证人签字授权后，方可查询其征信。对已发生信贷业务的贷后检查，可通过借款人的中征码编码、组织机构代码、名称或身份证号进行。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十章  信贷信息管理', '第五十七条', '相关部门和经办行除可查询中国人民银行所发布的公共信息外，只能查询与其发生或申请发生信贷业务关系借款人的资信情况。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十章  信贷信息管理', '第五十八条', '相关部门和经办行通过银行信贷征信系统查询获取的借款人资信情况，不得向第三方泄露。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十一章  信贷人力资源管理', '第五十九条', '信贷工作人员不得有下列行为：1.向客户指定或提供担保人；2.在授信申请获得有权审批人批准之前与客户签订合同或违规对外签订借款合同、对外提供担保、保函、票据、资信证明等；3.向关系人发放信用贷款或优于其他借款人的条件向关系人发放担保贷款；4.严禁私自留存借款人的权证类资料；5.严禁违规使用信贷业务档案；6.除消费贷款外，向有存量贷款的企业法人发放个人贷款；7.其他不符合本行信贷管理规定的行为。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十一章  信贷人力资源管理', '第六十条', '本行定期不定期组织培训，提高信贷工作人员的业务技能和道德素养。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十一章  信贷人力资源管理', '第六十一条', '本行人力资源部和信贷管理部负责客户经理的考核。本行实行客户经理等级管理制，等级评定每年一次。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十二章  附则', '第六十二条', '本制度由信贷管理部负责制订、解释和修改。'),
(NULL, '信贷管理部', '《信贷管理制度》', '第十二章  附则', '第六十三条', '本制度自印发之日起执行。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行信贷操作行为，大力拓展公职人员信贷业务，防范和化解贷款风险，根据《个人贷款管理暂行办法》等法律法规，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等制度，制定本办法。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第一章  总则', '第二条', '本行开展公职人员信贷业务，遵循“依法合规、审慎经营、平等自愿、公平诚信”的原则。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第三条', '本行所辖的支行、小微业务部、公司业务部（以下简称“经办行”）负责公职人员贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第四条', '授信评审部负责公职人员贷款业务的授信管理。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '三农业务部负责公职人员贷款业务的宣传策划、营销推动及日常督查管理。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定《公职人员贷款管理办法》，对贷款业务的用信管理和贷后检查管理的监督。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对公职人员贷款业务的用款审查。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '资产保全部负责对不良贷款的清收保全及相关管理。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '风险管理部负责对公职人员贷款业务的风险监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核公职人员贷款办法合规性和有效性。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '审计部负责对公职人员贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第三章  贷款对象、条件和贷款方式', '第十三条', '贷款对象。具有完全民事行为能力的公职人员。公职人员特指公务员或参照公务员管理的其他企事业单位、人民团体等组织内从事公共事务管理部门的正式在编工作人员。包括：（一）市、镇党委和政府机关及其组成部门的工作人员，人大、政协机关及其组成部门的工作人员；公安、检察院、法院的工作人员；（二）经批准设立的妇联、共青团、残联、侨联等由财政全额拨付经费的工作人员；（三）教育、卫生等公立单位工作人员；（四）实行垂直化管理的机关、事业单位的驻本市机构工作人员；（五）供电、烟草、盐业等垄断行业以及金融、邮政、电信行业的在编工作人员；（六）央企正式员工；（七）其他参照公务员管理的工作人员。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第三章  贷款对象、条件和贷款方式', '第十四条', '贷款条件。借款申请人除具备本行信贷管理制度规定的基本条件外，还应具备以下条件：（一）具有稳定的职业和收入，有按期偿还贷款本息的意愿和能力；（二）所在单位出具的工作关系证明；（三）担保方式发放的应该近3年内无借款、对外担保等重大不良信用记录；信用方式发放的应该近5年内无借款、对外担保等重大不良信用记录；（四）符合本行其他相关要求。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第三章  贷款对象、条件和贷款方式', '第十五条', '贷款方式。既可采取信用贷款方式，也可采取保证、抵押、质押等方式发放。对采用信用方式的，原则上应购买借款人意外伤害保险。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第四章  贷款用途', '第十六条', '公职人员申请贷款，可用于家庭日常生产、经营、消费等，如国家法律、法规、行政规章和所在单位对公职人员经营、经商等有限制的，应不违反法律规定。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第五章  额度、期限、利率及还款方式', '第十七条', '贷款额度。（一）符合条件的公职人员申请信用贷款，原则上按下列限额确定（同一家庭有多个公职人员，以最高职级的一人确定）：1.一般办事员或初级职称人员限额为10万元（含）；2.股级或中级职称人员限额为20万元（含）；3.科级或高级职称人员限额为30万元（含）；4.副处级（含）以上的限额为50万元（含）。（二）符合条件的公职人员申请保证贷款，在信用额度已使用的基础上，原则上按下列限额确定（同一家庭有多个公职人员，以最高职级的一人确定）；信用额度未使用的，也可直接以保证方式申请，可发放贷款额度累加计算：1.一般办事员或初级职称人员限额为10万元（含）；2.股级或中级职称人员限额为20万元（含）；3.科级或高级职称人员限额为30万元（含）；4.副处级（含）以上的限额为50万元（含）。能够提供有效抵（质）押物质，且符合抵（质）押物品条件的贷款，按《个人贷款管理实施细则》相关要求执行，贷款额度不受上述限制性条款约束。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第五章  额度、期限、利率及还款方式', '第十八条', '贷款期限。根据贷款方式综合确定贷款期限。信用贷款期限一般不超过1年；保证贷款期限一般为不超过3年；抵押贷款期限一般不超过5年。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第五章  额度、期限、利率及还款方式', '第十九条', '贷款利率。以行政级别或职称核定的贷款限额为基准，限额内贷款执行本行优惠利率（最高不超过50万），超过限额部分，按本行一般贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第五章  额度、期限、利率及还款方式', '第二十条', '还款方式。（一）期限在1年（含）以内的，实行“按季（月）结息，到期利随本清”的还款方式；（二）期限在1年（不含）以上的，实行分期还款方式，原则上采取等额本息或等额本金还款方式。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第六章  贷款流程', '第二十一条', '调查授信阶段（一）采用阳光信贷授信模式的，各经办行与贷款对象所在单位充分沟通，由贷款对象所在单位填制该单位在编公职人员清单，同时加盖该单位公章。（二）按其行政级别或所取得的职称计算信用授信总额的，以单位向总行授信评审部上报授信审批手续。授信评审部按授信条件对单位统一进行授信，实行名单制管理。公职人员申请保证贷款及抵押、质押贷款的，按正常阳光信贷流程上报审批。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第六章  贷款流程', '第二十二条', '签约发卡、用信阶段授信获批后，经办行应及时通知已获授信的单位，逐户联系获得授信的客户，及时为授信客户办理易贷通卡和贷款用信手续。办卡和用信签约可由经办行指定两名及以上工作人员到客户所在单位现场办理，采集签约图片；也可根据客户意愿，由客户本人到营业网点办理。1.收集客户基本资料（包括身份证、结婚证等），签订借款合同、开卡申请书、手机自助放款协议等，每一环节均须拍照；图片打印后，现场服务工作人员应及时签字确认，作为重要信贷资料留存。2.各经办行客户经理逐个调查了解该单位人员基本情况，由客户填制《公职人员贷款申请、审批书》，并与借款人及配偶（无配偶可由家庭其他成员）签订《个人信用报告查询授权书》。3.签约后，客户经理应及时指导客户用卡及手机银行操作，及时查询借款人及配偶（或家庭其他成员）征信报告。无不良征信记录的，应及时录入相关信贷系统，确保客户3日后可直接通过手机银行自助放款或到柜面借款，无需再申报授信。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第六章  贷款流程', '第二十三条', '贷后检查阶段在客户用信后15日内，按首次贷款检查要求进行贷后检查。一年内必须对存量用信客户全部检查一遍。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第七章  风险防范', '第二十四条', '贷款发放后，公职人员因工作调动，且服务处所发生改变的，在合同期限（含展期）届满后必须收回贷款本息。如工作单位发生变动，属于跨区域调动（离开市），一般应在调动发生之日起一周内予以收回贷款本息（抵质押贷款以及本地籍借款人除外）。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第七章  风险防范', '第二十五条', '对于部分工资水平明显偏低、对外负债较多、社会信誉不高以及有明显恶习的公职人员，或工作年限较短、自身资信条件较差的个人，应根据具体情况予以考虑调减或取消贷款额度，坚持做到用信与还款能力相匹配。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第七章  风险防范', '第二十六条', '凡有下列情形之一的,不得向其提供用信或增加用信：（一）仍然在接受国家刑事、行政处罚，或受到有关部门党纪、政纪处分未满处分期的；（二）有过不良信用记录且属于其个人主观因素造成的，在申请贷款之前尚未清偿，或虽已清偿但造成恶劣影响；（三）在其履行职务过程中，对本行工作不支持、不配合，态度恶劣，或有其他间接的不良行为，已给本行的工作开展造成影响；（四）贷款用途与其岗位职责严重相悖、无指定用途或违反国家法律、法规的规定；（五）个人道德操守败坏，已有不良社会影响或社会评价较低的情形；（六）其他可能对本行债权带来不利影响的情形。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第七章  风险防范', '第二十七条', '公职人员对外担保原则上不得超过3户，对外担保金额合计不得超过本人的信用额度。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第七章  风险防范', '第二十八条', '对于公职人员贷款逾期（含展期后到期）的，除进行催收外，必要时还可告知其单位，通过道义劝告、纪律约束、协助扣划工资等其他方式进行催收。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第八章  尽职免责', '第二十九条', '业务经办人员必须严格遵守本办法以及本行《贷款业务尽职管理办法》，进行评议调查、授信、用信、签约发卡及贷后检查、催收的，实行尽职免责。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第九章  附则', '第三十条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第九章  附则', '第三十一条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《公职人员贷款管理办法及流程》', '第九章  附则', '第三十二条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第一章  总则', '第一条', '为进一步规范本行内部员工及近亲属贷款管理，完善内控制度，建立健全有效的信贷监督机制，防范操作及道德风险，促进信贷资产安全运行，特制定本办法。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第一章  总则', '第二条', '办理员工及近亲属贷款业务必须严格执行《中华人民共和国商业银行法》、《商业银行与内部人和股东关联交易管理办法》、《贷款通则》、《个人贷款管理暂行办法》和《商业银行股份有限公司信贷管理制度》等金融法律、法规和规章制度。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所称的内部员工及近亲属贷款（以下简称“员工贷款”）是本行向在编员工（不含已退休人员）及其近亲属发放的个人消费性贷款（经营性贷款按公司贷款、个人贷款管理办法执行）。本办法所称近亲属包括员工配偶、成年子女及其配偶、员工及配偶双方父母。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第一章  总则', '第四条', '员工贷款申请、调查、审查、会办、审批、发放、贷后检查等全流程应严格执行亲属回避制度。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第一章  总则', '第五条', '员工贷款应根据贷款管理责任制度的规定，落实贷款责任。造成风险和损失的，严肃追究责任。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '员工贷款集中营业部办理,其它经营单位不得擅自发放员工贷款（质押贷款以及总行同意的，可由受理机构办理）。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '授信评审部负责对员工贷款业务的授信管理。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定员工贷款管理办法，对员工贷款业务的用信管理和贷后检查管理的监督。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '运营管理部根据权限负责对员工贷款用款进行审查。第十条 风险管理部负责对员工贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核员工贷款办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '审计部负责对员工贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第三章  贷款对象和基本条件', '第十三条', '员工贷款申请对象必须为本行非试用期的在编员工或其近亲属。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第三章  贷款对象和基本条件', '第十四条', '申请员工贷款应具备以下基本条件：（一）贷款的用途正当、合理。购置商品房（按揭），须提供经房产管理部门备案的有效购房合同和首付款税务发票；购车须提供购车合同及相关凭证；自建房须提供自建房的相关批文手续；房屋装潢须提供本人（配偶）或子女房屋产权证、土地使用权证及装潢合同（合同可根据实际情况确定）；子女上学须提供入学通知书、缴费通知等缴费证明材料。员工近亲属确因自办实体、自主创业所需贷款的按本行贷款流程上报审批，但同样必须遵守本人回避原则和关系人贷款相关规定。（二）有稳定的家庭收入，能按期还本付息。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第三章  贷款对象和基本条件', '第十五条', '不得向下列员工及近亲属发放贷款：（一）近3年有重大不良贷款记录的；（二）本人或近亲属尚欠不良贷款本息的；（三）本人或近亲属所担保贷款形成不良未收回的；（四）人为责任贷款未处理完毕的；（五）本行认为其他不符合贷款条件的。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第三章  贷款对象和基本条件', '第十六条', '员工贷款的准入条件不得优于普通借款人，营业部（或经本行同意，明确办理贷款的属地支行）对员工贷款必须严格把关，严禁冒名、借户发放员工贷款，严禁贷款用于投资入股、炒股（基金）、民间借贷等，一经发现一律作违规贷款论处。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第四章  贷款期限、额度、利率、用途和还款方式', '第十七条', '员工贷款期限应根据员工的收入、经济状况、还款能力等情况综合确定，贷款期限不超过五年（按揭贷款除外），员工借款时年龄加贷款期限不得超过法定退休年龄（按揭贷款按有关规定执行）。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第四章  贷款期限、额度、利率、用途和还款方式', '第十八条', '员工贷款利率，参照本行公职人员利率定价办法执行。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第四章  贷款期限、额度、利率、用途和还款方式', '第十九条', '员工贷款用途主要为解决借款人工作、家庭生活需要，如购车、购置营业用房、建房、装修、子女上学等。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第四章  贷款期限、额度、利率、用途和还款方式', '第二十条', '员工贷款实行单笔管理，可采用质押、抵押、保证等方式，不得发放信用贷款。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第四章  贷款期限、额度、利率、用途和还款方式', '第二十一条', '还款方式。对于贷款期限在一年内（含）的，实行按季结息，到期还本的还款方式；贷款期限在一年以上的，实行等额本息或等额本金还款方式。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第四章  贷款期限、额度、利率、用途和还款方式', '第二十二条', '员工贷款实行按户发放，实行“一户一贷、余额控制”。贷款额度应根据申请人家庭资产负债水平、家庭收入、贷款用途、自有资金比例等因素综合确定，存单质押贷款不受此限。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第二十三条', '贷款受理及调查。客户经理在受理员工贷款申请时，应要求申请人提供员工及近亲属的基本信息、在他行及本行各营业网点的贷款情况，同时应履行尽职调查职责，对贷款申请内容和相关情况的真实性、准确性、完整性进行调查核实，形成调查评价意见。贷款调查包括但不限于对借款人、担保人的基本情况、资信状况、借款用途、偿还能力、贷款风险度、资料真实性及抵（质）押物的调查、核实。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第二十四条', '授信审查。贷款审查人应对贷款调查内容的合法性、合理性、准确性进行全面审查，重点关注调查人的尽职情况和借款人的偿还能力、诚信状况、保证人担保资格、抵（质）押比率、风险程度等。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第二十五条', '授信审批。本行领导班子成员及近亲属贷款在信贷审查委员会审议通过后报风险管理与关联交易控制委员会审议（批），并由授信评审部在批准之日起十个工作日内报告监事会。其他员工及近亲属贷款需报信贷审查委员会审批，并由授信评审部按季汇总上报风险管理与关联交易控制委员会备案。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第二十六条', '合同签订。签订书面借款合同时，借款人及其配偶、担保人均须到场，并依法合规办理相关手续；如系员工配偶、子女或双方父母承借的（除经营性贷款外），员工必须作为共同借款人或参与担保，同时需作出贷款如逾期由贷款人直接扣收员工报酬还款的承诺。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第二十七条', '贷款发放与支付。完备手续后，按合同约定发放贷款并严格按照支付管理要求进行支付。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第二十八条', '营业部客户经理应按照信贷档案管理的要求建档，保证档案资料的完整性、连续性、有效性。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第二十九条', '营业部客户经理在员工贷款发放后15日内必须进行首次贷后检查，重点检查是否按约定用途使用贷款，如发现转移用途、所提供相关证明材料不真实，应立即向信贷管理部书面回报，信贷管理部核实后提请人力资源部对相关员工作停岗处理；客户经理对发现的问题不及时回报的，查实后一并作停岗处理。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第三十条', '贷款到期如借款人未能按期归还本息的，从贷款到期的次月起，按月从员工月收入中扣收，即以每月全部收入扣除生活费（以市政府公布的最低生活费标准为依据）以外的全部收入用于归还所欠贷款本息，对逾期三个月仍未主动履行全部还款义务关联员工实行停岗清收。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第五章  业务操作流程规定', '第三十一条', '除在本行为员工贷款担保或为本行员工申请住房公积金贷款担保外，严禁员工对外（近亲属除外）进行担保。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第六章  附则', '第三十二条', '业务经办人员必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第六章  附则', '第三十三条', '本办法及流程未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第六章  附则', '第三十四条', '本办法及流程由信贷管理部负责制定、修改和解释。'),
(NULL, '信贷管理部', '《内部员工及近亲属贷款管理办法及流程》', '第六章  附则', '第三十五条', '本办法及流程经职代会通过后执行。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第一章  总则', '第一条', '为进一步加大支持“三农”力度，满足家庭农场、专业大户、农户等农业生产贷款需求，有效防范信贷风险，根据《中华人民共和国农村土地承包法》、《中华人民共和国物权法》、《农村承包土地的经营权抵押贷款试点暂行办法》和国家有关政策以及省联社信贷管理制度，特制定本办法。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第一章  总则', '第二条', '本办法所称农村承包土地的经营权是指经依法流转取得的农村集体所有的土地或原农户承包的土地经营权。本办法所称农村承包土地的经营权抵押贷款，是指本行向符合条件的承包方农户或农业经营主体发放的以借款人的农村承包土地的经营权作为抵押担保、在约定期限内还本付息的贷款。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第三条', '本行所辖的支行、部（以下简称“经办行”）按信贷管理规定，负责农村承包土地的经营权抵押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第四条', '授信评审部负责农村承包土地的经营权抵押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第五条', '三农业务部负责农村承包土地的经营权抵押贷款业务的宣传、推动及日常管理工作。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定农村承包土地的经营权抵押贷款管理办法及流程，对农村承包土地的经营权抵押贷款业务的用信管理和贷后检查管理。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对农村承包土地的经营权抵押贷款用款进行审查。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第八条', '风险管理部负责对农村承包土地的经营权抵押贷款风险的检测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第九条', '合规管理部负责审核农村承包土地的经营权抵押贷款管理办法和相关操作流程的合规性和有效性。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第二章  组织管理体系', '第十条', '审计部负责对农村承包土地的经营权抵押贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第三章  贷款对象、条件和用途', '第十一条', '贷款对象。农村承包土地的经营权抵押贷款对象为通过招标、拍卖、公开协商等方式依法取得土地承包经营权和通过合法流转方式获得承包土地的经营权的农户及农业经营主体。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第三章  贷款对象、条件和用途', '第十二条', '贷款条件。借款申请人除具备本行信贷管理制度规定的基本条件外，还应具备以下条件：（一）通过家庭承包方式依法取得土地承包经营权的农户以其获得的土地经营权作抵押申请贷款的，应符合以下条件：1.具有完全民事行为能力，无不信用记录；2.用于抵押的承包土地无权属争议；3.依法拥有县级以上人民政府或政府相关主管部门颁发的土地承包经营权证；4.承包方已明确告知发包方承包土地的抵押事宜；5.本行规定的其他条件。（二）通过合法流转方式获得承包土地的经营权的农业经营主体申请贷款时应符合以下条件：1.具备农业生产经营管理能力，无不良信用记录。从事粮食种植的，面积不少于100亩；从事其他种植、养殖的，面积不少于50亩；2.从事农业生产经营项目自有资金比例不低于30%；3.用于抵押的承包土地没有权属争议；4.已经与承包方或者经承包方书面委托的组织或个人签订了合法有效的经营权流转合同，或依流转合同取得了土地经营权权属确认证明，并已按合同约定方式支付了土地租金；5.承包方同意承包土地的经营权可用于抵押及合法再流转；6.承包方已明确告知发包方承包土地的抵押事宜；7.本行规定的其他条件。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第三章  贷款对象、条件和用途', '第十三条', '贷款用途。借款人获得的承包土地经营权抵押贷款，应主要用于农业生产经营等本行认可的合法用途。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第四章  贷款额度、期限和利率', '第十四条', '贷款额度。农村承包土地的经营权抵押贷款额度应根据借款人实际资金需求、还款能力以及抵押物的评估价值确定，同时不超过贷款期间农业生产经营项目投入资金的50%。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第四章  贷款额度、期限和利率', '第十五条', '贷款期限。抵押期限应小于或等于租赁合同期限，对合同约定按年缴纳租金的，每次贷款期限最长不超过1年，可采取“利随本清”、“按月（季）结息、到期还本”等方式。对合同约定一次性缴纳租金的，实际贷款期限可根据农产品生产周期的实际情况合理确定，一般为1年以内，最长不超过3年，实施分期还款，每年归还不得少于2次。对实际贷款期限超过租金缴纳期的，必须由借款人在本行开设预存租金专户，按季预存，具体标准由借贷双方共同协商。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第四章  贷款额度、期限和利率', '第十六条', '贷款利率。贷款利率按本行贷款利率文件执行。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第五章  抵押担保管理', '第十七条', '农村土地承包经营权可单独抵押，也可与其他资产抵（质）押、保证担保等进行组合担保。对采用农村土地承包经营权单独抵押的，鼓励其购买人身意外伤害保险。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第五章  抵押担保管理', '第十八条', '用于抵押的农村土地承包经营权应符合以下条件：（一）土地不改变农业用途，具备持续农业生产能力。（二）对以土地承包权作为股权入股流转的，需提供股份公司或者合作社章程；股份公司或合作社再流转的，应提供连带担保承诺。（三）对以转包、出租方式流转的，需取得农村土地承包经营权流转合同，受让方对流转的土地进行再流转的，应取得原承包方同意。（四）已投保农业保险的，保险期须覆盖借款存续期，同时在保险条款中明确本行为该项保险的第一受益人。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第五章  抵押担保管理', '第十九条', '下列土地经营权不得作为抵押物：（一）土地权属不清或存在纠纷的；（二）土地受到污染或毁损，无法进行正常农业生产的；（三）不能办理抵押登记的；（四）政府即将征收、征用的；（五）受其他形式限制的。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第五章  抵押担保管理', '第二十条', '抵押率。农村承包土地的经营权抵押贷款的抵押率确定应综合考虑农村土地承包经营权市场价格、权利变现难易程度、抵押贷款期限等因素确定，抵押率最高不超过农村土地经营权评估价值的60%。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第五章  抵押担保管理', '第二十一条', '农村土地承包经营权价值评估可采取委托第三方评估机构评估、本行自评或借贷双方协商等方式，必须公平、公正、客观、合理确定农村承包土地的经营权价值。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第五章  抵押担保管理', '第二十二条', '农村土地承包经营权抵押实行登记制度，登记部门为市农村产权交易中心。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第五章  抵押担保管理', '第二十三条', '借款人如变更抵押物，应向本行提出申请，经同意后办理相关抵押物变更手续。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第二十四条', '贷款申请。借款人申请农村承包土地的经营权抵押贷款业务，除提供本人及其配偶有效身份证明、居住证明等相应的基础资料外，还必须提供农村承包土地的经营权证。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第二十五条', '贷前调查。农村承包土地的经营权抵押贷款业务在执行相应业务品种调查内容的基础上，重点调查抵押物是否符合农业用途，承包或流转手续是否齐全合法，与登记部门核对抵押物是否真实有效，是否可以办理抵押登记，抵押物评估价值是否合理、是否易于变现，未来国家或地方政府的发展规划是否影响抵押物等。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第二十六条', '授信审查。授信审查人员应对客户资料和信贷调查（评估）资料进行审查。重点审查内容包括借款人生产经营状况、财务情况、市场评价情况和在其他金融机构的贷款状况以及贷款用途是否符合规定、抵押物流转手续是否齐全、抵押担保手续是否合法有效、抵押物市场价值是否合理且易变现等。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第二十七条', '合同签订。贷款经审批同意后，支行客户经理应与借款人、抵押人签订合同，向产权交易中心办理抵押登记。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第二十八条', '贷款审核。由放款审核岗负责审查和放款审核。审核的内容主要包括抵押是否已依法办妥登记、是否按规定办理保险等。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第二十九条', '贷款发放与支付。贷款发放应采用受托支付方式，对于无法确定交易对象、不具备有效使用非现金结算条件的，可按规定采用自主支付方式。贷款用于支付租金的，必须采取受托支付方式。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第三十条', '贷款展期。如遇到重大自然灾害或重大疫情导致贷款到期无法偿还，但仍具备正常生产经营能力的，在担保措施不丧失或不削弱的情况下，可申请办理展期。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第三十一条', '贷款归还。当借款人还清贷款后，借贷双方终止合同协议，向原登机关办理抵押注销手续。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第三十二条', '贷后管理。经办行应严格按照本行贷后管理规定进行贷后管理。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第三十三条', '建立风险预警制度和重大风险报告制度。在借款期限内发生下列情况的，要按规定及时预警和报告，并采取相应的风险控制和化解措施。（一）国家或地方政府的区域规划、土地调控发生变化，经营无法实现预期效益，可能对贷款产生不利影响的。（二）抵押物被改变农业生产用途、受污染损毁不适用农业生产等不再符合抵押规定，借款人又未能提供其他合法、足值担保的。（三）借款人产品市场需求下降、市场价格出现大幅度回落的。（四）出现重大自然灾害事件、病虫害或其他疫情，对农业生产产生重大影响的。（五）经营户连续两年弃耕抛荒等可能导致发包方收回土地的。（六）借款人涉及诉讼，可能影响贷款安全的。（七）其他导致贷款风险增加的情况。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第三十四条', '对办理农村承包土地的经营权抵押贷款不良率超过2%的经办行，暂停开办此类业务。清收后不良率降低至2%以下的，经总行验收批准后，方可恢复该项业务。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第三十五条', '在抵押期间内，未经本行同意，抵押人不得将抵押物再次抵押或流转。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第六章  贷款流程', '第三十六条', '抵押人死亡、依法被宣告死亡或被宣告失踪的，其抵押物的合法继承人或代管人应当继续履行原抵押合同。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第七章  抵押物处置', '第三十七条', '借款人在贷款到期（含分期）后，确实无力偿还的，应及时采取以下措施处置已抵押的农村土地经营权：（一）流转。在不损害原承包人利益的情况下，本行依据相关法律文件将抵押物通过产权交易中心挂牌流转，以流转收益清偿贷款本息。（二）协商。与村委会协商，由当地村集体经济组织或其他具有资质的合法中介组织按基准价格收购抵押的农村承包土地经营权，所得价款用于偿还贷款本息。（三）诉讼。农村土地经营权无法流转，或与村委会协商后达不到贷款清偿目的的，应依据合同约定，及时向人民法院提起诉讼。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第八章  附则', '第三十八条', '本办法所称的农村承包土地的经营权抵押贷款方式还包括以承包土地经营权抵押占主导作用的贷款、以承包土地经营权抵押占辅助作用的贷款、以承包土地经营权反担保的贷款、以后期追加承包土地经营权抵押的贷款等四种类型。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第八章  附则', '第三十九条', '业务经办人员必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第八章  附则', '第四十条', '业务授权、客户评级、授信等本办法未尽事宜按照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第八章  附则', '第四十一条', '本办法由信贷管理部负责制定、解释，并根据国家相关政策和产品试点情况进行修订完善。'),
(NULL, '信贷管理部', '《农村承包土地的经营权抵押贷款管理办法（暂行）》', '第八章  附则', '第四十二条', '本办法自印发之日起试行。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行动产质押贷款业务，根据《中华人民共和国担保法》、《流动资金贷款管理暂行办法》、《中华人民共和国物权法》，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关的信贷管理制度，特制定本办法。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称动产质押贷款指借款人（与出质人同一人）以其合法占有或具有处分权的动产,作为在本行贷款的担保，由本行指定专业监管公司进行监管，本行据此向借款人发放贷款的融资方式。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所称动产是指借款人合法占有或具有处分权的原材料、半成品和产成品。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第一章  总则', '第四条', '本办法将动产质押业务分为静态质押和动态质押两种模式。静态质押模式是指质押期间质物处于封存状态（不改变质物形态、数量和占有人），直至质物所担保的融资资金清偿后方可解除质押。动态质押模式是指借款人按约定提供质物，确保质物种类、质量、数量和价值等不低于约定限额或条件，本行或授权监管公司允许限额以上或符合条件的出库，在规定条件下可以其它动产替代质押动产。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第一章  总则', '第五条', '本办法适用于各支行、公司业务部、小微业务部（以下统称“支行”）。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '支行负责动产质押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '授信评审部负责动产押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定动产质押贷款管理办法及流程，对动产质押贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '运营管理部根据权限负责对动产质押贷款用款进行审查。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '风险管理部负责对动产质押贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核动产质押贷款管理办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '审计部负责对动产质押贷款进行审计稽核。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第三章  监管公司的确定', '第十三条', '动产质押统一授权专业监管公司进行监管，监管公司必须符合以下条件：㈠必须是本行认可的具有一定资质的专业监管公司，具有良好信誉和经济实力；㈡依法合规经营、管理严格，有完善的动产质押监管制度和流程，无未履行监管合同等不良记录；㈢如实向本行提供质物清单、管理台账、监管报告、保险等情况，积极实施货物的监控、管理等，配合本行对质物的处置；㈣与借款人无动产质押监管以外的业务往来且无关联关系；㈤本行规定的其他条件。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第三章  监管公司的确定', '第十四条', '合作的监管公司按信贷企业授信准入条件择优确定，并向分支机构公布。本行与监管公司的合作必须签订《商品融资专项合作协议》（附件1）。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第四章  借款、动产对象及条件', '第十五条', '借款对象。依法持有可流通、有价值的货物动产的本地企（事）业单位、其它经济组织，因生产、经营等资金需要，均可申请办理动产质押贷款。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第四章  借款、动产对象及条件', '第十六条', '借款人申请动产质押贷款，除具备本行相关信贷管理制度规定的基本条件外，还应具备下列条件：㈠本行确认的信用等级A级以上（含）企业; ㈡产权明晰、法人代表或主要经营者素质高，企业处于成长期或稳定期，具有质押物资经营资质，并经工商登记及相关部门许可；㈢对提供给本行的质物拥有所有权或处分权，必须提供质物的合法取得证明即物权证明（增值税发票、购销合同或协议、产成品入库单、运输部门运单或卖方送货单等）；㈣自愿承担监管费用和保险费用，为质押物办理足额财产保险，且在保险合同中约定本行为第一受益人；㈤自愿承担监管过程中发生的损耗（非人为因素）；㈥本行规定的其他贷款条件。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第四章  借款、动产对象及条件', '第十七条', '用于质押的动产应具备下列条件：㈠法律、法规允许流通的物品，所有权明晰且无法律纠纷；㈡质量符合国家相关标准和规范；㈢市场成熟，用途广，易变现；㈣市场价格稳定，波动小，不易贬值；㈤无形损耗小，不易变质，便于保存；对不熟悉的或监管要求高的动产应谨慎介入。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第五章  贷款额度、期限和利率', '第十八条', '贷款额度。在确保借款人正常生产经营能足额收回贷款本息的前提下确定每笔贷款的额度，静态质押最高按认可动产评估价值的70%确定，动态质押最高不超过认可动产评估价值的60%确定，且单户动产质押贷款总额不超过2000万元（不含）。贷款期限较长、动产价值不够稳定的应根据实际情况降低质押率。价值可以由借款人和贷款人双方约定的方式进行，也可由本行委托其认可的具有一定资质的专业中介评估机构。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第五章  贷款额度、期限和利率', '第十九条', '贷款期限。根据借款人的实际情况合理确定贷款期限，单笔最长期限不超过6个月。采用静态质押的，贷款到期日期必须在保质期前3个月到期；采用动态质押，借款人必须在质物保质期到期3个月前进行置换。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第五章  贷款额度、期限和利率', '第二十条', '贷款利率。按本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十一条', '动产质押贷款操作程序分为授信申请与受理、贷款授信、贷款用信、实施监管程序、签订合同、贷款发放、贷后管理、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十二条', '授信申请与受理。对借款人提出的申请，由支行负责受理，并要求其按《流动资贷款操作规程》提供借款人资料，同时还应提供下列材料，且对所提供资料的真实性和合法性负责：（一）前两个年度财务报表及近期财务月报表；（二）经营资质证明文件（营业执照或经营许可证等）；（三）质物的物权证明（增值税发票、运输部门运单或卖方送货单等）；（四）公司董事会或股东会按章程规定同意办理动产质押贷款的决议文件；（五）本行认为需要提供的其他资料。支行在接到贷款申请后，按照本行流动资金贷款管理办法的要求，对借款人进行初步调查，调查内容主要包括客户法定代表人和财务负责人的品行、敬业精神以及客户的行业地位、诚信情况，重点调查有无不良记录，决定是否受理。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十三条', '贷款授信调查。对同意受理的申请人，应及时对申请人的有关情况进行全面深入调查，调查的主要内容有：㈠借款人提供的货物（动产）是否真实有效，有无损坏、变质，是否存在争议或法律纠纷；㈡借款人提供的资料是否齐全、真实、有效；㈢借款人资产、负债和经营效益的真实性，第一还款来源的充足性；㈣借款人提供的货物（动产）的合法性以及价值是否充足；㈤质物产权依据（增值税发票、运输部门运单或卖方送货单等）的真实性、合法性；㈥查询借款人在本行和他行的贷款履约情况；㈦货物（动产）是否可流通，市场需求及价格走势如何。客户经理必须查清借款人在其他金融机构是否存在动产抵（质）押贷款，严防同一动产重复抵（质）押现象的发生。客户经理认定调查内容属实后，在借款申请审批表和《动产质押授信监管审批表》（附件2）上签注调查意见。支行初审同意授信的，支行应邀请监管公司一起赴现场考查。考查的内容包括质物存放环境是否符合要求，质物是否便于管理和清点验收，质物种类、质量、数量和价值等是否达到约定条件和限额。存放环境和质物同时符合条件的，由监管公司在《动产质押授信监管审批表》签署监管意见。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十四条', '贷款授信审查、审批。授信审查应着重审查以下内容：㈠借款人主体资格、贷款投向是否符合国家有关政策及本行规定；㈡借款人经营是否稳健，内部管理水平如何；㈢资料是否齐全；㈣动产是否符合规定条件，质押率是否适当；㈤保管、监管方式是否妥当；㈥借款人的第一还款来源是否充足。贷款授信审批按《转授权管理办法》执行。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十五条', '贷款用信的审查与审批。用信审查应着重审查以下内容：㈠对客户提供的用信资料完整性、有效性进行审查核实，分析其是否符合法律法规和本行的相关信贷政策；㈡查验授信文件的有效性；㈢客户经营现状、质物变化是否满足用信条件，有无在授信日后发生重大不利变化；㈣查验授信时存在的限制性条款有无落实到位。贷款用信审批按《转授权管理办法》执行。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十六条', '实施监管程序。贷款批准后，按下列程序实施监管：㈠签订《动产质押监管协议》。本行、监管公司和借款人协商一致后，由三方共同签订《动产质押监管协议》（附件3-1或3-2）。对于非监管公司授权名单的下属企业或超出授权范围的项目，监管公司还应向本行出具《质押监管项目审批通知书》（附件4）。㈡进场清点、验收质物。进场前，客户应出具《出质通知书》（附件5-1或5-2），随后支行客户经理和借款人、监管公司三方一起清点质物，验收完毕，监管公司应签发《出质通知书（确认回执）》（附件5-3），质物无误的同时填写《质物验收暨接管清单》（附件6），三方签字盖章确认，其中质物单价根据购物发票、购货合同或市场价孰低原则确定，以最低的价格为准。监管公司依据《质物验收暨接管清单》签发《动产质物清单》（附件7-1或7-2）。㈢办理质物财产保险。质物须办理以本行为第一受益人的财产保险后方可发放贷款，金、银等贵重物资作为质物的，须追加投保盗抢险。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十七条', '签订合同。借款人须与支行签订借款合同和动产质押合同，公司全体股东或董事会成员还应与本行签订连带保证合同。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十八条', '质押登记。质押需要登记的，支行客户经理应与借款人一同到登记部门办理质押登记手续，取得质押登记证。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十九条', '贷款发放和使用。收妥动产质物清单并办妥其它手续后，支行按照合同约定立据发放贷款，将资金划入借款企业的结算账户，并严格监督借款企业按合同约定支付方式支付贷款资金。大额用款由运营管理部根据权限审查。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十条', '贷款发放后，应按照本行信贷管理相关制度办法进行贷后管理。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十一条', '贷款归还。借款人归还贷款后，支行应及时签发《提货通知书》（附件8），注明出库货物名称、规格/型号、生产厂家、出货数量（重量）、价值等内容。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十二条', '质物的交接。对所有质押给本行的质物，应由支行客户经理、专业监管人员共同与借款人办理质物交接手续，按质物交接清单内容共同进行清点，有条件的必须给质物加注封条，无条件的要放置质押标签，并在质物验收暨接管清单上签章确认，清单应注明质物品名、规格/型号、入库数量、存放地点和保质期等。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十三条', '质物的存放。质物可存放于经本行指定的仓库，也可租赁借款人部分仓库作为本行质物仓储、监管的专用场地。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十四条', '质物保管方式。针对企业运作和质物属性的不同，实行分类保管，分为静态保管和动态保管。方式的选择由本行根据上述因素分析后，在《动产质押授信监管审批表》上审批确定。动态监管的，质物的价格需经支行定期确认，每月1日（有条件的实行每旬）以《质物单价调整通知书（适用动态质押）》（附件9）的形式，支行书面通知监管人员进行调整。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十五条', '对于租赁借款人部分仓库存放本行质物的，应由监管公司与借款人签订《租赁及监管协议》（附件10）。协议的租赁内容应明确租赁标的物、租赁期限、租赁费用，明确由监管公司代表本行对质物进行监管，并要求监管公司在租赁库区四周设置隔离物悬挂显目的监管质押标志，借款人应为监管人员提供出入厂区、仓库的一切便利。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十六条', '质物的退还。根据借款人请求，动产质押贷款归还后，退还全部质物；部分归还贷款的，准予退还的质物数量及价值按贷款同比例计算且存量贷款与存量动产价值之比不低于约定质押率。出库时，须办理相应的出库手续。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十七条', '质物出库（非置换货物），须凭支行客户经理、支行负责人共同签字的《提货通知书》，同时附交相应的本息还款证明或保证金入账凭证（须止付），方可按规定程序办理出库手续，经借款人签字交接。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十八条', '质物的退还和置换方式。质物的退还和置换采用以下两种操作：㈠静态质押下的退还：指在质押期间质物处于封存状态，直至质物所担保的贷款清偿为止。采用静态质押贷款的，贷款清偿后，支行签发《提货通知书》，监管公司凭此单放行货物，同时解除监管责任。㈡动态质押下的退还和置换：为方便借款人正常生产经营活动，采用对质物实行动态活封，质押期间质物在三方设定条件和下限内按约定方式置换、流动，“补新出旧”，贷款归还后按约定办理相应手续后出货。具体约定方式分三种：一是还款提货，具体操作方式同静态质押。二是约定条件下的置换：企业生产经营过程中需要对质物进行调整置换的，在符合合同和《质物种类、价格、最低要求通知书》约定条件下，按照“等值交换、先进后出”原则办理出入库，先由支行或授权监管公司审查确认《质物置换同意书》（附件11-1或11-2），然后监管公司、借款人对置换待入库质物进行进仓清点确认并办理相关货物的出库。支行审查确认《质物置换同意书》，客户经理也应参加上述工作。验收完毕后，监管公司应向出质人开具《质物置换清单》（附件12），并及时登记相应账册。（三）追加保证金（即打款赎货）：企业生产经营过程中如需提取部分质押货物的，按照质押合同约定，应先在约定保证金账户交存保证金并办理止付后，由支行签发《提货通知书》，监管公司凭单放行通知书列示货物。质物的置换超出原合同约定范围的，须重新按本行《转授权管理办法》授信、用信，并重新签订合同，质押合同必须和监管协议内容保持一致。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第三十九条', '代保管物资的记账。监管公司对所有质物均需建立总账和明细账，货物分类按品名、规格/型号以及单价相同或相近者分类。支行客户经理应至少每月盘点一次质物和核实价值以及计算当前质押率，并按月上报《动产质押物资盘查月报表》（附件13）。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十条', '借款人应支付监管费用。监管费用由借款人与监管公司自行商定费用标准，并按约定支付。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十一条', '对质物保质期届满的质物，监管公司应在保质期届满前90日通知贷款支行，同时通知借款人及时进行更换。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十二条', '质押物资验收，应抽取一定比例的样品送交有权部门检测，采用随机抽样方式。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十三条', '除经本行明确同意外，三方监管协议必须明确由监管方对质押物资的数量、品质等承担责任，责任不局限于质物名称一致、包装完整、数量相符，还包括实物质量、规格/型号与约定相符。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十四条', '支行负责对质物价值的控制，密切注意质物的市场价格。对质物市场价格发生重大变动，导致质物价值大幅度下降的（下降10%以上），应及时要求借款人补充质物或提供本行认可的其他担保。对借款人拒绝补充质物或提供本行认可的其他担保的，支行应要求借款人提前归还相应数额的贷款，直至达到出质时约定的质押率。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十五条', '监管人员应根据《出质通知书》、《质物置换同意书》等认真核实质物，及时确认。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十六条', '监管人员应定期对质物进行清点，登记查库记录。发现有损质物的情况，应积极采取避免损失的措施，同时立即将有关情况向贷款支行反映。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十七条', '监管公司应严格出入库手续，并要求借款人有序堆放质物。未经本行书面同意不得将质物出库，确保质物不出现丢失、错发、短缺等情况，保证质物的安全。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第七章  质物的管理', '第四十八条', '一旦出现违约情况,如缺货少物、突破保全下限、强行提货等现象的，监管人员必须及时提出整改意见，同时将有关情况填写《动产质押违规操作情况报告表》（附件14），立即报告本行，以便本行及时采取措施控制风险。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第八章  附则', '第四十九条', '本办法及流程由信贷管理部负责制定、修改和解释。'),
(NULL, '信贷管理部', '《动产质押贷款管理办法及流程》', '第八章  附则', '第五十条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行单位定期存单质押贷款业务，根据《中华人民共和国商业银行法》、《中华人民共和国物权法》、《单位定期存单质押贷款管理规定》和本行《信贷管理制度》、《贷款业务尽职管理办法》等规定，特制定本办法。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称单位定期存单质押贷款，是指借款人以本行所辖支行、营业部（以下简称“存款行”）签发的未到期的单位定期存单，以及与我行有合作协议的其他银行（以下简称“他行”）签发的未到期的单位定期存单作质押，从本行所辖支行、小微业务部、公司业务部（以下简称“贷款人”）获得人民币贷款的一种贷款方式。出质人可以是借款人本人，也可以是自愿出质的第三人。本办法所指的借款人是经有权部门核准登记的企(事)业法人、其他经济组织、个体工商户或具有合法身份证件、在本行辖区内有有效居住证明（含暂住证）的具有完全民事行为能力的自然人。本办法所称单位包括企业、事业单位、社会团体以及其他组织。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所称单位定期存单，是指借款人为办理质押贷款而委托贷款人依据开户证实书向存款行申请开具的人民币单位定期存单，或与我行有合作协议的其他银行签发的未到期的单位定期存单。单位定期存单只能以质押贷款为目的开立和使用。单位在存款行办理定期存款时，存款行为其开具的《单位定期存款开户证实书》不得作为质押的权利凭证。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第一章  总则', '第四条', '单位定期存单质押贷款活动应当遵守国家法律、行政法规，遵循平等、自愿、诚实信用的原则。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '贷款人承担单位定期存单质押贷款的受理、“三查”实施及相关资料的保管。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '授信评审部负责单位定期存单质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '信贷管理部负责管理办法和流程的制定，承担用信的审查、审批及贷后检查的管理监督。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '运营管理部负责对单位定期存单质押贷款用款进行审查。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '风险管理部负责对单位定期存单质押贷款业务风险的识别、评估、计量、监控，并独立评估提交风险审查报告。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核管理办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '审计部负责对单位定期存单质押贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第三章  客户对象和基本条件', '第十二条', '客户应当是经有权部门核准登记的企(事)业法人、其他经济组织、个体工商户或具有合法身份证件、在本行辖区内有有效居住证明（含暂住证）的具有完全民事行为能力的自然人。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第三章  客户对象和基本条件', '第十三条', '客户申请单位定期存单质押贷款应当具备下列基本条件：（一）从事符合国家产业政策和社会发展规划要求的建设和生产经营活动；（二）借款用途明确、合法；（三）有合法稳定的收入或收入来源，具有按期还本付息能力；（四）在本行开立基本账户、一般存款账户或结算存款账户。借款人为自然人的应在本行开设个人结算账户或办理银行卡。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第十四条', '借款人办理单位定期存单质押贷款，除按本行有关规定提交各种文件、资料外，还应向贷款人提交下列文件、资料：（一）借款人为法人客户，应提供与原件一致的营业执照或事业法人登记证复印件，借款人为自然人的应提供与原件核对一致的身份证复印件；（二）开户证实书，包括借款人所有的或第三人所有而向借款人提供的开户证实书；（三）存款人委托贷款人向存款行申请开具单位定期存单的委托书(附件2)；（四）存款人在存款行的预留印鉴或密码；（五）借款以及出质的有效授权书；（六）本行要求的其他资料。开户证实书为第三人向借款人提供的，应同时提交第三人同意向借款人为质押贷款目的而使用其开户证实书的协议书或承诺书（附件3）。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第十五条', '贷款人审查同意借款人的贷款申请的，应将开户证实书和开具单位定期存单的委托书一并提交给存款行，向存款行申请开具单位定期存单和确认书（附件4）。贷款人经审查不同意借款人的贷款申请的，应将开户证实书和委托书及时退还给借款人。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第十六条', '存款行收到贷款人提交的有关材料后，应认真审查开户证实书是否真实，存款人与本行是否存在真实的存款关系，以及开具单位定期存单的申请书上的预留印鉴或提供的密码是否和存款人在存款时预留的印鉴或密码一致。必要时，存款行可以向存款人核实有关情况。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第十七条', '存款行经过审查认为开户证实书证明的存款属实的，开具单位定期存单、保留开户证实书、委托书、第三人同意由借款人使用其开户证实书的协议书或承诺书。存款行不得开具没有存款关系的虚假单位定期存单或与真实存款情况不一致的单位定期存单。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第十八条', '存款行在开具单位定期存单的同时，应对单位定期存单进行确认，确认后认为存单内容真实的，应在出质承诺书（附件1）上进行记载并签章。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第十九条', '存款行对单位定期存单进行确认的内容包括：（一）单位定期存单所载开立机构、户名、账号、存款数额、存单号码、期限、利率等是否真实准确；（二）借款人提供的预留印鉴或密码是否一致；（三）需要确认的其他事项。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第二十条', '存款行经过审查，发现开户证实书所载事项与账户记载不符的，不得开具单位定期存单，并及时告知贷款人，认为有犯罪嫌疑的，应及时向司法机关报案。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第二十一条', '贷款人不得接受未经确认的单位定期存单作为贷款的担保。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第二十二条', '贷款人对质押的单位定期存单及借款人或第三人提供的预留印鉴和密码等应妥善保管，预留密码的要取消凭密支取。因保管不善以及操作不当造成其丢失、毁损或泄密的以及贷款损失的，由贷款人承担责任。质押存续期间，除法律另有规定外，任何人不得擅自动用质押存单。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第四章  单位定期存单的开立与确认', '第二十三条', '以他行签发的单位定期存单质押，按照我行与他行签订的《合作协议》办理。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第五章  贷款额度、期限、利率和还款方式', '第二十四条', '贷款金额。期限一年内，贷款额度一般不超过质押物的90%，期限一年以上，贷款额度一般不超过质押物的80%。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第五章  贷款额度、期限、利率和还款方式', '第二十五条', '贷款期限。由借款人、出质人、贷款人协商确定，最长不超过3年，贷款到期日不得超过质物的到期日。若质物为多张、到期日间隔时间较长的，以距离到期日时间最近者确定贷款期限，或质押贷款采取分笔发放，确保贷款到期日与质物到期日符合前款规定。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第五章  贷款额度、期限、利率和还款方式', '第二十六条', '贷款利率。按本行贷款利率定价管理办法执行。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第五章  贷款额度、期限、利率和还款方式', '第二十七条', '在贷款到期日前，借款人可申请展期。办理展期应当根据借款人资信状况和生产经营实际需要，按审慎管理原则，合理确定贷款展期期限，同时，由借款人、出质人、贷款人共同办理相关展期手续。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第五章  贷款额度、期限、利率和还款方式', '第二十八条', '还款方式。公司客户实行按月结息，到期还本方式；个人客户实行利随本清或期间内按季结息，到期利随本清的方式。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第二十九条', '单位定期存单质押贷款操作程序：借款人申请，授信审查、审批，用信审查、审批，合同文件的签订，贷款发放与支付，贷后管理，贷款收回和档案管理。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十条', '借款人申请单位定期存单质押贷款，应向贷款人提供贷款金额、用途、期限、还款来源、质押物等基本情况，并按本行《信贷管理制度》及操作规程的要求提供资料。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十一条', '贷款人接到借款人的申请后，进行尽职调查。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十二条', '贷款人应对贷款调查内容的合法性、合理性、准确性进行全面审查，重点关注借款人的偿还能力、诚信状况、质押比率、风险程度等。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十三条', '贷款授、用信审批，按本行《转授权管理办法》规定执行。他行签发的单位存单必须双人到存单签发行进行查询，办理冻结止付，并且要求出质人出具贷款发生违约的扣划授权书。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十四条', '按本行《信贷业务操作规程》有关条款规定，签订业务合同。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十五条', '按本行《信贷业务操作规程》有关条款规定，进行贷款的发放、支付审核与柜面支付'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十六条', '质押品处理。（一）柜员接到押品和《质押品出账通知书》后，应认真检查质押品是否收集齐全，同时填制《抵、质押品保管凭证》由客户签章。在《抵、质押品保管凭证》上加盖业务章，收据联交客户，质押品和《抵、质押品保管凭证》（管理卡联）交后台管理并登记表外登记簿。（二）根据《放贷通知书》，柜员应对质押贷款手续认真进行审查，审查要素是否齐全、利率执行是否符合规定、大小写是否一致、签字审批是否齐全等，审核无误后，打印借据交由客户签章或签字加盖指模，进行贷款发放并按支付管理要求进行支付。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十七条', '贷款收回。贷款期满借款人履行债务的，或者借款人提前偿还所担保的贷款的，柜员进行还本还息处理，收妥款项后，收回出质人提交的《抵、质押品保管凭证》（收据联），进行质押品注销处理，同时将质押的单位定期存单退还存款行。存款行收到退回的单位定期存单和结清贷款本息凭证后，应将开户证实书退还贷款人并由贷款人退还借款人。将开户证实书退还客户，并及时办理登记注销手续。以他行签发的单位定期存单质押，发生违约本行应按照出质人授权书等相关规定解除止付并且扣划还贷;按期归还结清贷款的，本行应通知存单签发行解除止付。若还款时出质人未提交《抵、质押品保管凭证》（收据联），则不得进行“质押品注销”的操作，质押品也不取出，以后出质人可凭《抵、质押品保管凭证》（收据联）办理注销、领取手续。如出质人将《抵、质押品保管凭证》（收据联）遗失，由出质人、借款人共同出具书面证明，并凭证明按照储蓄挂失的要求和手续办理取回质押存单。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十八条', '客户经理是存单质押贷款发生后管理的责任人，负责客户贷款业务发生后的日常管理。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第三十九条', '短期质押贷款到期应提前15天、中期质押贷款到期应提前30天通知借款人。质押贷款逾期或发生欠息等违约行为，经向借款人、出质人催收仍不还款的，按合同约定及出质人承诺（授权书），贷款人可兑付存单归还贷款本息。贷款收回后，应相应进行质押物注销处理，销记表外登记簿。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第四十条', '资料管理。办理质押贷款涉及的《出质承诺书》、《抵、质押品保管凭证》等应按本办法的规定和注明的用途使用，并作为相应业务凭证的附件订入当日传票。《质押担保借款合同》和《贷款到期通知书》、《逾期贷款催收通知书》、《担保人履行责任通知书》的回执等文本资料应作为重要信贷档案由客户经理按月整理装订归档，按档案管理规定的期限保管。对以他行存单质押的贷款，相关资料应一户一档，专夹保管。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第四十一条', '单位定期存单质押担保的范围包括贷款本金和利息、罚息、损害赔偿金、违约金和实现质权的费用。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第四十二条', '有下列情形之一的，贷款人可依中国银行业监督管理委员会《单位定期存单质押贷款管理规定》的规定或质押合同约定的方式处理质押的定期存单清偿贷款：（一）质押贷款合同期满，借款人未按期归还贷款本金和利息的；（二）借款人或出质人违约，贷款人需依法提前收回贷款的；（三）借款人或出质人被宣告破产或解散的；（四）借款人死亡而无继承人履行合同的。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第四十三条', '有第四十条所列情形之一的，贷款人可按合同规定以单位定期存单兑现或以法律规定的其他方式处分单位定期存单。以单位定期存单兑现时，贷款人应向存款行提交单位定期存单和贷款主从合同、借据以及相关的承诺、协议书等。以他行存单质押的，按照我行与他行签订的《合作协议》办理。单位定期存单处分所得不足偿付第四十条规定的款项的，贷款人应当向借款人另行追偿；偿还第四十条规定的款项后有剩余的，其超出部分应当退还出质人。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第四十四条', '用于质押的单位定期存单在质押期间丢失或毁损的，贷款人应立即通知借款人和出质人，并申请挂失补办。质押期间，未经贷款人同意，存款行不得受理存款人提出的挂失申请。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第六章  贷款操作程序', '第四十五条', '出质人合并、分立或债权债务发生变更时，贷款人仍然拥有单位定期存单所代表的质权。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第七章  附则', '第四十六条', '本办法及流程由信贷管理部负责制订、解释和修改。'),
(NULL, '信贷管理部', '《单位定期存单质押贷款管理办法及流程》', '第七章  附则', '第四十七条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第一章  总则', '第一条', '为推进本行贷款方式创新，完善信贷业务产品结构，防范和化解信贷风险，根据《中华人民共和国担保法》、《中华人民共和国物权法》、《中华人民共和国商标法》以及相关法律法规，及本行《信贷管理制度》、《贷款业务尽职管理办法》，并结合本地区实际，特制定本办法。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第一章  总则', '第二条', '商标专用权质押贷款，是指本行向借款人发放并以借款人或者第三人（下称“出质人”）合法所有的注册商标专用权作为质押担保的贷款。借款人不按期归还到期贷款时，本行有权按照商标专用权质押合同的约定以质物折价或者以拍卖、变卖质物所得的价款优先受偿。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第一章  总则', '第三条', '商标专用权质押担保管理目的是通过建立健全商标专用权质押担保管理制度，完善商标专用权质押担保手续，强化贷后管理，努力实现担保债权，提高贷款的安全性、流动性和效益性。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第一章  总则', '第四条', '商标专用权质押贷款担保的范围应当在借款担保合同中明确，包括借款合同项下的借款本金、利息、复利、罚息、违约金、赔偿金、实现债权的费用（包括诉讼费、律师费等）和其他所有应付费用。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第一章  总则', '第五条', '本办法所称商标专用权质押系本行所辖公司业务部为法人客户提供的贷款业务。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '公司业务部负责商标专用权质押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '授信评审部负责商标专用权质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定商标专用权质押贷款管理办法及流程，对商标专用权质押贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '运营管理部根据权限负责对商标专用权质押贷款用款进行审查。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '风险管理部负责对商标专用权质押贷款风险的监督、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核商标专用权质押贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '审计部负责对商标专用权质押贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第三章  贷款的对象、条件和用途', '第十三条', '贷款对象。符合《商业银行股份有限公司信贷管理制度》规定的基本条件，并持有《商标注册证》的法人企业。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第三章  贷款的对象、条件和用途', '第十四条', '贷款条件。一、借款人商标专用权应符合以下要求：㈠取得国家商标局依法核定的《商标注册证》；㈡获得中国驰名商标或获得江苏省著名商标；㈢商标注册证书须在有效期限内；㈣商标未经转让、注销或撤销。㈤商标专用权无争议。借款应符合以下条件要求：㈠出质人必须是该商标专用权的合法所有人；㈡出质的注册商标具有较高知名度，企业须前三年连续盈利，具有一定的生产经营规模，有稳定的产品或经营市场，在同行业中有较强的竞争力；㈢商标专用权的价值须经专门的商标评估机构确认；㈣商标专用权法律手续齐全，无任何权利瑕疵；㈤借款人恪守信誉，有还本付息的能力；㈥借款人、出质人财务制度健全，年度会计报告经会计事务所审定；㈦注册商标专用权质押期间，借款人未经本行书面同意，不得以任何形式处置质押的注册商标专用权；㈧本行规定的其他条件。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第三章  贷款的对象、条件和用途', '第十五条', '有下列情况之一，不能申请商标专用权质押贷款：㈠商标申请未获得商标专用权的；㈡商标专用权被提出撤销请求或已终止的；㈢商标专用权已过有效期或贷款期限超过商标专用权有效期的；㈣商标专用权存在纠纷的（包括民事纠纷、行政纠纷）；㈤商标专用权归属不明确的；㈥商标专用权已转让的；㈦其他不符合法律法规规定的。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第三章  贷款的对象、条件和用途', '第十六条', '贷款用途。借款只限用于生产流动资金贷款和承兑业务。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第四章  贷款的额度、期限、利率', '第十七条', '贷款额度。商标专用权质押贷款的额度不超过商标评估价值的50%。商标评估价值的确定，可由借款人委托本行认可的有资质的中介机构评估。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第四章  贷款的额度、期限、利率', '第十八条', '贷款期限。商标专用权质押贷款期限不超过三年，且不能超过商标专用权的有效期限。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第四章  贷款的额度、期限、利率', '第十九条', '贷款利率。根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十条', '商标专用权质押贷款操作程序分为客户申请、受理与调查、授信审查审批、用信审查审批、合同签订、发放与支付、贷后管理等阶段。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '借款人申请商标专用权质押贷款，除按本行《流动资金贷款管理办法》等制度要求提供相关资料外，还需提交拟质押注册商标专用权的《商标注册证》复印件等资料。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '对符合条件的申请人，公司业务部在受理后应对借款人的主体资格、借款用途、偿贷能力、还款意愿、拟质押商标使用（尤其是许可使用）情况及《商标注册证》的真实性与有效性、实现质权的可靠性等进行调查核实。并向当地工商行政管理局或上网查阅相关资料。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '公司业务部在办理商标专用权质押贷款业务时，应预测和评估注册商标专用权质押贷款可能存在的市场风险，合理设定注册商标专用权质押贷款的金额、期限、利率，确保贷款安全。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '商标专用权质押贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。特别提示：《商标法》规定，已经注册的商标，违反商标法有关规定的，商标局依法可以撤销。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '公司业务部根据贷款批复和借款人签订质押借款合同，合同签订之日起15日内，填报《商标专用权质押登记申请书》与借款人、抵押人一同向国家工商行政管理总局申请办理注册商标专用权出质登记手续，也可委托商标代理机构办理。登记手续办理完毕取得《商标专用权质押登记证》和借款人提交的《商标注册证》后，本行按合同约定和本行信贷管理规定发放贷款，将资金划入借款人结算账户，并严格监督借款人按合同约定支付方式支付贷款资金，不符合受托支付条件的应采用自主支付方式。运营管理部根据权限负责对商标专用权质押贷款用款进行审查。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '借款人按合同规定偿还全部贷款本息后，贷款合同终止，支行应将质押物权属证明交还出质人，并与出质人向原登记机关办理注销登记。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '商标专用权质押贷款发放后，支行要加强贷后跟踪检查，密切关注企业的生产经营活动，要检查贷款是否按合同用途使用，检查借款人的财务状况、经营情况和偿贷能力、影响质押注册商标专用权的市场价值因素等，并撰写贷后检查报告。若出现借款人违约的，应及时将借款人违约情况书面报告相关管理部门，并采取相应措施，切实防范、化解贷款风险。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '因主债权消失、质权实现或法律规定的其他原因导致质权消灭的，出质人与贷款人应当及时到国家工商行政管理总局申请办理注册商标专用权质押注销登记。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '借款合同约定的还款期限届满，借款人到期未履行还款义务或者发生当事人约定的实现质权的情形，本行可以依法行使质权，拍卖或变卖质押的注册商标专用权，并从所得价款中优先受偿。优先受偿后，有剩余金额的，退交借款人；不足以偿还借款本息的，对不足部分本行有权向借款人追偿。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '档案管理。按本行信贷档案管理有关规定收集、保管相关资料。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第五章  贷款操作程序', '第三十一条', '出质登记事项发生变更以及因主债权债务转移或者其他原因而发生质押转移的，应当及时向国家工商行政管理总局申请办理注册商标专用权变更登记、补充登记或者重新登记手续。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第六章  附则', '第三十二条', '本办法及流程由信贷管理部负责制订、解释和修改。'),
(NULL, '信贷管理部', '《商标专用权质押贷款管理办法及流程》', '第六章  附则', '第三十三条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第一章  总则', '第一条', '为规范本行固定资产贷款业务经营行为，加强固定资产贷款审慎经营管理，促进固定资产贷款业务健康发展，防范固定资产贷款经营风险，依据《中华人民共和国商业银行法》、《贷款通则》、《固定资产贷款管理暂行办法》、《项目融资业务指引》和《江苏省农村信用社固定资产贷款管理实施办法》，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等信贷制度，制定本办法。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第一章  总则', '第二条', '本办法所称固定资产贷款，是指本行为解决企业固定资产投资活动的资金需求而发放的贷款，主要用于固定资产项目的建设、购置、改造以及相配套设施建设的中长期人民币贷款。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第一章  总则', '第三条', '本行开展固定资产贷款业务遵循依法合规、审慎经营、平等自愿、公平诚信的原则。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第一章  总则', '第四条', '本行将固定资产贷款纳入对借款人及借款人所在集团客户的统一授信额度管理，并按区域、行业、贷款品种等控制固定资产贷款的风险限额。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第一章  总则', '第五条', '固定资产贷款项目的选择必须符合国家产业政策和金融政策，向有利于农村经济持续、快速、健康发展和农村各项社会事业全面进步的基础产业、支柱产业以及具有较大竞争力和发展潜能的新兴产业倾斜。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第一章  总则', '第六条', '本行应与借款人约定明确、合法的贷款用途，约定贷款发放条件、支付方式、接受监督以及违约责任等事项，并按照约定检查、监督贷款的使用情况，防止贷款被挪用。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '公司业务部、小微业务部（以下统简称“经办行”）经办行负责固定资产贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '授信评审部负责对固定资产贷款业务的授信管理。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '信贷管理部负责拟定固定资产贷款管理实施细则及固定资产贷款业务操作规程，对固定资产贷款业务的用信管理和贷后检查的监督。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '运营管理部根据权限负责对流动资金贷款用款进行审查、支付。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '资产保全部负责不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '风险管理部负责对固定资产贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第十三条', '合规管理部负责审核固定资产贷款管理实施细则及固定资产贷款业务操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第二章  组织管理体系', '第十四条', '审计部负责对固定资产贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第三章  贷款的对象与条件', '第十五条', '固定资产贷款的对象是：经工商行政管理机关或主管机关核准登记，实行独立核算的企业法人、事业法人或其他经济组织。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第三章  贷款的对象与条件', '第十六条', '借款人向本行申请固定资产贷款，应同时具备以下条件：（一）借款人依法经工商行政管理机关或主管机关核准登记，项目必须经相关有权部门批准，取得有权部门的立项批文；（二）借款人信用状况良好，无重大不良记录；（三）新设项目法人的，其控股股东同时应有良好的信用状况，无重大不良记录；（四）在本行设立基本账户或一般存款账户，提供近3年来的资产负债表、损益表（利润表）、现金流量表，新成立的提供成立以来的财务报表；（五）项目的投资主体资格和经营资质符合国家规定要求；本行要求房地产开发企业须连续经营三年以上，市区房地产开发企业的资质等级二级（含）以上，乡镇可适当放宽至三级（含）以上；（六）明确、合法的贷款用途和充足可行的还款来源；（七）项目符合国家的产业、土地、环保等相关政策，并按规定履行了固定资产投资项目的合法管理程序；（八）符合国家有关投资项目资本金制度的规定。经办行要根据借款主体和项目实际情况，执行好国家规定的最低资本金比例制度要求，对资本金的真实性、投资收益和贷款风险进行全面审查和评估。根据国务院《关于调整和完善固定资产投资项目资本金制度的通知》，目前有关行业投资项目资本金比例最低标准如下：1.城市和交通基础设施项目：城市轨道交通项目最低资本金比例为20%，港口、沿海及内河航运、机场项目最低资本金比例为25%，铁路、公路项目最低资本金比例为20%。2.房地产开发项目：保障性住房和普通商品住房项目最低资本金比例为20%，其他项目最低资本金比例为25%。3.产能过剩行业项目：钢铁、电解铝项目最低资本金比例为40%，水泥项目最低资本金比例为35%，煤炭、电石、铁合金、烧碱、焦炭、黄磷、多晶硅项目最低资本金比例为30%。4.其他工业项目：玉米深加工项目最低资本金比例为20%，化肥（钾肥除外）项目最低资本金比例为25%。5.电力等其他项目最低资本金比例为20%。6.城市地下综合管廊、城市停车场项目，以及经国务院批准的核电站等重大建设项目，可以在规定最低资本金比例基础上适当降低。（九）房地产开发贷款须同时满足：建设用地出让金已全部缴齐和“四证”齐全，包括《国有土地使用证》（出让性质）、《建设用地规划许可证》、《建设工程规划许可证》、《建筑工程施工许可证》。对已开始预售的项目，还需提供《销、预售许可证》；（十）提供本行认可的担保方式；（十一）本行要求的其他条件。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第三章  贷款的对象与条件', '第十七条', '借款人不得在本行同一辖区内的两个或两个以上同级分支机构申请取得贷款。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第四章  固定资产贷款的种类、期限、利率', '第十八条', '固定资产贷款包括基本建设贷款、更新改造贷款、房地产开发贷款以及其他固定资产投资贷款等四大类。（一）基本建设贷款是指贷款人为支持以外延扩大再生产，增加固定资产数量和提高技术装备水平而发放的贷款。其范围包括借款人新建、扩建、改建、恢复、重建的基本建设项目。（二）更新改造贷款是指贷款人为支持以内涵扩大再生产为主的技术改造和技术引进而发放的贷款。其范围包括：支持企事业单位引进采用新技术、新设备、新工艺、新材料；开发新产品，推广、应用科技新成果；提高产品质量、发展优质名牌产品，降低能源、原材料消耗以及环境污染等。（三）房地产开发贷款是指贷款人为支持借款人从事房产或地产开发经营活动有关的贷款。其范围包括：住房开发、商业用房开发以及土地开发等。（四）其他固定资产投资贷款是指贷款人支持借款人除基本建设投资、更新改造投资、房地产开发投资以外的其他固定资产投资而发放的贷款。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第四章  固定资产贷款的种类、期限、利率', '第十九条', '固定资产贷款期限主要根据借款人的项目项目建设需要、预测现金流和投资回收期、还款能力和本行的信贷资金平衡能力等因素，合理确定贷款期限和还款计划，由借贷双方协商确定，一般不超过10年，其中：（一）基本建设贷款期限一般为3-5年，一般最长不超过10年，国家大型建设项目贷款期限可根据实际情况另外确定；（二）更新改造贷款期限一般为1-3年，一般最长不超过5年；（三）房地产开发贷款期限一般不超过3年；（四）其他固定资产投资贷款期限控制在5年以内。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第四章  固定资产贷款的种类、期限、利率', '第二十条', '固定资产贷款利率依照本行《贷款利率定价管理办法》执行。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第四章  固定资产贷款的种类、期限、利率', '第二十一条', '固定资产贷款以中长期贷款形式发放的，应根据项目投资回收期及其他因素，合理确定贷款分期还款期数及金额，不得整贷整还，每年还款期不得少于两次。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第五章  贷款担保', '第二十二条', '固定资产贷款方式分为保证、抵押、质押贷款。（一）保证贷款。是指本行按法律规定的连带责任保证方式以第三人承诺在借款人不能按期偿还贷款时，按约定承担连带责任为前提而发放的贷款。借款人不能履约偿还债务时，本行按约定有权要求保证人履行承担偿还债务连带责任。（二）抵押贷款。是指贷款人按《担保法》规定的抵押方式以借款人或第三人（统称抵押人）可以抵押的财产作为抵押物发放的贷款。借款人不能按期清偿债务时，贷款人有权按法律规定和合同约定处分抵押物并优先受偿。办理抵押贷款，借款人与贷款人在签订借款合同时，需相应签订抵押合同，作为借款合同的从合同。（三）质押贷款。是指贷款人以借款人或第三人可以质押的动产或权利作为质物发放的贷款。借款人不能按期清偿债务时，贷款人有权按法律规定和合同约定处分质物并优先受偿。办理质押贷款，借款人与贷款人在签订借款合同时，需相应签订质押合同，作为借款合同的从合同。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第六章  固定资产贷款的授信申请、受理与调查', '第二十三条', '借款人申请固定资产贷款，应当向经办行提出固定资产贷款的书面授信申请并提供以下资料：（一）借款人基本情况资料。主要包括借款申请书，营业执照、组织机构代码证、税务登记证（或提供由有权部门发放带有“统一社会信用代码”的营业执照）、公司章程、特许经营许可证、财务报表以及法定代表人身份证明及中征码等，公司董事会（股东会）按公司章程同意申请固定资产贷款的借款决议和授权书、董事会签字样本。（二）项目情况资料。1.项目提出的有关背景材料；2.经有权审批机关批准的项目投资计划及年度投资计划文件；3.经有权审批机关批准的项目贷款计划及年度贷款计划文件（指列入国家专项计划以内的）；4.项目建议书及批准文件、可行性报告、环境影响报告书；5.项目自筹资金和其他建设资金、生产资金筹措方案以及落实资金来源的证明材料；资本金到位情况必须有合法的验资证明并尽可能专户存入本行；6.项目各项规划、建设等方面的许可证件，有关用地、项目劳动保护、环境保护以及节能减排落实证明，房地产开发应符合房地产企业相关资质要求且“四证”齐全；7.项目概算资料、项目前期准备完成情况报告及有关材料；8.申请人因本项目与有关单位、部门签订的合同、章程和各种协议、认可书、意向书以及相关设备清单等。（三）担保材料。担保人的证明文件、财务资料、担保的承诺以及有权人同意担保的决议或文件，抵（质）押物清单及权属证明等。（四）本行认为需要提供的其他资料。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第六章  固定资产贷款的授信申请、受理与调查', '第二十四条', '经办行受理申请后，应落实具体的客户经理（至少双人）按照尽职调查要求，履行尽职调查并形成书面报告。对于额度较大固定资产贷款申请，本行授信评审部可直接参与经办行固定资产贷款项目的现场调查。调查人应对客户提交资料和项目调查评估报告内容的真实性、完整性和有效性负责，做到尽职调查，并要求借款人恪守诚实守信的原则，承诺所提供资料的真实、完整、有效。（一）尽职调查的程序：1.组建调查小组，确定主调查人；2.制订调查工作计划；3.调查、收集整理材料；4.对资料进行分析、审核，与借款人主要负责人、财务负责人交谈，现场、实地核查，做出预测与评价；5.撰写调查评估报告。（二）调查评估（评价）要点：1.借款人评价。主要包括：借款人概况、经营者素质评价、经济技术实力评价、资产负债分析、资产运用效率评价、盈利能力分析、现金流、信用状况评价、发展前景评价等；2.项目建设条件评价。主要包括：项目建设必要性评价、项目实施进程情况评价、工艺技术条件评价、经济规模评价、环境保护以及节能减排评价等；3.市场评估。主要包括：市场环境评估、市场竞争能力分析、市场前景评估等；4.投资估算和筹资评价。主要包括：投资估算评估、筹资评价、项目投资计划评价等；5.偿债能力评估。主要包括：项目财务效益预测、在建工程效益评价、借款人综合效益评价等；6.贷款风险及效应评价。主要包括：贷款风险定量与定性分析、贷款担保评价、贷款效应评价等；7.其他需要调查或评估的事项。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第七章  固定资产贷款授信的审查与审批', '第二十五条', '授信审查要点:（一）申报的材料是否齐全；（二）借款单位的法人资格、资信情况、财务状况是否符合贷款人有关规定的条件；担保单位的资信情况、财务状况是否符合保证人有关规定的条件；（三）项目批准文件是否齐全、项目概况描述是否到位；（四）项目资本金和其他配套资金的落实情况；（五）贷款尽职调查或项目评估报告是否真实、完整、准确，是否对风险点进行了充分揭示，是否制定了有效的风险防范措施；（六）招标和投标、工程监理、保险以及账户管理的情况是否符合规定；（七）抵（质）押物的权属关系；（八）其他需要审查的内容。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第七章  固定资产贷款授信的审查与审批', '第二十六条', '风险评价应根据科学、客观、公正、审慎、全面的原则进行，并对与贷款偿还紧密相关的要素进行重点评估，充分揭示项目风险。合规风险审查要点：（一）借款单位、担保单位的法人资格、资信情况是否符合法律法规、各类监管制度的规定；（二）项目批准文件是否齐全、真实与有效，认真审查项目的合规性；（三）贷款尽职调查或项目评估报告是否对风险点进行了充分揭露，是否制定了有效的风险防范措施；（四）账户监管、支付委托等相关手续的合规性、有效性；（五）其他需要审查的内容。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第七章  固定资产贷款授信的审查与审批', '第二十七条', '审查通过后，按转授权管理办法进行审批。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第八章  固定资产贷款的用信审查与审批', '第二十八条', '经办行用信申请，按转授权管理办法进行用信审查、审批。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第八章  固定资产贷款的用信审查与审批', '第二十九条', '用信审查内容主要包括：已授信贷款中限制性条款的落实情况，用信的额度、期限、用途是否合理，贷款实际用途是否符合授信条件等。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第八章  固定资产贷款的用信审查与审批', '第三十条', '已授信未用信的期限超半年，授信失效。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第九章  固定资产贷款的合同签订', '第三十一条', '合同签订之前，经办行应对贷款条件再确认。主要是依据项目的实际情况，按照贷款尽职调查或项目评估报告、审批、批复，对借款人的借款资格、财务状况、项目风险以及担保措施等方面再次进行审核。审核过程中，发现项目有较大变化，有可能加大信贷风险时，应将有关情况和处理方案上报。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第九章  固定资产贷款的合同签订', '第三十二条', '经办行与借款人及其他相关当事人签订的合同文本应在视频监控下（签约台）进行，抵押的应按合同约定办理抵押物登记，所签订的合同文本未经换人审核的，不得外出。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第九章  固定资产贷款的合同签订', '第三十三条', '经办行应在借款合同中约定由借款人承诺以下事项：（一）贷款项目及其借款立项符合法律法规的要求；（二）及时向经办行提供真实、完整、有效的材料；（三）配合经办行进行贷款支付管理、贷后管理及相关检查；（四）发生影响偿债能力的重大不利事项时及时通知本行；（五）进行合并、分立、股权转让、对外投资、实质性增加债务融资等重大事项前征得本行有权部门同意；（六）经办行有权根据借款人资金回笼情况提前收回贷款；（七）其他需要借款人做出承诺的事项。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第九章  固定资产贷款的合同签订', '第三十四条', '经办行应与借款人在借款合同中约定，出现以下情形之一时，借款人应承担的违约责任和本行可采取的措施：（一）未按约定用途使用贷款的；（二）未按约定方式进行贷款资金支付的；（三）未遵守承诺事项的；（四）突破约定财务指标的；（五）发生重大违约事件的；（六）违反借款合同约定的其他情形的。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第九章  固定资产贷款的合同签订', '第三十五条', '保险相关规定。经办行应要求借款人、担保人到有关保险机构办理相关保险，保险期不得短于贷款合同主合同履行期限，保险金额不得小于主合同贷款本息，保险合同及保险单中应当注明出险时本行为保险赔偿的第一受益人，保险单存根应交存经办行。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第九章  固定资产贷款的合同签订', '第三十六条', '合同变更相关规定。合同变更包括合同当事人变更、贷款额度调整、合同展期等。合同不允许变更，如有变更事项，必须由客户经理受理、审核，并按授信审批权限逐级上报审批。合同中格式条款的变更必须经本行合规管理部或法律顾问审核。合同中应明确约定以下内容：（一）贷款金额、期限、利率及计结息方式；（二）贷款发放与还款账户，并明确约定贷款、还款的账户以及对账户的监控措施；（三）贷款资金支付接受本行管理和控制等与贷款使用相关的条款，包括贷款发放与支付方式、借款人自主支付的标准、贷款发放与支付申请时应提供的资料等；（四）提款条件，包括与贷款同比例的资本金已足额到位、项目实际进度与已投资额相匹配等；（五）其他需要约定的内容。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第九章  固定资产贷款的合同签订', '第三十七条', '担保合同。在签订借款合同时，经办行应同时办理贷款担保手续，作为借款合同的从合同，同时按规定手续或程序办理相关登记。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第三十八条', '放款审查、审批。经办行权限内的，由有权人审查、审批；经办行权限外的，提交有权部门审查、审批。客户经理根据审批意见，打印《放款通知单》至柜面办理用款。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第三十九条', '用款审批。柜面接到客户提交的《放款通知单》，权限内的，由运营主管（或兼合规员）进行审查、发放；权限外的，经运营管理部放款中心有权人审查、审批后发放。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十条', '经办行运营主管（或兼职合规员）按监管要求，在发放贷款前应确认借款人满足合同约定的提款条件，并按照合同约定的方式对贷款资金的支付实施管理与控制，监督贷款资金按约定用途使用。对于分批分期发放的，经办行要按照支付规定和项目进度逐笔对贷款发放进行审核。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十一条', '合同约定专门贷款发放账户的，贷款发放和支付应通过该账户办理。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十二条', '本行应通过受托支付或借款人自主支付的方式对贷款资金的支付进行管理与控制。受托支付是指本行根据借款人的提款申请和支付委托，将贷款资金支付给符合合同约定用途的借款人交易对手。借款人自主支付是指贷款经办行根据借款人的提款申请将贷款资金发放至借款人账户后，由借款人自主支付给符合合同约定用途的借款人交易对手。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十三条', '单笔金额超过项目总投资5%或超过500万元人民币的贷款资金支付，应采用受托支付方式。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十四条', '采用本行受托支付的，经办行应在贷款资金发放前审核借款人相关交易资料是否符合合同约定条件。经办行审核同意后，将贷款资金通过借款人账户支付给借款人交易对手，并应做好有关细节的认定记录。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十五条', '采用借款人自主支付的，经办行应要求借款人定期汇总报告贷款资金支付情况，并通过账户分析、凭证查验、现场调查等方式核查贷款支付是否符合约定用途。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十六条', '固定资产贷款发放和支付过程中，经办行应确认与拟发放贷款同比例的项目资本金及同比例的自有资金足额到位，并与贷款配套使用。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十章  固定资产贷款的发放与支付', '第四十七条', '在贷款发放和支付过程中，借款人出现以下情形的，经办行应与借款人协商补充贷款发放和支付条件，或根据合同约定停止贷款资金的发放和支付：（一）信用状况下降；（二）不按合同约定支付贷款资金；（三）项目进度落后于资金使用进度；（四）违反合同约定，以化整为零方式规避贷款本行受托支付。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第四十八条', '经办行客户经理在贷款发放后15个工作日内进行首次跟踪检查,并填写贷后检查表。主要检查客户是否按合同约定用途使用贷款，对未按借款合同规定用途使用贷款的，应查明原因并提出处置的意见和建议。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第四十九条', '对项目建设、项目经营期内的贷后检查，按本行信贷相关规定执行。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第五十条', '项目实际投资超过原定投资金额，借款人申请追加贷款的，应配套追加不低于项目资本金比例的投资和相应担保，经本行重新风险评价和审批后，由本行决定是否追加贷款。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第五十一条', '经办行应对抵（质）押物的价值和担保人的担保能力建立贷后动态监测和重估制度。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第五十二条', '经办行应对固定资产投资项目的收入现金流以及借款人的整体现金流进行动态监测，对异常情况及时查明原因并采取相应措施。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第五十三条', '合同约定专门还款准备金账户的，经办行应按约定根据需要对固定资产投资项目或借款人的收入现金流进入该账户的比例和账户内的资金平均存量提出要求。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第五十四条', '借款人出现违反合同约定情形的，经办行应及时采取有效措施，必要时应依法追究借款人的违约责任。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第五十五条', '固定资产贷款形成不良贷款的，经办行应对按信贷管理要求进行清收保全，及时制定清收或盘活措施。对借款人确因暂时经营困难不能按期归还贷款本息的，贷款经办行可与借款人协商进行贷款重组。对确实无法收回的固定资产不良贷款，经办行按照相关规定对贷款进行核销后，应继续向债务人追索或进行市场化处置。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十一章  贷后管理', '第五十六条', '固定资产贷款到期前30天内，客户经理应向借款人通过书面、短信、QQ、微信等途径发送到期履行债务通知，并取得有效回执。有效回执包含但不限于从客户处取得书面签收回执、图片截屏等。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十二章  附则', '第五十七条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《固定资产贷款管理办法及操作规程》', '第十二章  附则', '第五十八条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第一章  总则', '第一条', '为推动“阳光信贷”工程建设，规范本行圆鼎易贷通卡业务工作，根据《中华人民共和国担保法》、《江苏省农村信用社（以下简称“省联社”）圆鼎借记卡章程》、《江苏省农村信用社圆鼎易贷通卡业务管理办法（试行）》等法律以及制度规定，制定本办法。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第一章  总则', '第二条', '圆鼎易贷通卡业务是本行为推行“阳光信贷”、简化贷款手续、提升服务质量，将贷款与银联标准借记卡进行集成创新的一款金融产品。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第一章  总则', '第三条', '圆鼎易贷通卡既具备圆鼎借记卡所有支付结算、跨行取款和商户消费等功能，又能够满足客户小、频、急的贷款需求。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第一章  总则', '第四条', '本行所辖的支行、营业部、公司业务部、小微业务部（以下简称“经办行”）按信贷管理规定对圆鼎易贷通卡申请人进行调查、审核、征信、评估、授信申报、文本签订、用信手续办理等，然后在柜面通过圆鼎易贷通卡直接发放贷款或通过本行电子渠道由借款人自助放款。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第一章  总则', '第五条', '圆鼎易贷通卡贷款业务实行存贷一卡、一次授信、随用随贷、余额控制、循环使用。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第六条', '经办行负责圆鼎易贷通卡业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第七条', '授信评审部负责圆鼎易贷通卡的业务授信管理。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定圆鼎易贷通卡业务管理办法及流程，对授信贷款的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第九条', '电子银行部负责对圆鼎易贷通卡的非贷款业务的业务管理。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第十条', '运营管理部负责圆鼎易贷通卡业务柜面业务的管理、培训及用款审查。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第十一条', '计划财务部负责制定圆鼎易贷通卡业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第十二条', '科技信息部负责圆鼎易贷通卡业务系统的稳定运行、日常维护及技术支持。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第十三条', '风险管理部负责圆鼎易贷通卡业务风险的监测、识别、评估、控制。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第十四条', '合规管理部负责审查圆鼎易贷通卡业务办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第十五条', '资产保全部负责对不良贷款的清收保全及相关管理。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第二章  组织管理体系', '第十六条', '审计部负责对圆鼎易贷通卡业务进行审计。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第三章  发放对象及条件', '第十七条', '圆鼎易贷通卡发放对象为国家规定可以作为借款人、具有完全民事行为能力的自然人，且借款人年龄加上贷款授信期限不超过 65 周岁。具体发放对象可分为城乡居民、个体工商户、小微企业主和公职人员等。本行可根据不同的客户类型、行业类别、信用评级等情况，确定圆鼎易贷通卡相应的授信额度、授信年限、贷款利率等。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第三章  发放对象及条件', '第十八条', '圆鼎易贷通卡申请条件（一）城乡居民应具备的基本条件1.从事符合国家产业政策和行业发展规划要求的生产经营活动；2.本地有固定住所，且正常居住时间在 1 年（含）以上；从事个体工商户的要有固定经营场所，且正常经营时间在 1 年（含）以上，有一定的经营规模，懂经营、会管理；3.具有按期还本付息的能力，社会信誉良好，无重大法律诉讼纠纷。 采用担保方式发放的，近 3 年内无借款或对外担保等重大不良信用记录；采用信用方式发放的，近 5 年内无借款或对外担保等不良信用记录。4.在本行开设结算账户或一般存款账户，并在账户内保持一定的存款作为结算支付保证，自愿接受本行的信贷监督和结算监督，能将经营收入款项存入本行，定期或不定期向本行提供生产、经营情况；6.具有合法、可靠的担保手续（信用贷款除外）；7.借款用途符合国家政策、法律法规；8.本行规定的其他条件。（二）小微企业主应具备的基本条件1.有固定的生产经营场所和经营项目，借款人为企业的法人代表或最大股东；2.实际控制企业近 3 年生产经营正常、盈利能力较好，且正常缴纳税款；3.在本行贷款，实际控制企业的核心资产一般要参与抵押担保；4.实际控制企业上一年度全年销售收入与贷款授信金额比例不得低于 200%；5.本行规定的其他条件。（三）公职人员应具备的基本条件按照《商业银行股份有限公司公职人员贷款管理办法》执行。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第四章  贷款担保方式、用途、额度、期限及利率', '第十九条', '易贷通贷款业务根据担保方式不同，分为信用贷款和担保贷款、其中：担保贷款分为保证贷款、抵押贷款和质押贷款。（一）信用贷款是指以借款人的信誉发放的贷款，具体要求参照《商业银行股份有限公司个人信用贷款管理办法》执行。（二）保证贷款是指按《担保法》规定的保证方式以第三人承诺，在借款人不能偿还贷款时，按约定承担一般保证责任或连带责任而发放的贷款。本行易贷通业务只发放连带责任的保证贷款。（三）抵押贷款是指按《担保法》规定的抵押方式以借款人或第三人的财产作为抵押物发放的贷款。本行易贷通业务只发放土地、房产为抵押物的抵押贷款。（四）质押贷款是指按《担保法》规定的质押方式以借款人或第三人的动产或权利作为质物发放的贷款。本行易贷通业务只发放存单质押的质押贷款。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第四章  贷款担保方式、用途、额度、期限及利率', '第二十条', '贷款用途（一）种植业、养殖业等农业生产临时周转资金需要；（二）加工业、手工业、商业等个体工商业周转资金需要；（三）制造业、加工业等小微企业生产经营周转资金需要；（四）购车、装修、婚嫁、教育等消费资金需要；（五）其他具有合法用途的资金周转或消费资金需要。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第四章  贷款担保方式、用途、额度、期限及利率', '第二十一条', '经办行应根据客户生产经营活动的规模、实际经营周期、还贷来源，科学确定合理的授信额度、授信期限、用信期限。（一）授信额度。单户授信总额应综合考虑客户的信用评级、财务状况、非财务状况等，最高授信额度不得超过300万元；（二）一次授信期限：信用类期限最长一般不超过 1 年，担保类最长不超过三年；（三）单笔用信期限一般不超过一年。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第四章  贷款担保方式、用途、额度、期限及利率', '第二十二条', '圆鼎易贷通卡贷款利率按照本行利率定价管理办法执行。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第五章  圆鼎易贷通卡贷款管理', '第二十三条', '圆鼎易贷通卡的授信（一）建立档案。经办行对申请圆鼎易贷通卡客户要进行细致调查，客观、真实地反映申请人信用状况、生产经营及经济情况、还款能力、收入预测，建立客户信贷档案。经办行根据调查的结果对申请人进行客观公正的测评，确定信用等级，合理确定其授信额度和期限并上报至授信评审部；授信评审部权限内的，由有权人员审批；权限外的，提交信贷审查委员会审议，并报总行行长备案。授信应符合省联社《关于推进“阳光信贷”工作的指导意见》的相关要求。该授信是本行对客户的贷款承诺，在客户符合用信条件的情况下，经办行应及时满足客户的用信要求。（二）授信申请1.城乡居民应提供的资料（1）申请人本人及其配偶身份证原件及复印件；单身借款人需提供单身承诺并提供共同借款人，其中：单身借款人有工作的，由单位出具相关证明；无工作的，由所在街道、居委会或村委会出具相关证明。（2）申请人从事生产经营活动所能提供的资料（种植、养殖合同、租赁证明等）；（3）本行要求提供的其他有关资料。2.个体工商户和小微企业主应提供的资料（1）申请人本人及其配偶身份证原件及复印件；单身借款人需提供单身承诺并提供共同借款人，其中：单身借款人有工作的，由单位出具相关证明；无工作的，由所在街道、居委会或村委会出具相关证明。（2）申请人营业执照、税务登记证原件及复印件等；（3）本行要求提供的其他有关资料。⒊申请人为公职人员的按照《商业银行股份有限公司公职人员贷款管理办法》执行。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第五章  圆鼎易贷通卡贷款管理', '第二十四条', '圆鼎易贷通卡可采取客户到柜面办卡或本行批量开卡的方式，其中：㈠柜面办卡1．客户需提供的资料：⑵按规定填写好的开卡申请表；⑴客户本人身份证原件；⑶需要提供的其他资料。2.开卡流程经办行客户经理在信贷风险管理系统中完成客户资料录入后打印开卡通知单，经办行有权人签字后，将开卡通知单交给客户，客户凭开卡通知单及本人有效身份证件到柜面，柜面人员须核对开卡人身份证件、开卡通知单、开卡申请表的一致性。核实无误后，柜面人员向客户发放圆鼎易贷通卡。㈡批量开卡采取阳光信贷模式批量授信的客户，经办行根据本行批量开卡流程集中开卡，客户凭本人有效身份证件到经办行领取卡片并激活或由经办行指派 2 名及以上的工作人员到客户处集中发卡，现场指导客户激活卡片。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第五章  圆鼎易贷通卡贷款管理', '第二十五条', '合同签订根据圆鼎易贷通卡“一次授信、随用随贷、余额控制、循环使用”的特点，经办行应与客户及其他相关人签订最高额性质的借款担保合同，抵押的应办理相关登记，并按《个人贷款业务操作规程》办理贷款业务，激活易贷通卡贷款功能。采取批量方式开卡后采取现场发卡、现场签约方式的，应收集客户基础资料复印件（包括借款人及其配偶身份证、户口簿、结婚证，保证人身份证等），与客户签订借款合同、手机自助放款协议、个人信用报告查询授权书等重要法律文书时，均须拍照佐证，并作为信贷资料留存。同时，客户经理应及时整理客户基本资料并根据客户授权查询借款人及配偶（或家庭其他成员）征信报告，无不良征信记录的，及时录入信贷管理系统，激活易贷通卡贷款功能。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第五章  圆鼎易贷通卡贷款管理', '第二十六条', '圆鼎易贷通卡用信管理客户在有效授信期内第一次到经办行申请用信时，客户经理应与客户进行面谈，并做好记录，如采取电子银行等自助渠道放款的客户，面谈在合同签订阶段进行。面谈记录作为重要信贷资料与客户信贷档案一并留存保管。首次用信面谈后，客户在有效授信期限内再次用信时，可根据客户实际情况、用信情况等确定是否需要面谈，其中：客户已授信未用信且期限超过 6 个月的用信申请，客户经理受理后应按照信贷业务操作规程》的相关要求进行必要的调查、核实和面谈（电子银行等自助渠道放款的除外）。授信有效期限内，客户本人凭圆鼎易贷通卡及有效身份证件，在最高授信额度内，直接到经办行申请用信，有权人应按照《个人贷款业务操作流程》进行支付、放款审核，无误后柜面按流程发放。采用自助设备及手机银行、网上银行等电子渠道自助用信的客户，易贷通卡授信额度最高为30万元（含），对于个体经营户生产经营贷款，最高授信额度可放宽至50万元（含）。对符合自助用信条件的客户，经办行应及时与客户签订《手机银行自助用信服务协议》，协议中要注明借款人的放款账号或实名制的银行卡号，同时要复印作为自助放款载体的易贷通卡，并由借款人在复印件上签字，作为协议的附属资料，审批通过后，客户可直接自助用信。每日日终，经办行柜员应打印当天自助发放的贷款借据和贷款明细清单，并由运营主管将清单交管户客户经理。借款人一般应办理借款人意外伤害保险，其中：从事运输、建筑、化工等危险作业的借款人必须办理，如以生产性厂房作抵押的，应办理企业财产保险，其中：从事化工、纺织、服装、运输等高危、易燃、易爆行业的，必须办理企业财产保险。保险期不得短于贷款主合同履行期限，保险合同及保险单中应当注明出险时本行为保险赔偿金的第一受益。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第五章  圆鼎易贷通卡贷款管理', '第二十七条', '贷后管理贷款业务发生后，管户客户经理应在15日内进行首次跟踪检查（贷款存续期不足15天的除外），主要检查客户是否按用途使用贷款，对未按规定用途使用贷款的，应查明原因并提出处置的意见和建议。经办行对易贷通贷款的常规贷后检查应按照“发放与检查不为同一人”原则进行，检查的时间与频率按本行规定执行，切实做好风险防控工作。检查完毕后，客户经理应及时填制《贷后检查报告表》，经有权人复核后归档备查并及时上传至信贷风险管理系统。对检查过程中发现影响贷款安全的重大事项，应及时揭示，提出防范和化解信贷风险的措施并报送经办行负责人。当月新增单户50万元以上的个人不良贷款应向风险管理部、信贷管理部进行书面报告。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第五章  圆鼎易贷通卡贷款管理', '第二十八条', '贷款归还。短期贷款到期一周之前、中长期贷款到期一个月之前，客户经理应通过书面、短信、QQ、微信等途径向借款人发送到期履行债务通知，并取得有效回执，及时做好到期贷款催收。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第六章  圆鼎易贷通卡业务管理', '第二十九条', '圆鼎易贷通卡的年检。经办行需在授信后一年内进行年检，年检的主要内容包括：（一）客户生产经营情况是否正常，是否存在影响贷款归还的因素；（二）客户用信情况是否正常，有无不良记录；（三）客户授信金额、授信期限是否合理，是否有增（减）授信的需求；（四）客户担保贷款履行情况；（五）征信记录是否发生劣变；（六）其他相关情况。经办行贷款会办小组对易贷通贷款年检情况进行会办，确定其授信金额、授信期限、授信方式是否调整和变化，并对照冻结、注销、增（减）信的要求进行相应处理。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第六章  圆鼎易贷通卡业务管理', '第三十条', '圆鼎易贷通卡的到期管理圆鼎易贷通卡授信到期后，经办行应及时对圆鼎易贷通卡及对应账户停止用信功能。然后根据客户用信情况及今后的用信需求确定是否终结授信或者重新给予授信。（一）授信终结确认客户全部结清贷款及利息后，经办行应对圆鼎易贷通卡做相应的终结业务处理。（二）授信再申请在圆鼎易贷通卡授信期限到期前 15 天，圆鼎易贷通卡内所有贷款本息已全部按时或提前偿还的，管户客户经理应该提前通知客户，客户可向经办行申请继续使用圆鼎易贷通卡。经办行可依据本办法,审核申请人是否符合条件，并进行相应处理。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第六章  圆鼎易贷通卡业务管理', '第三十一条', '圆鼎易贷通卡的异常处理（一）圆鼎易贷通卡的遗失或毁损圆鼎易贷通卡在使用过程中发生遗失或毁损，持卡人须按规定向发卡行申请办理易贷通卡挂失手续，发卡行审核无误后可按规定补（换）发新的圆鼎易贷通卡。发卡行在补（换）发卡的同时须调整相关登记表信息和信贷风险管理系统相关信息。（二）圆鼎易贷通卡的冻结圆鼎易贷通卡客户发生下列情况之一，可根据客户经理的书面申请，经会办小组会办同意后，对其授信予以冻结。1．客户经理在贷后跟踪或贷后管理过程中，发现客户挪用贷款或转借信用；2．贷款本金或利息出现逾期；3．贷款虽未到期但借款人生产经营已停止或者生产经营情况不佳，销售收入、经营利润下降或者出现流动性不足的征兆，一些关键财务指标出现异常等；4．对圆鼎易贷通卡保管不善，一年内丢失两次（含）以上的；5．借款人在其他金融机构有借款，或为他人担保、已超过本人承受能力的；6．不配合本行人员进行贷后检查；7．其他可以冻结的事项。（三）圆鼎易贷通卡的注销圆鼎易贷通卡客户发生下列情况之一的，可根据客户经理的书面申请，先对其授信贷款业务予以冻结，并待其归还所欠贷款本息后予以注销。1．借款人采用隐瞒事实、提供虚假资料等不正当手段取得圆鼎易贷通卡；2．客户挪用贷款或转借信用且未在本行规定的时间内纠正到位；3．借款人经营管理出现问题，对正常生产经营构成实质损害，妨碍债务及时足额清偿；4．借款人贷款本金或利息逾期在 30 天（含）以上；5．对圆鼎易贷通卡保管不善，一年内丢失四次（含）以上；6．其他可以予以注销的事项。（四）圆鼎易贷通卡解冻圆鼎易贷通卡一经冻结，待其贷款本息还清后，授信贷款业务不予解冻，确需解冻的，应报信贷管理部审批同意后方可进行。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第七章  附则', '第三十二条', '本办法及流程未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第七章  附则', '第三十三条', '本办法及流程由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《圆鼎易贷通卡业务管理办法及流程》', '第七章  附则', '第三十四条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第一章  总则', '第一条', '为进一步规范本行信贷业务行为，防范和化解贷款风险，根据《项目融资业务指引》、《中华人民共和国城市房地产管理办法》、《中华人民共和国担保法》、《城市房地产抵押管理办法》等法律法规，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关信贷管理制度，结合本地区实际，制订本办法。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第一章  总则', '第二条', '本办法所称在建工程，是指正在施工建设的房屋。本办法所称在建工程抵押，是指抵押人为取得在建工程继续建造所需资金，以其合法方式取得的土地使用权连同在建工程的投入资产，以不转移占有的方式抵押给本行作为贷款担保的行为。本办法所称抵押人，是指将依法取得的在建工程提供给本行，作为履行债务担保的法人。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第一章  总则', '第三条', '在建工程抵押贷款业务遵循自愿、互利、公平和诚实信用的原则。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第四条', '公司业务部、小微业务部（以下简称“经办行”）负责在建工程抵押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第五条', '授信评审部负责在建工程抵押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定在建工程抵押贷款管理办法及操作规程，对在建工程抵押贷款业务的用信管理和贷后检查进行监督。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对在建工程抵押贷款的用款进行审查。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '计划财务部负责制定在建工程抵押贷款业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '风险管理部负责对在建工程抵押贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核在建工程抵押贷款管理办法和操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '资产保全部负责对在建工程抵押贷款业务形成的不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '审计部负责对在建工程抵押贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第三章  贷款的对象和条件', '第十三条', '贷款对象。经主管部门批准设立，由依法履行工商行政管理职能的机构核准登记，取得企业法人营业执照，并按规定办理年报、年检手续的各类企事业单位。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第三章  贷款的对象和条件', '第十四条', '在建工程贷款须具备以下条件：（一）贷款资金须用于在建工程继续建造；（二）抵押人已合法取得在建工程占有的土地使用权，且是以出让方式取得的国有土地使用权；（三）在建工程已取得有权部门的立项批文和“四证”，即：建设用地规划许可证、国有土地使用权证、建设工程规划许可证、建筑工程施工许可证。“四证”载明的权利人和建设单位须一致，在建工程项目与批准文件内容须相符；（四）在建工程未通过竣工验收也未取得房屋所有权证，且在建工程投资额已完成总投资额的50%以上；（五）法律法规规定和本行认为须具备的其他条件。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第三章  贷款的对象和条件', '第十五条', '不接受下列在建工程作抵押：（一）在建工程存在合同争议的；（二）被依法查封或采取其它限制措施的；（三）公益性质的（学校、医院、幼儿园等）在建工程；（四）法律、法规规定和本行认为不得设定抵押的其他情况。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第四章  贷款金额、期限、利率和还款方式', '第十六条', '贷款金额。根据抵押人的在建工程的现值确定，一般不超过抵押物评估可变现价值的50%。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第四章  贷款金额、期限、利率和还款方式', '第十七条', '贷款期限。根据在建工程进度合理确定。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第四章  贷款金额、期限、利率和还款方式', '第十八条', '贷款利率。根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第四章  贷款金额、期限、利率和还款方式', '第十九条', '还款方式。贷款期限在1年以内（含1年）的，实行按月结息，到期还本方式；贷款期限在1年以上的，实行按月结息，每年至少两次还本的方式。贷款发放后，允许借款人提前还款。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十条', '在建工程抵押贷款操作程序为借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十一条', '借款人申请办理在建工程抵押贷款时须按本行有关信贷管理和操作规定等制度规定需提供的（包括但不限于）如下资料的原件及复印件：（一）营业执照；（二）法定代表人身份证明及签字样本或印鉴；法定代表人授权委托书、委托代理人身份证明及签字样本或印鉴；（三）房地产开发企业资质证书并经审核合格；（四）企业章程（与依法履行工商行政管理职能的机构备案章程一致）；（五）有权部门出具的立项批文和“四证”，即：建设用地规划许可证、国有出让土地使用权证，建设工程规划许可证、建筑工程施工许可证；（六）建筑工程预算报告、施工进度及工程日期计划、建设工程承包合同、工程款支付凭证；（七）出让土地缴费原始发票、在建工程价格评估报告；（八）抵押人同意提供抵押担保的合法、有效的书面文件；（九）本行认为需要提交的其他材料。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十二条', '以共有的在建工程抵押的，还应有全体共有人同意以该在建工程设定抵押的书面文件，抵押人为全体共有人。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十三条', '经办行接到借款人申请资料后，按本行《信贷业务操作规程》进行调查，调查的内容包括但不限于以下方面：（一）对借款人提供的资料完整、真实、有效性进行核实；（二）了解工程价款的支付情况，根据抵押人提交的建设工程承包合同、工程款支付凭证向建设工程承包人进行查证。对已发生抵押人拖欠工程价款的，经办行在确定抵押物价值时扣除该部分工程价款；（三）了解商品房预售情况，并向有关房地产管理部门进行核实。对抵押人已预售部分商品房的，经办行在确定抵押物价值时扣除抵押人已经预售的商品房价款；（四）本行认为应调查的其它方面。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十四条', '在建工程价值的确定，可以由抵押人和本行双方约定的方式进行，亦可由本行委托其认可的评估机构进行评估确定；对国有土地使用权受让未超过2年的，抵押人还须提供缴纳土地出让金原始发票。在此基础上，经办行参照抵押人建筑工程预算报告以及各地段房产、土地使用权价格标准，根据在建工程完成情况（是否完成基础工程、主体工程、设备工程、内外装修、附属工程等）、是否存在工程款拖欠现象、是否有商品房预售行为合理确定在建工程的价值。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十五条', '经办行在确定在建工程价值时，按照下列公式核定在建工程的价值：在建工程价值＝续建完成后的不动产价值－（续建成本+投资利息+税金+续建投资利润+租售费用）－未支付的工程价款－已预售的价款续建完成后的不动产价值是指工程按设计标准完工后的市场可以接受的销售价格，常用市场比较法求得，不能按抵押人制定的预售价格确定；续建成本包括土建工程费用、设施设备及安装费用、内外装修工程费用、专业费用及不可预见费；已预售的价款可根据实际情况确定。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十六条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十七条', '抵押人须与经办行签订抵押合同。签订抵押合同时经办行、借款人、抵押人必须亲自到场签字，不得由他人代签。经办行须再次审核抵押人是否是在建工程的合法所有人或全部共有人，具备完全民事行为能力的共有人必须全部到场签字。抵押合同必须包括以下主要内容：（一）被担保的主债权种类、数额；（二）合同借款人履行债务的期限；（三）抵押在建工程的名称、面积、价值、质量、状况、所在地、所有权权属或者使用权权属；（四）抵押担保的范围；（五）抵押物登记与保险；（六）双方的权利和义务；（七）违约责任；（八）合同的生效、变更、解除和终止。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十八条', '经办行须在签订抵押合同前要求抵押人提供建设工程承包人放弃该在建工程优先受偿权的书面承诺。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十九条', '在建工程抵押担保须办理抵押登记，抵押权自登记之日起生效。办理在建工程抵押登记的部门为有权房产、土地或不动产登记管理部门。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十条', '经办行必须在签订抵押合同后，与借款人、抵押人一同前往登记部门依法分别办理在建工程抵押登记，取得相应的抵押登记证书。抵押物登记手续未办妥的不得发放在建工程抵押贷款。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十一条', '抵押合同签订后，经办行须要求抵押人到有关保险机构按照下列条件办理抵押在建工程的保险手续：（一）办理抵押在建工程足额保险；（二）保险期限不得短于合同履行期限；（三）保险金额不得小于合同贷款本息；（四）保险合同及保险单中应当注明，本行为保险赔偿金的第一受益人；（五）保险单中不得有任何限制本行权益的条款；（六）抵押在建工程保险费用全部由抵押人承担，保险单证原件应交由经办行保管。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十二条', '借款人办妥上述手续后，本行按形象工程进度发放贷款，将资金划入借款人本人的结算账户，并严格监督借款人按合同约定支付方式支付贷款资金。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十三条', '借款人按合同规定偿还全部贷款本息后，借款合同终止，经办行客户经理应当将保管的抵押物权属证明交还借款人，并配合借款人向原登记机关办理注销登记。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十四条', '贷款发放与支付按本行有关信贷管理制度实施。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十五条', '贷款发放后，应按照本行信贷管理相关制度办法进行贷后管理。贷后检查的重点包括但不限于：（一）监督建设工程承包合同的履行情况，对发生拖欠工程价款情形的，要求借款人追加担保或采取其他降低风险措施。（二）监督抵押人预售、销售商品房情况，并要求抵押人将售房款提前归还贷款或予以提存。（三）检查在建工程的进展情况，及时掌握在建工程竣工时间。对竣工的在建工程应敦促抵押人办理房产、土地或不动产抵押登记手续，防止抵押物流失。经办行客户经理须对贷后检查形成检查结论，对抵押人发生重大变化、抵押权受到或即将受到限制、侵害的，须及时作出风险预警，并如实填写贷后检查表，签字确认。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十六条', '抵押权存续期间，抵押的在建工程灭失、毁损或者被征用的，经办行应当要求抵押人以所获得的保险金、赔偿金或者补偿金优先用于提前清偿所担保的主债权，或者向双方约定的第三人提存。发生抵押在建工程灭失后所得赔偿金数额不足清偿情况的，对不足清偿的部分要求借款人提供新的担保。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十七条', '经办行应按本行信贷档案管理有关规定收集、保管相关资料。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十八条', '借款合同履行期限届满，借款人未能按期归还贷款本息的，经办行应当按照与抵押人达成的协议以抵押在建工程折价或者以拍卖、变卖抵押在建工程所得的价款受偿；协议不成的，经办行应当在诉讼时效内及时提起诉讼或者申请仲裁。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十九条', '经办行应积极参与和监督抵押人（发包人）和承包人对抵押在建工程的处置，并在必要时依法及时行使抗辩权或撤销权。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第四十条', '抵押担保的债权部分清偿后，借款人未能清偿剩余债权的，经办行就抵押在建工程的全部行使抵押权。经登记的抵押在建工程被分割或者部分转让的，经办行就分割或者转让后的抵押在建工程行使抵押权。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第四十一条', '经办行依法处分抵押在建工程所得的价款，用于清偿实现抵押权的费用、贷款本金和利息。处分抵押在建工程所得价款不足以清偿上述所列金额的，经办行须就不足部分继续向借款人追偿；清偿上述所列金额后有剩余的须退还借款人。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第六章  附则', '第四十二条', '本办法由信贷管理部负责制定、修改、解释。'),
(NULL, '信贷管理部', '《在建工程抵押贷款管理办法及操作规程》', '第六章  附则', '第四十三条', '本办法自下发之日起执行。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第一章  总则', '第一条', '为加强本行客户经理队伍建设，提高客户经理综合素质，充分调动和激发客户经理的工作积极性、主动性、创造性，形成长效的激励机制，促进各项业务的持续、健康、稳定发展，特制定本办法。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第一章  总则', '第二条', '本办法所称客户经理是指经本行批准，代表本行拓展和维护客户关系，为客户提供资产、负债、中间业务等金融服务的我行在职人员。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第一章  总则', '第三条', '客户经理岗位基本职责：1.开拓市场。主动寻找、发掘潜在客户，发现并把握业务发展机会，积极向客户营销我行产品、提供金融服务以及拓展中间业务。2.客户维护。开展客户关系管理，跟进客户需求，检查营销效果，稳定并提高客户在我行的业务总量、综合效益和资产质量；保持与经管客户的密切联系，关注客户经营状况、信用变动状况，对可能发生的风险，及时采取措施加以控制和化解，主动发现服务中存在问题，积极整改和完善，促进服务水平提高。3.质量管理。按规定开展贷后检查、风险分类及贷款日常管理和本息收回工作，采取诉讼和自我清收等方式压降不良贷款。4.关注市场信息。客户经理对市场信息要具备高度的敏感性，主动收集和分析目标客户的各种信息，及时反馈，提前应对，争取把握市场先机。5.日常管理。各级客户经理要规范日常行为，强化业务学习，提高自身素质。对信贷档案资料实行定期整理、及时更新，并做好日常管理工作。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第一章  总则', '第四条', '本行成立员工等级考评领导小组，组长由分管人力资源管理工作的行长担任，组员为机关各部室负责人；客户经理等级考评按照“统一领导、分级负责、客观公正”的原则，由信贷管理部牵头、其他相关部门配合，对全行所有客户经理进行等级初评、分管行长审核后交人力资源部汇总，报员工等级考评领导小组审定；年初各经营单位须明确各岗位工作职责、目标任务。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第二章  考评项目与评定条件', '第五条', '客户经理等级考核评定工作每年组织一次，一般在每年员工业务技能测试后举行。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第二章  考评项目与评定条件', '第六条', '考评从职业素养、业务素质、业务技能、工作质量、工作业绩、服务效能等六个方面综合评价。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第二章  考评项目与评定条件', '第七条', '参加客户经理等级评定人员应具备的条件：1.具有良好的职业道德，较好地履行岗位职责，文明服务，秉公守法，廉洁奉公。2.熟悉和自觉遵守国家有关法律、法规和本行各项规章制度，行为规范，无不良嗜好和违法违纪记录。3、熟练掌握商业银行基本业务知识和岗位专业知识，熟练掌握岗位业务技能及办公业务系统操作技能。4.具有经济类（或会计类）初级以上级别证书，或银行业专业人员职业资格初级2门（其中：1门必须为《银行业法律法规与综合能力》）以上证书。5.取得岗位需要的业务技能级别。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第三章  等级评定标准和内容', '第八条', '客户经理等级评定实行百分制考核，具体标准和内容：㈠职业素养考核（10分）1.拥护党的路线、方针、政策，具有较高的政治素质和思想觉悟，积极支持和参与本行中心工作及重点工作，从正当途径反映工作问题或工作建议，得2分；2.熟悉相关法律法规和管理规定，通过总行组织的合规及法律法规知识测试，得1分；3.具有较强的事业心和责任感，作风正派，身心健康，遵纪守法，有良好的职业素养和从业记录，得3分；受到警告、记过、记大过等纪律处分的分别扣1分、2分、5分（多次受到处分的以最高等级计算，受到党纪处分的按对应等级扣分）；4.近三年年度考核均为称职以上，得1分；5.自愿服从组织调配，得1分；不服从部门工作安排的每次扣1分，不服从总行工作调配的每次扣3分；6.自觉遵守本行管理制度，得1分；发生不遵守本行管理制度行为未造成重大影响的每次扣2分，造成重大影响的每次扣5分；7.遵守廉洁从业规定，得1分；发生违背廉洁从业规定行为，经查实，情节较轻，未造成影响的扣5分，情节较重，造成社会影响的，扣10分。㈡业务素质考核（20分）业务素质考核包括学历、相关职称、职业资格、学习能力四个方面，学历5分、职称7分、职业资格8分，学习能力为加、减分项。1.学历考核，取得本科学历的得4分，取得研究生以上学历计5分，大专学历扣1分，中专及高中学历扣2分，高中以下学历扣3分。2.职称考核，分为高级职称、中级职称和初级职称三个等级，分别计7分、3分和1分，无职称的扣2分。职称必须与本人工作岗位相近，取得多个的按最高级别计算，不重复计算得分。3.职业资格考核，包括银行业专业人员职业资格和从业资格，银行专业人员职业资格占5分，从业资格占3分。其中银行业专业人员职业初级资格（《银行业法律法规与综合能力》加任一专业科目）得2分，另通过一个专业科目加0.5分；取得银行业专业人员职业中级资格（《银行业法律法规与综合能力》加任一专业科目）的得5分，另通过一个科目加1分；未取得初级资格的扣2分。取得与岗位相匹配的从业资格，得２分，另取得一个加0.5分；应取得未取得的扣2分（无要求的不扣）。4.学习能力考核，总行组织的培训测试及其他考试实行积分制，低于60分（或不合格）每次扣1分，高于80分（或合格）每次加分，最高加分不超过5分。㈢业务技能考核（20分）业务技能考核包括综合业务知识、会出业务实务、信贷业务实务、电子银行实务、数字录入、汉字录入、手工点钞等7个项目；通过（即全部达到D级）五项以上的每项得1分，每项每提高1个等级加0.5分；通过四项以下的不得分；五个以上项目为A级的本项目满分。㈣工作质量考核（10分）1.报表、材料报送。迟报、错报、漏报，每次扣2分，被通报批评的，每次扣5分，累计两次以上的该项不得分。2.因工作完成质量差，被总行通报批评的，有一次扣5分。3.在各项检查中发现重大问题的，每次至少扣3分，直至扣完为止。4.岗位工作。不按时按质完成的，每次视情至少扣2分；突击性工作不能按时按质完成的，加倍扣分。㈤工作业绩考核（30分）工作业绩考核按公司客户经理、小微客户经理和个人客户经理三种类别分别进行考核。考核内容包含存款维护、存款日平拓展、贷款维护、贷款日平增长、收息额、收息率等指标。㈥服务效能考核（10分）服务效能主要考核客户投诉、限时服务等，发生一起客户有效投诉扣2分，发生一次超过服务限时扣2分，扣完为止。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第九条', '客户经理等级由高到低设置为一级、二级、三级、四级、五级、六级和七级等7个等级。其中：一级为专家级客户经理，二级为资深级客户经理，三级为高级客户经理，四级、五级为中级客户经理，六级为初级客户经理,七级为见习客户经理。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第十条', '客户经理等级认定结合百分制考核得分及等级要求进行综合评定。1.符合下列条件的，评定为一级客户经理：⑴在本行连续从事信贷工作10年以上；⑵具有本科以上学历、经济类（或会计类）中级职称和银行业专业人员职业资格中级资格证书；⑶技能考核项目全部通过（即D级以上）且等级达5个A以上；⑷考核期内，百分制考核得分90分以上，到期贷款收回率达100%，且无新增五级不良贷款。2.符合下列条件的，评定为二级客户经理：⑴在本行连续从事信贷工作8年以上；⑵具有本科以上学历、经济类（或会计类）中级职称和银行业专业人员职业资格中级资格证书；⑶技能考核项目全部通过（即D级以上）且等级达5个A以上；⑷考核期内，百分制考核得分85分以上，到期贷款收回率达100%，无新增五级不良贷款。3.符合下列条件的，评定为三级客户经理：⑴在本行连续从事信贷工作5年以上；⑵具有本科以上学历、经济类（或会计类）中级职称和银行业专业人员职业资格中级资格证书；⑶技能考核项目全部通过（即D级以上）且等级达5个A以上；⑷考核期内，百分制考核得分80分以上，到期贷款收回率不低于99.8%。4.符合下列条件的，评定为四级客户经理：⑴在本行连续从事信贷工作3年以上；⑵具有专科以上学历；⑶经济类（或会计类）初级职称或银行业专业人员职业资格《银行业法律法规与综合能力》、《公司信贷》、《个人贷款》等三门以上证书；⑷技能考核等级达5个D以上；⑸考核期内，百分制考核得分70分以上。5.符合下列条件的，评定为五级客户经理：⑴在本行连续从事信贷工作1年以上；⑵具有高中以上学历；⑶经济类（或会计类）初级职称或取得银行业专业人员职业资格《银行业法律法规与综合能力》、《个人贷款》等两门以上证书；⑷考核期内，百分制考核得分60分以上。6.有下列情形之一的，暂定为见习客户经理：⑴从事客户经理岗位不足1年；⑵百分制考核得分低于60分；7.其他百分制考核得分60分以上人员评定为六级客户经理。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第十一条', '对从支行行长或信贷主管岗位转岗为客户经理的，暂定为四级客户经理，待下一年度评定时按标准重新确定。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第十二条', '45周岁以上的女职工和50周岁以上的男职工参加业务技能测试测试合格后，按实际评定等级上调一级（一级除外）。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第十三条', '有下列情形的，在考评时给予加分。1.本人受到本级或市属部门表彰的，加计2分；受到市级或市属部门表彰的加计5分，受到市级或省属部门表彰的加计8分，受到省部级表彰的加计20分。2.代表本行参加级别（包括行管组）业务技能比赛或知识竞赛获得团体或单项前1－3名（或一、二、三等奖）的，分别加计3分、2分、1分和5分、3分、2分；盐城级别业务技能比赛获得团体或单项前1－3名（或一、二、三等奖），分别加计5分、3分、2分和10分、8分、5分；省级农商行系统业务技能比赛获得团体或单项前1－3名，分别加计15分、12分、10分，个人获得前10名的，分别加计20分、18分、17分、15分、14分、13分、12分、11分、10分、9分。3.发生本行认为可以加分的情形，经评级小组集体认定后予以加分。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第十四条', '有下列情形的，取消评级、降级或缓评级。1.受到记大过以上处分的，在处分期内取消原等级，降为见习级。2.受到服务对象投诉或其他因素引发重大舆情风险，对本行造成声誉影响的，已评级的降为见习级一年，未评级的缓评级一年。3.合规员工考核不达标或违规记分一年内累计记分100分以上的，按原评级下降一个等级评定，累计记分150分以上的，已评级的取消等级一年，未评级的缓评级一年。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第十五条', '在业务部门的领导下开展工作，并由总行信贷管理部进行考核与管理，凡有以下情形之一者，可提出诫勉：1.存、贷款日均余额或贷款客户数连续二个季度下降；2.不良贷款持续上升的；3.连续两个季度未完成主要业务指标的；4.客户投诉两次以上、经查实为主观原因的，被总行或上级管理部门通报的；5.其它因工作失误造成不良影响，需要诫勉的。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第四章  等级确定', '第十六条', '客户经理在日常工作中，凡有以下情形之一者，可提出黄牌警告，并在下一年度评定中降级任用：1.年度考核不称职的；2.诫勉后六个月业务实绩未有起色的；3.合规不达标且情节轻微的。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第五章  等级管理', '第十七条', '客户经理等级认定实行按年任用，按年调整。公司业务部、小微业务部由部门经理具体负责本部门客户经理等级申报及审核工作；三农业务部负责三农业务客户经理等级申报及审核工作。本行客户经理等级管理领导小组按程序审核后，确定年度客户经理等级。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第五章  等级管理', '第十八条', '客户经理按认定等级可分别从事下列岗位：四级及四级以上客户经理可从事公司贷款的调查、发放和客户经理五级以下的岗位。五级客户经理可从事100万元（不含）以上个体经营类贷款的调查、发放和六级以下的岗位。六级客户经理按实际岗位，可从事个人消费类贷款和100万元（含）以下个体经营类贷款的调查与发放，或专职从事不良贷款清收、存款组织、贷款推介、电子银行产品营销等工作。见习客户经理可作为1-6级客户经理的调查B角参与贷前调查，但不得独立承担贷前调查业务；也可从事存款组织、贷款推介、电子银行产品营销等工作。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第五章  等级管理', '第十九条', '客户经理上岗资格需达到本行岗位等级要求。对本办法出台前，实际从事岗位与等级要求不符的，给予一年达标宽限期。期满后仍未达标的，按程序进行竞岗。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第五章  等级管理', '第二十条', '实行客户经理等级评定公示、复议制度。客户经理对公示的等级评定结果有异议的，可向本行员工等级评定领导小组提出书面复议申请。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第五章  等级管理', '第二十一条', '等级评定结果与薪酬待遇挂钩，挂钩比例按照相关办法执行。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第六章  附则', '第二十二条', '本办法由信贷管理部负责制定、修改和解释。'),
(NULL, '信贷管理部', '《客户经理等级管理办法(试行)》', '第六章  附则', '第二十三条', '本办法经职工代表大会审议通过后，自2016年1月1日起施行。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第一章  总则', '第一条', '为着力提升金融服务小微企业能力，有效缓解小微企业贷款转贷中“先还后贷”的资金周转压力，缓释信贷风险，依据银监部门、省联社相关意见，结合本行相关信贷管理制度，特制定本办法。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第一章  总则', '第二条', '本办法所称“转贷”指从事合法、合规生产经营的小微企业，在正常生产经营过程中，因现金流不匹配，在流动资金周转贷款到期前（不包含固定资产贷款或项目融资），无法完全归集还贷资金，经企业主动申请并经本行同意，允许其在未全部归还即将到期贷款的情况下办理转贷。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第一章  总则', '第三条', '转贷模式。小微企业流动资金贷款转贷采用先贷后还模式，即在借款人结清贷款利息，但未全部结清即将到期贷款本金的情况下，向借款人发放新贷款，用于归还到期贷款本金。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第一章  总则', '第四条', '本办法所称的小微企业需符合工信部联企业〔2011〕300号文件中“各行业小型、微型划型标准”（附件1）。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第一章  总则', '第五条', '本行推行的“续益贷”坚持“依法合规、审慎推行、名单管理、风险控制”的原则。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第二章  贷款对象及条件', '第六条', '申请转贷客户一般为本行存量小微企业，风险分类形态至少关注以上；对本行的核心客户、重要客户、高黏性客户确有转贷需求的，在符合本办法规定条件的基础上，经总行贷审会同意后，可参照本办法执行；'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第二章  贷款对象及条件', '第七条', '借款人应满足以下基本条件：（一）借款人信用状况良好，还款能力与还款意愿较好，无欠本欠息。（二）借款人依法合规经营，所属行业符合国家法律法规和产业政策要求，符合环保政策要求，无违法违规经营或偷逃税费行为。（三）生产经营正常，销售收入、存货及应收款项周转、经营利润、现金流、对外履约等方面未出现明显异常，资产负债率、流动资产周转率、销售利润率、坏账率等关键性财务指标优于同行业平均水平。（四）属于本行“增加”、“保持”类客户，对符合条件的“减类”客户，承诺按期压降，或接受利率适当提高的，可参照办法执行。（五）拟转贷的流动资金贷款尚未逾期违约，且符合新发放流动资金周转贷款的4个条件：借款生产经营活动正常，能按时支付利息；重新办理了贷款手续，责任人明确；贷款担保有效；属于周转性贷款。（六）办理转贷时还本比例一般不低于10%，最低不少于20万元；客户自有还贷资金应在新发放贷款出账前已归还到位或足额存入指定账户。（七）贷款担保级次不降低，贷款担保方式发生变化的，调整（或追加）的担保实力不低于原有的担保代偿能力，能完善相关手续，确保法律效力。（八）本行规定的其他条件。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第二章  贷款对象及条件', '第八条', '存在下列情形之一的，不得办理转贷：（一）被本行列为“退出”类客户；（二）近期有法律诉讼事项、受到重大处罚或存在妨碍司法执行的因素；（三）对外担保到期未履行担保义务；（四）涉及高利贷或非法集资的;（五）本行认为的其他条件。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第三章  贷款额度、期限、利率、用途', '第九条', '“续益贷”贷款转本额度一般不高于原贷款本金额度的70%。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第三章  贷款额度、期限、利率、用途', '第十条', '贷款期限。转贷后贷款期限不得超过原贷款期限且不超过一年。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第三章  贷款额度、期限、利率、用途', '第十一条', '贷款利率。贷款利率按正常发放的同类贷款利率标准执行。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第三章  贷款额度、期限、利率、用途', '第十二条', '贷款用途。转贷部分的用信申请书、借款借据及信贷系统中的贷款用途统一规定为“转贷”，自筹部分按实际用途填写并按规定支付使用。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第十三条', '客户筛选。小微企业流动资金贷款转贷实行“名单制”管理，经客户申请，支行（部）按照准入条件审查确定后，报总行授信评审部认定、审批，对符合条件的小微客户纳入“名单库”，由总行统一录入信贷管理系统，按转贷流程实施管理和业务操作。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第十四条', '转贷申请。纳入“名单库”管理的客户，在生产经营过程中，因回笼不及时等原因造成临时资金困难确需转贷的，应至少在原贷款到期前10日向我行提出书面申请。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第十五条', '受理调查。调查人员根据“续益贷”贷款准入标准，按照信贷尽职调查要求对小微企业转续贷资格、转贷原因、经营情况、财务情况、担保情况等进行尽职调查。通过现场调查和其他渠道尽可能的搜集、核实借款人经营与财务状况、对外融资与担保措施、关联关系以及企业主个人资信等信息，客观分析借款人的还贷意愿、还贷能力及贷款的担保情况等，准确判断和识别贷款转贷后还款可能性及风险控制措施落实事项。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第十六条', '贷款审查。审查人员在审查信用风险的基础上，重点审查业务办理的规范性和合规性，防范操作风险和道德风险。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第十七条', '贷款审批。客户经理需填写《公司流动资金借款转贷申请(审批)书》（附件2），依据本行转授权规定的权限逐级审批。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第十八条', '合同签订。（一）所有小微企业转贷贷款，必须与客户重新签订单笔借款合同及相应的担保合同。（二）借款合同的“贷款用途”一栏中直接填明“转贷”。（三）签订合同时，应严格履行向担保人的告知义务，借款人和保证人必须同时签订《小微企业流动资金贷款转贷确认书》（附件3），防止保证人以贷款人和借款人“恶意串通”骗取担保为由行使债务抗辩权。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第十九条', '贷款发放与支付。（一）小微企业流动资金贷款转贷一律采取“受托支付”方式，无需客户提供支付委托书、购销合同等支付凭证，信贷系统中固化交易对手为借款客户自身，资金要封闭运行，确保新发放贷款全部用于归还已有的贷款，防止新发放贷款脱离封闭或被客户挪用。（二）贷款发放时，经办客户经理须填写一式两份《“续益贷”贷款扣款确认函》（附件4），连同借款凭证等材料一同交贷款发放岗入账并即时进行还款操作，严禁贷款资金在账户停留。贷款发放岗完成还款操作后在《“续益贷”贷款扣款确认函》签字确认，一份交事后监督随传票装订，一份交客户经理与信贷资料统一保管。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第二十条', '信贷资产分类。根据企业经营状况，严格按照贷款风险分类基本原则、分类标准，充分考虑借款人的还款能力、正常营业收入、信用评级以及担保等因素，合理确定续贷贷款的风险分类；符合正常类标准的，应当划为正常类。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第二十一条', '贷后检查。客户经理应定期开展转贷贷款的贷后管理，每季对客户至少开展一次实地调查，及时出具风险评估和风险预警报告。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第四章  贷款流程', '第二十二条', '贷款回收。客户经理应至少在转贷的贷款到期前10日与借款人取得联系，通知贷款到期，提醒备款还贷。贷款到期后，按贷款合同的规定及时收回贷款本息。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第五章  “名单制”管理客户的退出与更新', '第二十三条', '各支行（部）要加强对纳入转贷“名单制”客户的动态管理，根据小微企业经营发展以及相关条件的变化情况，及时进行调整、更新。对符合下列条件的客户，及时上报总行从“名单库”中退出：（一）企业发展壮大，依据《中小企业划型标准规定》划型，已经超出小型或微型企业标准的；（二）借款人、担保人的组织架构、公司治理、内部控制，以及生产经营或财务状况等情况发生重大不利变化的；（三）客户自己申请退出“名单库”的；（四）主要经营证照到期，没有办理年检或更新手续的；（五）借款人、担保人征信系统出现不良信用记录，主要负责人（或经营者）出现不良行为记录的；（六）擅自改变借款用途，挤占挪用信贷资金的；（七）本行认定的其它情形。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第五章  “名单制”管理客户的退出与更新', '第二十四条', '对新增符合条件的小微企业流动资金贷款转贷客户，各支行（部）在调查并报总行认定、审批后，及时纳入“名单制”管理。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第六章  责任及处罚', '第二十五条', '“续益贷”贷款发放后，支行（部）信贷内勤要建立“续益贷”贷款管理台账，并做好日常管理。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第六章  责任及处罚', '第二十六条', '对未按要求办理“续益贷”贷款的，总行将按相关制度规定给予经济处罚或行政处分。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第六章  责任及处罚', '第二十七条', '对在办理“续益贷”贷款过程中，额外向客户收取费用的，按照《商业银行股份有限公司员工违规行为处理办法》从重处罚；涉嫌犯罪的，移交司法机关依法追究其刑事责任。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第七章  附则', '第二十八条', '本办法由信贷管理部负责制定、修改和解释。'),
(NULL, '信贷管理部', '《小微企业流动资金贷款“续益贷”管理办法及流程》', '第七章  附则', '第二十九条', '本办法自发文之日起实施。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第一章  总则', '第一条', '为规范应收账款质押担保管理，切实防范风险，根据《中华人民共和国物权法》，《应收账款质押登记办法》、《中国人民银行征信中心应收账款质押登记操作规则》、《农村电网建设与改造工程电费收益权质押贷款管理办法》，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关信贷业务制度，制定本办法。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第一章  总则', '第二条', '本办法所称应收账款是指权利人因提供一定的货物、服务或设施而获得的要求义务人付款的权利，包括普通应收账款和收费权，但不包括因票据或其他有价证券而产生的付款请求权。其中，普通应收账款是指权利人因向义务人提供货物、服务等而要求义务人付款的权利；收费权是指权利人合法拥有的经相关法律法规明确可以质押的就某一设施向使用人收取费用的权利。应收账款付款人是指与借款人因业务往来而发生债权债务关系的债务人，是质押应收账款的付款人。应收账款质押贷款是指借款人其拥有最终处置权，并经付款人确认应收账款的收款权利作为质押发放的贷款。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第一章  总则', '第三条', '本行可接受作为质押担保的普通应收账款包括：（一）销售产生的债权，包括销售货物，供应水、电、气、暖等。（二）提供服务产生的债权，包括提供劳务、咨询、工程建设等。（三）出租产生的债权，包括出租动产或不动产。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第一章  总则', '第四条', '本行可接受作为质押担保的收费权包括：（一）公路、公路桥梁、公路隧道或者公路渡口等不动产收费权。（二）农村电网建设与改造工程电费收费权。（三）法律、行政法规规定可以出质的其他收费权。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第一章  总则', '第五条', '办理应收账款质押贷款应遵循“谨慎择优、质押从严、集中授信”的原则。谨慎择优。为防范应收账款质押贷款风险，业务人员应认真审查借款人、应收账款债务人的有关资料，择优选择信誉良好，资金实力雄厚的应收账款债务人，严把准入关，谨慎办理每一笔业务。质押从严。质押的应收账款要充分考虑其真实性、有效性、到期支付的可能性等因素，从严办理。集中授信。应收账款质押贷款应包含在借款人的全部授信额度以内，授信额度应根据借款人的履约情况、付款人支付能力等因素合理确定。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '公司业务部、小微业务部（以下简称“经办行”）负责应收账款质押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '授信评审部负责应收账款质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定应收账款质押贷款管理办法及操作规程，对应收账款质押贷款业务的用信管理和贷后检查进行监督。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '运营管理部根据权限负责对应收账款质押贷款的用款进行审查。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '计划财务部负责制定应收账款质押贷款业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对应收账款质押贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核应收账款质押贷款管理办法和操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十三条', '资产保全部负责对应收账款质押贷款业务形成的不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十四条', '审计部负责对应收账款质押贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十五条', '贷款对象经主管部门批准设立，由依法履行工商行政管理职能的机构核准登记，取得企业法人营业执照，并按规定办理年报、年检手续的各类企事业单位。电费收益权质押贷款，仅限于电网经营企业'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十六条', '除本行信贷管理制度规定的贷款条件外，所质押的应收账款还必须同时符合以下条件：（一）出质人（应收账款收款方，下同）已经向付款方提供了货物或服务，应收账款已经现实产生；或应收账款虽未现实产生，但出质人已有特定交易对象，交易双方已经签署合法有效的交易合同（购销合同、服务提供合同、工程建设合同、租赁合同等，下同），应收账款预计将于未来产生。（二）应收账款权属清楚，没有瑕疵，出质人未将其转让给其他任何第三人，也未在其上为其他任何第三人设定任何质权或其他优先受偿权。（三）应收账款出质人不得为自然人、学校、医院；应收账款对应的付款人不得为自然人。（四）应收账款尚未到期，且到期日不得晚于所担保债务的到期日。同时，应收账款的到期期限一般应在1年以内，最长不超过2年。对付款人信用等级在AA级（含）以上，且为分期付款方式的应收账款，到期期限可延长至3年。对因政府采购和军队采购形成的应收账款，除必须同时满足上述条件外，政府采购行为还必须由地市级（含）以上政府的采购部门统一组织，且符合《中华人民共和国政府采购法》的相关要求；军队采购行为必须由军级（含）以上单位的采购部门统一组织，且符合《军队物资采购管理规定》的相关要求。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十七条', '除经总行同意，经办行不得接受以下应收账款质押：（一）出质人尚无特定的交易对象，或虽有特定对象，但双方并未签署合法有效的交易合同、货物或服务尚未提供，目前尚未实际产生的应收账款。（二）涉及特许经营、专利、商标、知识产权等市场不易定价的产权交易形成的应收账款。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第四章  贷款期限、金额、利率及基本操作要求', '第十八条', '贷款期限贷款期限应根据借款人生产经营周期、还款能力，并结合应收账款的到期期限合理确定，并根据所质押应收账款的回款特点制订合理的还款计划，到期后不得办理展期。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第四章  贷款期限、金额、利率及基本操作要求', '第十九条', '贷款金额贷款金额应根据实际业务需要和应收账款作为第一还款来源或第二还款来源的不同，综合考虑应收账款出质人及付款方的资信情况、应收账款质量、结构、期限、付款进度安排及可控程度、预期坏账比率、交易合同约定的义务履行情况、违约事项及违约金等相关因素合理确定。以应收账款质押作为担保的，质押率一般不超过60%；其中，借款人与出质人为同一人，所质押应收账款为第一还款来源。质押率的计算公式：质押率=[应收账款质押贷款金额（含期限内利息）/应收账款金额（合同值）]×100%≤60%'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第四章  贷款期限、金额、利率及基本操作要求', '第二十条', '贷款利率应收账款质押贷款根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第四章  贷款期限、金额、利率及基本操作要求', '第二十一条', '基本操作要求以应收账款质押作为担保的，应按照人民银行《应收账款质押登记办法》、《中国人民银行征信中心应收账款质押登记操作规则》的规定，在人民银行应收账款质押登记公示系统（以下简称质押登记系统）中进行查询和登记。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十二条', '贷款操作程序为借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后管理及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十三条', '借款人申请除本行贷款管理办法规定外，借款人申请办理应收账款质押贷款时还须提供如下资料：（一）出质人与付款方之间对应本笔应收账款的交易合同。（二）货运单据、发票、经业主确认的工程进度表等能够证明应收账款真实性、确定应收账款实有金额的相关材料原件。（三）本行需要的其他材料。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十四条', '贷款受理与调查经办行客户经理在调查时，除须按照相应信贷业务和贷款担保管理的一般要求进行调查外，还应遵循以下要求：（一）调查了解应收账款及交易双方是否符合本办法规定的条件，应收账款对应的交易是否存在争议或纠纷；交易双方是否存在关联关系、在本行有无不良信用记录。（二）收集取得可以证明应收账款真实性与合法性的必要资料，并核实资料的真实性。（三）调查了解付款方是否确已收妥货物、接受服务或租赁使用动产或不动产，是否确认应收账款金额及付款期限，是否已经支付了部分款项。（四）登录人民银行质押登记系统，查询出质人是否已将拟质押的应收账款的部分或全部出质或转让给其他第三人、是否存在异议登记等。查询后应下载保存查询证明等相关页面资料，作为报送信贷审查审批的材料。经办行操作人员通过系统查询无法准确判断拟质押的应收账款是否已经出质或转让的，应向出质人进一步了解情况。对于已经存在的可能导致异议或引发歧义的登记，应要求出质人与原质权人协商变更登记。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十五条', '贷款授信、用信审查审批应收质押贷款的授信、用信审查审批按本行《转授权管理办法》执行。信贷审查人员在审查应收账款质押贷款时，除须按照相应信贷业务和贷款担保管理的一般要求进行审查外，还应重点审查以下内容：（一）交易双方的交易关系是否良好稳定，应收账款的付款方式及付款周期是否符合行业交易惯例，应收账款账龄结构、周转率、坏账率等是否合理。（二）应收账款是否真实可靠、余额是否准确，付款方的付款意愿及付款能力是否存在问题。（三）以关联企业之间的应收账款质押的，要从严审查交易背景真实性、合理性和定价的公允性。（四）对以符合本办法第四条第（一）款规定的预计将于未来产生的应收账款质押的，还应对交易合同的真实性、合同履行的可能性、付款安排等进行重点审查。（五）对以符合本办法规定的政府采购形成的应收账款质押的，还应对应收账款纳入政府采购预算的情况，以及政府财政（上级主管部门）向出质人核批的年度相关用款计划执行情况进行重点审查。（六）对以符合本办法规定的军队采购形成的应收账款质押的，还应对出质人纳入“军队物资供应商库”情况，交易双方合作记录和相关预算执行情况进行重点审查。（七）对因提供劳务咨询、承接工程、租赁等非销售货物原因所产生的应收账款质押的，还应对其交易的真实性、定价的合理性等从严审查。以应收账款质押办理信贷业务的，审查审批时应将在质押登记系统中办妥质押登记、开立应收账款收款专户等作为放款前提条件。其中，对以符合本办法第四条第（一）款规定的预计将于未来产生的应收账款质押的，还应将出质人按交易合同约定提供货物或服务、质押应收账款现实产生作为放款（含开立保函、信用证，银票承兑等，下同）前提条件，且信贷业务实际办理金额不得超过经质押率调整后的应收账款已现实产生的部分。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十六条', '合同签订信贷业务审批通过后，有权签约人与客户签订借款合同（含开证协议、承兑协议、开立担保协议等，下同）、与出质人签订《质押合同》、《应收账款质押登记协议》等，必要时应与出质人及付款方签订三方协议，确保应收账款资金划入指定账户。（一）经办行应与客户在借款合同中约定但不限于以下内容：在融资期内如出现下列情况之一，经办行有权宣布应收账款质押担保项下的信贷业务提前到期，并要求客户立即支付本行未偿还债务本息，或追加经本行认可的合法、有效、足值的其他担保：1.客户在本行的信贷业务出现逾期、垫款、欠息等不良行为。2.应收账款出质人对付款方的应收账款坏账率连续2个月上升。3.应收账款出质人对付款方已到期未收回的应收账款占对该购货方应收账款余额的5%以上。4.应收账款出质人与付款方或其他第三方产生贸易纠纷（包括但不限于质量、技术、服务方面的贸易纠纷）、债务纠纷和债务追索，导致应收账款可能无法到期按时偿付的。（二）经办行应与出质人在《质押合同》中约定但不限于以下内容：1.明确出质应收账款的到期期限、金额、支付方式、付款方名称及地址、产生应收账款的交易合同履行情况等相关信息。2.若付款方或任意第三方对出质人的应收账款提供了任何形式的担保，可以转让的，出质人应一并转让给本行；不能转让的，出质人应在本行需要时协助本行行使质权。（三）经办行应尽量与出质人签署账户监管协议。确实无法签署账户监管协议的，经办行还应与出质人在《质押合同》中增加但不限于以下账户监管条款：1.出质人在经办行开立应收账款收款专户，且出质人承诺与付款方约定应收账款回款必须转入该收款专户，未经经办行同意，不得变更收款账户。2.经办行有权对应收账款收款专户的支取进行控制。在所担保的债务到期清偿前，出质人不得支取已质押应收账款的收回款项。3.当应收账款所担保的债务出现逾期（含本条第（一）项所规定的由经办行宣布提前到期后形成逾期，下同）时，经办行有权从应收账款收款专户中直接扣划质押应收账款的已收回款项。4.出质人与借款人为同一人的，经办行还应与出质人约定：出质的应收账款逾期未收回的，经办行有权要求出质人在应收账款收款专户中保留不少于所担保债务本息的金额；当应收账款所担保的债务出现逾期时，经办行有权对未收回的债务本息行使抵销权和追索权，即可根据未偿还的债务本息等额处置出质人在本行任一账户的相应金额。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十七条', '贷款发放与支付有权签约人签订相关合同协议后，经办行操作人员应登录质押登记系统进行查询，确认出质人未将拟质押的应收账款出质或转让给其他第三方、也不存在任何异议登记后，根据信贷审批书的要求，在质押登记系统办理质押登记，并上传《应收账款质押登记协议》。经办行操作人员办妥质押登记后，将登记证明等相关页面下载保存作为前提条件落实情况的材料。在确认应收账款质押已经办妥、其他前提条件也已落实的情况下按总行转授权规定进行放款与支付操作。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十八条', '贷后管理及监管信贷业务办理后，相关人员应将有关应收账款质押的查询证明、登记证明等相关材料和记录作为信贷档案的一部分，按照相关要求进行归档。在贷后管理中，经办行除按照所办理的信贷业务和贷款担保管理的一般要求进行贷后管理之外，还应遵循以下管理要求：（一）了解应收账款付款方对出质人的付款履约情况，必要时应了解付款方资信变化情况。（二）应收账款到期前，视情况敦促出质人通知付款方及时支付款项。（三）经办行应与信贷人员密切协作，监控应收账款收款账户的款项汇入和汇出情况，并根据应收账款所担保债务的履行情况，按照《质押合同》的约定，及时冻结扣划相应款项。（四）出质人或其他利害关系人对已质押给本行的应收账款提出异议的，经办行应按照《商业银行股份有限公司应收账款质押登记公示系统管理办法》的相关规定进行处理。对确属登记内容错误的，经办行应及时向有权部门说明情况，经部门负责人确认并采取相应的风险防范措施后，信贷人员按照部门负责人的确认意见对原质押登记进行修改，并将修改后的登记证明及相关页面资料按照相关要求进行作业监督并归档。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十九条', '贷款偿还客户足额偿还债务后，信贷人员应在10个工作日内在应收账款质押登记系统中办理注销登记。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第六章  附则', '第三十条', '本办法由信贷管理部负责制定、修改、解释。'),
(NULL, '信贷管理部', '《应收账款质押贷款管理办法及操作规程》', '第六章  附则', '第三十一条', '本办法自印发之日起实施。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第一章  总则', '第一条', '为规范本行房地产开发贷款管理，根据《中华人民共和国物权法》、《中华人民共和国担保法》、《商业银行房地产贷款风险管理指引》、《项目融资业务指引》等有关法律法规，及本行《信贷管理制度》、《贷款业务尽职管理办法》等信贷管理的相关规定，制订本办法。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第一章  总则', '第二条', '房地产开发贷款管理的任务是通过建立健全房地产开发贷款管理制度，完善房地产开发贷款手续，规范房地产开发贷款操作，强化贷款“三查”制度，防范房地产开发贷款风险。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第一章  总则', '第三条', '本办法所指房地产开发贷款是指向本行辖区范围内的房地产开发企业发放的用于开发、建造向市场销售、出租等用途的房地产项目的贷款。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第一章  总则', '第四条', '房地产开发贷款的发放，必须符合国家产业政策、信贷政策，遵循贷款效益性、安全性、流动性原则。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第五条', '公司业务部、小微业务部（以下简称“经办行”）负责房地产开发贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '授信评审部负责对房地产开发贷款业务的授信管理。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定房地产开发贷款管理办法及操作规程，对房地产开发贷款业务的用信管理和贷后检查的监督。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对房地产开发贷款用款进行审查。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '计划财务部负责制定房地产开发贷款贷款业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '风险管理部负责对房地产开发贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核房地产开发贷款管理办法和操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '资产保全部负责对房地产开发贷款业务形成的不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第二章  组织管理体系', '第十三条', '审计部负责对房地产开发贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第三章  贷款的对象和条件', '第十四条', '贷款对象经国家房地产业各主管部门批准设立、工商行政管理机关核准登记，取得企业法人营业执照及房地产开发企业资质证书，并按规定办理年检手续的各类房地产开发企业。本行对开发企业实现“名单制”管理，名单由授信评审部建立并对贷款经办行公布，对不符合条件的不予准入，经办行应对“名单制”内的企业贷款做好经营及资金的监测，有效防控信贷风险。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第三章  贷款的对象和条件', '第十五条', '房地产开发贷款的借款人应符合《信贷管理制度》规定，同时应具备以下条件：（一）具有法人营业执照，企业经营管理制度健全，财务状况良好；（二）具有相应房地产开发企业资质证书，并经年检合格；（三）在本行开立基本存款账户或一般存款账户；（四）企业具有良好的经营业绩和信誉，有按期偿还贷款本息的能力；（五）开发项目必须已依法取得“四证”,即：《国有土地使用证》、《建设用地规划许可证》、《建设工程规划许可证》、《建筑工程施工许可证》；（六）开发项目自筹资本金比例不得低于35%，并已投资到位；（七）能提供本行认可的有效担保；（八）本行规定的其他条件。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第三章  贷款的对象和条件', '第十六条', '严禁发放下列房地产开发贷款：（一）严禁对“四证”不全的房地产开发项目发放任何形式的贷款；（二）严禁违反房地产开发项目自有资金比例规定对房地产开发企业发放贷款；（三）严禁发放房地产开发贷款跨地区使用（银团贷款除外）；（四）严禁以流动资金名义发放房地产开发贷款；（五）严禁向经国土资源部门、建设主管部门查实具有囤积土地、囤积房源行为的房地产开发企业发放任何形式的贷款；（六）严禁向取得土地使用权两年以上尚未开发的房地产企业发放任何形式的贷款。（七）严禁用于购买土地。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第四章  贷款期限和利率', '第十七条', '贷款期限根据借款人开发项目预测投资收益的能力及回收期，合理确定贷款期限，贷款期限一般不超过五年。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第四章  贷款期限和利率', '第十八条', '贷款利率根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第十九条', '房地产开发贷款操作程序为借款人申请、受理与调查、授信审查审批、用信审查审批、合同签订、发放与支付、贷后管理、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十条', '房地产开发企业申请房地产开发贷款须提交下列资料原件及复印件：（一）单位资料1.经年检并核准登记的法人营业执照、法人组织机构代码证、税务登记证（或新版营业执照），法人代表及其授权代理人证明书和印鉴样本，股份制企业的借款授权书，公司章程，本期财务报表和经审计的近3年年报，验资报告、开户许可证的正副本；2.房地产开发企业资质证书及年检证明；3.借款申请报告。其主要内容包括：借款项目名称、金额、用途、期限、用款计划和还款来源等；4.合法有效的担保手续（包括担保人同意担保意见书，抵（质）押物所有权证明等）；5.本行认为需要提交的其他资料。（二）项目资料1.申请贷款项目的有关材料，主要包括项目建议书、可行性研究报告及批准文件、项目年度投资计划以及批准文件、《建设用地规划许可证》、《建设工程规划许可证》、《国有土地使用证》、《建筑工程施工许可证》、土地出让合同及地价款缴纳凭证复印件、建设规划红线图；2.项目开发建设自有资金落实的证明文件、资料；3.本行要求提供的其他资料。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十一条', '调查的主要内容参照对固定资产贷款的要求进行初步审查，并重点审查确认以下几项内容：（一）借款人是否具有进行房地产开发建设的资格；（二）借款人开发建设资格批准文件是否在有效期内；（三）贷款项目是否经有权机关批准。项目建议书、可行性研究报告及批准文件、项目年度投资计划、设计以及批准文件等是否完备、有效；（四）项目建设条件落实情况1.贷款项目承建人与借款申请人是否相符；2.贷款项目是否列入国家或地方政府的建设规划；3.经济适用住房是否列入国家经济适用住房（安居工程）建设计划；4.《建设用地规划许可证》、《建设工程规划许可证》、《国有土地使用证》、《建筑工程施工许可证》的原件与复印件是否一致；5.房地产开发贷款项目的自有资金比例不低于35%，借款人必须按照规定比例及时足额地将自有资金存入本行，或投入项目前期工程使用。借款人自有资金没有足额到位的，不予批准贷款；6.开发项目的文件是否完整、真实、有效，是否能进行实质性开发建设。（五）建筑工程施工单位和监理单位的落实情况；（六）联合开发的合同或协议的合法性；（七）贷款担保的合法性调查。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十二条', '房地产贷款项目，必须按照以下方法进行风险评估：（一）项目主要关系人（如其母公司、主要投资人）资信评估。包括主要关系人经营情况、财务状况、信用情况的考察分析，各关系人之间的产权及其他控制关系等。（二）项目概况和建设情况评估。包括对项目建设的基本情况、建设条件、实施进程、设计施工监理情况、环境保护条件等进行综合论证分析，分析投资该项目是否符合国家产业政策、区域发展战略及本行信贷投向政策，对建设进程、设计、施工、监理情况、环境保护条件等进行评估。（三）项目市场情况评估。贷款调查评估人员要通过市场调查与预测，分析房地产投资环境、经营方式和项目的市场定位，对项目的市场竞争能力、销售前景作出判断。（四）项目投资估算与融资方案评估。1.对项目的投资估算进行分析和审查，要分析项目工程各项内容和成本费用是否齐全，有无任意扩大取费范围和提高标准，扩大投资规模；分析投资估算中有无漏项、少算或人为压低造价等情况；分析投资构成比例合理与否。在此基础上，调整项目成本费用。2.对项目资金来源的评估，应对项目资金来源的合理性、可靠性进行分析论证，侧重于对本行贷款以外的其他资金来源的合理性、可靠性进行评估。在对项目各项资金来源进行调查分析的基础上，确定项目切实可行的融资计划。3.测算项目资金的回收期和贷款偿还期。（五）财务效益评估。财务效益评估应在进行基础财务数据测算与分析的基础上，根据国家现行财税制度及有关规定，测算财务效益评估指标，考核项目的盈利能力。（六）风险分析。房地产项目风险分析应分析不确定性因素对项目可能造成的影响，并进而分析可能对贷款偿还带来的风险。房地产项目风险分析主要包括盈亏平衡点分析、敏感性分析等,进行风险分析的因素主要有：租售量、租售价格、出租率、开发周期、项目总投资等。在对项目不确定性进行分析的基础上，须对项目的风险点作出判断并尽可能提出防范风险，控制项目不确定性的措施。（七）银行效益与风险防范措施评估。1.银行效益评估应采用定性和定量的分析方法，具体分析项目资金在本行结算情况及带给本行的相关效益。2.风险防范措施评估。在评估第一还款来源的基础上，应对借款人提供的保证、抵押、质押等贷款担保措施的可行性进行分析，并分析项目贷款的主要风险，如何采取措施降低、规避、分散风险。（八）评估总结。1.应对借款人基本情况、项目建设情况、市场状况、项目投资估算及资金来源筹措、项目财务效益、项目建设的环保情况、风险分析、银行效益与风险防范措施及项目的经济、社会经济效益等方面做综合性论述。2.应对贷款项目当前存在的有关问题进行阐述，并提出建议及解决措施。3.应对贷款项目提出评估意见，就是否发放贷款、贷款的金额、条件、期限、利率等提出明确的意见。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十三条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十四条', '申请房地产开发贷款的借款人，应对借款合同提供本行认可的财产抵（质）押或第三方不可撤销的连带责任担保。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十五条', '房地产贷款担保可采取抵押、质押、保证等担保方式；经办行可根据借款人及担保人的具体情况，采用一种或同时采用几种贷款担保方式。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十六条', '用于抵（质）押的财产，须委托本行认可的资产评估机构进行评估。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十七条', '担保方式为抵（质）押的，经办行客户经理必须在签订抵（质）押合同后，与借款人、抵（质）押人共同前往登记部门依法分别办理房地产开发抵（质）押登记，取得抵（质）押登记证书。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十八条', '担保方式为抵（质）押的，申请开发贷款的借款人，应在借款合同签订前办理相应的财产保险。保险期限不得短于借款期限，投保金额不得低于贷款本金和预计利息金额，保险单不得有任何有损贷款人权益的限制条件，并应当明确本行为该保险的第一受益人。办理保险所需的一切费用由借款人负担。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十九条', '借款人办妥上述手续后，本行按房地产工程进度发放贷款，将资金划入借款人的结算账户，并严格监督借款人按合同约定支付方式支付贷款资金。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十条', '房地产开发贷款必须专款专用，借款人必须按合同规定或双方约定使用贷款。借款人如违反借款合同规定或双方约定使用贷款，本行有权停止贷款和提前收回部分或全部贷款，对不按合同规定或双方约定用途使用贷款的部分，按合同约定加收罚息。借款人应按借款合同约定的用途使用贷款并按约定的还款计划、还款方式偿还贷款本息。（一）贷款支用时，要严格按照项目建设的工程进度付款；要求借款人提供工程建设合同、建设单位的付款通知书、材料供应合同、设备采购合同等有关文件，作为支用贷款的依据；贷款资金的支用应该在项目自有资金之后；借贷双方约定本行贷款与其他资金按比例投入开发建设项目的，应审核其他资金投入的落实情况；（二）按揭贷款的首付款，应该交入双方约定在本行的指定账户，优先偿还贷款本息；（三）住房开发贷款的预（销）售应纳入资金监管范围，按照销售进度分期还款，确保项目销售80%前收回全部贷款本息；（四）转让、出售的商业用房开发贷款应按照销售进度分期还款，确保项目销售80%前收回全部贷款本息。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十一条', '贷款发放和支付按本行《固定资产贷款管理办法及操作规程》执行。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十二条', '经办行应在贷款后15个工作日内首次现场检查，检查客户是否按照借款合同约定的用途使用贷款，对未按借款合同规定用途使用的，应查明原因并提出处置的意见和建议。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十三条', '贷款项目贷后检查的时间与频率，按本行相关信贷管理规定执行。检查的重点：（一）信贷资金封闭运行执行情况；（二）项目资金及其他资金来源落实情况；（三）项目销售款归本行情况；（四）向其他金融机构的融资情况、与债权人的合作关系以及信用履约情况；（五）项目进展情况、投资是否突破预算、累计完成工作量与累计财务支出是否匹配；（六）主要材料供应及价格变化情况；（七）配套设施落实和施工质量等情况；（八）销售市场情况及销售资金回笼情况；（九）其他需要检查的内容。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十四条', '借款人如不能按照按期归还贷款，应在贷款到期前向本行提出展期的书面申请，经批准后，借款人、贷款人、担保人签订展期协议，已到期的抵（质）押登记、保险应该续办手续。贷款展期要从严控制，已经实现销售收入，但没有按销售收入的约定比例及时还贷的房地产开发贷款不得展期。项目开发贷款一般不得借新还旧。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十五条', '借款人按合同履行还款义务后，经办行应当与抵（质）押人向原登记机关办理注销登记。如借款合同履行期限届满，借款人未能按期归还贷款本息的，应按合同规定依法要求担保人承担担保责任。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第六章  附则', '第三十六条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第六章  附则', '第三十七条', '本办法由信贷管理部负责制定、解释和修订。'),
(NULL, '信贷管理部', '《房地产开发贷款管理办法及操作规程》', '第六章  附则', '第三十八条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第一章  总则', '第一条', '为规范本行担保公司担保贷款运作，确保信贷资金安全，分散、减少和化解贷款风险，根据《中华人民共和国担保法》、《贷款通则》等有关法律，本行《信贷管理制度》《贷款业务尽职管理办法》等有关信贷制度，特制定本办法。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第一章  总则', '第二条', '担保公司担保的贷款以安全性、流动性、效益性为原则。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第一章  总则', '第三条', '本办法所称的担保公司，是指与商业银行股份有限公司（以下简称“本行”）签署合作协议的经国家有权部门批准的从事中小企业及自然人贷款担保的担保公司。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第一章  总则', '第四条', '本办法规定的贷款担保种类为公司短期流动资金贷款和个人经营性贷款，担保方式为连带责任保证。一般不办理中长期贷款担保。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第一章  总则', '第五条', '由担保公司担保的贷款利率按本行规定的利率标准执行。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '各支行、营业部、小微业务部、公司业务部（以下简称“经办行”）须在本行确定的开展担保业务合作的范围内开展业务合作，以及贷款的受理和日常管理工作。其中，营业部扎口负责担保公司保证金账户的核算和管理工作。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '授信评审部负责对担保公司担保贷款业务的授信管理。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定担保公司担保贷款管理办法，开展与担保机构业务合作的日常管理工作，及审核担保机构是否符合本行准入条件，担保业务是否符合本行相关信贷管理要求，担保手续是否完备及用信工作。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '运营管理部根据权限负责对担保公司担保贷款业务用款进行审查、支付。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '计划财务部负责制定担保公司担保贷款业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对担保公司担保贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核担保公司担保贷款管理办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第十三条', '资产保全部负责对担保公司担保贷款业务形成的不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第二章  组织管理体系', '第十四条', '审计稽核部负责对担保公司担保贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第三章  担保公司准入条件', '第十五条', '凡与本行建立合作关系的担保公司必须符合以下条件：1.担保公司持有有效的营业执照、融资性担保机构经营许可证；2.实收资本在10000万元（含）以上；3.经市经济与信息化委员会备案；4.经本行认可的资信评估机构评级且信用等级在BBB级（含）以上；5.有健全的财务制度和完善的内部控制制度；6.经营时间一般应超过一年；7.专职从业人员不少于5人；8.担保公司必须在本行开立基本账户或一般账户；9.信用记录良好,在他行无未解决的违约事实。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第三章  担保公司准入条件', '第十六条', '对符合条件与本行建立合作关系的担保公司，本行与之签订担保合作协议。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第三章  担保公司准入条件', '第十七条', '与本行签订担保合作协议的担保公司应向本行提交下列资料：1.营业执照；2.融资性担保机构经营许可证（副本及复印件）和年检证明；3.真实、有效的财务报表；4.担保公司章程、资本金验资报告；5.担保机构主要股东、借款及对外担保情况；6.董事会成员和主要经营管理负责人名单和签字样本；7.董事会同意办理担保业务的决议；8.业务由授权委托人办理的，需提供担保机构法定代表人授权委托书原件；9.担保机构的收费标准；10.担保机构的内部控制制度及其他文件。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第四章  担保对象', '第十八条', '依法登记注册，生产经营有项目、产品有市场、发展有前景，且符合本行信贷管理制度中相关规定的企事业法人和自然人。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第五章  担保比例', '第十九条', '担保公司需在本行开立基本账户或一般账户。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第五章  担保比例', '第二十条', '担保公司对单户贷款的担保金额最高不超过担保公司净资产的10%。担保公司注册资金在1亿元（含）以上、信用等级在A级（含）以上的，担保的最高余额不超过其实收资本的10倍。担保公司注册资金在1亿元以下、信用等级BBB级（含）以上的，担保的最高余额不超过其实收资本的8倍。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第六章  担保业务办理', '第二十一条', '担保公司担保贷款由借款人实际经营所在地经办行负责办理，经办行在受理客户借款申请基础上进行实地调查认证，对拟同意发放担保公司担保贷款的，经办行应按信贷业务流程规定操作。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第六章  担保业务办理', '第二十二条', '担保公司独立对贷款申请人调查，决定是否同意担保。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第六章  担保业务办理', '第二十三条', '对双方调查同意的贷款申请人，按本行信贷管理制度中担保贷款办理规定和信贷操作规程要求以及合作协议约定办理贷款。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第二十四条', '信贷管理部对担保公司担保的贷款进行登记，负责监管单户贷款比例和担保贷款倍数。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第二十五条', '信贷管理部明确专人对担保公司的协议管理、经营状况、担保基金额度、代偿能力等日常管理实施监管。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第二十六条', '担保公司担保基金不足时，信贷管理部拒绝办理贷款登记。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第二十七条', '信贷管理部对担保公司超比例、超能力担保的，拒绝办理贷款登记。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第二十八条', '信贷管理部对担保公司出现违法、违纪经营的或担保公司出现影响担保能力情形的，拒绝办理贷款登记并以书面形式及时向行长室报告。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第二十九条', '经办行和信贷管理部应对担保公司担保的贷款建立专项统计台账，双线共管，详细记载每笔贷款的发放时间、贷款金额、到期日期、贷款用途等相关要素。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第三十条', '经办行对担保公司担保贷款管理登记责任人为各经办行运营主管（合规员）或者明确的其他专人。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第三十一条', '信贷管理部对担保公司提交的相关资料专夹保管。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第三十二条', '担保公司担保贷款的发放必须经本行审批同意后方可办理，经办行对担保公司担保贷款的档案资料专夹保管。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第七章  担保监管', '第三十三条', '当担保公司发生下列情形之一时，信贷管理部应及时对担保公司经营情况进行了解，并向行长室报告：1.组织形式发生变化；2.涉及重大经济纠纷；3.注册资本、法人代表、经营场所发生变更；4.拟被解散、撤销、破产；5.其他重要情况。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第八章  违约贷款处理', '第三十四条', '借款人出现违约行为，本行在借款人出现违约行为后应按合作协议规定及时通知担保公司，借款人出现违约行为超过协议规定时间仍未清偿本行贷款的，本行有权直接从担保公司担保基金账户中按合作协议扣收借款本息（不受单笔对应的保证金限制）,扣收不足部分以及合作协议无保证金约定的要求担保公司履行连带保证责任。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第九章  特别约定', '第三十五条', '担保公司向借款人每月收取的担保费用应符合国家政策规定。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第九章  特别约定', '第三十六条', '担保公司不得以任何形式抽逃、挪用注册资金。担保公司不得超其经营范围违法、违规经营。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第九章  特别约定', '第三十七条', '担保公司注册资本发生变动,应在取得相关证明文件后5日内报本行备案。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第九章  特别约定', '第三十八条', '担保公司出现未按规定代偿等违约行为,本行可按约定终止合作协议，并且追究担保公司法律责任。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第十章  附则', '第三十九条', '本办法由信贷管理部制订、修改和解释。'),
(NULL, '信贷管理部', '《担保公司担保贷款管理办法及操作规程》', '第十章  附则', '第四十条', '本办法自下发之日起执行。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第一章  总则', '第一条', '为推进现代农业发展，加快新型农业经营主体培育，实现农业现代化工程建设，加强本行新型农业经营主体贷款业务审慎经营管理，促进个人贷款业务健康发展，依据中国人民银行关于改善农村金融服务、增加“三农”信贷投入的相关通知、《个人贷款管理暂行办法》以及《江苏省农村信用社个人贷款管理实施办法（试行）》，制定本办法。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称新型农业经营主体是指以农村家庭为经营主体的各类种养殖专业大户和家庭农场（以下简称“专业大户”）。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第一章  总则', '第三条', '新型农业经营主体贷款是指本行所辖各支行、小微业务部（以下简称“支行”）向符合条件的专业大户发放的人民币贷款。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第一章  总则', '第四条', '本行开展新型农业经营主体贷款业务应当遵循依法合规、审慎经营、平等自愿、公平诚信的原则。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第一章  总则', '第五条', '本行应加强新型农业经营主体贷款用途与支付方式的管理。贷款用途应符合国家法律法规和有关政策，不得发放无实际指定用途的新型农业经营主体贷款。支行应在借款合同中与专业大户约定贷款发放条件、支付方式等事项，并按约定的支付方式和受托事项办理支付手续，有效防范新型农业经营主体贷款业务风险。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第一章  总则', '第六条', '应根据专业大户经营情况、偿债能力、信用状况，结合其收入、支出、资产负债、用途、担保情况等因素综合考虑，合理确定新型农业经营主体贷款金额和期限。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第一章  总则', '第七条', '应对专业大户进行内部评级，采用科学合理的授信方法，做好综合授信，建立新型农业经营主体贷款客户资信档案。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '支行负责新型农业经营主体贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '授信评审部负责新型农业经营主体贷款业务的授信管理。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '信贷管理部负责拟定新型农业经营主体贷款管理办法及业务操作规程，对新型农业经营主体贷款业务进行用信管理和贷后检查监督管理。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '运营管理部根据权限负责对新型农业经营主体贷款用款进行审查。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '风险管理部负责新型农业经营主体贷款的风险监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '合规管理部负责审核新型农业经营主体贷款管理实施细则及其操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第二章  组织管理体系', '第十四条', '审计部负责对新型农业经营主体贷款进行审计稽核。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第三章  贷款对象与条件', '第十五条', '新型农业经营主体贷款对象为以农村家庭为经营主体的各类种养殖大户和家庭农场经营者。（一）专业大户是指从事某种农产品生产、具有一定生产规模和专业种养水平的农户。粮食种植：土地规模在30-100亩；园艺种植：露天生产10-50亩；设施栽培：5-30亩；畜牧养殖：生猪年出栏50-500头，肉禽年出栏2000-20000羽，蛋禽存栏500-5000羽，奶牛存栏20-50头，水产养殖10-50亩。（二）家庭农场经营者是指以家庭成员为主要劳动力，从事农业规模化、集约化、商品化生产经营，以农业收入为家庭主要收入来源（占80%以上）的新型农业经营主体经营者。粮食种植：土地规模在100亩以上；园艺种植：露天生产50亩以上；设施栽培：30亩以上；畜牧养殖：生猪年出栏500头以上，肉禽年出栏20000羽以上，蛋禽存栏5000羽以上，奶牛存栏50头以上，水产养殖50亩以上。种养结合：主要产业规模达到上述标准下限的70%以上。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第三章  贷款对象与条件', '第十六条', '借款人应具备以下条件：（一）借款人（除异地贷款办法规定的对象外）户口在本市，或户口虽不在本市，但固定经营场所在本市服务范围内；（二）借款人信用状况良好，无不良信用记录；（三）贷款用途明确、合法；（四）贷款申请数额、期限和币种合理；（五）借款人具备还款意愿和还款能力；（六）能够提供本行认可的担保；（七）年龄在18-60周岁之间（经本行批准的除外）；（八）本行要求的其他条件。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第四章  贷款的种类、期限与利率', '第十七条', '新型农业经营主体贷款种类新型农业经营主体贷款按对象分为专业大户贷款、家庭农场贷款；按用途分为粮食种植贷款、园艺种植贷款、设施栽培贷款、畜牧养殖贷款等。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第四章  贷款的种类、期限与利率', '第十八条', '新型农业经营主体贷款期限主要根据专业大户的生产经营周期、未来现金流、还款能力和自身的资金供给能力等因素，并由借贷双方协商确定。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第四章  贷款的种类、期限与利率', '第十九条', '新型农业经营主体贷款的利率按本行利率定价办法执行。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十条', '客户申请专业大户以书面形式提出贷款申请，并提供以下资料：（一）申请人及家庭基本情况资料主要包括夫妻双方身份证、户口簿、结婚证、实际居住地证明资料以及家庭主要财产、收入明细资料等，单身的要求提供单身证明，并提供共同借款人。借款人为家庭农场经营者的还应提供：1.家庭农场的营业执照；2.家庭农场成员的身份证、户口簿、结婚证、实际居住地证明资料以及家庭主要财产、收入明细资料等，单身的要求提供单身证明，并提供共同借款人。（二）贷款用途证明材料主要包括各类合同、协议以及自有资金筹集的渠道与存储情况等证明资料。（三）各类授权文件主要包括查询申请人本人、配偶、共同借款人以及拟提供保证自然人的个人信用报告的书面授权，拟向借款人提供担保的公司类客户应出具查询企业征信的书面授权。（四）拟提供担保的相关资料个人提供担保的，主要提供担保人有效身份证明、家庭主要财产、收入情况证明资料等；法人提供担保的须按本行信贷业务操作规程规定，提供相应的资料。（五）本行要求提供的其他资料'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '支行应对申请人提供的授信申请材料的方式和具体内容提出要求，要求申请人恪守诚实守信原则，承诺所提供材料真实、完整、有效。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '支行接受借款人申请必须坚持借款人面谈制度。新型农业经营主体贷款面谈可与贷款调查同步进行，主要通过面谈了解借款申请人的基本情况、家庭收入、资产负债情况、家庭成员构成、贷款用途、贷款期限、还款来源、担保方式、法律纠纷等内容。客户经理应将上述内容据实填入《个人贷款面谈记录》，并与借款人共同签字确认。《个人贷款面谈记录》作为信贷档案统一规范保管（每个客户档案中至少有一份面谈记录）。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '尽职调查支行受理授信申请后，客户经理应按照双人调查原则履行尽职调查，形成书面报告，就贷款同意与否、贷款金额、利率、期限、担保方式等提出明确意见，并对其内容的真实性、完整性和有效性负责。（一）尽职调查的程序与方法贷款调查应以实地调查为主、间接调查为辅，采取现场核实、电话查问以及信息咨询等途径和方法。1.核实申请人提供资料的真实性、有效性与完整性；2.深入申请人生产、工作以及生活活动区域进行实地调查或走访；3.与申请人本人面谈，也可以同时根据贷款尽职调查要求与需要与申请人家庭成员、担保人等相关人员面谈；4.根据授权查询申请人、家庭成员及担保人等相关人的个人信用报告；5.采取实地调查与间接调查相结合的方法，对担保意愿性、合规性、充足性进行调查。（二）尽职调查要点1.申请人基本情况。借款人姓名、性别、年龄、身份证号、学历、婚姻状况、现居住地址及工作地址、联系方式、家庭成员、个人及家庭有无不合理大额负债、个人及家庭成员有无重大疾病、有无赌博等不良嗜好。2.申请人收入情况。个人及家庭总收入及来源，家庭纯收入及人均收入等情况及相关证明文件原件（如工资存折、房屋出租租赁合同协议、相关股权证明及其他收入证明文件）。3.借款用途。申请贷款的原因、用款计划，用途是否符合国家有关政策和本行信贷政策，实际用途与借款人申请时所述用途是否一致。4.还款来源、还款能力及还款方式。通过分析借款人的生产经营周期、未来现金流量、预计利润水平等，确认借款人还款来源是否真实，还款能力是否充足，还款方式确定是否合理。5.保证人担保意愿、担保能力或抵（质）押物价值及变现能力。保证人担保意愿是否真实，是否具备担保能力，抵（质）押物的权属是否清晰，是否易于变现，价值是否稳定等。6.其他需要调查或核查的事项。（三）尽职调查要求1.按《商业银行授信工作尽职指引》开展全面客观的调查，形成调查报告。2.尽职调查人员应对调查报告内容或调查结果的真实性、完整性和有效性负责。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '在不损害申请人合法权益和风险可控的前提下，可将贷款调查中的部分特定事项委托第三方代为办理，但必须明确第三方的资质条件与代理责任。不得将贷款调查的全部事项委托第三方完成。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '授信审查支行审查岗和本行授信评审部审查人负责对借款人基本条件进行审查。审查要点包括：（一）报送的材料是否齐全；（二）借款人、担保人的主体资格是否合法、合规；（三）借款人、担保人的资信情况、财务状况是否符合本行有关规定的条件；（四）贷款尽职调查反映的内容是否真实、完整、准确；（五）抵（质）押物的权属关系是否明晰；（六）借款人生产经营、财务状况、信誉状况、发展前景及内部管理是否良好；偿还能力、诚信情况是否符合规定；（七）借款用途是否合法、合规；（八）贷款金额、期限、利率是否合理；（九）第二还款来源是否充足、可靠、合法、有效；（十）贷款风险是否揭示，计划采取的防控措施是否可行；（十一）其他需要审查的内容。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '风险评价贷款风险评价应以分析借款人现金收入为基础，采取定量和定性分析方法，全面、动态地进行资信等级评定、贷款审查和风险评估。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '本行授信评审部负责对支行授信申请事项的审查，并按本行转授权管理办法提交有权审批人审批。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '对未获批准的授信申请，支行应及时告知客户并做好解释工作。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '本行将根据重大经济形势变化、违约率明显上升等异常情况，对支行贷款审查、审批环节进行评价分析，及时、有针对性地调整审批政策，加强相关贷款的管理。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '支行权限内的用信申请由支行有权人审查、审批；超出支行审批权限的用信申请，经支行用信小组会办后提交本行信贷管理部用信中心审核，对使用特殊文本的用信须交合规管理部进行审查。用信中心权限内，由用信中心有权人审查、审批后，报分管行长备案；用信中心权限外，提交用信会审议，形成审议结果后报行长备案。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十一条', '用信审查的主要内容包括：（一）借款人、担保人的资信情况是否符合制度的规定；（二）对客户提供的用信资料完整性、真实性、有效性进行审查核实，并分析其是否符合法律法规和本行的相关信贷政策；（三）查验授信文件的有效性，客户用信条件是否满足授信条件；（四）查验授信时是否提出限制性条款，有无落实到位;（五）其他需要审核的内容。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十二条', '用信申请时应提供下列资料：（一）用信审查审批表；（二）授信审批文件及授信限制性条款落实情况的证明材料；（三）用信需求的证明文件(包括购销合同、协议等)；（四）其他需要审核的材料。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十三条', '合同的签订应在视频监控下的签约台进行，客户经理根据本行信贷管理要求，与借款人及其他相关当事人签订相应的合同文本，需抵押的应按合同约定办理抵押物登记，所签订的合同文本未经换人审核的，不得外出。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十四条', '支行应在借款合同中与借款人明确约定贷款的金额、期限、利率、用途、支付、还款、结息方式等条款。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十五条', '上条所指“支付”条款，包括但不限于以下内容：（一）贷款资金的支付方式和本行受托支付的金额标准；（二）支付方式变更及触发变更条件；（三）借款资金支付的限制、禁止行为；（四）借款人应及时提供的贷款资金使用记录和资料。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十六条', '支行应在借款合同中约定由借款人承诺以下事项：（一）及时向本行提供完整、真实、有效的材料；（二）配合支行进行贷款支付管理、贷后管理及相关检查；（三）发生影响其偿债能力的重大不利事项时及时通知本行；（四）支行有权根据借款人资金回笼情况提前收回贷款；（五）其他需要借款人做出承诺的事项。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十七条', '支行应与借款人在借款合同中约定，出现以下情形之一时，借款人应承担的违约责任和支行可采取的措施：（一）未按约定用途使用贷款的；（二）未按约定方式进行贷款资金支付的；（三）未遵守承诺事项的；（四）突破约定财务指标的；（五）发生重大违约事件的；（六）违反借款合同约定其他情形的。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十八条', '签订合同时，应提请客户仔细阅读合同条款，对客户提出的问题或疑问，支行应予以解答。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第三十九条', '保险相关规定支行要求借款人、担保人到保险机构办理相关保险，保险期不得短于贷款主合同履行期限，保险金额不得小于主合同贷款本息，保险合同及保险单中应当注明出险时本行为保险赔偿金的第一受益人。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十条', '合同变更相关规定合同变更包括合同当事人变更、贷款额度调整、合同展期等。一般合同不允许变更，合同如有变更事项，必须由客户经理受理、审核，并按授信审批权限逐级上报审批。合同中格式条款的变更必须经本行合规管理部或本行法律顾问审核。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十一条', '经办客户经理根据审批意见，在信贷管理业务系统中录入相关信息，打印《放贷通知单》，交客户至柜面办理用款。柜面受理后，支行权限范围内的，由会计主管审查；权限外的，经用款中心有权人审批后发放。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十二条', '支行柜面人员应根据合同约定的受托支付或借款人自主支付方式支付贷款资金，监督贷款资金按约定用途使用。支行受托支付是指支行根据借款人书面的提款申请和支付委托，将贷款资金支付给符合合同约定用途的借款人交易对象。借款人自主支付是指支行根据借款人的提款申请将贷款资金直接发放至借款人账户，并由借款人自主支付给符合合同约定用途的借款人交易对象。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十三条', '借款人交易对象可以事先确定、具备使用非现金结算方式且合同金额在50万元（含）以上的新型农业经营主体贷款，贷款资金应当采用支行受托支付方式向借款人交易对象支付，其余贷款经支行同意可以采取借款人自主支付方式。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十四条', '采用受托支付的，支行应根据借款人申请约定的贷款用途，审核借款人提供的支付申请所列支付对象、支付金额等相关交易资料和凭证是否符合合同约定条件。审核同意后，支行按借款人授权将贷款资金通过借款人账户支付给借款人交易对象。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十五条', '采用借款人自主支付的，支行应按借款合同约定，要求借款人定期报告贷款资金支付情况，并通过账户分析、凭证查验或现场检查等方式核查贷款支付是否符合约定用途。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十六条', '签订最高额借款合同实行循环放款的，可以确定支付条件、方式的，在合同中约定具体条件以及相应的支付方式；不能确定支付方式的，应根据核定额度和期限内的每笔贷款的实际用途，在借款人《提款支付审查意见表》上予以明确。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十七条', '在贷款发放和支付过程中，借款人出现下列情形之一的，支行应与借款人协商补充贷款发放和支付条件，或根据合同约定变更贷款支付方式，停止贷款资金的发放和支付：（一）信用状况下降；（二）不按合同约定支付贷款资金；（三）借款人收入出现异常变化；（四）贷款资金使用出现异常；（五）违反合同约定，以化整为零方式规避支行受托支付。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十八条', '客户经理应在15个工作日内对新客户首笔贷款、新增贷款进行跟踪检查，主要检查客户是否按合同约定用途使用贷款，对未按借款合同规定用途使用贷款的，应查明原因并提出处置的意见和建议。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第四十九条', '支行对所有新型农业经营主体贷款的贷后检查应按照“发放与检查不为同一人”原则进行，检查的时间与频率按规定执行。贷后检查的重点为：（一）检查借款人、担保人的资产和生产经营、财务状况是否正常，按月或按季结息的是否正常付息，分期还款是否正常。（二）了解掌握借款人、担保人的家庭和经营出现的重大事项，分析这些变动是否影响或将要影响借款人的生产经营和贷款安全。（三）检查抵（质）押物的完整性和安全性，抵押物的价值是否受到损失，抵押权是否受到侵害，质押物的保管是否符合规定。（四）其他需要检查的内容。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第五十条', '短期贷款到期前15天、中长期贷款到期前30天内，客户经理要向借款人发送到期、担保人履行债务通知单并取得回执。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第五十一条', '支行应定期跟踪分析评估借款人履行借款合同约定内容的情况，并作为与借款人后续合作的信用评价基础。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第五十二条', '支行应当按照法律法规规定和借款合同的约定，对借款人未按合同承诺提供真实、完整信息和未按合同约定用途使用、支付贷款等行为追究违约责任。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第五十三条', '新型农业经营主体贷款需要展期的，借款人应在贷款到期前10天向支行提出书面申请，支行应对贷款展期原因进行审查，决定是否受理展期，并合理确定贷款展期期限，按规定程序报批，加强对展期的后续管理。一年以内（含）的新型农业经营主体贷款，展期期限累计不得超过原贷款期限；一年以上的新型农业经营主体贷款，展期期限累计与原贷款期限相加，不得超过该贷款品种规定的最长贷款期限。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第五十四条', '借款人出现违反合同约定情形的，支行应及时采取有效措施，必要时应依法追究借款人的违约责任。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第五十五条', '形成不良贷款的，支行应对其进行专门管理，并及时制定清收或盘活措施。对借款人确因暂时经营困难不能按期归还贷款本息的，支行可与借款人协商进行贷款重组。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第五章  贷款操作程序', '第五十六条', '对确实无法收回的新型农业经营主体不良贷款，支行按照相关规定对贷款进行上报核销后，应继续向债务人追索或进行市场化处置，直至债务本息清偿完毕。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第六章  责任追究', '第五十七条', '形成不良贷款的，按照《XXX商业银行股份有限公司不良信贷资产责任追究管理办法》执行。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第七章  附则', '第五十八条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《新型农业经营主体贷款管理办法及流程》', '第七章  附则', '第五十九条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第一章  总则', '第一条', '为规范本行新建商品房预售资金管理，防范操作风险，根据国家相关法律法规和《市新建商品房预售资金监管办法》等规定，结合本行实际，制定本实施办法。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第一章  总则', '第二条', '本办法所称新建商品房预售资金委托管理是指房地产开发企业在本行营业部、支行（以下简称“经办行”）开立商品房预售资金专用存款账户，账户开立机构依据《新建商品房预售资金委托管理协议书》（以下简称“协议书”）明确的权利、义务，对商品房预售资金专用存款账户资金实施委托管理的行为。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第二章  职责划分', '第三条', '经办行负责商品房预售资金专用存款账户的开立、协议书用印申请以及协议书签署后专用存款账户中预售资金的日常使用管理，对专用账户开立的合规性进行把关，并按协议书明确的权利、义务和房地产开发企业在市住建局备案的用款计划，对账户资金日常使用的真实性、合理性进行审核、拨付等。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第二章  职责划分', '第四条', '运营管理部负责专用存款账户开立资料合法性、合规性的审查和账户检查管理。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第二章  职责划分', '第五条', '信贷管理部负责协议书签署（用印申请）受理、初审。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第二章  职责划分', '第六条', '合规管理部负责协议书条款的法务审查。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第二章  职责划分', '第七条', '审计部负责商品房预售资金专用存款账户开户及预售资金委托管理等执行情况的检查、监督。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第三章  业务受理、审查、审批', '第八条', '经办行受理房地产开发企业预售资金委托管理申请后，单位负责人和客户经理应在2个工作日内对房地产开发企业进行实地核查，了解项目开发及楼盘销售情况，对符合条件的房地产开发企业，按照账户管理要求及时收集账户开户资料并开立商品房预售资金专用存款账户。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第三章  业务受理、审查、审批', '第九条', '经办行根据房地产开发企业提供的《协议书》（一般为市住建局提供的固定格式文本），由运营主管（无运营主管单位，由单位负责人落实专人）对协议书内容的进行审查、核实，确保协议书真实、完整且符合本行相关管理规定。确认无误后，通过流程银行平台“用印申请”流程提出用印申请，并将协议书逐页扫描件上传，提交信贷管理部审查。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第三章  业务受理、审查、审批', '第十条', '非固定格式文本，信贷管理部对协议书内容进行初审后，应提交合规管理部进行法务审查，并根据审查意见办理和答复。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第三章  业务受理、审查、审批', '第十一条', '按流程审查、审批结束后，同意用印的，经办行应及时派员到总行办理协议签署用印手续。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第四章  资金管理', '第十二条', '经办行依据协议书约定的权利及义务进行日常的监督和管理，按《市新建商品房预售资金监管暂行办法》要求，统一建立新建房预售资金的收入、支出台账，并密切配合房地产开发企业共同做好商品房预售资金的收存和使用。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第四章  资金管理', '第十三条', '房地产开发企业提出用款申请时，经办行应按照经住建局备案的用款计划和房地产开发企业提供的采购合同，采取受托支付方式进行拨付。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第四章  资金管理', '第十四条', '房地产开发企业有下列情况之一的，经办行按照协议约定，有权拒绝拨付：1.前次用款计划未按规定使用的；2.未按规定将预售资金（含定金、预付款）、房价款（含公积金贷款、商业贷款）等款项存入商品房预售资金专用存款账户的；3.预售资金未用于项目建设被挪作他用的；4.其他不符合使用规定的情形。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第四章  资金管理', '第十五条', '房地产开发企业与购房人解除《买卖合同》需退回预售资金时，经办行应审核市住建局出具的退房证明及存入预售资金专用存款账户的缴款凭证，无误后，办理该房房款监管解除，并将房款直接划入购房人账户。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第四章  资金管理', '第十六条', '完成房屋所有权初始登记后，经办行根据房地产开发企业提供的市住建局项目初始登记手续，办理专用存款账户结算和账户注销手续。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第五章  管理责任', '第十七条', '协议书签署后，经办行应严格按照约定履行监管职责，单位负责人为预售资金管理第一责任人，负责预售资金使用的审批；运营主管为预售资金专用存款账户监管直接责任人，负责预售资金收入和使用的审查、监测；客户经理具体负责与房地产开发企业日常事务的沟通和协调等。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第五章  管理责任', '第十八条', '因履职、监管不力或未按协议执行导致监管资金发生风险、损失或被上级部门处罚的，由相应岗位人员承担全额赔偿责任。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第六章  附则', '第十九条', '本办法由信贷管理部制定，并负责解释、修改和补充。'),
(NULL, '信贷管理部', '《新建商品房预售资金委托管理实施办法》', '第六章  附则', '第二十条', '本办法自公布之日起施行。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第一章  总则', '第一条', '为贯彻国家宏观经济政策，支持新能源产业，推进新能源应用，改善生态环境，规范本行新能源设备分期贷款管理，根据《中华人民共和国担保法》、《中华人民共和国物权法》等相关法律、法规，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关制度，特制定本办法。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第一章  总则', '第二条', '本办法所称的新能源设备，是指企业或个人利用自有或租赁具备条件的建筑屋顶(含附属空闲场地)、种植大棚顶部、水产养殖水面上方等资源，安装分布式发电设施进行发电，运行方式侧重于用户自发自用，余电上网，且在配电系统平衡调节为特征的发电系统。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第一章  总则', '第三条', '本办法所称的新能源设备分期贷款，是指本行向借款人发放的用于向分布式发电设备供应商购买分布式发电设备的贷款，以按揭贷款形式发放。其中，设备供应商必须为本行合作客户，须与本行签订相关合作协议，向其他设备供应商购买分布式发电设备的贷款按一般贷款要求执行。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第一章  总则', '第四条', '新能源设备分期贷款的经办行为本行辖区内支行、小微业务部、公司业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第五条', '经办行负责新能源设备分期贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '授信评审部负责对新能源设备分期贷款业务的授信管理。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '信贷管理部负责对新能源设备分期贷款业务的用信进行管理，对贷款进行贷后检查。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对新能源设备分期贷款放款进行审查。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '风险管理部负责对新能源设备分期贷款的风险进行监督、识别、评估、控制，并向经营管理层报告风险。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核新能源设备分期贷款管理办法和相关操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '审计部负责对新能源设备分期贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第三章  贷款对象和条件', '第十三条', '办理新能源设备分期贷款的借款人，包括本市企（事）业法人、其他经济组织、个体工商户，或18周岁以上、具有完全民事行为能力，且年龄加上贷款年限不超过65周岁的自然人。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第三章  贷款对象和条件', '第十四条', '借款人除应具备本行信贷管理制度规定的基本条件外，还应具备以下条件：（一）与分布式发电设备供应商签订的光伏发电设备购买协议或合同，由施工单位出具项目可行性报告、光伏发电系统设计、安装施工图等。自然人安装新能源设备的需取得市供电公司同意接入电网的函，企业安装新能源设备的需取得政府相关部门的审批备案文件。（二）在自有资源上建造新能源设备的，需持有自有资源权属证明，如房屋所有权证、土地使用权证、或其他房产权属证明等；在租赁资源上建造的，需持有该资源使用权租赁合同，如土地使用权租赁合同、养殖水面租赁合同等，且租赁余期不短于贷款期限。（三）在本行开设存款账户，且同意电费收入及相关补贴由供电公司直接转入该账户。（四）具备分期偿还贷款本息的能力。（五）本行认为必须满足的其他条件。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、担保', '第十五条', '贷款额度根据借款人资信、还款能力、担保措施等情况综合确定。（一）自然人客户。光伏发电系统投额总价大于10万元的，贷款额度不高于投资总价的80%。（二）企业客户。贷款额度不超过光伏发电系统建设投资总额的70%。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、担保', '第十六条', '贷款方式及期限采取按揭贷款方式，根据借款人综合实力合理确定贷款期限，自然人贷款期限最长不超过十年，公司贷款期限最长不超过五年。一年以内的自然人贷款实行按季结息到期还本方式，公司贷款实行按月结息到期还本方式。一年以上的自然人及公司贷款均采取等额本息或等额本金还款法，从贷款发放日的次月开始，按月分期偿还贷款本息。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、担保', '第十七条', '贷款利率根据本行贷款利率定价管理办法执行。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、担保', '第十八条', '担保措施借款人申请贷款，除符合条件的信用贷款外，借款人应按要求提供本行认可的保证、抵押或质押等担保措施，各经办行需对担保人的资格、担保意愿、担保能力或抵（质）押物价值及变现能力等方面进行调查、核实。其中，以投资建设的光伏设备提供抵押的，须满足以下条件：以其投资建设的光伏设备的资产设定抵押担保，光伏设备必须建设完毕且已并网发电；发电效率达到设计要求。光伏设备供应商承担连带担保责任，承担无条件回购责任。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、担保', '第十九条', '光伏设备供应商须在本行营业部开立保证金专户，根据贷款发放情况逐笔交纳保证金，交纳比例为每笔贷款金额的10%，上限不超过200万元，用于支付借款人到期应还本息和履行担保责任所需费用。若借款人发生违约事项，本行有权扣划保证金，用于支付借款人到期应还款本息及相关合理费用。保证金扣划后，保证金专户余额低于规定的保证金额度时，光伏设备供应商应在10个工作日内补足。如借款人所借贷款逾期2个月以上，经催收后无法收回的，光伏设备供应商应按协议约定对其光伏发电设备予以回购，同时负责归还我行剩余贷款本息。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十条', '新能源设备分期贷款操作程序分为借款人申请、贷款受理与调查、贷款授信审查审批、签订协议、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十一条', '借款人申请。借款人以书面形式向本行提出贷款申请，也可以通过客服热线、微信、手机银行等申贷渠道提出贷款申请。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十二条', '贷款受理与调查。符合申请条件的新能源设备分期贷款由经办行负责受理、调查。调查的基本内容主要包括：申请人的综合素质、信用状况、家庭财产状况、年收入、经营项目、偿还能力、贷款的用途、拟装新能源设备收益等情况，对申请人提供的担保人（物）的合规性、变现能力进行调查和评估，并形成综合调查意见。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十三条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十四条', '签订承诺函（见附件）。借款人签订承诺函，明确借款人在本行开设的存款账户，作为其接受售发电收入及相关补贴款的专用账户，在贷款未结清前，账户不得单方面变更，账户中的电费及相关补贴收入只可用于偿还贷款本息。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十五条', '合同签订。贷款经审批同意后，经办行客户经理应及时通知借款人签署合同。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十六条', '贷款发放与支付。办妥上述手续后，经办行按合同约定发放贷款，贷款必须采用受托支付的方式支付给新能源设备供应商。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十七条', '贷款展期。借款人因特殊原因需要展期的，经办行需在贷款到期日前向本行提出申请，上报审批。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十八条', '贷款发放后，经办行应按照本行信贷管理相关制度办法进行贷后管理。其中，包括但不限于对借款人的偿债能力、按期还本付息情况、新能源设备运行情况进行贷后评价，遇火灾、台风等危及新能源设备安全，或国家对光伏发电项目重大政策调整等不利于贷款偿还等情况，应及时进行预警和监控，并采取相应措施。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十九条', '贷款归还。借款人结清贷款后，协议终止。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第六章  附则', '第三十条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《新能源设备分期贷款管理办法及操作规程》', '第六章  附则', '第三十一条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行权利质押贷款业务，防范贷款风险，提高信贷资产质量，根据《中华人民共和国担保法》、《中华人民共和国物权法》等法律法规，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等规定，特制定本办法。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称权利质押贷款，是指以本行所辖支行、营业部（以下简称“支行”）或与我行有合作协议的其他银行（以下简称他行）签发的个人定期储蓄存单、单位定期存单以及《中华人民共和国物权法》中所规定的其他权利凭证作质押，从本行获得人民币贷款的一种贷款方式。出质人可以是借款人本人，也可以是自愿出质的第三人。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所指借款人，是经有权部门批准登记的企（事）业法人、其他经济组织或具有完全民事行为能力的自然人。以本行签发的存单质押，以存单签发支行为质押贷款办理行，非本支行签发的系统内存单如确需质押的，必须经签发行在《出质承诺书》（附件）栏签署查询意见并同意质押后方可办理；以他行签发的存单质押，按照本行具体的存单质押管理办法以及与他行签订的《合作协议》办理；以其他权利凭证作质押的，一般以借款人所在地的支行为质押贷款办理行。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第一章  总则', '第四条', '权利质押贷款的用途必须是借款人依法从事生产、经营和消费等方面的资金需求。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第一章  总则', '第五条', '所有权属不明或有争议，已担保、已挂失或已止付、冻结及审查确认无效的权利凭证和本办法第二条规定的质物以外的权利凭证不得质押。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '支行、公司业务部、小微业务部负责权利质押贷款的受理、“三查”实施及相关资料的保管。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '授信评审部负责权利质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责对管理办法和流程的拟定，负责用信的审查、审批及贷后检查的监督。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '运营管理部负责对权利质押贷款用款进行审查。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '风险管理部负责对权利质押贷款业务风险的监测、识别、评估和控制，向经营管理层提交风险审查报告。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核管理办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '审计部负责对权利质押贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第三章  客户对象和基本条件', '第十三条', '客户应当是经有权部门核准登记的企(事)业法人、其他经济组织、个体工商户或具有合法身份证件、在本行辖区内有有效居住证明（含暂住证）的具有完全民事行为能力的自然人。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第三章  客户对象和基本条件', '第十四条', '客户申请权利质押贷款应当具备下列基本条件：（一）从事符合国家产业政策和社会发展规划要求的建设和生产经营活动；（二）借款用途明确、合法；（三）有合法稳定的收入或收入来源，具有按期还本付息能力；（四）在本行开立结算账户，自愿接受本行的信贷监督和结算监督，如实向本行提供有关财务报表、税务证明等相关资料；（五）除自然人外，企业法人应当依法在有权部门有效登记，特殊行业须持有有权机构颁发的特殊行业许可证；事业法人依照《事业单位登记管理暂行条例》的规定向事业单位登记管理机关办理了登记或备案手续。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十五条', '贷款金额。按“预计贷款归还日，质物可兑现本息大于贷款本息”的原则确定，期限一年内，贷款额度不超过质押物的90%，期限一年以上，贷款额度一般不超过质押物的80%。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十六条', '贷款期限。由借款人、出质人、贷款人协商确定，但贷款到期日不得迟于质物中最近的到期（兑付）日，最长不超过三年。如质物为多张、到期（兑付）日间隔时间较长的，质押贷款应以最近的到期（兑付）日为贷款到期日或分为多笔发放，确保贷款到期日与质物到期（兑付）日相匹配。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十七条', '贷款利率。按本行贷款利率定价管理办法的规定执行。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第四章  贷款金额、期限、利率和还款方式', '第十八条', '还款方式。实行利随本清或期间内按季结息，到期利随本清的方式。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第十九条', '权利质押贷款操作程序：借款人申请，授信审查、审批，用信审查、审批，合同文本的签订，贷款发放与支付，贷后管理，贷款收回和档案管理。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十条', '借款人申请权利质押贷款，应向本行提供申请贷款金额、贷款用途、期限、还款来源、质押物等基本情况，并提供下列资料：（一）借款人及出质人基本情况；（二）借款人及出质人的身份证明；（三）用于质押的权利凭证；（四）本行要求提供的其他资料。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '客户经理接到借款人的申请后，应及时进行调查。调查的主要内容包括但不限于：（一）借款人提供的质押权利凭证是否真实有效，有无挂失、冻结、止付，是否存在争议或法律纠纷，如果质押权利凭证系他行存单，按照我行与他行签订的《合作协议》办理查询；（二）借款人的身份证件是否真实有效，是否具有完全民事行为能力；（三）以他人的质押权利凭证办理质押贷款，出质人的身份证件是否真实有效，出质人是否具有完全民事行为能力；（四）还款来源是否落实。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '支行运营主管或公司业务部、小微业务部明确的审查人员应对贷款调查内容的合法性、合理性、准确性进行全面审查，重点关注借款人的偿还能力、诚信状况、质押比率、风险程度等。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '贷款授、用信审批，按本行《转授权管理办法》、《信贷业务操作规程》规定执行。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '客户经理办理贷款手续，主要包括以下内容：（一）严格执行贷款面谈、面签制度。（二）出质承诺及背书。出质人在《出质承诺书》的出质人承诺栏作出“如贷款不能按期归还，同意质权人将质押的权利凭证兑现，并以所得价款偿还贷款”的承诺；在出质人背书栏填写相关信息，自然人填写身份证件名称和号码并签字加盖指模，法人单位加盖公司印鉴。他行存单质押按本行与他行签订的《合作协议》要求办理。（三）签订权利质押担保借款合同。客户经理对《出质承诺书》及其它资料审查无误后，与借款人、出质人签订权利质押担保借款合同，由借款人、出质人同时到场在合同上签字、盖章或加盖指模。权利质押贷款自签订借款合同和质押合同并办妥质物移交后发放（质权需登记的在办理登记后发放）。（四）客户经理进入信贷风险管理系统录入相关信息，按权限审批通过后，打印《放贷通知书》，并按有关规定做好支付管理工作。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '质押品处理。（一）柜员接到质押品和《押品出账通知书》后，应认真检查质押品是否收集齐全，同时填制《抵、质押品保管凭证》由客户签字。在《抵、质押品保管凭证》上加盖业务章，收据联交客户，质押品和《抵、质押品保管凭证》（管理卡联）交后台管理并登记表外登记簿。（二）根据《放贷通知书》，柜员应对质押贷款手续认真进行审查，审查要素是否齐全、利率执行是否符合规定、大小写是否一致、签字审批是否齐全等，审核无误后，打印借据交由客户签字、盖章或加盖指模，进行贷款发放并按支付管理要求进行支付。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '客户经理是权利质押贷款发生后管理的责任人，负责客户贷款业务发生后的日常管理。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '短期质押贷款到期应提前15天、中期质押贷款到期应提前30天通知客户。质押贷款逾期或发生欠息等违约行为，经向借款人、出质人催收仍不还款的，按合同约定及出质人承诺，可兑付质物归还贷款本息，结清贷款本息后的多余部分应退给出质人，不足部分应继续向借款人追偿。贷款收回后，应相应进行质押物注销处理，销记表外登记簿。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '贷款收回。借款人提前或到期归还贷款，柜员进行还本收息处理，收妥款项后，收回客户提交的《抵、质押品保管凭证》（收据联），进行质押品注销处理，提出质押品交还客户，后台保管人员销记表外登记簿；他行存单，按照本行与他行签订的《合作协议》要求办理。还款后客户未提交《抵、质押品保管凭证》（收据联）的，不得进行“质押品注销”的操作，质押品不得取出，待日后客户凭《抵、质押品保管凭证》（收据联）办理注销、领取手续。如客户的《抵、质押品保管凭证》（收据联）遗失，应比照储蓄挂失的要求和手续办理。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '资料管理。办理质押贷款涉及的《出质承诺书》、《抵、质押品保管凭证》等应按本办法的规定和注明的用途使用，并作为相应业务凭证的附件订入当日传票。《权利质押担保借款合同》和《贷款到期通知书》、《逾期贷款催收通知书》、《担保人履行责任通知书》的回执等文本资料应作为重要信贷档案由客户经理按月整理装订归档，按档案管理规定的期限保管。对以他行存单质押的贷款，相关资料应一户一档，专夹保管。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第六章  附则', '第三十条', '本办法由信贷管理部制订、解释和修改。'),
(NULL, '信贷管理部', '《权利质押贷款管理办法及流程》', '第六章  附则', '第三十一条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第一章  总则', '第一条', '为了规范本行林权抵押贷款管理，根据《中华人民共和国森林法》、《中华人民共和国担保法》、《中华人民共和国物权法》、《森林资源资产抵押登记办法（试行）》、《关于林权抵押贷款的实施意见》等相关法律、法规，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等制度，特制定本办法。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第一章  总则', '第二条', '林权抵押贷款是指森林、林木资源财产权利人，因融资需要以其依法有权处分的森林、林木权利办理抵押登记，向本行申请办理的贷款。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第一章  总则', '第三条', '林权抵押的经办行为本行辖区内支行、公司业务部、小微业务部（以下简称“支行”）。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第四条', '支行负责林权抵押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '授信评审部负责对林权抵押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定林权抵押贷款管理办法及流程，对林权抵押贷款业务的用信进行管理，对相关贷后检查进行管理监督。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对林权抵押贷款用款进行审查。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '风险管理部负责对林权抵押贷款的风险进行监督、识别、评估、控制，并向经营管理层报告风险。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '合规管理部负责审核林权抵押贷款管理办法和相关操作流程的合规性和有效性。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '审计部负责对林权抵押贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第三章  贷款对象和条件', '第十一条', '办理林权抵押贷款的借款人，是经工商部门登记核准的从事生产经营活动的企（事）业法人、其他经济组织和个体工商户，或从事生产经营活动的有完全民事行为能力，且年龄在65周岁以下的自然人。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第三章  贷款对象和条件', '第十二条', '借款人除应具备本行信贷管理制度规定的基本条件外，还应具备以下条件：（一）借款人依法办理林权登记并取得林权证和森林、林木林地使用权；（二）能按时交付租地费用和林木维护费用；（三）无重大不良信用记录；（四）抵押林为符合国家规定能作为抵押物的用材林、经济林、薪炭林等；（五）林木生长期不低于三年（含）；（六）林地面积不少于50亩，林木平均胸径不低于8cm；（七）本行认为需要的其他条件。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第三章  贷款对象和条件', '第十三条', '下列林权可以作为抵押：（一）用材林、经济林、薪炭林；（二）用材林、经济林、薪炭林的林地使用权；（三）用材林、经济林、薪炭林的的采伐迹地、火烧迹地的林地使用权；（四）国务院规定的其他森林、林木和林地使用权。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第三章  贷款对象和条件', '第十四条', '不得用于抵押的森林、林木和林地使用权：（一）生态公益林；（二）权属不清或存在争议的森林、林木使用权；（三）未经依法办理林权登记而取得林权证的森林、林木和林地使用权；（四）第三人林木使用权；（五）国家规定不得抵押的其他森林、林木和林地使用权。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第四章  贷款额度、期限、利率', '第十五条', '贷款额度不超过评估价值的40%，评估价值由具有林木评估资质的中介机构评估，评估价值必须经本行确认。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第四章  贷款额度、期限、利率', '第十六条', '根据借款人的生产经营周期、信用状况和贷款用途等因素合理协商确定林权抵押贷款的期限，贷款资金用于林业生产的，贷款期限要与林业生产周期相适应。贷款期限不应超过林地使用权的剩余期限，森林、林木资源属于承包、租赁，抵押担保的期限最长不得超过承包、租赁合同规定的使用年限减去已承包、租赁年限的剩余年限，最长不超过五年（含）。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第四章  贷款额度、期限、利率', '第十七条', '贷款利率。按本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第十八条', '林权抵押贷款操作程序为借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第十九条', '借款人申请。申请人需向支行提供借款人（已婚者包含其配偶）的户口簿、身份证复印件，拟借款金额、用途、期限、权证等相关基本情况和资料。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十条', '贷款受理与调查。符合申请条件的林权抵押贷款由支行负责受理、调查。调查的基本内容主要包括：申请人的综合素质、家庭财产状况、信用观点、年收入、经营项目、产品市场行情、偿还能力、贷款的用途等情况，对申请人提供的抵押物合法性、变现能力进行调查和评估，并形成综合调查意见。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '合同签订。（一）贷款经审批同意后，支行客户经理应及时通知借款人签署合同、并向当地县级以上人民政府林业主管部门办理抵押登记。合同应约定贷款种类、贷款用途、贷款金额、贷款利率、还款期限、还款方式、违约责任和双方认为需要约定的其他事项。(二)签订合同时，须在特别约定事项中注明应以现有的和将有的林木一并设定浮动抵押（浮动抵押是指权利人以现有的和将有的全部财产或者部分财产为其债务提供担保）。(三)在设定抵押时，林木所有权与林地使用权一并抵押。(四)经营国家无偿划拨森林资源资产的单位，以其经营的森林资源资产申请抵押的，应先办理相关的森林、林木出让手续并补交相关费用。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '申请抵押登记需提供以下资料：（一）森林资源资产抵押登记申请书；（二）借贷双方营业执照，组织机构代码证书、税务登记证书、法人证书或个人身份证；（三）贷款申请审批表；（四）抵押合同；（五）林权证及林权所有人同意抵押意见书；（六）拟抵押森林资源资产的相关资料，包括：林地类型、坐落位置、四至界址、面积、树种、树龄、蓄积量等；（七）抵押物的评估报告；（八）抵押登记部门认为应提交的其他文件资料。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '借款人应在合同签订前，办理抵押物保险，保险期不得短于借款期限，投保金额不得低于贷款本金和利息之和，保险单不得有任何有损本行权益的限制条件，保险第一受益人必须为本行，保险所需一切费用由借款人负担。在贷款未偿清期间，保险单正本交本行执管。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '贷款发放。办妥上述手续后，支行在登记手续办理完毕并取得《森林资源资产抵押登记证》后，按合同约定发放贷款。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '贷款展期。借款人需要且符合本行展期条件的，抵押人与抵押权人协商同意延长抵押期限的，借贷双方及抵押人应当在抵押合同期满之前1个月内向原登记机关申请办理续期登记。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '贷款归还。当借款人还清贷款后，双方终止合同协议，持原《森林资源资产抵押登记证》向原登记机关办理注销手续，取回《林权证》。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '发生火灾、洪涝、台风、干旱等情况或借款人经济发生变化应及时进行预警和监控，并采取相应处理措施。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第六章  附则', '第二十九条', '本办法及流程由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《林权抵押贷款管理办法及流程》', '第六章  附则', '第三十条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第一章  总则', '第一条', '为满足汽车消费需求，规范汽车消费贷款业务管理，防范汽车消费贷款风险，促进汽车消费贷款业务健康发展，根据《中华人民共和国担保法》、《中华人民共和国物权法》、《汽车贷款管理办法》，及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关信贷管理制度，特制定本办法。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称汽车消费贷款，是指本行向借款人发放的用于购买汽车（不含二手车）的贷款。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第一章  总则', '第三条', '所购买车辆的净车价不低于10万元，核定载乘人数在7人座（含）以下。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第一章  总则', '第四条', '汽车消费贷款实行“部分自筹，有效担保，专款专用，按期偿还”的原则。借贷双方应签订书面借款合同。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第一章  总则', '第五条', '汽车消费贷款的借款人、汽车经销商、保险人和担保人应在同一城市辖区。未经本行批准不得发放异地贷款。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第一章  总则', '第六条', '本行所辖的支行、小微业务部（以下简称“经办行”）办理汽车消费贷款业务。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '经办行负责汽车消费贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '授信评审部负责对汽车消费贷款业务的授信管理。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '信贷管理部负责拟定汽车消费贷款管理办法及流程，对汽车消费贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '运营管理部根据权限负责对汽车消费抵押贷款用款进行审查。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对汽车消费贷款的风险的监督、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核汽车消费贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '审计部负责对汽车消费贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第三章  贷款种类和条件', '第十四条', '本行汽车消费贷款分为个人汽车消费贷款和机构汽车消费贷款两种。个人汽车消费贷款，是指本行向个人借款人发放的用于购买汽车的贷款。机构汽车消费贷款，是指本行对法人、其他经济组织（以下简称机构借款人）发放的用于购买汽车的贷款。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第三章  贷款种类和条件', '第十五条', '借款人申请个人汽车消费贷款，应当同时符合以下条件：㈠借款人必须是我市常住户口居民，年龄在18周岁（含）以上65周岁（含）以下，具有完全民事行为能力； ㈡借款人具有还款能力，无不良贷款记录；㈢已在本行开立存款账户，并存入不少于规定数额的首期购车款、保险费；㈣具备分期偿还贷款本息的能力；㈤已与本行认可的汽车经销商签订了购车合同；㈥提供本行认可的抵、质押物或有足够代偿能力的个人或单位提供第三方不可撤销的连带责任保证；㈦本行要求的其他条件。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第三章  贷款种类和条件', '第十六条', '借款人申请机构汽车消费贷款，必须同时符合以下条件：㈠具有企业或事业单位登记管理机关核发的企业法人营业执照或事业单位法人证书等证明借款人具有法人资格的法定文件；㈡具有合法、稳定的经营收入；㈢能够支付本办法规定的首期付款；㈣无重大违约行为或信用不良记录；㈤借款人除以所购车辆向本行抵押担保外，还愿意提供本行认可的其它财产抵押、质押或第三方不可撤销的连带保证；㈥本行要求的其它条件。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第四章  首付金额、期限和利率、还款方式', '第十七条', '首付金额。首付金额不得低于所购汽车价格的50%。对于50万元以上的高档车以及进口车的车贷首付不低于40%。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第四章  首付金额、期限和利率、还款方式', '第十八条', '贷款期限。汽车消费贷款期限一般为3年，最长不超过5年。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第四章  首付金额、期限和利率、还款方式', '第十九条', '贷款利率。根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第四章  首付金额、期限和利率、还款方式', '第二十条', '还款方式。贷款期限在1年（含）以内的，实行按季结息、到期还本。贷款期限在1年以上的，应按月还款，还款方式可与借款人约定采用等额本息还款法或等额本金还款法。本行应与借款人约定还款方式，每月还款的应约定每月还款日。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第四章  首付金额、期限和利率、还款方式', '第二十一条', '本行应要求借款人签订书面协议，授权本行从其提供的账户中直接扣收每期应还款本息。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '汽车消费贷款操作程序为客户申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '申请汽车消费贷款时，借款人应向经办行提出书面申请，并按要求提供资料。申请个人汽车消费贷款所需提供资料为：㈠个人汽车消费贷款申请表；㈡借款人及其配偶的有效身份证明（居民身份证、户口本、军官证，港澳同胞回乡证或其他有效居留证件）原件及复印件、婚姻证明，借款人如未婚应提供未婚证明，并提供共同借款人；㈢借款人偿债能力证明（包括借款人家庭经济收入证明、纳税证明、银行对账单、租赁合同、银行储蓄存单及有价证券等的原件及复印件）；㈣与本行指定的汽车经销商签订的购车协议或合同；㈤汽车经销商出具的购车首付款凭证及复印件；㈥担保所需的证明或文件，包括抵（质）押物清单和有处分权人（含财产共有人）同意抵押、质押的证明；有权部门出具的抵押物所有权或使用权证明、书面估价证明、同意保险的文件；质押物须提供权利证明文件；保证人同意履行连带责任保证的文件、有关资信证明材料；㈦本行要求提供的其他材料原件（购车发票、车辆购置附加税完税凭证、汽车行驶证及车辆登记证等）。申请机构汽车消费贷款所需提供资料为：㈠具有企业或事业单位登记管理机关核发的企业法人营业执照或事业单位法人证书等证明借款人具有法人资格的法定文件及年检证明；㈡与经销商签订的购车合同或协议；㈢经审计的上一年度及近期的财务报表，中国人民银行颁发的有效贷款卡；㈣出租汽车公司等需出具出租汽车营运许可证（或称经营指标）；㈤缴付首期购车款的付款证明；㈥担保所需的证明或文件，包括抵（质）押物清单和有处分权人（含财产共有人）同意抵押、质押的证明；有权部门出具的抵押物所有权或使用权证明、书面估价证明、同意保险的文件；质押物须提供权利证明文件；保证人同意履行连带责任保证的文件、有关资信证明材料;㈦本行要求的其他条件。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '经办行在收到借款人申请后，应对借款人和担保人的资信状况、偿债能力、抵（质）押物状况及变现能力以及提供文件资料的真实性等方面进行调查，并形成调查报告，对能否授信提出初审意见。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '贷款的授、用信审批按本行《转授权管理办法》执行。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '对符合条件的借款人，贷款经审批同意后，客户经理应及时通知借款人和保证人签订《借款合同》与《抵／质押合同》（或《保证合同》），填写抵押登记或质押登记申请表，办理抵押物保险、缴付有关费用等。合同中应约定贷款种类、贷款用途、贷款金额、贷款利率、还款期限、还款方式、违约责任和双方认为需要约定的其他事项。保证人为汽车经销商的,应开立保证金账户，并存入规定数额的保证金存款；保证人应签署贷款未按期归还本息时委托本行从其账户转账付款授权书。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '以所购车辆抵押的，经办行在贷款审批同意后，应向经销商出具同意向借款人发放贷款的书面通知。经销商在收到银行通知，应协助借款人按照本行要求到车辆登记等部门办理抵押登记和保险等手续，并将购车发票、各种缴费凭证和保险单直接交与本行。以其他财产抵押或质押的，按本行信贷管理的有关规定执行。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '借款人应办理抵押物保险，保险期不得短于借款期限，投保金额不得低于贷款本金和利息之和，保险单第一受益人应为本行，保险所需一切费用由借款人负担。在贷款未结清期间，保险单正本交本行保管。以所购车辆作为抵押物的，借款人必须按要求投保机动车辆险、车辆损失险、第三者责任险和附加盗抢险。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '贷款发放与支付。办妥上述手续后，本行按合同约定发放贷款，将资金划入借款人结算账户，并严格监督借款人按合同约定支付方式支付贷款资金，运营管理部根据权限负责对贷款用款进行审查。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '贷款发放后，应按照本行信贷管理相关制度办法进行贷后管理。检查贷款抵（质）押品有无变化，检查借款人的经营收入状况和第三方保证人的代偿能力。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第三十一条', '展期贷款的处理。贷款期限在一年以内并采用一次性归还贷款本息方式的，借款人不能按照合同规定的期限偿还贷款本息，应提前10个工作日向经办行申请展期。展期申请经本行审查批准后，借贷双方应签订展期协议。展期协议须经抵（质）押人、保证人书面认可，并办理延长抵（质）押登记、保险手续；对一年以上以分期付款方式偿还贷款的，不得办理展期，借贷双方可协商进行贷款重整。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第三十二条', '借款人在清偿全部贷款本息及相关费用后，主从合同自行终止。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第三十三条', '借款人未按借款合同约定还款计划偿还贷款本息的，本行应书面发送催收通知。逾期3个月以上、或在贷款期间内累计出现6个月以上逾期的，客户经理须依法向人民法院提起诉讼。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第五章  贷款操作程序', '第三十四条', '客户经理应加强贷款档案管理，定期对档案保管情况进行检查，防止贷款档案损坏、丢失，特别要检查借款合同、借款借据和抵（质）押合同及保证合同、保险单等重要法律文件的完整性。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第六章  附则', '第三十五条', '以上所称汽车价格，是指汽车实际成交价格（不含各类附加税、费及保费等）与汽车生产商公布的价格的较低者。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第六章  附则', '第三十六条', '本办法及流程由信贷管理部负责制订、修订和解释。'),
(NULL, '信贷管理部', '《汽车消费贷款管理办法及流程》', '第六章  附则', '第三十七条', '本办法及流程自印发之日起执行。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第一章  总则', '第一条', '为规范本行流动资金贷款业务经营行为，加强流动资金贷款审慎经营管理，促进流动资金贷款业务健康发展，依据《中华人民共和国商业银行法》《贷款通则》、《流动资金贷款管理暂行办法》和本行《信贷管理制度》、《信贷业务操作规程》、《流动资金贷款管理办法》制定本办法。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第一章  总则', '第二条', '本行所辖公司业务部（以下简称“经办行”）办理流动资金贷款业务必须遵守本规程。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第一章  总则', '第三条', '本行开展流动资金贷款业务应当遵循“依法合规、审慎经营、平等自愿、公平诚信”的原则。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第一章  总则', '第四条', '流动资金贷款项目的选择必须符合国家产业政策和金融政策，向有利于农村经济持续、快速、健康发展和农村各项社会事业全面进步的中小企业、新型农村经济合作组织以及具有较大竞争力和发展潜能的新兴行业倾斜。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第一章  总则', '第五条', '本规程分为申请与受理、授信审查与审批、用信审查与审批、合同签订、发放与支付、贷后管理、贷款收回、贷款档案管理等部分。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第二章  申请与受理', '第六条', '借款人申请借款人需要流动资金贷款，应向经办行提出书面申请。借款人填写包括借款金额、借款用途、偿还能力以及还款方式等主要内容的《借款申请书》，并参照本行《信贷业务操作规程》要求，提供如下资料：（一）借款人基本信息资料1.营业执照、组织机构代码证（非必有项）、税务登记证（非必有项）、开户许可证、中征码、征信报告。2.企业房产、土地使用权证明、最新公司章程、最近一期验资报告。3.法人代表身份证复印件、个人简历。4.企业资质证明、经营许可证、环保排污许可证（非必有项）。（二）借款人非财务信息资料1.企业高管人员简历、企业职工人数、近6个季度一线员工工资单、电费及其他主要能耗（或水/气/煤等）发票复印件。2.企业采购品种、年交易额（万元）、前三大供应商名称、结算方式、结算周期。3.企业销售品种、年交易额（万元）、前三大客户名称、结算方式、结算周期。4.目前企业在建项目提供可行性研究报告、在建进度等。5.关联企业概况及介绍、营业执照、组织机构代码证（非必有项）、中征码、财务资料、关联情况说明及征信查询授权书。（三）借款人财务信息资料1.企业前三年和当年最近一期财务报表，如有审计报告提供审计报告。2.企业对外提供担保明细（包括：对外担保对象、担保金额、融资机构、起止日期、担保方式）。3.固定资产明细(包括：机器设备清单)。4.目前获得各银行授信情况，开户银行对账单。5.长、短期借款明细（包括借款类别、借款银行、借款金额、起止日期、担保方式）。6.当年最近一期报表科目明细（可到现场视具体情况筛选）。（1）货币资金明细；应收、应付账款明细；其他应收、其他应付款明细；预收、预付账款明细；（2）存货明细；应付票据明细；（3）其他占比较大的科目明细。7.企业纳税申报表。（四）借款人贷款申请相关资料1.流动资金贷款用途证明材料（包括购销合同等）。2.第二还款来源相关资料。担保人为企业的，需提供营业执照、组织机构代码证（非必有项）、税务登记证（非必有项）、开户许可证、中征码、公司章程、最近一期验资报告、法人代表身份证复印件及个人简历、特种行业经营许可证（环评合格证、行业资质证等）、前两年和当年最近一期财务报表、企业征信查询授权书等；担保人为自然人的，需提供担保人有效身份证明、个人征信查询授权书、家庭主要财产证明、职业收入证明等；抵押的，需提供权属证明（房屋、土地使用权、机器设备等复印件）、评估报告（非必有项）；质押的，需提供存单复印件、质押物清单、第三方监管情况等。（五）本行认为需要提供的其他资料。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第二章  申请与受理', '第七条', '经办行受理经办行应对借款人提供的申请材料的方式和具体内容提出要求，要求借款人恪守诚实守信原则，承诺所提供材料真实、完整、有效。根据内部制约机制的要求，客户经理应独立对借款人、担保人提供的资料进行认真审核，作出是否受理的意见。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第二章  申请与受理', '第八条', '经办行在正式受理借款人申请后，必须坚持面谈制度和“双人四眼”原则，从以下几方面集中获取客户的相关信息：（一）客户的公司状况。包括历史背景、股东背景、资本构成、组织构架、产品情况、经营现状。（二）客户贷款需求状况。包括贷款背景、贷款用途、贷款规模、贷款条件等。（三）客户的还款能力。包括现金流量构成、经济效益、还款资金来源、担保人经济实力等。（四）抵、质押品的可接受性。包括押品的种类、权属、价值、变现难易程度。（五）客户与银行的关系。包括客户与本行及他行的业务往来状况、信用履约记录等。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第二章  申请与受理', '第九条', '经办行受理借款人借款申请后，客户经理按照双人调查原则履行尽职调查，并形成书面报告，就同意贷款与否、贷款金额、利率、期限、担保方式等提出明确意见，对其内容的真实性、完整性、合规性和有效性负责。（一）尽职调查的程序与方法贷款尽职调查是通过调查掌握借款申请人生产经营状况、资产负债结构、发展前景和管理存在的问题，分析借款人的偿债能力和贷款潜在风险，为贷款决策提供准确的信息和依据。一般采取查阅有关资料与实地调查相结合、定性分析与定量分析相结合的方法开展调查。1.查阅信贷管理系统、征信系统有关报告、资料和信贷档案，适当开展外延走访；2.查阅借款申请人提交的有关资料和近三年的财务报表，并对各种资料和财务报表的真实性进行审核；3.深入实地，查阅客户有关报表、账簿，调查客户提交的有关资料是否真实；4.与客户主要负责人、董事会成员、监事会成员或者有关职能部门负责人、工作人员交谈；5.查阅提供的担保资料，实地查验抵（质）押物情况，走访保证人；6.测算贷款风险度。（二）尽职调查要点1.借款人的组织架构、公司治理、内部控制及法定代表人和经营管理团队的资信等情况；2.借款人的经营范围、核心主业、生产经营、贷款期内经营规划和重大投资计划等情况；3.借款人所在行业状况；4.借款人的应收账款、应付账款、存贷等真实财务状况以及各种税费缴纳、水电费缴纳、工资发放等敏感性数据变动情况；5.借款人营运资金总需求和现有融资性负债情况；6.借款人关联方及关联交易等情况；7.贷款具体用途及与贷款用途相关的交易对手资金占用等情况；8.还款来源情况，包括生产经营产生的现金流、综合收益及其他合法收入等；9.对有担保的流动资金贷款，还需调查抵（质）押物的权属、价值和变现难易程度，或保证人的保证资格和能力等情况；10.对照流动资金贷款测算方法，测算借款人营运资金需求，初步确定流动资金贷款额度，并就金额、期限、利率、担保和还款方式等贷款要素提出初步意见；11.其他需要调查或核查的事项。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第二章  申请与受理', '第十条', '经办行运营主管（或兼职合规员）负责对客户经理的调查资料进行书面的完整性和合规性审查。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第三章  授信审查与审批', '第十一条', '授信评审部负责借款人借款合规性、安全性、效益性、还款来源可靠性、担保等角度进行贷款风险评价。贷款审查与风险评价要点但不限于：（一）基本条件审查要点1.送审的材料是否齐全；2.借款单位、担保单位是否符合本行有关规定的条件；3.借款人生产经营、财务状况、信誉状况、发展前景及内部管理是否良好；4.借款用途是否合法、合规、合理；5.贷款金额、期限、利率、还款方式等贷款要素是否合理、合规；6.第一、第二还款来源是否充足、可靠、合法、有效；7.借款人信用总量和各分项信用余额是否控制在最高综合授信额度及各分项授信额度内；8.客户经营中存在的主要问题及对贷款安全的潜在影响；9.贷款尽职调查报告是否真实、完整、准确；10.其他需要审查的内容。（二）合规风险审查要点1.借款单位、担保单位的资格、资信情况是否符合法律法规、符合监管制度的规定；2.贷款尽职调查报告是否对风险点进行了充分揭露，是否制定了有效的风险防范措施；3.账户监管、支付方式等相关手续的合规性、有效性；4.其他需要审查的内容。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第三章  授信审查与审批', '第十二条', '贷款审批授信评审部对经办行提交的流动资金贷款业务进行授信审查，按照本行管理权限分别报有权审批人审批和报备：（一）授信评审部权限内的授信，由授信评审部集体会办后形成审查意见，经有权人审批后出具授信批复。（二）超信评审部权限的授信，经授信评审部初审后形成贷款授信运作表，提交信贷审查委员会审议，信贷审查委员会表决确定授信结果后上报至本行行长备案，总行行长具有一票否决权，报备完成后出具授信批复。（三）对重大关联交易，经信贷审查委员会审议确认，报董事会风险管理与关联交易控制委员会审核后报董事会审批，董事会审议完成后由授信评审部出具授信批复。（四）所有审议通过的授信均由授信评审部按照审批内容批复至经营单位，作为用信和贷后检查的依据。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第四章  用信审查与审批', '第十三条', '用信申请时应提供下列资料：（一）用信审查审批表；（二）授信审批文件及授信限制性条款落实情况的证明材料；（三）用信需求的证明文件(包括购销合同、协议等)；（四）其他需要审核的材料。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第四章  用信审查与审批', '第十四条', '用信审查的主要内容包括：（一）对客户提供的用信资料完整性、真实性、有效性进行审查核实，并分析其是否符合法律法规和本行的相关信贷政策；（二）查验授信文件的有效性，客户用信条件是否满足授信条件；（三）查验授信时是否提出限制性条款，如有，是否落实到位。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第四章  用信审查与审批', '第十五条', '用信审查完毕后，审批人员根据审查意见按权限进行审批。经办行权限内的用信申请，由经办行有权人审批；权限外的用信申请，经办行会办后提交信贷管理部审查，对使用特殊文本的用信需交合规管理部进行审查。信贷管理部对经办行提交的用信申请审查审批，对已授信贷款授信条件的落实情况，授信的额度、期限、用途是否合理，贷款实际用途是否符合授信条件进行审查并审批。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第五章  合同签订', '第十六条', '贷款用信审批同意后，经办行及时通知借款人、担保人进行借款合同商谈。经办行参与借款合同商谈的人数至少在两人以上（含两人）。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第五章  合同签订', '第十七条', '合同商谈的主要内容（一）各方当事人的权利、义务及违约责任，避免对重要事项未约定、约定不明或约定无效；（二）具体的贷款金额、期限、利率、用途、还款保障以及风险处置等要素和有关细节；（三）约定贷款支付的相关事项，包括但不限于以下内容：1.贷款资金的支付方式和本行受托支付的金额标准；2.支付方式变更及触发变更条件；3.借款资金支付的限制、禁止行为；4.借款人应及时提供贷款资金使用记录和资料。（四）与借款人约定的提款条件以及贷款资金支付接受本行管理和控制等与贷款使用相关的条款；（五）对借款人相关账户实施监控，必要时可约定专门的发放账户；（六）要求借款人对与贷款相关的重要内容做出承诺，承诺内容应包括：1.及时向本行提供完整、真实、有效的材料；2.配合经办行进行贷款支付管理、贷后管理及相关检查；3.发生影响其偿债能力的重大不利事项时及时通知本行；4.进行合并、分立、股权转让、对外投资、实质性增加债务融资等重大事项前征得本行同意；5.本行有权根据借款人资金回笼情况提前收回贷款；6.其他需要借款人做出承诺的事项。（七）告知借款人出现以下情形之一时，借款人应承担的违约责任和本行可采取的措施：1.未按约定用途使用贷款的；2.未按约定方式进行贷款资金支付的；3.未遵守承诺事项的；4.财务指标触及预警设定值；5.发生重大交叉违约事件的；6.违反借款合同约定其他情形的。（八）其他需要商谈的事项。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第五章  合同签订', '第十八条', '合同商谈完成后，根据相关法律法规、监管制度的要求，结合合同商谈的结果，由经办行和借款人及其他相关当事人签订借款合同及其他相关协议。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第五章  合同签订', '第十九条', '合同的签订的具体规定须按本行《信贷操作规程》执行。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第五章  合同签订', '第二十条', '经办行在签订借款合同时，同时办理贷款担保手续，作为借款合同的从合同，同时按规定手续或程序办理相关登记。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第五章  合同签订', '第二十一条', '本行可以要求借款人、担保人到有关保险机构办理相关保险，保险期不得短于贷款主合同履行期限，保险金额不得小于主合同贷款本息，保险合同及保险单中应当注明出险时本行为保险赔偿金的第一受益人。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十二条', '客户经理根据审批意见，打印《放贷通知单》，交客户至柜面办理用款。柜面受理后，经办行权限的，由会计主管审查、发放；权限外的，经运营管理部审查审批后发放。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十三条', '经办行在发放贷款前应确认借款人满足合同约定的提款条件，并按照合同约定的方式通过本行受托支付或借款人自主支付的方式对贷款资金的支付进行管理与控制，监督贷款资金按约定用途使用。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十四条', '签订流动资金循环借款合同实行循环放款的，可以确定支付方式的，在合同中约定相应的支付方式；不能确定支付方式的，应根据核定额度和期限内的每笔贷款的实际用途，在借款人《提款支付审查意见表》上予以明确。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十五条', '经办行可以通过借款合同的约定，要求借款人指定专门的贷款发放账户，贷款发放和支付应通过该账户办理。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十六条', '经办行通过受托支付或借款人自主支付的方式对贷款资金的支付进行管理与控制。受托支付是指经办行根据借款人书面的提款申请和支付委托，将贷款通过借款人账户支付给符合合同约定用途的借款人交易对象。借款人自主支付是指经办行根据借款人的提款申请将贷款资金发放至借款人账户后，由借款人自主支付给符合合同约定用途的借款人交易对象。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十七条', '经办行应根据借款人的行业特征、经营规模、管理水平、信用状况等因素和贷款业务品种，合理约定贷款资金支付方式及本行受托支付的金额标准。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十八条', '具有以下情形之一的流动资金贷款，应采用本行受托支付方式：（一）与借款人新建立信贷业务关系且信用等级在BBB级以下（不含BBB级，下同）且单笔支付金额超过300万元，未评级企业视同信用等级在BBB级以下；（二）除上述对象之外，支付对象明确且单笔支付金额大型企业超过1000万元、中型企业超过800万元、小型企业超过500万元（企业划分标准参照国家相关规定）；（三）本行认定的其他情形。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第二十九条', '采用受托支付的，经办行应根据约定的贷款用途，审核借款人提供的支付申请所列支付对象、支付金额等信息是否与相应的商务合同等证明材料相符。报信贷管理部审核同意后，经办行应将贷款资金通过借款人账户支付给借款人交易对象。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第三十条', '采用借款人自主支付的，经办行应按借款合同约定要求借款人定期汇总报告贷款资金支付情况，并通过账户分析、凭证查验或现场检查等方式核查贷款支付是否符合约定用途。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第六章  发放与支付', '第三十一条', '在贷款发放和支付过程中，借款人出现下列情形的，经办行应与借款人协商补充贷款发放和支付的条件，或根据合同约定变更贷款支付方式，停止贷款资金的发放和支付。（一）信用、财务状况下降；（二）不按合同约定支付贷款资金；（三）主营业务盈利能力不强；（四）贷款资金使用出现异常；（五）违反合同约定，以化整为零方式规避本行受托支付。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十二条', '经办行客户经理在贷款发放后15个工作日内对新客户首次贷款、新增贷款进行跟踪检查，主要检查客户是否按合同约定用途使用贷款，对未按借款合同规定用途使用贷款的，应查明原因并提出处置的意见和建议。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十三条', '除本规程第三十二条规定的情形外，经办行对所有流动资金贷款的贷后检查应按照“交叉检查”原则进行，检查的时间与频繁率按规定执行。贷后检查的重点：（一）检查借款人、担保人的资产和生产经营、财务状况是否正常，按月结息的是否正常付息，主要产品的市场变化是否影响产品的销售和经济效益。（二）了解掌握借款人、担保人的家庭、机构、体制及高层管理人员人事变动等重大事项，分析这些变动是否影响或将要影响借款人的生产经营和贷款安全。（三）检查抵（质）押物的完整性和安全性，抵押物的价值是否受到损失，抵押权是否受到侵害，质押物的保管是否符合规定。（四）检查借款人与关联人之间的关联交易是否正当，有否对本行的资金安全构成威胁。（五）其他需要检查的内容。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十四条', '经办行应按照本行《信贷资产风险风类实施细则》的要求，及时准确的对流动资金贷款进行风险分类。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十五条', '经办行要通过借款合同的约定，要求借款人指定专门资金回笼账户并及时提供该账户资金进出情况。经办行可根据借款人信用状况、融资情况等，与借款人协商签订账户管理协议，明确约定对指定账户回笼资金进出的管理。经办行应关注大额及异常资金流入流出情况，加强对资金回笼账户的监控。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十六条', '经办行要对抵（质）押物的价值和担保人担保能力建立贷后动态监测和重估制度。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十七条', '经办行要动态关注借款人经营、管理、财务及资金流向等重大预警信号，根据合同约定及时采取提前收贷、追加担保等有效措施防范化解贷款风险。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十八条', '经办行要评估贷款品种、额度、期限与借款人经营状况、还款能力的匹配程度，作为与借款人后续合作的依据，必要时及时调整与借款人合作的策略和内容。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第三十九条', '经办行要根据法律法规规定和借款合同的约定，参与借款人大额融资、资产出售以及兼并、分立、股份制改造、破产清算等活动，维护本行债权。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第四十条', '流动资金贷款需要展期的，借款人应在贷款到期前十天向经办行提出书面申请，经办行应审查贷款所对应的资产转换周期的变化原因和实际需要，决定是否展期，合理确定贷款展期期限，按规定定程序报批，并加强对展期贷款的后续管理。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第七章  贷后管理', '第四十一条', '借款人出现违反合同约定情形的，经办行应及时采取有效措施，必要时应依法追究借款人的违约责任。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第八章  贷款收回', '第四十二条', '流动资金贷款按借款人生产经营周期或综合还款来源，合理约定贷款归还时间，一经约定，不得变更（展期除外）。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第八章  贷款收回', '第四十三条', '短期贷款到期一周之前、中长期贷款到期一个月之前，客户经理应向借款人通过书面、短信、QQ、微信等途径发送到期履行债务通知，并取得有效回执，随时关注还款资金筹集情况。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第八章  贷款收回', '第四十四条', '借款人按期归还贷款后，经办行主动退还抵押、质押权证，按规定流程办理他项权证的退还手续。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第八章  贷款收回', '第四十五条', '流动资金贷款形成不良贷款的，经办行应对其进行专门管理，并及时采取清收或盘活措施。对借款人确因暂时经营困难不能按期归还贷款本息的，经办行可与借款人协商进行贷款重组。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第八章  贷款收回', '第四十六条', '对确实无法收回的流动资金不良贷款，本行按照相关规定对贷款进行处置后，应继续向债务人追索或进行市场化处置。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第九章  贷款档案管理', '第四十七条', '经办行应按照本行《信贷档案管理办法》的要求，对信贷档案进行分类、整理，将借款借据、权证等进行入库保管；对贷款运作资料进行集中保管，并建立档案调阅登记簿，规范信贷档案的保管、查阅、检查、更新。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第十章  附则', '第四十八条', '本规程由信贷管理部负责制订、解释。'),
(NULL, '信贷管理部', '《流动资金贷款操作规程》', '第十章  附则', '第四十九条', '本规程自印发之日起执行。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第一条', '为规范本行流动资金贷款业务经营行为，加强流动资金贷款管理，促进流动资金贷款业务健康发展，依据《中华人民共和国商业银行法》、《贷款通则》、《流动资金贷款管理暂行办法》、和本行《信贷管理制度》、《贷款业务尽职管理办法》等制定本办法。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第二条', '本办法所称流动资金贷款，是指本行向企（事）业法人或国家规定可以作为借款人的其他组织（以下称借款人）发放的，用于借款人日常生产经营周转的人民币贷款。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第三条', '本行开展流动资金贷款业务应当遵循依法合规、审慎经营、平等自愿、公平诚信的原则。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第四条', '流动资金贷款项目的选择必须符合国家产业政策和金融政策，向有利于农村经济持续、快速、健康发展和农村各项社会事业全面进步的中小企业、新型农村经济合作组织以及具有较大竞争力和发展潜能的新兴行业倾斜。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第五条', '合理测算借款人营运资金需求，审慎确定借款人的流动资金授信总额及具体贷款的额度，不得超过借款人的实际需求发放流动资金贷款。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第六条', '根据借款人生产经营的规模和周期特点，结合自身资金供给能力，合理设定流动资金贷款的业务品种和期限，以满足借款人生产经营的资金需求，实现对贷款资金回笼的有效控制。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第七条', '本行将流动资金贷款纳入对借款人及其所在集团关联客户的统一授信管理，统一授信除包括流动资金贷款外，还包括固定资产贷款、贴现、银行承兑等表内外信贷业务。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第八条', '本行对借款人进行信用等级评定，建立客户资信记录，并根据经济运行状况、行业发展规律和借款人的有效信贷需求等，合理确定流动资金贷款额度。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第一章  总则', '第九条', '流动资金贷款应与借款人约定明确、合法的贷款用途。流动资金贷款不得用于固定资产、股权等投资，不得用于国家禁止生产、经营的项目和领域。流动资金贷款不得挪用，应按照合同约定检查、监督流动资金贷款的使用情况。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十条', '公司业务部（以下统简称“经办行”）负责流动资金贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十一条', '授信评审部负责对流动资金贷款业务的授信管理。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十二条', '信贷管理部负责拟定流动资金贷款管理实施办法及流动资金贷款业务操作规程，对流动资金贷款业务的用信管理和贷后检查的监督。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十三条', '运营管理部根据权限负责对流动资金贷款用款进行审查、支付。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十四条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十五条', '资产保全部负责对不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十六条', '风险管理部负责对流动资金贷款的风险监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十七条', '合规管理部负责审核流动资金贷款管理实施办法及流动资金贷款业务操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第二章  组织管理体系', '第十八条', '审计部负责对流动资金贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第三章  贷款的对象与条件', '第十九条', '流动资金贷款的对象为国家规定可以作为借款人的、经履行工商行政管理职能的机构核准登记，实行独立核算的企业法人、事业法人或其他组织。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第三章  贷款的对象与条件', '第二十条', '流动资金贷款借款人应具备以下条件：㈠借款人须持有履行工商行政管理职能的机构核准登记的证件，特殊行业须持有有权机构颁发的特殊行业许可证；事业法人依照《事业单位登记管理暂行条例》的规定向事业单位登记管理机关办理了登记或备案手续；㈡借款人信用状况良好，无重大不良记录；㈢借款人生产经营合法、合规，经营项目符合国家的产业、土地、环保等相关政策，并按规定履行了各项固定资产投资建设的合法管理程序；㈣借款用途明确、合法、合理；㈤借款人具有持续经营能力，有明确的、合法的、充足的还款来源；㈥符合本行发放贷款的相关条件：1.在本行开立基本账户或一般存款户；2.除信用贷款外，落实本行认可的担保;3.本行要求的其他条件。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第四章  贷款的种类、期限与利率', '第二十一条', '流动资金贷款种类㈠按流动资金贷款期限分：1.短期流动资金贷款，期限1年以内（含），主要用于借款人正常生产经营周转的资金需要；2.中期流动资金贷款，期限1年至3年（含），主要用于借款人正常生产经营中经常占用。对落实债务，并签订分期还款计划的流动资金贷款，经总行审批同意后，可放宽贷款期限。㈡按流动资金贷款周转分：1.一般流动资金贷款，贷款按照合同约定发放，到期收回，不得循环使用；2.流动资金循环贷款，本行与借款人一次性签订最高额借款合同，在合同规定的有效期内，允许借款人多次提取贷款、逐笔归还贷款、循环使用贷款。㈢按流动资金贷款还款方式分：1.流动资金整贷整偿贷款，借款人可一次提取，到期偿还；2.流动资金整贷零偿贷款，借款人可一次提取，分期偿还。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第四章  贷款的种类、期限与利率', '第二十二条', '流动资金贷款的期限设定贷款期限主要根据借款人的生产经营周期、未来现金流、还款能力和本行的资金供给能力等因素，进行借款需求分析，由借贷双方协商确定，一般不超过3年，特殊情况经总行审批后，可放宽贷款期限。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第四章  贷款的种类、期限与利率', '第二十三条', '流动资金贷款的利率按本行利率定价办法执行。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第五章  贷款的申请、受理与调查', '第二十四条', '借款人申请流动资金贷款，应当向经办行提出书面借款申请，申明借款金额、期限、用途、贷款方式、还款来源等基本情况，并提供相关资料。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第五章  贷款的申请、受理与调查', '第二十五条', '经办行受理经办行明确客户经理负责借款申请，受理审查有关资料，并要求借款人恪守诚实守信原则，承诺所提供材料真实、完整、有效。经办行客户经理对借款人、担保人提供的资料进行认真审核，及时作出是否受理的意见。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第五章  贷款的申请、受理与调查', '第二十六条', '经办行调查经办行在正式受理借款人申请后，必须坚持面谈制度和“双人四眼”原则进行贷款调查，采取现场与非现场相结合的方式对借款人主体资格、条件满足程度、生产经营情况以及还款来源可靠性、担保措施等风险因素进行尽职调查，并形成书面调查报告，并对其内容的真实性、完整性和有效性负责。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第六章  贷款授信的审查与审批', '第二十七条', '坚持审贷分离、分级审批的原则，建立流动资金贷款授信分级审查机制。客户经理负责对客户申请资料的真实性、完整性和有效性的核实。经办行运营主管（或兼职合规员）负责对客户经理的调查资料进行书面审查。授信评审部对经办行上报的授信意见复查，初步确定授信金额，权限内的，由授信评审部有权审批人审批后，报分管行长备案；权限外的，由授信评审部提交信贷审查委员会审议；按审议结果报行长备案。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第六章  贷款授信的审查与审批', '第二十八条', '贷款审查、审批按照本行《转授权办法》规定及流程进行操作，确保风险评价和审批的独立性。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第七章  贷款的额度', '第二十九条', '经办行应根据借款人经营规模、业务特征及应收账款、存货、应付账款、资金循环周期等要素测算其营运资金需求，综合考虑借款人现金流、负债、还款能力、担保等因素，合理确定贷款金额。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第七章  贷款的额度', '第三十条', '经办行应根据流动资金需求量的测算额（附件），审慎确定借款人的流动资金授信总额及具体贷款的额度，不得超过借款人的实际需求发放流动资金贷款，并且符合本行的风险限额规定。风险限额包括但不限于以下内容：㈠单一客户贷款集中度不超过本行资本净额的10%，集团（关联）性客户贷款集中度不超过本行资本净额的15%；㈡本行规定的其他风险限额。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第八章   贷款的用信审查与审批', '第三十一条', '贷款用信的审批按权限实行分级审批。经办行权限内的用信申请，由经办行有权人审批；权限外的用信申请，经办行会办后提交本行信贷管理部审查，对使用特殊文本的用信需交合规管理部进行审查。信贷管理部对经办行提交的用信申请进行审查。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第八章   贷款的用信审查与审批', '第三十二条', '用信审查内容主要包括：已授信贷款授信条件的落实情况，授信的额度、期限、用途是否合理，贷款实际用途是否符合授信条件等。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第三十三条', '合同签订之前，经办行应确认贷款授信限制性条件是否落实，依据实际情况，按照贷款尽职调查、审查、审批记录以及贷款承诺函的要求，对借款人的借款资格、提款条件及担保措施等方面再次进行审核。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第三十四条', '合同签订必须在视频监控下进行，客户经理根据本行信贷管理要求，与借款人及其他相关当事人签订相应的合同文本，需抵押的应按合同约定办理抵押物登记，签订的合同文本需经办行运营主管（或兼职合规员）复核，未经复核的合同文本，不得外传。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第三十五条', '经办行应在借款合同中与借款人明确约定流动资金贷款的金额、期限、利率、用途、支付、还款、结息方式等条款。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第三十六条', '前条所指“支付”条款，包括但不限于以下内容：㈠贷款资金的支付方式和本行受托支付的金额标准；㈡支付方式变更及触发变更条件；㈢借款资金支付的限制、禁止行为；㈣借款人应及时提供的贷款资金使用记录和资料。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第三十七条', '经办行应在借款合同中约定由借款人承诺以下事项：㈠及时向本行提供完整、真实、有效的材料；㈡配合本行进行贷款支付管理、贷后管理及相关检查；㈢发生影响其偿债能力的重大不利事项时及时通知本行；㈣进行合并、分立、股权转让、对外投资、实质性增加债务融资等重大事项前征得本行同意；㈤本行有权根据借款人资金回笼情况提前收回贷款；㈥其他需要借款人做出承诺的事项。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第三十八条', '经办行应与借款人在借款合同中约定，出现以下情形之一时，借款人应承担的违约责任和经办行可采取的措施：㈠未按约定用途使用贷款的；㈡未按约定方式进行贷款资金支付的；㈢未遵守承诺事项的；㈣突破约定财务指标的；㈤发生重大交叉违约事件的；㈥违反借款合同约定其他情形的。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第三十九条', '保险相关规定本行可以要求借款人、担保人到指定保险机构办理相关保险，保险期不得短于贷款主合同履行期限，保险金额不得小于主合同贷款本息，保险合同及保险单中应当注明出险时本行为保险赔偿金的第一受益人。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第九章  贷款的合同签订', '第四十条', '合同变更相关规定合同变更包括合同当事人变更、贷款额度调整、合同展期等。合同不允许变更，如确有变更事项，必须由客户经理受理、审核，并按授信审批权限逐级上报审批。合同中格式条款的变更必须经本行合规管理部或本行法律顾问审核。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十一条', '客户经理依据贷款用信审批意见，在信贷管理系统中进行贷款发放操作，进行相关信息、数据维护。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十二条', '经办行权限内，经有权人审查、审批；权限外，按转授权办法执行。根据审批意见，打印《放款通知单》，交客户至柜面办理放款。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十三条', '柜面接到客户提交的《放款通知单》，经办行权限内，由有权人进行审查、发放；权限外，经运营管理部审查、审批后发放。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十四条', '经办行可以通过借款合同的约定，要求借款人指定专门的贷款发放账户，贷款发放和支付应通过该账户办理。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十五条', '具有以下情形之一的流动资金贷款，应采用受托支付方式：㈠与借款人新建立信贷业务关系且信用等级在BBB级以下（不含BBB级，下同）且单笔支付金额超过300万元，未评级企业视同信用等级在BBB级以下；㈡上述对象以外，支付对象明确且单笔支付金额大型企业超过1000万元、中型企业超过800万元、小型企业超过500万元（企业划分标准参照国家相关规定）；㈢本行规定的其他情形。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十六条', '采用本行受托支付的，经办行应根据本办法的规定进行审核。审核后，根据借款人的提款申请书，由柜面人员将贷款资金通过借款人账户支付给借款人交易对象。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十七条', '采用借款人自主支付的，客户经理应按借款合同约定要求借款人定期汇总报告贷款资金支付情况，及时收集、归档客户支付的资料，并通过账户分析、凭证查验或现场调查等方式核查贷款支付是否符合约定用途。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十八条', '签订最高额借款合同实行循环放款的，可以确定支付方式的，应在合同中注明支付方式；不能确定的，应根据核定额度和期限内的每笔贷款的实际用途，在借款人书面提款申请上予以明确。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十章  贷款发放与支付', '第四十九条', '在贷款发放和支付过程中，借款人出现下列情形的，经办行应与借款人协商补充贷款发放和支付条件，或根据合同约定变更贷款支付方式，停止贷款资金的发放和支付：㈠信用状况下降；㈡不按合同约定支付贷款资金；㈢主营业务盈利能力不强；㈣贷款资金使用出现异常；㈤违反合同约定，以化整为零方式规避本行受托支付。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十条', '客户经理应在15个工作日内对新客户首笔贷款、新增贷款进行跟踪检查，主要检查客户是否按合同约定用途使用贷款，对未按借款合同规定用途使用贷款的，应查明原因并提出处置的意见和建议。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十一条', '除本办法第五十条规定的情形外，经办行对所有流动资金贷款的贷后检查应按照“发放与检查不为同一人”原则进行，检查的时间与频率按规定执行。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十二条', '经办行应按借款合同的约定，要求借款人明确专门资金回笼账户并及时提供该账户资金进出情况，并加强对资金回笼账户的监控。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十三条', '经办行必须对抵（质）押物的价值和担保人担保能力进行贷后动态监测。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十四条', '经办行应动态关注借款人经营、管理、财务及资金流向等重大预警信号，根据合同约定及时采取提前收贷、追加担保等有效措施防范化解贷款风险。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十五条', '经办行应评估贷款品种、额度、期限与借款人经营状况、资金归行率、还款能力的匹配程度，作为与借款人后续合作的依据，必要时及时调整与借款人合作的策略和内容。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十六条', '经办行应根据法律法规规定和借款合同的约定，参与借款人大额融资、资产出售以及兼并、分立、股份制改造、破产清算等活动，维护本行债权。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十七条', '流动资金贷款需要展期的，经办行应审查贷款所对应的资产转换周期的变化原因和实际需要，决定是否展期，并合理确定贷款展期期限，按规定程序报批，并加强对展期贷款的后续管理。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十八条', '流动资金贷款形成不良的，经办行应按本行信贷管理制度的要求进行管理和清收。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十一章  贷后管理', '第五十九条', '对确实无法收回的不良贷款，按照本行呆账贷款核销的规定予以核销后，落实相应的岗位继续向债务人追索或进行市场化处置。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十二章  责任追究', '第六十条', '信贷管理部、风险管理部、审计部负责流动资金贷款业务的检查、审计和监督。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十二章  责任追究', '第六十一条', '各业务岗位违反本办法规定办理流动资金贷款业务的，根据各岗位责任大小，按本行责任追究制度规定予以经济或行政处罚。有下列违规行为之一的，将追究有关责任人的相应责任：㈠贷款调（审）查、风险评价、用信审核、贷后管理、风险处置等环节未尽职的；㈡对借款人违反合同约定的行为应发现而未发现，或虽发现但未及时采取有效措施的；㈢以降低信贷条件或超过借款人实际资金需求发放贷款的；㈣未按本办法规定签订借款合同的；㈤与借款人串通违规发放贷款的；㈥放任借款人将流动资金贷款用于固定资产投资、股权投资以及国家禁止生产、经营的领域和用途的；㈦超越或变相超越权限审批贷款的；㈧未按本办法规定进行贷款资金支付管理与控制的；㈨严重违反审慎经营规则的其他情形的。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十三章  附则', '第六十二条', '本办法由信贷管理部负责制订、解释。'),
(NULL, '信贷管理部', '《流动资金贷款管理办法》', '第十三章  附则', '第六十三条', '本办法自发文之日起执行。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第一章  总则', '第一条', '为规范海域使用权抵押贷款管理，丰富融资担保方式，防范信贷风险，根据《中华人民共和国物权法》、《中华人民共和国海域使用管理法》、《江苏省海域使用管理条例》、《海域使用权登记办法》等有关法律法规的规定，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等信贷管理制度，结合本地实际，制定本办法和流程。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第一章  总则', '第二条', '本办法及流程所称海域使用权，是指民事主体基于县级以上人民政府海洋行政主管部门的批准和颁发的海域使用权证书，依法在一定期限内使用一定海域的权利。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第一章  总则', '第三条', '本办法及流程所称海域使用权抵押贷款，是指借款人以本人或第三人合法取得的海域使用权抵押给商业银行股份有限公司（以下简称“本行”）申请贷款的行为。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第一章  总则', '第四条', '本办法及流程适用于本行辖区内支行、公司业务部、小微业务部（以下简称“支行”）。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '支行负责海域使用权抵押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '授信评审部负责对海域使用权抵押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定海域使用权抵押贷款管理办法及流程，对海域使用权抵押贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对海域使用权抵押贷款用款进行审查。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '风险管理部负责对海域使用权抵押贷款风险的监督、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核海域使用权抵押贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '审计部负责对海域使用权抵押贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第三章  贷款的对象和条件', '第十二条', '贷款对象为经工商部门登记核准的从事生产经营活动的单位和个体工商户，或从事生产经营活动的有完全民事行为能力的自然人。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第三章  贷款的对象和条件', '第十三条', '单位和个人申请办理海域使用权抵押贷款，应具备下列条件：（一）抵押人持有合法有效的《海域使用权证书》，其海域使用权权属明确；（二）抵押人使用海域符合《海域使用权证书》核准的用海类型、用途；（三）海域使用权期限未满，已按规定足额缴纳抵押期限内应缴纳的海域使用金；（四）抵押人使用的海域用于水产养殖的，抵押人还应持有合法有效的《养殖证》；（五）本行规定的贷款条件。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第三章  贷款的对象和条件', '第十四条', '有下列情形之一的海域使用权不允许作抵押：（一）抵押申请人不是合法的海域使用权人；（二）海域使用权及海域上的建筑物、构筑物及其他设施有争议的；（三）经批准临时用海的；（四）抵押人与其他海域使用权人共同使用的海域事先未取得其他使用权人的书面同意的；（五）抵押人未按规定缴纳海域使用金的；（六）抵押人有擅自改变海域使用用途等违法行为的；（七）抵押期限超出海域使用权期限或者海域使用金已缴纳期限的；（八）属于公益性用海的海域使用权、免缴海域使用金的海域使用权；（九）抵押人有违法用海行为正在立案查处尚未结案的；（十）村民委员会或村经济合作社持有的海域使用权；（十一）经营收益用于老渔民生活补助的海域使用权；（十二）国家规定的其他不得抵押的情形。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第三章  贷款的对象和条件', '第十五条', '海域使用权抵押时，其海域地上建筑物、其他附着物及其他设施随之抵押；海域上建筑物、其他附着物及其他抵押时，其使用范围内的海域使用权随之抵押。海域使用权连同海域建筑物、其他附着物及其他设施作为不动产抵押时，不得分开单独抵押或重复抵押。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第四章  贷款额度、期限、利率', '第十六条', '海域使用权抵押贷款的额度一般不超过海域使用权评估价值的50%。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第四章  贷款额度、期限、利率', '第十七条', '贷款期限。海域使用权抵押贷款的期限一般不超过海域使用权证书载明的使用期限扣除使用权人已使用的时间后的年限，应结合贷款用途合理确定贷款期限。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第四章  贷款额度、期限、利率', '第十八条', '贷款利率。根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第十九条', '海域使用权抵押贷款操作程序为借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十条', '申请人申请海域使用权抵押贷款时应提供以下资料:（一）申请人为个人的，应提供借款人及其配偶的有效身份证明原件及复印件、婚姻证明，未婚借款人应提供未婚证明，并签署单身承诺函，借款人偿债能力证明（包括借款人家庭经济收入证明、纳税证明、银行对账单、租赁合同、银行储蓄存单及有价证券等的原件及复印件）；申请人为公司的，应提供包括但不限于营业执照副本及年检登记文件、组织机构代码证、企业法定代表人身份证明、税务登记证及按时纳税证明、公司章程、验资报告、董事会有关决议（合伙企业有关书面证明）、申报时点的资产负债、损益、现金流量等报表；（二）抵押人持有合法有效的《海域使用权证书》原件及复印件。（三）海域使用金缴纳凭证原件及复印件。（四）本行要求提供的其他相关材料原件及复印件。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十一条', '海域使用权抵押贷款，先由借款人填写贷款申请审批表，客户经理对借款人提出的海域使用权抵押贷款书面申请，应当进行审查。审查内容包括：（一）借款人、抵押人提供的资料身份证明是否真实、有效；（二）抵押人提供的《海域使用权证书》是否合法有效；（三）借款人的生产经营和信用情况；（四）借款人的还款来源和还款能力。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十二条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十三条', '抵押人、抵押权人接到海洋行政主管部门同意海域使用权作抵押的意见书后，对海域使用权进行价值评估。海域使用权的价值评估，应由本行委托其认可的评估机构，对拟抵押的海域使用权进行价值评估，出具相应的评估资料。评估所需费用由借款人承担。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十四条', '抵押权人根据海洋行政主管部门同意海域使用权抵押意见、海域使用权价值评估资料和抵押人的申请材料，与抵押人签订《借款合同》和《最高额抵押合同》，按规定办理海域使用权抵押登记。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十五条', '海域使用权抵押当事人应在签订《借款合同》和《最高额抵押合同》后，向海洋行政主管部门申请办理海域使用权抵押登记。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十六条', '申请办理海域使用权抵押登记，应提交下列材料：（一）海域使用权抵押登记申请表；（二）海域使用权抵押贷款申请审批表；（三）抵押双方当事人身份证明材料(原件和复印件)：当事人属于公司的，提交营业执照、章程、法人代表资格证明，股份制公司还应出具董事会有关决议，属于合伙企业申请的，提交所有合伙人的身份证明和本人同意将其所有的海域使用权抵押的书面证明，当事人为个人的提交本人身份证明；（四）《海域使用权证书》（原件和复印件），养殖用海的，还应提交《养殖证》（原件和复印件）；（五）海域上建筑物或其它附着物及其他设施权属证明；（六）《最高额抵押合同》和《借款合同》（原件和复印件）；（七）海域使用权价值评估报告书及抵押权人对该评估报告书的认可意见书；（八）抵押人缴纳海域使用金的单据；（九）抵押人和本行委托他人办理抵押登记的，提交委托人身份证明、委托授权书和代理人身份证件；（十）依法应提供的其他材料。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十七条', '借款人办妥上述手续，并取得《海域使用权利证书》后，本行按贷款用途发放贷款，将资金划入借款人本人的结算账户，并严格监督借款人按合同约定支付方式支付贷款资金。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十八条', '借款人按合同规定偿还全部贷款本息后，客户经理应当将保管的抵押物权属证明交还借款人，并与借款人到原登记机关办理注销登记。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第二十九条', '贷款发放后，应按照本行信贷管理相关制度办法进行贷后管理。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第三十条', '抵押合同变更。抵押合同内容发生变更的，双方当事人应当重新签订抵押合同，并持有关材料到原登记机关申请办理抵押变更登记。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第三十一条', '海域使用权处置。借款人到期不能清偿债务的，本行可以依法处置抵押的海域使用权，并就处置所得优先受偿。处置所得优先受偿后仍有剩余的，应当退交抵押人；不足偿还贷款本息的，本行有权向借款人追偿。抵押的海域使用权处置后，应办理海域使用权权属变更登记手续。未经主管机关批准，不得改变对处置的海域使用权的原有用途。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第五章  抵押贷款程序', '第三十二条', '档案管理。支行应按本行信贷档案管理有关规定收集、保管相关资料。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第六章  附则', '第三十三条', '本办法及流程由信贷管理部负责制定、修改、解释。'),
(NULL, '信贷管理部', '《海域使用权抵押贷款管理办法及流程》', '第六章  附则', '第三十四条', '本办法及流程自下发之日起执行。'),
(NULL, '信贷管理部', '《用信管理办法》', '第一章  总则', '第一条', '为规范本行用信操作流程，防范和控制信贷风险，根据国家相关法律法规制度，结合本行《信贷管理制度》、《贷款业务尽职管理办法》等相关信贷管理要求，制定本办法。'),
(NULL, '信贷管理部', '《用信管理办法》', '第一章  总则', '第二条', '本办法所称授信业务是指单一客户法人、其他经济组织或自然人的各类授信业务。包括但不限于贷款、票据承兑和贴现、保函、担保、贸易融资、贷款承诺等表内外信用发放形式的统一综合授信。'),
(NULL, '信贷管理部', '《用信管理办法》', '第一章  总则', '第三条', '本办法所称用信，是指信贷授信业务审批后按照授信批复要求落实贷款发放条件，签订合同（协议），办理抵、质押担保手续，放款审核和发放信用等信贷业务实施工作。'),
(NULL, '信贷管理部', '《用信管理办法》', '第一章  总则', '第四条', '用信必须坚持“先授信、后用信”、“谁受信、谁用信”、“先落实授信条件、后实施用信”的原则。'),
(NULL, '信贷管理部', '《用信管理办法》', '第二章  职责与权限', '第五条', '支行、营业部、公司业务部、小微业务部（以下简称“经办行”）负责用信权限内信贷业务的审查、审批，用信权限外信贷业务的审查、审核上报工作，对用信资料的真实性、完整性、有效性、合规性及业务风险进行把关。'),
(NULL, '信贷管理部', '《用信管理办法》', '第二章  职责与权限', '第六条', '信贷管理部负责经办行用信权限外授信条件落实以及用信资料完整性、合法性、有效性等形式要件审查、审批。'),
(NULL, '信贷管理部', '《用信管理办法》', '第二章  职责与权限', '第七条', '金融市场部负责审核票据承兑和贴现等业务资料的真实性、合法性、有效性、完整性；负责票据承兑和贴现等业务资料的保管。'),
(NULL, '信贷管理部', '《用信管理办法》', '第三章  合同签订', '第八条', '经办行客户经理根据授信最终审批意见，落实好授信条件，办理合同签订、抵押登记等用信前准备事宜。'),
(NULL, '信贷管理部', '《用信管理办法》', '第三章  合同签订', '第九条', '所有信贷业务都必须签订合同，内容包括：信用种类、币种、用途、金额、期限、利率或费率、担保方式、支付对象（范围）、支付金额、支付条件、支付方式、还款方式、合同双方的权利、义务等内容，如采取多人（两人及以上）保证担保方式且事前约定各自担保份额比例的，应按照授信最终审批意见，在保证合同中进行明确。'),
(NULL, '信贷管理部', '《用信管理办法》', '第三章  合同签订', '第十条', '信贷合同必须按规定使用统一制式合同文本。对情况特殊的信贷业务，在不违背制式合同文本基本要求的基础上，合同双方可以在协商一致的前提下增加若干补充条款或签订非制式文本的信贷合同，但必须经合规管理部审查同意。'),
(NULL, '信贷管理部', '《用信管理办法》', '第三章  合同签订', '第十一条', '信贷合同由借款合同和担保合同组成，分为主从合同合一和主、从合同分开两种形式，其中，采取主、从合同分开形式的，借款合同是主合同，担保合同是从合同，主从合同必须相互衔接。信贷合同的填写和签章应符合下列要求：（一）先签订主合同，后签订从合同；采用经办行提供的蓝黑或黑水笔书写，内容填制必须完整，如属文本式合同，其正副文本的内容必须一致，不得涂改；若合同采用打印方式的，应固化合同版本，并严格落实相应的岗位制衡机制；（二）信贷合同业务种类、币种、金额、用途、期限、利率或费率、还款方式和担保合同应与信贷业务审批内容相符；（三）合同签订应在签约台监控下进行，并做好登记，确保合同相关当事人在合同文本上签字、盖章（或摁指模）真实、有效。客户因特殊情况无法在签约台监控下签约的，应由经办行双人共同监督相关当事人当面签字、盖章（或摁指模），拍照留存；（四）合同当事人为个人的，以当事人签字加指印（通常情况下为右手大拇指指印）为准。合同当事人为非个人类客户的，应以其公章、法定代表人（负责人）章印及法定代表人（负责人）或其授权代理人的签字为准。代理签订合同，经办行要审查授权委托书的真实、有效性。'),
(NULL, '信贷管理部', '《用信管理办法》', '第三章  合同签订', '第十二条', '信贷合同应进行统一编号，并在相应的信贷业务合同和担保合同上填写，主从合同编号必须相互衔接。'),
(NULL, '信贷管理部', '《用信管理办法》', '第三章  合同签订', '第十三条', '合同签订后，客户经理应及时提交审查人员审核，无误后加盖“贷款合同专用章”并及时登记《贷款合同专用章使用情况登记簿》。审查人员审核内容包括：（一）合同文本的使用是否恰当；（二）合同填制的内容是否符合要求，主从合同的编号是否衔接；授信批复中明确采取按份额保证方式的，是否在合同相关条款中载明；（三）合同的补充条款是否合法合规，是否符合制式合同文本的基本条款；（四）合同相关当事人在合同文本上签字、盖章（或摁指印）是否齐全；合同经办人是否在合同文本上签字；（五）多页式合同所有合同当事人是否已加盖骑缝章，所加盖的骑缝章是否覆盖合同内所有页数。'),
(NULL, '信贷管理部', '《用信管理办法》', '第三章  合同签订', '第十四条', '合同签订后，应区别担保方式，与借款人、保证人、抵押人、质押人共同办理以下事宜：（一）抵押、质押担保的，抵质押物类型及抵质押权值以授信批复为准，需要登记的，应到相关有权登记部门办理抵押、质押登记，并将登记权属证书交柜面入库保管；（二）以存单、国债、债券、保单等权利凭证质押的，应办理止付手续；（三）抵（质）押登记权属证书、质押物与柜面交接时，应填制“抵质物代保管凭证”，要与抵押人、出质人共同签章办理交接手续。'),
(NULL, '信贷管理部', '《用信管理办法》', '第四章  用信调查及资料收集', '第十五条', '经办行客户经理根据授信最终审批意见，逐一落实授信条件，根据客户的用信申请和用途证明资料，进行用信初审，判断用信的可行性，确定支付方式，并做好用信资料的收集。'),
(NULL, '信贷管理部', '《用信管理办法》', '第四章  用信调查及资料收集', '第十六条', '客户经理用信初审，判断用信是否在授信审批结束后6个月内，审查内容有所区别。（一）6个月内用信1.授信条件是否落实，贷款用途是否真实、合规。2.信用情况、经营情况、财务状况、担保情况是否正常，有无明显异常变化。（二）6个月后用信1.经营情况、财务状况、担保情况有无重大异常变化，对外担保有无重大不利变化等。2.查询征信报告，调查借款人信用状况是否符合条件。3.通过“启信宝”等进行工商查询，调查企业年检、法人代表、股权结构等有无异常变化。4.查询法院裁判文书网、被执行人信息查询系统、失信信息查询系统等，查询是否有被诉讼、被执行记录，有无执结等。5.抵押贷款需到有权部门核实抵押物是否有被保全、查封、异议登记等情况、抵押资产是否被转移或灭失、毁损等；质押担保的，需查询质押物状况等情况。'),
(NULL, '信贷管理部', '《用信管理办法》', '第四章  用信调查及资料收集', '第十七条', '用信资料的收集（一）发放审批相关资料：授信批复、公司客户提供《公司流动资金借款审查（审批）表》、个人客户提供《自然人授信客户用信审查（审批）表》等；（二）合同文本资料：借款合同、担保合同、抵（质）押物品清单、担保书、抵质押登记证明、不动产登记证明、手机银行自助用信服务协议等、借款单位和担保单位的决定文书、各类承诺书等；（三）贷款用途资料：购货合同、协议、工程进度表，同比例自有资金证明等；（四）其他相关资料：面谈记录、提款通知书、提款支付审查意见表、委托支付协议等资料。'),
(NULL, '信贷管理部', '《用信管理办法》', '第五章  信贷系统操作', '第十八条', '在用信审查与签订合同等相关手续办妥后，客户经理进行信贷系统提交审核操作，主要分为用信模块系统操作和用信资料的扫描上传。'),
(NULL, '信贷管理部', '《用信管理办法》', '第五章  信贷系统操作', '第十九条', '系统操作应符合下列要求：（一）借款期限、金额、利率、用途、合同编号、结息方式等必须与纸质合同一致；（二）担保信息必须与纸质合同一致；（三）抵质押信息必须与抵、质押物清单、权利登记证明上记载信息一致；（四）受托支付信息必须与购销合同上交易对手的信息一致；（五）如果还款方式为“按计划表还本”，系统内应录入还款信息并与纸质合同协议一致；（六）如果保证担保合同中约定各保证人保证份额的，系统内应按实录入并与纸质合同协议一致；（七）综合授信额度在30万元及以下（经营类个人授信客户可放宽至50万元及以下）的客户，开通手机银行自助用信时，必须通过信贷风险管理系统提交申请，并上传《手机银行自助用信服务协议》和《授信批复》，审批通过后方可自助用信。'),
(NULL, '信贷管理部', '《用信管理办法》', '第五章  信贷系统操作', '第二十条', '客户经理应将所有用信资料扫描并提交至信贷风险管理系统指定菜单内。'),
(NULL, '信贷管理部', '《用信管理办法》', '第六章  用信审查审批', '第二十一条', '经办行审查、审批人员负责对纸质用信资料和信贷风险管理系统扫描资料的完整性、合规性、合法性、有效性进行核对和复审，并对信贷风险整体把关，符合用信条件的，权限内直接审批发放，权限外提交总行。信贷管理部负责经办行权限外用信审查、审批，对经办行通过信贷风险管理系统扫描提交的用信资料进行复查，符合用信条件的，直接审批；不符合用信条件的，退回重新补充、完善，保证用信资料完整、有效、合规。'),
(NULL, '信贷管理部', '《用信管理办法》', '第六章  用信审查审批', '第二十二条', '经办行审查、审批人审核的内容主要包括：（一）用信资料的完整性按照授信批复，审核用信资料是否齐全完整，主要有：1.基础资料：包括《公司授信客户用信审查（审批）表》或《自然人授信客户用信审查（审批）表》、个人面谈记录等；2.合同等法律文书：包括借款合同、担保合同、担保书、借款单位董事会或股东会决议、担保单位董事会或股东会决议、抵质押物品清单、动产抵押登记书、不动产登记证等；3.出账手续：包括购销协议、提款通知书、提款支付审查意见表、委托支付协议等；4.其他资料：其他要求提供的资料等。（二）用信资料的有效性按照授信批复，审核用信资料是否符合授信业务的有关规定和要求。主要有：1.授信条件是否落实；2.用款是否控制在授信额度内；3.合同文本使用是否正确；4.合同签订是否在授信规定的有效期内；5.借款单位和担保单位决议金额和期限是否有效；6.抵质押物共有人是否签字；7.购销协议是否符合授信用途、期限是否有效；8.支付方式是否符合规定；9.对用信中发现的可疑点或可能产生的重大法律风险，客户经理是否按合规管理部出具的答复意见办理；10.其他需要审查的资料。（三）用信资料的合规性1.合同要素的合规性：合同填写必须做到标准、规范、要素齐全、数字正确、字迹清晰、不错漏、不潦草，不涂改，是否按要求加盖骑缝章。2.其他资料的合法、合规性：授信业务是否符合法律、法规的规定；授信条件是否得到完全落实，手续是否完备，不能落实的事项是否经有权人批准；借款单位和担保单位决议和同意人数或股权是否符合法律以及章程规定；抵（质）押贷款的抵（质）押物是否有效，抵（质）押登记等法律手续是否合法、合规及有效等。（四）其他方面审核1.授信要求追加担保的是否签订担保书；追加企业担保的，担保书上除盖单位公章外法定代表人是否签字，股东会或董事会是否有决议；2.合同期内再次用信时，客户经理是否至有权登记部门对抵（质）押物进行查询并收集查询结果，异地查询无法收集有权部门查询结果资料的，必须在用信调查报告中反映；3.其他需要审核的资料。'),
(NULL, '信贷管理部', '《用信管理办法》', '第六章  用信审查审批', '第二十三条', '总行信贷管理部用信审查岗审核的主要内容包括：（一）授信条件是否落实；（二）用信资料是否齐全，合同是否有效、合规，信贷风险管理系统上传资料是否完整、清晰。'),
(NULL, '信贷管理部', '《用信管理办法》', '第六章  用信审查审批', '第二十四条', '对审核中发现的可疑点或可能产生的重大法律风险，审核人员必须征求本行合规管理部意见，并按答复意见办理。'),
(NULL, '信贷管理部', '《用信管理办法》', '第七章  贷款发放与支付', '第二十五条', '用信审查通过后，客户经理打印放贷通知书，负责将不动产登记证原件及其复印件、放贷通知书、抵质押品入账通知书及用信资料等送交柜面人员审查后办理账务处理。'),
(NULL, '信贷管理部', '《用信管理办法》', '第七章  贷款发放与支付', '第二十六条', '柜面人员在办理账务处理前应审查以下内容：（一）审查信贷业务是否经有权审批人审批同意；（二）借据的借款人名称、借款金额、还款日期、借款利率、借款合同号等内容是否与信贷业务合同的内容相符；（三）押品通知书内容与权利证书是否相符；（四）借款凭证的章印是否与预留印鉴一致；（五）资金支付手续是否完备。'),
(NULL, '信贷管理部', '《用信管理办法》', '第七章  贷款发放与支付', '第二十七条', '对于个人信贷业务，柜面放款出账时，借款人必须持有效身份证件亲自到柜面办理。办理时，柜面人员应当认真核对、确认借款人身份以及指定结算账户，并要求借款人在“借款凭证”上签字加指模。贷款资金直接发放至借款人本人账户，不得现金发放。如采取受托支付方式的，按规定进行账务处理。每日日终，柜员应打印当天自助发放的贷款借据和贷款明细清单，并落实专人将清单交管户客户经理。'),
(NULL, '信贷管理部', '《用信管理办法》', '第八章  档案管理', '第二十八条', '贷款发放后，柜员除将不动产登记证原件、用于质押的有价单证入库保管外，其他用信资料以及借款借据（信贷管理联）一律交客户经理或审查人员集中保管，并按总行规定制作清单后定期上缴总行档案管理中心。'),
(NULL, '信贷管理部', '《用信管理办法》', '第八章  档案管理', '第二十九条', '用信档案的移交、归档、管理以及电子化档案管理等，按照总行《信贷档案管理实施办法》的有关规定执行。'),
(NULL, '信贷管理部', '《用信管理办法》', '第九章  罚则', '第三十条', '凡提交总行用信审查，因下列情形之一退回的，根据《员工违规行为处理办法》相关规定给予记分处理：1.因用信审查（审批）表、授信批复、受托支付（协议、购销合同）、展期协议等基础资料不全、不清晰等被退回的，每次扣2分；2.因信贷合同要素不全、章印不全、内容填写不正确或涂改等原因被退回的，每次扣5分；3.因授信限制性条款未落实被退回的，每次扣10分。'),
(NULL, '信贷管理部', '《用信管理办法》', '第九章  罚则', '第三十一条', '上述记分直接记扣到经办行，由经办行负责人按客户经理A角、审查人8:2的责任比例分摊处罚到人。'),
(NULL, '信贷管理部', '《用信管理办法》', '第十章  附则', '第三十二条', '综合授信额度50万元及以下的易贷通客户，首次用信时必须按照本办法办理用信手续，在授信、合同有效期限内，客户继续申请用信时，可由客户凭身份证、易贷通卡直接到柜面办理。实行临时授信管理的或综合授信额度超过50万元的客户，不论单笔用信金额大小，均需按本办法执行。'),
(NULL, '信贷管理部', '《用信管理办法》', '第十章  附则', '第三十三条', '符合手机银行等自助渠道放款的客户，首次用信时必须按照本办法办理用信手续并签订自助用信服务协议。'),
(NULL, '信贷管理部', '《用信管理办法》', '第十章  附则', '第三十四条', '本办法由信贷管理部制定，并负责解释、修订。未尽事宜，按照本行《信贷管理制度》等有关业务管理制度、办法办理。'),
(NULL, '信贷管理部', '《用信管理办法》', '第十章  附则', '第三十五条', '本办法自发布起施行，原有规定中与本办法相抵触的，以本办法为准。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第一章  总则', '第一条', '为实现本行客户分类营销和管理，更好地服务优质客户，根据《个人贷款管理暂行办法》，以及本行《个人贷款管理实施细则》、《个人信用贷款管理办法》规定，特制定本办法。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第一章  总则', '第二条', '本办法所称类存单质押贷款是向本行定期储蓄存款客户发放的，用于短期融资、无需提供担保的人民币贷款。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第一章  总则', '第三条', '本办法所称贷款人是指本行所辖支行、营业部、小微业务部、公司业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第四条', '经办行负责对类存单质押贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第五条', '授信评审部负责对类存单质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第六条', '三农业务部负责类存单质押贷款业务的宣传策划、营销推动及日常督查管理。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定类存单质押贷款管理办法，对类存单质押贷款业务的用信管理和贷后检查管理的监督。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对类存单质押贷款放款进行审查。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第九条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第十条', '科技信息部负责对类存单质押贷款客户的数据采集、归并，并提供技术支撑。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第十一条', '资产保全部负责对不良贷款清收保全及相关管理。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第十二条', '风险管理部负责对类存单质押贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第十三条', '合规管理部负责审核类存单质押贷款办法及流程的合规性和有效性。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第二章  组织管理体系', '第十四条', '审计部负责对类存单质押贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十五条', '申请类存单质押贷款的客户为具有完全民事行为能力的自然人，且借款人年龄加上贷款期限不超过65周岁。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十六条', '申请类存单质押贷款的客户应符合下列条件：（一）资信等级评估为良好以上；（二）近3年内无借款、担保以及经济纠纷未解决等重大不良信用记录；（三）本人及其配偶持有本行定期一年及以上期限的储蓄存款；（四）持有本行借记卡或易贷通卡，并注册开通本行手机银行；（五）本行规定的其他条件。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十七条', '额度确定。根据借款人及其配偶在本行定期一年及以上期限的储蓄存款总额的80%和借款人前3年总存款日平的20%综合计算，或按借款人及其配偶在本行定期一年及以上期限的储蓄存款总额的90%计算，两项指标值按就高原则给予借款人一定的贷款信用额度，单户最高限额一般不超过10万元。信用额度可每年调整一次。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十八条', '类存单质押贷款信用额度授信期限最长1年，期限内可根据借款人意愿，实行单笔或循环周转使用，采取按季（月）结息，到期一次性还本还息的还款方式。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第三章  贷款对象、条件、额度等', '第十九条', '贷款利率。按本行利率定价办法执行。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十条', '类存单质押贷款信用额度授信实行内部统一授信方式。每年年初，各经办行根据本单位业务系统生成的内部数据，结合借款人资信状况、信用记录、定期一年及以上期限储蓄存款总额等指标，综合确定拟授信客户当年信用额度，并及时上报授信评审部审查审批。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十一条', '对同一家庭，类存单质押贷款与存单质押贷款不得同时存在，已办理类存单质押贷款不得申请存单质押贷款，已办理存单质押贷款也不得申请类存单质押贷款。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十二条', '用信申请。申请人向本行申请类存单质押贷款时，应提供以下资料的原件和已核原件的复印件：1.借款人身份证、户口簿；2.借款人在本行定期一年及以上期限的储蓄存单；3.借款人本人签署的征信授权书；4.本行要求提供的其他资料。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十三条', '合同签订。借款人如采取单笔借款的，直接签订个人信用贷款借款合同；如采取周转使用的，需明确一名家庭主要成员（配偶或成年子女）为保证人，并签订最高额保证担保借款合同，共同承担贷款本息按期偿还责任。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十四条', '借款人须办理意外伤害保险，且明确本行为第一受益人，保险金额须覆盖贷款本金及预计利息。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十五条', '类存单质押贷款不得办理展期。贷款存续期内，借款人出现影响贷款按期收回的不利因素时，应及时协商处置或诉讼保全。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十六条', '类存单质押贷款日常管理参照本行《个人贷款管理实施细则》执行。采用本行电子银行渠道自助放款的，按相关协议要求执行。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十七条', '有下列情形之一时，仍按《商业银行股份有限公司个人定期存单质押贷款管理办法及流程》执行。1.年龄超过65周岁的；2.申请贷款额度超过10万元的；3.以借款人及其配偶以外的第三人储蓄存单质押的；4.其他原因申请办理存单质押的。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第四章  其他规定', '第二十八条', '对在本行无定期储蓄存款但近3年保持一定存款日平的客户，可结合客户经济实力，按《商业银行股份有限公司个人信用贷款管理办法》执行。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第五章  附则', '第二十九条', '业务经办人员必须严格遵守本行《贷款业务尽职管理办法》办理类存单质押贷款业务。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第五章  附则', '第三十条', '本办法未尽事宜，参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第五章  附则', '第三十一条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《类存单质押贷款管理办法》', '第五章  附则', '第三十二条', '本办法自公布之日起执行。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第一章  总则', '第一条', '为拓展优质信贷市场，规范和促进本行经营性物业贷款业务健康发展，根据《中华人民共和国担保法》、《中华人民共和国物权法》、《固定资产贷款管理暂行办法》、《项目融资业务指引》等有关规定，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关制度，制定本办法。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第一章  总则', '第二条', '本办法所称的经营性物业是指已竣工验收并投入运营，经营管理规范、经营收入稳定、现金流充裕、综合收益较好的商业营业用房、办公用房、宾馆酒店、大型购物中心、酒店式公寓、工业和仓储用房等物业形式。经营性物业贷款是指本行向具有合法承贷主体资格的经营性物业所有权人发放的，以其所拥有的物业作为贷款抵押物，以该物业的经营收入作为主要还款来源的贷款。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第一章  总则', '第三条', '发放经营性物业贷款应符合国家法律、法规的有关规定，遵循“安全性、流动性、效益性”原则。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第四条', '公司业务部、小微业务部（以下简称“经办行”）负责经营性物业贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第五条', '授信评审部负责经营性物业贷款业务的授信管理。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定经营性物业贷款管理办法及操作规程，对经营性物业贷款业务的用信管理和贷后检查进行管理监督。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对经营性物业贷款用款进行审查。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '计划财务部负责制定经营性物业贷款业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '风险管理部负责对经营性物业贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '合规管理部负责审核经营性物业贷款管理办法和操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '资产保全部负责对经营性物业贷款业务形成的不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '审计部负责对经营性物业贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第三章  贷款条件和用途', '第十三条', '贷款条件。贷款条件分为贷款对象条件和经营性物业条件，贷款对象分为法人类和自然人类。（一）法人类贷款条件1.资信良好。法人及其法定代表人、实际控制人近 3 年在本行及他行未发生过逾期和不良记录，未受过公安和行政部门处罚，信用良好；2.具有法人营业执照并按时办理年报公示手续；3.属于房地产开发企业的，应取得房地产开发主管部门核发的房地产开发企业资质等级证书，并办理年检手续；4.产权清晰、法人治理结构健全、经营管理规范、财务状况良好；5.在本行开立基本账户或一般账户，无不良信用记录；成立不足2年的新设立企业其控股股东应有良好的信用记录；6.拥有经营性物业全部产权，持有合法、有效的房产所有权证和土地使用权证；7.董事会或相应决策机构同意将其拥有的经营性物业作为贷款抵押物；8.同意与本行签订租金账户监管协议，接受本行对物业经营收入、支出款项的封闭式监管。同时，承诺物业经营所产生的资金结算、代收代付等中间业务在本行办理；9.本行要求的其他条件。（二）自然人类贷款条件1.个人资信良好。在本行及他行未发生过逾期和不良记录，社会形象佳，未受过公安和行政部门处罚，个人及家庭负债与其经营收入相符；2.拥有经营性物业产权，持有合法、有效的房产所有权证和土地使用权证，个人名下物业可为与他人共有，但须征得共有人的书面同意；3.借款人及共有人同意将其拥有的经营性物业作为贷款抵押物；4.同意与本行签订租金账户监管协议，接受本行对物业经营收入、支出款项的封闭式监管。同时，承诺物业经营所产生的资金结算、代收代付等中间业务在本行办理；5.本行要求的其他条件。（三）经营性物业具备的基本条件1.经营性物业应符合地方规划要求，经竣工综合验收合格并办妥房产证，且已投入商业运营；2.地理位置优越，物业应位于城市中央商务区、中心商业区、工业集居区等交通便捷、人流、物流、车流充裕，商业、商务氛围浓厚的繁华地段；3.经营性物业已使用期限一般不超过10年；经营性物业用地已使用年限一般不超过10年；4.经营性物业定位准确，经营情况稳定，出租市场前景较好，出租率不得低于80%，在贷款期内现金流足够按期归还贷款本息；5.经营性物业市场价值和租金价格稳定或有上涨趋势；6.经营性物业具有较强的变现能力，有利于整体处置；7.经营性物业必须通过出让方式合法取得国有土地使用权证书。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第三章  贷款条件和用途', '第十四条', '贷款用途。主要用于物业在经营期间的资金需求，具体可分为两种类型：（一）自行建造的物业。可用于物业本身的装修改造，也可用于置换负债性资金和超过项目资本金规定比例以上的自有资金）。（二）购置的物业。可用于置换购置款。贷款不得用于下列用途：（一）不得用于国家明令禁止的投资领域和用途；（二）不得用于偿还存量不良贷款和违规贷款；（三）不得用于其他不符合国家政策和本行信贷要求的情形。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、还款方式', '第十五条', '贷款额度。经营性物业贷款的额度根据物业可用于还贷的现金流确定，同时应综合考虑物业的评估价值，贷款额度最高不超过抵押物评估价值的60%。其中，非工业和仓储用房类经营性物业贷款的抵押担保额度不得超过抵押物评估价值的60%，工业和仓储用房类经营性物业贷款的抵押担保额度不得超过抵押物评估价值的50%。经营性物业贷款额度为一次性额度，额度随分期还款计划逐步递减，不得循环使用。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、还款方式', '第十六条', '贷款期限。经营性物业贷款期限最长不超过10年，具体贷款期限应综合考虑借款人融资需求、现有融资情况、物业预期经营现金流状况、借款人其他综合还款来源等因素合理确定。贷款期限最长不超过借款人法定经营期限，也不得超过物业产权证的剩余使用年限。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、还款方式', '第十七条', '贷款利率。参照本行贷款利率定价办法规定标准执行。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、还款方式', '第十八条', '还款方式。在贷款发放前，要根据物业现金流情况、项目出租前景和租金收取方式制定切实可行的分期还款计划，考虑到物业的租金回收具有一定周期性，可给予最长不超过一年的宽限期。采用综合还贷方式的，应制定明确的按年（季）还款计划，物业经营收入以外用于还款的综合收入应分期落实。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第五章  贷款担保方式', '第十九条', '经营性物业贷款以竣工验收合格、取得房产证的经营性物业进行抵押担保，必要时还应提供本行认可的其他财产抵（质）押或第三方以及借款人、法定代表人或其实际控制人连带责任保证担保。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第五章  贷款担保方式', '第二十条', '经营性物业贷款抵押物须在相关管理部门办理房产、土地抵押登记，办理抵押物登记时须提供抵押物房产证、土地使用权证、借款合同和抵押合同等相关材料。置换他行贷款的，为防止他行抵押权灭失并为我行设定抵押前本行贷款处于悬空状态，应要求借款人提供有效的阶段性担保。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第五章  贷款担保方式', '第二十一条', '为防止借款人通过修改租赁合同、另行签订长期低租金合同或以其他方式对抗我行抵押权，应要求出租人和承租人持租赁合同到房管部门办理租赁登记备案手续，并由承租人向本行出具放弃相关权利的承诺书。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第五章  贷款担保方式', '第二十二条', '为确保工业和仓储用房类经营性物业的变现能力，应要求借款人将必要的生产、生活配套服务设施一并抵押。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第六章  贷款调查和评估', '第二十三条', '借款人申请经营性物业贷款时，除提供本行信贷管理制度及相关文件要求的资料外，还需提供以下资料：（一）经竣工验收合格的经营性物业合法有效证明材料、产权证书；（二）租赁的物业。须提供真实的租赁合同原件，出租人的营业执照或身份证复印件；（三）物业用于出租的，须提供真实的租赁合同、协议原件、承租方营业执照、承租方知晓物业抵押给本行和放弃相关权利的声明等。租赁合同涉及多页的，页面与页面之间必须同时加盖双方骑缝公章；（四）物业用于经营的，须提供经营主体的营业执照、组织机构代码证、税务登记证；（五）本行要求提供的其他证明文件和资料。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第六章  贷款调查和评估', '第二十四条', '经营性物业贷款的调查主要包括合法性、安全性和效益性调查。调查主要包括以下内容：（一）借款人是否具备合法承贷主体资格，法人营业执照是否有效。（二）物业的权属状况。物业是否经竣工验收合格、是否取得产权证，物业设定抵押情况。（三）物业的出租率、租赁合同的租期、租金水平和租金支付方式。调查租金收入与贷款额度、贷款期限是否匹配；调查租金支付方式，防止物业在抵押给本行时，承租人已经一次性向借款人支付长期租金，或者是将租金用来偿还出租人所欠承租人的债务，导致本行届时无法从租金中获得还款来源或者增加处置抵押物的难度。（四）租约情况。调查租约的真实性以及租赁合同中是否存在损害本行利益的条款。要特别注意承租人依法享有的承租权、优先购买权及其他权利对本行抵押权的限制和影响，对抵押权实现的难易程度作必要的预估，并要求承租人向本行出具放弃租赁权、优先购买权等相关权利的承诺书。（五）承租人资信、经营状况、支付租金能力等情况。是否存在承租人经营状况或资信不佳、按期足额支付租金有困难或多次拖欠租金等情况。（六）物业在当地同类市场的竞争优劣势情况，其经营收益、转让价值等市场行情的历史波动状况以及贷款期内的预期变化趋势。（七）贷款用途、金额、期限、还款计划是否合理，是否与物业的经营特征相符。（八）贷款期内借款人的经营计划和重大投资计划，及其对物业经营的影响。（九）对于采用综合还款方式的，要重点调查借款人的整体经营及财务状况，财务管理水平和资产营运能力，收入、利润来源和现金流情况；借款人除租金收入外的其他经营收入来源，企业是否具备综合还款能力。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第六章  贷款调查和评估', '第二十五条', '贷款评估应重点评估以下内容：（一）借款人评价。包括借款人的组织形式、股权结构、经营业绩、信用状况、财务状况、现金流量状况等。（二）抵押物评价。包括抵押物的出租经营情况、实际建造或购置成本、市场价值情况、变现能力等。（三）偿债能力评价。包括财务效益预测、现金流量预测及偿债能力预测等，并对物业每年经营收入进行评价，经营收入应扣除定期大修和日常维护成本。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第六章  贷款调查和评估', '第二十六条', '经营性物业贷款的抵押物价值评估可以以抵押人和本行双方约定的方式进行，亦可由本行委托认可的评估机构进行评估确定。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第二十七条', '经营性物业贷款操作程序分借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。条二十八条  对经营性物业贷款的审查和审批按本行转授权办法等信贷制度执行。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第二十九条', '经办行接到借款人申请资料后，按本行《信贷业务操作规程》进行调查。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十条', '签订抵押合同时，借款人、抵押人、共有人必须亲自到场签字，不得由他人代签。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十一条', '抵押担保须办理抵押登记，抵押合同自登记之日起生效。办理经营性物业抵押登记的部门为不动产登记部门。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十二条', '经办行经办人员必须在签订抵押合同后，与借款人、抵押人一同前往登记部门依法办理抵押登记，取得不动产他项权利证书。抵押物登记手续未办妥的不得发放贷款。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十三条', '贷款发放前，必须对经营性物业办理保险，需明确本行为第一受益人，保险期限不得短于本行贷款期限，保额不得小于借款本金和预计利息。物业保险可采用一次性购买或以年度为单位分期购买方式，采用以年度为单位分期购买的，借款人要与保险公司签订覆盖借款期限的续保协议，并保证保险期限的连续性。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十四条', '经营性物业贷款的借款合同中要补充明确以下内容：（一）本行有权在贷款期内要求由指定的房地产估价机构对抵押物价值重新进行评估，若抵押物价值发生贬值，本行有权要求借款人补充抵押物或收回相应贷款。（二）若房地产市场出现重大变化或发生其他影响贷款安全的情形，本行可与借款人协商变更借款合同中关于贷款额度、期限、利率、每期还款金额等条款，协商不成的，本行有权按照合同约定采取停止发放贷款、要求借款人提前偿还已发放贷款、处置抵押物等措施。（三）借款人未按约定归还贷款本息，本行可依法处置抵押物。（四）抵押权存续期间，未经本行书面同意，抵押人不得以变卖、再次抵押、抵偿债务、赠与或以其他任何方式处置抵押物业。本行书面同意后，借款人可进行抵押物业变卖，实现的价款应优先用于偿还本行贷款。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十五条', '借款人应与本行签订资金监管协议，明确借款人需在经办行开立资金监管专户，对专户资金实行收支两条线管理，建立收入、支出明细台账，物业的经营性收入直接进入专户，经办行对专户资金的实际使用情况进行监管；一旦借款人未按时偿还贷款本息，本行有权直接从该账户中扣划，如借款人出现逃避资金监管的行为，本行有权提前要求偿还贷款本息或处置抵押物。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十六条', '借款人办妥上述手续后，经办行按合同规定发放贷款，将资金划入借款人账户，并严格监督借款人按合同约定支付方式支付贷款资金。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十七条', '对于经营性物业贷款，需采用本物业产生的现金流归还贷款，若借款人或其母公司实力雄厚，现金流充裕，有确定的其他合法收入，并愿以其还款的，可以以其综合收入还款，但本物业产生的现金流占还款来源的比重不得低于60%。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十八条', '借款人按合同规定偿还全部贷款本息后，借款合同终止，客户经理应当将保管的抵押物权属证明交还借款人，并配合借款人向原登记机关办理注销登记。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第三十九条', '贷款发放后，应按照本行信贷管理相关制度办法进行贷后管理。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第四十条', '经办行在贷款期内应采用定期或不定期再评估方式对抵押物价值进行动态监测。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第四十一条', '对已抵押给本行的经营性物业用于租赁的，应审查租赁合同约定的条款是否存在不利于本行抵押权实现的情形，并要求出租人事先告知承租人该经营性物业已抵押给本行；同时要求承租人向本行出具书面承诺，承诺在本行实现抵押权时，租赁合同 将无条件提前终止，不得以其租赁权阻碍或干扰本行行使抵押权。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第四十二条', '经办行需及时掌握出租状况，准确估算项目经营性现金流量，动态掌握借款人经营情况。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第七章  贷款操作流程', '第四十三条', '经办行应制定贷后监管方案，指定专门客户经理加强贷后管理，经办行需定期对经营性物业贷款进行检查，发现物业出租情况恶化、借款人实际租金收入明显低于贷款申报时水平、租金收入不足以维持正常经营和归还分期还款额及其他风险预警信号时，经办行应及时按要求进行风险预警采取风险防范措施，并及时向本行管理部门报告。由于本贷款品种期限较长，期间遇有客户经理变动的，需做好交接工作，落实客户经理责任制。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第八章  附则', '第四十四条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《经营性物业贷款管理办法及操作规程》', '第八章  附则', '第四十五条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第一条', '为规范本行股东在本行贷款及以本行股权出质向担保人提供反担保的行为，根据《中华人民共和国物权法》、《中华人民共和国担保法》、《商业银行与内部人和股东关联交易管理办法》等法律的规定，制定本办法。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第二条', '本行股东向本行申请贷款，必须符合法律、法规及本行申请贷款的条件。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第三条', '本行股东向本行申请保证担保贷款，一般应以其持有的本行股权质押给保证人作为反担保，再由保证人为本行股东提供担保。保证人应分别与本行、本行贷款股东签订担保合同、股权质押（反担保）合同（见附件1）。出质反担保的股权包括但不限于借款人自身、借款人主要股东、借款人实际控制的企业、借款人家庭成员等合法拥有的本行股权。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第四条', '出质人、质权人签订股权质押（反担保）合同，必须在本行股权管理部门及工商行政管理机关办理股权质押登记，本行股权管理部门为董事会办公室和计划财务部。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第五条', '本办法实施之前，本行股东在本行已有保证担保贷款的，须按本办法完善手续，以其在本行的股权出质向保证人提供反担保。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第六条', '本行股权质押的质权人，可以是经本行许可的融资性担保公司，也可以是经本行许可符合担保条件的自然人、法人或其他企、事业单位。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第七条', '提供反担保出质的股权必须是依法可以转让和出质的股权。对于已经被依法冻结的股权，在解除冻结之前，不得提供反担保出质。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第八条', '本行股东以其持有的本行股权为本人或他人提供反担保，在办理反担保股权出质登记前，应当向本行董事会办公室提出申请，由其提交议案至本行董事会并征得同意，再由本行董事会办公室通知计划财务部进行内部登记，然后由出质人、质权人共同到工商行政管理机关办理股权质押登记，股权证可由本行代为免费保管（《代保管合同》见附件2）。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第九条', '办理反担保股权出质登记手续需提交材料包括：(一)出质人和质权人签名（自然人）或盖章（单位）的《股权出质设立登记申请书》（见附件3）。(二)出质人和质权人签名（自然人）或盖章（单位）的《股权出质指定代表或者共同委托代理人的证明》（见附件4）。(三)出质人、质权人的主体资格证明复印件。出质人、质权人为企业法人的，提交营业执照副本复印件；为事业法人的，提交事业法人登记证书复印件；为社会团体法人的，提交社团法人登记证复印件；为民办非企业单位的，提交民办非企业单位证书复印件（上述复印件，必须加盖证书持有人公章）；出质人、质权人为自然人的，提交居民身份证原件及复印件。(四)出质人持股证明，以出质人股金证复印件加盖本行公章。(五)出质人、质权人签订的《股权质押（反担保）合同》。（六）工商行政管理部门要求提交的其他材料。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第十条', '办理反担保股权出质手续和登记事项包括：（一）出质人和质权人的姓名或名称；（二）本行全称；（三）出质股权的数额。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第十一条', '出质人、质权人应当对反担保股权质押材料的真实性、质权合同的合法有效性、出质股权权属的完整性自行承担法律责任。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第十二条', '本行股东未能按期偿还贷款本息，致使担保人承担了担保责任，担保人有权以折价、拍卖、变卖等方式处理所质押的股权，所得价款优先受偿；或经双方协商，由担保人收购上述股权。本行其他股东，对上述股权处分时，在同等条件下，有优先受让的权利。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第十三条', '本行股东以其持有的本行股权出质提供反担保向他行贷款或者向他人借款时，参照本办法执行。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第十四条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《股东贷款及股权质押管理办法》', '', '第十五条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第一章  总则', '第一条', '为规范股权质押贷款行为，加大金融产品创新力度，支持地方经济发展，拓宽本行金融服务领域，根据《中华人民共和国公司法》、《中华人民共和国担保法》和《工商行政管理机关股权出质登记办法》及有关规定，制订本办法。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第一章  总则', '第二条', '本办法所指股权是指在江苏省内登记注册的非上市地方中小金融机构、非上市股份有限公司和有限责任公司的股权，不包括外商投资公司的股权。该股权须为出质人合法持有，并已经依法履行工商行政管理职能的机构依法登记。以国有股权出质的，应当符合国有资产监督管理的相关法律规定。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第一章  总则', '第三条', '本办法所称股权质押贷款（以下简称“贷款”）是指借款人以其自身或第三人合法持有的股权为质物，并按规定办妥质押登记手续后，由本行向借款人发放的用于解决借款人生产经营过程中正常资金需求的贷款。出质人为提供股权质押的法人、其他经济组织或自然人，质权人为接受股权质押的贷款人。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第一章  总则', '第四条', '贷款资金只能用于生产经营活动，不得用于证券投资、期货交易和股本权益性投资等国家明令禁止的项目。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第一章  总则', '第五条', '股权质押贷款活动应遵守法律、法规和国家信贷政策，遵循平等、自愿、公平、诚实信用原则。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '各支行、营业部、公司业务部、小微业务部（以下简称“经办行”）具体负责股权质押贷款业务的受理以及贷款“三查”的具体实施办理。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '授信评审部负责对股权质押贷款业务的授信管理。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '信贷管理部负责股权质押贷款管理办法的制定以及用信审批和贷后检查管理。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '计划财务部负责制定相应的股权质押业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '运营管理部根据权限负责对股权质押贷款用款进行审查。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对股权质押贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审查股权质押贷款管理办法和操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十三条', '资产保全部负责对股权质押贷款业务形成的不良贷款进行专业的清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第二章  组织管理体系', '第十四条', '审计部负责对股权质押贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十五条', '贷款对象经依法履行工商行政管理职能的机构核准登记注册并按规定办理年检备案手续的企（事）业法人、其他经济组织、个体工商户和自然人。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十六条', '申请质押的股权需具备以下基本条件：（一）出质人对所质押的股权合法持有，拥有完全的所有权或处分权，并经工商行政管理部门依法登记；（二）依法可以流通，即法律文件、企业章程等内部规章、对股权质押、转让未做出限制或禁止性规定；（三）未设立质押或原质押已解除； （四）非上市企业股权；（五）上年末每股净资产不低于1元；（六）股权所属公司近三年无违法、违规行为，且经营良好，连续三年盈利；（七）本行规定的其他条件。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十七条', '申请贷款的企（事）业法人、其他经济组织需具备下列条件：（一）企（事）业法人、其他经济组织须持有法人营业执照或有权部门批准设立的证明文件，经营管理制度健全，财务状况良好；（二）符合国家产业政策、经营业绩良好；（三）信誉良好，具备履行合同、偿还债务的能力，无不良信用记录；（四）有与贷款用途相符的合法证明材料；（五）在本行开立基本存款账户或一般账户；（六）本行规定的其他条件。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十八条', '申请贷款的自然人需具备下列条件：（一）具有完全民事行为能力，且借款人年龄加上贷款期限不超过65周岁身体健康，在本地有固定住所或经营场所，持有有效身份证件；（二）有与贷款用途相符的合法证明材料，如承包合同、购买合同、协议等；（三）有稳定的收入来源和按期偿还贷款本息的能力，第一还款来源充足；（四）品行良好，无违约行为和不良信用记录，愿意接受贷款人信贷、结算监督；（五）在本行开立结算账户；（六）本行规定的其他条件。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第十九条', '以下股权不得办理质押贷款：（一）被人民法院依法冻结的。（二）已办理出质登记再次出质的。（三）以有限责任公司未经过登记机关登记的。（四）以本行股权出质的。（五）公司章程禁止出质的。（六）所有权和处分权不明或有争议的。（七）法律、行政法规规定不得质押的其他情形。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第三章   贷款对象及条件', '第二十条', '以下类型不得办理贷款：（一）有不良社会及商业信用记录的。（二）不符合国家产业政策的。（三）贷款用于投入股市等国家明令禁止的项目。（四）没有正当贷款用途的。（五）不符合本行信贷管理规定的其他情形。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率', '第二十一条', '贷款额度根据股权发行企业的经营情况、每股净资产值及行业性质等因素，由贷款人与借款人、出质人协商确定，一般每股贷款额度不超过每股净资产的70%。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率', '第二十二条', '贷款期限根据借款人的贷款用途、生产经营周期、还款能力等因素综合确定，流动资金贷款期限最长不得超过一年（含），固定资产贷款期限最长不超过三年。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率', '第二十三条', '贷款利率根据本行贷款利率管理办法执行。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十四条', '股权质押贷款操作程序为借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、质押登记、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十五条', '借款人申请借款人应按本行信贷管理规定，在贷款申请时提交以下材料（包括但不限于）：（一）企（事）业法人1.借款人书面申请报告；2.营业执照、账户开户许可证，特殊行业的须提交合格有效的经营许可证；3.法定代表人身份证明及签字样本或印鉴；4.法定代表人授权委托书，委托代理人身份证明及签字样本或印鉴；5.公司章程；6.借款人财务报表；7.借款人董事会或股东会同意借款的决议，法人分支机构申请贷款须提供法人出具的有效授权书；8.与贷款用途相符的证明材料；9.本行要求提供的其他文件或资料。（二）自然人1.借款人书面申请报告；2.本人有效身份证件；3.借款人为已婚者需提供配偶有效身份证件、结婚证，借款人单身的,需提供相关证明和承诺；4.与贷款用途相符合的证明材料。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十六条', '出质人应提供以下资料（一）出质人为法人1.营业执照；2.公司章程；3.股权凭证；4.同意出质的证明材料，包括但不限于董事会或股东大会同意出质的决议。（二）出质人为自然人1.有效身份证件；2.出质人为已婚者需提供配偶有效身份证件、结婚证，出质人单身的,需提供相关证明和承诺；3.股权凭证；4.同意出质的证明材料，包括但不限于本人和共有人同意出质的书面承诺。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十七条', '股权所属公司应提供以下资料：（一）董事会或股东大会同意质押的决议；（二）公司章程；（三）近3年年末财务报告；（四）营业执照复印件；（五）股权质押事项记载于公司股东名册的复印件（须加盖公司公章）。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十八条', '贷款调查经办行受理客户申请后，客户经理应按尽职调查要求进行调查，提出调查意见送交经办行审查会办，并重点调查以下内容：（一）出质人资信、是否存在虚假注册、抽逃股本等情况；（二）拟出质股权份额，股权凭证是否有效，拟出质股权所在公司经营情况；（三）实现质权的可行性；（四）其他需要审查的内容。必要时向依法履行工商行政管理职能的机构调阅相关登记档案。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十九条', '授信、用信审查审批贷款的授信、用信审查审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十条', '签订合同经审批同意发放贷款的，经办行与借款人、出质人分别签订借款合同、质押合同。并要求出质人在质押合同中与本行约定：（一）若质押股权价值下降导致质押率不足时，本行应立即通知借款人和出质人，要求其追加质物、保证金或其他风险规避措施，及时补足因股权价值下降造成的质押价值缺口，否则，在本行向借款人发出通知的第七个工作日，本行可终止借款合同，处置质押物，所得款项用于还本付息及其他相关费用，余款清退给借款人，不足部分向借款人继续追偿；（二）质物在质押期间所产生的孳息（包括送股、分红、派息等）随质物一起质押。质物在质押期间发生配股时，出质人须购买并随质物一起质押；（三）明确出质人下列事项的及时通知义务1.减资、合并、分立、解散及申请破产；2.股权变更；3.其他重大事项。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十一条', '质押登记股权质押合同签订后，应在约定的时限内及时到依法履行工商行政管理职能的机构办理股权质押登记。申请办理股权出质登记，应提交下列材料：（一）出质人和质权人共同签署的《股权出质设立登记申请书》；（二）质押合同；（三）记载有出质人姓名（名称）及其出资额的有限责任公司股东名册复印件或出质人持有的股份有限公司股权证复印件（均需加盖公司公章）；（四）出质人、质权人的主体资格证明或者自然人身份证明复印件（出质人、质权人属于自然人的由本人签名,属于法人的加盖法人印章）；（五）国家工商行政管理总局要求提交的其他材料。指定代表或者共同委托代理人办理的，还应提交申请人指定代表或者共同委托代理人的证明。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十二条', '贷款发放与支付经办行应在办妥股权出质登记，取得依法履行工商行政管理职能的机构出具的相关登记权证后，方可发放贷款，并按规定支付贷款。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十三条', '本行客户经理应全程参与质押权的登记，质押权设立后，经办行应当收妥质物登记凭证。质押物的所有权凭证及其他相关资料正本应当经本行、出质人双方共同确认，在交接清单上签字盖章后交由本行统一入库保管，并列入表外科目核算。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十四条', '经办行应当按照档案管理、信贷管理系统的规定，加强对质押贷款的档案管理及信贷管理系统录入内容的管理，确保档案和信贷管理系统录入内容的完整性、有效性和连续性。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十五条', '经办行应当加强贷后检查和跟踪管理，关注下列异常行为现象，督促出质人按照合同约定履行各项义务：（一）出质人涉及重大经济纠纷。（二）出质人经营机构或组织结构发生变化，包括但不限于承包、租赁、合并、分立、股份制改造、联营与外商合资或合作等。（三）出质人经营范围、注册资本变更、股权变动。（四）出质人破产、歇业、解散、停业整顿、被吊销营业执照、被撤销。（五）出质人章程、法定代表人住所、电话等发生变更。（六）股权被依法查封、扣押。（七）质押权受到或可能受到来自任何第三方的侵害。（八）经营发生劣变，公司净资产大幅下降。（九）其他需要关注的异常行为现象。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十六条', '股权出质期间，出质人未经本行书面同意不得以任何形式处置被出质的股权。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十七条', '经办行在发放贷款后，应关注出质股权所在公司经营状况，准确把握影响出质股权的市场价值因素，评估出质股权的价值，采取有效措施防范和控制信贷风险。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十八条', '出质股权数额变更，以及出质人、本行名称或者出质股权所在公司名称更改的，出质人和本行应当及时向股权登记部门申请办理股权出质变更登记。出现主债权消灭、质权实现、本行放弃质权或法律规定的其他情形导致质权消灭的，出质人和本行应当及时向股权登记部门申请办理股权出质注销登记。股权出质合同被依法确认无效或者被撤销的，出质人或本行应当及时向股权登记部门申请办理股权出质撤销登记。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第五章  贷款操作程序', '第三十九条', '质押物的处置（一）质押物的处置方式：依照法律程序处理。（二）质押期间，发生下列情形之一的，应及时处置质押物：1.质押股权价值下降导致质押率不符合本行要求，借款人未按要求追加质物、保证金或其他风险规避措施，以及时补足因股权价值下降造成的质押价值缺口的。2.出质人被依法宣告破产、关闭、解散及发生其他危及本行债权的。3.贷款未能按期归还，应与借款人及出质人协商，以折价、拍卖或变卖的方式依法转让实现质权，扣除拍卖或变卖的费用和扣缴应缴税金后，本行有优先受偿权，剩余款项交还出质人，不足部分向借款人继续追偿；如出质人拒绝或协商不成，则本行有权向法院提起诉讼，要求实现质权。4.质物的股权价值有明显减少的可能，足以危害贷款人权利的，应要求借款人提供相应的担保，借款人不提供的，本行有权拍卖、变卖质押股权，并与出质人通过协议将拍卖、变卖所得的价款提前清偿债务或者提存。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第六章  附则', '第四十条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第六章  附则', '第四十一条', '本办法由信贷管理部制定、解释和修改。'),
(NULL, '信贷管理部', '《股权质押贷款管理办法及操作规程》', '第六章  附则', '第四十二条', '本办法自发文之日起执行。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第一章  总则', '第一条', '为进一步加强对船舶贷款的管理，规范信贷行为，强化贷款责任，防范和化解船舶贷款风险，切实提高船舶贷款质量和贷款整体效益。根据《中华人民共和国物权法》、《中华人民共和国海商法》、《固定资产贷款暂行办法》、《项目融资业务指引》以及相关信贷管理制度办法等规定，特制定本办法。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第一章  总则', '第二条', '遵循安全性、流动性、效益性原则，坚持自有资金为主、贷款支持为辅和平等、自愿、公平、诚实信用的原则。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第一章  总则', '第三条', '船舶贷款是指商业银行（以下简称“本行”）向自然人或法人发放因船舶更新、正在建造船舶或新购船舶的贷款。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第一章  总则', '第四条', '本办法适用于本行辖内各支行、营业部、公司业务部、小微业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第五条', '各经办行负责船舶贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第六条', '授信评审部负责船舶贷款业务的授信管理。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第七条', '三农业务部负责船舶贷款业务的宣传策划、营销推动及日常督查管理。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '信贷管理部负责拟定船舶贷款管理办法及流程，对船舶贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '运营管理部根据权限负责对船舶贷款用款进行审查。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '资产保全部负责对不良贷款专业清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对船舶贷款风险的监督、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核船舶贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '审计部负责对船舶贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第三章  贷款的对象和条件', '第十四条', '借款人应为各经办行辖区内法人单位或具有完全民事行为能力，且借款人年龄加上借款期限不超过65周岁的自然人。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第三章  贷款的对象和条件', '第十五条', '借款人申请船舶贷款应当符合以下条件：（一）信用记录良好；（二）有60%以上的自有资金和按期还本付息能力；（三）存在挂靠关系的，船舶所挂靠航运公司在市范围内且在经办行开立基本账户或一般存款账户；（四）存在挂靠关系的，借款人与挂靠公司协议期限不低于贷款期限；（五）借款人的经营活动必须遵守国家政策、法律、法规；（六）借款人或其合伙人应有一定的船舶运输经验；（七）借款申请人应出具办理保险承诺书，且明确本行必须为第一受益人；（八）本行规定的其他条件。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第三章  贷款的对象和条件', '第十六条', '办理船舶贷款应关注船舶的股东构成，不得向同一船舶的多个股东发放贷款。对船主或大股东发放贷款后，严禁向其小股东发放贷款。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第四章  贷款额度、期限、还款方式、利率', '第十七条', '符合条件的贷款，根据船舶的成新率，合理确定抵押率，款最高限额为船舶评估价格的40%。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第四章  贷款额度、期限、还款方式、利率', '第十八条', '贷款实行“一船一贷，期限内收回”。贷款期限一般不超过两年，实行按季结息、到期利随本清或等额本息分期还款的方式。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第四章  贷款额度、期限、还款方式、利率', '第十九条', '贷款利率。根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十条', '船舶贷款分为授用信申请、受理与调查、授信审查审批、用信审查审批、合同签订、发放与支付、贷后管理、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '借款人申请船舶贷款时除具备本行相关信贷管理制度规定的基本条件外，还应提供以下资料:（一）申请人为个人的，应提供借款人及其配偶的有效身份证明原件及复印件、婚姻证明，单身借款人应提供单身承诺，并由所在街道、居委会（村委会）或所在单位出具证明。借款人偿债能力证明（包括借款人家庭经济收入证明、纳税证明、银行对账单、租赁合同、银行储蓄存单及有价证券等资料的原件及复印件）。申请人为法人的，应提供包括但不限于须持有履行工商行政管理职能的机构核准登记的证件、公司章程、验资报告、财务报表等，特殊行业还须持有有权机构颁发的特殊行业许可证等许可证件；（二）已婚借款人的配偶、单身借款人家庭主要成员应对借款人借款行为进行确认，并作为共同借款人或出具承担连带还款责任的书面承诺；（三）申请贷款金额、用途、期限；（四）申请人与造船厂的购船协议；（五）存在挂靠关系的，申请人与挂靠运输公司的协议；（六）运输等相关许可证；（七）存在挂靠关系的，挂靠单位营业执照副本及复印件、法定代表人身份证、组织机构代码证、章程、同意保证或抵押决议；（八）贷款担保材料原件及复印件；（九）各经办行要求提供的其他相关材料原件及复印件。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '各经办行接到借款人申请资料后，按本行《信贷业务操作规程》进行调查。调查的重点：（一）核查申请真实性，对申请人提交的借款申请、访谈内容和实际状况是否一致、真实，资料是否完整。核实其资产负债状况、自有资金、资金回笼、经营效益、对外担保、经济纠纷及造船资金来源、分析预测船舶投入营运后效益等情况。与他人合伙，应了解其股东构成、各股东的投资比例、借款申请人的投资额及营运后的利润分配方案等。严格审查抵押船舶的真实权属，不得以船舶所有权登记证书作为判断船舶所有人的唯一标准，须要求抵押人提供抵押船舶所有权原始取得证明（如船舶建造合同或船舶买卖合同、在建船舶初始登记证书、相关船舶建造款或购船款支付凭证等）。（二）核查经营合法性，经营船舶装运的货物品种是否超出国家规定范围，是否按规定航线营运。（三）核查需求合理性，60%以上的自有资金是否到位，应根据船舶购置价、种类、陈新度，参照目前市场价值等因素确定评估价值，然后以评估价值乘以抵押率确定贷款可贷额度。（四）核查担保有效性，了解借款申请人所提供保证人的资金实力及品行，调查核实抵押船舶（含在建船舶）的市场价值及变现能力。（五）核查贷款风险性，在办理船舶抵押时，要认真核查船舶优先权、船舶留置权等优于抵押权受偿因素。船舶优先权是指海事请求人依照《海商法》第21条的规定，向船舶所有人、光船承租人、船舶经营人提出海事请求，对产生该海事请求的船舶具有优先受偿的权利；船舶留置权是特指船舶建造人、修船人在合同另一方未履行合同时，可以留置所占有的船舶，以保证造船费用或者修船费用得以偿还的权利。船舶优先权先于船舶留置权受偿，船舶抵押权后于船舶留置权受偿。收集、核实船舶建造合同和建造款支付凭证，判断有无船舶留置权存在，存在留置权的需由造船（修船）方出具书面承诺，放弃优先受偿权。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '贷款的授、用信审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '发放船舶贷款担保方式应采取抵押加保证的方式。抵押人应为船舶的所有权人，且该船舶的全体股东均应有同意抵押的书面承诺。对涉及船舶挂靠的，须要求船舶实际所有人和船舶登记所有人作为共同抵押人参加船舶抵押合同的签订，同时追加挂靠公司担保。以在建船舶抵押的，也必须办理抵押登记。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '被抵押船舶的评估价值可以由本行和抵押人协议约定，或由本行委托评估机构评估确定。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '授、用信审批通过后，船舶抵押的贷款由客户经理通知借款人、抵押人到场签订相关合同，客户经理在备齐相关资料后与借款人、抵押人一同至登记部门办理抵押登记，取得船舶抵押登记证，同时到保险公司办理保险手续，财产保险金额不得少于本金和预计本息，且明确商业银行股份有限公司作为第一受益人。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '办妥上述手续后，本行按信贷管理规定发放贷款，将资金划入借款人结算账户，并严格监督借款人按合同约定支付方式支付贷款资金。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '贷后检查。客户经理应在贷款发放后15日内，对贷款使用情况进行首次跟踪检查，并形成书面贷后检查记录。对转移贷款用途的，应查明原因并提出处置意见。考虑到船舶贷款借款人的流动性较大，且大部分时间在水上作业，客观上给贷后管理带来一定的难度，贷款首次检查后，客户经理平时应通过电话联系的方式，向其他船主了解等方式更多地关注船舶运输户的经营情况，每半年对贷款使用情况及风险状况至少检查一次，形成贷后检查报告并存档。对发现不利于归还本行贷款等重大事项时应及时采取补救措施并向相关部门书面回报。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '各经办行按规定对船舶贷款进行催收和管理，抵押贷款本息结清后客户经理应协助客户到登记部门办理抵押登记注销手续；对发生不良的，按本行信贷管理制度对相关责任人进行责任追究。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '档案管理。各经办行应按本行信贷档案管理有关规定收集、保管相关资料。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第六章  附则', '第三十一条', '业务经办人员必须严格遵守本行《贷款业务尽职管理办法》。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第六章  附则', '第三十二条', '本办法未尽事宜参照本行相关信贷制度及规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第六章  附则', '第三十三条', '本办法由信贷管理部负责制定、修改和解释。'),
(NULL, '信贷管理部', '《船舶贷款管理办法及流程》', '第六章  附则', '第三十四条', '本办法自颁布之日起执行。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第一章  总则', '第一条', '为规范本行设备按揭贷款业务管理，防范设备按揭贷款风险，促进设备按揭贷款业务健康发展，根据《中华人民共和国担保法》、《中华人民共和国物权法》、《固定资产贷款管理暂行办法》等有关法律法规，以及本行《信贷管理制度》、《贷款业务尽职管理办法》等相关的信贷管理规定，特制定本办法。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第一章  总则', '第二条', '本办法所称设备按揭贷款，是指本行向借款人发放的用于在专业生产企业处购买其生产经营所需的设备等资产，并以其所购的资产作为抵押同时由专业生产企业承担连带担保责任及提供无条件债权回购的按揭贷款。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第一章  总则', '第三条', '本办法所称设备，是指借款人在生产经营中广泛使用且经本行认可的各类成型、成套设备。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第一章  总则', '第四条', '本办法所称专业生产企业，是指本辖区专业生产设备且经本行认可并签订合作协议的生产企业。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第一章  总则', '第五条', '本办法所称债权回购，是指本行与专业生产企业签订的《商业银行设备按揭贷款合作协议》（以下简称“协议”）中约定，按揭贷款发放后，借款人连续三期或累计六期未能按期足额还款，本行有权提前终止合同，并由专业生产企业无条件对借款人未归还本行贷款本息及相关费用承担付款责任，本行在专业生产企业足额偿付后将债权转让给专业生产企业。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第一章  总则', '第六条', '设备按揭贷款实行足额首付、有效担保、专款专用、按期偿还的原则。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第一章  总则', '第七条', '本办法设备按揭贷款仅限于公司业务部办理。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第二章  组织管理体系', '第八条', '公司业务部负责设备按揭贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第二章  组织管理体系', '第九条', '授信评审部负责对设备按揭贷款业务的授信管理。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十条', '信贷管理部负责拟定设备按揭贷款管理办法及流程，对设备按揭贷款业务的用信管理和贷后检查的监督。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十一条', '风险管理部负责对设备按揭贷款风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十二条', '合规管理部负责审核设备按揭贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十三条', '营业部负责保证金的核算和管理。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第二章  组织管理体系', '第十四条', '审计部负责对设备按揭贷款进行审计稽核和监督。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第三章  贷款条件', '第十五条', '专业生产企业须具备以下条件：（一）依法注册登记且法定地址在本市境内的企业法人和其他经济组织；（二）在本行开立人民币基本存款账户或一般存款账户，并一次性存入不低于1000万元的担保金，担保金与担保贷款额度的放大倍数为1：3；（三）无不良信用记录；（四）本行认为必须满足的其他条件。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第三章  贷款条件', '第十六条', '借款人申请设备按揭贷款，应当同时符合以下条件：（一）借款人必须是经有权部门批准成立，依法持有法人营业执照并已办年检手续，实行独立核算，具有法人资格的企（事）业法人或具有完全民事行为能力的自然人（不限于本行辖区客户）；（二）无不良信用记录；（三）已在本行开立存款账户；（四）具备分期偿还贷款本息的能力；（五）已与本行认可的专业生产企业签订设备购买合同；（六）本行认为必须满足的其他条件。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第四章  贷款金额、期限和利率、还款方式', '第十七条', '设备按揭贷款的借款人首付金额原则不低于所购设备价格的30%。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第四章  贷款金额、期限和利率、还款方式', '第十八条', '贷款期限。企业法人、个人设备按揭贷款期限最长不超过三年。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第四章  贷款金额、期限和利率、还款方式', '第十九条', '贷款利率。执行基准利率上浮不低于30%（含）。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第四章  贷款金额、期限和利率、还款方式', '第二十条', '还款方式。贷款期限在1年（含）以内的，实行按月或按季结息，贷款本金结清时利随本清。贷款期限在1年以上的，实行按月等额本息还款法，并自使用贷款后第二个月开始按月偿付贷款本息。贷款发放后，允许借款人提前还款。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十一条', '设备按揭贷款操作程序分为借款人申请、贷款受理调查、落实担保、贷款授信审查审批、签订合同、贷款发放、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十二条', '借款人申请办理设备按揭贷款时应提供如下资料：（一）借款人为个人客户1.借款人及其配偶的有效身份证明（居民身份证、户口簿、军官证、港澳同胞回乡证或其他有效居留证件）原件及复印件、婚姻证明，借款人如未婚应提供未婚证明并签署单身承诺函；2.借款人偿债能力证明（包括借款人家庭经济收入证明、纳税证明、银行对账单、租赁合同、银行储蓄存单及有价证券等）原件及复印件；3.与本行指定的专业生产企业签订的设备购买协议或合同；4.专业生产企业出具的设备购买首付款凭证及复印件；5.贷款担保材料原件及复印件；6.本行要求提供的其他相关材料原件及复印件。（二）借款人为公司客户1.申请贷款的书面报告，内容包括借款人基本概况、资产负债、生产经营状况、企业法人代表情况、申请借贷原因、用途、偿债能力、贷款期限等情况；2.客户资信资料，有权部门颁发的资信等级证书复印件；3.借款人基础资料，包括但不限于营业执照副本及年检登记文件、组织机构代码证、企业法定代表人身份证明、税务登记证及按时纳税证明、公司章程、验资报告、申报时点的资产负债、损益、现金流量等报表；5.董事会或相应决策机构同意将其所购经营性设备作为贷款抵押物的书面材料原件；6.本行要求提供的其他相关材料原件及复印件。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十三条', '设备按揭贷款，借款人应提供以下担保：（一）以其购买的设备等资产设定抵押担保；（二）专业生产企业承担连带担保责任及承担无条件债权回购责任。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十四条', '专业生产企业须在本行营业部开立保证金专户，存入保证金，用于支付借款人到期应还本息和履行担保责任所需费用。若担保和回购项下贷款的借款人或生产企业发生违约事项，本行有权扣划保证金，用于支付借款人到期应还款本息及相关合理费用。保证金扣划后，保证金专户余额低于规定的保证金额度时，专业生产企业应在15个工作日内补足。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十五条', '贷前调查重点关注以下内容：（一）借款人资格、信誉及还款意愿；（二）借款人生产经营情况，每期现金流是否能覆盖当期还款额；（三）所购设备投入使用项目的收益情况；（四）专业生产企业经营情况、与本行合作情况等。（五）专业生产企业设备成熟度、售后服务能力等情况。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十六条', '贷款审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十七条', '设备按揭贷款审批通过后，借款人应及时将按揭贷款首付款存入本行，并落实相关贷款条件后与借款人及专业生产企业签订相关贷款合同，合同中必须明确借款人与专业生产企业之间的任何商务纠纷与本行无关，双方任何商务纠纷不得成为借款人和专业生产企业不履行和本行签订的有关协议或合同的抗辩理由。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十八条', '本行在收到所购的设备发票等凭证后，与借款人共同到相关部门办理抵押登记或公证手续。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第二十九条', '按揭贷款所购设备必须办理相关保险，保险单中必须明确第一受益人为本行，保险期限不得短于贷款期限。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十条', '借款人办理上述手续后，本行将贷款款项转入专业生产企业账户。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十一条', '专业生产企业收到《设备按揭贷款通知书》和首付款后，借款人即可在经销商处提取设备。专业生产企业应协助借款人到相关部门办理缴费等相关手续，并在规定的时限内将设备的发票、合格证等各种凭证原件直接缴于本行。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十二条', '借款人按合同规定偿还全部贷款本息后，贷款合同终止，本行释放设定贷款资产上的抵押权，客户经理应将保管的抵押物权属证明交还借款人，并与借款人一起到抵押登记部门办理抵押登记注销手续。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第五章  贷款操作程序', '第三十三条', '贷款发放后，应按照本行信贷管理相关制度办法进行贷后管理，包括但不限于对专业生产企业经营情况及回购担保能力进行贷后评价，对专业生产企业经营情况及担保基金配比情况进行贷后评价，对借款人的按期还本付息情况进行贷后评价，除贷后15日进行首次检查外，在还本付息正常的情况下每半年检查一次；对当月不能还本付息的公司、自然人，必须从次月开始进行跟踪检查。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第六章   附则', '第三十四条', '本办法及流程由信贷管理部负责制定、解释。'),
(NULL, '信贷管理部', '《设备按揭贷款管理办法及流程》', '第六章   附则', '第三十五条', '本办法及流程自印发之日起施行。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第一章  总则', '第一条', '为强化本行信贷管理，规范信贷行为，防范贷款业务风险发生，实现贷款业务风险的有效控制，根据《中华人民共和国商业银行法》、《贷款通则》、《商业银行授信工作尽职指引》、《商业银行小企业授信工作尽职指引（试行）》、等国家法律法规和本行《信贷管理制度》之规定,特制定本办法。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第一章  总则', '第二条', '本办法所称的贷款业务包括授信业务、用信业务、合同签订、发放支付以及贷后管理。（一）授信业务是指支行、公司业务部、小微业务部（以下统称“支行”）客户经理（包括信贷内勤）在受理授信申请人的授信需求后，通过以实地调查为主、间接调查为辅的方式，结合申请人的提交资料提出授信调查报告，按转授权办法审查、审批后，由有权人确定可向客户直接提供信贷资金的可用额度。（二）用信业务是指支行客户经理在受理客户的用信申请后，客户经理调查核实用信条件是否具备，按转授权办法完成审查、审批后，由有权人核准贷款发放实际额度的业务。（三）合同签订是指支行信贷内勤在签约台与借款人、担保人等人员真实、规范签订合同、承诺书等。（四）发放支付包括放款、用款的审查、审批。（五）贷后检查是指贷款发放后，对借款人资信、履行借款合同、资金使用、经营情况、还款能力等情况进行的检查以及档案管理。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第一章  总则', '第三条', '对出现问题的贷款业务将依据尽职管理办法对各级相关责任人进行独立验证、评价和报告；对未尽职或违反规章制度的责任人进行责任追究；对出现问题，责任人已严格按照相关管理制度和业务流程规定进行操作的，予以部分或全部免责。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第一条', '授信申请'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第四条', '客户申请。客户必须以书面形式（格式文本）向支行提出贷款、贴现、银行承兑、保函等表内外资产业务（以下简称“贷款业务”）授信贷款申请，其内容主要包括申请理由、授信金额、授信期限、授信用途、担保方式、担保人、有关承诺等。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第五条', '法人客户（含企业、事业法人、其他经济组织等，以下简称“法人客户”）申请贷款业务授信用于经营的，须按具体用途提供以下资料或其中对应资料的原件和已核原件的复印件（复印件需加盖公章,下同）：（一）营业执照正副本；（二）组织机构代码证和税务登记证正副本；（三）贷款卡及资信等级证书；（四）开户许可证；（五）公司章程；（六）法定代表人、主要股东身份证明；（七）经办人身份证及法定代表人的授权委托书；（八）公司按章程规定由董事会或股东会出具同意借款的决议书；（九）前两年度和近期的财务报表、当期纳税记录；（十）验资报告、企业资质证明、公司成立批文、生产经营许可证、进出口批文、进出口许可证、生产经营计划、购销合同（加工、承建、承包、委托等合同、协议）等；（十一）特殊行业生产经营许可证；（十二）抵押物作价的证明资料（含评估报告、购置发票等）；（十三）新客户还需提供印鉴卡；（十四）如属于房地产企业的，须提供对应项目的《建设用地许可证》、《国有土地使用权证》、《建设工程规划许可证》、《建设工程施工许可证》等“四证”原件及复印件，商品房已经预（销）售的，还应提供《商品房预售许可证》或《商品房销售许可证》；如属特种行业，须有特种行业经营许可证；（十五）申请中、长期项目贷款还应提供以下资料：1.有权部门批准的项目建议书、项目可行性研究报告、环境影响报告书；2.实施项目必须有相关部门的有效批文；3.项目开工前期准备工作完成情况；4.在开户行存入规定比例资本金的证明；5.项目投资来源及资本金到位情况；6.根据信贷业务品种、信用方式需提供的其他资料。（十六）集团客户须按照本行集团客户统一授信要求提供相关资料；（十七）其他资料（如消防合格证、安全合格证、环境评价报告等）。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第六条', '自然人客户贷款按用途区分经营性贷款和消费性贷款。（一）申请贷款授信用于生产经营的，自然人客户应提交下列资料或其中对应资料原件和已核原件的复印件：1．本人及配偶的身份证件、户口簿、结婚证等;2．借款人配偶承诺函；3．借款人(家庭)的房产证、土地证；4．经营项目和贷款用途的证明资料，包括承包、租赁、购销等合同、协议；5．其他资料。自然人客户申请贷款授信用于其企业生产经营的，还需提供所经营企业的资料，资料的内容为第五条所列项目。（二）申请贷款授信用于消费的，需按具体用途提供以下资料或其中对应资料原件和已核原件的复印件：1．本人及配偶的身份证、户口簿、结婚证等；2．借款人配偶承诺函；3．借款人（家庭）房产证、土地证；4．还款能力的证明资料（包括但不限于工作单位收入证明等）；5．贷款用途的证明资料，包括购房、承建、装修等各类合同、协议；6．首付款凭证、发票等；7．自筹资金数额及来源证明资料；8．其他资料。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第七条', '担保人分为自然人客户和法人客户，提供相应的担保资料。（一）担保人为自然人的，需提供下列资料和已核原件的复印件：1．身份证；2．还款能力的证明资料（包括但不限于工作单位收入证明等）；3．拟抵押、质押的资料；4．其他资料。（二）担保人是法人客户的，需提供如下资料原件和已核原件的复印件：1．营业执照（事业单位登记证）正副本；2．组织机构代码证和税务登记证正副本；3．贷款卡及资信等级证书；4．开户许可证；5．公司章程；6．法定代表人、主要股东身份证明；7．经办人身份证及法定代表人的授权委托书；8．公司按章程规定由董事会或股东会出具同意担保的决议书；9．拟抵押、质押的资料；10．前两年度和近期的财务报表；11．当期纳税记录；12．其他资料。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第二条', '授信调查'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第八条', '授信受理各支行均可受理全市范围内客户的贷款授信申请，受理申请应严格执行借款人“面谈”制度。支行负责人可根据内部分工及时安排客户经理对客户基本情况及项目可行性进行初步调查，认定客户是否具备发放贷款的基本条件,资料是否完整、齐全。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第九条', '授信调查授信调查实行客户经理“双人AB角调查”制度，调查人对客户申请事项进行现场核实后出具书面调查报告。调查的主要内容包括但不限于以下几个方面：（一）对客户提供的资料是否真实、完整、有效进行调查核实。1．真实性。检查客户提供的原件资料真实性，并将复印件与原件逐一比对，客户签章须在本行规定人员的目视下进行。对原件存在涂改、丢失或其他疑点应标明，要到发证机关核对或由发证机关出具有效证明。2．完整性。客户提供的资料是否完整，要素填列是否完整。3．有效性。客户提供的证件资料（包括身份证件、营业执照、组织机构代码证、贷款卡等）是否有效，有无办理年检；公司出具的文书（相关借款、担保决议、授权委托书等）是否符合章程规定和公司法规定，表达有无歧义，上下是否衔接。（二）调查客户信用以及法人治理状况。1．查询人民银行信贷征信等系统，了解客户目前贷款、其他负债和提供担保情况，查验贷款卡反映的贷款金额与财务报表反映的是否一致，是否有不良信用记录，是否被列入“黑名单”，对外提供的担保是否超出客户的承受能力等，是否涉及诉讼等。2．对法人客户应调查分析投资、管理情况，包括股本结构（注册资本是否真实到位，是否存在抽逃注册资本）、组织架构、内部控制等，还应调查了解客户法定代表人、董事长、总经理及财务部、销售部等主要部门负责人的品行、经营管理能力和业绩，是否有个人不良记录等。（三）调查客户的财务（收支）情况、产销情况以及信贷需求，分析还款方案的可靠性。法人客户应重点调查以下项目：1．调查分析客户及担保人经营范围、主营项目、生产经营规模、经营方针策略等；2．深入客户及担保人单位，查阅其资产负债表、损益表、现金流量表等账表，对客户的资产、负债、所有者权益、收入、成本、利润等情况进行分析，突出应收账款、存货等重点科目的分析，并进行“账账、账表、账实”等核对；3．调查客户及其担保人生产经营是否合法、正常，是否超出规定的经营范围，核实企业“三表”情况，重点调查分析主要产品技术含量、产销情况、市场竞争力、市场趋势等情况；4．查验商品交易的真实性，分析商品交易的必要性，在此基础上调查分析贷款需求的原因以及合理的贷款需求量；5．调查分析贷款投向是否符合国家的金融政策、产业政策以及贷款用途的合法性；6．调查关联企业的情况，贷款有无可能转移用途，是否对贷款的还款构成影响；7．调查分析还款时间的合理性和还款来源的可靠性；8．固定资产以及项目贷款按本行相关规定必须开展的其他方面的调查；9．其他方面。自然人客户应重点调查以下项目：1．授信申请人基本情况，包括职业或经营项目以及家庭情况；2．授信申请人收入情况，包括个人及家庭总收入及来源，测算家庭纯收入及人均收入等情况；3．个人及家庭的资产负债情况，有无不合理大额负债；4．申请授信用途的合法性、真实性、效益性；5．个人及家庭主要成员的信用情况；6．家庭成员有无重大疾病、有无赌博等不良嗜好；7．还款来源是否真实，还款能力是否充足，还款方式是否合理；8．其他方面。（四）本行应关注和搜集集团客户及关联客户的有关信息，有效识别授信集中风险及关联客户授信风险。特别是通过注册一连串公司并以更具隐蔽性的关联企业交叉担保（即不限于两个公司互为对方的贷款提供担保，而是多家公司连环交叉的间接担保）更要加强真实性调查和核对工作。（五）对客户提供的担保资料进行分析，确定客户提供的担保是否符合条件，并核实其担保能力。提供保证的，要对担保人的现金流或经济收入进行调查；提供抵（质）押担保的，要对抵（质）押物价值的稳定性、变现能力进行调查。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十条', '调查分析结束后，客户经理应出具规范的调查报告，明确调查意见并签署责任人，连同上述贷款资料移交支行风险员审查。调查报告的主要内容包括但不限于：（一）法人客户授信调查报告的内容1．客户基本情况（包括借款主体资格认定、股东构成、管理情况、关联信息等）；2．经营项目及产品分析，包括行业态势分析、产品供求关系、法人客户主要产品的技术含量以及法人客户的市场竞争力；3．财务状况，包括资产负债、经营效益以及现金流量分析，关注资金回笼情况，重点分析客户的稳定性、成长性；4．资金筹措以及贷款需求分析，非贷款资金到位情况以及资金缺口是否真实、合理；5．贷款授信的具体用途及合法、合规性分析；6．非财务分析，包括客户及主要股东的信用情况（突出本行历史以及存量贷款的履约情况），有无涉诉或其他重大情况；7．担保情况；8．还款来源的落实情况；9.信贷业务的综合效益分析；10．贷款风险评价；11．结论。调查人对是否同意办理本次贷款授信以及对授信金额、用途、期限、利率、还款方式、担保方式和限制性条款等提出明确意见。（二）自然人客户授信调查报告的内容1．授信申请人的基本情况，包括职业或经营项目以及家庭情况；2．个人及家庭主要成员的信用情况（突出本行历史以及存量贷款的履约情况分析），有无涉诉或其他重大情况；3．授信申请人及家庭财务情况，包括收支情况以及资产负债情况，有无不合理大额负债；4．资金筹措情况分析，非贷款资金到位情况以及资金缺口是否真实、合理；5．贷款授信的具体用途及合法、合规性分析；6．担保情况；7．还款来源的落实情况；8．贷款风险评价；9．结论。调查人对是否同意办理本次贷款授信以及对授信金额、用途、期限、利率、还款方式、担保方式和限制性条款等提出明确意见。自然人申请授信如为企业使用的，应详细分析企业的经营状况、财务状况等。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第三条', '授信审查'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十一条', '贷款授信的审查实行分级审查。支行风险员负责对客户的基础资料和客户经理的调查资料（以下称“贷款资料”）进行审查，经贷款会办小组会办形成授信意见后报授信评审部或授信评审中心进行复审。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十二条', '贷款业务授信审查的要点和主要内容包括：（一）基本要素审查1．客户及担保人有关资料是否齐备；2．贷款授信的流程资料及手续是否齐全。（二）主体资格审查1．客户及担保人主体资格、法定代表人有关证明材料是否符合规2．客户及担保人产权关系是否明晰；3．客户及担保人的法定代表人、主要股东有无不良记录。（三）信贷政策审查1．贷款用途是否符合国家法律、政策及本行规定；2．贷款期限、方式、利率等是否符合监管和本行有关规定。（四）贷款风险审查1．是否分析、揭示客户的财务风险、经营管理风险、市场风险等风险要素；2．是否提出适当的风险防范措施。对客户申请的授信业务，应根据不同客户、不同授信品种的风险特征，有针对性地制定授信分析评价审查要求，重点关注可能影响授信安全的因素：流动性短期资金需求应关注：（1）融资需求的时间性（常年性还是季节性）；（2）对存货融资，要充分考虑当实际销售已经小于或将小于所预期的销售量时的风险和对策，以及存货本身的风险，如过时或变质；（3）应收账款的质量与坏账准备情况；（4）存货的周期。设备采购和更新融资需求应关注：（1）时机选择，宏观经济情况和行业展望；（2）未实现的生产能力；（3）其他提供资金的途径：长期授信、资本注入、出售资产；（4）其他因素可能对资金的影响。（五）本行规定的其他审查要点。（六）提出审查结论和有关限制性条款。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十三条', '审查人员对调查人员或支行移交的贷款业务授信资料不全、调查内容不完整、不清晰的授信业务，应要求调查人员或支行补充完善。审查同意的，审查人员应在审批表中签署审查意见，连同信贷业务授信审查材料和调查报告，经贷款小组会办后移交有权部门审批。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第四条', '授信审批'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十四条', '授信评审部负责对支行上报的授信意见进行初步复查、确定授信金额，授信评审部权限内的，由授信评审部直接审批；信贷审查委员会权限内的，由授信评审部提交信贷审查委员会会办后，报总行行长备案，总行行长具有一票否决权。对一般关联交易，按规定流程审议后，向风险管理与关联交易控制委员会实施事后备案。对重大关联交易，重大关联交易经贷审会审议，报董事会风险管理与关联交易控制委员会审查后，交董事会审议批准。重大关联交易应当在批准之日起十个工作日内报告监事会，同时报告中国银行业监督管理委员会。与本银行董事、总行高级管理人员有关联关系的关联交易应当在批准之日起十个工作日内报告监事会。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十五条', '授信评审部认为支行调查资料不完善的或者调查报告不实的，可将贷款资料退回支行重新调查，情节严重的将对相关责任人进行处罚。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十六条', '贷款授信审议的主要内容包括：1．贷款业务是否合法合规，是否符合国家产业政策、信贷政策；2．贷款业务的风险和防范措施；3．其他需要审议的内容。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十七条', '支行贷款会办小组会办时须明确专人对信贷业务授信审议过程进行记录，并确定审议结论后按转授权权限报批。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十八条', '需向省联社报备的贷款授信，由本行授信评审部按省联社规定的要求准备资料，统一上报。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第十九条', '不得对以下用途的业务进行授信：（一）国家明令禁止的产品或项目；（二）违反国家有关规定从事股本权益性投资，以授信作为注册资本金、注册验资和增资扩股；（三）违反国家有关规定从事股票、期货、金融衍生产品等投资；（四）其他违反国家法律法规和政策的项目。”'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第二章  授信管理尽职要求', '第二十条', '客户未按国家规定取得以下有效批准文件之一的，或虽然取得，但属于化整为零、越权或变相越权和超授权批准的，本行不得提供授信：（一）项目批准文件；（二）环保批准文件；（三）土地批准文件；（四）其他按国家规定需具备的批准文件。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第一条', '用信申请'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二十一条', '本行贷款业务审批实行“先授信、后用信”的原则。已授信客户的用信，由客户向所在支行提交用信申请，超出支行用信审批权限的申请，经支行审查后向本行信贷管理部申报。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二十二条', '对客户已授信而未用信的，且授信日与用信日间隔期限超过6个月的，必须申请重新授信后方可用信。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二十三条', '已授信客户的贷款用信申请由客户经理负责受理。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二条', '用信审查'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二十四条', '贷款用信的审查按权限实行分级审查。支行权限内的用信申请由支行风险员或合规员审查；超出支行审批权限的用信申请，经支行风险员或合规员审查，支行用信小组会办后报送本行信贷管理部用款支行审核。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二十五条', '用信申请时应提供下列资料：1．用信审查审批表；2．授信审批文件及授信限制性条款落实情况的证明材料；3．用信需求的证明文件(包括购销合同、协议等)；4．其他需要审核的材料。本行信贷管理部审查人员对支行提交的资料不全、调查内容不完整、不清晰的贷款用信申请，应要求支行补充或完善。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二十六条', '用信审查的主要内容包括：1．对客户提供的用信资料完整性、有效性进行审查核实，并分析其是否符合法律法规和本行的相关信贷政策；2．查验授信文件的有效性；3．客户经营现状是否满足用信条件，有无在授信日后发生重大不利变化；4．查验授信时存在的限制性条款有无落实到位。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第三条', '用信审批'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第三章   用信管理尽职要求', '第二十七条', '用信审查人员审查完毕后，按权限报有权审批人审批。贷款用信的审批按权限实行分级审批。支行权限内的用信申请由支行有权人审批；支行权限外的用信申请由支行提交至信贷管理部审批。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第四章  合同签订尽职要求', '第二十八条', '收到授信和用信批复后，支行信贷内勤负责与借款人、担保人签订借款合同以及担保合同，担保合同要件必须与主合同相互衔接。信贷内勤须在签约台当面与借款人签订书面合同，内容包括：贷款种类、币种、用途、金额、期限、利率、支付方式、还款方式、担保方式、合同双方的权利、义务、争议解决等。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第四章  合同签订尽职要求', '第二十九条', '信贷内勤是对外签订借款合同的经办人，所有借款合同须经支行风险员、合规员审核，未经审核的合同，不得外出。经审核无误后，由主管会计加盖贷款合同专用章。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第四章  合同签订尽职要求', '第三十条', '合同必须按规定使用统一制式合同文本。支行可在不违背制式合同文本基本要求的基础上，为了增加保全措施或减少风险，经信贷管理部同意后，合同双方可以在协商一致的前提下签订补充协议，补充协议格式和内容应符合法律规定，并经合规管理部审查同意。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第四章  合同签订尽职要求', '第三十一条', '借款合同和担保合同的填写和签章应符合下列要求：1．合同必须采用黑色或蓝黑墨水钢笔、黑色水笔书写或进行打印（但签章处必须手工填写），内容填制必须完整，正副文本的内容必须一致，不得涂改；2．借款合同的贷款业务种类、金额、用途、期限、利率、还款方式和担保合同中的相应内容，应与审批的内容一致；3．信贷内勤应按贷款品种、金额大小遵循支付管理要求，在合同中明确贷款资金按受托支付或借款人自主支付的方式；4．信贷内勤必须在视频监控下的签约台前，监督借款人客户以及担保人本人（法人客户为法定代表人或授权委托人）在合同文本上签字、盖章、核对预留印鉴，确保签订的合同真实、有效。借款合同、担保合同等重要资料应要求借款人、担保人加摁指模（仅指借款人、担保人为自然人）。合同签订过程应进行录像并妥善保存。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第四章  合同签订尽职要求', '第三十二条', '风险员、合规员对合同审核包括但不限于以下内容：（一）合同文本的使用是否恰当；（二）合同是否统一编号，编号是否衔接，是否按编号顺序依次登记《信贷合同登记簿》；（三）合同填制的内容是否规范、完整；（四）合同约定的其他事项补充条款是否合法、合规，是否与格式合同文本的基本条款冲突；（五）借款人客户以及担保人本人（法人客户为法定代表人或授权委托人）是否在合同文本、面谈内容等签字栏签字盖章，提示语录是否为当事人亲笔抄录；（六）中长期贷款还款是否明确每年须两次；（七）合同签订过程是否合规。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第四章  合同签订尽职要求', '第三十三条', '以抵质押方式担保的，客户经理应与借款人、抵押人或质押人共同办理以下相关事宜：（一）以抵押、质押担保的要到相关的房地产、车辆、海关、工商行政管理等有权登记的职能部门办理抵押、质押登记，并将他项权证按规定转交本行保管；（二）以存单、国债、债券、保单等质押的权利凭证应办理止付等相关手续；（三）质押担保的质物交接应填制“权利或动产质押清单”，办理质物交接手续。以动产动态质押方式担保的，本行必须直接或间接实现有效监管。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第五章  发放支付尽职要求', '第三十四条', '支行信贷内勤依据贷款用信批复负责在信贷管理系统中进行贷款发放操作。支行权限内，经风险员、合规员审查、支行有权人审批；权限外，提交放款中心审查，由放款中心有权人审批。信贷内勤应根据审批意见，进行相关数据维护，打印《放款通知单》，交客户至柜面办理用款。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第五章  发放支付尽职要求', '第三十五条', '柜面接到客户提交的《放款通知单》，支行权限内，由会计主管进行审查、发放；权限外，经用款中心有权人审批后发放。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第五章  发放支付尽职要求', '第三十六条', '客户应依据借款合同约定的用款计划或约定，一次或分次用款。支行应在合同有效期限内办理贷款发放手续。（一）保证担保的贷款业务自签订借款合同和担保合同后发放，按借款合同约定用款计划的日期使用贷款；（二）抵押担保的贷款业务自设立抵押权登记后发放，按借款合同约定用款计划的日期使用贷款；（三）质押担保的贷款业务自签订借款合同和质押合同并办妥质物移交后发放（质权需登记的自办理登记后发放），按借款合同约定用款计划的日期使用贷款。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第五章  发放支付尽职要求', '第三十七条', '信贷内勤、用款审查人员、柜面发放人员应各尽其职，做好贷款发放的衔接工作。（一）信贷内勤在信贷系统的录入登记应准确、完整，并监督法人客户签章，确保借款借据（易贷通除外）填制符合下列规定： 1．填制的借款人名称、金额、用途、还款日期、利率、合同号等内容要与借款合同的内容一致；2．借据中的借款日期不得在借款合同生效日期之前，借款到期日不得超过合同到期日；3．借款借据的大、小写金额必须一致，分笔发放的，借款借据的合计金额不得超过相应借款合同的金额；4．法人客户在借款借据上的签章应与其预留印鉴一致，并加盖其行政公章；5．本行的其他规定。（二）用款审查人员应审查贷款相关资料，发现下列情形之一的，不得审查同意发放：1．没有调查人、审查人、审批人同时签注意见并签名的（易贷通贷款除外），超过支行权限的贷款没有本行用信批复的；2．非借款人本人办理或代理人不能提供有效授权委托书的；3．贷款资料（包括借款申请书、授用信审查审批表、放贷通知书、面谈面签记录、借款人身份证复印件等）不全的；4．借款资料内容要素不一致、不全的；5．资料要素涂改未经有效确认的；6．抵押贷款没有取得他项权利证书的；7．质押贷款对质押物未止付处理的；8．自然人担保无身份证件复印件的；9．未提供客户提款申请书的；10．贷款支付方式、支付对象符合法律、本行规定和合同约定以及明显不合理的；11．其他不符合贷款条件的情形。柜面发放人员负责监督自然人客户签字并加摁指模（法人客户由客户经理监督签章确认）；对易贷通贷款应根据客户经理的面谈面签记录进行贷款发放，准确录入贷款用途、金额、利率、期限等要素。柜面发放人员应审查贷款业务是否经有权审批人审批同意，是否借款人本人或有权代理人经办借款业务等，审查无误后进行贷款业务柜面支付，将资金划入借款人本人的结算账户，发现借据要素不齐全或不正确的，核实情况后进行抹账处理。执行受托支付的，贷款发放人员还必须按合同约定将贷款资金从借款人本人的结算账户汇转至交易对手账户。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第三十八条', '客户经理是贷款业务发生后管理的责任人，负责贷款业务发生后的日常管理。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第三十九条', '支行应真实、全面收集、管理档案资料，保证信贷资料的有效、完整和方便利用。（一）以客户为单位建立信贷档案；（二）实行信贷档案资料原件的定期上交，本行集中保管，支行以扫描件备查；（三）实行档案资料查询、调阅登记制度；（四）实行信贷档案交接制度。客户经理工作交接由支行风险员、合规员进行监交。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十条', '建立多层级、常态化的贷后检查体系，各检查层按各自职责开展检查，揭示风险点。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十一条', '支行对辖内所有贷款的日常检查，按照“发放和检查不为同一人”的原则实行。客户经理对阳光信贷客户贷款，每月不少于20户，每年至少检查一遍；非阳光信贷客户，每月不少于10户，每半年须检查一遍；对特殊性的贷款检查按相关信贷业务产品管理办法执行；公司客户每季检查一遍。支行行长对公司客户、贷款金额在50万元（含）以上个人客户，一般每月分别检查不少于5户。贷后检查应填制检查报告，报告内容要真实、全面、深入。对检查过程中发现影响贷款安全的重大事项，要提出防范和化解信贷风险的措施，及时报送支行行长和本行管理部门。贷款业务发生后，支行应组织开展跟踪检查和非跟踪检查。（一）跟踪检查内容主要包括：1、负责检查的客户经理应在15日内对所有项目贷款、新客户首笔贷款、新增贷款进行首次跟踪检查；2、检查客户是否按照借款合同规定的用途使用贷款，对未按借款合同规定用途使用的，应查明原因并提出处置的意见和建议。（二）非跟踪检查内容主要包括：1．检查借款人、担保人的生产经营、财务状况是否正常，主要产品的市场变化是否影响产品的销售回笼和经济效益；2．了解掌握借款人、担保人的非财务情况，包括股东构成、管理状况、诚信履约等情况，有无对生产经营产生重大影响；3.检查抵（质）押物的完整性和安全性，抵押物的价值是否受到损失或降低，抵押权是否受到侵害，质押物的保管是否符合规定； 4.检查固定资产建设项目进展情况。根据固定资产项目管理规定，逐项检查项目资金是否按期到位，是否按招投标计划进行，项目贷款是否被挤占挪用，项目工程进展是否正常，项目是否能按期竣工，项目竣工投产能否达产等内容。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十二条', '建立风险预警体系。通过非现场和现场检查，及时发现授信主体的潜在风险并发出预警风险提示。各级授信工作人员应及时对授信情况进行分析，发现客户违约时应及时制止并采取补救措施。”'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十三条', '信贷管理部对全辖贷款业务每年至少组织一次综合性检查；每半年至少组织对两个支行贷款业务的全面检查；每季组织开展两次以上的行业性、系统性、区域性的检查。检查各项信贷管理制度的执行情况：信贷管理责任制是否落实，信贷业务操作是否合法，审批批复限制性条款是否落实，督导、检查贷款业务发生后的跟踪检查和日常检查，检查发现的问题是否及时报告，是否采取了相应措施，已发现问题是否整改到位，是否存在屡查屡犯等现象。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十四条', '风险管理部每年至少开展一次关联交易检查，每半年至少组织一次风险分类检查，每季至少开展一次重点贷款检查。检查制度执行合规性、操作规范性及整体风险控制到位情况等。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十五条', '合规管理部每年组织开展信贷制度执行有效性的检查，评估制度执行的效果及存在的问题等。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十六条', '总行检查发现的问题，检查人员应交被检查支行行长确认，被检查支行行长有异议的，应说明理由。无异议的，被检查支行应将整改意见报本行职能部门。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十七条', '风险分类。贷款发放后，支行应按本行《信贷资产实施细则》等风险分类规定及时认定，且认定资料完整。支行要根据检查结果及时提出调整资产风险分类意见，按季报经风险管理部认定批准后实施。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十八条', '短期贷款到期前15天、中长期贷款到期前30天内，客户经理要向借款人发送到期、担保人履行债务通知单并取得回执。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第四十九条', '对符合条件的展期，可由借款人申请，按以下要求办理贷款展期手续。（一）客户应提交书面展期申请。客户应在贷款到期10天前填制并向支行提交《贷款展期申请书》；（二）贷款展期的调查、审查、审批。客户经理应对客户申请展期的原因、金额、期限、还款措施和还款资金来源进行调查，签注调查意见，按照审批权限审批；（三）签订贷款展期协议。贷款展期批准后，客户经理与客户、担保人签订《贷款展期协议书》；（四）贷款展期账务处理。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第五十条', '借新还旧贷款（转据）的管理。所有借新还旧的贷款业务必须按本行转授权规定进行审批，且转据后的合同以及借据贷款用途应注明“借新还旧”。办理借新还旧贷款必须符合以下标准：（一）借款人生产经营正常；（二）能够按时支付到期利息；（三）重新办理贷款手续，责任人明确；（四）风险程度有所降低，如办理了有效担保手续等。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第五十一条', '贷款到期之日营业终了尚未归还的贷款转入逾期科目核算。客户经理应填制逾期贷款催收通知书，分别发送至借款人和担保人并取得回执。逾期一年内的贷款每季至少催收一次，逾期一年以上未诉讼的贷款，每半年应催收一次，已诉讼的，客户经理要确保债权、跟踪执行。当月新增不良贷款月末未收回的，支行必须按新增不良贷款统计报表的格式要求向本行管理部门报告，说明形成原因和采取的清收措施。因管理松弛、玩忽职守、催收措施不力等主观原因导致贷款诉讼（执行）时效丧失造成损失的，本行将追究相关责任人员的责任。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第五十二条', '贷款逾期后，支行应明确逾期贷款清收措施。清收责任人（客户经理）抓好清收措施的落实。与借款人（担保人）订立还款计划的，必须经本行批准。贷款逾期三个月内，借款人没有落实还款计划或拒不偿还贷款的，支行应采取诉讼等方式进行强制清收。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第六章  贷后管理尽职要求', '第五十三条', '借款人还清全部贷款后，支行应将抵押、质押的权利凭证交还借款人并作签收登记，设定抵押、质押登记的应按借款人要求向登记部门办理登记注销手续。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第七章  授信工作尽职评价要求', '第五十四条', '建立授信工作尽职评价制度。本行授信工作尽职评价由本行根据本行授信业务发展状况，配备相应的授信工作尽职评价人员。从事授信尽职评价的人员应具备较完备的授信、法律、财务等知识，接受相关培训，并依诚信和公正原则开展工作。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第七章  授信工作尽职评价要求', '第五十五条', '支持授信工作尽职评价人员独立行使尽职调查评价职能，调查评价可采取现场或非现场的方式进行。必要时，可聘请外部专家或委托专业机构开展特定的授信尽职调查工作。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第七章  授信工作尽职评价要求', '第五十六条', '对授信业务流程的各个环节进行尽职评价，评价授信工作人员是否尽职，确定授信工作人员是否免责。评价可采取现场或非现场的方式进行，被调查人员应积极配合调查人员的工作。授信工作尽职调查评价人员应及时报告尽职评价结果。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第七章  授信工作尽职评价要求', '第五十七条', '对授信工作尽职调查评价人员发现的问题，经过确认的程序，应责成相关授信工作人员及时进行纠正。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第七章  授信工作尽职评价要求', '第五十八条', '本行应根据授信工作尽职调查人员的调查结果，对具有以下情节的授信工作人员依法、依规和本行的追究制度追究责任。（一）进行虚假记载、误导性陈述或重大疏漏的；（二）未对客户资料进行认真和全面核实的，对异常情况未作进一步调查的；（三）未按照规定对抵（质）押物进行实地核查的；（四）故意隐瞒真实情况的，特别是隐瞒与借款人关系，或隐瞒借款人、担保人及其业主的不良信用记录等；（五）授信决策过程中超越权限、违反程序审批的；（六）未按照规定实施授信后管理，致使授信风险未及时防范、控制的；（七）授信客户发生重大变化和突发事件时，未及时报告，未及时实地调查，未及时采取必要措施的；（八）未根据预警信号及时采取必要保全措施的；（九）未将客户违约信息及时向监管机构报告的；（十）不配合授信尽职调查评价人员工作或提供虚假信息的，对授信尽职评价工作中发现的问题逾期不予纠正的；（十一）其他。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第七章  授信工作尽职评价要求', '第五十九条', '经检查监督和责任认定，有证据表明，对于严格按照授信业务流程及有关法规，在客户调查和业务受理、授信分析与评价、授信决策与实施、授信后管理和问题授信管理等环节都勤勉尽职地履行职责的授信工作人员，授信一旦出现问题，可视情况免除相关责任。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第八章  附则', '第六十条', '本办法末尽事宜执行《信贷管理制度》等规定。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第八章  附则', '第六十一条', '本办法由信贷管理部负责制订、修改和解释。'),
(NULL, '信贷管理部', '《贷款业务尽职管理办法》', '第八章  附则', '第六十二条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第一条', '为强化贷款担保管理，规范贷款担保行为，正确运用担保手段防范信贷风险，根据《中华人民共和国担保法》、《中华人民共和国物权法》和商业银行股份有限公司(以下简称“本行”)《信贷管理制度》等规定，制订本办法。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第二条', '本办法所称的贷款担保，是指在本行发放贷款时，要求借款人提供担保，以保障贷款债权实现的法律行为。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第三条', '本办法所称的贷款担保管理是指通过建立健全贷款担保管理制度，恰当选择担保方式，完善担保手续，规范担保合同内容，确保担保债权的实现。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第四条', '本行各支行、营业部、小微业务部、公司业务部(以下简称“经办行”)在办理贷款担保业务时应当遵守国家相关法律、法规、金融规章和本行有关规定，遵循平等、自愿、公平和诚实信用的原则。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第五条', '贷款担保的范围应当包括借款合同项下的借款本金、利息、复利、罚息、违约金、赔偿金、实现债权的费用(包括但不限于诉讼费、律师费等)和所有其他应付费用。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第六条', '贷款担保的方式为保证、抵押和质押。（一）担保的方式可以单独使用，也可以合并使用。本行认为使用一种担保方式不足以防范和分散贷款风险的，应当选择两种以上的担保方式。（二）同一笔贷款设定两种以上担保方式时，各担保方可以分别担保全部债权，也可以划分各自担保的债权份额。（三）同一笔贷款既有保证又有借款人自己提供的抵押(或质押)担保的，担保合同没有约定代偿顺序的，本行应当首先行使抵押(或质押)权。（四）同一笔贷款既有保证又有第三人提供的抵押(或质押)担保的，本行一般不主动划分保证担保和抵押(或质押)担保的份额，各担保方均对全部债权承担担保责任，本行可以选择任一担保方式或全部担保方式，要求保证人或者抵押人(或出质人)承担全部担保责任。签订担保合同时，如果保证人和抵押人(或出质人)要求划分担保份额的，可以在保证合同和抵押(或质押)合同中约定。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第七条', '同一担保方式的担保人可以是一人，也可以是数人。（一）同一笔贷款有两个以上保证人的，本行一般不主动划分他们所担保的债权份额；如果保证人要求划分其担保的债权份额的，可以在保证合同中约定。（二）同一笔贷款有两个以上抵押人或出质人的，本行一般不主动划分他们所担保的债权份额；如果抵押人或出质人要求划分其担保的债权份额的，双方可以在抵押合同或质押合同中约定。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第八条', '公司客户或大额自然人客户（授信 500 万元及以上）向我行申请贷款，其核心资产（包括借款人拥有所有权或处分权的房产以及价值较高、流动性较好的其它资产）应抵（质）押给我行（总行批准除外）。借款人为法人或其他组织的，其法定代表人、实际控制人以及股权比例5%以上（含）股东必须对借款人在本行贷款提供连带保证担保，财务核算不健全的法人或其他组织的，实际控制人家庭成员必须追加提供连带保证责任。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第九条', '经办行应采用本行制发的格式文本签订担保合同。特殊情况需要对格式文本的条款作删改或者不采用格式文本的，必须经本行合规管理部审查。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第一章  总则', '第十条', '本办法适用于向本行申请各类贷款的担保。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第一条', '保证人的资格'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十一条', '具有代为清偿债务能力的法人、其他组织或者自然人，可以作为借款人在本行贷款的保证人。本办法所指的其他组织主要包括：（一）依法登记领取营业执照的私营独资企业、合伙组织；（二）依法登记领取营业执照的合伙型联营企业；（三）依法登记领取我国营业执照的中外合作经营企业、中外合资企业、外商独资企业；（四）经核准登记的领取社会团体登记证非以公益为目的的社会团体。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十二条', '本行在办理贷款保证担保时，应当优先选择代为清偿债务能力强、信誉状况好的法人为保证人；对集团公司本部(母公司)为本集团公司内部成员单位担保、集团公司内部成员单位之间担保、其他保证人之间相互担保，由本行根据实际情况确定。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十三条', '法人、其他组织为保证人的，应当同时符合下列条件：（一）依法经履行工商行政管理职能的机构(或者其他主管机关)核准登记并履行年度报告手续；（二）独立核算，自负盈亏，有健全的管理机构和财务制度，并享有所有权或者依法处分权的独立财产。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十四条', '专业担保机构为保证人的，应当同时符合下列条件：（一）依照法律及有关规定办理注册及年检手续；（二）有完善的法人治理结构和内部组织机构；（三）自主经营、独立核算，在营业执照规定的经营范围内从事经营活动；（四）应有一定数额的担保基金存入在本行设立的专门账户，实行专项储存、专户管理；（五）经本行认可，并与本行签订了合作协议。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十五条', '不得接受下列单位、组织提供的担保：（一）国家机关；（二）学校、幼儿园、医院等以公益为目的的事业单位、社会团体，但从事经营活动的事业单位、社会团体除外；（三）企业法人的分支机构，但有企业法人的书面授权，且不超出该书面授权范围的除外；（四）企业法人的职能部门。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十六条', '自然人为保证人的，应当符合下列条件：（一）拥有中华人民共和国国籍；（二）具有完全民事行为能力；（三）有合法的居留身份；（四）有固定的住所；（五）有稳定的合法收入来源和充足的代偿能力。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十七条', '对借款人新增贷款，不得接受下列对象保证担保：（一）担任法定代表人、董事或高级管理人员所在公司有过破产、逃废银行债务等行为的；（二）借款、对外担保未按借款合同、担保合同约定履行义务或有重大经济纠纷未解决的；（三）不具有代为清偿债务能力；（四）有逃废银行债务等行为；（五）本行认为不适宜提供保证担保的其他对象。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二条', '保证人应提交的材料'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十八条', '法人、其他组织为保证人的，应提交下列材料：（一）营业执照(或事业单位登记表，属于金融、建筑、食品、医药等需要批准方能经营的特殊行业的，须同时提交政府主管部门颁发的经营许可证)及最近年度的报告公示、组织机构代码证、税务登记证明（营业执照为三证合一的，则不需提供组织机构代码证、税务登记证明）；（二）法定代表人(负责人)身份证明；（三）如有需要委托的事宜，要提供法定代表人授权委托书、委托代理人身份证明；（四）最新的章程及验资报告（非必有项）；（五）当期及近一年的财务报表。报表主要包括：资产负债表、损益表、现金流量表等；（六）或有负债清单及情况说明；（七）信用征信记录；（八）本行认为需要提交的其他材料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第十九条', '有限责任公司、股份有限公司担保的，还应提供公司董事会或股东(大)会依公司章程作出的同意提供该担保的决议。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十条', '承包经营企业为保证人的，还应当提交发包人同意该担保的书面文件。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十一条', '专业担保机构为保证人的，应当首先取得本行的认可，并与本行签订了相关协议。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十二条', '自然人为保证人的，应提交下列材料：（一）保证人的有效身份证件(居民身份证或其他有效居住证件)；（二）保证人财产及收入状况证明(合法、有效的财产所有权证明；单位财务或人事部门出具的收入证明、个人所得税纳税证明，或经办行客户经理调查测算的收入证明及经办行认可的其他证明材料)；（三）本行要求提供的其他材料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三条', '保证担保的调查评审'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十三条', '经办行接到保证人的有关材料后，应当按照现行法律、法规、规章制度和业务管理办法，对保证人的主体资格、意思表示、授权情况以及其他相关手续和文件进行审查，确定其真实性、完整性、合法性和有效性，并符合监管部门和本行的有关业务规定。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十四条', '经办行接到保证人的有关材料后，应当对保证人的资信状况、代偿能力等事项进行审查评估，确保保证人符合本行保证担保的要求。经办行认为确有必要的，也可以委托具有合法资质的社会中介机构对保证人的资信状况、代偿能力等事项进行评估，但须事前报经总行批准。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十五条', '保证人的保证额度。（一）单户贷款保证一般不超过保证人净资产的10％；（二）保证人对外担保总额加自身银行负债一般不得超过其有效净资产或主营业务收入的50%；（三）保证客户一般不得超过3户。保证人的净资产以信贷人员实际调查为准。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十六条', '对专业担保机构的保证额度，按本行与专业担保机构签订的合作协议规定办理。根据借款人的资信情况、贷款风险度和该担保机构的管理状况，在防范风险的前提下，为同一借款人担保的贷款总额不超过专业担保机构净资产的10％，累计担保责任余额不得超过担保机构存入本行担保基金数额规定的倍数。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十七条', '在对保证担保进行调查评审时，应当安排双人对保证人的下列情况予以核实：（一）法人、其他组织为保证人的1.主体资格情况，主要包括保证人的名称、住所、注册资本、法定代表人、经营范围、营业执照号码及注册登记和年度报告公示情况，经营许可证记载及年检情况等；2.授权情况，主要包括保证人提供保证担保是否已获必需的政府批文、内部决议和授权，内部决议和授权文书是否按照保证人章程记载的议事规则作出；3.意思表示情况，主要包括提供本次担保并承担连带责任的意思表示是否自愿、真实；4.资信及代偿能力情况，主要包括保证人资产状况、经营状况、银行账户，本次担保金额、已对外提供担保金额及可担保能力，是否有逃废银行债务等不良信用记录，是否涉及重大的债权债务纠纷；5.印章及签字情况，主要包括保证人公章、法定代表人或授权代理人签字样本或印鉴的真伪。（二）自然人为保证人的1.主体资格情况，主要包括保证人的姓名、家庭住址、通讯地址、联系电话、身份证件、户口簿及民事行为能力；2.意思表示情况，主要包括保证人提供本次担保并承担连带责任的意思表示是否自愿、真实；3.资信及代偿能力情况，主要包括保证人财产与收入状况，本次担保金额、已对外提供担保金额及可担保能力，是否有贷款逾期、欠息、逃废银行债务等不良信用记录，是否涉及重大的债权债务纠纷及治安或刑事处罚；4.签字情况，主要是保证人本人签字的真伪。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十八条', '经办行对上述事项的调查时，应当形成调查结论。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第二十九条', '经调查对符合规定条件的保证担保，应当按照信贷业务审批程序予以确认；对不符合规定条件的，应及时告知借款人重新提供新的保证人或其他有效担保。未经调查和有权人审批的，不得与保证人订立保证合同，更不得与借款人订立借款合同发放贷款。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四条', '保证合同的订立'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十条', '经办行经过审查，确认借款人提供的保证担保具有合法性、有效性和可靠性，并经有权人批准后，方可与保证人订立保证合同。保证合同自合同双方当事人签章（或签字加按指模）之日起生效。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十一条', '经办行与保证人可以就单个借款合同分别订立保证合同，也可以协议在最高债权额限度内就一定期间连续发生的借款合同订立一个最高额保证合同。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十二条', '保证合同应当包括以下主要内容：（一）被保证的主债权种类、数额；（二）主合同借款人履行债务的期限；（三）保证方式；（四）保证范围；（五）保证期间；（六）双方的权利和义务；（七）违约责任；（八）合同的生效、变更、解除和终止。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十三条', '经办行应当确保保证合同的编号与借款合同担保条款约定的担保合同编号一致，保证合同中保证人的名称与借款合同担保条款约定的保证人名称一致。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十四条', '经办行应当确保保证合同各方加盖的公章、法定代表人或授权代理人的签字真实、有效。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十五条', '经办行应当与保证人约定下列特别条款，由保证人签字盖章（或签字加按指模）确认：（一）上市公司作保证人的，应当承诺：本保证人保证按照《证券法》、《证券交易所股票上市规则》等法律、法规和规章制度的要求及时就该担保事项履行信息披露义务；如果因未及时履行信息披露义务影响贷款入担保债权的实现，本保证人愿承担由此产生的全部责任；（二）需要约定的其他特别条款。经办行认为确有必要，可以在保证合同中与保证人约定其他特别条款，但须经合规管理部审查同意。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十六条', '同一笔贷款有两个以上保证人的，经办行应当按照本办法第七条第一款的规定与保证人签订保证合同。同一笔贷款既有保证又有借款人自己提供的抵押(或质押)担保的，经办行应当按照本办法第六条第三款的规定与保证人和抵押人(或出质人)分别签订保证合同和抵押合同(或质押合同)。同一笔贷款既有保证又有第三人提供的抵押(或质押)担保的，经办行应当按照本办法第六条第四款的规定与保证人和抵押人(或出质人)分别签订保证合同和抵押合同(或质押合同)。有关抵押合同(或质押合同)的签订，依照本办法第三章(或第四章)的规定办理。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第五条', '保证担保的管理'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十七条', '经办行应当按照本行有关贷款档案、信贷管理系统的规定，加强对信贷管理系统录入内容及贷款保证担保档案扫描影像上传的管理，确保信贷管理系统录入内容和上传档案影像的真实性、完整性、有效性和连续性。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十八条', '保证合同有效期间，经办行应当就下列事项事先取得保证人同意继续担保的书面意见：（一）与借款人协议变更借款合同履行期限的；（二）与借款人协议变更借款合同项下币种、金额、利率等内容，加重或将要加重借款人的债务的；（三）许可借款人转让债务的。经办行应当将保证人同意上述第（一）、（二）、（三）项的书面文件作为保证合同的必要附件。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第三十九条', '保证合同有效期间，经办行依法将主债权转让给第三人的，应当及时通知保证人。保证合同另有约定的，从其约定。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十条', '保证合同有效期间，经办行应当按照本行贷后检查的相关规定，对保证人的资信状况和偿债能力及保证合同的履行情况定期进行检查，督促保证人按照保证合同的约定按期提交有关材料并履行各项义务。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十一条', '经办行应当主要检查保证人是否发生下列情形：（一）财务状况恶化或涉及重大经济纠纷；（二）经营机制或组织结构发生变化，包括但不限于承包、租赁、合并、分立、股份制改造、联营、与外商合资或合作等；（三）经营范围与注册资本变更，股权变动；（四）破产、歇业、解散、停业整顿、被吊销营业执照、被撤销；（五）章程、法定代表人、住所、电话等发生变更。经办行对上述情形的检查，应当形成书面检查结论并作出风险预警，同时经检查人员及有关负责人签字确认。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十二条', '保证合同有效期间，保证人如发生分立、合并、股份制改造以及其他经营机制或组织结构变化的，经办行应当及时督促保证人妥善落实保证合同项下全部保证责任。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十三条', '借款合同确定的借款尚未到期，人民法院受理保证人破产案件的，经办行应当及时书面通知借款人另行提供符合本行要求的担保，借款人未能按要求提供所需担保的，经办行应当及时要求借款人提前归还贷款本息；借款合同确定的借款到期，人民法院受理保证人破产案件的，如果在破产宣告前保证人已经被生效的法律文书确定承担保证责任，经办行可以在债权申报期间就该保证责任向人民法院申报债权，参加保证人破产财产分配，并就破产程序中未受清偿的部分继续向借款人起诉追偿。保证合同有效期间，人民法院受理借款人破产案件的，经办行按照有利予贷款债权保护的原则，既可以直接要求保证人承担保证责任，也可以在债权申报期间向人民法院申报债权，参加借款人破产财产分配，并在破产程序终结后六个月内，就破产程序中未受清偿的部分继续要求保证人承担保证责任。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十四条', '保证合同有效期间，保证人如发生歇业、解散、被停业整顿、被吊销营业执照、被撤销、财务状况恶化、涉及重大经济纠纷以及其他不利予其担保的贷款债权变化的，经办行应当及时书面通知借款人另行提供符合本行要求的担保，借款人未能按要求提供所需担保的，经办行应当及时要求借款人提前归还贷款本息。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十五条', '保证合同有效期间，经办行依借款合同约定依法解除借款合同的，或者依借款合同约定的其他情形提前收回贷款的，应当同时书面通知保证人提前承担保证责任。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第六条', '担保债权的实现'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十六条', '贷款到期前，经办行除须按照有关规定向借款人、保证人催收外，还应当在借款合同履行期届满后六个月内向保证人送达催收通知书，要求保证人承担保证责任。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十七条', '借款人不归还贷款本息，保证人亦不履行保证责任的，经办行应当在保证期间或诉讼时效内及时提起诉讼或者申请仲裁。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十八条', '保证人拒绝履行人民法院发生法律效力的民事判决、裁定、调解书和支付令的，经办行应于生效法律文书规定的生效日起3个月内向人民法院申请执行。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第四十九条', '已经取得执行依据但尚未申请执行的经办行，在借款人已无财产可供执行，保证人也因全部或主要财产被人民法院查封、扣押或冻结而无其他财产可供执行或其他财产不足清偿全部债务时，应当及时向该人民法院书面申请参与分配。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第二章  贷款保证担保', '第五十条', '执行程序中，因借款人、保证人均暂无执行能力或人民法院采取各种强制执行措施所得的数额仍不足清偿债务，经办行又不能提供被执行人下落和新的财产线索时，可以向人民法院申请债权凭证，待被执行人具备履行能力时再申请执行。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第一条', '抵押物的范围'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十一条', '经办行可以接受下列财产抵押：（一）抵押人所有的房屋和其他地上定着物(但以享受国家优惠政策购买的房屋抵押的，其抵押额以抵押人可以处分和收益的份额为限；以具有土地使用年限的房屋抵押的，抵押期限不得超过土地使用权出让合同规定的使用年限减去已经使用年限后的剩余年限)；（二）抵押人正在建造中的房屋或者其他建筑物(仅限于抵押人以其作抵押向经办行申请该贷款用于在建工程继续建设资金款)；（三）抵押人购买的预售房屋(仅限于购房人在经办付首期规定的房价款后以其作抵押向经办行申请贷款用于其购房款)；（四）抵押人所有的机器、交通运输工具和其他财产；（五）抵押人依法有权处分的国有的土地使用权、林权、房屋和其他地上定着物等；（六）抵押人依法有权处分的国有的机器、交通运输工具和其他财产；（七）抵押人依法承包并经发包方同意抵押的荒山、荒沟、荒丘、荒滩等荒地的土地使用权：（八）依法可以抵押的其他财产。抵押人可以将前款所列财产一并抵押。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十二条', '经办行在办理贷款抵押担保时，应当优先选择现房、以出让方式取得的国有土地使用权及其他价值相对稳定、变现能力较强的抵押物；对机器、设备及其他不易变现或价值波动较大的抵押物应当从严掌握，不接受专用性较强的机器、设备及其他财产抵押。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十三条', '以依法取得的国有土地上的房屋抵押的，该房屋占用范围内的国有土地使用权同时抵押；对超出房屋占用范围的国有土地使用权抵押的必须办理登记。以出让方式取得的国有土地使用权抵押的，应当将该国有土地上的房屋同时抵押。以划拨方式取得的国有土地使用权不得单独抵押。以划拨土地上的房屋及其他建筑物抵押的，应当将其占用范围内的土地使用权同时抵押，并在核定抵押率时充分考虑应缴纳的土地使用权出让金因素。以在建工程抵押的，可将在建工程占用范围内的土地使用权，连同在建工程的投入资产同时抵押，并在核定抵押率时充分考虑建设工程承包人的工程价款因素。一是要求客户提供与在建工程施工单位签订的建设工程合同，着重审查合同约定工程的总造价、工程款支付条件、支付方式以及是否存在施工单位垫资建设等情况；二是查明其建设工程款项的支付情况，并取得承建人出具的建设工程款已付清的书面凭据；三是要求工程施工单位对我行书面承诺放弃工程款优先受偿权；四是测算在建工程抵押率时，应将尚欠施工单位的工程款从抵押物价值中剔除。如果已经办理的在建工程抵押价值不足，还应就建设过程中逐渐直至最终形成的财产及其占用范围内的土地使用权办理后续抵押。以上所列房地产抵押，应当将同宗土地上的道路、绿地、天台、走廊、停车场、空余地或者其他公用设施同时抵押。上述各款规定的抵押物范围均须在抵押物清单中载明。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十四条', '经办行不得接受下列财产抵押：（一）土地所有权及其他依法禁止流通或者转让的自然资源或财产；（二）耕地、宅基地、自留地、自留山等集体所有的土地使用权，但法律规定可以抵押的除外；（三）国家机关的财产;（四）学校、幼儿园、医院等以公益为目的的事业单位、社会团体的教育设施、医疗卫生设施和其他社会公益设施；（五）所有权、使用权不明或有争议的财产；（六）以法定程序确认为违法、违章的建筑物；（七）依法被查封、扣押、监管或采取其他强制性措施的财产；（八）租用或者代管、代销的财产(出租人同意除外)；（九）已出租的公有住宅房屋和未定租赁期限的出租住宅房屋；已依法公告在国家建设征用拆迁范围内的房地产；已列为文物保护的古建筑、有重要纪念意义的建筑物；（十）已经折旧完或者在贷款期内将折旧完的固定资产，淘汰、老化、破损和非通用性机器、设备；（十一）未成年人拥有产权的财产；（十二）依法不得抵押的其他财产。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十五条', '抵押物所担保的贷款债权不得超过抵押物的价值。经办行不得接受已经设置抵押的抵押物重复抵押，但经总行同意的除外。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十六条', '经办行不得接受已经提交破产预案或者已经进入破产程序的法人、其他组织对原来没有财产担保的债务提供抵押担保。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十七条', '借款人有多个普通债权人的，经办行不得在清偿债务时与借款人恶意串通以其全部财产设定事后抵押。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第二条', '抵押人应提交的材料'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十八条', '抵押人应当向经办行提交下列材料：（一）营业执照(或事业单位登记表，属于金融、建筑、食品、医药等需要批准方能经营的特殊行业的，须同时提交政府主管部门颁发的经营许可证)及最近年度的报告公示、组织机构代码证、税务登记证明（营业执照为三证合一的，则不需提供组织机构代码证、税务登记证明）；（二）法定代表人身份证明及签字样本和印鉴；（三）法定代表人授权委托书、委托代理人身份证明及签字样本或印鉴；（四）章程及验资报告（非必有项）；（五）抵押人同意提供抵押担保的书面材料；（六）抵押人对抵押物享有所有权或者使用权或者依法处分权的权属证明文件；（七）抵押物的清单及基本资料，包括抵押物的名称、数量、质量、状况、所在地，同一抵押物已向其他债权人设定抵押的情况证明等；（八）经办行认为需要提交的其他材料，如税务机关出具的抵押人纳税情况证明等。抵押人为借款人时，可以不再重复提交本条（一）至（五）项材料。抵押人为自然人时，不需提供本条（一）至（六）项材料，但需另行提供下列材料：1．抵押人及配偶的有效身份证件(居民身份证或其他有效居留证件)；2.以房屋所有权证书抵押的，须抵押人和配偶以及房产证书上注明的房屋共有人共同出具同意提供抵押担保的书面材料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五十九条', '以共同共有的财产抵押的，还应有全体共有人同意以该财产设定抵押的书面材料，抵押人为全体共有人。以按份共有的财产抵押的，还应有抵押人对该财产占有份额的证明及其他共有人同意抵押人以其所占份额设定抵押的书面材料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十条', '国有企业、事业单位法人以国家授予其经营管理的财产抵押的，还应提供其政府主管部门同意抵押的批准文件，但法律、法规、司法解释或政府主管部门另有规定的除外。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十一条', '以中外合资企业、合作经营企业和外商独资企业的财产抵押的，还应提供该企业董事会或联合管理机构依企业章程作出的同意抵押的书面决议。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十二条', '以有限责任公司、股份有限公司的财产抵押的，还应提供该公司董事会或者股东（大）会依公司章程作出的同意抵押的书面决议。国有独资公司未设董事会的，应提供国家授权投资机构或者国家授权部门同意抵押的书面材料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十三条', '以承包经营企业的财产抵押的，还应提供发包方同意抵押的书面材料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十四条', '以非法人联营企业的财产抵押的，还应提供联营各方同意抵押的书面材料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十五条', '以已出租的财产抵押的，经办行应当告知承租人其承租房产已抵押给本行的事实，并向承租人出具《抵押物租赁情况核实书》，同时取得抵押人、承租人书面确认。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十六条', '以房地产抵押的，还应提供国有土地使用权证、房屋所有权证或房地产权证；以预售房屋抵押的，应提供商品房预售许可证及生效的预购房屋合同。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十七条', '以在建工程抵押的，应提供国有土地使用权证、建设用地规划许可证、建设工程规划许可证、建设许可证、开工证明、建筑工程规划图纸；证明已交纳的土地使用权出让金或需交纳的相当于土地使用权出让金的款额、已投入在建工程的工程款、施工进度及工程竣工日期、已完成的工作量和工程量等事项的书面材料；建设工程承包合同及证明建设工程价款预、决算及拖欠情况的书面材料。同时，经办行应要求抵押人提交由建设工程承包人出具的同意放弃应得未得工程价款优先受偿权的书面承诺。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十八条', '以船舶抵押的，还应提供船舶所有权证书、未先期抵押给其他债权人的证明材料以及全部份额共有人同意抵押的证明材料。在办理船舶抵押时，要充分考虑船舶优先权、留置权因素：（一）根据船舶的成新率，合理确定抵押率，最高不超过40%；（二）严格审查抵押船舶的真实属性，不得以船舶所有权登记证书作为判断船舶所有人的唯一标准，须要求抵押人提供抵押船舶所有权原始取得证明（如船舶建造合同或船舶买卖合同、在建船舶初始登记证书、相关船舶建造款或购船款支付凭证等）；（三）对涉及船舶挂靠的，须要求船舶实际所有人和船舶登记所有人作为共同抵押人参加船舶抵押合同的签订；（四）收集、核实船舶建造合同和建造款支付凭证，判断有无船舶留置权存在。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六十九条', '以机动车辆抵押的，还应有机动车登记证书。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十条', '以除船舶、车辆以外的机器设备、原辅材料、产品或商品以及其他动产抵押的，还应有抵押物的所有权或者使用权证明以及抵押物的存放状况资料。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第三条', '抵押担保的调查评审'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十一条', '经办行接到抵押人提交的有关材料后，应当对抵押人的主体资格、意思表示、授权情况，抵押物的权属、清单以及其他相关手续和文件进行审查，确定其真实性、完整性、合法性和有效性，并符合监管部门和本行的有关业务规定。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十二条', '抵押物价值的确定，抵押物价值以双方协商确定或由本行认可的具有评估资质的评估机构进行评估，经办行应对评估机构出具的评估报告应当与市场价格进行对比、分析，防止抵押物价值高估。购买时间在五年以内的房产或两年以内的其它抵押资产，原购买价(需提供正规发票)可作为抵押价的参考依据，评估要求不受上述规定限制。信贷管理部要适时跟踪抵押物价格走势，确保抵押的有效性和充足性。其中，外地房屋以当地网上二手房屋交易均价为参考依据中介机构评估价高于本行认可的最高限价，以本行认可的最高限价为准。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十三条', '经办行在对贷款抵押担保进行调查评审时，应当按照下列公式核定抵押物的最高担保额度：抵押物最高担保额度=抵押物评估价值×抵押率'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十四条', '经办行应当综合考虑抵押物的状况、抵押物所处位置、使用年限、折旧程度、功能状况、估价可信度、变现能力、租金、变现时可能发生的价格变动、变现费税等因素，根据抵押物的不同种类确定合理的抵押率，并据此与抵押人签订抵押合同。办理抵押贷款应当对抵押物的权属、有效性和变现能力以及所设定抵押的合法性进行审查，签订抵押合同并办理抵押物的有关登记手续。根据抵押物的不同情况，合理确定贷款抵押比例，严格执行本行关于抵押贷款抵押率的规定。商业用地、门面房抵押以及三线（含）以上城市个人住宅，抵押率可适当提高，但最高不得超过抵押物评估值的70%，三线（含）以上城市名录以《第一财经周刊》等权威网站公布为准；在建工程抵押率应当控制在50%以下；存货抵押率控制在70%以内；机械设备抵押率控制在40％以内。机械设备、运输工具抵押的，应考虑设备的通用性、使用年限及成新率等因素，按一定比例确认抵押值。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十五条', '经办行在对抵押担保进行调查评审时，应当安排双人对抵押人及抵押物的下列事项进行实地核查：（一）抵押人是否具有合法的主体资格、是否对抵押物享有所有权或者依法处分权，其设定本次抵押担保的意思表示是否自愿、真实且已获得本办法规定的各种书面同意或授权；（二）抵押物是否属于本办法规定的可以接受抵押的财产，其权属是否明确、产权证书或其他权属文件是否完整、真实、有效；（三）抵押物是否存在出租、已抵押、查封、扣押、监管等限制情况；（四）抵押物的评估价值及变现能力是否与抵押物现状存在显而易见的出入；（五）抵押物的所在地及存在状态是否与产权证书的指向相符合；（六）经办行认为需要核查的其他事项，如抵押人是否有欠缴的税款、用于抵押的在建工程是否拖欠建设工程价款等。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十六条', '经办行应当形成调查评审结论，并按信贷业务审批程序确定是否同意接受该抵押担保。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第四条', '抵押合同的订立'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十七条', '经办行应当比照本办法第三十六条规定的程序和时间，与抵押人订立抵押合同。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十八条', '抵押合同的成立应当采取经办行与抵押人签订书面合同的形式。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七十九条', '经办行与抵押人可以就单个借款合同分别订立抵押合同，也可以协议在最高债权额限度内就一定期间连续发生的借款合同订立一个最高额抵押合同。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十条', '抵押合同应当包括以下主要内容：（一）被担保的主债权种类、数额；（二）主合同借款人履行债务的期限；（三）抵押物的名称、数量、质量、状况、所在地、所有权权属或者使用权权属；（四）抵押担保的范围；（五）抵押物登记与保险；（六）双方的权利和义务；（七）违约责任；（八）合同的生效、变更、解除和终止。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十一条', '经办行可以在签订抵押合同时，争取与抵押人预先以自愿拍卖、变卖抵押物所得的价款优先受偿达成协议，并在抵押合同中约定为特别条款，但不得与抵押人约定在债务履行期届满贷款未受清偿时，抵押物的所有权转移为本行所有。经办行认为确有必要，可以在抵押合同中与抵押人约定其他特别条款，但需经合规管理部审查同意。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第五条', '抵押物登记与保险'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十二条', '贷款抵押担保均须办理抵押物登记。相关手续的办理必须由本行人员全程监督。经办行与抵押人签订抵押合同后，双方必须依照有关法律规定，就该抵押合同项下的抵押物进行登记，取得他项权利证书或者抵押登记证书。抵押物登记手续办妥的日期不得迟于抵押贷款的实际发放日期（增加担保补办抵押除外）。抵押登记的内容以登记簿为准，抵押登记的范围应当包括现有的及将来可能发生的本行所有债权及实现债权需要发生的费税。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十三条', '以土地使用权、房屋所有权、海域使用权、林木、船舶、机动车辆、设备、其他动产等抵押、质押的，必须到相关不动产登记中心、车辆、海关等有权登记的职能部门办理抵押、质押登记，并按规定将他项权证交本行档案中心保管。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十四条', '抵押合同签订后，因登记部门的原因致使无法办理抵押登记的或抵押人违背诚实信用原则拒绝办理抵押登记的，应当停止发放贷款。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十五条', '经办行认为需要办理抵押物财产保险的，在抵押合同签订后、贷款发放前，应当要求抵押人到有关保险机构按照下列条件办理抵押物的财产保险手续：（一）办理抵押物(土地使用权除外)足额财产保险；（二）保险期限不得短于主合同履行期限；（三）保险金额不得小于主合同贷款本息；（四）保险合同及保险单中应当注明，出险时本行为保险赔偿金的第一请求权人；（五）保险单中不得有任何限制本行权益的条款；（六）保险品种必须包括保险物的主要风险。纺织、化工、宾馆、会所、娱乐企业的房地产、设备，船舶等高风险行业的抵押物，抵押资产应进行保险。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第六条', '抵押担保的管理'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十六条', '抵押权设定后，经办行应填写《抵、质押品保管凭证》，经抵押人确认后，收妥抵押物他项权利证书(或其他抵押登记文书)、抵押物的所有权或使用权凭证、保险单证及其他相关资料。抵押物他项权利证书等相关凭证原件，经办行应按规定及时上缴总行保管，经办行只留存复印件，但法律法规或本行另有规定的除外。经办行应加强对信贷管理系统录入内容及上传贷款抵押担保档案扫描影像的管理。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十七条', '在抵押合同有效期内，经办行应当就下列事项事先要求借款人提交抵押人同意继续担保的书面意见：（一）延长借款合同履行期限的；（二）增加贷款金额的；（三）第三人提供抵押担保时，经办行许可借款人转让债务的。经办行应当将抵押人同意上述事项的书面文件作为抵押合同的必要附件。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十八条', '在抵押合同有效期内，抵押物由抵押人占管。抵押人在占管期间应维护抵押物的完好，不得采用非合理方式使用抵押物而使其价值产生减损。经办行应当按照本行规定的贷后检查的相关规定对抵押物的使用、管理和变化情况进行检查，并督促抵押人按照抵押合同的约定履行各项义务。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第八十九条', '经办行应当主要检查抵押人及抵押物是否发生下列情形：（一）抵押人涉及重大经济纠纷；（二）抵押人经营机制或组织结构发生变化，包括但不限于承包、租赁、合并、分立、股份制改造、联营、与外商合资或合作等；（三）抵押人经营范围与注册资本变更，股权变动；（四）抵押人破产、歇业、解散、停业整顿、被吊销营业执照、被撤销；（五）抵押人章程、法定代表人、住所、电话等发生变更；（六）抵押物权属发生争议；（七）抵押物毁损、灭失、价值减少，或者被征用；（八）抵押物产生附合物、混合物、加工物，或者孳息；（九）抵押的在建工程竣工或者形成新增财产；（十）抵押物被再设立抵押、质押，或被出租、转让、馈赠；（十一）抵押物被有关机关依法查封、扣押；（十二）抵押物财产保险到期，或被中断、撤销；（十三）抵押权受到或可能受到来自任何第三方的侵害。经办行对上述情形的检查，应当形成检查结论，并作出风险预警。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十条', '抵押权存续期间，已办理抵押登记备案手续但抵押物价值不足的在建工程形成新增财产的，经办行应当就建设过程中形成的新增财产及其占用范围内的土地使用权办理后续抵押，直至抵押物价值足额。抵押权存续期间，已办理抵押登记备案手续的在建工程竣工的，经办行应当督促抵押人及时领取房地产权属证书，并重新办理房地产抵押登记。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十一条', '抵押权存续期间，抵押物发生毁损、灭失的，经办行应当要求抵押人立即采取措施防止损失扩大，并及时向经办行提交有关主管机关出具的发生毁损、灭失的原因证明。在抵押物灭失、毁损或者被征用的情况下，经办行应当要求抵押人以所获得的保险金、赔偿金或者补偿金优先用以提前清偿所担保的主债权，或者向双方约定的第三人提存。抵押物灭失后，经办行应当就抵押物灭失后所得赔偿金数额不足清偿部分，要求借款人提供新的担保。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十二条', '抵押权存续期间，如抵押物发生保险事故，经办行应当要求将保险赔偿金全额作为抵押财产提前清偿所担保的主债权，或者经本行同意后用于恢复抵押物的价值。如抵押物财产保险被中断，经办行应当要求抵押人立即按照抵押合同的约定办理保险手续，抵押人仍未办理的，经办行有权代为办理。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十三条', '抵押权存续期间，抵押人的行为足以使抵押物价值减少的，经办行应当要求抵押人停止其行为，并恢复抵押物的价值或提供与减少的价值相当的担保。抵押人对抵押物价值减少无过错的，经办行除确保抵押物价值未减少部分仍继续作为担保之外，还应当要求抵押人在所获损害赔偿的范围内提供担保。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十四条', '抵押权存续期间，抵押权受到或可能受到来自任何第三方的侵害时，经办行应当要求抵押人积极协助处理以避免侵害。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十五条', '抵押权存续期间，未经经办行书面同意，抵押人不得有转移、转让、出租或其他处分抵押物的行为。经办行同意抵押人以本行认可的最低转让价款转让抵押物的，应当要求抵押人以转让抵押物所得的价款优先用以提前清偿所担保的债权。转让价款超过担保债权数额的部分除外。抵押物未经本行同意被转让的，经办行应当要求抵押人以转让抵押物所得的价款优先用予提前清偿所担保的债权，转让价款超过担保债权数额的部分除外。经办行未能以转让抵押物所得的价款优先受偿的，可以要求受让人代替债务人清偿债务；受让人不代为清偿的，经办行应当就该抵押物行使抵押权。抵押物依法被继承或者赠与的，经办行应当向继承人或者受赠人行使抵押权。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十六条', '抵押权存续期间，抵押物被他人申请采取查封、扣押等财产保金或者执行措施的，经办行应当依法及时向采取前述措施的执法机关主张抵押权。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十七条', '抵押权存续期间，有下列情形之一的，经办行应当及时、适当地采取警告、制止、补救等措施，直至请求人民法院或者仲裁机构依法保护抵押权；（一）抵押人未经本行同意擅自处分抵押物的；或者虽经本行同意转让，但未以处分抵押物所得的价款优先用于提前清偿所担保的债权的；或者以明显不合理的低价转让抵押物，对本行要求其提供相应的担保不予理睬的；（二）抵押人的行为足以使抵押物价值减少，但对本行要求其停止行为、恢复抵押物价值或提供与减少的价值相当的担保不予理睬的；（三）抵押人不将因抵押物灭失、毁损或者被征用所获得的保险金、赔偿金或者补偿金优先用以提前清偿所担保的主债权，或者向双方约定的第三人提存的；（四）借款人或抵押人被依法宣告破产的；（五）抵押人有故意阻碍贷款行依法实现抵押权的其他行为的。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第七条', '抵押权的实现'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十八条', '借款合同履行期限届满，借款人未能按期归还贷款本息的，经办行应当按照与抵押人的预先约定或者与抵押人新达成的协议以抵押物折价或者以拍卖、变卖抵押物所得的价款受偿；没有预先约定又协议不成的，经办行应当在诉讼时效内及时提起诉讼或者申请仲裁。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第九十九条', '经办行申请人民法院依法扣押抵押物的，应当及时将扣押抵押物的事实通知应当清偿法定孳息的义务人，并有权自扣押之目起收取由抵押物分离的天然孳息和法定孳息。抵押权的效力及于抵押物的从物、附合物、混合物、加工物，以及因毁损、灭失或被征用而产生的保险金、赔偿金、补偿金，经办行实现抵押权时应当依法对其主张权利。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第三章  贷款抵押担保', '第一百条', '抵押担保的债权部分清偿后，借款人未能清偿剩余债权的，经办行可以就抵押物的全部行使抵押权。经登记的抵押物被分割或者部分转让的，经办行可以就分割或者转让后的抵押物行使抵押权。第一百零一条  主债权被分割或者部分转让的，经办行应当就所享有的债权份额行使抵押权。第一百零二条  经办行与抵押人协议以抵押物折价受偿的，原则上应当由本行认可的中介机构评估后折价。在没有合适的中介机构或者其他特殊情况下，经办行也可以参照市场价格与抵押人协商折价，但须由本行审查确认。第一百零三条  经办行与抵押人协议以拍卖抵押物所得价款受偿的，应当与抵押人共同委托拍卖机构进行拍卖。经办行未与抵押人达成拍卖协议的，应当依法提起诉讼或者申请仲裁后，申请人民法院强制拍卖。第一百零四条  经办行与抵押人协议以变卖抵押物所得价款受偿的，应当与抵押人、买受人就抵押物价格达成一致意见。抵押物为限制流通物的，经办行不能与抵押人协议变卖，只能按照有关法律、法规的规定对该财产进行处理。经办行未与抵押人达成变卖协议的，应当依法提起诉讼或者申请仲裁后，申请人民法院强制变卖。第一百零五条  经办行依法处分抵押物所得的价款，用于清偿实现抵押权的费用、贷款利息和本金。处分抵押物所得价款不足以清偿前款所列金额的，应当就不足部分继续向借款人追偿；清偿前款所列金额后有剩余的，应当将剩余部分退还抵押人。第一百零六条  抵押担保的贷款本息全部清偿的，经办行应当与抵押人向原登记机关办理注销登记，并及时将所保管的抵押物权属证明及有关单证交还抵押人，办妥交接手续。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第四章  贷款质押担保', '第一条', '质物的范围第一百零七条  经办行可以接受符合下列条件的动产质押：（一）出质人享有所有权或依法处分权；（二）依法可以流通、转让；（三）依法可以特定化和转移占有；（四）易变现、易保值、易保管。第一百零八条  经办行可以接受能够以移交经总行确认的，具有合法主体资格的仓储公司或现货交易市场保管，并开具以本行为受益人收据的形式特定化的存货质押。前款所指存货应以大宗、高流动性的原材料为主。第一百零九条  经办行可以接受下列权利质押：（一）本行或与本行有合作协议的其它银行开出的存款单；（二）本行认可的银票、保单、股权、仓单；（三）本行规定的其他权利质押。第一百一十条  经办行在办理贷款质押担保时，应当优先选择价值相对稳定、变现能力较强的质物；对存货及其他价值波动较大、不易监测、不易保管、不易变现或不易办理登记手续的质物应当从严掌握。第一百一十一条  经办行不得接受具有下列情形之一的动产或权利质押：（一）依法禁止流通、转让，或依法不能强制执行和处理的；（二）所有权有争议的；（三）已挂失、失效或被依法止付的；（四）被依法查封、冻结、扣押或采取其他强制性措施的；（五）在质押期间易腐烂、易虫蛀、易变质的；（六）难以判断实际价值或难以变现、保值和保管的；（七）票据或其他权利凭证上记载“不得转让”、“委托收款”、“现金”、“质押”等字样的；（八）已经质押的存款单、仓单；（九）上市公司所有的动产或权利为本公司的股东或者其他个人债务提供质押担保的；（十）具有不宜质押的其他情形。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第四章  贷款质押担保', '第二条', '出质人应提交的材料第一百一十二条  依法对质物享有所有权或者处分权的法人、其他组织或者自然人，可以作为借款人在本行贷款的出质人。第一百一十三条  出质人应当向经办行提交下列材料：（一）营业执照(或事业单位登记表，属于金融、建筑、食品、医药等需要批准方能经营的特殊行业的，须同时提交政府主管部门颁发的经营许可证)及最近年度的报告公示、组织机构代码证、税务登记证明（营业执照为三证合一的，则不需提供组织机构代码证、税务登记证明）；（二）法定代表人身份证明及签字样本或印鉴；（三）法定代表人授权委托书、委托代理人身份证明及签字样本或印鉴；（四）税务登记证明及最近年度的年检证明；（五）公司章程、验资报告(非必有项)；（六）出质人同意提供质押担保的书面文件；（七）出质人对质物享有所有权或者依法处分权的权属证明文件；（八）质物的清单及基本资料，包括质物的名称、数量、质量、状况等；（九）经办行认为需要提交的其他材料，如税务机关出具的抵押人欠税情况证明等。出质人为借款人时，可以不再重复提交本条（一）至（五）项材料。出质人为自然人时，不需提供本条（一）至（六）项材料，但需另行提供下列材料：（一）出质人的有效身份证件(居民身份证或其他有效居留证件)；（二）出质人同意提供质押担保的书面文件。第一百一十四条  以单位定期存单质押的，还应提供以下材料：（一）开户证实书，包括借款人所有的或第三人所有而向借款人提供的开户证实书。开户证实书为第三人向借款人提供的，应同时提交第三人同意由借款人为质押贷款目的而使用其开户证实书的书面文件。（二）存款人委托经办行向存款行申请开具单位定期存单的委托书。（三）存款人在存款行的预留印鉴或密码。（四）以他行开具的单位定期存单质押的，按我行与他行签订的《合作协议》要求提供资料。具体业务按本行《单位定期存单质押贷款管理办法及流程》。第一百一十五条  以本行签发的个人储蓄存单质押的，还应提供以下材料：（一）储蓄存单，包括借款人所有的或第三人所有而向借款人提供的储蓄存单。储蓄存单为第三人向借款人提供的，应同时提交第三人同意由借款人为质押贷款目的而使用其储蓄存单的书面材料。（二）存款人在存款行的预留印鉴或密码。（三）以他行开具的个人储蓄存单质押的，按我行与他行签订的《合作协议》要求提供资料。具体业务按本行《个人定期存单质押贷款管理办法及流程》。第一百一十六条  以仓单质押的，还应提供仓单或仓单持有凭证以及证明仓单开具人的合法主体资格和仓单项下货物的品名、数量、入库检验、保管期限等事项的书面材料，详见本行仓单质押贷款管理办法。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第四章  贷款质押担保', '第三条', '质押担保的调查评审第一百一十七条  经办行应当综合考虑借款人的资信状况、偿债能力、贷款期限、贷款风险度，出质动产所处位置、使用年限、折旧程度、功能状况、估价可信度、变现能力、变现时的价格变动、变现费税等因素，根据质物的不同种类确定合理的动产质押率，并据此与出质人签订质押合同。经办行应当综合考虑质押权利的易变现性、市场价格、商业风险、相关费用等因素，确定合理的权利质押率，并据此与出质人签订质押合同。存单、银票质押的，质押率不得超过其面值的90%；存货、仓单质押按期限长短，结合质押物质押率不得超过产品购入价或成本价的60%至70%。第一百一十八条  经办行在对质押担保进行调查评审时，应当安排双人对出质人及动产质物进行实地核查，形成核查结论，并由出质人签字盖章（或加按指模）、核查人签字确认后作为质押合同附件留存。第一百一十九条  经办行在对质押担保进行调查评审时，应当安排双人就出质存款单向存款行核押，并取得存款行开具的，由负责人签字和加盖单位印章的，载明存单内容真实、预留印鉴或密码一致以及存款行保证未经本行申请不在质押期间受理存款人提出的经办支取或挂失申请等内容的确认书。第一百二十条  经办行在对质押担保进行调查评审时，应当安排双人就出质仓单向期货交易所或仓储公司核押，并取得期贷交易所或仓储公司开具的，由负责人签字和加盖单位公章的，载明仓单内容真实、为出质人合法持有以及期货交易所或仓储公司保证未经本行申请不在质押期间交割、交易、转让、重复质押、注销、挂失、解冻、出库等内容的确认书。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第四章  贷款质押担保', '第四条', '质押合同的订立第一百二十一条  动产质押合同应当包括以下主要内容：（一）被担保的主债权种类、数额；（二）主合同借款人履行债务的期限；（三）质物的名称、数量、质量、状况、所在地、所有权权属或者使用权权属；（四）质押担保的范围；（五）质物的移交、保管和提存；（六）质物的保险；（七）双方的权利和义务；（八）违约责任；（九）合同的生效、变更、解除和终止。第一百二十二条  权利质押合同应当包括以下主要内容：（一）被担保的主债权种类、数额；（二）主合同借款人履行债务的期限；（三）质物的名称、数量、质量、状况、评估价值、所有权权属或者使用权权属；（四）质押担保的范围；（五）质物的移交、保管、提前清偿或提存；（六）质押登记：（七）双方的权利和义务；（八）违约责任；（九）合同的生效、变更、解除和终止。第一百二十三条  以仓单质押的，按本行《仓单质押贷款管理办法及流程》执行。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第四章  贷款质押担保', '第五条', '质押登记与保险第一百二十四条  依本办法规定必须办理出质登记的，经办行应当与出质人在签订质押合同后及时到有关机关办理质押登记手续，取得质押登记证书。质押合同签订后，质押人违背诚实信用原则拒绝办理质押登记的，应当停止发放贷款。第一百二十五条  用于质押的物资必须参加保险，经办行应当要求质押人到有关保险机构按照下列条件办理抵押物的财产保险手续：（一）办理质押物足额财产保险；（二）保险期限不得短于主合同履行期限；（三）保险金额不得小于主合同贷款本息：（四）保险合同及保险单中应当注明，出险时本行为保险赔偿金的第一请求权人；（五）保险单中不得有任何限制本行权益的条款；（六）保险品种必须包括保险物的主要风险。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第四章  贷款质押担保', '第六条', '质押担保的管理第一百二十六条  质押权设定后，经办行应填写《抵、质押品保管凭证》，经质押人确认后，收妥质押物他项权利证书(或其他质押登记文书)、质押物的所有权或使用权凭证、保险单证及其他相关资料。质押物他项权利证书等相关凭证原件，经办行应按规定及时上缴总行保管，经办行只留存复印件，但法律法规或本行另有规定的除外。经办行应当加强对信贷管理系统录入内容及贷款质押担保档案扫描影像上传的管理。第一百二十七条  在质押合同有效期内，经办行应当就下列事项事先要求借款人提交质押人同意继续担保的书面意见：（一）与借款人协议延长借款合同履行期限的；（二）与借款人协议增加贷款金额的；（三）第三人提供质押担保时，经办行许可借款人转让债务的。经办行应当将质押人同意上述事项的书面文件作为质押合同的必要附件。第一百二十八条  在质押合同有效期内，质押物由经办行占管或第三方保管。经办行在占管或第三方保管期间应维护抵押物的完好，不得采用非合理方式使用抵押物而使其价值产生减损。经办行应当按照本行贷后检查的相关规定定期检查质押物管理和变化情况。第一百二十九条  质押权存续期间，质押物发生毁损、灭失的，经办行应当立即采取措施防止损失扩大，并及时向本行提交有关主管机关出具的发生毁损、灭失的原因证明。在质押物灭失、毁损或者被征用的情况下，经办行应当要求出质人以所获得的保险金、赔偿金或者补偿金优先用以提前清偿所担保的主债权，或者向双方约定的第三人提存。质押物灭失后，经办行应当就质押物灭失后所得赔偿金数额不足清偿部分，要求借款人提供新的担保。'),
(NULL, '信贷管理部', '《贷款担保管理办法（试行）》', '第四章  贷款质押担保', '第七条', '质押权的实现第一百三十条  借款合同履行期限届满，借款人未能按期归还贷款本息的，经办行应当按照与质押人的预先约定或者与质押人新达成的协议，以质押物折价或者以拍卖、变卖质押物所得的价款受偿；没有预先约定又协议不成的，经办行应当在诉讼时效内及时提起诉讼或者申请仲裁。第一百三十一条  质押权的效力及于抵押物的从物、附合物、混合物、加工物，以及因毁损、灭失或被征用而产生的保险金、赔偿金、补偿金，经办行实现质押权时应当依法对其主张权利。第一百三十二条  质押担保的债权部分清偿后，借款人未能清偿剩余债权的，经办行可以就质押物的全部行使抵押权。第一百三十三条  主债权被分割或者部分转让的，经办行应当就所享有的债权份额行使质押权。第一百三十四条  经办行与质押人协议以质押物折价受偿的，应当由本行认可的中介机构评估后折价。在没有合适的中介机构或者其他特殊情况下，经办行也可以参照市场价格与质押人协商折价，但须由本行审查确认。第一百三十五条  经办行与质押人协议以拍卖质押物所得价款受偿的，应当与质押人共同委托拍卖机构进行拍卖。经办行未与质押人达成拍卖协议的，应当依法提起诉讼或者申请仲裁后，申请人民法院强制拍卖。第一百三十六条  经办行与质押人协议以变卖抵押物所得价款受偿的，应当与质押人、买受人就抵押物价格达成一致意见。经办行未与质押人达成变卖协议的，应当依法提起诉讼或者申请仲裁后，申请人民法院强制变卖。第一百三十七条  经办行依法处分质押物所得的价款，用于清偿实现质押权的费用、贷款利息和本金。处分质押物所得价款不足以清偿前款所列金额的，经办行应当就不足部分继续向借款人追偿；清偿前款所列金额后有剩余的，经办行应当将剩余部分退还质押人。第一百三十八条  质押担保的贷款本息全部清偿的，经办行应当与质押人向原登记机关办理注销登记，并及时将所保管的质押物权属证明及有关单证交还质押人，办妥交接手续。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第一章  总则', '第一条', '为有效应对本行贷款突发风险，降低贷款突发风险造成的损失，最大程度维护本行权益，制定本预案。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第一章  总则', '第二条', '本预案中的贷款是指本行所有个人客户和公司客户的贷款。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第一章  总则', '第三条', '本预案中突发风险是指由于外部或内部的原因，导致本行贷款风险突然暴露，致使资产可能发生重大损失的状况。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第一章  总则', '第四条', '本预案中贷款突发风险的种类有：（一）外部风险：指客户信用风险，是由于国家政策变化、行业市场变化、自然灾害、担保物价值降低、客户信誉度下降等原因导致本行贷款可能受到损失的风险。（二）内部风险：指员工操作风险，由于内部员工主观故意或过失不当操作致使贷款可能受到损失的风险。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第一章  总则', '第五条', '贷款突发风险应急处置应当遵循以下原则：（一）及时应对原则：一旦发生突发风险，各支行（含部门，下同）应立即启动应急处置预案，采取措施积极应对。（二）实时汇报原则：各级应将突发风险情况和处置进展在第一时间向信贷管理部汇报。（三）联动协作原则：在突发风险处置工作中，涉及到的部门和人员应当相互协作，紧密配合。（四）严格保密原则：在相关风险处置过程中，总行知情人员应保守秘密，未经贷款突发风险应急处置工作领导小组批准，不得对外透露风险及处置情况。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第二章  组织体系及职责', '第六条', '本行设立贷款突发风险应急处置工作领导小组，领导小组组长由本行行长担任，副组长由副行长担任，成员由信贷管理部、授信评审部、风险管理部、合规管理部、审计稽核部、监察保卫部等部门负责人组成。贷款突发风险应急处置工作领导小组下设办公室，办公室设在风险管理部，由分管行长任办公室主任，负责领导办公室的日常工作。公司业务部、小微业务部、各支行（以下统称“支行”）应成立贷款突发风险应急处置工作小组，支行负责人任小组组长，并为贷款突发风险应急处置工作的第一责任人。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第二章  组织体系及职责', '第七条', '工作职责（一）贷款突发风险应急处置工作领导小组的工作职责：1.制定应急处置工作领导机构成员工作职责；2.负责审议本行贷款突发风险应急处置预案；3.决定启动和终止应急处置预案；4.统一指挥应急处置工作；5.调配各类应急处置资源；6.决定向系统内上级机构报告突发风险及其处理情况；7.决定向银监、人行、省联社、政府等职能部门报告应急处置相关事项；8.审定应急处置信息披露事项；9.总结应急处置工作经验教训；10.决策应急处置工作的其他重要事项。（二）贷款突发风险应急处置工作领导小组办公室的工作职责：1.有计划组织实施、督促检查应急处置预案的演练等预警工作；2.接收支行上报的有关突发风险信息，提出突发风险级别判定意见；3.制订或审查支行应急处置议案的可行性； 4.组织实施应急处置预案，并限时完成保全、诉讼、执行、处置等相关工作；5.指导督促支行应急处置预案的执行，及时向领导小组报送处理结果；6.根据领导小组的决定，联系银监、人行、省联社、政府等职能部门协调处理突发风险，及时报送有关处置情况；7.按权限对外披露相关信息；8.收集、整理、保管相关档案资料；9.完成应急处置工作领导小组交办的其他事项。（三）支行贷款突发风险应急处置工作小组的工作职责： 1.及时向贷款突发风险应急处置工作领导小组办公室上报有关应急处置信息；2.制订并组织实施本支行的贷款突发风险应急处置预案；3.根据实际情况，经贷款突发风险应急处置工作领导小组办公室同意，联系相关系统外职能部门协助处理突发风险；4.落实应急处置预案的演练工作；5.完成总行领导小组交办的其他应急处置工作事项。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第三章  贷款突发风险级别界定', '第八条', '级别划分根据贷款突发风险可能给本行造成的损失程度，将突发风险分为四个级别：（一）特大贷款突发风险（Ⅳ级）指突发风险可能给本行造成损失或特大声誉损失或涉及犯罪，贷款金额在1000万元（含）以上的。（二）重大贷款突发风险（Ⅲ级）指突发风险可能给本行造成损失或重大声誉损失或涉及一般违法行为，贷款金额在500万元（含）以上1000万元以下的。（三）较大贷款突发风险（Ⅱ级）指突发风险可能给本行造成损失或较大声誉损失或涉及重大违规，贷款金额在50万元（含）以上500万元以下的。（四）较轻贷款突发风险（Ⅰ级）指突发风险可能给本行造成损失或较大声誉损失或涉及重大违规，贷款金额在50万元以下的。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第三章  贷款突发风险级别界定', '第九条', '处置权限。对特大、重大、较大贷款突发风险由总行应急处置工作领导小组办公室具体负责；较轻贷款突发风险由支行贷款突发风险应急处置工作小组向贷款突发风险应急处置工作领导小组办公室报送突发风险情况及处置方案，并组织实施。贷款突发风险应急处置工作领导小组办公室应密切监控贷款突发风险的处置过程和形势发展，当突发风险事态激化或缓解时，可相应按照升级或降级后的级别处理。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第四章  贷款突发风险应急处置', '第十条', '贷款突发风险发生后，支行贷款突发风险应急处置工作小组应在第一时间报告总行信贷管理部，由信贷管理部负责向本行贷款突发风险应急处置工作领导小组汇报。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第四章  贷款突发风险应急处置', '第十一条', '总行贷款突发风险应急处置工作领导小组视具体情况可以决定采取相应的应急处置措施。所采取的措施应当在依法合规的前提下包括但不限于以下种类：1.支行落实专人对突发风险进行跟踪管理；2.立即调查核实突发风险的情况，取得相关证明资料，及时向上级机构汇报，并随时监督风险发展情况；3.根据风险情况，采取资产保全措施，如冻结资金账户、申请法院诉前财产保全、向公安机关报案以及申请外部相关部门给予协助等。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第五章  贷款突发风险预防机制', '第十二条', '总行指定风险管理部负责贷款风险预警信息的跟踪收集、识别报告及实施工作。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第五章  贷款突发风险预防机制', '第十三条', '风险管理部根据贷款风险应急处置工作领导小组的要求及时将相关资料报送监察保卫部，监察保卫部按程序上报银监、人行、省联社和政府相关职能部门。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第六章  贷款突发风险应急处置总结评价', '第十四条', '原因调查。贷款突发风险应急处置工作结束后，总行及其支行应组织调查事件发生的深层原因，查找工作不足。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第六章  贷款突发风险应急处置总结评价', '第十五条', '评估总结。总行应对贷款突发风险发生的原因、应对措施、处理经过、处理结果进行总结，吸取经验教训，提高服务风险防范能力，并形成总结报告。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第六章  贷款突发风险应急处置总结评价', '第十六条', '奖励与问责。（一）对在重大突发事件应急管理和处置工作中作出突出贡献的集体和个人给予表彰和奖励。（二）对有下列情形之一的，严格按照有关规定追究相关责任人的责任：1.未及时发现贷款突发事件，并采取有效的风险防范和资产保全措施，造成风险加大或损失的。2.未按本行要求及时、积极采取有效措施，至使贷款风险程度加大，造成经济损失的。3.故意拖延处置时间，致使贷款风险未及时处置、造成风险蔓延，导致经济损失的。4.尽管已采取了相应的处置措施，但未按照要求报告的。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第七章  附则', '第十七条', '本预案由信贷管理部负责解释、修订。'),
(NULL, '信贷管理部', '《贷款突发风险应急处置预案》', '第七章  附则', '第十八条', '本预案自印发之日起执行。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第一章  总则', '第一条', '为规范本行银团贷款业务，有效控制和化解风险，优化信贷结构，更好地为重点企业和项目提供融资服务，根据《中华人民共和国商业银行法》、《贷款通则》、《银团贷款业务指引》等有关法律法规，结合本行《信贷管理制度》、《贷款业务尽职管理办法》规章制度，制定本办法。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第一章  总则', '第二条', '本办法所称银团贷款是指由两家及两家以上银行基于相同贷款条件，依据同一贷款协议，按约定时间和比例，通过代理行向借款人提供的人民币贷款或授信业务。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第一章  总则', '第三条', '开办银团贷款业务，应当遵守国家有关法律、法规，符合国家信贷政策，坚持平等互利、公平协商、诚实履约、风险自担的原则。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第四条', '公司业务部负责银团贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第五条', '授信评审部负责对银团贷款业务的授信管理。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第六条', '信贷管理部负责拟定银团贷款管理办法，对银团贷款业务的用信管理和贷后检查的管理监督。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第七条', '运营管理部根据权限负责对银团贷款用款进行审查、支付。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第八条', '计划财务部负责制定利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第九条', '风险管理部负责对银团贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第十条', '合规管理部负责审核银团贷款办法和流程的合规性和有效性。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第十一条', '资产保全部负责对不良贷款专业清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第二章  组织管理体系', '第十二条', '审计部负责对银团贷款进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第三章  贷款对象、用途和期限', '第十三条', '向本行申请银团贷款的借款人应本行有关信贷管理制度规定的借款人条件外，还需具备以下条件：（一）在参加银团贷款成员银行开立银行结算账户；（二）还款记录良好，近三年内没有发生拖欠贷款本息的情况；（三）符合国家产业政策要求，优先考虑与农业产业化经营和农村经济结构调整相关的企业；（四）符合法律法规规定的其他条件。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第三章  贷款对象、用途和期限', '第十四条', '本行应根据国家产业政策、地方发展规划、经营管理能力确定银团贷款投向，主要用于以下方面：（一）企业流动资金贷款；（二）企业购置固定资产、技术更新改造、设备租赁等中长期贷款；（三）现金流量充足、能够按期还本付息的农业和农村基础设施项目。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第三章  贷款对象、用途和期限', '第十五条', '本行牵头或参加的银团贷款最长期限不超过十年，超过一年的中长期银团贷款每年至少还款两次。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第四章  银团贷款成员', '第十六条', '按照在银团贷款中的职能和分工，银团贷款成员分为牵头行、代理行和参加行等角色，也可根据实际规模与需要在银团内部增设副牵头行等，并按照银团贷款相关协议履行相应职责。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第四章  银团贷款成员', '第十七条', '牵头行是指受客户委托发起组织银团贷款，负责分销银团贷款份额的银行，是银团贷款的组织者和安排者。主要职责是：（一）发起和筹组银团贷款，并分销银团贷款份额；（二）对借款人进行贷前尽职调查，草拟银团贷款信息备忘录，并向潜在的参加行推荐；（三）代表银团与借款人谈判确定银团贷款条件；（四）代表银团聘请相关中介机构起草银团贷款法律文本；（五）组织银团贷款成员与借款人签订书面银团贷款协议；（六）协助代理行进行银团贷款管理；（七）银团协议确定的其他职责。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第四章  银团贷款成员', '第十八条', '本行单家担任牵头行时，承贷份额不少于银团融资总金额的20％，分销给其他银团贷款成员的份额一般不少于50%。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第四章  银团贷款成员', '第十九条', '按照牵头行对贷款最终安排额所承担的责任，银团牵头行分销银团贷款可以分为全额包销、部分包销和尽最大努力推销三种类型。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第四章  银团贷款成员', '第二十条', '代理行是指受银团委托负责贷款管理的银行，由牵头行确定或牵头行与借款人协商确定，一般由项目所在地的银行或借款人基本存款账户开户行或一般存款账户开户行担任。代理行可以由牵头行担任，也可由银团贷款成员协商确定。主要职责是：（一）审查、督促借款人落实贷款条件，并提供贷款或办理其他授信业务；（二）办理银团贷款的抵（质）押担保手续，并负责抵（质）押物的日常管理工作；（三）制作账户管理方案，开立专门账户管理银团贷款资金，对专户资金的变动情况进行逐笔登记；（四）根据约定用款日期或借款人的用款申请，按照银团贷款协议约定的承贷份额比例，通知银团贷款成员将款项划到指定账户；（五）划收银团贷款本息和代收相关费用，并按承贷比例和银团贷款协议约定及时划转到银团贷款成员指定的账户；（六）负责银团贷款贷后管理和贷款使用情况的监督检查，并定期向银团贷款成员通报；（七）密切关注借款人财务状况，特别是贷款期间发生企业并购、股权分红、对外投资、资产转让、债务重组等影响借款人还款能力的重大事项时，应在获借款人通知之日起三个营业日内，按银团贷款协议约定以专项报告形式通知各银团贷款成员；（八）借款人出现违约事项时，应及时组织银团贷款成员对违约贷款进行清收、保全、追偿或其他处置；（九）组织召开银团会议，协调银团贷款成员之间的关系；（十）接受各银团贷款成员不定期的咨询与核查，办理银团会议委托的其他事项等。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第四章  银团贷款成员', '第二十一条', '参加行是指接受牵头行邀请，参加银团并按照协商确定的承贷份额向借款人提供贷款的银行。参加行主要职责是参加银团会议，按贷款协议履行其权利和义务。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第四章  银团贷款成员', '第二十二条', '担保代理行是指在担保结构比较复杂的银团贷款中，负责落实银团贷款的各项担保及其抵（质）押物转让、管理等工作的银行。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第二十三条', '有下列情形之一的大额贷款，可考虑采取银团贷款方式：（一）大型集团客户和大型项目的融资以及各种大额流动资金的融资；（二）单一企业或单一项目的融资总额超过本行资本金余额10%的；（三）单一集团客户授信总额超过本行资本金余额15%的；（四）借款人以竞争性谈判选择银行业金融机构进行项目融资的。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第二十四条', '银团贷款的发起由借款人或银行等提出。经借款人同意或选定的牵头行，应与借款人谈妥银团贷款的初步条件，并出具载明这些条件的银团贷款预约书。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第二十五条', '本行作为牵头行应按照本行《贷款业务尽职管理办法》的要求，对借款人或贷款项目进行贷前尽职调查，并在此基础上与借款人进行前期谈判，商谈贷款的用途、额度、利率、期限、担保形式、提款条件、还款方式和相关费用等，并据此编制银团贷款信息备忘录。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第二十六条', '银团贷款信息备忘录是本行作为牵头行在贷前调查基础上协助借款人编制，并由本行分发给潜在参加银行，作为潜在参加银行审贷和提出修改建议的重要依据之一。银团贷款信息备忘录内容主要包括：银团贷款的基本条件、借款人的法律地位及概况、借款人的财务状况、项目的概况及市场分析、项目的财务现金流量分析、担保人/担保物介绍、抵押品物权、风险因素及避险措施、项目的准入审批手续及有权环保机构出具的环境影响监测评估文件等。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第二十七条', '本行作为牵头行在编制信息备忘录过程中，应如实向潜在参加行披露借款人的全部真实信息，并确保信息备忘录中所包括的资料在实质上是真实、完整和正确的。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第二十八条', '本行作为牵头行在向其他银行发送信息备忘录前，信息备忘录应经借款人及担保人审阅，并签署“对信息备忘录所载内容的真实性、完整性负责”的声明。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第二十九条', '为提高信息备忘录等银团资料的独立性、公正性和真实性，本行作为牵头行可聘请外部中介机构如会计师事务所、资产评估事务所、律师事务所及相关技术专家负责评审编写有关信息及资料、出具意见书。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十条', '本行作为牵头行经与借款人协商，向潜在参加银行发出银团贷款邀请函，并随附贷款条件清单、信息备忘录、保密承诺函、贷款承诺函等文件。银团贷款邀请函是本行作为牵头行向潜在参加银行发出的要约邀请，是本行代表借款人，按照列示的主要贷款条件，邀请其参加银团贷款的有效法律文件。贷款条件清单中应列明银团贷款的基本条款，主要包括贷款用途、金额、币种、期限、利率、还款方式、贷款担保等内容。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十一条', '本行收到贷款邀请函，应按照“信息共享、独立审贷、自主决策、风险自担”的原则，根据银团贷款信息备忘录的内容，在全面掌握借款人相关信息的基础上做出是否参加银团贷款的决策。当备忘录信息不足以满足潜在银团贷款参加行的审批要求时，可要求牵头行追加提供相关信息或提出相关工作建议，必要时直接进行相关调查。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十二条', '本行作为牵头行应根据被邀请行实际反馈的情况，合理确定各银团成员的贷款份额。在超额认购或认购不足的情况下，可按事先约定的条件或与借款人协商后重新确定各银团成员行的承贷份额。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十三条', '本行作为牵头行或有意向成为银团贷款成员，应在协商议定的基础上，与其他成员签订银团贷款协议。银团贷款协议是银团贷款成员与借款人、担保人根据有关法律、法规，经过协商后共同签订，主要约定银团贷款成员与借款人、担保人之间的权利义务关系的法律文本。银团贷款各有关当事人代表应分别在贷款协议上签字，并加盖各单位的公章。银团贷款协议应包括以下主要条款：（一）当事人基本情况，主要包括借款人、担保人、牵头行、代理行、参加行的名称及处所；（二）定义及解释，对协议中特定用语的含义进行界定和解释；（三）与贷款有关的约定，包括贷款金额与币种、贷款期限、贷款利率、贷款用途、还款方式及还款资金来源、贷款担保组合、贷款展期条件、提前还款约定等；（四）银团各成员承诺的贷款额度及贷款划拨的时间；（五）提款先决条件；（六）费用条款；（七）代理行的权利义务；（八）财务约束条款；（九）非财务承诺，包括资产处置限制、业务变更和信息披露等条款；（十）违约事件及处理；（十一）有关银团会议的召集及银团会议决定的约定；（十二）适用法律；（十三）其他附属文件。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十四条', '银团贷款成员之间权利义务关系可在银团贷款协议中约定，也可另行签订《银团内部协议》（或称为《银团贷款银行间协议》等）。银团贷款成员间权利义务关系主要包括：银团贷款成员内部分工，权利与义务，银团贷款额度的分配，银团贷款额度的转让；银团会议的议事规则；银团贷款成员的退出和银团解散；违约行为及责任；解决争议的方式；法律法规要求银团贷款成员认为有必要约定的其他事项。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十五条', '银团贷款协议签订后银团即告成立。签订协议的银行均为银团的成员行。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十六条', '本行作为牵头行和代理行在开展银团贷款业务时，不得损害成员行的合法权益。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十七条', '本行作为银团贷款成员应严格按照贷款的约定，及时足额划付贷款款项，按照贷款合同履行职责和义务。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第五章  贷款发起和筹组', '第三十八条', '借款人应严格按照银团贷款协议约定，保证贷款用途，及时向代理行划转贷款本息，如实向银团贷款成员提供有关情况。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第六章  银团贷款的发放和收回', '第三十九条', '银团贷款采取“认定总额、各成员分担”的方式办理。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第六章  银团贷款的发放和收回', '第四十条', '对银团贷款的分担金额按“自愿认贷、协商确定”的原则进行。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第六章  银团贷款的发放和收回', '第四十一条', '银团贷款根据贷款种类分别按相应的贷款管理办法办理。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第六章  银团贷款的发放和收回', '第四十二条', '银团贷款利息按人民银行规定的贷款利率和办法计收，具体按照银团贷款协议书执行。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第六章  银团贷款的发放和收回', '第四十三条', '贷款的发放和本息收回，由代理行办理。贷款发放时，牵头行、各参加行应按协议的约定，将款项划到借款人在代理行的专门账户。本息的收回，由借款人按照协议约定及时归还代理行，代理行及时按比例划入到牵头行和各参加行。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第六章  银团贷款的发放和收回', '第四十四条', '贷款到期，借款人应按期如数归还贷款本息。借款人提前还款的，应至少在最近一个预定还款日的60个营业日前通知代理行，并在征得银团贷款成员同意后，根据银团贷款协议所列的相关贷款余额的到期次序，按后到期先还的原则偿还最后期贷款本息，对提前还款的，可按银团贷款协议的约定收取违约金。如不能按期全额归还银团贷款时，对已归还的部分，代理行应依照协议约定，根据牵头行、参加行的贷款份额按比例分别划归牵头行、参加行，逾期部分的罚息由代理行按银团贷款协议的约定向借款人计收。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第六章  银团贷款的发放和收回', '第四十五条', '贷款到期不能按期归还但符合展期条件，借款人应在贷款到期前申请贷款展期。本行作为银团成员，应按权限进行审批。担保贷款申请展期，还应出具担保人同意展期并继续担保的书面证明。展期贷款条件不得低于原贷款条件。短期贷款展期不超过原期限，中期贷款展期不超过原期限的一半，长期贷款展期最长不超过3年。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第七章  银团贷款管理', '第四十六条', '银团贷款的日常管理工作主要由代理行负责。代理行负责银团贷款协议签订后的组织与实施。代理行必须对银团贷款的使用情况进行认真的检查和监督，落实各项措施，核实借款人及担保人的经济效益和还款能力等有关情况，定期向贷款银团成员通报贷款使用情况，按时通知还本付息等有关事项，并接受贷款银团成员的咨询与核查。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第七章  银团贷款管理', '第四十七条', '牵头行要协助代理行跟踪了解项目的进展情况，及时发现银团贷款项目可能出现的问题，并尽快以书面形式通知贷款银团成员，召开银团会议共同寻求解决办法。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第七章  银团贷款管理', '第四十八条', '银团贷款成员要严格按照贷款协议的规定，及时足额划付贷款款项，按照贷款协议履行其职责和义务。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第七章  银团贷款管理', '第四十九条', '银团贷款存续期间，通常由牵头行或代理行负责定期召开银团会议，也可由三分之一以上的银团贷款成员共同提议召开。银团会议的主要职能是讨论、协商银团贷款管理中的重大问题。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第七章  银团贷款管理', '第五十条', '银团会议商议的重大事项主要包括：对代理行进行尽职评估、修改银团贷款协议、调整贷款额度、变更担保、变动利率、终止银团贷款、通报企业并购和重大关联交易、认定借款人违约事项、贷款重组和调整代理银行等。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第七章  银团贷款管理', '第五十一条', '银团贷款存续期间，本行作为银团贷款成员一般不得在银团之外向同一项目提供有损银团其他成员利益的贷款或其他授信。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第七章  银团贷款管理', '第五十二条', '借款人必须按照银团贷款协议的有关规定，保证按约定用途使用贷款，及时向代理行划转贷款本息，如实向银团贷款成员提供有关情况。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第八章  违约处理', '第五十三条', '借款人有下列情况之一的，构成违约行为：（一）所提供的有关文件被证实无效；（二）未能履行和遵守协议所约定的义务及所作的承诺；（三）未能按协议约定支付利息和本金；（四）以假破产等方式逃废债务，逃避银行监督；（五）违反监管法规和银团贷款协议约定的其他违约行为。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第八章  违约处理', '第五十四条', '对于借款人的违约行为，通知整改而未整改，代理行应负责召集银团会议。银团会议协商后的处罚决定，以书面形式通告借款人及其保证人。其处罚可依法采取停止发放贷款、提前收回贷款等办法。必要时向人民法院提起诉讼。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第八章  违约处理', '第五十五条', '贷款人有下列情况之一的，构成违约行为：（一）银团贷款成员收到代理行按协议约定的时间发出的划款通知后，未按协议约定划付款项的；（二）贷款银团参加行擅自提前收回贷款的。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第八章  违约处理', '第五十六条', '代理行有下列情况之一的，构成违约行为：（一）因代理行管理不善造成贷款损失的；（二）借款人归还银团贷款本息而代理行不如约向贷款银团成员划付款项的。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第八章  违约处理', '第五十七条', '经银团贷款会议审核认定违约的，可以对违约银团成员做出违约赔偿处理。情节严重的，应承担法律责任。银团成员之间的上述纠纷，不影响银团与借款人所定贷款协议的执行。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第九章  贷款收费', '第五十八条', '银团贷款收费是指银团成员接受借款人委托，为借款人提供财务顾问、贷款筹集、信用保证、法律咨询等融资服务而收取的相关中间业务费用。本行收取的费用纳入中间业务管理。银团贷款收费应按照“自愿协商、公平合理、质价相符”的原则由所有银团成员和借款人协商确定，并在银团贷款协议或费用函中载明。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第九章  贷款收费', '第五十九条', '银团收费的具体项目可包括安排费、承诺费、代理费等。银团费用仅限为借款人提供相应服务的银团贷款成员享有。安排费一般按银团贷款总额的一定比例一次性支付；承诺费一般按未用余额的一定比例，每年以银团贷款协议约定的方式收取；代理费可根据代理行的工作量按年支付。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第九章  贷款收费', '第六十条', '银团贷款的收费应遵循“谁借款、谁付费”的原则，由借款人支付。其费用种类和金额由借贷双方协商确定，不得在利率基础上加点确定。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第九章  贷款收费', '第六十一条', '牵头行不得向银团贷款成员附加任何不合理条件，不得以免予收费的手段，开展银团贷款业务竞争，不得借筹组银团贷款向银团贷款成员和借款人搭售其他金融产品或收取其他费用。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十章  不良贷款的处置', '第六十二条', '银团贷款划为不良贷款，或目前虽被划为关注类，但如不及时采取措施极有可能影响贷款本息偿还时，本行作为代理行应迅速组织召开银团会议。银团会议上应确定管理和处置不良贷款方案，并负责具体实施。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十章  不良贷款的处置', '第六十三条', '本行作为银团贷款成员行，如到期债权借款人无法部分或全部清偿，应通过代理行向借款人、担保人进行追索，必要时可直接向借款人、担保人追索。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十章  不良贷款的处置', '第六十四条', '在借款合同规定的借款期内，借款人被宣布破产并被清算，本行与其他成员行按债权比例同等受偿。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十章  不良贷款的处置', '第六十五条', '当借款人逾期还款时，归还的款项不足以清偿所欠贷款的，本行与其他成员行按各自在银团贷款中所占比例受偿。本行作为代理行，对逾期部分的罚息应按照合同约定统一向借款人计收，并按逾期贷款的额度在其他成员行中分配。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十一章  外部监督和管理', '第六十六条', '银团贷款的会计数据由各成员行分别进行统计，但应加以说明。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十一章  外部监督和管理', '第六十七条', '在银团贷款中的出资额必须符合银监会对单户贷款监管的比例和限额规定。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十一章  外部监督和管理', '第六十八条', '本行不良银团贷款应及时向省联社报告。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十二章  附则', '第六十九条', '本行牵头、参与的银团贷款授信、用信应按本行转授权管理办法执行，其他业务操作及管理按同类贷款相关规定执行，如与国家法律、法规及相关规定不一致的地方，以国家法律、法规及相关规定为准。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十二章  附则', '第七十条', '本办法由信贷管理部负责制定、解释和修改。'),
(NULL, '信贷管理部', '《银团贷款管理办法》', '第十二章  附则', '第七十一条', '本办法自印发之日起执行。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第一章  总则', '第一条', '为解决项目经理的资金需求，进一步规范本行项目经理贷款的管理，保证项目经理贷款的稳健发展，根据《个人贷款管理暂行办法》等法律法规和本行的相关制度规定，特制定本办法。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第一章  总则', '第二条', '本办法所称的项目经理贷款是指本行向从事土木工程、房屋建设、房屋装潢、水电安装、路桥工程等业务的项目经理或项目承包人发放的用于解决其所承包的工程项目施工所需资金的贷款。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第一章  总则', '第三条', '项目经理贷款应遵循资格认定、项目评估、贷款核定、按实发放、项目竣工、贷款收回的原则。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第一章  总则', '第四条', '本办法适用于本行辖区内各支行、营业部、小微业务部、公司业务部（以下简称“经办行”）。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第五条', '经办行负责项目经理贷款业务的受理以及贷款“三查”的具体实施。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第六条', '授信评审部负责对项目经理贷款业务的授信管理。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第七条', '信贷管理部负责拟定项目经理贷款管理办法及操作规程，对项目经理贷款业务的用信管理和贷后检查进行管理监督。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第八条', '运营管理部根据权限负责对项目经理贷款用款进行审查。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第九条', '计划财务部负责制定项目经理贷款业务利率定价办法，并且对利率执行进行监督检查。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第十条', '风险管理部负责对项目经理贷款的风险的监测、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第十一条', '合规管理部负责审核项目经理贷款管理办法和操作规程的合规性和有效性。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第十二条', '资产保全部负责对项目经理贷款业务形成的不良贷款清收、保全以及相关管理。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第二章  组织管理体系', '第十三条', '审计部负责对项目经理贷款业务进行审计稽核和监督检查。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第三章  贷款对象和条件', '第十四条', '项目经理贷款的对象应为国家规定可以作为借款人的、具有完全民事行为能力的自然人，且借款人年龄加上贷款期限不超过65周岁。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第三章  贷款对象和条件', '第十五条', '向本行申请项目经理贷款应具备本行有关个人贷款管理规定的条件外，还应具备以下条件：（一）遵纪守法、信用良好、有较强的经济实力和较好的建筑施工管理能力，无不良嗜好；（二）申请人须具有本行辖区内户籍或有申请人房屋产权，证明该项目经理在上述范围内具有常住性；（三）所承接的项目符合国家产业、土地、环保等相关政策，一般选择土木工程、房屋建设、路桥工程、房屋装潢、水电安装等施工业务；（四）具有注册建造师二级及以上资质；（五）已承包过二个（含）以上工程项目的施工业务，无拖欠工人工资、无重大质量事故和安全责任事故发生，无其他纠纷；（六）在本行开立个人结算账户；（七）能提供足值、有效的抵（质）押或第三方连带责任保证；（八）所隶属或挂靠的建筑企业需经我行认可且具备三级及以上资质；（九）本行规定的其他条件。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、用途', '第十六条', '项目经理贷款额度根据申请人的资金实力、承接工程量、工程付款进度、担保及资金需求情况确定。贷款额度的确定必须遵循下列原则：（一）贷款额度不超过承接工程合同价的10%；（二）贷款额度不超过项目经理家庭净资产的50%；（三）以抵质押方式申请的，根据项目经理的信用状况、偿债能力、抵质押财产的价值等综合确定，贷款额度一般不超过500万元；（四）以所隶属或挂靠的公司保证的，贷款额度一般不超过300万元。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、用途', '第十七条', '贷款期限。根据施工合同中项目工期合理确定贷款期限，不得超过施工合同约定的工程款支付日期。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、用途', '第十八条', '贷款利率。根据本行贷款利率定价办法执行。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第四章  贷款额度、期限、利率、用途', '第十九条', '贷款用途。主要为项目添置工程设备及材料采购。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十条', '项目经理贷款操作程序为借款人申请、贷款受理与调查、贷款授信审查审批、贷款用信审查审批、合同签订、贷款发放与支付、贷后检查及监管、贷款偿还等阶段。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十一条', '申请人提出贷款申请时需要提交贷款申请及以下材料：（一）借款人及配偶的有效身份证明及户籍证明（或居住证明）、婚姻证明；（二）借款人的家庭资产证明，包括但不限于房产证明、车辆证明、存款证明及有价证券等；（三）借款人的注册建造师资质证书，承建项目建筑企业的资质证书、营业执照；（四）项目建设施工合同、建筑企业与项目经理签订的项目承包（分包）合同以及项目承包保证金支付凭证，购买建筑材料等涉及贷款用途的购销合同；（五）项目施工许可证等承建项目工程的各项批文；（六）抵押、质押财产权属证明或保证人资料；（七）本行认为需要提交的其他材料。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十二条', '经办行客户经理在受理贷款后应对申请人的资料原件进行审核，在取得借款人及配偶书面授权后打印其个人征信报告，同时结合借款人及配偶在我行的综合信息查询情况，根据借款人的资信、实力、人品、所承建的工程项目、所属建筑单位的情况，撰写调查报告。重点对以下四个方面做调查：（一）借款人基本情况、从业经历、资产负债情况、个人品行及信用情况；（二）借款人所承建的工程项目情况、工程项目手续的合法完整性、建筑企业的结算模式、所属的建筑企业经营情况和资产实力、发包方的付款能力；（三）担保人的资产负债情况和担保能力，抵押物的评估及变现能力；（四）工程进度与还贷资金来源的预测。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十三条', '贷款的授信、用信审查审批按本行《转授权管理办法》、《信贷业务操作规程》执行。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十四条', '抵押物的管理要遵循专人办理、总行保管、定期核对的原则，对于异地抵押物，经办行需指定双人负责具体的抵押登记及实物管理工作。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十五条', '经有权人审批同意后，可与借款人签订相关借款合同和担保合同，签定合同必须落实双人面签手续，手续办妥后办理授信额度或贷款发放手续。经办行需根据指定的授信用途，采用受托支付的方式放款。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十六条', '经办行要对资金流向进行监控，确保资金用于添置工程设备或材料采购，并要求借款人根据资金的划转情况提供相应的供货合同和发票。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十七条', '贷后检查。客户经理对项目经理贷款发放后15日内，对贷款使用情况进行首次跟踪检查，并形成书面贷后检查记录。日常检查需按月进行，采用现场和非现场结合的方式（现场检查每季至少一次），重点关注借款人所承包项目的进展情况、质量安全、工程回款、对外负债变动等情况，对检查中发现的风险预警信号，应尽早提出风险防范、控制和化解措施，并将检查报告上报总行。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十八条', '项目经理贷款的催收、重组及不良贷款管理按照本行相关规定执行。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第五章  贷款操作程序', '第二十九条', '档案管理。经办行应按本行信贷档案管理有关规定收集、保管相关资料。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第六章   附则', '第三十条', '本办法由信贷管理部负责修订、解释和修改。'),
(NULL, '信贷管理部', '《项目经理贷款管理办法及操作规程》', '第六章   附则', '第三十一条', '本办法自印发之日起实施。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第一章  总则', '第一条', '为规范xxx银行（以下简称“本行”）内部规章文件审查工作程序，保证内部规章文件的合法、合规，制定本办法。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第一章  总则', '第二条', '本办法所称内部规章文件审查是指内部规章文件在本行领导签发前由合规管理部对其进行的合规性审查。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第一章  总则', '第三条', '本办法适用于以本行名义颁发的各种管理性规章制度、办法，主要包括：规章制度、管理办法、实施细则、流程等文件。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第四条', '内部规章文件的审查依据包括法律法规、部门规章、规范性文件以及本行已制定的规章制度。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第五条', '对内部规章文件的审查内容：（一）是否与法律法规、部门规章、规范性文件的规定一致；（二）与本行已有规章制度否统一、衔接；（三）文件规定是否明确；（四）程序是否符合规定。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第六条', '内部规章文件应在银丰平台由拟搞人提起发文，必须述明制定（修订）原因和背景，选择“制度办法”选项，是否选择会签、哪些部门会签根据实际情况确定，提交拟稿部门负责人审核。审核不同意的退回拟稿经办人员；审核同意的提交相关部门会签，无会签的直接提交合规管理部审查。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第七条', '合规管理部实行复审制。经相关部门会签后，交由合规审查。合规人员初审后提交合规管理部负责人复审。涉及重大、复杂法律法规、程序以及较专业的问题的内部规章文件，审查人员可以建议合规管理部集体讨论，形成审查意见。必要时，合规管理部可以聘请专业律师、专业技术人员和其他相关有资质人员发表意见。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第八条', '合规管理部对内部规章文件进行审查，可直接对具体内容修改，也可提出意见交由拟稿部门修订。合规管理部审查认为制定依据存在明显问题，或者存在整体上缺乏可操作性等情况，经合规管理部负责人复审后可予驳回。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第九条', '经合规审查后，提交办公室核稿。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第十条', '办公室核稿后，交由拟稿人提起领导审核、审批。根据业务是否涉及本条线外的其他领导分工，不涉及的选择本条线分管领导审核，涉及的选择本条线分管领导以及业务相关领导会签。分管领导审核或者相关领导会签后，提交总行行长签发。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第十一条', '总行提供学习平台，供员工查阅制订出台的规章文件。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第十二条', '经审查的内部规章文件在履行过程中如发生补充、修改等重大变更，须重新履行审查程序。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第十三条', '属于应经合规审查范围内的内部规章文件，未经合规审查直接施行的，给予经办部门负责人经济处罚或行政处分。合规审查人员不正确履行职责而造成重大风险或经济损失的，应给予审查人员经济处罚或纪律处分。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第二章  具体规定', '第十四条', '合规管理部按季下载制度办法，按年制作目录清单，实行电子和纸介质双保管。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第三章  附则', '第十五条', '本办法未尽事宜以及与国家有关法律法规、规章的规定冲突的，按国家法律法规、规章等规定执行。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第三章  附则', '第十六条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《内部规章文件审查办法》', '第三章  附则', '第十七条', '本办法自印发之日起施行。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第一章  总则', '第一条', '为保证xxx银行（以下简称“本行”）内部规章的合规性、严谨性和适用性，对内部规章实行规范管理，不断完善本行内部规章体系，根据工作实际，制定本办法。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第一章  总则', '第二条', '本办法所称内部规章是指本行职能部门根据管理职权，以本行名义制定发布的用以规范本行各项内部经营管理工作关系的具有全局普遍约束力的内部工作制度、细则、办法、规定、标准、通知及其他规范性文件等。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第一章  总则', '第三条', '本办法所称“管理”是指对内部规章起草、审定、发布、评估、反馈、解释、修改、清理、废止、汇编等过程进行全面、系统的管理。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第一章  总则', '第四条', '内部规章的管理工作坚持立、改、废并重，对内部规章进行经常性的清理、修改，使其不断完善。建立健全内部规章的全员参与机制，鼓励员工对内部规章的起草以及修改工作提出合理意见或建议。建立完善内部规章的汇编管理制度，方便查阅使用，促进内部管理工作效率和水平的不断提高。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第一章  总则', '第五条', '本行合规管理部负责监督内部规章的管理工作。本行办公室负责内部规章的公文处理有关工作，并负责有关内部规章的组织工作。本行监察部门负责对内部规章的管理工作实施效能监察。本行职能部门负责内部规章的拟定、会签。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第二章  体例', '第六条', '内部规章的名称应当准确、规范，并符合上级有关公文制定规范及《xxx银行机关公文处理办法》的规定（以下简称《公文处理办法》）。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第二章  体例', '第七条', '内部规章的内容应当科学、合理、完备和规范，一般应包括下列事项：（一）制定的目的、依据和原则；（二）调整对象、适用范围和主管部门；（三）主体的权利、义务和法律责任等；（四）工作程序或流程；（五）解释权属；（六）实施日期。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第二章  体例', '第八条', '内部规章应当结构严谨、条理清楚、文字简明、用词准确，不得使用有歧义或易误解的词汇、概念。内部规章内容用条文表达，条文较多的，可以分章、节、条。条可以分款、项、目。款不冠数字，项和目冠数字。项数以带括号的汉字表示；目数以阿拉伯数字表示。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第九条', '内部规章由本行有关职能部门负责起草，一般应包括以下七个阶段：（一）调查研究阶段；（二）起草文件草案阶段；（三）征求意见阶段（根据实际确定，可跳过）；（四）部门会签阶段（根据实际确定，可跳过）；（五）合规审查阶段。（六）办公室核稿阶段（七）审批签发阶段起草内部规章时应注意与法律法规、规章以及上级管理部门、监管部门、本行的有关文件规定的衔接与统一。与本行文件规定不一致的，应在文件提交送审时说明理由。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第十条', '内部规章草稿拟定后，根据实际情况，确定有无必要征求支行、部门意见。一般通过局域网、业务论坛或其他方式发布内部规章草稿，征求意见和建议。一般情况下，征求意见时限为5个工作日；特殊情况下，急文征求意见时限为2个工作日。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第十一条', '负责起草工作的本行职能部门在采纳合理意见并完成相应修改工作后，在银丰平台提起发文，必须选择“制度办法”选项，是否选择会签、哪些部门会签根据实际情况确定。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第十二条', '银丰平台中，在相关部门会签后，合规管理部进行合规审查。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第十三条', '合规管理部对内部规章的以下内容进行审查：（一）内部规章的内容是否合法，具体包括：1、是否符合法律法规、规章以及上级管理部门、监管部门、本行的有关文件规定；2、是否齐全完整，有明确的程序和要求； 3、是否恰当合理，具有可操作性；（二）内部规章的内容是否充分征求了员工及有关部门的意见和建议，有关记录是否齐备。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第十四条', '银丰平台中，合规管理部签署审查意见后，修改文稿重回起草部门。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第十五条', '在内部规章起草、征求意见以及部门会签中有分歧意见的，由起草部门汇总各方面意见后报相应分管领导决定；送审部门对合规审查意见有异议的，由合规管理部报请本行领导决定。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第三章  起草、审定和发布', '第十六条', '内部规章文件的核稿、签发、发布等按照《公文处理办法》执行。董事会、行长室、监事会等规章按照各自权限规定进行签发。需提交职工代表大会、股东大会讨论通过的内部规章按照本行《章程》和工会相关规定执行。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第四章  内部规章评估、清理', '第十七条', '内部规章实施定期评估，从政策性、合规性和适用性等方面进行评估。评估工作由合规管理部牵头，相关职能部门配合。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第四章  内部规章评估、清理', '第十八条', '鼓励员工对实施中的内部规章提出意见或建议，各职能部门和员工通过书面形式向合规管理部反馈有关意见或建议。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第四章  内部规章评估、清理', '第十九条', '根据内部规章的评估结果、内部管理的实际需要，正常按照会计年度对本行内部规章进行清理（含修改、合并、废止，下同），必要时可动态性地进行及时清理，确保内部规章结构合理、数量适当、合法有效。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第四章  内部规章评估、清理', '第二十条', '内部规章清理工作应当遵循以下原则进行：（一）适用原则。凡不符合工作实际的内部规章必须进行及时清理。（二）精简原则。对规范同一类管理事项的内部规章进行最大限度的精简、合并，减少文件数量。（三）合规原则。对于文件全部或部分内容已不符合国家有关规定的内部规章，应当根据实际情况对文件予以废止或对其内容进行相应修改。（四）公开原则。引入员工参与机制，公开向员工征求对内部规章的修改意见或建议，切实做到公开、公平和公正。（五）规范原则。内部规章的名称、体例、格式、用语等应当规范一致。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第四章  内部规章评估、清理', '第二十一条', '内部规章清理按照以下程序进行：职能部门调查研究——起草初稿——征求意见——部门会签——合规管理部审查——办公室核稿——本行领导审查、签发或提交审议（按授权权限、程序）——印发。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第四章  内部规章评估、清理', '第二十二条', '内部规章清理工作完成后，由合规管理部统一拟订清理工作报告，并对予以保留、废止、修改、合并或移交文件的名称及数量做出最终统计汇总。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第五章  汇编管理', '第二十三条', '为加强对内部规章的统一、动态管理，方便查询、学习和使用，在银丰平台知识库栏目下提供学习查阅，必要时由办公室编印《内部规章汇编》，做好定期更新。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第六章  附则', '第二十四条', '本办法未尽事宜以及与国家有关法律法规、规章的规定冲突的，按国家法律法规、规章等规定执行'),
(NULL, '合规管理部', '《内部规章管理办法》', '第六章  附则', '第二十五条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《内部规章管理办法》', '第六章  附则', '第二十六条', '本办法自印发之日起施行。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第一章  总则', '第一条', '为做好制度流程的维护、修订工作，不断完善本行内部制度体系，保证xxx银行（以下简称“本行”）制度流程持续合规、适用、科学，根据银监会《中国银监会办公厅关于印发银行业金融机构案防工作办法的通知》、《xxx银行内部规章管理办法》等相关规定，特制定本办法。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第一章  总则', '第二条', '制度流程后评价是根据现行的法律、法规、监管政策、省联社规定以及内外部经营环境等，准确对制度流程作出客观、公允的评价，为制度流程修订工作提供科学的参考依据。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第一章  总则', '第三条', '本办法适用于本行所有规章制度、办法及操作流程，包括在全行具有普遍约束力的内部工作制度、办法、细则、规定、标准、通知及其他规范性文件等。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第二章  评价原则', '第四条', '制度流程后评价遵循以下原则：（一）坚持“动态评价”的原则，新出台的制度流程1年内进行评价，其余制度流程根据监管政策要求适时进行评价。（二）坚持“贴近实际”的原则，制度流程后评价，应该采取征求意见、组织座谈等多种方式，收集、听取基层反映建议。（三）坚持“紧扣外规”的原则，制度流程的后评价，必须根据现行的法律、法规、监管政策、省联社规定等进行。（四）坚持“统一实施”的原则，制度流程的后评价，由总行合规管理部统一组织实施，相关条线部门做好配合。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第三章  评价流程', '第五条', '评估组织。合规管理部在实施前应制订具体的评价方案，成立评价小组。评价方案应该明确评价对象、标准、完成时间、配合部门及质量要求等。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第三章  评价流程', '第六条', '评估准备。后评价配合部门为制度管理的条线部门，接到评价通知后，配合部门应及时做好制度流程自评、提供制订依据等工作。同时，合规管理部应搜集当前适用的相关法律、法规、规章以及监管规定，以备内外规比较。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第三章  评价流程', '第七条', '评估方式。合规管理部应通过日常合规监测发现问题，及时登记、汇总，监测方式包括支行（部门）合规报告、本行审计、合规检查或条线检查、监管部门检查以及省联检查（审计）等。同时，合规管理部还应采取征求意见、组织座谈等多种方式，收集、听取基层的反馈意见和建议。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第三章  评价流程', '第八条', '评估分析。评价小组分析、总结制度流程问题，应编写工作底稿，并且换人复核，并且小组内组织集中讨论，对评价意见逐条分析确定，最终形成评价报告。报告应该全面、客观，做到分析透彻、有理有据。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第三章  评价流程', '第九条', '评估总结。评价工作结束，合规管理部应根据评价报告结果编制制度流程后评价工作建议书反馈给有关责任部门。责任部门收到建议书后，应在30日内按照建议要求对制度流程完成修订，并且将修订整改情况报送合规管理部，对无法修订整改的必须作出书面说明。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第四章  评价标准', '第十条', '制度流程的评价标准：（一）合规性评估：是否违反现行的国家法津、法规、规章、监管政策规定以及省联社规定。（二）合理性评估：权力与责任是否相当，权利与义务是否一致。（三）协调性评估：制度之间是否协调，有无矛盾；制度流程之间是否冲突，或者存在不衔接。（四）操作性评估：制度是否具体可行，能否解决经营管理和操作中的具体问题；条件是否恰当、科学；措施是否必要、充分，程序是否合理、易于操作。（五）严谨性评估：立规技术是否规范、逻辑结构是否严密、用语是否规范、条文表述是否准确，是否影响到制度的正确、有效实施。（六）执行性评估：是否得到普遍遵守和执行，是否实现预期目的；员工、客户反映的共性问题有无得到及时修订。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第五章  其他规定', '第十一条', '在制度流程修订方面，本行审计、合规检查、条线检查、监管部门检查、省联检查（审计）发现问题较多的，或者支行反映强烈的，以及客户诉求较多的，条线部门应该认真分析，及时修订相关制度流程。修订不及时、不彻底的，总行合规管理部应启动具体制度流程的评价工作。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第五章  其他规定', '第十二条', '为持续做好制度流程的评价、修订工作，条线部门应将制度流程及其执行情况纳入各类检查方案，并在检查报告中予以反馈。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第六章  罚则', '第十三条', '本行开展制度流程后评价工作过程中，对评价人员工作敷衍了事,评价工作明显失准以及条线部门、支行不积极配合的,依照本行相关办法规定对有关责任人员进行相应的处罚。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第七章  附则', '第十四条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《制度流程后评价管理办法（试行）》', '第七章  附则', '第十五条', '本办法自印发之日起执行。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第一章  总则', '第一条', '为有效监测、识别、评估和控制合规风险，完善合规组织架构，根据《商业银行合规风险管理指引》、《江苏省农村信用社合规管理工作指导意见》等规章制度的相关规定，结合xxx银行（以下简称“本行”）实际，特制定本办法。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第一章  总则', '第二条', '本办法所称合规员，包括专职、兼职合规员。专职合规员是指合规部门专职从事合规管理的人员，兼职合规员是指本行在各职能部门、各经营单位设置的协助本部门、本单位负责人施行合规政策的人员。兼职合规员由经营单位运营主管（或业务骨干）担任，专业部门由熟悉条线业务的人员担任。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第一章  总则', '第三条', '合规员应当尽职履行本办法所规定的职责，认真贯彻落实合规政策，推进合规文化建设。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第一章  总则', '第四条', '本行为合规员履职创造良好的外部环境和工作便利，包括但不限于：合规培训、系统支持、考核倾斜、员工问责等。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第二章  任职条件', '第五条', '合规员应当具备下列条件：1.具有较高的政策水平，熟悉相关法律法规、行业准则、规章制度；2.具有良好的组织、沟通和协调能力，有较强的分析能力；3.具有良好的业务理论素养，通过省联社合规资格考试，熟悉本行各项业务操作流程，具备相应的管理和工作经验，；4.具有较强的合规意识，工作责任心强；5.品行端正、作风严谨、道德品质良好。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第二章  任职条件', '第六条', '专职合规员由总行根据合规管理需要，优先从具有多个岗位工作经验人员中选拔，但至少配备一名具有法律专业背景人员。兼职合规员的设置，各支行、部门至少配备一名。探索向业务量较大的机构、重要部门以及问题严重的机构派驻专职合规管理员。已设运营主管的支行、部门，由兼职合规员兼任。其他部门合规员由部门推荐，合规管理部考评，报行长室确定。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第三章  岗位职责', '第七条', '合规员是本行合规管理体系中的重要组成部分，专兼职合规员均应履行下列职责：1.协助负责人贯彻执行总行的合规政策、制度办法等规范性文件，并予以监督；2.协助负责人组织所在单位（部门）人员学习相关的法律法规、监管规定、合规风险管理指引等文件精神，引导全员合规；3.有效监测、识别、评估和控制所在单位（部门）经营管理中的风险点，及时登记《合规检查跟踪系统》；4.做好本单位（部门）员工的合规性咨询和业务辅导工作；5.及时向合规部门报送所在单位（部门）合规报告、合规管理信息等；6.向所在单位（部门）负责人以及合规部门提出合规管理的建议、意见。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第三章  岗位职责', '第八条', '专职合规员除须履行第七条规定的职责外，还应履行下列职责：1.持续关注相关的法律法规、监管要求、规章制度、规范性文件的变化，协助部门负责人适时制定、审查和维护本行制度办法，并监督贯彻情况；2.协助部门负责人将全行适用的法律法规、监管要求、规章制度、规范性文件及时传达到相关部门和风险相关方；3.审核对外签订的合同、协议等法律性文件；4.开展合规检查，发现并且报告问题；5.负责规章制度与流程的合规性审核，参与新业务、新品种开发的合规性审核；6.协助负责人管理兼职合规员队伍，指导、监督其履行职责；7.及时答复合规相关咨询；8.其他合规管理工作'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第三章  岗位职责', '第九条', '职能部门合规员除须履行第七条规定的职责外，还应履行下列职责：    1.协助本部门负责人制定和执行本条线的年度检查计划，及时向合规部门报送本部门的检查通知或方案、检查结果； 2.参与本行以及合规部门组织的合规检查、合规培训、会议等其他活动；3.协助编制本条线的制度办法、业务操作规程、业务手册，做好审核工作；4.负责本条线的协议、合同等法律性文件的合规审核；5、保管合规工作档案；6、其他合规管理工作。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第三章  岗位职责', '第十条', '经营单位合规员除须履行第七条规定的职责外，还应履行下列职责：1.参与对本单位重要业务、重要事项的讨论、会办（包括但不限于贷款会办），并对合规管理情况进行有效监督；2.开展日常性的合规监督或检查，及时登记合规检查跟踪系统，对不能现场整改的问题登记《合规整改落实登记簿》，督促责任人进行整改；3.负责审查本单位会计、信贷类月（季、年）报表以及重要的临时性报表，做好报送工作；4.对本单位的协议、合同等进行合规审核；5.保管合规工作档案；6.其他合规管理工作。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第三章  岗位职责', '第十一条', '合规员应当加强合规风险监测，密切关注其所在业务条线、单位的合规状况，对日常业务、制度流程在实际操作中可能存在的合规风险保持高度敏感，对于发现的合规风险就其不同程度按照权限和级别报告处理。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第三章  岗位职责', '第十二条', '对于本行布置的各类自查、整改等任务，合规员应当协助所在单位（部门）负责人做好组织实施工作。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第三章  岗位职责', '第十三条', '合规员应督促所在单位（部门）开展合规制度学习，包括但不限于：1．经济、金融政策；2．与本行业务相关的最新法律、法规、规章；3．本行制定的各项制度、规定、流程；4．本行布置的其他合规学习内容。对于所在单位（部门）开展的合规学习以及其他员工参加各类合规性学习情况，合规员应当做好日常记录和汇总工作。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第四章  合规考核', '第十四条', '合规员实行按月考核，按季兑现，年终统算。考核结果将作为聘任聘用等工作的重要依据。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第五章  附则', '第十五条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《合规员管理办法(修订）》', '第五章  附则', '第十六条', '本办法自印发之日起施行。'),
(NULL, '合规管理部', '《合规员考核办法》', '第一章  总则', '第一条', '为全面提升xxx银行（以下简称“本行”）合规管理水平，增强合规管理人员的事业心和责任感，提高对合规事项的识别、评估、控制、监测和管理能力，确保各项合规管理目标的实现。根据省联社合规管理要求以及本行合规员管理办法，特制定本办法。'),
(NULL, '合规管理部', '《合规员考核办法》', '第一章  总则', '第二条', '本办法所指考核对象为各支行、营业部和机关各部室合规员（以下简称“合规员”）。'),
(NULL, '合规管理部', '《合规员考核办法》', '第一章  总则', '第三条', '合规员由合规管理部扎口考核，其他职能部门配合。'),
(NULL, '合规管理部', '《合规员考核办法》', '第一章  总则', '第四条', '本行设专项考核资金用于合规员履职情况的考核，标准为每人每月500元。'),
(NULL, '合规管理部', '《合规员考核办法》', '第一章  总则', '第五条', '本办法对合规员采取百分制考核的方式，每位考核对象基础分为100分，扣分以扣完该项基本分为限。计分方法采用单项计分，累计总分的方式。'),
(NULL, '合规管理部', '《合规员考核办法》', '第一章  总则', '第六条', '合规员考核实行按月考核，按季兑现，年终统算（仅指部分考核项目）。其结果将作为聘任聘用等工作的重要依据。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第七条', '兼职合规员包括支行合规员和机关部室合规员。其中，支行合规员指基层支行、营业部、公司业务部、小微业务部合规员；机关部室合规员指除公司业务部、小微业务部之外的部门合规员。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第八条', '兼职合规员考核内容包括合规资格、合规报告、合规跟踪系统、合规检查、合规问责与整改、违规积分、合规审查、合规咨询与培训、合规建议、合规文化建设等部分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第九条', '合规资格（5分）合规员应符合《合规员管理办法》第五条所列任职条件，并取得省联社合规员资格证书。未能做到任职条件要求的，视程度扣1-5分，未取得省联社资格证书的扣5分。新任用合规员享有上任期至考试期间的扣分豁免，但其应认真学习，积极参加省联社合规员资格考试，并一次通过。无故不参加或参考未通过的合规员自考试次月起该项不得分，任职满一年仍未取得省联社上岗资格的或仍不能满足任职资格的，总行将取消其任职资格（省联社未组织考试除外）。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十条', '合规报告、报表（支行合规员本项20分，机关部室合规员本项10分）合规报告、报表须及时上报，经催要后仍未上报的，该大项不得分。逾月仍未上报，扣除次月岗位津贴。（一）定期合规报告合规员应按《合规报告制度》具体要求向合规管理部报送合规报告。1.合规报告应按时上报。每月2日前未上报的，视同上报不及时，每次扣4分；每月5号前仍未上报的，视同当月未上报，该大项不得分。2.合规报告格式不符合要求的，每处扣2分。格式不符合要求包括缺少签字、缺少章印、日期不符、版式不符等。3.合规报告未同时上报纸质和电子版本的，少一项扣4分。4.合规报告内容应按《合规报告制度》要求，每少一项扣2分，总行检查通报的单位在报告中未反映问题的扣5分。5.合规报告质量欠缺的视程度扣2-20分。包括描述不清、累月重复、无实质内容等。（二）员工行为排查合规员为本单位员工行为的协查责任人，对员工触及“三十条禁令和十个一律”规定内容的，合规员必须及时向总行报告，并填制员工行为排查表。1.员工行为排查报表格式填写不符合要求的，每处扣2分；2.上报不及时，每次扣2分；3.排查流于形式，未能如实反映问题，被总行检查发现的，本大项不得分。（三）不定期合规报告（重大事项报告）对涉及本单位的重大违规事件(包括突发事件)，合规员应在第一时间迅速向总行合规管理部门汇报，未及时报告的，扣10分。因未及时报告引发风险或案件的，视程度扣减1-12月岗位津贴；程度严重的，给予撤职并追究相应的责任。（四）临时工作报告。合规管理部门布置的临时性工作报告，未按时完成的，每次扣4分；完成质量不合格的，每次扣4分；未完成的，每次扣10分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十一条', '合规跟踪系统（10分）1.合规报告反映的自查问题未能及时整改的，应登记合规跟踪系统，每少登记一个问题扣4分。每季未有登记问题的，该项每季最后一个月不得分。2.合规跟踪系统应及时登记。具体规定:问题发现之日起2个工作日内登记问题、专业部门督导之日起2个工作日内登记整改措施、整改结束之日起2个工作日内登记整改结果，每有一个问题中任一环节登记不及时的扣2分，扣完为止。3.存在描述不清楚、重复登记等问题视程度扣2-10分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十二条', '合规检查（支行合规员本项10分，机关部室合规员本项15分）1.机关部室合规员应及时将本部门组织开展的检查计划、方案、通报、问责整改情况或者接受的上级管理部门检查意见、后续整改报告等资料报送合规管理部，未按时交付的，每次扣4分，经催要后仍未交付的，每次扣10分；2.机关部室合规员应配合合规管理部门做好违规问题库的更新，不能积极配合或配合敷衍了事的，视程度扣2-10分；3.合规员应主动进行日常或定期的合规性自查，未按要求进行合规自查的，或者自查次数少于规定次数的，每次扣2分；4.合规员应积极配合合规管理部门开展的合规检查，检查敷衍了事的，视程度扣2-10分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十三条', '合规问责与整改（支行合规员本项10分，机关部室合规员本项15分）1.机关部室检查过程中未严格执制，对发现的问题未严格问责的，对部室合规员视程度每次扣2-15分；2.自查或检查发现问题未能及时督改的，每次扣2分；3.员工对存在的问题整改不到位导致重复发生的，每次扣5分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十四条', '违规积分（10分）1.本单位所有员工当月违规行为（文优服务、考试不及格除外）记分之和，每5分扣减1分，合规员本人违规被扣罚的（管理责任除外），每1分扣减1分，扣完为止，两处不重复扣分。2.机关部室合规员应于本部门违规处罚通报作出后的次月2日前将违规记分情况表报送至合规管理部，未及时报送的每次扣2分，未将纸质和电子档同时报送的，每次扣5分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十五条', '合规审查（10分）合规员应做好合规审查工作（包括部门提交的法务审查的对外协议、合同），审慎提出合规审查意见，同时对自身的审查意见负责。合规审查意见存在明显瑕疵等问题，对经营活动造成不利影响的，每次扣4分，造成实际损失的，视程度扣减1-12月岗位津贴。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十六条', '合规咨询与培训（10分）1.合规员应及时将总行最新精神和文件要求传达给本单位员工，因未及时传达导致员工延误总行工作或者员工向上级反映的，每次扣4分；2.机关部室合规员应及时将本部门合规培训情况报至合规管理部，未报送的，每次扣4分； 3.合规员应负责本单位的合规咨询工作，对员工在经营过程中遇到的合规问题，依据法律、规章和准则，提供参考性意见与建议，未能提供合规咨询或提供意见错误的，每次扣5分。如遇不能解决的合规咨询，应及时向有关部门反映，未及时反映的，每次扣5分，本项无反证即得分。4.合规员应每月组织全员学习不少于一次，学习内容包括但不限于本行制定的各项制度、规定、流程和与本行业务相关的最新法律、法规，新出台制度办法学习最迟不超过一周。该项得分与员工参与总行考试成绩挂钩。本单位员工在总行组织的考试（不包括技能考试）中不及格（以总行或部门公布的成绩为准）的，视同未组织学习，每人次扣4分，本人参加考试不及格的，每次扣6分。员工参加技能考试中的理论项目不及格的，每人次扣1分，补考仍不及格的，每人次扣2分。本项扣分从考试成绩公布的当月考核中扣分。5.机关部室合规员应配合合规管理部门做好制度办法题库的更新，在本条线新制度出台后，应建立健全制度题库，未及时完成的每次扣2分，经催要后仍未完成的，每次扣5分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十七条', '合规建议（5分）合规员应针对业务经营或管理过程中存在的问题，及时向合规部门提交合规建议，每年应提供不少于1次有价值建议且被总行明确采纳，12月份统算，如全年未有报送采纳的，以60分扣减。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十八条', '合规文化建设（10分）根据大力发展合规文化的要求，合规员应保证本单位员工上报合规信息等达到规定篇目，每少一篇扣2分。合规员本人上报篇目不符合要求的，每少一篇扣4分。其他有关合规文化建设的事项，视完成程度扣2-10分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第二章  兼职合规员考核内容', '第十九条', '其他事项本办法未罗列的其他与合规管理相关的事项，视完成程度扣分，扣分标准应与上文保持一致，造成不良影响的，视程度扣减1-12月岗位津贴；程度严重的，给予撤职并追究相应的责任。员工提供的合规建议被总行采纳的，或者员工在合规管理、合规文化建设中作出贡献被总行表彰的，视贡献程度给员工所在单位合规员加2-10分，加分后合规员总分不超过100分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第三章  专职合规员专项考核内容', '第二十条', '专职合规员指合规管理部门合规员。'),
(NULL, '合规管理部', '《合规员考核办法》', '第三章  专职合规员专项考核内容', '第二十一条', '专职合规员除需考核第九条至第十八条内容外，还需考核以下内容。1.审慎审核对外签订的合同、协议等法律性文件，未按时审核的，每次扣2分，未审慎审核的，每次扣4分，因未审慎审核造成损失的，扣1-12月岗位津贴；2.负责规章制度与流程的合规性审核，参与新业务、新品种开发的合规性审核，未按时审核的，每次扣2分，未审慎审核的，每次扣4分，因未审慎审核造成的损失的，扣1-12月岗位津贴；3.负责合规文化建设的宣传工作，做好合规小贴士、合规管理专刊、法律广角镜等合规文章的刊登工作，未按年初计划完成的，每次扣4分；4.持续关注相关的法律法规、监管要求、规章制度、规范性文件的变化，并及时提炼合规提示传达给相关部门和风险相关方，未能及时运用的，每次扣4分；5.及时答复合规条线的相关咨询，回复不及时的，每次扣4分，不能立即回复的应协调解决，未尽职的，每次扣4分；6.及时做好合规员考核工作，因未及时考核影响资金兑现的，每次扣5分；7.做好制度办法的修订、整理工作，视完成程度扣2-10分；8.及时更新合规、制度办法题库，视完成程度扣2-10分；9.做好合规达标创建工作，按要求序时公布合规评价结果，视视完成程度扣2-10分；10.其他需要扣分的事项，视完成程度扣2-10分。'),
(NULL, '合规管理部', '《合规员考核办法》', '第四章  附则', '第二十二条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《合规员考核办法》', '第四章  附则', '第二十三条', '本办法自下发之日起执行。'),
(NULL, '合规管理部', '《合规报告制度》', '第一章  总则', '第一条', '为及时、准确掌握全辖各支行、部的合规状况，规范合规报告行为，提升报告质量，更好地推动xxx银行（以下简称“本行”）合规工作的开展，根据《江苏省农村信用社合规管理工作指导意见》、《江苏省农村信用社合规报告制度》等文件的要求，并结合全辖各支行、部的实际，制订本制度。'),
(NULL, '合规管理部', '《合规报告制度》', '第一章  总则', '第二条', '各支行、部履行合规报告行为，适用本制度。各支行、部应明确本单位的兼职合规员负责合规报告工作。'),
(NULL, '合规管理部', '《合规报告制度》', '第一章  总则', '第三条', '各支行、部的合规报告应遵循及时、全面、真实、准确等原则。'),
(NULL, '合规管理部', '《合规报告制度》', '第二章  报告的种类与规范', '第四条', '合规报告按报送时间以及报送内容的不同分为定期合规报告和不定期合规报告。定期合规报告是指各支行、部门就报告期内本单位（部门）的合规现状及变化、规章制度建设与完善、经营管理中的违规事件和主要合规缺陷、合规工作组织与实施、合规工作目标与实现等情况作出的报告，分为月度报告、季度报告、半年报告和年度报告。不定期合规报告是指各支行、部在经营管理过程中发生的某一或某类重大突发性违规事件、合规缺陷或重要合规信息以及对外部监管部门检查出具的书面监管意见的及时报告。'),
(NULL, '合规管理部', '《合规报告制度》', '第二章  报告的种类与规范', '第五条', '合规报告的格式规范。（1）定期合规报告的格式一般包括：1.标题。“某支行、部某年某月（或某季度、上半年、年度）合规报告”。2.主送部门（合规管理部）。3.正文。4.附件。根据实际需要确定。5.负责人意见（手写）、报告单位（签章）以及报告日期。（2）不定期合规报告的格式一般包括：1.标题。“某单位某年度不定期合规报告之几”。2.主送部门（合规管理部）。3.正文。4.附件。根据实际需要确定。5.负责人意见（手写）、报告单位（签章）以及报告日期。'),
(NULL, '合规管理部', '《合规报告制度》', '第二章  报告的种类与规范', '第六条', '合规报告正文，区分经营单位和业务管理部室，以及是否为月度报告、非月度定期报告和不定期报告，报告的要点有所侧重。一、经营单位（含各支行、营业部、公司业务部、小微业务部、金融市场部）定期报告1.月度合规报告针对报告期内内控管理以及执制情况进行报告，主要报告以下内容：（1）本期采取的管理措施；（2）本期总行检查以及单位自查情况：①总行在本支行（部）开展哪些检查（审计），发现哪些违规问题或风险点；②本支行（部）自查开展哪些项目，发现哪些违规问题或风险点；③上述问题产生的原因、风险状况、损失程度以及责任人；④本期发现问题的整改情况以及对新发现的风险点已（拟）采取的管控措施。对有违法（侵占挪用、贿赂、诈骗、金融从业人员特定犯罪、其他犯罪）、违反本行禁令以及大额交易（单笔超过5万元或者当月超过50万元的），必须在报告中单列反映，详细报告行为事实、性质，同时在《员工异常行为排查月报表》（附件1）如实反映。（3）合规建议。2.非月报报告（季报、半年报、年报）除报告上述月度合规报告的要点外，还应报告：（1）本期具体针对哪些业务进行了流程或者岗位调整；（2）本期合规文化建设情况，包括对各项规章制度的学习、合规考试、合规文章、合规小贴士上报等。（3）银监、省联社以及其他管理部门在本支行（部）检查，须单列反映检查项目、发现问题、整改情况等。（4）本年累计记分处罚情况（人数、人次、积分多少）以及行政处分情况。二、业务管理部室（除营业部、公司业务部、小微业务部、金融市场部）1.月度合规报告针对报告期内条线管理以及执制情况进行报告，主要包括以下内容：（1）条线管理：本期主要采取了哪些管理方法、措施，调整了哪些业务流程，加强了哪些风险点的管理；（2）本期检查：①本条线开展了哪些检查；②发现哪些违规问题或风险点；③产生的原因、风险状况、损失程度以及责任人；④问责情况：本期及本年累计记分处罚情况（人数、人次、积分多少）以及行政处分情况。⑤整改情况：本期发现问题的整改情况以及对新发现的风险点已（拟）采取的管控措施。2.非月报报告（季报、半年报、年报）除报告上述月度合规报告要点外，还应报告：（1）制度建设：本条线制度流程的废、改、立情况，通过调研、检查以及基层、合规等相关部门反馈，从6个方面对本条线制度流程评价。①合规性评价：是否违反现行的国家法津、法规、监管政策规定以及省联社规定。②合理性评价：权力与责任是否相当，权利与义务是否一致。③协调性评价：制度之间是否协调，有无矛盾；制度流程之间是否冲突，或者存在不衔接。④操作性评价：制度是否具体可行，能否解决经营管理和操作中的具体问题；条件是否恰当、科学；措施是否必要、充分，程序是否合理、易于操作。⑤严谨性评价：立规技术是否规范、逻辑结构是否严密、用语是否规范、条文表述是否准确，是否影响到制度的正确、有效实施。⑥执行性评价：是否得到普遍遵守和执行，是否实现预期目的；员工、客户反映的共性问题有无得到及时修订。（2）培训辅导本期开展了哪些培训，培训的方式、对象、主要内容以及成效；对经营单位开展了哪些辅导，辅导的方式、对象、主要内容以及成效；（3）银监、省联社以及其他管理部门在本条线检查的，须单列反映检查项目、发现问题、整改情况等。（4）问责情况，本年条线累计记分处罚情况（人数、人次、记分多少）以及行政处分情况。三、不定期合规报告（所有支行、部室）正文部分主要针对经营管理过程中发生的某一或某类重大突发性违规事件、合规缺陷或重要合规信息，具体从时间、地点（或环节）、性质、原因、可能造成的损失和影响以及拟采取的纠正措施等方面进行描述和监管部门出具的书面监管意见。'),
(NULL, '合规管理部', '《合规报告制度》', '第二章  报告的种类与规范', '第七条', '定期合规报告应在规定时间内上报纸质与电子介质报告，电子介质报告报送至银丰平台合规管理部公邮；不定期合规报告，各支行、部可先通过电话向总行合规管理部负责人口头报告，然后通过银丰平台报送电子介质报告。'),
(NULL, '合规管理部', '《合规报告制度》', '第三章  报告的时间与频率', '第八条', '各类定期合规报告的报送时间均为本期终了后次月2日内。报送非月报的月份，不需要报送月报。不定期合规报告报送时间为发生重大突发性违规事件、合规缺陷或重要合规信息后立即电话报告，上报纸质与电子介质报告必须在事发后2日内。'),
(NULL, '合规管理部', '《合规报告制度》', '第四章  报告的组织与安排', '第九条', '明确兼职合规员，履行本支行或者本部门的合规报告职责。'),
(NULL, '合规管理部', '《合规报告制度》', '第四章  报告的组织与安排', '第十条', '各支行、部要结合本单位（部门）的实际情况，建立本单位（部）的报告汇总机制，为兼职合规员开展工作创造条件。'),
(NULL, '合规管理部', '《合规报告制度》', '第五章  报告的责任与奖惩', '第十一条', '为确保报告质量，总行将合规报告报送纳入兼职合规员月度管理职能考核，并且纳入年度单位管理目标考核。'),
(NULL, '合规管理部', '《合规报告制度》', '第五章  报告的责任与奖惩', '第十二条', '为明确责任，各支行、部的兼职合规员和负责人分别为直接责任人和管理责任人。报告反映失实、不完整、不准确以及不及时，造成风险损失或者不良影响的，总行将对照《xxx银行员工违规行为处理办法》对有关责任人进行处罚。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第十三条', '重大违规行为是指《江苏省农村商业银行工作人员违规行为处理办法》以及本行办法明确给予违规人员记大过以上纪律处分、停职以上组织处理以及变更劳动关系处理方式的违规行为。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第十四条', '重要合规信息是指各支行、部合规管理的组织架构发生重大调整与变化。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第十五条', '重大违规事件是指由于发生重大违规行为而引发重大风险、形成损失以及造成严重不良影响的违规事件。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第十六条', '重大合规缺陷是指规章制度、操作流程、计算机系统的功能和程序等方面存在明显的缺失与不完善，并可能引发重大风险、形成损失以及造成严重不良影响的。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第十七条', '合规状况是指某一业务或管理范畴合规的总体情况，违规或合规风险的主要种类、发生频次、来源、发生的原因以及变化的趋势、控制的措施以及管理的情况等。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第十八条', '逐层上报。合规管理部及时总结全行合规管理情况，并向行长室报告；行长室按章程规定向本行董事会报告合规管理情况。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第十九条', '本制度由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《合规报告制度》', '第六章  附则', '第二十条', '本制度自下发之日起执行。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第一条', '为科学评价本行合规案防管理的有效性，依据《商业银行合规风险管理指引》和《江苏省农村信用社合规工作评价办法》等相关制度，结合本行实际，制定本办法。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第二条', '本办法适用于对xxx银行（以下简称“本行”）合规案防管理工作的评价。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第三条', '本办法明确了合规案防管理工作评价的方式、内容、标准、流程等。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第四条', '合规案防管理评价坚持依法、独立、客观、公正、公平的原则。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第五条', '合规案防管理评价的总体目标。通过有效开展合规案防管理评价工作，全面梳理自身合规管理状况，及时采取措施弥补合规缺陷和问题，持续改进和强化合规管理机制，不断夯实依法合规经营和案件风险防控的坚实基础。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第六条', '本行根据经营规模、业务复杂程度、内控风险状况等及省联社考核评价内容，合理确定合规案防风险管理评价的重点、范围和频度，每年应至少开展一次全面合规案防管理自我评价。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第七条', '本行合规案防管理工作评价的内容包括基本评价内容和专项评价内容两部分。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第一章  总则', '第八条', '合规案防管理工作评价采用百分制考核，基本评价部分分值为100分，按照相应分值和评分标准据实记分；专项评价部分实行加减分。最终得分为基本评价和专项评价两部分得分之和。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第二章  组织管理与职责', '第九条', '本行成立合规案防管理评价小组，组长由董事长担任，行长、监事长任副组长，副行长以及相关部门负责人为组员，评价办公室设在合规管理部。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第二章  组织管理与职责', '第十条', '合规管理部负责评价工作的实施和汇总上报。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第二章  组织管理与职责', '第十一条', '相关部门负责配合做好合规管理评价工作。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第二章  组织管理与职责', '第十二条', '审计部负责对评价工作进行监督。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第三章  基本评价内容', '第十三条', '基本评价内容包括合规案防工作组织、合规风险管理全流程、合规管理保障等方面。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第三章  基本评价内容', '第十四条', '合规案防工作组织，主要评价“两会一层”以及合规部门、审计部门等内容。1.董事会：审议批准商业银行的合规政策并监督实施；对商业银行管理合规的有效性作出全面评价，发现合规案防管理缺陷并及时有效解决；授权董事会下设的风险管理委员会、审计委员会或专门设立的合规管理委员会对商业银行合规风险、案防管理进行日常监督。2.监事会：至少每半年对董事会和经营层合规管理、案防职责履行情况进行实质性评价，并形成书面材料。3.经营层：制定合规政策，并根据合规风险管理状况以及法律、规则和准则的变化情况适时修订合规政策；明确规定合规管理部门为案防工作的牵头部门，配置有效履行案防职能的资源；为其履行职责配备充分和适当的合规管理人员，并确保合规管理部门及部门负责人的独立性；合规部门分管领导不得分管业务条线；报告提供充分依据并有助于董事会成员判断高级管理层合规、案防管理有效性。4.合规部门：根据合规政策制定科学、合理、连续的合规、案防管理工作计划，明确工作目标并能按序时完成；专职合规管理人员占在职员工比例达到规定比例，除合规部门以外职能部门和分支机构均明确兼职或专职合规管理人员；根据监管部门和省联社要求，结合本行合规政策确定合规部门职责和专兼职合规管理人员岗位职责，制定完善合规制度，并得到有效贯彻执行。5.审计部门：内审部门对本单位合规管理工作每两年开展一次审计，出具审计报告且报告质量达到要求。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第三章  基本评价内容', '第十五条', '合规风险管理全流程。主要评价事前预防机制、事中发现机制和事后解决机制等内容。（一）事前预防机制1.强化员工职业规范约束。建立并完善员工管理制度，将员工入职前背景考察、职业操守、八小时内外行为规范等要素纳入合规、案防管理范畴。2.合规风险管理：制定有效的识别、监测、评估、缓释合规风险程序；建立合规风险分类矩阵，进行分类管理；并适时跟踪、评价风险缓释效果。3.制度流程管理：按规定开展规章制度、对外合同（协议）、新业务（产品）等的合规审查和咨询工作，建立台帐，出具书面审查意见或问题解决方案；建立制度流程梳理维护机制，制定相关制度办法，明确流程，持续关注法律、规则和准则的最新发展，定期对本单位的制度流程进行修订、优化完善；建立制度后评价机制，定期牵头组织制度后评价并出具评价报告并根据评价报告落实到位。4.教育培训：结合工作实际制定针对性的年度合规案防培训计划，明确培训对象、内容，建立培训效果评估机制；培训对象覆盖全员、新进员工、新上任的中层管理人员、高风险部门和关键岗位人员；对专兼职合规管理人员定期进行专项合规管理业务培训且全年不少于2次。5.合规手册：出台合规手册，并定期、不定期的修定完善。并对新制度、新文件、新要求进行及时解读。6.合规手册：建立合规考试机制，出台合规考试管理办法。举办形式多样、针对性强、全员性合规考试。（二）事中发现机制。主要评价合规检查、系统监测、诚信报告与举报、合规案防报告。1.合规检查：合规管理部门扎口管理全行合规检查，制定规范、具体的检查操作流程，根据检查情况提出合规建议并监督实施；制定全年检查计划。合理设定检查频率、覆盖范围、延伸要求、工作方式及工作质量评价标准，确保每年合规案防检查对机构及条线的全覆盖，全年抽查业务量不低于30%，加大重点业务、机构、岗位、人员以及案件易发部位和薄弱环节检查力度；定期分配一道防线自查内容。合规管理部门加强对一道防线自查的复查，抽查率不低于20%。2.系统监测：开发数据分析系统，监测关键指标，排查疑点数据，提高合规案防管理效率；3.建立诚信报告与举报制度并有效实施；4.合规案防报告：完善合规案防报告制度，明确合规案防报告要求、路径、频率、责任及奖惩等，并严格执行到位。（三）事后解决机制。主要评价检查整改成果运用、违规问责和绩效考核。1.检查整改成果运用：统计汇总全行违规事件，建立违规事件库，建立整改跟踪机制，加强违规事件整改管理，提高整改率；对全行违规事件进行多维度进行成因分析，提出合规建议及合规提示，根据分析成因完善优化制度流程；根据检查情况，结合外部案例，分层级、分条线进行合规辅导。2.违规问责：完善员工违规问责机制，根据员工违规积分管理办法及员工违规行为处理办法严格问责到位。3.绩效考核：将各机构合规管理情况纳入绩效考核，占比不得低于20%。同时与机构（部门、个人）的考核、授权、职务晋升等进行有效挂钩。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第三章  基本评价内容', '第十六条', '合规管理保障。主要评价合规人员管理和科技支撑等内容。1.合规人员管理：合规管理关口前移，参与新业务（产品）开发、开展、合同签定等，在主要业务条线、部门和业务量大的机构设置专职合规员；制定专兼职合规人员考核办法，对专兼职合规人员实行等级管理。对兼职合规管理人员额外提取（不包含在应发工资内）每人每年不少于5000元的奖励，根据考核情况兑现；2.科技支撑：加大信息科技投入，上线并充分使用合规管理系统；对整合内控、合规、案防和操作风险四合一系统功能，全面推广并成熟使用；与其它各类风险控制、监测系统实现对接，加强操作风险控制，提升合规风险管理。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第四章  专项评价内容', '第十七条', '专项评价内容包括工作配合、合规管理创新、违规揭示和考评资料等事项。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第四章  专项评价内容', '第十八条', '工作配合。包括配合省联社合规管理工作，较好地完成工作任务；对积极投稿并采用；根据省联社要求报送资料；省联社抽调使用。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第四章  专项评价内容', '第十九条', '合规管理创新，受到上级管理部门表彰并推广应用的；'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第四章  专项评价内容', '第二十条', '违规揭示。对合规管理薄弱环节，特别是对重大违规行为、重大合规缺陷未及时有效地予以揭示和上报。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第四章  专项评价内容', '第二十一条', '考评资料。所提供考评资料实事求是，如发现考评资料弄虚作假，双倍扣除对应项目分值，扣分不封顶，并取消年度合规评先资格。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第五章  其他考评内容', '第二十二条', '省联社合规工作评价出现新的要求本办法未详尽或考核权重发生变化，考评内容应及时增加和调整，与省联社考评方向保持一致。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第五章  其他考评内容', '第二十三条', '上级监管部门对合规管理工作提出的新要求应在评价办法中补充的，考核内容将及时增加和调整。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第五章  其他考评内容', '第二十四条', '如遇法律、政策变化导致评价内容与之冲突的，考评内容以最新法律、政策要求为准。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第六章  评价管理流程', '第二十五条', '合规案防管理评价分为评价准备、评价实施、评价报告三个阶段。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第六章  评价管理流程', '第二十六条', '评价准备阶段。（一）成立合规案防管理评价小组。（二）评价小组办公室制定评价实施方案，明确评价目的、内容、范围、重点、要求等，全面收集评价期内与合规案防管理相关的文件、工作记录、有关数据等资料；评价实施方案经评价小组组长审批后实施。（三）可聘请外部机构承担部分内容的评价工作，但应监督其评价过程，确保其合规性，并承担其评价结果及相关责任。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第六章  评价管理流程', '第二十七条', '评价实施阶段。（一）评价小组应通过查阅资料、现场询问、符合性测试、问卷调查、合规案防检查等方法，逐项对照评价内容对相关信息资料进行梳理、分析，审核各项政策和程序的合规性，测试制度和流程在业务活动中的贯彻执行情况，确认风险控制点和关键环节操作的合规性。（二）通过分析可能预示潜在合规问题的有关数据和运行指标，对潜在的合规缺陷和违规事项进行识别和确认。（三）评价小组应详细记录评价过程和发现的相关问题，并作进一步分析核实和调查确认，最终做出客观、准确的判断。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第六章  评价管理流程', '第二十八条', '评价报告阶段。（一）评价小组根据评价实施情况，在全面整理、深入分析基础上，撰写合规案防管理评价报告。评价报告包括但不限于以下内容：评价工作组织开展情况、报告期内合规案防风险管理状况及变化、已识别的违规事件和合规缺陷、已经采取或建议采取的纠正措施等。（二）合规案防管理评价报告要准确定性和积极量化本行因政策、制度、流程及客户、产品、服务所引发的合规风险，以及特定机构、部门、业务或经营活动的风险暴露，形成对合规风险管理状况的明确评价结果。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第六章  评价管理流程', '第二十九条', '合规案防管理评价报告应以合规案防管理行为对应的充分书面资料作为得出评价结论的适当证据。书面资料可包括正式发布的文件资料和非正式发布但经有效程序审核认定的书面记录或描述。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第六章  评价管理流程', '第三十条', '本行将加强识别、计量、监测和评价合规风险的方法和途径研究，探索建立合规风险监测指标体系和评价模型，做到科学评价合规案防管理。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第七章  评价结果的运用', '第三十一条', '本行应充分利用合规案防管理评价成果，持续完善合规案防管理机制。董事会和高级管理层应认真研究评价报告提出的改进建议，相关责任部门应落实整改措施，合规部门应做到持续关注。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第七章  评价结果的运用', '第三十二条', '合规管理部门应以评价结果为导向，研究确定下一步合规案防管理计划，全面提升合规案防管理的前瞻性、针对性和有效性。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第七章  评价结果的运用', '第三十三条', '年度合规案防管理评价报告应按照规定报送省联社和银监部门。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第八章  附则', '第三十四条', '本制度由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《合规案防管理工作评价办法》', '第八章  附则', '第三十五条', '本制度自印发之日起施行。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第一章  总则', '第一条', '为纠正“重经营、轻管理”的思想和扭转“重检查、轻整改”的不良现象，及时全面发现问题，促进职能部门强化业务指导，确保违规整改效果，真正做到问题“清前堵后”，保障各项业务健康发展，特制定本办法。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第一章  总则', '第二条', '本办法适用于本行各支行、部门以及其负责人、合规员等人员。 第二章  管理原则'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第一章  总则', '第三条', '按照“谁检查，谁发现，谁登记”原则，自查发现由自查单位运营主管登记，总行部门检查问题由检查部门登记，银监、人行、省联社等外部监管机构检查发现，由对接的牵头部门负责登记。发现问题，登记坚持“及时、全面、客观”的原则。问题登记必须及时，登记时间为自发现之日起2个工作日内；问题登记必须全面，不得有任何遗漏；问题登记必须客观，一目了然，不得主观地对问题进行夸大或缩小。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第一章  总则', '第四条', '对责任人的处罚区分是否自查发现以及工作是否尽职等，坚持“自查从轻，检查从重”、“尽职免责、渎职从重”以及“首次从轻，累犯从重”的原则。从轻或从重处罚的，应在相应违规级别中选择记分较小值或较大值，不得跨级别选择处罚。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第一章  总则', '第五条', '职能部门应区分问题的紧急程度，坚持“及时、认真”的指导原则。对特急、紧急以及一般性的问题，职能部门应分别在登记问题的次日起2个、4个、7个工作日内给予及时指导，并视问题性质在必要时进行现场指导或电话指导。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第三章  具体规定', '第六条', '对自查应该发现而未能发现问题，或虽发现问题但未及时准确录入“合规检查跟踪系统”（以下简称“跟踪系统”）的，对经营单位合规员和负责人给予每次记4分、2分的处罚。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第三章  具体规定', '第七条', '对经营单位存在的问题，职能部门应在上述第五条规定期限内进行专业指导，对确实不属于本职能部门管理的问题应及时通知存在问题单位及时修改归口专业部门。未在规定期限给予指导、指导明显不当以及对非本职能部门问题不通知修改的，对部门合规员和负责人分别给予每次记4分、2分的处罚。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第三章  具体规定', '第八条', '职能部门未在上述规定的期限内给予指导的，合规管理部应在职能部门指导期满的次日起规定期限内（特急、紧急、一般分别在2个、4个、7个工作日）提出督导意见，在规定期限内未进行督导或督导意见明显不当的，分别对合规部门合规员和负责人给予每次记4分、2分的处罚。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第三章  具体规定', '第九条', '对经营单位已整改问题，职能部门应在经营单位录入整改结果的次日起3个工作日内进行鉴定。职能部门鉴定不予确认的，经营单位应继续组织整改，直到鉴定确认。未在规定期限内进行鉴定或鉴定确认结果明显不当的，分别对职能部门合规员和负责人分别给予每次记4分、2分的处罚。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第三章  具体规定', '第十条', '对职能部门已鉴定的问题，合规部门应在职能部门录入鉴定意见的次日起3个工作日内进行鉴定。合规部门鉴定不予确认的，退回职能部门的，职能部门应重新鉴定；退回经营单位的，经营单位应继续组织整改，直到鉴定确认。合规部门未在规定期限内进行鉴定或鉴定确认结果明显不当的，分别对合规部门合规员和负责人分别给予每次记4分、2分的处罚。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第三章  具体规定', '第十一条', '存在问题是在单位负责人、合规员或业务主管的授意、指使、教唆下发生的，授意、指使、教唆人员同样认定为直接责任人，给予从重处罚。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第三章  具体规定', '第十二条', '对存在问题的直接责任人，总行按相关问责办法进行处理，涉嫌犯罪的移送司法机关，依法追究法律责任。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第四章  附则', '第十三条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《合规检查跟踪系统管理办法》', '第四章  附则', '第十四条', '本办法自发文之日起施行。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第一章  总则', '第一条', '为促进xxx银行（以下简称“本行”）合规管理水平的提高，有效监测、识别、评估和控制合规风险，确保本行发展战略和经营管理目标的实现，依据《中华人民共和国银行业监督管理法》、《商业银行合规风险管理指引》等法律法规、规章政策和省联社关于合规管理的意见要求，结合本行的实际情况，制定本办法。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第一章  总则', '第二条', '本行的合规管理目标是指通过建立健全合规风险管理框架，实现对合规风险的有效监测、识别、评估和控制，促进本行全面风险管理体系的建设，确保本行依法合规经营。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第一章  总则', '第三条', '本办法所指合规的含义是本行的经营管理活动应与法律、规则和准则相一致；合规风险的含义是本行在经营管理活动中因未能遵循法律、规则和准则，而可能遭受法律制裁、监管处罚、重大财务损失或声誉损失的风险；合规管理是指本行采取各类措施识别、评估、监测合规风险，确保本行合规稳健运行。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第二章  合规文化', '第四条', '合规文化是企业文化的核心组成部分，本行坚持“合规从高层开始，合规人人有责”的方针，奉行主动合规、全员合规、全面合规、持续合规的合规理念，具体包括：1.主动合规。每位员工应时刻遵守并严格执行各项规章制度，主动对自身经办的业务进行认真审查，并对其业务合规性负责；同时每位员工也有权利和义务举报其知晓的其他员工违规行为。2.全员合规。合规是全体员工的共同责任，倡导人人合规理念，营造人人合规环境，并应从经营管理层做起，在全行推行诚信正直和公平交易的行为准则。3.全面合规。及时制订和完善制度办法，确保其有效性和可操作性，实现制度办法在各项业务流程、各个工作岗位、各个操作环节的全覆盖，并对所有制度办法认真执行。4.持续合规。建立和完善长效的合规管理机制，为本行的可持续发展提供保障。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第三章  合规管理程序', '第五条', '合规管理程序主要包括以下环节：1.董事会审议批准并监督实施合规政策；2.经营管理层制定和贯彻执行合规政策；3.监事会监督董事会和经营管理层合规管理履职情况；4.合规管理部门（以下简称“合规部门”）承担合规工作综合管理职能，拟定合规政策以及管理规章制度，组织各支行和各部门开展合规管理工作；5.各支行和各部门监测、识别、评估和控制合规风险；6.各部门拟定和执行规章制度以贯彻落实合规规则，检查和监督执行情况；7.各支行执行规章制度，并对执行情况进行自查；8.合规部门审核各部门拟定的规章制度并提供支持帮助，进行监督检查；9.内部审计部门对各支行和各部门经营管理活动的合规情况进行独立审计；10.本行按规处理违规事件，追究违规责任，各支行、各部门采取纠正措施，跟踪整改结果。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第四章  合规管理职责', '第六条', '董事会应履行以下合规职责：1.审议批准本行的合规政策，并监督合规政策的实施；2.审议批准经营管理层提交的合规管理报告，并对管理合规风险的有效性作出评价；3.授权董事会下设的风险管理与关联交易控制委员会、审计委员会对本行合规风险管理进行日常监督； 4.确保本行建立并实施适当的合规绩效考核、问责（免责）机制、激励机制等配套措施；5.董事会制订的本行发展战略应体现对合规文化的倡导和鼓励，建立合规管理的长效机制；6.部署、推动、监督本行案件防控工作；6.《章程》规定的其他合规管理职责。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第四章  合规管理职责', '第七条', '董事会下设的风险管理与关联交易控制委员会负责监督本行日常合规管理工作，通过与合规分管领导单独面谈和其他有效途径，了解合规政策的实施情况和存在问题，及时向董事会或经营管理层提出相应的意见和建议，监督合规政策的有效实施，并承担董事会授权的其他合规管理工作。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第四章  合规管理职责', '第八条', '监事会负责监督董事会和经营管理层合规管理的有效性，应履行以下合规职责：1.监督董事会、经营管理层建立完善风险管理体系；2.监督董事会、经营管理层履行合规管理职责，并至少每半年评价一次；3.对经营管理层执行合规管理政策情况实施检查；4.要求董事会成员及经营管理层人员纠正其损害机构整体利益的行为并监督执行；'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第四章  合规管理职责', '第九条', '经营管理层应有效管理本行的合规风险，履行以下合规职责：1.制定书面的合规政策，并根据合规管理状况以及法律、规章和准则的变化情况适时修订合规政策，报经董事会审议批准后传达给全体员工； 2.贯彻执行合规政策，确保发现违规事件时及时采取适当的纠正措施，并追究违规责任人的相应责任； 3.任命合规负责人，并确保合规负责人的独立性； 4.明确合规部门及其组织结构，为其履行职责配备充分和适当的合规管理人员，并确保合规部门的独立性； 5.识别本行所面临的主要合规风险，审核批准合规风险管理计划，确保合规部门与风险管理部门、内部审计部门以及其他相关部门之间的工作协调；6.明确合规部门为案防工作的牵头部门，配置有效履行案防职能的资源。7.每半年向董事会提交合规风险管理报告，报告应提供充分依据有助于董事会成员判断经营管理层管理合规风险的有效性； 8.及时向董事会或其下设专门委员会、监事会报告任何重大违规事件； 9.每半年至少一次专题会议，评估本行所面临的主要合规风险以及化解合规风险的计划和措施；10．章程和董事会授权的其他合规管理职责。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第四章  合规管理职责', '第十条', '合规部门应履行以下职责：1．持续关注法律、规则和准则的最新发展，正确理解法律、规则和准则的规定及其精神，准确把握法律、规则和准则对本行经营的影响，及时为经营管理层提供合规建议； 2．制定并执行合规案防管理计划，包括合规检查、合规风险评估、合规培训与教育、员工行为排查等； 3．牵头组织各部门建立、完善本行的制度办法和操作规程，使其符合法律、规则和准则的要求；4．协助人事部门对所有员工进行合规培训，重点做好新入行员工、新上任中层管理人员、高风险部门和关键岗位人员的合规培训；5．接受员工有关合规问题的咨询； 6．组织制定合规管理程序以及合规手册、案防手册、员工行为准则等合规指南，为员工恰当执行法律、规则和准则提供指导； 7．积极主动地识别和评估与本行经营活动相关的合规风险，包括为新产品和新业务的开发以及新客户的拓展提供必要的合规性审核和测试等，建立完整的数据库，提出合规管理建议，并跟踪建议的落实情况，同时采取措施积极防范潜在风险； 8．实施合规案防风险的检查和测试，评估本行制度办法是否符合法律、规则和准则的要求以及是否得到有效执行，定期向经营管理层报告；9．保持与监管机构日常的工作联系，跟踪和评估监管意见和监管要求的落实情况。10．其他合规管理工作。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第四章  合规管理职责', '第十一条', '兼职合规员的职责：1.协助负责人贯彻执行本行的合规政策、制度办法等文件精神，予以监督；2.协助负责人组织所在单位（部门）人员学习相关的法律法规、监管规定、合规风险管理指引等文件精神，引导全员合规；3.有效监测、识别、评估和控制本单位（部门）经营管理中的风险点；4.做好本单位（部门）员工的合规性咨询和业务辅导工作；5.及时向单位（部门）负责人、合规部门报送合规报告、合规管理信息等；6.向所在单位（部门）负责人以及合规部门提出合规管理的建议、意见。7.协助处理危机、投诉事件等。8.其他合规管理工作。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第五章  合规流程管理', '第十二条', '建立合规风险的全流程管理，包括识别、监测、评估、控制环节。合规部门应组织开发、运用合规管理系统,提升对合规案防风险的识别、监测、评估和控制能力，保障合规管理效果。同时，应建立多途径的监测途径，通过各单位（部门）报送的合规报告、经营单位自查、业务部门检查、内审部门稽核、合规部门自身组织的检查、案防举报等方式，积极主动监测本行在经营活动中所产生的合规风险。合规部门应对业务职能部门的年度检查计划、检查方案的合规性进行审查，监督计划的执行情况，收集并分析检查报告。合规部门应密切跟踪银行最新适用的法律法规和准则等有关要求，对监测到的风险点进行准确识别，及时建立违规问题数据库，并对影响大小进行评估，适时提出合规提示或合规建议，并跟踪提示或建议的落实情况。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第五章  合规流程管理', '第十三条', '建立合规风险逐级报告机制。各单位（部门）兼职合规员定期向合规部门报送合规报告，同时应向本单位（部门）负责人报送；合规部门应定期向经营管理层报告合规管理情况，并上报监管部门以及上级管理部门；经营管理层应定期向董事会以及风险管理与关联交易控制委员报告合规管理情况。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第六章  合规部门', '第十四条', '本行合规管理的组织架构应与本行的管理体制架构相匹配，与本行的全面风险管理组织体系相适应，并确保合规部门的独立性。本行合规管理的组织架构为：1．本行设立合规部门，配备专兼职合规人员。合规部门配备专职合规人员，专门从事合规管理工作,其中至少配备一名具有法律专业背景的人员；各职能部门、各支行至少应配备一名兼职合规员;探索向业务量较大的机构、重要部门以及问题严重的机构派驻专职合规管理员；2．本行明确一名经营管理层成员全面负责协调合规风险管理，不得兼管业务条线，并确保合规人员的合规职责与其所承担的其他职责之间不产生利益冲突。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第六章  合规部门', '第十五条', '合规部门或合规人员在职责范围内独立履行工作职责。合规管理部门和合规管理人员在履行工作职责时，有获取相关工作信息并与相关人员进行沟通的权利，其它部门、机构和员工在提供这些信息方面有给予密切合作的义务。对可能违反合规管理规定的事项，合规部门可独立进行调查，经授权合规部门也可委托其它机构进行调查；经调查发现的异常情况或可能的违规行为，合规部门可直接向董事会或其下设专门委员会报告。本行经营管理层应确保合规部门报告路线的畅通；合规部门接受审计部门的合规审计，对合规人员履职行为应给予客观公正的评价。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第六章  合规部门', '第十六条', '合规部门应根据业务计划编制合规计划，对列入计划的业务资源，本行应给予有效保障，包括必要的人力、财力和技术支持等。合规部门应配备充分和适当的合规人员。合规人员应具有与其职责履行相匹配的资质、经验、专业技能和个人素质，具备正确把握合规政策对本行经营影响的能力，能持续跟踪法律、法规和准则的最新变化，及时做好外规内化工作。合规部门在开展合规风险监测、识别、评估和控制工作中应有效利用核心业务系统、信贷业务系统或其它业务支持系统，设计合规风险监测指标，提高管理合规风险的技术手段。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第七章  与其他部门的关系', '第十七条', '合规部门与业务部门的关系1．各业务部门定期、不定期开展检查，并向合规部门报送检查计划、检查方案、检查报告；2．各业务部门应依据相关法律、规则和准则的变化和合规部门的建议，及时起草、修订相关的规章制度和流程；3．合规部门为各业务部门提供合规咨询和意见，并为业务与产品创新提供合规支持。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第七章  与其他部门的关系', '第十八条', '合规部门与风险管理部门的关系1．合规部门与风险管理部门应坚持“各尽其职、协同合作”的原则，明确各自的管理职责和权限，同时应相互合作； 2．合规部门应就其发现的操作风险等问题及时告知风险管理部门，并为风险管理部门提供必要的合规支持；风险管理部门应加强自身部门的合规性管理，并就其发现的合规风险通报合规部，为合规部门管理合规风险提供信息、技术等方面的支持。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第七章  与其他部门的关系', '第十九条', '合规部门与内审部门的关系1．合规部门与内审部门相互分离但又相互支持，均有权对经营活动进行合规性检查；2．合规部门应接受内部审计部门的检查、监督和评价，内审部门应对合规部门的履职情况定期进行评价；3．内审部门应将有关合规检查情况及结论随时告知合规部门，为合规部门识别、监测和评估合规风险提供信息来源和依据；'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第七章  与其他部门的关系', '第二十条', '合规部门与监察部门的关系1．合规部门与监察部门在保证全体员工行为合规、纠正违规等方面负有共同责任，应在合规管理事务方面建立密切合作机制。2．监察部门在日常工作中掌握的重大违规信息应及时提供给合规部门共享。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第七章  与其他部门的关系', '第二十一条', '合规部门与其他职能部门的关系1．其他职能部门对本部门业务条线的合规性负有直接责任；2．其他职能部门应主动寻求合规部门的支持和帮助，主动进行定期或不定期的合规性自查，并向合规部门提供检查报告以及合规风险信息；3．其他职能部门应支持并配合合规部门的风险监测、识别、评估和控制活动。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第七章  与其他部门的关系', '第二十二条', '合规部门与外部监管部门的联系合规部门作为本行与外部监管部门联系的枢纽，负责与监管部门保持日常的工作联系，并承担以下职责：1．负责对监管部门下发的监管政策、监管意见和风险提示等监管文件进行传导和阐释，持续跟踪在本行内部的执行情况，为员工理解、执行合规文件提供咨询或指导；2．与监管部门进行必要的工作联系，按时向监管部门报送相关的文件、报告、报表等资料；3．积极参与监管部门组织的工作座谈、调研、合规培训等活动，提出相关意见或建议；4．跟踪监管部门意见的实施和要求的落实情况。5．按监管要求向监管部门备案，报告合规部门总经理的任命和离任情况等监管事项。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第八章  合规责任', '第二十三条', '合规不仅仅是合规部门或专职合规人员的责任，更是本行每一位员工的责任。合规部门作为经营管理层下专门管理合规风险的职能部门，合规人员作为合规管理的工作人员，应履行尽职责任，每一位员工应对其所经办的经营活动的合规性负直接责任。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第八章  合规责任', '第二十四条', '董事会对本行经营活动的合规性负最终责任，经营管理层对本行经营活动的合规性负管理责任，监事会对本行经营活动的合规性负监督责任，各部门负责人、支行负责人应对本部门、本单位经营活动的合规性负首要责任。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第八章  合规责任', '第二十五条', '本行的绩效考核应充分体现“倡导合规、惩处违规”的价值观，注意协调业务拓展与合规管理的关系，以增强全体员工的合规意识，促进本行良好合规文化的形成。本行应建立有效的合规评价制度，考核评价各业务部门、支行人员管理合规风险的能力时，应征询合规部门负责人的意见。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第八章  合规责任', '第二十六条', '本行应制定违规的内部责任追究制度和纠错机制，落实合规责任。对违规责任人的处理过程和处理结果，以及所采取的纠正措施，应充分体现本行的合规价值取向。本行鼓励主动报告合规风险，对于发现或者应当发现合规问题却隐瞒不报的，一旦被外部监管部门或内部职能部门查实，应给予隐瞒不报者严厉处罚；对于主动报告合规问题或合规风险隐患的，本行可视情况给予主动报告者减轻处罚或免予处罚。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第九章  附则', '第二十七条', '本办法适用于本行各部门、各支行及其员工。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第九章  附则', '第二十八条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《合规管理办法(修订）》', '第九章  附则', '第二十九条', '本办法自董事会通过并颁布之日起实施。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第一章  总则', '第一条', '为了培育xxx银行（以下简称“本行”）全面风险管理理念，强化合规风险的全程管理，增强监测、识别、评估、控制风险能力，提高合规风险管理水平，确保本行安全稳健运行，根据中国银行业监督管理委员会《商业银行合规风险管理指引》、《农村中小金融机构风险管理机制建设指引》等规定和要求，并结合本行实际，制定本制度。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第一章  总则', '第二条', '本制度所指的合规管理联动是指本行各合规管理主体在履行合规管理职责过程中，通过一定的路径，与其他主体之间互相配合，开展交流与协调的联动机制。合规联动分内部联动与外部联动，内部联动是指本行各合规主体在业务开展中实现信息共享、统一行动、各司其职、分工落实，实现团队联动的构成、职责和运作方式；外部联动是指本行合规管理与省联社及银监、人行等外部监管机构（以下统称“外部监管机构”）相互协调的联动机制。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第一章  总则', '第三条', '合规联动的目标：建立各职能部门之间、各职能部门与经营单位以及本行与外部监管机构之间的联动机制，巩固三道防线，确保合规经营，风险可控，以实现本行的可持续发展。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第一章  总则', '第四条', '本行成立合规管理联动工作领导小组，由行长担任组长，各位副行长为副组长，各职能部门、各支行负责人为成员。领导小组下设办公室，办公室设在合规管理部，负责合规管理联动工作的统一组织协调。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第二章  职责分工', '第五条', '以各经营单位为第一道防线，业务管理部门、合规风险部门为第二道防线，审计部门为第三道防线，不同防线分工明确、职责清晰、相互制衡、运行高效。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第二章  职责分工', '第六条', '第一道防线的职责按照本行规章规定，建立、健全本单位（部门）内控机制，明确岗位职责，开展对员工的业务辅导、工作检查和日常管理，监测、识别、评估、控制风险。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第二章  职责分工', '第七条', '第二道防线的职责业务管理部门的职责：负责对全员开展本条线的业务辅导、培训、检查和管理，监测、识别、评估、控制本条线的风险。风险管理部门的职责：负责全行风险监测、识别、评估、控制或缓释等，组织对风险管理政策、制度和流程的执行效果进行检查评估，根据风险报告制度进行报告。合规管理部门的职责：制订合规政策，结合外部法律、规则和准则的最新发展变化，做好外规内化工作，监督各条线、各经营单位的制度执行，引导全员合规。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第二章  职责分工', '第八条', '第三道防线的职责开展独立审计，评价本行内控机制是否健全完善并有效运行，监督全行执行国家方针、法律法规、监管政策以及本行的规章制度情况。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第三章  机制联动', '第九条', '按照联动方向，合规联动可分为向上联动和向下联动以及与本行与外部监管部门的联动。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第三章  机制联动', '第十条', '向上联动一道防线应向二三道防线报告本单位（部门）的管理情况，包括但不限于：向业务管理部门报送本单位（部门）的管理报告、报表；向合规风险管理部报送合规报告、风险报告，向合规、风险、审计部门报送案件风险核查情况等。本单位（部门）出现重大风险的，除须在24小时内向业务管理部门、合规风险部门报告外，还必须向经营管理层报告。业务管理部门、合规风险管理部门分别为二道防线的前置部门、后置部门。业务管理部门必须向合规风险管理部门报告本条线的管理情况，包括但不限于：定期向合规管理部报送培训计划、检查计划、制度修订计划、合规风险报告，检查前后分别报送检查方案、检查报告。本条线出现重大风险的，除须在24小时内向合规风险部门报告外，还必须向经营管理层报告。业务管理部门必须配合合规风险部门、审计部门，对后者所开展检查、审计发现的问题，进行跟踪督导，督促各经营单位整改。根据审计工作需要的，合规风险部门应向审计部门提供监管政策变化、全行合规风险管理情况。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第三章  机制联动', '第十一条', '向下联动审计部门的审计报告应及时向合规风险管理部门提供,涉及业务条线的，应向相关业务管理部门提供《协助整改意见书》,以便相关部门掌握存在的问题和合规风险状况。审计部门应及时向被审计单位出具审计意见，对本行共性问题应通过审计简报进行通报，提示面上防范和整改。审计部门可以审计简报、专题会议等方式，分析存在问题，指导各单位、各部门加强内控管理。合规管理部门应为业务管理部门提供合规咨询、培训等，通过提供合规建议、提示、评价等方式，指导、督促业务部门管理合规风险，并为业务发展或产品创新等提供合规支持。合规风险部门的检查报告应及时向相关业务管理部门提供,以便业务管理部门掌握本条线存在的问题和合规风险状况。同时，检查结果应及时向被检查单位提供，对本行共性问题应进行通报，提示面上防范和整改。合规风险部门应定期召开合规风险员会议，开展合规风险培训，分析全行合规风险情况，指导各单位、各部门有效开展合规风险监督。业务管理部门应重视培训辅导和业务检查工作。培训工作应有计划的开展，并对培训效果进行考核。业务管理部门应定期、不定期开展检查，检查项目应覆盖本条线，并突出重要环节、主要风险点。检查结果应及时告知被检查单位，对本行共性问题应进行通报，提示面上防范和整改。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第三章  机制联动', '第十一条', '本行与监管部门的联动通过参加外部监管机构召开的会议以及监管谈话、专题报告等方式，向外部监管机构报告本行管理情况以及存在问题，掌握外部监管政策、方向、重点，按照监管要求进行问题整改和问责。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第三章  机制联动', '第十二条', '除上述向上联动、向下联动以及与监管部门的联动外，会议联动、检查联动和制度修订是另外3种重要方式。合规管理联动工作领导小组至少每半年召开一次合规管理联动工作会议，通报合规管理联动工作情况和信息，并进行总结部署。检查联动是根据检查需要，业务管理部门、合规风险以及审计部门之间相互配合，共同组织检查。制度修订通常由业务管理部门拟稿，相关部门会签、合规部门审核等环节联动组成。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第四章  罚则', '第十三条', '对部门、经营单位不积极配合，给本行合规联动工作带来不利影响的，或被监管部门、省联社通报的，将按照本行相关办法对有关负责人和责任人员进行处罚。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第五章  附则', '第十四条', '本制度未尽事宜以及与国家有关法律法规、规章的规定冲突的，按国家法律法规、规章等规定执行'),
(NULL, '合规管理部', '《合规管理联动制度》', '第五章  附则', '第十五条', '本制度由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《合规管理联动制度》', '第五章  附则', '第十六条', '本办法自印发之日起执行。'),
(NULL, '合规管理部', '《员工参与民间融资处置预案（修订）》', '', '第一条', '为应对复杂多变的经济金融形势，防范外部风险向银行内部转移，有效控制员工参与民间融资带来的各类风险，根据《中国银行业监督管理委员会关于印发〈银行业突发事件应急预案〉的通知》、《中国银行业监督管理委员会办公厅关于印发〈重大突发事件报告制度〉的通知》等文件精神，总行特制订本应急预案。'),
(NULL, '合规管理部', '《员工参与民间融资处置预案（修订）》', '', '第二条', '员工出现下列情形之一，即视为参与民间融资，总行应立即启动应急处理机制。㈠以各种形式参加非法集资活动；㈡介绍机构和个人参与高利贷或向机构和个人发放高利贷；㈢借银行名义或利用银行员工身份私自代客投资理财；㈣与客户发生非正常资金往来（含作为中间人介绍票据业务、从事资金掮客活动等）；㈤自办或参与经营典当行、小额贷款公司、担保公司等机构；㈥向民间借贷资金提供担保；㈦允许非本行员工以各种方式进入银行业金融机构办公或营业场所开展民间借贷、非法集资活动等。'),
(NULL, '合规管理部', '《员工参与民间融资处置预案（修订）》', '', '第三条', '应急处理遵循的原则统一领导，分级负责。总行对员工参与民间融资应急处理工作实行统一领导，各支行负责人按照总行的要求具体负责本支行（含营业部、公司部、小微业务部，下同）员工参与民间融资的防范处置工作。㈡及早排查，及时报告。各支行、部门要重视员工账户交易及交友等情况，发现异常情况，要尽可能摸实情况。涉嫌参与民间融资的，要按规定向总行报告。㈢查实情况，果断处理。总行接到支行报告，反映员工参与民间融资的，要及时成立专门调查组，采取各种手段、方法摸清涉嫌人员参与情况。总行视员工参与民间融资后果、影响，对当事人进行处理。涉嫌犯罪的，移送司法机关处理。'),
(NULL, '合规管理部', '《员工参与民间融资处置预案（修订）》', '', '第四条', '总行、支行分层成立应急小组总行成立应急处置领导小组，为非常设机构。董事长江志刚为组长，行长刘刚、监事长叶厚明为副组长，总行领导班子其他成员、机关各部室负责人为成员。应急处置领导小组下设办公室于合规管理部。各支行相应成立应急预案小组，负责人为组长，合规员、职工代表为成员。'),
(NULL, '合规管理部', '《员工参与民间融资处置预案（修订）》', '', '第五条', '应急处置内容㈠有效排查。各支行成立应急预案小组后，要明确小组成员分工，切实开展防范、排查工作。不仅要对员工、员工配偶账户资金流水进行监控，发现频繁交易、大额交易、与授信客户交易等异常情况存在的，要了解交易背景、事实，而且要关注员工的交友情况，与担保公司、典当行、票贩、民间融资组织者联系较多的，要重点关注，并且留意其他迹象。㈡及时报告。各单位、部门员工出现交友、资金流水等疑点，多个方面得到一致印证的，必须在6小时内向总行合规管理部书面报告。发现个别重要疑点，但无法从其他方面得到印证的，必须在24小时向总行合规管理部书面报告。㈢核实情况。接到报告后，总行48小时内成立专门工作组，迅速组织全面、深入调查，采取调阅账户、走访客户、职工谈话以及公示监督等方式，摸清情况后向总行应急处置领导小组及时报告。㈣恰当处置。确定员工涉及组织、参与民间融资，原则上3个月内必须对当事人处置结束。融资数额超过50万元或产生恶劣影响的，将对当事人给予开除处分；融资数额不足50万元且社会影响较小的，将视融资结果、社会影响对当事人进行相应的行政处分和经济处罚，但必须调离重要岗位。同时，将追究当事人所在单位负责人、运营主管以及总行相关管理部门人员的失职责任。构成案件的，向公安部门及时报告，移交司法处理。㈤适当披露。查实员工参与民间融资后，由总行办公室统一做好信息披露工作，及时、稳妥公布事实情况。遇有客户疑惑或散布谣言，当事人所在单位应做好解释工作，必要时向公安机关报警，避免事态进一步扩大。同时，应及时向银监部门、省联社报告事发情况和对当事人的处理情况。㈥总结弥补。查实员工参与民间融资后，总行应急预案领导小组应分析形成原因，排查管理漏洞，并在全行范围内进行警示教育。同时，总行依法加大对员工的监督，并且及时弥补管理漏洞，防范类似问题再次发生。'),
(NULL, '合规管理部', '《员工参与民间融资处置预案（修订）》', '', '第六条', '考核情况当事人参与民间融资的，取消当事人、失职管理人员以及所在单位、部门争先评优资格。同时，扣发相关单位、部门当年年终效益考核工资，停发当事人当年所有考核款项，停发失职管理人员一年管理职能工资。'),
(NULL, '合规管理部', '《员工参与民间融资处置预案（修订）》', '', '第七条', '本预案由xxx银行合规管理部负责解释和修订，自印发之日起施行。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第一章  总则', '第一条', '为建立员工行为排查长效工作机制，提升全行员工行为管理水平和实效，保障xxx银行（以下简称“本行”）稳健发展，根据《商业银行操作风险管理指引》、《银行业金融机构案件风险排查管理办法》以及《xxx银行案防工作实施办法》等规定，结合本行实际，制定本办法。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第一章  总则', '第二条', '本办法所称员工行为排查，是对全行员工涉嫌案件的行为以及可能诱发案件的行为主动识别，并及时采取措施控制、化解风险的管理活动。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第一章  总则', '第三条', '本办法适用于所有在编人员（含退养员工），派遣人员参照本办法执行。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第一章  总则', '第四条', '员工行为排查在本行案件防控领导小组领导下开展，合规管理部归口管理和牵头组织，各支行和部门具体实施以及配合。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第一章  总则', '第五条', '各支行（部门）对本支行（部门）员工行为排查负主体责任，负责人为排查的第一责任人，合规员为协查责任人。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第二章  排查原则', '第六条', '员工行为排查应遵循以下基本原则：（一）责任明确。建立排查工作责任制，坚持“谁主管、谁负责，谁排查、谁负责”方针。（二）全面覆盖。员工行为排查应覆盖全行所有员工及其各项业务，各支行、部门应对每位员工行为进行识别与监测，确保排查工作无死角、无盲点。 （三）重点突出。员工行为排查应将既往存在问题的员工和新发现“行为异常、社交异常、收入异常和家族异常”不良苗头或可疑迹象的员工作为排查重点，深度排查了解。（四）实事求是。排查问题坚持客观公正，不得无中生有、颠倒是非，不得打击报复、诽谤诬陷他人。（五）群防群治。建立员工联保和有奖举报机制，充分发挥员工的主人翁意识，全员应积极参与监督排查。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第七条', '员工行为排查重点关注以下两类行为：（一）涉嫌案件的行为；（二）存在风险隐患可能诱发案件的行为。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第八条', '涉嫌案件的行为，是指可能构成犯罪案件的可疑行为，包括但不限于侵占挪用、贿赂、诈骗、金融从业人员特定犯罪、其他犯罪等。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第九条', '侵占挪用类包括但不限于：（一）侵占本行及客户资金；（二）挪用本行及客户资金；（三）设立小金库或账外经营；（四）组织、参与私分本行财产。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第十条', '贿赂类包括但不限于：（一）索取、收受贿赂；（二）行贿；（三）介绍贿赂。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第十一条', '诈骗类包括但不限于： （一）诈骗罪；（二）贷款诈骗罪；（三）骗取贷款、票据承兑、金融票证罪；（四）票据诈骗罪；（五）金融凭证诈骗罪；（六）信用证诈骗罪；（七）信用卡诈骗罪；（八）有价证券诈骗罪；（九）集资诈骗罪；'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第十二条', '金融从业人员特定犯罪类包括但不限于：（一）违法发放贷款；（二）吸收客户资金不入账；（三）违规出具金融票证；（四）对违法票据承兑、付款、保证；'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第十三条', '其它犯罪包括但不限于：（一）背信运用受托财产罪； （二）非法吸收公众存款；（三）高利转贷；（四）伪造、变造货币以及相关犯罪；（五）其它犯罪。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第十四条', '存在风险隐患可能诱发案件的行为，是指虽未构成案件，但已违反法律法规、规章准则、监管规定、省联社以及本行制度办法和禁令规定，如果不及时采取处置措施，可能发展成案件的事项。排查要点包括但不限于：（一）严禁以各种形式参与民间融资（二）严禁违规签署法律文书（三）严禁与客户发生非正常资金往来（四）严禁非法访问计算机系统（五）严禁经商办企业（六）严禁违规放贷（七）严禁收受或索取贿赂（八）严禁代客理财（九）严禁商业欺诈（十）严禁内幕交易（十一）严禁侵占银行和客户资金财产及权益（十二）严禁从事账外经营（十三）严禁内外勾结，骗取银行信用（十四）严禁泄露商业机密（十五）严禁违规使用业务档案（十六）严禁违规办理柜台业务（十七）严禁违规使用和管理重要单证（十八）严禁违规代客户办理银行业务和保管物品（十九）严禁擅自接受媒体采访（二十）严禁参加其他违法违规活动（含“九种人”行为，已在上述条款单列除外）。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第三章  排查内容', '第十五条', '员工个人账户是本行排查的重点抓手。账户仅限员工本人用于员工及其家庭成员（指员工的父母、配偶、子女）日常生活支出，以及在本行办理的合法、合规的资金业务，大额交易（单笔超过5万元以及当月超过50万元）必须排查交易实质、背景。员工账户在使用与管理过程中严禁有下列行为：（一）严禁员工通过本人账户归集、过渡银行和客户资金、代客户套取资金、违规转账；（二）严禁违规利用本人账户为客户办理支付结算业务、代办存款、代客还贷、代客理财、外汇买卖等；（三）严禁空存、空取现金，利用本人账户虚增存款和库存现金，为客户办理大额取现业务；（四）严禁通过网银进行虚假交易；（五）严禁通过本人账户代收保险手续费等中间业务收入；（六）严禁拆分交易（化整为零）逃避监管和授权权限控制；（七）严禁本人账户与客户账户违规发生资金往来；（八）严禁利用本人账户存放、过渡经费账户资金；（九）严禁利用贷记卡进行套现；（十）严禁持有并使用他人活期存款账户，利用该账户代办存款、代客还贷、代办结算、调剂资金等。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第四章  员工行为排查方式', '第十六条', '员工行为排查以支行（部门）日常排查为主，总行集中排查、滚动审计为辅。日常排查定期进行。各支行（部门）兼职合规员应每月至少对本支行（部门）全员进行排查一遍，负责人应每季至少对本支行（部门）全员进行排查一遍，其它员工应加强监督，积极参与和配合排查，主动报告问题。总行集中排查由合规管理部牵头组织实施，每年不少于2次，且前后排查的时间跨度应该无缝对接。审计部开展全面审计，应将员工行为排查作为重要项目，三年内对全辖网点员工应检查一遍。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第四章  员工行为排查方式', '第十七条', '员工行为排查，按排查主体分为员工自查、员工互查、专人排查。（一）员工自查，是指员工对照规定的排查项目，对本人是否存在涉嫌犯罪和不良行为进行自我检查，如实向所在支行（部门）报告。（二）员工互查，是指员工对照规定的排查项目，对一定范围内的其他员工行为进行的排查，包括但不限于制约、监督岗位和联保小组成员开展的排查。（三）专人排查，是指员工所在支行（部门）的负责人、兼职合规员以及总行相关部门（包括不限于审计、合规、监察、人事、信贷、运营等部门）人员对员工进行的排查。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第四章  员工行为排查方式', '第十八条', '员工行为排查，按排查方式分为飞行排查、常规排查。（一）飞行排查，是指不提前通知的情况下，突击对员工进行的排查。（二）常规排查是指先下发排查通知，然后到排查单位进行的排查。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第四章  员工行为排查方式', '第十九条', '员工行为排查应当结合实际，灵活运用各种方式，包括但不限于：（一）观察。察看、留意员工的言行举止，及时发现异常情况。（二）谈话。主动与员工谈心，掌握员工思想动态变化。（三）走访。走访员工家庭、客户以及当地公安、司法、纪检、工商等有关部门了解情况。（四）公开征集问题线索。通过站牌、网络、微信等方式，公示排查内容、反馈途径等，通过员工及公众举报获取问题线索。（五）调阅分析。调阅监控录相、履职资料、账户往来、征信记录等进行的排查。（六）问卷。组织员工填写调查问卷，了解员工行为动态。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第四章  员工行为排查方式', '第二十条', '对收到举报线索，总行应对被举报员工认真排查，核实违规情况，不得交由被举报单位（部门）自行调查。总行不仅保护举报人隐私，而且对举报属实的举报人员进行奖励。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第五章  报告整改', '第二十一条', '各支行（部门）兼职合规员负责对发现问题督促整改，不能现场整改的必须对责任人签发整改通知书，明确整改要求、整改期限，并且跟踪问题整改到位。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第五章  报告整改', '第二十二条', '总行建立员工行为排查档案，对有不良行为的员工实行名单化台账管理，明确专人跟踪、监测，督促整改。各支行应按总行规定报送员工行为排查资料，总行做好收集、整理、归档。排查内容包括但不限于排查表、排查报告、会议记录、员工谈话记录、家庭走访记录、账户流水以及其他查证记录等。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第五章  报告整改', '第二十三条', '各支行（部门）应对照第三章排查内容，全面深入排查，次月 2 日前向合规管理部报送上月的员工行为排查报表（附件1）、报告，反映问题及整改情况。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第五章  报告整改', '第二十四条', '合规管理部对各支行（部门）发现的涉嫌案件或者重大风险事件的线索，应提请总行成立专门工作小组，全面、深入进行调查。构成犯罪的，移送司法机关处置。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第六章  责任追究', '第二十五条', '排查人员在员工行为排查工作中有下列情形之一的，按照本行规定追究渎职或失职责任，从重处理。支行（部门）负责人在责任年度内发生因排查不力发生案件的，应主动引咎辞职。（一）应组织排查而未组织排查的，导致发生案件或本行资金、声誉损失扩大的；（二）排查工作流于形式，应当发现问题而未发现，导致发生案件或本行资金、声誉损失扩大的；（三）排查发现问题但未如实上报的，导致总行未能及时发现案件线索的；（四）对违反保密要求造成排查信息泄露或扩散，产生不良后果的。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第六章  责任追究', '第二十六条', '排查发现不良行为，总行对责任人员采取批评教育、经济处罚、组织处理、纪律处分等措施，并对其经办业务进行重点检查。对违法违规性质恶劣或者造成本行资金、声誉损失的，以及未在规定期限内整改，本行予以开除或解除劳动关系。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第七章  附则', '第二十七条', '本办法由xxx银行合规管理部负责解释和修改。'),
(NULL, '合规管理部', '《员工行为排查管理办法》', '第七章  附则', '第二十八条', '本制度自印发之日起施行。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第一章 总则', '第一条', '根据xxx银行（以下简称“本行”）商务转型和全面风险管理建设要求，为加强本行业操作风险管理，促进本行建立健全操作风险管理体制和机制，完善全面风险管理体系，实现各项业务可持续发展，依据银监会《商业银行操作风险管理指引》、《商业银行资本管理办法（试行）》、《商业银行内部控制指引》、《银行业金融机构全面风险管理指引》以及省联社《江苏省农村商业银行全面风险管理指导意见（试行）》、《江苏省农村信用社联合社关于操作风险管理的指导意见（试行）》等规定，结合本行实际，制定本办法。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第一章 总则', '第二条', '本办法适用于各支行、各部门。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第一章 总则', '第三条', '本办法所称操作风险是指由不完善或有问题的内部程序、员工和信息科技系统，以及外部事件所造成损失的风险，包括法律风险，但不包括策略风险和声誉风险。操作风险管理是识别、评估、监测、控制及缓释、报告操作风险的全过程。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第一章 总则', '第四条', '操作风险管理目标通过建立科学的操作风险管理组织架构和内部控制体系，划分明确的风险管理职责，制定有效的风险管理策略、程序和制度，确定操作风险偏好，使用适当的操作风险管理工具和方法，把操作风险的管理纳入到全面风险管理体系中，实现对操作风险的量化管理，最大程度减少操作风险事件，最大限度降低操作风险损失。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第一章 总则', '第五条', '本行操作风险管理遵循以下原则：（一）全覆盖原则。本行操作风险管理要覆盖各项业务、产品和各个操作环节，覆盖所有部门、网点和岗位，并由全体员工执行。（二）制衡性原则。本行操作风险管理办法应符合本行董事会战略安排要求，按照内部控制的制衡模式，确保得到全面贯彻执行，任何人在任何岗位办理任何业务均受内部控制约束。（三）审慎性原则。本行操作风险管理应当坚持风险为本、审慎经营的理念。开展经营活动时，尤其是涉及设立机构或开办新业务时，应坚持内控优先；在进行操作风险损失确认时，应审慎确认，客观、公允统计，准确计量损失金额。（四）成本效益原则。本行操作风险管理要重点突出，做好关键指标监测、重大风险点的排查和识别，通过付出合理的成本将操作风险损失控制在本行经营所能承受的范围内，保证以合理的控制成本达到最佳的控制效果。（五）相匹配原则。本行操作风险管理要与具体业务的性质、规模、复杂程度和特点相适应，有效地识别、评估、监测、控制及缓释操作风险，并根据情况变化及时进行调整。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第一条', '操作风险管理组织架构'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第六条', '操作风险管理体系。本行应按照外部监管要求，持续改进和完善操作风险管理，建立与本行的业务性质、规模和复杂程度相适应的操作风险管理体系，有效地识别、评估、监测和控制及缓释操作风险。操作风险管理体系包括以下要素：（一）董事会的最终控制职责；（二）监事会的监督职责（三）高级管理层的职责；（四）适当的组织架构；（五）操作风险管理政策、方法和程序；（六）计提操作风险所需资本的规定。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第七条', '操作风险管理组织架构。本行应建立与操作风险特点相适应的组织架构，包括董事会、监事会、高级管理层和相关职能部门，明确其在操作风险管理中的角色和职责边界，健全操作风险管理三道防线，并指定合规管理部门作为操作风险归口管理部门负责操作风险管理体系的建立和实施，并与业务条线管理部门（下称操作风险责任部门）应保持独立，确保全行范围内操作风险管理的一致性和有效性。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第二条', '操作风险管理职责'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第八条', '董事会对操作风险管理有效性负有最终职责。主要职责包括但不限于以下内容：（一）制定与本行战略目标相一致且适用于全行的操作风险管理战略、总体政策；（二）通过审批及检查高级管理层有关操作风险的职责、权限及报告制度，确保本行的操作风险管理决策体系的有效性，并尽可能地确保将本行从事的各项业务面临的操作风险控制在可以承受的范围内；（三）定期审阅高级管理层提交的操作风险报告，充分了解本行操作风险管理的总体情况、高级管理层处理重大操作风险事件的有效性以及监控和评价日常操作风险管理的有效性；（四）审批本行部门设置及职责分工方案，确保高级管理层采取必要的措施有效地识别、评估、监测和控制及缓释操作风险；（五）建立内审部门定期审查操作风险管理体系的机制，确保本行操作风险管理体系接受本行内审部门的有效审查与监督；（六）制定适当的奖惩制度，在全行范围有效地推动操作风险管理体系建设；（七）本行章程规定的其他职责。董事会可以授权其下设的风险管理与关联交易委员会履行以上部分职能,风险管理与关联交易委员会应定期向董事会提交有关报告。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第九条', '监事会对本行操作风险管理体系的建立和运行负有监督责任。主要职责包括但不限于以下内容：（一）对董事会及高级管理层在操作风险管理中的履职情况进行监督评价，每年至少一次在股东大会上报告董事会及高级管理层在操作风险管理中的履职情况；（二）揭示操作风险管理方面存在的问题，并向董事会和高级管理层提出整改意见；（三）对高级管理层执行操作风险管理战略、政策和操作风险管理工作进行检查监督；（四）对董事会和高级管理层就操作风险的有关问题进行咨询，并要求董事会成员、高级管理人员纠正其损害本行利益的行为；（五）本行章程规定的其他职责。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第十条', '高级管理层负责执行董事会批准的操作风险管理战略、总体政策及体系，领导全行操作风险的具体管理工作。主要职责包括但不限于以下内容：（一）根据董事会制定的操作风险管理战略及总体政策，负责制定、定期审查和执行操作风险管理的政策、程序和具体的操作规程，并定期向董事会提交操作风险总体情况的报告，并接受监事会监督；（二）全面掌握本行操作风险管理的总体状况，特别是各项重大的操作风险事件或项目；（三）明确界定各职能部门的操作风险管理职责以及操作风险报告的路径、频率、内容，督促各部门切实履行操作风险管理职责，以保证操作风险管理体系的正常运行；（四）为操作风险管理配备适当的资源，包括但不限于提供必要的经费、设置必要的岗位、配备合格的人员、为操作风险管理人员提供培训、赋予操作风险管理人员履行职责所必需的权限等；（五）及时对操作风险管理体系进行检查和修订，有效地应对内部程序、产品、业务活动、信息科技系统、员工及外部事件和其他因素发生变化所造成的操作风险事件；（六）本行操作风险管理政策规定的其他职责。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第十一条', '合规管理部门作为操作风险归口管理部门，负责全行操作风险管理体系的建立和实施，主要职责包括但不限于以下内容：（一）拟订本行操作风险管理政策、程序和具体的操作规程，提交董事会和高级管理层审批；（二）建立适用全行的操作风险基本控制标准，并指导和协调全行范围内的操作风险管理；（三）建立并组织实施操作风险识别、评估、缓释(包括内部控制措施)和监测方法以及操作风险报告程序；（四）协助业务条线管理部门、分支机构识别、评估、监测、控制及缓释操作风险；（五）定期分析总结本行操作风险管理体系运作情况，推动操作风险管理工具在本行的实施，持续研究、改进和创新操作风险管理方法、技术和工具；（六）牵头建设操作风险管理系统；（七）检查并分析业务条线管理部门的操作风险管理情况；（八）定期向风险管理部门、高级管理层提交操作风险管理评估分析报告，及时报告重大操作风险事件；（九）为各部门提供操作风险管理方面的培训,协助各部门提高操作风险管理水平、履行操作风险管理的各项职责；（十）确保操作风险制度和措施得到遵守。风险管理部门作为全面风险管理牵头部门，负责各类风险的统筹管理工作，操作风险管理主要职责包括但不限于以下内容：（一）协助合规管理部门拟订本行操作风险管理政策、程序和具体的操作流程；（二）对本行新产品、新业务的潜在风险（含操作风险）进行审核、评估；（三）检查并评估操作风险管理体系建设情况，确保操作风险管理纳入全面风险管理体系；（四）根据合规管理部门的报告分析本行面临的操作风险状况，评估操作风险管理政策、制度和流程的执行效果，持续提高操作风险管理的有效性。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第十二条', '各业务条线管理部门作为操作风险责任部门，具体管理条线运行操作风险和本部门操作风险，对操作风险的管理情况负直接责任。主要职责包括但不限于以下内容：（一）指定专人负责条线运行操作风险管理，其中包括遵守操作风险管理的政策、程序和具体的操作规程；（二）根据本行统一的操作风险管理评估方法，识别、评估条线运行操作风险，建立持续、有效的操作风险监测、控制及缓释和报告等操作规程，并组织实施；（三）在制定本部门或条线业务流程和相关业务政策时,充分考虑操作风险管理和内部控制的要求,应保证各级操作风险管理人员参与各项重要的程序、控制措施和政策的审批,以确保与操作风险管理总体政策的一致性；（四）监测关键风险指标，定期检查并分析本部门或条线操作风险管理情况，并定期向操作风险归口管理部门报告本部门或条线操作风险管理的总体状况，并及时报告重大操作风险事件；（五）根据实际情况制定本条线业务连续性计划或灾难恢复计划，定期测试和演练，保证持续经营；（六）负责范围内条线运行操作风险进行持续、有效的监测和控制，确保操作风险政策、制度和措施得到遵守；（七）负责本部门或条线操作规程和风险防控管理培训，履行本部门或条线在操作风险管理中的各项职责。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第十三条', '分支机构在操作风险管理中的职责包括但不限于以下内容：（一）负责行内各项业务操作风险管理，确保总行操作风险管理政策以及各条线的制度得到严格贯彻执行；（二）汇总损失数据并向合规管理部门报告；（三）对合规管理部门或操作风险责任部门识别、监测到的风险进行整改、处置，并将处置结果反馈至合规管理部门或操作风险责任部门。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第十四条', '操作风险责任部门和各分支机构为操作风险管理第一道防线。操作风险责任部门主要负责人是本条线、本部门操作风险管理的主要责任人，对本条线、本部门的操作风险负有管理责任；分支机构负责本分支机构的操作风险管理，分支机构负责人是分支机构操作风险管理第一责任人，对分支机构的操作风险负有管理责任；各岗位员工作为本职岗位操作风险的直接管理者,对本职岗位的操作风险负直接责任。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第十五条', '信息科技、安全保卫、人力资源等管理部门在管理好本部门操作风险的同时，在涉及其职责分工及专业特长的范围内应为其他部门管理操作风险提供相关资源和支持。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第二章  操作风险管理架构', '第十六条', '审计部门为操作风险管理第三道防线。审计部门不直接负责或参与操作风险归口管理部门、操作风险责任部门和分支机构的操作风险管理，但应定期检查评估全行的操作风险管理体系运作情况，监督操作风险管理政策的执行情况，对新制定的操作风险管理政策、程序和具体的操作规程进行独立评估，并向董事会报告操作风险管理体系运行效果的评估情况。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第一条', '一般规定'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第十七条', '本行操作风险管理应依据操作风险的识别、评估、监测、统计、控制及缓释等流程，设置和完善操作风险管理程序。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第十八条', '本行应制定有效的程序，定期监测并报告操作风险状况和重大损失情况。针对潜在损失不断增大的风险，建立操作风险预警机制，及时采取措施控制，降低风险和损失事件的发生频率及损失程度。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二条', '操作风险的识别与评估'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第十九条', '操作风险识别与评估包含识别、分析和评估。操作风险识别是指识别经营管理活动及业务、管理流程中是否存在风险及存在何种风险。操作风险分析是指对识别出的风险进行分析，判断风险发生的可能性及风险发生的条件。操作风险评估是指评估风险可能产生损失的大小及对银行实现经营管理目标的影响程度和是否可接受或接受的程度。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十条', '本行根据自身特点，设定操作风险识别与评估模型。在日常操作风险管理中，收集审计、业务排查、监测报告等各类方式和渠道反馈的信息和相关数据，对各级模型进行整理、更新和维护。1.损失事件模型。对所有可能的损失事件类型进行识别和分类。2.组织模型。对组织维度进行识别、分析和定义。3.风险因素模型。对导致损失事件的所有可能原因、因素进行识别和分类。4.指标模型。对支持合理的主观估计的信息和指标进行识别。5.影响模型。对一个事件的所有可能影响进行识别和分类。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十一条', '识别与评估流程操作风险识别与评估流程包括：（一）前提与准备1.制度先行：从制度上规范风险管理内容，形成制度体系。2.流程梳理为媒介：风险识别评估与业务操作、流程进行结合。3.风险分类为基础：多层次、多角度识别风险点。4.方法为手段：完善的风险识别评估方法。（二）计划与组织1.操作风险责任部门制定条线具体实施计划，形成计划书，明确范围、目标、人员安排和职责分工等，并向合规管理部门报备。2.合规管理部门牵头业务条线管理部门对相关人员进行培训，明确识别与评估程序及方法等。（三）具体措施1.从固有风险和剩余风险两方面进行评估；2.从风险发生可能性和影响程度两个维度分析；3.通过相关矩阵，对风险级别进行认定，形成风险图谱等识别评估结果；4.不同性质风险选择不同的风险评估方法进行评估。（四）风险应对1.操作风险责任部门依据识别评估结果制定风险应对方案，如持续改进或制定改进计划。2.合规管理部门针对风险方案提出相关建议。3.应根据确定的风险应对方案，对识别评估结果进行持续跟踪和落实。（五）报告及成果固化1.操作风险责任部门形成风险相关报告、表格，上报合规管理部门；2.合规管理部门对相关报告、表格、数据等资料进行审阅和汇总，提交高级管理层、董事会审阅；3.合规管理部门与操作风险责任部门共同确定关键风险指标、风险责任体系等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十二条', '操作风险识别与评估应具有前瞻性，并应识别评估出各业务管理流程存在的主要操作风险事件、风险因素、影响类型、控制措施等，为操作风险监测、控制及缓释等提供基础。操作风险的识别与评估可采用风险控制自我评估、风险对应关系、操作风险目录、关键风险指标和记分卡等方法。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三条', '操作风险的监测与统计'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十三条', '本行应建立合适的操作风险监测统计体系，通过设置并监测各类操作风险指标，动态、持续的监测操作风险状况、控制及缓释措施的效果，快速发现政策、流程和内部控制出现的问题并采取相应的补救措施。监测范围应涵盖各业务品种和业务条线以及内部管理制度的执行环节。操作风险监测可采用关键风险指标等操作风险工具，量化相应条线关键环节面临的操作风险程度，并依据本行操作风险容忍度实施针对性控制管理。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十四条', '本行应定期监测操作风险状况和造成损失情况，做好操作风险事前、事中和事后监测。（一）事前监测。本行应建立早期操作风险预警机制，建立覆盖主要风险类别和重点产品群组的操作风险管理目录，设置操作风险关键风险指标，关注关键风险具体指标。事前监测坚持合理分工、统筹联动。本行分支机构负责监测行内的操作风险变化情况，发现早期操作风险预警信号，及时向对口的操作风险责任部门报告；操作风险责任部门负责本条线内操作风险的监测与预警；合规管理部门负责全行整体操作风险的监测与预警。通过对关键风险指标的持续、动态监测，实现操作风险的主动管理与事前预警。（二）事中监测。当某类操作风险关键风险指标超过预警阈值时，该操作风险的条线操作风险责任部门应及时分析评估宜采取何种操作风险管理措施或启动相应应急预案，组织必要的资源来控制、化解相关分支机构出现的操作风险；出险分支机构应积极主动落实相应操作风险管理措施，并负责事中监测与反馈其实施效果，实时向条线操作风险责任部门分析总结该操作风险事项的管理情况报告并同时报备操作风险归口管理部门。（三）事后监测。事后监测主要侧重于各类操作风险事件发生后风险控制措施落实、制度完善评估、责任界定追究的跟踪监测。通过收集、整理、分析、统计本行各类操作风险事件，监测操作风险事项的异动状况，强化对本行操作风险管理目录的维护。通过对本行操作风险事项进行定期检查、情景分析和压力测试，监测评估面临的操作风险状况，实现对操作风险的事后控制。本行分支机构负责监测行内的操作风险变化情况，并将行内的操作风险事件数据进行初步收集整理、归类统计，及时报送条线操作风险责任部门；操作风险责任部门负责本条线内的操作风险事件监测，定期检查本条线内操作风险事项，将本条线内操作风险事件数据进行收集整理、归类统计，及时报送合规管理部门；合规管理部门协同操作风险责任部门对业务运行操作风险进行定期检查、情景分析和压力测试，并负责操作风险事件库的建立与维护工作。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十五条', '操作风险事件统计应至少包含以下内容：损失事件发生的时间、发现的时间及损失确认时间、损失事件类型（详见附件1:操作风险事件分类表）、业务条线名称（详见附件2:操作风险业务条线归类目录）、涉及金额、损失金额、缓释金额、非财务影响、与信用风险和市场风险的交叉关系等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十六条', '本行应当根据操作风险事件监测统计工作的重要性原则，合理确定操作风险事件监测统计的金额起点。对设定金额起点以下的操作风险事件和未发生财务损失的操作风险事件也可进行记录和积累。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十七条', '本行在监测统计操作风险事件时，应清晰界定操作风险事件统计范围，合理区分操作风险损失和其他风险损失界限。对于跨区域、跨业务种类的操作风险事件，应当合理确定损失统计原则，避免重复统计。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第四条', '操作风险的控制与缓释'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十八条', '操作风险的控制及缓释是指根据操作风险评估或监测结果，制订并组织实施操作风险控制及缓释行动计划的过程。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第二十九条', '本行对识别评估出的操作风险点应提出相应控制措施。控制措施种类包括但不限于以下内容：（一）高层审核措施。上级管理者对照预算、预测、过往业绩和竞争者的情况对相关业务或经营情况进行审核。（二）直接的职能或活动管理措施。如审核业绩报告、新业务数据，关注合规问题，调节资金头寸等。（三）信息处理措施。通过一系列的核对与批准，保证交易的准确性、完整性和已授权。（四）实物控制措施。建立财产日常管理制度和定期清查制度，采取财产记录、实物保管、定期盘点、账实核对等措施，确保财产安全。（五）业绩指标分析措施。综合一系列的指标，通过因素分析、对比分析、趋势分析等方法，发现存在的问题，及时查明原因并加以改进。（六）不相容职责分离措施。全面系统地分析、梳理业务流程中所涉及的不相容职务，实施相应的分离措施，形成各司其职、各负其责、相互制约的工作机制。（七）其他措施。如：绩效考评控制、预算控制、信息系统控制等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十条', '本行根据自身操作风险偏好和管理水平，在权衡成本效益基础上，选择确定不同业务操作风险的承受、控制、转移、规避等风险缓释策略来控制面临的操作风险。（一）对在本行风险承受范围之内的、潜在风险损失小且事件发生频率低（低危低频、承受之内）的业务操作风险，主要通过配置操作风险经济资本或计提风险准备金的方法承受操作风险；（二）对在本行风险承受范围之内的、潜在风险损失小且事件发生频率高（低危高频、承受之内）的业务操作风险，主要通过流程优化、强化培训等方法来控制操作风险；（三）对在本行风险承受范围之内的、潜在风险损失高且事件发生频率低（高危低频、承受之内）的业务操作风险，应重点考虑购买保险、业务外包等缓释措施来分散转移操作风险；（四）对在本行风险承受范围之内、潜在风险损失高且事件发生频率高（高危高频、承受之内）的业务操作风险，主要采取限额管理、减少业务等措施来缓释降低操作风险；（五）对在本行风险承受范围之外、潜在风险损失高且事件发生频率高（高危高频、承受之外）的业务操作风险，通过建立健全并严格相应业务准入制度来规避拒绝操作风险。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十一条', '本行为缓释风险可以外包部分业务，但应当建立健全外包管理制度，明确外包管理组织架构和管理职责，确保业务外包有严谨的合同和服务协议、各方的责任义务规定明确，并至少每年开展一次全面的外包业务风险评估。外包业务活动中对外包服务商相关人员的授权必须遵守“必须知道”和“最小授权”原则，涉及战略管理、风险管理、内部审计及其他有关核心竞争力的职能不得外包。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十二条', '本行可购买保险以及与第三方签订合同，并将其作为缓释操作风险的一种方法，但保险理赔的缓释最高不超过操作风险监管资本要求的20%。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第五条', '重点领域操作风险管理'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十三条', '人员管控。人员因素作为操作风险四大风险因素之一，应建立人员管控体系以强化人员管控。本行应完善员工考勤管理、干部、员工定期岗位轮换制度、强制休假、近亲属回避、岗位任职资格等制度，还应制定员工行为准则，规范员工行为，强化员工职业操守，明确对员工的禁止性规定，建立员工行为排查机制，加强对员工行为的监督和排查。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十四条', '业务管控。本行应优化组织架构基础上，根据单位自身的资产规模和管理实际需要，在全行或分区域按业务条线设置专业化的集中作业平台，提升专业能力和集约化水平，强化业务条线的事前事中事后控制。（一）开办新业务、提供新产品和服务，应当对潜在的风险进行评估，并制定相应的管理制度和业务流程，确保制度先行。（二）建立有效的业务操作风险排查机制，按照业务操作风险特点，对各项业务及业务外包情况定期、不定期的开展排查，识别和揭示风险，采取有效的风险缓释措施，防范和化解风险隐患。（三）制定与其业务规模和复杂性相适应的应急和业务连续方案，建立恢复服务和保证业务连续运行的备用机制，并应当定期检查、测试其灾难恢复和业务连续机制，确保在出现灾难和业务严重中断时这些方案和机制的正常执行。（四）建立健全客户投诉处理机制，制定投诉处理工作流程，定期汇总分析投诉反映事项，查找问题，有效改进服务和管理。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十五条', '流程管理。本行应对流程银行建设、商务转型有关操作风险管理方面的成果进行固化，编制操作风险相关管理手册，制定标准的岗位职责体系，执行标准统一的业务流程和管理流程，确保规范运作。（一）部门、岗位职责说明书。本行应根据内外部环境变化和经营管理需要，适时梳理部门、岗位职责权限，合理确定部门、岗位的职责及权限，明确相应的报告路线和工作流程，形成规范的部门、岗位职责说明。（二）管理流程手册。本行应根据外部规定、内外部环境的变化和经营管理需要，充分运用风险控制自我评估等管理工具，梳理管理环节风险点和管理制度流程，形成管理条线风险目录，制定对应的风险控制措施，形成标准统一的管理流程，并汇编成册。（三）业务操作手册。本行应根据内外部环境变化、产品业务创新和经营管理需要，充分运用关键风险指标等管理工具，梳理操作环节风险点和业务制度流程，形成业务条线风险目录，制定对应的风险控制措施，形成标准统一的业务操作流程，确保一项业务一个流程，并对已梳理形成的业务流程汇编成册。本行应收集审计、业务排查、监测报告等各类方式和渠道反馈的信息，定期评价部门、岗位职责和设置、管理流程、业务操作流程等控制不足和控制过度环节，并持续改进和完善，确保有效防控操作风险各个风险点。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第六条', '操作风险管理报告'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十六条', '本行应建立有效的操作风险报告制度，明确操作风险报告路径。合规管理部门、操作风险责任部门和分支机构、审计部门发现操作风险问题，均应有畅通的报告渠道和有效的纠正措施。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十七条', '操作风险报告应满足以下要求：（一）真实性。客观、真实、准确地反映操作风险状况。（二）及时性。及时分析、报告重大操作风险事件。（三）准确性。提出准确有效、切实可行的操作风险控制措施。（四）保密性。操作风险报告要遵守有关保密管理及信息披露规定。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十八条', '操作风险报告具体可分为一般操作风险事件报告、重大操作风险事件报告和操作风险管理定期报告等形式。操作风险事件包括重大风险事件和一般操作风险事件，具体划分标准参照《商业银行操作风险管理指引》相关内容确定。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第三十九条', '一般和重大操作风险事件报告应至少包括以下内容：事件发生的时间、发现的时间及损失确认的时间、业务名称、操作风险事件类型、业务金额、损失金额、涉案人员、对事件的描述和非财务影响等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第四十条', '操作风险管理定期报告应至少包括以下内容：报告期内操作风险整体状况、面临的主要风险因素及风险趋势、操作风险及控制措施的评估结果、关键风险指标监测情况、主要操作风险事件、已确认或潜在的重大操作风险损失、加强操作风险管理的建议等内容。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第四十一条', '各类操作风险报告应实行双线报告：本行操作风险责任部门和分支机构应定期或不定期向合规管理部门报告行内或条线各类操作风险事项；合规管理部门应按季向风险管理部门、高级管理层报送操作风险管理定期报告，高级管理层应按年度向董事会报送操作风险管理定期报告。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第三章  操作风险管理程序', '第四十二条', '本行应建立有效的操作风险事件内部报告路径，具体路径为：（一）一般操作风险事件报告路径1.出险单位根据规定启动行动方案，采取相应的风险控制措施；2.出险单位按规定向条线部门及条线分管领导报告；3.出险单位按规定向合规管理部门报告。（二）重大操作风险事件报告路径1.出险单位当即向合规管理部门、风险管理部门报告，并第一时间报请启动应急预案，同时按规定向董事会、高级管理层报告；2.合规管理部门接到出险单位的风险事件报告后，应立即按规定向董事会、高级管理层报告；3.董事会、高级管理层根据具体事件，做出决策，启动应急预案，采取行动和措施，对需要向监管部门和省联社报告的，按规定及时报告；4.出险单位、合规管理部门在报告的同时，应在职权范围内采取相应措施控制操作风险，防止财务或非财务损失的扩大和蔓延；5.重大操作风险事件还应当按规定进行事件后续跟踪报告。（三）操作风险管理定期报告路径1.操作风险责任部门和分支机构要按季定期向合规管理部门报送操作风险管理定期报告；2.合规管理部门在整理分析的基础上，负责向风险管理部门、高级管理层报告，同时抄报内审部门；3.高级管理层按年度向董事会或其下设的风险管理与关联交易控制委员会，同时抄报监事会。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第四十三条', '本行结合自身实际，积极引进和应用操作风险三大工具，即风险控制自我评估、关键风险指标、损失数据收集。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第一条', '风险控制自我评估'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第四十四条', '风险控制自我评估是识别和评估潜在操作风险以及自身业务活动的控制措施、适当程度及有效性的操作风险管理工具，是对经营和业务活动中一系列可能承受的潜在操作风险进行自我评估。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第四十五条', '风险控制自我评估最常见的形式是发放问卷。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第四十六条', '实施风险控制自我评估六大关键因素：（一）制定易于执行的风险控制自我评估程序；（二）确定标准的风险控制自我评估方法与工具；（三）对风险控制自我评估执行、监督和检查的权责进行清晰划分；（四）董事会、高级管理层及各条线部门的高度重视；（五）跨部门的协调合作；（六）风险控制自我评估过程和结果的充分应用。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第四十七条', '风险控制自我评估流程1.范围定义。操作风险责任部门确定本条线、本部门涉及的风险控制自我评估范围，从各条线、本部门收集信息，进行信息准备。2.创建问卷。操作风险责任部门根据定义的范围和收集的信息，设计问卷。针对问卷种类定义问题和阈值，并确定评估方法。3.执行（问卷实施）。操作风险责任部门根据设计好的问卷和确定的评估方法，开展问卷调查，定量收集问卷调查信息，识别风险因素。4.审阅和调整。合规管理部门对操作风险责任部门收集整理的问卷调查资料和信息进行结果分析，并与风险责任部门进行讨论，对相关结果进行必要调整。5.报告。合规管理部门根据讨论分析结果，形成风险控制自我评估报告交高级管理层和董事会。董事会根据报告分析，调整相关政策，完善业务、管理流程和关键风险指标及风险控制措施，设定新的操作风险容忍度水平。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第四十八条', '本行要通过风险控制自我评估，完成对所有业务、管理流程的风险点及控制措施的识别与评估，建立和形成业务流程库、风险点库及控制措施库。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第二条', '关键风险指标'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第四十九条', '关键风险指标是指代表某一风险领域变化情况并可定期监控的统计指标，是用来考察银行的风险状况的统计指标，具备可操作性和前瞻性。关键风险指标可用于监测可能造成损失事件的各项风险及控制措施，并作为反映风险变化情况的早期预警指标，具体指标例如：每亿元资产损失率、每万人案件发生率、百万元以上案件发生比率、超过一定期限尚未确认的交易数量、失败交易占总交易数量的比例、员工流动率、客户投诉次数、错误和遗漏的频率以及严重程度等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十条', '本行定期（逐月或逐季）对关键风险指标进行审查，以提示有关风险可能的变化，及时提供预警信息。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十一条', '关键风险指标选取原则（一）全面覆盖原则。指标应当全面覆盖四大风险因素、七大类型损失事件类型、九大业务条线。（二）数据可得原则。指标应当与潜在风险高度相关并可预测，选取的指标应能持续地获得完整的数据支持。（三）指标可控原则。选取的指标应当可以通过可选的管控措施进行有效的控制。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十二条', '关键风险指标选取及阈值设定（一）收集信息、分析并识别潜在风险1.根据全面覆盖原则，收集各条线部门内部信息，如历史数据和风险控制自我评估结果，进行汇总和整理。2.全面分析业务流程和内部信息，识别潜在风险。（二）建立风险指标清单分析识别得到的潜在风险，明确风险点，确定对应的风险指标，建立风险指标清单。（三）列出关键风险指标目录1.根据数据可得原则，剔除可得较差的指标；2.根据指标可控原则，剔除难以被有效控制的指标；3.进行指标整理，建立关键风险指标目录。（四）定义阈值和监测方式1.根据历史损失数据和风险控制自我评估结果，结合外部经验和内部实际情况，合理设定指标阈值；2.制定指标监测方式，包括频率；3.在关键风险指标提交审批前进行初始验证，测试指标的有效性。（五）指标审批和验证1.将选取的关键风险指标目录、阈值和监测方式上报高级管理层审批。2.在关键风险指标审批生效后，启动指标监测工作。参考监测结果，定期对关键风险指标进行验证。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十三条', '关键风险指标监测、报告（一）监测。合规管理部门定期监测关键风险指标数据的变动情况，当关键风险指标值超过预设的阈值范围，对相关部门进行风险提示，并提出管理建议或要求。（二）分析。合规管理部门定期对关键风险指标的变化因素作详细分析，对相关部门风险提示落实情况和关键风险指标控制情况进行跟踪分析。（三）报告。合规管理部门评估关键指标风险控制效果，在关键指标变化详细分析的基础上形成报告，定期向风险管理部门、高级管理层、董事会或其风险管理与关联交易委员会提交关键风险指标分析报告。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十四条', '关键风险指标调整（一）调整的发起。关键风险指标使用部门根据关键风险指标实际应用的有效性提出关键风险指标管理政策及清单的修改意见，如：阈值设置范围调整，将修改意见以报告形式上交合规管理部门审阅。（二）审阅和分析。合规管理部门根据关键风险指标的使用情况分析及审阅关键风险指标管理政策及清单的增减、修改及定期重检调整申请意见，然后交由高级管理层审议核准。（三）调整的审批。对于关键风险指标管理政策及对关键风险指标体系、关键风险指标清单的修改，需要高级管理层审批，必要时可由董事会授权风险管理委员会审批。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十五条', '本行要通过实施关键风险指标工具，完成对关键风险指标的开发、采集及监控，建立和形成关键风险指标库。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第三条', '损失数据收集'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十六条', '损失数据收集是指依据监管规定、本行的操作风险偏好与管理需求所定义的收集范围，针对操作风险事件的相关信息，进行数据收集、内容分析、整改方案设计与执行、损失分配、内外部报告的过程。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十七条', '损失数据收集标准损失数据收集流程包括具有高质量标准的操作损失的识别、统计、验证、管理和报告等环节，并达到完备性、及时性、可获得性等，确保信息的数量和质量。损失数据收集的完备性主要是指所收集损失的整体情况；损失数据收集的及时性主要是指损失事件记录的时间点；损失数据收集的可获得性主要是指获取信息的成本；损失数据收集信息的数量和质量主要包括损失的时间、性质、数量及风险暴露的信息和评级信息等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十八条', '损失数据收集要素环节损失数据收集要素环节主要包括收集内容、信息来源、收集方式和收集人员。收集内容主要包括损失数据收集标准、损失类型、信息种类、阈值、特别案例、分类指南等；收集方式主要包括方法、程序、流程、与其他系统连接、变革管理、培训等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第五十九条', '损失数据收集信息设定本行对于所有需要收集的损失事件应设置一定的数据项，至少包括以下项目：损失额、日期（损失事件发生日期、损失事件发现日期、损失事件录入日期、损失记账日期）、损失事件类型、损失形态、组织维度、风险因素及其他。操作风险损失形态包括：法律成本、监管罚没、资产损失、对外赔偿、追索失败、账面减值和其他损失。法律成本。因本行发生操作风险事件引发法律诉讼或仲裁，在诉讼或仲裁过程中依法支出的诉讼费用、仲裁费用及其他法律成本。如违反知识产权保护规定等导致的诉讼费、外聘律师代理费、评估费、鉴定费等。监管罚没。因操作风险事件所遭受的监管部门或有权机关罚款及其他处罚。如违反产业政策、监管法规等所遭受的罚款、吊销执照等。资产损失。由于疏忽、事故或自然灾害等事件造成实物资产的直接毁坏和价值的减少。如火灾、洪水、地震等自然灾害所导致的账面价值减少等。对外赔偿。由于内部操作风险事件，导致本行未能履行应承担的责任造成对外的赔偿。如因本行自身业务中断、交割延误、内部案件造成客户资金或资产等损失的赔偿金额。追索失败。由于工作失误、失职或内部事件，使原本能够追偿但最终无法追偿所导致的损失，或因有关方不履行相应义务导致追索失败所造成的损失。如资金划转错误、相关文件要素缺失、跟踪监测不及时所带来的损失等。账面减值。由于偷盗、欺诈、未经授权活动等操作风险事件所导致的资产账面价值直接减少。如内部欺诈导致的销账、外部欺诈和偷盗导致的账面资产或收入损失，以及未经授权或超授权交易导致的账面损失等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第六十条', '损失数据收集流程本行应结合监管要求、自身实际情况和内部管理需求确定损失数据收集流程。1.识别。操作风险责任部门首先要识别、评估操作风险损失。2.统计。操作风险责任部门在识别、评估操作风险损失的基础上，建立损失数据库，收集操作风险损失信息，并进行信息分类。3.验证。操作风险责任部门根据会计索引和选择的方法模型对损失数据进行数据分析，合规管理部门在分析的基础上进行数据分析验证。4.管理。合规管理部门对已分析验证数据进行管理，对相关的风险损失事件做好分析。5.报告。合规管理部门在综合分析的基础上形成报告，并报告风险管理部门、高级管理层和董事会。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第六十一条', '本行通过实施损失数据收集工具，明确损失数据的收集标准、收集规则及收集流程，做好损失数据收集方法和损失数据信息源的维护，形成常态化的损失数据机制，建立和形成损失数据库。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第四章  操作风险管理工具', '第六十二条', '本行通过建立损失数据库，跟踪和记录每项损失事件的频率、严重性和其他相关信息，对损失事件及损失金额，按照事件类型和业务条线进行矩阵分布分析，评估蒙受操作风险的程度以及开发控制及缓释风险的政策。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第五章  操作风险偏好和资本计量', '第六十三条', '本行董事会应根据本行操作风险管理政策、经营发展水平、风险承受能力、风险管理控制能力、内外部环境等，确定本行的操作风险偏好和容忍度。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第五章  操作风险偏好和资本计量', '第六十四条', '本行在确定操作风险偏好和容忍度时，应强调业务规模、效益与风险承受度的匹配，主要依据下列因素来确定可接受的操作风险水平：（一）股东的预期；（二）统一的风险偏好和整体风险容忍度范围；（三）总资本及储备；（四）业务发展战略和业务发展能力；（五）操作风险管理能力；（六）操作风险的潜在损失；（七）法律、法规规定的最低资本金。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第五章  操作风险偏好和资本计量', '第六十五条', '操作风险偏好和容忍度要坚持动态调整原则。本行在确定本行操作风险偏好和容忍度后，应针对本行不同时期的业务发展和经营管理需要，根据内外部环境的变化，结合经营管理水平和风险管控能力，适时调整本行操作风险偏好和容忍度。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第五章  操作风险偏好和资本计量', '第六十六条', '本行应按照《商业银行资本管理办法（试行）》的相关要求，为所承担的操作风险计提充足的资本，并根据本行实际情况及可操作性探索为操作风险计量配置相应的经济资本，逐步建立科学的风险管理约束机制。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第六章  操作风险应急处理与信息披露', '第六十七条', '本行应当制定与其业务规模和复杂性相适应的应急预案和业务连续方案，建立恢复服务和保证业务连续运行的备用机制，并应当定期检查更新、测试和演练，确保在出现灾难和业务严重中断时这些方案和机制的正常执行。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第六章  操作风险应急处理与信息披露', '第六十八条', '本行应当建立操作风险应急工作领导小组，按业务条线成立操作风险应急工作小组，对操作风险存在重大影响的情形或应急事项，应按制定的应急和业务连续方案开展应急工作，确保恢复服务和保证业务连续及正常运行。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第六章  操作风险应急处理与信息披露', '第六十九条', '本行发生重大操作风险事件，应在第一时间启动应急预案，及时向董事会、高级管理层或相关管理人员报告，并按有关规定向省联社和监管部门报告。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第六章  操作风险应急处理与信息披露', '第七十条', '本行遵照监管部门和本行信息披露的规定，披露操作风险状况的定量和定性信息。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第七章  操作风险管理系统建设', '第七十一条', '本行应当建立并逐步完善操作风险管理系统，为有效地识别、评估、监测、控制和报告操作风险提供技术支撑。操作风险管理系统的建设可与内控合规、案防等相关系统的建设有机融合。合规管理部门负责牵头建设操作风险管理系统，信息科技部门负责操作风险管理系统的日常维护和技术支撑，操作风险责任部门和分支机构负责数据收集、录入和日常管理与维护。风险管理部门应参与操作风险管理系统建设，对系统建设进行风险评估，并提供相关建议。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第七章  操作风险管理系统建设', '第七十二条', '本行通过建立操作风险管理系统，实现对操作人员的身份识别，将操作风险防控嵌入到经营管理的每个环节，为操作风险管理流程和管理工具的实施和整合提供平台，确保风险控制自我评估工具模块、关键风险指标工具模块和损失数据收集工具模块间的联系和数据共享，为本行风险决策提供支撑，实现风险预警、风险识别和风险控制，定期跟踪监测风险并进行风险评估。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第七章  操作风险管理系统建设', '第七十三条', '操作风险管理系统应当包括以下内容：（一）操作风险库建设。包括业务流程库、风险点及控制措施库、关键风险指标库、损失数据库等。（二）操作风险系统模型建设。包括数据分析模型、识别评估模型、监测模型、控制模型等。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第七章  操作风险管理系统建设', '第七十四条', '操作风险系统工作流程（一）业务处理。日常业务处理，形成纸质文档、电子文件和系统数据等档案资料。（二）数据收集。从已形成的纸质文档、电子文件和系统数据等档案资料中收集有关操作风险数据。（三）数据录入。通过手工录入或系统导入的方式，将收集的操作风险数据录入操作风险管理系统。（四）审核验证。将录入的数据，通过管理系统进行审核验证，通过后，系统根据数据性质和特点分发到操作风险库。（五）系统分析。对审核验证分发后的数据，按照自己设定的模型规则，由系统对数据进行分析，自动形成结论。（六）措施行动。将形成的结论分发给对应的责任部门，责任部门根据结论，决定是否启动行动预案或采取何种控制、缓释措施。（七）报告。根据规定将情况报告给董事会和高级管理层，董事会和高级管理层根据报告内容作出操作风险管理决策，促进操作风险管理的调整与提高。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第八章  操作风险的审计', '第七十五条', '本行对操作风险管理实行定期审计稽核制度。本行审计部门应至少每年一次对操作风险管理体系各个组成部分和环节的有效性进行全面审查与评价，还应根据单位面临的操作风险状况不定期采用专项审计等方式对操作风险管理的部分内容进行审计。根据业务发展需要，还可以委托社会中介机构对本行操作风险管理体系定期进行外部审计和评价。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第八章  操作风险的审计', '第七十六条', '操作风险管理内部审计既要面向操作风险归口管理部门、责任部门和分支机构，也要面向董事会和高级管理层，全面审计至少应包括如下内容：（一）操作风险管理体系的完备性与运作效果；（二）操作风险管理一、二道防线履职情况；（三）操作风险管理政策、制度执行遵守情况；（四）违反规章 制度的责任追究情况；（五）操作风险管理系统建设情况。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第八章  操作风险的审计', '第七十七条', '审计部门对操作风险管理的审计报告向应董事会、监事会和高级管理层报告，同时抄送合规管理部门、风险管理部门。委托社会中介机构对本行操作风险管理体系进行审计的，应将外部审计报告提交省联社，并根据需要报送监管部门。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第八章  操作风险的审计', '第七十八条', '董事会应督促高级管理层对内部审计所发现的问题提出改进方案并采取改进措施。审计部门应跟踪检查改进措施的实施情况，并直接向董事会和高级管理层提交有关报告。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第九章  操作风险管理的考核与奖惩', '第七十九条', '本行应将操作风险年度管理情况纳入到对董事、高级管理层的年度履职考核中。高级管理层应针对操作风险管理建立明确的内部评价考核机制，将各分支机构、部门或主要业务条线形成的操作风险与其收益挂钩，有效防范因规模扩大等原因而放松对操作风险的控制。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第九章  操作风险管理的考核与奖惩', '第八十条', '本行应明确操作风险管理与监管机构联络的归口部门，负责向监管部门、省联社报送、接收相关信息，跟踪、评估、考核监管意见和管理要求的落实情况。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第九章  操作风险管理的考核与奖惩', '第八十一条', '对于监管部门在监管中或省联社在行业管理中发现的有关操作风险管理的问题，本行应当在规定的时限内，提交整改方案并落实整改措施，并将相关问题整改情况纳入考核。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第十章  附则', '第八十二条', '本办法由xxx银行负责解释，适时补充、修订和完善。'),
(NULL, '合规管理部', '《操作风险管理制度与流程 》', '第十章  附则', '第八十三条', '本办法自印发之日起施行。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第一章  总则', '第一条', '为规范xxx银行（以下简称“本行”）新业务合规审查，防范新业务开发、宣传、实施中的合规风险，确保新业务的合规性，依法诚信合规经营，维护本行合法权益，结合本行实际，制定本办法。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第一章  总则', '第二条', '本办法适用于本行各类新业务的合规审查。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第一章  总则', '第三条', '本办法所指新业务是指拟开办前期本行尚未开办的业务产品，创新的业务操作、产品营销措施，以及新的重大客户合作关系的建立变更等。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第一章  总则', '第四条', '本办法所指新业务合规审查是指在新业务实施前，本行内部对新业务的合规性进行审查的程序。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第一章  总则', '第五条', '本办法所指评审会议是指本行金融创新管理委员会召开以讨论审议是否开办新业务为主要内容的专题会议。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第一章  总则', '第六条', '本办法所指业务主管部门是指开发新业务或对新业务负有经营管理职责的本行职能部门。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第二章  职责与权限', '第七条', '业务主管部门（一）调研开发新业务，送交其业务条线的分管领导审核；（二）提出新业务合规审查，并报送审查所需相关材料；（三）根据合规管理部审查意见，对新业务进行完善；（四）向创新管理委员会办公室提起新业务评审申请，报送评审资料；（五）负责向监管机构、上级管理部门申请新业务报批、报备；（六）开展新业务培训，组织推广新业务；（七）跟踪新业务运行情况，出具并报送运行报告。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第二章  职责与权限', '第八条', '合规管理部法律事务岗（一）受理新业务的合规审查；（二）尽职审查并出具初审意见；（三）受邀参加评审会议。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第二章  职责与权限', '第九条', '合规管理部总经理（一）复核法律事务岗出具的审查意见，并上报分管领导审核；（二）参加评审会议。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第二章  职责与权限', '第十条', '合规管理分管领导（一）审核合规管理部出具的新业务合规审查意见；（二）参加评审会议。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第二章  职责与权限', '第十一条', '评审会议，对新业务合规性、可行性等进行集体决策。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第二章  职责与权限', '第十二条', '行长审批根据评审会议意见行使审批权，评审未通过的，行长不得审批同意；但对评审通过的，行长具有最终决策的权力，可以行使否决权。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第三章  原则与基本规定', '第十三条', '合规审查的基本原则（一）合规原则：新业务应当符合法律法规、规章以及上级管理部门、监管部门、本行的有关文件规定。（二）公平竞争原则：不得以排挤竞争对手为目的，进行低价倾销、恶性竞争或其他不正当竞争。（三）尊重他人知识产权原则：不得侵犯他人的知识产权和商业秘密。（四）专业审核原则：审查应从合规风险控制的角度重点审核新业务与现行法律、法规和相关政策的符合性、一致性；评审会议参加人对新业务开展的可行性、收益与风险以及与各条线管理规定的一致性进行讨论审核。第十四  基本规定（一）新业务合规审查的范围包括但不限于：1. 新的业务品种与产品，仅简单改变产品名称且不会引起客户误解的除外；2. 新的业务营销方式、营销、宣传材料、宣传用语等；3. 新的重大客户或合作关系的建立、变更。（二）新业务合规审查的内容包括：1. 开办新业务是否违反现有的法律法规、规章以及上级管理部门、监管部门、本行的有关文件规定；2．合作各方是否具有合法的主体资格；3. 新业务的营销方式、操作形式是否违反诚实信用、公平竞争的准则；4. 新业务的宣传材料是否存在明显不利于本行的描述或致人误解的表述；5．新业务是否有明确的准入标准；6. 其它需要审查的合规性问题。（三）合规管理部提前介入：是指法律事务性强的新业务，办理前期应约请合规管理部提前介入。合规管理部可根据实际情况，指派人员参加有关会议，参与业务谈判及法律性文件的拟定、修改，争取有利的谈判地位和对本行有利的文件草案，提高业务审查效率。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第四章  操作流程', '第十五条', '送审新业务送审，由业务主管部门提起。1. 开办新业务前，业务主管部门提请合规管理部对新业务合规性进行审查。2. 提出申请时，应当填写新业务合规审查审批表，并由部门总经理签字，加盖部门公章，然后提交业务分管领导审核。3. 提出合规性审查需提供以下材料：（1）关于新业务的定义、特点、操作的基本说明。（2）新业务相关的记录表单、宣传材料。（3）已制定的关于该新业务的合同文本、管理制度、操作指导书等。（4）已知与该新业务相关的法律法规、规章以及上级管理部门、监管部门、本行的有关文件规定。（5）其它必须接受审查的材料。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第四章  操作流程', '第十六条', '合规性审查（一）受理1.法律事务岗审查人员（以下简称“审查人员”）收到业务主管部门提交的新业务合规审查时，重点审查：（1）是否为新业务，非新业务在申请书写明退审理由与意见，经合规管理部总经理复核后退审。（2）送审材料的充分性。对于送审材料不充分的，审查人员有权要求业务主管部门进行补充，补充材料必须一次性告知，待材料齐全后应进行合规性审查。 （二）初审新业务的合规初审原则上自业务主管部门提交齐全审查资料之日起3个工作日内审查完毕，并在合规审查审批表上签署初审意见。（三）复核合规管理部总经理对于审查人员出具的合规审查意见进行审阅，审查合规意见是否全面、准确、合理，在审查审批表上签署明确的审核意见，签字确认后报送合规分管领导批示。合规管理部总经理的审核，原则上应当在收到合规审查意见起一个工作日内审查完毕。合规管理部总经理审核后，认为审查人员审核意见不全面、不准确、不合理的，可以要求审查人员补充审查或者重新审查。 （四）合规分管领导审核合规分管领导对合规管理部的审查意见进行审核，认为审查意见不全面、不准确、不合理的，可以要求合规管理部补充审查或者重新审查。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第四章  操作流程', '第十七条', '评审（一）评审会议前准备1.新业务主管部门将新业务合规审查审批表及审查相关材料，报送创新管理委员会办公室，创新管理委员会办公室至少提前一日将评审资料送交会议参加人。2. 创新管理委员会办公室提议召集评审会议。（二）集体评审1.召开新业务评审会议，通过集体讨论的形式，审议是否实施新业务。2. 业务主管部门应当对新业务基本情况、收益、风险及防范措施等做详细说明。3. 合规管理部总经理或法律事务审查人员应当对业务合规审查情况进行报告。4. 各参会人员可以就新业务合规风险及防范发表意见。5. 会议应当详细记录各参会人员意见，并由参会的合规管理部人员在新业务合规审查审批表记录会议讨论结果。 6. 评审通过的，由业务主管部门应报实施新业务的行长审批。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第四章  操作流程', '第十八条', '审批对评审通过的新业务，行长具有最终决策。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第四章  操作流程', '第十九条', '业务开展与监督（一）开展新业务1. 新业务经内部审批同意开办后，由业务主管部门负责必要的报备、报批以及具体宣传策划推广、培训、具体实施管理等工作。2. 在开办新业务过程中，业务主管部门与具体经办人员应当有效识别合规风险，谨慎合规操作，履行合规报告的相关义务。（二）报告业务运行新业务实施后，业务主管部门跟踪新业务实施情况，对实际操作情况与合规风险情况进行跟踪调查检查，并及时出具业务运行报告。1. 业务运行报告，应当包括新业务运行情况、预期收益实现情况、运行中发现的潜在合规风险点、拟采取的改进措施等内容。2. 业务运行报告一般应当在业务运行后的一个季度内出具，并报送业务分管领导、合规管理部。（三）监测、上报合规管理部根据业务主管部门的运行报告、经营单位的反馈意见以及外部投诉、诉讼、客户意见、监管意见等，对新业务运行合规性进行监督评价，并向合规分管领导进行报告。（四）监督合规分管领导获知新业务合规运行情况，并提出建议。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第四章  操作流程', '第二十条', '检查监督审计部负责新业务合规审查尽职以及合规操作情况。新业务开办二年内必须检查一次，检查结果上报董事会。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第五章  合规责任', '第二十一条', '尽职审查责任审查人员应认真分析新业务送审材料，然后出具审查意见。因过失或故意未能识别应识别合规风险或未予以提示的，审查人员对出具不当的审查意见承担失职责任。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第五章  合规责任', '第二十二条', '保密责任审查人员及外聘律师出具的审查意见仅限行内采纳；未经合规管理部同意，任何部门和个人不得向外部单位提供。对于审查过程中接触到的有关国家秘密和商业秘密，审查人员应按规定予以保密。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第五章  合规责任', '第二十三条', '擅自开办新业务责任未经总行合规审查、评审以及审批，业务部门或经营单位擅自开办新业务，为超权限行为。总行将按相关办法追究相关人员责任，给予经济处罚或行政处分，造成损失的由渎职人员全额赔偿。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第六章  附则', '第二十四条', '本办法未尽事宜以及与国家有关法律法规、规章的规定冲突的，按国家法律法规、规章等规定执行。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第六章  附则', '第二十五条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《新业务合规审查办法（修订）》', '第六章  附则', '第二十六条', '本办法自发文之日起执行。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第一章  总则', '第一条', '为有效控制违法违规案件发生，规范xxx银行（以下简称“本行”）案件防控管理工作，根据有关法规和《银行业金融机构案防工作办法》（银监办发〔2013〕257号）规定，结合本行实际，制定本办法。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第一章  总则', '第二条', '本行案防工作的目标是：通过建立、健全案防管理体系，完善案防管理制度和流程，建立起“查、防、堵、惩、教”相结合的案件防控长效机制，实现案防关口前移，及时防范和化解案件风险，全面提升内控和风险管理水平，实现全行健康有序运营。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第一章  总则', '第三条', '本办法所称案件是指本行从业人员独立实施或参与实施的，或外部人员实施的，侵犯本行或客户资金或其他财产权益的，涉嫌触犯刑法，已由公安、司法机关立案侦查或按规定应当移送公安、司法机关立案查处的刑事案件。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第四条', '本行应当建立与风险管理、资产规模和业务复杂程度相适应的案防管理体系，有效监测、预警和处置案件风险。案防管理体系至少应当包括以下基本要素：（一）董事会职责；（二）监事会职责；（三）经营层职责；（四）适当的组织架构；（五）管理政策、制度和流程；（六）内部监督与检查。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第五条', '董事会对本行案防工作负最终责任，是本行案防工作的第一责任主体。董事长为本行案件风险防范的第一责任人。本行董事会将案件风险作为本行的一项重要风险进行管理和监控，履行以下案防工作职责：（一）按照监管要求建立与本机构风险管理、资产规模和业务复杂程度相适应的案防管理体系，采取各种有效手段监测、预警和处置案件风险。（二）审议批准案防工作的基本制度； （三）听取经营管理层或专门委员会的案防工作报告，指导案防工作；（四）股东大会授予的其他案防工作职责。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第六条', '董事会下设的风险管理与关联交易控制委员会负责本行案防工作，根据董事会授权组织指导案防工作，对董事会负责，履行以下案防工作职责：（一）审议批准案防工作总体政策，推动案防管理体系建设；（二）明确经营管理层有关案防职责及权限，确保经营管理层采取必要措施有效监测、预警和处置案件风险；（三）提出案防工作整体要求，审议案防工作报告；（四）考核评估本行案防工作有效性；（五）确保内审稽核对案防工作进行有效审查和监督；（六）董事会授予的其他案防工作职责。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第七条', '监事会应当监督董事会和经营管理层有效履行案防工作职责，通过监事会、履职评价委员会履行监督职责。监事会履职评价委员会定期对本行的案防工作有效性进行评估。监事长为案防工作监督的第一责任人。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第八条', '经营管理层负责有效管理本行案件风险。行长为案防制度制定和执行的第一责任人。案防方面的工作职责主要包括：（一）制定各部门及分支机构案防工作的职责分工；（二）研究制定年度案防工作计划；'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第九条', '经营管理层下设风险与内控管理委员会，主要工作职责包括：（一）组织起草年度案防工作计划，并监督执行；（二）定期召开案防工作会议，听取案防工作情况报告，研究部署案防工作，并向经营管理层报告;（三）组织对案防有效性进行评价；（四）经营管理层授予的其他案防工作职责。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第十条', '明确一名领导班子成员分管合规及案防工作，履行案防方面的主要职责包括：（一）组织定期审查、检查和监督本行各部门、分支机构执行案防政策、制度和操作规程；（二）全面掌握本行案防工作总体状况，定期报告；（三）建立与各业务条线和分支机构的协调配合机制，形成权责明确、报告路线清晰、运行有序的案防工作机制；（四）风险与内控管理委员会授予的其他案防工作职责。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第十一条', '合规管理部牵头负责本行案防工作的组织实施，案防方面的主要工作职责包括：（一）拟定本机构案防管理政策、制度和操作规程；（二）组织实施、协调落实案防工作决策和年度案防工作计划；（三）收集汇总案防工作情况，定期分析本行案防形势，确定案防工作重点；（四）督促各部门及分支机构切实履行案防职责，确保案防管理体系正常运行；（五）跟踪落实监管部门提出的案防监管要求；（六）定期组织案防工作培训；（七）完成其他案防工作任务。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第十二条', '本行各部门主要负责人为本部门及分管条线案防工作任务组织落实的第一责任人，案防方面的主要职责包括：（一）制定本部门及分管业务条线案防工作计划，并组织落实；（二）执行案防工作政策、制度和操作规程； （三）根据职责分工对分管条线的业务和岗位进行检查、监督；（四）配合案防牵头部门落实各项案防管理工作，并按案防工作牵头的要求报送相关资料；（五）完成其他涉及本条线及本部门的案防工作任务。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第十三条', '分支机构主要负责人为本机构案防工作的第一责任人，对总行负责。案防工作的主要职责包括：（一）严格执行案防工作政策、制度和操作规程；（二）组织落实案防工作各项任务。分支机构兼职合规员为案防工作联络员，主要负责以下工作：（一）按时按要求向总行案防工作牵头部门及相关条线管理部门报送本机构的案防工作报告及相关报表；（二）按要求报送重大风险事件及案件资料；（三）参加案防工作会议，传达总行案防工作文件、会议精神；（四）完成其他案防工作任务。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第二章  组织架构和工作职责', '第十四条', '本行所有员工为本岗位案防工作的直接责任人，负责本岗位的案防工作，履行以下职责：（一）遵守国家法律法规，严格自律，依法合规办理业务；（二）严格执行本行各项规章制度，严防案件发生；（三）制止并按规定路径举报各类违法违纪行为；（四）接受总行及本机构的各项业务检查，并如实报告涉及工作事项；（五）按要求参加各项业务及案防知识培训；（六）完成其他案防工作任务。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第十五条', '建立健全案防制度体系。本行各职能部门应对照监管政策要求，按照业务发展与风险控制同步的原则，建立符合内控要求、覆盖本行全部业务流程的制度和办法，明确各岗位职责，确保员工理解掌握制度并明晰违规应当承担的责任。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第十六条', '建立制度后评价体系。合规管理部应对本行制度合规性和有效性进行评估，促进内控制度体系不断完善，能够覆盖所有业务领域和环节。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第十七条', '加强授信风险全流程管理。授信与风险审查岗位分工合理、职责明确，岗位之间相互配合、相互制约，做到审贷分离、业务经办与会计账务处理分离。不断完善统一授信管理制度。健全客户信用风险识别与监测体系，不断完善授信决策与审批机制，采取有效措施防止对单一客户、关联企业客户和集团客户授信风险的高度集中，防止违反信贷原则发放关系人贷款和人情贷款，防止信贷资金违规使用。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第十八条', '强化全面风险管理。风险管理部门负责全行全面风险管理政策的落实，牵头制定和不断完善各类风险管理制度和操作流程，以风险审查、后督预警以及集中对账、集中诉讼等手段，进行事前、事中、事后的风险识别、评估和预警，并采取积极措施缓释、化解风险，确保全行各项风险管理政策的有效实施。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第十九条', '加强信息科技系统建设。发挥科技支撑作用，提高通过技术手段防范案件的能力，支持各类管理信息适时、准确生成，对关键业务环节实时监控，确保业务的连续性、系统的安全性和稳定性。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第二十条', '不断改进和完善绩效考核机制。绩效考评体系应体现业务发展与内部控制、风险管理能力的有效匹配，规范分支机构及员工行为，遵守监管部门、省联社以及本行的员工行为禁令规定，加强竞业监督。将内控建设和合规管理情况纳入年度经营目标考核和支行行长、职能部门负责人考核的范围，细化考核指标，不断完善绩效考核体系。实行案件与绩效工资挂钩制度，与员工的职级调整、绩效工资分配挂钩，其中，管理人员的挂钩比例高于一般员工。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第二十一条', '推进合规达标工作。根据省联社的要求和自身案防工作需要，推进合规达标创建工作，实现员工、部门、网点合规达标创建的全覆盖，加强内控管理，规范员工职业行为，坚决遏制违规行为、案件和风险事件的发生。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第三章  案防工作内容', '第二十二条', '完善举报、抵制违法违规行为和堵截案件奖励制度。强化员工参与案防工作的激励引导，拓宽监督渠道，主动接受内部监督和社会监督，在营业网点公布监督电话，开展有奖举报，充分发挥内外部监督作用。依法保护举报人的合法权益。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第四章  内控环境', '第二十三条', '建立和完善专业化管理和部门协作相结合的内控运作机制。本行各业务条线、分支机构、合规管理部门、风险管理部门和审计部门应加强合规联动，建立良好的案防协作机制。各业务条线和支行的负责人承担对本条线和本支行的业务指导、培训、监督、检查等职责，对本条线、本支行经营活动的合规性负主要责任。同时，各业务条线和支行应主动寻求合规管理部门的支持和帮助，主动进行定期的合规性自查，并向合规管理部门提供合规风险信息或风险点，支持、配合合规管理部门的风险监测和评估。合规管理部门和分支机构、职能部门的兼职合规员对合规风险实施专业化管理，为各业务条线和支行提供事前和事中的合规咨询，帮助各业务条线和支行管理好合规风险，并为本行业务与产品创新提供合规支持，对合规风险进行全过程控制。审计部门应对内部控制健全性、有效性、适应性进行监督检查，按本办法要求对经营活动的合规性和风险管理的有效性等进行事后的审计评价，并将有关合规检查情况抄送合规管理部门，为合规管理部门识别、监测和评估合规风险提供信息来源和依据。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第四章  内控环境', '第二十四条', '完善人员和业务双控制的风险控制机制。（一）建立和完善对各级业务管理层和重点岗位人员的风险控制机制。1.加强对各级业务管理层的风险控制。根据董事会授权，清晰界定各层级管理人员和支行负责人的职责权限，落实分级授权管理制度，出台分级授权文件，签订授权书。2.完善决策问责机制，按照“谁决策、谁负责”的原则，明确各决策层的职责权限和应负的责任，重点防止将个人责任转化为集体责任，加强对决策权力的制约和控制，完善责任追究的程序、健全惩处制度。3.加强对支行负责人和运营主管、客户经理等重要岗位人员的管理。严格落实支行负责人、运营主管的定期交流；落实会出等重要岗位人员的定期轮岗；落实运营主管委派制和重要岗位人员离岗审计制度。4.加强员工行为管理。合规管理部应定期开展员工从业行为排查，每年不少于二次，重点排查关注参与民间融资、与客户发生不正当资金往来以及“九种人”等异常行为的员工，认真执行轮岗、交流制度，有效防范操作风险和道德风险的发生。5.进一步规范员工账户使用。运营管理部对员工账户使用情况进行日常预警、监控；合规管理部门每年至少组织1次对员工账户非正常资金往来情况排查；审计部门在对支行全面稽核时                     将员工账户使用情况列为必查内容之一，严禁员工通过本人及其控制的账户归集或过渡客户资金。（二）建立对重点业务和重点环节的风险控制机制。1.加强会计风险控制。运营管理部门应根据省联社业务系统和会计工作规范，对会计业务流程、岗位责任、授权管理、凭证审查、联行清算、重要物件(印、押、证、章、盘)管理、内外账户核对以及票据和表外业务等主要环节进行重点控制。实施会计业务远程集中授权，强化风险事中控制。2.加强财务费用管理。财务计划部门应制定财务费用计划，并监督执行；加强成本核算，定期对财务费用使用情况进行分析，按规定进行信息披露。3.进一步发挥后督系统的风险预警作用。后督中心应及时暴露风险信息，分析根源，强化整改，形成风险及时暴露、快速处置的运行机制。4.加强信贷风险控制。信贷管理部门应不断优化信贷业务操作流程，强化贷款审批管理，严禁逆程序、越程序操作的审贷行为；建立和完善严密科学的贷前调查制度和以风险评估与控制为核心的信贷风险管理制度；在科学测算第一还款来源、合理评估财务指标真实性的基础上，严格审查贷款担保手续，确保第二还款来源及抵（质）押物合法、足值、有效；逐步实现授（用）信远程审批，利用科技手段，提高授（用）信预警预报和风险控制能力；加强对贷款用途的监督，提高贷后检查质量。5.加强安全保卫等要害部位和重要环节的控制。安全保卫部门需加强对金库尾箱、监控录像等安全防范措施的落实。6.加强创新业务的风险控制。各职能部门要坚持“内控优先”原则，加强对授信业务、资金业务、票据业务、电子银行业务、表外业务、理财业务和其他创新业务的风险控制，及时评估相关业务的效益和风险，完善内部控制办法。7.加强大宗物品采购与处置环节风险防控。严格执行财务管理制度，完善招投标工作制度与议事规则，规范运作，接受审计部门的审计监督，防范大宗物品采购与处置环节的风险。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第四章  内控环境', '第二十五条', '完善内部审计监督机制。本行内部审计部门应当独立于经营管理，独立于内部控制政策的制定和执行。内部审计部门对董事会负责，在董事会下设的审计委员会指导下开展工作，负责制定内部审计程序，落实年度审计工作计划，评价机构整体内控管理及风险情况，揭示重点风险领域和问题，监督审计问题的整改，并对审计项目质量负责。按省联社要求加强审计档案管理。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第四章  内控环境', '第二十六条', '建立和完善内控制度后评价机制。1.制定和不断完善内部规章管理和内部规章制度及合同文件合规审查制度。合规管理部归口负责内控制度和合同文件的合规性审查，并对制度及其执行有效性进行后评价。2.健全各项规章制度。各职能部门在职责范围内，根据内控制度覆盖业务和岗位原则，健全各项管理规章。同时根据业务和环境的发展变化定期对规章制度进行清理、修订和完善，确保制度建设与业务发展同步，做到“一项业务一本手册、一个流程一项制度、一个岗位一套规定”，逐步实现经营风险由部门控制向流程控制的转变。3.实施内部风险提示制度。各职能部门应及时对业务发展风险、业务操作风险、易发案件环节和重点关注人员进行风险提示，在管理层信息共享。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第四章  内控环境', '第二十七条', '完善信息披露和行务公开机制。1.按照《商业银行信息披露办法》规定，不断完善信息披露制度以及相应的工作机制，保证信息披露的真实性和有效性，提高信息披露质量。2.建立和完善行务公开机制。凡涉及职工利益、福利、薪酬、奖罚等方面的重要制度应经职工代表大会或其他形式听取职工的意见和建议，以激发员工积极性和创造性，培养员工的责任感和归属感。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第五章  人员培训和行为管理', '第二十八条', '建立完善的员工合规及案防培训体系。员工培训教育是本行案防工作的重要组成部分，人力资源部归口负责本行员工的教育培训工作，制定全员培训的中长期规划和年度员工培训计划，并监督实施。创新培训模式，针对不同岗位和专业技能的不同特点，开展差别化培训，形成包括全员培训（各层面员工），全程培训（前台、中台、后台）、全方位培训（知识、技能、素质、企业文化等多方面）的多层次培训体系，完善培训机制，提高全员的综合素质。培训计划中应包含合规及案防培训和重点业务培训计划，对员工培训效果的考核应与其岗位、待遇、职务晋升等挂钩。加强对案防管理人员的专业技能培训，不断提高案防管理人员的履职能力。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第五章  人员培训和行为管理', '第二十九条', '完善用人机制。不断完善定岗定员、择优录用、竞争上岗、分流退出、薪酬分配、劳动保险等工作机制，鼓励员工参加从业资格认证考试和技术职称考试。建立和完善中层管理人员及重点岗位公开选聘制度，逐步完善人才激励机制。不断完善人才储备机制，建立后备人员储备库，并根据聘用情况适时递补。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第五章  人员培训和行为管理', '第三十条', '强化员工职业规范约束。相关职能部门应将员工行为管理与操作风险管理有机结合。主要工作包括：1.建立并不断完善员工管理制度，将员工入职前背景考察、职业操守、八小时内外行为规范等要素纳入案防管理范畴。2.加强对员工参与民间借贷、非法集资、充当资金掮客、与客户发生不正当资金往来、洗钱、涉黄、涉赌、涉毒、经商办企业、过度消费及负债、频繁请假等异常行为的监督检查力度。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第六章  检查监督', '第三十一条', '加大监督检查和整改力度。1.三道防线各部门加强合规联动，明确责任，狠抓各项基本制度的落实。2.加大内控检查力度，健全问题发现机制。职能部门根据全行经营目标制定能覆盖全部业务、重点岗位、重点领域、风险易发环节的年度和阶段性检查计划，科学确定检查的内容和方式，按计划逐项予以落实，充分暴露各类问题，及时向合规管理部报送。3.加大对违规行为的整改力度。切实重视对各项检查发现问题的整改，落实整改部门和整改责任人，责任部门应建立整改情况台账，利用合规检查跟踪系统，抓好后续跟踪督促，确保整改实效。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第六章  检查监督', '第三十二条', '完善内控问责机制。1.完善员工违规以及不良贷款问责的制度流程，科学、合理确定违法、违规责任人责任追究和赔偿标准。2.加大问责力度，对发生的违规违纪行为进行严格问责，以提高内控问责制度的权威性和威慑力。3.完善违规行为量化评价制度，增强操作风险监测、评价的有效性、科学性，促进内控管理的自我完善。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第七章  评价与报告', '第三十三条', '建立自我评价制度。合规管理部应制定案防工作评价标准，定期对本行职能部门、分支机构落实案防工作的情况进行评价，评价要素应包括：1.案防工作组织及质量；2.内控制度建设；3.制度执行；4.案发情况；5.违规违纪行为的责任追究；6.问题整改情况；7.其他案防工作落实情况。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第七章  评价与报告', '第三十四条', '强化内审监督作用。审计部门每年应制定年度审计工作计划，并按计划组织实施内部审计，案防工作应作为内审监督的一项重要内容。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第七章  评价与报告', '第三十五条', '评价报告。合规管理部应按计划完成评价工作，书面报告总行。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第八章  附则', '第三十六条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第八章  附则', '第三十七条', '本办法实施前的有关制度与本办法有冲突的，以本办法规定为准。未尽事项，按本行相关规定执行。'),
(NULL, '合规管理部', '《案防工作实施办法》', '第八章  附则', '第三十八条', '本办法自印发之日起实施。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第一章  总则', '第一条', '为进一步加强案防工作，客观评估xxx银行（以下简称“本行”）的案防工作成效，根据《银行业金融机构案防工作办法》、《银行业金融机构案防工作评估办法》以及本行《案防工作实施办法》等法律法规的规定，结合本行实际，制定本办法。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第一章  总则', '第二条', '案防工作评估目标：通过评估，客观认识本行案防工作情况、成效及存在问题，有针对性的进行整改，弥补漏洞缺陷，从而进一步健全案防体系，真正做到责任落实到位、风险揭示到位、防控部署到位、漏洞堵塞到位，有效遏制案件发生，扎实有效推进案件防控长效机制建设。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第一章  总则', '第三条', '本办法所称的案防工作评估是由支行、部门自我评估和总行总体评估构成。支行、部门自我评估是指依据本办法以及当年的案防工作评估方案对本支行或本条线的年度案防工作开展情况进行评估。总行评估是指总行依据本办法以及当年的案防工作评估方案对本行年度案防工作整体开展情况进行总体评估。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第二章  组织领导及职责分工', '第四条', '本行成立以董事长为组长，行长、监事长为副组长，副行长为组员的案防工作评估领导小组，负责督导全行的案防工作评估。领导小组下设办公室于合规管理部，负责牵头协调总行各部室、各支行的案防工作评估，组织推动案防工作评估方案的落实。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第二章  组织领导及职责分工', '第五条', '合规管理部为本行案防评估工作的牵头部门，负责制定年度案防工作评估方案，牵头协调总行各部室、各支行开展案防工作评估，组织推动案防工作评估方案的落实。总行各部室、各支行为具体案防工作评估的实施主体，负责本部室、本支行职责范围内的案防工作评估。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第六条', '总行总体评估的内容主要包括案防工作组织、制度及质量控制、案防工作执行、监督与检查及考核与问责五个方面，支行、部门自我评估内容由本行根据上述内容，结合当年的案防形势、监管要求，在当年的案防工作评估方案中进行明确。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第七条', '案防工作组织评估主要是对董事会对案防工作的部署、推动情况，董事会下设委员会对案防工作的推动、落实情况；监事会对案防工作的监督履职情况；经营管理层对案防工作的组织落实履职情况；案防工作牵头部门对案防工作的执行情况等内容的评估。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第八条', '案防制度及质量控制评估主要是对具体业务部门发起制定的业务制度和操作细则的完备性、制度建设的全流程管理、案防制度体系建设等制度体系建设；授权、岗位分离刚性约束、信息系统建设等内部控制有效性；部门间、各层级机构间工作沟通协调机制、完善案件防控工作保障体系等案防工作机制建设等内容的评估。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第九条', '案防工作执行评估主要是对案防管理人员配备、案防培训体系建设、员工行为管理、案件和案件风险报送情况、案防统计制度报送情况、案件堵截质效和案件查处质效等内容的评估。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第十条', '案防工作监督与检查评估主要是对合规审查、监督和检查质效、案防工作开展情况、专项检查开展情况等内部检查情况；经由内外部审计部门、上年度案防监管评估中已发现问题以及监管部门发现问题的暴露情况；已发现问题的整改情况等内容的评估。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第十一条', '案防工作考核与问责评估主要是对案件责任追究机制、监督检查工作质效及问责、案件暴露问题的整改和案件责任人处理等案件整改工作情况等内容的评估。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第十二条', '总行总体评估内容的总分值为100分，各项评估内容所占权重分别为：案防工作组织13%、制度及质量控制15%、案防工作执行45%、监督与检查14%、考核与问责13%。支行、部门的自我评估内容的总分值为100分，具体评估内容及权重在当年的案防工作评估方案中进行明确。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第三章  评估内容与结果', '第十三条', '本行根据评估结果确定支行、部门案防等级。综合评分85分（含）及以上，评为“绿牌”；综合评分70分（含）及以上85分以下，评为“黄牌”，提醒关注薄弱项目；综合评分70分以下，评为“红牌”，约谈负责人，督促加强案防工作。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第四章  评估方式和时间', '第十四条', '案防评估周期为1年，从上年度12月份起至本年度11月份止。总行各部室、各支行应根据本行制定的案防工作评估方案要求，对职责范围内的案防工作进行评估，撰写《案防工作自我评估报告》，填报相关报表，每年11月底报送至合规管理部。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第四章  评估方式和时间', '第十五条', '合规管理部及时整理、汇总总行各部室、各支行的案防工作评估情况，并且进行复查，最终形成《xxx银行自我评估报告》，每年12月底前报送至银行业监督管理机构。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第五章  附则', '第十六条', '总行各部室、各支行应高度重视本行案防工作评估，切实做好本部室、本支行的案防评估工作，对不开展自评或者自评流于形式的部室或支行，总行将会对负责人和相关责任人员进行问责。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第五章  附则', '第十七条', '本评估办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《案防工作评估办法》', '第五章  附则', '第十八条', '本评估办法自印发之日起施行。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第一章  总则', '第一条', '为适应xxx银行（以下简称“本行”）经营管理需要，防范法律风险，维护本行合法权益，保证本行对外出具的各类法律性文件的真实、合法、有效，特制定本办法。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第一章  总则', '第二条', '法律事务审查是指本行合规管理部以及送审单位（部门）对法律性文件进行审查、审核，对法律风险进行识别、提示和控制的专门活动。送审单位（部门）须对法律性文件完整性、真实性负责；合规管理部对合法性、有效性负责。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第一章  总则', '第三条', '本办法适用于本行所有机构（含职能部门、各支行等），审查的法律性文件包括但不限于：1．对外产生权利义务关系的各类合同、业务协议、履约凭证、保函等法律文书；2．债务重组、资产处置等重要事项的法律文书；3．各类委托代理书、授权书等；4．新设、合（兼）并、分立、撤销经营机构或其他变更组织形式的法律文书；5．其他对外产生权利义务关系的法律性文件。本行统一使用的未做任何补充、修改的标准格式合同除外。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第二章  基本规定', '第四条', '有权签字人对法律性文件签字并加盖公章之前，应经合规管理部对法律性文件进行合规性、有效性审查，在合规管理部签署法律意见后方可签署。任何机构或个人不得越程序、逆程序未经本行合规管理部签署法律意见，直接擅自对外签署法律性文件。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第二章  基本规定', '第五条', '法律事务审查必须独立进行，法律意见应该客观、公允。任何单位或个人不得以市场竞争、业务拓展为由干预法律事务审查，不得掩盖风险，影响法律意见的出具。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第二章  基本规定', '第六条', '对于涉及或可能涉及重大、复杂法律问题的业务，有关单位或部门应在业务办理的前期，约请合规管理部早期介入。合规管理部指派人员参与业务谈判，审核相关合同、文件草案等内容，谋求本行最大利益。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第二章  基本规定', '第七条', '提审单位（部门）必须在审查事由中客观、全面地述明审查背景，涉及采购物资、系统开发等事项，一并提交相应的立项文件。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第二章  基本规定', '第八条', '已经审查的法律性文件在履行过程中如发生补充、修改等重大变更，须重新履行审查程序。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第二章  基本规定', '第九条', '对涉及重大、复杂业务或管理问题的法律性文件，合规管理部审查人员应认真听取有关单位或部门提审人员以及管理人员的业务意见，详实了解后方可出具法律审查意见。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  岗位职责', '第十条', '支行（部门）经办人员：1.提供待审的法律性文件，准备相关的立项材料；2.在银丰平台提起法务审查；3.与对方当事人磋商确认文稿;4.对相关材料按规定要求进行归档。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  岗位职责', '第十一条', '支行（部门）合规员：负责提审材料的合规性、完整性进行审查，提出初步审查意见。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  岗位职责', '第十二条', '支行（部门）负责人：负责对提审材料进行总体审核，签署单位（部门）审核意见。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  岗位职责', '第十三条', '合规管理部法律事务岗：负责按照管理办法的要求对送审材料进行全面审查，提出审查意见。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  岗位职责', '第十四条', '合规管理部总经理岗：负责对法律性文件审查意见进行复核;2.对提审的经办人员与对方当事人磋商确认的文稿审核定稿。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  岗位职责', '第十五条', '领导审批（核）根据本行授权规定，业务分管领导对其权限内事项审批，超过其权限的由总行有权人审批。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  操作流程', '第十六条', '法律事务审查在银丰OA中进行，操作流程按先后顺序分为送审阶段、审查阶段、审批阶段和签约归档阶段。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第三章  操作流程', '第十七条', '流程说明：1．送审阶段（1）提出申请支行（部门）经办人员提起法务审查，事由中应客观、全面地述明审查背景，涉及采购物资、系统开发等事项，一并提交相应的立项文件。合同（协议）对方当事人为具体人员时选择“非格式化合同”，为不具体的某类人群时选择“格式化合同”。（2）初步审查支行（部门）合规员对送审文件进行初步审查，出具初审意见。（3）审核支行（部门）负责人对送审文件进行总体审核，出具审核意见。对审核同意的提交合规管理部审查，对审核不同意的退回经办人员。2．审查阶段（1）要件审查①主体资格审查：- 合同主体是否真实、合法；- 是否超越核定的经营范围；- 是否取得经营相关行业的许可或批准。②形式要件审查：- 主合同、从合同及相关附件是否齐全；- 合同文本是否规范；- 合同的主要条款是否齐全；- 主、从合同的签定日期、合同字号、份数及地点是否准确；- 法律规定需经有关部门批准、登记才能生效的合同是否经过批准、登记。③实质要件审查：- 合同当事人签订合同的意思表示是否真实；- 合同内容是否违反国家法律、法规或社会公共利益；- 合同条款是否违反本行规章制度或有损本行利益、声誉；- 法律关系的客体是否为国家禁止流通物或未经许可的限制流通物以及法律、政策所禁止的行为；- 责任追究是否恰当。（2）出具意见①答复时限：合规管理部对法律性文件的审查一般应在收到全部送审材料后3个工作日内完成，重大、复杂或疑难的法律性文件应在收到全部送审材料后7个工作日内完成。②签署意见：合规管理部对所审查的法律性文件实行“复审制”，先由合规管理部审查人员核稿，再由合规管理部负责人复核，审核采取直接修改的方式。合规管理部审查认为制定依据存在明显问题，或者存在整体上缺乏可操作性等情况，经合规管理部负责人复审后可予驳回。3．审批阶段①提审部门业务分管领导对其权限内事项审批，超过其权限的按规定流程提交总行有权人审批。②非格式合同，经总行领导审批后，提审人员应与对方当事人再次磋商合同条款，对方提出的对个别条款的修改意见，不构成合同主要条款的修改，在合规管理部总经理核对后定稿；构成重大修改的，退回经办人员，需重启法务审核流程。4．签约归档本行做好法律性文件以及立项文件等相关材料归档保存，区分非格式合同和格式合同保存。格式合同填列完整经审查后签章，本行保存二份，一份由提审业务部门保存，一份交由办公室统一保存。非格式合同由提审的业务部门统一印制，并且指导填写。提审业务部门和合规管理部做好审核定稿的下载保存工作。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第四章  附则', '第十八条', '本办法未尽事宜以及与国家有关法律法规、规章的规定冲突的，按国家法律法规、规章等规定执行。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第四章  附则', '第十九条', '本办法由xxx银行合规管理部负责解释和修订。'),
(NULL, '合规管理部', '《法律事务审查办法》', '第四章  附则', '第二十条', '本办法自印发之日起施行。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第一章  总则', '第一条', '为进一步规范信贷审查委员会(以下简称“贷审会”)工作程序，明确工作职责，提高审议决策水平，防范和化解信贷风险，结合本行实际，特制定本议事规则。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第一章  总则', '第二条', '贷审会是本行经营管理层信贷业务审查、管理、决策议事机构，对行长室负责，接受本行董事会、监事会的检查和监督。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第一章  总则', '第三条', '贷审会实行“集体审议，独立表决，以多数委员意见为准”的审议决策方式，遵循“客观公正、意见明确、严控风险”的基本原则。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第二章  组织机构', '第四条', '人员构成出席人员。贷审会由总行分管中后台副行长，信贷管理部、授信评审部、风险管理部、资产保全部、发展规划部等部门负责人及本行具有评审能力的人员组成，分管授信业务的副行长任主任委员。具有评审能力的人员由“三长”中任一或主任委员根据会办需要指定，总数不得超过2人。列席人员。董事会、监事会、审计稽核部应派员列席会议，但不作为委员参加投票。涉及重大事项或需说明具体情况的，申报部门、支行负责人可列席会议。贷审会成员工作变动时，其贷审会委员职务同时取消。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第二章  组织机构', '第五条', '组织机构贷审会下设办公室，办公室设在授信评审部，作为贷审会的具体办事机构。贷审会办公室主任由授信评审部总经理担任。贷审会办公室的主要职责有：（一）受理部门、支行上报贷审会审议的调查报告等材料，并审查其是否合法合规，负责在会办前将提请审议的事项提交贷审会各委员；（二）提请召开贷审会会议，核实参会委员资格，确认出席会议的委员是否达到规定人数，组织当场投票和计票；（三）负责会议记录并根据审议情况填制《贷审会表决意见汇总表》；（四）根据贷审会审批意见以及总行行长最终报备意见，起草并下发授信批复或审批结论；（五）按照档案管理要求对贷审会审议的各项信贷事项的资料及有关文件的归档保存；（六）协调和落实贷审会交办的其他事项。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第三章  工作职责', '第六条', '贷审会审议范围包括：（一）研究制定本行信贷政策、信贷业务发展策略、各项信贷业务管理制度与流程、信贷业务授权管理制度等；（二）审议超过授信评审部权限以外的授信业务（含借新还旧、展期、重组等）；（三）审议利率优惠事项；（四）审议新开办的信贷业务，审议相关信贷业务执行情况；（五）审议总行行长或分管行长认为有必要提交贷审会审议的特别授权及其他信贷特别事项；（六）审议其他需要向董事会提交的信贷事项。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第四章  权利与义务', '第七条', '贷审会委员在履行职责时享有下列权利：（一）向相关部门了解需经贷审会审议的客户生产经营状况、个人资信等各方面情况以及部门信贷管理情况；（二）对提交贷审会审议的事项独立发表个人意见；（三）对改进贷审会工作提出建议；（四）对经贷审会审议，主任委员审批通过的信贷事项落实情况享有质询权。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第四章  权利与义务', '第八条', '贷审会委员在行使权利时需履行以下义务：（一）加强学习，提高自身综合素质和履职能力；（二）按时出席贷审会会议，无特殊情况不得无故缺席，并就会议审议的事项认真发表意见和建议，客观、公正地对审议事项进行审议；（三）秉公办事，严格按照审议程序和信贷政策、原则、制度进行审议，并及时签署意见。严禁委员在会前私下讨论拟审议事项；（四）严格遵守保密制度，对贷审会审议的事项及各委员发表的审议意见、表决情况不得泄漏。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第九条', '会议时间贷审会召开时间相对固定，原则上每周星期二、星期五召开，无审议事项的除外。对时间要求紧迫的授信业务或其他紧急重大事项，经主任委员提议，可召开临时会议，具体时间由主任委员决定。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十条', '出席人员贷审会会须三分之二（含）以上的委员出席方可召开，委员因故不能出席的，不得由他人代为行使表决权。必要时，主任委员可以邀请总行法律顾问列席会议，在法律、法规方面提供决策支持，但不参与投票。如因特殊情况无法召开贷审会会议，但需审议事项紧急的，可以通过流转方式进行表决，即由贷审会办公室将审议材料当面送交委员，委员在了解审议事项后进行书面表决。或通过电话向委员简述审议事项并听取其表决意见，并在事后进行书面确认。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十一条', '主持人贷审会会议由主任委员主持，主任委员因故不能主持贷审会会议时，可委托其他委员主持，待主任委员回行后补签会议记录和审议簿，其审批责任不变。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十二条', '会议准备凡需提交贷审会审议的信贷事项，授信评审部应及时受理、登记和初审，对项目贷款及时组织评估并提出复查意见。凡超过授信评审部权限的贷款，由申报部门、支行调查，授信评审部复查，审查后提交贷审会集中审议。超过贷审会权限的及时提交董事会决策审批。（一）受理。凡拟提交贷审会审议的信贷业务，授信评审部至少提前1个工作日将经其部门负责人审查签字的内部运作资料及信贷申请报告、调查报告、评估报告（或专家咨询意见）等资料明确专人送交贷审会办公室。（二）审查。贷审会办公室按规定对拟提交审议的有关信贷事项进行审查，对符合规定要求的信贷事项及时安排提交贷审会审议。对不符合规定要求的，应要求提请审议的部门、支行补充完善。（三）会议准备。由贷审会办公室提请主任委员及时召开贷审会，并提前做好会议资料准备，通知贷审会委员和汇报、列席人员按时参加会议。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十三条', '会议程序（一）情况介绍。授信评审部负责人介绍需审议事项，并根据法律法规、信贷管理制度以及本行相关规定，汇报审查意见。（二）会议讨论、审议。与会委员根据阐述情况及审查意见，本着实事求是、客观公正、防范风险的要求以及认真负责的态度，就审议事项的主要方面和突出问题进行慎重审议，并在会上发表观点明确的意见。（三）委员表决。表决采取记名投票表决方式，一人一票，表决意见分为“同意、否决、另行复议”三种，不得弃权，表决意见“否决”或“另行复议”的，必须在表决意见书上说明原因。（四）形成决议。贷审会实行记名投票表决方式，并当场公布投票结果。贷审会的委员当场投票，会后不得补票。表决人未明确提出不同意,视为同意确认。贷审会办公室负责计票，审计稽核部参会人员负责监票，并在《贷审会表决意见汇总表》上签名，并记入贷审会记录。贷审会投票结果按以下标准认定：同意时，超过参加会办人数三分之二的为通过，否则为不通过。主任委员有一票否决权，但同意时仅算一票。（五）会议记录。贷审会办公室设立专门的会议记录簿，专人负责对贷审会审议的信贷事项进行记录。记录人员要记录会议的次序、时间、地点、主持人、出席委员、列席人员、缺席人员、记录人，逐项记载审议的情况，会议记录内容包括审议事项、报告部门和报告人、报告内容、委员的审议发言、表决结果等。对贷审会的会议记录，贷审会委员应在会议记录簿上签字，贷审会办公室列入永久档案管理范围妥善保管。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十四条', '行长审核贷审会办公室将贷审会决议报总行行长审核对贷审会审议通过的信贷事项，总行行长可行使一票否决权。对经投票未通过的信贷事项，总行行长不得行使一票赞成权。但不论投票结果如何，总行行长均有提出复议的决定权。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十五条', '贷审会批复贷审会办公室应在会办同意后，根据总行行长审核、备案情况出具授信批复。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十六条', '督促与检查贷审会办公室应督促支行落实贷审会审议通过审批的信贷事项，并对执行情况进行跟踪检查。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第五章  议事规则及程序', '第十七条', '超权限事项超过贷审会审批权限的审议事项，经贷审会审议后，按照总行转授权办法报送至董事会审批。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第六章  复议程序', '第十八条', '有下列情况之一的，可以申请复议：（一）贷审会委员不同意或多数委员质疑，主任委员认为有必要进一步论证的信贷业务，可指定有关部门补充论证意见，报下次贷审会复议；（二）申请部门、支行对贷审会否决的事项，认为否决不当的可以申请复议。如需复议的事项，复议时需提交该事项情况的说明、初议疑问落实情况以及初议提出需补充的材料。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第六章  复议程序', '第十九条', '对总行行长签署的意见为复议的信贷事项，由贷审会办公室直接通知部门、支行，根据贷审会和总行行长签署提出的复议意见准备复议报告。对审议结果为否决的事项，总行行长如签署复议意见，均须在审议簿上注明复议的理由和复议的重点内容。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第六章  复议程序', '第二十条', '对总行行长签署的意见为否决的信贷事项，支行如提请复议，须提出书面申请，并说明提请复议的理由。贷审会办公室受理后，报贷审会主任委员审批同意后，做复议准备。授信评审部根据贷审会审议和总行行长签署的意见，进行核实，形成复议报告报贷审会。复议报告应包括该信贷事项情况的简要说明、上次贷审会和总行行长签署提出的问题、对这些问题调查核实和落实的情况、需补充资料的完善情况、审查意见等。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第六章  复议程序', '第二十一条', '贷审会对各类信贷事项的复议只限一次。经复议的信贷事项，若仍达不到决议票数，即为否决，复议被否决的事项三个月内不得提交贷审会审议，除非经贷审会主任委员或总行行长提议或项目情况发生重大变化，对贷审会审批结论将产生重大影响，并经贷审会主任委员或总行行长批准同意。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第七章  其他规定', '第二十二条', '专家咨询制度。贷审会可视情况建立兼职专家（法律、经济、金融、技术等）评审队伍，对信贷审查中有疑问的事项进行咨询，参与系统内的项目评估。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第七章  其他规定', '第二十三条', '实行重大项目列席制度。对大额贷款项目、风险较大的贷款项目、行业性和系统性客户的大额授信，以及其他需要重点说明的事项，申报部门、支行负责人经主任委员批准后可列席贷审会会议，补充说明情况，但在表决前须退席。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第七章  其他规定', '第二十四条', '建立报告制度。对贷审会讨论决策的重大事项及贷审会认为有必要报告的事项，定期向董事会、监事会书面报告。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第七章  其他规定', '第二十五条', '实行检查制度。贷审会定期对以前审议发放的贷款或同意的其他信贷事项进行事后检查，以便及时总结经验、吸取教训，不断提高审贷决策水平。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第七章  其他规定', '第二十六条', '回避制度。对下列情况，委员应主动申请回避：（一）审议与委员本人或其近亲属有关的事项；（二）审议与委员近亲属投资或者担任高级管理职务的公司、企业和其他经济组织有关的事项；（三）审议其他与委员本人或其近亲属可能有重大利益冲突或有其他可能导致不公正对待的事项。主任委员认为本人应该回避的，应向总行行长提出申请回避，经同意后由行长指定的其他副行长主持会议。其他委员认为符合回避条件的，应主动向主持人说明情况，由主任委员决定该委员是否应该回避。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第八章  附则', '第二十七条', '本规则由本行信贷审查委员会办公室负责解释和修订。'),
(NULL, '授信评审部', '《信贷审查委员会议事规则》', '第八章  附则', '第二十八条', '本规则自下发之日起执行。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第一条', '为建立和健全本行评级机制，科学评价客户信用状况，优化信贷客户层级结构，依据《中华人民共和国商业银行法》、《中华人民共和国银行业监督管理法》、“三个办法一个指引”等有关法律、规章 的规定，结合本行实际以及新一代信贷系统管理的要求，特制订本办法。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第二条', '本行服务的法人类客户和自然人客户统称为客户。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第三条', '本行客户信用等级评定指采取定量分析与定性分析相结合的方法，综合考核财务指标和非财务指标，以偿债能力为核心，从履约能力、偿债能力、盈利能力、经营及发展能力等方面，对客户的经营状况和资信状况进行综合评价和信用等级确定。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第四条', '客户信用等级评定由本行下辖各支行、营业部部按权限具体操作。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第五条', '客户信用等级评定应遵循客户自愿、统一指标、综合评价、分类确定、定期评估、适时调整、分级管理、有效组织的原则。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第六条', '客户信用等级评定应坚持“独立、客观、公正”和“回避”的原则。《中华人民共和国商业银行法》规定的“关系人”以及与其有利害关系的其他人员作为评级对象时，相关工作人员不得参与评级业务，应予以回避。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第七条', '信用等级评定是申请授信业务的前提条件和重要依据，客户信用等级评定应坚持“先评级、后授信”的原则。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第一章  总则', '第八条', '本行内部评级结果不得对外公布。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第二章  评级对象', '第九条', '本行客户信用评级对象包括：1、已与本行建立信贷关系的客户；2、拟申请建立信贷关系的客户；3、需提供资信证明的客户；4、申请资信评估等业务的客户；5、其他需要进行信用评级的对象。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第二章  评级对象', '第十条', '只作为担保人的客户，可按本办法的规定进行信用等级评定。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第三章 评级设置', '第十一条', '法人类客户信用等级评定实行百分制。按评级结果从高到低设置AAA、AA、A、BBB、BB、B共六个信用等级。信用等级标准分数信用度AAA级客户得分在95分以上（含95分）优秀AA级客户得分在90分-94分好A级客户得分在80分-89分一般BBB级客户得分在70分-79分不佳BB级客户得分在60分-69分恶化B级客户得分在51分-59分信用等级为AAA级、AA级、A级客户为优良客户，BBB级客户为一般客户，BB级为限制客户，B级为淘汰客户。本行客户信用等级的核心定义：AAA级：法人类客户生产经营符合国家产业政策，管理层素质高，信用记录很好；负债远低于行业标准水平，经营性现金净流量充足；违约风险较小，发展前景很好。AA级：法人类客户生产经营符合国家产业政策，管理层素质较好，各类信用记录较好，资产负债率低于行业标准水平；现金流量较充足，具有较强偿债能力；违约风险较低，发展前景良好。A级：法人类客户生产经营符合国家产业政策，管理层素质良好，客户信用状况良好，资产负债结构合理；现金流量基本充足；经营和财务风险影响一般；违约风险较低，发展前景稳定。BBB级：表明法人类客户生产经营符合国家产业政策，管理层素质一般，信用记录较好，无拖欠贷款本息现象；经营和财务实力一般，应付较大风险能力一般，现金流量基本能够保证；经营和财务风险的影响较多；违约风险较低，发展前景一般。BB级：表明法人类客户生产经营符合国家产业政策，管理层素质一般，经营和财务管理存在一定缺陷；信用记录较差，自觉按时归还贷款本息的意识不强；经营和财务实力弱化，负债率居高不下，现金流量偏紧；经营和财务风险的影响显著增强，偿债能力明显削弱，发展前景较差。B级：表明法人类客户生产经营符合国家产业政策，管理层素质较差，经营和财务管理存在缺陷；信用状况差，自觉按时归还贷款本息的意识不强；经营实力和财务实力相对削弱，现金流量衰减；经营或财务风险相对较高，偿债能力相对不足，发展前景差。自然人客户信用评级分五级：优秀，良好，一般，不佳，恶化。优秀：整体实力较强，家庭、居住及职业稳定，职业发展或个私经营发展获得显著成功且前景十分看好，经济收入高，家庭综合经济实力强，还贷能力强，承贷能力强，信用品质佳，信用风险小。良好：整体实力强，家庭、居住及职业稳定，个私经营发展较为顺利且前景看好，经济收入高，承贷及还贷能力强，信用品质好，信用风险小。一般：整体实力较强，家庭、居住及职业较稳定，个私经营发展较为顺利且前景较好，具有一定的发展潜力，经济收入较高，承贷及还贷能力较强，信用品质好，信用风险较小。不佳：整体实力较弱，家庭、居住及职业的稳定性一般，个私经营发展一般，自然人发展前景不确定，经济收入偏低，承贷及还贷能力偏弱，信用风险较大。恶化：整体实力差，家庭、居住及职业的稳定性差，经济收入极低或收入不稳定，承贷及还贷能力很弱或丧失，存在不良信用记录，信用风险大。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第四章 评级指标体系与计分标准', '第十二条', '法人类客户信用评级指标体系法人类客户主要从偿债能力、盈利能力、经营管理、履约情况及发展能力和潜力等方面对企业客户信用状况进行评价。自然人客户主要依据个人健康状况、婚姻状况、学历状况、社会影响、家庭信用状况、家庭年收入及支出、经营项目、存贷比例、市场前景、年收益、家庭净资产等综合情况确定量化评价指标，由此构成一个完整的信用评级指标体系。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第四章 评级指标体系与计分标准', '第十三条', '根据评级对象的行业差异及资金运用特点，分别设置各类客户信用评级指标体系与计分标准（详见附表）。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第五章  信用评级程序', '第十四条', '受理申请。由客户向各有贷支行、营业部提出申请。法人类客户提供以下资料：（一）基本信息资料1、经年检的营业执照、组织机构代码证书(副本及复印件)；2、贷款卡、银行开户情况；3、法定代表人（负责人）或主要股东个人身份证明及必要的信息；4、近两年的财务报表；5、法定代表人（负责人）或主要股东报告期借款及对外担保情况；6、税务部门年检合格的税务登记证明和近一年税务部门纳税证明复印件；7、合同或章 程(原件及复印件)；8、董事会成员和主要经营管理负责人、财务负责人、技术负责人名单和签字样本等；9、若为有限责任客户、合资合伙客户或承包、租赁客户，要求提供董事会、全体合伙人或发包人同意申请授信业务的决议、文件或具有同等法律效力的文件或证明；10、授信业务由授权委托人办理的，需提供客户法定代表人授权委托书(原件)；11、其他必要的资料。（二）非财务信息资料：1、客户关键人员如经营决策人员、主要执行人员的个人职业经历、受教育背景、品行、健康状况等；2、法定代表人（负责人）或主要股东个人及其家庭其他投资、资产负债及或有负债情况；3、法定代表人（负责人）或主要股东家庭成员情况、家庭居住情况，婚姻状况，家庭大致日常收入、生活开支情况；4、法定代表人（负责人）或主要股东个人资信情况，企业征信系统信息和个人征信系统信息；客户在工商、税务、海关等部门的信用记录情况；5、客户近一年的水电费或其他公用事业收费清单；6、客户近一年的设备运转和开工率；主要生产设备的技术水平；7、客户成品仓库的入库、出库情况；8、客户的纳税清单；9、客户的资产、职工人数、收入情况；10、客户近一年的现金流量情况；11、客户主要供应商和销售商情况；12、其他情况。自然人客户提供以下资料：1、户口簿及身份证明复印件（公务员须所在单位出具公务员身份证明）；2、在银行开户证明复印件（如存折）；3、家庭资产及负债情况（房、地产权证明）；4、个体工商户提供营业执照副本复印件，纳税证明；5、抵（质）押物清单和有权处分人同意抵（质）押承诺；6、担保人有效身份证明复印件；7、担保人资质证明（营业执照）；8、其它需提供的相关资料。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第五章  信用评级程序', '第十五条', '评审确认。法人类客户评审：支行、营业部受理法人类客户申请后，组织调查人员深入调查核实客户提供的基本信息资料、非财务信息资料的真实性，按本办法规定的评级指标和计分标准对客户信用等级进行初评，填写客户信用等级评定报告，经单位负责人签定确认信用等级后，通知客户，超权限的报授信中心或授信管理部，评级权限与授信额度的审批权限一致。自然人客户评审：各支行、营业部受理自然人客户申请后，组织调查人员深入调查、核实自然人客户提供资料的真实性，按本办法规定的评级指标和计分标准对自然人客户信用等级进行初评，由经支行、营业部、负责人签定确认信用等级后，通知客户，超权限的报授信中心或授信管理部，评级权限与授信额度的审批权限一致。授信中心或授信管理部负责对小支行、营业部上报的客户评级资料进行审查，审查后交有权人审批。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第五章  信用评级程序', '第十六条', '客户评级应适时调整，并实行动态跟踪。客户的评级周期原则上不得超过一年，各单位必须结合贷后检查对客户的评级做一期复评。客户生产经营情况发生重大变化的，信用等级应及时进行调整。新客户信用等级的评定按“随报随评”的方式进行。评级时应采用最近一期的月度、年度财务报表的财务数据。各评级人员必须提高对客户财务报表质量的要求，加强对其财务报表的真实性进行审查。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第五章  信用评级程序', '第十七条', '客户信用等级评定情况确定后，若发现客户用虚假资料骗取较高级别信用等级的，本行应立即取消其评级资格，并实施信贷制裁。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第六章  评级调整', '第十八条', '在客户信用等级有效期内，如客户发生下列情况之一，各支行、营业部有责任及时提示并按程序调整授信客户的信用等级。法人类客户：1、主要评级指标明显恶化，导致评级分数及信用评级结果降低；2、主要管理人员涉嫌重大贪污、受贿、舞弊或违法经营案件；3、出现重大经营困难或财务困难；4、在与本行业务往来中有严重违约行为；5、发生或涉入重大诉讼或仲裁案件；6、与其他债权人的合同项下发生重大违约事件；7、其他应改变评定等级情况。自然人客户：1、从事赌博、吸毒、贩毒等违法经营活动；2、有不良信用记录；3、借款转借他人使用；4、经营的业务资不抵债；5、从事高利贷活动；6、用贷款从事炒股；7、其他应改变评定等级的情况。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第七章  罚则', '第十九条', '有下列情况之一的，视情节轻重对有关责任人予以相应的经济处罚、行政处分，造成损失进行追偿。构成犯罪的，依法移交司法机关追究其刑事责任和经济责任：1、参与或默许客户以编制虚假报表等方式骗取较高信用等级；2、未经许可，擅自向客户透露信用等级内部评级；3、不当泄露客户信息资料；4、存在其它不当行为造成严重后果的；5、评级结果未体现在授信审批表上的且评级表未作为上报审批材料的。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第八章  附则', '第二十条', '本办法由本行授信评审部负责解释和修订。'),
(NULL, '授信评审部', '《客户信用评级管理办法》', '第八章  附则', '第二十一条', '本办法自颁布之日起实行。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第一章  总则', '第一条', '为加强和规范抵（质）押物权证的管理，保证抵（质）押物权证的安全性和完整性，依法维护银行债权，防范和化解信贷风险，特制定本办法。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第一章  总则', '第二条', '本办法明确了抵（质）押物权证的转入、转出、保管等内容和要求。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第一章  总则', '第三条', '本办法中的抵（质）押物权证均指原件。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第一章  总则', '第四条', '抵（质）押物权证的范围：（一）不动产抵押权证。包括房屋和其他地上定着物、建造中的商品房、预售房屋等不动产所有权抵押相关权证。 （二）动产抵押权证。包括工程机械、航空器、船舶、车辆和依法可以抵押的其他财产等不动产所有权抵押相关权证。（三）权利质押权证。汇票、本票、国债、金融债券、存款单、标准仓单、提单、依法可以质押的具有现金价值的人寿保险单、依法可以转让的商标专用权、专利权、著作权中的财产权、农村土地承包经营权、水面承包经营权、林权、应收账款债权、车辆合格证和依法可以质押的其他权利等权利质押相关权证。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第二章  组织管理体系', '第五条', '经办支行负责人对抵（质）押物权证的真实性负责。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第二章  组织管理体系', '第六条', '授信评审部负责抵（质）押物权证的统筹管理；总行档案室负责抵（质）押物权证的保管。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第二章  组织管理体系', '第七条', '合规管理部负责抵（质）押物权证管理的合规检查。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第二章  组织管理体系', '第八条', '审计部负责抵（质）押物权证管理的审计监督。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第三章  抵（质）押物权证管理流程', '第九条', '外部环节流程1.实质审核。客户提出抵押申请后，中心或支行须明确客户经理现场了解拟抵押的产权属性、位置、现状、用途及权属证原件等，提出明确的审核意见。特别提醒：对抵押财产已出租的，重点把握下列环节,即提供租赁合同原件或加盖抵押人印章的复印件一份存档；租赁合同期限超过5年的，应审慎办理抵押，超过10年的，不得抵押；抵押期限须与租赁期相匹配；抵押人、承租人应分别出具放弃收益权、知晓抵押权的承诺函。2.申请办证。对授信的贷款由客户准备相关权证资料，提交经办行审查同意后，客户经理进行市场询价，客户委托在本行准入的资产评估公司进行评估，双方确认资产抵押值，客户经理陪同抵押人一起到有关部门办理登记手续。总行要求，客户经理须参加办理至领证全过程，严禁交由抵押人单独办理登记和领证。原则上不提倡以异地的不动产抵押，对确因需要而办理的，资产价值原则上应经有资质的评估公司评估而定，其他手续与本地资产的流程、要求一致，不得由抵押人代办和代领。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第三章  抵（质）押物权证管理流程', '第十条', '、内部环节流程1.他项权证入账。他项权证原件被客户经理领回后，不得再交抵押人或借款人保管，也不允许留存客户经理手中过宿，应立即移交柜面代保管。他项权证原件入库代保管前，客户经理应复印一份留存后，将原件与抵押人或借款人一起交柜员办理代保管手续。柜员审核后，开具一式叁份代保管凭证，柜员、客户经理、客户各执一份。日终，柜员将原件交主出纳保管，并记载内容，包括权证编号、抵押人、抵押面积、抵押值、抵押期间、入库时间、上缴时间、退库时间等。客户经理将他项权证复印件和代保管凭证存入信贷档案资料中，集中保管。2.权证上缴。支行每月将他项权证原件集中上缴总行档案室保管，上缴的流程按“重要空白凭证”的管理要求进行。档案室一方面要制定各支行定期上缴的时间，以便各支行执行，另一方面，要建立他项权证管理台账，与支行实行双套账管理。3.权证借（查）阅。权证原件保管期间，原则上不允许外借，对因贷款展期，办理抵押物续期登记、变更抵押物、法律诉讼、补办房产证、他项权证，以及因上级部门查阅等因素而需外借的，须由支行申请，指定合规员和经办客户经理为外借期间管理责任人，经授信评审部审核批准后，方可外借，办理借阅的必须由支行凭手续办理。档案室收到管理部门批准的外借申请后，按档案管理规定流程操作。4.权证退回。因借款人贷款已结清，或抵押期满需取回抵押证原件的，由支行申请，明确合规员或经办客户经理办理，经授信评审部审核批准后，到档案室办理退回手续。合规员或客户经理将取回的他项权证书原件不得直接退还抵押人或借款人，而应履行移交手续，将原件交主出纳，由抵押人或借款人凭代保管凭证到主出纳处，办理退回手续。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第三章  抵（质）押物权证管理流程', '第十一条', '抵债资产的取得和处置、抵押物的拍卖、变卖，抵押物的变更、债权转移等所产生的抵押物权证的转入和转出业务参照上述流程办理。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第三章  抵（质）押物权证管理流程', '第十二条', '监督与检查1.合规管理部牵头，信贷管理部、风险管理部、授信评审部配合，定期对抵（质）押物从法律上的有效性、价值上的充足性、担保续存期间的安全性和执行上的可变现性四个方面检查进行检查，保证抵（质）押物权证的账账、账实相符。2.审计部对抵（质）押物权证管理情况进行审计监督。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第四章  罚则', '第十三条', '违反本办法相关规定的责任人员依照本行员工违规行为处罚相关规定予以经济处罚和行政处理；造成损失的，由责任人赔偿；触犯法律的，移交司法机关处理。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第四章  罚则', '第十四条', '抵（质）押权证保管人包括移交前的和移交后的，责任的界定以权证转移为节点。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第五章  附则', '第十五条', '本办法由授信评审部负责解释和修订。'),
(NULL, '授信评审部', '《抵（质）押权证集中管理办法》', '第五章  附则', '第十六条', '本办法自印发之日起试行。'),
(NULL, '授信评审部', '《授信管理办法》', '第一章  总则', '第一条', '为进一步完善本行授信工作机制，规范授信业务管理，明确授信工作尽职要求，促进本行审慎规范经营，依据《中华人民共和国商业银行法》、《中华人民共和国银行业监督管理法》、《商业银行授信工作尽职指引》和“三个办法一个指引”等有关法律、法规的规定，结合本行实际，制定本办法。'),
(NULL, '授信评审部', '《授信管理办法》', '第一章  总则', '第二条', '本办法中所称“授信”是指根据客户的申请，本行按照规定的程序和条件确定可向客户直接提供信贷资金的可用额度或者对客户在经济活动中可能产生的赔偿、支付责任做出保证。'),
(NULL, '授信评审部', '《授信管理办法》', '第一章  总则', '第三条', '本办法中所称“授信业务”是指本行对客户提供的各类信用的总称，包括本外币贷款、贴现、银行承兑、保函、贷记卡等表内外资产业务。'),
(NULL, '授信评审部', '《授信管理办法》', '第二章  授信管理组织体系', '第四条', '按照“管理垂直化，机构扁平化”原则，设立前台营销部门、中后台管理部门和决策层三个层级。'),
(NULL, '授信评审部', '《授信管理办法》', '第二章  授信管理组织体系', '第五条', '前台营销部门为各支行、营业部、公司业务部、小微业务部、电子银行部、金融市场部（以下统简称“经办行”）。承担信贷业务的受理、调查、评估，负责收集授信材料、进行尽职调查、提出授信初步意见。授信审批完毕后，根据授信审批意见落实授信条件，负责授信业务的日常管理。'),
(NULL, '授信评审部', '《授信管理办法》', '第二章  授信管理组织体系', '第六条', '中后台管理部门为授信评审部、信贷管理部、风险管理部、合规管理部等部门。授信评审部负责组织年度集中授信、日常授信，对各经办行提交的授信材料进行审查，提出授信意见后按照本行管理权限分别报有权审批人审批和报备。信贷管理部负责对授信业务的贷后管理工作，对重点行业、区域、客户和授信品种进行跟踪监控和系统性风险的调查研究，为授信评审工作提供预警、防范风险的指导性意见。风险管理部对信贷审查委员会（以下简称“贷审会”）审议提交董事会风险与关联交易管理管理委员会大额授信申报备案审查事项进行事前风险审查，并出具审查意见，供董事会决策。合规管理部负责审查本行授信管理办法的合规性和有效性。'),
(NULL, '授信评审部', '《授信管理办法》', '第二章  授信管理组织体系', '第七条', '决策层为贷审会。贷审会是本行授信业务决策的议事机构，在授权范围内负责授信业务的审议和决策，对有权审批人进行制约以及智力支持。人员由分管中、后台业务的行长和相关部门负责人组成，分管行长任贷审会主任委员，实行记名投票表决。'),
(NULL, '授信评审部', '《授信管理办法》', '第二章  授信管理组织体系', '第八条', '实行授信业务授权管理制度。本行董事会对行长进行授权，行长对贷审会、授信评审部进行转授权。对超过贷审会权限的、及其他重大事项需报董事会风险管理与关联交易控制委员会审议。'),
(NULL, '授信评审部', '《授信管理办法》', '第三章  授信原则', '第九条', '授信业务实行客户统一授信管理。客户统一授信管理是本行对客户实施集中统一控制客户信用风险的管理制度。客户授信的条件由本行统一确定，做到授信“主体统一、标准统一、内容统一、对象统一”。（一）主体统一是指只能由一个经办行按规定程序和转授权权限对同一个自然人或法人客户进行授信；（二）标准统一是指所有客户的授信额度均根据本行制定的各项标准进行合理确定；（三）内容统一是指授信包括表内授信和表外授信，其中，表内授信包括贷款、贴现、透支、拆借和回购等，表外授信包括贷款承诺、保证、信用证、票据承兑等；（四）对象统一是指本行授信的对象是具有完全民事行为能力的自然人或具备独立法人资格的客户，不得对无民事行为能力的自然人或不具备法人资格的客户授信。'),
(NULL, '授信评审部', '《授信管理办法》', '第三章  授信原则', '第十条', '坚持“审贷分离，分级审批”的原则，建立严格的授信风险垂直管理体制，对授信进行统一管理。'),
(NULL, '授信评审部', '《授信管理办法》', '第三章  授信原则', '第十一条', '坚持“先评级，后授信”的原则，信用等级评定是申请授信业务的前提条件和重要依据。（一）公司客户信用等级分为 AAA、AA、A、BBB、BB、B、CCC、CC、 C 级，评定内容主要包括信用履约、偿债能力、盈利能力、经营能力、客户领导者素质和发展前景等因素。（二）自然人信用等级分为优秀、良好、一般、不佳、恶化等五级，评定内容主要包括个人基本信息、财务信息、家庭情况、经营情况、社会关系等因素。'),
(NULL, '授信评审部', '《授信管理办法》', '第三章  授信原则', '第十二条', '坚持“先授信，后用信”的原则，客户用信时必须存在有效的授信额度。'),
(NULL, '授信评审部', '《授信管理办法》', '第三章  授信原则', '第十三条', '坚持“独立、客观、公正”和“回避”的原则。'),
(NULL, '授信评审部', '《授信管理办法》', '第三章  授信原则', '第十四条', '授信后用信时，一笔用信业务不得同时占用综合授信额度与临时授信额度，也不能同时占用多个临时授信额度。用信申请必须在授信的有效期内发起，用信申请的到期日可以超过授信的到期日。有条件授信时应遵循“先落实条件，后实施用信”的原则，授信条件未落实或条件发生变更未重新决策的，不得使用授信。'),
(NULL, '授信评审部', '《授信管理办法》', '第四章  授信分类', '第十五条', '按照期限长短分为短期授信、中期授信、长期授信。（一）短期授信指一年（含）以内的授信。（二）中期授信指一年以上五年（含）以内的授信。（三）长期授信指五年以上的授信。'),
(NULL, '授信评审部', '《授信管理办法》', '第四章  授信分类', '第十六条', '按照授信方式分为综合授信、临时授信。（一）综合授信。指对客户的财务状况和信用风险等方面进行综合测算的基础上，确定给予客户的最高综合授信额度，采取“一次核定，周转使用，总额控制”的管理方式。1.综合授信额度为可循环使用的授信额度，该额度项下授信业务在授信额度有效期内和用信余额不超过授信总额的前提下，履行用信审批程序后可循环使用。2.单一客户仅存在一个有效的综合授信额度。新的综合授信额度审批通过后，自动替换原来的综合授信额度，原综合授信额度项下业务自动归并到新的额度项下。但存量业务占用金额超过新的授信额度时，不允许发生新业务，直至存量业务占用授信额度小于新的授信额度。3.法人或自然人申请综合授信时已存在有效临时授信的，按授信管理要求，原有临时授信应归并至新的综合授信内，并注明综合授信内含临时授信额度。（二）临时授信。采取“一次核定，单笔管理，到期收回”的管理方式。1.临时授信可单独存在，综合授信不是临时授信存在的前提条件。2.临时授信额度不可循环使用，临时授信结清后，该临时授信止。'),
(NULL, '授信评审部', '《授信管理办法》', '第四章  授信分类', '第十七条', '按照授信对象分为单一客户授信、集团（关联）客户授信和第三方额度授信。（一）单一客户授信。指对单一自然人、法人客户或其他经济组织进行的授信。1.单一客户授信总额不得超过本行资本净额的10%。2.单一客户授信额度等于各单项业务授信额度之和。（二）集团（关联）客户授信。指对一个集团（关联）所辖成员总体的一揽子授信，其授信对象是本行定义的集团（关联）客户（含虚拟集团和实体集团）。1.集团（关联）客户是指具有以下特征的企事业法人：（1）在股权上或者经营决策上直接或间接控制其他企事业法人或被其他企事业法人控制的；（2）共同被第三方企事业法人所控制的；（3）主要投资者个人、关键管理人员或与其近亲属（包括三代以内直系亲属关系和二代以内旁系亲属关系）共同直接控制或间接控制的；（4）存在其他关联关系，可能不按公允价格原则转移资产和利润，本行认为应当视同集团（关联）客户进行授信管理的。2.集团（关联）客户授信总额不得超过本行资本净额的15%。（三）第三方额度授信。应合作方客户申请，用于非本人用信的授信额度，主要用于第三方或按揭担保等业务。1.一个合作方客户可以根据不同项目同时存在多个有效的第三方授信额度。2.楼宇、汽车、机械、船舶等其他第三方授信额度均为非循环授信额度。3.对合作的担保公司第三方授信为循环授信额度。'),
(NULL, '授信评审部', '《授信管理办法》', '第四章  授信分类', '第十八条', '按照授信发生类型分为新增授信、存量授信、增量授信、调整授信。（一）新增授信是对客户的第一次授信；（二）存量授信是不超过客户存量余额授信额度的续授信；（三）增量授信是对超过存量余额授信额度的增加部分授信；（四）调整授信是对原有授信使用条件进行的部分调整，除授信对象不可修改外，授信类型、金额、期限等其他授信条件可按照实际情况进行适当调整。'),
(NULL, '授信评审部', '《授信管理办法》', '第五章  授信对象和条件', '第十九条', '授信对象分为公司客户和个人客户。1.公司客户指客户应当是经依法履行工商行政管理职能的机构核准登记的企(事)业法人、其他经济组织等；2.个人客户是指具有合法身份证件、在本行辖区内有有效居住证明（含暂住证）具有完全民事行为能力的自然人（除按揭类贷款外）。借款人为自然人的，借款时年龄与借款期限相加不超过65周岁（存单质押贷款除外）。'),
(NULL, '授信评审部', '《授信管理办法》', '第五章  授信对象和条件', '第二十条', '客户申请授信应当具备下列基本条件：（一）从事符合国家产业政策和社会发展规划要求的建设和生产经营活动，借款用途明确、合法；（二）有合法稳定的收入来源，具有按期还本付息能力；原应付贷款利息和到期贷款已清偿或落实了经本行认可的还款计划；（三）在本行开立结算账户，自愿接受本行的信贷监督和结算监督，如实向本行提供有关财务报表、税务证明等相关资料；（四）除自然人外，企业法人须持有履行工商行政管理职能的机构核准登记的证件，特殊行业须持有有权机构颁发的特殊行业许可证；事业法人依照《事业单位登记管理暂行条例》的规定向事业单位登记管理机关办理了登记或备案手续；（五）采用担保方式发放的，近3年内无借款、对外担保等重大不良信用记录；采用信用方式发放的，近5年内无借款、对外担保等不良信用记录；（六）申请短期贷款，申请客户的资产负债率原则上应低于70%。申请固定资产项目贷款的客户，应有经国家有权机关批准的项目立项批文，并按照相关管理办法规定提供相应比例的资本金；（七）申请流动资金贷款，客户经理需根据企业财务数据或实际需求进行流动资金贷款需求量测算；（八）申请票据贴现的，必须持有合法、有效的票据并提供具有真实商品交易背景的相关资料；（九）按照中国人民银行的有关规定，企业法人在发生信贷业务前，必须在征信业务系统中申请中征码；（十）其他应该具备的本行要求的授信条件。'),
(NULL, '授信评审部', '《授信管理办法》', '第五章  授信对象和条件', '第二十一条', '有下列情形的客户，不得对其授信：（一）不具有独立核算的法人类客户，或不具有完全民事行为能力的自然人；（二）贷款用途不明确的；（三）信用下降，存量贷款出现逾期或欠息的；（四）从事国家明令禁止的产品或项目的；（五）违反国家有关规定使用信贷资金，从事股本权益性投资，以授信资金作为注册资本金，注册验资和增资扩股的；（六）违反国家有关规定使用信贷资金，从事股票、期货、金融衍生产品等投资的；（七）提供虚假资料骗取授信的；（八）具有其他违反国家法律、法规、政策和本行强制性规定的行为；（九）新拓客户，已在两家金融机构有贷款授信的，在充分考虑其企业状况、总体信贷规模后采取审慎进入原则；对已在三家金融机构有贷款授信的，严禁新增保证贷款，以核心资产抵押的可以进入；对已在四家金融机构有贷款授信的，严禁进入；（十）异地注册，异地个人在异地使用的贷款（按揭贷款除外）；（十一）其他不具备本行授信条件的客户。'),
(NULL, '授信评审部', '《授信管理办法》', '第六章  授信额度管理', '第二十二条', '经办行应根据授信客户信用等级评定结果、资产负债率和其他要素，确定客户最高综合授信额度。本行对客户提供的本外币贷款、贴现、敞口银票等资产用信余额之和不超过最高综合授信额度。'),
(NULL, '授信评审部', '《授信管理办法》', '第六章  授信额度管理', '第二十三条', '固定资产贷款授信额度上限等于投资总额减去本行规定的各种项目投资最低资本金。'),
(NULL, '授信评审部', '《授信管理办法》', '第六章  授信额度管理', '第二十四条', '流动资金贷款授信额度按银监会下发的《流动资金贷款需求量的测算参考》，根据现金、存货、应收账款、应付账款、预收账款、预付账款等及周转时间合理估算借款人营运资金量。新增流动资金贷款授信额度=营运资金量－借款人自有资金－现有流动资金贷款－其他渠道提供的营运资金。营运资金量＝上年度销售收入×（1－上年度销售利润率）×（1＋预计销售收入年增长率）/营运资金周转次数，其中：营运资金周转次数＝360/(存货周转天数+应收账款周转天数－应付账款周转天数＋预付账款周转天数－预收账款周转天数)。'),
(NULL, '授信评审部', '《授信管理办法》', '第六章  授信额度管理', '第二十五条', '对小企业融资、订单融资、预付租金或者临时大额债项融资等情况，可在交易真实性的基础上，确保有效控制用途和回款情况下，根据实际交易需求确定流动资金额度。'),
(NULL, '授信评审部', '《授信管理办法》', '第七章  授信流程', '第二十六条', '授信流程为：授信申请、授信受理、授信调查、授信审查审批、授信管理和档案管理。'),
(NULL, '授信评审部', '《授信管理办法》', '第七章  授信流程', '第二十七条', '授信申请。由客户向经办行提出申请，并提供相关资料。（一）自然人客户需提供（包括但不限于）：申请人及配偶的身份证件、婚姻状况证明、收入证明、抵质押证明资料、购销合同等；（二）法人类客户需提供（包括但不限于）：营业执照、开户许可证、财务报表、公司章程、验资报告、特种行业许可证、购销合同、担保资料等。'),
(NULL, '授信评审部', '《授信管理办法》', '第七章  授信流程', '第二十八条', '授信受理。经办行负责受理客户的授信申请并进行初审，对不符合授信条件和信贷政策的，应及时退还资料，并作出不予授信的解释说明；符合条件的，应收集齐全客户的基本资料，建立档案并告知客户业务受理情况。'),
(NULL, '授信评审部', '《授信管理办法》', '第七章  授信流程', '第二十九条', '授信调查。经办行授信调查要实行双人实地调查，收集申请人的经营、财务、信誉、偿债能力、担保情况等信息，对客户进行信用等级评定，撰写授信调查报告，测算授信额度，完善各项资料，集体会办后按权限提交审查审批。'),
(NULL, '授信评审部', '《授信管理办法》', '第七章  授信流程', '第三十条', '授信审查审批。授信评审部对经办行提交的授信业务进行审查，按照本行管理权限分别报有权审批人审批和报备：（一）授信评审部权限内的授信，由授信评审部集体会办后形成审查意见，经有权人审批后出具授信批复。（二）超权限的授信经授信评审部初审后形成贷款授信运作表，提交贷审会审议，贷审会表决确定授信结果后上报至行长备案，行长具有一票否决权，报备完成后出具授信批复。（三）对重大关联交易，经贷审会审议确认，报董事会风险管理与关联交易控制委员会审核后报董事会审议，董事会审议完成后由授信评审部出具授信批复。（四）所有审议通过的授信均由授信评审部按照审批内容批复至经办行，作为用信和贷后检查的依据。'),
(NULL, '授信评审部', '《授信管理办法》', '第七章  授信流程', '第三十一条', '授信管理。授信审批后，经办行应根据客户的实际经营情况和本行信贷政策，动态管理客户的授信，按要求进行贷后检查、风险预警、授信调整申请等相关工作。'),
(NULL, '授信评审部', '《授信管理办法》', '第七章  授信流程', '第三十二条', '档案管理。档案是提供授信、管理授信、收回授信过程的真实记录，包括客户及担保人的资料和授信运作资料。经办行应以客户为单位建立授信档案，完整记录每户授信业务活动的全过程。'),
(NULL, '授信评审部', '《授信管理办法》', '第八章  授信管理', '第三十三条', '授信管理应坚持实事求是反映风险原则，严禁授信调查、复查人员弄虚作假或者与客户恶意串通，骗取授信。'),
(NULL, '授信评审部', '《授信管理办法》', '第八章  授信管理', '第三十四条', '授信管理的尽职调查。发现下列情况，将对授信调查、复查人员进行责任追究。（一）未对客户认真全面核实，授信资料弄虚作假、误导性陈述和重大遗漏；（二）授信后用信检查不到位，未按期对授信单位的担保物和用信状况检查；（三）隐瞒客户风险情况，未及时上报和采取必要措施；（四）其他对授信产生影响，可能或者已经导致风险的行为。'),
(NULL, '授信评审部', '《授信管理办法》', '第九章  附则', '第三十五条', '严格按照差别原则和风险控制原则进行审慎授信。根据客户需求和本行相关管理规定进行授信品种创新，为客户提供满意的授信服务。'),
(NULL, '授信评审部', '《授信管理办法》', '第九章  附则', '第三十六条', '本办法由本行授信评审部负责解释和修订。'),
(NULL, '授信评审部', '《授信管理办法》', '第九章  附则', '第三十七条', '本办法自印发之日起执行。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第一条', '为规范农商银行股东贷款授信管理，并有效控制关联交易风险及维护本行的合法权益，促进本行安全、稳健运行，根据《中华人民共和国商业银行法》、《中华人民共和国担保法》、《贷款通则》、《商业银行与内部人和股东关联交易管理办法》、《公司法》和《物权法》等有关规定，特制定本办法。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第二条', '本办法适用持有本行股份的自然人股东和非自然人股东在本行的授信。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第三条', '本行对股东客户授信应当遵循依法合规、审慎经营、平等自愿、公平诚信的原则。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第四条', '本行自然人股东是指持有或控制本行股份或表决权的自然人。主要自然人股东是指持有或控制本行5%以上股份或表决权的自然人股东，自然人股东的近亲属持有或控制的股份或表决权应当与该自然人股东持有或控制的股份或表决权合并计算。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第五条', '本行非自然人股东主要是指能够直接、间接、共同持有或控制本行股份或表决权的法人或其他组织。主要非自然人股东是指能够直接、间接、共同持有或控制本行5%以上股份或表决权的非自然人股东。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第六条', '股东客户授信是指本行向本行股东直接提供资金支持，或者对其在有关经济活动中可能产生的赔偿、支付责任作出保证，包括但不限于贷款、承兑、贴现、证券回购、贸易融资、保理、信用证、保函、透支等表内外授信（用信）业务（以下简称股东授信）。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第七条', '主要自然人股东和主要非自然人股东为本行的关联方。股东授信涉及本行关联交易的同时须按本行《关联交易管理办法》执行。一般关联交易是指商业银行与一个关联方之间单笔交易金额占商业银行资本净额1%以下，且该笔交易发生后商业银行与该关联方的交易余额占商业银行资本净额5%以下的交易。重大关联交易是指商业银行与一个关联方之间单笔交易金额占商业银行资本净额1%以上，或商业银行与一个关联方发生交易后商业银行与该关联方的交易余额占商业银行资本净额5%以上的交易。 计算关联自然人与商业银行的交易余额时，其近亲属与商业银行的交易应当合并计算；计算关联法人或其他组织与商业银行的交易余额时，与其构成集团客户的法人或其他组织与商业银行的交易应当合并计算。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第八条', '本行不得向关联方发放无担保贷款；不得接受本行的股权作为质押提供贷款；不得为关联方的融资行为提供担保，但关联方以银行存单、国债等提供足额反担保的除外。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第九条', '本行股东或受其控制的关联人向本行申请授信的，担保方式为保证担保的，应当以其拥有的本行股权质押给担保人作为反担保，再由担保人为其在本行授信提供连带责任担保。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十条', '股东授信基本流程包括授信申请、受理与调查、风险评价、授信审批、合同签订、股权反担保质押登记、贷款发放、贷款支付、贷后管理、回收与处置。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十一条', '持有本行股份的股东在本行保证担保的授信额度达100万元（含）以上，必须办理股权反担保手续。本行股权反担保质押登记应按照《xxx省农商银行关于本行股权出质反担保办法》（见附件2）办理。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十二条', '股东授信审批有权人或报备管理部门应按照银监会《商业银行与内部人和股东关联交易管理办法》的要求，对涉及本行股东的关联交易行为加强管理，着重控制授信额度和授信条件，控制关联交易风险。关联交易应当按照商业原则，以不优于对非关联方同类交易的条件进行。对一个关联方的授信余额不得超过本行资本净额的10%，对一个关联法人或其他组织所在集团客户的授信余额总数不得超过本行资本净额的15%，对全部关联方的授信余额不得超过本行资本净额的50%。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十三条', '适当提高对股东授信第二还款来源的要求。对股东授信金额大于其在本行股权投资的，应当更加审慎决策，尽量要求客户采取抵（质）押担保的方式；如采用保证担保方式的，也应适当提高对保证人保证能力的要求；股东授信不允许以股东关联方作为唯一保证人，不允许互保。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十四条', '加强对本行股东对外融资情况的了解，密切关注股东以本行股权在他行融资担保的情况，并要求股东对本行提供股权安全性的保证承诺。特别要对本行股东以本行股权代偿债务情况的了解，并要采取措施积极预防和应对以本行股权代偿后可能会对本行造成的不利影响。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十五条', '加强与其他金融机构信息沟通，特别是对跨区域的集团客户股东，应加强与涉及地区的金融机构的沟通联系，互通有无，全面掌握股东客户的情况。加强对股东客户的贷后管理工作，对出现风险状况的客户，及时进行风险预警，并及时采取措施化解风险。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十六条', '本行关联交易控制委员会负责确认本行的关联方，并向董事会报告，并及时向本行相关工作人员公布其所确认的关联方。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十七条', '对本行股东的授信权限按本行授信授权执行，对股东授信中担保方式为保证方式的，须报总行授信审查部门进行事前报备（事前报备表见附件1）。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十八条', '股东授信申请被否决后，在六个月内不得就同一内容的交易进行审议。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第十九条', '本行内部审计部门应当每年对本行的股东授信情况进行专项审计，并将审计结果报本行董事会和监事会。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第二十条', '本行可能出现下列情形之一的，在本行有借款的股东应立即归还到期借款，未到期的借款应提前偿还：（一）流动性比例＜25%；（二）人民币超额备付率＜2%；（三）不良贷款率≥15%。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第二十一条', '本行的股东通过向本行施加影响，迫使本行从事下列行为的，本行董事会将根据情节严重，对该股东作出贷款限期收回、停止发放贷款、限制投票表决权、停止分红、责令其转让股权等相应的处罚：（一）未按规定进行关联交易，给本行造成损失的；（二）向关联方发放无担保贷款的；（三）违反本办法规定为关联方融资行为提供担保的；（四）接受本行的股权作为质押提供授信的；（五）聘用关联方控制的会计师事务所为其审计的；（六）对关联方授信余额超过本办法规定的比例的；（七）未按规定披露信息的。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第二十二条', '本办法中涉及关联交易的股东授信同时适用本行《关联交易管理办法》。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第二十三条', '本办法如与国家有关法律、法规或本行章程相抵触，按国家有关法律、法规和本行章程的规定执行。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第二十四条', '本办法由授信评审部负责解释和修订。'),
(NULL, '授信评审部', '《股东客户授信管理办法》', '', '第二十五条', '本办法自发布之日起施行。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第一章  评估机构的准入', '第一条', '凡与我行开展业务合作的资产评估机构必须同时具备以下条件：（一）基本条件1.必须是依法注册、具有独立核算能力；2.必须依法具备国家有关主管部门对其颁发的从事相关评估业务资质认定的证明材料；3.相关业务的资质证明必须通过国家有权机构的年度审核；4.依法开展业务，在行业内具有较高声誉，具有较高的评估能力，能遵守行业规范、职业道德，近三年无不良执业记录；5.具有当地有关部门备案批复（房产管理部门、国土管理部门、或其他有权部门）；6.有固定的营业场所，内部管理制度健全；7.在当地设立机构或设有分支机构，并从事评估业务至少1年以上；8.我行认定的其他应具备的资格条件。（二）专业条件各类评估机构除符合上述基本条件外，还应根据自身经营范围，符合以下专业条件：1.从事土地评估的评估机构：（1）资质等级三级以上（含三级）；（2）注册资金达到40 万元及以上（合伙制企业20 万元）；（3）有4 名（合伙制企业3 名）以上符合条件的专职注册土地估价师。2.从事房地产估价的评估机构：（1）资质等级三级以上（含三级）；（2）注册资金达到50万元及以上（合伙制企业30万元）；（3）有3名（合伙制企业2名）以上符合条件的专职注册房地产估价师。3.从事资产估价的评估机构：（1）财政部或省级财政部门批准设立；（2）注册资金达到30万元及以上（合伙制企业10万元）；（3）有8名（合伙制企业5名）以上符合条件的专职注册资产估价师。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第一章  评估机构的准入', '第二条', '所有评估机构由总行授信评审部根据评估机构申请资料进行审核。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第一章  评估机构的准入', '第三条', '经信贷审批委员会审批同意后，我行方可与准入评估公司签订合作协议，明确双方责任和权利，合作有效期1年。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第一章  评估机构的准入', '第四条', '合作协议采用总行统一格式。如需修改协议内容的，由授信评审部统一提交合规管理部审核。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第二章  评估机构的重检', '第五条', '我行对准入的评估机构实行一年一次准入条件重检，重检时间原则上为每年3月份，重检内容主要包括：（一）营业执照是否有效；（二）组织机构代码证是否有效；（三）房地产估价机构资质证书是否有效；（四）土地估价机构资质证书是否效；（五）房地产估价师资格证书（有权部门颁发）；（六）土地估价师资格证书（有权部门颁发）；（七）有关部门备案批复（房产管理部门、国土管理部门或其他有权部门）；（八）相关评估师人员配置是否达到本行规定。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第二章  评估机构的重检', '第六条', '本地评估机构的重检由总行授信评审部负责实施。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第二章  评估机构的重检', '第七条', '准入重检通过后，授信评审部需及时公布准入评估机构名单和联系方式。对在上述准入重检中未达标的评估机构,我行将立即暂停与其的业务合作，并给予一个月宽限期，在宽限期内达标的可恢复合作，宽限期结束后仍未达标的，停止与其合作。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第二章  评估机构的重检', '第八条', '对重检发现有资料更新的，授信评审部应及时收集更新资料，并进行归档。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第九条', '客户经理应按总行准入的评估机构名单向信贷客户推荐。具体评估项目由本行信贷客户委托本行推荐的评估机构进行评估。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第十条', '总行授信评审部应每半年对准入评估机构进行一次全面评价，日常管理中发现的问题也应一并记入半年度评价。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第十一条', '全面评价采用抽检方式，抽检比例不低于该评估机构在我行业务量的30%，抽检应兼顾不同地理区域、房产（土地）类型。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第十二条', '抽检人员可按地理区域、房产（土地）类型对各评估机构出具的评估报告进行对比分析，同时应通过咨询房地产公司、房地产中介机构、网络等渠道获取相关信息用以分析，对抽检的评估物价值逐笔进行内部评审，必要时可聘请外部专家参与。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第十三条', '全面评价采用打分方式进行，各评估机构每半年基本分为10分，全年基本分为20分。评估机构出现下列事项的，每发现一笔扣2分，直至基本分扣完：（一）评估偏离度超10%未达到20%；（二）未按照我行要求对评估标的物认真进行现场勘验；（三）未对评估标的物一物一照；（四）照片未能全面反映评估标的物周边情况；（五）评估报告不完整、缺页；（六）评估报告遗漏评估师注册章；（七）评估报告出现文字错误。偏离度的定义：我行评估中发现内部评审的评估价值与评估机构的评估价值之间的差异为偏离度。偏离度按每份评估报告计算。计算公式：（评估机构评估值-我行评估值）/我行评估值。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第十四条', '如评估机构半年积分10 分被全部扣完，我行将暂停与其合作6个月，并要求该评估机构进行整改，提交整改报告。暂停期限届满后，我行将视其整改情况，决定是否继续合作。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第十五条', '若评估机构全年20分积分被全部扣完，合作期限未满的，则我行停止与其合作，双方签订的合作协议即行终止。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第三章  评估机构业务的管理', '第十六条', '若评估机构有下列情形之一的，我行有权向评估机构发出书面通知，单方面宣告解除双方合作，并在3年内不予重新准入：（一）出具虚假记载，误导性陈述或重大遗漏等不合格的评估报告；（二）发现有弄虚作假、恶意串通等不诚实行为；（三）发现通过不正当手段来招揽业务；（四）违反国家法律法规和行业约定；（五）向任何第三方披露甲方、借款客户提供的相关资料以及所出具的评估报告，或泄露我行商业秘密；（六）执业水平、执业质量不高，其出具的中介执业报告经专家审查有重大质量问题或与实际情况严重不符；（七）有严重技术差错、违反行业规则的不良行为、被行业协会处罚的不良记录、主要负责人有违规违法行为；（八）抵（质）押物评估价值与实际严重不符，影响我行授信评判或导致甲方贷款出现较大损失；（九）我行认为其他不符合要求的情况，包括但不限于我行内部评审中发现的评估机构单笔评估偏离度达到或超过20%。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第四章  附则', '第十七条', '凡为我行授信业务提供物权担保的，原则上应由我行准入的具有相应资格的评估机构提供抵押物评估的公允价值，主要包括用以抵押的商品房、厂房、土地使用权、在建工程等不动产抵押；用以抵押或质押的企业自有的机器设备或存货等其他动产；个人客户提供的房产、车辆等担保物，以及其他按规定需进行评估的资产。各业务部门不得接受非我行准入的评估公司或超合作范围的评估公司提供的评估报告。特殊情况应报总行审批。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第四章  附则', '第十八条', '在评估机构准入、合作、评价等过程中，一旦发现我行相关人员存在内外勾结、徇私舞弊等违法违规行为，将按我行有关规定严肃责任追究，触犯法律的，移交司法机关处理。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第四章  附则', '第十九条', '本办法由我行授信评审部负责解释和修订。'),
(NULL, '授信评审部', '《评估机构合作管理办法》', '第四章  附则', '第二十条', '本办法自发文之日起执行。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第一章  总则', '第一条', '为进一步加强本行集团客户的授信风险管理，根据《商业银行集团客户授信业务风险管理指引》等法律法规及本行相关信贷管理制度，制订本办法。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第一章  总则', '第二条', '本办法所称集团客户（包括关联客户）是指具有以下特征的企事业法人客户：（一）在股权上或者经营决策上直接或间接控制其他企事业法人或被其他企事业法人控制的。（二）共同被第三方企事业法人所控制的。（三）主要投资者个人、关键管理人员或与其近亲属（包括三代以内直系亲属关系和二代以内旁系亲属关系）共同直接控制或间接控制的。（四）存在其他关联关系，可能不按公允价格原则转移资产和利润，本行认为应当视同集团客户进行授信管理的。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第一章  总则', '第三条', '本办法所称授信业务包括：贷款、拆借、票据承兑和贴现等。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第一章  总则', '第四条', '本办法所称集团客户授信业务风险是指由于本行对集团客户多头授信、过度授信和不适当分配授信额度，或集团客户经营不善以及集团客户通过关联交易、资产重组等手段在内部关联方之间不按公允价格原则转移资产或利润等情况，导致不能按期收回由于授信产生的贷款本金及利息，或给本行带来其他损失的可能性。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第一章  总则', '第五条', '对集团客户授信应遵循以下原则：（一）统一原则。对集团客户授信实行本行统一管理，集中对集团客户及关联企业授信，进行风险控制。（二）适度原则。根据授信客体风险大小和本行风险承担能力，合理确定对集团客户的总体最高授信额度，防止风险过度集中。同时根据集团客户内的各个授信对象，充分考虑其自身的信用、经营和财务状况，区别授信。最高授信额度根据集团客户的经营和财务状况适时做出调整。（三）预警原则。建立风险预警制度，及时防范和化解集团客户授信风险。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第一章  总则', '第六条', '集团客户授信应与本行风险承受能力相匹配，对单一集团客户授信总额不得超过本行资本净额的15%。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第二章  组织管理体系', '第七条', '公司业务部负责收集集团客户的授信材料、尽职调查、提出授信初步意见，根据授信审批落实授信条件以及集团客户经营管理信息的跟踪收集、风险预警通报和贷后管理工作。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第二章  组织管理体系', '第八条', '授信评审部负责拟定集团客户授信管理制度及操作流程，组织年度集中授信，对支行提交的集团客户授信材料进行审查、实施权限内审批，负责集团客户授信的统计、分析和上报工作。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第二章  组织管理体系', '第九条', '风险管理部负责对大额集团客户授信业务进行风险评估、识别和控制。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第二章  组织管理体系', '第十条', '合规管理部负责审查本行集团客户授信管理办法的合规性和有效性。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第二章  组织管理体系', '第十一条', '审计稽核部负责对集团客户授信业务进行审计稽核和监督检查。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第三章  授信条件', '第十二条', '集团客户的授信对象必须具备《授信管理办法》规定的条件。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第四章  授信流程', '第十三条', '集团客户的授信流程按《授信管理办法》规定的授信流程执行。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第十四条', '集团客户原则上只能在公司业务部取得授信。集团客户内成员因特殊原因需在不同支行取得授信的，由本行整体控制，协调分配。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第十五条', '对集团客户授信时，应当要求集团客户提供真实、完整的信息资料，包括但不限于集团客户各成员的名称、相互之间的关联关系、组织机构代码、法定代表人及证件、实际控制人及证件、注册地、注册资本、主营业务、股权结构、高级管理人员情况、财务状况、重大资产项目、担保情况和重大诉讼情况以及在其他金融机构授信情况等。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第十六条', '对集团客户内各个授信对象核定最高授信额度时，在充分考虑各个授信对象自身的信用状况、经营状况和财务状况的同时，还应当充分考虑集团客户的整体信用状况、经营状况和财务状况。最高授信额度应当根据集团客户的经营和财务状况变化及时做出调整。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第十七条', '集团客户的授信需求超过本行风险的承受能力时，应当采取组织银团贷款、联合贷款等措施分散风险。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第十八条', '对集团客户授信时，担保措施应尽可能采用抵（质）押贷款方式；采取保证贷款方式时，应严格控制集团客户内部直接控股或者间接控股的关联方之间的相互担保，以防贷款风险。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第十九条', '对集团客户授信后，应做好集团客户授信后信息收集与整理工作，集团客户贷款的变化、经营状况的变化、关键管理人员的变动以及集团客户的违规经营、被起诉、欠息、逃废债、提供虚假资料等重大事项必须及时登记录到本行信贷信息管理系统。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第二十条', '对集团客户后用信时，应当在贷款合同中约定，贷款对象有下列情形之一的，本行有权单方决定停止支付借款人尚未使用的贷款，并提前收回部分或全部贷款本息，依法采取其他措施：（一）提供虚假材料或隐瞒重要经营财务事实的。（二）未经本行同意擅自改变贷款原定用途，挪用贷款或用贷款从事非法、违规交易的。（三）利用与关联方之间的虚假合同，以无真实贸易背景的应收票据、应收账款等债权到本行贴现或质押，套取本行资金或授信的。（四）拒绝接受本行对其信贷资金使用情况和有关经营财务活动进行监督和检查的。（五）出现重大兼并、收购重组等情况，本行认为可能影响到贷款安全的。（六）通过关联交易，有意逃废本行债权的。（七）本行认定的其他重大违约行为。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第五章  授信风险管理', '第二十一条', '支行要加强对集团客户授信后风险管理，客户经理要定期或不定期开展针对整个集团客户的尽职检查，掌握其整体经营和财务变化的情况。每季至少一次对集团客户进行一次全面的核查并上报信贷管理部和风险管理部。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第六章  附则', '第二十二条', '本办法未尽事宜执行《授信管理办法》的相关规定。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第六章  附则', '第二十三条', '本办法由本行授信评审部负责解释和修订。'),
(NULL, '授信评审部', '《集团客户授信管理办法》', '第六章  附则', '第二十四条', '本办法自印发之日起施行。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第一章  总则', '第一条', '为加强农村商业银行便民宝业务管理，规范便民宝业务的操作流程，促进便民宝业务健康发展，根据《中国人民银行关于推广银行卡助农取款服务的通知》、《江苏省农村信用合作联社收单业务管理办法》、《圆鼎借记卡章程》，特制定本办法。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第一章  总则', '第二条', '本办法适用于开展便民宝业务的各营业网点。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第一章  总则', '第三条', '本办法所指便民宝是指本行布放的，为本行圆鼎借记卡客户提供查询、取款、转账、消费、缴费等服务的转账电话设备。安装了本行便民宝设备的客户，为本行便民宝商户（以下简称为特约商户）。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第一章  总则', '第四条', '各营业网点在选择特约商户时，根据当地实际情况，遵循“方便农户、位置便利、兼顾安全”的原则，择优录用。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第五条', '电子银行部负责便民宝业务的制度建设、规划布局、业务推广、统计分析、考核评价等工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第六条', '计划财务部负责机具押金的收取核算工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第七条', '办公室负责便民宝设备采购（招标）。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第八条', '合规管理部负责便民宝各项制度及业务的合规审查。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第九条', '风险管理部负责便民宝各项业务的风险评估、监测、防范及化解工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第十条', '信贷管理部负责配合电子银行部做好便民宝信贷业务功能创新工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第十一条', '审计部负责便民宝业务的核算审计工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第二章  机构、人员和职责', '第十二条', '各营业网点负责便民宝业务以及特约商户的日常管理工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第三章  申请受理', '第十三条', '商户应以书面形式向本行当地营业网点提出申请，且应同时具备以下条件：（一）具有完全民事行为能力且身体健康的本地居民（或者在当地有固定房产的外地人）；（二）具备安装和使用便民宝的环境条件；（三）诚实守信、服务热情，在当地有较好的口碑；（四）经营地点位于人口集中的中心区域或交通要道口；（五）经营场所固定，收入稳定。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第三章  申请受理', '第十四条', '商户应提供下列资料：（一）营业执照；（二）经营者的个人身份证件；（三）营业场所的证明材料（房屋产权证、土地证或租赁合同）；（四）商户本人持有的本行圆鼎借记卡；（五）如商户为当地农保所或村委会只需提供负责人身份证件和本行圆鼎借记卡。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第四章  审查审批', '第十五条', '营业网点受理商户申请后，客户经理应及时对申请人进行尽职调查，对申请人业务申请表填列的内容、提供的申请资料与原件进行核对，初审其合法性、真实性、完整性，并提交《便民宝商户评分表》进行商户风险等级评估。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第四章  审查审批', '第十六条', '客户经理应到商户经营场所进行实地调查、走访，了解情况内容，包括但不限于以下方面：（一）商户的商誉；（二）商户的经营地点;（三）经营范围与品种；（四）村民对该商户的基本评价；（五）“阳光信贷”的相关情况。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第四章  审查审批', '第十七条', '商户审查岗负责对客户经理上报的商户申请资料、身份资料的完整性、真实性进行全面审查，重点关注商户的信誉度、有无简单的金融基础知识，是否能够掌握便民宝的日常操作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第四章  审查审批', '第十八条', '商户审批岗应依据审查结果，在充分评价商户安装便民宝必要性、合理性的前提下审慎审批。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第四章  审查审批', '第十九条', '商户录入岗根据审批结果，对获得批准的商户进行商户信息的录入和维护，对未获得批准的申请应及时告知商户。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第五章  签约安装', '第二十条', '营业网点负责与获准申请商户签订书面协议，协议双方当事人全称、绑定圆鼎借记卡账户结算信息等要素必须填列齐全。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第五章  签约安装', '第二十一条', '营业网点负责派专人前往商户经营场所安装便民宝，并进行调试，调试成功后交由商户使用，同时完善交接手续。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第五章  签约安装', '第二十二条', '便民宝使用前，营业网点必须组织商户进行业务培训，培训合格且签字确认后，方可开展业务。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第六章  业务变更', '第二十三条', '对于商户名称、经营所在地、联系电话、主营项目、经营范围、法定代表人（负责人）以及绑定的结算账户等信息发生变更的，商户应及时通知客户经理，并提供相关证明材料，填列《便民宝业务申请（修改、撤销）表》。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第六章  业务变更', '第二十四条', '电子银行部管理人员审核相关资料真实性，并进行商户信息变更。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第七章  机具的管理', '第二十五条', '便民宝机具由电子银行部集中管理，商户需要新装或更换机具时，由客户经理向电子银行部提出申请，审批同意后，无特殊情况应当在三个工作日内完成机具安装、测试工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第七章  机具的管理', '第二十六条', '密钥管理1．便民宝机具传输密钥由电子银行部专人生成、灌入；2．便民宝机具传输密钥执行一机一密制度；3．严禁将同一便民宝信息配置在多台便民宝机具上。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第七章  机具的管理', '第二十七条', '电子银行部机具管理人员要负责对便民宝出入库情况进行记录；建立机具管理台账，记录机具的型号、编号、装机位置和状态，确保账实相符。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第七章  机具的管理', '第二十八条', '对发生故障的便民宝机具，电子银行部负责与供货商取得联系，并尽快解决问题。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第七章  机具的管理', '第二十九条', '机具的回收1．对强制退出的商户，管户客户经理在收到撤机申请批核后一个工作日内将机具收回；2．因其他原因而被退出的，在撤机申请批核后三个工作日内收回机具；3．撤机时，检验机具的完好性，并做好相应登记。撤回的机具在两个工作日内入库；4．因商户倒闭、失踪等原因而无法收回的机具的，由客户经理负责进行追查，追查超过半年仍无法收回的，向本行电子银行部申请报损处理；5．发现机具无法使用报废现象的，由客户经理向本行电子银行部上报机具报废申请书，详细注明报废原因，机具型号和台数,并将机具收缴至电子银行部。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十条', '营业网点对商户所填写的申请表和提供的资料的真实、完整、有效性负责。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十一条', '便民宝为圆鼎借记卡持卡人、本行活期存折持有人提供服务，持卡（折）人需凭本人借记卡（折）、卡（折）密码、本人身份证件到特约商户处办理相关业务。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十二条', '圆鼎借记卡持卡人、本行活期存折持有人办理业务时，每卡（折）每日提现金额不得超过2000元，每月提现累计不超过12次；消费每卡（折）每日不超过2次；转账每卡（折）每日转出不超过2笔；新农保预缴款每季转入卡号必须为新农保签约卡，每季转入卡号转入笔数不超过3笔；电费预缴款转入卡号必须为电费签约卡，每月转入卡号转入笔数不超过5笔。业务办理金额及次数的限制本行可根据相关规定予以调整。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十三条', '严禁特约商户在提供便民金融服务过程中向客户收取任何费用，但本行规定的除外。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十四条', '特约商户负责保管每笔由客户签字确认的交易凭条，及时将交易凭条粘贴于业务登记簿，并将每笔业务及时登记到《便民宝业务登记簿》，交易凭条应要求客户签名或按指纹确认，每天交易结束后，特约商户必须及时轧账并登记。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十五条', '特约商户必须按规定参加相关年检或年度报告，确保经营资质持续有效。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十六条', '本行拥有便民宝设备的所有权，特约商户在协议期内享有设备的使用权，不得擅自买卖、转让、拆卸和毁损电话设备以及加密部件。特约服务终止后，本行有权收回便民宝设备。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第八章  风险管理', '第三十七条', '各营业网点负责监控便民宝的日常业务，一旦发现有异动交易，各营业网点管户客户经理要及时查明原因，并及时做出相应的处理。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第九章  商户管理', '第三十八条', '便民宝安装后，实施管户客户经理分片包干负责制，营业网点管户客户经理应定期不定期对特约商户使用便民宝情况进行检查，实地检查每月不少于两次，并对检查发现的问题及时进行处理。重点检查以下几个方面的内容：（一）特约商户是否掌握银行卡基本知识，是否能熟练操作、正确使用便民宝，是否具备一定的日常交易风险防控能力；（二）便民宝机具、验钞机是否放置在指定位置，机具上的易碎标签是否完整，有无改动或者加装其他设备现象的；（三）特约商户经营的产品、业务活动是否有异常变化；（四）根据商户交易情况逐户进行分析，判断特约商户经营是否正常。管户客户经理到特约商户处检查（服务、辅导）流程为：首先在便民宝上办理一笔业务，并在便民宝业务登记簿上登记并签字，记录业务办理的时间，以此作为开始检查的标志；之后进行相应的检查、服务与辅导工作；检查结束后再办理一笔业务并登记，两次时间间隔不得低于1小时（以两次业务单据打印时间为准）。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第九章  商户管理', '第三十九条', '营业网点负责采取有效方式，向辖内客户了解特约商户的商业道德、服务态度等情况，并且每季召开一次特约商户例会，对特约商户的服务质量进行评价。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第九章  商户管理', '第四十条', '营业网点负责与特约商户签订服务协议，规范特约商户服务行为。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第九章  商户管理', '第四十一条', '商户有（但不仅限于）下列情况之一的，本行将终止与商户的协议：（一）商户违反本协议，将便民宝藏匿、损毁影响使用的；（二）恶意拆分业务逃避交易限制的，除终止协议外，本行将向商户追索虚假业务产生的通讯费；（三）因年龄较大等原因丧失便民宝业务操作能力的；（四）信誉差、借便民宝业务强制销售商品、服务态度差、消费者反映强烈的；（五）营业不正常，业务量偏低，难以履行为民服务职责的；（六）申请时隐瞒劣迹事实，在当地有负面影响，消费者普遍对其反感的；（七）停业转行或营业场所搬迁至偏僻区域，不便持卡人办理本行业务的；（八）商户与第三方发生类似本协议的服务行为的；（九）商户参与吸储、放贷、集资以及民间借贷等活动的；（十）其他有损坏本行声誉、损害消费者利益的。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十章  档案管理', '第四十二条', '商户与本行签订的《便民宝服务协议》，提供的营业执照、税务登记证、法定代表人（负责人）身份证件或其他有效证件资料复印件以及《便民宝业务申请（修改、撤销）表》、《便民宝商户评分表》等档案资料交电子银行部永久保存。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十章  档案管理', '第四十三条', '对便民宝机具出入库登记、安装维护记录和有违规记录的商户资料需分别登记造册，妥善保管。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十章  档案管理', '第四十四条', '营业网点客户经理负责每月上收特约商户登记的《便民宝业务登记簿》，与商户做好登记簿的交接工作。同时与主管会计做好登记簿的交接，主管会计参照《会计档案管理办法》做好档案管理工作，交接时填列《便民宝登记簿交接表》。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十章  档案管理', '第四十五条', '管户客户经理须每季度末25日前将商户的回访和培训记录装订成册，上缴至本行电子银行部归档保存。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十章  档案管理', '第四十六条', '特约商户应妥善保管交易签购单凭证，在保存期间应配合本行对产生的交易异议进行调单等工作。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十章  档案管理', '第四十七条', '便民宝商户管理工作作为电子银行先进单位评选重要依据，排名后十名单位不得作为先进单位。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十一章  罚则', '第四十八条', '依据《员工违规违章行为积分管理办法》对包含但不限于下列情形的相关责任人给予处罚：（一）营业网点负责人不重视便民宝布设工作，推进速度慢、覆盖面不全，影响便民服务，当地农户反感强烈，严重影响本行声誉的；（二）客户经理受理商户申请后未到实地调查了解商户的基本情况，与不符合条件的商户签订协议、布设便民宝，严重影响为民服务的；（三）在选择商户时存在利益输送，未按照“公平、公开、公正”的原则择优选取，放弃优质商户，选择劣质商户，交易额度未达到全行平均水平或造成恶劣影响的；（四）对特约商户培训不及时或不到位，特约商户未真正掌握便民宝操作技能，导致发生业务差错，产生纠纷，致使本行遭受经济损失的；（五）未按要求对特约商户进行回访，便民宝使用过程中出现问题未及时得到妥善处理，严重影响为民服务的；（六）按照协议及本办法规定，应与商户终止协议而未及时终止，导致损害客户利益事件发生，严重影响本行与客户的关系，影响本行声誉的；（七）档案管理不善，导致协议文本等法律资料损毁、遗失，造成纠纷处理中无证据提供，给本行造成损失的；（八）其他给本行造成经济损失、声誉损害应予处罚的。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十二章  附则', '第四十九条', '本办法由负责解释和修订。'),
(NULL, '电子银行部', '《便民宝业务管理办法》', '第十二章  附则', '第五十条', '本办法自印发之日起执行。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第一章  总则', '第一条', '为建立和完善管理体系，规范便民宝商户行为，提升金融服务水平，根据《中国人民银行关于推广银行卡助农取款服务的通知》、《圆鼎借记卡管理办法》、《便民宝业务管理办法》等文件规定，特制定本办法。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第一章  总则', '第二条', '本办法适用便民宝所有商户。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第一章  总则', '第三条', '本办法所指便民宝是本行布放的，为辖内持有圆鼎借记卡和活期存折的农户提供查询、转账、消费、缴款、小额取款等服务的电话终端。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第二章  资金管理', '第四条', '便民宝业务办理过程中所需的资金，均为商户自有资金，商户须有足够的实力、资金从事便民宝相关业务。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第二章  资金管理', '第五条', '商户圆鼎借记卡上的资金及常备现金量须满足本地客户日常取款、新农保缴费等业务办理的资金需求。商户现金不足时，须及时组织现金，现金过多时须将多余的现金及时解缴到开户网点绑定卡上。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第三章  风险防范', '第六条', '商户在办理业务完毕后，必须保管好由客户签字确认的交易凭条，及时将交易凭条粘贴于业务登记簿，并将每笔业务及时登记到《便民宝业务登记簿》，由客户签名或按指纹确认，每月由营业网点分工客户经理核对确认。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第三章  风险防范', '第七条', '商户必须每天进行轧账，轧账方法为：用上次轧账余额加上绑定卡转入金额，减去绑定卡转出金额，得到本期余额，然后在便民宝上查询出绑定卡余额，两个余额核对一致，则轧账成功；若不一致，则应仔细核对登记簿，查找原因。若仍然无法核对正确，则应联系开户网点，请开户网点提供便民宝发生明细及绑定卡业务发生明细协助查找。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第三章  风险防范', '第八条', '商户要向持卡人明示安全用卡须知、假币识别、举报投诉电话等事项。要加强宣传，引导客户全面了解并正确使用便民宝提供的服务，培养持卡人正确的用卡习惯。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第三章  风险防范', '第九条', '商户要保证便民宝业务办理场所的安全，安装防盗门、窗，配备保险箱（柜），必要时须安装监控设备。商户到本行解缴、提取现金时，要有必要的安全防范措施，在此过程中出现的安保问题由商户自行负责。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第三章  风险防范', '第十条', '商户对客户的交易记录等资料负有保密义务，不得向第三方透露，法律另有规定的除外。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第三章  风险防范', '第十一条', '同一持卡人在商户处办理取现业务时，每卡每日提现不得超过2000元，每月提现累计不超过12次；消费每卡每日不超过2次；转账每卡每日转出不超过2笔；新农保预缴款每季转入卡号必须为新农保签约卡，每季转入卡号转入笔数不超过3笔；电费预缴款转入卡号必须为电费签约卡，每月转入卡号转入笔数不超过5笔。业务办理金额及次数的限制本行可根据相关规定予以调整。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第四章  差错处理', '第十二条', '出现系统通讯故障，交易未完成（未成功）的，不要重复交易。办理取款业务，商户要通过查询持卡人卡余额、明细和自己绑定卡余额、明细，确认一致后才能付款。办理缴费业务，商户要通过查询持卡人卡余额、明细和自己绑定卡余额、明细，确认一致后，再决定是否收现。信息核对不一致，需要稍作等待再重新查询，必要时联系开户网点协助查找。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第四章  差错处理', '第十三条', '因商户操作失误引起的差错，商户应迅速联系差错持卡人，及时协商解决问题的办法。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第五章  纠纷处理', '第十四条', '持卡人对现钞有争议的，商户应当场处理。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第五章  纠纷处理', '第十五条', '持卡人对交易有争议的，商户应立即轧账，确认业务无差错后才能继续办理下一笔业务；若出现账务差错且无法查找的情况，可联系开户网点协助查找。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第六章   其他', '第十六条', '商户应将便民宝等设备放置在指定位置，机器上的易碎标签应保持完整，且不得改动或者加装其他设备。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第六章   其他', '第十七条', '商户应保持便民宝设备及使用环境的整洁卫生。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第六章   其他', '第十八条', '本行与商户之间为合作关系，商户在为持卡人办理业务时产生的通讯费用，本行将根据实际情况给予补贴。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第六章   其他', '第十九条', '本行与便民宝商户签订《便民宝服务协议》，该合作协议为一年一签，在每年协议到期前应与本行续签下一年的合作协议。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第六章   其他', '第二十条', '商户有（但不仅限于）下列情况之一的，本行将终止与商户的协议：（一）商户违反本协议，将便民宝藏匿、损毁影响使用的；（二）恶意拆分业务逃避交易限制的，除终止协议外，本行将向商户追索虚假业务产生的通讯费；（三）因年龄较大等原因丧失便民宝业务操作能力的；（四）信誉差、借便民宝业务强制销售商品、服务态度差、消费者反映强烈的；（五）营业不正常，业务量偏低，难以履行为民服务职责的；（六）申请时隐瞒劣迹事实，在当地有负面影响，消费者普遍对其反感的；（七）停业转行或营业场所搬迁至偏僻区域，不便持卡人办理本行业务的；（八）商户与第三方发生类似本协议的服务行为的；（九）商户参与吸储、放贷、集资以及民间借贷等活动的；（十）其他有损坏本行声誉、损害消费者利益的。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第六章   其他', '第二十一条', '对存在欺诈、套现、卡片侧录、利用受理终端不正当牟利、故意使用假币等行为的商户，本行应立即终止协议、收回受理终端和标识牌，构成犯罪的，将移交司法机关。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第七章  附则', '第二十二条', '本办法由负责解释和修订。'),
(NULL, '电子银行部', '《便民宝商户管理办法》', '第七章  附则', '第二十三条', '本办法自文发之日起执行。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第一章  总则', '第一条', '为加强农村商业银行（以下简称“本行”）圆鼎借记卡业务管理，促进借记卡业务健康发展，根据《中国人民银行关于规范单位结算卡业务管理的通知》、《中国人民银行关于加强银行卡业务管理的通知》、《中国银监会关于进一步加强银行卡服务和管理有关问题的通知》以及省联社等有关规定和《江苏圆鼎借记卡章程》，制定本办法。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第一章  总则', '第二条', '本办法所称圆鼎借记卡是由江苏省各市（县、区）农村商业银行、农村信用合作联社委托江苏省农村信用社联合社统一发行的以“圆鼎”为统一品牌标识的借记卡。辖内受理圆鼎借记卡业务的营业网点均须遵守本办法。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第一章  总则', '第三条', '圆鼎借记卡是人民币银联标准卡，按发行对象不同分为单位（结算）卡、个人卡；按信息载体不同分为磁条卡、纯芯片卡、磁条芯片复合卡（以下纯芯片卡和磁条芯片复合卡通称为“芯片卡”）。芯片卡具备电子现金支付和信息存储功能；电子现金支付功能包括小额脱机消费、圈存、圈提、查询等;信息存储功能包括记录持卡人的基础信息和相关行业应用信息等。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第一章  总则', '第四条', '圆鼎借记卡是集存取现金、转账结算、购物消费和查询等全部或部分功能的综合性电子支付工具，使用时联线作业（脱机交易除外）、实时记账、先存后取，不具有透支功能。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第一章  总则', '第五条', '圆鼎借记卡个人卡一卡多户，可同时开设活期账户、定期账户、专有账户等。圆鼎借记卡单位卡仅可开设活期主账户。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第一章  总则', '第六条', '圆鼎借记卡个人代理开户的，需要代理人提供代理人和被代理人双方身份证件，如被代理人在系统内留有有效联系方式的，应通过留存的联系方式与被代理人进行核实。单位代理开户的，应提交申请资料，签订代理批量开卡协议，并对被代理人身份的真实性负责。存款、取款、挂失申请等业务由他人代理的，代理人须提供代理人和被代理人双方身份证件，经办人员在核实代理人和被代理人身份无误后，方可按人民银行相关规定办理。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第七条', '凡具有完全民事行为能力的个人和经营效益、信誉等良好的单位，均可向本行辖内营业网点申领圆鼎借记卡及附属卡，境内16周岁以上中国居民可凭身份证或临时身份证开立个人银行账户，16岁周岁以下或16周岁以上无民事行为能力的由监护人代理，其债权债务关系由监护人承担。个人在同一发卡机构最多申领10张圆鼎借记卡。受理圆鼎借记卡申请的网点即为圆鼎借记卡发卡网点，对申领人的资信资料负有审核、保密责任。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第八条', '个人卡账户管理（一）个人卡凭本人有效身份证件申领，有效身份证件包括：居民身份证、军官证、警官证、户口簿、护照、港澳台同胞回乡证等符合人民银行储蓄实名制规定的身份证件。（二）个人卡账户内资金只限于以其持有的现金存入或以其工资性款项及其他合法收入转账存入。（三）个人凭本人有效身份证件和存折（个人活期一本通、活期储蓄存折、个人活期涉农补贴存折）可至本行辖内任意网点进行配卡。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第九条', '单位卡账户管理（一）单位卡可凭中国人民银行核发的基本账户开户许可证、单位组织机构代码证、营业执照和单位法定代表人或授权人的书面授权指定，由被指定人出示有效身份证件申领，申领人应如实填写申请表，经发卡机构确认无误后，对符合条件的准予领卡。单位卡持卡人由申领单位法定代表人或其委托代理人书面授权指定，持卡人用卡产生的债权债务关系由申领单位负责。（二）单位卡账户内资金一律从其基本存款账户转账存入，不得存取现金，不得将销货收入存入单位卡。单位卡可办理商品交易和劳务费等款项的支付结算，单笔交易超过规定起点的，按有关规定办理。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十条', '单位结算卡账户管理（一）单位结算卡由申领单位凭单位组织机构代码证、营业执照、单位结算账户开户凭证和单位法定代表人或授权人的书面授权指定，由被指定人出示有效身份证件申领，每个申领单位最多可申请5张单位结算卡，单位结算卡持卡人用卡产生的债权债务关系由申领单位负责。（二）单位结算卡开卡关联单位结算账户，单位结算卡用卡产生的账务信息记入关联的单位结算账户。单位结算卡管理遵循《人民币银行结算账户管理办法》，单位结算卡是否可存取现金取决于其关联的单位结算账户性质，关联基本户的单位结算卡可以存取现金。（三）单位结算卡支持通存通兑，可在江苏省内各农村商业银行（农村信用合作联社）柜面办理存取款或转账等业务。（四）单位结算卡可通过本行或贴有银联标识的ATM、POS等渠道办理业务。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十一条', '圆鼎借记卡个人卡卡内专户资金来源主要为贷款发放、个人现金存入、转账存入。贷款专户具有一次授信、循环贷款、专款专用的特点。专户资金只能用于定向转出和指定专户消费，不可取现。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十二条', '对批量发卡（含批量存折领卡）的客户，须由客户本人持有效身份证件至本行任意网点或通过发卡机构认可的其他方式进行激活并设置交易密码，激活前卡片为止付状态，激活交易原则上不允许代办。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十三条', '圆鼎借记卡凭密码使用（芯片卡电子现金业务除外），持卡人账户实时记账，签名仅作为持卡人认可交易的辅助措施，不作为风险责任划分依据。芯片卡电子现金业务是指发卡机构根据持卡人要求将现金或账户存款转至芯片卡内储存，交易时不校验密码直接从芯片卡内扣款的业务，芯片卡电子现金账户视同现金管理，不计息。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十四条', '圆鼎借记卡持卡人可以在办行公告的网点柜面办理现金存取（单位卡和部分单位结算卡除外）、转账等交易。大额交易须根据《反洗钱法》、《人民币大额和可疑支付交易报告管理办法》、中国人民银行及发卡机构有关规定和要求办理。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十五条', '圆鼎借记卡电子银行交易渠道包括自动柜员机（含ATM、CDS等现金类自助设备，下同）、POS终端、转账电话、自助查询终端、网上银行、网上支付、手机银行、电话银行（96008）、短信银行等，本行须按各电子银行交易渠道相关规定和操作流程为申请人办理业务开通手续。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十六条', '持卡人可在本行辖内网点、特约单位办理代收、代付业务，操作可参照转账交易处理，也可根据协议委托本行办理代收、代付业务。本行通过圆鼎借记卡办理的各项代理业务，不得为协议单位和持卡人垫付资金。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第二章  账户及交易管理', '第十七条', '本行不向持卡人寄送对账单。持卡人可以通过发卡网点柜面、自动柜员机、网上银行、手机银行、电话银行（96008）、微信银行等渠道查询账户余额、交易明细等信息。如果持卡人对账户余额或交易明细等提出异议，本行根据交易记录向持卡人做出回复。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第三章  挂失、换卡、止付、冻结管理', '第十八条', '挂失管理（一）口头挂失圆鼎借记卡被盗或遗失时，持卡人可通过网点柜面、电话银行（96008）、网上银行、手机银行等渠道办理口头挂失。经办人员在核实持卡人身份后，应及时为其办理挂失手续，挂失手续办妥后，卡片立即止付。挂失前造成的损失由持卡人负责。口头挂失有效期为5日，过期自动失效，芯片卡电子现金账户不可挂失。（二）书面挂失圆鼎借记卡被盗或遗失时，持卡人可直接或在口头挂失后5日内，持本人有效身份证件至本行任意网点办理书面挂失。持卡人申请书面挂失应填写《挂失申请书》，经办人员经认真审核，确认挂失申请书内容完整，并与开卡资料一致，为持卡人办理书面挂失。网点受理挂失必须登记《挂失登记簿》，以备查询。（三）密码挂失持卡人遗忘密码，须持卡片和本人有效身份证件到本行任意网点办理密码挂失。经办人员应在核实持卡人身份后，立即为其重置密码。（四）挂失撤销持卡人办理挂失后尚未换领新卡又找回原卡的，可申请撤销挂失。口头挂失撤销可通过网点柜面办理；书面挂失撤销由持卡人凭挂失申请书回执和有效身份证件到原挂失网点办理。办理挂失撤销时，须填写撤销挂失申请书，待网点受理后可继续使用原卡。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第三章  挂失、换卡、止付、冻结管理', '第十九条', '换卡管理（一）圆鼎借记卡办理书面挂失后，经办人员在核对身份后可立即为持卡人办理换卡，或由持卡人事后凭挂失申请书回执和本人有效身份证件到原挂失网点申请换领新卡，经办人员为新卡与原卡片中的各账户建立关联关系，并要求持卡人修改交易密码。芯片卡挂失换卡后，新卡电子现金账户余额为0元，原卡电子现金账户不作处理。（二）若遇卡片损坏，持卡人可持卡片和本人有效身份证件到本行任意网点申请换领新卡。换卡时，持卡人需填写换卡申请表并交回原卡片，经办人员为新卡与原卡片中的各账户建立关联关系，并要求持卡人修改交易密码。芯片卡损坏换卡后，新卡电子现金账户余额为0元，原卡电子现金账户以销户申请后30天后脱机交易流水到账的结果为准，可提现。（三）单位卡、单位结算卡如因工作变动或其他原因更换持卡人时，应由申领单位开具更换持卡人的证明，并及时到发卡机构办理变更手续，在此之前发生的风险和损失，由申领单位承担。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第三章  挂失、换卡、止付、冻结管理', '第二十条', '止付管理（一）持卡人对已遗失卡片办理挂失手续后，本行对挂失卡片进行止付。（二）单位法定代表人或委托代理人以书面形式申请对单位卡、单位结算卡止付时，本行可对其卡片进行止付。（三）持卡人通过柜面、ATM、CDS、自助查询终端、网银、手机银行等渠道办理业务连续三次输错交易密码，本行有权对该圆鼎借记卡进行锁定并止付，次日自动解锁，连续六次输错交易密码，则须由持卡人持卡片和本人有效身份证件至本行辖内任意网点进行解锁。（四）当止付原因不复存在时，可撤销止付，已经撤销止付的圆鼎借记卡可继续使用。（五）单位结算卡止付后，其关联的单位结算账户不受影响，可正常使用。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第三章  挂失、换卡、止付、冻结管理', '第二十一条', '冻结管理任何单位和个人不得随意冻结持卡人账户，人民法院因审理或执行案件，检察院、公安机关因查处经济犯罪案需要冻结持卡人账户的，须提供《协助冻结存款通知书》、执法人员工作证或执行公务证。经办人员受理冻结时须严格检查《协助冻结存款通知书》各项内容，如有不符则退回。冻结期限不能超过一年，如有特殊原因需延长的，决定冻结单位须办理续冻手续，续冻期限最长不超过一年，逾期不办理续冻手续的，视为自动撤销冻结。冻结期限内如需解冻，应以决定冻结单位的《解除冻结存款通知书》为凭，不得自行解冻。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第四章  销户管理', '第二十二条', '属于下列情况之一的，本行可为持卡人办理销户：（一）持卡人凭本人有效身份证件和密码，交回圆鼎借记卡，要求注销其圆鼎借记卡账户；（二）持卡人办理书面挂失后，凭本人有效身份证件，要求注销其圆鼎借记卡账户；（三）个人卡持卡人死亡、失踪等情况，可按照储蓄管理条例的有关规定办理销户；（四）单位卡、单位结算卡凭申领单位法定代表人或授权委托人的书面委托书及其他相关资料申请销户。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第四章  销户管理', '第二十三条', '持卡人办理销户须持有效身份证件和圆鼎借记卡卡片至本行任意网点，填写销户申请书，交回卡片，挂失销户的，须至原挂失网点办理销户手续。（一）个人无折主卡销卡，须首先将所申领的附属卡销卡，再办理主卡卡内其他账户销户（除主账户外），最后办理主卡销卡，结清账户余额。（二）个人有折主卡销卡，须首先进行有折卡脱折，再办理主卡卡内其他账户销户（除主账户外），最后办理主卡销卡。（三）单位卡主卡销卡，须首先将所申领的附属卡销卡，再办理主卡销卡，结清账户余额，将对应账户余额（连同利息）转入基本存款账户，不得提取现金。（四）单位结算卡销卡，须首先将所申领的附属卡销卡，再办理主卡销卡，单位结算卡销卡后，其关联的单位结算账户可继续使用，单位结算账户销户的，必须先注销其关联的单位结算卡。（五）芯片卡销户，须首先办理电子现金账户余额结清或全额圈提，再办理主卡销户。（六）有电子渠道签约管理的卡片进行销户的，须首先解除相关签约关系方可办理销户。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第四章  销户管理', '第二十四条', '圆鼎借记卡个人卡卡内专户一般不允许销户。专户销户须在专户余额结清后，并经有权人员授权后方可进行。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第五章  卡片管理', '第二十五条', '成品卡管理（一）成品芯片卡由卡厂寄送至本行中心库房。本行中心库房收到卡厂寄送或领回的成品卡后，应作为重要空白凭证，及时清点核对、登记入库。本行未收到或领回成品卡前，成品卡由卡厂负责保管。（二）重要空白凭证管理部门接到营业网点领卡申请后，填写《重要空白凭证领用单》，并通过综合业务系统做凭证出库处理。凭证传递至营业网点，营业网点主出纳领卡并核对卡片数量无误后，加盖营业网点业务章并签字，通过综合业务系统接收凭证，并于当日登记相关登记簿。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第五章  卡片管理', '第二十六条', '作废卡管理（一）回收作废卡必须剪角或打洞（破坏磁条信息或芯片信息）。当日营业终了，全部作废卡抄列清单交废卡保管员入库保管，并登记圆鼎借记卡作废登记簿，注明日期、作废原因、作废卡号(卡号不清则空）及数量，同时记载表外科目。（二）作废卡销毁应做到以下几条：1.作废卡实际数量与圆鼎借记卡作废登记簿核对无误后，方可销毁；2.作废卡销毁应经本行重要空白凭证管理部门负责人申请，由行分管行长批准，并指定专人（运营管理部、计划财务部、审计部、安全保卫部等部门）共同负责销毁；3.作废卡应定期销毁，销毁时要认真清点核对，严防散失，销毁后，由销毁人员、监销人员在作废卡销毁清单上签名盖章。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第五章  卡片管理', '第二十七条', '检查监督（一）重要空白凭证管理人员调动时，必须在业务主管监督下进行重要空白凭证交接并逐类清点明细，核对有关账表并作好记录，确保账实相符、账账相符。（二）坚持查库制度，采取定期与不定期两种方式，业务主管每月至少对空白卡进行一次检查。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第六章  收费管理', '第二十八条', '圆鼎借记卡磁条卡暂不设有效期，芯片卡有效期1-10年，具体以卡面印制有效期为准，本行可根据有关规定自主决定是否收取年费、开卡（包括补卡、换卡）工本费等相关费用。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第六章  收费管理', '第二十九条', '本行为持卡人办理圆鼎借记卡卡片挂失时，可根据业务种类按规定收取费用。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第六章  收费管理', '第三十条', '圆鼎借记卡持卡人在办理存取款、转账等业务时，按规定须支付相应手续费的，手续费由本行直接从持卡人账户扣收，手续费具体标准按有关规定执行。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第七章  附则', '第三十一条', '本办法由负责解释和修订。'),
(NULL, '电子银行部', '《圆鼎借记卡业务管理办法》', '第七章  附则', '第三十二条', '本办法自印发之日起执行。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第一条', '为规范圆鼎借记卡的发行和使用，更好地为持卡人服务，依据国务院《储蓄管理条例》、中国人民银行《银行卡业务管理办法》、《中华人民共和国银行业监督管理法》等有关法律法规及规章制度，结合农村商业银行（以下简称“本行”）实际，特制定本章程。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二条', '圆鼎借记卡品牌是由本行委托江苏省农村信用社联合社申请，供本行发行使用的统一的借记卡品牌。本行为圆鼎借记卡的发卡机构。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第三条', '圆鼎借记卡是具备存取现金、转账汇款、消费结算、账户查询等全部或部分功能的金融支付工具，先存款后使用，不提供透支服务。圆鼎借记卡按发行对象不同分为单位卡、个人卡；按信息载体不同分为磁条卡、纯芯片卡、磁条芯片复合卡（以下纯芯片卡和磁条芯片复合卡通称为芯片卡）。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第四条', '凡自愿遵守本章程规定并符合发卡条件的个人和法人，均可凭有效证件向本行辖内营业网点申领圆鼎借记卡。申请人须填写申请书，确认遵守《圆鼎借记卡章程》和本行相关规定。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第五条', '单位卡凭单位基本存款开户许可证、组织机构代码证、营业执照、法人登记证和单位法定代表人或授权人的书面指定，由被指定人出示有效身份证件申领；个人卡凭本人有效身份证件申领，原则上不得代为办理，特殊情况下需要代为办理的，代理人需要提供委托人的有效授权书、委托人的有效身份证件及本人有效身份证件办理申领，申领人申办圆鼎借记卡时，应如实填写申请表，经确认无误后，对符合条件的准予领卡,圆鼎借记卡申领须符合国家实名制有关规定。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第六条', '圆鼎借记卡单位卡起存金额为人民币1000元，账户内资金一律从其基本存款账户转账存入，不得存取现金，不得将销货收入存入单位卡。单位卡可办理商品交易和劳务供应款项的结算，单笔交易超过规定起点的，按相关规定办理。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第七条', '圆鼎借记卡个人卡起存金额为人民币1元，多存不限，并可随时续存，账户内资金只限于以其持有的现金存入或以其工资性款项及其他合法收入转账存入，卡内主账户为个人结算账户，账户内的存款（芯片卡电子现金除外）按照中国人民银行规定的相应存款利率及计息办法计付利息，并由本行依法代扣代缴利息税。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第八条', '持卡人可通过本行营业网点、境内外贴有“银联”标识的自动柜员机（包括ATM、CDS等现金类自助服务设备，下同）、电话银行（96008）、网上银行、手机银行、境内外贴有“银联”标识的特约商户等渠道按照相关规定办理金融交易。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第九条', '持卡人可持具备电子现金功能的芯片卡办理电子现金业务。芯片卡电子现金业务是指本行根据持卡人要求将现金或账户存款转至芯片卡内储存，交易时直接从芯片卡内扣款的业务。芯片卡电子现金视同现金管理，不计息，不挂失。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十条', '圆鼎借记卡只限持卡人本人使用，不得出租或转借，凡因出租或转借造成的损失由持卡人承担。单位卡如因工作变动或其他原因更换持卡人时，应由开户单位开具更换持卡人的证明，并及时到原发卡网点办理变更手续，在此之前发生的损失由开户单位承担。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十一条', '持卡人应妥善保管圆鼎借记卡密码，防止泄漏。凡录入密码与系统密码相符的交易均视为持卡人或本人授权的合法交易，本行依据密码为持卡人办理交易所产生的电子信息记录为该项交易的有效凭证。持卡人承担因不慎泄漏密码造成的损失。芯片卡电子现金交易不使用密码，凡使用芯片卡电子现金进行的交易均视为持卡人本人所为，持卡人因芯片卡保管不善造成的损失，本行不承担责任。持卡人授权通过第三方平台签约（如银联在线签约）后的交易均视为持卡人或本人授权的合法交易。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十二条', '持卡人如连续三次输入密码不正确，本行有权对借记卡实施账户锁定，持卡人须持本人有效身份证件到本行辖内任意网点申请解锁。持卡人如遗忘卡密码，应及时持本人有效身份证件到本行辖内任意营业网点办理密码重置手续。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十三条', '持卡人如不慎遗失圆鼎借记卡，应及时通过电话银行（96008）办理口头挂失或至本行辖内任意营业网点办理书面挂失。持卡人应在口头挂失后5日内补办书面挂失手续，否则口头挂失自动失效。挂失手续办妥后即时生效，持卡人可在书面挂失后至原挂失网点办理补发新卡或至原开卡网点办理销卡手续。挂失生效前或挂失失效后持卡人因遗失借记卡造成的损失，本行不承担责任。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十四条', '若圆鼎借记卡被自动柜员机吞没，持卡人应及时与自动柜员机所属营业网点联系，并凭本人有效身份证件在4日内到指定网点办理领回手续，逾期未领回而被吞卡机具所属网点作废卡处理的，由持卡人自行负责。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十五条', '磁条卡不设有效期，芯片卡设置有效期为10年。圆鼎借记卡如有损坏须更换卡片时，持卡人应持圆鼎借记卡和本人有效身份证件到原发卡营业网点申请换领新卡，并按规定缴纳工本费。芯片卡芯片损坏后磁条卡正常使用，换卡后新卡电子现金账户余额为0元，原卡电子现金账户余额以销户申请45天后脱机交易流水到账的结果为准，可提现或转至其他圆鼎借记卡账户。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十六条', '持卡人需注销圆鼎借记卡时，应持本人有效身份证件向原发卡网点提出书面申请，结清余额，交回圆鼎借记卡。单位卡销户时，卡内账户资金转入其基本存款账户。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十七条', '持卡人须按本行规定的收费项目、收费标准和缴费方式缴纳相关费用。借记卡收费项目、收费标准如有变动以本行最新公告为准。本行根据国家相关法律、法规和规章制定圆鼎借记卡服务费项目和收费标准，收费项目和收费标准可通过营业网点等渠道查询。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十八条', '持卡人有关信息资料变更时，须及时、如实向本行提出书面变更申请，否则因信息资料变化引发的全部责任由持卡人承担。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第十九条', '持卡人办理业务时，如遇拒绝受理，有权向本行及上级主管部门投诉。持卡人如对交易记录有疑问，有权向本行要求查询核对本人卡内账户余额、交易明细等信息，本行须根据行内记录保存情况为持卡人提供该项服务。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二十条', '圆鼎借记卡属于本行，若持卡人违反本章程和国家有关规定，或持卡人未按本行规定缴纳相关费用，本行有权随时取消持卡人的使用资格并收回圆鼎借记卡，由此产生的损失由持卡人承担。圆鼎借记卡停止使用后，尚未结清的债权债务关系继续有效。本行因其他原因停止持卡人使用借记卡，应通过电话、短信以及在营业网点或网站以公告方式提前通知持卡人；因供电、电讯、系统故障等原因导致持卡人用卡受阻的，本行可视情况协助持卡人解决问题或提供必要的帮助，但不承担因此可能给持卡人造成的损失。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二十一条', '虚假挂失，伪造、变造圆鼎借记卡，使用伪造或作废的圆鼎借记卡及冒用他人圆鼎借记卡进行诈骗财物的，本行有权申请法律保护并依法追究有关当事人的经济责任和法律责任。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二十二条', '本行有权依照法律规定协助国家司法机关或其他有权机关对持卡人的圆鼎借记卡账户进行查询、冻结和扣划。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二十三条', '本行根据业务发展需要，有权依据有关规定对本章程进行修订，在营业网点或网站将修改后的章程进行公告。公告满30个自然日后，修改后的章程即为生效。在公告期内，持卡人可以选择是否继续使用借记卡，如对章程的修改有异议而决定不继续使用借记卡的，可向本行营业网点提出销卡申请，本行营业网点为其办理销卡手续。公告期满，持卡人未提出销卡申请的视为同意。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二十四条', '本行以合法方式（包括但不限于营业网点或网站公告）发布的，在持卡人用卡期间持续有效的公告（包括领取借记卡之前和之后发布的），均同样适用于持卡人。如公告与章程不一致的，以发布在后的为准。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二十五条', '本章程未尽事宜，按《银行卡业务管理办法》和主管部门制定的业务规章办理。持卡人在用卡交易过程中发生争议时，按中华人民共和国有关法律和金融法规予以解决。'),
(NULL, '电子银行部', '《圆鼎借记卡章程》', '', '第二十六条', '本章程由负责解释、修订。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第一条', '圆鼎单位结算卡是农村商业银行（以下简称“本行”）面向企事业单位发行的，具备存取现金、转账汇款、消费结算、账户查询等全部或部分功能的支付结算工具。圆鼎单位结算卡是圆鼎系列借记卡之一。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第二条', '凡在本行开立单位结算账户的企事业单位，均可凭单位结算账户开户凭证、组织机构代码证、营业执照和单位法定代表人或授权人的书面指定，由被指定人出示有效身份证件申领圆鼎单位结算卡。申领人应如实填写申请表，经确认无误后，对符合条件的准予领卡,圆鼎单位结算卡申领须符合国家实名制有关规定。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第三条', '圆鼎单位结算卡关联单位结算账户，与单位结算账户共享额度，单位结算卡交易产生的账务全部计入关联的单位结算账户。圆鼎单位结算卡能否存取现金取决于其关联的单位结算账户性质，关联基本户的单位结算卡可以存取现金。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第四条', '圆鼎单位结算卡持卡人可通过发卡机构营业网点、境内外贴有“银联”标识的自动柜员机（包括ATM、CDS等现金类自助服务设备，下同）、电话银行（96008）、网上银行、境内外贴有“银联”标识的特约商户等渠道按照相关规定办理业务。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第五条', '圆鼎单位结算卡凭密支付（电子现金交易除外），可在本行柜面按照相关规定办理存取款或转账等交易。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第六条', '圆鼎单位结算卡被锁定或止付或销卡后，其关联的单位结算账户可正常使用，单位结算账户被锁定或止付后，单位结算卡则不能继续使用。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第七条', '圆鼎单位结算卡有效期1-10年。如有损坏须更换卡片的，应持本人有效身份证件及单位组织机构代码证、营业执照和单位法定代表人或授权人的书面说明到发卡网点申请换领新卡。换卡后新卡电子现金账户余额为0元，损坏卡换卡原卡电子现金账户余额以销户申请30天后脱机交易流水到账的结果为准，可提现，挂失换卡原卡电子现金账户不作处理。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第八条', '持卡人注销单位结算卡时，应持本人有效身份证件及单位组织机构代码证、营业执照和单位法定代表人或授权人的书面说明向原发卡网点提出书面申请，交回单位结算卡。单位结算卡注销后，关联的单位结算账户可以继续使用。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第九条', '如因工作变动或其他原因更换单位结算卡持卡人时，应由申领单位出具更换持卡人的证明，并及时到原发卡网点办理变更手续，在此之前发生的损失由申领单位承担。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第十条', '本章程未尽事宜，按《圆鼎借记卡章程》、《人民币银行结算账户管理办法》、本行和主管部门制定的业务规章办理。本行与持卡人发生争议时，按有关法律和金融法规予以解决。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第十一条', '本章程由负责解释和修订。'),
(NULL, '电子银行部', '《圆鼎单位结算卡章程》', '', '第十二条', '本章程自印发之日起施行。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第一章  总则', '第一条', '为规范农村商业银行（以下简称“本行”）圆鼎定活通（以下简称“定活通”）业务，根据《圆鼎借记卡章程》、《圆鼎借记卡业务管理办法》等规定，制定本办法及流程。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第一章  总则', '第二条', '定活通业务是本行为适应新形势、提升产品竞争力，以借记卡为载体，为资金宽裕的客户提供附加增值功能的金融产品。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第三条', '信息科技部负责为定活通业务提供技术支持以及相关数据统计工作。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第四条', '运营管理部负责定活通业务处理、业务指导和运行管理等工作。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第五条', '计划财务部负责定活通业务成本核算等工作。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第六条', '合规管理部负责定活通各项规章制度及业务的合规审查，监督定活通业务规章制度、规定的执行情况。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第七条', '风险管理部负责定活通业务的风险监测、识别、控制、消除等工作。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第八条', '审计部负责对定活通业务运行、管理、内部控制、考核等情况进行监督。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第九条', '本行电子银行部：（一）负责定活通业务的指导；（二）负责定活通业务的市场调研、客户分析；（三）负责定活通业务的组织、实施和推广，承担产品专业支持和营销服务等工作；（四）负责定活通业务功能增加，完善产品服务；（五）负责定活通业务的交流和人员培训；（六）负责定活通业务经营管理相关的其他工作。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第二章  机构、人员职责', '第十条', '各营业网点:（一）根据本行的部署，承担定活通业务的市场营销任务，负责定活通各相关制度的贯彻执行；（二）负责开展定活通业务宣传，受理业务咨询及对账服务；（三）负责受理定活通业务开通申请和业务变更申请；（四）负责办理定活通其他相关业务。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第三章  服务对象及开通流程', '第十一条', '服务对象定活通服务对象为本行借记卡客户。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第三章  服务对象及开通流程', '第十二条', '开通方式及流程（一）柜面开通1.客户提供需要开通定活通服务的借记卡、本人身份证，填写定活通服务申请表，开通定活通服务。同一客户可以多次申请开通定活通服务，但以最后一次申请为有效。2.开通流程：（1）客户提供申请资料，填写定活通服务申请表；（2）柜台信息录入；（3）柜台打印信息；（4）客户确认签字；（5）开通成功。（二）网银开通1.客户登录个人网银选择开通定活通服务，阅读并同意定活通服务协议，按要求填写定活通服务申请。2.个人网上银行提供7×24小时服务。3.开通流程：（1）登录个人网银；（2）选择开通定活通服务；（3）阅读并同意定活通服务协议；（4）按要求填写定活通服务申请；（5）申请成功。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第四章  业务处理', '第十三条', '定活通业务处理系统于每日日终自动处理定活通业务，先按批量支付规则处理支付业务，再按转存规则处理转存业务。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第四章  业务处理', '第十四条', '定活通业务会计核算（一）定活通下挂账户中活期存款、定期存款、通知存款的存期（期限）及起存金额、计息方式分别按照综合业务系统相应规则进行核算。（二）所有的转存、支付业务的账务处理都遵循综合业务系统的账务处理规则。（三）客户可通过个人网银或到柜面主动将定期（通知）存款转回活期存款账户。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第四章  业务处理', '第十五条', '定活通业务转存规则（一）最低转存金额：活期转定期，最低5000元且为1000元的整数倍；活期转通知，最低50000元且为10000元的整数倍。（二）转存后的活期账户最低留存金额为2000元。（三）客户成功签约后，按照转存规则每日日终自动进行账务处理，不再通知客户。（四）定活通服务终止后，转存规则自动失效。（五）定活通最低转存金额、最低留存金额发生变化的，以本行公告为准。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第四章  业务处理', '第十六条', '定活通业务支付规则（一）活期存款账户余额足够支付时，从该账户中支付。（二）活期账户余额不足支付的，由定期存款或通知存款账户按以下顺序转回：1.到期的存款；2.离起息日最近的未到期通知存款或定期存款；3.未到期的通知或定期存款起息日相同的，存期最短的先转回；4.未到期通知存款或定期存款起息日和存期均相同的，最迟转入的先转回；5.涉及多笔转回活期存款的，先转回到期的存款，再依次转回离起息日最近的存款。（三）未到期的存款转回，按活期存款利息计付。（四）涉及多账户转回，转回账户不能超过3户。本行系统检查符合条件的前3户，若转回合计金额与原活期存款账户余额合计仍不足支付的，终止转回，由客户至柜面或通过网上银行,进行手工销户转回。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第四章  业务处理', '第十七条', '定活通业务变更、撤销（一）变更定活通业务客户申请变更定活通业务，需提交定活通业务申请表及发生变更的相关客户资料，营业网点操作员需按定活通业务开户要求，核对资料无误后留存相关资料复印件，并在相关业务菜单中完成变更操作。（二）终止定活通业务客户申请终止定活通业务，需提交定活通业务申请表及客户相关证件资料，营业网点操作员需按定活通开户要求，核对资料无误后留存相关资料复印件，并在相关业务菜单中完成撤销操作。（三）定活通业务变更、撤销资料并入当日操作员交易凭证管理。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第五章  收费处理', '第十八条', '定活通业务的收费按照本行公示的收费标准执行。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第五章  收费处理', '第十九条', '定活通业务的收费方式采取系统自动扣收方式。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第六章  附则', '第二十条', '本办法及流程由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《圆鼎定活通业务管理办法及流程》', '第六章  附则', '第二十一条', '本办法及流程自印发之日起执行。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第一章  总则', '第一条', '为进一步规范农村商业银行(以下简称“本行”)圆鼎贷记卡业务的管理，明确岗位责任，促进圆鼎贷记卡业务快速健康的发展，根据中国人民银行《银行卡业务管理办法》和《圆鼎贷记卡章程》、《圆鼎贷记卡业务管理办法》及有关规定，制定本流程。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第一章  总则', '第二条', '圆鼎贷记卡业务操作流程是指本行根据所掌握的申请人资料及已发卡片状态，对不同阶段的圆鼎贷记卡相关操作的规定。业务办理流程为：客户申请→受理申请→客户经理对客户进行资信调查→营业网点负责人与运营主管进行初审→电子银行部复核岗进行核查→授信评审部进行风险评估和授信→电子银行部权限内用信审批→分管电子银行行长在权限内审批→经办岗客户信息录入→上报省联社制卡→发卡→客户经理对持卡人的管理和关系维护。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第一章  总则', '第三条', '圆鼎贷记卡各项业务操作时，要坚持客观、公正、科学、定量分析与定性分析相结合的基本原则。各项操作所依据的材料和事实必须真实、合法、规范。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第二章  申请人的条件和资料', '第四条', '申请人的条件：一、申请圆鼎贷记卡单位卡应具备的条件（一）申请单位卡普卡的申请人应具备的基本条件：1、具有人民银行核发的基本账户开户许可证；2、在本行营业网点所在地注册并在当地金融机构开立银行基本账户、办公地点或经营场所在本行营业网点所在地；3、具备法人资格或经法人授权，在有关部门合法登记并注册。有合法经营执照，经营一年以上且经营管理效益良好；4、没有法人资格的单位须提供机构设置批文，其他经济组织必须经相关部门登记注册；5、须经本行授信评审部统一授信，如未统一授信的，须经本行电子银行部进行资信评估后报本行授信评审部审批；6、具备良好的资信状况和相应的偿债能力；7、单位卡的持卡人必须是申请单位指定，且具有完全民事行为能力的该申请单位的个人。（二）申请单位卡金卡的申请人应具备的基本条件：1、有固定的办公场所或生产场地，注册资本在300万元以上，连续3个月的银行人民币账户存款日平均余额在30万元以上，财务制度健全；2、单位领用单位卡普卡1年以上，无不良信用记录，消费频繁，偿债能力正常，经本行重新评估并审核批准后，可换领单位金卡；3、申请单位不符合1、2项条件，但愿意提供授信额度50％以上本行定期存款凭证进行质押的。二、申请圆鼎贷记卡个人卡应具备的条件（一）申请个人卡普卡的申请人应具备的基本条件：1、本国人士年满18周岁至60周岁，境外人士年满25周岁至60周岁，具有完全民事行为能力；2、户籍或工作单位在本行营业网点所在地，职业稳定且有较高的经济收入（月均收入在当地平均水平以上），具有按期偿还本息的能力；3、具备以上条件，无重大负债且在本地有固定住所（公有租赁房或产权房），常住户口或有效居住证明的下列人员申请圆鼎贷记卡可简化担保手续：（1）国家机关、事业单位的公务员和职员；（2）金融机构工作人员（保险公司人寿推销员除外）；（3）持有国家专业机构认定的各类专业技术资格证书的在职专业人士；（4）跨国性全球知名企业、大中型公司、工商企业、科研机构的管理人员、专业技术人员和技术工人；（5）能向本行提供家庭资产证明其足以具有相应的偿债能力；（6）本行圆鼎借记卡的忠诚客户，圆鼎借记卡日均存款额达5000元以上，或10万元以上定期储蓄存款的客户；（7）其他有优良职业和稳定收入的人员。（二）申请个人卡金卡的申请人应具备的基本条件：1、首次申请圆鼎贷记卡的，原则上给予办理普通卡，如申请人愿意提供本行所要求的资信材料，经审核和评估后证明其拥有良好的信誉和足够偿债能力的，经本行授信评审部审批同意后可以申请金卡。2、直接申请个人卡金卡的，如申请人无法提供足够的资信材料或资信状况一般而又确需使用金卡的应当提供质押，具体业务操作流程如下：（1）申请人提供个人担保的，同时提供金卡授信额度80％以上的本行签发的定期存单进行质押；（2）申请人不提供个人担保的，提供金卡授信额度115％本行签发的定期存单进行质押。（三）申请个人卡白金卡的申请人应具备的基本条件：1、具有一定经济实力的个人；2、我行现有贷记卡金卡客户；3、我行客户或他行客户，持卡年消费金额达到5万元且消费笔数达到15笔；或持有他行信用卡的白金卡客户；4、在我行三个月（含）以上日均存款达到30万元的个人客户；5、党政机关处级（含）以上领导干部和财务部门负责人；6、有财政拨款的各类事业单位（地市级或以上）高层领导和财务部门负责人；7、地市级（含）以上重点中、小学校校级领导，重点高等院校的校、院级领导和财务部门负责人；8、三级重点医院院级领导和财务部门负责人；9、市级（含）以上金融、邮政、电信、电力、市政公用行业高层领导和财务部门负责人；10、在我行信用等级为AA级（含）以上重点营销企业的高层领导和财务部门负责人；11、金融行业处级（含）以上领导或发卡机构负责人推荐的社会知名人士及相关行业重要人士；12、金融行业处级（含）以上管理人员；13、卡片升级为金卡、白金卡的客户，须满足上述准入标准，其信用状况还需满足下列条件：信用卡账户及卡片必须处于“正常”状态；最近1年账户不存在连续收取2次违约金的情形且最近一个月未被收取违约金。（四）境外或外地人申请圆鼎贷记卡还应符合如下条件1、已办妥本地居住证，或已购置价值50万元以上本地商品房的境外或外来（在本地工作的外地人，下同）申请人，比照本地居民办理；2、上述范围以外的境外或外来的圆鼎贷记卡申请人，应提供个人担保，并提供不低于授信额度115％的本行签发的定期存单质押。（五）自由职业者申请办法具体条件视申请人信用记录、学历水平、从业经验、家庭资产状况、收支情况和偿债能力等条件进行评估和审核后决定。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第二章  申请人的条件和资料', '第五条', '申请人需提交的资料（一）圆鼎贷记卡申领人或单位应提供以下基本资料：1、单位卡的申请应向本行提供以下基本资料：（1）符合规范填写要求的单位卡申请表；（2）人民银行核发的基本账户开户许可证及复印件；（3）单位营业执照副本及复印件；（4）法人资格证明或法人授权委托证明书及复印件；（5）近三个月银行账户对账单或经其他商业银行开户行加盖业务公章和经办人员私章的日均存款证明（非本行开户的申请单位还需提供近期资产负债表和现金流量表）；（6）经办人身份证件等资料的原件及复印件；（7）由单位提供担保的，需提供担保单位的相关资料（同申请单位）；（8）以质押方式提供担保的，需提供规定金额的质物。（二）个人卡的申请应向本行提供以下基本资料：1、符合规定填写要求的圆鼎贷记卡个人卡申请表；2、申请人身份证、户籍等资料原件及复印件，本行要求提供担保的，还需提供保证人身份证原件及复印件或质物；3、与申请表所填内容相符的主卡申请人全部或部分个人资信材料；4、申请人现居住地最近三个月的电话账单或水、电、煤气缴费单据任一；5、工资或其他收入证明；6、学历、职称证书等原件及复印件；7、家庭资产证明的原件及复印件；8、个人卡由个人提供担保的，保证人必须与本行签订保证合约并在有关签名栏内亲笔签字确认；9、港、澳、台同胞和现役军官申请人，申请圆鼎贷记卡须出具居住地公安部门签发的居住证明或军官证明。境外人员须提供护照和在本行营业网点所在地就业证明或市公安局出入境管理处颁发的外国人居留证，并提供有关资信材料；10、为便于联系和服务，除提供担保的申请人之外，申请圆鼎贷记卡应当提供一名联系人，联系人应具备下列条件：（1）与申请人关系密切，了解申请人的基本状况，无不良行为记录；（2）当本行与持卡人失去联系时能协助本行找到持卡人；（3）联系人应如实向本行提供家庭地址、工作电话及其他通讯工具（如手机等）的号码；（4）联系人的身份证复印件；（5）非本行营业网点所在地户籍的人员不能作为联系人。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第二章  申请人的条件和资料', '第六条', '按本行要求担保的，应根据本行的要求提供担保，并提供相应的担保资料。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第三章  圆鼎贷记卡的担保', '第七条', '圆鼎贷记卡担保形式及规定圆鼎贷记卡的担保采用保证、抵押、质押三种形式。（一）保证保证是指保证人和债权人约定，当债务人不履行债务时，保证人按照约定履行债务或者承担连带责任的行为。具有代为清偿债务能力的公民可以作为个人卡的保证人；具有代为清偿债务能力的法人可以作为单位卡或个人卡的保证人。本行只接受连带保证，保证人承担连带保证责任。（二）抵押抵押是指申领人或第三人不转移财产的占有，将财产作为债权的担保。申领人或第三人应办理抵押品的登记手续，因办理抵押手续所发生的费用，由申领人承担。抵押合约是领卡合约的从合同，在领用合约履行期内，未经本行同意，申领人或第三人不得解除抵押合约。（三）质押质押是指申领人或第三人转移财产的占有，将财产作为债权的担保。采用质押担保方式的，持卡人的质物由本行营业网点负责管理。本行根据主从合约保管和处置持卡人的质物，在贷记卡信用存续期间不得应持卡人的要求为其办理支取或结清手续。在持卡人正式销户前，质物不得挂失、不得提前支取，在圆鼎贷记卡领卡合约履行期内，出质人不得向本行请求返还。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第三章  圆鼎贷记卡的担保', '第八条', '担保条件（一）为圆鼎贷记卡提供担保的单位，须具备下列条件：1、具备法人资格或经法人授权；2、主要经营场所及银行基本存款账户在本行营业网点的所在地；3、注册资本在50万元以上，近三个月银行存款日平均余额在10万元以上，银行信誉良好，财务制度健全。4、为金卡（含白金卡）申请单位提供担保的单位，注册资本应在100万元以上，近三个月的银行存款日平均余额20万元以上，无逾期、欠息贷款情况，银行信誉良好，财务制度健全；5、为多个申请单位提供担保的单位，注册资本应在500万元以上，近三个月银行存款余额不得低于其所担保的总授信额度的100％；6、有限责任公司、股份有限公司提供担保须按照公司章程提供股东会或董事会决议。（二）为圆鼎贷记卡提供担保的个人，须具备下列条件：1、具有本地户籍，并在本地有自有住房；2、年龄在22周岁至60周岁之间，具有完全民事行为能力；3、工作单位在本行营业网点所在地，职业稳定，有合法收入且收入在当地平均水平以上；4、不能相互担保办卡，同一人原则上只能给一人提供担保，若要为两个以上申请人提供担保，必须得到客户经理的认可；5、本行规定的其他条件。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第三章  圆鼎贷记卡的担保', '第九条', '抵押条件 抵押物只限于担保法规定的易于处理的抵押人可自主支配的不动产。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第三章  圆鼎贷记卡的担保', '第十条', '质押条件申请人或第三人以《中华人民共和国担保法》所规定的权利凭证（目前仅限本行签发的定期存单）作质押担保的，本行营业网点应止付权利凭证账户，出质后的凭证不得挂失和转让，权利凭证的有效期（到期日）应超过圆鼎贷记卡的有效期限（定期存单约定自动转存除外），除本行同意外，在圆鼎贷记卡结清之前，出质人不得向本行请求返还。持卡人的圆鼎贷记卡销户后，可凭本人有效身份证件及权利凭证代保管通知单取回权利凭证。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第三章  圆鼎贷记卡的担保', '第十一条', '下列人士不得申请圆鼎贷记卡，也不得为他人申请圆鼎贷记卡作保证人：（一）无具体供职单位的自由职业者；（二）人户分离且职业不稳定者；（三）月收入低于当地平均水平的；（四）征信记录显示申请人信誉欠佳或有不良行为记录的；（五）所提供的电话账单或水、电、煤缴费单据金额过小，不足以证明其长期在其住所居住的；（六）长期在异地工作的；（七）经本行核实后仍难以确认信用状况的。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第四章  初审尽职要求', '第十二条', '受理客户申请的客户经理，要及时审查客户提交的申请材料：（一）检查申请表的字迹是否清楚，填写的内容是否完整（特别是评分项目、通讯地址、邮编、电话等），并确认相关签名是否为申请人和担保人本人所签；（二）检查申请人提供的资料是否齐全；（三）通过联网核查系统核实申请人提供的身份证件真实性；（四）通过人民银行的征信系统进行查询，申请人是否有过不良的贷款记录以及客户在其他银行的资信状况，并在申请资料中真实、详细的记录。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第四章  初审尽职要求', '第十三条', '对符合发卡条件的申请人，客户经理必须对其资金实力、信用状况、经济收入稳定性以及担保能力实行亲访亲核，全面调查，并对申请人提出明确的“建议授信额度”。客户经理对客户资信情况的真实性、相关合约签字的真实性、有效性和“建议授信额度”负责，因调查失职或弄虚作假等原因导致本行资产形成风险的，参照本行不良贷款认定有关规定进行处理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第四章  初审尽职要求', '第十四条', '客户经理调查结束后，在申请表上明确签署调查意见后，交营业网点负责人和运营主管进行审查，营业网点负责人和运营主管对申请人申请资料的合规性和完整性负责，如因审查不力导致本行资产形成风险的，参照本行不良贷款认定的有关规定进行责任认定并进行处理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第四章  初审尽职要求', '第十五条', '签订合约客户经理受理并对申领人填写的申请表及提供的资料进行初步审查及资信情况调查后，指导符合条件的申领人、担保人在相应的申请表、领用合约、保证合约、抵押合约、质押合约上亲笔签名，在重要提示中，申请人确认栏应载明以下语句，并要求客户抄录后签名：“本人已阅读全部申请材料，充分了解并清楚知晓该信用卡产品的相关信息，愿意遵守领用合同（协议）的各项规则”，确保相关合约的真实性和有效性。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第四章  初审尽职要求', '第十六条', '营业网点在本行信息管理工作平台上登记申请人相关信息后，将审查通过的申请人资料原件于次日上报至本行电子银行部。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第五章  复审尽职要求', '第十七条', '复审是指本行电子银行部复核岗对申请贷记卡的单位、个人所提供资料的真实性、完整性及合规性进行调查、核实的过程。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第五章  复审尽职要求', '第十八条', '审核内容：（一）检查填写的字迹是否清楚，内容是否完整，提供的资料是否齐全、有效：1、卡种、卡类明确，卡别、额度无争议；2、账单地址明确（如遇同一单位团办申请中，大多数人寄往单位，而个别申请人未勾选，默认为单位）；3、单位电话、住宅电话必填；团办可确认申请人工作的前提下，若未填单位电话，按同公司其他同事信息补入；若客户注明无住宅电话，可以用其直系亲属、朋友、同事电话补入；4、至少提供一位联系人资料，且联系人必须有一个电话与申请人联系电话不同；5、至少提供三个不同的有效联系电话。（二）检查申请人提供的资料是否齐全（如网点负责人审核意见、运营主管签名、客户经理号等），若缺少资料可根据客户情况做相应退回或补充处理。（三）在发卡系统中查询申请人是否已持有圆鼎贷记卡或以前是否持有过圆鼎贷记卡，因何种原因注销卡片，在系统黑名单中查询是否有此申请人的记录。（四）在人民银行“征信系统”和本行“借款人基本信息系统”上，对网点提交的客户信用情况进行核实，并查看客户是否为不良信用客户。（五）对无法查询到的资料通过以下方式核实：1、电话核实：根据申请人在申请表上填写的家庭电话、单位电话（拨打114查询，反查所填单位电话是否为客户所填单位，如核查不到再正查，用114正查结果电话查核客户是否在本单位工作）、联系人电话，核实申请人资料的真实性以及是否出于申请人本人的意志提交的申请。对一次无法核实的，以换人、换电话的方式分别核实。2、上门核实：对直接申请单位卡金卡的申请人必须要进行上门核实，实地查看一下企业的规模，生产经营状况等；其他认为有必要进行上门核实的申请。3、对采用保证担保方式的申请，对保证人的资料同样需要进行核实。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第五章  复审尽职要求', '第十九条', '复查过程中发现申请人有下列情况之一的，应拒绝其申请：（一）申请表上填写内容或所提供的证明材料不真实的;（二）因有不良信用记录，已被本行、同业或社会征信机构列入黑名单或不良客户名单的；（三）不符合本行条件的；（四）在本行有卡但有不良记录的；（五）在本行或同业有贷款或贷记卡，不能按期偿还或有恶意拖欠记录的；（六）有恶意骗取银行资金行为的；（七）已经或即将被卷入重大的诉讼、仲裁或其他法律纠纷的；（八）重复申请同一卡类别卡片的；（九）已持有其他卡类别且有欺诈嫌疑的；（十）有刑事记录或有多次违反社会治安行为记录的；（十一）本行认为不能发卡的其他情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第五章  复审尽职要求', '第二十条', '在复查过程中发现有下列情况之一的，应将此申请人列为不良记录人：（一）申请人所填资料的主要内容虚假；（二）申请人或保证人所提供的有效证件证明系伪造的；（三）单位申请人所提供的申请资料中加盖的公章系伪造的；（四）单位申请人所提供的申请资料中营业执照等证明材料系伪造或部分伪造的；（五）申请人所附车产、房产为伪造信息；（六）申请单位为代办公司的；（七）申请单位被当地中国人民银行列入“黑名单”的；（八）银联不良客户；（九）本行认为应列为不良记录人的其他情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第五章  复审尽职要求', '第二十一条', '复审结束后，将复审通过的申请材料由经办人员在申请材料上签署意见后，提交复核人审核；未通过复审的申请材料，由经办人员以电话或信函的形式通知申请人或营业网点客户经理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第五章  复审尽职要求', '第二十二条', '经办人、复核人员对申请人资料的真实性、完整性及合规性必须通过多种方式进行调查、核实，保证相关资料内容真实、完整、合规、有效，如因核查不到位导致本行资产形成风险的，参照本行不良贷款认定有关规定，由复审人员承担相应责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第六章  风险评估与授信额度确定', '第二十三条', '风险评估及授信工作由本行授信评审部负责。（一）风险评估的内容包括：1、首先应侧重申请人的信用调查和评估，把握从源头控制风险的原则对申请人进行调查审核，通过综合评估向本行电子银行部提供授信额度和发卡建议。2、对须提供担保的申请人，若采用抵质押担保，须确定抵（质）押物的合理价值；若采用保证担保的，须核实保证人情况，向保证人明确其所应承担的责任和义务。3、授信评审部根据申请人的综合情况，为申请人进行信用评分审核（具体方法详见《圆鼎贷记卡评分方法及具体授信额度参照标准》），根据最后的评分结果为申请人评定一个授信额度并在申请表上签署评估意见。4、信用评分分值达到160分（含）以上的申请人，可以简化担保手续，批准发卡。5、信用评分分值低于160分（不含），但高于100分的申请人（本行职工除外），如能提供本行认可的有效担保或其他有效风险控制措施，也可批准发卡。采用保证人担保方式的，其初始授信额度为4000元；采用保证金、抵押、质押担保方式的，授信额度不得超过担保金额的90%，且不得超过5万元。抵押物仅限于抵押人自有、易于变现且未设定其他抵押的房产；质押物仅限于本行定期存单。（二）授信额度确定1、单位卡的申请单位已经本行授信评审部统一综合授信的，其账户授信额度最高不超过单位授信额度的3%，由授信评审部确定其卡类。2、申请人如未经本行授信评审部综合授信，则应申请并按业务管理办法和有关规定给予授信。（1）单位卡的授信额度按以下标准执行：单位金卡人民币账户信用额度原则上最低为50000元，最高为100000元；单位普通卡人民币账户信用额度原则上最低为20000元，最高为50000元。（2）个人卡的授信额度按以下标准执行：个人白金卡人民币账户信用额度原则上最低为50000元，最高为100000元；个人金卡人民币账户信用额度原则上最低为20000元，最高为50000元；个人普通卡人民币账户信用额度原则上最低为1000元，最高为20000元。3、持卡人通过ATM等自助机具办理现金提取业务，每卡每日累计不得超过人民币10000元，且累计取现额不得超过授信额度的50%。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第六章  风险评估与授信额度确定', '第二十四条', '授信评审部对申请人信用评分和授信额度要有充足的理由和依据，对信用评分和授信额度的准确性负责，授信额度不得超过营业网点“建议授信额度”，如因信用评分不合理或授信依据不充足导致本行资产形成风险的，参照本行不良贷款认定的有关规定，由授信评审部承担相应责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第七章  用信审批', '第二十五条', '电子银行部用信审批应认真对资信调查和审核情况进行严格审查，对资信评估结果进行审核，根据授信评审部提供的授信额度和发卡建议，在授信额度内为申请人确定卡种和卡额度，超电子银行部权限的，需报分管行长审批。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第八章  系统操作和卡片领取', '第二十六条', '被批准发卡的申请人，由电子银行部经办岗再次电话确认申请人自愿申请并有用卡需求的前提下，在操作系统进行客户信息录入、复核、审批后，将客户相关信息上报省联社。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第八章  系统操作和卡片领取', '第二十七条', '省联社统一制卡，通过邮局挂号信寄送至客户，并发出短信提醒客户及客户经理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第八章  系统操作和卡片领取', '第二十八条', '卡片领取人在领用卡片时，保管人必须要求其在相应的登记簿上签名确认，以示负责，确保“领有登记、出有登记”。如因签名领用手续履行不到位形成卡片遗失或被他人盗用，由此引发的责任由卡片保管人全部承担。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第八章  系统操作和卡片领取', '第二十九条', '卡片发出后，客户可致电客服热线4008296008进行卡片激活和使用。发卡后，客户经理负责对持卡人进行跟踪和维护，如持卡人资信状况发生重大变化或呈现不良发展趋势，必须主动及时与本行电子银行部沟通，必要时采取有效的预防措施控制信用风险。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第九章  授信额度的调整', '第三十条', '核准发卡后，持卡人有需要调整卡片授信额度的，可向本行申请调整授信额度，由授信评审部授信审批后，电子银行部进行额度的调整。（一）持卡人因职业、收入等基本情况与申请时相比发生良性变化而向本行申请提高授信额度或临时提高授信额度；1、临时提高授信额度调整：持卡人因临时需要提高授信额度时，可致电24小时客服热线申请临时调额，调高额度不得超过原授信额度的10%（同时调整后的授信额度，不得超过原卡种的最高限额）。时间不得超过1个月（由本行委托省联社客服统一办理）。2、长期提高授信额度调整：（1）持卡人主动申请调整授信额度的，须到营业网点填写《圆鼎贷记卡信用额度调整申请表》并向客户经理提供相关的资料。采用担保方式办卡的，应要求其补充提供相应的担保。采用抵押、质押方式办卡的，应要求其先行补充提供相应金额的抵押物和质押物。客户经理按相关规定进行初审，将符合调整条件的申请人资料报本行电子银行部，电子银行部风险控制岗根据营业网点提供的资料，对申请人的申请理由以及现在的资信情况进行核查，核查通过的交由电子银行部经理审核，授信评审部按权限进行审批，方可进行授信额度的调整（但调整后的授信额度，不得超过原卡种的最高限额）。（2）除持卡人主动申请的，电子银行部风险控制岗人员可根据持卡人的用卡情况，拟定出适度调高持卡人的授信额度报告，由电子银行部经理审核、交由授信评审部按权限进行审批，方可进行授信额度的调整（但调整后的信用额度，不得超过原卡种的最高限额），但经办岗人员必须提前通知持卡人，如持卡人不同意调高授信额度，则不予调整。（3）电子银行部风险控制岗人员对持卡人财务情况恶化、连续两个月未偿还最低还款额的、本行无法联系到持卡人的、本行认为应调低其信用额度的其他情况，拟定调低持卡人授信额度的报告，报本行电子银行部经理审核后，可调低其信用额度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十一条', '存款/还款持卡人需要向圆鼎贷记卡缴存现金或还款，可到本行所有网点办理，各营业网点柜员应通过圆鼎贷记卡存款/还款交易进行处理。存款/还款交易视同圆鼎卡网络异地存款交易处理，可凭卡号办理，但不收取存款手续费。持卡人或其代理人可直接以现金还款，也可从还款人在本行开立的个人银行结算账户（还款网点与开户网点不同时，该账户须为通存通兑户）转账还款，其中用于还款的个人银行结算账户的款项转出交易是否收费及收费标准统一视同取现交易处理。交存支票的，支票“用途”栏中注明事项应能证明该笔款项的来源符合《人民币银行结算账户管理办法》的规定。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十二条', '取款/预借现金持卡人需取款或预借现金，可通过营业网点柜面或自助设备（ATM）办理。个人贷记卡溢缴款可在营业网点柜面全额支取，预借现金每卡每日累计支取不得超过5000元，且不得超过透支信用额度，但持卡人可取出溢缴款全额，即持卡人柜面取现最高额=溢缴金额+可使用授信额度的50%（最高5000元）。ATM取现每卡每日累计不得超过20000元（参照目前借记卡ATM取款标准），预借现金每卡每日累计支取不得超过10000元（具体标准参照人民银行规定）。取现透支累计金额不允许超出信用额度的50%（发卡系统可以对每卡进行设定），有效期到期的卡允许取现，但不允许透支取现。持卡人取现透支不享受免息期待遇。取现透支从记账日起计算透支利息，并按月计收复利。在柜面提取现金超过5万元（不含）以上的，应核对持卡人有效身份证件，在柜面取现不论金额大小均应经柜面负责人授权后方可办理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十三条', '资金清算本行发行的圆鼎贷记卡资金一律通过省联社进行清算。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十四条', '到期换卡本行对符合条件的持卡人提供到期自动换卡服务。有以下情况之一的卡片，不得进行到期换卡：（一）有效期内从未使用过的；（二）持卡人当前迟缴时间超过一个月的；（三）挂失后未申请补发的；（四）已被持卡人主动申请停用的；（五）已被本行停用的；（六）已申请卡片到期不换发的；（七）其他同业认为不应换卡的情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十五条', '提前换卡持卡人因有特殊原因时，可向本行提出提前换卡申请，本行电子银行部经办岗审查无误后，办理换卡手续。（一）申请提前换卡的卡片距到期的时间原则上应不超过三个月。（二）申请提前换卡的持卡人须提供其还款代理人资料。（三）其他规定与到期换卡相同。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十六条', '挂失持卡人所持圆鼎贷记卡遗失或被盗，既可向本行营业网点柜面申请挂失，也可向省联社圆鼎贷记卡客户服务部门申请电话挂失。电话挂失即为正式挂失。挂失手续办妥后发生的不是由持卡人本人意思表示而产生的债务和损失不再由持卡人承担。挂失补发的新卡的有效期一般应与原卡相同，若旧卡距到期的时间在三个月以内，经审核具备到期换卡条件，补发的新卡的有效期可适当调整，但最长不超过5年。以下情况不予补发新卡：（一）持卡人迟缴时间超过一个月的，须待正常还款后方可补发；（二）卡片已被本行停用的；（二）其他本行认为不应补发的情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十七条', '毁损补发持卡人因卡片毁损（含签名有误）、磁条消磁等原因申请补发新卡的，须要求其先行退回旧卡。毁损补发的新卡的有效期一般应与原卡相同，若旧卡距到期的时间在三个月以内，经审核具备到期换卡条件，补发的新卡的有效期可适当调整，但最长不超过5年。有以下情况之一的，不得进行毁损补发：（一）持卡人迟缴时间超过一个月的，须待正常还款后方可补发；（二）卡片已被本行停用的；（三）其他同业认为不应补发的情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十八条', '卡片升级电子银行部可对符合规定条件的优质持卡人，应其申请或主动将其所持普通卡升级为金卡。（一）对于符合以下条件之一的，经审核可予以办理卡片升级：1．持卡人职业、职务、收入等基本情况发生显著变化，已达到金卡申请标准，且用卡及还款记录正常；2．持卡人持有圆鼎贷记卡普通卡的时间在两年以上，用卡及还款记录正常，年均交易额（消费和取现）达到现持有卡种最高授信额度70%以上的。（二）同意办理卡片升级的，应要求持卡人在得到金卡卡片后同时退回其原持有的普通卡。（三）升级后的卡片的有效期为自同意升级发卡月份起5年，并改按金卡标准记收年费。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第三十九条', '账务查询或更正处理持卡人对账务变动情况有疑义的，可在规定时间内向本行提出查询或更正要求。对超过规定时间提出查询或更正要求的，本行根据实际情况进行处理。本行在收到持卡人否认交易或对交易金额有异议的投诉后，应根据持卡人提出疑义交易的清算信息来源确定差错处理机构（中国银联或本行相关部门），按照该交易的种类和相应机构规定的条件、程序，通过该机构向收单行提出调单要求，必要时还可依照该机构的有关规定提请争议处理。处理结果应及时通知持卡人。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第四十条', '持卡人主动申请停卡主卡持卡人申请注销副卡的，应要求主卡持卡人退回申请停用的卡片。不能退回卡片的，主卡持卡人对该卡发生的有效交易款项及相应的利息、费用等继续负有偿还责任。持卡人在贷记卡卡片有效期内或到期后不再使用卡片的，并在清偿贷记卡账户所有欠款后向本行申请销户的，本行在受理销户的同时对芯片贷记卡电子现金账户进行余额转移登记（30天后电子现金账户余额自动转入贷记卡主账户），在受理销户申请45天后为持卡人办理正式销户，对已收的年费不予退还。办理正式销户后，本行继续保留对持卡人之前或之后发生的贷记卡账户的追索权，并受理持卡人还款。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十章  日常业务处理', '第四十一条', '日常业务的经办人员对因经办圆鼎贷记卡业务而产生的操作风险和管理风险承担相应的责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十一章  催收', '第四十二条', '电子银行部风险控制岗对持卡人迟缴款项截止到期还款日后的第一个对账单打印日时，持卡人仍未还款或还款金额不足最低还款额的，自次日起进入催收程序，开始计算迟缴时间。对进入催收程序的账户每期均应在对账单上列印欠款信息，提醒持卡人还款，直至持卡人无迟缴款项为止。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十一章  催收', '第四十三条', '催收是指当圆鼎贷记卡持卡人未在规定的还款期内偿还最低还款额时，本行根据其欠款时间的不同，通过电话、信函、上门或司法等方式向其催讨欠款的过程。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十一章  催收', '第四十四条', '迟缴情况分析及处理1、迟缴在30天以内，透支金额较小或未超过保证金的，且上期有缴款的，由客户经理采用短信和电话进行催收。2、迟缴超过30天未满60天，透支金额较大，但前期有缴款记录的，将其信用额度降为原来的50%，欠款缴清后额度恢复至原信用额度。由客户经理通过电话和信件催收，并留意持卡人近期的交易记录：如发现持卡人有较大金额的消费或提现，或有超额使用意图的，应立即加紧催收并列入下一等级。3、迟缴超过60天未满90天，透支金额超过授信额度的，将其信用额度降为零，由系统进行锁卡处理（VIP客户除外）。客户经理具体负责上门催收，电子银行部风险控制岗负责每星期进行电话催收，建立持卡人迟缴档案。4、迟缴超过90天未满180天的，客户经理具体负责上门催收并冻结该客户名下的所有账户，电子银行部风险控制岗负责将持卡人列为不良客户管理。并将持卡人相关资料移交信贷管理部，由信贷管理部发出法律通知书，按不良贷款管理要求实施依法催收。5、迟缴超过180天的，将其账户的欠款转为呆账贷款。电子银行部风险控制岗将持卡人资料列入黑名单，视具体催收情况采用上门催收或与公安机关进行联合催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十一章  催收', '第四十五条', '电子银行部风险控制岗负责客户催收信息的整理和通报，并采用电话、信函等方式积极主动地向持卡人催讨欠款，客户经理具体负责上门催收，达到期限必须进行司法催收。因催收不力或消极应付导致本行资产形成风险的，参照本行不良贷款认定的有关规定，由相关催收人员承担相应责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十二章  风险监控及档案管理', '第四十六条', '电子银行部风险控制岗应不定期分析持卡人用卡情况，及时掌握和了解持卡人的资信变化，检测持卡人还款情况，分析持卡人拖欠原因，定期制定持卡人资信分析报告，主动及时地与客户经理进行信息沟通和交流，有效掌控持卡人可能产生的信用风险。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十二章  风险监控及档案管理', '第四十七条', '电子银行部风险控制岗根据卡系统定期生成的各项报表，对使用状况不良的持卡人建立追踪档案，并定期生成圆鼎贷记卡的五级分类报表。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十二章  风险监控及档案管理', '第四十八条', '电子银行部风险控制岗要有效利用实时监控系统对持卡人的用卡情况进行监控，对大额、可疑的交易要即时进行登记并追踪，并对客户用卡习惯进行分析，可疑交易要和客户进行确认。同一持卡人单笔透支发生额个人卡不得超过2万元、单位卡不得超过5万元，同一账户月透支余额个人卡不得超过5万元，单位卡月透支余额不得超过10万元；透支期限最长为56天。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十二章  风险监控及档案管理', '第四十九条', '业务发展中产生的各种风险损失，按照省联社下发的《江苏省农村信用社联合社关于做好呆账核销管理工作的指导意见》及本行规定的核销业务操作流程进行核销处理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十三章  罚则', '第五十条', '在贷记卡的申请、调查、办理过程中，存在下列行为的，对相关责任人按记分管理办法记分，并视情节轻重，追究相关人员的行政和法律责任：1、贷记卡申请资料不全、填写不完整和不清晰的，客户、担保人、网点负责人、经办人、审核人员未审核签字的，无各网点业务公章的；或贷记卡申请资料没有在企业信息管理平台里录入相应贷记卡数据和资料的，或录入不全的；2、因填写的客户联系电话有误，造成回访失败、无法催收的，或对于错误的客户联系电话，客户经理不及时联系客户更新联系电话的；3、因客户账单地址不真实，影响账单投递的，或发生账单地址错误，客户经理不及时上报新的地址，造成账单长期投递失败的；4、相关部门不按规定核准贷记卡额度，造成额度明显偏低，引起客户不满的，或贷记卡信用额度调整申请未符合标准的；5、新办和挂失补寄的贷记卡代领回网点后，不及时通知客户领取或不及时分发给客户，未造成不良影响的；6、客户经理对客户调查不真实，授信额度偏高，造成风险的，或与持卡人勾结，帮助持卡人利用信用卡恶意套取现金的；串通商户套取银行资金，造成严重经济损失的；7、贷记卡申报资料不真实、客户经理审核不认真导致相关管理部门责罚，造成较大影响的；8、对于产生逾期的客户，客户经理不及时催收，未造成资金损失的，或因客户经理催收不力，导致贷记卡逾期三次以上，造成10000元以下资金损失的；9、贷记卡开卡后未激活使用的，或未经客户同意，私自激活客户贷记卡引起客户投诉的，或将客户贷记卡据为己有，挪用客户资金、贷记卡形成死卡或造成10000元以上资金损失的；10、其他违反贷记卡管理办法，造成恶劣影响的。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十四章  附则', '第五十一条', '本办法由负责解释和修订。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务操作流程》', '第十四章  附则', '第五十二条', '本办法自印发之日起实施。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第一章  总则', '第一条', '为加强农村商业银行（以下简称“本行”）圆鼎贷记卡业务管理，促进贷记卡业务健康发展，根据人民银行、银监会、省联社等有关规定和《江苏农村商业银行圆鼎贷记卡章程》，制定本办法。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第一章  总则', '第二条', '圆鼎贷记卡品牌是由本行委托江苏省农村信用社联合社申请，供本行发行使用的统一贷记卡品牌，本行为发卡机构，是圆鼎贷记卡的债权、债务人。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第一章  总则', '第三条', '本办法所称圆鼎贷记卡是发卡机构向社会公众公开发行，给予持卡人一定信用额度，持卡人可在信用额度内透支使用，具有消费、存取现金、转账结算、分期交易、信用贷款等全部或部分功能，可在境内外贴有银联标识的商户或自助终端上使用的支付结算工具。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第一章  总则', '第四条', '圆鼎贷记卡为人民币银联标准卡，按卡片等级不同分为、白金卡、金卡和普卡；按信息载体不同可以分为磁条卡、纯芯片卡、磁条芯片复合卡（以下纯芯片卡和磁条芯片复合卡统称为芯片卡）。芯片卡具备电子现金支付功能,包括小额脱机消费、圈存、圈提、查询等;信息存储功能,包括记录持卡人的基础信息和相关行业应用信息等。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第一章  总则', '第五条', '本办法适用于圆鼎贷记卡个人业务管理，办理圆鼎贷记卡业务的营业网点、业务经办人员等均应遵守本办法。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第一章  总则', '第六条', '圆鼎贷记卡业务发展遵循“统一规划、统一品牌、统一标准、统一清算、统一客服、自主经营”的基本原则，各经办机构、持卡人、特约商户须遵守国家有关法律法规和发卡机构有关制度规定，维护有关各方的合法权益。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第一章  总则', '第七条', '本办法中使用的术语，遵从如下定义：1、申请人：指向发卡机构申请贷记卡的申请者本人，如无特殊说明，包括主卡申请人和附属卡申请人。2、持卡人：指向发卡机构申请个人卡并获得卡片核发的申请者本人，包括主卡持卡人和附属卡持卡人。贷记卡卡片仅限持卡人本人使用，不得出租和转借。3、信用额度：也称账户授信额度，指发卡机构根据申请人资信情况等为其核定的在圆鼎贷记卡有效期内可循环使用、向发卡机构透支的最高限额。4、卡类别：发卡机构根据圆鼎贷记卡持卡人资信情况、持卡人享受特种优惠、持卡人账务处理等方面的不同而确定的产品分类。5、交易日：指持卡人实际消费、存取现、转账交易或与相关机构实际发生交易的日期。6、银行记账日：指发卡机构在持卡人发生交易后将交易款项记入其圆鼎贷记卡账户，或根据规定将有关费用、利息记入其圆鼎贷记卡账户的日期。7、账单日：指发卡机构每月对持卡人的累计未还消费交易本金、取现交易本金、费用等进行汇总，结计利息，并计算出持卡人应还款额的日期。8、到期还款日：指发卡机构规定的持卡人应该偿还其全部应还款额或最低还款额的最后日期。9、实际还款日：指持卡人以存现、转账等方式向发卡机构偿还其欠款的日期，以发卡机构收到客户还款资金的实际日期为准。10、全部应还款额：指截至当前账单日，持卡人累计未还的费用、交易本金以及利息等的总和。11、最低还款额：指发卡机构规定的持卡人透支后应该偿还的最低金额，包括累计未还消费本金的一定比例，所有利息、超过信用额度的透支金额、取现本金和费用，以及以前月份最低还款额未还部分的总和，持卡人办理分期付款的，分期付款本期入账本金和本期入账手续费全额计入当期最低还款额。12、免息还款期：指持卡人于银行记账日至到期还款日（含）前偿还全部应还款额的前提下，可享受免息待遇，其交易记账日至到期还款日期间为免息还款期。13、溢缴款：是指持卡人实际还款金额高于全部应还款额的部分，该部分金额不计息。14、违约金：指发卡机构与持卡人约定的，持卡人到期还款日（含）前还款金额不足最低还款额时，应支付给发卡机构的费用。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第八条', '年满18周岁、具有完全民事行为能力且资信良好的中国居民，常住境内的具有完全民事行为能力的外国人及具有完全民事行为能力的港澳台同胞，均可凭本人有效身份证件和相关证明文件申领个人卡主卡，还可为其他具有完全民事行为能力或限制民事行为能力的自然人申领附属卡。附属卡申请材料必须由主卡持卡人和附属卡持卡人亲自签名确认。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第九条', '申请人申请圆鼎贷记卡，须由本人填写申请表、在领卡合约上签字，并提供有效身份证件。身份证件原则上须校验原件并由发卡机构留存复印件备查，特殊情况下（如采用邮寄或电子方式申请的）可只提供复印件或扫描件。有效身份证件类型有：1、境内公民：中国居民须提供本人居民身份证，中国人民解放军军人须提供军人身份证件，中国人民武装警察须提供武警身份证件。附属卡申请人可提供户口簿；2、港澳台居民：香港、澳门居民须提供港澳居民往来内地通行证和我国公安部门签发的暂住证，台湾居民须提供台湾居民往来大陆通行证（台胞证）和我国公安部门签发的居留证；3、外国公民：须提供护照和我国公安部门签发的居留证。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十条', '申请人申请圆鼎贷记卡须提供收入证明。收入证明可为申请人所在工作单位出具的，注明申请人月薪或年薪收入及在本单位工作时间的证明原件，或以申请人本人名义办理的代发工资存折复印件或代发工资卡对账单原件、个人所得税交缴凭据复印件等等，提供其中之一即可。除收入证明外，申请人还可提供有助于证明其资信的其他材料，包括以申请人本人名义办理的购房证明及居住证明等。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十一条', '对于发卡机构现有优质客户及其他重要目标客户，属于发卡机构主动营销办卡的，申请人须提供的资信证明材料可由发卡机构自行确定。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十二条', '境内公民申请圆鼎贷记卡一般采用免担保发卡方式。对于个别证明资料不足、无法判断其资信情况的申请人，可在其提供质押担保或保证人担保后同意发卡。申请人要求交存保证金的，在为其办理定期存款后按质押处理（以下合称“质押担保”）。保证人须同时符合以下条件：1、具有完全民事行为能力，且年龄在22周岁至60周岁之间；2、具有发卡机构辖内区域户籍，并在当地有自有住房；3、在发卡机构辖内区域有合法固定的职业和较高的收入；4、信用状况良好。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十三条', '港澳台居民、外国公民申请圆鼎贷记卡的，原则上必须提供保证人担保或质押担保。保证人及质押担保的具体要求同上条。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十四条', '经发卡机构征信审核认定符合要求、同意发卡的申请人，由发卡机构核定其卡片初始信用额度。卡片初始信用额度在开卡函中注明，开卡函随卡片寄送至持卡人。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十五条', '对于具有一定社会地位或年收入高于发卡机构辖内区域人均年收入、资信优良的申请人，经申请人同意，可发给金卡或白金卡。金卡、白金卡持卡人可享受较高信用额度及发卡机构提供的更多差别化服务。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十六条', '申请人有以下情况之一的，发卡机构应拒绝其领卡申请：1、申请表填写内容或所提供的证明材料不真实的；2、因有不良信用记录，已被发卡机构、同业或社会征信机构列入黑名单或不良客户名单的；3、在发卡机构或同业有贷款，不能按期偿还或有恶意拖欠记录的；4、有刑事记录或有多次违反社会治安行为记录的；5、有恶意骗取银行资金行为的；6、已经或即将被卷入重大的诉讼、仲裁或其他法律纠纷的；7、重复申请同一类别卡片的；8、已持有其他类别卡且有欺诈嫌疑的；9、发卡机构认为不能发卡的其他情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十七条', '发卡机构对同意发卡的申请资料应归档管理，并永久保存；对未批准发卡的申请资料不予退回，并应至少存档三个月，经发卡机构贷记卡负责人批准后，指定二人以上共同销毁。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第二章  申领', '第十八条', '发卡机构应妥善保管和处理申请人的申请资料以及持卡人用卡交易信息等客户个人资料，持卡人有关基本信息发生变化时，应及时调整、更新和维护。未经允许，不得擅自将申请人/持卡人的基本信息、信用信息以及持卡人交易信息等客户个人资料提供给本单位以外的任何机构或个人，法律法规另有规定的除外。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第三章  信用额度管理', '第十九条', '圆鼎贷记卡的信用额度管理遵从以下基本规定：信用额度以人民币表示；个人白金卡初始最高信用额度为100000元，最低信用额度为50000元；个人金卡初始最高信用额度为50000元，最低信用额度为20000元；个人普卡初始最高信用额度为20000元，最低信用额度为1000元。第二十条 持卡人初始信用额度由发卡机构按以下规定核定：1、根据申请人申请时的资信情况或所提供的担保，以及本人申请额度的大小，按“孰低”原则核定；2、持卡人初始信用额度由发卡机构根据自身风险策略进行核定，但最高不得超过100000元。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第三章  信用额度管理', '第二十一条', '持卡人因临时交易需要，可向发卡机构申请临时增加信用额度。发卡机构核实后可为持卡人调高临时信用额度，临时额度不得超过原额度的10%，临时额度期限最长30天。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第三章  信用额度管理', '第二十二条', '持卡人因职业、收入等基本情况与申请时相比发生良性变化而向发卡机构申请提高信用额度的，发卡机构应在收到申请后，根据其提供的证明材料进行相应调整。因其他原因提出的提高长期信用额度的申请一般不予核准。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第三章  信用额度管理', '第二十三条', '发卡机构对基本情况优化或未发生变化的持卡人，可根据其使用及还款等情况定期提高其个人信用额度：1、信用额度调整比例根据持卡人在相应周期内的用卡情况确定。2、发卡机构提高持卡人信用额度后，应及时通知持卡人，如持卡人不同意提高信用额度，则应恢复其原信用额度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第三章  信用额度管理', '第二十四条', '持卡人有下列情况之一的，不得调高其信用额度：1、有欺诈嫌疑的；2、卡片已经停用的；3、卡片到期前主动申请不续卡的；4、卡片挂失后未申请补发的；5、当前有逾期欠款的；6、当前还款正常，但在过去的一年中有三次以上逾期时间在一个月内的记录，或有两次以上逾期时间超过一个月的记录的；7、其他不应进行调整的情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第三章  信用额度管理', '第二十五条', '采用担保方式办卡的，发卡机构不主动提高其信用额度，如持卡人申请长期调高或临时增加信用额度，应要求其补充提供相应担保。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第三章  信用额度管理', '第二十六条', '对于符合以下情况之一的持卡人，发卡机构应及时调低其信用额度，情节严重的，应予停卡处理：1、财务情况恶化的；2、连续二个月未能偿还最低还款额的，信用额度调低50%；3、发卡机构无法联系到的；4、其他应调低其信用额度的情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第二十七条', '发卡机构应提醒申请人领卡后立即在卡背面签名栏内签上与申请表相同的签名，并在交易时使用相同的签名，否则，由此产生的损失由持卡人负责。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第二十八条', '凡使用密码进行的交易，均视为持卡人本人所为，签名仅作为持卡人认可交易的辅助措施，不作为风险责任划分依据。依据密码办理的刷卡消费、存取款、转账结算、网上支付、手机支付、电话支付等各类交易所产生的电子信息记录均为有效交易凭证。凡未使用密码进行的交易，记载有持卡人姓名的交易凭证或凭贷记卡磁条、芯片、卡号等电子数据而办理的各项交易所产生的信息记录之一或全部均属于该项交易的有效凭据。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第二十九条', '芯片卡电子现金业务是指发卡机构根据持卡人要求将现金或账户存款转至芯片卡内储存，交易时不校验密码直接从芯片卡内扣款的业务，芯片卡电子现金账户视同现金管理，不计息，不挂失。电子现金账户资金可通过圈存获得，其总余额不得超过1000元。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十条', '发卡机构可根据业务风险控制策略对圆鼎贷记卡设置首次取现额度、单笔透支额度、每日预借现金取现累计额度（转账视同预借现金）、累计透支取现额度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十一条', '圆鼎贷记卡办理存款和消费不需要支付交易手续费，芯片贷记卡指定账户圈存视同消费，不收取交易手续费。但在他行网点及自助设备上办理业务时，须按规定支付手续费。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十二条', '圆鼎贷记卡开通预借现金和转账功能后，即可通过ATM、网点柜面以及其他可提供现金服务的渠道提取现金或转账。办理预借现金或转账交易部分不享受免息还款期待遇，从预借现金或转账交易日起至清偿日按日收利息，日利率上限为万分之五、下限为万分之五的0.7倍（折合年利率上限为18%、下限为18%的0.7倍），并按月计收复利，并收取一定的预借现金或转账交易手续费，如中国人民银行的有关规定发生变动，按其规定执行。发卡机构若因业务需要，可与贷记卡申请人另行约定。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十三条', '圆鼎贷记卡账户内的存款不计付利息。持卡人在提取（转出)贷记卡账户存款时，按贷记卡预借现金或转账交易手续费收费标准收取。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十四条', '持卡人因交易或未还款额超过核定的授信额度时，不适用免息还款期的规定，应按交易金额支付透支利息。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十五条', '圆鼎贷记卡有效期为5年，发卡机构为持卡人提供到期自动换卡服务，换卡后，新芯片贷记卡电子现金账户余额为0，原卡电子现金账户可以继续使用或对原卡电子现金账户进行余额转移，转移至主账户。若持卡人到期不再换卡，应于圆鼎贷记卡有效期到期前一个月以书面或发卡机构认可的其他形式通知发卡机构。圆鼎贷记卡到期后，其账户下尚未结清的债权债务关系仍然有效。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十六条', '发卡机构有义务向持卡人提供咨询、查询、对账、挂失、投诉等服务。当持卡人对其账务情况提出质询和改正要求时，发卡机构对人民币账户交易应当于30日内给予回复，对外币账户交易应当于90日内给予回复。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第四章  交易管理', '第三十七条', '贷记卡持卡人一日内连续三次输入交易密码不正确的，当天该卡片即被锁定，次日自动解锁。如连续9次输入密码错误，卡片将被冻结。持卡人须凭有效身份证件向发卡机构申请办理密码解锁、密码重置等手续。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第三十八条', '持卡人应在到期还款日前（或在到期还款日后，但在银行给予的宽限期前，以下统称到期还款日前）偿还全部应还款额或最低还款额。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第三十九条', '全额还款方式下，持卡人在到期还款日前（含）偿还全部应还款额的（或偿还款项与全部应还款项差额不高于10元的），其当期账单上本期发生的消费交易款项享受自银行记账日至到期还款日期间的免息待遇，免息还款期最长56天。发卡机构若因业务需要，可与贷记卡申请人另行约定。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十条', '最低还款额方式下，持卡人未能在到期还款日前（含）全额还款的，不享受免息。不符合免息条件的交易款项从银行记账日开始计算利息，日利率上限为万分之五、下限为万分之五的0.7倍（折合年利率上限为18%、下限为18%的0.7倍），并按月计收复利，如中国人民银行的有关规定发生变动，按其规定执行。发卡机构若因业务需要，可与贷记卡申请人另行约定。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十一条', '还款计算公式为：全额还款金额＝消费交易透支的100％＋现金透支（取现）的100％＋本期利息费用（年费、挂失费、利息、违约金和各类手续费）＋上月未还金额的100％；最低还款额＝信用额度内消费交易透支的10％（该百分比可调整，现设定为10%）＋超限部分非现金交易的100％＋现金交易（取现）透支的100％＋本期利息费用（年费、挂失费、利息、违约金和各类手续费）＋上月最低还款额未还部分＋上期视同全额还款未还部分的100%。计算出的最低还款额小于人民币20元的，如全部应还款额大于或等于人民币20元，则最低还款额按人民币20元收取；如全部应还款额小于人民币20元的，则最低还款额等于全部应还款额。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十二条', '圆鼎贷记卡有自行还款或约定自动还款两种还款方式。约定自动还款的，由发卡机构在到期还款日从持卡人指定的在发卡机构开立的账户转账还款。对持卡人的还款，按照费用、利息、取现交易（含转账交易）本金、消费交易本金的顺序逐项抵偿其透支款项。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十三条', '持卡人未在到期还款日前还清最低还款额时，除应按照上述计息方法支付利息外，对不足最低还款额的差额部分，还应支付违约金。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十四条', '本行所有网点均应受理圆鼎贷记卡的人民币主动还款交易。人民币主动还款交易视同存款交易处理，可凭卡号办理，不收取存款手续费。持卡人或其代理人可直接以现金还款，或从还款人在江苏·农村商业银行系统内各发卡机构开立的个人银行结算账户（还款网点与开户网点不同时，该账户须为通存通兑户）转账还款，暂不收取交易手续费。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十五条', '系统收到持卡人还款信息的当日，按还款顺序自动进行日终批处理，并根据实际还款金额相应恢复持卡人的卡片可用额度。还款顺序为：费用、利息、取现交易本金、消费交易本金。如还款额高于上期全部应还款额而且当期（上一对账单打印日次日至还款日）有记账金额的，则依取现交易本金、消费交易本金的顺序冲抵其欠款。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十六条', '持卡人到发卡机构办妥还款手续的实际还款日在到期还款日前的，即使还款信息在到期还款日后到达，仍视同到期还款日前还款。对于因还款信息晚于实际还款日到达而给持卡人账户多计算的利息、费用，由发卡机构做相应的冲回处理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十七条', '持卡人还款金额超过全部应还款额的部分，或持卡人主动存入的款项，作为溢缴款。溢缴款不计付利息，但相应增加持卡人主卡的卡片可用额度。持卡人账户有溢缴款的，产生新记账金额时，先行冲减溢缴款。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第五章  还款管理', '第四十八条', '持卡人圆鼎贷记卡账户正式结清时，如账户有溢缴款，可全额提出。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第四十九条', '圆鼎贷记卡持卡人发生的各项费用，均在费用发生后采用内扣方式直接在持卡人账户中记收，不采用外收方式。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十条', '年费按年预先收取。收取标准为：个人白金卡280元/年，附属卡180元/年，首年免年费,当年刷卡满6次或累计金额满20000元免次年的年费(取现、转账除外)；个人金卡主卡120元/年，附属卡60元/年，个人普通卡主卡60元/年，附属卡30元/年。公务卡终身免年费，个人卡首年免年费,当年刷卡满3次或累计金额满3000元免次年的年费(取现、转账除外)。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十一条', '取现手续费按笔收取，费率为取现金额的1%，最低3元人民币。境内取现不区分同城异地以及是否跨行。转账视同取现。境外取款按每笔取现金额3%收取，最低20元（人民币）。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十二条', '持卡人累计未还用款金额超过核定信用额度时，按超出部分的5%收取超限费（暂不收取）。计算公式为：超限费=(超限日累计未还用款金额-核定信用额度)×5%。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十三条', '截止到期还款日，如持卡人未还款或还款金额不足最低还款额，则按最低还款额未还部分的5%收取违约金，最低为1元人民币。计算公式为：违约金=（最低还款额-截止到期还款日持卡人已还款额）×5%'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十四条', '持卡人因卡片损坏等原因要求换领新卡的，收取损坏换卡手续费。标准为：20元/卡。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十五条', '持卡人挂失卡片收取挂失手续费30元/卡，补发新卡收取换卡手续费20元/卡。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十六条', '持卡人申请调阅签购单服务的，如经调阅签购单确认交易为持卡人所为的，调单收取手续费25元/份，调阅国外消费签购单收取手续费100元/份。如经调阅签购单查明交易确为他人冒用或记账错误的，则不向持卡人收取本项费用。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第六章  费用管理', '第五十七条', '发卡机构须在《圆鼎贷记卡领卡合约》以及发卡机构网站等渠道明示圆鼎贷记卡的各项收费标准。收费标准变更后，应及时通过相关渠道向持卡人公告。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第七章  账务管理', '第五十八条', '采用质押担保方式的，持卡人的质物由发卡机构网点负责管理。网点必须根据发卡机构通知处置持卡人质物，不得应持卡人要求为其办理支取或结清手续。在持卡人正式销户或收到发卡机构解除质押的通知前，质物不得挂失、不得提前支取。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第七章  账务管理', '第五十九条', '质物为存单或凭证式国库券的，到期后持卡人提出兑付申请的，应要求其先行补充提供相应金额的其他质物。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第七章  账务管理', '第六十条', '省联社每日收到资金清算网络发来的交易清分数据后，根据交易明细对发卡机构账务进行相应处理。持卡人用卡交易的本金在收到交易清分数据的当日记账，取现交易手续费在费用生成当日（即取现交易记账当日）记账，其他费用在费用产生后的首个账单日记账。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第七章  账务管理', '第六十一条', '圆鼎贷记卡每月分两批对所有持卡人上月的账务情况进行结计处理，每月7日、17日为账单日，每个持卡人只能对应一个固定的账单日。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第七章  账务管理', '第六十二条', '每个账单日日终，对持卡人当期（上一账单日次日至当前账单日）的账务进行结计利息和费用汇总处理，计算持卡人本期全部应还款额和最低还款额，确定到期还款日，并生成对账单信息。对当月有交易、费用发生，或虽未发生交易、费用，但账户欠款余额不低于1元人民币的持卡人，发卡机构应在账单日次日发送电子账单等。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第七章  账务管理', '第六十三条', '对持卡人提出疑义的交易款项，按差错交易款项处理。差错交易款项的计息按以下规定执行：1、持卡人对交易款项提出疑义后，已计算的利息及相关费用，无论记账与否，均在差错提出当日做冲回处理，差错交易处理完毕释放差错款项前暂不做计息处理；2、差错交易处理完毕，如确认该交易为持卡人的责任，应补记该笔交易款项自原记账日起计算的利息及相应费用。发卡机构因处理差错交易而向有关机构交纳的费用，从持卡人账户中扣收；3、差错交易处理完毕，如确认该交易非持卡人责任，但最终无法向收单行成功退单的，对该笔交易款项做损失挂账处理。发卡机构因处理差错交易而向有关机构交纳的费用，在业务管理费中列支。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第七章  账务管理', '第六十四条', '对进入司法程序或由公安部门进行追讨的圆鼎贷记卡欠款本息，可自立案日起暂停计算利息。若持卡人欠款本息全部追回，则可对其账户作销户处理，对立案日至还款日之间的利息，能收回的应一次收回，不能一次收回的保留追索权；若只追回部分欠款本息，则应本着“先还息、后还本”的原则，自持卡人还款日起对其未还本金部分恢复计息，同时补记原本金自立案日至还款日间的利息，同时对持卡人保留追索权。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第八章  卡片管理', '第六十五条', '圆鼎贷记卡卡片由省联社统一审定卡面。各发卡机构须在每年1月底前预计全年各卡种发卡量，并提交圆鼎贷记卡订单至省联社，由省联社汇总后统一向生产厂商订购。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第八章  卡片管理', '第六十六条', '贷记卡个人化由省联社负责制作，制卡完成后，负责将卡片封装，并以约投挂号信形式寄送到持卡人指定地址，发卡机构有特殊要求的除外。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第八章  卡片管理', '第六十七条', '省联社负责贷记卡空白卡和已制未寄成品卡的保管，空白卡视同重要空白凭证管理，建立卡片交接登记簿，严格卡片出入库登记，制卡后剩余的空白卡应及时做退库处理，定期对库存卡片数量进行清点登记。成品卡视同现金管理，清点核对后建立成品卡登记簿。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第八章  卡片管理', '第六十八条', '贷记卡作废卡、收回的毁损卡及过期卡等需要销毁的卡片在销毁之前作为重要物品保管，双人以上出库并监督销毁。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第九章  催收管理', '第六十九条', '催收是指当圆鼎贷记卡持卡人未在规定的还款期内偿还最低还款额时，发卡机构根据其欠款时间不同，通过电话、信函、上门或司法等方式向其催讨欠款的过程。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第九章  催收管理', '第七十条', '如截止到期还款日后的第一个账单日，持卡人仍未还款或还款金额不足最低还款额，则为逾期，自次日起进入催收程序，开始计算逾期时间。对进入催收程序的账户每期均应在账单上列明欠款信息，提醒持卡人还款，直至持卡人无逾期款项为止。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第九章  催收管理', '第七十一条', '发卡机构应设立催收岗位，配备相应催收人员，加强催收业务培训和管理，建立催收人员考核激励机制和催收工作信息通报制度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第九章  催收管理', '第七十二条', '催收工作应按照时间先后，先大额后小额，逐一落实每一笔欠款；对催收难度较大且欠款额达到一定金额的，应运用全部催收手段反复催讨，杜绝重易轻难。催收人员必须在尽量追回欠款的同时妥善处理好与持卡人的关系，不得损害持卡人的合法权益。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第九章  催收管理', '第七十三条', '根据账户逾期时间的不同，催收人员应采取电话催收、信函催收、上门催收、司法催收等不同催收方式。对于资信情况或财务情况恶化的持卡人，应不受账户逾期时间所限，尽早上门催收或进行司法催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第九章  催收管理', '第七十四条', '发卡机构可根据持卡人账户逾期情况调减其卡片信用额度。对逾期三个月的，系统自动对卡片进行止付处理，并将持卡人列为不良客户管理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第九章  催收管理', '第七十五条', '逾期六个月的账户欠款，自次日起转为呆账贷款，此后不再产生利息和违约金。符合核销规定的呆账贷款，由发卡机构整理提供相关申报材料后，按规定核销。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第七十六条', '发卡机构对符合条件的持卡人提供到期续卡服务。有以下情况之一的卡片，不得进行到期续卡：1、有效期内从未使用过的；2、持卡人当前逾期时间超过一个月的；3、状态不正常的卡片，包括已挂失、已销户、已止付等各种非正常状态的卡片；4、持卡人已申请卡片到期不续发的；5、其他不应续卡的情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第七十七条', '圆鼎贷记卡遗失或被盗的，应及时到发卡机构就近网点或通过贷记卡客服热线办理书面挂失或电话挂失。电话挂失和书面挂失均视为正式挂失，立即生效。挂失生效前所发生的一切经济损失由持卡人承担，芯片贷记卡电子现金账户不挂失。挂失换卡后，新卡继承旧卡全部债权债务关系，但是新卡电子现金账户余额为0。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第七十八条', '贷记卡损坏的，持卡人可申请损坏换卡，经发卡机构确认卡片损坏的，发卡机构收回损坏卡后，由发卡机构柜员对原卡电子现金账户进行余额转移登记，30天后，系统自动将原卡电子现金账户余额转移至持卡人主账户。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第七十九条', '挂失/损坏补发的新卡有效期一般应与原卡相同，若旧卡距到期的时间在三个月以内，经审核具备到期换卡条件，补发的新卡有效期可适当调整，但最长不超过5年。以下情况不予补发新卡：1、持卡人当前逾期时间超过一个月的，须待正常还款后方可补发；2、卡片已被发卡机构停用的；3、其他不应补发的情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第八十条', '对符合条件的优质持卡人，发卡机构可应其申请或主动将其所持贷记卡进行升级：1、持卡人职业、职务、收入等基本情况发生显著变化，已达到上一等卡片申请标准，且用卡及还款记录正常；2、持卡人持有卡片时间在两年以上，用卡及还款记录正常，年均交易额（消费和取现）达到现持有卡种最高授信额70%以上的；3、同意办理卡片升级的，应要求持卡人在收到高等级圆鼎贷记卡卡片后退回其原持有的圆鼎贷记卡；4、升级后的卡片有效期为自同意升级发卡月份起5年，并按高等级卡片标准记收年费。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第八十一条', '持卡人对账务变动情况有疑义的，可在规定时间内向发卡机构提出查询或更正要求。对超过规定时间提出查询或更正要求的，发卡机构根据实际情况进行处理。发卡机构在收到持卡人否认交易或对交易金额有异议的投诉后，应根据持卡人提出疑义交易的清算信息来源确定差错处理机构，按照该交易种类和相应机构规定的条件、程序，向收单行提出调单要求，必要时还可依照有关规定提请争议处理。处理结果应及时通知持卡人。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第八十二条', '持卡人在贷记卡卡片有效期内或到期后不再使用卡片的，并在清偿贷记卡账户所有欠款后向发卡机构申请销户的，发卡机构应在受理销户的同时对芯片贷记卡电子现金账户进行余额转移登记（30天后电子现金账户余额自动转入贷记卡主账户），发卡机构在受理销户申请45天后为持卡人办理正式销户，发卡机构对已收的年费不予退还。办理正式销户后，发卡机构继续保留对持卡人之前或之后发生的贷记卡账户的追索权，并受理持卡人还款。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十章  特殊业务管理', '第八十三条', '主卡持卡人申请注销附属卡的，应要求主卡持卡人退回申请停用的卡片。不能退回卡片的，主卡持卡人对该卡发生的有效交易款项及相应的利息、费用等继续负有偿还责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第八十四条', '发卡机构应对圆鼎贷记卡业务经营过程中的持卡人信用风险、伪冒欺诈风险以及操作风险等进行预警识别、防范控制，对已发生的风险损失进行分析和认定化解，以合理控制风险、保证业务健康发展。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第八十五条', '为防范因持卡人信用不良而导致的持卡人信用风险和减少损失，应做到：1、严格征信审核工作，对不符合规定条件的申请人不予发卡；2、建立持卡人信用风险评估制度和风险预警系统，设置风险监控指标，及时掌握和了解持卡人的资信变化、用卡和还款情况，分析持卡人拖欠原因，定期提出持卡人资信分析报告；3、对持卡人卡片升级、额度调整、到期换卡等严格审核；4、对逾期欠款加强催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第八十六条', '发现主卡持卡人失踪、死亡、丧失还款能力，以及非境内公民持卡人申请个人破产等情况，应立即采取相应措施减少损失。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第八十七条', '持卡人连续三个月不偿还最低还款额的，须对其名下所有卡片止付，并采取相应的债务追索措施；采用担保方式办卡的，可要求其保证人履行保证责任或用其质物冲抵欠款。必要时还应提请司法机关追究持卡人的法律责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第八十八条', '对可能是伪卡或冒用卡的情况，应立即对该卡进行监控，与持卡人联系核实有关情况并进行相应处理，尽量减少损失。对通过调查取证等方式确定卡片被伪造或被冒用的，应及时采取措施，必要时可申请立案。发现卡片被伪造、变造的应及时通报。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第八十九条', '持卡人如有利用圆鼎贷记卡进行欺诈等恶意行为，须对其圆鼎贷记卡止付，授权特约商户、取现网点收回该卡，并采取措施追回欠款，必要时可提请司法机关追究持卡人的法律责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第九十条', '对因套现或欺诈等原因被发卡机构停用的卡片，应及时上报省联社。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第九十一条', '开办圆鼎贷记卡业务的发卡机构应严格内部岗位分工，重要岗位不得混岗操作；实行操作权限的分级管理，各级人员只能在设定权限内操作，不得越限操作。贷记卡系统操作员须报省联社备案，由省联社在卡系统中设置各级操作权限。贷记卡信审系统操作员由各发卡机构管理员设置，各发卡机构管理员须报备省联社备案，由省联社在贷记卡信审系统中设置。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第九十二条', '省联社与发卡机构之间应建立风险信息的及时快速共享机制。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第九十三条', '发卡机构均应建立风险报告制度，定期分析业务发展中遇到的风险情况，提出防范和改进措施。对重大案件建立一案一报的专题报告制度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第九十四条', '根据持卡人潜在还款风险的不同，对圆鼎贷记卡的持卡人欠款质量实行五级分类管理。五级分类标准按照省联社有关规定执行。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十一章  风险管理', '第九十五条', '对业务发展中发生的各种风险损失，按照省联社下发的《江苏省农村信用社联合社关于做好呆账核销管理工作的指导意见》要求及发卡机构有关文件的规定进行核销处理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十二章  附则', '第九十六条', '本办法未尽事宜依照国家有关政策、法律、法规及江苏省农村信用社联合社的有关规定办理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十二章  附则', '第九十七条', '本办法由江苏农村商业业银行股份有限公司负责解释和修订。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务管理办法》', '第十二章  附则', '第九十八条', '本办法自印发之日起实施。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第一章  总则', '第一条', '为加强农村商业银行(以下简称“本行”)圆鼎贷记卡业务管理，提高贷记卡资产质量，防范和化解金融风险，根据《圆鼎贷记卡业务管理办法》、《圆鼎贷记卡业务操作流程》规定，制定本办法。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第一章  总则', '第二条', '圆鼎贷记卡业务风险管理实行统一管理、分级负责、规范运作的原则，重点防范信用风险和欺诈风险。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第三条', '本行信息科技部负责配合省联社科技部门做好贷记卡系统的维护与管理、制定与实施应用级技术安全策略、交易欺诈的监测，负责本行贷记卡数据的提供等技术管理工作。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第四条', '本行运营管理部负责贷记卡会计业务管理和培训，负责制定、修改、完善贷记卡会计业务操作规程等工作。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第五条', '本行合规管理部负责贷记卡各项规章制度及业务的合规审查，监督圆鼎贷记卡业务各项规章制度、规定的执行情况。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第六条', '本行风险管理部负责贷记卡业务的风险监测、识别、控制管理工作。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第七条', '本行审计部负责对贷记卡运行、管理、内部控制、考核等情况进行稽核监督。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第八条', '本行授信评审部负责贷记卡的风险评估和额度授信工作。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第九条', '本行电子银行部负责圆鼎贷记卡的资料审查、资信审查、透支催收、不良资产处置等工作。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第二章  组织管理体系', '第十条', '根据发卡审批和透支管理两个流程，总行设立复查岗、审批岗、风险控制岗、电话催收岗、信函催收岗、司法催收岗；支行设立受理岗、调查岗、审查岗、上门催收岗等岗位。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第三章  业务流程', '第十一条', '支行受理岗负责对申请人资料的完整性、真实性进行审核。(一)对申请人的审核1．负责核对资料的真实性，申请表填写内容应与所附证明文件一致。2．审核申请表上内容是否填写完整、字迹是否清晰、签字及印章是否齐全。3．负责审核申请人所提供的证明材料是否齐全。4．审核是否符合申办年龄，户籍及工作是否在当地，职业是否稳定。5．收入水平是否在当地城镇人均水平以上。6．不具备免担保条件的，需提供担保。港澳台居民、外国公民需提供经公安机关认可的在本地合法居留的证明文件。(二)对担保的审核1．保证：保证人年龄在22周岁至60周岁之间，具有完全民事行为能力，无不良信用记录；具有发卡机构所在地户籍，并在当地有自有住房；工作单位在发卡机构所在地，从事非限制行业，合法收入在当地平均水平以上；保证人与申请人不为夫妻关系。2．质押：人民币、外币质押物限于发卡机构辖内网点签发的自动转存的整存整取定期存单。3.抵押：抵押物只限于担保法规定的易于处理的，抵押人可自主支配的不动产。(三)对信用额度申请的审核审核持卡人填写的《圆鼎贷记卡申请表》是否正确，所需证明材料是否齐全。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第三章  业务流程', '第十二条', '支行调查岗对受理岗提供的所有申请材料进行确认，对申请人资料是否符合标准以及真实性负责。(一)对申请人及担保人的调查1．电话核实申请人填写的工作单位内容是否真实，职业情况如何(应避免向申请人直接询问)。2．电话核实申请人填写的住宅电话以及住宅地址的真实性。3．电话核实保证人、联系人资料是否属实(保证人、联系人是否同意作为申请人的保证人、联系人，保证人是否本人签名)；联系人是否符合条件。4．对申请人电话核实无法确认的，可采用信函方式核实，即要求申请人收到信函后回电联系，以便发卡机构进一步调查。5．对申请人提供真实的收入证明和资产证明进行验证，可根据申请人行业和企业收入水平测算，估测的依据为每年发卡机构对当地主要行业和企业人员的收入评估汇总表。6．对申请人的年收入、资产情况、职业稳定性进行综合评价，提出申请人信用等级建议。7．对担保人资格的调查，下列人员不得作为担保人：与申请人是配偶关系；或有负债过高的人；士兵、军官、本地户口在外地工作者；离退休、下岗人员和已申请出国、出境者；其他法律规定的不可作为保证人的。(二)信用额度调整1．长期调高信用额度审核持卡人填写的《圆鼎贷记卡申请表》；审核账户是否启用一年以上且两次提升信用额度的时间间隔不少于180天，账户是否正常使用且无不良记录，持卡人职业、收入等基本情况是否发生良性变化。2．临时调高信用额度客户因紧急需要临时要求调整信用额度的，需拨打客户服务热线4008296008提出申请。审核客户提供的相关信息，账户是否正常使用且无不良记录，必须告知持卡人同意额度和使用期限，临时调高信用额度一般不超过持卡人原信用额度的10%且有效期最长为30天。3．对本行掌握的用卡消费额较大、还款情况良好的优质客户，可以主动建议持卡人增加授信额度，操作程序同上，但提升的信用额度最高不超过原信用额度的50%。4．持卡人委托他人代办的，要有持卡人本人签署的委托书和本人的身份证，以及受托人的身份证，经确认后方可受理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第三章  业务流程', '第十三条', '支行审查岗负责审查调查结论是否正确，过程是否规范，并作出审查结论。(一)申请人及担保人审查1．通过人行征信系统、身份联网核查系统或向其他相关部门核实申请人身份证，户籍是否属实及申请人是否有不良记录。2．查询综合业务系统、企业信息管理平台数据资料，核实申请人是否有不良记录。3．对已在本行办理个人住房贷款，汽车消费贷款的，须核实合同的真伪，以及是否有违约记录，并签署查证意见。4．对重点营销的优质公司客户及其主要管理人员的申请人，须向系统内有关业务部门进行查询，并签署查询意见。5．审查申请人年收入估算结果，审查申请人资产情况，审查申请人就业收入稳定性，审查调查岗核实结果是否正确，是否存在遗漏内容，是否签注意见和签字确认。6．对调查岗提出的申请人信用等级和授信额度进行复审，提出建议。(二)对授信额度调整的复审1．复审持卡人是否符合授信额度调整条件，是否提供相关收入或资产证明，调查岗是否签注调整意见，对审查同意调整的应签字确认。2．对临时调整授信额度的，在到期前检查透支额是否在原额度内，负责在规定时间内调回到原来的额度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第三章  业务流程', '第十四条', '总行复查岗负责对申请人资料的完整性和表面相符进行审核。(一)对申请人申请内容进行电话核实。(二)对担保人担保情况进行电话核实。(三)信用额度调整申请的审核。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第三章  业务流程', '第十五条', '总行审批岗对发卡的审批建议负责。(一)贷记卡发卡审批1．对是否批准办卡申请作出建议结论。2．根据风险情况定期建议调整贷记卡准入条件及额度发放标准。3．建议首次办理贷记卡的等级和信用额度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第三章  业务流程', '第十六条', '总行风险控制岗职责（一）对申请人的信用调查和评估，把握从源头控制风险的原则对申请人进行调查审核，通过综合评估提供发卡建议。（二）根据持卡人申请、持卡人消费行为及相关规定对其账户信用额度的调整签署意见，交授信评审部最终审批。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第四章  风险监控', '第十七条', '总行风险控制岗要对持卡人账户进行动态监控，经常分析和掌握持卡人使用情况，及时发现欺诈交易信息，降低信用风险和欺诈风险。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第四章  风险监控', '第十八条', '及时了解单位卡和集体办卡所属企业的情况，关注企业法人外部市场环境的变化、技术的发展、生产经营状况以及涉诉案件的进展情况，掌握影响偿还透支本息的因素，快速应对出现的风险。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第四章  风险监控', '第十九条', '按照透支分类进行管理，根据风险状况及特点采取有针对性的管理措施。对正常透支，要注意加强风险预警，不能放过任何一个可疑因素；对关注类透支，要密切跟踪潜在风险因素的变化情况，采取有效手段；对次级类透支，要加强透支本息的催收，密切注意贷款保证及质押物情况的变化。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第四章  风险监控', '第二十条', '建立透支分析制度，定期撰写圆鼎贷记卡风险评估分析报告向行长室报告。分析报告内容必须覆盖以下内容：圆鼎贷记卡业务运行特点、风险状况分析、风险防范措施有效性、清收不良资产采取的措施及遇到的主要问题、加强信用风险防范的建议或对策等。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第四章  风险监控', '第二十一条', '严格进行不良持卡人账户和信息管理，落实账户冻结及卡片止付规定。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第四章  风险监控', '第二十二条', '与省联社电子银行部建立风险信息的共享机制。对重大案件建立一案一报的专题报告制度。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第五章  风险种类及风险资产分类', '第二十三条', '贷记卡业务风险分为：操作风险、信用风险、欺诈风险。操作风险是指工作人员在贷记卡业务处理过程中，违规或违法操作给银行造成损失的可能性。信用风险是指贷记卡持卡人不愿偿还或无力偿还，不能按时足额归还透支本息而给银行资产带来损失的可能性。欺诈风险是指不法分子恶意透支、骗领、冒用、使用伪造、变造或作废的银行卡以及特约单位诈骗给银行造成经济损失的可能性。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第五章  风险种类及风险资产分类', '第二十四条', '采用“M0-M6+”的分类标准对贷记卡透支资产的风险程度进行认定，根据逾期期限确定逾期资产的风险等级。认定逾期资产的起点(即M1的起点)是“第一次延滞日”，最迟不得超过“第二个账单日”。延滞是指在到期还款日，未偿还或未足额偿还最低还款额的行为；而“第一次延滞日”是指第一次延滞所属的到期还款日。M0- M6+的具体规定如下：M0是指未到到期还款日或足额归还最低还款额；M1是指未足额归还最低还款额，且逾期期限为1至30天(含)；M2是指未足额归还最低还款额，且逾期期限为31至60天(含)；M3是指未足额归还最低还款额，且逾期期限为61至90天(含)；M4是指未足额归还最低还款额，且逾期期限为91至120天(含)；M5是指未足额归还最低还款额，且逾期期限为121至150天(含)；M6是指未足额归还最低还款额，且逾期期限为151至180天(含)；M6+是指未足额归还最低还款额，且逾期期限为181天(含)以上；对于已归还对账单通知的最低还款额的逾期资产，重新转期为M0。与五级分类标准的对应关系“M0-M6+”的分类标准与五级分类标准之间的对应关系为：M0为正常，M1-M3为关注，M4为次级，M5-M6为可疑，M6+为损失。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第六章  风险责任管理', '第二十五条', '对损失类透支资产，由总行对损失资产责任进行认定，根据风险责任确定损失赔偿比例，按照损失赔偿比例落实赔偿到相关责任人。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第六章  风险责任管理', '第二十六条', '风险责任认定原则参照《不良信贷资产责任追究管理办法》相关规定执行。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第七章  催收流程', '第二十七条', '圆鼎贷记卡透支催收实行以电话催收为主、法律催收为辅的高效催收机制，主要形式有四种，即电话催收、信函催收、上门催收、司法催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第七章  催收流程', '第二十八条', '催收流程是指根据圆鼎贷记卡透支的风险程度采取不同的催收形式催收的动态过程。催收流程一般按照电话催收、信函催收、上门催收、司法催收的顺序进行，对突发性的严重的透支风险可以直接采取严厉的催收方式。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第七章  催收流程', '第二十九条', '总行电话催收岗负责对正常和关注类的透支账户进行催收。省联社客户服务中心每月20日根据本行提供的月逾期清单，进行电话协助催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第七章  催收流程', '第三十条', '总行信函催收岗负责次级类的透支催收，以及电话联系不上或连续三次电话催收无效的透支催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第七章  催收流程', '第三十一条', '支行上门催收岗及总行司法催收岗负责可疑和损失类的透支催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第七章  催收流程', '第三十二条', '总行电子银行部负责监控整体透支风险状况，最大限度地增加良性透支规模，对发卡机构资产业务的安全性、流动性、效益性负责。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第三十三条', '圆鼎贷记卡核销管理包括透支呆账的认定、呆账准备的计提及核销处理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第三十四条', '经采取所有可能的措施和必要程序之后，仍无法收回的贷记卡透支款项可认定为呆账。具体认定情形参照《信贷资产风险分类实施细则》。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第三十五条', '贷记卡透支呆账准备包括一般准备和透支损失准备。其中，透支损失准备是指发卡机构对各项透支预计可能产生的损失计提的损失准备。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第三十六条', '对贷记卡透支应计提一般准备和透支损失准备。其中，一般准备按照银行根据所有承担风险和损失的资产风险状况确定的比例提取，原则上一般准备余额不低于风险资产期末余额的1%。透支损失准备的提取标准为：关注类(M1-M3)计提比例为2%，次级类(M4)计提比例为25%，可疑类(M5-M6)计提比例为50%，损失类(M6+)计提比例为100%。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第三十七条', '每个季末，根据当季透支资产的透支期限进行五级分类，并对透支损失准备总额进行差额调整。次级类和可疑类透支资产的透支损失准备，计提比例可以上下浮动20%。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第三十八条', '透支呆账准备以原币种计提，即人民币透支以人民币计提透支呆账准备，外币透支以外币计提透支呆账准备，人民币和外币准备分别核算和反映。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第三十九条', '发生呆账冲减已计提的呆账准备。已冲销的呆账，以后又收回的，其核销的呆账准备予以转回。超过本金的部分，先还利息再还费用，利息计入利息收入科目，费用计入其他银行卡业务收入科目。对于已经确认的欺诈损失、或者由于持卡人失踪死亡或破产造成的损失，即使逾期未达6个月，也应该在损失确认当季末划分为损失类，全额税前提取准备金。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第四十条', '透支呆账准备采用每季末集中提取方式。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第四十一条', '贷记卡透支呆账准备的提取和使用应纳入银行呆账准备统一管理。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第四十二条', '年度损失率=(年末损失类透支资产余额+当年累计核销总额-上年末损失类透支资产余额)/当年透支资产月末平均余额，将年度损失率8%作为衡量贷记卡业务风险状况的行业参考标准。对于年度损失率不超过8%(含)的发卡机构，可比照本办法进行核销。对于年度损失率超过8%的发卡机构，单户透支10000元(含10000元，含等值外币)以下的账户，可比照本办法进行核销。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第四十三条', '贷记卡透支呆账核销的原则：严格核销条件，提供确凿证据，逐户办理审批，账销案存。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第八章  核销管理', '第四十四条', '对符合核销条件的贷记卡透支呆账必须提供确凿的证据，并组织申报以下材料。(一)持卡人资料，包括申请表及审批材料、透支发生明细材料、持卡人和担保方式的基本情况和现状、财产清偿情况等。(二)调查报告，包括透支呆账和其他损失形成的原因，具体追收经过，采取的补救措施及结果，核销的理由，发卡的有关经办人、部门负责人和单位负责人情况等。(三)其他相关材料1．逾期期限为181天(含)以上(即M6+)的，须提交6次以上的催收记录，包括电话催收记录和上门催收记录等。2．持卡人和保证人经依法宣告破产，财产经法定清偿后，未能还清的透支款项，须提交法院破产证明和财产清偿证明。3．持卡人和保证人死亡或经依法宣告失踪、死亡，以其财产或遗产清偿后，未能还清的透支款项，须提交失踪或死亡证明和财产或遗产清偿证明。4．诉讼或仲裁并经强制执行程序后，仍无法收回的透支本息，须提交判决书或仲裁书和强制执行书证明。5．贷记卡因被伪造、冒用、骗领、商户诈骗等欺诈原因发生的应由银行承担的净损失，须提交公检法部门出具的法律证明材料或其他具有法律效力的文件；对于无法提供法律证明材料的，无论金额大小，一律须报省联社备案。对于境外发生的上述欺诈，由跨国信用卡公司提供的交易记录、交易单据和有关操作规定可作为参考证明材料。6．本行工作人员在业务操作中因失误或其他原因造成损失的，除向责任人员追偿外，须提交对有关责任人员的处理情况报告。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第九章  附则', '第四十五条', '本办法由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《圆鼎贷记卡业务风险管理办法（试行）》', '第九章  附则', '第四十六条', '本办法自印发之日起实施。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第一章  总则', '第一条', '为加强农村商业银行（以下简称“本行”）圆鼎贷记卡业务管理，完善业务风险控制机制，规范透支催收操作流程，根据《圆鼎贷记卡业务管理办法》和《圆鼎贷记卡业务操作流程》，制定本办法。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第一章  总则', '第二条', '本办法所称的催收，是指当圆鼎贷记卡持卡人未在规定的还款期内偿还最低还款额时，本行根据其欠款时间不同，通过电话、信函、上门或司法等方式向其催讨欠款的过程。圆鼎贷记卡催收是圆鼎贷记卡资产业务风险管理的重要组成部分，是经营风险控制的主要手段。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第二章  催收形式', '第三条', '圆鼎贷记卡催收实行以电话催收为主、司法催收为辅的高效催收机制，主要形式有四种，分别为：电话催收、信函催收、上门催收、司法催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第二章  催收形式', '第四条', '电话催收可分为单向提示催收和电话谈判催收。单向提示催收是指用手机短信、电话语音、电子邮件等方式提示客户，客户无须回应；电话谈判催收是指与客户在电话中协商约定具体还款计划，利用贷记卡管理系统监控客户还款承诺是否兑现。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第二章  催收形式', '第五条', '信函催收是指通过向客户寄送《圆鼎贷记卡逾期催收通知书》进行催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第二章  催收形式', '第六条', '上门催收是指催收人员到客户单位或家中当面催收并落实还款计划。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第二章  催收形式', '第七条', '司法催收是依照有关法律，通过公安部门向客户追讨或向法院提起法律诉讼追索欠款。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第三章  透支资产风险分类标准', '第八条', '采用“M0-M6+”的分类标准对信用卡透支资产的风险程度进行认定，根据逾期期限确定逾期资产的风险等级。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第三章  透支资产风险分类标准', '第九条', '透支资产风险分类认定逾期资产的起点(即M1的起点)是“第一次延滞日”，最迟不得超过“第二个账单日”。延滞是指在到期还款日，未偿还或未足额偿还最低还款额的行为；而“第一次延滞日”是指第一次延滞所属的到期还款日。M0- M6+的具体规定如下：M0是指未到到期还款日或足额归还最低还款额；M1是指未足额归还最低还款额，且逾期期限为1至30天(含)；M2是指未足额归还最低还款额，且逾期期限为31至60天(含)；M3是指未足额归还最低还款额，且逾期期限为61至90天(含)；M4是指未足额归还最低还款额，且逾期期限为91至120天(含)；M5是指未足额归还最低还款额，且逾期期限为121至150天(含)；M6是指未足额归还最低还款额，且逾期期限为151至180天(含)；M6+是指未足额归还最低还款额，且逾期期限为181天(含)以上；对于已归还对账单通知的最低还款额的逾期资产，重新转期为M0。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第三章  透支资产风险分类标准', '第十条', '与五级分类标准的对应关系“M0-M6+”的分类标准与五级分类标准之间的对应关系为：M0为正常，M1-M3为关注，M4为次级，M5-M6为可疑，M6+为损失。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第四章  催收流程', '第十一条', '催收流程是指根据圆鼎贷记卡透支的风险程度采取不同的催收形式催收的动态过程。催收流程一般按照电话催收、信函催收、上门催收、司法催收的顺序进行，对突发性的严重的透支风险可以直接采取严厉的催收方式。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第五章  催收岗位设置及规范', '第十二条', '透支催收按照风险程度，总行设置电话催收岗、信函催收岗及司法催收岗、催收主管岗；支行设置上门催收岗。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第五章  催收岗位设置及规范', '第十三条', '电话催收岗职责(一)岗位职责总行电子银行部负责对关注类的透支账户进行催收。(二)电话催收规范1．对重点透支户和超限透支户应重点催收，当月新增逾期户应及时催收。每月对透支逾期户至少催收1次。2．在实际催收工作中，应灵活使用持卡人、担保人的家庭电话、办公电话和移动电话，区分不同时间、场合、接听人，采取适宜的语气和措辞提醒、督促持卡人或担保人尽快偿还所欠款项。3．催收工作应有理有节，并合理解答持卡人对账户交易情况的疑问。对正常类的透支，主要应使用感谢持卡人用卡，提醒还款的语气进行；对关注类的透支，要明确告知尽快还款，以维护良好用卡记录。4．要求持卡人偿还的款项不得低于系统计算的“最低还款额”，并告知所欠本金、利息以及还款时间差可能产生新的利息。5．在透支台账上逐户登记催收情况，要做到言简意赅、及时准确。6．对于多次承诺还款后仍违约的持卡人，或催收过程中认为具有较大风险而要重点监控的账户，应设置重点监控标志。7．对于严重违约需要取消用卡资格的，要马上提醒总行电子银行部对其进行止付。8．对电话联系不上，电话催收后连续3个月未足额偿还“最低还款额”的，应尽快转入下一催收流程。9．下一催收流程可选择信函催收、上门催收或司法催收等方式，对透支本金在1000元(含)以内的，应首先转入信函催收；对透支本金在1000元以上的，可直接转入上门及司法催收；对有犯罪嫌疑或重大风险隐患的，应及时转入上门及司法催收。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第五章  催收岗位设置及规范', '第十四条', '信函催收岗职责(一)岗位职责总行电子银行部负责次级类的透支信函催收，以及电话联系不上或连续三次电话催收无效的透支催收。(二)信函催收规范1．结合透支情况和电话催收情况，尽快邮寄催收函。2．对于信函被退回等催收情况及时登记，负责将地址变更的情况在系统中进行相应的修改。3．对于发出信函后30日内未还款或未与银行联系的，应立即转入上门及司法催收流程。4．对于严重违约需要取消用卡资格的，应立即提请催收主管对其进行止付。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第五章  催收岗位设置及规范', '第十五条', '上门及司法催收岗职责(一)岗位职责各支行负责可疑和损失类的透支上门催收，总行电子银行部负责可疑和损失类的透支司法催收，对透支本金在1000元以上的，可直接转入上门及司法催收。(二)上门及司法催收规范1．对诉讼时效预警的账户应立即进行催收，防止因诉讼时效届满丧失诉权，尽量挽回经济损失。2．重点透支户和超限透支户应重点催收。3．上门催收应尽量取得持卡人或担保人签字的催款回执。只有确实取得了足以使诉讼时效中断的证据后，才可在透支台账中填写有效送达日期。4．对于一次还清所有透支本息有困难的，应要求持卡人或担保人出具还款计划书。操作员在风险可控的前提下，掌握还款计划书中每次偿还的款项，但要求持卡人每月偿还的款项不得低于系统计算的“最低还款额”。5．对于严重违约需要取消用卡资格的，要立即向总行电子银行部提请对其进行止付。6．对临近有效诉讼日期仍未与持卡人和担保人取得联系的，或上门催收后1个月未足额偿还“最低还款额”的，应立即通过司法手段主张权利。7．对符合呆坏账及其他损失核销条件的，及时组织材料，准备申报核销。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第五章  催收岗位设置及规范', '第十六条', '催收主管职责(一)岗位职责总行电子银行部催收主管负责监控整体透支风险状况，把不良透支占比控制在规定指标内，最大限度地增加良性透支规模。对本行贷记卡资产业务的安全性、流动性、效益性负责。负责提供诉讼相关材料。(二)具体规范1．负责催收人员岗位设置和管理。2．负责监督催收人员依岗履职。3．提出风险管理建议。4．负责调配各角色催收人员工作任务。5．对贷记卡连续2次未足额偿还最低还款额的，应及时判断是否有必要对其进行止付处理。对足额偿还透支本息后仍要求继续持卡的客户，经过再次审核确认能够达到发卡机构要求的，应进行撤止处理。6．负责考核催收人员的工作业绩。7．每季召开工作例会，交流透支催收情况，总结经验，通报催收进度，分析风险状况。8．随时监控辖内透支风险状况，定期向行长室提出分析报告和工作计划。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第六章  催收要求', '第十七条', '短信催收，需要提供短信通知的截屏照片，截屏应可以看出短信发送时间及短信收件人的手机号码。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第六章  催收要求', '第十八条', '电话催收，需要提供催收的电话录音并说明催收时间。贷记卡持有人电话无法打通，需要对拨打电话的手机录制视频，视频中应可以看出电话拨打时间及联系的电话号码，并可以听出手机传出无法接通的提示音。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第六章  催收要求', '第十九条', '上门催收，需要提供现场照片，照片影像中包括贷记卡持有人或持有人的直系亲属，并注明拍摄时间。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第六章  催收要求', '第二十条', '贷记卡营销管理机构每月应将催收资料(纸质或电子档)及时报送至电子银行部。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第七章  附则', '第二十一条', '本办法由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《圆鼎贷记卡催收管理办法（试行）》', '第七章  附则', '第二十二条', '本办法自印发之日起实行。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第一章  总则', '第一条', '为向社会提供优质的消费信用和支付结算服务，保护贷记卡业务各方当事人的合法权益根据国家有关法律、法规、规章制度，特制定本章程。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第一章  总则', '第二条', '圆鼎贷记卡品牌是由江苏省各农村商业银行委托江苏省农村信用社联合社申请，供委托单位发行使用的统一贷记卡品牌，各委托单位为发卡机构。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第一章  总则', '第三条', '圆鼎贷记卡是发卡机构向社会公众公开发行，给予持卡人一定信用额度，持卡人可在信用额度内透支使用，具有消费、存取现金、转账结算、分期交易等全部或部分功能的支付结算工具。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第一章  总则', '第四条', '圆鼎贷记卡为人民币银联标准卡，按信息载体不同可以分为磁条卡、纯芯片卡、磁条芯片复合卡（以下纯芯片卡和磁条芯片复合卡统称为芯片卡）。芯片卡具备电子现金支付功能,包括小额脱机消费、圈存、圈提、查询等;信息存储功能,包括记录持卡人的基础信息和相关行业应用信息等。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第一章  总则', '第五条', '持卡人个人名下的多个贷记卡账户授信额度、各种分期付款汇总授信额度、附属卡授信额度等受发卡机构对持卡人设定的总授信额度上限控制。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第一章  总则', '第六条', '发卡机构、持卡人、担保人均须遵守本章程。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第一章  总则', '第七条', '本章程涉及的部分名词遵从如下定义：申请人：指向发卡机构申请贷记卡的申请者本人，如无特殊说明，包括主卡申请人和附属卡申请人。持卡人：指向发卡机构申请个人卡并获得卡片核发的申请者本人，包括主卡持卡人和附属卡持卡人。贷记卡卡片仅限持卡人本人使用，不得出租和转借。信用额度：也称账户授信额度，指发卡机构根据申请人资信情况等为其核定的在圆鼎贷记卡有效期内可循环使用、向发卡机构透支的最高限额。卡类别：发卡机构根据圆鼎贷记卡持卡人资信情况、持卡人享受特种优惠、持卡人账务处理等方面的不同而确定的产品分类。交易日：指持卡人实际消费、存取现、转账交易或与相关机构实际发生交易的日期。银行记账日：指发卡机构在持卡人发生交易后将交易款项记入其圆鼎贷记卡账户，或根据规定将有关费用、利息记入其圆鼎贷记卡账户的日期。账单日：指发卡机构每月对持卡人的累计未还消费交易本金、取现交易本金、费用等进行汇总，结计利息，并计算出持卡人应还款额的日期。到期还款日：指发卡机构规定的持卡人应该偿还其全部应还款额或最低还款额的最后日期。实际还款日：指持卡人以存现、转账等方式向发卡机构偿还其欠款的日期，以发卡机构收到客户还款资金的实际日期为准。全部应还款额：指截至当前账单日，持卡人累计未还的费用、交易本金以及利息等的总和。最低还款额：指发卡机构规定的持卡人透支后应该偿还的最低金额，包括累计未还消费本金的一定比例，所有利息、超过信用额度的透支金额、取现本金和费用，以及以前月份最低还款额未还部分的总和，持卡人办理分期付款的，分期付款本期入账本金和本期入账手续费全额计入当期最低还款额。免息还款期：指持卡人于银行记账日至到期还款日（含）前偿还全部应还款额的前提下，可享受免息待遇，其交易记账日至到期还款日期间为免息还款期。溢缴款：是指持卡人实际还款金额高于全部应还款额的部分，该部分金额不计息。违约金：指发卡机构与持卡人约定的，持卡人到期还款日（含）前还款金额不足最低还款额时，应支付给发卡机构的费用。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第二章  申领条件和手续', '第八条', '具有完全民事行为能力，有合法、稳定收入来源且资信良好的个人均可凭本人有效身份证件和相关资料向发卡机构申领个人卡主卡，还可为其他具有完全民事行为能力或限制民事行为能力的自然人申领附属卡。附属卡申请材料必须由主卡持卡人和附属卡持卡人亲自签名确认。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第二章  申领条件和手续', '第九条', '附属卡持卡人和主卡持卡人共享同一账户信用额度，主卡持卡人可在账户信用额度内设定附属卡信用额度，附属卡所有交易款项及相应利息、费用等均计入主卡账户。主卡持卡人对主卡及附属卡项下发生的债务承担全部清偿责任。具有完全民事行为能力的附属卡持卡人对主卡及附属卡项下发生的债务互相承担连带清偿责任；具有限制民事行为能力的附属卡持卡人只对其所持附属卡发生的债务承担清偿责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第二章  申领条件和手续', '第十条', '申请人申领圆鼎贷记卡时，应按发卡机构要求，正确、完整、真实地填写申请表并本人亲自签名确认，呈交资信证明等证明文件。申请人在申请表上签字确认即表示其已完全获知并同意遵守申请表、本章程与领用合约、收费表及其后的修订版本中的各项规定。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第二章  申领条件和手续', '第十一条', '为审核申请人的贷记卡申请或进行后续风险管理或为申请人提供综合化服务的目的，申请人同意并授权发卡机构在审核其贷记卡申请或进行后续风险管理或为其提供综合化服务的过程中向中国人民银行金融信用信息基础数据库等依法设立的征信机构及其他相关合法机构了解和查询其资产、资信、个人信用信息等情况，并保留相关资料。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第二章  申领条件和手续', '第十二条', '发卡机构根据申请人的资信状况，决定是否同意领卡申请、是否要求申请人提供担保及担保方式、确定领卡方式、领卡种类以及授信额度。发卡机构将妥善保管申请资料，并履行相应保密义务，未通过发卡机构审批的申请资料不予退回。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第二章  申领条件和手续', '第十三条', '担保方式包括保证、抵押或质押。担保范围为持卡人圆鼎贷记卡项下发生的全部债务（包括但不限于信用额度内透支及超信用额度透支的本金、利息和费用）。采用担保形式的，相关当事人应另行签订担保合同。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第十四条', '持卡人在领取圆鼎贷记卡后，应立即在卡背面签名栏内签上与申请表相同的签名，并在交易时使用相同的签名，否则，由此产生的损失由持卡人负责。持卡人可通过发卡机构指定途径设置查询密码（适用于客户服务热线电话语音确认身份）、交易密码。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第十五条', '凡使用密码进行的交易，本行均视为持卡人本人所为，签名仅作为持卡人认可交易的辅助措施，不作为风险责任划分依据，持卡人应妥善保管密码。依据密码等电子数据信息办理的各类结算交易所产生的电子信息交易记录均为该项交易的有效凭证，持卡人应承担不慎泄露密码造成的损失。凡未使用密码进行的交易，电子现金或300元以下的免密交易以及授权通过第三方平台（如银联在线、支付宝、京东、财付通、百付宝等）签约后的交易或签约委托代扣等协议支付的，记载有持卡人姓名的交易凭证或凭贷记卡磁条、芯片、卡号等电子数据而办理的各项交易所产生的信息记录之一或全部均属于该项交易的有效凭据。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第十六条', '持卡人一日内连续三次输入交易密码不正确的，当天该卡片即被锁定，次日自动解锁。如连续9次输入密码错误，持卡人须凭有效身份证件向发卡机构申请办理密码解锁、密码重置等手续。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第十七条', '持卡人应在安全的技术和商户环境下在互联网上使用圆鼎贷记卡，本行保留拒绝处理或支付怀疑涉及非法赌博等根据适用法律可能为不合法的互联网交易。因可能涉及非法互联网交易所导致的风险和损失由持卡人自行承担责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第十八条', '圆鼎贷记卡仅限持卡人本人使用，不得出租和转借，且应妥善保管圆鼎贷记卡、卡号、密码、身份证号、有效期、安全码（签名条后三位数字）等相关信息。持卡人因出租、转借圆鼎贷记卡或泄露圆鼎贷记卡卡号、密码、身份证号、有效期、安全码（签名条后三位数字）等相关信息而造成的损失由持卡人承担。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第十九条', '芯片卡电子现金业务是指发卡机构根据持卡人要求将现金或账户存款转至芯片卡内储存，交易时不校验密码直接从芯片卡内扣款的业务，芯片卡电子现金账户余额上限为1000元，视同现金管理，不计息，不挂失。持卡人使用支持电子现金账户的圆鼎贷记卡可在境内带有“银联”标识且支持电子现金受理的特约受理商户的芯片接触或非接触式受理终端上进行脱机消费，交易时无须输入密码，打印交易凭证也无须持卡人签名确认，所有交易均视为持卡人本人所为，发卡机构不受理因脱机消费引起的争议。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第二十条', '圆鼎贷记卡挂失、损坏换卡或到期续卡后，新卡继承旧卡全部债权债务关系，但新卡（芯片卡）电子现金账户余额为0。原卡电子现金账户可以继续使用或对原卡电子现金账户进行余额转移，转移至主账户（原卡丢失的除外）。圆鼎贷记卡挂失换卡须按规定向发卡机构支付挂失换卡费。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第二十一条', '持卡人在境内外消费、取现及使用其他自助设备时，须遵守国家有关法律法规，以及银联、发卡机构和收单银行有关规定。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第二十二条', '发卡机构对圆鼎贷记卡设定有效期，发卡机构将依据国家有关规定及持卡人历史用卡记录，决定是否为持卡人更换新卡，但持卡人在到期前一个月以书面或发卡机构认可的其他形式通知发卡机构不续卡、不换卡、销户的除外。若持卡人有存续的分期交易且不愿一次性结清账务的，发卡机构有权拒绝持卡人的到期不续卡、不换卡、销户申请。本章程、领用合约及其他业务规定对已过期的圆鼎贷记卡继续有效，发卡机构继续保留对已过期圆鼎贷记卡的管理权、追索权等权利。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第二十三条', '发卡机构有权根据国家规定、收费标准、领用合约以及持卡人历史用卡记录等向持卡人收取贷记卡年费。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第三章  使用及账户管理', '第二十四条', '持卡人申办销户手续后，仍须清偿圆鼎贷记卡在销户前发生的一切债务和损失，发卡机构对已收的年费不予退还。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第二十五条', '对有交易发生或虽未发生交易但账户有未清偿欠款的，发卡机构通过持卡人预留的电子邮件地址提供当期对账单(不包含电子现金账户相关信息)或通过微信等渠道向持卡人推送当期对账单。持卡人应注意查收对账单，如未按时收到对账单，应及时向发卡机构查询，持卡人不能以未收到对账单为由拒绝还款。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第二十六条', '发卡机构对持卡人的还款，按照已出账单、未出账单的顺序偿还；逾期1—90天（含）的，按照应收利息、各项费用、分期分摊资金、透支取现及转账款、透支消费款的顺序进行冲还；逾期91天（含）以上的，按照分期分摊资金、透支取现及转账款、透支消费款、各项费用、应收利息的顺序进行冲还。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第二十七条', '持卡人在到期还款日前（含）偿还全部应还款额的，其当期账单上本期发生的消费交易款项享受自银行记账日至到期还款日期间的免息待遇。发卡机构与圆鼎贷记卡申领人另有约定的除外。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第二十八条', '持卡人可按照发卡机构规定的最低还款额还款。持卡人未能在到期还款日前（含）全额还款或超过信用额度用卡，不享受免息还款期待遇。发卡机构对持卡人不符合免息条件的交易款项从银行记账日开始计算利息，日利率上限为万分之五、下限为万分之五的0.7倍，并按月计收复利。如中国人民银行的有关规定发生变动，按其规定执行。发卡机构与圆鼎贷记卡申领人另有约定的除外。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第二十九条', '持卡人在到期还款日前还款不足最低还款额时，还须按与发卡机构约定支付违约金。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第三十条', '持卡人开通圆鼎贷记卡预借现金和转账功能的，即可通过ATM、网点柜面以及其他可提供现金服务的渠道提取现金或转账。圆鼎贷记卡办理预借现金或转账交易部分不享受免息还款期待遇，发卡机构从预借现金或转账交易日起至清偿日计收利息，透支利率按照中国人民银行的有关规定执行，按月计收复利，并按照发卡机构圆鼎贷记卡收费标准收取一定的预借现金或转账交易手续费，发卡机构与圆鼎贷记卡申领人另有约定的除外。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第三十一条', '发卡机构对圆鼎贷记卡账户内的存款不计付利息。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第三十二条', '持卡人可选择自行还款或约定自动还款。约定自动还款的，由发卡机构在到期还款日从其指定的在发卡机构开立的借记存款账户转账还款。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第四章  计息及费用规定', '第三十三条', '发卡机构按照对外统一规定的收费项目及标准收取费用，收费项目及标准如有变动，以发卡机构最新公告为准。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第五章  权利和义务', '第三十四条', '发卡机构的权利1、发卡机构有权索取申请人的个人信息和其他申请资料，向有关部门、单位和个人了解其身份、财产和其他有关信息，根据《领用合约》向合法设立的征信机构查询和报送其信用信息和其他有关信息。2、发卡机构有权决定是否向申请人核发、续发圆鼎贷记卡及具体发卡、续卡条件，并根据《领用合约》核定和调整持卡人的信用额度。3、持卡人未能按时偿还应还款项的，发卡机构有权采取催收、依法追索以及停止持卡人卡片的使用等必要的资产保全措施，督促持卡人还款，并追究其法律责任。4、发卡机构有权根据本章程和《领用合约》向持卡人收取透支利息和各项费用。5、对不遵守有关法律法规、本章程等规定的持卡人，发卡机构有权收回圆鼎贷记卡卡片或停止持卡人使用圆鼎贷记卡，授权特约商户收回圆鼎贷记卡卡片；发卡机构有权随时因其认定的正当理由（包括但不限于：持卡人有任何舞弊、欺诈或非真实交易的情形；持卡人存在洗钱、出卖银行卡等行为时；贷记卡因被取消、管制、终止、过期并不被续期等原因变为不正常状态；持卡人未依约还款；持卡人身故；持卡人违反有关法律法规、本章程或发卡机构的相关规定等）或卡片的风险控管因素，限制或取消持卡人的贷记卡交易，调降持卡人的账户信用额度，中止或停止持卡人使用贷记卡的权利。6、持卡人申领圆鼎贷记卡以一定金额的存款作为质押担保的，未经发卡机构同意，质押存款出质后至圆鼎贷记卡销户后45天内，不得支取。持卡人未按时清偿圆鼎贷记卡欠款时，发卡机构有权随时扣划作为担保的质押存款以清偿欠款。7、因非发卡机构原因引起的供电、通讯、网络故障或不可抗力等原因导致持卡人不能正常用卡的，发卡机构视情况协助持卡人解决问题或提供必要的帮助，但对因此而可能给持卡人造成的损失不承担责任。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第五章  权利和义务', '第三十五条', '发卡机构的义务1、发卡机构应以书面或通过网站等形式提供圆鼎贷记卡使用的有关资料，包括章程、领用合约、使用指南、安全提示、收费项目及标准等。2、发卡机构应向持卡人核发符合安全管理要求的贷记卡，并根据《领用合约》为持卡人提供贷记卡使用服务。3、发卡机构应设立24小时客服热线和其他服务渠道，为申请人/持卡人提供贷记卡申请处理进度和结果查询、贷记卡激活、对账单和账务查询、挂失补发和密码重置、业务咨询和办理、投诉处理等服务。4、发卡机构对申请人/持卡人的个人信息负有保密义务, 但适用法律或《领用合约》另有规定的除外。5、本章程及《领用合约》规定的发卡机构的其他义务。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第五章  权利和义务', '第三十六条', '申请人或持卡人的权利1、持卡人享有发卡机构对贷记卡所承诺的各项服务，有权监督发卡机构的服务质量，并对与承诺不符的服务进行投诉。2、申请人/持卡人有权知悉贷记卡的功能、使用方法、各项费用的收费项目和标准、透支利率及有关的计算方式等信息。3、持卡人有权获取贷记卡对账单，了解贷记卡的账务情况，并对有疑问的交易提出异议。4、在法律法规规定及支付清算组织规定的期限内，持卡人有权向发卡机构申请协助调阅签购单副本，若调查结果证明该交易确属持卡人所为，相关费用由持卡人承担。5、持卡人不承担其圆鼎贷记卡在挂失生效后因该卡被伪冒、盗用所产生的经济损失，但国家有权机关依法扣划持卡人名下贷记卡内资金所导致的后果均由持卡人承担。6、本章程及《领用合约》规定的持卡人的其他权利。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第五章  权利和义务', '第三十七条', '申请人或持卡人的义务1、申请人应向发卡机构提供准确、完整、真实、有效的资料。若发卡机构认为需要，申请人应向发卡机构提供符合条件的担保及其他有关资料2、持卡人和担保人如工作单位、通讯地址、电话、电子邮箱、身份证件信息等发生变更，应当及时到发卡机构或与发卡机构联系办理资料变更手续。否则，因此导致的损失由持卡人自行承担。3、持卡人应妥善保管自己的圆鼎贷记卡卡片及密码。若因卡片或密码保管不善等原因造成的损失，由持卡人自行承担。卡片一旦遗失，应及时通过发卡机构客户服务热线办理挂失；圆鼎贷记卡仅限持卡人本人使用，因出租、转让或转借信用卡而产生的一切后果由持卡人承担。4、持卡人需要修改、重置密码的，应按发卡机构指定的方式申请更换密码。贷记卡损坏时，持卡人应按发卡机构规定的或双方约定的其他方式办理换卡手续。5、圆鼎贷记卡遗失、被窃或遭他人占有时，持卡人应及时通过圆鼎贷记卡客户服务热线办理挂失。发卡机构在核对相关资料后进行相关挂失处理。电话挂失经发卡机构确认后即生效，挂失正式生效前所发生经济损失均由持卡人承担，但持卡人和发卡机构另有约定的除外。贷记卡遗失、被窃或遭他人占有的，持卡人应配合发卡机构调查情况。持卡人与他人合谋、恶意串通、套现、洗钱或有其他任何违法、不诚信的行为，持卡人承担全部法律后果和责任。6、持卡人应按照与发卡机构的约定，按时偿还透支款本金、利息、年费、手续费等费用，不得以与商户纠纷或与其他第三方的纠纷等为由拒绝支付所欠发卡机构款项。如遇贷记卡的单据有误或内容不全，但经确认交易确实存在且金额无误，持卡人不得拒绝支付该交易款项。7、持卡人用卡后应妥善保管交易凭证，以防卡片资料被他人盗取。持卡人应注意查收对账单，如未按时收到对账单，应主动及时向发卡机构查询。持卡人不能以未收到对账单为由拒绝向发卡机构支付欠款（包括相应的费用、利息等）。8、为防范风险，保障发卡机构、持卡人利益，持卡人应配合发卡机构及特约商户完成风险防范操作。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第六章  附则', '第三十八条', '本章程未尽事宜，按照有关法律、法规、规章和领用合约执行。持卡人与本行的具体权利和义务由领用合约具体约定。持卡人在使用芯片卡或磁条芯片复合卡时，还应遵守该类卡的相关规定。'),
(NULL, '电子银行部', '《圆鼎贷记卡章程》', '第六章  附则', '第三十九条', '本章程由本行负责制定和解释。本行有权在法律法规允许的范围内对本章程进行修改，并提前以网站公告或营业网点公告方式通知持卡人。上述修改在公告期满后即于公告确定的实施日期正式生效，并对所有当事人产生约束力。如持卡人有异议，有权根据领用合约约定办理圆鼎贷记卡销户手续，否则视为同意上述修改并受其约束。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第一章  总则', '第一条', '为加强本行手机银行（客户端版）业务管理（以下简称“新版手机银行”），促进新版手机银行业务健康发展，根据银监会《电子银行业务管理办法》等有关规定，特制定本办法。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第一章  总则', '第二条', '本办法所称的新版手机银行业务是指本行依托相关移动通讯运营商网络、新版手机银行后台及各业务子系统，以下载客户端版软件的手机为终端，向客户提供的自助式金融产品。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第一章  总则', '第三条', '新版手机银行业务是本行服务广大客户的一种新型金融服务手段，是传统银行业务的扩充和延伸，是本行电子银行服务的重要组成部分，。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第一章  总则', '第四条', '新版手机银行服务实行统一组织和管理，并接受有关管理部门的管理和监督。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第二章   组织管理体系', '第五条', '开办新版手机银行业务的具体职责分工如下：（一）本行电子银行部负责新版手机银行业务管理制度及操作流程的制定和完善；负责新版手机银行业务指导、业务宣传、市场策划及营销；负责指导和分析全行新版手机银行业务的开展，负责新版手机银行业务涉及资金变化的动态分析。（二）本行计划财务部负责新版手机银行会计业务操作管理和培训。（三）本行运营管理部负责新版手机银行会计业务差错的处理；负责动态口令牌等重要空白凭证的管理。（四）本行信息科技部负责制定与实施应用级技术安全策略；负责本行新版手机银行数据的提供等技术工作。（五）本行信贷管理部负责配合电子银行部做好新版手机银行业务的市场策划和营销；配合做好新版手机银行业务的功能创新。（六）本行合规管理部负责对新版手机银行业务相关制度和操作流程的合规性、有效性进行审核。（七）本行风险管理部负责新版手机银行业务的风险监测、识别、评估、控制管理工作。（八）本行审计部负责对新版手机银行业务的运行、管理、内部控制等情况进行稽核监督。（九）各营业网点承担新版手机银行业务的市场营销任务，负责新版手机银行各相关制度的贯彻执行；负责受理新版手机银行业务的签约/撤销等申请；负责客户新版手机银行操作员分配、动态密码器的管理及新版手机银行业务咨询等基本业务。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第三章  系统维护操作', '第六条', '新版手机银行操作员采用分级管理方式，各级、各类操作员的签发、注册、变更和注销应遵循“双人操作、换人复核”的原则。（一）本行新版手机银行管理员本行新版手机银行管理员由省联社新版手机银行主管部门操作人员设置、分配、管理。（二）营业网点新版手机银行操作员营业网点新版手机银行操作员由本行新版手机银行管理员进行设置、分配、管理。营业网点新版手机银行操作员新增、变更及注销需向本行电子银行部提交“江苏农村商业银行后台管理系统操作员（注册、变更、注销）申请表”（附件1）及操作员身份证复印件。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第四章  服务对象与内容', '第七条', '新版手机银行业务服务对象为拥有本行个人结算账户、具有民事行为能力的个人。本行根据不同的客户类型、注册方式和申请服务项目，为客户提供相应的新版手机银行服务。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第四章  服务对象与内容', '第八条', '新版手机银行业务服务范围和内容包括但不限于：查询、转账汇款、账单支付、缴费、信用卡服务等金融业务。服务范围和内容的变更将通过营业网点或江苏农信电子银行业务门户网站（http://www.js96008.com）进行公告。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第五章  新版手机银行操作流程', '第九条', '新版手机银行基本规定（一）凡持有本行个人结算账户的客户，均可申请成为新版手机银行注册客户。注册、注销及新版手机银行信息变更业务必须由客户本人办理。（二）新版手机银行客户相关资料和账户信息的注册登记等操作，均需通过后台管理系统实现，客户通过新版手机银行办理的相关业务纳入综合业务系统统一进行账务处理。（三）客户填写的开户申请书及开户打印的申请回执单作为该笔业务的记账凭证的附件，其他非账务性打印资料附在操作员当日核心凭证的最后。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第五章  新版手机银行操作流程', '第十条', '系统安装江苏省农村信用社新版手机银行客户端分别支持ios、Andriod和Windows phone三大操作系统，下载安装方式如下：1、苹果（ios）商城通过Appstore苹果官方商店下载“江苏农信”新版手机银行客户端最新版本，下载完成后直接点击安装即可。2、安卓（Andriod）商城通过Andriod（安卓）官方网站直接下载“江苏农信”新版手机银行客户端最新版本，下载完成后直接安装即可。3、Windows Phone商城通过Windows phone 官方网站直接下载“江苏农信”新版手机银行客户端最新版本，下载完成后直接安装即可。4、江苏农信电子银行门户网站通过手机访问江苏农信电子银行门户网站（http://）下载新版手机银行客户端最新版本进行安装，也可直接下载到电脑，并通过数据线连接手机进行安装。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第五章  新版手机银行操作流程', '第十一条', '新版手机银行开通有两种方式：一是通过银行柜面开通，二是通过手机自助开通。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第五章  新版手机银行操作流程', '第十二条', '客户若已开通本行个人网银，在办理新版手机银行动态口令牌（卡）绑定时，系统将默认个人网银动态口令牌（卡）为绑定设备，只需输入验证码直接进行绑定。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第五章  新版手机银行操作流程', '第十三条', '新版手机银行客户注册流程（一）柜面开通1.客户持有效身份证件及复印件、银行卡等前往本行营业网点申请开通新版手机银行。2.填写《江苏农村商业银行电子银行客户申请表》，签署《电子银行服务协议》。3.营业网点操作员验证客户身份证件等资料信息的真实性，核对卡号、手机号码的准确性，并通过综合业务系统51209交易码验证客户账户和密码的真实性；4.营业网点操作员通过后台管理系统为客户进行开户：（1）进入后台管理系统，选择手机银行客户信息管理，手机银行开户；（2）输入客户信息，客户全名、证件类型、证件号码，点击查询，输入补充信息；已开通个人网银的客户补充信息已存在，则审核补充信息是否准确；（3）选择服务功能，添加客户签约账号；（4）选择验证方式开通，是否使用动态口令牌（卡），由客户决定，新版手机银行操作员须告知客户未绑定动态口令牌（卡）的只可以进行小额账户交易，需要在网银后台中设置：手机银行用户管理、账号管理、签约账号管理、选择查询/转账。如客户需要使用动态口令牌（卡）则按凭证领用要求向主出纳申领。（5）复核员在交易审核待审核交易中进行复核；（6）复核员打印密码信封，网点操作员须提醒客户修改初始登录密码；（7）开户完成；5.客户通过Appstore、Andriod、Windows phone或江苏农信电子银行业务门户网站（http://）下载新版手机银行最新客户端，安装成功后，使用手机号码及密码信封的初始密码登陆系统，完成设备绑定后，即可使用。（二）自助开通1.客户通过江苏农信电子银行业务门户网站、安卓商城、Windows phone官方网站下载江苏农信新版手机银行最新客户端，并通过互联网提出申请，在阅读知晓同意遵守《江苏省农村信用社电子银行个人客户手机银行服务协议》和其他业务管理规定的基础上，签署《江苏省农村信用社电子银行个人客户手机银行服务协议》。2.客户须持有本行银行卡或存折等有效账户，输入身份证件、账户和支付密码。系统将于验证客户身份证件、账户和密码等信息的真实性后，为客户完成新版手机银行与客户手机、银行卡号（或存折账户账号等）的签约绑定，并开通相关业务功能。3.客户自主决定交易中是否使用动态口令牌（卡），如选择使用，需到本行任一营业网点领取动态口令牌（卡），然后在系统中完成动态口令牌（卡）的绑定，也可在营业网点柜面直接绑定。4.自助开通流程（1）连接网络，客户自行下载新版手机银行客户端，打开“新版手机银行”导航菜单，点击“注册”按钮进入下一步；（2）阅读“新版手机银行协议”进入下一步，可以选择“阅读详细内容”阅读全部新版手机银行协议内容；（3）选择“我已阅读新版手机银行服务协议”，如不选择“我已阅读”选择框将无法确定进入下一步。点击“确定”按钮进入下一步；（4）选择显示详细内容，即可将江苏省农村信用社电子银行个人客户新版手机银行协议全文，供客户了解阅读。当没有选择“我已阅读新版手机银行服务协议”之前，确定按钮为灰色状态，无法进入下一步。当选择“我已阅读新版手机银行服务协议”后，确定按钮为正常状态，并进入下一步；（5）选择证件类型：身份证、军官证、文职干部证、警官证、护照、港澳居民往来通行证、户口簿、边民出入境通行证、外国人永久居留证、临时身份证、离休干部荣誉证等；（6）输入证件号码：输入证件类型对应的证件号码；（7）输入客户姓名：输入真实姓名，如果是英文名需切换英文输入法；（8）拥有动态口令牌（卡）：（选择是或否），是指是否已有本行提供的动态口令牌（卡），如果没有，请选择“否”进入下一步；有则需要动态口令牌（卡）代码和验证码进入下一步；如已拥有个人网银动态口令牌（卡），则直接输入个人网银动态口令牌（卡）证码进入下一步；（9）输入手机号码：输入11位手机号码（必输项）；（10）输入邮政编码：输入6位邮政编码（非必输项）；（11）输入电子邮箱：（非必输项）；（12）输入预留信息：输入预留信息，便于登录密码忘记找回密码（必输项）；（13）输入账号：输入19位银行卡号或22位存折账号（必输项）；（14）选择账号类型：选择账户类型（银行卡、存折、一证通、信用卡），已激活的信用卡方可注册；（15）选择账号币种：根据在银行开户的账户币种进行相应选择，默认人民币；（16）输入交易密码：交易密码即支付密码，通常为6位；（17）输入登录密码：用户自行输入登录密码，使用登录新版手机银行客户端；（18）输入重复密码：用户自行再输入登录密码，便于验证登录密码的两次是否一致，注册成功后只需输入一次登录密码；（19）输入短信验证码：点击获取按钮获取短信验证码，获取后新版手机银行系统会采用短信方式通知短信验证码，查看官方短信验证码，并输入列表中；（20）点击注册按钮：点击注册按钮进行下一步处理；（21）弹出对话框，提示是否确定注册？选择确定即进行下一步，反之选择取消按钮即放弃当前操作；（22）提示“数据验证中”；（23）提示“注册成功”，注册完毕。登录新版手机银行客户端进行相关操作，需打开网络连接（或连接WLAN）方可进行相关操作，否则无法正常操作和显示相关内容信息。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第五章  新版手机银行操作流程', '第十四条', '新版手机银行业务变更、撤销（一）变更新版手机银行业务客户申请变更新版手机银行业务（包括变更客户信息、变更操作员信息等），需提交新版手机银行业务变更申请表及发生变更的相关客户资料，营业网点新版手机银行操作员需按新版手机银行开户要求，核对资料无误后留存相关资料复印件，并在相关业务菜单中完成变更操作。（二）撤销新版手机银行业务客户申请撤销新版手机银行业务，需提交撤销新版手机银行申请表及客户相关证件资料，营业网点手机银行操作员需按新版手机银行开户要求,核对资料无误后留存相关复印件，并在相关业务菜单中完成撤销操作。（三）新版手机银行业务变更、撤销资料并入当日操作员交易凭证管理。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第六章  收费处理', '第十五条', '收费依据。按照本行公示的收费标准执行。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第六章  收费处理', '第十六条', '收费方式。新版手机银行的收费方式采取系统自动扣收方式。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第七章  动态口令牌（卡）管理', '第十七条', '新版手机银行动态口令介质主要包括动态口令刮刮卡、动态口令牌等几种。新版手机银行动态口令牌（卡）是指由专业公司提供，通过预置密钥和一定的算法，可以生成动态口令的一款电子产品，经客户与新版手机银行建立绑定关系后，在客户通过新版手机银行进行交易时可以用于识别客户身份。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第七章  动态口令牌（卡）管理', '第十八条', '各营业网点须将动态口令牌(卡)作为重要空白凭证进行管理，严格执行分级权限管理制度和业务分离制度。手机银行复核员、操作员要严格在各自的权限范围内操作并强化岗位间的制约机制，执行空白介质（动态口令卡、动态口令牌等）保管与使用分离、审核与录入操作分离制度，严格办理相关使用登记手续。（一）省联社电子银行部作为新版手机银行业务主管部门，负责管理厂商提供数据导入需要的密钥，并及时协调相关部门将厂商提供的动态口令牌（卡）相关配套数据导入动态口令管理服务器中，以便客户进行注册绑定、登录和交易等操作时校验身份。（二）运营管理部根据业务发展需求，每季度初向省联社电子银行部申请订购动态口令牌（卡）计划。（三）营业网点的操作员在客户注册新版手机银行时，根据客户要求提供动态口令牌（卡）。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第七章  动态口令牌（卡）管理', '第十九条', '动态口令牌（卡）的挂失管理。动态口令牌（卡）的挂失管理包括挂失和解挂失。客户凭有效身份证件可到注册营业网点办理动态口令牌（卡）挂失、解挂失手续。营业网点操作人员审核无误后完成相关操作。在没有要求重新绑定新的动态口令牌（卡）前，客户可至注册营业网点对已挂失的动态口令牌（卡）进行解挂失。客户重新绑定新的动态口令牌（卡）时，操作员须先对旧的动态口令牌（卡）做解绑定，再做新动态口令牌（卡）的绑定。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第七章  动态口令牌（卡）管理', '第二十条', '动态口令牌（卡）的解锁。客户输入动态口令错误超过3次时，动态口令牌（卡）会进入锁定状态。客户可持有效证件在开户营业网点柜面申请对动态口令牌（卡）解锁。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第七章  动态口令牌（卡）管理', '第二十一条', '动态口令牌（卡）的同步。动态口令牌（卡）与动态口令后台管理不同步时，会出现虽然客户每次输入正确动态口令，但到后台验证时却不能够通过的现象。客户须持有效证件和动态口令牌（卡），至开户营业网点柜面做动态口令的同步处理。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第七章  动态口令牌（卡）管理', '第二十二条', '客户应妥善保管动态口令卡(生成器)和密码；凡使用动态口令或密码进行的操作，均视为客户本人所为；如因保管不当,泄露密码或丢失动态口令牌（卡）等造成纠纷或资金损失，由客户自行负责。在客户动态口令牌（卡）有效期内，如客户的动态口令牌（卡）毁损，应及时办理补办手续，在更新生效前造成的资金损失由客户自行承担。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第八章  业务处理相关规定', '第二十三条', '新版手机银行业务注册经办机构指的是为客户开通新版手机银行业务的营业网点。自助开通客户的注册经办营业网点是指客户绑定的第一个账户所属营业网点（若客户已开立个人网银则优先所属个人网银开户营业网点）。新版手机银行业务发起机构指的是转出方客户发起交易的账户所属营业网点。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第八章  业务处理相关规定', '第二十四条', '新版手机银行最多可绑定3部手机，没有动态口令或注册时选择不使用动态口令的客户，在登陆新版手机银行时只有选择绑定该手机方可登陆，不绑定该手机则无法登陆，客户如需要对已经绑定的手机进行解绑，可以在登陆客户端版后在账户管理模块设备管理功能中解绑该设备。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第八章  业务处理相关规定', '第二十五条', '新版手机银行业务系统中账务性交易限额由各营业网点根据客户申请的限额在综合系统里进行设置，不得超过省联社限额设置范围。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第八章  业务处理相关规定', '第二十六条', '新版手机银行业务的账务处理、资金清算纳入综合业务系统、贷记卡系统等原相关系统管理。江苏农信系统内部账户互转实时处理，客户资金即时到账。跨系统转账或汇兑业务，在正常营运期间按照相关规定处理。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第八章  业务处理相关规定', '第二十七条', '各业务发起机构根据系统提示打印代用凭证或分科目清单，审核盖章轧平当日账务后作为当日会计记账依据订入传票作为会计档案统一保管。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第八章  业务处理相关规定', '第二十八条', '新版手机银行根据注册客户的电子指令为其办理转账付款等业务。（一）客户凭证打印。客户可通过新版手机银行提供的查询交易查询或通过营业网点柜面实现新版手机银行相关交易明细的查询打印。（二）经办机构业务清单打印。经办机构每日营业终了，打印相关业务清单，专夹保管，视业务量情况按月、季、年装订，资料管理参照会计档案保管规定执行。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第八章  业务处理相关规定', '第二十九条', '密码锁定与重置新版手机银行客户当日连续输错3次手机银行登陆密码，系统自动锁定登陆功能半小时；新版手机银行客户连续输错6次手机银行登陆密码，系统自动锁定登陆功能，客户可通过客户端版“找回您的密码”功能或凭有效证件至柜面进行登陆密码重置。新版手机银行客户当日连续输错3次手机银行支付密码，系统自动锁定涉及资金交易的业务，当日客户可凭有效证件至柜面进行支付密码重置，重置后当即恢复被锁定的业务功能。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第九章  业务安全管理', '第三十条', '经办营业网点负责审核客户申请注册及更改信息的真实性、准确性、完整性，审核客户注册资格，落实相关安全控制制度等。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第九章  业务安全管理', '第三十一条', '与新版手机银行业务账务处理、资金清算相关的内部风险控制按照相关规定执行。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第九章  业务安全管理', '第三十二条', '办理新版手机银行业务的营业网点应加强对新版手机银行操作员的管理，更换操作员时应及时办理有关手续。每个新版手机银行操作员对应的操作号必须本人专用。新版手机银行操作员应加强安全保密意识，妥善保管自己的密码，不定期修改密码，防止泄密，严禁用他人的操作号进行操作。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第九章  业务安全管理', '第三十三条', '经办营业网点须妥善保管新版手机银行客户资料，不得擅自对外提供（法律另有规定除外），严格保障客户信息安全。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第九章  业务安全管理', '第三十四条', '开办新版手机银行业务的营业网点应告知客户只能通过我行发布的网址下载客户端（江苏农村商业银行电子银行业务门户网站http://www.js96008.com、安卓商城、Windows phone官方网站），如有疑问可拨打我行客户服务热线96008。客户因下载错误客户端造成的资金损失与本行无关。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第十章  重大事项处理', '第三十五条', '新版手机银行业务重大事项包括服务中断、业务差错和风险事件。新版手机银行业务重大事项处理应在有效控制风险的前提下，提高应急处理效率，尽量缩小不良影响范围。新版手机银行服务中断是指由于系统故障或者不可抗力等原因造成客户暂时无法办理新版手机银行业务。新版手机银行业务差错是指由于系统故障、操作员操作失误等原因造成的错记、重记、漏记或少记客户账务等差错。新版手机银行风险事件分为业务风险事件、技术风险事件、法律风险事件和声誉风险事件。其中，业务风险事件是指由于新版手机银行客户自身原因造成其信息或账户资金被非法窃取的各类事件；技术风险事件是指新版手机银行系统存在安全漏洞、遭到攻击或破坏的各类事件；法律风险事件是本行新版手机银行业务相关法律诉讼事件；声誉风险事件是指媒体负面报道、聚众滋事等影响本行新版手机银行声誉的各类事件。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第十章  重大事项处理', '第三十六条', '营业网点出现新版手机银行重大事项后，应填写《江苏农村商业银行电子银行业务重大事项报告表》（附件4）、《江苏农村商业银行电子银行业务重大事项处理情况跟踪信息表》（附件5），按照规定履行报告职责，并对事项的处理情况实行动态管理。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第十章  重大事项处理', '第三十七条', '服务中断处理。营业网点均应根据上述业务规定及本单位实际情况制定详细的业务应急预案，在新版手机银行业务服务中断的情况下，按照预案组织应急处理，并做好客户解释工作。（一）对于因系统故障等原因造成客户无法办理新版手机银行业务的，应建议客户到营业网点办理，或按约定的应急处理方式办理。短期内不能恢复服务的，要以电话、传真等方式通知重点客户按约定的应急处理方式办理业务。（二）经办营业网点对需要进行应急处理的业务要逐笔详细登记，每日营业终了，会计主管须将应急业务登记簿和手工凭证及附件进行核对。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第十章  重大事项处理', '第三十八条', '业务差错处理。新版手机银行业务差错处理应遵守据实、准确和及时的原则。营业网点应指定人员负责新版手机银行业务差错处理工作，并明确权限和职责。（一）系统原因造成的业务差错，营业网点按照规定进行账务处理。因自身系统、内控制度或为其提供服务的第三方机构原因，造成电子支付指令无法按约定时间传递、传递不完整或被篡改，并造成客户损失的，营业网点应及时做好客户安抚工作，并及时汇报本单位领导和电子银行部，以便尽快处理，减少损失和负面影响，营业网点还应根据与第三方服务机构的协议对其进行追偿。（二）由于客户自身原因造成的电子支付指令未执行、未适当执行、延迟执行等业务差错，营业网点应主动通知客户改正或配合客户采取补救措施。（三）客户通过新版手机银行办理异地汇款业务，款项汇出后若由于账号、户名不符、汇款无人领取等原因需要汇入行退回的，按柜面汇入行主动退汇的处理手续处理。（四）营业网点对新版手机银行业务差错应详细备案登记，记录内容应包括差错时间、差错内容、处理部门、处理人员姓名、客户资料、差错影响或损失、差错原因、处理结果等。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第十章  重大事项处理', '第三十九条', '新版手机银行风险事件的处理应遵守审慎、高效、保密的原则。电子银行部为新版手机银行风险事件处理的牵头部门与协作部门，妥善处理好风险事件。（一）营业网点负责处理本单位新版手机银行风险事件并按规定履行报告职责；安全保卫部负责就新版手机银行风险事件向公安机关报案，配合公安机关的调查工作；组织开展对客户的安全教育工作；信息科技部负责技术分析，研究制定有关技术应对方案，提供新版手机银行风险事件涉及的交易信息查询等技术支持工作。（二）营业网点发现内部人员利用从事新版手机银行工作之便窃取客户信息或账户资金的事件，应立即向营业网点负责人和电子银行部汇报。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第十一章  附则', '第四十条', '本办法由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《手机银行（客户端版）业务管理办法》', '第十一章  附则', '第四十一条', '本办法自印发之日起执行。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第一章  总则', '第一条', '为加强农村商业银行（以下简称“本行”）支付宝卡通业务管理，规范业务操作，防范业务风险，根据《圆鼎借记卡章程》、《圆鼎借记卡业务管理办法》等规定，制定本办法。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第一章  总则', '第二条', '支付宝卡通业务是江苏省农村信用社联合社与支付宝公司联合推出的一种网上支付服务，签约客户可使用圆鼎卡在支持支付宝付款的网上商城完成商品购买、支付结算等功能的一项电子服务。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第三条', '信息科技部负责为支付宝卡通业务提供技术支持以及相关数据统计工作。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第四条', '运营管理部负责支付宝卡通业务处理、业务指导和运行管理等工作。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第五条', '计划财务部负责支付宝卡通业务成本核算等工作。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第六条', '合规管理部负责支付宝卡通各项规章制度及业务的合规审查，监督支付宝卡通业务规章制度、规定的执行情况。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第七条', '风险管理部负责支付宝卡通业务的风险监测、识别、控制、消除等工作。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第八条', '信贷管理部负责配合做好支付宝卡通业务的市场策划和营销。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第九条', '审计部负责对支付宝卡通业务运行、管理、内部控制、考核等情况进行监督。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第十条', '电子银行部：（一）负责支付宝卡通业务的指导；（二）负责支付宝卡通业务的市场调研、客户分析；（三）负责支付宝卡通业务的组织、实施和推广，承担产品专业支持和营销服务等工作；（四）负责支付宝卡通业务功能增加，完善产品服务；（五）负责支付宝卡通业务的交流和人员培训；（六）负责支付宝卡通业务经营管理相关的其他工作。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第二章  机构、人员职责', '第十一条', '各营业网点:（一）根据本行的部署，承担支付宝卡通业务的市场营销任务，负责支付宝卡通各相关制度的贯彻执行；（二）负责开展支付宝卡通业务宣传，受理业务咨询及对账服务；（三）负责受理支付宝卡通业务开通、关闭和变更申请；（四）负责办理支付宝卡通其他相关业务。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第三章  业务操作', '第十二条', '为控制业务风险，银行卡客户开通支付宝卡通业务，必须先到支付宝网站（www.alipay.com）注册，然后到本行营业网点申请。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第三章  业务操作', '第十三条', '建立支付宝卡通业务关系前，经办柜员必须提示客户阅读《支付宝卡通服务协议》，让客户清楚了解支付宝卡通业务的功能约定、责任和风险情况。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第三章  业务操作', '第十四条', '银行卡客户签约（解约）支付宝卡通业务服务，应填写《支付宝卡通业务申请表》，并提供本人有效身份证件及复印件、银行卡、支付宝账户、手机号码，经办人员必须核对客户身份证件'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第三章  业务操作', '第十五条', '客户完成支付宝卡通业务签约后，需登录支付宝官方网站进行业务激活，激活成功后方可使用支付宝卡通功能进行网上支付。圆鼎借记卡持卡人只需在支付宝平台提供借记卡卡号、户名、手机号码等信息，即可完成圆鼎借记卡支付宝快捷支付的在线签约（解约）及支付功能。如果客户的借记卡已经签约支付宝快捷支付，则下次支付时只需输入支付宝支付密码即可完成支付。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第三章  业务操作', '第十六条', '客户完成支付宝卡通业务签约（解约）申请后，经办柜员应将申请表的网点留存联及身份证复印件作当日传票附件上缴至事后监督中心。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第三章  业务操作', '第十七条', '银行卡客户开通支付宝卡通功能后，办理挂失、销户等特殊业务时，须按照圆鼎借记卡业务管理办法规定办理，销户后圆鼎借记卡及支付宝账户的绑定关系随之撤销。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第四章  业务管理', '第十八条', '支付宝卡通业务通过银行卡主活期账户结算，单笔限额5000元、当日累计限额5000元，客户可设置限额区间为0—5000元，网上交易过程中会校验支付宝账号及激活时预留的卡通密码，因此经办柜员应提示客户必须妥善保管好支付宝账号及卡通密码。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第四章  业务管理', '第十九条', '客户注销“支付宝卡通”业务或注销已开通“支付宝卡通”业务的银行卡前，经办柜员必须提醒客户确认是否已将支付宝卡通账户内所有款项转入银行卡，否则客户注销支付宝卡通业务后，支付宝卡通账户内款项将无法转入签约的银行卡。系统控制未解约“支付宝卡通”业务的银行卡不允许销卡。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第五章  附则', '第二十条', '本办法由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《支付宝卡通业务管理办法》', '第五章  附则', '第二十一条', '本办法自印发之日起执行。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第一章  总则', '第一条', '为规范农村商业银行银行卡收单外包服务行为，促进银行卡收单业务健康有序发展，根据《银行卡收单业务管理办法》、《中国人民银行关于加强银行卡业务管理的通知》、《中国人民银行关于加强银行卡收单业务外包管理的通知》等规定，特制定本办法。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第一章  总则', '第二条', '收单非核心业务包括但不限于商户拓展与服务、终端布放与维护、交易接入等。与特约商户签订受理协议和商户受理银行卡的资金结算等收单核心业务不得对外委托收单外包机构。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第一章  总则', '第三条', '根据本行委托，收单外包机构提供的服务仅限于本办法规定的收单非核心业务。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第一章  总则', '第四条', '商户拓展与服务是指按照本行和中国银联的规定及要求，筛选并联系新的银联卡特约商户，或者按照收单机构、中国银联的规定和要求，提供特约商户培训、回访等服务。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第一章  总则', '第五条', '终端布放与维护是指布放ATM、CDS、多媒体自助终端、POS、固定电话等各类银行卡受理终端，提供所布放终端日常保养维修，耗材更换以及应用程序更新（受理终端密钥管理和密钥下载除外）、参数调整等服务。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第一章  总则', '第六条', '交易接入服务是指对ATM、CDS、多媒体自助终端、POS、移动电话、互联网、固定电话等各类支付终端发起的交易信息进行收集、交易报文定制和转发的服务。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第七条', '遵守国家法律、法规及有关规章，执行监管机构的规定，依据本办法及银联卡业务规章和市场规则，合规开展业务。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第八条', '本行依据本办法、银联卡业务规章和市场规则对收单外包机构提供的服务实施有效监控，确保其承诺遵守本办法及银联卡业务规章和市场规则的相关要求，确保银联卡受理服务水平符合规范要求。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第九条', '接受本行委托的收单外包机构应承诺接受收单机构和中国银联的指导和监督，共同维护收单市场稳定、规范发展。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十条', '本行就银联卡收单业务委托服务与收单外包机构订立合作协议。合作协议包含本办法相关内容及以下相应条款:1.收单外包机构承诺遵守本办法，遵守银联卡业务规章和市场规则。合作协议不得与银联卡业务规章和市场规则相冲突，承诺因违反本办法及银联卡业务规章和市场规则受到惩罚或带来经济损失时，承担相应的责任。2.按照银联卡业务规章和市场规则及本行要求运作委托业务。交易接入外包机构须按照本行的要求进行交易路由设置。3.本行和中国银联有权对收单外包机构的规章制度建设、业务运作、账户信息及交易数据安全等进行检查。4.收单外包机构承诺满足本行及中国银联信息安全与保密方面的要求。5.收单外包机构承认中国银联是“银联”商标的唯一拥有者。6.收单机构终止与中国银联合作，其委托收单外包机构开展的银联卡收单业务服务自动终止。合作终止后，收单外包机构有义务协助收单机构做好后续工作。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十一条', '收单外包机构使用的所有业务资料须经本行同意，并有义务应本行要求及时归还或销毁。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十二条', '业务资料包括但不限于银联卡业务规章、市场规则及规范，委托业务产生的文件资料，如商户申请、商户协议、交易单据等。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十三条', '收单外包机构代表本行使用银联标识时，应严格遵守《银联卡业务运作规章》、《银联卡受理标识使用规则》等中国银联关于银联标识使用的各项规定。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十四条', '收单外包机构应严格遵守《银联卡账户信息与交易数据安全管理规则》等所有中国银联关于储存和传输账户数据的相关规则，只能存储用于交易清分所必需的最基本的账户信息和交易数据，不得存储磁条信息、IC卡芯片信息、卡片验证码及个人密码等。收单外包机构发生篡改、泄露、破坏账户信息与交易数据的安全事故时，有义务立即通知本行和中国银联，启动相应应急预案并立即对安全事故进行调查处理。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十五条', '收单外包机构未达到账户信息安全管理要求或者泄漏账户信息导致中国银联及其入网机构遭受损失的，中国银联有权依据《银联卡账户信息与交易数据安全管理规则》的规定对经认定的责任方进行处罚。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十六条', '收单外包机构不得以任何理由和目的擅自使用保密信息，只在为本行提供银联卡受理相关服务时使用相应保密信息。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十七条', '收单外包机构建立内部信息安全措施及保密管理制度，按照合作本行及中国银联的要求保管保密信息，确保不被泄漏，不得出售、利用、复制、传播或修改保密信息。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十八条', '收单外包机构根据本行及中国银联的要求，自委托业务服务终止生效时立即停止使用所有保密信息，并交还本行，中国银联提供的相关信息直接交还中国银联。对于私自保留保密信息产生的一切后果，承担所有责任。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第十九条', '保密信息包括但不限于：银联卡业务规章和市场规则、本行业务管理办法、系统说明、卡片信息、BIN信息及密钥相关技术参数等。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第二十条', '根据风险评估情况，本行可适当向收单外包机构收取风险保证金或向保险机构定制专业保险。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第二十一条', '对由于收单外包机构原因造成中国银联及中国银联入网机构损失的，本行有权使用风险保证金或保险赔付先行进行赔偿，并保留对外包机构进一步追偿的权利。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第二十二条', '收单外包机构不得自行向商户收取任何与委托业务有关的费用。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第二十三条', '发生重大业务事件（如发生篡改、泄漏和破坏账户信息与交易数据，大范围病毒感染，网络被攻击等）或业务变更（如公司重组、主要股东变更、管理人员变更、业务变更、开展新业务品种等）后，收单外包机构有义务立即通知本行及中国银联，对须采取应急方案的应及时报告处理情况和整改措施。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第二十四条', '收单外包机构在本行授权的范围内代表本行从事业务活动并进行与此相应的宣传活动，不得以任何形式明示或暗示代表中国银联或与中国银联有直接关联性。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第二章  收单业务委托基本要求', '第二十五条', '收单外包机构应以自有资源（包括但不限于雇员、设备和系统等）向本行提供专业化服务，禁止以任何方式向其他机构转包合作业务。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第三章  收单业务处理基本要求', '第二十六条', '收单外包机构必须遵守以下业务管理基本要求：（一）商户拓展与服务开展商户拓展与服务委托服务，应遵守：1.商户收单协议的主签约方应为收单机构，协议中应明确规定商户资金清算等收单核心业务由本行承担。商户收单协议原件应由本行保存。（二）商户扣率及商户类别码（MCC）本行负责商户扣率的确定和商户类别码（MCC）的编制，并对商户类别码（MCC）准确性承担责任。（三）商户信息1.收单外包机构须向本行准确提供商户信息及其变更情况。2.收单机构须全面确认并及时掌握其委托收单外包机构拓展的特约商户名称、地址等商户信息。（四）终端布放与维护开展终端布放与维护委托服务，应遵守：1.收单外包机构受托布放的各类受理终端，应在显著位置张贴收单机构标识及银联标识，不得张贴外包服务机构标识。2.收单机构直接承担被没收卡片的回收、登记、销毁、转递等工作。3.收单外包机构必须建立机具定期回访及问题维护24小时响应机制。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第四章  收单业务外包监督管理', '第二十七条', '本行可以委托收单外包服务机构为特约商户提供受理终端布放、维护，特约商户营销拓展、培训、现场检查等服务。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第四章  收单业务外包监督管理', '第二十八条', '凡已办理工商注册并领取营业执照、独立核算、经营状况良好、信誉度高、客流量大、销售额较多、有较好通讯条件的饮食娱乐、旅游交通、商品销售等工体工商户和企业，并在本行开立结算账户的，均可成为营销对象。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第四章  收单业务外包监督管理', '第二十九条', '商户开展业务前，业务外包机构应对商户进行银行卡收单业务及资金结算培训，提高商户查验和受理卡片、交易操作的效率和质量，防止和减少因操作不当造成的风险损失。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第四章  收单业务外包监督管理', '第三十条', '商户开展业务后，根据业务发展和风险控制需要，收单业务外包机构应对商户进行定期和不定期培训，原则上一年不少于两次，并做好每次培训记录。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第四章  收单业务外包监督管理', '第三十一条', '收单业务外包机构应加强对商户定期和不定期回访、检查，积极收集与商户有关的各类信息，检查过程中发现商户有影响经营的负面信息、违规行为或其他异常现象的，需要对商户提出警告，并协助其立即改正，必要时按照规定及时终止合作，收回商户机具。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第五章  纪律与责任', '第三十二条', '收单外包服务机构在业务开展中存在下列情形之一的，收单机构应终止收单外包服务协议：发生本办法第十条规定的行为，情节严重的；（二）收单外包服务机构跨地市开展业务的；（三）拒绝配合人民银行开展的现场检查工作的；（四）违反本办法规定的其他行为。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第五章  纪律与责任', '第三十三条', '收单机构在收单业务外包中存在下列情形之一的，由所在地人民银行分支机构按照《银行卡收单业务管理办法》规定进行处理：对外包业务疏于管理，造成他人利益损失或造成社会不良影响的；（二）其外包服务机构发生账户信息泄露事件的。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第六章  附则', '第三十四条', '本办法由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《收单业务外包服务管理办法》', '第六章  附则', '第三十五条', '本办法自发布之日起施行。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第一章  总则', '第一条', '为加强农村商业银行特约商户和POS设备的管理，根据《银行卡收单业务管理办法》、《中国人民银行关于加强银行卡业务管理的通知》等有关规定，结合本行实际，特制定本办法。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第一章  总则', '第二条', '本办法中的特约商户，是指与本行签订银行卡受理协议、按约定受理银行卡并委托本行为其完成交易资金结算的企事业单位、个体工商户或其他组织。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第一章  总则', '第三条', 'POS机，是指用于受理银行卡结算的终端设备（含EPOS、内联POS机）。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第一章  总则', '第四条', '本办法所指的收单行是指本行。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第五条', '信息科技部（一）负责配合省联社制定POS机技术标准和设备的选型；（二）负责本行POS管理系统及网络的管理和维护等工作；（三）负责配合省联社做好POS机设备软件的开发、测试和版本管理工作，并根据业务部门提出的需求，配合省联社进行系统软件升级和技术测试工作；（四）负责本行POS业务数据的提供等技术管理工作。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第六条', '运营管理部负责特约商户和POS机消费款项的结算、账务核对、错账处理。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第七条', '计划财务部负责特约商户和POS机押金的收取等财务核算工作。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第八条', '合规管理部负责特约商户和POS机各项规章制度及业务的合规审查，监督特约商户和POS机各项规章制度、规定的贯彻执行情况。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第九条', '风险管理部负责特约商户和POS机的风险监测、识别、控制管理工作。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第十条', '审计部负责对收单业务的运行、管理、内部控制、考核等情况进行稽核监督。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第十一条', '电子银行部（一）负责特约商户的市场调研、客户分析、POS业务的指导；（二）负责协调与银联（包括银联商务公司）、其他商业银行之间的业务联系；（三）对营业网点的特约商户、POS机进行管理考核；（四）受理处理客户有关特约商户的投诉和纠纷；（五）负责拟定POS机购置计划，统一管理、分配、维护POS机设备；（六）负责POS机业务知识的交流和专业人员培训；（七）负责向信息科技部提出POS机新增功能的业务需求；（八）督促银联商户公司对POS机进行升级、更新程序、保证及时调整到位；（九）负责POS欺诈交易的监测、业务经营管理等其他工作。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第二章  组织管理体系', '第十二条', '各营业网点（一）负责特约商户的市场营销任务，负责特约商户和POS机各相关制度的贯彻执行；（二）负责特约商户拓展和资料搜集工作，并确保资料的真实完整性；（三）对商户的经营和业务情况进行审核、调查，填写《商户信息调查表》；（四）负责与商户签订《银联网络特约商户受理人民币银行卡协议书》；（五）负责监督检查商户日常使用情况，并填写《特约商户培训（回访、检查）登记表》；（六）负责POS业务的日常指导与培训工作。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第三章   特约商户的拓展', '第十三条', '商品零售（批发）、文教、交通、旅游、医药等适合银行卡支付结算的行业，都是特约商户的发展对象。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第三章   特约商户的拓展', '第十四条', '电子银行部、各营业网点应调查了解特约商户的经营范围和经营状况，对商户的交易量、发展前景等做详细的市场调研，选择经营状况良好、财务状况稳定的商户作为发展对象。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第三章   特约商户的拓展', '第十五条', '发展特约商户过程中，电子银行部应与相关部门密切配合，充分发挥本行综合营销优势。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第三章   特约商户的拓展', '第十六条', '与特约商户签约时，应使用本行统一制式的《银联网络特约商户受理人民币银行卡协议书》，对协议书上未尽事宜可另行签订补充协议，明确双方权利和义务。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第三章   特约商户的拓展', '第十七条', '本行应当按照中国银联特约商户编码规则对特约商户进行编码。当特约商户的主营项目或经营范围发生变更时，应重新编制商户编码。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第三章   特约商户的拓展', '第十八条', '对于新增特约商户，收单行应布放相应数量的POS机设备，并协同特约商户在收银台的显著位置摆放、张贴统一的本行和银联标识。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第四章  特约商户的准入条件', '第十九条', '商户准入条件主要包括：（一）具备合法经营资格的超市、批发零售、餐饮服务、学校等企事业单位、个体工商户以及其他经济组织，并同意在本行开立基本账户或一般结算账户；（二）有专设的收银柜台，收银员素质符合要求；（三）愿意严格执行协议的有关制度和义务。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第四章  特约商户的准入条件', '第二十条', '以下商户不列入准入范围：（一）中国银联明确禁入的商户；（二）不同意在我行开立账户的商户。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第五章   特约商户的培训', '第二十一条', '本行应做好商户的培训和再培训工作，提高商户受理、查验卡片和交易操作的效率和质量，以防止和减少特约商户操作不当而造成的风险损失。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第五章   特约商户的培训', '第二十二条', '对新发展的特约商户以及原有收银员发生变动的商户，必须对相关人员进行培训并确认其能够正确操作后方可允许其开办业务。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第五章   特约商户的培训', '第二十三条', '对特约商户的培训主要包括以下内容：（一）机具的基本使用方法和简单的故障排除方法；（二）可接受的银行卡、交易类型及操作流程；（三）银行卡的基本知识、银行卡的识别及相关的银行卡风险防范；（四）验卡、验证的基本知识及相关业务流程；（五）各种凭证的正确使用方法；（六）账务处理流程、收单行进行查询及差错业务处理流程。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第六章   特约商户银行卡交易资金清算', '第二十四条', '特约商户必须在本行开立账户，用于银行卡资金清算。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第六章   特约商户银行卡交易资金清算', '第二十五条', '特约商户POS机连线交易，本行应于次日完成资金清算。（一）对于预授权交易，本行须在30天的有效期限内完成资金清算；（二）会计人员应认真核对POS机交易明细和特约商户消费票据，发现错账要及时冲正、调整并通知商户。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第七章  对特约商户的管理', '第二十六条', '本行应建立健全特约商户的档案资料，包括《银联网络特约商户受理人民币银行卡协议书》、《商户信息调查表》、《特约商户培训（回访、检查）登记表》、《机具协议》等。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第七章  对特约商户的管理', '第二十七条', '本行须建立特约商户资料库，统计分析特约商户经营、受理客户持卡消费及持卡人对商户投诉情况等。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第七章  对特约商户的管理', '第二十八条', '本行进行调单时，特约商户必须提供相应的交易资料，不得无故拒绝。对不能提供交易资料的，相应损失由特约商户承担。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第七章  对特约商户的管理', '第二十九条', '本行要根据特约商户数量和业务开展情况配备特约商户管理员（拓展POS机的客户经理），负责商户管理和POS机设备的维护工作。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第七章  对特约商户的管理', '第三十条', '特约商户管理员要根据银行卡各项促销活动的要求，积极争取特约商户支持和配合；经常与特约商户组织联谊活动，增进相互间的协作关系。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第八章  POS机的管理', '第三十一条', '本行POS机设备由电子银行部集中管理，特约商户需要新装或更换POS机时，特约商户管理员向本行电子银行部提出申请，审批同意后，在十日内完成设备安装、测试工作。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第八章  POS机的管理', '第三十二条', '密钥管理（一）POS机密钥由省联社生成，本行电子银行部灌入；（二）机具密钥执行一机一密制度；（三）严禁将同一特约商户信息配置在多台POS机具上。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第八章  POS机的管理', '第三十三条', '电子银行部机具管理人员要对POS机出入库情况进行记录，建立POS机设备管理台账，记录POS机的型号、编号、装机位置和状态，确保账实相符。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第八章  POS机的管理', '第三十四条', '电子银行部要指定专人负责POS机程序的保管、下载和POS机参数的设置等工作，并及时登记。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第八章  POS机的管理', '第三十五条', '对发生故障的POS设备，电子银行部应及时与供货商取得联系争取尽快解决问题。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第八章  POS机的管理', '第三十六条', '机具的回收（一）对强制退出的商户，特约商户管理员在收到撤机申请批核后一个工作日内将机具收回；（二）因其他原因而被退出的，在撤机申请批核后三个工作日内收回机具；（三）撤机时，检验机具的完好性，并做好相应登记，撤回的机具在两个工作日内入库；（四）因商户倒闭、失踪等原因而无法收回的机具，由特约商户管理员负责进行追查，追查超过半年仍无法收回的，向本行电子银行部申请报损处理。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第九章  风险管理', '第三十七条', '营业网点采取的商户风险管理措施包括：（一）做好商户发展前的资信调查工作；（二）做好商户的培训、维护和走访，及时了解和掌握商户经营状况；（三）帮助商户提高卡片受理、资金清算等工作的效率和质量，增强风险意识和防范能力，防止和减少因收银员操作不当而造成的风险损失；（四）做好不良客户的信息管理。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第九章  风险管理', '第三十八条', '电子银行部负责监控POS机的日常业务，一旦发现有异动交易，及时通知营业网点，要求特约商户管理员查明真相，并及时做出相应的处理。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第三十九条', '商户调查营业网点负责对商户的调查，并如实填写《商户信息调查表》：（一）检查验证商户的营业执照等证件是否符合规定，并在上报的复印件资料上注明“与原件核对一致”;（二）对收银员基本业务知识和操作技能的资格审核;（三）对商户的经营场所进行实地考察，并拍照存档;（四）调查商户实际营业范围与营业执照登记是否相符;（五）查阅商户财务报表，分析经营状况是否良好。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十条', '商户审查电子银行部受理商户资料后,对准入范围内的目标商户进行以下风险审查：（一）审查商户或商户法定代表人（或负责人）是否已被列入中国银联不良信息系统；（二）审查商户营业执照、税务登记证、法定代表人（负责人）身份证件等证照的有效性；（三）对目标商户进行实地调查，商户是否在本行开设基本账户或一般结算账户。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十一条', '商户审批电子银行部审查岗完成对商户的审核后，将符合条件的商户协议递交电子银行部经理审批。对完成签约确认的商户，由电子银行部进行归档整理，归档材料包括商户协议书、商户信息调查表、商户补充协议、商户有关申请资料以及图片等材料。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十二条', '商户签约（一）商户须与本行营业网点签订《银联网络特约商户受理人民币银行卡协议书》和《收单设备出借协议》，并同意接受协议中的所有条款；（二）商户签订协议时必须加盖本单位公章和法人代表印章；（三）商户须提供营业执照、税务登记证、法定代表人身份证件或其他有效证件资料的复印件。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十三条', '商户注册电子银行部是商户注册的主管部门，负责对商户的信息录入，并向省联社进行注册登记等。（一）电子银行部负责填写《商户入网注册表》，并将协议书、商户营业执照等资料扫描后，发送至省联社申请办理；（二）省联社审核同意后进行商户注册；（三）电子银行部在综合业务系统里进行基础数据录入，开通此项业务。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十四条', '商户维护特约商户管理员应定期对所辖商户进行巡检，每季度至少巡检一次，巡检内容包括：（一）了解客户持卡消费情况，帮助商户解决业务受理中存在的问题；（二）对POS机具、通讯线路及周边环境进行检查，确保交易正常运行。对现场不能解决的问题，应先为商户安装备用POS机，不得影响商户交易；（三）根据商户的需要，及时配送签购单、标识牌、宣传材料等日常业务用品；（四）对特约商户的业务进行风险检查，并定期组织培训；（五）收集特约商户和持卡人对本行工作的意见和建议。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十五条', '协议解除（一）特约商户由于各种原因，需要与本行解除协议的，必须按协议规定提前以书面形式通知本行，到期后协议自动解除，停止受理POS机收单交易。协议解除后，在法律规定的有效期限内，商户仍需承担已发生交易的风险损失，收单行保留追索权利。（二）特约商户有下列情形之一，本行可以书面通知特约商户解除协议，收回POS机具，并保留对发现损失的追索权利：1．多次出现违规操作；2．有欺诈行为的；3．多次无故拒绝受理银行卡的；4．多次无理拒绝或故意拖延收单行查询业务和调单要求的；5．经营困难或即将破产的商户；6．六个月内未办理POS收单交易的。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十六条', '特约商户的暂停或撤销（一）对于出现下列情况之一的商户，营业网点特约商户管理员落实情况后登记《特约商户培训（回访、检查）登记表》报电子银行部，经电子银行部经理批准后可暂停或撤销特约商户资格：1．商户经营状况恶化，停业歇业；2．商户经营人、经营范围发生变更，无法执行有关优惠协议；3．商户经营地址和服务电话发生变更，超过一个月无法取得联系的；4．商户营业规模发生变更，变更后规模远低于本行要求；5．商户严重违规经营，社会形象不佳的；6．客户投诉属实，多次不执行有关优惠协议的商户；7．与金融同业签订优惠条件优于本行的类似特约商户协议的商户；8．恶意损害本行形象，侵害本行标识、名誉权的商户；9．其他经本行审核认为不合格的特约商户。（二）发现商户出现以下风险情况之一，且情节严重的，应立即书面通知商户终止银联卡交易：1．虚假申请：以虚假资料或盗用其他商户资料向本行申请为特约商户；2．侧录：商户默许、纵容、与不法分子共谋或发现后不制止不法分子在POS机具上装载侧录仪器，盗录持卡人磁条信息，出卖给伪卡制作团伙或自行制作伪卡；3．泄露账户及交易信息：商户违反保密条款，将银行卡账户及交易数据信息泄露给不法分子使用；4．套现：商户与不良持卡人或其他第三方勾结，或商户自身以虚拟交易套取现金；5．洗单：商户将其他未签约商户的交易在本商户的POS机上刷卡，假冒本商户交易；6．恶意倒闭：商户接受用卡支付的预付款后故意破产，使本行承担退单损失；7．虚假交易：在持卡人不知情的情况下利用其账户编造虚假交易或在持卡人消费的同时多压印单据或重复刷卡，并冒用持卡人签名进行虚假交易；8．伪冒交易超过一定比率：商户一定时期内的伪冒交易超过规定比率；9．名义经营范围与实际情况不符：商户名义上经营范围正常，或以正常名义申请成为特约商户后，实际从事禁入商户类型的经营活动；10．因银行卡欺诈交易已被司法机关立案或介入调查；11．中国银联已书面通知收单机构强制解约；12．已被其他卡组织认定为高风险商户；13．经营不善，已破产或停业；14．其他风险原因。（三）对强制退出的商户，本行应将其列入不良商户名单，收单行在两年内，不得与其再次签约受理银行卡业务。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十七条', '商户变更（一）对于商户名称、所在地、联系电话、主营项目、经营范围、法定代表人（负责人）、绑定的结算账户等信息发生变更时，商户应及时提交加盖本单位公章的《商户入网注册表》，并提供相关证明材料；（二）电子银行部经办岗审核《商户入网注册表》后报省联社，由省联社进行商户信息变更。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十章  特约商户管理程序', '第四十八条', '特约商户的投诉处理（一）客户因以下原因或情形可以向电子银行部进行投诉：1．业务承诺没有兑现；2．客户对提供的服务不满意；3．其他事宜。（二）电子银行部收到客户投诉后，与信息科技部维护负责人员联系共同解决，必须在3个工作日内给予回复并及时向客户作出解释。（三）各营业网点收到客户投诉后，需填写《特约商户受理和处理客户投诉报告表》，并注明客户姓名、联系方式、特约商户名称及投诉原因，于1个工作日内传至电子银行部。电子银行部收到各营业网点转来客户投诉后，应通知特约商户管理员，核实情况和提出整改措施，于3个工作日内直接回复客户，同时将整改情况反馈相应网点。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十一章  档案管理', '第四十九条', '在商户正常开展业务期间，商户与本行签订的《银联网络特约商户受理人民币银行卡协议书》，提供的营业执照、税务登记证、法定代表人（负责人）身份证件或其他有效证件资料复印件以及《商户信息调查表》等档案资料需永久保存。在与商户解约后，从协议终止日起，资料仍需至少保存两年以上。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十一章  档案管理', '第五十条', '商户保管的银联卡交易签购单凭证，保存期限自交易日起至少一年，在保存期间应协助本行对产生的交易异议进行调单等手续。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十二章  罚则', '第五十一条', '本行员工在POS机运行过程中存在违规行为的，对相关责任人按《员工违规违章行为记分考核办法》记分，并视情节轻重，追究相关人员的行政和法律责任。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十三章  附则', '第五十二条', '本办法由负责解释和修订。'),
(NULL, '电子银行部', '《特约商户及POS机管理办法》', '第十三章  附则', '第五十三条', '本办法自印发之日起执行。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第一章  总则', '第一条', '为促进农村商业银行电子银行业务稳健发展，建立一支学习能力强、业务水平高、服务态度好的电子银行骨干队伍，发挥电子银行专管员在电子银行业务发展中的主力军作用，依据《江苏省农村信用社电子银行专管员管理办法（试行）》等有关规定，特制订本办法。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第一章  总则', '第二条', '本办法所称电子银行业务是指圆鼎借记卡、圆鼎贷记卡、网上银行、电话银行、手机银行、短信银行、POS收单、转账电话、无卡支付、各类自助设备等相关业务，以及今后开办的其他创新型电子银行业务。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第一章  总则', '第三条', '本办法所称电子银行专管员，是指在辖内营业网点聘用的一名电子银行业务主管人员。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第二章  工作职责', '第四条', '负责做好电子银行产品的营销策划、活动组织、产品宣传等工作。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第二章  工作职责', '第五条', '负责做好电子银行业务的售前指导、售中服务、售后管理，做好客户回访督查工作。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第二章  工作职责', '第六条', '负责做好电子银行业务知识和相关规章制度的二次培训工作。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第二章  工作职责', '第七条', '协助网点运营主管做好电子银行业务制度执行和风险防控工作。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第二章  工作职责', '第八条', '协助网点负责人做好电子银行绩效考核工作。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第二章  工作职责', '第九条', '负责收集产品需求、客户意见，提出改善产品功能的合理化建议。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第二章  工作职责', '第十条', '完成本行电子银行部交办的其他事项。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第三章  工作要求', '第十一条', '电子银行部每季组织一次电子银行专管员培训。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第三章  工作要求', '第十二条', '经培训后，电子银行专管员应能熟练掌握各项产品功能以及操作流程，能为客户进行现场演练和指导。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第三章  工作要求', '第十三条', '电子银行专管员应严格履职，按照电子银行业务相关制度要求开展工作，严格执行各项规章制度。发现其他员工违反相关规章制度的，应及时与网点运营主管沟通并向网点负责人报告。情况紧急重大的，直接报告电子银行部。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第三章  工作要求', '第十四条', '电子银行专管员要进一步树立服务意识，提高服务水平。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第三章  工作要求', '第十五条', '做好相关产品的宣传、营销工作，加强对客户的回访,及时传递相关信息，维护本行形象。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第三章  工作要求', '第十六条', '应按月上报工作总结，报告本单位电子银行业务运行情况，对存在风险提出整改意见。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第三章  工作要求', '第十七条', '各经营单位电子银行专管员因人事变动或其他情况需进行调整，所在网点需将人员调整情况书面报告本行电子银行部，否则以未配置专管员论处。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第四章  工作绩效', '第十八条', '为更好地推动电子银行专管员制度的执行，调动电子银行专管员积极性，对电子银行专管员实行绩效考核制。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第四章  工作绩效', '第十九条', '工作绩效按照《电子银行专管员考核细则》实行百分加减制，以100分为基础进行加、减分。电子银行专管员绩效低于60分的，视为考评不合格。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第四章  工作绩效', '第二十条', '对未按要求配置电子银行专管员的经营单位，电子银行年度考核得分按实绩的90%计算，且不得参加电子银行各类单项奖励评比。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第四章  工作绩效', '第二十一条', '本行电子银行部具体负责电子银行专管员绩效考核工作。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第五章  选拔与退出', '第二十二条', '电子银行专管员实行考核聘用制，各经营单位负责人根据日常工作业绩从网点员工中推荐，人选报送总行同意后方取得任职资格。电子银行专管员任职期限为一年，任期届满经考核合格后可续聘。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第五章  选拔与退出', '第二十三条', '电子银行专管员应坚持学习、更新专业知识，认真履行工作职责，积极完成各项考核指标。对一年任期内，季度考评三次不合格的，年度考评不合格的或无法胜任该项工作的，取消其专管员资格。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第五章  选拔与退出', '第二十四条', '对日常工作中，不积极配合电子银行部开展工作的，工作缺乏主动性的，服务意识淡薄以及多次缺席培训或测试的，取消其专管员资格。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第六章  附则', '第二十五条', '本办法由电子银行部解释和修改。'),
(NULL, '电子银行部', '《电子银行专管员管理办法》', '第六章  附则', '第二十六条', '本办法自发文之日起执行。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第一章  总则', '第一条', '为有效处置农村商业银行电子银行突发事件，防止风险扩散和蔓延，提高快速反应和处置能力,最大程度降低突发事件的危害,保障电子银行业务安全、稳定、健康发展，根据银监会《电子银行业务管理办法》等有关规定，特制定本预案。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第一章  总则', '第二条', '电子银行突发事件的处置，应坚持“分级负责、分类处置、快速高效、安全稳妥”的原则，有关部门密切配合、各支行协调联动，尽快恢复电子银行的正常运营，最大限度地减少损失和降低不良影响。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第一章  总则', '第三条', '本预案适用于超出本行的处置能力，或需要由省联社负责协调、处置的电子银行重大突发事件的应急处置工作。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第二章  组织机构及职责', '第四条', '电子银行部负责建立和完善电子银行突发事件的防范、处置机制；接收、整理电子银行突发事件的有关信息资料；协调和督办电子银行突发事件应急处置的落实工作；收集保管有关档案资料；向分管电子银行业务的副行长报告有关情况。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第二章  组织机构及职责', '第五条', '各支行、部，行政办公室、计划财务部、信息科技部、安全保卫部等相关部门予以配合，负责处置各自职责范围内涉及电子银行突发事件应急事宜，提出具体应急处理措施并予以落实。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第三章  突发事件的分类及级别界定', '第六条', '本预案所指电子银行突发事件是指突然发生，对电子银行业务正常经营管理带来较大影响，造成或者可能造成客户及本行重大财产损失、声誉损失，或者可能带来全行性连锁反应及系统性风险，造成较大社会影响的电子银行紧急事件。主要包括以下四类:（一）重大突发电子银行运营事件。主要包括:发生电子银行应用系统、应用技术出现重大问题,或被人为攻击破坏等而导致电子银行服务连续中断（正常工作时间内4小时或正常工作时间外8小时以上）的事件。（二）重大突发电子银行案件事件。主要包括:发生批量客户资料丢失、威胁客户资金安全的案件；发生巨额资金（50万元以上）被盗案件；发生假冒网站、假冒电话银行及假冒手机银行等案件。（三）重大突发电子银行违规操作事件。主要包括:发生从业人员操作失误或违规操作,导致电子银行业务出现重大差错或隐患的事件;发生从业人员利用工作便利盗取客户资料、证书等，引发客户资金风险的事件。（四）其他电子银行重大突发事件。主要包括:发生大规模的电子银行客户群体性事件;其他不可抗力因素对电子银行业务造成严重影响等事件。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第三章  突发事件的分类及级别界定', '第七条', '按照影响范围和严重程度，电子银行突发事件分为以下三级：（一）有可能影响省联社电子银行正常经营管理活动，或者影响全行电子银行业务稳定的事件，已经或者即将出现全行性连锁反应，需要全行协同配合共同处置的突发事件或发生100万元以上客户巨额资金损失、危害严重的突发事件为Ⅰ级电子银行突发事件。（二）涉及两个或两个以上的支行、部，有可能影响省联社电子银行正常经营管理活动，或者影响省联社电子银行业务稳定的突发事件为Ⅱ级电子银行突发事件。（三）涉及两个或两个以上支行、部，有可能对本行电子银行经营管理带来较大影响，或者可能影响本行电子银行业务稳定的突发事件为Ⅲ级电子银行突发事件。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第三章  突发事件的分类及级别界定', '第八条', '电子银行突发事件等级界定：当电子银行突发事件分级指标有交叉，难以判断级别时，应按照较高级别处理；当电子银行突发事件随着事态发展有所升级，按升级后级别处理。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第四章  应急响应', '第九条', '电子银行突发事件应急响应程序（一）电子银行突发事件发生时，本行电子银行部要立即开展处置工作，采取有效措施控制事态发展。及时向省联社报告突发事件情况，上报实行逐级报告制。（二）各支行、部在接到突发事件情况报告后，应在第一时间通过电话将情况向电子银行部报告，随后上报《江苏农村商业银行电子银行重大突发事件报告表》,并在突发事件处置完毕后上报处置结果。电子银行突发事件发生后,本行电子银行部应该按照有关规定报告本地金融监管机构。（三）由省联社发现的电子银行突发事件，电子银行部应向事发行核实情况，进行事件级别认定，对Ⅰ级和Ⅱ级电子银行突发事件立即上报省联社主管领导。属于Ⅲ级电子银行突发事件，通报全行处置。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第四章  应急响应', '第十条', '电子银行突发事件报告内容向省联社上报电子银行突发事件应填写《江苏农村商业银行电子银行重大突发事件报告表》，内容包括：（一）发生突发事件的机构名称、时间和联系人。（二）发生突发事件的原因、性质、等级、影响范围，以及可能涉及的金额和人数。（三）事态的发展趋势，可能造成的损失，已经采取和准备进一步采取的应对措施，需要省联社解决的问题。（四）其他与本事件有关的内容。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第四章  应急响应', '第十一条', '电子银行突发事件信息发布电子银行突发事件发生后，行政办公室根据管理权限和规定程序通过有关媒体对外发布信息或进行公告。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第五章  应急处置', '第十二条', '重大突发电子银行运营事件处置（一）各支行、部应加强对电子银行业务的监测和维护,及时处置电子银行运营事件，确保电子银行系统连续、正常运行。发现电子银行应用系统、应用技术重大问题的，首先积极协调本行信息科技部门予以解决；本行不能解决的，本行信息科技部应及时报告省联社予以解决。（二）在阶段时间内由于交易量过大或其他原因造成网络拥堵的，本行信息科技部应及时向省联社报告请求解决。（三）由于通讯设备或公众网络故障造成业务较长时间中断的，本行信息科技部应及时报告省联社，并积极联系设备服务商或网络运营商，查明原因，排除故障，恢复正常运营。（四）在解决问题的过程中，各支行、部要向客户做好解释说明和安抚工作，及时引导客户通过柜台等其他渠道办理业务，为客户提供持续的、适当的服务，基本满足客户的需要和期望。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第五章  应急处置', '第十三条', '重大突发电子银行案件处置（一）发生批量客户资料丢失、威胁客户资金安全的案件处置。1．各支行、部应加强对客户资料等敏感信息的保护。如发生批量客户资料丢失，从业人员盗取客户资料、客户证书或银行证书等案件，电子银行部要及时协调安全保卫部门向当地公安机关报案，协助公安机关侦破案件。2.对证书被盗等威胁客户资金安全的，各支行、部要立即采取果断措施，通过通知客户更改密码、证书挂失、暂停渠道及在必要时联系信息科技部对涉及客户的账户资金予以暂时止付等手段，确保客户资金安全，尽量减小损失和影响。（二）发生客户巨额资金被盗案件处置。1.各支行、部应加强对电子银行案件的防范工作。发生客户巨额资金被盗案件，本行安全保卫部门应尽早向公安部门报案，并及时向省联社报告；电子银行部应协助公安部门侦破案件帮助客户追缴损失资金。2.本行电子银行部请求省联社协查可疑交易记录情况、客户账户资金情况或者客户资料等信息的，须在案件报告中予以说明。3.由于本行业务、技术等自身原因引发的电子银行业务风险问题，造成客户资金损失的，应依照相关法律法规，承担赔偿责任。各支行、部要首先向客户垫付风险资金，尽量避免媒体炒作；垫付资金的事发行要及时向本行计划财务部报告并申诉理由，安全保卫部根据案件发生实际情况向有关责任方追缴垫付资金。4.各支行、部在处理客户投诉的过程中，要妥善安抚客户，并根据案件性质、客户损失金额大小、客户的情绪和社会影响等因素具体分析，采用不同办法进行处理，尽量把社会负面影响降到最低限度。（三）假冒网站、假冒电话银行、假冒手机银行案件处置。1.本行电子银行部应加强对假冒网站、假冒电话银行和假冒手机银行的监测和预警。发现假冒网站、假冒电话银行和假冒手机银行的，要将有关情况及时报告本行安全保卫部门，由安全保卫部门负责向当地公安机关报案，安全保卫部门同时负责将有关情况上报省联社。2.电子银行部负责将假冒网站、假冒电话银行和假冒手机银行有关情况上报省联社电子银行管理部门并填写《假冒网站（假冒电话银行、手机银行）和可疑电子邮件举报通知书》；负责跟踪假冒网站、假冒电话银行和假冒手机银行的关闭情况，及时将跟踪情况向省联社电子银行部报告。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第五章  应急处置', '第十四条', '突发电子银行违规操作事件处置（一）发生操作人员操作失误或违规操作，导致电子银行业务重大差错或风险的处置。电子银行部应加强对电子银行从业人员的业务培训和管理，避免违规操作事件的发生，发现问题要立即采取措施纠正。对从业人员违规操作引发的重大突发事件，各支行、部要迅速启动本级应急预案,开展先期处置工作，对事件进行调查,核查事件发生真相并评估由此形成的危害程度及影响范围,组织协调有关单位及部门,采取积极的补救措施及后续管理措施,做好控制和降低风险的各项工作，并及时向本行电子银行部报告。（二）发生从业人员利用工作便利取得客户资料、证书，引发客户资金风险的处置。电子银行部应加强对电子银行从业人员管理，发现问题应立即责令离岗，委派其他人员追索丢失资料。尽快联系信息科技部或省联社对涉案资金予以暂时止付，通知客户更换账户密码，必要时通过安全保卫部向公安部门报案。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第五章  应急处置', '第十五条', '电子银行重大突发事件处置（一）电子银行部应加强监测和预警。当行内发生大规模的电子银行客户群体性事件或其他不可抗力因素对电子银行业务造成严重影响等事件时,必须立即向省联社报告。行政办公室、安全保卫部要密切关注外界媒体动向，配合电子银行部利用正面宣传平息事态。（二）发生大规模电子银行客户群体事件和不可抗力因素对电子银行业务造成严重影响的事件，各支行、部要立即开展先期处置工作，做好客户解释和安抚工作，把影响降低到最小程度。（三）根据电子银行突发事件的严重程度，必要时请省联社组织相关部门成立突发事件工作组，迅速赶赴现场，指导突发事件处置工作。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第五章  应急处置', '第十六条', '电子银行突发事件时必须严格执行相应的保密规定。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第六章  后期处置', '第十七条', '调查与情况总结（一）突发事件处置完毕后，本行电子银行部应对突发事件、应急处置工作的有关过程及处置结果进行全面总结，对由突发事件导致的直接和间接损失情况进行收集整理和量化统计，并向省联社电子银行部做出正式报告。（二）本行电子银行部应针对电子银行突发事件处置过程中暴露出的问题，提出切实可行的防范措施和办法，对业务制度、安全控制方式和技术手段等进行改进和完善,并进一步修改、完善本级应急预案。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第六章  后期处置', '第十八条', '责任与奖惩（一）电子银行突发事件处置工作实行领导负责制和责任追究制。对发生下列情况的直接责任人和相关责任人给予纪律处分，并对本行有关负责人进行问责。1.违反本预案规定，漏报、瞒报、谎报突发事件，造成严重后果的；2.违反制度规定或因失职、渎职行为造成突发事件发生的；3.突发事件发生后没有采取有效措施处置，造成重大损失或事态扩大，影响恶劣的。（二）对在重大突发事件应急管理和处置工作中作出突出贡献的集体和个人给予表彰和奖励。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第七章  附则', '第十九条', '本制度由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《电子银行突发事件应急预案》', '第七章  附则', '第二十条', '本制度自发布之日起执行。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第一章  总则', '第一条', '为加强农村商业银行（以下简称“本行”）网上银行（以下简称“网银”）业务管理，促进网银业务的健康发展，根据银监会《电子银行业务管理办法》、《江苏省农村信用社网上银行业务管理办法（试行）》、《江苏省农村信用社网上银行服务业务章程》等有关规定，结合本行实际，制定本办法。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第一章  总则', '第二条', '本办法所称网银业务是指基于互联网技术，通过江苏省农村信用社联合社（以下简称“省联社”）网银平台，并依托省联社各业务系统，为客户提供相关金融服务。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第二章   组织管理体系', '第三条', '开办网银业务的具体职责分工如下：（一）本行电子银行部负责网银业务管理制度及操作流程的制定和完善；负责营业网点网银操作员的管理；负责网银业务指导、市场策划、宣传及营销；负责网银业务考核及数据分析。（二）本行运营管理部负责网银会计业务差错的处理；负责网银业务动态口令牌等重要空白凭证的管理。（三）本行计划财务部负责网银的会计业务操作管理和培训。（四）本行信息科技部负责网银业务系统的维护与保障；协助电子银行部做好网银业务培训及指导工作；负责网银数据采集以及为考核提供技术支持。（五）本行信贷管理部负责配合做好网银业务的功能创新。（六）本行合规管理部负责对网银业务相关制度和操作流程的合规性、有效性进行审核；监督网银业务各项规章制度执行情况。（七）本行风险管理部负责网银业务的风险监测、识别、控制工作。（八）本行审计部负责对网银业务的运行、管理、内部控制等情况进行审计监督。（九）本行事后监督中心负责网银业务申请资料的审核和保管工作。（十）本行对账中心负责客户网银业务的对账工作。（十一）辖内各营业网点承担网银业务的市场营销任务，负责网银各相关制度的贯彻执行；负责受理网银业务的签约/撤销等申请；负责客户网银操作员分配、动态口令牌或刮刮卡的管理及网银业务咨询等基本业务。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第三章  网银客户及服务分类', '第四条', '网银客户是指办理了客户注册手续，申请了客户号，与本行签订了网银服务协议，并取得了动态口令牌或刮刮卡，访问省联社网银站点，进行信息、账户查询，或办理金融交易、衍生业务的个人或企业客户。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第三章  网银客户及服务分类', '第五条', '个人客户可以到本行辖内任一营业网点办理网银注册手续；企业客户必须到开户营业网点办理注册手续，不得跨营业网点办理。客户在网银系统注册成功后，其客户号不可重复使用再行注册网银，但可将其同一客户号下的账户在网银系统进行追加。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第三章  网银客户及服务分类', '第六条', '网银为不同性质的网银客户提供相应的服务。（一）个人网银本行银行卡个人客户、活期存折个人结算账户，需要在营业网点进行个人网银签约后，方可使用个人网银。（二）企业网银本行对公客户，需要在开户网点先进行企业网银开户（签约），方可使用企业网银。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第四章  网银业务流程管理', '第七条', '客户申请开通网银业务（一）个人网银1．客户需办理本行银行卡或在本行开立活期存折个人结算账户。2．客户持本人有效身份证件原件到本行营业网点柜面办理个人网银申请（可在本行辖内的任一网点提交申请），提交签字确认的申请表（网银服务协议在申请表背面一并载明），并留存相关证件复印件。个人网银的签约不得由他人代理。3．营业网点网银操作人员需对客户身份证件的有效性及复印件与原件的一致性进行审核，并在复印件上签注“与原件核对无误”印章及操作人员名章。同时，通过联网核查系统核查打印客户身份信息，无任何疑异后，按客户申请当场为其开通网银，绑定动态口令牌或刮刮卡并打印密码信封交客户签收。4．为保证客户资金安全，各营业网点在经客户同意的情况下，应该为客户在短信服务系统开通“短信通”服务。网银交易使用的短信通无需另填申请及协议，在电子银行申请表上选择开通短信银行，并填写客户本人的手机号码，将账户资金变动情况及时通知客户。5．客户使用密码信封中的签约用户名、初始密码及动态口令牌或刮刮卡的同步密码登录，享受网银提供的更多服务。（二）企业网银1．必须是本行开户的对公客户才能申请开通本行企业网银。2．客户需持相关有效证件原件（营业执照、税务登记证、组织机构代码证、法定代表人身份证等）及所设网银操作人员的身份证件原件，至开户网点柜面提交盖有企业公章和印鉴章的网银开户申请表（网银服务协议在申请表背面一并载明），并留存相关证件复印件。企业网银签约需由法定代表人办理，如由企业其他人员办理，需法人出具授权书，并出具代办人身份证件。3．营业网点网银操作员需对客户证件的有效性、复印件与原件的一致性、客户印鉴的相符性进行审核，并在复印件上签注企业公章、“与原件核对无误”印章及操作人员名章，同时，通过联网核查系统核查打印所有相关身份信息。无任何疑异后，按客户申请当场为企业开通网银，设置审核流程，增加操作员角色分配，打印企业网银操作员密码信封，并为企业网银操作人员进行动态口令牌或刮刮卡的绑定（动态口令牌和密码信封需当场交客户签收）。4．为保证客户资金安全，各营业网点在经得客户同意后，应该为客户在短信服务系统开通“短信通”服务，具体手续与个人开通相同。企业接收短信的须为法定代表人手机号码，如为企业其他人员的，须在授权书中一并载明。5．企业操作员根据营业网点提供的密码信封中的操作员号和初始密码，同时使用动态口令牌或刮刮卡中的同步密码登录企业网银系统，办理各类业务以及账户信息查询。（三）网银开户资料并入当日柜员交易凭证管理。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第四章  网银业务流程管理', '第八条', '网银业务的变更、撤销（一）变更网银业务客户申请变更网银业务（包括变更客户信息、变更操作员信息等），需提交网银业务变更申请表及发生变更的相关客户资料，营业网点网银操作员需按网银开户要求，核对资料无误后留存相关资料复印件，并在相关业务菜单中完成变更操作。（二）撤销网银业务客户申请撤销网银业务，需提交撤销网银申请表及客户相关证件资料，网点网银操作员需按网银开户要求，核对资料无误后留存相关复印件，并在相关业务菜单中完成撤销操作。（三）网银业务变更、撤销资料并入当日柜员交易凭证管理。（四）对于客户账户已销户，但未主动办理企业网银注销手续的，开户行要及时清理，并主动办理网银账户撤销手续，同时留存相关说明资料。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第五章  网银服务内容', '第九条', '网银为客户提供以下服务：（一）查询类服务：账户状态、余额查询、交易明细查询、网银交易查询等。（二）账务类交易服务：转账、支付、汇款、集团服务、代发工资、代理缴费业务、信用卡业务等。（三）理财服务：活期转定期、定期转活期、通知存款管理等。（四）账户管理服务：账户挂失、下挂账户设置等。（五）个性化定制服务：界面排列、业务选择。（六）公共服务：收款人名册维护、修改密码、留言公告查询等。（七）江苏农信及本行以后增加的其他服务。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第六章  网银操作员管理', '第十条', '网银操作员采用分级管理方式，各级、各类操作员的注册、变更和注销应遵循“双人操作、换人复核”的原则，及时办理有关手续。（一）电子银行部网银操作员电子银行部网银操作员由省联社网银主管部门进行设置、分配和管理。（二）营业网点网银操作员营业网点网银操作员由电子银行部网银操作人员进行设置、分配、管理。网点网银操作员新增、变更及注销需向电子银行部提交柜员申请表及操作员身份证复印件。（三）企业网银操作员企业网银操作员由营业网点网银操作人员按照企业申请要求进行设置、分配、管理。为防范资金风险，应至少为企业设定两名以上的网银操作员，使录入、审核互相分离，互相制约。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第六章  网银操作员管理', '第十一条', '营业网点网银操作员要严格在各自的权限范围内操作并强化岗位间的约束机制，严格执行网银业务审核与录入操作分离制度。为保证安全，营业网点网银操作员必须15天更改一次登录密码。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第六章  网银操作员管理', '第十二条', '营业网点网银操作员号必须本人专用，特殊情况下需要移交时必须办理交接手续，并在会计主管坐班日志上登记，交接后必须更换密码。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第七章  网银动态口令牌和刮刮卡的管理', '第十三条', '网银动态口令牌和刮刮卡是由专业的公司提供，通过预置密钥和一定的算法，生成动态口令，客户网银注册时可以与动态口令牌或刮刮卡绑定，以便客户进行网银交易时，后台识别客户身份。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第七章  网银动态口令牌和刮刮卡的管理', '第十四条', '动态口令牌和刮刮卡作为重要空白凭证保管，按重要空白凭证管理制度规定进行管理。（一）动态口令牌和刮刮卡实行专人保管，并严格执行签收手续。各网点从清算中心领回的动态口令牌和刮刮卡由主出纳签收入库保管，同时登记重要空白凭证登记簿。（二）动态口令牌和刮刮卡发放时，须按重要空白凭证支出登记要求进行相关使用登记手续。（三）动态口令牌和刮刮卡在综合业务系统的发放、领用程序与其他重要空白凭证相同。（四）严格执行业务、岗位分离制度，执行动态口令牌和刮刮卡保管与使用分离，各网点不应将保管动态口令牌和刮刮卡的主出纳设为网银操作员。（五）网银操作员按客户申请开立网银时，应凭客户填写的网银申请表和“重要空白凭证领用证”向主出纳申领动态口令牌或刮刮卡，其中企业客户应要求其在“重要空白凭证领用证”上签字并加盖预留银行的企业签章，个人客户应要求其在“重要空白单证领用证”上签字，帮客户绑定成功后，在核心系统做手工收费及销号，交客户签收，并在凭证使用登记簿上做好相关登记。动态口令牌和刮刮卡随用随领，按编号顺序发放，不得向操作员多发。（六）动态口令牌和刮刮卡与操作员一一对应，不可重复绑定；口令牌和刮刮卡解绑定后即作废，不可循环使用。（七）已销户的网银必须收回动态口令牌或刮刮卡并进行登记，各网点要在每个季初的3个工作日内向运营管理部和电子银行部上报上季度网银开户及销户动态口令牌和刮刮卡使用的明细清单（附件1），并将销户的动态口令牌和刮刮卡附销户清单按季上缴电子银行部。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第七章  网银动态口令牌和刮刮卡的管理', '第十五条', '动态口令牌和刮刮卡的挂失管理动态口令牌和刮刮卡的挂失管理包括挂失和解挂失，个人网银客户挂失、解挂失可在本行的任一网点办理，单位网银客户需到注册网点办理。客户动态口令牌和刮刮卡丢失，应至本行网点柜面办理挂失。解挂失也需至网点柜面对已挂失的动态口令牌和刮刮卡进行解挂，但解挂失必须是客户没有要求重新绑定新的动态口令牌或刮刮卡。如挂失后，客户需要重新绑定新的动态口令牌或刮刮卡，柜员要先对旧的动态口令牌或刮刮卡做解绑定，再做新动态口令牌或刮刮卡的绑定。动态口令牌和刮刮卡的挂失、解挂失，个人客户需携带本人身份证件原件，填写申请表到本行网点柜面办理挂失、解挂失手续，不得由他人代理。企业客户如为非法定代表人办理，需同时出具法人授权书、法定代表人身份证原件及代理人身份证原件，法定代表人办理的仅须提供本人身份的原件，其余手续与个人客户相同。受理网点审核无误后，留存相关证件复印件，完成相关操作。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第七章  网银动态口令牌和刮刮卡的管理', '第十六条', '动态口令牌和刮刮卡的解锁客户输入动态口令错误超过3次数时，会导致动态口令牌和刮刮卡的锁定，客户在未去柜面解锁前无法再使用动态口令，客户需要持有效证件至柜面申请动态口令牌和刮刮卡的解锁。动态口令牌和刮刮卡的解锁手续与挂失、解挂相同。企业客户需至注册网点申请解锁，个人客户可至本行任一网点办理。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第七章  网银动态口令牌和刮刮卡的管理', '第十七条', '动态口令的同步管理动态口令牌和刮刮卡与动态口令后台管理不同步时，客户每次输入的正确动态口令到后台验证时不能通过，客户需要持动态口令牌或刮刮卡到网点柜面做动态口令的同步处理。动态口令的同步申请与动态口令牌和刮刮卡的挂失、解挂、解锁相同。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第七章  网银动态口令牌和刮刮卡的管理', '第十八条', '凡使用动态口令或密码进行的操作，均视作客户本人所为。客户应妥善保管动态口令牌或刮刮卡和密码等，如因保管不当,因密码泄露或动态口令牌或刮刮卡丢失等造成纠纷或损失，由客户自行负责。在客户动态口令牌或刮刮卡有效期内，客户的动态口令牌或刮刮卡毁损，应及时办理补领手续，在更新生效前造成的资金损失由客户承担。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第十九条', '网银开立、变更、撤销及动态口令牌或刮刮卡重新申领、动态口令牌或刮刮卡解锁、密码重置等操作的申请表及留存的相关资料复印件统一列入当日传票上缴至事后监督中心进行业务监督和保管，并做好交接手续。事后监督中心设专人结合客户原开立结算账户的资料，审核客户网银申请资料的真实性、完整性，留存印鉴的真实性，如发现异常，立即退给营业网点，并通知该网点针对异常情况采取有效的处理方式。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十条', '网银业务账务处理、资金清算纳入综合业务系统、信用卡系统等原相关系统中进行。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十一条', '网银根据注册客户的电子指令为其办理转账付款等业务。根据电子指令生成的凭证加盖银行和经办人员印章后，作为合法、有效的原始凭证。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十二条', '网银交易由客户发起。系统对客户发起的行内转账业务进行实时处理；跨行转账，根据各地人民银行所规定的汇款服务时间有所不同（一般为工作日的9:00－17:00，汇款金额在5万元以下的快速汇款可实行7×24小时运行），在服务时间内办理跨行转账汇款，资金实时到达收款行，但具体到账时间取决于收款行系统。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十三条', '网银业务以转出方账户开户机构为业务发起网点。业务系统将网银系统提交的业务数据分业务种类传送业务发起网点，各网点根据系统提示打印代用凭证或分科目清单，审核盖章轧平当日账务后作为当日会计记账依据订入传票作为会计档案统一保管。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十四条', '开办网银业务的各营业网点应设专人负责网银业务的会计处理。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十五条', '凭证清单打印（一）客户凭证打印。企业客户可通过网银提供的相关查询打印功能，实现网银相关交易凭证的打印、补打。个人网银相关交易明细可通过网银提供的查询交易查询或通过本行辖内的柜面实现查询打印。（二）经办机构业务清单打印。经办机构每日营业终了，打印相关业务清单，参照会计档案管理要求进行装订和保管。（三）管理部门统计报表打印。管理部门需要时打印相关统计报表及各类监测数据。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十六条', '网银交易手续费收取标准：客户办理转账付款业务按中国人民银行的有关规定交纳邮电费、手续费等费用以及本行相关规定，以从客户账户内扣划方式收取。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第八章  业务处理', '第二十七条', '其他费用收取标准：动态口令牌和刮刮卡工本费、动态口令牌和刮刮卡挂失手续费等按相关收费标准执行。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第二十八条', '本行网银安全控制体系由省联社负责。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第二十九条', '本行各网银业务相关部门按照职责协助省联社做好网银的安全管理及风险防控工作。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第三十条', '与网银业务账务处理、资金清算相关的内部控制按本行相关制度要求执行。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第三十一条', '网银经办人员在客户申请注册时必须向客户进行必要的网银安全提示，在密码设置、病毒防范等方面给客户以必要的指导。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第三十二条', '事前监督各营业网点要严格按照网银业务的相关规定办理业务，加强对网银操作员操作权限的管理。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第三十三条', '事中监督各营业网点网银操作员要严格审核客户开办网银业务的条件，杜绝冒名开办网银业务，挪用资金的违法违规事件的发生。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第三十四条', '事后监督（一）要按照会计监管及网银业务操作流程的要求将网银业务纳入会计监管范围，实施综合业务事后监督体系的统一管理，有效防范和控制风险。（二）网银经办网点的会计主管应对本营业网点的网银业务进行逐笔检查。检查的内容包括：客户网银的开通、变更、注销，密码重置，动态口令牌和刮刮卡的挂失、解挂、解锁等，手续是否齐全；客户网银交易凭证是否及时打印；客户通过网银进行的交易是否符合上报可疑交易标准，如符合，是否及时上报等。（三）事后监督中心对营业网点提交的客户网银资料进行再次审核，核对注册网银的资料是否齐全，严格审核申请表各要素的完整性。（四）事后监督中心一旦发现客户有违规、违法等异常现象，应立即暂停客户的网银支付功能，并与客户取得联系，待异常消除后重新启用。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第九章  网银安全管理', '第三十五条', '根据国家反洗钱的有关规定，网银系统处理的所有交易必须纳入反洗钱处理和监测系统中，由各营业网点进行监控和报告。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第三十六条', '网银重大事项包括服务中断、业务差错、风险事件等。（一）网银服务中断是指由于系统故障或者不可抗力等原因造成客户暂时无法办理网银业务的情形。（二）网银业务差错是指由于系统故障、操作员操作失误等原因造成的错记、重记、漏记或少记客户账务等差错的情形。（三）网银风险事件分为业务风险事件、技术风险事件、法律风险事件和声誉风险事件。其中，业务风险事件是指由于网银客户自身原因造成其信息或账户资金被非法窃取的各类事件；技术风险事件是指网银系统存在安全漏洞、遭到攻击或破坏的各类事件；法律风险事件是网银业务相关法律诉讼事件；声誉风险事件是指媒体负面报道、聚众滋事等影响网银声誉的各类事件。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第三十七条', '各营业网点发现网银重大事项后，应填写《网上银行业务重大事项报告表》（附件2），及时报告。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第三十八条', '网银重大事项的处理情况实行动态管理，各营业网点应及时将重大事项的最新情况通过《网上银行业务重大事项处理情况跟踪信息表》（附件3）报告。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第三十九条', '网银业务应急处理应在有效控制风险的前提下，提高应急处理效率，尽量缩小不良影响范围。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十条', '如遇网银服务中断，本行采取要求客户到营业网点柜面办理业务的应急方式；对于集团客户主动收款业务，建议客户直接通知其分支机构到开户机构办理付款手续；对于客户已提交的指令不采取应急处理。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十一条', '遇到网银服务中断情况，各相关业务部门要立即排查原因，尽快恢复系统，并做好客户解释工作。对于因系统故障等原因造成客户无法办理网银业务的，应建议客户到指定营业网点办理。短期内不能恢复服务的，要以电话、传真等方式通知重点客户。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十二条', '因系统升级或改造而需要暂时中止提供网银业务服务超过3小时（含）时，各营业网点应在接到本行通知后，及时告知客户，以便客户做好准备。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十三条', '各网点应建立应急业务登记簿，对需要进行应急处理的业务要逐笔详细登记，每日营业终了，会计主管应将应急业务登记簿和手工凭证及附件进行核对。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十四条', '营业网点操作员无法登录网银内部管理系统时，应主动查找原因，待网络恢复正常后再办理业务。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十五条', '网银业务差错处理应遵守据实、准确和及时的原则。各营业网点应指定专人将网银业务差错详细备案登记，记录内容应包括差错时间、差错内容、处理部门、处理人员姓名、客户资料、差错影响或损失、差错原因、处理结果等，及时提交电子银行部。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十六条', '各网点发现或接到客户反映出现网银差错的，应根据差错情况进行相应处理。（一）系统原因造成的业务差错，信息科技部及网银相关业务部门要配合查清原因，提出处理意见，由部门负责人签字确认，送交相关业务部门按照规定进行账务冲正处理，涉及手续费、资金汇划费的要同时冲正。涉及其他机构账务的，应报上级主管部门协调解决；（二）柜员操作失误造成的业务差错，按本行相关业务规定处理。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十七条', '因自身系统、内控制度或为其提供服务的第三方机构的原因，造成电子支付指令无法按约定时间传递、传递不完整或被篡改，并造成客户损失的，各营业网点应及时做好安抚工作，并及时汇报本行主管部门及分管领导，以便尽快处理，减少损失和负面影响。因第三方服务机构的原因造成客户损失的，本行将根据与第三方服务机构的协议对其进行追偿。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十八条', '由于客户自身原因造成的电子支付指令未执行、未适当执行、延迟执行等业务差错，各营业网点应主动通知客户改正或配合客户采取补救措施。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第四十九条', '客户通过网银办理异地汇款业务，款项汇出后若由于账号、户名不符、汇款无人领取等原因需要汇入行退回的，按柜面汇入行主动退汇的处理手续处理。其中对于因账号、户名不符导致挂账需要退汇的，汇入行应在当日（最迟在三个工作日内）办理退汇手续。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十章  重大事项处理', '第五十条', '网银风险事件的处理应遵守审慎、高效、保密的原则，各相关部门要积极配合，通力合作，妥善处理好风险事件。（一）电子银行部负责处理网银风险事件并逐级报告，协助本行安全保卫部门向公安机关报案，配合公安机关进行调查，组织对客户的安全宣传教育工作。（二）安全保卫部负责就网银风险事件向公安机关报案，协助电子银行部处理风险事件，配合公安机关的调查工作。（三）信息科技部负责技术分析，研究制定有关技术应对方案；向电子银行部和安全保卫部门提供网银风险事件涉及的交易信息查询等技术支持工作。（四）各营业网点负责监督内部人员的操作，一旦发现内部人员利用从事网银工作之便窃取客户信息或账户资金的事件，应立即向领导和上级主管部门汇报。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十一章  收费管理', '第五十一条', '营业网点根据本行网上银行业务资费标准收取年费、工本费等相关费用。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十一章  收费管理', '第五十二条', '营业网点为客户办理动态口令牌和刮刮卡挂失时，根据种类按规定收取费用。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十一章  收费管理', '第五十三条', '网银客户在办理转账、汇款等业务时，须支付相应手续费时，手续费将直接从网银绑定账户扣收，手续费按本行网上银行业务资费标准收取。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十一章  收费管理', '第五十四条', '本行网银的收费按照收费标准执行，本行按照相关规定可以对收费标准进行调整。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十二章  罚则', '第五十五条', '本行员工在网上银行运行过程中存在违规行为的，对相关责任人按本行相关规定记分，并视情节轻重，追究相关人员的行政和法律责任。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十三章   附则', '第五十六条', '本办法由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《网上银行业务管理办法》', '第十三章   附则', '第五十七条', '本办法自印发之日起实施。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第一章  总则', '第一条', '为加强农村商业银行（以下简称“本行”）自助设备规范化管理，确保自助设备安全稳定运行，充分发挥自助设备作用，提升自助设备服务质量，根据银监会《关于商业银行ATM等专业机具管理有关事项的通知》、《江苏省农村信用社自助银行管理办法》等有关规定，特制定本办法。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第一章  总则', '第二条', '本办法所指自助设备是指本行提供的、供客户自助进行存款、取款、转帐、理财、查询、改密、代缴费等金融服务的自助设施，包括自动取款机（ATM）、自动存取款机（CDS）、存折补登机、网银机、自助终端等设备（银行提供商户使用的转账电话、POS机的管理另有办法规定）。自助设备管理包括上述设备及相应的服务场所和设施的管理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第一章  总则', '第三条', '自助设备管理实行统一规划，统一规范，统一监控，分级管理的原则。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第一章  总则', '第四条', '本办法对自助设备安装、运营和维护等各个环节进行规范，各级行、各部门的自助设备管理工作均须遵守本办法。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第五条', '本行人力资源部负责设立、迁址、撤销自助银行（设备）向当地银监局的报批（备）及有关统计、上报工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第六条', '本行电子银行部负责自助设备制度建设、规划布局、提交需求、市场营销、业务推广、统计分析、考核评价等工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第七条', '本行信息科技部负责自助设备的安装管理、故障处理、预防性维护、升级管理、停机管理和服务质量管理等技术管理工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第八条', '本行运营管理部负责自助设备现金管理、账务核算和运行管理等工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第九条', '本行安全保卫部负责自助设备安全保卫工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第十条', '本行行政办公室负责自助银行（自助区）建设及规范化工作，自助设备配套设备的安装及维护工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第十一条', '本行合规部负责自助设备各项制度及业务的合规审查。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第十二条', '本行风险管理部负责自助设备各项业务的风险监测。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第十三条', '本行审计部负责自助设备的核算审计工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第二章  机构、人员职责', '第十四条', '各营业机构负责自助设备日常运行管理工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第十五条', '自助设备设立地点须远离强振动源、强噪声源、强电磁场；不得设立在用水设备的下层和存放腐蚀、易燃、易爆物的地方。自助设备安装用房的四周墙体和门窗必须坚固，玻璃应符合防爆要求。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第十六条', '自助设备的强、弱电线路必须分开铺设。市电、不间断电源（UPS）必须有单独的供电线路，不得与其他线路混用，UPS要单独设立；UPS电源插座面板要有明显标识；自助设备的接地电阻必须小于2欧姆。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第十七条', '监控、报警装置。（一）自助设备要设立监控系统、安防报警系统，配备必要的消防灭火设备，并符合保卫、公安部门的相关安保标准。监控系统须能监控取款人、出钞口和加钞环境；自助服务区须设立对整体环境的监控。（二）自助设备须建立集中式录像监控系统。（三）自助服务区须设立应急灯，安装警铃或求助电话，当发生意外时，客户可以迅速按警铃报警或通过求助电话进行求助。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第十八条', '自助设备员工操作间要安装空调，并在环境温度超出设备正常工作温度时开启；自助设备网络线路对外不得露明线。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第十九条', '自助设备服务区（对外称自助银行，以下简称自助服务区）建设规范。（一）新设自助服务区使用面积一般不得小于，空间高度要符合自助设备的安装、维护要求；自助服务区必须采用综合布线系统。（二）自助服务区要有单独的供电线路，不得与其他线路混用（即单独从外线输出配电柜拉出一相线，安装单独开关）。（三）自助服务区要根据具体情况选择门禁系统或按钮门、推拉门、自动门等进出方式。若安装门禁系统，则门禁电源要采用ＵＰＳ电源线路，门禁在遇到停电时必须能自动解锁，以保证客户能够手动将门扇平滑推开。有条件的自助服务区要建立客户无障碍通道。自助服务区客户操作区要设立1米线标志。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第二十条', '自助设备及相应区域、设施的形象标识、灯光、告示张贴等规范由本行按照有关规定统一制定并下发执行。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第二十一条', '自助设备及自助服务区的施工建设方案须报本行电子银行、信息科技部、安全保卫部等部门审批后实施，建设规范和标准须符合本办法及其他有关规定。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第三章  自助设备设立规范及安装验收管理', '第二十二条', '新装自助设备在安装、测试正常后，设备管辖机构进行签字验收，并对设备出厂密码进行更新；自助设备及自助服务区必须经建设或改造主管部门组织验收合格后方可提供对外使用。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第二十三条', '自助设备分为在行式和离行式，在行式设备由所在网点负责日常管理，离行式设备由提解中心集中管理。区域离行式设备达到一定规模的，要进行集中式管理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第二十四条', '各营业网点应设立至少一名自助设备管理员，负责本网点自助设备的日常管理工作；应建立自助设备管理登记簿；自助设备管理人员应对加钞、轧库、吞卡、投诉、维修、维护、保养、日常管理和差错处理等工作情况进行详细登记。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第二十五条', '自助设备的密码、钥匙管理（一）自助设备的密码、钥匙必须双人分管，平行交接，钥匙应入库保管，不得随身携带；备用密码应密封后交网点负责人入库保管，备用钥匙应密封后交运营管理部入库保管，并设立交接登记簿；备用钥匙与密码非急需不得启封，如需启封，必须经网点负责人批准，使用完毕后立即密封入库保管。（二）自助设备管理员调动、休假时，须办理交接手续（交接内容包括密码、钥匙、钱箱等），设立交接登记簿，做好交接登记，并由网点负责人负责监交。（三）自助设备密码须至少每三个月更换一次，重置密码须由网点负责人核实，并做好登记，密码更改后密封交网点负责人入库保管。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第二十六条', '自助设备现金管理（一）自助设备的钞箱视同金库管理，实行双人开箱、双人复核、双人装钞。现金的领取与上缴，必须执行我行出纳制度，实行双人清点、双人押运，办理出入库手续。现钞要符合自助设备使用要求。（二）自助设备轧库加钞步骤1．暂停设备，清点钱箱，核对账款（离行设备加钞须通知在行人员配合核对），发现长短款或其他异常情况时，须按规定登记、处理。2．现金放入自助设备时，应按面额和币种对号入箱，严防现金放错钞箱，造成设备付款错误（有备用钞箱的，轧库加钞也可通过直接更换钞箱，事后再轧库的方式进行）。3．加钞后须对存取款业务各进行一笔交易，确认设备运转正常。4．填写自助设备管理登记簿相关内容。（三）对钱箱进行现金操作须在录像监控范围内进行。（四）正常工作日期间，对自助设备要每天进行轧库加钞及清钞，加钞量以满足一天的取款量为宜；节假日期间，轧库加钞时间可延长至2-3天一次（周末双休日为2天），加钞量以满足2-3天的取款量为宜。（五）离行式设备加钞、清钞的现金在途押运必须严格执行现金押运规定，现金进出操作间或现金操作在开放环境进行时，要进行安全警戒。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第二十七条', '自助设备管理操作时必须局部清场，双人操作，人离机锁。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第二十八条', '自助设备日常管理（一）对在行式自助设备，自助设备管理员须每日早晚检查、维护自助设备及周围环境各一次；对离行式自助设备须每天至少进行一次检查、维护。检查、维护要求为：保持钞箱、打印纸充足，保持色带打印清楚，保持自助设备插卡口、进钞口、出钞口正常，保持自助设备及周围地面、墙面环境整洁、完好，检查自助设备监控录像系统是否正常，保持灯光照明设施正常，检查周围是否有非法告示、无关广告、非法摄像头或其他异常情况，及时更换破损的操作指南、联网标志，公告须按要求张贴并及时清理。遇到情况要及时处理或向有关部门反映。检查、维护结束后须进行一笔实际交易，以确认设备运作正常。（二）自助设备管理员每日要对自助设备员工操作间电源、监控录像、网线、空调等设施进行检查，确保各项设施运转正常。（三）出现以下情况时，由自助设备管理员负责自主处理：客户凭条打印机和流水账打印机缺纸、塞纸、更换色带；读卡机夹卡；键盘按键卡住；由于钞票质量或放置不当所引起的出钞不顺或夹钞。（三）自助设备流水纸带用完后，须按卷归档保管，并标明起止日期。（四）自助设备管理员每月须对机柜进行除尘清洁处理。（五）自助银行（自助设备）要安排人员进行安全巡查，巡查间隔时间一般为2-3小时，每晚检查2-3次。巡查内容为：查看门禁系统、自助设备上方等处有无非法安装的解码器、摄像头等，有无诈骗性或非法张贴的公告，自助设备插卡口、出钞口等有无堵塞；发现异常情况要高度重视，认真进行分析，并调取录像查看，当发现可能对客户形成风险时，要及时提醒客户更换密码。（六）自助设备的各项检查、维护工作须做好记录。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第二十九条', '设备故障处理（一）在自助设备出现故障时，网点自助设备管理员应先进行自检，在故障无法自行修复时，及时按规定进行报修。如遇重大技术问题，管理员应上报本行电子银行部，电子银行部会同相关部门、维护公司迅速采取措施排除故障。对于可能造成银行损失的问题，管理员应采取关停机等紧急措施，并立即上报。（二）维护过程管理1．维护公司自助设备维修护人员（以下简称维护人员）到场进行设备维护时，必须出示本人的维护工作证，经网点自助设备管理员审查有效后方能进行维护。维护工作须在网点自助设备管理人员的陪同下进行。2．维护人员使用自助设备的磁盘、U盘、可读写光驱等输入/输出设备，应有正当理由，并经网点陪同人员同意，在陪同人员的监督下使用。3．维修人员排除故障后，自助设备管理人员应进行实际交易确认设备正常运行；对存取款交易，交易金额应覆盖所装钞箱的所有面额种类。4．维护公司对自助设备进行维修，自助设备管理员须进行登记，并由维护人员签字确认。5．维护结束后，自助设备管理员要对维护公司维修报告中有关栏目进行如实填写并加盖本人签章。（三）维护公司按照维护合同主动安排对我行自助设备进行检查保养时，须提前通过本行电子银行部进行安排，并提前通知设备管辖网点。具体检查保养过程管理同故障维护管理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第三十条', '自助设备因移机、网点装修等情况需长时间停机时，应向本行电子银行部报告；自助设备开通或恢复开通时，须向本行电子银行部报备。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第三十一条', '自助设备应用过程中出现可能影响到本行设施安全、本行或客户资金安全的紧急情况时，网点相关负责人和自助设备管理人员应及时到现场处理，并及时向本行有关部门报告。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第三十二条', '网点遇到客户关于自助设备使用方面的咨询投诉，应耐心做好答复解释工作。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第三十三条', '自助设备监控录像资料保存时间不得低于3个月。外部人员或机构需调阅录像的，要按照有关规定处理；内部因工作需要需调看有关录像资料的，须经网点负责人同意。涉及治安、冒领、诈骗的要及时向本行安全保卫部门报告，以便迅速处理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第四章  日常管理', '第三十四条', '网点相关负责人须对本网点各项自助设备管理工作进行经常性的监督检查，确保各项工作按照有关规定执行。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第一条', '吞卡处理'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第三十五条', '自助设备管理员应每日拿取被吞卡片，并及时进行登记，被吞卡片应由专人负责保管。由于被吞卡片保管不当造成的损失由自助设备管辖网点承担。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第三十六条', '吞卡因发卡机构指令性吞卡而引起的，客户不得领取被吞卡片。对非发卡机构指令性吞卡，持卡人可在吞卡后次日起三个工作日内（特殊情况下最长可延长至十个工作日内），持本人有效身份证件及其它可以证明为卡片持有者的材料到自助设备的挂靠机构办理领卡手续；客户有要求的，在行式自助设备挂靠机构可在营业期间当时为客户拿取被吞卡片。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第三十七条', '经办人须按以下程序核对客户相关资料，无误后，登记客户相关资料，客户在登记簿上签名并领取卡片。（一）对卡片正面有持卡人姓名字母或背面有持卡人签名的本行及他行银行卡，须核对客户本人身份证件上的姓名与所领卡片正面持卡人姓名字母及背面签名一致。（二）对不满足本条第一款条件的能调阅卡片资料的本行银行卡，须核对卡片资料中卡片持有人的姓名及身份证件号码与客户本人身份证件上姓名与身份证件号码一致。（三）对不满足本条第一、第二款条件的本行及他行银行卡，在检查本人身份证件后，须通过卡片个人密码验证（如通过查询交易等）。（四）能确认所领卡片持卡人身份的其他法律认可的方式。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第三十八条', '如所吞卡片逾期无人领取，须对卡片进行剪角处理，即沿卡片背面左上角磁条下沿且与水平不大于45度角方向进行剪切处理，破坏磁条，在银行卡管理登记簿上注明“作废”字样，卡片作废卡处理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第二条', '长短款处理'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第三十九条', '自助设备本行卡长款是指本行卡在我行自助设备上取款或存款，未能正常出钞或存入而发生的长款或其他原因发生的长款。自助设备他行卡长款是指他行卡在我行自助设备上取款，未能正常出钞而发生的长款或其他原因发生的长款。自助设备短款是指因我行自助设备机械故障多吐钞或其它原因而发生的短款。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第四十条', '长款处理（一）轧库时发现长款，应详细进行登记，不得以长补短。（二）长款的处理按照《江苏省农村信用社银行卡差错处理规程》及其后续的相关规定执行。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第五章  自助设备业务差错处理', '第四十一条', '短款处理（一）轧库时发现短款，应立即停机检查自助设备，查阅日志文件和流水账，调阅监控资料，查找短款原因，进行登记并向上级主管部门报告，在原因查明之前不得开机运行。（二）自助设备管理员要及时联系相关人员查找原因，如系机器故障造成短款，责任由设备供应厂商或保修厂家承担；如因我行原因造成，短款的处理按照《江苏省农村信用社银行卡差错处理规程》及其后续的相关规定执行。（三）发生短款的设备检修后，须经测试正常方可对外使用。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第六章  自助设备运行、监控、监督、投诉及考核', '第四十二条', '大堂式自助设备，对外服务时间与网点或商户营业时间相同；穿墙式和自助银行式自助设备，实行全天24小时服务。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第六章  自助设备运行、监控、监督、投诉及考核', '第四十三条', '本行电子银行部对全行自助设备运行情况进行监控，当自助设备出现应用障碍（如缺纸、缺钞及其他故障等）时，系统产生报警信息，并通过短信平台自动将故障信息发送给网点相应管理人员，以及时处理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第六章  自助设备运行、监控、监督、投诉及考核', '第四十四条', '省联社客户服务中心及本行电子银行部受理客户有关自助设备使用的咨询、投诉，并及时解答或协调处理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第六章  自助设备运行、监控、监督、投诉及考核', '第四十五条', '自助设备管辖机构接到本行自助设备故障处理超时督办要求，须做好记录，并及时联系有关人员进行处理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第六章  自助设备运行、监控、监督、投诉及考核', '第四十六条', '网点自助设备管理人员收到本行监控系统发送的故障信息后，须及时进行处理，遇到无法解决的问题须及时上报。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第六章  自助设备运行、监控、监督、投诉及考核', '第四十七条', '电子银行部对自助设备的运行维护情况进行定期与不定期的检查考核，并将检查考核情况进行通报。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第七章  安全管理', '第四十八条', '本行信息科技部要将自助设备技术安全管理纳入计算机安全管理范围，定期不定期开展对自助设备相关技术设施安全情况的检查。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第七章  安全管理', '第四十九条', '自助设备软件在正式投入使用前，须进行测试。测试包括测试环境下的集成测试与业务测试。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第七章  安全管理', '第五十条', '本行信息科技部须定期对自助设备运行的相关设备、系统和环境进行技术安全检查和维护，保证自助设备的安全稳定运行。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第七章  安全管理', '第五十一条', '自助设备的相关前后台电子交易资料（包括自助设备上的交易流水、前置系统的交易资料）须保存一年备查。自助设备的纸质流水记录及相关登记簿、必须按有关会计档案的要求进行保管。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第七章  安全管理', '第五十二条', '自助设备及相关系统的软件、技术参数及技术资料由信息科技部按有关规定管理。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第八章  奖惩处理', '第五十三条', '对以下违反本办法的行为，对有关责任单位和人员按有关规定给予行政处分和经济处罚。 （一）各营业网点未按本办法要求设立相应自助设备管理人员，或相应管理职责不落实的；（二）自助设备管理人员未按本办法规定履行各自职责，或履行职责有误的。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第八章  奖惩处理', '第五十四条', '对自助设备管理工作做出突出贡献的单位和人员，给予表扬和奖励。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第九章  附  则', '第五十五条', '本办法由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《自助设备管理办法》', '第九章  附  则', '第五十六条', '本办法自发文之日起执行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第一章  总则', '第一条', '为加强和规范自助设备管理，提高自助银行业务的运营质量和综合效益，根据《自助设备管理办法》等有关规定，特制定本规程。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第一章  总则', '第二条', '本规程适用于自助取款机、自助存款机、自助存取款机、自助服务终端等自助设备。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第一章  总则', '第三条', '本规程所称自助设备运营内容包括自助设备的到货验收、安装开通、巡检维护、现金管理、账务核对与差错处理、吞没卡处理、客户服务、风险防范、档案管理等。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第一章  总则', '第四条', '总行所辖各级机构在自助设备管理、运营过程中，必须执行本规程。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第二章  机构及岗位职责', '第五条', '自助设备运营操作涉及机构和岗位主要包括总行、总行自助设备管理员，营业网点、营业网点负责人、网点自助设备管理员等。本规程主要对以上岗位职责进行明确和规范。总行相关职责参照《自助设备管理办法》执行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第二章  机构及岗位职责', '第六条', '总行电子银行部设置总行自助设备管理员岗位，负责落实自助设备管理员岗位责任制。总行自助设备管理员负责辖内自助设备管理，落实、监督、检查各项业务制度执行情况。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第二章  机构及岗位职责', '第七条', '总行自助设备管理员职责（一）负责配合总行做好辖内自助设备选址布局和安装验收等工作；（二）负责辖内新增及迁址设备的安装调试，设备停运及各种事项申报、相关业务数据统计、业务监控及业务系统参数的增加、删除、修改上报工作；（三）负责辖内自助设备运营管理，组织设备巡检工作，保障设备稳定运行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第二章  机构及岗位职责', '第八条', '营业网点职责（一）负责自助设备管理，落实、监督、检查各项业务制度执行情况。（二）负责落实自助设备管理员岗位责任制，设置营业网点设备管理员岗位，至少配备1名设备管理员、1名加钞员，自助设备管理员要相对固定。（三）负责协助自助设备安装、验收、开通等工作。（四）负责自助设备的日常巡检、维护报修、耗材管理、账务核对与差错处理、吞没卡处理、登记簿与交易流水档案管理、周边环境清洁等。（五）负责自助设备的清钞、加钞等现金管理以及自助设备钥匙和密码管理。（六）负责自助设备安全巡查与监控管理，定期调阅监控录像资料。（七）负责受理持卡人有关自助设备的咨询、投诉及服务引导工作，遇重大问题及时向总行报告。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第二章  机构及岗位职责', '第九条', '营业网点负责人职责（一）负责自助设备管理员及加钞员岗位管理。（二）负责加钞、调款等现金管理以及差错处理等业务管理的检查工作。（三）监督指导营业网点自助设备制度执行情况、客户咨询投诉处理、风险控制等工作。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第二章  机构及岗位职责', '第十条', '网点自助设备管理员网点自助设备管理员分为加钞员（A岗人员）、设备管理员（B岗人员）。设备管理员（B岗人员）为自助设备的第一责任人。各岗位职责如下：（一）A岗人员1、保管自助设备保险箱钥匙;2、为自助设备进行加钞、配钞;3、掌握自助设备现金头寸。（二）B岗人员1、保管自助设备保险箱密码；2、配合A岗人员选钞、配钞，配钞时必须排除安全隐患，严格按照制度要求配款；3、负责账务核算，检查更换打印机流水用纸、凭条纸、色带，汇总、打印主机提供的各种清单，填制相关凭证；4、负责对自助设备显示屏、键盘、插卡槽和打印机、出钞模组等设备进行定期检查、清理；5、负责向持卡人宣传、解释自助设备的使用方法和受理业务的时间、范围，听取持卡人的意见和建议，并及时汇总上报；6、负责向有关部门反映因通讯、程序及机器设备等原因造成的故障；7、有义务监督、报告、监控录像设备的运行情况。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第一条', '自助设备签收、安装验收与上线运行'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十一条', '自助设备到货后，总行电子银行部、营业网点及设备供应商三方同时到场完成设备签收工作，确认外包装是否完好、防倾斜标志是否正常，记录设备序列号等，填写《自助设备到货验收单》，上报电子银行部。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十二条', '自助设备要在到货签收后一周内完成安装、调试工作并上线运行。因特殊原因不能安装的要进行迁址安装。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十三条', '自助设备安装前，人力资源部按照有关要求向当地监管部门和公安部门履行报批或报备手续。设备安装要符合总行相关规定和要求。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十四条', '总行负责组织自助设备安装工作，营业网点、设备供应商配合实施，包括提出安装申请，落实设备安装环境的准备工作，申请通讯线路，协助上级行进行强弱电测试、设备参数分配、网络接入、软硬件调试，申请并协助上级行进行摄像监控系统的安装、调试，防雨、防尘设施安装，标识标牌张贴，运营环境检查等工作。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十五条', '设备安装完毕后，总行、营业网点共同进行验收，验收内容包括设备软硬件测试、界面测试、正常交易测试、异常交易测试等。由总行电子银行部统一进行监控、管理和考核。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二条', '自助设备巡检与维护'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十六条', '在行式设备，网点设备管理员每日营业前和营业终了要进行巡检；离行式设备巡检由营业网点负责，每日巡检不少于一次。巡检内容包括对自助设备机身、银亭、灯箱广告等进行清洁，清除非法张贴物和非法装置；补充流水打印纸、凭条打印纸、色带等耗材，处理卡钞、卡纸、吞没卡等；检查设备监控摄像头，防止堵塞或损坏。巡检结束后要进行一笔实际交易，确认设备运行正常,并填写《自助设备管理登记簿》，签字确认。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十七条', '网点设备管理员通过自助设备监控管理系统发现缺纸、缺钞、吞卡或其他故障的，要及时处理。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十八条', '总行自助设备管理员要定期、不定期地组织对自助设备及运行环境的全面巡检（包括夜间巡检）。巡检完毕，填写《自助设备管理登记簿》。发现问题的，通知营业网点负责人及时解决。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第十九条', '自助设备出现故障后，网点设备管理员要先做自检，及时排除故障。如无法排除，网点设备管理员要及时通知设备维护厂商或科技部门进行现场支持。对因设备故障可能造成银行损失的，自助设备管理员要采取必要的关停机等紧急措施，并上报总行电子银行部。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十条', '设备维护维修前，网点设备管理员要检查设备吞卡槽和出钞通道，取出吞没卡和卡钞，在确认设备无吞没卡和卡钞后方可允许进行维修。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十一条', '设备维护厂商进行设备维护维修时，网点设备管理员必须到达现场并全程陪同，审核维护维修人员的身份证和工作证件（或介绍信）。如维护维修工作涉及自助设备的硬盘、密码键盘、验钞模块或系统软件安装与更新、参数调整、工作日志提取等，须我行技术人员现场监督。如需更换设备配件，营业网点管理员需检查新配件的型号、性能是否与原模块一致，防止恶意更换部件。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十二条', '自助设备维护维修时，未经批准，网点设备管理员不得允许厂商维护人员将U盘、笔记本电脑等任何外部设备接入我行自助设备，不得允许将拆卸后的硬盘带走。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十三条', '维修工作完成后，网点设备管理员进行设备验收，并进行一笔实际交易，在《江苏农村商业银行自助设备管理登记簿》上记录故障报修时间、原因、修复时间等信息，将情况反馈至总行电子银行部。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三条', '密码钥匙与现金管理'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十四条', '加钞员（A岗人员）要根据实际交易情况合理配置钞箱现金头寸，每日网点的营业初始和终了前要通过综合业务系统检查自助设备钞箱头寸，针对夜间和节假日制定相应的加钞计划，保证自助设备的正常运行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十五条', '自助设备钥匙和密码由A、B岗管理员分管分用，平行交接。钥匙用毕，应入专用保管柜或加锁后入营业机构保险柜保管，不得随身携带。自助设备密码由B岗管理员管理，人员变动后，要更换密码并及时登记《自助设备管理登记簿》。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十六条', '自助设备的备用钥匙、密码要分别加封保管，备用钥匙、备用密码由A、B岗管理员会同营业网点负责人或主管会计当面共同密封，分别签章，办理登记签收手续，备用钥匙上缴总行电子银行部，备用密码由营业网点自行保管；备用钥匙、密码如需启封，要经总行分管领导批准，使用完毕后要立即密封放入保险柜；备用密码启用后要立即变更。自助设备密码至少每三个月更换一次，使用密码的管理员短期休假或离职时要更改密码。管理员要做好交接手续，由营业网点负责人或会计主管负责监交，并及时在《自助设备管理登记簿》上登记。自助设备旧密码封要交给原密码管理员现场销毁，并登记《自助设备管理登记簿》。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十七条', '自助设备钞箱只允许放入规定面值的现金，现金要符合人民银行的流通标准，严禁假钞、残钞放入钞箱。不得强行挤压堆放超量的钞票。新旧版人民币不能混装在同一钞箱内。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十八条', '从自助设备废钞箱中取出的钞券应装入款包并加封加锁后带回处理。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第二十九条', '自助设备钞箱中的现金要执行双人清点、复核制度。加钞、取钞和清钞工作要在监控状态下由两名设备管理员共同进行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十条', '集中管理的自助设备，装入钞箱的现金应是经清分机清分的钞券，从自助设备废钞箱中取出的钞券应装入款包并加锁后带回处理。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十一条', '集中管理的自助设备和离行式采用整体更换全部钞箱的方式加钞、取钞，钞箱要加封（加锁）。从设备废钞箱取出的钞券应装入款包加锁后带回处理。集中管理配送方式要配有A、B岗人员、保卫人员及车辆，集中管理的自助设备出现客户投诉等情况时，要第一时间到达现场。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十二条', '集中管理的自助设备加钞前，在综合业务系统中经会计主管调出所需现金到自助设备，然后领取现金实物进行加钞工作。集中管理的自助设备钞箱取出的现金，经清点无误后，进行现金实物的移交工作，可在核心系统中经会计主管审批从自助设备调出相应现金到库房。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十三条', '在行式自助设备加钞、取钞工作具体分为以下两种情况：（一）自助设备加钞、取钞工作在营业柜台以外进行的，原则上在营业前或营业终了后封闭进行。特殊情况需在营业中加钞、取钞时，有独立密闭空间的，可在监控下双人持防卫器械进行柜台外加钞、取钞；没有独立密闭空间的，需临时划定一定的警戒范围，并持必备的防卫器械进行。（二）自助设备加钞、取钞工作在营业柜台内进行的，可以根据需要进行加钞、取钞。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十四条', '离行式自助设备加钞、取钞工作视同到营业机构接送款，要严格执行运钞管理制度，确保人员和资金安全。加钞、取钞时，要与自助设备放置地点单位协商，原则上在其内部未正式对外营业前封闭进行。特殊情况需在商场、宾馆等人员较多地点现场加钞、取钞时，要持必备的防卫器械进行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十五条', 'B岗管理员输入密码时要实行有效遮挡，防止偷窥。加钞、取钞前钞箱管理员要关闭自助设备的存取款功能，打印核查清单。加钞、取钞完成后，及时开放自助设备的存取款功能，并做好存、取款测试。（一）采用整体更换全部钞箱的方式加钞、取钞时，取下原自助设备全部钞箱，把配好款的自助设备钞箱或空的备用钞箱装入自助设备，原自助设备钞箱双人送回并在监控下双人清点余额，并做好账款核对工作。（二）采用补充现金的方式加钞时，要清点钞箱余额，账款核对相符后方能加钞。（三）直接从钞箱取钞的，双人在监控下取钞，取出的现金要装入款包，双人上锁后送回营业机构并在监控下双人清点余额。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十六条', 'A岗管理员加钞、取钞完毕后，B岗管理员要打乱自助设备保险柜密码。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十七条', '在行式自助设备，营业网点会计主管要每月至少跟班加钞、取钞核查一次；离行式自助设备，营业网点会计主管或运营管理部门负责人要不定期跟车加钞、取钞核查，并每月至少通过监控录像抽查加、取钞情况一次。核查后，在《自助设备管理登记簿》上做好登记工作。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第四条', '自助设备的清机、加钞流程'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十八条', '自助设备的清机、加钞，必须实行双人操作，并在监控下进行，未按操作流程操作、未在监控下操作，以及有意遮挡操作等视为违规。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第三十九条', '离行式自助设备的清机、加钞管理（一）对离行式自助设备每天至少清机一次，自助设备清机加钞时必须双人一组。（二）离行式自助设备加钞时应采用更换钞箱的形式进行加钞，在安全条件允许的情况下也可以采用直接加现金的方式进行加钞。（三）自助设备钞箱中的现金数量，要按业务量大小合理确定。（四）自助设备现金的领用、上缴、保管均按本行出纳基本制度执行。自助设备现金的装入、取出及清点等工作实行专人负责、双人操作。（五）自助设备备付现钞必须是无破损、无断裂、无透明纸粘贴、无褶皱及卷角等7—8成新的人民币。禁止使用原封新钞和潮湿的现钞。自助设备备付现钞在装机前，须经有识别假币功能的点钞机及人工反假、过数。（六）自助设备清机、加钞流程：1、安全保卫人员对押运车辆、押运人员进行检查，将当日加钞线路告知押运人员，并目视A岗人员与B岗人员在押运人员护卫下进入运钞车，前往指定加钞地点。2、运钞车到达加钞地点后，押运人员先下车，A岗人员，B岗人员携带钞箱或现金后下车。3、在押运人员清场后，A岗人员与B岗人员按照设备操作手册，进行清机、加钞操作。4、B岗人员打开后台门，将自助设备从工作状态转换到维护状态。5、B岗人员按系统提示进行交易的结账，打印清机凭证。6、A岗人员打开自助设备保险箱钥匙锁，B岗人员打开密码锁。7、A岗人员取出设备内原有钞箱，打开钞箱，在B岗人员的监督下往钞箱中放入现金，同时将废钞箱内所有现金装入款包密封。8、如果是更换钞箱，分两种情况：（1）更换取款箱的A岗人员将取款箱取出，经B岗人员审核所携带的取款箱编号等信息，无误后装入自助设备。（2）更换存款箱的，A岗人员将自助设备存款箱取出，将空存款箱装入。9、B岗人员按系统提示进行设备硬件自检，并检查凭条纸、流水纸、打印色带的使用情况，以确保设备正常对外营业。将自助设备从维护状态转换到工作状态，关闭自助设备上箱体柜； 10、B岗人员用业务测试卡做几笔取款交易，确认钞箱放置正确，设备运行正常。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第三章  自助设备运营管理', '第四十条', '在行式自助设备的清机、加钞管理（一）在行式自助设备每天至少清机2次。（二）在行式自助设备所有现场清机、加钞操作可以采用更换备用钞箱或直接加现金的方式完成；（三）自助设备钞箱中的现金数量，要按业务量大小合理确定；（四）自助设备现金的领用、上缴、保管均按本行出纳基本制度执行。自助设备现金的装入、取出及清点等工作实行专人负责、双人操作；（五）自助设备备付现钞必须是无破损、无断裂、无透明纸粘贴、无褶皱及卷角等7—8成新的人民币。禁止使用原封新钞和潮湿的现钞。自助设备备付现钞在装机前，须经有识别假币功能的点钞机及人工反假、过数；（六）清机流程1、到达需加钞的自助设备后，B岗人员打开后台门，将自助设备从工作状态转换到维护状态；2、B岗人员按系统提示进行交易的结账，打印清机凭证；3、A岗人员打开自助设备保险箱钥匙锁，B岗人员打开密码锁；4、A岗人员取出设备内所有钞箱；5、A、B岗在监控下双人清点、复核钞箱现金；6、A岗在B岗人员的监督下往钞箱中放入现金，关闭保险箱，打乱密码；7、B岗人员按系统提示进行设备硬件自检，并检查凭条纸、流水纸、打印色带的使用情况，以确保设备正常对外营业。将自助设备从维护状态转换到工作状态，关闭自助设备上箱体柜；8、B岗人员用业务测试卡做几笔取款交易，确认钞箱放置正确，设备运行正常。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第四章  自助设备账务核对及差错处理', '第四十一条', '自助设备清机后，营业网点管理员要在视频监控范围内，双人清点钞箱、回收钞箱和废钞箱的现金，清点时要查验伪钞。回收钞箱和废钞箱现金要单独存放，按顺序清点，以备对账。清点完毕，将钞箱现金、柜员现金箱余额及ATM核查清单进行核对。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第四章  自助设备账务核对及差错处理', '第四十二条', '网点管理员发现自助设备长短款，当日填写《自助设备管理登记簿》，经会计主管审批后进行挂账处理，在清机日起3个工作日内完成查找工作。查找时，要结合系统柜员流水和ATM交易流水日志，找出发生长短款的具体交易，未查出原因的上报至电子银行部协查处理。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第四章  自助设备账务核对及差错处理', '第四十三条', '对已查实的长短款交易，营业网点要及时进行相关处理：（一）发生长款的，依照《关于印发〈江苏省农村信用社银行卡差错处理规程〉的通知》（东信联发〔2012〕12号）的相关规定执行。（二）发生短款的，查明原因后，属于自助设备或设备系统原因的，由供应商承担。属于本行系统原因的，本行本地卡短款，由营业网点负责协调处理；本行异地卡短款，由营业网点上报电子银行部进行协助处理；他行卡短款，由营业网点上报电子银行部，再请省联社差错处理部门向中国银联请款；未查明长短款原因的，营业网点逐级上报电子银行部协查，仍然无法查明原因的，按照会计办法相关规定执行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第四章  自助设备账务核对及差错处理', '第四十四条', '自助设备差错处理的其他相关规定见《关于印发〈江苏省农村信用社银行卡差错处理规程〉的通知》（东信联发〔2012〕12号）。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第五章  自助设备吞没卡处理', '第四十五条', '自助设备加钞、清机时，营业网点管理员须检查设备吞卡槽内是否有吞没卡。如有吞没卡，营业网点管理员取出后要在《自助设备管理登记簿》上逐张登记并复核签章。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第五章  自助设备吞没卡处理', '第四十六条', '网点会计主管负责暂时保存被吞卡片（卡片需入库保管，且应与登记簿分人保管），并填写“银行卡业务吞卡清单”。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第五章  自助设备吞没卡处理', '第四十七条', '如所吞卡片逾期无人领取，须对卡片进行剪角处理，即沿卡片背面左上角磁条下沿且与水平不大于45度角方向进行剪切处理，破坏磁条，在银行卡管理登记簿上注明“作废”字样，卡片作废卡处理。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第五章  自助设备吞没卡处理', '第四十八条', '吞卡因发卡机构指令性吞卡而引起的，客户不得领取被吞卡片。对非发卡机构指令性吞卡，持卡人可在吞卡后次日起三个工作日内（特殊情况下最长可延长至十个工作日内），持本人有效身份证件及其它可以证明为卡片持有者的材料到自助设备的挂靠机构办理领卡手续；客户有要求的，在行式自助设备挂靠机构可在营业期间当时为客户拿取被吞卡片。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第五章  自助设备吞没卡处理', '第四十九条', '营业网点在办理吞没卡领取业务时，必须核对持卡人相关资料，符合以下条件之一的，领卡人签字领取卡片。（一）持卡人有效身份证件上姓名与卡正面姓名字母或客户背面签名一致。（二）持卡人持本人有效身份证件并通过卡片密码验证。能够确认持卡人身份的其他法律认可的证明方式视同有效身份证件。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第五章  自助设备吞没卡处理', '第五十条', '验证无误后，营业网点柜员在《自助设备管理登记簿》中注明处理日期和结果，登记持卡人的证件类型及号码，由持卡人签字领取。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第六章  客户服务', '第五十一条', '通过客户服务中心和柜面受理的客户投诉，要登记持卡人姓名、卡号、联系方式、投诉内容、日期及时间等要素并及时通知营业网点管理员。营业网点管理员要及时处理并反馈客户。如无法立即解决的要及时上报，得到总行答复后，反馈客户。原则上客户投诉反馈时间不超过一周。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第六章  客户服务', '第五十二条', '自助设备紧急事件主要指持卡人通过我行客户服务中心或其他渠道请求紧急处理的事件，包括设备吞卡及发生长短款后持卡人要求紧急处理等；营业网点负责人为紧急事件第一责任人。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第六章  客户服务', '第五十三条', '紧急事件的应急处理（一）紧急取卡：营业网点管理员在接到吞卡投诉后，要尽快向客户了解具体情况，进行初步判断。符合吞卡领取规定的，按照相应的领卡制度和手续，做好检查、登记及账务处理等工作。（二）长短款处理：自助设备管理员在接到投诉后，要尽快向客户了解具体情况，进行初步判断。确认需紧急处理的，经营业网点负责人批准，立即进行清机对账，按长短款交易相关规定进行处理并告知客户。（三）属于风险事件的，按照突发事件进行应急处置。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第五十四条', '自助设备静态画面中要明确显示风险提示画面，标注24小时客户服务电话，操作界面中要根据客户交易类型显示相应的操作风险提示。严禁在自助设备外部张贴纸质公告。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第五十五条', '现金类自助设备要安装针对持卡人面部、出钞口、环境及加钞作业区等部位的视频监控摄像头,调校监控范围与监控角度，对密码键盘位置进行屏蔽处理，确保成像效果清晰可辨；调整监控时间，与自助设备系统时间相一致。未安装视频监控、监控不完整、未联网的不得上线运行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第五十六条', '安全保卫部门定期、不定期调阅自助设备监控录像资料，定期调阅间隔时间不超过1个月。调阅内容包括确认录像设备工作正常，录像资料完整清晰，有无可疑人员及异常行为。发现有作案嫌疑和安全隐患时要按相关规定及时处理。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第五十七条', '自助设备监控录像资料的保存时间不少于三个月，视频监控系统要具备断路报警、震动报警等功能，并符合当地公安机关相关要求。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第五十八条', '自助设备视频监控系统由总行安全保卫部门负责管理和维护。监控设备出现故障后营业网点应立即停机并报总行监察保卫部协调供应商及时维修处理，以确保自助设备正常运行。更换设备时，要对原设备中的录像资料进行备份，保存时间不少于三个月。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第五十九条', '电子银行部定期组织对自助设备及安装场所安全防范措施的全面巡查（包括夜间巡查），发现问题及时处理。在行式设备，定期巡查间隔时间不得超过3个月；离行式设备，定期巡查间隔时间不得超过1个月。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第六十条', '各营业网点要加强自助设备突发事件处置和管理。（一）发现针对自助设备欺诈、客户资金被盗取、人为破坏设备等犯罪行为情节严重的，营业网点管理员要立即上报总行电子银行部，经批准后采取关停机措施，并原地等待总行人员。（二）总行自助设备管理部门和安全保卫人员接到通知后要派人尽快到现场及时处置，分析作案手段，必要时要向公安部门报案。（三）现场取证完毕后，营业网点管理员要及时进行清机，清点现金，核对账款，确认是否造成损失。（四）自助设备发生其他重大突发事件，总行相关部门、营业网点负责人、营业网点管理员要同时到现场进行处置，清查损失情况，并及时上报相关部门。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第六十一条', '加强对设备供应商及其维护工程师的管理。营业网点要备案设备供应商维护工程师信息资料，信息资料要及时更新。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第六十二条', '营业网点管理员清机时发现假币或变造币的要立即停止对外服务，并上报运营管理部，情节严重时报公安部门立案侦查。自助设备验钞模块升级并进行测试后方可开通运行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第七章  风险管理', '第六十三条', '营业网点管理员发现长短款时要立即进行清机处理，检查是否卡钞。仍未查明原因的，上报营业网点，通知维护厂商现场核实。待查明原因后，对设备进行一次预防性维护，测试后开通运行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第八章  档案管理', '第六十四条', '营业网点要建立自助设备管理档案，档案内容要及时维护，确保准确无误。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第八章  档案管理', '第六十五条', '自助设备的相关前后台电子交易资料（包括自助设备上的交易流水、前置系统的交易资料）须保存一年备查。自助设备的纸质流水记录及相关登记簿、必须按相关会计档案的要求进行保管。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第八章  档案管理', '第六十六条', '自助设备交易流水日志与核查清单要统一保管，每季度由会计主管检查一次。纸质交易流水日志要注明交易的起止日期和设备号，有序存放。交易流水日志的归档、保管、移交、调阅及销毁参照《会计档案管理办法》执行。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第九章  附则', '第六十七条', '本规程由电子银行部负责解释和修订。'),
(NULL, '电子银行部', '《自助设备运营操作规程（试行）》', '第九章  附则', '第六十八条', '本规程自印发之日起执行。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第一章	总	则', '第一条', '为加强本行人民币资金头寸管理，保证支付，防范风险，确保资金头寸安全，根据银监会《商业银行内部控制指引》和《商业银行流动性风险管理办法（试行）》及《xxx省农村信用社联合社支付清算业务考核办法》等相关规定，结合本行实际，特制定本办法。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第一章	总	则', '第二条', '本办法所称资金头寸是指资金往来，主要包括备付资金、准备金、拆借资金等。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第一章	总	则', '第三条', '资金头寸管理目标是：确保支付、防范风险、提高效益。确保支付是指本行保证正常支付能力，保持合理备付头寸，防止账户透支。防范风险是指由于内外因素的变化而发生不规则波动，对其产生冲击并引发相关损失的风险。提高效益是指在保证支付能力的前提下，尽可能降低资金头寸，合理运用资金头寸，提高资金效益。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第一章	总	则', '第四条', '资金头寸管理的原则是：统一调度、头寸集中、分级管理、分工负责。统一调度是指由总行负责本行资金的统一调度和运作，通过银行间市场或其他资金运作渠道进行统一融资。头寸集中是指各支行（部）在保留正常业务所需的头寸后，应将富余头寸及时集中到本行清算团队。本行资金头寸应集中在人民银行准备金账户或xxx省联社清算账户中。分级管理是指总行负责本行资金头寸管理并对各支行（部）资金头寸进行监控，实施管理，各支行（部）负责本单位的资金头寸管理。分工负责是指本行计划财务部、运营管理部（含清算团队和远程授权团队）、金融市场部和各支行（部）应根据本办法的有关规定，各负其责，切实履行工作职责，既要相互配合与协作，又要相互监督，防范透支风险。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第一章	总	则', '第五条', '资金头寸实行垂直管理，各支行（部）之间不得相互调拨资金，严禁对外融通资金，包括拆借、转存等。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第六条', '计划财务部负责每日汇总本行头寸计划，根据计划制定初步拆入或拆出资金方案通知金融市场部。日中对备付金账户进行实时监控，及时通知金融市场部拆入或拆出资金，同时定期对资金的流动性进行分析和科学预测。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第七条', '金融市场部负责日常资金头寸管理，合理运用资金，既要用足资金头寸，又要防范账户透支，同时根据计划财务部资金方案拆入或拆出资金。与上日相比金融市场部资产变动额减负债变动额的差额不得超过 1000 万元（计划财务部通知拆入或拆出资金除外）。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第八条', '清算团队和远程授权团队负责依据计划财务部汇总计划协助计划财务部管理本行资金头寸。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第九条', '电子银行部负责对大额存款户电子银行渠道汇款额度实行动态调整。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第十条', '各支行（部）应密切关注每日大额资金往来情况，及时掌握大额资金存放本行的期限、划款方式、清算时间等方面的信息，并严格执行大额资金往来报告制度。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第十一条', '为掌握大额资金往来情况，防止本行清算账户出现透支现象，各支行（部）应根据业务种类及时将大额资金往来情况通过电话、银丰平台等渠道上报计划财务部，并遵守下列规定：各支行（部）在每个营业日内，客户单笔或单日累计付款金额在 500 万以上的，应在付款日上午九点前向计划财务部报告，上述未报备的紧急付款需经计划财务部总经理同意后方可付款。客户单笔或单日累计进账余额在 500 万（含）以上的，应在及时充分了解客户用款计划后立即向计划财务部报告。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第十二条', '计划财务部应明确专职资金计划员，对本行的资金头寸进行科学管理，督促各支行（部）加强资金头寸管理，提高资金管理水平，定期监测和通报各支行执行大额资金往来报告制度情况。各支行运营主管为本支行资金头寸预测和报备的责任人，公司业务部和小微业务部明确专员担任本部资金头寸预测和报备的责任人。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第二章	职责分工与报备制度', '第十三条', '公司业务部专员兼为财政清算资金头寸主要责任人，主要负责：一是及时报告财政局大额预算外资金计划；二是每日下午四点前匡算当日柜面结算的预算外资金金额并上报计划财务部；三是因清算额度不足或系统挂账等原因导致预算内财政资金无法正常入账时，应及时与财政局和人民银行沟通，并通知营业部进行应急处理，同时根据实际情况向计划财务部汇报，以便计划财务部及时通知金融市场部拆借补充头寸。营业部应极积配合财政资金清算人员做好财政清算资金头寸管理工作，主要职责：一是关注预算内资金到位情况，清算资金到位后及时归还至垫款账户并报告给计划财务部；二是配合财政资金清算人员做好应急处理工作。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第三章	准备金管理', '第十四条', '本行上缴的法定存款准备金按照人民银行有关文件规定执行，实时对准备金进行调整。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第三章	准备金管理', '第十五条', '本行法定存款准备金的调整由计划财务部根据本行每旬各项存款平均余额计算，并提前做好资金头寸运用准备，清算团队按时进行调整与划款。如有变动由计划财务部将具体的调整方案提前与清算团队核对，并注意以下两点：一是每日透支额不超过总存款的一个百分点；二是考核期平均法定存款准备金余额不得低于法定存款准备金额度。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第三章	准备金管理', '第十六条', '清算团队于每月5 号、15 号、25 号（遇节假日顺延，人民银行临时要求调整存款准备金，另行规定除外）进行存款准备金调整。根据具体调整方法计算缴存准备金，并与计划财务部计算的缴存准备金进行核对，核对无误后，填制相关凭证，按时、准确地缴存人民银行。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第三章	准备金管理', '第十七条', '清算团队应建立准备金账户对账制度，定期与人民银行进行对账，确保内部账户相符。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第四章	备付金和现金管理', '第十八条', '本行的备付金管理实行额度控制、定期监测、防止透支的管理制度。额度控制是指本行的备付金额度应控制在合理的水平，同时控制连续两天备付金变化幅度。当天日终上存资金较上一工作日下降幅度超过20%按要求在规定时间前由清算团队通过核心系统报备省联社相关部门，同时保证报备金额与日终余额误差不得超过 20%；当天日终上存资金较上一工作日超过 2 亿元，由计划财务部在当天下午4:30前报省联社相关部门。定期监测是指计划财务部每日对备付金进行预测，同时定期对流动性状况进行监测分析和评估。防止透支是指各清算账户必须留有一定的资金头寸，保证支付需要，防范账户出现透支。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第四章	备付金和现金管理', '第十九条', '各支行应加强现金日常管理，准确匡算现金头寸计划，每天及时通过核心系统报现金计划。严格控制库存现金限额，认真执行人民银行现金管理条例、大额现金登记备案和审批制度，遵守反洗钱规定，确保现金资产的安全。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第四章	备付金和现金管理', '第二十条', '本行对外拆借资金，必须按照人民银行核定的同业拆借限额规范运作。单笔最高拆入和最高拆出金额为13.2亿，拆入期限最长不超过1年。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第四章	备付金和现金管理', '第二十一条', '清算团队应建立备付金账户对账制度，定期与xxx省农村信用社联合社进行对账，确保内部账户相符。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第五章	资金透支处理', '第二十二条', '当清算账户发生透支时，应迅速查明透支原因，并根据透支原因采取相应的处理方式，以最快的速度调度头寸弥补透支。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第五章	资金透支处理', '第二十三条', '凡属资金调度、记账串户、大额资金划出、交易对手方或其他原因造成总行结算账户透支的，应立即弥补透支，按有关规定及时进行处理。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第五章	资金透支处理', '第二十四条', '本行清算账户发生透支后，计划财务部必须在第一时间内向本行领导做出透支说明和调查报告。凡属本行责任造成的透支，要严肃追究责任部门及相关责任人的责任。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第六章	监督与检查', '第二十五条', '审计部负责对全辖资金头寸管理、执行内控制度情况进行稽核检查，提出整改意见和建议。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第六章	监督与检查', '第二十六条', '计划财务部负责对全辖资金的流动性、存贷款比例等指标进行监控，定期进行风险评价和提示，并及时向本行领导报告。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第七章	罚  则', '第二十七条', '有下列行为之一的，对责任人按本行员工违规违章行为管理规定的相关条款进行处罚，并视情节轻重，追究相关人员的行政和法律责任：（一）清算资金账户出现透支的；（二）未执行大额资金往来报告制度的；（三）未按时足额缴存准备金或缴存准备金出现差错的；（四）未按规定对外拆借资金的；（五）其他违反监管部门规定和本行规定的情形。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第八章	附	则', '第二十八条', '本办法由本行计划财务负责解释和修订。'),
(NULL, '计划财务部', '《人民币资金头寸管理办法》', '第八章	附	则', '第二十九条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第一章  总  则', '第一条', '为提高本行成本精细化核算水平，建立和完善成本分摊机制，服务于本行多维度盈利分析的需要，根据《中华人民共和国商业银行法》结合本行经营管理实际情况，特制订本办法。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第一章  总  则', '第二条', '全成本主要包括：资金成本、运营成本、税务成本、风险成本和资本成本等。全成本管理目标主要包括：全方位管理、全环节管理、全过程管理和全员参与管理。资金成本指资金使用者向资金所有者和总行资金中心支付的占用费和筹集资金，主要以内部资金转移定价进行核算。运营成本指本行为正常开展经营管理活动所发生的成本支出，主要指本行财务核算系统各核算层级核算的业务及管理费的汇总。税务成本指全行根据相关规定缴纳的税款，包含增值税、企业所得税等。风险成本指全行承担的信用风险、市场风险、操作风险、流动性风险等导致的可预期损失。本行目前以信用风险资产拨备为主。资本成本指本行股东为覆盖非预期损失而保留资本所产生的机会成本。 本行资金成本以内部资金转移定价进行核算，以“全额资金管理”模式进行管理。总行全额转移（出售或收购）各经营单位每一笔资金，并集中进行管理。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第一章  总  则', '第四条', '运营成本、税务成本、风险成本适用于本行成本分摊与多维度成本核算管理。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第一章  总  则', '第五条', '成本分摊是指将本行财务核算系统各核算层级核算的成本按照一定规则、方法和路径归集、分摊至本层级产品，核算出本层级产品的成本费率的过程。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第一章  总  则', '第六条', '多维度成本核算是指根据产品与其他维度的归属关系，按照各层级产品的成本费率核算出产品、机构、条线、部门、客户、客户经理等多维度运营成本的过程。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第一章  总  则', '第七条', '根据本行现阶段管理会计系统建设情况，多维度盈利分析包括产品、机构、条线、部门、客户、客户经理、渠道共七个维度：㈠产品维度：是指具有管理价值、源系统数据支持损益独立核算的最小粒度的基础产品。㈡机构维度：是指承担经营责任的经营机构。㈢条线维度：是指创造利润的业务条线，包括对公、对私、资金、中间业务等条线。㈣部门维度：是指条线内部按照职责细分的总行业务部门，即直接经营管理部门。㈤客户维度：是指本行为其提供产品或服务的个人或组织。㈥客户经理维度：是指专职维护本行客户，协助其获取本行各类产品或服务的本行员工，客户经理名单由人力资源部提供并要求与绩效考核系统一致。㈦渠道维度：是指本行向客户提供各类产品和服务的平台和途径。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第一章  总  则', '第八条', '基本原则为提高成本分摊效率，本行成本分摊与多维度成本核算遵循以下原则：㈠统一规则。本行执行统一的成本分摊和多维度成本核算规则。㈡平行分摊。各核算层级的成本向本层级产品归集和分摊。㈢精细核算。成本列支时应明确受益对象，按照“谁受益、谁承担”的原则进行精细化成本核算并记录多维度受益信息。㈣直接成本最大化。先归集后分摊，对能够明确直接受益对象的成本应优先归集至相应受益对象上，只有无法明确直接受益对象的成本再进行成本分摊。机构、条线（部门）、客户、客户经理等维度的运营成本、税务成本、风险成本、资本成本根据产品与其归属关系，按照各级产品的成本（率）计算得出。 本行成本分摊工作由总行计划财务部牵头，总行各部（室）、各分支机构根据职能分工和管理需要，协同完成各项管理工作。年初计划财务部依据全行成本预算、上年度经营情况、本年度经营策略、同业竞争态势和市场变化趋势对各经营单位进行目标成本分解，并报全面预算管理委员会审批。计划财务部定期对各经营单位全成本执行情况进行监测，并对成本偏差较大的单位进行分析，形成报告，报全面预算管理委员会，同时依据实际情况对目标调整，报全面预算管理委员会审批。审计部每年至少对全成本管理情况进行一次审计，并将相关情况形成报告报董事会。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第二章  成本分摊', '第十条', '成本分摊的直接受益对象是各核算层级的责任中心，最终受益对象是产品。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第二章  成本分摊', '第十一条', '根据职责分工，将本行各核算层级下属机构的内设部室分为三大类责任中心。责任中心是指具有一定管理权限，并承担相应经济责任的内部责任单元。㈠服务中心，属于后台部门，以其他部门为服务对象，与业务没有直接关系。不接受其他责任中心向其分摊成本，其成本按不同类别向营业网点分摊。包括董事会办公室、监事会办公室、办公室、计划财务部、审计部、人力资源部、信息科技部、监察保卫部、工会、经营管理层。㈡管理中心，属于后台部门，对业务中心进行相应职能管理、提供相应职能服务。不接受其他责任中心向其分摊成本，其自身的直接成本向营业网点分摊。包括运营管理部、电子银行部、授信评审部、信贷管理部、合规管理部、风险管理部。㈢利润中心，属于前台部门，其拥有的产品明确，自身成本不向其他责任中心分摊，在成本分摊时摊给自身。本行利润中心有支行、营业部、公司部、小微业务部和金融市场部，其中，金融市场部为特殊利润中心，不接受成本中心的分摊，将自身直接成本分摊给所辖资金类产品。各支行（部）将承接总行成本中心的费用与本支行（部）发生的费用，按照一定动因分摊给所辖产品。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第二章  成本分摊', '第十二条', '成本分摊路径本行成本分摊采用平行分摊的原则，各项费用只在本核算层级内部分摊，不在上下级机构间进行跨层级分摊。在同一核算层级中，成本分摊采用分步、顺序分摊的方法。责任中心大类是划分成本分摊顺序的依据，最先分摊的是为其他责任中心提供服务最多的责任中心，最后分摊的是为其他责任中心提供服务最少的责任中心，逐项分摊责任中心归集的费用。成本只在不同责任中心大类之间进行分摊，同一责任中心大类内部不交叉分摊。最终承担费用的责任中心将归集到的费用直接分摊到产品。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第二章  成本分摊', '第十三条', '成本分摊动因成本分摊动因，是指根据成本归因将成本分摊到受益对象时参考的具体指标。成本分摊动因有两类：一类是责任中心成本分摊动因，用于成本在责任中心分摊时参考；另一类是产品成本分摊动因，用于成本由业务中心向产品分摊时参考。目前，本行选择的部门维度分摊动因包括网点发卡量、网点存贷款规模占比、网点贷款规模占比（贷款减值损失）、网点总收入占比（管理费）、固定资产占比（保险费）、网点资产规模占比（监管费）、网点人数法等。产品维度的分摊动因包括贷款产品规模法、资金产品规模法、所有产品规模占比、账户数量法、产品系数法等。客户经理和关键客户分摊动因包括将产品粒度细化，明细分摊结果，进行客户经理和关键客户维度结果汇总。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第二章  成本分摊', '第十四条', '成本分摊流程产品成本分摊包括两个步骤：即直接成本归集、间接成本分摊。责任中心成本分摊包括三个步骤，即直接成本归集、归口成本还原、间接成本分摊。直接归集到产品的费用作为产品直接成本不再进入责任中心成本分摊过程。不能直接归集到产品的费用作为产品间接成本，按责任中心成本分摊的步骤进行分摊。㈠直接成本归集直接成本归集，是指通过财务等系统的成本明细核算，将能明确受益对象的成本直接归集到责任中心和产品上的过程。㈡归口成本还原归口成本还原，是指因成本归口管理需要将成本归口核算至某一责任中心，而成本管理责任中心和实际受益责任中心不一致时，按照相应规则将成本从归口核算责任中心还原到实际受益责任中心的过程。行内归口核算的成本，按照“谁受益、谁负担”原则进行还原。㈢间接成本分摊间接成本分摊，是指在完成直接成本归集、归口成本还原后，将仍无法明确受益对象的成本，按照一定的动因分摊到责任中心和产品上。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第三章  分摊管理', '第十五条', '分摊规则设定管理会计系统内成本分摊规则完成初始设定后，可根据业务和管理需要，进行调整。当出现以下情况且规则无法满足管理需要的，可以对成本分摊规则进行变更：㈠机构、部门发生新增、撤销或职能调整，影响原有成本分摊规则的执行；㈡原有成本分摊规则计算所得分摊结果呈现明显不合理，相关部门共同提出规则变更；㈢基础数据获得良好提升，提供更优成本分摊规则供选择。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第三章  分摊管理', '第十六条', '分摊规则变更流程成本分摊规则的变更遵循以下流程：㈠相关部门提出变更申请，阐述变更原因、变更内容；㈡计划财务部评估规则变更的合理性及可行性，征求信息科技部数据支持情况，信息科技部给予明确回复；㈢计划财务部制定拟变更规则具体内容，征求关联部门意见，关联部门给予明确答复；㈣计划财务部就规则是否变更提交全面预算管理委员会审定；㈤计划财务部完成规则变更维护。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第三章  分摊管理', '第十七条', '数据补录成本分摊所需要的动因数据来源自两部分：一部分来自于现有系统内，由数据库统一供数；另一部分来自于系统外手工统计。总行相关业务部门负责定期向计划财务部提供本部门成本分摊基础参数。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第三章  分摊管理', '第十八条', '分摊计算流程成本分摊计算每月定期执行，须遵循以下流程：㈠数据库按照规定向管理会计系统提供成本分摊所需的系统内基础数据；㈡总行相关业务部门向计划财务部提供成本分摊所需的系统外基础数据；㈢计划财务部检查成本分摊计算所需数据准备情况，准备就绪后进行成本分摊计算操作；㈣成本分摊计算完成后，由管理会计系统统一出具相应分析报表。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第四章  附  则', '第十九条', '信息保密成本分摊的相关规则以及系统内业务数据和生成的各类报表属于本行的保密数据，各层级应严格保密，未经总行批准，不得对外提供或披露。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第四章  附  则', '第二十条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《全面成本管理办法》', '第四章  附  则', '第二十一条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第一章  总  则', '第一条', '为进一步提高本行的预算管理水平，通过系统的方法来贯彻、监控本行经营目标和战略目标的实施，规范预算编制及调整，明确指标体系及监控制度，严格预算执行与分析考核，根据《中华人民共和国商业银行法》结合本行经营管理实际情况，特制定本办法。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第一章  总  则', '第二条', '全面预算是指本行在一定时期内各项业务活动、财务表现等方面的总体预测。全面预算是在本行战略目标的指导下，对未来的经营活动和相应财务结果进行充分、全面的预测和筹划，并通过对执行过程的监控，将实际完成情况与预算目标不断对照和分析，从而及时指导经营活动的改善和调整，帮助管理者更加有效地管理企业和最大程度地实现战略目标。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第一章  总  则', '第三条', '全面预算的要求：事前有计划、事中有控制、事后能考评、追溯。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第二章 全面预算组织体系', '第四条', '本行设立全面预算管理委员会，由总行行长任主任委员，分管财务副行长为副主任委员，成员由行长室其他副行长及风险管理部、运营管理部、信贷管理部、计划财务部主要负责人组成。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第二章 全面预算组织体系', '第五条', '全面预算管理委员会下设办公室，办公室设在计划财务部，具体拟定全行全面预算管理政策等，并负责全面预算管理委员会的日常工作。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第二章 全面预算组织体系', '第六条', '全面预算管理委员会的主要职责权限：（一）审议全行的全面预算管理制度；（二）审议全行全面预算管理目标；（三）监督、指导全行全面预算执行情况；（四）监督、指导全行全面预算考核情况；（五）审议全行全面预算调整情况；（六）全面预算管理工作中的其他重要事项；（七）行长室授权的其他事宜。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第七条', '本行的预算编制遵循以下原则：㈠与本行战略目标相结合的原则。各部门编制的预算应与本行近期经营目标、中长期发展规划相结合。㈡上下结合原则。各部门预算方案经预算管理委员会审查、汇总、综合平衡后，在此基础上编制年度预算并下达到各部门执行。㈢全员参与原则。本行的全体员工都要直接或间接地参与预算管理过程，要形成“人人头上有指标，个个肩上有任务”。㈣开源节流、效益第一的原则。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第八条', '本行的预算期为每年公历的1月1日-12月31日，各部门应该在每年11-12月，进行下一年度的预算编制。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第九条', '全面预算编制工作流程㈠编制草案上报预算编制采取“上下结合、分级编制、汇总平衡”的程序进行。各分支机构、总行预算责任部门应根据业务发展需要，结合自身实际，分别编制分支机构、总行本部预算草案，报送计划财务部。㈡条线审核草案总行预算责任部门根据全行业务发展需要对分支机构报送的预算草案进行审核、汇总和平衡后，编制全行各业务条线发展预算草案并报送本部门分管领导审核后报总行计划财务部汇总。㈢汇总平衡草案计划财务部按照业务发展规划要求，在汇总分支机构和各业务条线年度预算草案基础上，初步拟定全行年度预算方案，经充分协调、沟通、修正、平衡，提交全面预算管理委员会审议后，报董事会或股东大会审议批准。㈣分解下达预算计划财务部将已审议通过的预算分解下达到总行预算责任部门执行。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第十条', '全面预算管理编制基础依据：㈠本行长期发展规划； ㈡本行年度发展计划，如资产、负债规模增长及结构情况、机构增设、撤除计划、人员编制情况；㈢上年度预算执行情况；㈣上年度生息资产收息率和付息负债付息率情况；㈤资本充足率、不良资产迁徙情况、拨备覆盖率要求及年度不良资产处置计划；㈥年度营运费用和专项费用计划；㈦金融政策导向和市场利率变化估计；㈧监管要求及其他需要提供的相关资料。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第十一条', '本行的全面预算包括业务预算、资本预算、财务预算、利润预算。其中业务预算是全面预算的起点，资本性支出预算、财务预算是基础，利润预算是目标。㈠业务预算1.发展规划部牵头编制存款业务预算草案、贷款业务预算草案，各分支机构、营业部、公司业务部、小微企业部配合；2.计划财务部牵头编制资本预算草案，其他相关部门配合；3.金融市场部负责编制资金业务预算草案；4.三农业务部牵头编制中间业务预算草案；5.电子银行部牵头编制电子银行业务预算草案；6.风险管理部负责编制不良资产控制等业务预算草案；㈡资本性支出预算总行相关管理部门根据全行业务发展需要，以及相关管理规定，负责编制全行资本性支出预算草案，资本性支出预算指受益期在一个会计年度以上的项目支出预算。1.行政办公室负责编制办公设备、机器机械、运输工具、总行及各分支机构营业用房装修、改造等年度预算草案； 2.信息科技部牵头编制电子设备等年度预算草案，电子银行部配合编制全行各分支机构银行自助设备的年度预算草案； 3.安全保卫部负责编制全行安全防卫设备、网点装修中的安防费用年度预算草案;4.信息科技部牵头，总行相关管理部门配合负责编制信息系统建设及相关外围设备年度预算草案。㈢财务预算1.人力资源部牵头，发展规划部、计划财务部配合负责编制全行职工薪酬的年度预算。具体包括：职工工资、职工福利费、职工教育经费、工会经费、劳动保护费、基本养老保险金、基本医疗保险金、工伤保险金、生育保险金、失业保险金、补充养老保险金、补充医疗保险金、非货币性福利、住房公积金、劳务费等；2.行政办公室牵头负责编制总行办公设备、办公家具等低值易耗品以及会议费、差旅费、邮电费、劳动保护费、水电费、公杂费、咨询费、绿化费、会费、物业费、租赁费、修理费（含各分支机构修理费用）、交通工具耗用费用的年度预算；负责编制总行业务宣传费、广告费、业务招待费等费用的年度预算；负责编制全行房屋、车辆、等保险费用的年度预算；负责编制基建装潢相关费用额度预算； 3.资产保全部编制诉讼费、评估费及执行费、资产损失审计费用等相关费用的年度预算，风险管理部配合；4.授信管理部负责编制评估费等相关费用的年度预算；5.安全保卫部负责编制联防治安费、安保服务费及钞币运送费用的年度预算；6.运营管理部负责编制全行业务凭证印刷费，以及网点营业机具等低值易耗品的年度预算，其他相关部门配合；7.董/监事会办公室负责编制董事会、监事会及其成员履行职能而发生各项费用年度预算；8.计划财务部负责编制全行管理费、税费、折旧及摊销、会计报表年度审计、企业所得税汇算清缴审计等审计费用的年度预算；9.电子银行部配合负责编制电子银行方面低值易耗品的年度预算；10.信息科技部负责编制科技方面的低值易耗品，以及电子设备运转费的年度预算；11.总行其他相关部门负责编制各自职责管理范围内所需业务及管理费用预算；㈣利润预算1．发展规划部根据编制的存贷款业务预算草案，参照上年度贷款收息率及存款付息率，初步测算存贷款利息收支的年度预算，计划财务部配合，并根据定价水平及宏观形势预判进行适度微调； 2.电子银行部、三农业务部依据当年电子银行业务、中间业务预算草案情况，结合近三年增减变化情况，编制手续费及佣金收支的年度预算，计划财务部配合；3.金融市场部依据业务预算中资金业务预算草案，编制金融机构往来收入、投资收益的年度预算，计划财务部配合；4.计划财务部牵头，风险管理部、资产保全部配合负责资产减值损失的年度预算。风险管理部对不良贷款的新增进行预测，资产保全部拟定贷款核销计划，计划财务部按照金融市场部、风险管理部提供的业务预算，结合银监部门拨备覆盖率等监管目标编制资产减值损失年度预算。 5.计划财务部负责编制全行税金的年度预算，主要包括企业所得税、增值税、其他税金及附加等。6.人力资源部根据退休职工“关爱金”管理办法，结合我行实际情况编制营业外支出年度预算，计划财务部配合； 7.计划财务部根据预算责任部门编制的业务预算草案和条线年度财务预算，结合本行资产负债结构、预计收息率、付息率、市场利率以及国家利率政策的变化等因素，汇总编制本行全面预算草案。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第十二条', '预算的编制方法：㈠增量预算法，以基期水平为基础，分析预算期业务量水平及有关影响因素的变动情况，编制相关预算的方法。㈡零基预算法，以零为基础编制预算的方法，不考虑以往期间的项目和费用数额，主要根据预算期的需要和可能分析费用项目和费用金额的合理性，综合平衡编制费用预算。业务预算的编制一般可参考增量预算法，财务费用预算、资本性支出预算的编制一般可参考零基预算法。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第十三条', '本行的预算需经过董事会批准后下达实施，年度内一般不做调整。但有以下情形之一，导致预算编制基本假设发生重大变化的，经全面预算管理委员会讨论通过后，可予以调整：㈠自然灾害等不可抗力因素；㈡国家经济政策出现重大调整；㈢金融行业市场环境发生重大变化；㈣对于不影响预算目标的预算内项目指标调整'),
(NULL, '计划财务部', '《全面预算管理办法》', '第三章 　全面预算的编制与调整', '第十四条', '预算调整事项不能偏离企业发展战略和年度预算目标，调整方案应当在经济上能够实现最优化。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第四章   预算监控与考核', '第十五条', '预算的监控就是通过比较实际执行结果与预算目标，确定其差异额及其差异原因。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第四章   预算监控与考核', '第十六条', '由风险管理部牵头，金融市场部、计划财务部等部门配合，在全面预算草案通过后，制定全年监管指标目标值与触发值表格，明确各个指标的监管值、目标值与触发值，由统计分析部对指标进行动态监控。凡是触发预警值的，及时提醒责任部门，调整业务方向，确保预算执行不偏离。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第四章   预算监控与考核', '第十七条', '定期对预算执行情况进行分析，分析主要包括下列内容：㈠金融政策变更、利率变化、同业竞争等对资产、负债规模结构的影响；㈡资产、负债和所有者权益的结构和变化及其对财务收支的影响；㈢营运费用的增减变化及原因；㈣预算的执行差异情况及原因；㈤改进和加强财务管理的建议和措施。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第四章   预算监控与考核', '第十八条', '预算执行情况分析由计划财务部牵头，总行预算责任部门共同参与，最终形成全行的预算执行情况分析报告。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第四章   预算监控与考核', '第十九条', '审计部应定期对预算执行情况进行专项审计。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第四章   预算监控与考核', '第二十条', '全面预算管理委员会对全面预算执行情况进行适度考评。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第五章  附 则', '第二十一条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《全面预算管理办法》', '第五章  附 则', '第二十二条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第一章  总  则', '第一条', '为加强本行对全面预算管理的领导组织工作，处理好安全性、流动性、效益性三性经营原则之间的关系，实现全面预算结构的优化组合，根据《中华人民共和国商业银行法》、《农村商业银行管理暂行规定》、《xxx省农村信用社财务管理基本规范》等相关规定及本行章程，特制定本议事规则。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第一章  总  则', '第二条', '全面预算管理委员会工作原则是综合运用现代化的管理办法与技术，坚持实事求是、上下结合、分级编制、汇总平衡的原则，确保预算编制合理化、执行有效化、分析深刻化、调整规范化、考核严格化。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第二章  人员组成', '第三条', '全面预算管理委员会由九名委员组成，行长为全面预算管理委员会主任委员，分管财务副行长为副主任委员，成员由行长室其他副行长及风险管理部、运营管理部、信贷管理部、计划财务部主要负责人组成。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第二章  人员组成', '第四条', '全面预算管理委员会下设办公室，办公室设在计划财务部，具体拟定全行全面预算管理政策等，并负责全面预算管理委员会的日常工作。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第三章  职责权限', '第五条', '全面预算管理委员会的主要职责权限：㈠审议全行的全面预算管理制度； ㈡审议全行全面预算管理目标；㈢监督、指导全行全面预算执行情况；㈣监督、指导全行全面预算考核情况；㈤审议全行全面预算调整情况；㈥全面预算管理工作中的其他重要事项；㈦行长室授权的其他事宜。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第三章  职责权限', '第六条', '全面预算管理委员会对行长室负责。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第四章  管理内容', '第七条', '业务预算管理包括资产负债、中间业务、表外业务等的计划，预算内容包括现金、清算资金、贷款业务、债券投资、金融市场资产业务、存款业务、金融市场负债业务、中间业务、银行承兑汇票净敞口、保函业务、信用证敞口等。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第四章  管理内容', '第八条', '财务预算管理包括经营收支预计完成的计划，预算内容包括营业收入预算、成本费用预算、其他收支预算、资产购置预算、利润预算等。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第四章  管理内容', '第九条', '资本预算管理包括资本充足率达标计划，包括盈利增加的资本、资本净额、核心资本净额、加权风险资产、资本充足率等。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第五章  监督考核', '第十条', '全面预算管理采用“统一管理、分类实施、适时调整、严格考核”办法进行。统一管理。即由总行统一制定全行全面预算管理目标和政策，在全行范围内统一进行与全面预算相对应的人力、费用、资本等资源的合理配置。分类实施。即根据实现全行全面预算管理目标的需要，针对各利润中心（包括支行、营业部、金融市场部等）的实际情况，分别对每个利润中心的各项指标确定不同的年度目标值。适时调整。即通过定期编制、填报全面预算管理表，适时分析、监控全面预算进行状态，掌握整体运行方向，预测运行趋势，调整运行偏差。严格考核。即对各利润中心的全面预算管理的执行情况，按月份、季度、年度进行严格考核。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第五章  监督考核', '第十一条', '全面预算管理委员会根据监控指标和各利润中心的现状，逐一制定年度指标，对执行全面预算管理指标情况以利润中心为单位进行考核。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第五章  监督考核', '第十二条', '全面预算管理委员会负责对各利润中心全面预算管理实行监督、指导，统一对全面预算运行状况进行考核、评价，并实施奖惩。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第五章  监督考核', '第十三条', '各利润中心要把全面预算管理的各项要求融入日常经营管理之中，并通过编制和实施业务经营计划加以实现。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第十四条', '建立全面预算管理委员会例会制度。听取全行全面预算管理情况，重点讨论全面预算管理中存在的问题，并研究制定相应的改进措施。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第十五条', '全面预算管理委员会会议每季度召开一次，并于会议召开前三天通知全体委员，遇特殊情况，可召开临时会议。会议由主任委员主持，主任委员不能出席时可委托副主任委员主持。召开的形式由总行办公室通知委员会各成员。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第十六条', '全面预算管理委员会会议应当由三分之二以上的委员出席方可举行。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第十七条', '全面预算管理委员会委员对提交议案发表明确的赞成、反对或复议的意见。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第十八条', '议案的表决采用口头形式表决，表决内容记录于会议记录，并经参会人员签名确认。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第十九条', '会议做出的决议，必须经全体委员过半数通过方为有效。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第二十条', '全面预算管理委员会召开会议时，应邀请本行董事会、监事会、审计部派员列席会议。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第二十一条', '全面预算管理委员会会议的召开程序、表决方式和会议通过的议案必须遵循有关法律、法规、本行章程及本制度的规定。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第二十二条', '报批和备案对属董事会权限内的审议事项，经委员会审议通过后上报董事会审批。委员会及时向董事长、监事长和相关部门报告本行有关全面预算方面管理的重大决议和决策等事项。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第二十三条', '出席会议的人员均对会议所议事项有保密义务，不得擅自披露有关信息。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第六章  议事规则', '第二十四条', '全面预算管理委员会会议应当有记录，出席会议的委员应当在会议上签名；会议记录日常由全面预算管理委员会办公室保存，年度终了，移交本行档案室归档保管。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第七章  附  则', '第二十五条', '本议事规则由本行全面预算管理委员会办公室负责解释和修订。'),
(NULL, '计划财务部', '《全面预算管理委员会议议事规则》', '第七章  附  则', '第二十六条', '本议事规则自发文之日起施行。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第一章  总 则', '第一条', '为适应本行业务经营发展需要，规范本行利润分配管理，根据《企业会计准则》、《xxx银行股份有限公司章程》及《xxx银行股份有限公司财务管理办法》等有关规定，制定本办法。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第一章  总 则', '第二条', '本办法所指的利润是本行的税后利润。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第二章  分配原则及顺序', '第三条', '本行利润总额按国家有关规定作相应调整后，依法缴纳所得税，缴纳所得税后净利润除国家另有规定外，按照下列顺序分配：（一）弥补以前年度的亏损（不超过五年弥补期限的亏损）；（二）提取法定盈余公积。按照净利润的10%提取法定盈余公积。当法定盈余公积已达注册资本的50%时，可以不再提取；（三）提取任意盈余公积；（四）提取一般风险准备；（五）向投资者分配利润。经过以上分配后的剩余利润，便是本行的未分配利润，结转下年继续使用。上述第三款任意盈余公积、第四款一般风险准备的具体提取比例、第五款向投资者分配利润根据每年的经营状况由董事会提出建议，交股东大会表决。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第三章  分配规定', '第四条', '本行不得在弥补亏损和提取公积金之前向股东分配利润。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第三章  分配规定', '第五条', '股东大会决议将公积金转为股本时，报银行业监督管理机构批准后按股东原有股份比例派送新股。但法定盈余公积转为股本时，所留存的该项法定盈余公积不得少于注册资本的25%。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第三章  分配规定', '第六条', '严格按照xxx省农村信用社联合社、xxx省财政厅发布的年度决算文件实行非固定比例股金分红，根据本行当年经营情况，合理确定股金分红比例,采取现金或者派送股份方式分配红利。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第三章  分配规定', '第七条', '存在下列情况之一的，不对股东分配现金红利：（一）当年亏损；（二）未全部弥补历年亏损挂账；（三）资本充足率未达到规定。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第三章  分配规定', '第八条', '股东大会对利润分配方案作出决议后，董事会须在股东大会召开后两个月内完成红利（或股份）的派发事项。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第四章  附  则', '第九条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《利润分配管理办法》', '第四章  附  则', '第十条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第一章  总  则', '第一条', '为规范本行增值税的会计核算，根据《企业会计准则》、《增值税会计处理规定》，结合本行实际，制定本办法。'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第二条', '计提贷款利息收入，会计分录为借：应收利息贷：利息收入贷：应交税金-应交增值税-销项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第三条', '自结息日起 90 天内发生的应收利息需缴纳增值税，会计核算同上。'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第四条', '收到自结息日起 90 天后发生的应收利息，会计分录为借：活期存款等科目贷：利息收入贷：应交税费-应交增值税-销项税贷：表外应收利息'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第五条', '发生贴现业务，会计分录为借：贴现资产贷：活期存款贷：贴现资产 —利息调整贷：应交税费—应交增值税-销项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第六条', '取得中间业务收入，会计分录为借：活期存款/其他应付款等科目贷：手续费及佣金收入等科目贷：应交税费 —应交增值税-销项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第七条', '按税法规定，视同销售需缴纳增值税，会计分录为借：业务及管理费等科目贷：应交税费—应交增值税-销项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第八条', '出售金融商品、贵金属，会计分录为借：存放同业款项等科目贷：投资收益/其他业务收入等科目贷：应交税费-应交增值税-销项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第九条', '出租固定资产，收取租金时，会计分录为借：存放同业款项等科目贷：其他业务收入等科目贷：应交税费-应交增值税-销项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第二章	增值税销项会计核算', '第十条', '出售使用过的固定资产，会计分录为借：固定资产清理贷： 应交税费-应交增值税-销项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第三章  增值税进项会计核算', '第十一条', '购入应税劳务或资产，会计分录为借：业务及管理费/手续费及佣金支出/固定资产等科目借：应交税费-应交增值税-进项税贷：其他应付款等科目'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第三章  增值税进项会计核算', '第十二条', '接受捐赠转入的资产，会计分录为借：固定资产/无形资产等科目借：应交税费-应交增值税-进项税贷：营业外收入'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第三章  增值税进项会计核算', '第十三条', '购进允许抵扣进项税的不动产，会计分录为借：固定资产借：应交税费-应交增值税-已认证待抵扣进项税借：应交税费-应交增值税-进项税贷：其他应付款等科目第二年借：应交税费-应交增值税-进项税贷：应交税费-应交增值税-已认证待抵扣进项税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第三章  增值税进项会计核算', '第十四条', '对于不得从销项税额中抵扣的进项应转出，会计分录为借：营业外支出/业务及管理费/待处理财产损溢等科目贷：应交税费-应交增值税-进项税转出'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第四章  购买增值税税控系统专用设备会计核算', '第十五条', '购买增值税税控系统专用设备，会计分录为借：固定资产贷：存放同业款项等科目借：应交税费-应交增值税-减免税款贷：递延收益按期计提折旧时借：业务及管理费贷：累计折旧借：递延收益贷：业务及管理费'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第四章  购买增值税税控系统专用设备会计核算', '第十六条', '发生技术维护费时，会计分录为借：业务及管理费贷：存放同业款项等科目借：应交税费-应交增值税-减免税款贷：业务及管理费'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第五章  增值税缴纳会计核算', '第十七条', '按季度计算应缴纳的增值税当季度为应交增值税，会计分录为借：应交税费-应交增值税-转出未交增值税贷：应交税费-未交增值税当季度为多交增值税，会计分录为借：应交税费-未交增值税贷：应交税费-应交增值税-转出多交增值税'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第五章  增值税缴纳会计核算', '第十八条', '缴纳增值税，会计分录为借：应交税费-未交增值税贷：应交税费-待交增值税款-应交增值税款借：应交税费-待交增值税款-应交增值税款贷：存放同业款项等科目'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第五章  增值税缴纳会计核算', '第十九条', '发生预缴增值税的，会计分录为借：应交税费-应交增值税-已交增值税贷：应交税费-待交增值税款-已交增值税款借：应交税费-待交增值税款-已交增值税款贷：存放同业款项等科目'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第六章  增值税纳税检查调整会计核算', '第二十条', '纳税检查后应调减进项税额或调增销项税额的，会计分录为借：业务及管理费等科目贷：应交税费-增值税特别调整-纳税检查调整'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第六章  增值税纳税检查调整会计核算', '第二十一条', '纳税检查后应调增进项税额或调减销项税额的，会计分录为借：应交税费-增值税特别调整-纳税检查调整贷：利息收入等科目'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第六章  增值税纳税检查调整会计核算', '第二十二条', '全部调账事项入账后，对科目余额进行结转，会计分录为1）若科目余额在借方借：应交税费-应交增值税-进项税贷：应交税费-增值税特别调整-纳税检查调整2）若科目余额在贷方，且应交增值税科目无余额借：应交税费-增值税特别调整-纳税检查调整贷：应交税费-未交增值税3）若科目余额在贷方，应交增值税科目有借方余额且等于或大于该余额借：应交税费-增值税特别调整-纳税检查调整贷：应交税费-应交增值税有关科目借方余额科目4）若科目余额在贷方，应交增值税科目有借方余额且小于该余额借：应交税费-增值税特别调整-纳税检查调整贷：应交税费-未交增值税贷：应交税费-应交增值税有关科目借方余额科目'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第七章  附	  则', '第二十三条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《增值税会计核算办法》', '第七章  附	  则', '第二十四条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第一章   总  则', '第一条', '依据和宗旨为规范本行增值税发票管理，防范和控制风险，根据《中华人民共和国发票管理办法》和《省农村商业银行增值税发票管理暂行办法》相关规定，结合本行实际情况和操作要求，制定本实施细则。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第一章   总  则', '第二条', '适用范围本办法所称增值税发票包括增值税专用发票和增值税普通发票，适用于本行使用增值税发票的各项业务。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第一章   总  则', '第三条', '岗位设置及职责法定代表人：对本单位增值税发票管理负领导责任，并对本单位增值税发票管理和使用依法承担法律责任。财务负责人：组织协调、指导监督本单位增值税发票管理工作。税务专管员：负责增值税发票的管理，各税种的申报及缴纳工作。税管系统操作员：负责增值税发票的开具工作和税管系统的日常维护工作。税管系统审核员：负责对开具增值税发票的审核及发票专用章的管理。税管系统审批员: 负责对开具增值税发票的审批工作。发票保管员:负责增值税发票的领购、保管、进出库的登记。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第二章   增值税发票管理日常工作', '第四条', '发票领购㈠发票保管员根据增值税发票剩余数量以及实际用量拟定领购量，经计划财务部总经理同意后及时向主管税务机关领购。㈡领购发票时必须在税务机关发票窗口当场核对领购发票的信息,包括:发票代码及发票号码、发票领购簿记录与实物、数量是否一致,有无缺联毁损等情况。如存在以上问题,应立即向售票人员反映并得到及时处理。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第二章   增值税发票管理日常工作', '第五条', '发票保管㈠增值税发票纳入重要空白凭证管理，执行领购、使用、销号、作废、结存、检查和与发票专用章分管分用等规定。㈡领购的空白发票及时入库(箱),双人保管,需设置专用的保险设施避免丢失或被盗。㈢登记发票信息后,将报税盘中所领购发票读入税控发票开票软件,读取时核对读入发票的号码是否与纸质发票号码相同。㈣发票领购后,及时登记空白发票领用、作废及库存变动信息, 及时掌握增值税发票的变动情况。㈤计划财务部总经理监督税务专管员和发票保管员每月末对发票进行盘点,核对发票库存是否与税控开票软件内库存数额一致,发现不一致及时核实追查。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第二章   增值税发票管理日常工作', '第六条', '发票开具（一）各支行、业务部门根据计划和实际需求提交开票申请；（二）税管系统操作员根据各支行、业务部门提交的开票需求开具发票；（三）具体开票流程参照第三章发票开具流程办理。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第二章   增值税发票管理日常工作', '第七条', '进项税发票认证各支行、部应于每月 25 日前向计划财务部提交进项税发票抵扣联；计划财务部收到增值税专用发票抵扣联后要及时进行认证,认证通过的专用发票抵扣联应与未认证的发票抵扣联加以区分并由发票保管员保存；将认证相符的增值税专用发票抵扣联和《认证结果清单》进行装订。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第三章   发票开具', '第八条', '开具增值税专用发票㈠税管系统操作员开具发票前，应确认当前税控系统开具的空白发票代码、号码与纸质发票号码是否一致。㈡税管系统操作员只能为一般纳税人开具增值税专用发票，对不属于增值税一般纳税人的只能开具增值税普通发票。㈢发票开具必须按照发票号码顺序填开，全部项目、联次一次开具，与实际交易相符。㈣对于频繁发生同一应税行为的，与客户协商一致后可以按季（月）汇总开具增值税发票。㈤原则上遵循先收款再开票，在收取客户款项后向客户开具增值税发票。㈥需作废重开、提前开具增值税发票和开具红字增值税专用发票的，经计划财务部总经理同意后按规定办理。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第三章   发票开具', '第九条', '不得开具增值税专用发票的情形：不得向消费者个人开具增值税专用发票；不得向适用免征增值税规定的应税业务开具增值税专用发票；不得就金融商品转让开具增值税专用发票；不得就已开具增值税普通发票的收款项目开具增值税专用发票；不得开具增值税专用发票的其他情形。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第三章   发票开具', '第十条', '开具发票审核发票审核员应对开票流程进行审核，确保手续齐全；发票审核内容包括开票日期、购货单位名称、纳税人识别号、货物或应税劳务名称、计量单位、数量、单价、金额、税率、税额、价税合计等内容。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第三章   发票开具', '第十一条', '在开具增值税专用发票当月，发生应税收入冲回、开票有误等情形，收到退回的发票联、抵扣联同时符合下列情形的按作废处理。（一）收到退回的发票联、抵扣联时间未超过开票当月；（二）未抄税并且未记账；（三）付款方未认证或者认证结果为“纳税人识别号认证不符”、“专用发票代码、号码认证不符”。对于作废的纸质增值税专用发票（含未打印的专用发票）应在各联次上注明“作废”字样，全联次留存。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第三章   发票开具', '第十二条', '开具增值税专用发票后，发生应税收入退回、开票有误、应税服务中止以及发票抵扣联、发票联均无法认证等情形但不符合作废条件，需要开具红字增值税专用发票的，按以下方法处理：㈠增值税专用发票尚未交付款方或者付款方拒收的，应于增值税专用发票认证期限内登录税务机关增值税发票系统填开并上传《开具红字增值税专用发票信息表》。㈡凭税务机关校验通过的《开具红字增值税专用发票信息表》开具红字增值税专用发票。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第三章   发票开具', '第十三条', '运营管理部应负责及时更新客户相关信息，确保开具的发票客户信息准确齐全。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第四章  发票的保管和归档', '第十四条', '已开具增值税发票的记账联，由计划财务部集中保管。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第四章  发票的保管和归档', '第十五条', '开具的增值税发票应以当面交接、委托邮寄等方式移交给客户，及时办理移交手续，明确相关单位和人员责任。在完成交接前，开具完成的发票全联次要视同空白发票管理，整理排序后入库（箱）保管。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第四章  发票的保管和归档', '第十六条', '发票保管员应于发票使用当日对所保管的增值税空白发票进行盘点,由开票复核员监盘并审核盘点结果后双人入库（箱）保管,管理人员定期（最长每周）对增值税空白发票进行检查。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第四章  发票的保管和归档', '第十七条', '按照税务机关的规定存放和保管发票，不得擅自损毁。已经开具的增值税发票记账联，应当保存 5 年。保存期满，报经税务机关查验后销毁。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第四章  发票的保管和归档', '第十八条', '增值税发票在保管过程中出现破损、水浸或存在印制质量问题及停用换版等原因，需退回税务机关的，应及时与税务机关协调缴销事宜。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第四章  发票的保管和归档', '第十九条', '遗失增值税专用发票的，由遗失部门人员出具增值税专用发票遗失情况说明，经部门负责人证明由总行分管该部门领导审核后办理。㈠ 遗失发票联和抵扣联1．如果丢失前已认证相符的，需获取收款方提供的相应增值税专用发票记账联复印件（加盖发票专用章）及收款方主管税务机关出具的《丢失增值税专用发票已报税证明单》，作为增值税进项税额的抵扣凭证。2．如果丢失前未认证的，需获取收款方提供的相应增值税专用发票记账联复印件进行认证，认证相符的可凭增值税专用发票记账联复印件（加盖发票专用章）及收款方主管税务机关出具的《丢失增值税专用发票已报税证明单》，作为增值税进项税额的抵扣凭证。㈡仅遗失抵扣联1．如果丢失前已认证相符的，可使用增值税专用发票发票联复印件留存备查；2．如果丢失前未认证的，可使用增值税专用发票发票联认证，发票联复印件留存备查。㈢仅遗失发票联可将增值税专用发票抵扣联作为记账凭证，增值税专用发票抵扣联复印件留存备查。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第五章  检查监督', '第二十条', '运营管理部对营业网点开具、经营业网点传递但暂未传递给客户的已开具增值税发票参照重要空白凭证管理要求进行管理，应进行定期和不定期检查。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第五章  检查监督', '第二十一条', '审计部应将增值税发票管理情况纳入审计范围，并按规定审计。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第五章  检查监督', '第二十二条', '对检查审计中发现的问题应妥善处理，及时改正，不留死角。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第六章   附	 则', '第二十三条', '本细则由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《增值税发票管理办法实施细则》', '第六章   附	 则', '第二十四条', '本细则自发文之日起施行。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第一章  总 则', '第二条', '资产负债管理委员会是全行利率定价的决策机构，对全行利率工作实行统一管理，利率定价管理方针政策的调整需经本行资产负债管理委员会讨论通过。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第一章  总 则', '第三条', '存款利率定价遵循以下基本原则：（一）市场化原则。以市场供求为基础，以实施经营发展战略为基本要求，实现存款量价平衡发展。在市场竞争中准确定位，有效防控风险，实现财务可持续发展。（二）匹配性原则。经营管理存款的成本和所获取的收益是确定存款利率定价的重要参考因素，确保负债成本和资产收益相匹配，形成资产业务和负债业务良性互动，实现经营利润目标。（三）差异性原则。根据不同存款产品的性质、金额、期限、区域以及客户综合贡献度等因素，在精细化核算的基础上，实行差异化的存款利率定价。（四）合规性原则。严格遵守国家法律、法规和利率管理规定及市场利率定价自律规则，确定并执行存款利率定价管理权限，禁止任何形式的利率违规行为。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第一章  总 则', '第四条', '本行各营业网点及业务部门均应遵守本办法。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第二章  存款利率定价组织结构', '第五条', '资产负债管理委员会为本行的存款利率定价决策机构，负责根据全行发展战略及市场形势，审议并决定本行存款利率定价策略、规则、流程、授权、内控制度及其他重大利率管理决策，定期研究本行存款利率执行情况，并及时调整和优化定价策略，确定本行各档次的挂牌利率。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第二章  存款利率定价组织结构', '第六条', '在资产负债管理委员会授权下，计划财务部作为本行存款利率定价的归口管理部门，负责根据经审定的存款利率定价策略和规则，具体制订本行存款业务的内外部利率定价模式、管控办法，发布存款利率，按授权规定组织审批各业务条线或各分支机构的超权限存款利率定价申请，对存款利率执行情况进行监督、分析与后评价等。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第二章  存款利率定价组织结构', '第七条', '各支行、营业部、资金营运部是本业务条线的存款利率执行部门，负责本业务条线存款利率定价执行、实施、监测、分析和后评价，及时与利率归口管理部门沟通市场情况及同业动向，提出存款利率定价相关建议等。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第二章  存款利率定价组织结构', '第八条', '运营、信息科技、合规、审计、监察等部门是存款利率定价的支持部门，分别负责提供本行存款利率定价所需的数据信息，开发和维护存款利率定价管理相关信息系统，以及对本行存款利率定价管理和执行情况进行监督、审计，处理存款利率定价违规行为。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第二章  存款利率定价组织结构', '第九条', '计划财务部负责本行银行账户利率风险管理，对本行银行账户利率风险进行识别、计量、监测和控制。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第二章  存款利率定价组织结构', '第十条', '各支行、营业部在贯彻执行本行存款利率定价管理政策的同时，要做好利率政策规定的宣传、解释、落实和反馈工作，收集、整理当地存款利率定价情况，并完成总行要求的其他存款利率管理工作。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第三章  存款利率定价方法', '第十一条', '存款定价应以实现本行的阶段性经营目标为前提，在综合分析同业竞争、本行的承受能力、资金用途、盈利能力、资金价格走势等因素后，制定出合理的价格标准。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第三章  存款利率定价方法', '第十二条', '存款价格必须与各种资金来源价格之间保持适宜的比价关系，保证能够筹集到足够的资金，且资金成本适当。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第三章  存款利率定价方法', '第十三条', '根据存款来源的性质和特点，实行差别资金价格。一般而言，活期存款利率低于定期存款，短期存款利率低于长期存款利率，流动性大的存款利率低于流动性小的存款利率。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第三章  存款利率定价方法', '第十四条', '存款利率定价方法主要包括：（一）以成本为中心的定价方法，即在保证本行自身利润基础上的定价方法。1.目标利润定价法。基本原理是按存款资金运用可得到的平均价格，减去本行经营成本和目标利润后得出的可以接受存款成本，以此作为存款价格。优点是简单易行，保证目标利润。2.边际成本定价法。基本原理是将新增存款需增加利息支出与本行用新增存款发放贷款所预期增加的利息收入相比较，以此来选择最佳的利率水平。边际成本即新增存款需增加的利息支出。（二）以市场为中心的定价法1.市场渗透存款定价法。基本原理是提供高于市场水平的高利率，或向客户收取大大低于市场水平的费用，从而吸引尽可能多的客户。通过由此带来的存款量的增加和相关贷款业务的增加提高利润率。2.随行就市定价法。基本原理是根据银行同业的平均价格水平或竞争对手的现行价格为基础制定本行的存款价格。（三）综合定价法。即综合考虑客户存款的金额、期限、取款方式、计息方式、品牌和声誉、市场利率走势等因素，进行综合定价。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第三章  存款利率定价方法', '第十五条', '存款利率定价体系包括：存款内部指导利率、存款挂牌利率、存款实际执行利率。（一）存款内部指导利率，是指本行进行存款利率定价时的内部参考水平。通过测算本行存款资金运用的总体净收益率，扣除存款管理成本、存款保险费率等成本费用，并扣减目标利润率（即分摊至存款业务部门的目标利润率）后，确定本行的存款内部指导利率。（二）存款挂牌利率，是指本行对外公告的各期限品种存款利率水平，对本行普通客户的存款利率一般应照此执行。通过本行存款内部指导利率，充分考虑本行发展战略、资产负债状况以及市场竞争环境、同业存款挂牌利率情况等因素，最终确定本行的存款挂牌利率。（三）存款实际执行利率，是指本行与客户在存款合同中实际执行的利率水平。在本行存款挂牌利率基础上，根据客户综合贡献度、金额、地域等调整项，以及细分市场的具体情况，确定本行对客户实际执行的存款利率。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第三章  存款利率定价方法', '第十六条', '存款利率定价公式为：（一）存款内部指导利率=存款资金运用净收益率-存款管理成本率-存款保险费率-目标利润率；其中，存款资金运用净收益率=资金运用收益率-资金成本率-税金及附加-风险成本率-资本成本率；（二）存款挂牌利率=存款内部指导利率+战略调整点差+市场竞争调整点差；（三）存款实际执行利率=存款挂牌利率+客户调整点差+金额调整点差+区域调整点差。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第三章  存款利率定价方法', '第十七条', '在当前利率市场化改革进程中，本行的存款挂牌利率和实际执行利率均应不高于央行规定的存款利率浮动区间上限。未来存款利率浮动区间上限放开后，本行将根据中国人民银行及市场利率定价自律机制的相关要求，结合本行实际情况，拟定存款实际执行利率的上限，并以此对本行存款利率定价实施必要的上限控制。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第四章  存款利率定价流程', '第十八条', '计划财务部根据本行存款利率定价所需的数据信息计算、测算存款资金运用净收益率和存款管理成本率，作为存款利率定价基础。同时依据中国人民银行利率政策，结合本行实际拟定或调整存款内部指导利率与挂牌利率，报资产负债管理委员会审定后执行。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第四章  存款利率定价流程', '第十九条', '对于一般客户，存款实际执行利率按本行确定的存款挂牌利率执行。对于议价客户，存款实际执行利率可在存款挂牌利率上下浮动，需由资产负债管理委员进行审议，并由计划财务部根据审议结果下发利率执行通知。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第四章  存款利率定价流程', '第二十条', '定期组织存款利率专项检查，并将存款利率管理检查纳入对各支行、营业部综合考核的内容之中，对于出现利率违规行为的支行或部门，视情节轻重和造成危害的程度，对其主要负责人及其直接业务人员进行必要的惩罚，违反法律法规的依法移交有关部门处理。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第五章   附 则', '第二十一条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《存款利率定价管理办法》', '第五章   附 则', '第二十二条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第一章  总  则', '第一条', '为加强本行实物资产的管理，规范实物资产的购建、使用、保管、处置等操作程序，提高实物资产的使用效率，根据《中华人民共和国企业所得税法》、《金融企业财务规则》及有关规定，结合本行实际情况，制定本办法。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第一章  总  则', '第二条', '实物资产分为固定资产和低值易耗品。固定资产，是指同时具有下列特征的有形资产：（一）为生产商品、提供劳务、出租或经营管理而持有的；（二）使用寿命超过一个会计年度。除固定资产以外的实物资产为低值易耗品。具体如下：（一）房屋建筑物：主要是指房产及建筑物。（二）电子设备：主要指电脑、图形终端、监控设备、密码键盘、指纹仪、扫描仪、ATM、CDS、UPS电源、存储设备、路由器、服务器、POS机等电子设备。（三）运营设备：主要是指点钞机、鉴别仪、捆钞机、清分机、扎把机等设备。（四）办公设备：主要是指复印机、传真机、LED显示器、钱槽、办公桌椅、档案柜、空调、冰箱、彩电、热水器、床等办公设备或单位配备的员工生活设施。（五）运输设备：主要是指轿车、面包车等运输车辆。（六）其他设备：不属于以上分类的设备。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第一章  总  则', '第三条', '资产管理的基本原则实物资产按照“统一领导、分级管理、责任到人、物尽其用”的原则，实行行长责任制，各部室及网点负责人为实物资产管理的第一责任人。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第二章  部门工作职责', '第四条', '实物资产归口管理部门行政办公室、信息科技部、电子银行部、运营管理部、安全保卫部和资产保全部为实物资产归口管理部门，主要职责如下：行政办公室负责土地、自有房屋建筑物、车辆、空调、电视、办公桌椅等实物资产的管理；信息科技部负责电脑、打印机、高拍仪等电子设备的管理；电子银行部负责ATM、CDS、POS等银行自助机具的管理；运营管理部负责点钞机、捆钞机、票据识别仪、清分机等运营设备的管理；安全保卫部负责监控报警设备、互动门等技防物防设施的管理；资产保全部负责抵债资产的管理。归口管理部门主要职责如下：（一）组织建立相关资产台账，监督、协调支行（部）建立健全资产台账及账目；（二）负责实物资产的验收、入库、领用、发出、调拨、报废、出售等记录和统计，提出资产出租、变卖等建议，并负责办理相关产权登记手续；（三）负责实施实物资产清查盘点，配合计划财务部做好固定资产账账、账卡、账实核对工作。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第二章  部门工作职责', '第五条', '计划财务部为实物资产牵头管理部门，其主要职责如下：（一）负责登记固定资产卡片及相关账务的核算；（二）负责组织相关部门对固定资产的清查，协调、监督固定资产账账、账卡、账实的核对工作。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第二章  部门工作职责', '第六条', '各部室、支行为实物资产使用部门，其主要职责如下：（一）负责配合资产管理部门实施实物资产调拨、租入、租出、盘盈、盘亏、维护、修理、报废、出售等工作；（二）做好下发资产的验收、点交、盘点、清理等工作；（三）负责建立实物资产台账；（四）负责贯彻和落实实物资产管理办法，根据“谁使用，谁负责”的原则，归口管理，责任到人，正确使用、保养和保管实物资产；（五）组织资产归口管理部门，做好实物资产清查与盘点工作，负责账账、账卡、账实的核对工作。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第三章  资产的购建', '第七条', '购建预算 各支行（部）年初编制资产购建预算，报本行资产归口管理部门审核、汇总；资产购建预算经资产归口管理部门审核同意，交计划财务部初审，计划财务部初审同意后提交财务管理审查委员会或资产负债管理委员会会办，报本行领导审批。各支行（部）根据经批准的资产购建预算作资产支出的资金预算。各支行（部）原则上不允许在资产购建预算之外购建资产，特殊情况下须向本行资产归口管理部门报请追加资产购建预算，资产追加预算经计划财务部初审并提交财务管理审查委员会或资产负债管理委员会会办同意后生效。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第三章  资产的购建', '第八条', '申购流程资产购建申请资产使用单位（各支行或部门）因经营管理的需要购建资产，须填写《xxx银行股份有限公司资产申购报告单》（见附件1），附经本行批准的资产预算，根据资产类别分别报相关资产归口管理部门审核。㈡资产购建审批资产的购建金额在2万元（含）以下的，报分管财务副行长审批。资产的购建金额在2万元以上的，经财务管理审查委员会会办同意后，由分管财务副行长审批通过。购建大型资产的，按照《xxx省农村信用社大型资产购建管理办法》的相关规定执行。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第三章  资产的购建', '第九条', '物资招标采购（一）本行设立采购招标委员会，负责资产购建，其他任何支行、部门或个人都无权采购。采购招标委员会由本行纪检监察部、行政办公室、计划财务部、审计部、合规管理部、风险管理部等相关部门组成，牵头部门为行政办公室，拟申请购建资产的归口管理部门列席物资采购招标。（二）资招标采购权限1．一般性物资采购权限⑴1000元(含)以下由行政办公室负责人洽谈定价后直接实施;⑵1000元以上、5000元（含）以下由行政办公室负责人洽谈定价，经分管行政办公室副行长同意后直接实施;采取双人以上比价洽谈方式直接采购；⑶5000元以上、2万元（含）以下项目由行政办公室负责人、分管行政办公室副行长洽谈定价，经总行行长同意后直接实施，采取双人以上比价洽谈方式直接采购;⑷2万元以上项目立项，经财务管理审查委员会同意，由行政办公室采用招标、邀标或比价洽谈等方式进行，同期合同价、招标价、省联社下达指导价可比照执行。批量或单件在20万元（含）以上的，原则上采取招标方式，同时邀请总行纪检监察部门参加。特殊情况（如涉密、技术要求唯一、物品种类繁杂、上级部门指定等）无法实施招标的，可经委员会会办同意后采用邀标和比价洽谈方式；2．工程类项目权限⑴工程类项目1000元以下，由行政办公室负责人洽谈定价后直接实施;⑵工程类项目1000元以上、5000元（含）以下由行政办公室负责人洽谈，经分管行政办公室副行长同意后直接实施；⑶工程类项目5000元以上、2万元（含）以下由行政办公室负责人、分管行政办公室副行长共同洽谈，向总行行长报备后实施。其中，1万元（不含）以上工程类项目提交审计；⑷工程类项目2万元以上，经财务管理审查委员会同意，由行政办公室采用招标、邀标或比价洽谈等方式进行，同期合同价、招标价可比照执行。装饰类工程项目在20万元（含）以上或土建、安装类工程项目50万元（含）以上的，原则上采取招标方式，同时邀请总行监察部门参加。如国家另有相关政策文件规定的，可依据文件执行，采用邀标、比价洽谈或商务谈判等方式进行；工程竣工须由分管副行长、行政办公室及相关职能部门组织验收；3．工程类项目变更权限⑴工程类项目变更1000元以下，由行政办公室负责人同意后实施;⑵工程类项目变更1000元以上、5000元（含）以下由行政办公室负责人洽谈，分管行政办公室副行长同意后直接实施；⑶工程类项目变更5000元以上、2万元（含）以下由行政办公室负责人、分管行政办公室副行长共同洽谈，经总行行长同意后实施（该变更项目纳入审计）；⑷工程类项目变更2万元以上，重新对变更部分立项，经财务管理审查委员会同意后实施，由行政办公室采用邀标、比价洽谈或商务谈判等方式进行，同期合同价、招标价可比照执行。对装饰类工程项目变更20万元（含）以上或土建、安装类工程项目50万元（含）以上的，原则上采取招标方式，同时邀请总行监察部门参加。如国家另有相关政策文件规定的，可依据文件执行，采用邀标、比价洽谈或商务谈判等方式进行。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十条', '验收及入账资产购建后，由资产归口管理部门和采购小组负责资产的验收。计划财务部根据资产领用的实际情况及库存分别记账。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十一条', '资产领用资产使用单位（支行或部门）领用资产时，填写《xxx银行股份有限公司资产领用单》（见附件2），向相关资产归口管理部门办理领用登记手续。相关资产归口管理部门及时将资产领用情况在资产台账上予以登记，同时将资产领用情况及时传递到计划财务部予以登记。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十二条', '台账登记本行资产归口管理部门负责归口管理的资产台账的登记，及时登记资产的增减变动情况，按季度与计划财务部核对资产账务，做到账账相符。计划财务部根据《xxx银行股份有限公司资产领用单》、《xxx银行股份有限公司资产调拨单》（见附件3）等单据及时登记资产的增减变动情况。资产台账应记载资产的生产厂商、购建日期、发票号码、名称、规格、数量、使用单位、使用人、责任人等信息。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十三条', '权证管理及资产保险所有资产的权证由行政办公室负责办理及保管。对需要办理保险手续的资产，由行政办公室统一办理。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十四条', '自有资产出租行政办公室负责自有房屋建筑物等相关固定资产出租的管理。对出租的固定资产须设立台账，记录出租固定资产的明细情况，对出租过程和交易记录、租赁合同复印件等资料设专人建档保管。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十五条', '资产维修（一）资产使用单位对保管使用的资产需要维修的须提出申请报资产归口管理部门，2万元（含）以下的资产归口管理部门报经本行分管领导审批后负责维修；2万元以上的，需经财务管理审查委员会会办后，报行长审批同意。（二）对使用不当、管理不善造成维修的，资产归口管理部门应进行相关责任认定，并追究相关责任人的责任。（三）资产归口管理部门负责做好资产维护中发生的资料保管存档工作。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十八条', '资产盘点计划财务部牵头并会同资产归口管理部门每年对各归口管理的资产组织一次全面盘点，核对账实、账账是否相符。出现差异的，查明差异原因、追究相关责任。资产非客观原因盘亏的，资产使用人承担按价赔偿的责任。对于资产盘盈的，资产归口管理部门负责督促相关资产使用部门及时将盘盈的资产提交计划财务部入账。资产盘点应编制资产盘点清册，盘点人员和资产使用单位（支行或部门）及资产归口管理部门须在资产盘点清册上签字。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第四章  资产的使用管理', '第十九条', '任何人不得故意损坏或转移资产，故意损坏或丢失的按价赔偿。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第五章  资产调拨、转移、处置、报废', '第二十条', '资产调拨和转移㈠各支行（部）因工作的实际需要，需办理资产调拨时，首先由相关的资产归口管理部门开具《xxx银行股份有限公司资产调拨单》；相关人员凭签字手续齐全的《xxx银行股份有限公司资产调拨单》方能进行资产的调拨；擅自调拨资产的，本行追究相关责任人的经济责任。资产归口管理部门及时对资产的调拨情况进行跟踪、确认、登记；并从调拨之日起10日内将调拨单送交计划财务部。逾期移交调拨单的，本行将对资产归口管理部门的直接责任人员进行处罚。原资产使用人员未能取得资产物品调拨单、自行进行资产转移的，本行仍认定原资产使用人为该资产的责任人，总行保留向原资产使用人进行资产追偿的权利。㈡资产使用人员因工作岗位调动或离职须向相关资产归口管理部门办理资产的转移或退还手续。相关资产归口管理部门及时将资产转移或退还情况在资产台账上予以登记，并及时传递到计划财务部登记。人力资源部办理员工的岗位变动或离职手续时，应通知资产归口管理部门及时检查员工资产的归还情况，资产未移交清楚的，不予办理相关手续。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第五章  资产调拨、转移、处置、报废', '第二十一条', '资产处置㈠闲置资产的利用和处置。资产使用单位（支行或部门）必须保证该支行（部）闲置资产的安全和完整并及时上报资产归口管理部门，由资产归口管理部门对该资产做出调拨、出租、或变卖的处理方案，按规定的审批权限报批，并及时提交计划财务部作账务处理。㈡拆除资产的处置。相关资产进行拆除前，拆除资产的牵头管理部门须会同拆除资产所涉及到的所有资产归口管理部门进行会办，对将要拆除的资产的使用、调拨或处置提出方案，报分管行长批准后实施。㈢资产清查中发现的资产盘盈、盘亏、毁损、报废，分别由资产使用单位（支行或部门）负责查明原因，落实责任，资产归口管理部门负责人签字，资产归口管理部门填写《xxx银行股份有限公司闲置资产处理申请单》（见附件4），详细填写处置申请原因，如毁损、报废、长期闲置等，及时提出处置方案报本行，按照本行转授权管理办法进行审批。⒈资产净值在50万元（含）以下的报本行行长室批准；⒉资产净值在50万元以上500万元（含）以下的报董事会批准；⒊资产净值在500万元以上的报股东大会批准。经批准后，资产归口管理部门按处理方案实施。㈣各支行（部）未经批准，不得擅自处置资产，未经批准擅自处置资产的，本行追究相关责任人的经济责任。㈤凡因工作不负责任，导致资产流失，将追究各支行、部门责任人的责任，并同时对归口管理部门负责人进行问责，责任追究的方式主要有经济赔偿、罚款及行政处分。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第六章  审计监督', '第二十二条', '审计部定期或不定期对各支行（部）资产管理使用情况进行审计。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第六章  审计监督', '第二十三条', '审计部实施常规审计或离任审计项目时，将资产的使用管理情况纳入审计内容。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第七章  附  则', '第二十四条', '抵债资产的管理按照《xxx银行股份有限公司待处理抵债资产管理办法》执行。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第七章  附  则', '第二十五条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《实物资产管理办法》', '第七章  附  则', '第二十六条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第一章   总  则', '第一条', '为加强本行无形资产管理，维护本行权益，根据《企业会计准则》有关规定，结合本行实际情况，制定本办法。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第一章   总  则', '第二条', '无形资产指本行拥有或者控制的没有实物形态的可辨认非货币性资产。包括：商标权、专利权、软件、土地使用权等。无形资产必须同时满足下列条件，才能予以确认：㈠与该无形资产有关的经济利益很可能流入本行；㈡该无形资产的成本能够可靠计量。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第一章   总  则', '第三条', '无形资产管理的基本原则无形资产管理根据“统一领导，归口管理”的原则，由总行行长统一领导，采用总行集中管理的模式。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第二章　无形资产管理职责', '第四条', '计划财务部为无形资产管理牵头管理部门，主要职责如下：㈠负责制定或修订各类无形资产的管理办法和实施细则；㈡负责无形资产财务核算工作，建立和管理全行无形资产总账及卡片账。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第二章　无形资产管理职责', '第五条', '行政办公室、信息科技部为无形资产管理归口管理部门，主要职责如下：（一）负责无形资产需求的审核、购建及后续管理，其中行政办公室为非软件类无形资产的归口管理部门，信息科技部为软件类无形资产的归口管理部门；（二）负责建立相关无形资产台账，配合计划财务部做好无形资产账账、账卡核对工作。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第二章　无形资产管理职责', '第六条', '审计部负责无形资产的审计工作。审计的内容包括：各项资产管理制度是否合规有效； ㈡各项资产是否登记齐全，统计数字是否真实、完整、准确；㈢各项资产是否做到合理使用、有效管理；㈣资产购建、处置是否按规定程序办理审批手续；㈤资产是否遭到侵犯、损害；㈥其他需要监督的内容。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第三章　无形资产的审批权限', '第七条', '各支行、部及机关各部室购置无形资产必须提出购置申请，报总行无形资产归口管理部门审核。购建2万元以内（含）的无形资产由分管财务副行长直接审批；购建超过2万元的无形资产必须经过财务管理审查委员会审议通过。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第三章　无形资产的审批权限', '第八条', '处置闲置不用的无形资产，必须报总行无形资产管理部门审核，资产管理部门根据无形资产出售额度分别按下列程序报批：处置净值2万元以内（含）的无形资产由分管财务副行长审批；处置净值超过2万元的无形资产必须经过财务管理审查委员会审议通过。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第三章　无形资产的审批权限', '第九条', '超过使用期限，确已失去效能或因自然灾害等原因致损而无法修复的无形资产的报废，必须报本行资产管理部门审核，分管财务副行长审批。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第四章  无形资产的购建', '第十条', '无形资产的购置需履行审批程序，由使用机构向管理部门提出请购申请。具体流程如下：使用机构向管理部门提出请购申请→管理部门审核通过（按审批权限提交上级行领导审批）→审批通过，由管理部门组织购建→验收无误→交付使用机构使用→计划财务部依据合同约定，凭供货单位有效发票及本行验收凭据，进行账务处理。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第四章  无形资产的购建', '第十一条', '无形资产购建必须遵循先审批后购建的原则，不得未经审批擅自购建或逆程序操作先购建后审批。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第四章  无形资产的购建', '第十二条', '计划财务部必须根据无形资产验收单或审计报告、发票等，与相关信息核对一致后，方能办理入账手续。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第四章  无形资产的购建', '第十三条', '大型资产购置或建设一般应实行公开招标办法，并按规定报行长室、董事会批准。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第五章  无形资产计量和摊销', '第十四条', '无形资产应当按照成本计量。外购无形资产的成本，包括购买价款、相关税费，以及使无形资产达到预定用途所发生的其它支出。自行开发无形资产的成本，在符合会计准则规定的前提下，由研发该项资产达到预定可使用状态前所发生的必要支出构成。投资者投入无形资产的成本，应当按照投资合同或协议约定的价值确定，但合同或协议约定价值不公允的除外。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第五章  无形资产计量和摊销', '第十五条', '本行应当根据无形资产的性质和使用情况，合理区分无形资产的使用寿命，对使用寿命有限的无形资产计算应摊销金额，在使用寿命内系统合理摊销；对使用寿命不确定的无形资产不进行摊销，但要每年进行减值测试。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第六章  无形资产处置及报废', '第十六条', '处置闲置不用的无形资产，报批后，应当将处置收入扣除账面价值和相关税费后的金额计入当期损益。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第六章  无形资产处置及报废', '第十七条', '无形资产预期不能为本行带来经济利益的，报批后，应当将该无形资产账面价值予以转销并终止确认。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第七章  在建工程的管理', '第十八条', '如发生大型软件开发等工程期较长的无形资产项目，经有权部门批准后由计划财务部负责在建工程核算。工程结束，无形资产交付使用后，经有权部门批准后建立无形资产账。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第七章  在建工程的管理', '第十九条', '在建工程包括研发前期准备、正在研发和虽已完工但尚未交付使用等状态。在建工程按实际成本计价。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第八章  罚  则', '第二十条', '本行审计部应对无形资产的购置进行监督，购置的审批及采购程序要符合规定，不得越权审批或采购，如有违反规定将追究当事人和主管人员责任。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第八章  罚  则', '第二十一条', '本行计划财务部应做好无形资产建帐等工作，出现无形资产财务混乱，视情节轻重应追究当事人和主管人员的责任。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第八章  罚  则', '第二十二条', '本行资产管理部门负责无形资产后续管理，如因管理不善造成资产流失或损失的，将追究当事人、管理人员责任。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第八章  罚  则', '第二十三条', '未经审批，自行添置、赠送或报废处理无形资产的，按规定对主管人员进行处理。违反法律法规，造成资产严重损失、流失，情节严重，构成犯罪的，移送司法机关依法追究刑事责任。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第九章  附  则', '第二十四条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《无形资产管理办法》', '第九章  附  则', '第二十五条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第一条', '为规范本行服务收费行为，明确服务价格收费标准，保护客户合法权益，促进本行稳健发展，根据《中华人民共和国商业银行法》、《中华人民共和国价格法》、《商业银行服务价格管理办法》、《关于印发商业银行服务政府指导价政府定价目录的通知》以及其他有关规定，特制定本办法。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第二条', '本办法所称服务价格收费，是指本行在为客户办理业务并提供相关服务时所收取的各种服务费用和手续费。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第三条', '本办法适用于全行所有支行及机关所有部门。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第四条', '服务价格行为应当遵守国家法律、法规、规章和有关监管规定，遵循公开、公平、诚实、信用原则，接受社会监督。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第五条', '根据服务的性质、特点和市场竞争状况，服务价格分别实行政府指导价、政府定价和市场调节价。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第六条', '服务价格管理原则：（一）坚持合法合规原则。加强对服务收费行为的合规管理，严格执行政府定价，合理审慎在规定的幅度内确定政府指导价。（二）坚持定价测算原则。对属于市场调节范畴的相关服务价格，应进行科学合理的成本测算，在充分考虑市场供求、竞争状况、企事业单位和个人的承受能力、公众反映的意见和建议等因素基础上合理确定价格。（三）坚持履行社会责任原则。切实维护客户的合法权益，做好原有客户和新增客户相关收费政策的衔接工作。对合约中明确免费的服务项目，不得在变更合约前直接收费或单方面变更合约进行收费。（四）坚持风险可控原则。切实加强法律风险和声誉风险管理，做好服务收费的舆情引导工作。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第七条', '充分加强服务价格的内部控制，充分披露服务价格信息，保障客户获得服务价格信息和自主选择服务的权利。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第一章  总  则', '第八条', '本行服务价格管理活动接受中国银行业监督管理委员会和国务院价格主管部门依照有关法律、法规及本办法的规定进行监督管理。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第二章  政府指导价、政府定价的实施和执行', '第九条', '对客户普遍使用与国民经济发展和人民生活关系重大的银行基础服务，按国家规定实行政府指导价或政府定价。基础服务包括部分转账汇款、现金汇款、取现和票据等。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第二章  政府指导价、政府定价的实施和执行', '第十条', '实行政府定价的服务价格收费，其收费项目和收费标准根据《关于印发商业银行服务政府指导价政府定价目录的通知》规定，由本行结合省联社下发的具体收费标准，统一制定执行。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第三章  市场调节价的制定和调整', '第十一条', '除政府指导价、政府定价范围以外的，实行市场调节价的服务价格，由总行统一制定和调整。分支机构因地区性明显差异需要实行差别化服务价格的，由总行区别情况确定并公布，分支机构不得自行确定项目和标准。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第三章  市场调节价的制定和调整', '第十二条', '制定和调整市场调节价，按照以下程序：（一）制定相关服务价格的定价策略和定价原则；（二）综合测算相关服务项目的成本和收入情况；（三）进行价格决策；（四）形成统一的业务说明和宣传材料；（五）在各类相关营业场所的醒目位置公示；（六）在网站主页醒目位置公示。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第三章  市场调节价的制定和调整', '第十三条', '制定和调整实行市场调节价的服务价格，应当合理测算各项服务支出，充分考虑市场因素进行综合决策。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第三章  市场调节价的制定和调整', '第十四条', '按照市场化原则接受相关单位的委托，办理代收水、电、燃气、通讯、有线电视、交通违章罚款等费用以及代付工资、社会保险金、住房公积金等代收代付业务，按照“谁委托、谁付费”的原则收取委托业务相关手续费，不得向委托方以外的其他单位和个人收取费用。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第三章  市场调节价的制定和调整', '第十五条', '客户因本行调整服务价格或变更服务合同，要求终止或变更服务的，相关营业机构应当根据客户要求、相关服务合同或其他已签署的法律文件采取合理有效的措施，依法及时终止或变更相关服务和对应的服务合同。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第三章  市场调节价的制定和调整', '第十六条', '向客户收取的服务费用，应当对应明确的服务内容。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第四章  服务价格的报告', '第十七条', '本行应每年至少向有关部门报送一次服务价格的管理执行情况。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第四章  服务价格的报告', '第十八条', '服务价格报告的内容至少包括以下情况：（一）服务价格管理的组织架构和服务价格管理总体情况；（二）服务收费项目设置、调整情况和相应的收入变化情况；（三）免费服务项目设置情况、调整情况、相应的收入变化情况，在服务价格方面承担社会责任的情况；（四）服务项目的收入结构和评估情况；（五）服务价格的信息披露情况，包括信息公示的方式和渠道；（六）与服务价格相关的投诉数量、分类和处理情况；（七）对客户反馈意见的解释说明情况和意见采纳情况；（八）本行服务的分类、具体项目、价格水平等情况；（九）与服务价格相关的其他情况。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第十九条', '本行严格按照相关规定进行服务价格信息披露。在营业场所醒目位置和网站主页醒目位置，及时、准确公示实行政府指导价、政府定价和市场调节价的服务项目、服务内容、服务价格、适用对象、政府指导价或政府定价的文件文号、生效日期、咨询（投诉）的联系方式等。公示的各类服务价格项目应当统一编号。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第二十条', '积极采取措施保护客户相关权益：（一）在营业场所的醒目位置提供相关服务价格目录或说明手册等，供客户免费查阅，条件许可时采用电子显示屏、多媒体终端、电脑查询等方式披露服务价格信息；（二）在总行网站主页醒目位置公示服务价格目录或说明手册等，供客户免费查阅；（三）使用电子银行等自助渠道提供服务的，在收取服务费用之前，提示客户相关服务价格，并保证客户对相关服务的选择权；（四）各分支机构同城业务覆盖的区域范围为xxx市范围之内。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第二十一条', '各支行（部）应当提醒客户提供真实有效的联系信息并在相关信息变更后及时通知本行，以便本行调整服务价格时按照合同约定方式及时告知客户。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第二十二条', '服务价格信息的公示涉及优惠措施的，明确标注优惠措施的生效和终止日期。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第二十三条', '提高实行市场调节价的新的服务价格，至少于实行前3个月按照本办法规定进行公示，必要时采用书面、电话、短信、电子邮件、合同约定的其他形式等多种方式通知相关客户。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第二十四条', '本行接受其他单位委托开展代理业务收费时，应当将委托方名称、服务项目、收费金额、咨询（投诉）的联系方式等信息告知客户，并在提供给客户的确认单据中明确标注上述信息。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第二十五条', '严格执行服务价格信息披露的有关规定，在为客户提供服务之前，各支行（部）应当告知相关服务项目、服务价格、优惠措施（含生效和终止日期），客户确认接受该服务价格后，方可提供相关服务；客户在使用服务前明确表示不接受相关服务价格的，不得强制或变相强制客户接受服务。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第五章  服务价格信息披露', '第二十六条', '对于需要签署服务章程、协议等合同文件的银行服务项目，在相应的合同文件中应以通俗易懂、清晰醒目的方式明示服务项目或服务内容、服务价格、优惠措施及其生效和终止日期、与价格相关的例外条款和限制性条款、咨询（投诉）的联系方式等信息。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第二十七条', '按照审慎经营原则，本行建立健全服务价格管理制度和内部控制机制，严格执行内部授权管理。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第二十八条', '服务价格管理制度应当严格遵守国家法律法规，明确价格行为违规的问责机制和内部处罚措施。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第二十九条', '总行行长室在政策规定的范围内决定服务价格的收费项目和标准，作出调整收费或免收的决定，审议提高服务价格执行情况的工作报告。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第三十条', '本行计划财务部门牵头负责服务价格管理工作，根据政府定价、政府指导价、市场调节价要求制定本行服务价格并提交财务管理委员会会办，经行长室审批、董事会备案后实施；运营管理部、审计部适时对服务价格管理进行指导、评估和检查，及时纠正相关问题，对服务价格执行过程中的相关信息反馈牵头部门；行政办公室负责组织开展服务价格相关公告、宣传；业务前台、支行（部）柜台做好服务价格解释及制度的落实，纪检监察部接洽投诉处理等工作。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第三十一条', '建立服务价格投诉管理制度，做好客户投诉登记、调查、处理、报告等事项的管理流程、负责部门和处理期限，确保客户投诉能够及时进行调查处理。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第三十二条', '设立统一的投诉电话、书面投诉联系方式等渠道，并在营业场所和网站醒目位置进行公示，以便及时受理客户对服务价格的相关投诉。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第三十三条', '认真处理和及时答复客户投诉。建立相应的投诉自查机制，对投诉管理制度的落实情况、投诉处理情况进行定期或不定期自查。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第六章  服务价格的内部管理', '第三十四条', '除国家法律、法规、委托代理合同有相关规定和要求的情况以外，拒绝任何单位和个人利用银行渠道直接向客户收取任何费用。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第七章  服务价格的外部监督', '第三十六条', '鼓励有关单位和个人对本行服务价格违法行为进行监督。有关单位和个人发现服务价格行为存在侵害其合法权益问题的，可依照法律、法规规定采取相关法律措施或投诉。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第七章  服务价格的外部监督', '第三十七条', '总行定期或不定期组织服务价格的检查，充分反映各支行（部）的服务价格执行情况以及投诉管理制度落实和处理情况，对违规情况向相关部门及时反馈，对违规人员和违规单位及时处置。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第八章  附  则', '第三十八条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《服务价格管理办法》', '第八章  附  则', '第三十九条', '本办法自2014年8月1日起执行。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第一章  总	 则', '第一条', '为建立健全本行流动性风险管理体制和机制，完善全面风险管理体系，保证本行各项业务可持续发展，《商业银行流动性风险管理办法（试行）》、《商业银行风险监管核心指标（试行）》，并结合本行实际，制定本办法。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第一章  总	 则', '第二条', '本办法所指流动性风险是指无法以合理成本及时获得充足资金，用于偿付到期债务、履行其他支付义务和满足正常业务开展的其他资金需求的风险。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第一章  总	 则', '第三条', '流动性风险管理是指识别、计量、监测和控制流动性风险的全过程。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第一章  总	 则', '第四条', '本行流动性风险管理原则是：全员参与、动态预防、科学量化、审慎管理。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第一章  总	 则', '第五条', '本行流动性风险管理目标是在符合流动性监管指标要求的前提下，通过对现金流量进行合理安排，确保目前和未来业务资金支付需要，同时避免额外成本，包括资金备付的机会成本、市场筹资溢价和变卖资产损失。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第一章  总	 则', '第六条', '流动性管理基本要求：（一）提高流动性管理的预见性。制定切实可操作的资产负债增长计划，把握整体流动性状况。（二）加强资产负债期限结构管理。掌握在未来特定时段内到期资产数量（现金流入）与到期负债数量（现金流出）的构成状况，及时调整优化资产负债的期限结构，尽可能做到到期资产与到期负债近似匹配。（三）加强流动性指标分析。利用流动性比率指标/指标法、缺口分析法、现金流分析法等指标评估流动性风险，并对未来情景和环境做出模拟，及时提出流动性风险的预警信号。（四）对存款增长及其它融资能力做出适当评估。对存款增长的依赖程度有充分的预计，对敏感资金保持适当流动性，对核心存款保持一定的流动性。（五）增强对信贷投放期限结构和投放节奏等的把握能力，对信贷资金的需求和供给能力做出恰当的预计。（六）建立多层次的流动性屏障，建立以一级备付（各备付金账户）、二级备付（包括存放同业与库存现金）、三级备付（包括购买的各种债券和证券）等多级流动性保障体系，实现基本保证的、弹性的、多层次的流动性保障。客观统计分析本行正常运营所需超额准备率，确保将一级备付和二级备付维护在合适水平。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第一章  总	 则', '第七条', '本行通过建立流动性风险管理组织架构，划分明确的风险管理职责，制定有效的风险管理策略、程序和制度，强化考核监督，持续推动流动性风险管理工作开展。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第二章  流动性风险管理架构与职责', '第八条', '本行建立与流动性风险特点相适应的组织架构，包括董事会、监事会、高级管理层和相关职能部门。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第二章  流动性风险管理架构与职责', '第九条', '董事会承担流动性风险管理的最终职责。具体包括：（一）审核批准本行流动性风险管理体系和重要政策；（二）监督高级管理层在风险管理体系内对流动性风险进行有效管理和控制；（三）对本行流动性风险管理信息系统的完整性、准确性和有效性承担最终责任；（四）决定与流动性风险相关信息披露内容；（五）审核批准本行流动性风险承受能力、流动性风险偏好、流动性风险管理策略与程序、流动性风险限额和流动性风险应急预案，并根据风险管理需要及时对以上内容进行审议修订，审议修订工作至少每年一次；（六）持续关注流动性风险状况，定期获得关于流动性风险水平和相关压力测试报告，及时了解流动性风险重大变化和潜在转变；（七）根据内部审计结果，督促高级管理层针对内部审计发现的问题采取及时有效整改措施，适时调整和完善有关流动性风险管理策略和程序；（八）授权并听取董事会风险管理与关联交易控制委员会开展流动性风险管理的相关工作；（九）法律、法规规定的其他职责。董事会授权下设风险管理与关联交易控制委员会履行以上规定的部分职能，风险管理与关联交易控制委员会要定期向董事会提交有关报告，并将相关资料向监事会备案。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第二章  流动性风险管理架构与职责', '第十条', '高级管理层负责流动性风险具体管理工作，应履行以下职责：（一）根据本行总体发展战略测算流动性风险承受能力，并提请董事会风险管理与关联交易控制委员会审批；根据总体发展战略及内外部经营环境变化及时提出对流动性风险承受能力修订建议，并提请董事会风险管理与关联交易控制委员会审议；（二）根据董事会风险管理与关联交易控制委员会批准的流动性风险承受能力，制定流动性风险管理策略、程序、限额，其中重要策略、程序和限额需提请董事会风险管理与关联交易控制委员会审批后执行；（三）根据董事会风险管理与关联交易控制委员会批准的流动性风险管理策略、程序和限额，对流动性风险进行管理，制定并监督执行有关流动性风险管理的内部控制制度；（四）充分了解并定期评估本行流动性风险水平及管理状况，向董事会风险管理与关联交易控制委员会定期汇报本行流动性风险状况，及时汇报流动性风险的重大变化或潜在转变；（五）建立完善的管理信息系统，支持流动性风险的识别、计量、监测和控制工作；（六）根据董事会和董事会风险管理与关联交易控制委员会批准的相关政策、策略和程序，组织实施压力测试和情景分析，并定期将测试结果向董事会风险管理与关联交易控制委员会汇报，推动压力测试成果在战略决策和风险管理中的应用；（七）制定流动性风险应急预案，并提请董事会风险管理与关联交易控制委员会审批； （八）确保流动性风险偏好、流动性风险管理策略、政策和程序在商业银行内部得到有效沟通和传达；识别并了解可能触发应急预案的事件，并建立适当机制对这些触发事件进行监测； （九）定期对流动性风险管理进行内部审计，并对审计提出的整改措施实施情况进行后续审计，并及时向董事会风险管理与关联交易控制委员会提交审计报告；（十）指定专门部门和人员负责流动性风险管理工作，负责流动性风险管理部门应当职责明确，并建立完善的报告制度。流动性风险管理部门和人员应保持相对独立性，特别是独立于从事资金交易的部门。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第二章  流动性风险管理架构与职责', '第十一条', '计划财务部门为本行流动性风险管理归口部门，履行以下职责：（一）拟定流动性风险管理策略、政策和程序，提交高级管理层和董事会审核批准。（二）识别、计量和监测流动性风险。持续监控优质流动性资产状况；监测流动性风险限额遵守情况，及时报告超限额情况；组织开展流动性风险压力测试；组织流动性风险应急计划的测试和评估。（三）识别、评估新产品、新业务和新机构中所包含的流动性风险，审核相关操作和风险管理程序。（四）定期提交独立的流动性风险报告，及时向高级管理层和董事会报告流动性风险水平、管理状况及其重大变化。（五）拟定流动性风险信息披露内容，提交高级管理层和董事会审批。（六）其他有关职责。各业务部门、支行和业务条线为流动性风险管理的第一防线，认真贯彻执行总行的各项流动性管理措施，及时向计划财务部门报告存在的流动性风险可疑点。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第二章  流动性风险管理架构与职责', '第十二条', '监事会对董事会及高级管理层在流动性风险管理中的履职情况进行监督评价。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第三章  流动性风险管理内容', '第十三条', '本行流动性风险管理分为日常管理和应急管理。日常管理采取存量与流量管理相结合的方法，应急管理通过制定和实施应急预案来实现。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第三章  流动性风险管理内容', '第十四条', '日常流动性管理总体原则是：尽可能提高负债的稳定性和资产的流动性，做好危机处理方案，明确弥补现金流动量不足的工作程序，建立多层次的流动性屏障，充分利用金融市场加强融资能力，降低流动性风险。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第三章  流动性风险管理内容', '第十五条', '对流动性风险实施限额管理，根据业务规模、性质、复杂程度、流动性风险偏好和外部市场发展变化情况，设定流动性风险限额。流动性风险限额包括但不限于现金流缺口限额、负债集中度限额、内部交易和融资限额。每年对流动性风险限额至少进行一次评估，必要时进行调整。流动性存量管理主要包括对流动性风险监管指标、缺口限额和易变现证券运作三项内容的管理。（一）流动性风险监管指标包括流动性比例、核心负债比例、流动性缺口率和超额备付金率。本行流动性比例、核心负债比例、流动性缺口率和超额备付金率不低于监管部门监管标准。（二）借款限额按照最大市场融资能力乘以一定折算系数确定，并根据本行资产负债运行状况和市场资金形势进行定期调整。（三）易变现证券运作。易变现证券是指可通过金融市场进行出售和回购的有价证券，衡量流动性二级准备能力。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第三章  流动性风险管理内容', '第十六条', '流动性监测与监控。（一）计划财务部门对流动性限额或指标实行“按日监测，及时调控，定期总结”。（二）实施大额资金进出报告制度。大额资金进出报告制度是指对流动性管理影响较大的资金进出所规定的报告制度。总行根据资金备付能力，规定大额资金进出报告的最低额度和报告频率。（三）实施压力测试与报告制度。本行制定统一的流动性压力测试情景和假设，并动态调整，压力测试情景、假设的设定和重大调整由风险管理与关联交易控制委员会审定。计划财务部门牵头对本行流动性压力进行检测，并发布压力测试报告。各资金使用部门和支行要根据压力测试报告，改善其流动性储备。加强日间流动性风险管理，确保具有充足的日间流动性头寸和相关融资安排，及时满足正常和压力情景下的日间支付需求。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第三章  流动性风险管理内容', '第十七条', '本行按照监管要求定期披露流动性风险管理情况，在出现流动性危机时，适时披露相关情况说明以提高交易对手、客户及公众的信心，最大限度地减少信息不对称可能给本行带来的不利影响。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第四章  流动性风险管理的程序', '第十八条', '计划财务部门定期向资产负责管理委员会报告本行流动性风险管理状况，说明各项流动性限额或指标执行情况，适时提出调整建议。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第四章  流动性风险管理的程序', '第十九条', '高级管理层定期向董事会风险管理与关联交易控制委员会提交本行流动性风险管理书面监测报告，详细说明风险管理情况和下一步完善措施。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第四章  流动性风险管理的程序', '第二十条', '对本行发生的重大流动性风险事件，高级管理层应及时启动应急预案，并在第一时间向董事会风险管理与关联交易控制委员会和监管部门报告。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第四章  流动性风险管理的程序', '第二十一条', '流动性风险管理纳入内部审计范畴，定期审查和评价流动性风险管理的充分性和有效性。内部审计应当涵盖流动性风险管理的所有环节，包括但不限于：（一）流动性风险管理治理结构、策略、政策和程序能否确保有效识别、计量、监测和控制流动性风险。（二）流动性风险管理政策和程序是否得到有效执行。（三）现金流分析和压力测试的各项假设条件是否合理。（四）流动性风险限额管理是否有效。（五）流动性风险管理信息系统是否完备。（六）流动性风险报告是否准确、及时、全面。流动性风险管理的内部审计报告提交董事会和监事会；对监管部门现场检查和内外部审计机构审计中发现的问题，在高级管理层落实整改措施后，及时向董事会报告。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第五章  附  则', '第二十二条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《流动性风险管理办法》', '第五章  附  则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第一章  总  则', '第一条', '为预防和最大程度地减少本行因流动性原因产生的支付风险而引发的突发流动性风险事件，保护本行股东和广大存款人的合法权益，确保本行各项业务的稳健运行，根据银监会《银行业突发事件应急预案》、《商业银行流动性风险管理办法（试行）》和有关法律、法规，特制定本应急预案。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第一章  总  则', '第二条', '本预案所指的流动性风险，是指本行无法以合理成本及时获得充足资金，用于偿付到期债务、履行其他支付义务和满足正常业务开展的其他资金需求的风险。当流动性不足时，将会导致无法以合理的成本满足对外支付和业务发展的流动性需求，一旦处置不当，将会发生存款挤提、头寸不足和流动性缺口扩大等危机和风险。流动性风险事件，主要是指由于以下原因引发本行无法满足支付需求而出现的流动性风险。具体包括：（一）贷款过快增长，超过正常幅度；（二）存款出现大幅下跌，出现异常大额支付；（三）从紧货币政策影响带来的流动性紧缩风险；（四）资产负债期限匹配不当；（五）可能导致流动性风险的其他原因。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第二章  组织管理体系', '第三条', '本行成立突发流动性风险事件应急预案处置领导小组（以下简称“应急预案处置领导小组”）。由董事长任组长，行长任副组长,成员由总行副行长及各部门负责人组成。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第二章  组织管理体系', '第四条', '应急预案处置领导小组下设办公室，作为日常办事机构，办公室设在计划财务部，负责全行突发流动性风险事件的组织、协调和处置工作。其主要职责为：制订本行突发流动性风险事件应急预案；遇突发流动性风险事件发生时，启动应急预案，及时处置和化解突发流动性风险，确保突发事件期间和风险处置后业务经营的正常运行。同时，负责及时向银监部门、人民银行、省联社和地方政府等上级部门报告。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第三章  突发流动性风险事件的预防和预警', '第六条', '计划财务部牵头，各业务部门配合，加强全行资产负债比例管理，重视资产、负债的期限匹配和数量匹配，合理安排贷款投放规模与节奏,保持适当的存贷比。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第三章  突发流动性风险事件的预防和预警', '第七条', '计划财务部应综合衡量流动性、安全性和效益性之间的关系，对央行可能采取的紧缩流动性调控措施提前做好充分准备；坚持每日对资金头寸进行监测，防止头寸不足，保证一定的超额备付余额。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第三章  突发流动性风险事件的预防和预警', '第八条', '计划财务部应逐步建立和完善流动性风险监测技术，努力运用情景分析等方法，提高应对多种不利情况下流动性问题的能力，做到防患于未然。流动性比例必须达到 25%以上。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第三章  突发流动性风险事件的预防和预警', '第九条', '计划财务部对照流动性风险预警指标，至少每年对应急计划进行一次监测和评估，必要时时行修订。若发生异常情况，必须及时报告，以便及时启动应急预案。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第三章  突发流动性风险事件的预防和预警', '第十条', '运行管理部负责支付结算及库存现金管理，在发生挤兑、现金库存不足时，与人民银行进行协商，及时调运现金以确保现金正常支付。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第三章  突发流动性风险事件的预防和预警', '第十一条', '金融市场部负责资金的头寸调度，在头寸不足时及时进行资金调度，缓释流动性风险。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第三章  突发流动性风险事件的预防和预警', '第十二条', '行政办公室负责制定对外答复口径、与上级主管部门沟通协调、媒体联络与舆情监控；及时了解和掌握系统内员工的思想动态及社会反映；对不安定因素、风险隐患及突发事件要及时发现、及时报告；与当地政府、公安机关沟通、协调，确保得到公安机关的帮助和支持；维持现场秩序，控制事态发展。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第四章  突发流动性风险事件的应急处置', '第十三条', '发生突发流动性风险事件应坚持依法、快速、高效、稳妥、保密的处置原则。（一）依法原则：即在处置突发流动性风险事件过程中，要注意掌握政策、依法办事，不得采取非法、违规手段使问题复杂化。（二）快速原则：即发现问题，立即启动预案及时处置，制止事态进一步发展，将风险损失降低到最小程度。（三）高效原则：即处置突发流动性风险事件，应按照统一指挥、措施得力，统筹协调、部门联动的原则组织实施，相关部门应按照自身的权限和职责，各司其职，服从指挥。（四）稳妥原则：即处置突发流动性风险事件，必须积极主动，快速反应，冷静、有序、果断，有效控制局势，做到指挥统一、宣传解释统一、行动步骤统一、不失控、不失序、不失真。（五）保密原则：即应急处置领导小组各成员要遵守职业道德和组织纪律，保守国家秘密，对于涉及到机密(含)以上的事项，应严格遵守保密法规，不得泄漏。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第四章  突发流动性风险事件的应急处置', '第十四条', '突发流动性风险的应急预案处置措施发生突发流动性风险事件后，应急预案处置领导小组应立即启动资金救助应急预案。资金救助原则上遵循自我救助、行业救助、同业救助、人民银行救助等顺序，但情况紧急时，也可从快从速，多法并举，快速、稳妥地处置各种流动性风险。（一）自我救助：主要措施有大力组织存款、调整资产组合、控制贷款投放、提前收回贷款、卖出持有债券、变卖固定资产、要求股东增补股本金等。（二）行业救助：通过省联社系统内资金调剂，并与省联社订立协议，取得流动性互助借款。（三）同业救助：通过银行间市场融资、全国银行间同业拆借市场和债券市场交易系统等方式，及时融入资金，包括同业存款、同业拆借、债券回购、票据回购等。（四）人民银行救助：向人民银行申请短期再贷款、再贴现，订立《常备借贷便利》，提出救助申请。在采取一切必要的手段后，仍存在严重支付困难但又不符合再贷款条件的，可以申请动用法定存款准备金。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第四章  突发流动性风险事件的应急处置', '第十五条', '突发事件发生后，应急预案处置领导小组应在事件发生的第一时间内将相关情况向人民银行、银监分局、省联社及当地政府报告，并通报公安机关。报告的内容包括：事件发生的时间、地点；事件发生的原因、性质、等级、可能涉及的金额和人数、危害程度、影响范围以及事件发生后的社会稳定情况；事态发展趋势、可能造成的损失、已经及拟进一步采取的措施；其他与本事件有关的内容。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第五章  突发流动性风险事件的后期处置', '第十六条', '突发流动性风险事件应急处置完毕后，应在各方帮助下，尽快组织恢复正常的营业秩序，协调新闻单位做好正面宣传报道工作，把握正确的舆论宣传导向，最大程度地降低突发流动性风险事件带来的不良影响。同时，要密切关注流动性情况，严防突发流动性风险事件出现反复。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第五章  突发流动性风险事件的后期处置', '第十七条', '突发流动性风险事件处置完毕后，要及时对事件的原因及全过程进行彻底调查，并追究相关人员的责任。有下列行为之一的，视情节轻重，给予相关责任人员相应的行政或纪律处分：（一）故意隐瞒风险情况造成风险蔓延扩大的；（二）风险事件发生后应急处置不力的；（三）发生风险事件后未及时向有关部门报告的；（四）未经批准向外界泄漏风险情况酿成不良后果的。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第六章  附	  则', '第十八条', '本预案由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《突发流动性风险事件应急预案》', '第六章  附	  则', '第十九条', '本预案自发文之日起施行。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第一章   总  则', '第一条', '为科学有效地组织开展统计工作，确保银监会、人民银行等各级管理部门的非现场监管报表、金融统计报表及各类统计资料上报准确、及时、全面、真实，充分发挥统计对本行经营管理在信息、咨询、监督等方面的重要作用，根据《中华人民共和国统计法》、《中华人民共和国商业银行法》、《银行业监管统计管理暂行办法》、《金融统计管理规定》及《银行监管统计数据质量管理良好标准（试行）》等，制定本实施细则。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第一章   总  则', '第二条', '工作目标：认真执行“统一规范、准确及时、科学严谨、实事求是”的统计原则，切实完善“数据信息整合的归口管理和内部协调机制、数据信息填报的流程规范和质量管理机制、统计人员管理的教育培训和考核约束机制”等三项机制，不断加强统计工作信息化建设，增强数据自动采集和整合功能，提高数据报送自动化程度，保障统计数据的真实性、准确性和完整性，积极构建科学、规范、高效的统计长效管理体系。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第一章   总  则', '第三条', '本实施细则适用于本行所辖机构及人员。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第二章   统计工作范围与要求', '第四条', '本行统计工作的基本范围是指根据国家的方针、政策和银行业监管统计管理规定以及本行经营管理要求，准确、及时、全面、真实地进行统计汇总，为监管部门提供监管依据；调查、搜集、整理、积累各项业务资料及有关经济信息，综合分析、预测、反馈本行资金活动情况，为本行制定业务发展规划、深化资产负债管理、研究同业、开拓市场等经营活动提供统计咨询意见，实行统计监督等。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第二章   统计工作范围与要求', '第五条', '本实施细则所称监管报表，指按要求上报的各类统计报表、数据及业务分析资料，按报送内容划分为非现场监管报表、金融统计报表、业务分析资料及其他四大类;按报送频率划分为日报、旬报、月报、季报、半年报、年报等。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第二章   统计工作范围与要求', '第六条', '统计工作包括以下具体要求:（一）统计目的。准确、及时、全面、真实地依据统计源数据和各项业务台账资料，系统地编报各类统计报表等。                                                                                                                                                                                                                                                     （二）统计手段。熟练运用现代化运算、信息传输工具，依据统计报表制度及时对统计信息系统进行维护，保障系统正常运行和信息传输网络畅通。（三）统计资料。科学、系统地搜集、整理、积累、汇编统计资料并妥善保管。建立健全统计台账制度并进行必要的原始登记记录，确保资料的准确性、全面性和连续性。（四）统计分析与预测。运用科学的手段对各项经营活动进行总结、评价和预测，为业务经营、管理、决策提供理论依据。（五）统计调查。积极开展统计调查和专题调查，获取第一手资料，充分利用统计信息资源，为监管和决策提供详实可靠的信息。（六）统计监督。充分利用统计信息对业务经营情况进行检查、监督、指导，合理调配资金，促进业务的健康发展。（七）统计报表管理。本行统计分析部负责统一管理、审核、报送各类统计报表。各支行及相关业务部门根据业务需要编制本支行和各业务条线统计报表（或台账）。各类统计报表必须严格实行复核制度。 （八）统计资料管理。逐步建立科学、高效的统计信息管理系统，实现信息资源共享和统计资料的科学管理，发挥统计监督职能。（九）信息反馈。本行统计分析部根据本行经营管理和业务发展需要，及时反馈相关的统计信息，提供统计咨询服务。 （十）信息披露。为确保对外公布统计信息的准确统一，凡对外公开披露的数据，均须报分管领导批准后公布。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第三章   统计机构、人员设置与分工', '第七条', '本行董事会制定明确的政策，将数据质量管理纳入内控合规体系和战略规划之中，并对其有效性和执行情况进行评估；本行高级管理层确立数据质量管理的目标，建立机制和流程，明确职权和责任，组织对本行数据质量管理水平进行评估，并有效落实数据质量问责制，对制度性变革等重大监管统计事项及时研究部署，在资源调配方面予以充分支持和保障；本行行长对本行监管统计数据的真实性负责。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第三章   统计机构、人员设置与分工', '第八条', '本行对监管统计工作实行统一管理、分级负责的管理体制。授权统计分析部为统计归口管理部门，负责全行监管统计领导、组织、协调和管理工作。（一）负责贯彻落实监管部门的统计管理办法和各项统计制度。（二）负责提出监管统计数据质量管理措施，制定全行监管统计工作制度和流程，经批准后组织实施。（三）负责将各类监管报表项目分解落实到相关业务部门统计人员，协调和督促各统计相关业务部门在规定时间内完成填报任务，科学、系统地收集、整理、积累各项统计资料，并完成汇总、审核、校验工作。（四）按规定及时向有关部门提供监管统计报表和统计资料，协调、审核、提供对外公布的相关统计资料。（五）负责对监管统计数据实施有效的日常监督和跟踪监控，不断提高统计数据质量。（六）牵头组织行内监管统计法规、制度执行及数据质量等情况的现场检查，对发现的重大问题，依据有关规定，提请高级管理层对相关部门及责任人予以问责和处理。（七）组织本系统开展统计法规及相关业务技能的培训工作。（八）负责对监管统计信息合理分类，实现本行分权限统计信息共享。牵头建立监管统计信息分析框架，做好监管统计信息的监测、分析和风险预警工作。（九）组织开发完善本行统计报表管理信息系统，推广先进统计工作管理方法和经验。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第三章   统计机构、人员设置与分工', '第九条', '本行统计分析部以外的统计相关部门和辖内各支行配备兼职统计人员，在总行统一领导下，切实履行监管统计相关职责。（一）将本部门（或支行）负责填报的各类监管报表的统计内容或项目分解落实到相关统计人员。（二）合理安排统计人员工作，及时帮助解决统计工作中遇到的困难和问题，督促统计人员在规定时间内完成填报工作。（三）对统计数据资料进行审核，在规定时间内报送牵头负责部门汇总或经分管领导批准直接报送。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第三章   统计机构、人员设置与分工', '第十条', '本行重要统计岗位设立A、B角统计人员，确保各类监管统计资料及时报送。（一）A角统计人员应根据部门（或支行）分工具体负责各类监管报表的数据采集、填报、验证、修改工作，并及时提交B角复核。（二）B角（主要人员为合规员、风险员、业务主管）具体负责统计报表的复核工作，并在确认无误后报送本部门或支行负责人审核。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第三章   统计机构、人员设置与分工', '第十一条', '部门或支行负责人应对本部门或支行统计数据认真审核，并对统计数据的真实性、准确性负责。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第三章   统计机构、人员设置与分工', '第十二条', '分管领导主要负责指标预警、指标审查和同意上报（授权签名）及组织检查评估等工作。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第十三条', '统计人员的职责 相关部门及统计人员应认真贯彻执行党和国家的方针、政策和统计法令、规定以及本行统计制度；爱岗敬业，恪守职业道德，认真负责地完成各项统计任务；努力学习业务及相关法律法规知识，借鉴先进管理方法，提高综合素质，依法开展统计工作；做好统计调查、分析、预测，真实反映新情况、新问题，并就本行业务发展实际情况总结经验，提出解决问题的思路和方法；实事求是，保证统计数据的真实性和准确性，不得虚报、瞒报、伪造、篡改统计数字；发现有错报、漏报情况必须及时反映，主动更正；认真执行国家《中华人民共和国保密法》和本行保密制度的相关规定，严守国家机密和行业秘密，在本行规定的脱密期内（至少为3个月）不得擅自离职；妥善管理好统计资料，未经批准不得随意对外提供统计信息。统计人员对统计调查对象的商业秘密负有保密责任。相关部门具体如下：报表编制实行AB角负责制。A角负责每张报表的填报工作，B角负责报表的协助填报及复核工作，AB角均须确保数据采集渠道正确，数据填报精准。（一）归口部门（统计分析部）A岗负责人行金融统计工作，具体金融统计所有指标的汇总、校验、上报，B岗负责对A岗数据的复核；B岗负责银监各类报表的汇总、校验、上报，A岗负责对B岗数据的复核。统计分析系统上线后，由系统进行各类报表的生成，AB岗负责对相关报表的校验、上报，并负责对各报表生成规则、参数的校验、调整。（二）业务及相关管理部门（信贷管理部、金融市场部、电子银行部、合规管理部、授信评审部、三农业务部、营业部、人力资源部、运营管理部等）及各支行计划财务部：A岗负责工资、税金、各类费用等数据的收集、补录和上报；负责应收应付款帐龄的分析。B岗负责对A岗数据的复核；信贷管理部：A岗负责信贷类相关指标、利率报表、个人住房贷款基础数据的收集和上报；负责贷款五级分类的管理及调整；负责涉农及精准扶贫贷款客户的确认；负责林权抵押贷款的收集和上报；负责贷款相关信息的补录和对下指导。B岗负责对A岗数据的复核；金融市场部：A岗负责理财类、票据类、债券类、贴现类、承兑类指标基础数据的收集、补录和上报等。B岗负责对A岗数据的复核；电子银行部：A岗负责电子银行业务相关指标基础数据的收集、补录和上报。B负责对A岗数据的复核；合规管理部:A岗负责风险监管类指标相关基础数据的收集、补录和上报,B岗负责对A岗数据的复核；授信评审部：A岗负责五级分类大中小微、政府平台、个体工商户、小微企业主、平台贷款、科技金融基本情况、贷款授信、用信台账等数据的收集、补录和上报。B岗负责对A岗数据的复核；营业部：A岗负责下岗失业人员贷款的收集和上报。B岗负责对A岗数据的复核；三农业务部：A岗负责对贵金属、保险及“两权”抵押贷款台账基础数据的收集、补录和报表的上报。B岗负责对A岗数据的复核；人力资源部：A岗负责对员工、高管、社保、医保基金等相关数据的收集、上报。B岗负责对A岗的复核；运营管理部：A岗负责各机构、库存现金等信息的收集和上报。B岗负责对A岗的复核；各支行：A岗负责本机构的相关数据统计、补录和上报，并对相关业务部门负责。B岗负责对A岗的复核。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第十四条', '本行统计机构及人员依照国家颁布的统计法律、法规和本细则行使上述职责，任何单位和个人不得非法干预。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第十五条', '统计人员的权益（一）按照国家有关规定，参与技术职称的评定和聘用。（二）针对统计工作要求报表时点性强、数字准确性高的特点，对加班加点的人员按《中华人民共和国劳动法》规定给予补休或工资补贴。（三）根据工作需要，统计人员有权要求有关部门提供相关数据，查阅有关原始资料，询问有关情况，参与有关会议，与本行业务部门统计人员互相配合，通力协作，共同努力做好统计工作。（四）统计人员有权要求下属支行和有关部门核查统计资料的准确性，并有权要求更正不确实的统计资料。（五）统计人员有权参加本系统、人民银行、银监部门、统计局和其他有关机构的统计业务培训、学习，充实专业知识，增强业务能力，不断提高工作质量和工作效率。（六）对违反国家统计法律、法规和本细则编制发布的统计调查表，统计人员有权拒绝填报；对违反统计法规、制度的错误行为有权检举揭发。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第十六条', '加强统计团队建设，把具备一定业务素质的人员不断地充实到统计队伍中。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第十七条', '构建有效的内部统计工作协调机制。根据部门和岗位职责，明确业务分工、工作责任、时间要求和考核措施，确保统计人员认真履行相关职责，及时准确完成监管统计相关工作。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第十八条', '强化制度执行，对监管统计制度及相关业务制度的执行情况进行监督、检查和考核，将违规统计行为纳入积分考核和奖惩范围，对不认真履职的统计人员明确相应的纠改措施和尽职规定，以强化责任和激励约束机制。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第十九条', '保障统计队伍的相对稳定，做好统计人员离岗交接工作，在接替人员能够独立工作后，方可调离，确保统计工作任务的连续、全面完成。统计人员调整必须报经总行领导批准。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第二十条', '加强对统计人员的依法合规教育、政治思想教育以及职业操守教育；大力支持统计人员合法合规统计行为，保障统计人员合法权益；严禁阻挠、破坏统计工作和打击、迫害统计人员。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第四章   统计部门及人员职责、权益与管理', '第二十一条', '加强对统计人员特别是基层统计人员的系统培训，组织好统计人员的上岗培训和后续教育工作，从总体上提高本行统计队伍的整体业务素质；为统计人员创造良好的学习、工作环境，保障统计人员业务能力的不断提高。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十二条', '本行监管统计相关数据标准参照国家标准化政策及监管统计规定执行。以省联社业务系统为依托，监管统计相关的业务领域及系统遵循统一的业务规范和技术标准，建立统一的监管统计数据架构，客户、产品、机构、账户等基础信息跨系统保持名称、定义、口径、来源等方面的一致性。各系统建立统一的数据交换标准，实现系统间信息共享。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十三条', '本行加强对业务基础系统的管理，确保统计数据采集来源准确、可靠。业务基础系统信息需及时录入、复核，并动态更新，保证全面、及时、准确地反映业务实际。监管统计人员及系统管理人员及时反馈存在完整性、准确性等缺陷的质疑信息，并及时上报省联社，申请予以核查并纠正。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十四条', '监管统计人员及系统管理人员应及时向省联社反馈统计业务需求，为省联社业务基础系统的设计、建设和升级改造提供基本信息，实现系统功能全面支持监管统计信息的自动采集。金融统计数据原则上应从xxx省联社的综合业务系统、信贷管理系统中采集，因受条件限制确实无法直接采集的某些特定数据必须建立相关台账或系统进行补录，由相关业务部门负责，支行（部）报表责任人负责补录、上报。各支行（部）应建立贷款行业分类、企业贷款大中小微分类台账；信贷管理部应建立贷款受托及自主支付台账、重组贷款台账、关联贷款台账、应收息台账、五级分类、代理保险台账；授信评审部应建立贷款授信、用信台账、大中小微台账；金融市场部应建立贴现、签发银票、票据、债券、理财、资金拆入拆出台账；三农业务部应建立贵金属、“两权”抵押贷款台账；资产保全部应建立抵债资产账龄台账；计划财务部应建立应收款、诉讼费台账等。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十五条', '加强统计监管系统的建设工作，有计划、有步骤地建立适应监管统计工作需要的、完善的监管统计系统，实现流程控制的程序化，确保监管统计系统能够自动提取和加载所需数据，有良好的质量控制与错误信息提示功能，保障数据安全和规范操作，并具有可拓展性和灵活性。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十六条', '统计相关部门应根据监管统计制度制定相应的报表填报操作流程，明确数据来源和统计方法，明确每一项数据的归属关系，明确数据的报送渠道、报送流程和报送时间，包括数据的取得、数据的加工和数据的复核等，确保数据来源完整覆盖和准确反映各项监管统计要求。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十七条', '在监管统计制度变更或本行统计相关的信息（如新业务的开展、会计方法的变更、信息系统的升级）变更时，要及时对报表填报操作流程做出相应变更。监管统计报表填报操作流程应与本行主监管员充分沟通后，报银监局（分局）监管部室和统计部门备案。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十八条', '上报监管报表必须按照监管部门的指定方式与指定渠道报送，报送频率和时间要求按照监管部门的规定执行。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第二十九条', '各类监管报表的填报流程为：（一）本行各类监管报表的数据采集、加工，主要通过以下渠道：省联社财管系统、综合业务系统、信贷管理系统及各类电子、手工台账等。（二）填报部门（或支行）统计人员登记台账、编制报表，统计复核人员复核。（三）统计各相关业务部门分别汇总、审核。（四）统计分析部汇总、审核、校验。（五）校验通过的报表报行长室（分管行长或行长）审定。（六）统计分析部按规定方式上报至各监管部门。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第五章   统计数据标准、采集与填报', '第三十条', '本行建立报送联系人制度，所有报表均需明确相关联系人，联系人发生变动及时修改并报备监管机构。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第六章   统计数据的分析与应用', '第三十一条', '本行统计分析部应加强统计分析和应用，通过对统计数据的对比分析、变化分析、因素分析，充分发现本行业务经营的亮点、特点或成功经验，以及存在的风险隐患、管理缺陷，有针对性地提出工作意见或合理化建议，为本行合规审慎经营提供决策参考。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第六章   统计数据的分析与应用', '第三十二条', '在保密安全的前提下，全行充分共享和使用监管统计信息，促进业务经营发展。同时，通过扩大共享、加强分析、适时披露，促进数据质量的提升。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第七章   统计资料管理与公布', '第三十三条', '为确保统计资料的完整，各支行和相关部室应当加强对统计资料的管理，保证监管统计数据信息的安全性和连续性。统计资料的交接和档案保管应严格执行本行相关档案管理制度。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第七章   统计资料管理与公布', '第三十四条', '本行统计管理部门定期整理监管统计资料，做到相关统计资料口径可比、数据连续、存储安全，提高监管统计数据的研究和使用价值。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第七章   统计资料管理与公布', '第三十五条', '为保证对外公布的统计资料准确、统一，本行统计资料的公布实行“集中统一，分级管理”的办法。（一）总行对外公布全面的统计资料，须经总行主管领导批准，报监管部门备案；公布单项统计数据，须报总行分管领导批准。（二）分支机构公布的统计资料，须报经本级行行长审批，并报总行备案。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第七章   统计资料管理与公布', '第三十六条', '统计资料的审核，由各支行和相关部室负责人依照统计管理制度和管理权限进行，不得随意修改，如果发现统计资料不实，应当及时责成统计相关部门或人员核实订正。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第七章   统计资料管理与公布', '第三十七条', '对外公布有密级的金融统计资料的审批权限，实行“分级负责”制度，未经批准，任何单位或个人不得擅自对外公布。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第七章   统计资料管理与公布', '第三十八条', '报送监管机构的监管统计信息与对外披露的相关信息间如有差异，应将其中重大差异主动向监管机构做出说明。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第八章  统计网络维护与应急处置', '第三十九条', '本行在统计分析部设置监管统计专用终端，并根据要求通过专用网络报送渠道上报监管统计资料，专网与互联网实现物理隔离，保障数据安全传输。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第八章  统计网络维护与应急处置', '第四十条', '为统计业务配置的电脑软、硬件和网络带宽应满足报送要求，并且能够根据数据报送要求变化进行扩展和升级。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第八章  统计网络维护与应急处置', '第四十一条', '统计专用电脑和网络管理的维护制度参照本行计算机相关管理制度执行。由统计专用终端使用人员及科技部门共同负责电脑软、硬件和网络的维护、管理和升级。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第八章  统计网络维护与应急处置', '第四十二条', '监管统计报送网络应急预案参照本行计算机网络相关应急预案执行。（一）统计网络应急处置办公室设在统计分析部，领导小组组长由统计分析部的分管行长担任，成员由统计分析部及信息科技部等其他统计相关业务部门的负责人组成。（二）相关统计人员应熟悉本行计算机网络突发事件应急处置流程，加强信息汇总分析，密切关注数据报送风险，有效预防突发事件发生。（三）如遇突发事件发生，统计人员应以最快的速度将突发情况上报应急处置领导小组，及时启动应急预案，同时组织相关人员开展处置工作。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第九章  统计质量监督、检查与评价', '第四十三条', '为确保统计数据质量，本行对各类监管统计数据生产报送全流程进行数据质量监控。（一）监管统计相关业务部门对本部门监管统计的数据源信息质量适时监控，对统计分析部提出的数据源质量问题及时整改，保证数据源信息的准确性和完整性。（二）统计分析部对监管统计数据实施有效的跟踪监控，强化总体复核，发现数据质量问题，反馈到相关责任部门并督促其及时纠正。（三）统计分析部建立与报表频度相适应的数据质量监测台账，对监管统计数据质量问题实时记录、分析。（四）统计分析部定期通报相关业务部门和分支机构提供的监管统计数据质量情况，总结交流经验，并提请实施问责。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第九章  统计质量监督、检查与评价', '第四十四条', '本行将数据质量检查纳入内控合规检查范围，合规管理部门在制定年度检查计划时应充分考虑统计分析部的意见和建议。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第九章  统计质量监督、检查与评价', '第四十五条', '本行建立监管统计现场检查制度，按照各统计项目具体要求进行现场检查。（一）统计分析部定期牵头组织行内监管统计现场检查，检查内容包括监管统计法规制度的执行及其数据质量等情况。（二）统计分析部应将统计现场检查报告报主管领导，对影响数据质量的重大问题，提交内控合规部门跟踪检查，并报监管机构。（三）统计分析部应督促被查单位根据统计现场检查发现的问题以及统计分析部的要求实施整改，及时对整改情况跟踪评价，确保整改落实到位。（四）监管统计相关业务部门定期对监管统计的数据源信息质量进行检查，及时组织整改，并将检查整改结果抄送统计分析部。（五）本行对监督检查发现的重大问题，依据有关规定对相关部门及责任人予以问责和处理。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第九章  统计质量监督、检查与评价', '第四十六条', '本行建立监管统计考核评价体系和奖惩机制，保障全系统监管统计工作的有效落实。（一）统计分析部定期对相关业务部门和分支机构提供数据的质量情况进行考核和评价，考评结果在行内公布。（二）统计分析部按照业务范围对监管统计数据源管理工作进行考核评价，落实相应的激励约束措施。（三）本行将监管统计数据质量的考核结果纳入绩效考核体系。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第十章   附  则', '第四十七条', '本实施细则由本行统计分析部负责解释和修订。'),
(NULL, '计划财务部', '《统计业务管理实施细则》', '第十章   附  则', '第四十八条', '本实施细则自发文之日起施行。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第一章   总  则', '第一条', '为充分利用统计资源，实现统计信息共享，发挥统计对本行”在经营管理在信息、咨询、监督等方面的重要作用，依据《银行业监管统计管理暂行办法》、《金融统计管理规定》、《银行监管统计数据质量管理良好标准（试行）》等，制定本制度。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第一章   总  则', '第二条', '本行统计信息共享遵循以下原则：（一）真实、准确、完整性原则，各机构提供的统计信息必须客观真实，资料或数据来源准确，保证统计信息精确、可靠。（二）及时性原则 ，各机构应对统计信息及时更新和补充，保证统计信息的时效性。（三）分权限原则，本行对统计信息进行合理分类，对使用对象设定合理的用户权限。（四）充分性原则，不断扩大统计信息的应用范围，充分实现行内统计信息共享。（五）保密原则，所有统计信息使用人都必须履行保密义务，任何人未经批准不得对外透露本行商业信息。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第一章   总  则', '第三条', '本制度适用于本行所辖机构及人员。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第二章   统计信息共享组织机构', '第四条', '本行成立统计信息共享机制领导小组，由统计分析部分管领导任组长，统计分析部及信息科技部等其他统计相关部门负责人为成员，领导小组下设办公室，由统计分析部负责人任办公室主任，具体负责该项工作的组织和协调。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第二章   统计信息共享组织机构', '第五条', '各部门职责（一）统计分析部负责牵头建立统计信息共享平台，对统计信息共享平台的日常运行情况进行监督和管理；牵头对监管统计信息进行合理分类，根据使用部门或人员岗位的不同设置不同的用户权限；对监管统计数据进行扎口管理，负责监管统计数据的行内共享发布。（二）信息科技部应加强统计信息化建设，为统计信息共享提供科技支持，负责具体搭建统计信息共享平台；依托相关统计、业务系统为相关部门提供各项数据采集支持，配合有关部门做好数据统计和信息分析工作；对统计信息共享平台进行日常系统维护，保证统计信息共享平台稳定运行，并及时更新数据。（三）其他各统计相关部门具体负责本业务条线统计信息的收集、整理和分析，经分管领导审定后，定期或不定期按要求上传至统计信息共享平台，供行内各机构及人员分权限使用。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第二章   统计信息共享组织机构', '第六条', '各机构应明确一名统计信息联络员，具体对本机构统计信息进行日常管理。统计信息联络员主要职责：(一)接收、传递统计信息，保管统计资料;(二)了解、归集统计信息使用人对统计信息的需求，并根据需求提请相关人员调整统计信息共享的内容;(三)对本机构统计信息共享平台信息进行日常监测、维护;(四)其他有关事项。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第三章   统计信息共享内容', '第七条', '本制度所称统计信息主要包括以下内容:（一）国家统计机构及银行业监管机构颁布的统计相关法律法规；(二) 各级监管部门统计监管规章及各类监管文件（包括但不限于业务动态信息、分析预警、工作指导意见、统计业务提示、统计工作通报等资料）;(三)地方政府对外公布的经济统计数据；（四）各同业金融机构共享的业务数据；（五）各类监管统计上报数据及分析报告（包括上报人行、银监、省联社、行管组等监管机构的报表、报告及监管指标分析等）；(六)各部门本业务条线相关统计数据、分析报告等；（七）其他相关统计资料。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第三章   统计信息共享内容', '第八条', '统计信息共享内容应及时更新，并视使用人需求情况不断进行补充、完善。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第四章   统计信息共享方式', '第九条', '本行统计信息共享通过搭建统计信息共享平台实现。由统计分析部牵头，信息科技部负责开发相关系统并对系统进行维护、管理。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第四章   统计信息共享方式', '第十条', '如遇时间要求紧、需求特殊的统计信息需求，统计信息提供人可经分管领导批准，针对使用人的要求通过本行内部安全的传输系统及时提供。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第四章   统计信息共享方式', '第十一条', '统计信息使用人通过自己的操作码登录统计信息共享平台，不同岗位使用人可根据自己不同的权限查看相关统计信息。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十二条', '本行统计分析部具体负责统计信息共享管理，对统计信息共享平台进行日常监测，对共享资料的准确性、统一性和及时性进行审核与督查，定期评估统计信息共享工作的开展情况。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十三条', '各部门对本业务条线的统计信息共享管理应遵照“分工明确，职责清晰”的原则进行，避免统计信息内容交叉、重复，提高统计信息平台信息供给效率。对本部门应提供的统计信息应抄列清单，提交统计分析部备案，并及时补充、更新。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十四条', '各支行、各部门应遵循本制度原则，保证统计共享信息准确、可靠，不得提供虚假及来源不确切的统计信息资料。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十五条', '各支行、各部门提供的统计信息应经本支行或部门负责人审核、分管领导审定同意后，上传至统计信息共享平台，并报统计分析部备案。原则上，各支行提供的统计信息应经分管业务条线的部门进行扎口管理。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十六条', '如发现有误差的统计信息，相关支行、部门应立即修订，并在相关公告界面及时进行公告，避免使用人因统计信息差错而做出错误决策。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十七条', '统计信息共享平台应严格分权限使用。使用人一人一码（密码）、一人一权限，密码定期修改，不得泄漏。人员调整、调离时，信息科技部应及时对该使用人操作码进行重新设置或销除。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十八条', '统计信息使用人应履行反馈使用情况的义务。发现差错或对统计信息共享工作有意见或建议，应及时告知统计分析部或相关统计信息提供部门。相关部门应对使用人反映的情况予以记录，对正确的意见予以采纳，群策群力，不断提高统计信息共享的效能。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第五章   统计信息共享管理', '第十九条', '加强统计共享信息的保密管理。对涉密的共享信息，严格按照本行《xxx银行股份有限公司保密制度》相关规定执行。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第六章   统计信息共享奖惩', '第二十条', '本行对违反本制度的统计信息共享行为予以处罚。对故意提供虚假统计信息的支行或部门负责人及直接责任人参照本行员工违规违章行为管理规定最高罚级予以严惩，如行为人存在违反国家法律法规的行为，可追究其法律责任。对其他违反本制度的行为，视造成影响轻重予以不同的惩处。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第六章   统计信息共享奖惩', '第二十一条', '本行鼓励全体员工对统计信息共享工作积极献计献策，凡相关建议被总行采纳的，予以一定物质奖励和精神奖励。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第七章   附  则', '第二十二条', '本制度由本行统计分析部负责解释和修订。'),
(NULL, '计划财务部', '《统计信息内部共享管理制度》', '第七章   附  则', '第二十三条', '本制度自发文之日起施行。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第一章    总    则', '第一条', '为加强统计工作管理，规范统计工作行为，确保各类统计工作及时、全面、准确地开展，提高统计工作质量和统计分析水平，根据《中华人民共和国统计法》、《金融统计管理规定》、《银行业监管统计管理暂行办法》、《银行监管统计数据质量管理良好标准（试行）》等制度和办法，制定本办法。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第一章    总    则', '第二条', '统计工作考核遵循客观、公平、公正的原则。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第一章    总    则', '第三条', '本行统计分析部是制定、执行、管理统计工作考核的主管部门，各支行、部，机关各部室及团队（以下简称“各单位”）可根据本办法组织本单位统计工作考核。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第一章    总    则', '第四条', '本办法适用于本行各单位。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第二章	考核内容', '第五条', '统计工作考核项目包括业务经营单位统计工作考核、相关管理部门统计工作考核、统计分析部考核三方面。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第二章	考核内容', '第六条', '经营单位统计工作考核包括：统计管理的规范性、调查收集的完整性、报送数据的准确性、报送资料的及时性。规范性：各单位应成立统计工作领导小组，明确统计责任人（A 角）、复核人（B 角），认真履行相关职责，积极参加本行统计部门组织的各类培训，对统计工作考核结果严格兑现。完整性：各类报表的报送做到表种齐全，无重复、遗漏，报送内容无漏缺，对相关系统补报的信息认真填报、无漏缺。及时性:严格执行总行统计分析部对各类统计报表、报告的报送时间要求，及时报送，无拖延。准确性：统计上报资料审核严格，逻辑关系正确，计量单位准确，统计指标齐全，数据真实可靠、无差错。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第二章	考核内容', '第七条', '相关管理部门包括业务管理部门、信息科技部、合规管理部、审计部等相关管理部门。统计工作考核内容包括：规范管理、调查收集、整理分析、监督管理、服务支持。规范管理：各部门应成立统计工作领导小组，明确统计责任人（A角）、复核人（B 角），认真履行相关职责，积极参加总行统计部门组织的各类培训，对统计工作考核结果严格兑现。调查收集：业务管理部门应认真调查，收集统计分析部要求报送的各类报表，做到表种齐全，无重复、遗漏，报送内容无漏缺，对相关系统补报的信息认真填报、无漏缺。整理分析：业务管理部门应按统计要求对统计资料细致整理，认真分析，符合上级单位对统计数据的要求。监督管理：合规管理部应将数据质量纳入年度专项检查或评估计划；审计部应将统计管理和数据治理纳入年度审计范围。服务支持：信息科技部应对系统的搭建、统计信息管理程序开发、测试和维护、数据标准架构及技术支撑、网络环境搭建、系统备份等职责范围内的工作提供有力的支持。相关管理部门具体考核内容如下：⑴业务管理部门：负责业务基础信息的准确性、完整性，对统计部门提出的基础信息质量问题及时整改；配合统计部门开展数据治理工作，落实数据质量管理各项要求，提升基础信息质量；准确建立业务基础信息相关台账，及时提出系统开发需求，减少人工误差；及时向统计部门反馈业务基础信息管理工作中存在的问题，并提出合理化建议。⑵信息科技部：负责基础业务系统的搭建与完善，确保系统功能满足业务基础信息管理要求和统计工作需要；负责统计信息管理系统的程序开发、测试和维护；负责根据国家标准化政策及监管规定逐步建立起全行统一的数据标准架构，为数据治理提供技术支撑；负责统计报送网络环境搭建、系统备份、电子数据的管理与应急预案的制订和演练；负责有关统计报送硬件设备的配置、软件更新与日常技术支持；⑶合规管理部：将数据质量纳入年度专项检查或评估计划；⑷审计部：将统计管理和数据治理纳入年度审计范围。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第二章	考核内容', '第八条', '统计分析部考核内容包括对外统计报送考核和对内统计、分析考核两部分，对外统计报送对象主要包括：人民银行、银监局、省联社、行管组等；对内统计、分析主要包括本行领导、各部门的统计数据需求及分析等。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第三章	考核对象、时间及方式', '第九条', '考核对象：各单位、统计责任人。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第三章	考核对象、时间及方式', '第十条', '考核时间：每月 1 日至当月底为一个统计考核周期。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第三章	考核对象、时间及方式', '第十一条', '考核方式：业务经营单位按月考核、公布，按季兑现；机关管理部门及统计分析部按季考核、公布，按年兑现。业务经营单位考核到单位和统计责任人；机关管理部门及统计分析部考核到单位。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第四章	考核计分规则', '第十二条', '计分规则统计责任人计分规则如下：统计责任人按培训情况、资料完整、报送及时、数据准确、整理分析进行考核，其中：资料完整：每次资料报送或填报完整的得 1 分，报送不完整的扣 1分，要求重报仍不完整的再扣 2 分，依此类推；报送及时：资料报送或填报及时的得 1 分，报送或填报发生迟报或漏报的扣 1 分；再次催报仍未及时报送或填报的再扣 2 分；三次以上催报仍未报送或填报的除每次多扣 1 分外，另按本行计分惩罚办法进行扣分处罚；因迟报而影响统计分析部上报的除按正常考核扣分外，再按上级部门对我行的处罚进行同等处罚；数据准确：统计数据报送或填报准确的得 1 分，不准确的扣 1 分，重报后仍不准确的扣 2 分。数据报送或填报错误的按本行计分惩罚办法进行扣分处罚；因错报而影响统计分析部上报的除按正常考核扣分外，再按上级部门对我行的处罚进行同等处罚；整理分析：统计数据整理分析正确得1分，错误扣 1 分。因整理分析错误而影响统计分析部上报的除按正常考核扣分外，再按上级部门对我行的处罚进行同等处罚。单位计分规则如下：单位按培训情况、统计责任人奖惩情况进行考核，其中：培训情况：参加统计培训的得 1 分，缺席培训的扣 1分。统计责任人各项考核的得分和扣分同步对单位进行得分和扣分。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第四章	考核计分规则', '第十三条', '奖惩规则业务经营单位奖惩规则：⑴每季对统计责任人和单位考核情况进行排序，统计责任人前三名分别奖励 300 元、200 元、100 元，后三名分别处罚 300 元、200 元、100元。⑵每季对统计单位前三名分别奖励 300 元、200 元、100 元；后三名分别处罚 300 元、200 元、100 元。相关管理部门奖惩规则：按年考核，未发生差错的，一次性奖励 1000元，发生差错的按《监管统计数据质量问题追责细则》规定处罚。统计分析部奖惩规则：月度奖惩规则。统计分析部负责统计轧口上报，未发生严重问题的奖励 500 元；发生漏报、错报、迟报，被上级单位通报批评的，按《监管统计数据质量问题追责细则》规定处罚。年度奖惩规则。本年度统计工作在人民银行、银监局、省联社、行管组等被报送单位获得荣誉的（优胜奖、先进奖、前三名）、个人为本行获得荣誉的（先进、优胜、前三名）按本行创新争优考核办法予以奖励。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第五章	附	则', '第十四条', '本办法由本行统计分析部负责解释和修订。'),
(NULL, '计划财务部', '《统计工作专项考核办法》', '第五章	附	则', '第十五条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第一章  总 则', '第一条', '为进一步提升本行金融统计水平，及时、准确地完成金融统计工作任务，充分发挥金融统计对本行经营管理决策的服务作用，根据《中华人民共和国统计法》、《中华人民共和国统计法实施细则》、《银行业监管统计管理暂行办法》、《银行监管统计数据质量管理良好标准（试行）》以及中国银行业监督管理委员会和中国人民银行相关统计制度的规定，结合本行实际情况，特制定本办法。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第一章  总 则', '第二条', '本办法所指统计是指本行及所属支行（部）以财务会计及各类业务信息为基础，以内部管理信息系统为依托，对总行及所属支行（部）各项经营情况和风险状况进行调查、分析、评价和预警，提供统计信息和统计咨询意见，满足监管工作要求，提供经营决策依据。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第一章  总 则', '第三条', '本行及所属支行（部）在各项金融统计工作中均必须遵守本办法。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第二章　统计工作的基本要求', '第四条', '统计工作的基本原则是统一规范、准确及时、科学严谨、实事求是。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第二章　统计工作的基本要求', '第五条', '统计工作要以会计及各类业务信息为基础，以会计报表为主要依据，调查、收集相关信息资料，确保统计数据来源的真实、准确、完整。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第二章　统计工作的基本要求', '第六条', '各类统计报表及其数据间必须相互核对平衡一致，严禁出现错报、漏报、虚报、估报、瞒报等现象。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第二章　统计工作的基本要求', '第七条', '统计工作应运用现代信息技术，逐步实现自动化、系统化和网络化。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第二章　统计工作的基本要求', '第八条', '统计工作应加强信息透明度建设，按照监管部门的要求建立信息披露制度，及时、规范地进行信息披露。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第三章 　统计机构及工作岗位设置', '第九条', '统计分析部是本行统计工作的主要牵头部门，负责本行统计工作的协调与管理。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第三章 　统计机构及工作岗位设置', '第十条', '各职能部室及各所属支行（部）是本行统计工作的具体实施部门，负责相关统计信息的收集、汇总、上报。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第三章 　统计机构及工作岗位设置', '第十一条', '各统计部门应紧密配合，加强内部协调，严禁出现推诿扯皮现象，保障本行统计工作的有序开展。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第三章 　统计机构及工作岗位设置', '第十二条', '统计分析部统计工作职责：(一)贯彻落实各项统计管理办法和统计制度；(二)督促各职能部室、各支行（部）及时报送各类报表，对报送的各类报表检查其数据平衡性、完整性、真实性、合规性等，确保不发生报送错误；(三)负责填报各种统计报表，严格按照上级单位制定的统计指标定义和口径，准确、及时地填报各类统计报表及统计调查表，并在规定时间内及时上报相关部门；(四)组织开展统计调研，完成统计分析，及时发现统计工作中存在的问题，并提出解决方案； (五)组织开展统计法规和统计质量检查活动，并配合有关部门做好统计检查工作；(六)组织开展统计培训工作；(七)配合董事会办公室进行相关信息的披露；(八)做好统计资料整理和存档管理工作；(九)为满足经营管理工作需要开展其他统计工作。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第三章 　统计机构及工作岗位设置', '第十三条', '各职能部室、各支行（部）统计工作职责：(一)保证统计人员的基本稳定，若确需调整，应征求本行职能部门意见，统计人员必须经过培训符合上岗条件，方能从事统计工作。统计人员应实行AB角制，统计人员的调整不能影响统计工作的正常开展；(二)督促统计人员在规定的时间内、以规定的方式保质保量完成日常统计资料报送工作，按时完成对相关业务的统计分析；(三)建立健全统计台账，保证统计数据准确可靠；组织条线人员严格按照统计分析系统提供的补录程序，对业务系统的数据进行补充录入；提供的各类统计资料必须实事求是，按规定及时、准确、完整地填报统计数据，不得虚报、瞒报、伪造、篡改统计数据；(四)报送的各类统计资料、报表应加盖业务章、主要负责人印章和统计员印章；(五)对专项或临时的统计调查工作，各支行（部）应按期完成，对需要内、外勤协同完成的统计工作，由支行（部）主要负责人做好业务协调与分工；(六)对统计工作中发现的问题，应及时向统计分析部反映；对如何完善统计工作提出合理化建议；(七)做好统计资料整理和存档管理工作。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第四章 　统计人员', '第十四条', '本行各职能部室以及各支行（部）根据业务类别和统计工作的要求设置统计岗位，配足配齐统计人员实行AB角制，明确专职或兼职统计员。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第四章 　统计人员', '第十五条', '本办法所指统计人员是指本行相关职能部室、各支行（部）负责人、相关统计工作人员以及各支行（部）涉及统计数据的提供人员。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第四章 　统计人员', '第十六条', '本行各职能部室以及各支行（部）负责人对本单位统计工作负主要责任，对本单位统计数据的真实性负责，定期对统计工作开展情况进行检查，及时调整、优化统计工作岗位人员配置。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第四章 　统计人员', '第十七条', '统计人员应符合以下要求：(一)具备良好的职业道德，具备执行统计任务所需的专业知识。对单位领导强令或者授意篡改统计资料或者编造虚假统计数据的行为，应当拒绝、抵制，依照本办法如实报送统计资料；(二)必须实行岗位培训，未经岗前培训或培训不合格者不得上岗，经考核不适宜担任统计工作的人员，应及时进行调整。具备执行统计任务所需要的专业知识和较强的学习能力，能够根据业务发展情况，不断增强自身业务素质，满足统计工作新的要求；(三)具有较强的工作责任心和吃苦耐劳的精神，能够按照有关要求保质保量地完成各项统计工作；(四)具备一定的综合分析能力，能够根据统计数据分析业务经营进展情况，撰写质量较高的统计分析材料。经考核不适宜担任统计工作的人员，应及时进行调整。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第四章 　统计人员', '第十八条', '统计人员调动工作或者离职，应当有符合规定条件的人员接替，必须办理交接手续。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第四章 　统计人员', '第十九条', '统计人员权利：(一)要求有关单位和人员如实提供资料和信息；(二)检查统计资料的准确性，要求有关部门和人员改正或完善不真实的统计资料；(三)拒报不符合规定的统计报表，检举和揭发违反统计法律、规定、制度的行为。统计工作人员依照有关统计法律、法规和本办法行使上述权利时，任何单位和个人不得非法干预。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第五章　统计资料管理', '第二十条', '统计资料是指用于编制统计报表的各种原始报表、台账等相关资料以及在统计活动中形成的统计报表。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第五章　统计资料管理', '第二十一条', '统计资料的保管实行分级管理原则，统计分析部的统计资料实行电子、纸质双重管理（上线统计分析系统后主要以电子资料保管为主，统计分析部负责数据的日常备份、科技信息部负责备份数据的安全）；各部门及支行负责本单位的统计资料整理、装订、保管，按月或按季移交档案室。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第五章　统计资料管理', '第二十二条', '统计人员应按期将本部门（支行）纸质统计资料分类装订成册，资料装订必须完整，有封面、封底及资料目录，各类统计资料应按月或按季移交档案室入库妥善保管，严防损坏和丢失，做好防火、防盗、防潮、防霉、防鼠，确保安全。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第五章　统计资料管理', '第二十三条', '建立统计资料查阅登记制度，在登记簿上详细记载相关调阅内容。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第五章　统计资料管理', '第二十四条', '统计资料需要移交时，填写移交清单，交接双方逐份清点无误后，履行交接手续。统计资料移交须由本单位或部门负责人监交。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第六章　 奖励与惩罚', '第二十五条', '本行根据有关规定对在统计工作中做出重要贡献和取得突出成绩的统计人员或集体给予奖励。奖励分为荣誉奖励和物质奖励。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第六章　 奖励与惩罚', '第二十六条', '有下列行为之一的，本行根据有关规定，视情节轻重对有关责任人员给予相应的行政处分，并按照本行《统计工作专项考核办法》进行相应的处罚：(一)虚报、瞒报、伪造、篡改及无理拒报统计资料的；(二)在接受统计检查时，拒绝提供情况或提供虚假情况以及转移、隐匿、毁弃原始统计记录、统计报表和与统计有关的其他资料的；(三)拒不接受或阻挠、抗拒统计检查的；(四)屡次漏报、迟报、错报统计资料，造成严重后果或者恶劣影响的。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第六章　 奖励与惩罚', '第二十七条', '本行及支行（部）负责人对拒绝、抵制篡改统计资料或者对拒绝、抵制编造虚假数据行为的统计人员进行打击报复的，依法给予行政处分；构成犯罪的，移交司法机关依法追究刑事责任。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第六章　 奖励与惩罚', '第二十八条', '统计人员泄露本行商业秘密，构成犯罪的，移交司法机关依法追究刑事责任；尚不构成犯罪的，依法给予行政处分。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第七章　 附  则', '第二十九条', '本办法由本行统计分析部负责解释和修订。'),
(NULL, '计划财务部', '《统计工作管理办法》', '第七章　 附  则', '第三十条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第一章   总 则', '第一条', '为进一步规范统计工作行为，提高统计数据质量，本行定期对统计业务实施现场检查制度。为使统计现场检查工作规范化、制度化，确保现场检查工作质量，根据《中华人民共和国统计法》、《银行业监管统计管理暂行办法》及《银行监管统计数据质量管理良好标准（试行）》等法律、法规和规章，制定本办法。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第一章   总 则', '第二条', '本行授权统计分析部牵头组织行内相关部门监管统计法规、制度执行及数据质量等情况的现场检查。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第一章   总 则', '第三条', '本办法所规定的统计现场检查分为检查准备、检查实施、检查报告、检查处理和检查档案整理五个阶段。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第四条', '本行统计分析部及统计相关业务部门均可以根据工作需要对统计工作进行现场检查。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第五条', '现场检查的范围可根据统计业务开展的情况进行确定，包括行内机构全面检查或重点机构检查。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第六条', '统计现场检查的内容包括：统计法规和监管统计制度的执行情况、统计质量和统计数据的真实性情况、统计资料的管理情况以及其他与统计工作相关的情况。每一次检查可根据工作需要选择一项或几项内容进行。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第七条', '拟开展的统计现场检查报分管领导批准后实施。统计相关业务部门独立开展的现场检查需报统计分析部备案。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第八条', '根据检查任务，成立现场检查组，确定检查人员。检查组设组长１人、主查人１人，其他检查人员若干。检查组可根据检查工作量下设检查小组，每组设小组长一人，负责专项检查工作。检查人员的业务能力要与检查的内容、任务相适应。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第九条', '统计现场检查组成员的基本职责（一）检查组组长的主要职责：全面负责现场检查工作，协调检查过程中的相关事项。（二）主查人的主要职责：负责现场检查工作的组织和实施，制定检查方案，复核、确认检查中发现的事实，撰写检查报告。（三）检查组其他成员的主要职责：负责所承担的具体检查任务，填写工作底稿、检查事实确认书等表格，做好检查组组长和主查人交办的其他工作。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第十条', '检查组应当根据现场检查的要求制定《现场检查方案》，经检查组组长审定后印发给检查组全体成员。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第二章   现场检查准备', '第十一条', '开展现场检查前，应当对检查组成员进行检查前培训，检查前培训的内容包括：（一）学习检查方案等文件，明确现场检查的目的、内容等； （二）学习与本次现场检查内容相关的法律法规、行政规章以及业务制度、操作规程等；（三）查阅被查单位以往报送的统计资料及报送情况；（四）查阅被查单位前期检查报告等，掌握被查单位以往存在的主要问题；（五）学习现场检查的方法和技巧。第十二条 开展现场检查前，应向被检查单位发出《现场检查通知书》。《现场检查通知书》一般在进入现场前1-7日内送达被检查单位（小型检查1天前，大型检查7天前），如有需要也可在进入现场时交被检查单位。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第十三条', '检查组人员按照《现场检查通知书》指定的时间进入被检查单位实施现场检查工作。组织被检查单位有关人员座谈，说明此次检查的目的、内容、范围、检查工作安排以及对被检查单位配合检查工作的要求等。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第十四条', '检查组进入现场检查的人员不得少于2人，否则检查行为无效。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第十五条', '检查组根据现场检查的目的及内容，向被检查单位出示《现场检查资料调阅清单》，由被检查单位凭清单提供需要调阅的原始凭证、会计账表、各类台账、统计资料、业务管理制度及工作总结等文件资料，调阅资料需交接清楚。如需上门进一步了解客户情况，可由支行（部）相关人员陪同前往，但不得接受客户吃请。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第十六条', '填制检查工作底稿。检查人员实施现场检查时，应当及时对检查工作进行记录，检查组成员按照分工内容填制《现场检查工作底稿》，交主查人汇总。《现场检查工作底稿》的填制要求：（一）检查人员对所填制的《现场检查工作底稿》的真实性负责；（二）填制《现场检查工作底稿》应当做到内容完整、真实、条理清楚；（三）在检查过程中如发现被查单位有统计违规行为，需进行处罚的, 《现场检查工作底稿》应附有对检查事实认定提供支持的必要证据，如凭证、报表、备忘录、说明材料、被检查人的文件等证明材料，取得证明材料时应不少于2人。被检查单位提供相应证明材料是复印件的，应当要求被检查人标注“与原件无误（异）”字样并签章；（四）《现场检查工作底稿》的填制必须及时，便于主查人及时掌握检查的情况；（五）由主查人复核检查期间的所有工作底稿，保证工作底稿足以支持检查结论，保证检查结论有根有据。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第十七条', '检查人员应当根据《现场检查工作底稿》，针对现场检查发现的情况与问题，进行比较分析、整理核对，对检查事项进行实事求是、客观公正评价，形成检查结论，编写《现场检查事实确认书》。《现场检查事实确认书》须经被查单位负责人或经办人签字确认。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第十八条', '检查组成员根据《现场检查工作底稿》和《现场检查事实确认书》，对检查的基本情况、检查查证的事实和问题进行全面的梳理、核实、总结，并由主查人组织检查组全体人员对各项检查内容进行复核、综合分析，形成对被查单位的初步检查报告。初步检查结论的事实必须有详细、充分的数据或文字材料的支持。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第十九条', '检查组应就初步检查报告与被查单位充分地进行意见交换，就异议进行再次核实，最终达成一致意见。如被查单位不予配合或因特殊原因不能反馈意见时，应向分管领导汇报情况，并以事实为依据，形成检查报告，并限期要求被查单位进行反馈，超过期限未反馈意见的视为无异议。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第二十条', '在整个现场检查期间，检查组组长要及时掌握检查工作进度和检查情况，查看检查人员的检查记录，根据需要及时调整分工，将重要问题查清查透，提高检查工作质量，保证如期完成检查任务，达到检查目的。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第三章   现场检查实施', '第二十一条', '检查组成员应当对在检查过程中接触到的被查单位的商业秘密保密。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第四章  现场检查报告和处理', '第二十二条', '检查组主查人应以经确认的初步检查报告为基础，结合现场检查工作的开展情况，以及被查单位的反馈意见，形成《现场检查报告》，经检查组组长审核后，报分管领导审定。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第四章  现场检查报告和处理', '第二十三条', '《现场检查报告》经分管领导审定后，检查组组长应就现场查检发现的问题根据法律法规或相关制度办法提出处理意见，重大处理决定还应征得高级管理层或董事会等相关机构的同意。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第四章  现场检查报告和处理', '第二十四条', '检查组组长应将检查处理意见送达被查单位，被查单位如有异议，应于规定期限内进行申述，否则视同无异议。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第四章  现场检查报告和处理', '第二十五条', '检查组组长应就检查处理的最终结果形成通报等正式文件，并视情况在全行一定范围内公布。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第四章  现场检查报告和处理', '第二十六条', '分管领导应落实专人负责相关检查问题的整改，制定措施，限期到位。被查单位收到处理意见后，在规定期限内，上报整改方案，组织整改，并汇报整改结果。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第四章  现场检查报告和处理', '第二十七条', '为监督被查单位的整改情况，检查组可实施后续现场检查。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第五章  现场检查整理', '第二十八条', '在统计现场检查过程中，检查组应当及时整理现场检查情况，包括检查通知书、检查方案、工作底稿、证明材料、事实确认书、检查报告、被查单位的书面反馈意见等。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第五章  现场检查整理', '第二十九条', '统计现场检查的资料应按照本行档案管理的有关规定妥善保管。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第六章   附  则', '第三十条', '本办法由本行统计分析部负责解释和修订。'),
(NULL, '计划财务部', '《统计现场检查管理办法》', '第六章   附  则', '第三十一条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《股金管理办法》', '第一章  总  则', '第一条', '为规范本行股金管理，保护本行和股东的合法权益，根据《关于规范向农村合作金融机构入股的若干意见》（银监发[2004]23号）、《中国银行业监督管理委员会农村中小金融机构行政许可事项实施办法》（银监会[2015]第3号令）、《关于规范xxx省内农村合作金融机构股权设置的指导意见》（及《xxx银行股份有限公司章程》等有关规定，制定本办法。'),
(NULL, '计划财务部', '《股金管理办法》', '第一章  总  则', '第二条', '向本行入股，应坚持入股自愿、风险自担、服务优惠、利益共享的原则。'),
(NULL, '计划财务部', '《股金管理办法》', '第一章  总  则', '第三条', '股东以其所持股金数额为限承担风险和民事责任。'),
(NULL, '计划财务部', '《股金管理办法》', '第一章  总  则', '第四条', '股东享有按规定获取优惠服务和参与收益分配的权利。'),
(NULL, '计划财务部', '《股金管理办法》', '第二章  入股条件', '第五条', '自然人、企业法人和其他经济组织符合向金融机构入股条件的，均可向本行申请入股，成为本行的股东。㈠自然人。具有完全民事行为能力；具有良好的社会声誉和诚信记录；具有良好的资本补充能力；入股资金为自有资金；银监会规定的其他审慎性条件。㈡企业法人。具有良好的社会声誉和诚信记录；法人治理结构健全；财务状况良好且最近两个会计年度连续盈利；净资产达到全部资产的30%以上；对外累计权益性投资不超过本企业净资产的50%；具有良好的资本补充能力；入股资金为自有资金且来源真实合法；银监会规定的其他审慎性条件。'),
(NULL, '计划财务部', '《股金管理办法》', '第二章  入股条件', '第六条', '股东应以货币资金入股，不得以实物资产、债权、有价证券等形式作价入股。'),
(NULL, '计划财务部', '《股金管理办法》', '第二章  入股条件', '第七条', '股东必须以自有资金入股，不得以贷款入股。'),
(NULL, '计划财务部', '《股金管理办法》', '第二章  入股条件', '第八条', '各级人民政府、财政资金不得直接向本行入股。'),
(NULL, '计划财务部', '《股金管理办法》', '第二章  入股条件', '第九条', '其他金融机构向本行入股，成为战略投资者的，应符合向金融机构入股的有关规定，并报经银行业监督管理机构批准。'),
(NULL, '计划财务部', '《股金管理办法》', '第三章  股权设置', '第十条', '本行采取股份制产权形式，根据股金来源设置自然人股、法人股两种股权。'),
(NULL, '计划财务部', '《股金管理办法》', '第三章  股权设置', '第十一条', '本行股金每股金额为人民币1元，全额设定为投资股，自然人股东入股起点金额为人民币1000元，法人股东入股起点金额为人民币10000元。'),
(NULL, '计划财务部', '《股金管理办法》', '第三章  股权设置', '第十二条', '股东以其所持股份为限参与本行的管理，自然人股实行每1000元为一个投票权，法人股每10000元为一个投票权。'),
(NULL, '计划财务部', '《股金管理办法》', '第三章  股权设置', '第十三条', '本行股金中，单个自然人及其近亲属合计投资入股比例不得超过股本总额的2％，单个法人及其关联企业持股总和不得超过总股本的10%；战略投资者持股最高不超过股本总额的20%。'),
(NULL, '计划财务部', '《股金管理办法》', '第三章  股权设置', '第十四条', '本行股金中，法人持股总额不得低于股本总额的50%，职工持股总额不超过股本总额的10%。其中单个高级管理人员持股不得超过50万股。持有本行股本总额5%及以上股份的股东资格需报经银行业监督管理机构批准。'),
(NULL, '计划财务部', '《股金管理办法》', '第四章  股金管理', '第十五条', '本行使用记名式股金证，作为股东所有权凭证和参与利益分配的依据。本行不得接受本行股金证作为质押标的。本行股东以本行股金证为本人或他人担保的应当事先告知并征得董事会同意。'),
(NULL, '计划财务部', '《股金管理办法》', '第四章  股金管理', '第十六条', '本行股东享有以下权利：㈠依照其所持有的股份份额获得股利和其他形式的利益分配；㈡依法请求召集、主持、出席或委派代理人参加股东会议；㈢依照其所持有的股份份额行使表决权；㈣享有选举权和被选举权；㈤对本行的经营行为进行监督，提出建议或者质询；㈥依照法律、法规和本章程的规定，转让、赠予、质押股份；㈦依照法律、法规和本章程的规定获得有关信息，包括：⒈免费索取本章程；⒉有权查阅和缴付合理费用后复印：⑴本人持股资料；⑵股东大会会议记录；⑶年度财务报告；⑷本行股本总额和股本结构。㈧本行终止或清算时，按其所持股份份额依法参加本行剩余财产的分配；㈨法律、法规和本章程所赋予的其他权利。'),
(NULL, '计划财务部', '《股金管理办法》', '第四章  股金管理', '第十七条', '本行股东承担以下义务：㈠承认并遵守本章程；㈡依其所认购的股份和入股方式缴纳出资；㈢除法律、法规规定的情况外不得退股；㈣以其所持本行股份为限对本行债务承担责任；㈤服从和履行股东大会决议；㈥维护本行的利益和信誉，支持本行合法开展各项业务；㈦本行法人股东应及时、真实、完整地向本行董事会报告关联关系及其参股其他企业、金融机构的情况；㈧本行法人股东如发生法定代表人、公司名称、注册地址、经营范围等重大事项变更，以及公司解散、被撤销或与其他公司合并、被其他公司兼并时，应在30日内书面通知本行；㈨法律、法规和本章程规定应当承担的其他义务。'),
(NULL, '计划财务部', '《股金管理办法》', '第四章  股金管理', '第十八条', '本行根据盈利情况，按照有关规定，对股东分配红利，但不对股金支付利息。本行当年亏损不对股东分配红利。'),
(NULL, '计划财务部', '《股金管理办法》', '第四章  股金管理', '第十九条', '本行股东持有的股金，经董事会同意，并按规定办理登记手续后，可依法转让、继承和赠予。'),
(NULL, '计划财务部', '《股金管理办法》', '第四章  股金管理', '第二十条', '本行股东持有的股金，存在下列情况之一的，可办理股金转让：㈠股东提出退股申请；㈡转让所持全部股金；㈢经董事会同意。股金转让原则上应在当年年底财务决算后办理，在年底财务决算前办理股金转让的，不支付当年股金红利。'),
(NULL, '计划财务部', '《股金管理办法》', '第五章　股金证管理', '第二十一条', '本行签发的股金证，在明显位置以入股须知的形式，对股东应了解的事项加以说明。入股须知包括以下内容：㈠股东入股前应详细阅读本行章程，知晓股东的权利、义务；㈡根据股东大会决议，按规定向股东分配红利；㈢股东转让应符合章程规定；㈢股东基本情况变更应及时申请；㈤股金证发生被盗、遗失、损坏时，应及时提供有关证明，经本行核实后补发新证；㈥本行股东以股份对外提供担保的，未经本行确认，担保无效；㈦股金证加盖本行公章后生效。'),
(NULL, '计划财务部', '《股金管理办法》', '第五章　股金证管理', '第二十二条', '本行股金证列明以下事项：㈠自然人股东列明姓名、住址、身份证号码、发证单位、发证日期、入股金额等事项；㈡法人股股东列明法人名称、法人代表姓名、营业执照号码、发证单位、发证日期、入股金额等事项。'),
(NULL, '计划财务部', '《股金管理办法》', '第五章　股金证管理', '第二十三条', '股金证纳入重要空白凭证管理。股东持有的股金证发生被盗、遗失、灭失或毁损时，法人股东持介绍信、自然人股东持有效身份证明按规定办理挂失手续。'),
(NULL, '计划财务部', '《股金管理办法》', '第六章　相关业务管理', '第二十四条', '本行实行统一核算的管理方法。股金在本行计划财务部统一核算，对扩股、分红、转让、继承等事项详细记载，并设立股东股金明细账。'),
(NULL, '计划财务部', '《股金管理办法》', '第六章　相关业务管理', '第二十五条', '本行根据业务发展需要募集股金时应遵循以下程序：㈠建立增资扩股领导小组，拟定《增资扩股方案（草案）》；㈡召开本行股东代表大会审议拟定的《增资扩股方案（草案）》；㈢请示银行业监督机构批准《增资扩股方案》；㈣宣传发动并公布募股说明书；㈤预约入股工作；㈥审查拟入股股东资格是否符合本行章程，审查通过后拟入股股东填写股金认购单，缴存认购资金；㈦经有关部门核准后，将认购资金进行入账处理；㈧签发股金证给入股股东，同时向入股股东收回股金认购单回单；㈨申请修改本行章程及变更注册资本。'),
(NULL, '计划财务部', '《股金管理办法》', '第六章　相关业务管理', '第二十六条', '本行股东根据本办法办理股权转让时，应执行以下规定：㈠持股人提出书面申请，附持股人和接受人身份证复印件，并与受让人签订转让协议，经本行董事会审核同意后，变更股权登记，签发股金证；㈡股权转让价格由出让方和受让方自行协商，并在转让协议中注明；㈢各支行（部）负责收回出让人的股金证；如为部分股权转让需在其股金证上作转让登记；收回的股金证应及时交清算团队做销账处理；㈣法人股转让时，如其法人是依照《公司法》设立的有限责任公司和股份有限公司，需提供其董事会决议；㈤办理股权转让时，须填写《xxx银行股份有限公司股权转让申请书》，计划财务部审查后签署意见报本行董事会审批。㈥股权转让人和受让人须填写《xxx银行股份有限公司股权转让申请书》，协议一式三份，出让人、受让人、计划财务部各执一份，计划财务部凭此核算股金分户账。'),
(NULL, '计划财务部', '《股金管理办法》', '第六章　相关业务管理', '第二十七条', '本行股东根据本办法申请股金证挂失时，手续如下：㈠申请股金证挂失的股东应提供有效本人身份证明（法人股东提供单位介绍信和法定代表人身份证明），并填写挂失申请书，仔细填列股东姓名、有效身份证件号码、入股时间、入股金额等有关情况，在清算团队申请挂失。㈡挂失申请书一式三联，清算团队经办人凭填写好的挂失申请书办理相关挂失手续，并补发新的股金证，第一联由清算团队登记挂失登记簿，第二联、三联由清算团队经办人放入传票中。'),
(NULL, '计划财务部', '《股金管理办法》', '第六章　相关业务管理', '第二十八条', '本行向股东分配股金红利时的核算要求：㈠本行根据年度会计决算盈利情况，制定利润分配方案与股金分红标准，及时对入股股金进行分红。㈡为确保股金红利及时分配到股东手中，本行与股东约定自然人股东分配的红利以转账方式转存至其在支行（部）开设的个人结算账户中，法人股东分配的红利转入其存款账户。㈢自然人股东分配的红利，按规定由本行代扣代缴个人所得税。'),
(NULL, '计划财务部', '《股金管理办法》', '第七章  附  则', '第二十九条', '本办法未尽事宜按《xxx银行股份有限公司章程》的有关规定执行。'),
(NULL, '计划财务部', '《股金管理办法》', '第七章  附  则', '第三十条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《股金管理办法》', '第七章  附  则', '第三十一条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '', '第一条', '为规范诉讼费用的管理，保证本行垫支诉讼费用按期收回，根据《中华人民共和国商业银行法》结合本行经营管理实际情况，特制定本办法。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第一章 诉讼费用借款的申请及报批流程', '第三条', '相关部门审批  （一）资产保全部负责审核借款合同、借据、催收通知书、还贷证明等信贷档案以及《诉讼费、执行费、收产抵债费用（以下统称诉讼费）呈批表》、《代垫费用明细清单》和《代垫诉讼费用借条》的真实性和完整性，认为有必要同意借用诉讼费的由资产保全部负责人在《诉讼费呈批表》上签署意见并签字确认，同时加盖本部门公章。（二）计划财务部负责审核诉讼费用和借款期限是否符合相关要求，审核无误后，由计划财务部部门负责人在《诉讼费呈批表》上签署意见并签字确认，同时加盖本部门公章。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第一章 诉讼费用借款的申请及报批流程', '第四条', '行长审批  (一)对单笔5万元（含）以下的诉讼费用，报请本行分管财务的行长审批。(二)对单笔超过5万元的诉讼费用经财务管理审查委员会同意后，报请本行分管财务的行长签署意见。    第五条  计划财务部出账 计划财务部按照相关批复及发票从“其他应收款”科目出账垫支诉讼费用，将款项汇至贷款清收责任人所在单位经费备用金账户，由贷款清收责任人领取。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第二章 诉讼费用审核及管理要求', '第六条', '诉讼费用审核要求:1.审核所借诉讼费的金额是否合理，是否符合相关的要求。2.诉讼费用垫支的期限：一般情况下，诉讼费用垫支期限最长时间不得超过3年；公告送达的不得超过2年；评估拍卖的不得超过3年。3.对于本行已鉴定确认的人为责任贷款和仅用于延长诉讼时效、无法落实还款来源的贷款应谨慎审批。总行同意代垫诉讼费后对上述借款向相关责任人追偿。    第七条  对贷款逾期达6个月，清收责任人经催收认为必须诉讼的，不得因诉讼费的呈批未通过而不追不诉。因此丧失诉讼时效的，本行将严肃追究清收责任人的责任。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第二章 诉讼费用审核及管理要求', '第八条', '代垫诉讼费实行台账管理制度。资产保全部、计划财务部要分别建立诉讼贷款及诉讼费用台账，明确专人负责登记，基层支行台账由各单位运营主管负责登记。诉讼费用台账按月由计划财务部负责全面对账一次，确保账账相符。如遇人员调整，诉讼费用台账列入移交范围，由本单位负责人监交，交接清单一式三份，一份交资产保全部，一份交计划财务部，一份留存。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第三章 诉讼费用的收回责任落实', '第九条', '诉讼费用由贷款清收责任人负责收回，贷款清收责任人必须在借款期限内归还所借支的诉讼费用，及时将款项上划本行计划财务部，并通知相关人员及时在台账上销账。案件在执行终结业务处理时应本着先归还诉讼费用，再归还贷款本金和利息的原则，对已收回全部或部分贷款本息而未收回诉讼费用的，本行代垫的诉讼费用将由贷款清收责任人承担。清收责任人发生岗位变动时，所借诉讼费与对应的贷款一并列入移交，不得因清收责任人变动而导致诉讼费收回责任不清。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第三章 诉讼费用的收回责任落实', '第十条', '落实2016年以前借支诉讼费的管理清收责任。1.判决书载明由本行承担的，由本行进行责任认定后，经财务审查委员会审查是否由总行列支；2.判决由借款人、保证人承担的诉讼费，清收责任人未执行先收费用后还贷的，由清收责任人偿还；3.判决后未及时申请执行且贷款、诉讼费未收回的由清收责任人限期收回，期限两年，超期限未收回的由清收责任人承担；4.2009年（含）以前借支的诉讼费，贷款执行已终结且无法落实清收责任的借支诉讼费由本行列支；5.对于一笔贷款重复诉讼借支诉讼费，重复部分由撤诉经办人承担。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第三章 诉讼费用的收回责任落实', '第十一条', '对到期未能按时归还的，按照所借未还金额，由贷款清收责任人承担。自借款到期的次月起，由本行从相关责任人的工资中分月直接扣款，直至全部扣还到位为止。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第三章 诉讼费用的收回责任落实', '第十二条', '对按期收回确实有困难的，在借款到期前一个月向本行资产保全部、计划财务部提出申请，按照权限经本行分管行长同意后方可延期归还。每笔诉讼费申请展期原则上只可一次，且展期时间最长不得超过两年。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第三章 诉讼费用的收回责任落实', '第十三条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《诉讼费用管理办法》', '第三章 诉讼费用的收回责任落实', '第十四条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第一章  总  则', '第一条', '为了加强财务管理，提高经营效益，进一步明确“统一法人，授权经营，分级核算，单独考核”的原则，充分调动本行员工增收节支的积极性，根据中华人民共和国《企业财务通则》、《金融企业财务规则》和国家税务总局、中国人民银行印发的《农村信用合作社财务管理实施办法》等有关法律法规制定本办法。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第一章  总  则', '第二条', '本行实行“独立核算，自主经营，自负盈亏，自担风险”的经营原则。其财务管理的主要任务是：以经济效益为中心，提高盈利能力；规范财务行为，强化经营约束；健全内部激励机制，正确处理分配关系；反映经营状况，监督各项财务活动，以维护股东和债权人的合法权益。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第一章  总  则', '第三条', '本行的计划财务部履行财务管理职责，对其所属各分支机构的财务工作负有管理、指导、检查、监督之责，并须做好各项财务收支的计划、控制、考核和分析工作，综合运用规划、预测、计划、预算、控制、监督、考核、评价和分析等方法，筹集资金，营运资产，控制成本，分配收益，配置资源，反映经营状况，防范和化解财务风险，实现持续经营和价值最大化。（一）建立健全财务风险控制体系，明确财务风险管理的权限、程序、应急方案和具体措施，以及财务风险形成当事人应承担的责任，防范和化解财务风险。（二）筹集资本金，应当符合国家有关资本金管理的规定，根据发展战略和经营规划拟定筹资方案，履行规定的程序。（三）统一管理资金账户，明确资金调度的条件、权限和程序。调度资金应当按照内部财务管理制度，依据有效合同和合法凭证办理手续，不得私存私放资金。（四）结合自身特点，按照内部财务管理制度，强化成本费用预算约束，实行成本费用全员管理和全过程控制。（五）年度实现净利润（减弥补亏损），应当按照提取法定盈余公积金、提取一般（风险）准备金、向投资者分配利润的顺序进行分配。法律、行政法规另有规定的从其规定。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第二章  财务核算', '第四条', '核算基本模式。本行实行“一级法人，二级核算，集中支付，分别考核”的核算方式。（一）所有者权益归总行统一核算管理，固定资产仍为各支行管理、使用、保管，资产折旧由总行统一计提，在受益单位成本中列支。（二）税费由总行统一缴纳。（三）各支行对其资产经营的当期收入、成本、费用等财务遵循权责发生制原则，如实反映、记载，年终将损益统一划转总行，由总行统一进行会计决算、编制年报。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第二章  财务核算', '第五条', '核算方法和要求。业务收支由各支行在授权范围内直接办理。各支行在业务经营过程中发生的收入，应收尽收；发生与经营有关的各项利息支出、金融机构往来支出、手续费支出、业务及管理费用以及其它营业支出等，按规定计入成本。对业务及管理费用要实行量入为出，以收定支，严格管理，限额使用。做到准确核算，及时反映，手续齐全，按时结报。各支行、总行各部（室）不得设账外账或私设小金库，不得自行规定有关支出标准和其它财务事项。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第三章   财务管理', '第六条', '总行以省联社财务管理系统为平台，对各支行（部）坚持“以收定支、限额使用、上移核算、按期结报”的原则，规范财务费用管理；对总行各部（室）财务费用实行归口管理，按权限审批。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第三章   财务管理', '第七条', '对支行的财务费用管理（一）以收定支、核定限额使用的科目业务及管理费用科目中的业务宣传费、业务招待费、印刷费、电子设备运转费、安全防卫费、钞币运送费、邮电费、公杂费、差旅费和水电费等十个项目由总行对各支行核定全年费用限额，各支行在限额范围内按季度控制使用。（二）除上款限额使用的费用项目外，其余费用各支行不得使用和结报。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第三章   财务管理', '第八条', '对本部财务费用实行归口管理（一）职工工资、基本养老保险金、基本医疗保险金、工伤保险金、生育保险金、失业保险金、补充养老保险金由人力资源部根据上级有关文件精神，按规定标准和比率计算到人，个人应承担部分由个人缴纳，由计划财务部组织实施。（二）应缴税金、职工福利费、职工教育经费、工会经费、住房公积金、劳动保护费、取暖及降温费、差旅费由计划财务部按规定使用，个人应承担的部分一律由个人承担。（三）审计费、广告费、业务宣传费、修理费、保险费、交通工具耗用费（车辆修理）、印刷费统一由总行行政办公室归口管理，相关部门配合，按批准权限审批，按规定程序实施。（四）业务招待费、会议费、水电费、邮电费、公杂费由行政办公室管理。总行各部（室）因工作需要进行业务招待，必须提前填报审批单，报分管行长批准后交由总行行政办公室负责安排。严格控制陪客人数，作陪人数最多不能超过宴请人数，无关人员禁止陪餐。水电费、邮电费、招待用烟酒，行政办公室要建好领、用、存台账，专人负责。（五）手续费支出、营业外支出、其他营业支出由计划财务部按制度规定报经批准列支。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第三章   财务管理', '第九条', '严格控制支行固定资产购建，实行计划管理和比例管理。确因业务发展需要，新建和翻建营业楼的，由总行行政办公室报经批准后组织实施。购建固定资产的条件：（1）营业用房狭小或地理位置偏僻，影响正常业务开展；（2）近三年经营盈余，没有亏损；（3）危房拆除、修建，必须有危房翻建报告和有权部门联合鉴定的《危险房屋质量鉴定书》。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第三章   财务管理', '第十条', '总行固定资产、低值易耗品由计划财务部、总行行政办公室根据本行相关规定共同管理执行。本部大宗固定资产、业务宣传用品、印刷品、职工服装的购置、车辆的修理等，价值在2万元以上的支出，必须通过招标方式进行，招标小组应有总行领导、职能部门负责人，以增加透明度。计划财务部及时做好固定资产及低值易耗品的建账和核算工作。总行行政办公室在建好固定资产及低值易耗品账、簿、卡的同时，要逐一落实保管责任人，明确职责，正常做好管理、检查、维护保养工作，确保资产不流失。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第四章  审批权限', '第十一条', '支行（一）支行限额使用的十项费用原则上实行由支行行长“一支笔”审批制度，在总行核定限额内节约的费用，按节约金额的120%增加次年的费用限额；费用限额超支的，从经营目标考核分配款中等额扣减。（二）支行需报批的业务费用，由支行事前向总行行政办公室等相关职能部门提出申请，各相关职能部门进行审核，统一报计划财务部按权限审批，其中，2000元以内（含）的由计划财务部审批，2000元至20000元以内（含）由分管财务的行长审批，2万元以上由财务管理审查委员会审批。（三）支行的代垫款实行分级审批，50000元以内（含）由分管行政办公室的行长审批，2万元以上由财务管理审查委员会审批。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第四章  审批权限', '第十二条', '总行（一）总行的业务费用实行分级审批，1000元以内（含）由计划财务部审批，1000元至20000元（含）由分管财务的行长审批，2万元以上由财务管理审查委员会审批。（二）总行的代垫款实行分级审批，50000元以内（含）由分管行政办公室的行长审批，2万元以上由财务管理审查委员会审批。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第五章  检查监督', '第十三条', '本行的财务工作实行行长负责制，同时接受广大职工的民主管理和监督，认真执行本行转授权管理制度，并定期向董事会、监事会报告财务状况。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第五章  检查监督', '第十四条', '总行建立正常的检查、监督网络和管理体制。每笔费用均需经办人、审核人、审批人签字，大额费用还须增加证明人签字，以示负责。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第五章  检查监督', '第十五条', '计划财务部作为财务管理的执行部门，必须认真做好事中监督，把好财务制度的执行关，对不符规定的开支坚决予以剔除。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第五章  检查监督', '第十六条', '审计部要加强财务费用管理的监督、稽查，采取定期稽核、抽查和年终审计等形式，加大监督管理力度，对不符规定的开支及时向行长室报告或直接向董事会、监事会报告，使本行的财务管理工作在有效的内控管理机制下健康运行。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第六章  附  则', '第十七条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《财务核算管理办法》', '第六章  附  则', '第十八条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《财务管理办法》', '第一章  总  则', '第一条', '为规范财务行为，加强经营管理，提高经济效益，全面加强本行财务监督和费用管理，提高财务管理水平和核算质量，勤俭办行，努力降本增效，根据《企业会计准则》、《金融企业财务规则》结合本行实际情况，特制定本办法。'),
(NULL, '计划财务部', '《财务管理办法》', '第一章  总  则', '第二条', '财务管理的基本任务是：认真贯彻国家的方针政策，维护财经纪律，监督检查和完善经营目标责任制，加强经营管理，努力增收节支，不断提高经济效益，依法计算缴纳国家税收，并接受主管机关的检查和监督。'),
(NULL, '计划财务部', '《财务管理办法》', '第一章  总  则', '第三条', '遵循权责发生制原则。凡是应属本期的收入和支出，不论款项是否在本期收付，都应当作为本期的收入和支出；凡是不属于本期的收入和支出，即使款项已在本期收付，也不应作为本期的收入和支出处理。'),
(NULL, '计划财务部', '《财务管理办法》', '第一章  总  则', '第四条', '计划财务部负责本行范围内财务管理活动，建立和完善各项财务开支的审批制度，包括系统内所辖单位的财务收支管理。'),
(NULL, '计划财务部', '《财务管理办法》', '第一章  总  则', '第五条', '财务管理工作的职责：认真贯彻执行国家政令、法规和各项财务制度，以业务经营为中心，树立效益观念，编制财务计划，组织财务收支、核算和管理，审查资金和财产余缺的处理，参与业务经营和管理，办理年度决算，编制财务报告，定期进行财务分析，实施财务检查和监督，并对存在问题提出改进意见。'),
(NULL, '计划财务部', '《财务管理办法》', '第二章  财务计划', '第六条', '财务计划是经营计划的重要组成部分，是进行经营决策，考核内部经济效益，加强成本管理，指导财务活动，开展财务评价的重要依据。'),
(NULL, '计划财务部', '《财务管理办法》', '第二章  财务计划', '第七条', '财务计划由财务收入、支出、盈利或亏损、利润分配四个部分组成。财务计划的编报内容包括营业收入、营业外收入、成本费用、营业外支出、税金及附加、盈利或亏损、利润分配等。'),
(NULL, '计划财务部', '《财务管理办法》', '第二章  财务计划', '第八条', '财务计划的编制要充分考虑国家宏观政策和主管部门的有关要求，依据利率政策、年度信贷计划和资产负债比例管理的规定、劳资计划、成本率、费用率，并考虑影响本年财务收支的新因素，提出本年度本单位的有关数据，由计划财务部审核后汇总编制。'),
(NULL, '计划财务部', '《财务管理办法》', '第二章  财务计划', '第九条', '计划财务部定期检查分析财务收支计划执行情况，总结经验，及时反映问题，提出建议。行长室根据计划财务部提供的财务情况报告，采取有效措施，防范风险，确保经营成果。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十条', '所有者权益是投资人对本行净资产的所有权，包括实收资本、资本公积、盈余公积和未分配利润。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十一条', '资本金不得随意改变，如需增减，必须严格按法定程序报批。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十二条', '在筹集资本金活动中，投资者缴付的出资额超出资本金的差额（包括溢价净收入），资产重估确认价值或合同、协议约定的价值与账面价值的差额，以及接受捐赠的财产等，计入资本公积金。资本公积金可以按法定程序转增资本金。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十三条', '负债包括吸收的各项存款、各项借入资金、金融机构往来资金、各种应付和预收款项以及其他负债。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十四条', '负债按偿还期限划分为长期负债和流动负债。长期负债是指偿还期限在一年或者一年以上的债务。包括定期存款、长期借款、应付长期债券以及长期应付款等。流动负债是指可以在一年以内偿还的债券。包括活期存款、短期借款、应付短期债券、预提费用、应付及预收款项等。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十五条', '以负债形式筹集资金，必须认真贯彻执行金融政策、法规，不得擅自提高或变相提高利率以及付费标准并应适时合理地调整负债结构，努力降低筹资成本。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十六条', '负债均按实际发生额计价。发行债券按债券面值计价，实际收到的价款超过或者低于债券面值的差额在债券到期以前一次或分次冲减或增加利息支出。发行债券发生的各项费用，应在债券到期以前一次或分次摊入成本。'),
(NULL, '计划财务部', '《财务管理办法》', '第三章　 所有者权益和负债', '第十七条', '以负债形式筹集的资金（包括发行债券和各类定期存款）按上级管理部门规定的适用利率分档次按规定期限计提应付利息，计入成本。活期存款和其他短期负债直接计入相关利息支出科目。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第十八条', '资产按其流动性分为流动资产和长期资产两类。流动资产包括：现金、各种短期贷款、短期投资等。长期资产包括中长期贷款、长期投资、固定资产、无形资产、递延资产以及除上述各项目以外的其他资产。对各类资产要加强管理，应做到：（一）降低非盈利、低盈利资产占比，提高资产营运能力；（二）合理资产投向，提高盈利资产收益水平；（三）积极转化风险资产，加强资产保全，防止资产流失；（四）优化资产结构，提高资产流动性。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第十九条', '现金资产管理要求：（一）认真办理日常的现金收付，保证现金核算的真实、准确，做到“账实相符”。（二）认真做好现金的保管、押运、调拨、管理工作，建立健全内部控制制度，保证各类现金资产的安全。（三）按中国人民银行规定比例，按时计算、足额缴存法定存款准备金。（四）合理制定库存限额，在保证支付的前提下，尽可能降低现金库存，提高资金使用效益。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十条', '发放贷款应按实际发生额计价，并按规定的利率和计息期限计算应收利息。其中：贴现贷款应以贴现票据的面值计价。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十一条', '发放的抵押贷款应按实际支付给借款人的金额计价，抵押品应及时办理表外核算和登记。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十二条', '贷款呆账准备金及其他风险准备金的提取与使用实行统一管理、集中审批、分级核销制度。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十三条', '投资业务在国家规定范围内统一由金融市场部办理，未经批准各支行不得进行各类投资活动和证券买卖。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十四条', '固定资产是指同时具有下列特征的有形资产：（一）为生产商品、提供劳务、出租或经营管理而持有的；（二）使用寿命超过一个会计年度；还需具备下列条件才能予以确认：（一）与该固定资产有关的经济利益很可能流入企业；（二）该固定资产的成本能够可靠地计量。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十五条', '不作为固定资产核算的物品，为低值易耗品。低值易耗品可一次或分期摊入成本。采用分期摊入成本的，摊销期限最长不得超过2年。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十六条', '无形资产的价值应按取得时的实际成本计价。自开始使用之日起，在无形资产的有效使用期限内平均摊入成本。无形资产的有效使用期限按下列原则确定：（一）法律和合同或申请书中分别规定有法定有效使用期限和受益年限的，按法定有效使用期限与合同或申请书规定的受益期限孰短的原则确定；（二）法律无规定有效使用期限，合同或申请书规定有受益年限的，按合同或申请书中规定的受益年限确定；（三）法律、合同或申请书均未规定法定有效使用期限和受益年限的，按预计的受益期确定；（四）受益期限难以预计的，按不少于10年的期限确定。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十七条', '无形资产转让，包括转让无形资产所有权和使用权。无形资产转让的净收入，除国家另有规定外，均应计入当期其他营业收入。转让使用权的无形资产，仍应作为本行拥有的资产进行核算，保留原账面价值。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十八条', '开办费自营业之日起，要在不少于5年内分期摊入成本。为取得固定资产、无形资产所发生的支出不得计入开办费。以经营租赁方式租入固定资产的改良支出以及其他长期待摊费用应作为递延资产核算，在规定期限内分期平均摊销。'),
(NULL, '计划财务部', '《财务管理办法》', '第四章 　资  产', '第二十九条', '其他资产主要包括自身的被冻结存款、被冻结物资以及涉及法律诉讼中的财产等。这类财产应设立专户，单独核算。'),
(NULL, '计划财务部', '《财务管理办法》', '第五章　	成本、费用管理', '第三十条', '在业务经营过程中发生的与经营有关的各项利息支出、金融机构往来利息支出、手续费支出、营业费用以及其他营业支出等，按规定计入成本。'),
(NULL, '计划财务部', '《财务管理办法》', '第五章　	成本、费用管理', '第三十一条', '成本核算，要严格区分本期成本与下期成本的界限、成本支出与营业外支出的界限。成本具体包括以下内容：（一）利息支出。指以负债形式筹集的各类资金（不包括金融机构往来资金），按国家规定的适用利率分档次提取的应付利息和国家政策允许列支的其他利息支出。（二）金融机构往来支出。指与中国人民银行、其他银行及金融机构之间资金往来发生的利息支出。其中如有当年应支付而未支付的利息，应逐笔计算应付利息，计入当年损益。（三）手续费及佣金支出。指办理金融业务过程中发生的手续费支出。（四）业务及管理费。包括：业务宣传费、广告费、印刷费、业务招待费、电子设备运转费、钞币运送费、安全防卫费、保险费、邮电费、诉讼费、公证费、咨询费、审计费、监管费、技术转让费、研究开发费、外事费、职工工资、职工福利费、职工教育经费、工会经费、劳动保护费、劳动保险费、失业保险金、公杂费、差旅费、水电费、会议费、低值易耗品摊销、长期待摊费用摊销、无形资产摊销、租赁费、修理费、劳务费、绿化费、董事会费、税费、管理费、会费、交通工具耗用费、开办费、物业费、住房公积金、固定资产折旧、其他费用等。（五）其他业务支出。在办理金融业务过程中发生的不属于以上项目的其他成本支出。（六）资产减值损失。包括呆账准备金及坏账损失等。'),
(NULL, '计划财务部', '《财务管理办法》', '第六章 　营业外收支及税金', '第三十二条', '营业外收支是指与业务经营无直接关系的各项财务收支。营业外收入包括资产清理收益、罚没款收入、长款收入、因债权人的特殊原因确实无法支付的应付款项等。营业外支出包括资产处置损失、久悬未取款项支出、无法确定赔偿责任的差错损失、公益救济性捐赠、非常损失、赔偿金、违约金等。'),
(NULL, '计划财务部', '《财务管理办法》', '第六章 　营业外收支及税金', '第三十三条', '各项营业外收支必须严格执行国家的有关规定，认真审批，慎重处理，据实列账。'),
(NULL, '计划财务部', '《财务管理办法》', '第六章 　营业外收支及税金', '第三十四条', '按规定核算、缴纳税金和有关费用。'),
(NULL, '计划财务部', '《财务管理办法》', '第七章 　利润及分配', '第三十五条', '利润总额由营业利润和营业外收支净额组成。计算公式为：利润总额=营业利润+营业外收入-营业外支出营业利润=营业收入-税金及附加-成本'),
(NULL, '计划财务部', '《财务管理办法》', '第七章 　利润及分配', '第三十六条', '利润总额按有关规定作纳税调整后，依法缴纳所得税。'),
(NULL, '计划财务部', '《财务管理办法》', '第七章 　利润及分配', '第三十七条', '发生的年度亏损，可以用下一年度的利润所得税前弥补，下一年度利润不足弥补，可以在五年内延续弥补。五年内不足弥补的，用税后利润弥补。'),
(NULL, '计划财务部', '《财务管理办法》', '第七章 　利润及分配', '第三十八条', '税后利润，按以下顺序分配：1、弥补以前年度亏损。2、提取盈余公积。按照税后利润的10%提取法定公积金，本行法定公积金累计额达到本行注册资本的50%时，可以不再提取；提取任意盈余公积金。3、提取一般风险准备。按照税后利润的一定比例提取一般风险准备。4、向股东分配利润。根据省联社每年度的会计决算工作意见，将可供股东分配利润按一定比例以现金或配股方式向股东分配。'),
(NULL, '计划财务部', '《财务管理办法》', '第八章　	财务报告与评价', '第三十九条', '财务报告是反映财务状况和经营成果的总括性书面文件，是检查政策执行情况、考核经营计划、进行财务分析、评价经营成果的重要依据。各支行（部）应定期提供财务报告。财务报告包括财务报表和有关财务情况说明书。'),
(NULL, '计划财务部', '《财务管理办法》', '第八章　	财务报告与评价', '第四十条', '财务报表包括资产负债表、损益表、业务状况表及其他附表。'),
(NULL, '计划财务部', '《财务管理办法》', '第八章　	财务报告与评价', '第四十一条', '财务分析是财务管理的重要组成部分，是实现财务管理职能化的必要手段，必须按照经济核算的原则，充分运用各项业务活动的数据资料和调查掌握的情况，剖析一定时期的财务收支情况，评价财务成果，预测财务前景，从而总结经验，提出措施，改善财务管理，提高管理水平。'),
(NULL, '计划财务部', '《财务管理办法》', '第八章　	财务报告与评价', '第四十二条', '财务分析要报送上级部门，有关情况还应在内部作适当披露，便于指导和监督，不断改进财务管理工作。'),
(NULL, '计划财务部', '《财务管理办法》', '第九章 　附  则', '第四十三条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《财务管理办法》', '第九章 　附  则', '第四十四条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第一章  总  则', '第一条', '为了规范本行财务管理审查委员会（以下简称“财审会”）的运作程序，切实提高财务管理水平，根据《中华人民共和国商业银行法》、相关财务制度和省联社完善法人治理结构工作指导意见，结合本行实际情况，特制定本议事规则。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第一章  总  则', '第二条', '财审会是在本行行长室领导下的重大财务管理决策机构，接受本行董事会、监事会的检查和监督。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第二章  财审会的组成', '第三条', '财审会由七名委员组成，分管财务工作副行长为财审会主任，行长室其他副行长及办公室、合规管理部、计划财务部主要负责人为财审会成员。本行董事会、监事会、审计部应列席或派员列席财审会会议。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第二章  财审会的组成', '第四条', '财审会的办事机构设在计划财务部，计划财务部负责人为财审会办公室主任，由其办理提交财审会审议、审批事项的受理、初审等事宜。委员会指定计划财务部一名工作人员为办公室成员，负责会议的准备工作及会议内容的记录，负责委员会审议事项的整理、资料的收集、入档等具体事务。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第三章  财审会职责', '第五条', '财审会的主要职责包括：（一）根据国家财经、金融方针政策，企业会计准则、省联社财务管理办法和本行章程,审核本行财务制度和相关规定；（二）审议调整财务、固定资产投资、劳资等专项发展规划和年度计划；（三）负责审议年度固定资产购建计划和大额费用开支，装修费用等；（四）负责对物品集中采购计划项目审议；（五）负责审议、拟定年度利润分配、弥补亏损草案；（六）负责审议、拟定财务预算草案和决算草案；（七）负责审议、制定和修改财务费用管理实施办法；（八）审议、制定全行重大财务费用支出、实物资产购建、修理、出租、出售、捐赠、报废等处置方案；（九）负责研究和处理与财务费用相关范围内的其他事项。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第四章  会议制度', '第六条', '按照报告事项的缓急程度划分，财审会会议分为定期召开和临时召开。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第四章  会议制度', '第七条', '定期召开的财审会会议每月至少召开一次。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第四章  会议制度', '第八条', '在下列情况下，可召开临时的财务管理委员会会议：（一）遇有重大或紧急事项时；（二）本行行长室或召集人认为必要时；（三）总行监事会提议时。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第五章  议事程序', '第九条', '议案的提出（一）财审会议案的提出，主要根据各部门的提案申请。具体包括：1.拟定年度财务预算方案和决算草案；2.拟定利润分配方案和弥补亏损草案；3.制定和修改财务费用管理实施办法；4.提交审批 2 万元（不含）以上财务费用项目，5 万元（不含）以上的单位或部门的代垫款、诉讼执行、收产和保全费等项目；总行日常固定费用支出，如电话费、水电费、业务招待费等，经有权人审批后支出，不再报财审会审议；5.提交年度固定资产购建计划；6.其他需提交财审委员会的提案。（二）各职能部门接受与各自职能相关的申请并进行调查核实，根据调查核实的情况提出部门报告，报财审会办公室，财审会办公室认为有必要的，接受部门可列席会议。（三）所有提案必须同时以书面和电子文件形式提交，书面提案必须由部门负责人签字、部门签章并经分管领导签批，报送计划财务部汇总审核，呈交主任委员及委员会成员预审，为保证有充足时间进行预审，列入当期会议的提案至少应提前 2 个工作日提交。（四）凡提交财审会提交案，应符合本行《“三重一大”决策制度实施办法》。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第五章  议事程序', '第十条', '会议的召集财审会会议由委员会主任召集并主持。委员会主任因特殊原因不能出席时，可委托委员会成员召集并主持。召集的形式由财审会办公室通知委员会各成员，会议事项由工作人员提前告知委员会各成员。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第五章  议事程序', '第十一条', '会议的出席财审会会议原则上必须有三分之二以上（含）成员出席方可举行，必要时可邀请有关人员列席。委员会成员本人因故不能到会，经会议召集人同意，可委派代表出席会议。列席人员有义务介绍有关情况，但没有表决权。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第五章  议事程序', '第十二条', '报告的审议会议在委员会主任的主持下对每一则报告逐项审议.会议在审议有关报告时，为了详尽了解其要点和过程情况，可要求承办负责人列席会议，听取和询问有关情况说明，以利于作出正确决定。审议中发现情况不明或方案可行性存在问题的报告，财审会应要求承办部门予以说明，可退回重新办理，暂不表决，另行复议。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第五章  议事程序', '第十三条', '表决财审会实行“集体审议，独立表决，以多数通过”的审议表决方式。所有参会成员须本着依法经营、合规管理、增收节支、促进本行长效发展的原则对提交议案发表明确的“同意”、“否决”或“另行复议”的意见。财审会审议提交的报告，须由出席会议三分之二以上委员会成员赞成，方可通过。报告审议的通过采用记名形式书面表决。财审会成员对其他成员的意见负有保密的义务，以形成统一意见或决议对外解释，对通过的事项由财审会办公室以书面形式进行回复。经财审会通过的事项，须报行长进行备案，行长具有一票否决权。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第五章  议事程序', '第十四条', '特殊事项对须由财审会审查批准的财务事项，因特殊情况必须立即决定或办理的，可由委员会主任处理决定，但事后必须由财审会补议。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第五章  议事程序', '第十五条', '会议记录财审会会议记录是委员会所议事项是否通过的正式证明，应由财审会办公室成员对所议事项做成详细的会议记录（原则上财审会成员不兼任记录员）。会议记录应包括以下内容：（一）会议召开的日期、地点、召集人或主持人以及出席成员的姓名；（二）会议议程；（三）成员发言要点；（四）提案的表决结果；（五）出席成员签字。会议记录及相关资料按年移交本行办公室归档。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第六章  管理和实施', '第十六条', '财审会审议通过的事项应及时向董事会、监事会、行长室报告；董事会、行长对财审会审议通过的事项与相关规章制度不符或明显不利于本行长效发展的，可行使否决权。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第六章  管理和实施', '第十七条', '对本行董事会表决通过的年初财务预算内的事项，由行长室按董事会授权和财务管理制度执行；对超过本行董事会表决通过的年初财务预算外的事项，按本行“三重一大”决策管理制度执行。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第六章  管理和实施', '第十八条', '对大型固定资产购建按省联社规定的要求和程序上报审批后方可购置。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第六章  管理和实施', '第十九条', '财审会审议通过的资产购置及处置事项、费用支出分别由办公室、计划财务部承办。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第七章  附	  则', '第二十条', '本规则由本行财务管理审查委员会办公室负责解释和修订。'),
(NULL, '计划财务部', '《财务管理审查委员会议事规则》', '第七章  附	  则', '第二十一条', '本规则自发文之日起施行。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第一章  总 则', '第一条', '为加强本行的财务管理工作，根据《企业会计准则》、《金融企业财务规则》和《xxx省农村信用社财务管理基本规范》、《xxx省农村信用社联合社关于进一步加强财务管理工作的通知》等有关法律、法规及其他规范性文件的规定，结合本行实际情况，制定本办法。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第一章  总 则', '第二条', '本办法适用于本行各支行（部）、机关各部室及团队（以 下简称“各单位”）。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第一章  总 则', '第三条', '本办法所称财务费用是指本行开展日常经营管理活动 发生的经济利益流出，包括人员费用、业务费用和专项费用。费用管理是指费用规划、预算、审查、核算、监督、控制、考核、分析等管理行为的统称。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第一章  总 则', '第四条', '费用管理的基本原则：实事求是、依法合规；流程控制、权责明确；保证业务需要、平衡效益和成本。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第二章  职责与权限', '第五条', '董事会负责年度收支预算的审批和调整。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第二章  职责与权限', '第六条', '行长或受权人负责审批经董事会批准的预算内项目费 用。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第二章  职责与权限', '第七条', '财务审查委员会负责确定全行各项费用支出标准。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第二章  职责与权限', '第八条', '各职能部室职责权限如下： （一）计划财务部门。计划财务部为费用的统筹管理部门，负责在行长室的领导下起草各项费用管理规章制度，组织编制年度费用预算，实施年度预算控制，做好日常费用报销管理，对费用预算执行情况进行监督、控制和考评。（二）费用需求部门。费用需求部门为费用的实际使用部门，负责在预算额度内，按照规章制度要求提供相关申报资料，经有权人审批后办理，并对支出的真实性和合规性负责。 （三）归口管理部门。归口管理部门是根据部门职责，负责制定与归口费用相关的管理措施，向计划财务部提出归口费用的支出标准建议，编制归口费用预算，并协助计划财务部对归口费用的支出情况进行控制。 （四）监督审计部门。审计部为费用管理监督部门，按照规章制度和本办法有关规定，对费用支出情况进行监督检查和专项审计。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第二章  职责与权限', '第九条', '各单位为费用支出的责任主体，负责在规定权限内根据预算或费用额度安排并控制本单位费用支出。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第三章 费用预算', '第十条', '费用实行预算管理，由计划财务部门具体牵头负责。（一）每年1月10日前，由归口管理部门按分管事项，提出年度归口费用预算及明细，由计划财务部审查汇总，形成当年费用预算。 （二）费用预算经行长或受权人同意，报财务管理审查委员会审议通过后，提交董事会批准。 （三）费用预算在执行过程中，因不可预见或者预算时未充分考虑的因素使业务经营受到重大影响需调整预算时，由归口管理部门会同计划财务部审查，经行长或受权人同意，报财务管理审查委员会审议通过后，提交董事会批准。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第四章 费用核算', '第十一条', '费用核算严格遵循《企业会计准则》、《金融企业财务规则》、《xxx省农村信用社财务管理基本规范》和相关财经法律法规的规定，不得超范围、超标准列支费用。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第四章 费用核算', '第十二条', '费用核算严格区分收益性支出与资本性支出，真实反映各项成本。 收益性支出是指需要列入当期损益的各项成本或者支出，包括营业支出和营业外支出。 资本性支出是指受益期在一年以上的各项支出，包括固定资产购置、投资性房地产支出、在建工程支出、无形资产及长期待摊费用等。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第四章 费用核算', '第十三条', '本行费用按用途分为人员费用、业务费用和专项费用。 （一）人员费用包括职工工资、奖金、津贴、补贴、各项工资性提留等。其标准按照省联社核定的工资总额及财务制度规定执行。 （二）业务费用包括各营业网点基础费用、业务宣传费用和总行机关管理费用。实行“按年预核、分期使用、统一管理、年末清算、超支自补”的原则，并按以下办法进行核定。 基础费用由财务管理审查委员会按照各营业网点的业务规模、区域及员工人数等实际情况确定支出标准，在年初一次性核定，由计划财务部按期下划，全年决算。 业务宣传费用由发展规划部根据各机构实绩并按照规定标准进行计算公布，各机构按财务费用管理办法相关规定向计划财务部门报销、列支和使用。 总行机关管理费用由财务管理审查委员会按照各部门业务开展实际情况确定支出标准，在年初一次性核定，全年决算。 （三）专项费用指在核定的基础费用范围以外的非常规和临时性费用，专项费用由各使用单位提出申请，由计划财务部根据相关证明文件和票据的用途审核后逐级审批下划。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第四章 费用核算', '第十四条', '费用核算的具体内容如下： 1.业务宣传费是指开展业务宣传活动所支付的费用，主要是指 未通过媒体传播的广告性支出，包括发放的印有农商行标志的宣传用品、纪念品等。 2.广告费是是指经过工商部门批准的专门机构制作、通过一定 的媒体传播，已实际支付并取得相应广告业发票的费用。不符合上述条件的，包括未通过媒体的广告性支出，一律视同业务宣传费处理。 业务宣传费及广告费两项支出应不超过全年营业收入的15%。 3.印刷费是指为了满足日常经营管理所支付的凭证费、纸张费等。 4.业务招待费是指为业务开拓、日常经营所支付的招待费用，业务招待费应不超过当年营业收入的0.5%。 5.电子设备运转费是指为维持电子设备稳定运行所支付的费用。符合固定资产确认条件的应列支固定资产。 6.钞币运送费是指为押送现金、凭证等重要物品所支付的押解、运送费用。不包括向押运人员发放的相关补贴和其他职工薪酬性支出。7.安全保卫费是指支付的与安全保卫相关的各项费用，包括安防设备费、联防保安费和安防其他费用。符合固定资产确认条件的列支固定资产，不包括向职工发放的安全值班费、保安人员补贴和其他职工薪酬性支出。8.保险费是指出于保障单位财产、职工人身安全目的，参加国家强制要求或认定需要保险所支付的费用，不包括为职工个人及家庭财产进行的保险。 9.邮电费是指日常经营中所支付的邮资费、电话费、网络费等费用。不包括向职工发放的通信补贴（现金）及其他职工薪酬性支出。 因工作需要，方便业务的开展和办理，手机通讯费按董事长600 元/月，行长、监事长、副行长500元/月，总经理、支行行长250元/月，副总经理、团队经理(副股级办事员)、运营主管150元/月、客户经理及机关办事员100元/月、综合柜员、专职客户经理60元/月，由总行统一代付费，超额部分自理。 10.诉讼费是指聘请律师处理有关法律事务所支付的代理费用、依法承担的法院诉讼受理和财产执行等与案件诉讼相关的费用。包括诉讼受理费、律师代理费、执行费、保全费和其他费用。 对于败诉发生的诉讼费用，在追究相关责任人责任后在本科目中列支；对于胜诉垫付的诉讼费用，应及时收回予以冲账；对于垫付的应由对方承担的诉讼费用，如有确凿证据表明对方无力支付和偿还，可按规定程序通过已计提坏账准备或营业外支出予以核销。 11.公证费是指委托公证机关办理契约、合同公证等业务所支付的费用，以及委托公安机关或有关技术鉴定机构鉴别假币、债券、印鉴、原始资料、证件等真伪所支付的费用。 12.咨询费是指聘请经济技术顾问、法律顾问、会计税务顾问等所支付的费用，包括合同规定的相关咨询人员的差旅费等。 13.审计费是指聘请专业机构进行查账验资和资产评估等所支付的各种费用，包括合同规定的专业机构人员的差旅费等。 14.技术转让费是指使用其他单位或个人拥有的专有技术或非专利技术所支付的费用。购置专利权或专有技术，应作为无形资产管理。 15.研究开发费是指为获取新技术、新产品研究和开发项目所支付的外购服务费、会议费、差旅费、培训费等各项费用。包括科技外购服务费、科技项目非合同性费用和产品研发费用。 16.外事费是指外事接待费用或因公临时出国按照规定标准报销的相关费用。 17.公杂费是指维持正常经营所支付的日常办公费用、报刊订阅费用等。 18.差旅费是指与经营管理活动相关的出差，按规定标准报销的车、船、飞机票、住宿费、住勤补助费、交通费以及交通补贴等费用。不包括向职工发放的伙食补助、车改补贴及其他职工薪酬性支出。 为满足业务经营和工作需要，对全行购置工作用车的员工按部门总经理、支行行长1000元/月，副总经理、团队经理(副股级办事员)800元/月，部门办事员、客户经理600元/月，运营主管500元/月，边远地区综合柜员、专职客户经理300元/月，其他地区综合柜员、专职客户经理200元/月标准结报差旅费，超支自理。办公室负责车辆使用费用报销对象的确认、车辆的日常管理、车身广告的制定；计划财务部负责根据办公室提供的报销对象的名单和标准进行报销手续的审核、费用列支，并建立台账，按年度审阅后装订归档。 19.水电费是指为保证营业场所正常运转所支付的水、电、煤、气费及增容费。不包括向职工发放的水电费补贴及其他职工薪酬性支出。 20.会议费是指召开或参加与经营管理活动相关会议所支付的伙食费、住宿费、场地租用费、标准内的交通费、印刷费、办公用品等相关费用。不包括向参会人员发放的补贴及其他职工薪酬性支出。 21.绿化费是指为美化办公营业环境所支付的植物花卉购买、租赁、养护费用。 22.董事会费是指董事会为履行其职能、召开董事会会议时所支付的相关费用。不包括向董监事发放的补贴，行长室绩效考核奖金及其他职工薪酬性支出。 23.会费是指本行参加社会团体组织所支付的相关费用。 24.交通工具耗用费是指支付的与交通工具相关的费用，包括车辆油料费、车辆停车过路费、车辆维修费、车辆年检费、车辆保险费及车辆其他费用。不包括向职工发放的汽车汽油费补贴、公里里程补助及其他职工薪酬性支出。 25.开办费是指组建或新设网点在批准筹建之日起，到正式营业之日止（即筹建期间）所支付的费用。 26.管理费是指承担的上级部门分摊的管理费用。 27.职工薪酬支出包括：职工工资、职工福利费、基本养老保险金、基本医疗保险金、工伤保险金、生育保险金、失业保险金、住房公积金、工会经费、职工教育经费、企业年金、补充医疗保险、辞退福利、股份支付、非货币性福利和其他职工薪酬支出。 （1）职工工资是指支付给职工的所有工资性支出，包括工资、 奖金、津贴和补贴（已经实行货币化改革的，按月按标准发放或支付的住房补贴、交通补贴或者车改补贴、通讯补贴、节日补助、未统一供餐而按月发放的午餐费补贴）等。对元旦、五一节、国庆节、行庆按每节每人1000元标准在工资中发放。不得在职工工资以外的科目列支职工薪酬性支出。 （2）职工福利费是指符合财政部《关于企业加强职工福利费财务管理的通知》（财企〔2009〕242号）等规定的职工福利费事项支出。职工福利费在职工工资总额的14%以内据实列支，不得先计提后使用。 自办职工食堂经费：按每人每月500元标准列支，用于职工在自办职工食堂的就餐费用，不得发放现金。支行支出时需附由职工签名的食堂支出证明、采购物品清单（主要包括名称、单价、数量、合计等）；总行支出时需附采购物品的清单（主要包括名称、单价、数量、合计等）。 （3）工会经费是指按职工工资总额计提工会经费发生的支出。工会经费按不超过职工工资总额的2%提取，拨交工会专户使用和管理，实际拨付款项时要取得工会划拨专用票据。工会经费按照《中华人民共和国工会法》和《中华全国总工会关于加强基层工会经费收支管理的通知》（总工办发〔2014〕23号）的规定管理、使用。 （4）职工教育经费是指用于职工教育和培训方面的支出，在工资总额的2.5%以内据实列支。包括业务培训及给予职工的学习奖励等支出。不包括应由职工个人负担的学历、学位教育费用。 （5）职工保险费用是指按国家有关规定计提并向社会保险经办机构缴纳的费用，包括基本养老保险费、失业保险费、工伤保险费、基本医疗保险费、生育保险、企业年金、补充医疗保险和职工住房公积金等。不包括应由职工个人负担的保险费用和向职工发放的节日补贴及其他职工薪酬性支出。 （6）劳动保护费是指因工作需要为职工配备或提供工作服、手套、安全保护用品、防暑降温用品等支出。 28.劳务费是指按照协议（合同）约定直接支付给劳务派遣公司的费用。 29.物业费是指参照当地政府规定所支付的房屋物业管理费。 30.租赁费是指根据经营性租赁合同确认当期的租赁费用，包括房租、电子设备租赁费、车辆租赁费等。 31.修理费是指为了使资产处于正常使用状态，恢复其使用价值，进行修理或修复所支出的费用。 32.其他费用是指符合业务及管理费确认条件，但不在上述范围内的其他费用。不包括向职工支付的薪酬性支出。 33.低值易耗品摊销，是指低值易耗品的摊销费用，可采用一次摊销法或者五五摊销法。 34.长期待摊费用，是指已经发生但应由本期和以后各期负担的分摊期限在一年以上的各项费用。 35.无形资产摊销，是指按规定摊销无形资产而在成本中列支的摊销费用。 36.固定资产折旧费。总行应对固定资产按月计提折旧，已提足折旧仍继续使用的固定资产和单独计价入账的土地除外。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第五章  费用审查和审批', '第十五条', '有权审批人。有权审批人指行长或受权人。本行费用实行授权管理下分级审批制度，有权审批人根据费用授权管理的规定，在授权范围内进行审批。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第五章  费用审查和审批', '第十六条', '事前审批。 （一）审批权限规定 1.支行、营业部、公司业务部、小微业务部权限 在包干额度内按规定使用，其他零权限。 2.计划财务部权限 （1）业务费用：对支行包干额度以外的单笔用款2000元（含） 以下；机关用款单笔支出1000元（含）以下； （2）应付款权限：单笔1000元（含）以下； （3）支行或者部门代垫款：零权限。 3.分管财务行长权限 （1）业务费用：对支行包干额度以外的单笔用款2000元（不含）以上、20000元（含）以下；机关用款单笔支出1000元（不含）以上、20000元（含）以下； （2）应付款权限：单笔1000元（不含）以上； （3）支行或者部门代垫款：单笔50000元（含）以下；诉讼执行、收产和保全费用50000元（含）以下。 对于职工工资、邮电费、水电费、社保基金、住房公积金、税金等为满足日常业务需要而发生的款项及按各类经审批的合同约定付款的，不论金额大小，均由分管财务行长直接审批通过。 4.财务管理审查委员会权限 ⑴业务费用：单笔用款20000元（不含）以上； ⑵支行或者部门代垫款：单笔50000元（不含）以上。 经财务管理审查委员会通过的事项，由分管财务行长直接审批通过。 （二）审批单样式及填列要求 1.费用报销审批单及填列要求 部门具体经办人员在经办人处签字，部门经理在证明人处签字，分管行长在分管行长审核处签字，再提交计划财务部审核、审批。相关事由撰写须简洁明了。 2.差旅费报销单 出差人在出差人处签字，应填写所有的出差人员，部门经理在证明人处签字，分管行长在分管行长审核处签字，再提交计划财务部审核、审批。 （三）上述需事前审批的事项，如因事情紧急或履行事前审批手续存在困难，未事前履行上述审批手续但事前经分管财务行长的同意后实施的费用事项，实际报销时仍须提供按照上述流程办理的审批签字手续。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第五章  费用审查和审批', '第十七条', '事中监控。事中要严格依照预算及支出标准进行控制。对大额、长期费用支出项目，计划财务部可根据实际需要，要求费用需求部门或归口管理部门提供费用支出事项的进展情况及相关资料。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第五章  费用审查和审批', '第十八条', '事后检查。事后要及时办理报销手续，并根据要求进行后续评价。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第十九条', '费用使用部门凭合规票据和相关审批资料向计划财务部申请报销。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十条', '计划财务部负责总行机关和支行费用报销手续审核及对外支付应由总行支付的费用报销款。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十一条', '费用报销时，计划财务部重点审核以下内容： （一）费用支出是否履行了规定审批程序。 基层报销费用须经本单位负责人在《费用报销审批单》上签字把关，机关部室报销费用按流程进行审批。 （二）报销资料是否符合财务核算制度以及本行内部管理的要求。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十二条', '计划财务部按要求对审核无误的资料予以报销；对缺少支持性材料、报销资料不完整的，计划财务部有权要求补充完善；对不合规的费用支出，计划财务部有权拒绝办理报销手续。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十三条', '报销资料包括报销单据、原始票据、相关审批资料、证明财务支出真实性的资料等。证明财务支出真实性的资料主要包括以下资料： 1.按合同付款的，合同应作为费用报销的资料之一。如按合同约定的进度分期付款的，合同应作为第一次报销的资料之一，第一次付款已提供总金额发票的，从第二次付款开始需提供经单位负责人签字及加盖单位章（部门加盖部门章，支行加盖业务公章，下同）的相关说明或付款通知，付款通知包含合同总金额、已付款金额、本次付款金额、剩余未付款金额、收款人名称、账号及开户银行信息等，作为支付依据。提供相关合同的，要素必须齐全。 2.小额发票（如汽油票、出租车票、百元以下定额发票）可汇总报销，逐张粘贴在报销审批单后，对于不同类别业务票据在办理报销时应粘贴在不同的费用审批单上。 3.报销业务宣传费的，对用于宣传事项的应提供宣传方案和宣传图片；对用于购制单笔1千元（含）以上宣传品的应提供宣传品资料图片、购制合同和供货方盖章的采购清单。 4.报销广告费的，应提供广告合同、广告图片和广告承办单位营业执照复印件。对于先付款后制作的广告，应在广告制作完成后及时将广告图片报计划财务部备案。 5.报销单笔1万元（含）以上电子设备运转费、钞币运送费、安全保卫费、保险费、租赁费的，应提供相关合同。 6.报销公证费、咨询费、审计费的，应提供相关合同和公证（咨询、审计）报告。 7.报销诉讼费的，应提供相关合同和法院相关资料。 8.报销外事费的，应提供外事部门相关资料。 9.报销公杂费的，对用于购买单笔1千元（含）以上办公用品的，应提供供货方盖章的采购清单。 10.报销差旅费的，应提供差旅费报销单，写明出差人员姓名、 地点、时间、任务等，报销单笔1万元（含）以上住宿费的还应提供住宿消费清单。 11.报销水电费的，应提供发票。对于因租用办公用房而集体支付水电费的可提供水电费分割单和水电费发票复印件及租赁合同复印件。 12.报销会议费、董事会费的，应提供会议通知，本行主办或委托主办的会议还应提供人员签到表，报销单笔1万元（含）以上住宿费的还应提供住宿消费清单。 13.报销会费、管理费的，应提供收款人出具的交款通知单。 14.报销交通工具耗用费的，对用于交通工具修理的应提供汽车维修业统一发票，单笔1千元（含）以上的修理费用需提供修理明细清单，清单中应注明车牌号、维修明细及单价等项目；对用于车辆保险的应提供车辆保险单。 15.报销职工教育经费的，对用于教育培训的应提供教育培训通知，由总行组织的集中培训，应提供与中介机构签订的服务合同。 16.报销劳动保护费的，应提供供货方盖章的劳保用品采购清单。 17.报销修理费的，对5万元（含）以上装修工程款应提供中介机构对项目合同决算的专项审计报告。 18.报销劳务费的，第一次报销的应提供相关劳务合同和用途为劳务费的发票，在合同期间报销的应提供用途为劳务费的发票。 19.报销代理业务手续费、其他手续费的，应提供相关合同、对方当事人是单位的应提供中介服务机构营业执照复印件、对方当事人是个人的应提供居民身份证复印件。对金融服务站支付相关手续费的可一次性提供营业执照或居民身份证的复印件。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十四条', '总行在营业部开立基本存款款账户（或经费专户）用于办理费用报销款支出。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十五条', '除个人差旅费用外,总行费用报销款为1万元（含）以上的必须采取转账方式对外支付，转账支付方式可采用双人网上银行支付，也可以采用填制结算凭证到营业部办理，确需支付现金的，应经分管财务行长审批同意后办理。对于大额报销款支付应按支付结算的相关规定实行授权。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十六条', '除员工个人报销的费用可支付给员工外，其他支出不得支付给员工或通过员工账户支付给原始票据收款人。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十七条', '支行报销实行备用金管理，即总行定期下划备用金，除个人差旅费用外,报销款为1万元（含）以上的必须采取转账方式对外支付，确需支付现金的，应经分管财务行长审批同意后办理。对于大额报销款支付应按支付结算的相关规定实行授权。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十八条', '支行的费用报销资料应于规定费用结报时上报计划财务部审核。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第二十九条', '本行实行原始票据等费用报销资料总行集中保管，保管部门为总行计划财务部。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第六章 费用报销规定', '第三十条', '各单位提供的原始票据应符合下列规定： （一）原始票据的要素必须齐全，应包括票据的名称，票据日期、填制票据单位名称或者填制人姓名、经济业务具体内容（或提供明细清单）、数量、单价和金额等。如有项目合并开票或多次业务合并一次开票的，需提供清单或说明。 1.原始票据的金额、日期、收款人名称和摘要不得更改,更改的票据无效。原始票据金额应以中文大写和阿拉伯数码同时记载，二者必须一致，二者不一致的票据无效。 2.原始票据出具人所开票据必须与其经营范围、票据摘要所列费用项目相适应。 （二）根据《财政部国家税务总局关于全面推开营业税改征增值税试点的通知》要求，自2016年5月1日起，涉及可抵扣的进项税费用报销需以开票单位开具的增值税专用发票为报销凭证，并在增值税专用发票上分别注明销售额和销项税额。增值税专用发票应达到下列要求： 1. 统一规范填写开票信息。票据抬头必须是“xxx银行股份有限公司”、纳税人税号：xxx、地址：xxx、电话：xxx、开户行及账号：xxx。 2.项目齐全，与实际交易相符。 3.字迹清楚，不得压线、错格。 4.加盖发票专用章。 5.按照增值税纳税义务的发生时间开具。 6.增值税发票管理的其他规定。 对不符合上述要求的增值税专用发票，不得受理。 （三）所有与外部企业单位发生业务往来产生的费用，报销时必须提供国家规定的该行业的印有税务监制章并加盖该企业发票专用章的正式发票作为原始依据，其他票据如收据等一律不得作为报销凭据。 （四）所有与外部行政事业单位发生的收费性项目，报销时必须提供印有财政监制章并加盖该单位财务专用章的行政事业单位结算凭证作为报销依据，不得使用仅具暂收暂付性质的收据作为报销凭证。 （五）发票日期与报销日期原则上不超过6个月，其中，招待费的发票原则上不超过2个月。 （六）报销10000元（含）以上的费用，一律采取转账方式进行结算，转账时不得将应划付收款单位的款项转入收款单位指定的个人账户或通过员工账户支付给原始票据收款人，确需转入个人账户的，应经分管财务的行长审批同意后办理。 对于10000元以下定点采购、修理和消费等费用通过转账方式进行结算。无法转账至收款单位的原则上由个人先行垫付，凭发票报销，转账入个人卡，刷卡垫付的报销时须将个人刷卡回单附转账凭证后作附件。 （七）费用单据为非增值税专用发票的，报销单笔发票金额在 10000元（外省发票5000元）（含）以上的，必须在网上验证，并将验证结果打印附发票后面，报销经办人对发票的真实性负责。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第七章 费用监控与考核', '第三十一条', '计划财务部按季监控费用预算执行情况，对费用总量或单项预算执行偏差较大、支出不均衡的支行按规定进行考核。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第七章 费用监控与考核', '第三十二条', '计划财务部按季对全行费用预算支出进度、结构以及重点项目的变动情况进行分析，并形成书面分析报告。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第七章 费用监控与考核', '第三十三条', '加强成本控制，努力提高成本费用的投入产出效率，严格控制各项费用支出，确保年度费用总量和成本收入比控制在计划之内并符合监管要求。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第八章 罚 则', '第三十四条', '对于费用管理中存在下列行为的，依照本行员工违规违章行为管理规定,对单位责任人及经费员进行处罚： （一）年度终了费用超支，有应付未付费用。 （二）违反财务审查、审批管理规定，采取化整为零方式分解列支，恶意逃避财务审查、审批。 （三）虚列各项费用、乱用会计科目或损益账户、在营业外支出或手续费支出等科目列支费用。 （四）对违规支出各项费用的行为不抵制、不报告。 （五）拒绝、阻挠费用检查，隐瞒真实情况，对检查发现问题未按要求整改的。 （六）其他违反费用管理规定的行为。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第八章 罚 则', '第三十五条', '各单位负责人在履行管理职责过程中，滥用职权， 玩忽职守，徇私舞弊，或者泄漏国家、商业秘密构成犯罪的，依法进行处理。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第九章 附 则', '第三十六条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《财务费用管理办法》', '第九章 附 则', '第三十七条', '本办法自2017年1月1日起实施。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第一章  总  则', '第一条', '为规范本行资产负债管理工作，促进本行经营实现安全性、流动性和效益性的最佳组合，协调短期和长期盈利目标，优化资源配置，提高决策效率，实现股东价值最大化，根据《中华人民共和国商业银行法》、《商业银行资本管理办法（试行）》、《商业银行内部控制指引》和《商业银行流动性风险管理办法（试行）》等法律法规，结合本行实际，制定本管理办法。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第一章  总  则', '第二条', '资产负债管理原则（一）“三性”均衡原则。即安全性、流动性和效益性实现最佳均衡的管理原则。（二）经济增加值持续增长原则。即通过协调管理短期和长期盈利目标，促进本行经济增加值的持续增长。（三）优化资本配置原则。即通过资产负债管理，引导各项业务主动收缩和扩张，促使资本占用向效益好、效率高、风险低的产品、业务和区域倾斜。（四）动态管理原则。即战略性、前瞻性地管理各项资产负债业务，做到事前调控，根据本行业务运行状况和对市场环境因素的判断等，选择业务发展策略和管理业务风险。（五）量化管理原则。即借鉴和引进先进的管理理念、技术手段和工具等对各种可控风险进行逐步量化，科学决策。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第一章  总  则', '第三条', '管理范围和方法资产负债管理范围包括对资产负债表内外所有项目及其衍生产品或工具的整体风险暴露、资本配置、定价管理、盈利能力等进行评估、决策和控制。本办法主要规范资产管理、负债管理、利率管理、资本管理、流动性管理和资产负债比例管理等相关内容。管理方法主要采用比例管理和限额管理（包括缺口限额管理、敞口限额管理、交易限额等）相结合，并辅之以压力测试和应急管理等管理方法和手段。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第二章　组织机构及分工', '第四条', '资产负债管理实行总行统一管理的管理体系，总行设立资产负债管理委员会。委员会设主任委员一名 ，由行长担任，负责主持委员会工作，副主任委员由分管财务副行长担任，成员由行长室其他副行长、信贷管理部、风险管理部、运营管理部、计划财务部负责人担任，委员会成员根据本行资产负债管理工作需要调整。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第二章　组织机构及分工', '第五条', '资产负债管理委员会下设办公室，作为日常办事机构，办公室设在计划财务部。计划财务部在资产负债管理委员会的领导下，负责本行资产负债管理政策传达、综合协调、监控指导、信息收集与整理汇总等，并负责定期向资产负债管理委员会提交资产负债管理分析报告及相关建议。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第二章　组织机构及分工', '第六条', '资产负债管理委员会委员部门负责及时提交涉及本部门的资产负债管理相关工作执行情况报告以及需经资产负债管理委员会讨论审批的事项。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第二章　组织机构及分工', '第七条', '资产负债管理委员会按照报告事项的缓急程度划分，会议分为定期召开和临时召开。例会由主任委员主持召开，如其因故不能出席，可指定其他人员主持召开。遇有重大事项或急办事项可随时召开。会议的一般程序是：听取并审议资产负债管理委员会办公室和各委员部门提交的分析报告和待议决事项，重点研究解决资产负债管理工作中存在的问题。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第八条', '本行资产主要包括存放中央银行款项、存放同业资金、买入返售金融资产、信贷资产、债券投资等生息资产和库存现金、固定资产、无形资产、抵债资产等非生息资产。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第九条', '本行资产业务经营管理遵循以下原则：（一）资本约束原则。全行应按照经济资本管理的原则，不断调整优化资产结构，避免高风险业务对资本的过快侵蚀，确保资本充足率维持在董事会确定的管理目标以上。总行职能部门应积极建设本行经济资本管理体系，逐步实施以经济资本为核心的绩效考核办法，引导全行资产业务健康发展。（二）风险控制原则。本行资产业务拓展应严格防范信用风险、市场风险、操作风险，总行职能部门应加强信贷管理、市场风险管理等信息系统建设，为识别、计量、监测、控制各类风险提供支持。（三）期限匹配原则。资产业务拓展应遵循资产、负债期限结构匹配的原则，以控制期限缺口、维持流动性顺畅。总行职能部门应积极建设流动性管理体系，通过监测、分析与报告、预警与应急等管理流程持续做好流动性管理工作，严密防范流动性风险。（四）盈利性原则。按照先贷款后存款的原则，制定存贷款定价管理规划、实施计划和定价模型，逐步实现存贷款利率系统定价、科学定价。支行在贷款议价时应充分考虑客户风险特征合理定价，促进经营效益增长。同时要努力压缩非生息资产，提高资产整体盈利水平。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第十条', '现金资产包括库存现金、存放中央银行款项、存放系统内款项以及其他形式的现金资产。（一）支行认真做好现金的核算、保管、押运等管理工作，防止发生损失；总行年初合理确定各支行库存现金限额，减少现金占用；按照规定缴存存款准备金、流动性互助资金和清算备付金，在保证支付的前提下减少中央银行超额准备金存款和存放系统内款项，提高资金使用效益。（二）支行应严格执行大额资金往账报备制度，以便总行头寸管理。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第十一条', '同业往来资产包括拆放同业、存放同业资金和买入返售金融资产。（一）同业往来资产业务逐步实行统一授信与限额管理，对同一法人金融机构的综合授信不得超过授信审查委员会核定的限额；融出资金余额不得超过中国人民银行核定的上限。（二）总行职能部门应建立健全各类同业往来资产业务的操作规程，同业业务由总行专营机构实施。总行内审部门应定期或不定期的对同业往来业务进行专项稽核，不断完善内部控制制度，切实防范操作风险。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第十二条', '信贷资产包括法人贷款、自然人贷款、贴现、垫款、贸易融资等项目。（一）按照国家产业政策、货币政策和本行信贷政策的要求，准确把握贷款投向和投放节奏，自觉贯彻本行“支持三农和小微客户”的服务宗旨，不断推进信贷结构转型。（二）严格遵守存贷比管理原则，切实做好存、贷款计划的协调一致，确保全行经营业务始终处于可持续发展的良性轨道中。（三）建立健全贷前调查、贷时审查和贷后检查制度并严格执行，严禁发生超权限、逆程序放贷等违规行为，切实防范信用风险。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第十三条', '投资包括国债、央行票据、政策性金融债券、企业债券等债券投资和对外股权投资项目。（一）本行投资业务由总行集中管理。（二）总行职能部门需切实履行债券资产经营管理职能，加强对宏观经济形势、债券市场等的研究，合理安排期限、品种结构，防范利率风险，争取实现较高的收益。（三）本行应严格控制对外股权投资的范围，不得对工商企业等非金融机构进行股权投资。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第十四条', '总行职能部门要切实履行统筹管理全行固定资产的职责，合理规划固定资产投资计划，科学设计本行固定资产购置的标准化评审体系，不断加大闲置固定资产处置力度，提高固定资产使用效率与效益，严格控制固定资产比例。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第十五条', '支行应加强抵债资产的管理，切实做好抵债资产的保管、价值评估、减值准备计提及处置等相关工作，避免因保管不善导致抵债资产灭失或价值下跌；按照有关处置期限规定及时完成抵债资产的处置工作，争取最大的收益、降低处置损失。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第三章  资产管理', '第十六条', '加强非信贷资产的管理，制订相应的管理制度和风险防范措施；总行职能部门须对各类挂账、垫款、抵债资产、待清理资产制订具体的清收、清理、处置办法和措施。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第四章  负债管理', '第十七条', '本行负债主要包括各项存款、同业往来等付息负债和应付款项等非付息负债。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第四章  负债管理', '第十八条', '支行需努力控制资金成本，严禁高息揽存等非法行为。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第四章  负债管理', '第十九条', '各项存款是指单位、居民个人存入本行的短期、定期资金。（一）支行须牢固树立“存款立行”的指导思想，始终紧抓存款这一工作主线，加大市场拓展力度；总行职能部门要不断加快金融创新步伐，丰富金融服务产品体系，促进存款增长。全行存款工作的目标是确保市场份额的持续稳定上升。（二）全行应积极开展主动负债管理，根据资产业务需要安排存款结构，不断优化期限结构，保证流动性监管指标维持在合理水平。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第四章  负债管理', '第二十条', '同业往来负债包括向中央银行借款、同业借款、同业存放资金、卖出回购金融资产等项目。总行职能部门应将同业往来负债作为辅助的资金来源加以管理，努力拓展同业往来业务，促进经营效益的增长。同时应合理安排同业负债期限结构，避免对流动性管理产生不利影响。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第四章  负债管理', '第二十一条', '应付款项包括应付职工薪酬、应交税费、应付利息等项目。支行应准确核算各类应付款项，确保利润核算真实、准确，应付职工薪酬应按总行职能部门核定的人事费用总额、按序时进度均衡列支；应交税费应按照相关税收法规的规定和本行财务核算结果准确提取；应付利息应根据负债品种、期限和利率结构足额提取，维持合理、充足的存款利息备付。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十二条', '利率管理的目标是为主动适应利率市场化改革要求，有效发挥利率杠杆对本行资产负债管理的导向作用，加强本行利率管理工作，提高本行利率市场化进程中防范市场风险的管理能力。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十三条', '利率定价主要遵循以下原则：（一）依法合规。利率的确定必须符合中国人民银行的有关规定，实际利率水平须在中国人民银行规定的业务区间内。（二）风险与收益匹配。利率定价须坚持价值导向，遵循收益与风险匹配的原则，全面测算各项成本和收益，科学准确的运用价格杠杆，对不同风险状况的客户进行定价。（三）以客户为中心。利率定价须坚持客户关系定价战略，统筹考虑基于客户关系的整体风险收益。（四）贴近市场。利率定价必须充分考虑市场资金供求和市场竞争能力，并结合自身的业务战略确定定价政策。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十四条', '利率管理的范围包括本外币自营存款、自营贷款、同业业务等。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十五条', '总行职能部门应建立健全本行内部资金转移定价系统（FTP），为全行控制资金成本提供明确的政策导向。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十六条', '具体业务的利率定价须综合考虑资金成本（收益）、运营成本、信用风险成本、资本占用成本、税负成本以及目标利润等相关因素综合确定。（一）资金成本（收益）：指业务的FTP价格。（二）运营成本：包括业务所发生的直接费用水平和分摊在业务上的间接费用率。（三）风险成本：指业务所面临的信用风险、操作风险、市场风险等成本。（四）资本成本：指业务占用的资本的价格，是风险加权资产比例、资本转换系数、资本成本率的乘积。（五）税负成本：业务所需缴纳的营业税及附加。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十七条', '对于执行浮动管理利率的人民币自营存、贷款业务，其利率按中国人民银行和总行业务部门的相关规定执行。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十八条', '人民币票据业务的指导利率由总行计划财务部公布，金融市场部在总行指导利率的基础上确定执行利率，如遇特殊情况需低于指导利率的，按总行执行优惠利率审批表的规定进行审批。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第二十九条', '人民币同业存款利率按当日相同期限档次的上海银行间同业拆放利率（以下简称Shibor）加浮动点来确定，若Shibor没有与之相对应的期限档次，则采用相邻期限档次利率加浮动点来确定。浮动点根据本行资金状况、市场收益情况、市场走势等因素确定。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第三十条', '人民币同业定期存款不得部分提前支取，需全额提前支取时，按照双方商定的利率计息。人民币同业定期存款到期不办理自动转存，到期日遇节假日顺延。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第三十一条', '内部资金利率是引导本行内部资金流动、优化资产负债结构的重要手段，本行内部资金利率的制定原则为：以配合本行发展战略的实现、实施本行的资产负债管理政策为主要目标，同时充分考虑市场利率走势。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第五章  利率管理', '第三十二条', '内部资金价格具有调整系统内利益分配的作用，总行制定的内部资金价格应促进各单位共同发展、公平分享利润，最大限度地调动积极性。在确定辖内内部资金价格时，不以盈利为目的，尽量做到收支平衡。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第三十三条', '资本管理的基本原则是：规模适度，配置合理；成本可控，收益约束。（一）“规模适度，配置合理”是指本行资产规模的增长和新业务的开展以资本规模为基础，既定资本优先支持高收益、低风险业务的发展，提高资本使用效益。（二）“成本可控，收益约束”是指在资本筹集过程中，充分树立资本成本意识和资本收益约束观念，通过对各种筹资方案进行当期筹资成本、未来资本收益的综合性分析测算，择优确定筹资方式、筹资规模和结构。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第三十四条', '资本管理的近期目标是在满足监管资本要求的基础上，支持本行业务发展，实现风险资产收益率最大化目标。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第三十五条', '资本管理的远期目标是在本行范围内全面推行经济资本管理模式，建立以经济资本和经济增加值为核心的资本分配和绩效考核机制。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第三十六条', '资本管理的主要内容包括：（一）确定资本发展规划、制定资本融资策略、选择和管理资本工具组合等。（二）管理资本风险。一是实施资本充足率管理，确保符合监管要求，包括资本充足率、一级资本充足率、核心一级资本充足率目标的确定、计量、分析、报告和披露；二是借助风险管理模型，为信用风险、市场风险和操作风险合理配置资本。（三）优化资本配置，提高经营效益。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第三十七条', '资本充足率的计算必须严格按照《商业银行资本管理办法（试行）》和有关统计制度执行。本行资本充足率不低于10.5%，一级资本充足率不低于8.5%，核心一级资本充足率不低于7.5%。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第三十八条', '在会计年度终了后四个月内，经董事会批准，及时、完整地披露本行资本充足率信息。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第三十九条', '资本充足率管理通过对风险加权资产（其中，市场风险资本和操作风险资本分别乘以12.5折算成风险加权资产）采取“总量控制、限额管理、有偿调剂”的方式执行。各条线不得突破风险加权资产限额或经济资本限额，通过优化资本配置，提高资本使用效率。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第六章  资本管理', '第四十条', '资本使用效率主要通过风险加权资产收益率或风险调整资本收益率(RAROC)以及经济增加值(EVA)等指标来衡量，以此来指导产品定价和改进资本管理和融资策略，实现股本回报率持续增长的经营目标。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第七章  流动性风险管理', '第四十一条', '流动性风险管理是对各分支机构、各业务条线的流动性风险进行有效识别、计量、监测和控制, 确保其以合理成本及时获得充足资金，用于偿付到期债务、履行其他支付义务和满足正常业务开展。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第七章  流动性风险管理', '第四十二条', '流动性风险管理分为日常管理和应急管理。日常管理采取存量与流量管理相结合的方法；应急管理通过制定和实施应急方案来实现。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第七章  流动性风险管理', '第四十三条', '流动性存量管理主要包括对流动性风险监管指标和借款限额的管理。（一）流动性风险监管指标包括流动性覆盖率和流动性比例。流动性比例为流动性资产余额与流动性负债余额之比；流动性缺口率为90天内表内外流动性缺口与90天内到期表内外流动性资产之比；核心负债依存度为核心负债与负债总额之比；净稳定资金比率为可用的稳定资金与业务所需的稳定资金之比。（二）借款限额管理。借款限额由总行统一管理，通过有效控制本行对外部借款资金的依存度，来防范流动性风险。1.借款限额管理的范围是在本行范围内办理的所有同业借入资金，包括：同业拆入资金、正回购资金、人民银行再贷款和再贴现资金等，以及本行负有承诺兑付责任的其他业务。2.借款限额管理主要是对同业拆入比例、同业负债依存度、最大十家同业融资比例等指标设定限额，并根据本行资产负债运行状况和市场资金形势进行定期调整。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第七章  流动性风险管理', '第四十四条', '流动性流量管理主要包括对特定时段最大累计现金流出（简称“MCO”）和流动性缺口进行限额管理。本行MCO限额依据核定的借款限额和静态流动性缺口分析设定；流动性缺口限额参照历史经验数据设定。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第七章  流动性风险管理', '第四十五条', '流动性监测与监控。（一）计划财务部对流动性限额或指标实行“按日监测，及时调控，定期总结”。（二）实施大额资金进出报告制度。大额资金进出报告制度是指对流动性管理影响较大的资金进出所规定的报告制度。总行根据资金备付能力，明确规定大额资金进出报告的最低额度和报告频率。（三）实施压力测试与报告制度。本行制定统一的流动性压力测试情景和假设，并动态调整，压力测试情景和假设的设定和重大调整由资产负债管理委员会审定。计划财务部负责对本行的流动性压力进行检测，并定期发布压力测试报告。各职能部门要根据压力测试报告，改善其流动性储备。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第七章  流动性风险管理', '第四十六条', '流动性应急管理。本行应建立完整的流动性应急机制和方案，应急机制和方案主要内容包括：（一）应急管理团队、沟通机制和工作流程。（二）应急方案类型。按照流动性压力程度从高到低，应急方案分为红色方案、橙色方案和黄色方案三个级别。红色方案是为预防和缓解极端情况下的流动性压力所制定的；橙色方案是为预防和改善流动性持续走低状况所制定的，流动性持续走低以流动性风险指标的实际值持续突破其监控值为衡量标准；黄色方案是为改善间断性流动性较差状况所制定的。（三）应急方案管理的主要内容。1.评估近期累计最大现金流出缺口；2.对目前资产变现能力、市场融资能力、获得央行支持的能力和存款资金筹集能力等进行分析和评估；3.通过以上评估和对今后的流动性压力进行假设，分别制定各级方案的预防和应急处理措施，包括：弥补缺口的各项措施和确定的资金来源及金额，阶段性流动性缺口的弥补方法和手段，对风险损失所采取的对冲策略和业务安排等。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第八章  资产负债比例管理', '第四十七条', '本行整体监控指标体系：为防范经营风险，建立以资本充足、资产质量、流动性和盈利能力为核心的指标体系，确定以下二十五项指标为本行整体资产负债比例管理的监控指标，以全行为单位统一考核。（一）资本状况指标1.资本充足率，大于等于10.5%；2.一级资本充足率，大于等于8.5%；3.核心资本充足率，大于等于7.5%；4.杠杆率，大于等于4%。资本状况指标相关公式：1.资本充足率=资本净额÷加权风险资产总额×100%；2.一级资本充足率=一级资本净额÷加权风险资产总额×100%；3.核心资本充足率=核心资本净额÷加权风险资产总额×100%；4.杠杆率=一级资本净额÷（表内总资产+表外业务规模+衍生产品-一级资本扣减项）×100%。（二）信用风险指标1.不良贷款率，小于等于5%；2.不良资产率，小于等于4%；3.单一客户贷款集中度，小于等于10%；4.单一集团客户授信集中度，小于等于15%；5.全部关联度，小于等于50%。信用风险（资产质量）相关公式：1.不良贷款率=五级分类后三类贷款余额÷各项贷款余额×100%；2.不良资产率=不良资产额÷资产总额×100%；3．单一客户贷款集中度=最大一家客户贷款总额÷资本净额×100%；4.单一集团客户授信集中度=最大一家集团客户授信净额÷资本净额×100%；5.全部关联度=全部关联方授信总额÷资本净额×100%。（三）拨备指标1.不良贷款拨备覆盖率，大于等于150%；2.贷款拨备比，大于等于2.5%。拨备指标相关公式：1.不良贷款拨备覆盖率=贷款损失准备÷不良贷款余额×100%；2.贷款拨备比=贷款损失准备÷各项贷款余额×100%。（四）流动性指标1.存贷款比例，小于等于75%；2.流动性比例，大于等于25%；3.净稳定融资比例，大于等于100%；4.核心负债依存度，大于等于60%；5.流动性缺口率，大于等于-10%；6.同业市场负债依存度，不高于20%。流动性指标相关公式：1.存贷款比例=各项贷款余额÷各项存款余额×100%；2.流动性比例=流动性资产÷流动性负债×100%；3.净稳定融资比例=可用的稳定资金÷业务所需的稳定资金×100%；4.核心负债依存度=（剩余期限三个月以上的定期存款+已发行债券+活期存款的50%）÷负债总额×100%；5.流动性缺口率=（90天内到期的表内外资产-90天内到期的表内外负债）÷90天内到期表内外资产；6.同业市场负债依存度=同业市场负债÷各项存款余额×100%。（五）盈利能力指标1.资产利润率，大于等于0.6%；2.资本利润率，大于等于11%；3.成本收入比，小于等于45%；盈利能力指标相关公式：1.资产利润率=当期税后利润÷资产平均余额×折年系数×100%；2.资本利润率=当期税后利润÷所有者权益平均余额×折年系数×100%； 3.成本收入比=（营业支出-营业税金及附加）÷营业净收入×100%；营业净收入=（贷款利息收入-存款利息支出）+金融机构往来利息净收入+其他利息净收入+手续费及佣金净收入+其他业务净收入+投资收益。（六）业务发展与经营效益指标1.净收入费用率，小于35%；2.人均存款额，大于等于3000万元；3.人均金融增加值，大于等于90万元；4.净资产占比，10%以上；5.每股净资产，2.5元以上。业务发展与经营效益指标相关公式：1.净收入费用率=业务及管理费÷净收入×100%；2.人均存款额=年末各项存款日均余额÷员工总人数按季平均数；3.人均金融增加值=金融增加值÷员工总人数；4.净资产占比=（净资产÷年末应用资本底线之后的风险加权资产余额）×100%；5.每股净资产=（年末资产-本年度溢价发行收入总额）÷股本金12个月月均余额。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第八章  资产负债比例管理', '第四十八条', '支行资产负债比例管理指标：四级分类不良贷款、五级分类不良贷款、涉农贷款“两个高于”、小微企业贷款“两个不低于”、抵质押贷款比例、不良贷款清收等指标。资产负债管理委员会每年可对支行资产负债比例管理指标项目和评分方法进行调整。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第九章  监控管理措施', '第四十九条', '资产负债管理监控由总行统一领导，统一口径，按月或按季监控。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第九章  监控管理措施', '第五十条', '监控管理的程序：本行整体监控指标由总行资产负债管理委员会负责，各支行管理指标由总行职能部门管理。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第九章  监控管理措施', '第五十一条', '总行相关职能部门要按照总行制定的比例管理指标口径和统计制度认真监控本行的指标执行情况，真正起到预警和防范经营风险的作用。达到预警值时应向资产负债管理委员会主要领导报告，并及时采取有效措施限期改进；对触发容忍底线的，各相关职能部门将进行联动处理，采取上收业务审批权限、暂停办理部分业务等措施进行调控，确保各项业务科学发展。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第九章  监控管理措施', '第五十二条', '计划财务部门按季对资产负债比例管理指标执行提交资产负债管理委员会。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第十章  附  则', '第五十三条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《资产负债管理制度（暂行）》', '第十章  附  则', '第五十四条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第一章  总  则', '第一条', '为规范本行资产负债管理委员会（以下简称“资管会”）的运作程序，保证资产负债管理委员会依法独立、规范、有效地行使职权，根据《中华人民共和国商业银行法》及有关法律法规规定，制定本议事规则。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第一章  总  则', '第二条', '资管会是本行行长室领导下的资产负债管理决策机构，接受本行董事会、监事会的检查和监督。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第二章  资管会组成', '第三条', '委员会设主任委员一名，由行长担任，负责主持委员会工作,副主任委员由分管财务副行长担任，成员由行长室其他副行长、信贷管理部、风险管理部、运营管理部、计划财务部负责人担任。资管会应邀请董事会、监事会、审计部派员列席会议。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第二章  资管会组成', '第四条', '资管会的办事机构设在总行计划财务部，计划财务部负责人为资管会办公室主任，由其办理提交资管会审议、审批事项的受理、初审等事宜。资管会指定计划财务部一名工作人员为办公室成员，负责会议的准备工作及会议内容的记录，负责资管会审议事项的整理、资料的收集、入档等具体事务。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第二章  资管会组成', '第五条', '委员会委员工作变动时，委员职务随之变更。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第三章  资管会职责', '第六条', '资管会履行以下主要职责：（一）负责制定全行的发展战略规划和年度经营计划；（二）审议全行资产负债管理目标和政策；（三）审议定价管理制度和政策；（四）负责全行资产负债比例管理工作、督促防范经营风险；（五）负责资本充足率、风险加权资产、存贷款比例、流动性比例、存款付息率、贷款收息率、拆入资金比例、拆出资金比例、中长期贷款比例、存款平均增长率等各项指标的测算，分析和监控，并将具体上述指标的日常管理，监测落实到各职能部门；（六）对有关资产负债的重大事宜提出决策建议；（七）监督、指导全行资产负债运行状况；（八）审议内部资金转移定价制度和政策，计价范围、定价规则、转移曲线、调整项等；（九）资产负债管理工作中的其他重要事项；（十）行长室授权的其他事宜。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第四章  管理内容', '第七条', '资产管理包括资产风险管理、资产流动性管理和资产盈利性管理。（一）资产风险管理。要求从流动性风险、信用风险、利率风险、和国家风险等方面进行认真分析，同时遵循对称原则、分散原则，转移原则、平行原则，择优原则和制约原则等管理原则。（二）资产流动性管理。利用存贷比率，流动比率等指标对资产流动性进行衡量，通过保持足够的准备资产，合理安排资产期限组合，以及对一些原本流动性较差的资产证券化和市场出售等方式，在总体上提高盈利资产变现的可能性，从而增加流动性；定期加强对流动性的压力测试。（三）资产盈利性管理。在保证安全性和流动性的前提下，合理确定盈利资产的数量和组合，通过机会成本分析，进行盈利机会比较，选择最佳的盈利机会，并利用资产利润率，资本利润率、净息差，收入利润率等指标，具体衡量和分析盈利水平。    第八条  负债管理包括存款负债管理、非存款负债管理和负债成本管理。存款负债管理。通过对存款结构的分析，合理确定活期存款、定期存款、储蓄存款的比例配置；同时努力开展存款服务创新、满足客户多方面、多层次的需求。非存款负债管理。通过同业存放，同业拆借、转贴现、再贴现、向中央银行借款，向回购协议借款等非存款渠道组织资金，合理扩大负债规模，增加可运作资金量。负债成本管理。通过对利息支出和费用支出的分析，利用平均成本率和边际成本率指标分析，衡量负债成本水平的高低，以评价本行的经营状况并决定资产价格。     第九条  资本管理。在保证资本充足率达到银行业监督管理机构所规定的标准的前提下，通过对资本结构的合理选择，实现资本成本最小和收益最大化。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第十四条', '按照报告事项的缓急程度划分，资管会会议分为定期召开和临时召开。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第十五条', '定期召开的资管会会议每季至少召开一次。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第十六条', '在下列情况下，可召开临时的资管会会议：（一）遇有重大或紧急事项时；（二）行长室或召集人认为必要时。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第十七条', '资管会会议由委员会主任召集并主持。委员会主任因特殊原因不能出席时，可委托委员会成员召集并主持。对提请审议的工作事项，提交部门应提前3天将有关资料送资管会办公室，经规范性审查后，由资管会办公室提前2天报送各位委员邮箱。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第十八条', '委员会应当由委员本人出席，如本人因故不能出席的，应及时向主任委员请假，经主任委员同意，方可由他人代为列席，列席人员无表决权。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第十九条', '委员会召开时邀请董事会、监事会、审计部列席,同时可通知相关部室人员列席。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十条', '委员会会议应由2/3（不含）以上委员出席方可召开。决议采用投票方式，每名委员有一票表决权。会议通过的决议必须经到会委员2/3（含）以上同意。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十一条', '议事程序（一）受理。凡提交委员会审议的事项，相关部门必须事先认真调查，提出初步意见，形成书面材料，经相关部室审查后，至少提前三个工作日提交至委员会办公室。 （二）审查。委员会办公室对提交的报告进行初审，确保提交材料的完整、合规。符合要求的，在会议前一个工作日送达委员会各成员审阅；不符合规范要求的，要求相关部门补充完善。（三）会议准备。由委员会办公室提请会议主持人及时召开会议，并提前做好会议资料的准备，通知各委员、列席人员按时参加会议。（四）审议。由相关部门负责人汇报会办事项，参加会议的委员就会办事项提出问题及发表意见，需要进行记名投票表决的事项,不得弃权。会议统计投票情况，当场宣布投票结果。（五）批复。委员会办公室根据资管会签署意见，出具审批通知单给申报单位，并向董、监事会备案。（六）落实执行。对于会办同意并经备案的事项，相关部室要迅速落实到位，最迟于会办同意日的次日传递会办同意通知，并取得回执，以明确责任。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十二条', '特殊事项对须由资管会审查批准的事项，因特殊情况必须立即决定或办理的，可由委员会主任处理决定，但事后必须由资管会补议。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十三条', '议事必须坚持公开、公平、公正、民主的原则，确保决策的科学性、准确性。凡超过委员会权限的，经委员会会办后上报董事会相关委员会审议。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十四条', '委员会会议由委员会办公室专人记录，会议记录日常由资产负债管理委员会办公室保存，年度终了，移交本行档案室归档保管。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十五条', '委员会会议记录包括以下内容：（一）会议召开的日期、地点和召集人姓名；（二）出席会议委员和列席人员的姓名；（三）会议议程；（四）参会人员发言要点；（五）每一决议事项的讨论表决结果；（六）记录人姓名；（七）出席会议人员签名。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十六条', '委员会委员对会议的决议承担责任，但经证明在表决时曾表明异议并记载于会议记录的，该委员可以免除责任。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第六章  议事规则', '第二十七条', '资产负债管理委员会作出的决策需按相关流程要求办理。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第七章  附  则', '第二十八条', '本议事规则由本行资产负债管理委员会办公室负责解释和修订。'),
(NULL, '计划财务部', '《资产负债管理委员会议事规则》', '第七章  附  则', '第二十九条', '本议事规则自发文之日起施行。'),
(NULL, '计划财务部', '《资本管理办法》', '第一章  总则', '第一条', '为进一步提升本行资本管理的科学性和有效性，全面提高资本抵御风险和支持经营管理的能力，根据《商业银行公司治理指引》、《商业银行资本管理办法（试行）》（以下简称《资本办法》）等法律法规、行政规章以及《xxx银行股份有限公司章程》的规定，制定本办法。'),
(NULL, '计划财务部', '《资本管理办法》', '第一章  总则', '第二条', '本行资本应抵御所面临风险，包括个体风险和系统性风险。'),
(NULL, '计划财务部', '《资本管理办法》', '第一章  总则', '第三条', '本办法所称资本充足率，是指本行持有的符合《资本办法》规定的资本与风险加权资产之间的比率。 一级资本充足率，是指本行持有的符合《资本办法》规定的一级资本与风险加权资产之间的比率。 核心一级资本充足率，是指本行持有的符合《资本办法》规定的核心一级资本与风险加权资产之间的比率。'),
(NULL, '计划财务部', '《资本管理办法》', '第一章  总则', '第四条', '本行按照《资本办法》规定计算并表和未并表的资本充足率，本行资本充足率计算建立在充分计提贷款损失准备等各项减值准备的基础之上。'),
(NULL, '计划财务部', '《资本管理办法》', '第一章  总则', '第五条', '本行按照《资本办法》的规定建立全面风险管理架构和内部资本充足评估程序。'),
(NULL, '计划财务部', '《资本管理办法》', '第一章  总则', '第六条', '本行按照《资本办法》的规定披露资本充足率信息。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第七条', '本行董事会承担资本管理的首要责任，履行以下职责： （一）设定与本行发展战略和外部环境相适应的风险偏好和资本充足目标，审批内部资本充足评估程序，确保资本充分覆盖主要风险； （二）审批资本管理制度，确保资本管理政策和控制措施有效； （三）监督内部资本充足评估程序的全面性、前瞻性和有效性； （四）审批并监督实施资本规划，满足本行持续经营和应急性资本补充需要； （五）至少每年一次审批资本充足率管理计划，审议资本充足率管理报告及内部资本充足评估报告，听取对资本充足率管理和内部资本充足评估程序执行情况的审计报告； （六）审批资本充足率信息披露政策、程序和内容，并保证披露信息的真实、准确和完整； （七）确保本行有足够的资源，能够独立、有效地开展资本管理工作。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第八条', '本行高级管理层负责根据业务战略和风险偏好组织实施资本管理工作，确保资本与业务发展和风险水平相适应，落实各项监控措施。具体履行以下职责：（一）制定并组织执行资本管理的规章制度； （二）制定并组织实施内部资本充足评估程序，明确相关部门的职责分工，建立健全评估框架、流程和管理制度，确保与本行全面风险管理、资本计量及分配等保持一致； （三）制定和组织实施资本规划和资本充足率管理计划； （四）定期和不定期评估资本充足率，向董事会报告资本充足率水平、资本充足率管理情况和内部资本充足评估结果； （五）组织开展压力测试，参与压力测试目标、方案及重要假设的确定，推动压力测试结果在风险评估和资本规划中的运用，确保资本应急补充机制的有效性； （六）组织内部资本充足评估信息管理系统的开发和维护工作，确保信息管理系统能及时、准确提供评估所需信息； （七）组织制定资本充足率信息披露政策、披露程序和披露内容，及时将拟披露的信息提交董事会审议； （八）充分理解压力条件下本行所面临的风险及风险间的相互作用、资本工具吸收损失和支持业务持续运营的能力，并判断资本管理目标、资本补充政策安排和应对措施的合理性。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第九条', '本行监事会负责对董事会及高级管理层在资本管理中的履职情况进行监督评价，并至少每年一次向股东大会报告董事会及高级管理层的履职情况。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第十条', '本行计划财务部为资本管理部门，主要负责： （一）根据高级管理层授权拟定并适时更新本行资本管理政策和规章制度，统筹推进资本管理体系的建设完善； （二）制订资本总量、结构和质量管理计划，编制并实施资本规划和资本充足率管理计划，向高级管理层报告资本规划和资本充足率管理计划执行情况； （三）持续监控并定期测算资本充足率水平，组织开展资本充足率压力测试，动态监测资本规划、资本配置的执行情况，定期编制资本监控报告； （四）组织建立内部资本计量、配置和风险调整资本收益的评价管理体系； （五）牵头组织实施本行内部资本充足评估程序，拟定并适时更新相关制度规范和工作流程，结合压力测试结果拟定资本充足率目标，组织编制内部资本充足评估报告、相关监管报送文件，定期提交高级管理层及董事会审议； （六）建立资本应急补充机制，组织实施资本筹集，并向高级管理层报告资本应急补充机制的执行情况； （七）牵头组织实施资本充足率信息披露工作，拟定并适时更新相关政策，建立并完善披露机制和程序，规范披露内容，组织编制披露材料； （八）牵头组织建立用于资本计量和管理的信息管理系统，确保系统能够及时和准确的提供资本、风险加权资产和资本充足率计量与管理、资本充足率水平监测、内部资本充足评估及资本充足率信息披露所需信息。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第十一条', '本行风险管理部牵头负责本行全面风险管理工作，拟定全面风险管理规程，明确全行风险管理职责分工，统筹推进全面风险管理体系的建设完善，以及各类风险管控政策制度和流程工具的建立健全；拟定并适时更新相关管理政策和制度，组织开展各类风险压力测试。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第十二条', '本行审计部负责资本管理的检查和评价等工作： （一）检查资本管理的治理结构和相关部门职责履行情况，评估相关人员的专业技能及资源充分性； （二）至少每年一次检查内部资本充足评估程序相关政策和执行情况； （三）至少每年一次评估资本规划的执行情况； （四）至少每年一次评估资本充足率管理计划的执行情况；（五）检查资本管理的信息系统和数据管理的合规性和有效性； （六）向高级管理层和董事会提交资本管理相关的审计报告，报告内容包括但不限于资本充足率管理、内部资本充足评估程序执行情况、资本计量高级方法管理等方面的审计情况。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第十三条', '本行董事会办公室参与资本充足率信息披露相关工作，及时将资本充足率信息披露相关议案提交董事会审批，并履行后续公开披露程序。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第十四条', '本行人力资源部负责制定合理的薪酬政策，确保薪酬水平、结构和发放时间安排与风险大小和风险存续期一致，反映风险调整后的长期收益水平，防止过度承担风险，维护财务稳健性。'),
(NULL, '计划财务部', '《资本管理办法》', '第二章  治理结构', '第十五条', '各相关部门根据需要参与资本管理的相关工作，包括资本和风险计量体系的建设、风险计量结果的应用、内部资本充足评估程序实施、资本规划与筹集、资本考核与分配、资本监测与报告，以及资本充足率信息披露等。'),
(NULL, '计划财务部', '《资本管理办法》', '第三章  资本充足率计量及资本要求', '第十六条', '本行总资本包括核心一级资本、其他一级资本和二级资本。核心一级资本包括：普通股、资本公积、盈余公积、一般风险准备、未分配利润、可计入核心一级资本的少数股东资本；其他一级资本包括：其他一级资本工具及其溢价、可计入其他一级资本的少数股东资本；二级资本包括：二级资本工具及其溢价、超额贷款损失准备、可计入二级资本的少数股东资本。本行发行的资本工具应符合银监会规定的合格标准。'),
(NULL, '计划财务部', '《资本管理办法》', '第三章  资本充足率计量及资本要求', '第十七条', '本行风险加权资产包括信用风险加权资产、市场风险加权资产和操作风险加权资产。'),
(NULL, '计划财务部', '《资本管理办法》', '第三章  资本充足率计量及资本要求', '第十八条', '本行依据相关规定开展资本和风险加权资产计量工作。'),
(NULL, '计划财务部', '《资本管理办法》', '第三章  资本充足率计量及资本要求', '第十九条', '本行资本充足率的计算范围包括本行所有分支机构。'),
(NULL, '计划财务部', '《资本管理办法》', '第三章  资本充足率计量及资本要求', '第二十条', '本行资本充足率应满足监管要求，包括最低资本要求、储备资本和逆周期资本要求以及第二支柱资本要求。'),
(NULL, '计划财务部', '《资本管理办法》', '第三章  资本充足率计量及资本要求', '第二十一条', '本行各级资本充足率不得低于如下最低要求：（一）核心一级资本充足率不得低于10%；（二）一级资本充足率不得低于10%；（三）资本充足率不得低于11.6%。'),
(NULL, '计划财务部', '《资本管理办法》', '第三章  资本充足率计量及资本要求', '第二十二条', '除上述资本充足率监管要求外，本行还应满足杠杆率监管要求。杠杆率的计算规则和监督管理按照银监会《商业银行杠杆率管理办法》执行。'),
(NULL, '计划财务部', '《资本管理办法》', '第四章  内部资本充足评估程序', '第二十三条', '本办法所称内部资本充足评估，又称内部资本充足评估程序，简称 ICAAP（Internal Capital Adequacy Assessment Process），是指为确保本行资本水平能够有效抵御所面临的主要风险，满足业务发展战略的需要，对资本充足状况进行评估的一整套程序和机制。'),
(NULL, '计划财务部', '《资本管理办法》', '第四章  内部资本充足评估程序', '第二十四条', '本行应当建立完善的风险管理框架和稳健的内部资本充足评估程序，明确风险治理结构，审慎评估各类风险、资本充足水平和资本质量，制定资本规划和资本充足率管理计划，确保资本能够充分抵御所面临的风险，满足业务发展的需要。'),
(NULL, '计划财务部', '《资本管理办法》', '第四章  内部资本充足评估程序', '第二十五条', '本行内部资本充足评估程序的目标是： （一）确保主要风险得到充分识别、计量或评估、监测和报告； （二）确保资本水平与风险偏好和风险管理水平相适应； （三）确保资本规划与本行经营状况、风险变化趋势和长期发展战略相匹配。'),
(NULL, '计划财务部', '《资本管理办法》', '第四章  内部资本充足评估程序', '第二十六条', '压力测试是本行内部资本充足评估程序的重要组成部分，本行将结合压力测试结果确定内部资本充足率目标。压力测试应覆盖各业务条线的主要风险，并充分考虑经济周期对资本充足率的影响。'),
(NULL, '计划财务部', '《资本管理办法》', '第四章  内部资本充足评估程序', '第二十七条', '内部资本充足评估程序是本行内部管理和决策的组成部分，本行将内部资本充足评估结果运用于资本预算与分配、授信决策和战略规划。'),
(NULL, '计划财务部', '《资本管理办法》', '第四章  内部资本充足评估程序', '第二十八条', '本行原则上每年实施一次内部资本充足评估程序。当经营情况、风险状况和外部环境发生重大变化时，及时进行调整和更新。'),
(NULL, '计划财务部', '《资本管理办法》', '第四章  内部资本充足评估程序', '第二十九条', '本行依据监管规定和本办法制定内部资本充足评估程序实施细则，并开展内部资本充足评估工作。'),
(NULL, '计划财务部', '《资本管理办法》', '第五章 资本规划', '第三十条', '本行资本规划是关于资本消耗和资本补充的中长期安排方案。基本指导思想是以充足的资本支撑业务发展，以合理的资本补充机制满足资本需求，以科学的资本结构提升股东回报，促进全行业务规模、质量和效益的协调发展。'),
(NULL, '计划财务部', '《资本管理办法》', '第五章 资本规划', '第三十一条', '资本规划的主要发展目标： （一）稳健的资本充足率水平。本行资本充足率目标的确定，应确保满足内部业务发展需求和外部监管要求。 （二）不断优化的资本结构。在资本管理过程中，积极优化并保持稳定的资本结构，提高抵御风险能力。 （三）不断提升的资本运营效率。本行在业务发展过程中，应充分、高效利用资本杠杆功能，加快业务转型发展，提高资本配置效率。'),
(NULL, '计划财务部', '《资本管理办法》', '第五章 资本规划', '第三十二条', '制定资本规划应考虑的主要因素： （一）综合考虑风险评估结果、未来资本需求、资本监管要求和资本可获得性，确保资本水平持续满足监管要求。资本规划应至少设定内部资本充足率三年目标； （二）确保目标资本水平与业务发展战略、风险偏好、风险管理水平和外部经营环境相适应，兼顾短期和长期资本需求，并考虑各种资本补充来源的长期可持续性； （三）审慎估计资产质量、利润增长及资本市场的波动性，充分考虑对银行资本水平可能产生重大负面影响的因素，包括或有风险暴露，严重且长期的市场衰退，以及突破风险承受能力的其它事件； （四）优先考虑补充核心一级资本，增强内部资本积累能力，完善资本结构，提高资本质量； （五）当本行资本充足率接近或实际低于预期目标时，将优先考虑通过优化资产结构、提高盈利能力、调整股息分派政策、适时调节风险资产增长节奏等措施提高资本充足率； （六）根据综合评估情况，合理确定资本消耗和资本补充方案。'),
(NULL, '计划财务部', '《资本管理办法》', '第五章 资本规划', '第三十三条', '本行资本补充坚持内源性资本积累为主、外源性融资为辅的原则。'),
(NULL, '计划财务部', '《资本管理办法》', '第五章 资本规划', '第三十四条', '内源性资本补充的主要措施包括： （一）不断提升盈利能力，加强自身积累；（二）合理确定分红比率；（三）合理确定计提拨备。'),
(NULL, '计划财务部', '《资本管理办法》', '第五章 资本规划', '第三十五条', '外源性资本补充的主要工具包括： （一）一级资本的补充。主要方式包括：公开增发、定向增发、配股及其他合格一级资本工具等；（二）二级资本的补充。主要方式是发行符合《资本办法》要求的二级资本工具。 第三十六条  本行通过资本充足率压力测试，测算不同压力条件下的资本需求和资本可获得性，并制定资本应急预案以满足计划外的资本需求，确保本行具备充足资本应对不利的市场条件变化。'),
(NULL, '计划财务部', '《资本管理办法》', '第五章 资本规划', '第三十七条', '对于重度压力测试结果，资本应急预案中应明确相应的资本补充政策安排和应对措施，并充分考虑融资市场流动性变化，合理设计资本补充渠道。资本应急预案包括但不限于紧急筹资的成本分析和可行性分析、限制资本占用程度高的业务发展、采用风险缓释措施等。'),
(NULL, '计划财务部', '《资本管理办法》', '第六章  资本分配与考核', '第三十八条', '本行资本分配通过经济资本分配的方式实现，经济资本分配是指在充分考虑缓冲、应急情况后，将经济资本可分配限额向业务单位和业务条线分配的过程。'),
(NULL, '计划财务部', '《资本管理办法》', '第六章  资本分配与考核', '第三十九条', '本行经济资本分配应遵循以下原则： （一）总额平衡原则。总体上应确保资本供给量与资本需求量动态平衡。分配过程中，预留一部分资本作为二次分配调整，或用于因监管政策调整等因素的影响导致资本需求总额超过分配额度。 （二）与业务计划匹配原则。资本分配计划为全面预算的重要部分，支持业务发展，并与业务计划衔接。 （三）效率优先原则。资本分配优先向盈利能力强的地区、业务倾斜，不断提高资本资源配置效率。'),
(NULL, '计划财务部', '《资本管理办法》', '第六章  资本分配与考核', '第四十条', '本行经济资本应用与考核遵循以下规定： （一）建立以经济利润（EVA）、经济资本回报率(RAROC)为核心指标的经济资本应用体系。 （二）信贷决策应用。加强对信贷业务风险及回报的平衡分析，将产品和客户的经济利润（EVA）、经济资本回报率（RAROC）作为信贷评估、授信、审批的重要指标和决策依据。 （三）定价管理应用。将经济资本回报率(RAROC)要求运用至表内外授信业务的盈利性分析，作为定价管理的重要依据。'),
(NULL, '计划财务部', '《资本管理办法》', '第七章  信息披露', '第四十一条', '本行依据监管规定和本行信息披露制度的相关要求开展资本充足率信息披露工作。'),
(NULL, '计划财务部', '《资本管理办法》', '第七章  信息披露', '第四十二条', '资本充足率信息披露的基本原则： （一）依法合规原则。本行严格按照监管规定和要求披露资本充足率相关信息。 （二）持续披露原则。资本充足率信息披露是本行持续责任，本行诚信履行持续信息披露的义务。 （三）公开、公正、公平的原则。本行应保证披露信息的真实性、准确性、完整性和及时性，无虚假记载、严重误导性陈述或重大遗漏，并确保所有投资者具有平等获得信息的权利，在境内外市场披露的信息要保持一致。 （四）充分披露原则。监管规定的披露内容是资本充足率信息披露的最低要求，本行应遵循充分披露的原则，并根据监管政策变化及时调整披露事项。'),
(NULL, '计划财务部', '《资本管理办法》', '第七章  信息披露', '第四十三条', '本行通过本行网站、办公系统及相关报刊等渠道向投资者和社会公众披露资本充足率相关信息，确保资本充足率信息披露的集中性、可访问性和公开性。'),
(NULL, '计划财务部', '《资本管理办法》', '第八章  监测与报告', '第四十四条', '本行建立资本监控和报告机制，定期监测和报告本行资本管理情况，包括但不限于资本充足率、资本和风险加权资产水平，变动趋势和影响因素，以及资本充足率预测情况。'),
(NULL, '计划财务部', '《资本管理办法》', '第八章  监测与报告', '第四十五条', '本行建立内部资本充足评估程序的报告体系，定期监测和报告银行资本水平和主要影响因素的变化趋势。报告至少包括以下内容： （一）评估主要风险状况及发展趋势、战略目标和外部环境对资本水平的影响；（二）评估实际持有的资本是否足以抵御主要风险；（三）提出确保资本能够充分覆盖主要风险的建议。 根据重要性和报告用途不同，本行明确各类报告的发送范围、报告内容及详略程度，确保报告信息与报送频率满足本行资本管理的需要。'),
(NULL, '计划财务部', '《资本管理办法》', '第八章  监测与报告', '第四十六条', '本行建立完整的文档管理机制，支持内部审计部门、监管部门对本行资本管理进行有效评估。文档包括但不限于：（一）董事会、高级管理层和相关部门的职责、独立性以及履职情况；（二）关于资本管理、风险管理等政策流程的制度文件；（三）资本规划、资本充足率管理计划、内部资本充足评估报告、压力测试报告、审计报告以及上述报告的相关重要文档；（四）关于资本管理的会议纪要和重要决策意见。'),
(NULL, '计划财务部', '《资本管理办法》', '第九章  附则', '第四十七条', '本办法经股东大会审议通过后生效。'),
(NULL, '计划财务部', '《资本管理办法》', '第九章  附则', '第四十八条', '本办法未尽事宜，依照法律法规、行政规章以及本行章程的有关规定执行。'),
(NULL, '计划财务部', '《资本管理办法》', '第九章  附则', '第四十九条', '本办法由本行负责解释和修订。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第三章  风险识别与计量', '第十八条', '本行用于利率风险计量的工具主要是内部资金转移定价系统，以及配套的数据库系统。为了保证系统和模型结果的准确，应当定期对系统和模型进行回溯检测，以检验其有效性和参数设置的合理性，并以此为依据调整系统设置和修正参数。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第一条', '限额的设立'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十条', '银行账户利率风险限额是根据董事会利率风险偏好，为防止本行过度承担风险，而设定的一系列反映本行银行账户利率风险的关键指标及其阀值。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十一条', '银行账户利率风险限额的设立采用自上而下的方式，具体分为董事会下达给高级管理层的风险限额（以下简称“董事会风险限额”）和高管层在银行风险管理活动中监控执行的风险限额（以下简称“高管层风险限额”）两层。董事会风险限额是董事会下达给高级管理层的风险限额（定量授权），每年初，由董事会或其风险管理与关联交易控制委员会向高级管理层下达；高管层风险限额是高管层在银行风险管理活动中监控执行的风险限额，每年初，由高管层相关机构提出，报行长办公会审议后执行。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十二条', '为确保对高管层风险限额的有效监控，高管层设立核心指标，用于衡量和监测全行总体利率风险。核心指标对应高管层风险限额。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十三条', '为有效识别、监测和管理核心指标，风险管理部设立关注指标。关注指标是对核心指标的分解和延伸，由风险管理部根据核心指标和高管层的风险管理要求提出建议，报高管层审批同意后执行。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十四条', '核心指标针对不同利率情景设置未来一年净利息收入的变化率和权益经济价值变化率限额值和预警值。正常情景：根据当前实际情况和利率走势假设的一系列可能出现的情景，该系列情景在每年初确定，其间如遇利率环境发生变化则动态调整；近10年最大升、降息情景：基于当前利率，假设未来一年利率变化重现最近10年来的一年跨度内最大升息或降息情形；标准冲击情景：假设所有利率即刻上升200BP和即刻下降200BP；'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十五条', '关注指标分为经营类指标、利率结构类指标、久期类指标和客户行为类指标等4类，指标监测方式分为静态监测和动态监测两种。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二条', '限额的计量、监测与报告'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十六条', '核心指标设立限额值和预警值，原则上限额值不得突破。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十七条', '核心指标突破预警值时的处理计划财务部分析主要风险来源，并将其关联至相关的关注指标；然后进一步挖掘业务层面原因，并结合经济金融形势提出风险管理建议；高管层决定是否需要采取风险管理策略将风险调整至预警值以内；如果需要采取风险管理策略，则进入相应的表内调解的工作流程。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十八条', '核心指标突破限额时的处理计划财务部分析主要风险来源，并将其关联至相关的关注指标；然后进一步挖掘业务层面原因，并结合经济金融形势提出风险管理建议，报送高管层审定；高管层做出风险管理决策，决定调整限或采取相应的风险管理策略；如果高管层决定调整限额，遵照本章第四节“限额的调整”执行；如果高管层风险管理与关联交易控制委员会决定采取风险管理策略，遵照第五章“风险管理与控制”执行。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第二十九条', '对关注指标设立参考区间。计划财务部定期监测关注指标，若主要关注指标偏离参考区间，则根据需要挖掘业务层面原因，并结合经济金融形式变化提出相关风险管理建议。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第三十条', '因业务系统技术性等原因导致限额指标超限时，由信息科技部对超限原因进行说明、提出处理建议，报高管层审批。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第三十一条', '银行账户利率风险限额的计量、监测报告原则上按季进行。如遇重大突发事件，启动临时应急流程，详见第五章第四节“应急处理”。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第三十二条', '银行账户利率风险限额的计量、监测和报告的内容和要求，详见第六章“风险报告与监督”。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第三条', '限额的调整'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第三十三条', '银行账户利率风险限额指标体系和具体限额指标数值一旦设定，原则上不得随意修改。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第四章  风险限额管理', '第三十四条', '对于因利率等外部环境发生较大变化，因业务需要或市场剧烈变动、系统性风险、政策性等原因，需要对银行账户利率风险限额指标体系进行调整时，由风险管理部提出调整建议，报高管层审议。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第三十五条', '总行通过内部资金转移定价将各分支机构的银行账户利率风险集中到总行计划财务部统一管理。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第三十六条', '凡是涉及银行账户利率风险的新产品和新业务在投放前，应事先将产品设计方案、业务操作流程和风险管理办法提交总行风险管理部评审，评审通过后方可进入下一环节。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第三十七条', '银行账户利率风险管理以及风险调整的策略应以计量结果为基础，在计量结果的运用方面应当既包括常规指标也包括压力测试结果指标。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第一条', '风险管理策略'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第三十八条', '根据业务特性，可将银行表内业务分为支行主导的传统银行业务和金融市场部管理的业务。支行主导的传统银行业务主要包括存、贷款业务，是银行账户利率风险的主要源头，其规模及利率风险特征受本行长期经营战略、年度经营计划、同业竞争状况等因素影响；金融市场部管理的业务主要包括投资组合、货币市场业务、债券以及同业融资类业务，是对传统银行业务的补充，该类业务是管理银行利率风险和流动性风险的工具，帮助本行在风险限额下实现利润最大化。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第三十九条', '本行主要通过金融市场部管理的业务调节利率风险敞口。基于长期风险管理目标，本行可通过业务计划调整、利率政策或内部资金转移定价政策引导核心银行业务的结构调整，但原则上不会因为短期因素的影响而调节支行主导的传统银行业务的利率敏感性敞口。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十条', '对于银行账户利率风险，可采用表内调节策略。表内调节指通过调整资产、负债的业务规模、期限结构及利率结构，确保银行利率风险敞口按照期望的方向发展。表内调节策略的制定应注意兼顾政策监管、绩效考核、全面预算和同业竞争等因素。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十一条', '风险管理策略的提出应以宏观经济和利率环境为基础。本行采取集体决策制方法，预测利率走势，以此作为利率风险管理策略提出的参考依据。利率环境主要分为以下5类：利率升息环境初期：受经济繁荣及通货膨胀等因素影响，央行开始持续采取加息（基准利率）政策，同时，市场利率也保持持续走高的态势：利率升息环境末期：宏观经济金融先行指标发生了转向变化，实体经济周期也表现出从波峰转向调整或下行，升息的利率环境将难以持续。需要判断的重点一是波峰持续期的长度，二是转向可能发生的时机；利率降息环境初期：受经济萧条及通货紧缩等因素影响，央行开始持续采取降息（基准利率）政策，同时，市场利率也保持持续走低的态势；利率降息环境末期：宏观经济金融先行指标发生了转向变化，实体经济周期也表现出从波谷转向调整或上行，降息的利率环境将难以持续。需要判断的重点包括低谷持续期的长度以及转向可能发生的时机；利率平稳期：利率走势相对稳定，未出现较大的向上向下波动。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十二条', '在利率走势特征非常明显、方向性判断非常明确的情况下，经高管层许可，本行可采取主动风险管理策略。主动风险管理策略的基本思路如下：处于升息周期初期或降息周期末期，可选择在一定时期内继续维持较高的资产敏感性特征，或适度提高一定时期内的资产敏感度；处于降息周期初期或升息周期末期，可选择适度降低一定时期内的资产敏感度；总行计划财务部负责按月跟进主动风险管理策略的执行情况及利率走势，一旦发现偏离预期利率动向，需上报高管层风险管理与关联交易控制委员会，由高管层做出相应决策。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第二条', '表内调节'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十三条', '表内调节方式包括但不限于：资产组合:包括投资组合、贷款组合以及票据组合等。投资组合。该策略通过减持、调整既有投资，或增持投资，改变当前债券固浮息占比、投资久期、重定价结构，从而调整重定价利率风险、收益率曲线风险等；贷款组合。该策略主要通过制定以及适时调整年度贷款目标和投向结构，改变资产结构、贷款平均期限、贷款重定价结构；票据组合。该策略主要通过主动管理票据业务规模和价格，调控银行信贷总量。负债组合：负债组合包括优化存款结构、发行债券、吸收协议存款等。优化存款结构。该策略主要通过调整对公、储蓄及同业存款的期限结构，改善负债的重定价特性；吸收协议存款。该策略主要是出于本行资产负债匹配的需要，在合理定价的基础上，由本行主动吸收协议存款，以优化本行的资产负债结构。管理工具：包括内部资金转移定价价格指引、新产品研发等。利率及内部资金转移定价价格调整。该策略主要通过调整产品利率及利率确定方式，改变银行收益水平和利率结构；通过调整内部资金转移定价政策，引导资源合理配置，指导业务单位科学定价；新产品研发。该策略主要用于部分监管政策带来的，银行无法通过调整业务结构改善的风险（如存贷款基准风险），本行通过研发新产品来冲减、规避或改善利率风险。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第三条', '评估与反馈'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十四条', '风险管理策略需由风险管理部逐项进行评估，其评估对象既包括对执行单位的评估，也包括对风险管理策略本身的评估。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十五条', '对执行单位的评估包括但不限于：执行时间的及时性、执行内容的一致性，问题和执行结果反馈的及时性和全面性。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十六条', '对管理策略的评估包括但不限于：选择开展表内策略时机的适宜性。开展该项策略应当适应前宏观政策、利率环境以及本行自身的需要；策略执行后对风险状况的改善程度。表内调节应达到下效果：优化利率期限结构、改善资产（负债）敏感性、保证资产收益水平、合理化业务久期等；成本收益的合理性。策略执行后账面利息损失或估值浮亏应当在本行可容忍或预期的范围内。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十七条', '风险管理部根据评估结果对执行单位予以评价、对风险管理策略予以优化，并于评估完成后向高管层汇报以及向执行单位予以反馈。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四条', '应急处理'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十八条', '应急处理机制适用于以下情况：外部危机事件，包括但不限于：利率环境突然转变，如在升息（降息）周期，或在利率环境并未有转向征兆的情况下突然发生降息（升息）；利率相关的政策突然发生颠覆性变化；发生全球或区域性的金融危机；内部突发事件，包括但不限于：系统突然瘫痪；核心人员流失。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第四十九条', '在遭遇外部危机事件时，应进入以下应急处理流程：总行风险管理部应在确定危机事件发生后及时向主管层汇报；并确定对外披露口径，经行领导批准后提供给相关部门；总行风险管理部牵头召开紧急磋商会议，向参会人员汇报危机事件及其影响，讨论制定切实可行的应急措施；磋商会上应针对危机事件做出以下决定：确定在限额被突破的情况下是调整业务还是申请调整限额；确定切实可行的应急措施。根据应急措施分工，各部门在会后根据分工执行。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第五十条', '在遭遇内部突发事件时，应进入以下应急处理流程。系统突然瘫痪时，应立即上报省联社予以修复，在修复期间运用手工等方式完成日常工作；如遇超出处理能力范围的内部突发事件，总行风险管理部应于当日紧急召开有相关部门和领导参加磋商会议，及时汇报并解决突发问题。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第五章  风险管理与控制', '第五十一条', '在内外部突发事件持续期间，总行风险管理部应密切关注其进展情况、应急措施推进效果，并及时向高管层汇报并跟踪结果。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第六章 风险报告与监督', '第五十二条', '风险管理报告的内容包括但不限于：银行账户利率风险管理报告：按季、半年、年度报告包括本期风险状况分析（引用风险计量报表的结果），本期风险管理建议、上期决策落实情况、限额指标超标情况说明、对冲交易损失情况说明（如有）有内的分析报告；不定期报告：要据董事会或高管层的要求不定期报告及内外部突发事件报告。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第六章 风险报告与监督', '第五十三条', '风险报告对象：季、半年、年度报告报高管层，经高管层风险评估后提交董事会及其风险管理与关联交易控制委员会。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第六章 风险报告与监督', '第五十四条', '审计部至少每年一次对本行银行账户利率风险管理体系各个组成部分和环节的准确、可靠、充分和有效性进行审核，审核内容包括但不限于：利率风险计量的相关假设和模型；风险计量和管理中所涉及的职责及授权；风险限额的设定以及处理超限额的政策、流程；压力测试的政策与程序；风险报告的形式、频率、内容和报告线路；风险管理方法、模型回测验证的政策和程序；新产品风险评估的政策和程序。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第七章  附  则', '第五十五条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《银行账户利率风险管理办法》', '第七章  附  则', '第五十六条', '本办法自发文之日起施行。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第五章  分类标准', '第十二条', '特别规定。（一）未准确核算的项目,或由于分类所需的资料不全，导致风险分类判断困难的项目，至少划分为关注类。（二）违反国家有关法律法规和非信贷资产的有关制度规定的，除已划分为损失类的，分类结果要下调一级。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第六章   组织机构、分类程序和认定权限', '第十三条', '组织机构⒈本行非信贷资产风险分类工作由本行组织实施，并成立由董事长为组长、行长室为成员的非信贷资产风险分类领导小组，负责分类工作的组织、领导和实施，对非信贷资产风险分类结果进行最终认定。⒉本行由计划财务、合规管理、风险管理、信贷管理等部门负责人组成风险分类工作小组，负责辖内风险分类的指导、监督工作，在权限内对初分意见进行初审和认定，对有疑问的风险分类进行现场核查。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第六章   组织机构、分类程序和认定权限', '第十四条', '本行按照初分、初审、认定三个程序进行非信贷资产风险分类。（一）初分⒈核对会计核算科目。初分人员对会计核算科目的核对要做到准确无误，即各类非信贷资产的账账、账表、账款、账实、账据、内外账务均要相符。⒉收集整理资料。初分人员按风险分类要求，收集完善分类信息资料，做好档案资料的整理和分类准备工作。（1）背景资料。主要包括：非信贷资产的形成过程及现状；非信贷资产交易对手的基本信息、财务状况、履约意愿等，对本行资产有重大不利影响的各种因素；非信贷资产同类资产的市场价格、以往资产处置、变现损失率等；非信贷资产有关操作管理情况。（2）附件材料。指该项资产所涉及有关资料的复印件，主要包括：主要法律文书；原始凭证和原始记录；司法部门或相关政府部门的文件；本行已有的调查分析报告；原认定的风险分类资料，包括非信贷资产风险分类认定表、资产管理委员会审议记录、批复文件等。特殊原因造成权属资料不全、对手信息不全等风险分类依据信息资料不完整时，必须书面说明形成经过和原因，经负责人及有关人员签字确认后作为风险分类依据备查。⒊提出初分意见。初分人员依据分类标准，对风险分类作出初步判断，提出初分理由和初步分类意见，并按要求制作工作底稿。⒋上报初分结果。将工作底稿及其他资料呈交风险分类工作小组进行初审。（二）初审⒈审核初分结果。风险分类工作小组对上报的初分资料进行分析，重点是审核资料完整性、程序合规性、分类理由的充分性和计算的准确性。⒉实地核查。根据初分情况选择重点项目进行实地核查，对初分结果不准确或有疑问的，要逐笔实地进行核实，并形成风险分类核查报告。⒊确定初审结果。⒋上报初审结果。将初审的结果及相关资料连同核查报告一并上报风险分类领导小组。（三）认定风险分类领导小组对初审结果进行审核，在分类认定表上签署分类认定意见，确定认定结果。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第六章   组织机构、分类程序和认定权限', '第十五条', '本行非信贷资产初分部门及种类分工。⒈计划财务部：安全性非信贷资产、同业债权、专项央行票据、委托及代理资产、其他应收款项、固定资产、在建工程、无形资产、递延资产、待处理财产损溢、固定资产清理、历年挂账损失。⒉信贷管理部：待处理抵债资产。⒊风险管理部：负责汇总、审核。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第六章   组织机构、分类程序和认定权限', '第十六条', '认定权限⒈安全性非信贷资产由所属部门直接认定。⒉按照本办法直接认定为损失类的，可在初分的基础上经分类工作领导小组初审后作为最终认定结果。⒊除安全性非信贷资产和直接认定为损失类的非信贷资产外，其他非信贷资产单笔分类资产在500万元（含）以内的在初分的基础上经风险分类工作小组初审后作为最终认定结果，500万元以上的经初分、初审后由风险分类领导小组最终认定结果。⒋原分类结果由下级调至上级的认定权限按上述规定执行，上级调至下级的由风险分类工作小组最终认定结果。⒌凡单笔金额超过2000万元（含）的大额非信贷资产如计入损失类，必须由风险分类领导小组认定，并向省联社事前报备。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第七章   分类管理', '第十七条', '档案管理。各部门应加强业务档案管理，及时收集、整理各项非信贷资产的档案资料，确保档案要素齐备，内容真实、完整（如有漏缺，应以书面形式说明）、有效，为各项非信贷资产的分类提供充分、可靠的信息基础。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第七章   分类管理', '第十八条', '管理考核。本行非信贷资产风险分类要按照“落实责任、动态管理、定期分析、科学考核”的要求进行。（一）落实责任。本行董事长为非信贷资产风险分类工作的第一责任人，风险分类领导小组具体负责分类工作的组织、领导、实施和权限内分类认定；风险分类工作小组负责风险分类的指导、监督工作，对初分意见进行初审并提出审核意见，对可疑风险分类进行现场核查，在权限内进行分类认定，提高非信贷资产管理水平；本行相关职能管理部门负责本部门风险分类的初分和管理工作。（二）动态管理。本行非信贷资产风险分类工作小组要加强对非信贷资产风险分类的监督检查，督促各相关部门建立动态管理机制。同业债权、待处理抵债资产、应收账款、其他应收款项、投资类资产应按季进行风险分类，其他非信贷资产原则上每年进行一次风险分类。同时，相关管理部门及管理人员要将非信贷资产风险分类工作与日常管理工作紧密结合，要随时监测、适时对非信贷资产的风险情况进行分析、认真收集和补充与非信贷资产有关的信息及资料，对于因风险因素变动导致非信贷资产形态发生变化的，要及时提出调整形态的建议并按本办法规定的分类程序，逐笔审核、认定并调整非信贷资产类别。（三）定期分析。各初分职能部门要建立分类台账，按季对分类结果进行汇总分析，并将分类结果的异常性变化及其成因、分类过程中存在的主要问题、改进措施等情况及时上报非信贷资产风险分类办公室。非信贷资产风险分类办公室设在风险管理部，主要负责全辖非信贷资产风险分类的结果、分析报告的汇总，并根据要求上报省联社或监管部门。(四)科学考核。本行董事会可根据非信贷资产风险分类的要求，合理确定各项考核指标，重点考核分类结果的准确性和不良非信贷资产风险的化解情况等，对责任人进行严格考核，并向监管部门和上级管理部门报告有关情况。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第八章   违规处理', '第十九条', '对存在违规行为的责任人，本行根据员工违规违章行为管理规定进行处罚。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第九章   附  则', '第二十条', '本行根据非信贷资产风险分类结果，参照《企业会计准则》、《金融企业准备金计提管理办法》等文件要求，提取资产减值准备。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第九章   附  则', '第二十一条', '本办法由本行计划财务部负责解释和修订。'),
(NULL, '计划财务部', '《非信贷资产风险分类实施细则》', '第九章   附  则', '第二十二条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《 防范电信网络新型违法犯罪管理办法》', '', '第八条', '建立单位开户审慎核实机制。对于被全国企业信用信息公示系统列入“严重违法失信企业名单”，以及经各支行（部）核实单位注册地址不存在或者虚构经营场所的单位，各支行（部）不得为其开户。各支行（部）应当至少每季度排查企业是否属于严重违法企业，情况属实的，应当在3个月内暂停其业务，逐步清理。对存在法定代表人或者负责人对单位经营规模及业务背景等情况不清楚、注册地和经营地均在异地等异常情况的单位，各支行（部）应当加强对单位开户意愿的核查。银行应当对法定代表人或者负责人面签并留存视频、音频资料等，开户单位在未获取人民银行核准或备案前，不得开通非柜面业务，开户初期原则上不开通非柜面业务，待后续了解后再审慎开通。'),
(NULL, '运营管理部', '《 防范电信网络新型违法犯罪管理办法》', '', '第九条', '加强对异常开户行为的审核。有下列情形之一的，各支行（部）有权拒绝开户：1．对单位和个人身份信息存在疑义，要求出示辅助证件，单位和个人拒绝出示的。2．单位和个人组织他人同时或者分批开立账户的。3．有明显理由怀疑开立账户从事违法犯罪活动的。各支行（部）应当加强账户交易活动监测，对开户之日起6个月内无交易记录的账户，各支行（部）应当暂停其非柜面业务，各支行（部）向单位和个人重新核实身份后，可以恢复其业务。'),
(NULL, '运营管理部', '《 防范电信网络新型违法犯罪管理办法》', '', '第十条', '严格联系电话号码与身份证件号码的对应关系。各支行（部）应当建立联系电话号码与个人身份证件号码的一一对应关系，对多人使用同一联系电话号码开立和使用账户的情况进行排查清理，联系相关当事人进行确认。对于成年人代理未成年人或者老年人开户预留本人联系电话等合理情形的，由相关当事人出具说明后可以保持不变；对于单位批量开户，预留财务人员联系电话等情形的，应当变更为账户所有人本人的联系电话；对于无法证明合理性的，应当对相关银行账户暂停非柜面业务。'),
(NULL, '运营管理部', '《 防范电信网络新型违法犯罪管理办法》', '', '第十一条', '增加转账方式，调整转账时间。自2016年12月1日起，各支行（部）提供转账服务时应当执行下列规定：　1．向存款人提供实时到账、普通到账、次日到账等多种转账方式选择，存款人在选择后才能办理业务。　2．除向本人同行账户转账外，个人通过自助柜员机（含其他具有存取款功能的自助设备，下同）转账的，发卡行在受理24小时后办理资金转账。在发卡行受理后24小时内，个人可以向发卡行申请撤销转账。受理行应当在受理结果界面对转账业务办理时间和可撤销规定作出明确提示。　3．本行通过自助柜员机为个人办理转账业务的，应当增加汉语语音提示，并通过文字、标识、弹窗等设置防诈骗提醒；非汉语提示界面应当对资金转出等核心关键字段提供汉语提示，无法提示的，不得提供转账。'),
(NULL, '运营管理部', '《 防范电信网络新型违法犯罪管理办法》', '', '第十二条', '加强银行非柜面转账管理。自2016年12月1日起，各支行（部）在为存款人开通非柜面转账业务时，应当与存款人签订协议，约定非柜面渠道向非同名银行账户和支付账户转账的日累计限额、笔数和年累计限额等，超出限额和笔数的，应当到银行柜面办理。    除向本人同行账户转账外，各支行（部）为个人办理非柜面转账业务，单日累计金额超过5万元的，应当采用动态口令牌等安全可靠的支付指令验证方式。单位、个人银行账户非柜面转账单日累计金额分别超过100万元、30万元的，本行应当进行大额交易提醒，单位、个人确认后方可转账。 第十三条  加强交易背景调查。各支行（部）发现账户存在大量转入转出交易的，应当按照“了解你的客户”原则，对单位或者个人的交易背景进行调查。如发现存在异常的，应当按照审慎原则调整向单位和个人提供的相关服务。 第十四条  加强特约商户资金结算管理。各支行（部）为特约商户提供T+0资金结算服务的，应当对特约商户加强交易监测和风险管理，不得为入网不满90日或者入网后连续正常交易不满30日的特约商户提供T+0资金结算服务。 第十五条  严格审核特约商户资质，规范受理终端管理。任何单位和个人不得在网上买卖POS机（包括MPOS）、刷卡器等受理终端。各支行（部）应当对全部实体特约商户进行现场检查，逐一核对其受理终端的使用地点。对于违规移机使用、无法确认实际使用地点的受理终端一律停止业务功能。 第十六条  建立健全特约商户信息管理系统和黑名单管理机制。中国支付清算协会、银行卡清算机构建立健全特约商户信息管理系统，组织银行详细记录特约商户基本信息、启动和终止服务情况、合规风险状况等。对同一特约商户或者同一个人控制的特约商户反复更换服务机构等异常状况的，本行应当审慎为其提供服务。 中国支付清算协会、银行卡清算机构建立健全特约商户黑名单管理机制，将因存在重大违规行为被银行终止服务的特约商户及其法定代表人或者负责人、公安机关认定为违法犯罪活动转移赃款提供便利的特约商户及相关个人、公安机关认定的买卖账户的单位和个人等，列入黑名单管理。中国支付清算协会将黑名单信息移送金融信用信息基础数据库。本行不得将黑名单中的单位以及由相关个人担任法定代表人或者负责人的单位拓展为特约商户；已经拓展为特约商户的，应当自该特约商户被列入黑名单之日起10日内予以清退。 第十七条  确保交易信息真实、完整、可追溯。支付机构与本行合作开展银行账户付款或者收款业务的，应当严格执行《银行卡收单业务管理办法》（中国人民银行令〔2013〕第9号发布）、《非银行支付机构网络支付业务管理办法》（中国人民银行公告〔2015〕第43号公布）等制度规定，确保交易信息的真实性、完整性、可追溯性以及在支付全流程中的一致性，不得篡改或者隐匿交易信息，交易信息应当至少保存5年。 第十八条  加强账户监测。本行应当加强对银行账户监测，建立和完善可疑交易监测模型，账户及其资金划转具有集中转入分散转出等可疑交易特征的，应当列入可疑交易。 对于列入可疑交易的账户，各支行（部）应当与相关单位或者个人核实交易情况；经核实后仍然认定账户可疑的，各支行（部）应当暂停账户非柜面业务，并按照规定报送可疑交易报告或者重点可疑交易报告；涉嫌违法犯罪的，应当及时向当地公安机关报告。 第十九条  强化支付结算可疑交易监测的研究。中国支付清算协会、银行卡清算机构根据公安机关、银行、支付机构提供的可疑交易情形，构建可疑交易监测模型，向银行发布。本行在收到发布的可疑交易监测模型时，适时添加至本行监测系统，进行有效拦截可疑交易。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第一章  总 则', '第一条', '为规范xxx银行（以下简称“本行”）个人存款证明业务的管理和操作，防范业务风险，促进储蓄相关业务的健康发展，根据《中华人民共和国合同法》、《储蓄管理条例》及本行有关规章制度，制订本办法。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第一章  总 则', '第二条', '符合法律规定的本行储蓄存单、存折、银行卡均可在开户网点办理个人存款证明。已被县以上司法部门止付和已用于的储蓄存款以及存款人死亡后，申请人未按司法部门要求办理过户手续的储蓄存款，不能办理个人存款证明。存款证明不具有担保作用，不能用于质押。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第二章  基本规定', '第三条', '存款证明只对储蓄存款者本人适用，严禁存款人以他人存款为自己办理存款证明。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第二章  基本规定', '第四条', '多份存单、存折、银行卡可合并开具一份存款证明。因特殊需要可就同一笔款项开具多份存款证明。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第二章  基本规定', '第五条', '开具存款证明书由储蓄开户网点审批、收费后通过综合业务系统签发，系统自动止付。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第二章  基本规定', '第六条', '本人办理个人存款证明时，客户应持本网点的存单、折、银行卡原件和有效身份证件原件(复印件各一份)提出申请，填写“开立个人存款证明申请书”（见附件2，以下简称“申请书”）。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第二章  基本规定', '第七条', '委托他人代办存款证明时，除以上资料外，还需提供经存款人本人签名的委托授权书原件和代理人的有效身份证件原件（复印件一份）。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第二章  基本规定', '第八条', '个人存款证明办理成功后，按照存款证明收费标准，每份收取费用30元。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第二章  基本规定', '第九条', '空白的存款证明书按重要空白凭证要求管理，系统自动销号。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第三章  业务流程', '第十条', '签发存款证明（一）客户持本开户网点的存单、存折、卡原件、存款人有效身份证件原件(复印件各一份)，如为他人代办，还需提供委托授权书原件和代理人身份证件（复印件一份）并提出申请，填写申请书。（二）柜员审核申请资料无误并通过综合业务系统检查账户资金情况属实后，在申请书“审核柜员意见”栏中注明“资料已核，资金情况属实”字样并签章，再将申请资料交由运营主管、机构负责人逐级在申请书相应意见栏中签署“经审核，同意办理”字样并签章。（三）签发存款证明书。柜员执行20541交易根据屏幕提示输入相关信息后打印存款证明书，系统自动对存单、存折、银行卡进行止付。执行操作前，必须保证柜员凭证库中有足够的空白存款证明书。（四）系统联动按照收费交易标准和签发的份数以现金方式收取手续费，自动打印收费凭证。（五）柜员在综合业务系统中查询存单、存折、银行卡的止付信息，在申请书“开证柜员意见”栏中注明“已收费、已止付”字样并签章确认。（六）签发好的存款证明书第一联经运营主管逐份复核并加盖本机构业务章后交予客户，登记《个人存款证明登记簿》。复核时运营主管需对存款证明书的内容及存单、存折、银行卡止付情况和收费情况进行再次确认，在申请书“运营主管复核意见”栏中注明“已复核”，并签名。（七）各要素填写齐全的申请书、存款证明书第二联、授权委托书、客户的存单、存折、银行卡复印件和身份证件复印件按顺序专夹保管，年终装订归档。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第三章  业务流程', '第十一条', '撤销（作废）存款证明（一）客户办理撤销存款证明时，应提出书面申请（见附件3），凭本人有效身份证件、所有已开出的存款证明书、存单、存折、银行卡原件交储蓄开户网点柜面办理。（二）柜员核对客户提供的资料无误、需撤销（作废）的《存款证明书》份数与原申请书上注明的份数相符后，交由运营主管审核，运营主管在客户书面申请上签注“经审核，同意办理”字样并签名后，交柜员办理。（三）柜员用20541交易撤销存款证明，将存单、存折、银行卡归还客户。（四）作废存款证明与撤销手续相同。（五）客户提出的撤销或作废存款证明书面申请（第一联）及存款证明书原件（加注“收回作废”字样后），在综合系统处理后作为流水凭证置于传票中；客户提出的撤销或作废存款证明书面申请（第二联）及存款证明书原件或复印件（加注“收回作废”字样后）专夹保管，年终装订归档。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第四章   附 则', '第十二条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《个人存款证明业务管理办法》', '第四章   附 则', '第十三条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第一章  总 则', '第一条', '为强化xxx银行（以下简称“本行”）内部控制机制，提高会计工作质量，防范业务操作风险，规范监督行为，根据《中华人民共和国票据法》、《支付结算管理办法》、《人民币银行结算账户管理办法》、《储蓄管理条例》等法律法规及本行相关制度办法规定，制定本办法。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第一章  总 则', '第二条', '本办法所称事后监督是指本行事后监督团队按照规定的程序和方法，对各支行（部）、清算团队日常会计核算业务进行复审和检验。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第一章  总 则', '第三条', '事后监督工作应遵循以下基本原则：（一）独立性原则。事后监督工作与前台核算要做到在空间上和人员上相互分离，事后监督人员不得参与被监督业务，不得接触各类重要印章。（二）连续性原则。事后监督工作应做到每日进行，保持连续性，不得随意中断。（三）及时性原则。及时实施事后监督，及时反馈监督结果，及时督促整改。（四）全面性原则。按规定的监督范围、内容和方法，对前台业务凭证、账务核算和单位银行结算账户资料等实施严格的事后监督。（五）重要性原则。在全面复核的基础上，以防范资金风险为重点对部分特殊业务进行重点监督。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第一章  总 则', '第四条', '实施事后监督应依据相关法律法规、制度，审查监督业务的合法性、合规性、准确性、完整性。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第一章  总 则', '第五条', '事后监督工作应把好会计核算质量关，促进会计核算制度化、规范化，防范资金风险和其他风险。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第二章  机构和人员', '第六条', '本行设立事后监督团队，隶属于运营管理部，负责对各支行（部）、清算团队已完成的日常会计核算业务进行集中或分散监督。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第二章  机构和人员', '第七条', '本行其他业务管理部门应加强对事后监督团队的业务指导，对于有关业务管理、内控制度等方面的办法、规定，要及时传达到事后监督团队，各业务部门组织业务培训应通知事后监督团队人员参加，事后监督团队要定期组织全体人员进行学习，及时掌握新的业务知识和有关制度规定。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第二章  机构和人员', '第八条', '事后监督团队应按照相互制约和分工负责的原则合理设置岗位，并根据业务量大小配备足够的事后监督人员。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第二章  机构和人员', '第九条', '事后监督团队的基本职责包括但不限于：（一）审核业务处理依据是否合法有效，账务处理手续是否符合制度规定，会计核算结果是否真实、准确。（二）检验前台会计出纳工作核算质量，发现、纠正核算过程中的差错和问题。（三）及时反映违规、违纪行为，对规章制度的制订和执行提出意见和建议。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第二章  机构和人员', '第十条', '事后监督团队设置团队负责人岗、凭证审核勾对岗、风险预警岗、档案管理岗、凭证扫描岗、再监督岗等基本岗位。具体岗位职责：（一）团队负责人岗职责⒈负责事后监督团队各种硬件设备的登记、管理工作，定期检查操作人员对本岗位硬件设备的维护和保养情况；⒉负责事后监督团队的业务管理、授权处理和检查监督工作，事后监督工作报告与反馈，重大问题及时汇报，组织学习培训；⒊负责电子会计档案查询用户的增加、修改、删除等管理工作；⒋负责纸质会计档案(含影像资料档案)的监交工作；⒌负责本岗位ＰＣ机等硬件设备的日常清洁和维护；⒍完成领导交办的其他工作任务。（二）凭证审核勾对岗职责（分散监督与集中监督职责相同）⒈按要求对计算机自动勾对中出现的未成功流水进行勾对、审核和确认；⒉审核各种需人工核对的记账凭证及各类特殊业务凭证，对需重点监督的业务进行逐笔审核（包括有关票据业务处理是否符合票据法规定；定期储蓄存款提前支取手续是否齐全；大额存、取现金及大额转账业务是否符合反洗钱规定等），并对审核确认业务的真实性负责；⒊发现有质量问题的影像资料和输入错误的批次信息，应及时记录并通知凭证扫描员重新处理，对错误凭证下达差错通知单，对计算机批量验印未成功的进行人工单张验印；⒋负责对本岗位的ＰＣ机等硬件设备的日常清洁和维护；⒌完成领导交办的其他工作任务。（三）风险预警岗职责⒈根据事后监督系统设定，对风险预警信息进行审核和删选；⒉对预警的风险信息进行分类处置，对确认存在的问题换人复审；⒊对预警的风险信息提出处理意见，并负责跟踪风险信息的处理落实情况；⒋对发现的可疑信息和风险隐患及时进行登记和报告；⒌负责签发和收回“差错通知单”，审核网点差错处理情况，并进行销账；⒍负责本岗位ＰＣ机等硬件设备的日常清洁和维护；⒎完成领导交办其他工作任务。（四）档案管理岗职责⒈负责做好纸质会计档案和电子会计档案的归档保管工作；⒉负责收理各支行（部）、清算团队和有权机关对纸质会计档案及电子会计档案的查阅工作；⒊完成领导交办的其他工作任务。（五）凭证扫描岗职责⒈负责指定支行（部）、清算团队业务传票的接收、拆包、整理、清点工作；⒉负责业务传票的索引信息输入及扫描工作，对扫描的影像在提交前进行自检、删除和补扫；⒊负责对柜员的业务传票数量进行核对，出现差错及时登记并向业务主管报告；⒋对抽检出的不合格图像及错误的批索引信息及时修改；⒌负责业务传票的整理、包装、塑封工作，中午或营业终了应将传票入柜保管，资料完整后及时登记档案登记簿并向档案管理员移交；⒍负责对本岗位ＰＣ机、扫描仪及塑封包装机等硬件设备进行日常的清洁维护；⒎完成领导交办的其他工作任务。（六）再监督岗职责1.负责定期对凭证审核勾对岗已监督的业务按照规定比例和规则进行再监督。2.在再监督过程中如发现问题及时下达差错。发现重大问题及时向领导反映。3.完成领导交办的其他工作任务。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第三章  范围和内容', '第十一条', '事后监督团队监督范围包括：业务凭证、账户管理、账务核算、特殊业务、风险控制及异常情况。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第三章  范围和内容', '第十二条', '业务凭证的监督（一）审查业务凭证的使用是否正确，基本要素是否齐全，合法有效，处理手续是否符合规定；审查单位银行结算账户转入个人银行结算账户的款项是否按规定办理。（二）审查支付凭证的真实性、完整性、合法性和对转关系是否正常，印章加盖是否正确。（三）审查各类自制凭证的合法性、内容的真实性以及审批权限是否符合规定。（四）审查各类授权业务凭证上是否有授权人签章。    （五）贯彻反洗钱工作，按照《金融机构大额交易和可疑交易报告管理办法》要求进行监督，内容包括：⒈审查大额转账业务款项的来源或用途是否符合规定；⒉审查单位提现是否符合现金管理条例；⒊审查大额现金支取是否符合规定。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第三章  范围和内容', '第十三条', '特殊业务的监督（一）审查调整单位（个人）账户信息，是否与《客户信息修改申请书》所列内容一致，申请书上是否有运营主管的签章。（二）办理冻结、解冻、扣划业务是否有县级（含）以上有权机关签发的通知书，扣划是否有有权机关出具的判决书或裁定书，通知书上是否有运营主管的签章。（三）挂失止付和挂失补开是否有申请人签名的《挂失申请书》，《挂失申请书》是否标注挂失受理时间。（四）挂失补开是否是本人办理。（五）存款人死亡的存款支取是否有有效的法律文书和受益人身份证复印件。（六）其他规定需监督的特殊业务。以上特殊业务的交易凭证必须与主机返还的相关信息进行逐笔勾对分析，分析交易内容是否真实，审批手续是否齐全，客户提供的身份证件是否真实有效，柜员流水信息与客户手工填写的信息是否一致，业务处理有无异常情况。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第十四条', '各支行每日业务资料应于当日整理完毕，并于次日随库款押运车送达本行事后监督团队，营业部、清算团队凭证指定专人于次日上午送达事后监督团队。如由于特殊原因不能按时送达的，应提前向事后监督团队负责人说明情况，批准后方能适当推迟接收时间。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第十五条', '事后监督团队应指定专人接收业务资料，严格执行传递交接手续。本行押解员清点每日收缴的业务资料包数量，登记款包交接登记簿，交库房人员；库房人员于上班时再交事后监督团队指定人员接收，事后监督团队人员清点业务资料包并与登记簿核对，无误后签字确认。营业部、清算团队与事后监督团队业务资料的交接由各方指定人员办理交接手续。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第十六条', '事后监督人员接到业务资料后，应立即实施扫描，不得积压。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第十七条', '事后监督人员必须按照规定的监督内容和程序，认真审查核对业务资料。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第十八条', '事后监督人员对事后监督过程中发现的差错，应准确及时进行差错登记，并经预警人员审核后在预警系统向差错网点发送“差错通知单”，通知差错网点及时进行整改。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第十九条', '被监督网点每天应登录预警系统查看“待处理差错”信息，在接到“差错通知单”的两个工作日内必须对存在的问题进行确认和处理，并将处理结果详细记录在“差错通知单”上。通知书打印后，第一联由网点留存，按月装订归档，第二联回复联加盖业务专用章并经运营主管和经办柜员签章后返还事后监督团队作销账处理。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第二十条', '差错处理规定（一）错、漏盖印章（客户印章除外），应打印该凭证影像，在凭证影像上补盖印章，补盖后随“差错通知单”回复联送事后监督团队作影像追加处理后专夹保管，定期整理封装后归档。（二）对错、漏盖单位客户印章或超过有效期、金额大小写不符、无效的凭证，应要求被监督网点在规定时间内更换符合制度规定的有效凭证，有效凭证与“差错通知单”回复联一并送事后监督团队进行凭证替换，凭证替换时应办理好交接手续，替换下的错误凭证加盖作废章后退还客户，“差错通知单”回复联作影像追加处理后专夹保管，定期整理封装后归档。（三）记账串户或利息、罚金、滞纳金、收费等方面的差错，被监督网点更正后，应在“差错通知单”回复联上注明更正结果，并将“错账冲正传票”复印件与“差错通知单”回复联一并送事后监督团队进行销账，“差错通知单”回复联和“错账冲正传票”复印件作影像追加处理后专夹保管，定期整理封装后归档。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第二十一条', '事后监督工作结束后，各支行（部）、清算团队的业务传票不再返还，由事后监督团队按照档案管理的有关要求，负责整理、封装、归档并保管。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第四章  工作程序', '第二十二条', '建立会计档案调（查）登记簿，各支行（部）、清算团队或有权部门需要调阅会计档案时，按照《xxx银行会计档案管理办法》规定，履行档案调阅手续，根据调阅要求在会计档案登记簿上登记处理结果。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第五章  工作制度', '第二十三条', '轮岗制度事后监督团队工作人员实行定期轮岗制度，每六个月至少轮岗一次。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第五章  工作制度', '第二十四条', '情况报告制度事后监督人员对发现的差错、事故等违规行为，实行电子和纸质同时上报的方式。（一）事后监督人员必须按规定的监督范围、内容和方法，严格监督核对，认真分析判断，检查被监督单位制度执行情况。发现重大差错、事故，要立即报告，不得延误。（二）事后监督团队对当日监督中发现的往账凭证上的收款人与往账清单不符、违反现金管理规定的大额付现、开销户手续不全、重大金额串户等重大事项，必须当日查明原因，落实处理结果。（三）对违规违纪的行为，如伪造凭证、乱用会计科目、编造虚假数据等现象要及时向负责人进行汇报。（四）事后监督团队要按月通报监督结果，即按月提供全行“差错统计表”，并根据“差错统计表”上的内容进行分析，对问题提出整改措施，促进前台工作质量的提高。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第五章  工作制度', '第二十五条', '事后监督团队人员考核奖惩制度（一）对忠于职守、坚持原则、工作突出的事后监督人员，特别是在事后监督过程中能及时发现重大问题并纠正，为本行挽回巨大经济损失的，将视情况给予有功人员通报表扬和物质上的奖励。（二）对事后监督工作不力、严重失职，发现问题故意隐瞒不报，袒护责任人，造成资金损失的，将按本行员工违规违章行为管理规定进行扣罚，情节严重的要按规定给予行政处分并调离事后监督工作岗位，触犯刑律的，要追究其刑事责任。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第六章  附  则', '第二十六条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《事后监督工作管理办法》', '第六章  附  则', '第二十七条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第一章 　总　则', '第一条', '为规范xxx银行（以下简称“本行”）人民币个人银行结算账户的开立和使用工作，加强银行结算账户管理，维护经济金融秩序稳定，根据中国人民银行《人民币银行结算账户管理办法》、《关于改进个人银行账户服务加强账户管理的通知》、《关于加强支付结算管理防范电信网络新型违法犯罪有关事项的通知》等规定，制定本实施办法。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第一章 　总　则', '第二条', '本办法所称存款人为自然人。本办法所称银行结算账户是指本行为存款人开立的办理资金收付结算的人民币活期存款账户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第一章 　总　则', '第三条', '存款人凭个人身份证件以自然人名称开立的银行结算账户为个人银行结算账户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第一章 　总　则', '第四条', '个人银行结算账户实行实名制管理，不得开立匿名或假名账户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第一章 　总　则', '第五条', '个人银行结算账户实行分类管理，分为Ⅰ类银行账户、Ⅱ类银行账户和Ⅲ类银行账户（以下简称Ⅰ类户、Ⅱ类户和Ⅲ类户），不同类别的个人银行账户有不同的功能和权限。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第一章 　总　则', '第六条', '各支行（部）应依法为存款人的银行结算账户信息保密。对个人银行结算账户的存款和有关资料，除国家法律另有规定外，有权拒绝任何单位或个人查询。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第七条', '个人银行结算账户是自然人因投资、消费、结算等而开立的可办理支付结算业务的存款账户。有下列情况的，可以申请开立个人银行结算账户：（一）使用支票、信用卡等信用支付工具的。（二）办理汇兑、定期借记、定期贷记、借记卡等结算业务的。存款人可根据需要申请开立个人银行结算账户，也可以在已开立的储蓄账户中选择并向各支行（部）申请确认为个人银行结算账户。存款人已开立Ⅱ类户的，可申请转为Ⅰ类户，存款人已开立Ⅲ类户的，可申请转为Ⅰ类户或Ⅱ类户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第八条', '异地存款人根据需要可以在本行开立个人银行结算账户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第九条', '存款人可通过本行柜面、自助机具开立Ⅰ类户、Ⅱ类户和Ⅲ类户，也可通过电子渠道开立Ⅱ类户和Ⅲ类户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十条', '存款人可以选择借记卡、存折作为Ⅰ类户、Ⅱ类户的支付介质，本行不为Ⅲ类户发放实体介质。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十一条', '存款人申请开立个人银行结算账户原则上应由开户申请人本人亲自办理；存款人选择存折作为支付价质时，符合条件的，可以由他人代理办理。（一）他人代理开立个人银行结算账户的，代理人应出具代理人、被代理人的有效身份证件及合法的委托书等，有必要时，代理人还应出具证明代理关系的公证书，无法确定代理关系的，不得办理开户。（二）开户申请人确因行动不便等原因不能前往网点办理，可向本行申请上门办理开户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十二条', '特殊情形的处理（一）存款人开立开发工资、教育、社会保障（如社保、医保、军保）、公共管理（如公共事业、拆迁、捐助、助农扶农）等特殊用途个人银行结算账户时，可由所在单位代理办理。单位代理个人开立银行账户的，应提供单位证明材料 、被代理人有效身份证件的复印件或影印件。单位代理开立的个人银行结算账户，在被代理人持本人有效身份证件到开户银行办理身份确认、密码设（重）置等激活手续前，该银行账户只收不付。（二）无民事行为能力或限制民事行为能力的开户申请人，由法定代理人或人民法院、有关部门依法指定的人员代理办理。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十三条', '存款人在柜面申请开立个人银行结算账户时，应填写“开立个人银行结算账户申请书”，存款人选择借记卡作为支付介质时，还应填写“圆鼎借记卡申请表”。首次在本行办理业务的客户，还需填写“对私客户信息采集表”。存款人签章后，并向各支行（部）出具下列证明文件：（一）在中华人民共和国境内已登记常住户口的中国公民为居民身份证；不满十六周岁的，可以使用居民身份证或户口簿。（二）香港、澳门特别行政区居民为港澳居民往来内地通行证。（三）台湾地区居民为台湾居民来往大陆通行证。（四）定居国外的中国公民为中国护照。（五）外国公民为护照或者外国人永久居留证（外国边民，按照边贸结算的有关规定办理）。（六）法律、行政法规规定的其他身份证明文件。除以上法定有效证件外，银行还可根据需要，要求存款人出示其他能证明身份的有效证件或证明文件，以进一步确认存款人身份。辅助身份证明材料包括但不限于：（一）中国公民为户口簿、护照、机动车驾驶证、居住证、社会保障卡、军人或武装警察身份证件、公安机关出具的户籍证明、工作证。（二）香港、澳门特别行政区居民为香港、澳门特别行政区居民身份证。（三）台湾地区居民为在台湾居住的有效身份证明。（四）定居国外的中国公民为定居国外的证明文件。（五）外国公民为外国居民身份证、使领馆人员身份证件或者机动车驾驶证等其他带有照片的身份证件。（六）完税证明、水电煤缴费单等税费凭证。（七）军人、武装警察尚未领取居民身份证的，除出具军人和武装警察身份证件外，还应出具军人保障卡或所在单位开具的尚未领取居民身份证的证明材料。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十四条', '各支行（部）应加强对客户身份的识别，在办理开户业务时，发现个人冒用他人身份开立账户的，应当及时向公安机关报案并将被冒用的身份证件移交公安机关。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十五条', '各支行（部）应对存款人提交的“开立个人银行结算账户申请书”、“圆鼎借记卡申请表”、“对私客户信息采集表”和相关证明文件的真实性、完整性、合规性进行认真审查，并通过联网核查公民身份系统核查存款人身份,将有关证明文件的复印件和身份核查信息留存归档。“开立个人银行结算账户申请书”一式二联，一联存款人留存，一联各支行（部）附传票后作附件交事后监督团队。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十六条', '存款人选择存折为支付工具时，各支行（部）应要求存款人签署“人民币个人银行结算账户管理协议”，审查存款人签章，审核无误后，在申请书加盖业务专用章和经办人私章，在账户管理协议上加盖业务专用章和负责人私章。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十七条', '“开立个人银行结算账户申请书”、“人民币个人银行结算账户管理协议”和相关文件的复印件等，应作为银行结算账户管理档案，按会计档案进行管理。该档案的保管期限为银行结算账户撤销后10年。他人代理开立个人银行账户的，应当登记代理人和被代理人的身份信息，留存代理人和被代理人的有效身份证件的复印件或者影印件、委托书原件等，有条件的还应单独留存开户过程的音频或视频等。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十八条', '信息科技部定期生成各支行（部）个人银行结算账户开户清单交运营管理部，由运营管理部集中导入人民银行账户管理系统进行备案。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第二章　账户的开立', '第十九条', '有下情形之一的，各支行（部）有权拒绝开户：（一）对个人身份信息存在疑义，要求出示辅助证件，个人拒绝出示的。（二）个人组织他人同时或者分批开立账户的。（三）有明显理由怀疑开立账户从事违法犯罪活动的。（四）存款人已在本行开立1个（含）以上的Ⅰ类户，再次申请开立Ⅰ类户的。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第三章 　账户的使用', '第二十条', '个人银行结算账户Ⅰ类户用于办理个人存款、购买投资理财产品、转账、消费和缴费支付、支取现金。Ⅱ类户用于办理个人存款、购买投资理财产品以及限定金额的消费和缴费支付。Ⅲ类户只可用于办理限定金额的消费和缴费支付。Ⅱ类户和Ⅲ类户不可用于办理现金存取业务。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第三章 　账户的使用', '第二十一条', '存款人可与本行签订协议，开通非柜面转账业务，约定非柜面渠道向非同名银行账户和支付账户转账的日累计限额、笔数和年累计限额等级，超出限额和笔数时，应到本行柜面办理。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第三章 　账户的使用', '第二十二条', '本行向存款人提供实时到账、普通到账、次日到账等多种转账方式选择，存款人在选择后才能办理业务。除向本人同行账户转账外，存款人通过自助柜员机（含其他具有存取款功能的自助设备）转账的，本行在受理24小时后办理资金转账。在本行受理后24小时内，存款人可以向本行申请撤销转账，各支行（部）应当及时办理。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第三章 　账户的使用', '第二十三条', '单位从其银行结算账户支付给个人银行结算账户的款项，每笔超过5万元的，付款人只要填明合法款项用途，可不提供付款依据。 从单位银行结算账户支付给个人银行结算账户的款项应纳税的，税收代扣单位付款时应向其开户银行提供完税证明。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第三章 　账户的使用', '第二十四条', '各支行（部）应对存款人使用账户情况进行监督，对存款人的可疑支付应按照中国人民银行规定的程序及时报告。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第三章 　账户的使用', '第二十五条', '各支行（部）发现或者收到被冒用身份的个人声明，并确认该账户为假名或虚假代理开户的，应立即停止相关个人银行账户的使用，在征得被冒用人或被代理人同意后予以销户，账户资金列入久悬未取专户管理。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第四章  	罚　则', '第二十六条', '存款人开立、使用个人银行结算账户，不得有下列行为： （一）违反本实施办法规定开立个人银行结算账户。 （二）伪造、变造证明文件欺骗银行开立个人银行结算账户。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第四章  	罚　则', '第二十七条', '各支行（部）在个人银行结算账户的使用中，不得有下列行为：（一）为储蓄账户办理转账结算。（二）违反规定为存款人支付现金或办理现金存入。经查实有上述第二十六条、第二十七条所列行为之一的，按《人民币银行结算账户管理办法》规定，给予警告并处以5000元以上3万元以下的罚款；对该行直接负责的主管人员、直接责任人员按规定给予纪律处分；构成犯罪的，移交司法机关依法追究刑事责任。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第五章	   附　则', '第二十八条', '本实施办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《人民币个人银行结算账户管理实施办法》', '第五章	   附　则', '第二十九条', '本实施办法自发文之日起施行。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第一条', '为规范xxx银行（以下简称“本行”）人民币单位协定存款业务，依据《人民币银行结算账户管理办法》、《支付结算办法》、《人民币单位存款管理办法》等有关规定，制定本办法。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第二条', '本办法所称单位协定存款，是指可以在中华人民共和国境内开立基本存款账户或一般存款账户的法人及其他组织（以下简称“单位”）与本行签订《xxx银行人民币单位协定存款合同》（见附件2），在基本存款账户或一般存款账户之上开立具有结算和协定存款双重功能的协定存款账户，并约定基本存款额度，由本行将协定存款账户中超过该额度的部分按协定存款利率单独计息的一种存款方式。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第三条', '凡申请开立人民币单位协定存款账户的单位，须在本行开立基本存款账户或一般存款账户。客户向开户支行（部）提出申请，填写《xxx银行人民币单位协定存款申请书》（见附件1），由开户支行（部）签署意见，上报本行运营管理部批准后，客户与开户支行（部）签订《xxx银行人民币单位协定存款合同》。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第四条', '《xxx银行人民币单位协定存款合同》应由存款单位的法定代表人或授权代理人与各支行（部）负责人共同签订，合同一式三份，双方各执一份,上报本行运营管理部一份。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第五条', '《xxx银行人民币单位协定存款合同》的双方当事人以存款单位协定存款账户所对应的基本存款账户或一般存款账户存款余额作为基本存款额度，基本存款账户余额不得低于人民币50万元。若账户余额连续三天低于基本存款额度，本行可发出销户通知，终止合同，存款单位未回应的，本行有权直接将协定存款账户利息结清后作为普通账户处理。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第六条', '《xxx银行人民币单位协定存款合同》的有效期为一年。合同期满，如双方均未书面提出终止或修改合同，即视为自动延期一年，原协定存款账户继续使用。两年后仍需继续使用的须与支行（部）重新签订合同。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第七条', '合同期满，存款单位需要销户的，必须于距合同到期日10天前向开户行（部）提交撤销申请书（见附件3），并于到期日办理销户手续。开户行（部）将协定存款账户的存款本息结清后，全部转入其基本存款账户或一般存款账户，同时协定存款账户销户。　  第八条 合同期内，存款单位原则上不得要求销户，如遇特殊情况，须向经办支行（部）提交提前销户申请书（见附件4），各支行（部）在收到提前销户申请后五个工作日内答复并经本行运营管理部审核批准后，按前条规定办理销户手续。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第九条', '各支行（部）按季对协定存款账户结息。协定存款账户中基本存款额度以内的存款按结息日活期存款利率计息；超过基本存款额度的存款按结息日中国人民银行公布的协定存款利率计息。　　第十条  对协定存款账户销户的，如果在结息日销户，超过基本存款额度的存款按协定存款利率计息；如果不在结息日销户，超过基本存款额度的存款从上一结息日起到销户日止，不再按协定存款利率计息，而按销户日活期存款利率计息。　　第十一条  各支行（部）对协定存款账户进行日常管理，不得为存款单位在协定存款账户上办理透支业务。在办理支付业务时，若协定存款账户的余额（大于零）低于基本存款额度，不属于透支。　　第十二条  各支行（部）不得违规办理协定存款业务，否则取消其开办资格，并对违规人员按照本行员工违规违章行为管理规定予以处罚。　　第十三条  对账团队人员应按规定及时与存款单位对账。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第十四条', '协定存款根据“一个账户、一个余额、两个结息积数、两种利率”的管理方式，将账户余额以合同规定的“基本存款额度”为界限分为两部分，账户余额小于或等于“基本存款额度”的部分是所形成的“结算存款累计积数”，按活期存款利率计算存款利息，账户余额大于“基本存款额度”的部分所形成的“协定存款累计积数”，按协定存款利率计算存款利息。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第十五条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《人民币单位协定存款管理办法》', '', '第十六条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第一章   总 则', '第一条', '为规范xxx银行（以下简称“本行”）人民币单位银行结算账户的开立、变更、使用和撤销工作，加强银行结算账户管理，维护经济金融秩序稳定，根据《人民币银行结算账户管理办法》、《人民币结算账户管理办法实施细则》、《人民币银行结算账户管理系统处理办法》和《中国人民银行关于加强支付结算管理防范电信网络新型违法犯罪有关事项的通知》并结合本行实际，制定本实施办法。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第一章   总 则', '第二条', '本办法所称的存款人，是指在本行开立银行结算账户的机关、团体、部队、企业、事业单位和其他组织（以下通称“单位”）。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第一章   总 则', '第三条', '本办法所称的人民币单位银行结算账户是指本行为存款人以单位名称开立的办理资金收付结算的人民币单位活期存款账户。个体工商户凭营业执照以字号或经营者姓名开立的银行结算账户，纳入单位银行结算账户管理。单位银行结算账户按用途分为基本存款账户、一般存款账户、专用存款账户、临时存款账户。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第一章   总 则', '第四条', '单位银行结算账户的存款人只能在银行开立一个基本存款账户。已开立基本存款账户的存款人，不得在本行再次开立基本存款账户。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第一章   总 则', '第五条', '各支行（部）应依法为存款人的银行结算账户信息保密。对单位银行结算账户的存款和有关资料，除国家法律、行政法规另有规定外，有权拒绝任何单位或个人查询。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第六条', '存款人开立基本存款账户、临时存款账户和预算单位开立专用存款账户实行核准制度，经中国人民银行核准后由人民银行核发开户许可证，但存款人因注册验资和增资验资需要开立的临时存款账户除外。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第七条', '基本存款账户是存款人因办理日常转账结算和现金收付需要开立的银行结算账户。下列存款人，可以申请开立基本存款账户: 　　（一）企业法人。 　　（二）非法人企业。 　　（三）机关、事业单位。 　　（四）团级(含)以上军队、武警部队及分散执勤的支（分）队。 　　（五）社会团体。 　　（六）民办非企业组织。 　　（七）异地常设机构。 　　（八）外国驻华机构。 　　（九）个体工商户。 　　（十）居民委员会、村民委员会、社区委员会。 　　（十一）单位设立的独立核算的附属机构。 　　（十二）其他组织。 　　第八条  一般存款账户是存款人因借款或其他结算需要，在基本存款账户开户银行以外的银行营业机构开立的银行结算账户。 　　第九条  专用存款账户是存款人按照法律、行政法规和规章，对其特定用途资金进行专项管理和使用而开立的银行结算账户。对下列资金的管理与使用，存款人可以申请开立专用存款账户: 　　（一）基本建设资金。 　　（二）更新改造资金。 　　（三）财政预算外资金。 　　（四）粮、棉、油收购资金。 　　（五）证券交易结算资金。 　　（六）期货交易保证金。 　　（七）信托基金。 　　（八）金融机构存放同业资金。 　　（九）政策性房地产开发资金。 　　（十）单位银行卡备用金。 　　（十一）住房基金。 　　（十二）社会保障基金。 　　（十三）收入汇缴资金和业务支出资金。 　　（十四）党、团、工会设在单位的组织机构经费。 　　（十五）其他需要专项管理和使用的资金。 　　收入汇缴资金和业务支出资金，是指基本存款账户存款人附属的非独立核算单位或派出机构发生的收入和支出的资金。 　　因收入汇缴资金和业务支出资金开立的专用存款账户，应使用隶属单位的名称。 　　第十条  临时存款账户是存款人因临时需要并在规定期限内使用而开立的银行结算账户。有下列情况的，存款人可以申请开立临时存款账户： 　　（一）设立临时机构。 　　（二）异地临时经营活动。 （三）注册验资。 存款人为时临机构的，只能在其驻在地开立一个临时存款账户，不得开立其他银行结算账户。存款人在异地从事临时活动的，只能在其临时活动地开立一个临时存款账户。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第十一条', '存款人有下列情形之一的，可以在异地开立有关银行结算账户： 　　（一）营业执照注册地与经营地不在同一行政区域（跨省、市、县）需要开立基本存款账户的。 　　（二）办理异地借款和其他结算需要开立一般存款账户的。 　　（三）存款人因附属的非独立核算单位或派出机构发生的收入汇缴或业务支出需要开立专用存款账户的。 　　（四）异地临时经营活动需要开立临时存款账户的。 （五）自然人根据需要在异地开立个人银行结算账户的。 “注册地“是指存款人的营业执照等开户证明文件上记载的住所地。　　第十二条  存款人申请开立基本存款账户,应向银行出具下列证明文件： 　　（一）企业法人，应出具企业法人营业执照正本。 　　（二）非法人企业，应出具企业营业执照正本。 　　（三）机关和实行预算管理的事业单位，应出具政府人事部门或编制委员会的批文或登记证书和财政部门同意其开户的证明；非预算管理的事业单位，应出具政府人事部门或编制委员会的批文或登记证书。 　　（四）军队、武警团级(含)以上单位以及分散执勤的支（分）队，应出具军队军级以上单位财务部门、武警总队财务部门的开户证明。 　　（五）社会团体，应出具社会团体登记证书，宗教组织还应出具宗教事务管理部门的批文或证明。 　　（六）民办非企业组织，应出具民办非企业登记证书。 　　（七）外地常设机构，应出具其驻在地政府主管部门的批文。 　　（八）外国驻华机构，应出具国家有关主管部门的批文或证明;外资企业驻华代表处、办事处应出具国家登记机关颁发的登记证。 　　（九）个体工商户，应出具个体工商户营业执照正本。 　　（十）居民委员会、村民委员会、社区委员会，应出具其主管部门的批文或证明。 　　（十一）独立核算的附属机构，应出具其主管部门的基本存款账户开户登记证和批文。 （十二）其他组织，应出具政府主管部门的批文或证明。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第十三条', '存款人申请开立一般存款账户，应向银行出具其开立基本存款账户规定的证明文件、基本存款账户开户登记证、机构信用代码证和下列证明文件： 　　（一）存款人因向银行借款需要，应出具借款合同。 　　（二）存款人因其他结算需要，应出具有关证明。 　　第十四条  存款人申请开立专用存款账户，应向银行出具其开立基本存款账户规定的证明文件、基本存款账户开户登记证、机构信用代码证和下列证明文件: 　　（一）基本建设资金、更新改造资金、政策性房地产开发资金、住房基金、社会保障基金，应出具主管部门批文。 　　（二）财政预算外资金，应出具财政部门的证明。 　　（三）粮、棉、油收购资金，应出具主管部门批文。 　　（四）单位银行卡备用金，应按照中国人民银行批准的银行卡章程的规定出具有关证明和资料。 　　（五）证券交易结算资金，应出具证券公司或证券管理部门的证明。 　　（六）期货交易保证金，应出具期货公司或期货管理部门的证明。 　　（七）金融机构存放同业资金，应出具其证明。 　　（八）收入汇缴资金和业务支出资金，应出具基本存款账户存款人有关的证明。 　　（九）党、团、工会设在单位的组织机构经费，应出具该单位或有关部门的批文或证明。 （十）其他按规定需要专项管理和使用的资金，应出具有关法规、规章或政府部门的有关文件。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第十五条', '存款人申请开立临时存款账户,应向银行出具下列证明文件（一）临时机构，应出具其驻在地主管部门同意设立临时机构的批文。 （二）异地建筑施工及安装单位，应出具其营业执照正本或其隶属单位的营业执照正本，以及施工及安装地建设主管部门核发的许可证或建筑施工及安装合同。 （三）异地从事临时经营活动的单位，应出具其营业执照正本以及临时经营地工商行政管理部门的批文。 （四）注册验资资金，应出具工商行政管理部门核发的企业名称预先核准通知书或有关部门的批文。 本条第二、三项还应出具其基本存款账户开户登记证、机构信用代码证。存款人因增资验资需要开立银行结算账户的，应出具其基本存款账户开户许可证、机构信用代码证、股东会或董事会决议等证明文件。建筑施工及安装单位企业在异地同时承建多个项目的，可根据建筑施工及安装合同开立不超过项目合同个数的临时存款账户。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第十六条', '存款人需要在异地开立单位银行结算账户，除出具本办法第十二条、十三条、十四条、十五条规定的有关证明文件外，应出具下列相应的证明文件： 　　（一）经营地与注册地不在同一行政区域的存款人，在异地开立基本存款账户的，应出具注册地中国人民银行分支行的未开立基本存款账户的证明。 　　（二）异地借款的存款人，在异地开立一般存款账户的，应出具在异地取得贷款的借款合同。 （三）因经营需要在异地办理收入汇缴和业务支出的存款人，在异地开立专用存款账户的，应出具隶属单位的证明。 属本条第二、三项情况的，还应出具其基本存款账户开户登记证、机构信用代码证。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第十七条', '存款人申请开立单位银行结算账户时，可由法定代表人或单位负责人直接办理，也可授权他人办理。 由法定代表人或单位负责人直接办理的，除出具相应的证明文件外，还应出具法定代表人或单位负责人的身份证件；授权他人办理的，除出具相应的证明文件外，还应出具其法定代表人或单位负责人的授权书及其身份证件，以及被授权人的身份证件。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第十八条', '单位开立银行结算账户的名称应与其提供的申请开户的证明文件的名称全称相一致。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第十九条', '存款人申请开立基本存款账户时应同时申请机构信用代码证，填写“机构信用代码证申请表”并加盖单位公章，出具开立基本存款账户规定的证明文件和人民很行规定格式的单位介绍信。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十条', '存款人申请开立单位银行结算账户时，应填制“开立单位银行结算账户申请书”和“单位人民币银行结算账户管理协议”并加盖单位公章。存款人有组织机构代码、上级法人或主管单位的，应在“开立单位银行结算账户申请书”上如实填写相关信息。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十一条', '存款人申请开立单位银行结算账户时，应向各支行（部）预留开户单位印鉴，在印鉴卡上注明启用日期、户名全称、账号、单位地址、联系电话等。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十二条', '存款人预留签章为该单位的公章或财务专用章加其法定代表人（单位负责人）或其授权的代理人的签名或盖章。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十三条', '存款人在申请开立单位银行结算账户时，其申请开立的银行结算账户的账户名称、出具的开户证明文件上记载的存款人名称以及预留银行签章中公章或财务专用章的名称应保持一致，但下列情形除外：　　（一）因注册验资开立的临时存款账户，其账户名称为工商行政管理部门核发的“企业名称预先核准通知书”或政府有关部门批文中注明的名称，其预留银行签章中公章或财务专用章的名称应是存款人与银行在银行结算账户管理协议中约定的出资人名称；　　（二）预留银行签章中公章或财务专用章的名称依法可使用简称的，账户名称应与其保持一致；'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十四条', '各支行（部）应遵循“了解你的客户”原则，对存款人提交的开户申请资料及开户申请书填写的事项的真实性、完整性、合规性进行认真审查。开户申请书填写的事项齐全，符合开立基本存款账户、临时存款账户和预算单位专用存款账户条件的，各支行（部）应将存款人的开户申请书、相关的证明文件和银行审核意见等开户资料报送总行清算团队，统一报送人民银行，经其核准后办理开户手续；符合开立一般存款账户、其他专用存款账户条件的，各支行（部）应办理开户手续，并于开户之日起5个工作日内在中国人民银行账户管理系统中录入备案。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十五条', '各支行（部）对存在法定代表人或者负责人对单位经营规模及业务背景行情况不清楚、注册地和经营地均在异地等异常情况的单位，应加强对单位开户意愿的核查，必要时应上门核实 。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十六条', '各支行（部）应对法定代表人或者负责人进行面签并留存视频、音频资料等，开户初期原则上不开通非柜面业务，待后续了解后再审慎开通。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第二章   账户开立', '第二十七条', '有下列情形之一的，各支行（部）有权拒绝其开户：（一）对单位信息存在疑义，要求出示辅助证件，单位拒绝出示的。（二）有明显理由怀疑开立账户从事违法犯罪活动的。（三）单位被全国企业信用信息公示系统列入“严重违法失信企业名单”，以及经核实其注册地址不存在或者虚构经营场所的。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第二十八条', '存款人银行结算账户信息变更应于5个工作日内向各支行（部）提出银行结算账户的变更申请，并出具有关部门的证明文件，填写“变更银行结算账户申请书”并加盖单位公章。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第二十九条', '各支行（部）接到存款人的变更通知后，应及时办理变更手续。存款人申请变更核准类银行结算账户的存款人名称、法定代表人或单位负责人的，各支行（部）应在接到变更申请后的2个工作日内，将存款人的“变更银行结算账户申请书”、开户许可证以及有关证明文件报送总行清算团队，由专人统一报送中国人民银行xx支行换发新的开户许可证，其他未涉及开户许可证内容变更事项的应将存款人的“变更银行结算账户申请书”和有关证明文件报中国人民银行xx支行核准。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十条', '存款人申请撤销银行结算账户时，应填写“撤销银行结算账户申请书”并加盖单位公章，必须与存款人核对银行结算账户存款余额，收回各种重要空白凭证和开户许可证，经核对无误后方可在人行账户管理系统和综合业务系统办理销户手续。存款人未按规定交回各种重要空白凭证和开户许可证的，应出具有关证明，造成损失的，由其自行承担。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十一条', '支行（部）在收到存款人撤销银行结算账户的申请后，对于符合销户条件的，应在2个工作日内办理撤销手续。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十二条', '存款人尚未清偿其各支行（部）债务的，不得申请撤销该账户。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十三条', '存款人因注册验资或增资验资开立临时存款账户后，需要在临时存款账户有效期届满前退还资金的，应出具工商行政管理部门的证明；无法出具证明的，应于账户有效期届满后办理销户退款手续。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十四条', '未获得工商行政管理部门核准登记的单位，在验资期满后，应向银行申请撤销注册验资临时存款账户，其账户资金应退还给原汇款人账户。注册验资资金以现金方式存入，出资人需提取现金的，应出具缴存现金时的现金缴款单原件及其有效身份证件。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十五条', '存款人存在以下情形，存款人超过规定期限未主动办理撤销银行结算手续的，各支行（部）有权停止其银行结算账户的对外支付。（一）被撤并、解散、宣告破产或关闭的。 （二）注销、被吊销营业执照的。（三）开户之日起6个月内无交易记录的。存款人因上述第（一）、（二）项原因撤销银行结算账户的，应先撤销一般存款账户、专用存款账户、临时存款账户，将账户资金转入基本存款账户后，方可办理基本存款账户的销户。存款人因迁址或其他原因撤销基本存款账户后，需要重新开立基本存款账户的，应在撤销其原基本账户10日内重新开立基本存款账户。存款人在本行撤销银行结算账户后重新开立银行结算账户时，重新开立的银行结算账户可自开立之日起办理付款业务。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十六条', '各支行（部）对一年未发生收付活动且未欠本行债务的账户,应通知单位自发出通知之日起30日内办理销户手续，逾期视同自愿销户，未划转款项列入久悬未取专户管理。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第三章   账户变更与撤销', '第三十七条', '存款人申请变更、撤销单位银行结算账户时，可由法定代表人或单位负责人直接办理，也可授权他人办理。　　由法定代表人或单位负责人直接办理的，除出具相应的证明文件外，还应出具法定代表人或单位负责人的身份证件；授权他人办理的，除出具相应的证明文件外，还应出具法定代表人或单位负责人的身份证件及其出具的授权书，以及被授权人的身份证件。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第四章   账户使用', '第三十八条', '基本存款账户是存款人的主办账户。存款人日常经营活动的资金收付及其工资、奖金和现金的支取，应通过该账户办理。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第四章   账户使用', '第三十九条', '一般存款账户用于办理存款人借款转存、借款归还和其他结算的资金收付。该账户可以办理现金缴存，但不得办理现金支取。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第四章   账户使用', '第四十条', '专用存款账户用于办理各项专用资金的收付。单位银行卡账户的资金必须由其基本存款账户转账存入。该账户不得办理现金收付业务。财政预算外资金专用存款账户不得支取现金。基本建设资金、更新改造资金、政策性房地产开发资金、金融机构存放同业资金账户需要支取现金的，应在开户时报中国人民银行xx支行批准。粮、棉、油收购资金、社会保障基金、住房基金和党、团、工会经费等专用存款账户支取现金应按照国家现金管理的规定办理。收入汇缴账户除向其基本存款账户或预算外资金财政专用存款户划缴款项外，只收不付，不得支取现金。业务支出账户除从其基本存款账户拨入款项外，只付不收，其现金支取必须按照国家现金管理的规定办理。各支行（部）应按照各项规定和国家对粮、棉、油收购资金使用管理规定加强监督，对不符合规定的资金收付和现金支取，不得办理。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第四章   账户使用', '第四十一条', '临时存款账户用于办理临时机构以及存款人临时经营活动发生的资金收付。 　　临时存款账户应根据有关开户证明文件确定的期限或存款人的需要确定其有效期限。存款人在账户的使用中需要延长期限的，应在有效期限内向开户本行提出申请，填写“临时存款账户展期申请书”，并加盖单位公章，连同临时存款账户开户许可证及开立临时存款账户时需要出具的相关证明文件 ，由本行报中国人民银行当xx支行核准后办理展期。临时存款账户的有效期最长不得超过2年。 　　临时存款账户支取现金，应按照国家现金管理的规定办理。 　　第四十二条  注册验资的临时存款账户在验资期间只收不付，注册验资资金的汇缴人应与出资人的名称一致。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第四章   账户使用', '第四十三条', '存款人开立单位银行结算账户，自正式开立之日起3个工作日后，方可办理付款业务。但注册验资的临时存款账户转为基本存款账户和因借款转存开立的一般存款账户除外。本办法所称“正式开立之日”具体是指：对于核准类单位银行结算账户，“正式开立之日”为中国人民银行xx支行的核准日期；对于非核准类单位银行结算账户，“正式开立之日”为各支行（部）为存款人办理开户手续的日期。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第五章   账户管理', '第四十四条', '本行对单位银行结算账户实施集中审核、统一管理。运营管理部负责各支行（部）银行结算账户开立和使用的管理，监督和检查其执行本办法的情况，纠正违规开立和使用单位银行结算账户的行为。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第五章   账户管理', '第四十五条', '各支行（部）运营主管为人民币银行结算账户审核管理人员，负责审核所辖支行（部）所有账户的开立、变更、使用和撤销，并确保存款人开立的账户在综合业务系统、人民币银行结算账户管理系统、中国人民银行征信中心机构信用代码管理系统和同城实时清算系统账户四个系统中所有录入信息的一致性、准确性。工作职责包括但不限于：（一）负责审查开户、变更资料的完整性和合规性，准确录入人民币银行结算账户管理系统和中国人民银行征信中心机构信用代码管理系统，及时报送清算团队审核；（二）做好开、销户登记簿的记载工作；（三）负责所有久悬未取户的确认及清理工作；（四）组织开展单位银行结算账户年检工作；（五）负责本单位银行结算账户的管理、检查和指导。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第五章   账户管理', '第四十六条', '各支行（部）主对公柜员为人民币银行结算账户日常管理人员，工作职责包括但不限于：（一）按照《人民币银行结算账户管理办法》和审核岗的审核要求进行银行结算账户的开立、变更、撤销、结转久悬未取户等业务操作；（二）负责所有银行结算账户日常的管理工作，监督其按照《人民币银行结算账户管理办法》的规定使用其银行结算账户。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第五章   账户管理', '第四十七条', '清算团队为本行账户资料复审、管理部门，工作职责包括但不限于：（一）负责复审各支行（部）的单位银行结算账户开立、变更、撤销等资料信息，需核准的账户及时送人民银行xx支行核准；（二）负责复审各支行（部）的开户单位机构信用代码证申请资料，及时报送人民银行xx支行核准打印机构信用代码证；（三）准确返还经复审或核准的各支行（部）开立、变更、撤销账户中的客户资料和机构信用代码证，做好交接记录；（四）负责各支行（部）单位银行结算账户开立、变更、撤销资料的整理、扫描、归档，同时按档案管理要求保存完整的纸质资料；（五）负责各支行（部）单位银行结算账户预留印鉴信息的建库、变更、销模等工作；（六）负责对各支行（部）单位银行结算账户使用情况进行日常监督。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第五章   账户管理', '第四十八条', '各支行（部）应对已开立的单位银行结算账户实行年检制度，检查开立的银行结算账户的合规性，核实开户资料的真实性；对不符合本办法规定开立的单位银行结算账户，应予以撤销。各支行（部）应当至少每季度排查单位是否属于严重违法企业，情况属实的，应当在3个月内暂停其业务，逐步清理。对经核实的各类银行结算账户的资料变动情况，应及时变更账户信息并报告中国人民银行xx支行。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第五章   账户管理', '第四十九条', '各支行（部）对存款人的可疑支付应按照反洗钱规定的程序及时报告。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第六章   账户档案保管', '第五十条', '各支行（部）的人民币单位银行结算账户开户、销户、变更等相关资料按规定报送清算团队，作为会计档案进行管理，该档案的保管期限为单位银行结算账户撤销后十年。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第六章   账户档案保管', '第五十一条', '开立人民币单位银行结算账户需要保管的开户资料：（一）基本存款户：开立单位银行结算账户申请书、单位人民币银行结算账户管理协议、使用支付密码协议书、人民银行核准的开户许可证副本、营业执照正本复印件、单位负责人身份证件复印件、机构信用代码证复印件，授权他人办理的，法人或负责人授权委托书原件以及被授权人身份证件复印件；（二）专用存款户：开立单位银行结算账户申请书、单位人民币银行结算账户管理协议、使用支付密码协议书、基本存款账户开立所规定的证明文件、基本存款账户开户许可证正本复印件、上级主管部门（基本存款账户存款人）同意其开户的文件或证明、财政部门同意开户的证明（预算单位专户）、单位负责人身份证件复印件、授权他人办理的，法人或负责人授权委托书原件以及被授权人身份证件复印件；（三）一般存款户：开立单位银行结算账户申请书、单位人民币银行结算账户管理协议、使用支付密码协议书、基本存款账户开立所规定的证明文件、基本存款账户开户许可证正本复印件、存款人因向银行借款需要出具的借款合同或存款人因其他结算需要出具的有关证明、授权他人办理的，法人或负责人授权委托书原件以及被授权人身份证件复印件。（四）临时存款户：临时机构：开立单位银行结算账户申请书、单位人民币银行结算账户管理协议、使用支付密码协议书、驻在地主管部门同意设立临时机构的批文、基本户开户许可证正本复印件、单位负责人身份证件复印件，临时账户开户许可证副本、授权他人办理的，法人或负责人授权委托书原件以被授权人身份证件复印件；异地建筑施工及安装单位：开立单位银行结算账户申请书、单位人民币银行结算账户管理协议、使用支付密码协议书、营业执照正本或隶属单位营业执照正本复印件、基本户开户许可证正本复印件、建筑施工及安装合同、单位负责人身份证件复印件、临时账户开户许可证副本、授权他人办理的，法人或负责人授权委托书原件以及被授权人身份证件复印件；异地从事临时经营活动的单位：开立单位银行结算账户申请书、单位人民币银行结算账户管理协议、使用支付密码协议书、营业执照正本以及临时经营地工商行政管理部门的批文复印件、基本户开户许可证正本复印件、单位负责人身份证件复印件，临时账户开户许可证副本、授权他人办理的，法人或负责人授权委托书原件以及被授权人身份证件复印件；注册验资资金：开立单位银行结算账户申请书、单位人民币银行结算账户管理协议、工商行政管理部门核发的企业名称预先核准通知书；'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第六章   账户档案保管', '第五十二条', '变更人民币银行结算账户需要保管的变更资料：变更银行结算账户申请书、单位人民币银行结算账户管理协议、变更后新的开户资料（存款人提供与原开户资料一致但内容已变更）。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第六章   账户档案保管', '第五十三条', '撤销人民币单位银行结算账户需要保管的撤销资料：撤销银行结算账户申请书。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第六章   账户档案保管', '第五十四条', '单位提供的所有证件复印件须加盖单位公章、开户银行加盖“与原件核对一致”章和核对人私章。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第七章   罚  则', '第五十五条', '各支行（部）在单位银行结算账户开立中不得有下列行为：（一）违反本办法规定为存款人多头开立单位银行结算账户；（二）明知或应知是单位资金，而允许以自然人名称开立账户存储。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第七章   罚  则', '第五十六条', '各支行（部）在单位银行结算账户使用中不得有下列行为： （一）提供虚假开户申请资料欺骗人民银行开立基本存款账户、临时存款账户、预算单位专用存款账户。 （二）开立或撤销单位银行结算账户，未按本办法规定在其基本存款账户开户许可证上予以登记、签章或通知相关开户银行。 （三）违反规定为存款人支付现金或办理现金存入。 （四）超过期限或未向中国人民银行报送账户开立、变更、撤销等资料。 　　各支行（部）如发生以上违规行为，将追究相关责任人员，并严格按照本行相关制度的规定进行处理。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第七章   附  则', '第五十七条', '本办法xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《人民币单位银行结算账户管理实施办法》', '第七章   附  则', '第五十八条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第一章  总 则', '第一条', '为规范xxx银行（以下简称“本行”）人民币同业银行结算账户的开立和使用，加强对同业银行结算账户的管理，确保本行资金安全，根据《人民币银行结算账户管理办法》和《中国人民银行关于加强银行业金融机构人民币同业银行结算账户管理的通知》等规定，特制定本管理办法。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第一章  总 则', '第二条', '本办法所称的同业银行结算账户是指本行在其他银行或其他银行在本行开立的用于资金划转的人民币银行存款账户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第一章  总 则', '第三条', '同业银行结算账户按用途分为结算性和投融资性两类。结算性同业银行结算账户是指用于代理现金解缴、代理支付结算等支付结算业务的账户；投融资性同业银行结算账户是指用于同业存款（结算性存款除外）、同业借款、买入返售（卖出回购）、同业投资等融资和投资业务的账户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第二章 职责与权限', '第四条', '营业部负责为其他银行开立、变更和撤销结算性同业银行结算账户，确保人行系统与核心系统账户信息、状态保持一致。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第二章 职责与权限', '第五条', '金融市场部根据业务需要提出同业银行结算账户的开户申请。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第二章 职责与权限', '第六条', '运营管理部为同业银行结算账户的归口管理部门，具体负责：1.同业银行结算账户的审核；2.投融资性同业银行结算账户在核心系统中开立、变更和撤销。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第二章 职责与权限', '第七条', '本行分管行长负责投融资性同业银行结算账户的审批。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第二章 职责与权限', '第八条', '清算中心具体负责：1.投融资性同业银行结算账户开立、变更与撤销资料的审核及在人行系统中进行备案；2.投融资性同业银行结算账户资金的清算；3.投融资性同业银行结算账户资金划转的形式要件审核；4.投融资性同业银行结算账户的核对，确保人行系统与核心系统账户信息、状态保持一致。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第二章 职责与权限', '第九条', '对账中心负责同业银行结算账户的对账工作，按照本行对账管理办法进行账务核对，并及时收回对账回执。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十条', '其他银行在本行开立结算性同业银行结算账户的，需提供真实、有效、完整的开户资料至本行营业部开户，由营业部负责办理开户手续。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十一条', '其他银行在本行开立投融资性同业银行结算同业存放账户的，需提供真实、有效、完整的开户资料，履行相关审批手续后，在本行系统内开立投融资性同业银行结算账户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十二条', '本行根据业务需要可在其他银行开立同业银行结算账户。由金融市场部先行履行开户审批手续（附件 1、2），备齐所需资料至其他银行办理同业银行结算账户开户手续。开户后凭回执至本行开立相应的内部账户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十三条', '履行开户审批手续的具体流程是：金融市场部根据业务需要提出同业银行结算账户开户申请并填制开户申请书，申请书由部门总经理及部门分管行长签署意见后附开户资料一并交清算中心初审资料的真实性、合规性、完整性，初审通过后交运营管理部总经理审核，最后由本行分管运营管理部的副行长审批通过。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十四条', '运营管理部根据审批通过的开户申请书在核心系统中开立账户，开户资料统一由清算中心保管，并由清算中心将账户信息在人行账户管理系统中核准或备案。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十五条', '同业银行结算账户应当严格按照《人民币银行结算账户管理办法》等银行结算账户制度管理。除开立基本存款账户外，其他账户一律按照专用存款账户开立，有异地开立专用存款账户需要的，可以异地开立。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十六条', '在我行开立投融资性同业银行结算账户原则上应当由存款银行一级法人和一级分行开立，二级分行确有开立需要的，应当由一级法人进行授权，不得转由一级分行授权。存款银行支行及以下分支机构不得在我行开立投融资性同业银行结算账户，不得异地开立同业银行结算账户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十七条', '其他银行分支机构在本行开立同业银行结算账户应当逐户获得其银行一级法人的内部书面授权。存款银行一级法人应当以正式发文形式进行授权，明确分支机构开立同业银行结算账户的开户银行、账户名称、用途以及经办人员等。其他银行为全国性银行，可以授权一级分行授权。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十八条', '本行在其他银行开立投融资性同业银行结算账户的，开户银行应当为二级分行及以上营业机构，不得在支行及以下分支机构开立同业银行结算账户。本行辖内支行也不得为异地（跨县市）存款银行开立同业银行结算账户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第十九条', '本行为存款银行开立投融资性同业银行结算账户的，应当为开户银行二级分行及以上营业机构。支行及以下分支机构不得作为投融资性同业银行结算账户的开户银行，也不得为异地（跨县市）存款银行开立同业银行结算账户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第二十条', '其他银行申请开立同业银行结算账户时，除按照《人民币银行结算账户管理办法》等银行账户制度规定出具有关开户证明文件外，还应当出具银行业监督管理部门颁发的金融许可证和经营范围批准文件；非一级法人开户的，还应当出具一级法人（或者一级分行）授权书原件。本行应当进行审查，对开立同业银行结算账户用途超越经营范围的，应当拒绝办理。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第三章 同业账户的开立', '第二十一条', '本行应当提高对同业开户的审核要求，采取多种措施对开户证明文件的真实性、完整性和合规性以及存款银行开户意愿真实性进行审核。1.执行同一银行分支机构首次开户面签制度，由本行两名以上工作人员共同亲见存款银行法定代表人（单位负责人）在开户申请书和银行账户管理协议上签名确认。2.严格执行开户证明文件原件的审核要求，不得以审核复印件或影像件代替，必须采取双人复核制度。3.认真审查法定代表人（单位负责人）身份证件的真伪，其中居民身份证应当通过联网核查公民身份信息系统核实，其他身份证件通过向公安机关查询等方式进一步核实。有条件的地区，通过工商、税务和银行业监督管理部门的政府信息公开网站查询营业执照、税务登记证件和金融许可证的有效性。4.至少采取下列 2 种方式对存款银行开户意愿的真实性进行核实：一是通过大额支付系统向存款银行一级法人进行核实（查询查复方法见附件 3）；二是到存款银行上门核实或者通过本银行在异地的分支机构上门核实。5.对账地址（联系地址）应当为存款银行经营所在地或者工商注册地的地址。6.完整留存对开户意愿和开户证明文件真实性核实的纸质、视频、电话等记录。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第四章  同业账户的使用、权限与管理', '第二十二条', '同业银行结算账户用于办理资金清算，未经中国人民银行核准，不得办理现金存取。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第四章  同业账户的使用、权限与管理', '第二十三条', '办理资金划转时，根据本行转授权办法，由金融市场部经办人提出申请，指定复核人复核，总经理审核，向清算中心出具经审批的“资金划转通知书”及合同办理资金划转。超出权限的，报计划财务部、分管行长审核，最终由本行行长审批。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第四章  同业账户的使用、权限与管理', '第二十四条', '本行应切实落实同业银行结算账户日常管理工作：1.本行应根据本办法规定和账户管理的约定使用同业银行结算账户，不得违规和超范围使用同业银行结算账户。2.严格执行开户生效日制度，同业银行结算账户自正式开立之日起3个工作日后方可办理付款业务。3.按照规定与存款银行及时对账。对方银行收到对账单或者对账信息后，应当及时核对账务并在规定期限内反馈，本行做好催回复工作，收集齐全对账回执。4.加强对同业银行结算账户使用情况的监测，对账户使用存在可疑情形的，应当及时联系存款银行进行核实，并在 2个工作日内向人民银行当地分支机构报告。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第五章  同业账户的变更与撤销', '第二十五条', '同业银行结算账户开户资料相关信息发生变更时，应按《人民币银行结算账户管理规定》及时向开户银行申请账户信息变更。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第五章  同业账户的变更与撤销', '第二十六条', '严格执行久悬管理制度，因业务开展需要等特殊情况的，开户银行可以不予撤销久悬账户，但应当停止账户支付；停止支付时间超过 5 年的，应当销户。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第五章  同业账户的变更与撤销', '第二十七条', '严格执行年检制度，对于不符合开立条件的，应当立即停止支付，并通知存款银行办理销户手续。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第六章  罚	 则', '第二十八条', '在同业银行结算账户管理过程中，如发生违规违章行为的，按本行员工违规违章行为管理规定，追究相关人员的责任。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第七章  附	 则', '第二十九条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《人民币同业银行结算账户管理办法》', '第七章  附	 则', '第三十条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《会计业务印章管理办法》', '第十二章   附  则', '第四十四条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第一章  总  则', '第一条', '为了进一步强化xxx银行（以下简称“本行”）会计工作，提高会计工作核算质量和管理水平，逐步实现会计工作制度化、规范化、现代化，充分发挥会计工作在加强本行经营管理、提高经济效益中的作用，根据《中华人民共和国会计法》、《会计基础工作规范》、《银行会计基本规范指导意见》和《江苏省农村信用社系统会计工作规范化等级管理实施办法》以及本行会计工作有关规定，特制定本办法。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第一章  总  则', '第二条', '实施会计工作规范化，是本行“深化改革、规范行为、提高效益”的内在要求，同时也是当前和今后一个时期本行财会工作的长期任务。会计工作规范化管理在本行的领导下，按照“统一领导、统一标准、统一推进”的原则组织实施。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第一章  总  则', '第三条', '会计工作规范化的等级考核以及相应会计工作等级的确认，均以辖内各支行(部)作为考核对象，按照本办法的规定，考核确认会计工作等级。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第一章  总  则', '第四条', '会计工作规范化实行等级制。等级分为：会计工作达标单位（简称：达标）、会计工作三级单位（简称：三级）、会计工作二级单位（简称：二级）、会计工作一级单位（简称：一级）四个等级。各支行（部）的会计工作按本办法规定经考核确认达到上述等级的，即为会计工作规范化相应的等级单位。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第二章  组织与领导', '第五条', '各支行（部）要加强会计工作规范化的组织和管理。（一）各支行（部）要成立由行长牵头的规范化工作领导小组和考核小组，全面负责该项工作的组织实施。（二）各支行（部）运营主管负责该项工作的具体实施，并做好日常会计工作的检查监督。（三）各支行（部）制订会计工作规范化的长远目标规划和具体实施方案，明确落实责任人。（四）充分发挥运营主管的检查监督作用，按照会计制度的要求，认真对照考核标准，定期或不定期地对所属机构进行检查辅导监督，适时填制检查辅导报告，做到时期考核（监督）和时点考核（监督）相结合，通过检查考核，全面促进本行会计工作规范化。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第六条', '全面实行会计工作运营主管负责制。运营主管业务上接受运营管理部的管理、指导、检查和监督，经本行授权全面负责本支行（部）的内部控制和会计管理工作。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第七条', '本行全面实行会计业务检查和事后监督制度。建立会计检查制度，运营管理部负责对辖内支行（部）会计工作的检查、辅导和监督，形成检查、辅导报告。本行每年至少进行一次会计制度执行情况的检查，对各支行（部）会计出纳工作进行综合评价，并形成检查报告。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第八条', '运营管理部全面实行会计人员岗前培训和在岗后续教育制度。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第九条', '本行全面实行岗位责任制和重要岗位人员定期轮换制。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第十条', '各支行（部）会计核算必须遵循：客观性原则、相关性原则、可比性原则、及时性原则、明晰性原则、实质重于形式原则、重要性原则、谨慎性原则等会计核算一般原则。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第十一条', '会计核算必须坚持双人临柜、钱账分管（综合柜员除外）；凭证合法，传递及时；科目账户，使用正确；当时记账，账折见面；现金收入，先收款后记账；现金付出，先记账后付款；转账业务，先借后贷；他行票据，收妥抵用；有账有据，账据相符；账表凭证，换人复核；当日结账，总分核对；内外账务，定期核对；印、证分管分用；重要单证，严格管理；会计档案，完整无损；人员变动，交接清楚等十六项基本规定。会计核算要达到“八相符”，即账账、账款、账实、账表、账据、账簿、账卡（折）、内外账务全部相符；账务处理做到“五无”，即账务无积压、结算无事故、计息无差错、记账无串户、存款无透支。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第十二条', '会计凭证是记录经济业务发生和完成情况的书面证明，是记账的重要依据。会计凭证根据其种类可分为原始凭证和记账凭证。从外单位取得的原始凭证，必须盖有单位的公章（或财务印鉴章）。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第十三条', '记账凭证必须要素齐全，一般应具备下列基本要素：填制凭证的日期；收、付款人的户名、账号和开户行；货币、金额及借贷方向；经济业务摘要和附件张数；银行办理业务的印章及经办、复核人员的签名或盖章；凭证编号等。各种凭证应按其用途、范围正确使用；受理凭证时，应根据各有关业务的具体要求进行认真审查，做到内容完整，符合规定；凭证传递应贯彻“先外后内、先急后缓，手续严密，保证安全”的原则；内部凭证由专门人员负责传递，不得通过客户传递。会计凭证上应由客户填写的内容，未经客户授权，内部人员不得代办。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第三章  规范化管理的基本规定与要求', '第十四条', '会计账簿与会计报表一、会计账簿㈠会计账簿的设置必须符合会计基本制度的规定和各种会计业务的需要。会计账簿包括总账、分户账（明细账）、登记簿。计算机打印的会计账簿必须数据衔接、编号连续，并经审核无误后装订成册。经管账簿人员及运营主管必须签章，防止账页的散失和被抽换，保证会计资料的完整性。㈡会计账簿的记载必须依据审核无误的会计凭证进行，并做到数字准确、摘要简明、字迹清楚。操作员必须根据合法有效的会计凭证进行数据输入。各项业务应实时处理，并做到日清月结。㈢计算机操作员不得自制凭证上机操作。凡涉及开销账户、错账更正、调整计息积数、调整利率、启动计息、修改结息标志、修改客户密码、存款查询、取消存款挂失、存款冻结、存款解冻等特殊会计业务的操作，均须经运营主管在有关凭证或登记簿上签章批准后办理。㈣由计算机打印输出的有关数据信息必须按时进行。二、会计报表㈠业务状况表。每月末营业终了生成、次日打印。6月末增加输出半年报，3月、9月末增加输出季报，11月末增加输出试算平衡表。㈡资产负债表。每季末次日打印。㈢损益表。每季末营业终了生成、打印。三、账表整理与装订㈠总账。总账账页按科目顺序号分月装订，归档保管。㈡分户账。年度中间输出的满页账页和结清户的账页，以账户类型分类，按账号页次顺序排列妥善保管。年度终了全部账页输出后，一并按账户类型、账号顺序、页次排列整理加填账首、目录装订，按档案保管期限登记归档。㈢登记簿。各种业务登记簿分类别按年归类（开销户登记簿、挂失登记簿除外），登记归档保管。㈣会计报表。业务状况表（日报）、余额表按月装订，月（季）度业务状况表、资产负债表、损益明细表按年装订；年度业务状况报告表、资产负债表、损益明细表随其他本年度决算资料合并装订，分别保管期限登记归档。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第四章  规范化管理考核内容', '第十五条', '规范化管理考核内容依据各支行（部）会计、出纳、结算、联行等相关制度分为十个方面。一、会计工作管理㈠根据会计核算管理的要求和制度规定设置会计机构，根据业务需要，按照效率和控制原则科学合理地设置会计岗位，配备会计人员。㈡运营主管应精通会计专业知识，具备相应的会计专业技术职务，具有组织、领导会计出纳工作的能力，能有计划、有目的、有重点、有秩序地开展会计出纳工作，阶段性成果明显；会计出纳人员应具有专业技能、持证上岗。会计出纳人员必须持有：《会计人员从业资格证》、《反假货币上岗资格证书》。㈢会计人员保持相对稳定，任免、调动符合《中华人民共和国会计法》有关规定。㈣业务规章学习制度正常，岗位培训定期开展。㈤运营主管坐班制度严格，岗位监督职责切实履行。㈥财会管理制度严格，检查辅导定期开展。㈦岗位责任制和质量考核制度健全，以岗定责，定期考核，奖惩分明。二、会计基本规定㈠按规定执行金融企业的会计核算基本规定。㈡按规定正确使用会计科目和内部账户（含表外科目）。㈢按规定执行人民币银行账户管理细则和储蓄实名制的有关规定。㈣严格内部制约制度，认真做好复核工作和事后监督。㈤严格按会计出纳制度规定正确办理各类会计业务。㈥会计出纳人员变动、短期离职均应办理交接，手续清楚。㈦会计电算化业务管理严密，制定了严格的会计电算化操作管理制度，做到岗位明确，责任清楚，安全可靠。计算机生成的凭证、账簿、报表符合电算化的有关规定。计算机生成的各类会计核算资料定期打印且编号连续。三、会计核算㈠会计凭证使用正确，受理凭证要素齐全，凭证传递准确、及时、手续严密。㈡按日结账，总分核对，账务核算必须达到“八相符”。㈢按规定设置和使用会计账簿，账、簿、表、卡设置齐全，遵循记账规则和计息规定，按规定更正错账，按规定办理年度会计决算和账务结转。㈣按照有关制度规定及时编报会计报告，会计报告内容完整、及时、真实、准确，签章齐全，各种会计报表之间相关数字衔接一致。四、会计档案管理㈠按规定装订账簿、报表。㈡各种会计核算资料按制度要求收集整理，立卷归档，完整无缺，专人专库（室）保管。磁性介质档案按规定备份、归档和保管。㈢严格遵守会计档案调（查）阅和销毁制度。五、出纳业务㈠现金收付双人临柜，换人复核（综合柜员制除外）；实行综合柜员制的营业支行必须达到实施综合柜员制的基本条件；当日核对账款；午休及营业终了所有现金、金银及有价单证等要扫数入库；中午值班按规定办理交接手续并登记有关登记簿，明确责任。营业期间大额现金支付执行人民银行规定。㈡票币挑剔符合规定；票币整点及时准确；出纳错款按规定处理；无空、溢库和白条或实物抵库现象；杜绝假币流入、出；不得拒收、换辅残币。㈢严格库款管理，坚持双人管库，双人守库，钥匙分管和单线交接制度。㈣坚持查库制度，查库内容齐全，查库记录完整。㈤严格库房管理，库房及营业间安全设施齐全；库房整洁无杂物；库款存放整齐有序；做到无潮湿、霉烂、虫蛀、鼠咬等现象。六、结算与联行㈠印、证按规定建立使用和保管登记簿；坚持分管分用制度，严密交接手续。㈡收发信件要有登记；往来对账、查清未达及时；查询、查复及时认真，查询、查复凭证妥善保管。电子汇兑、实时汇兑数据传输和接收准确及时，处理账务及时，数据按规定备份。㈢记账岗位和对账岗位必须严格分开，做到对未达账项和账款差错的查核工作不返原岗处理。㈣认真执行《中华人民共和国票据法》、《票据管理实施细则》、《支付结算办法》中的有关规定和原则，正确及时办理各种结算业务，按规定向客户收取结算费用。㈤重要空白凭证列入表外科目核算；库房有专人管理，领发手续严密，账实相符；建立使用销号制度，作废凭证按规定处理；每月检查重要空白凭证的领用、销号和结存情况，并有记录。七、财务管理㈠严格执行财务制度和收入、成本核算规定。㈡认真准确地编制财务收支计划。㈢正确执行利率政策，加强利息收支核算。㈣财务核算完整、真实、准确。无乱挤、乱摊成本、截留利润、转移收入、私设小金库等违章、违纪行为，无滥发奖金、实物、津贴、虚报冒领等违反财经纪律和制度规定的行为。㈤定期开展财务收支活动的预测和分析，检查财务收支执行情况及结果，总结经验，挖掘潜力，不断提高财务管理水平。㈥加强应收、应付款项管理，做到无非营业性款项挂账。㈦加强固定资产管理，建立账册，有账有卡，低值易耗品要建立登记簿，定期或不定期进行核查，做到账实相符，确保财产完整。㈧全面完成下达的各项财务指标。八、内部控制和会计监督㈠按照中国人民银行《商业银行内部控制指引》的规定和单位内部不相容职务相互分离控制的要求，建立涵盖各项业务及其各个重要环节职责分离、相互制约的内控制度。㈡实行劳动组合的集中管理，建立会计岗位定期轮换制度和重要岗位离岗稽核制度。㈢建立健全会计监督体系，明确监督责任，定期、不定期地开展制度执行情况大检查，形成检查报告。㈣按照公开、公平、公正的原则，建立科学、合理、有效的考核奖惩机制。九、支行（部）管理㈠支行（部）的库存现金实行限额管理，超限额部分及时解送支行，无白条抵库、挪用。㈡已使用的表、账、登记簿等会计档案，按规定抄列清单保管，无散失。㈢环境整洁优美。十、优质服务㈠坚持文明用语，杜绝服务禁语，实行挂牌服务和限时服务（特殊业务除外）。㈡设立便民设施，向社会公开监督电话，工作环境整洁优美。㈢运用现代化手段，提供优质高效安全的服务。㈣营业时间应方便客户，予以公布，不得擅自停止营业或缩短营业时间。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第五章  考核与申报方法', '第十六条', '会计工作规范化等级管理考核采取千分制，凡考核达到800（含800分）—850分的单位，为会计工作达标单位；凡考核达到850（含850分）—900分的单位为会计工作三级单位；凡考核达到900（含900分）—950分的单位为会计工作二级单位；凡考核达到950分（含950分）以上的单位为会计工作一级单位。以上各等级考核中，考核标准的每一项得分不得低于项目标准分的70％，同时第二项和第三项的得分合计应分别为：达标不得低于280分；三级不得低于290分；二级不得低于300分；一级不得低于310分。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第五章  考核与申报方法', '第十七条', '申报会计工作规范化等级的单位，必须达到以下要求：达标：贯彻执行国家颁布制定的有关会计方面的法律和行政法规，以及会计、出纳、结算、联行、财务等各项制度，按规定正确组织会计核算和处理各项业务，加强服务与监督，建立良好的会计工作秩序，基本上实现会计工作制度化、规范化。三级：在达标的基础上，完善内部管理所需要的各项规章制度和管理措施，积极参与本行的经营决策，工作质量和效率明显提高，取得较好的经济效益，有关经济指标达到考核标准。二级：在三级的基础上,对资金、成本（费用）、结算、库款等工作的质量和效益建立有效的计划、控制、核算、分析、考核办法和检测体系，在改善内部经营管理、提高核算质量和经营效益中发挥了积极作用。一级：在二级的基础上，会计工作达到科学化、现代化水平，在加强经营管理、提高核算质量和经营效益等方面成绩显著。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第五章  考核与申报方法', '第十八条', '考核期内凡有下列情形之一者，当年不得参加规范化等级考核。一、严重违反会计法规和财经纪律：㈠滥用科目，不及时记账，不对账，账目严重混乱；㈡篡改账表，财务报告虚假，与有关会计账簿记录不对应；㈢账外设账；㈣会计凭证不真实、不合法，情节严重的；㈤考核期内经有关部门检查确认有重大违反财经纪律问题的。二、发生丢失印章、重要空白凭证（有价单证）等事故。三、发生属于有章不循、有法不依、违章操作造成经济损失。四、发生失窃、火灾等事故，造成经济损失和不良影响。五、发生错账、错款等技术性差错，损失累计达2000元（含2000元）以上。六、违反结算纪律或监管规定，受到上级管理部门、人民银行或银监部门通报批评。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第五章  考核与申报方法', '第十九条', '本行规范化等级申报，应由低到高依次进行，一般不得跨级申报、确认。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第五章  考核与申报方法', '第二十条', '申报单位必须做好以下基本工作：一、对照本办法逐项进行自检评分，模拟验收，对自检发现的问题，制定整改方案。整改方案应包括整改的具体项目和内容，整改后达到的目标，执行和督促人的姓名、职务，完成时间和自验时间等，在整改过程中不得对过去制作的已生效的会计凭证和账簿进行重制或更换。整改工作完成后向本行考评小组提出申请。二、按照本行会计出纳基本制度、对会计工作的管理要求、会计辅导员对辖内支行常规性检查的工作报告以及每季一次的对辖内支行进行的检查评分结果，作为对申报单位考核验收时的重要依据。三、考核验收时，申报单位应向运营管理部提供有关本次验收活动的下列资料：㈠本行会计工作规范化管理工作规划及实施方案；㈡对照标准自查后发现的问题、制定的整改方案及整改结果；㈢整改后自查评分情况有关资料、以及按规定格式填写的申请表。㈣会计辅导员（含运营主管）考核期内会计工作检查记录及其他有关资料。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第五章  考核与申报方法', '第二十一条', '根据考核结果，确认被考核单位的会计出纳会计工作等级，会计工作规范化管理等级考核每年开展一次。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第六章  奖  惩', '第二十二条', '凡获得会计工作规范化一级荣誉称号的支行，本行将给予一定的精神和物质奖励。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第六章  奖  惩', '第二十三条', '根据本行会计工作规范化管理目标规划，辖内所有支行均在考核范围内。凡当年列入会计工作规范化考核而没有取得达标等级资格的单位，不得参与先进集体评选；单位负责人、运营主管不得参与先进工作者评选。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第七章  附  则', '第二十四条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《会计工作规范化等级管理办法》', '第七章  附  则', '第二十五条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第一章  总  则', '第二条', '本办法所指会计档案是本行在业务经营和财务活动中形成的会计记录和重要史料,包括通过计算机等电子设备形成、传输和存储的电子会计档案。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第一章  总  则', '第三条', '会计档案的管理应当坚持规范管理、集中保管、控制风险、方便查询的原则，确保会计档案真实齐全、完整无损。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第二章  组织管理体系', '第四条', '本行运营管理部负责对各支行（部）会计档案管理工作的指导、监督和检查；建立健全会计档案的归档、保管、查阅、交接、销毁和保密制度。各支行（部）运营主管负责本支行（部）会计档案的日常检查和管理。信息科技部门为电子档案的技术支持部门，负责本行电子会计档案的统一采集、归档和应用的技术支持，负责电子会计档案的存储备份工作。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第二章　 会计档案的分类', '第五条', '会计档案按照内容可分为会计凭证、会计账簿、财务会计报告及其他会计资料。（一）会计凭证，包括原始凭证、记账凭证；（二）会计账簿，包括总账、明细账、日记账、固定资产和低值易耗品卡片账及其他辅助性账簿；（三）财务会计报告，包括月度、季度、半年度、年度财务会计报告，包括各种报表、附表、附注、文字说明及其他财务会计报告等资料；（四）其他会计资料，包括银行对账单、纳税申报表、会计档案移交（保管、销毁）清册、机构撤销（合并）交接清册、计算机运行日志、客户挂失申请书、查询（查复）书、相关账户开（销）户资料、有权机关查询（冻结、扣划）资料、会计档案鉴定意见书及其他具有保存价值的会计资料。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第二章　 会计档案的分类', '第六条', '会计档案按保存介质可分为纸质会计档案和电子会计档案。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第二章　 会计档案的分类', '第七条', '会计档案可以利用计算机、网络通信等信息技术手段进行管理。同时满足下列条件的,内部形成的属于归档范围的电子会计资料可仅以电子形式保存,形成电子会计档案:（一）形成的电子会计资料来源真实有效,由计算机等电子设备形成和传输;（二）使用的会计核算系统能够准确、完整、有效接收和读取电子会计资料,能够输出符合国家标准归档格式的会计凭证、会计账簿、财务会计报表等会计资料,设定了经办、审核、审批等必要的审签程序;（三）使用的电子档案管理系统能够有效接收、管理、利用电子会计档案,符合电子档案的长期保管要求,并建立了电子会计档案与其相关联的其他纸质会计档案的检索关系;（四）采取有效措施,防止电子会计档案被篡改;（五）建立电子会计档案备份制度,能够有效防范自然灾害、意外事故和人为破坏的影响;（六）形成的电子会计资料不属于具有永久保存价值或者其他重要保存价值的会计档案。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第二章　 会计档案的分类', '第九条', '满足本办法第七条规定条件, 从外部接收的电子会计资料附有符合《中华人民共和国电子签名法》规定的电子签名的,可仅以电子形式归档保存,形成电子会计档案。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第三章　 会计档案的归档', '第十条', '会计档案应依据归档要求，整理立卷，装订成册，按照保管期限进行分类，以及内容和形成时间进行分类索引，编制会计档案保管登记簿，以便查阅。电子会计档案应能满足阅读及打印要求。会计档案管理部门按照归档范围和归档要求,负责定期将应归档的会计资料整理立卷,上年度会计档案原则上最迟于本年度3月底前归档完毕。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第三章　 会计档案的归档', '第十一条', '会计档案归档的基本要求：一、纸质会计档案归档的基本要求　　（一）分类组卷。会计档案立卷应采用“年度/保管期限/档案类别”的分类方法。即先将同一年度形成的会计档案按照保管期限分类，再将同一期限的报表、账簿、凭证等按类分开组卷，并形成分类索引。（二）整理与装订。⒈会计凭证的整理与装订。凭证应每日整理、扫描、塑封。传票整理顺序依次为柜员传票封面、柜员业务传票（按流水号顺序排列）、非账务性流水传票（按办理业务先后排列）、轧账单。柜员传票必须按柜员操作号从小到大依次排列，由主出纳汇总填制《事后监督资料交接登记表》，当日营业结束后上缴本行事后监督团队。事后监督团队负责对传票进行扫描、核对、封塑。塑封成册的凭证应在传票册封面上编写顺序号，注明日期、保管期限及档案号，由扫描人员、档案管理人员在加封处签章。⒉会计账簿的整理与装订。总账每月按科目顺序整理装订，活页分户账按科目、账号顺序按年整理装订。账页过多可分册整理装订。各种登记簿应分类别、科目按年整理装订。各种销讫卡片账按照销账日期按月或按季整理装订。在年度中间更换或结清账页，应按照账号顺序分科目妥善保管，待年度终了统一整理装订。整理装订成册的账页，要填写“账首”和“账页目录”，另加封面、封底，在装订处加封并登记封面，注明名称、年度、保管期限、档案号，由装订人员和运营主管在加封处签章。⒊会计报表的整理与装订。日计表、余额表等每日打印资料按月整理装订，月报表、季报表、（半）年报按年整理装订，另加封面、封底，在装订处加封，登记封面，注明名称、年度、保管期限、档案号，由装订人员和运营主管在加封处签章。4.其他会计档案的整理与装订，按有关规定执行二、电子会计档案归档的基本要求1.保存会计档案资料的磁盘或其他介质，也应按照档案资料的“年度/保管期限/类别”加以分类、编号、张贴标签并加装介质封套，注明档案资料名称、年度、保管期限及档案号，由经办人员在标签及介质封套上签章。2.电子会计档案应按照其生成频率进行归档。电子会计档案的追加、删除、替换、复制等操作必须严格按事权划分的原则进行办理，并保留操作的痕迹。3.为保证电子会计档案的可利用性，从电子文件形成开始就应有严格的管理制度和技术措施，确保其信息的真实性、安全性、完整性和可用性。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第四章　 会计档案的保管', '第十三条', '会计档案的保管期限分为永久、定期两类。定期保管期限一般分为10年和30年。会计档案的保管期限,从会计年度终了后的第一天算起，如有必要可以延长，但不能缩短。（一）永久保管的会计档案1.年度财务会计报表（报告）2.资本金、股金及股权明细3.挂失申请书、挂失登记簿4.会计档案保管及销毁清册、会计档案鉴定意见书5.存、贷款开销户记录6.机构变动交接清册7.有权机关查询、冻结及扣划书8.账销案存记录9.重要空白凭证的销毁，重要印章的领发、缴销记录10.其他会计资料（二）保管30年的会计档案1.会计凭证及附件2.总账及明细核算资料3.重要印章的交接、保管记录4.会计人员及会计档案移交的清册5.其他会计资料（三）保管10年的会计档案1.对账回单2.日、月、季、半年报表3.计算机应用系统运行日志4.流水账、余额表5.不定期报表6.已处置固定资产卡片（固定资产报废清理后保管5年）7.其他会计资料'),
(NULL, '运营管理部', '《会计档案管理办法》', '第四章　 会计档案的保管', '第十四条', '本办法规定的会计档案保管期限为最低保管期限。会计档案的具体名称如有同本办法第十三条所列档案名称不相符的,应当比照类似档案的保管期限管理。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第四章　 会计档案的保管', '第十五条', '同一介质上有不同保管期限会计档案的，应按最长期限保管。同一会计档案采用不同介质保管的，至少应有一种介质的会计档案满足保管期限的要求。　　第十六条　存款人开设的银行结算账户管理资料、会计电子化系统开发和修改的文档资料应作为会计档案进行管理。银行结算账户管理资料保管期限为该账户销户后10年，会计电子化系统开发和修改的文档资料保管期限为该系统停止使用或重大修改后5年。　　第十七条　运营主管应按照档案规定保管期限对会计档案进行分类管理，逐项编号、登记，并及时入库（柜）保管，确保会计档案的完整无缺。会计档案管理人员调动须办理交接手续。出纳人员不得兼管会计档案。　　第十八条　运营主管要定期检查会计档案，及时对破损、变质会计档案进行修复、复制或其他技术处理。　　第十九条　支行（部）分立，原机构存续的，会计档案由原机构保管；原机构解散的，会计档案由本行运营管理部协调处理。支行（部）合并，会计档案应由合并后的支行（部）保管。支行（部）关闭、撤销，会计档案由本行协调处理。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第四章　 会计档案的保管', '第二十条', '会计档案交接应经本行运营管理部批准后办理，交接应由移交人编制移交清册，一式三份。交接会计档案时,交接双方应当按照会计档案移交清册所列内容逐项交接,并由交接双方的单位有关负责人负责监督。交接完毕后,交接双方经办人和监督人应当在会计档案移交清册上签名或盖章。移交方编制的会计档案移交清册,应当列明移交的会计档案名称、卷号、册数、起止年度、档案编号、应保管期限和已保管期限等内容。纸质会计档案移交时应当保持原卷的封装。电子会计档案移交时应当将电子会计档案及其元数据一并移交,且文件格式应当符合国家档案管理的有关规定。特殊格式的电子会计档案应当与其读取平台一并移交。接收方应当对保存电子会计档案的载体及其技术环境进行检验,确保所接收电子会计档案的准确、完整、可用和安全。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第五章　 会计档案的调（查）阅与借出（归还）', '第二十二条', '各支行（部）及事后监督团队保管的会计档案确因工作需要且根据国家有关规定必须借出的,应当由本行运营管理部总经理及分管行长批准后方可办理。会计档案借用单位应当妥善保管和利用借入的会计档案,确保借入会计档案的安全完整,并在规定时间内归还。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第五章　 会计档案的调（查）阅与借出（归还）', '第二十三条', '各支行（部）及事后监督团队保管的电子会计档案原始档案不得外借，利用时使用其复制件，联网利用时要有安全保密防范措施和可靠的监管保障。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第六章　 会计档案的鉴定及销毁', '第二十四条', '由运营管理部门牵头，组织财务、审计、合规、监察等部门人员定期对本行已到保管期限的会计档案进行鉴定,并形成会计档案鉴定意见书。经鉴定,仍需继续保存的会计档案,应当重新划定保管期限;对保管期满,确无保存价值的会计档案,可以销毁。　　第二十五条　保管期限届满的会计档案，按照下列程序销毁：　　（一）由销毁会计档案的支行（部）运营主管编制会计档案销毁清册；　　（二）销毁会计档案的支行（部）行长在会计档案销毁清册上签署意见；　　（三）运营管理部、审计稽核部对销毁会计档案进行清查、核对会计档案销毁清册；　　（四）报请本行行长室审批（销毁清册、运营管理部、审计稽核部意见、支行（部）行长意见作为附件）；（五）运营、财务、审计、合规、安全保卫和事后监督团队共同派人现场监销。监销人在会计档案销毁前,应当按照会计档案销毁清册所列内容进行清点核对;在会计档案销毁后,应当在会计档案销毁清册上签名。（六）本行统一集中销毁会计档案，运营管理部编制销毁清册，并上报省联社财务会计部备案。　　会计档案销毁清册应注明要销毁会计档案的名称、卷号、册数、起止时间、档案号、应保管期限、已保管期限、销毁时间等内容；对会计凭证及附件要详细抄列凭证记载主要事项；采用磁盘或其他介质保存的会计档案，应注明保存介质的档案编号、名称等内容。销毁会计档案，应将档案信息与其保存介质一同销毁。对销毁用磁盘或其他介质保存的会计档案，应会同信息科技部有关技术人员共同进行。电子会计档案的销毁流程同纸质档案的销毁流程，同时还应当符合国家有关电子档案的规定。'),
(NULL, '运营管理部', '《会计档案管理办法》', '第六章　 会计档案的鉴定及销毁', '第二十六条', '保管期限届满但涉及未结清债权债务关系或其他未了事项的原始会计资料，不得销毁,纸质会计档案应当单独抽出立卷,电子会计档案单独转存,保管到未了事项完结时为止。单独抽出立卷或转存的会计档案,应当在会计档案鉴定意见书、会计档案销毁清册和会计档案保管清册中列明。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第一章  总 则', '第一条', '为规范xxx银行（以下简称“本行”）反假货币收缴行为，加强对反假货币工作的管理，建立反假货币工作组织体系，杜绝假币流通，维护货币信誉和货币持有人的合法权益，根据《中华人民共和国人民币管理条例》、《中国人民银行假币收缴、鉴定管理办法》等法律法规，结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第一章  总 则', '第二条', '本规定明确了反假货币业务人员、收缴、保管、上缴等内容和要求。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第一章  总 则', '第三条', '本规定适用于本行反假货币工作的管理。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第一章  总 则', '第四条', '本规定所称业务人员，是指办理假币收缴业务的人员。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第一章  总 则', '第五条', '货币是指人民币。人民币是中国人民银行依法发行的货币，包括纸币和硬币。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第一章  总 则', '第六条', '本规定所称假币，是指伪造、变造的货币。伪造的货币是指仿照真币的图案、形状、色彩等，采用各种手段制作的假币。变造的货币是指在真币的基础上，利用挖补、揭层、涂改、拼凑、移位、重印等多种方法制作，改变真币原形态的假币。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第二章 职责与权限', '第七条', '各支行、营业部负责假币的收缴、保管和上缴工作。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第二章 职责与权限', '第八条', '运营管理部负责反假货币工作管理规定的制定、修改、贯彻，并对支行反假货币的日常管理进行检查监督和考核；负责支行上缴假币的核对、汇总和上缴当地人行工作。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第二章 职责与权限', '第九条', '审计部负责反假货币的管理工作的审计监督。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第三章  原则与基本规定', '第十条', '必须在营业场所内向客户无偿提供鉴别人民币真伪的相关服务，配备人民币点钞、验钞一体机，并保持正常运行状态供客户使用。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第三章  原则与基本规定', '第十一条', '支行（营业部）反假宣传要坚持日常宣传和集中宣传相结合，要充分利用营业场所和其他户外宣传等形式开展全方位、多渠道、深层次的宣传活动，积极向社会宣传货币真伪知识。各营业网点要设立反假宣传橱窗（或公告牌），摆放宣传资料，让更多社会公众掌握反假知识，提高公众识别货币真伪的能力，切实维护人民群众的切身利益。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第三章  原则与基本规定', '第十二条', '支行（营业部）反假货币的内控要求（1）加强对“假币”印章的管理，建立保管使用登记制度，在《印章保管使用登记薄》中及时记录印章领用、上缴等情况，指定专人保管，明确责任；对于因网点撤销、合并而停止使用的“假币”印章，应及时上缴总行。（2） 应将《假币收缴凭证》纳入重要空白凭证管理，定期或不定期对保管和使用中的《假币收缴凭证》进行账实核对，保证其账实相符。（3） 建立完善对业务人员库存现金的定期不定期检查制度，保证对外支付货币的真实性。（4） 业务人员在临柜办理现金收付时，对大面额人民币不论数量多少，一律进行机点，对有疑问的人民币要进行手工复点，以辨别其真伪。（5） 对拟放入ATM机内的人民币，至少由2名以上现金业务人员在电视监控下进行清点，保证放入ATM机人民币的质量。（6）建立定期(每月一次)对营业场所验钞机具、录像等设备的检查维护、调阅制度，确保机具、设备良好，录像资料清晰完整。（7）建立反假货币知识学习培训制度，定期或不定期组织业务人员进行反假货币知识学习，支行行长要经常对业务人员进行有关法律法规知识教育，增强员工的法制意识、责任意识和信用意识，提高员工整体素质，维护本行信誉。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第三章  原则与基本规定', '第十三条', '反假货币工作考核方式（一）反假货币工作质量按年考核，总行采取定期检查和不定期实地检查，年终进行考核。定期检查主要查验上缴的假币实物、假币解缴单、收缴统计表，以及群众投诉、举报、上报的反假货币信息情况；不定期实地检查主要是对业务操作、各类凭证、假币实物、登记簿进行现场查阅。（二） 新上岗的现金业务人员必须经过本行反假货币知识培训，在一年内通过当地人民银行组织的反假货币知识考试，取得《反假货币资格上岗证书》后方可办理假币收缴工作。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第四章  管理程序', '第十四条', '假币的收缴各支行（营业部）网点业务人员在办理现金业务发现假币时，必须做到有2名以上持有《反假货币资格上岗证书》的业务人员在场，并在持币人视线范围内当场予以收缴，具体操作如下：（一）发现假币，将假币交另一业务人员复核确定，当面予以收缴；（二）向持有人指出被收缴的假币特征；（三）对假人民币纸币，应当面在假币的正反面加盖“假币”字样的戳记；（四）柜面人员在核心系统中进行登记，并打印一式两联空白假币收缴凭证。（五）将一式二联假币收缴凭证交持有人签字。如果持有人拒绝签字，收缴人可在收缴凭证上注明。（六）在假币收缴凭证上均加盖业务专用章、经办人及复核员私章后，第一联假币收缴凭证和假币实物由支行指定的人员保管，第二联为客户联，交于被收缴人，收缴的假币，不得再交于持有人，同时登记《假币收缴登记簿》。实行假币实物与收缴登记簿分开管理。（七） 向持有人告知有关事项，经办人员要耐心、准确地解答持有人提出的问题，并向持有人说明如果对被收缴的货币真伪有异议，可在3个工作日内持假币收缴凭证向中国人民银行当地分支机构或中国人民银行授权的中国工商银行、中国农业银行、中国银行、中国建设银行的分支机构申请鉴定。持有人如对金融机构做出的有关收缴或鉴定假币的具体行政行为有异议，可在收到假币收缴凭证或“货币真伪鉴定书”之日起60天内凭此证书向当地中国人民银行提出行政复议或向当地人民法院提起诉讼。（八）业务人员在收缴假币过程中有下列情形之一的，应当立即报告当地公安机关，提供有关线索。（1）一次性发现假人民币20张（枚）（含20张、枚）以上；（2）属于利用新的造假手段制造假币的；（3）有制造贩卖假币线索的；（4）持有人不配合金融机构收缴行为的。（九）假币的保管（1）假币和出具的假币收缴凭证留存联应实行账实两分管；（2）定期和不定期对假币、假币收缴凭证与《代收假币保管登记簿》进行核对，确保账簿、账实相符；（3）营业终了，假币、假币收缴凭证及假币印章应入箱（库）保管。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第四章  管理程序', '第十五条', '支行上缴总行每月15号前，支行将假币上缴总行中心库房，填写《假币解缴单》，要求做到数据准确、要素真实齐全、解缴及时，并在核心系统中操作。（一） 将假币连同《假币解缴单》，由支行指定人员送总行中心库房，并填写《假币收缴登记簿》，办理交接手续。（二） 总行中心库房收到支行送缴的假币，将假币与假币收缴凭证逐一配对入保险箱保管，并汇总登记《假币解缴单》和《收缴统计表》。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第四章  管理程序', '第十六条', '总行上缴人行总行中心库房按月汇总支行上缴假币，于每月末20日前将《假币解缴单》和《收缴统计表》连同假币实物解缴当地人民银行。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第四章  管理程序', '第十七条', '假币的鉴定（一）如本行假币收缴网点收到假币鉴定单位鉴定通知，收缴网点应当自收到鉴定单位通知之日起2个工作日内，将需要鉴定的货币送达鉴定单位。（二）对本行收缴的盖有“假币”字样戳记的人民币纸币，经鉴定为真币的，由鉴定单位交收缴网点按照面额兑换完整券退还持有人，收回持有人的假币收缴凭证，盖有“假币”戳记的人民币按损伤人民币处理。（三）中国人民银行各分支机构在复点清分金融机构解缴的回笼款时发现假人民币，应经鉴定后予以没收，向解缴单位开具假币没收收据，并要求其补足等额人民币回笼款。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第四章  管理程序', '第十八条', '对不按照规定进行假币的收缴、保管、上缴的网点和经办人员将按照本行员工违规违章行为管理规定进行处罚。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第五章   附  则', '第十九条', '本办法未尽事宜，按国家有关法律、法规的规定执行；本办法如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第五章   附  则', '第二十条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《假币收缴与鉴别管理办法》', '第五章   附  则', '第二十一条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第一章  总 则', '第一条', '为进一步保证假币收缴程序合规合法，减少假币纠纷，维护人民币尊严，建设和谐社会，根据《中华人民共和国人民币管理条例》、《中国人民银行假币收缴、鉴定管理办法》等有关规定，结合xxx银行（以下简称“本行”）的实际，特制定本处置机制。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第一章  总 则', '第二条', '本处置机制适用于柜面、取款机、存取款机上取出的人民币纸币。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第二章 客户查询申请', '第三条', '客户对柜面、取款机、存取款机上取出的人民币纸币真伪有异议时，可在办理取款业务之日起1个月内（含取款当日），携带有效证件、有异议纸币或《假币收缴凭证》、办理取款业务的证明资料（银行卡、取款凭证等），向付款网点提交《人民币冠字号码查询申请表》（见附件1），如委托第三方代理查询的，代理人除应提供上述 规定的证明材料外，还应提供本人有效合法证件。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第三章 受理查询机制', '第四条', '付款网点对于提供证明材料符合规定的查询申请，应指定专人受理，及时查询，在受理之日起3个工作日内办结，如有特殊情况需延长办理时间的，应提前告知客户。对于提供证明材料不完备的查询申请，查询受理单位应当面一次性告知客户补齐相关材料，不得无故拒绝受理查询申请。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第三章 受理查询机制', '第五条', '各网点应向客户明确，“冠字号码查询业务”只对经确认所持钞票为假币的银行客户提供查询服务，不受理柜面、现金自助设备付出的真币的冠字号码查询申请。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第四章  查询结果处置', '第六条', '根据查询结果判定为付款网点付出的现金，如果确属于假币的，付款网点应收回并全额赔付；如果属于真币的，完整券由付款网点全额退还客户，残损券由付款网点按照相关标准予以兑换；如果假币已被其他金融机构收缴或公安机关没收，付款网点应取得客户持有的《假币收缴凭证》或《假币没收收据》后予以赔付。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第五章  异议调处机制', '第七条', '客户如对处理结果或处理程序有异议的，可在收到处理结果后3个工作日内向付款网点或本行进行投诉。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第五章  异议调处机制', '第八条', '付款网点或本行应当自受理之日起15个工作日内，开展调查与处理工作。因技术原因对投诉事项不能进行处理的，由本行运营管理部向人民银行xx市支行进行汇报核查，对确因情况复杂不能在规定期限内完成的，经分管行长核批后，可延长至30个工作日，并向客户说明原因。处理结果应通过书面告知客户，并将相关事项在《假币纠纷处理登记簿》（见附件2）及时登记。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第六章   附  则', '第九条', '本处置机制由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《假币纠纷分级管理处置机制》', '第六章   附  则', '第十条', '本处置机制自发文之日起施行。附表1xxx银行人民币冠字号码查询申请表附表2xxx银行假币纠纷处理登记簿'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第一章	   总　则', '第一条', '为加强xxx银行（以下简称“本行”）综合业务系统通存通兑储蓄业务的管理，使账务核算规范、准确、及时，更好地为客户提供快捷、方便、优质的服务，根据《储蓄管理条例》、《xx省农村信用社会计业务操作流程(试行)》等有关规定，特制定本办法。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第一章	   总　则', '第二条', '通存通兑业务是指以全省农村信用社综合业务系统为基础，在不同机构之间利用计算机网络互相代理储蓄存取款业务，实行资金自动清算。通存通兑业务分为县级通存通兑业务和省级通存通兑业务两种业务级别。县级通存通兑业务主要指本行辖内支行（部）之间开办的通存通兑业务；省级通存通兑业务主要指江苏省内各联社（行）之间开办的通存通兑业务。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第一章	   总　则', '第三条', '本行辖内所有支行（部），均为办理通存通兑业务的代理行或被代理行，必须按照本办法办理储蓄通存通兑业务。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第二章 基本原则', '第四条', '通存通兑业务以“存款自愿、取款自由、存款有息、为储户保密”作为原则，以真实、合法、有效票据为依据，按照会计核算要求办理。通存通兑业务的储蓄凭证、传票不进行交换，由代理网点留存保管，被代理网点以系统打印的凭条、汇总凭证和清单作为记账凭证。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第二章 基本原则', '第五条', '通存通兑业务资金清算，本着“分级管理、及时清算、互不占用”的原则进行。省级通存通兑业务资金通过上存省联社清算中心清算备付金账户实行逐笔清算；县级通存通兑业务资金通过社内往来账户进行清算。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第三章  储蓄业务通存通兑业务范围及要求', '第六条', '符合办理通存通兑业务范围（一）活期储蓄存折（含个人结算账户）、一本通活期的续存、续取、换折、补登折，个人结算账户转账；（二）零存整取（含教育储蓄）的续存；（三）活期存单、定期存单、定活两便、通知存款、大额存单的销户；定期存单（含大额定期存单）的提前支取；（四）储蓄业务的口头挂失业务；（五）银行卡的续存、续取业务。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第三章  储蓄业务通存通兑业务范围及要求', '第七条', '不得异地办理的业务：（一）存折类账户的销户；（二）客户办理正式挂失(含密码挂失)、解挂、解除挂失的销户、挂失补发，冻结账户及解冻结；（三）定期存单（含大额定期存单）的部分提前支取业务；（四）客户存款账户通存通兑级别或支取方式不符合通存通兑条件的；（五）因各种原因止付的存折或存单的支取或销户；（六）存折或存单信息要素与业务系统信息不符；（七）存折或存单的账号、户名、金额、公章、经办员等要素辨认不清或被涂改；（八）修改存折或存单的密码；（九）长期不动户的支取；（十）通兑时不能提供存折或存单的；（十一）有奖储蓄存单、国库券代存单的支取；（十二）其他按照《储蓄管理条例》不得异地办理的业务。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第三章  储蓄业务通存通兑业务范围及要求', '第八条', '当日开户的存单或存折，当日可以通存通兑。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第三章  储蓄业务通存通兑业务范围及要求', '第九条', '通存通兑业务的现金管理：（一）办理通存通兑业务的现金管理要严格按照中国人民银行《现金管理暂行条例》、《关于大额现金支付管理的通知》、《关于加强金融机构个人存取款业务管理的通知》等要求，执行验证、登记、备案、审批、预约制度。（二）单笔通兑金额超过200万元(含200万元)的，不但要执行预约制度，代理网点还须及时通知上级清算中心。（三）通存金额不受限制，但仍需按有关规定执行登记备案制度。（四）银行卡的现金管理要求按照银行卡规定执行。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第三章  储蓄业务通存通兑业务范围及要求', '第十条', '办理通存通兑业务应遵循以下原则：（一）坚持存款业务先收款后记账，取款业务先记账后付款，转账业务先输入付款账户，后输入收款账户；（二）数据信息输入必须坚持以原始凭证为依据，实时输入、实时办理，严禁伪造凭证或无凭证输入数据；（三）柜员办理业务时，业务系统出现非正常提示，柜员要进行查询操作，待业务确认无误后，方可办理现金收付。（四）符合《储蓄管理条例》的有关规定。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第四章   业务处理流程', '第十一条', '存款通存业务处理柜员接到客户现金及存折（卡），应先审查是否属于通存通兑辖区所签发，存折要素是否有涂改现象；审查无误后，清点现金并与客户进行金额核对；柜员选择续存交易，输入客户账号（或刷折、卡）后，将系统提示信息与存折核对无误后，打印凭证交客户签字确认，将打印的存折和加盖业务办讫章的客户回单交客户。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第四章   业务处理流程', '第十二条', '存款通兑业务处理柜员接到客户提交的存单（折、卡），应先审查是否属通存通兑辖区所签发，存单（折、卡）要素是否有涂改现象；审查无误后，柜员选择相应交易，输入客户账号（或刷折、卡），由客户输入预留的密码，将系统交易提示信息与存单（折）核对无误后，打印凭证交客户签名确认；将打印的业务回单和现金交客户。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第四章   业务处理流程', '第十三条', '各支行（部）在办理通存通兑业务时，不得使用抹账交易。如发生存（取）金额与实际情况不一致、存取颠倒等情况，应事先征得客户的允许。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第五章   其  他', '第十四条', '清算团队逐日核对上存省联社清算备付资金，确保往来清算资金平衡。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第五章   其  他', '第十五条', '运营管理部负责检查与监督，规范核算手续，保证通存通兑储蓄业务的有序开展。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第五章   其  他', '第十六条', '各支行（部）要规范员工服务行为，不得以任何理由拒绝办理符合条件的通存通兑业务，对不符合通存通兑条件的业务，要认真对客户做好解释，严禁故意刁难客户。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第六章   附  则', '第十七条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《储蓄通存通兑业务管理办法》', '第六章   附  则', '第十八条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第一条', '为规范xxx银行（以下简称“本行”）支票影像交换系统的业务处理，保障支票影像交换系统的正常运行，依据《中华人民共和国票据法》、《票据管理实施办法》、《支付结算办法》、《江苏省农村信用社支票影像交换系统业务管理办法（试行）》及有关法律法规的规定，结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第二条', '支票影像交换系统指运用影像技术将实物支票转换为支票影像信息，通过计算机及网络将支票影像信息传递至出票人开户行提示付款的业务处理系统。本办法所称支票影像信息包括支票影像及其电子清算信息。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第三条', '通过支票影像交换系统处理的支票影像信息具有与原实物支票同等的支付效力，出票人开户行收到支票影像交换系统提交的支票影像信息，应视同实物支票提示付款。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第四条', '通过支票影像交换系统处理支票业务分为支票影像信息传输和支票业务回执处理两个阶段。本办法所称支票业务回执指出票人开户行通过小额支付系统返还给持票人开户行，对支票影像信息提示付款表明“同意付款”或“拒绝付款”的确认结果。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第五条', '影像交换系统与小额支付系统共享要素数据、行名、行号数据。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第六条', '影像交换系统处理金额在50万元以下的支票业务，超过金额上限的支票，影像交换系统拒绝受理。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第七条', '支票影像业务处理遵循“先付后收、收妥抵用、全额清算、银行不垫款”的原则。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第八条', '影像交换系统的清算资金统一通过小额支付系统处理。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第一章   总  则', '第九条', '支票影像交换系统的工作时序与小额支付系统一致。小额支付系统停运不影响支票影像交换系统的正常运行。支票影像交换系统实行7x24小时运行。如有调整，以中国人民银行公告的系统停运时间和启运时间为准。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第二章   基本规定', '第十条', '本行支票影像交换系统业务处理模式：集中处理模式，即支行提出支票影像业务时，自行录入支票电子信息，然后将支票实物、电子信息转送到清算团队，由清算团队统一扫描支票影像，对支票影像与电子信息逐一配对，最后发送到省清算中心，由省清算中心转发至人民银行；接收提入支票影像业务时，由各支行（部）接收审验支票影像、电子信息，发送支票业务回执到省清算中心，由省清算中心转发至人民银行，最后将业务处理清单交接收行。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第二章   基本规定', '第十一条', '各支行（部）在出售支票时，应在支票票面记载付款行的银行机构代码（支付系统12位行号）。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第二章   基本规定', '第十二条', '所有通过支票影像交换系统截留的业务凭证，由提出行按照规定进行留存管理，保管期限不低于15年；提入行以系统打印的凭证作为记账凭证。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第一条', '提出行业务处理流程'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第十三条', '提出行受理持票人提交的支票时，应认真审查下列事项：（一）支票票面是否记载银行机构代码；（二）支票金额是否超过中国人民银行规定的金额上限；（三）支票是否是统一规定印制的凭证，支票是否真实，是否超过提示付款期限，是否为远期支票；（四）支票填明的持票人是否在本机构开户，持票人的名称是否为该持票人，与进账单上的名称是否一致；（五）出票人的签章是否符合规定；（六）支票的大小写金额是否一致，与进账单的金额是否相符；（七）支票必须记载的事项是否齐全，出票金额、出票日期、收款人名称是否更改，其他记载事项的更改是否由原记载人签章证明；（八）背书转让的支票其背书是否连续，签章是否符合规定，背书使用粘单的是否按规定加盖骑缝章；（九）持票人是否在支票的背面作委托收款背书。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第十四条', '对拒绝受理的支票，持票人开户银行应制作一式二联拒绝受理通知书，加盖业务专用章后一联连同支票和进账单退还持票人，另一联留存，定期归档。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第十五条', '对审核无误的支票发起支票影像信息的处理：（一）通过综合业务系统或支票影像交换系统录入、复核支票电子清算信息后，将电子信息转至清算团队，同时将支票和进账单记账联交清算团队；（二）清算团队接到支票实物后，按以下流程操作：⒈将支票实物与进账单进行核对。⒉采集支票影像。未附粘单的支票应采集正面和背面影像；附粘单的支票应采集其正面和记载最后一手委托收款背书粘单的影像，其他背书信息在电子清算信息中连续记录。⒊将采集的影像与网点转发的电子信息进行匹配。⒋将支票实物与匹配后的信息进行核对，核对无误后发送。⒌支票与匹配后的信息核对如有错误，以电话方式通知支行，修改支票电子信息，重新转发。⒍将信息发送到支票影像交换系统成功后，打印提出票据清单。成功发送的提出票据清单、支票、进账单由清算团队统一保管。清算团队应按规定格式制作支票影像信息，在受理支票当日至迟下一个法定工作日上午10：00提交影像交换系统。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第十六条', '支票影像信息一经影像交换系统发出，不得更改或撤销。对发出有误的支票影像信息，提出行可以通过影像交换系统向提入行申请止付，登记支付申请及应答登记簿。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第十七条', '提出行收到拒绝止付或同意止付的应答后，应销记止付申请及应答登记簿。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第十八条', '提出行收到小额支付系统发送的支票业务回执后，对出票人开户行同意付款的，应立即贷记持票人账户。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第十九条', '提出行收到拒绝付款的回执，打印一式两联退票理由书，一联加盖业务专用章连同支票和进账单一并退还持票人，并办理签收手续，一联留存。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十条', '提出行在规定时间内未收到支票业务回执的，应主动通过影像交换系统向提入行发出查询；支票提示付款期满并收到提入行明确拒绝回执时，提出行可将实物支票退还持票人并办理签收登记。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二条', '提入行业务处理流程'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十一条', '提入行可以采用印鉴核验方式或支付密码核验方式对支票影像信息进行付款确认。印鉴核验方式，使用电子验印系统，付款确认以签章为主，支票影像其他要素为辅。支付密码核验方式，应与出票人签订协议约定使用支付密码作为审核支付支票金额的依据。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十二条', '提入行接收的支票影像业务报文后，进行解析处理，系统自动进行印鉴（支付密码）核验。系统自动核验有误的支票信息，可由人工再次进行核验，以最后核验的结果为准。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十三条', '提入行对核验成功的支票影像业务报文应审查下列事项：（一）票面记载的银行机构代码是否为本机构代码；（二）支票大小写金额是否一致；（三）支票必须记载的事项是否齐全；（四）出票人签章与预留银行印鉴是否相符，与客户约定使用支付密码的，其密码是否记载正确；（五）持票人是否在支票背面作委托收款背书；（六）电子清算信息与支票影像内容是否相符；（七）出票人账号、户名是否相符。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十四条', '支票印鉴（支付密码）核验、支票电子信息审核通过后，提入行确认付款的，应检查出票人账户状态是否正常，是否有足够支付的款项；如有足够支付的款项，应立即进行账务处理。扣款成功后，提入行应在规定期限（T+N）内，通过小额支付系统发起支票影像业务确认付款回执。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十五条', '支票印鉴（支付密码）核验、支票电子信息审核没有通过的，提入行应在规定期限（T+N）内，通过小额支付系统发起支票影像业务拒绝付款回执。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十六条', '提入行收到止付申请，未向小额支付系统发送支票业务回执的，应立即办理止付并发起“同意止付”应答。已发出支票业务回执的，如拒绝付款，应立即发起“同意止付”应答；如同意付款但未纳入轧差的，应立即申请撤销，撤销成功后办理止付并发起“同意止付”应答；如同意付款且已纳入轧差，应不予止付并发起“拒绝止付”应答。提入行应在收到止付申请报文的当日至迟下一个法定工作日发出止付应答。所有止付应答必须经清算团队负责人审核同意。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第三条', '查询查复'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十七条', '查询查复是正确办理影像交换系统业务的重要工作，是确保系统安全、高效运行的有力保障。处理查询查复要求规范、严谨、及时、准确。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十八条', '办理影像交换系统的查询查复业务必须做到“有疑必查，有查必复，复必详尽，切实办理”。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第二十九条', '查询通过综合业务系统或影像交换系统发送查询书并自动登记查询查复登记簿，打印查询书专夹保管。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第三十条', '查复行收到查询报文后，系统先自动登记查询查复登记簿，打印查询书并提交业务人员确认，业务人员依照查询书的内容，查明情况和原因。对更正支票业务要素的查询，必须严格以客户提交的原始凭证的记载内容为依据查复，经审核同意后，在当日至迟下一个法定工作日上午10：00前通过影像系统发起查复信息，打印查复书与查询书配对后专夹保管。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第三十一条', '查询行收到查复报文，自动销记查询查复登记簿，打印查复书经审核后，立即按照有关规定处理或通知客户，查复书与查询书配对后专夹保管。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第三章   业务处理流程', '第三十二条', '对应查复而未查复的机构，下一工作日不允许办理影像业务，必须先进行查复，才能办理业务。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第四章   纪律与责任', '第三十三条', '各支（部）应当遵守本办法以及其他相关规定，加强内部控制管理，建立健全应急处理机制，保证系统安全稳定运行和业务的及时、准确处理。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第四章   纪律与责任', '第三十四条', '提出行应按照本办法的规定审核支票，确保支票的真实性及票面信息的合规性、完整性。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第四章   纪律与责任', '第三十五条', '持票人开户行应按照本办法的规定办理退票。除法律和本办法规定事由外，持票人开户行不得拒绝受理持票人提交的支票，出票人开户行不得拒绝付款。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第四章   纪律与责任', '第三十六条', '各支（部）有下列情形之一的，由本行予以通报：（一）未按规定及时、准确发起支票影像信息的；（二）未按规定及时返回支票业务回执；（三）未按规定及时贷记持票人账户，延压客户资金的；（四）未按规定办理查询查复；（五）未按规定及时办理止付和发出止付应答的；（六）未按规定制作支票影像；（七）未按规定受理支票；（八）未按规定拒绝受理支票；（九）未按规定拒绝付款。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第四章   纪律与责任', '第三十七条', '提出行因保管、处理不当等原因造成支票票面污损、撕毁，导致该笔支票不获付款的，应承担相应责任。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第四章   纪律与责任', '第三十八条', '出票人签发空头支票、签发与其预留印鉴不符的支票或签发支付密码错误支票的，由中国人民银行按有关法律规章给予行政处罚。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第五章   附  则', '第三十九条', '本办法由xxx银行运营管理部负责解释和制订。'),
(NULL, '运营管理部', '《全国支票影像交换系统业务管理办法》', '第五章   附  则', '第四十条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第一条', '为加强xxx银行（以下简称“本行”）农信银资金清算系统银行汇票业务管理，规范农信银银行汇票业务操作，防范支付风险，根据《中华人民共和国票据法》、《票据管理实施办法》、《支付结算会计核算手续》和《省农村信用社农信银银行汇票业务管理办法》等相关制度，结合本行实际，制定本办法。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第二条', '本行农信银银行汇票业务是指农信银银行汇票的签发，并通过农信银资金清算中心完成汇票的解付以及出票行与代理付款行的资金清算。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第三条', '农信银银行汇票资金清算采取“逐笔申请、逐笔圈存、实时全额清算”的方式。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第四条', '本行辖内有农信银清算系统行号的各支行（部）均可签发农信银银行汇票。领用和对外签发的农信银银行汇票必须是经中国人民银行核准的农信银银行汇票凭证，必须由电脑打印签发，不得手工签发。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第五条', '各支行（部）受理农信银银行汇票须通过综合业务系统实时核对票据信息和资金清算业务。业务办理不受金额限制。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第六条', '各支行（部）必须做到汇票专用章、农信银银行汇票凭证分管分用。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第七条', '各支行（部）不得签发没有资金保障的空头农信银银行汇票。通过农信银资金清算系统办理的农信银银行汇票业务，应按照有关规定收费。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第一章   总 则', '第八条', '本行辖内各支行（部）办理农信银银行汇票的签发、解付、挂止、公示催告等业务适用本办法。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第九条', '农信银银行汇票可以用于转账，填明“现金”字样的农信银银行汇票也可以用于支取现金。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十条', '各支行（部）不得受理未在本支行（部）开立存款账户的持票人为单位直接提交的农信银银行汇票。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十一条', '各支行（部）签发现金农信银银行汇票需填写代理付款人名称。签发现金农信银银行汇票，申请人和收款人必须均为个人，各支行（部）收妥申请人交存的款项后，在农信银银行汇票“出票金额”栏先填写“现金”字样，后填写出票金额。申请人或者收款人为单位的，各支行（部）不得为其签发现金农信银银行汇票。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十二条', '各支行（部）签发农信银银行汇票应当记载下列事项：（一）标明“农信银银行汇票”的字样；（二）无条件支付的承诺；（三）出票金额；（四）付款人名称；（五）收款人名称；（六）出票日期；（七）出票人签章；（八）密押。欠缺上述第一至第七事项之一的，农信银银行汇票无效。欠缺第八项记载事项的，各支行（部）不予受理。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十三条', '各支行（部）签发农信银银行汇票的出票日期必须大写，汇票金额以大写中文字符和阿拉伯数码同时记载，两者必须一致。不一致的，农信银银行汇票无效。农信银银行汇票的出票金额、出票日期、收款人名称以及密押不得更改，更改的票据无效。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十四条', '申请人使用农信银银行汇票，应向各支行（部）填写“结算业务申请书”（以下简称“申请书”），填明申请人名称、申请日期、收款人名称、汇票金额等事项，并加盖预留银行印鉴。约定使用支付密码的，应在申请书上填写支付密码。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十五条', '各支行（部）受理申请书，经审核无误、收妥款项、签发银行汇票后，将银行汇票交付申请人。各支行（部）在农信银银行汇票上的签章，应为农信银银行汇票专用章加其法定代表人或者其授权代理人的签章。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十六条', '各支行（部）签发农信银银行汇票必须换人实时复核，超过规定限额的，必须经过主管会计授权。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十七条', '各支行（部）签发农信银银行汇票过程中，如出现打印失败等情况，可办理农信银银行汇票撤销。在收到撤销成功应签后，可重新进行银行汇票签发。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十八条', '本行在省联社清算中心的清算账户余额不足的，系统对签发的农信银汇票业务信息将作拒绝。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第十九条', '收款人可以将农信银银行汇票背书转让给被背书人，背书转让时，应在背书人栏签章，记载背书日期和被背书人全称。但填明“现金”字样的农信银银行汇票不得背书转让。农信银银行汇票的背书转让以实际结算金额为准。未填写实际结算金额或实际结算金额超过出票金额的农信银银行汇票不得背书转让。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第二章  出票与背书', '第二十条', '本办法所述农信银银行汇票适用于全国范围内背书转让和使用。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十一条', '农信银银行汇票的提示付款期限为自出票日起1个月。持票人超过提示付款期限提示付款的，各支行（部）不予受理。提示付款日期以持票人向各支行（部）提交票据的日期为准。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十二条', '在银行开立结算账户的持票人向开户银行提示付款时，应在农信银银行汇票背面“持票人向银行提示付款签章”处签章，签章应与预留银行签章相同，并将农信银银行汇票和进账单送交开户支行。各支行（部）审查无误后办理转账手续。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十三条', '未在各支行（部）开立结算账户的个人持转账银行汇票提示付款时，应在农信银银行汇票背面“持票人向银行提示付款签章”处签章，并填明本人身份证件名称、号码及发证机关，由其本人向代理付款支行（部）提交有效身份证件及其复印件。各支行（部）审核无误后，将其身份证件复印件留存备查，款项转入应解汇款待取。转账支付的，应由本人填制取款凭证，签章并交验其有效身份证件；需要支取现金的，由各支行（部）按照国家现金管理规定审查支付。持票人对填明“现金”字样的农信银银行汇票，需要委托他人向银行提示付款的，应在农信银银行汇票背面背书栏签章，记载“委托收款”字样、被委托人姓名和背书日期以及委托人身份证件名称、号码、发证机关。被委托人向各支行（部）提示付款时，应在银行汇票背面“持票人向银行提示付款签章”处签章，记载证件名称、号码及发证机关，并同时向银行交验委托人和被委托人的身份证件及其复印件。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十四条', '各支行（部）受理兑付农信银银行汇票，应对汇票进行审查，无误后通过农信银资金清算系统发出农信银银行汇票解付请求，待收到农信银银行汇票解付成功信息后，及时将汇票实际结算资金划转收款人账户。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十五条', '农信银银行汇票的实际结算金额不得更改，更改实际结算金额的银行汇票无效'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十六条', '各支行（部）解付农信银银行汇票有多余款的必须在当日将多余款退回原汇票申请人账户。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十七条', '农信银银行汇票未用退回的，必须由原申请人作出书面说明，并提供个人有效身份证件或单位证明，办理退汇手续。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十八条', '农信银银行汇票超过提示付款期，持票人请求付款时，应作出书面说明，并提供个人身份证或单位证明，经审核无误后，办理退款处理。转账农信银银行汇票的退款，只能转入原申请人账户；符合规定填明“现金”字样农信银银行汇票的退款，才能办理退付现金。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第二十九条', '各支行（部）代理受理农信银银行汇票挂失止付和解除挂失止付的，应通过农信银资金清算系统发出自由格式报文通知出票行，由出票行进行挂失止付和解除挂失止付确认办理。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第三十条', '支行（部）收到自由格式报文后，应在当日营业终了前进行业务处理并查复，如因特殊情况不能查复，最迟不得超过下一个工作日上午10时予以查复。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第三十一条', '农信银银行汇票的挂失止付与解除挂失止付必须经过授权方可办理。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第三十二条', '各支行（部）办理农信银银行汇票业务及资金清算时必须做到：“有疑必查、有查必复、查必及时、复必详尽”。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第三十三条', '办理农信银银行汇票业务时遇下列情况之一的，应当发出查询。（一）农信银银行汇票密押错误；（二）农信银银行汇票专用章不清晰；（三）其他需要查询的事项。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第三十四条', '农信银银行汇票的查询查复应通过农信银资金清算系统办理。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第三章   付　款', '第三十五条', '受理有缺陷有疑问的农信银银行汇票，必须按照《中华人民共和国票据法》、《支付结算办法》和其他相关规定，及时向出票行发出查询。出票行收到查询信息，应当在当日营业终了前进行查复，如因特殊情况不能查复，最迟不得超过下一个工作日上午10时予以查复，不得推诿和拒绝。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第四章   退票处理', '第三十六条', '本办法所称退票，是指代理付款各支行（部）拒绝受理农信银银行汇票和出票各支行（部）拒绝付款的行为。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第四章   退票处理', '第三十七条', '持票人提交的农信银银行汇票存在下列情形之一的，代理付款各支行（部）应拒绝受理：（一）必须记载事项记载不全；（二）银行汇票未按中国人民银行统一规定印制；（三）大、小写金额不一致或实际结算金额超过出票金额；（四）签章不符合规定；（五）无密押或密押不清晰；（六）背书不连续或不符合规定；（七）超过提示付款期；（八）不得更改事项更改或可更改事项未按规定更改；（九）出票银行在银行汇票正面记载“不得转让”的银行汇票已背书转让；（十）使用粘单未按规定加盖印章；（十一）出票日期使用小写数字填写；（十二）未使用规定格式的汇票专用章或票面上未记载代理清算行的银行机构代码；（十三）因票面污损导致必须记载事项无法辨认；（十四）单位持票人不在本支行（部）开户；（十五）个人持票人身份证件核查不符；（十六）中国人民银行规定的其他拒绝受理事项。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第四章   退票处理', '第三十八条', '代理付款的各支行（部）拒绝受理农信银银行汇票时，应向持票人出具拒绝受理通知书。出票各支行（部）拒绝付款时，由代理付款行根据出票各支行（部）的拒绝付款回执代为向持票人出具退票理由书。代理付款各支行（部）应将银行汇票连同拒绝受理通知书或退票理由书一并退交持票人。拒绝受理通知书和退票理由书均可作为拒绝付款的证明。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第四章   退票处理', '第三十九条', '拒绝受理通知书应记载下列事项：（一）汇票号码；（二）出票银行名称；（三）出票日期；（四）收款人名称；（五）持票人名称；（六）出票金额；（七）实际结算金额；（八）拒绝受理理由；（九）拒绝受理日期；（十）代理付款行名称；（十一）经办人及审批人签章；（十二）代理付款行签章。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第四章   退票处理', '第四十条', '退票理由书应记载下列事项：（一）汇票号码；（二）出票银行名称；（三）出票日期；（四）收款人名称；（五）持票人名称；（六）出票金额；（七）实际结算金额；（八）退票理由；（九）退票日期；（十）代理付款行名称；（十一）代理付款行代出票银行出具退票理由书的字样；（十二）经办人及审批人签章；（十三）代理付款行签章。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第五章   业务监督与责任', '第四十一条', '本行运营管理部应履行下列职责：（一）加强农信银银行汇票业务管理和清算账户管理，定期或不定期对农信银银行汇票业务进行监督和检查；（二）及时、安全、准确办理农信银银行汇票业务的资金清算；（三）负责辖内各支行（部）农信银银行汇票业务指导和培训工作；（四）建立风险预警控制机制，有效防范农信银银行汇票业务资金清算风险。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第五章   业务监督与责任', '第四十二条', '支行（部）经办人员，主管会计应定期核对农信银银行汇票卡片，确保农信银银行汇票账、卡、簿一致。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第五章   业务监督与责任', '第四十三条', '支行（部）不得签发无资金保证的农信银银行汇票；不得签发未通过农信银资金清算系统发送申请的农信银银行汇票，不得解付未收到解付成功信息的农信银银行汇票，发生错付或误付，由过错方承担责任。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第五章   业务监督与责任', '第四十四条', '支行（部）有下列行为之一的，应承担相应责任和经济赔偿；情节严重的，暂停或取消其办理农信银银行汇票业务资格。（一）对汇票业务有疑不查或查而不复的；（二）无理拒付农信银银行汇票或故意压票的；（三）违反农信银银行汇票印章、凭证管理规定的；（四）汇票专用章、空白农信银银行汇票凭证因保管不善，发生丢失或被盗的；（五）农信银银行汇票票样、汇票专用章印模丢失的；'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第六章   附  则', '第四十五条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《农信银汇票管理办法》', '第六章   附  则', '第四十六条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第一章 总 则', '第一条', '为加强农信银资金清算系统个人账户通存通兑业务的管理，防范支付风险，实现业务操作、管理的规范化和标准化，根据《农信银资金清算系统个人账户通存通兑业务操作规程》以及相关规定，制定本办法。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第一章 总 则', '第二条', '本办法所称农信银资金清算系统个人账户通存通兑业务（以下简称“通存通兑业务”）是指农信银资金清算系统参与者受客户委托，为其在异地农村信用社（含农村商业银行、农村合作银行等,下同）开立的个人账户办理存款、取款、转账、余额查询等业务的行为，通过农信银资金清算中心完成受理方参与者与开户方参与者之间的资金清算。本办法所称个人存款账户包括个人银行结算账户活期存折、个人银行结算账户借记卡和个人活期储蓄账户存折（以下简称“个人存款账户存折（卡）”）。本办法所称个人客户（以下简称“客户”）是指持有有效身份证件的自然人。有效身份证件包括居民身份证或户口簿（不满16周岁）、港澳居民往来内地通行证、台湾居民来往大陆通行证、护照及法律、行政法规规定的其他身份证明文件。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第一章 总 则', '第三条', '通过农信银资金清算系统办理通存通兑业务的网点和客户，应当遵守本办法及有关支付结算、现金管理、反洗钱等法规制度的规定。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第一章 总 则', '第四条', '客户可使用个人银行结算账户活期存折、个人银行结算账户借记卡和个人活期储蓄账户存折（以下简称“个人存款账户存折（卡）”）办理通存通兑业务。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第一章 总 则', '第五条', '发起与接收通存通兑业务的参与者分别称为受理社（行）和开户社（行）。　本办法所称受理社（行）是指根据客户委托发起通存通兑业务指令办理个人存取款业务的农村信用社。本办法所称开户社（行）是指接收受理行发来的通存通兑业务指令，并对客户指定的人民币个人存款账户进行业务处理的农村信用社。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第一章 总 则', '第六条', '通存通兑业务资金清算采取“逐笔发起、实时清算”的方式。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第二章  基本规定', '第七条', '本办法所指通存通兑的业务范围（一）活期账户异地现金续存业务，包括柜台有／无折存款，柜台有／无卡存款，自助设备存现；（二）活期账户异地取现业务，包括柜台有卡／有折取现，自助设备取现；（三）异地转账业务，包括柜台本地卡／折转入异地账户，柜台异地卡／折转入本地账户，自助设备异地卡转入本地账户，自助设备本地卡转入异地账户；（四）异地查询账户余额，包括柜台查询卡／折余额，自助设备查询卡余额。通存业务和通兑业务可委托他人办理，账户信息查询业务仅可由客户本人通过效验密码办理。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第二章  基本规定', '第八条', '本办法所指的通存通兑业务不能跨社（行）异地办理的业务：（一）更换存折或银行卡；（二）修改客户的支付条件（包括密码的修改）；（三）重写磁条信息；（四）有权执法机关的冻结、解冻、查询、扣划；（五）存折或银行卡的挂失及解挂；（六）补登折；（七）定期存款的结清、部分提前支取；（八）活期账户的销户；（九）明细账务查询及打印；'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第二章  基本规定', '第九条', '业务受理依据（一）客户凭存折、银行卡（或存折账号、银行卡卡号）、存款凭条在柜台办理通存业务，凭银行卡在自助设备上办理通存业务；（二）客户凭存折、银行卡、取款凭条及折、卡密码在柜台办理通兑业务，办理大额取现、转账业务的客户还应提供开户时使用的有效身份证件，凭银行卡及卡密码在自助设备上办理通兑业务；（三）客户本人凭存折、银行卡及折、卡密码在柜台办理余额查询业务，凭银行卡及卡密码在自助设备上办理余额查询业务。（四）办理异地通存通兑业务的客户存折，应记载（打印或加盖条形印章）开户社（行）的农信银行号。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第二章  基本规定', '第十条', '农信银资金清算系统支持７*２４小时处理通存通兑业务。在网点营业期间，客户可通过柜台和自助设备办理通存通兑业务，在网点停止营业期间，客户可通过自助设备办理相应业务。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十一条', '各支行、部应遵守本办法以及其他相关法规，强化内控管理，严密审核手续，加强业务管理、事后稽核和检查监督，确保通存通兑业务处理的准确和安全。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十二条', '清算团队应加强对上存清算资金的监控管理，确保客户通存通兑业务资金实时、及时清算。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十三条', '银行办理通存通兑业务应确保客户账户信息安全，技术设计上实现客户账户信息数据的密文传输，确保账户信息不被泄漏或窃取。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十四条', '在办理通存通兑业务过程中出现以下行为，将由受理社（行）承担相应的赔偿责任：（一）为客户办理无卡／无折取款或查询；（二）超过系统响应时间未收到应答，在不能确定通存交易是否成功的情况下将现金退还客户；（三）超过系统响应时间未收到应答，在不能确定通兑交易是否成功的情况下向客户支付现金；    （四）由于人为原因造成处理差错、未能及时冲正，造成资金损失的。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十五条', '开户社（行）未收到“存款确认通知”解止付客户账户资金，造成资金损失的，由开户社（行）承担赔偿责任。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十六条', '因系统故障造成的通存通兑业务差错，责任银行应主动协调解决，对方银行应密切配合，确保客户资金安全。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十七条', '有下列情形之一的，依法承担赔偿责任：（一）工作人员办理通存通兑业务玩忽职守或出现重大过失，造成客户资金损失的；（二）泄漏客户账户信息，造成客户资金损失的。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十八条', '因客户自身原因造成通存通兑业务处理错误的，由客户自行联系受理社（行）和开户社（行）协商解决，受理社（行）和开户社（行）应予以配合。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第三章  风险管理', '第十九条', '客户应妥善保管个人存款账户存折（卡）及其密码。因客户自身原因造成个人存款账户存折（卡）遗失或密码泄露导致资金损失和账户信息泄露的，客户自行承担责任。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第四章   附 则', '第二十条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《农信银行系统个人账户通存通兑业务管理办法》', '第四章   附 则', '第二十一条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《出纳基本制度》', '第一章  总则', '第一条', '为了加强xx银行（以下简称“本行”）出纳工作管理，不断提高工作质量，切实保障现金安全，根据《中华人民共和国商业银行法》、《储蓄管理条例》和《现金管理暂行条例》等法律法规，制定本制度。'),
(NULL, '运营管理部', '《出纳基本制度》', '第一章  总则', '第二条', '本行及所属各支行（部）办理出纳业务均应遵守本制度。'),
(NULL, '运营管理部', '《出纳基本制度》', '第一章  总则', '第三条', '出纳工作的主要任务：（一）按照国家法令、法规办理现金（包括人民币和外币现金，下同）的收付、整点以及损伤票币、大小票币的兑换，代理各种债券的保管、发行与兑付；（二）根据市场货币流通需要，调剂、调运各种票币，做好现金供应和回笼工作；（三）保管现金、金银、外币和有价单证，做好现金、金银、外币和有价单证调运的安全保卫工作；（四）严格库房管理，确保库款安全；（五）宣传爱护人民币，做好反假币、反破坏人民币的工作；（六）做好现金管理工作，加强核算，减少库存现金占用，提高经济效益；（七）加强柜面监督，维护财经纪律，防范贪污盗窃和各种违法活动。'),
(NULL, '运营管理部', '《出纳基本制度》', '第一章  总则', '第四条', '配备好出纳及押运人员，并保持相对稳定。本行运营管理部要指定专人管理出纳业务，各支行（部）要配备与出纳业务相适应的出纳人员。本行清算团队承担现金调拨工作职能，安排专职的管库人员和现金调缴人员。本行监察保卫部门负责安排现金押运人员。'),
(NULL, '运营管理部', '《出纳基本制度》', '第二章  基本规定', '第五条', '经办出纳业务，必须坚持以下基本规定：（一）现金收入先收款后记账，现金付出先记账后付款；（二）双人临柜、双人管库、双人守库、双人押运；（三）凡现金、金银、有价单证，以及库房钥匙，业务公章等重要物品换人经管时，必须办理交接登记手续；（四）及时核对库存，做到账款、账实相符；（五）未经业务技术培训的人员，不得直接对外办理现金出纳业务。实行综合柜员制的，按本行有关规定执行。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第六条', '现金收付必须依据内容正确，要素齐全的有效凭证办理，做到手续完备、责任分明、数字准确。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第七条', '出纳员不得代填或代改缴款凭证，收付现金要当面点清，一笔一清，票币封签对外无效。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第八条', '执行一天三次轧库制度，每日营业终了，交叉复核，应将账款核对一致，双人锁箱，将现金全部入箱保管(日终入箱至本行金库保管)。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第九条', '按本行财务部门核定的业务库存限额合理保留库存现金，超过限额要及时缴存。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第十条', '积极办理人民币大小票币、残缺票币兑换业务，不得拒绝办理。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第十一条', '现金柜员应有计划地做好主、辅币的调剂工作。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第十二条', '残缺票币的兑换标准，应按照《中国人民银行残缺污损人民币兑换办法》规定的标准和操作程序办理。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第十三条', '凡发现图案不全、墨色不正、裁切偏斜以及漏印花纹等印坏的票币，应予以收回。收回的印坏票币连同原封签通过本行中心库房送中国人民银行当地支行处理。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第十四条', '收付、整点票币时，应随时挑出损伤票币，挑剔损伤票币按照中国人民银行《不宜流通人民币挑剔标准》处理：（一）纸币票面缺少面积在20平方毫米以上；（二）纸币票面裂口2处以上，长度每处超过5毫米；裂口1处，长度超过10毫米；（三）纸币票面有纸质较绵软，起皱较明显，脱色、变色、变形，不能保持其票面防伪功能等情形之一；（四）纸币票面污渍、涂写字迹面积超过2平方厘米；不超过2平方厘米，但遮盖了防伪特征之一；（五）硬币有穿孔，裂口，变形，磨损，氧化，文字、面额数字、图案模糊不清等情形之一。'),
(NULL, '运营管理部', '《出纳基本制度》', '第三章  现金的收付、兑换与整点', '第十五条', '票币整点必须做到点准、墩齐、挑净、捆紧、盖章清楚。50元券、100元券纸币经点钞机初点后必须由手工进行复点。纸尖按券别平铺整理，100张为1把，10把为1捆，以双十字捆扎；硬币100枚（或50枚）为1卷，10卷为1捆。每把（卷）须盖带行号的经手人名章，每捆封签应标明单位行号行名、券别、金额、封捆日期，加盖带行号的封捆员和复核员名章。损伤票币除按上述方法整理外，还应对主币分版捆扎，并及时缴存中国人民银行当地支行。'),
(NULL, '运营管理部', '《出纳基本制度》', '第四章  库房管理与现金运送', '第十六条', '本行应设置出纳专用库房，库房的结构，设施要符合规定标准。'),
(NULL, '运营管理部', '《出纳基本制度》', '第四章  库房管理与现金运送', '第十七条', '库内摆列整齐、保持清洁。库内不准存放私人财物和其他物品。'),
(NULL, '运营管理部', '《出纳基本制度》', '第四章  库房管理与现金运送', '第十八条', '配备两名工作责任心强、诚实可靠的管库员负责管库工作。管库员要明确分工，出入库时必须同进同出，出入库的款项、实物实行双人办理，相互复核，共同负责。除管库员外，其他人员未经领导批准不得进入库房。管库员离职，必须办妥交接手续。'),
(NULL, '运营管理部', '《出纳基本制度》', '第四章  库房管理与现金运送', '第十九条', '库房、保险柜必须装备两把锁，且至少有一把为组合锁，机械锁要有正副钥匙各一套。（一）机械锁正钥匙的管理。机械锁正钥匙与组合锁密码由两名管库员分别掌管，不准随意委托他人代管，库房门和保险柜门随启随闭；（二）机械锁副钥匙的管理。本行中心库房的副钥匙由管库员、分管行长和运营管理部总经理当面共同密封，加盖印章，办理登记签收手续，由运营管理部妥善保管。各支行（部）的库房、保险柜副钥匙由管库员会同运营主管共同密封签章后，交本行监察保卫部集中装箱加锁入库保管。遇有特殊情况，须动用副钥匙时，经本行分管行长批准，并会同管库员及出纳(或会计)主管人员启封，并登记签章；(三)组合锁密码。组合锁密码由管库员会同分管行长共同设定，密码密封，加盖印章，办理登记签收手续，由分管行长妥善保管。开启密码由管库员会同分管行长共同启封，并登记签章。管库员变动，密码随之更换；(四)库房、保险柜钥匙必须严密保管，严禁随意放置，如有遗失应及时报告本行，并采取防范措施。库房、保险柜的修锁、换锁，必须向本行报告，由本行指定人员修、换，并办理以旧换新和交接手续。'),
(NULL, '运营管理部', '《出纳基本制度》', '第四章  库房管理与现金运送', '第二十条', '实行查库制度。各支行（部）运营主管每周至少查库一次；各支行（部）行长（总经理）每月至少查库一次，每季查库不少于四次；本行运营管理部每月至少对中心库房查库一次；本行分管运营管理部的副行长每季查库不少于一次；本行行长每年至少进行一次全面性查库。上级管理部门应对各支行（部）不定期进行查库，查库时必须携带查库介绍信，并应有管库员在场。查库的任务是：（一）盘点库存现金、金银等实物是否账实相符，有无白条抵库和挪用库款情况，是否发生虫蛀、鼠咬及霉烂事故。（二）各项安全措施（包括联防组织）是否落实；（三）库房、保险柜和守库室的安全设备是否齐全，是否符合安全条件。每次查库完毕，应将查库情况详细登记查库登记簿，并由检查人员和管库员共同签章，以备查考。如发现问题，要立即查明原因，严肃处理，重大问题要及时上报上级部门。夜间只查岗不查库。'),
(NULL, '运营管理部', '《出纳基本制度》', '第四章  库房管理与现金运送', '第二十一条', '运送现金、金银、外币及有价证券等贵重物品，必须由两人以上持枪(械)负责押运(不包括司机)，不准委托他人押运。各支行（部）解缴款应由本行专职押解员负责运送。运钞汽车必须保证解缴现金的使用，严禁徒步或自行车运送。'),
(NULL, '运营管理部', '《出纳基本制度》', '第四章  库房管理与现金运送', '第二十二条', '押运现金、金银、外币、有价证券必须注意安全，严密交接手续。对运送时间、地点和路线，要严守秘密，不得向任何无关人员透露。押运车辆不准搭乘无关人员及捎运其他物品。运送时，押运人员应按运送凭单金额或件数点收清楚，并检查运送包装是否牢固，无误后在运送凭条留存联上签章以示收妥。运送途中，在任何情况下都必须有两人严密看守，不得擅离。运到后，原封新券按箱验收，笼券须当面点清细数，如系零星调拨，须当面清点细数无误后，方能开给正式收据或回单。'),
(NULL, '运营管理部', '《出纳基本制度》', '第五章  错款的处理', '第二十三条', '发生出纳错款，应立即查找，并向有关部门报告。对于确实无法挽回的错款损失，要区别性质进行处理。（一）技术性错款，按规定的手续审批后，长款归公、短款报损，不得以长补短。误收确实难以辨认的假币，按规定的手续审批后报损；（二）责任事故错款，应按长款归公，短款自赔的原则处理；（三）属于自盗、挪用以及侵吞长款的，均应追回款项，并给予相应的处分；触犯刑律的，移交司法部门处理。'),
(NULL, '运营管理部', '《出纳基本制度》', '第五章  错款的处理', '第二十四条', '现金调拨发生的差错，调入行和调出行都必须负责认真追查。调入行在发现差错时，应先检查本行在款项调运、保管、收付过程中是否发生问题。如经调查分析，确非本行责任时，应立即将有关证件及详细经过情况送调出行追查。如无法确定调出行或调入行责任时，可按错款审批权限报批，由调入行作收益或报损。'),
(NULL, '运营管理部', '《出纳基本制度》', '第五章  错款的处理', '第二十五条', '原封新券（币）或调入款发生错款，应查明原因，如确非本行责任，应开具证明，说明发现经过（拆捆时原封有无破裂，录像，证明人等）及捆内各把票券的起讫号码、缺号、补号情况，并在证明上加盖经手人和证明人章，经运营主管和各支行（部）行长（总经理）审批后，连同原捆封签、原把纸条，通过本行中心库房报送中国人民银行当地支行审查。如发现捆内整把（券）短少或把（券）内短少十张以上者，应将原捆、原把票币原样保管，并立即将发现经过及原捆的冠字、号码、封捆时间和封捆员代号送交中国人民银行当地支行处理。'),
(NULL, '运营管理部', '《出纳基本制度》', '第五章  错款的处理', '第二十六条', '各支行（部）发生出纳错款时，应在出纳错款登记簿上逐笔详细登记，如实填报出纳错款报告表并按规定时间报送。'),
(NULL, '运营管理部', '《出纳基本制度》', '第六章  票样管理与反假', '第二十七条', '人民币票样是鉴别人民币真假和进行反假币斗争的重要资料，本行运营管理部要指定专人负责并建立领发保管手续。分发保管票样时，要办理签收手续，并登记票样登记簿，按券别、版别立户登记号码和领用单位名称，以便查考。换人保管票样时，应办理交接手续，真假币鉴别手册及资料保管，也按上述规定办理。'),
(NULL, '运营管理部', '《出纳基本制度》', '第六章  票样管理与反假', '第二十八条', '发现流入市场的票样，应予收回，并追查票样的来源，如经调查确系误收，可按照兑换办法兑回。'),
(NULL, '运营管理部', '《出纳基本制度》', '第六章  票样管理与反假', '第二十九条', '票样领用单位撤并时，应将多余的票样上缴上级管理部门。如管理部门变更，应将票样移交情况分别报原管理部门和变更后的管理部门。'),
(NULL, '运营管理部', '《出纳基本制度》', '第六章  票样管理与反假', '第三十条', '发现假币，一律没收，并应当着客户的面在假币下正、背面加盖带各支行（部）行号的“假币”戳记和经办员名章，同时开具假币收缴凭证，登记假币登记簿。已没收的假币（券），应通知持币人，收回临时收据，换开“假币没收证”；如非假币（券），亦通知持币人，收回临时收据，并按面值予以兑付。'),
(NULL, '运营管理部', '《出纳基本制度》', '第六章  票样管理与反假', '第三十一条', '入库保管的已没收假币（券），应按照中国人民银行当地支行规定上缴，并办好出库、交接手续，严防已没收假币（券）重新流入市场。'),
(NULL, '运营管理部', '《出纳基本制度》', '第六章  票样管理与反假', '第三十二条', '凡发现挖补、拼凑、揭去一面以及有意损坏、涂抹等被破坏的人民币，原则上均应没收。经追查如确系误收的，可按残缺人民币兑换办法给予兑换。'),
(NULL, '运营管理部', '《出纳基本制度》', '第六章  票样管理与反假', '第三十三条', '外币票样的管理和反假，按中国人民银行有关制度执行。'),
(NULL, '运营管理部', '《出纳基本制度》', '第七章  出纳交接', '第三十四条', '凡现金、金银、外币、有价单证、收付讫章等出入库和库房、保险柜钥匙（密码）的转移都必须办理交接手续。应由交接双方及监交人当面点交清楚，并登记交接登记簿和共同签章。交接登记簿应妥善保管，以备查考。'),
(NULL, '运营管理部', '《出纳基本制度》', '第七章  出纳交接', '第三十五条', '出纳人员调动工作或离职时，必须将经办的一切款、物、账移交清楚，其中管库人员应将保管的现金、金银、有价单证等，按当日实有库存详列移交表，与会计账簿核对相符，经接收人、监交人会同清点无误后，共同在移交表上签章；保管库房和保险柜副钥匙的人员调动，也应办理交接手续。未办妥交接手续不得离职。如临时离职亦应执行同一个人不得交叉接触两把钥匙（包括密码）的原则，出纳人员应与领导指定的代理人员办理交接手续，做到责任分明。'),
(NULL, '运营管理部', '《出纳基本制度》', '第七章  出纳交接', '第三十六条', '本行中心库房出纳主管人员调动工作、离职、退休时，应将出纳部门所保管的一切现金、金银、贵重物品等，全部填列移交表，与会计账簿核对，由本行运营管理部派人监交点验无误后，由监交人及交接双方在移交表上签字，移交表归档保管。'),
(NULL, '运营管理部', '《出纳基本制度》', '第八章  附 则', '第三十七条', '本制度由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《出纳基本制度》', '第八章  附 则', '第三十八条', '本制度自发文之日起施行。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第一章　 总　则', '第一条', '为加强xxx银行（以下简称“本行”）华东三省一市银行汇票业务的管理，规范票据行为，保障银行汇票业务活动中当事人的合法权益，根据《中华人民共和国票据法》、《票据管理实施办法》、《支付结算办法》等有关法律法规规定，结合本行实际，制定本办法。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第一章　 总　则', '第二条', '华东三省一市银行汇票（以下简称“银行汇票”）是华东三省一市（江苏、浙江、安徽、上海）区域内出票银行签发的，由其或代理付款行在见票时按照实际结算金额无条件支付资金给收款人或持票人的票据。单位和个人在华东三省一市区域内的人民币款项结算，均可以使用华东三省一市银行汇票。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第一章　 总　则', '第三条', '银行汇票的出票银行为银行汇票的付款人。代理付款行是指代理出票银行审核支付汇票款项的银行。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第一章　 总　则', '第四条', '本行辖内有支付系统行号的各支行（部）均可签发银行汇票。签发银行汇票应履行以下手续：（一）本行通过省联社资金结算中心向中国人民银行南京分行申请印制、订购银行汇票；（二）按照中国人民银行的统一规定刻制银行汇票专用章；（三）内控制度完善，分工职责明确，印证管理严格，熟悉并严格执行银行汇票的各项制度和办法，配备符合岗位条件的人员。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第一章　 总　则', '第五条', '各支行（部）受理银行汇票须通过综合业务系统实时核对票据信息和资金清算业务，业务办理不受金额限制。跨系统签发的银行汇票原则上应依托小额支付系统，通过支付平台中“票据解付”交易办理；系统内签发的银行汇票原则上应通过结算平台票据结算中的“银行汇票”交易办理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第一章　 总　则', '第六条', '银行汇票付款形式为见票即付。见票即付是指各支行（部）在代理付款或出票银行在受理收款人或持票人提交的银行汇票时，审核无误后按实际结算金额将票款即时解付给收款人或持票人的行为。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第七条', '银行汇票可以用于转账，填明“现金”字样的银行汇票也可以用于支取现金。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第八条', '申请人使用银行汇票，应向出票银行提交“结算业务申请书”，并向银行足额缴存汇票款项。“结算业务申请书”应填明申请日期、申请人名称、收款人名称、金额、经办人身份证件等事项并签章，单位签章为其预留银行印鉴，个人为其本人的签名或盖章。约定使用支付密码的，应在申请书上填写支付密码。申请人和收款人均为个人需要支取现金的，申请书大写金额栏应先填有“现金”字样，后有支付金额。申请人或收款人为单位的，不得在申请书金额栏填写“现金”字样。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第九条', '各支行（部）签发现金银行汇票，申请人和收款人必须均为个人，各支行（部）收妥申请人交存的款项后，在银行汇票“出票金额”栏先填写“现金”字样，后填写出票金额，签发现金银行汇票需填写代理付款人名称。申请人或者收款人为单位的，各支行（部）不得为其签发现金银行汇票。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十条', '各支行(部)受理结算业务申请书，经审核凭证要素无误、收妥款项，依托综合业务系统签发银行汇票。    第十一条  各支行（部）签发银行汇票应当记载下列事项：（一）表明“华东三省一市银行汇票”的字样；（二）无条件支付的承诺；（三）出票金额；（四）付款人名称；（五）收款人名称；（六）出票日期；（七）出票人签章；（八）密押。欠缺上述第一至第七事项之一的，银行汇票无效。欠缺第八项记载事项的，各支行（部）不予受理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十二条', '各支行（部）签发银行汇票的出票日期必须大写，汇票金额以大写中文字符和阿拉伯数码同时记载，两者必须一致。不一致的，银行汇票无效。银行汇票的出票金额、出票日期、收款人名称以及密押不得更改，更改的票据无效。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十三条', '各支行（部）签发银行汇票后，将银行汇票交付申请人。各支行（部）在银行汇票上的签章，应为银行汇票专用章加其法定代表人或者其授权代理人的签章。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十四条', '银行汇票可以背书转让。收款人可以将银行汇票背书转让给被背书人，背书转让时，应在背书人栏签章，记载背书日期和被背书人全称。但填明“现金”字样的银行汇票不得背书转让。银行汇票的背书转让以实际结算金额为准。未填写实际结算金额或实际结算金额超过出票金额的银行汇票不得背书转让。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十五条', '银行汇票不能满足背书人记载事项的需要，可以加附粘单，粘贴于银行汇票上。粘单上的第一记载人，应当在银行汇票粘单的粘接处签章。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十六条', '背书由背书人签章并记载背书日期。背书未记载日期的，视为在银行汇票到期日前背书。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十七条', '银行汇票以背书转让或者以背书将一定的银行汇票权利授予他人行使时，必须记载被背书人名称。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十八条', '以背书转让的银行汇票，背书应当连续。持票人以背书的连续，证明其票据权利；非经背书转让，而以其他合法方式取得银行汇票的，依法举证，证明其票据权利。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第十九条', '背书不得附有条件。背书时附有条件的，所附条件不具有本票上的法定效力。将汇票金额的部分转让的背书或者将汇票金额分别转让给二人以上的背书无效。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第二章   出票与背书', '第二十条', '出票银行在汇票正面记载“不得转让”字样的银行本票，不得背书转让；其直接后手再背书转让的，出票人对其直接后手的被背书人不承担保证责任，对被背书人提示付款或委托收款的银行汇票，银行不予受理。背书人在银行汇票背面背书人栏记载“不得转让”字样的，其后手再背书转让的，记载“不得转让”字样的背书人对其后手的被背书人不承担保证责任。但出票银行不能以此抗辩持票人。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十一条', '银行汇票的提示付款期限为自出票日起1个月。持票人超过提示付款期限提示付款的，各支行（部）不予受理。提示付款日期以持票人向各支行（部）提交票据的日期为准。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十二条', '在银行开立结算账户的持票人向开户银行提示付款时，应在银行汇票背面“持票人向银行提示付款签章”处签章，签章应与预留银行签章相同，并将银行汇票和进账单送交开户行。各支行（部）审查无误后办理转账手续。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十三条', '未在本支行（部）开立结算账户的个人持银行汇票提示付款时，应在银行汇票背面“持票人向银行提示付款签章”处签章，并填明本人身份证件名称、号码及发证机关，由其本人向代理付款行提交有效身份证件及其复印件。各支行(部)审核无误后，将其身份证件复印件留存备查，款项转入应解汇款待取。转账支付的，应由本人填制转账凭证，签章并交验其有效身份证件办理支付款项；需要支取现金的，由各支行（部）按照国家现金管理规定审查支付。持票人对填明“现金”字样的银行汇票，需要委托他人向银行提示付款的，应在银行汇票背面背书栏签章，记载“委托收款”字样、被委托人姓名和背书日期以及委托人身份证件名称、号码、发证机关。被委托人向各支行（部）提示付款时，应在银行汇票背面“持票人向银行提示付款签章”处签章，记载证件名称、号码及发证机关，并同时向银行交验委托人和被委托人的身份证件及其复印件。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十四条', '各支行(部)受理银行汇票时，应审查下列事项：（一）收款人是否为在本支行(部)开立存款账户的单位或个人；（二）是否在提示付款期限内；（三）必须记载的事项是否齐全，内容是否符合规定；（四）出票人签章是否符合规定；（五）出票日期是否使用中文大写；（六）出票金额、出票日期、收款人名称是否更改，更改的其他记载事项是否由原记载人签章证明；（七）记载的密押是否清晰；（八）是否记载实际结算金额，实际结算金额有无更改，其金额是否超过出票金额；（九）背书是否连续，背书人签章是否符合规定，背书使用粘单的是否按规定签章；（十）提示付款人为个人的还应审核背书人的有效身份证件并留存复印件。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十五条', '收款人受理申请人交付的银行汇票时，应在出票金额以内，根据实际需要的款项办理结算，并将实际结算金额和多余金额准确、清晰地填入银行汇票的有关栏目内。未填明实际结算金额和多余金额或实际结算金额超过出票金额的，各支行（部）不予受理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十六条', '银行汇票的实际结算金额低于出票金额的，其多余金额由出票银行退交申请人。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十七条', '持票人超过提示付款期限向代理付款支行（部）提示付款不获付款的，须在票据权利时效内向出票银行作出说明，并提供本人身份证件或单位证明，持银行汇票向出票银行支行（部）请求付款。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十八条', '申请人因银行汇票超过提示付款期限或其他原因要求退款时，应将银行汇票提交到出票行。申请人为单位的，应出具单位的证明；申请人为个人的，应出具本人的身份证件。出票行对于转账银行汇票的退款，只能转入原申请人账户；对于符合规定填明“现金”字样银行汇票的退款，才能退付现金。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第二十九条', '持票人提交的银行汇票存在下列情形之一的，代理付款行应拒绝受理：（一）必须记载事项记载不全；（二）银行汇票未按中国人民银行统一规定印制；（三）大、小写金额不一致或实际结算金额超过出票金额；（四）签章不符合规定；（五）无密押或密押不清晰；（六）背书不连续或不符合规定；（七）超过提示付款期限；（八）不得更改事项更改或可更改事项未按规定更改；（九）出票银行在银行汇票正面记载“不得转让”的银行汇票已背书转让；（十）使用粘单未按规定加盖印章；（十一）出票日期使用小写数字填写；（十二）未使用规定格式的汇票专用章或票面上未记载代理清算行的银行机构代码；（十三）因票面污损导致必须记载事项无法辨认；（十四）单位持票人不在本支行（部）开户；（十五）个人持票人身份证件联网核查信息不符的；（十六）中国人民银行规定的其他拒绝受理事项。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第三十条', '各支行（部）办理银行汇票业务及资金清算时必须做到“有疑必查、有查必复、查必及时、复必详尽”。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第三章  付 款', '第三十一条', '办理银行汇票业务时遇下列情况之一的，应当发出查询：（一）银行汇票密押错误；（二）银行汇票专用章不清晰；（三）其他需要查询的事项。银行汇票的查询查复应通过综合业务支付、结算系统办理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第四章   丧失票据权利', '第三十二条', '未填明“现金”字样的银行汇票丧失，不得挂失止付。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第四章   丧失票据权利', '第三十三条', '允许挂失止付的银行汇票丧失，应向代理付款人或出票人申请挂失止付，填写挂失申请书申请挂失止付，并在申请书上注明：(一)汇票丧失的时间、地点、原因；(二)汇票的号码、金额、出票日期、付款人名称、收款人名称等；(三)挂失止付人的姓名、住址、联系方法。欠缺上述记载事项之一的，各支行(部)不予受理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第四章   丧失票据权利', '第三十四条', '各支行(部)收到挂失申请书后，查明该汇票确未付款时，应立即依托综合系统进行挂失止付处理，暂停支付。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第四章   丧失票据权利', '第三十五条', '各支行(部)自收到挂失申请书之日起12日内未收到人民法院的止付通知书的，自第13日起，持票人提示付款并依法向持票人付款的，不再承担责任。各支行(部)在收到挂失止付通知书之前，已经向持票人付款的，不再承担责任。但是，恶意或重大过失付款的除外。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第四章   丧失票据权利', '第三十六条', '失票人可凭人民法院出具的其享有票据权利的证明，期满后向出票银行请求付款或退款。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第四章   丧失票据权利', '第三十七条', '银行汇票的公示催告和提起诉讼按照《中华人民共和国民事诉讼法》、《中华人民共和国票据法》的有关规定办理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第五章  纪律和责任', '第三十八条', '本行应当遵守本办法以及其他相关规定，加强内部控制管理，建立健全应急处理机制，保证系统安全稳定运行和业务的及时、准确处理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第五章  纪律和责任', '第三十九条', '各支行（部）应按照本办法的规定审核银行汇票，确保银行汇票的真实性及票面信息的合规性、完整性。代理付款行未按规定审查银行汇票，造成出票银行错付款项的，应承担相应的违规责任。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第五章  纪律和责任', '第四十条', '除法律规定和本办法规定外，各支行（部）不得拒绝受理持票人提交的银行汇票，不得拒绝付款。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第五章  纪律和责任', '第四十一条', '各支行（部）有下列情形之一的，本行应当予以通报：（一）违反本办法规定受理银行汇票的；（二）违反本办法规定拒绝受理银行汇票的；（三）未按本办法规定及时、准确发起银行汇票信息的；（四）未按本办法规定及时返回银行汇票业务回执的；（五）未按本办法规定及时将款项解付给持票人，延压客户资金的；（六）违反本办法规定拒绝付款的；（七）本行头寸不足，影响银行汇票兑付的；（八）违反银行汇票印、证管理规定。空白银行汇票凭证、银行汇票专用章发生丢失或被盗、密押系统发生泄密等重大事故的；（九）签发空头银行汇票、套取资金以及进行经济犯罪活动的；（十）其他违反本办法相关规定、情节严重的。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第六章   附  则', '第四十二条', '各支行（部）签发银行汇票使用统一的汇票专用章。汇票专用章的规格式样由中国人民银行统一规定。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第六章   附  则', '第四十三条', '各支行（部）签发银行汇票使用的密押系统由本行自行管理。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第六章   附  则', '第四十四条', '银行汇票的格式、联次、颜色、规格及防伪措施，由中国人民银行统一规定。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第六章   附  则', '第四十五条', '各支行（部）办理银行汇票业务，应按中国人民银行有关规定向申请人收取手续费、汇划费、凭证工本费等费用。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第六章   附  则', '第四十六条', '本办法未尽事宜，适用《中华人民共和国票据法》、《票据管理实施办法》和《支付结算办法》的有关规定。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第六章   附  则', '第四十七条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《华东三省一市银行汇票管理办法》', '第六章   附  则', '第四十八条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第一条', '为规范xxx银行(以下简称“本行”)协助查询、冻结和扣划单位、个人在本行营业网点存款的行为，根据《金融机构协助查询、冻结、扣划工作管理规定》（银发〔2002〕1号）、《关于印发银行业金融机构协助人民检察院公安机关国家安全机关查询冻结工作规定的通知》（银监发〔2014〕53号）和其他有关法律、法规和司法解释的规定，结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第二条', '本办法所称“协助查询、冻结、扣划”是指本行依法协助有权机关查询、冻结、扣划单位或个人在本行的涉案存款、汇款以及查询、冻结票据、投资产品等财产（以下简称其他财产）的行为。协助查询是指本行依照有关法律或行政法规的规定以及有权机关查询的要求，将单位或个人存款的金额、币种以及其他存款信息告知有权机关的行为。协助冻结是指本行依照法律的规定以及有权机关冻结的要求，在一定时期内禁止单位或个人提取其存款账户内的全部或部分存款（或其他财产）的行为。协助扣划是指本行依照法律的规定以及有权机关扣划的要求，将单位或个人存款账户内的全部或部分存款（或其他财产）资金划拨到指定账户上的行为。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第三条', '本办法所称有权机关是指依照法律、行政法规的明确规定，有权查询、冻结、扣划单位或个人在本行存款（或其他财产）的司法机关、行政机关、军事机关及行使行政职能的事业单位（详见附件一）。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第四条', '协助查询、冻结和扣划工作应当遵循依法合规、保护存款人和其他客户合法权益的原则。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第五条', '总行运营管理部为协助有权机关执行的管理部门。各支行（部）柜台及事后监督团队为接待部门和协助执行部门，应指定人员负责接待工作，及时处理协助事宜。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第六条', '各支行（部）在接到协助查询、冻结、扣划财产法律文书后，应当严格保密，严禁向被查询、冻结和扣划的单位、个人或者第三方通风报信，帮助隐匿或者转移财产。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第七条', '为维护客户的利益，各支行（部）应依法为存款人的银行账户信息保密。凡查询、冻结、扣划单位和个人存款者必须按法律、行政法规定办理，任何单位不得擅自查询、冻结、解除冻结和扣划单位和个人的存款（或其他财产）。有权机关必须符合如下条件，银行方可予以协助执行： （一）要求协助的机关应具备法律、法规规定的主体资格；（二）要求协助的事项应符合法律、法规规定；（三）到银行机构执行时，应至少由两名执法人员办理，同时出具本人工作证和执行公务证，或根据有关规定出示其中的一项证件；无法现场办理完毕的，可以由提出协助要求的人民检察院、公安机关、国家安全机关指派至少一名办案人员持有效的本人工作证或执行公务证和单位介绍信到经办行取回反馈结果； （四）账号、户名应填写正确，两者均填写时，必须一致； （五）查询、冻结、扣划存款（财产）通知书与解除冻结、扣划存款（财产）通知书均应由有权机关执法人员依法送达。本行各营业网点不接受有权机关执法人员以外的人员代为送达的上述通知书；异地有权机关委托当地有权机关代为送达、邮寄送达的解除冻结存款通知书的，银行应与有权机关直接送达的解除冻结存款通知书同等对待；特殊情形，按照相关法律法规执行； （六）相关法律文书应符合法律规定。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第八条', '各支行（部）协助有权机关办理查询、冻结或者解除冻结、扣划时，应当对办案人员的工作证或执行公务证以及协助查询、冻结/解除冻结、扣划存款（财产）法律文书进行形式审查。各级行应当留存上述法律文书原件及工作证或执行公务证复印件，并注明用途。各支行（部）应妥善保管留存的工作证或执行公务证复印件，不得挪作他用。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第九条', '协助查询存款、汇款可到存款人开户网点办理，也可到总行运营管理部办理；协助冻结和扣划应到原开户网点办理，办理冻结、解冻业务要坚持执行“谁冻结谁解冻”的原则，但有权机关通过网络“点对点”办理冻结和扣划业务可以由上级行办理。 人民检察院、公安机关、国家安全机关需要跨地区办理查询、冻结的，可以根据有关规定与协作地县级以上人民检察院、公安机关、国家安全机关联系和要求协作执行。办案地人民检察院、公安机关、国家安全机关可以通过其信息化应用系统传输加盖电子签章的办案协作函和相关法律文书，或者将办案协作函和相关法律文书及凭证传真至协作地对应有权机关。协作地对应有权机关接收后，经审查确认，在传来的协助查询财产或协助冻结/解除冻结财产法律文书上加盖本地对应有权机关印章，由两名以上办案人员持有效的本人工作证或人民警察证到我行现场办理，我行应当予以配合。对于xx市人民法院办理的查询、冻结/解除冻结、扣划业务，通过网络“点对点”办理。由总行运营管理部指定远程授权团队负责接收查询、冻结/解除冻结、扣划申请，保管相关资料并登记“协助查询、冻结、扣划登记簿”。将有关法律文书以邮件形式发送至查询、结冻/解除冻结、扣划相关账户开户网点。开户网点收到邮件后应立即按邮件通知办理相关业务并将结果反馈给远程授权团队，同时在“协助查询、冻结、扣划登记簿”中进行登记。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第十条', '对有权机关办理查询、冻结和扣划手续完备的，应当认真协助办理。运营主管应加强业务办理的事前、事中和事后复核，运营主管不在场的，可以由支行行长或换人进行复核。办理以上业务，不得拖延推诿。 对于人民检察院、公安机关、国家安全机关协助查询的，应当及时办理。能够现场办理完毕的，应当现场办理并反馈。如无法现场办理完毕，对于查询单位或者个人开户销户信息、存款余额信息的，原则上应当在三个工作日以内反馈；对于查询单位或个人交易日期、交易方式、交易对手账户及身份信息、电子银行信息、网银登陆日志等信息、POS机商户、自动机具相关信息的，原则上应当在十个工作日以内反馈。因技术条件、不可抗力等客观原因，经办行无法在规定时限内反馈的，应当向人民检察院、公安机关、国家安全机关说明原因，并采取有效措施尽快反馈。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第十一条', '协助执行时应注意为客户保密，严禁非执法人员接触银行账户资料，严禁执法人员接触非被执行人账户资料。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第十二条', '各支行（部）应当实行协助查询、冻结和扣划工作的登记制度。 协助执行应填写《有权机关查询、冻结、扣划登记簿》，详细记载要求协助执行机关的名称、执行人员姓名及证件号码、要求协助执行的项目、相关法律文书的名称和文号、被协助执行单位或个人的名称或姓名、账号、协助查询、冻结、扣划的时间和金额（如有金额），协助结果等。并由有权机关执法人员和银行经办人签字确认。 各级行应当妥善保管登记簿，并严格保守有关国家秘密。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第一章  基本规定', '第十三条', '在协助有权机关办理完毕查询存款手续后，有权机关要求予以保密的，应当保守秘密。在协助有权机关办理完毕冻结、扣划存款手续后，根据业务需要可以通知存款单位或个人。在存款单位或者个人查询时，应当告知其账户被冻结的情况，被冻结款项的单位或者个人对冻结有异议的，经办行应当告知其与作出冻结决定的人民检察院、公安机关、国家安全机关联系。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第二章  协助查询', '第十四条', '协助查询时，有权机关应提供以下法律文书： （一）人民检察院、公安机关、国家安全机关、军队（武警）保卫部门、海关走私犯罪侦查机关要求协助查询的，应出具县级（含）以上公安机关、国家安全局、军队（武警）保卫部门及海关走私犯罪侦查机关签发的“协助查询存款、汇款或财产通知书”。 （二）人民法院要求协助查询的，应出具县级（含）以上人民法院签发的“协助查询存款通知书”。 （三）监察机关要求协助查询的，应出具县级以上监察机关领导人批准的“查询存款通知书”；纪检机关需要查询的，应以监察机关名义使用监察文书。 （四）税务机关要求协助查询的，应出具县级以上财政部门负责人签字批准的《财政查询存款通知书》。 （五）审计机关要求协助查询的，应出具县级以上审计机关负责人签发的“协助查询单位账户通知书”或“协助查询个人存款通知书”和“审计通知书”副本。 （六）中国证监会要求查询的，应当出具证监会及其派出机构的查询通知书。 （七）反洗钱行政主管部门要求协助查询的，应出具国务院反洗钱行政主管部门或省一级派出机构的调查通知书。（八）工商行政管理部门、物价部门、国务院稽察特派员、海关要求协助查询的，参照第（一）至（六）项规定执行。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第二章  协助查询', '第十五条', '有权机关对个人存款账户不能提供账号的，应当要求有权机关提供该个人的居民身份证号码或其它足以确定该个人存款账户的情况。有权机关在查询单位存款情况时，只提供被查询单位名称而未提供账号的，经办行应当根据账户管理档案积极协助查询，没有所查询的账户的，应如实告知有权机关。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第二章  协助查询', '第十六条', '各支行（部）在协助查询单位或个人存款时，应当审查是否为有权机关查询，是否规定人员办理并提供相关证件；“协助查询存款或财产通知书”填写的内容是否规范，是否填写被执行单位名称（账号）或个人名称、账号或身份证号码等；是否为县级（含）以上有权机关签发并签章。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第二章  协助查询', '第十七条', '各开户行在协助查询后应告知执行机关，我行已开办储蓄及对公通存通兑业务、网上银行、手机银行及银行卡等业务，有可能发生查询时的余额与冻结时的余额不一致的情况。如执行机关同时出具协助冻结存款通知书，应在办完冻结手续后，在查询回执上填写查询余额。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第二章  协助查询', '第十八条', '有权机关依法向营业网点查询或查阅的资料应限于涉案的存款及财产信息，包括被执行单位或个人开销户、存款余额、交易日期、交易金额、交易方式、交易对手及身份信息，电子银行信息，网银登陆日志等信息，POS机商户、自动机具相关信息等以及与存款有关的会计凭证（票据）、账簿、对账单等资料，营业网点应及时如实提供。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第二章  协助查询', '第十九条', '有权机关对查询的会计资料原件不得带走，但可根据需要抄录、复制或照相，经办行在有关复制材料上加盖业务公章，如同时提交两份以上复印件的需同时加盖骑缝业务公章。有权机关抄录、复制或照相的会计资料情况档案保管人员必须在“协助查询通知书”上作详细记录。人民检察院、公安机关、国家安全机关要求提供电子版查询结果的，经办行应当在采取必要加密措施的基础上提供，必要时可予以标注和说明。 涉案账户较多，需要批量查询的，人民检察院、公安机关、国家安全机关应当同时提供电子版查询清单。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十条', '在协助冻结时，有权机关应提供以下法律文书： （一）公安机关、人民检察院、人民法院、国家安全机关、军队（武警）保卫部门、走私犯罪侦查机关要求协助冻结的，应出具县级（含）以上公安局、人民检察院、人民法院、国家安全局、军队（武警）保卫部门、走私犯罪侦查局签发的“协助冻结存款（或财产）通知书”。 （二）海关要求协助冻结的，应出具县级（含）以上海关签发的“税款缴纳扣划通知书”（对经营加工贸易单位的部分内销产品扣划税款时使用）或海关专用文书。（三）税务机关要求协助冻结的，应出具经县以上税务局（分局）局长批准的“暂停支付存款通知书”。 （四）中国证监会要求冻结/解冻单位和个人存款的，应当出示证监会及其派出机构的经证监会主要负责人批准的冻结/解除冻结通知书。 （五）按规定需提供的其他法律文书，如判决书、裁定书、决定书等。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十一条', '各支行（部）在协助冻结单位或个人存款时，应当审查以下内容： （一）“协助冻结存款或财产通知书”填写的需被冻结存款（或其他财产）的单位或个人开户行名称、户名和账号、大小写金额（如有确定金额）、冻结期限等要素； （二）协助冻结存款（财产）通知书上的义务人应与所依据的法律文书上的义务人相同； （三）协助冻结存款（财产）通知书上的冻结金额应当是确定的。暂时无法确定具体数额的，人民检察院、公安机关、国家安全机关应当在协助冻结财产法律文书上明确注明“只收不付”，我行做“全额止付”处理。如发现缺少应附的法律文书，以及法律文书有关内容与“协助冻结存款（财产）通知书”的内容不符，应说明原因，退回“协助冻结存款（财产）通知书”和所附的法律文书。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十二条', '冻结单位或个人存款、汇款的期限一般不得超过六个月。人民法院因民事诉讼需冻结被执行人的银行存款的期限不得超过一年。如需延长，应重新办理冻结手续。其中涉嫌洗钱被国务院反洗钱行政主管部门负责人批准采取临时冻结措施的，冻结期限最长不得超过四十八小时。对于重大、复杂案件，经设区的市一级以上人民检察院、公安机关、国家安全机关负责人批准，冻结涉案存款、汇款等财产的期限可以为一年。需要延长期限的，应当按照原批准权限和程序，在冻结期限届满前办理续冻手续，每次续冻期限最长不得超过一年。 有权机关应在冻结期满前办理续冻手续，逾期未办理续冻手续的，视为自动解除冻结。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十三条', '正常冻结期内的存款（财产），只有在原作出冻结决定的执行机关作出解冻决定并出具解除冻结存款（财产）通知书的情况下，银行才能对已经冻结的存款（财产）予以解冻。被冻结单位对不予解冻提出异议的，应告知其与作出冻结决定的执行机关联系，在存款冻结期限内银行不得自行解冻。但符合本办法第十四、十五条情形除外。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十四条', '冻结期内，银行在接到原冻结机关的解冻通知后，应予办理解冻手续。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十五条', '海关查明确属来源于走私行为非法取得的存款、汇款，海关可以书面通知银行暂停支付，暂停支付期限不得超过三个月。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十六条', '冻结状态下的账户只能办理存款及款项转入，不得办理对外支付业务。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十七条', '被冻结单位银行账户的存款余额不足冻结数额时，开户行应在冻结期限内冻结该单位可以冻结的存款，直至达到需要冻结的数额。被冻结单位银行账户的存款余额大于冻结数额时，存款人对大于部分可以正常进行对外支付。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十八条', '如不同的有权机关要求对已被冻结的存款再行冻结的，银行可以按规定办理轮候冻结。先前冻结解除的，登记在先的轮候冻结即自动生效。冻结期限届满前办理续冻的，优先于轮侯冻结。两个以上有权机关对同一单位或个人的同一笔存款采取冻结或扣划措施时，应当协助最先送达协助冻结、扣划存款通知书且手续完备的有权机关办理冻结、扣划手续。两个以上有权机关同时要求银行协助对同一单位或个人采取冻结或扣划措施有争议的，应由共同上级确定后，再予以协助。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第二十九条', '被冻结的款项，不属于赃款的，冻结期间应计付利息，扣划时其利息应付给债权人；属于赃款的，冻结期间不计付利息。如冻结有误，解除冻结时应补计冻结期间利息。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第三十条', '军队、武警部队等单位的特种存款账户，原则上不采取冻结诉讼保全措施。但军队、武警部队的其余存款除外。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第三十一条', '人民法院依法可以对银行承兑汇票保证金、信用证开证保证金采取冻结措施，但不得扣划。如果营业网点已对汇票承兑或已对外付款，根据营业网点的申请，人民法院应当解除对银行承兑保证金、信用证开证保证金相应部分的冻结措施。银行承兑汇票保证金、信用证开证保证金已丧失保证金功能时，人民法院可依法采取扣划措施。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第三十二条', '人民检察院、公安机关、国家安全机关对下列财产和账户不得冻结： （一）金融机构存款准备金和备付金； （二）特定非金融机构备付金； （三）封闭贷款专用账户（在封闭贷款未结清期间）； （四）商业汇票保证金； （五）证券投资者保障基金、保险保障基金、存款保险基金、信托业保障基金； （六）党、团费账户和工会经费集中户； （七）社会保险基金； （八）国有企业下岗职工基本生活保障资金； （九）住房公积金和职工集资建房账户资金； （十）人民法院开立的执行账户；（十一）军队、武警部队一类保密单位开设的“特种预算存款”、“特种其他存款”和连队账户的存款； （十二）金融机构质押给中国人民银行的债券、股票、贷款； （十三）证券登记结算机构、银行间市场交易组织机构、银行间市场集中清算机构、银行间市场登记托管结算机构、经国务院批准或者同意设立的黄金交易组织机构和结算机构等依法按照业务规则收取并存放于专门清算交收账户内的特定股票、债券、票据、贵金属等有价凭证、资产和资金，以及按照业务规则要求金融机构等登记托管结算参与人、清算参与人、投资者或者发行人提供的、在交收或者清算结算完成之前的保证金、清算基金、回购质押券、价差担保物、履约担保物等担保物，支付机构客户备付金； （十四）其他法律、行政法规、司法解释、部门规章规定不得冻结的账户和款项。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第三十三条', '人民检察院、公安机关、国家安全机关对金融机构账户、特定非金融机构账户和以证券登记结算机构、银行间市场交易组织机构、银行间市场集中清算机构、银行间市场登记托管结算机构、经国务院批准或者同意设立的黄金交易组织机构和结算机构、支付机构等名义开立的各类专门清算交收账户、保证金账户、清算基金账户、客户备付金账户，不得整体冻结，法律另有规定的除外 。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第三章  协助冻结', '第三十四条', '经查明冻结财产确实与案件无关的，人民检察院、公安机关、国家安全机关应当在三日以内由作出原冻结决定的以上有权机关出具协助解除冻结财产法律文书等到原冻结经办行办理解除冻结手续，并书面通知被冻结财产的所有人；因此对被冻结财产的单位或者个人造成损失的，我行不承担法律责任，但因我行自身操作失误或设备故障造成被冻结财产的单位或者个人损失的除外。 上级人民检察院、公安机关、国家安全机关认为应当解除冻结措施的，应当责令作出冻结决定的下级人民检察院、公安机关、国家安全机关解释冻结。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第四章  协助扣划', '第三十五条', '在协助扣划时，有权机关应提供以下法律文书： （一）人民法院要求协助扣划的，应当出具县级（含）以上人民法院签发的“协助扣划存款通知书”，并出具人民法院发生法律效力的判决书、裁定书、决定书、调解书、支付令副本、仲裁文书、具有强制执行效力的公证文书、行政处罚决定书等法律文书。（二）海关要求协助扣划的，应当向银行提供“税款缴纳扣划通知书”或“罚没款划转通知单”。（三）税务机关要求协助扣划的，应当送达“扣缴税款通知书”并附送纳税人逾期未执行的“催缴税款通知书（留存联复印件）”。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第四章  协助扣划', '第三十六条', '各支行（部）在协助扣划时，应当参照第二十一条审查内容进行审查。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第四章  协助扣划', '第三十七条', '对协助下列特殊事项的扣划，除应要求人民法院出具县级（含）以上人民法院签发的“协助扣划存款通知书”等规定外，还应作如下审查： （一）对人民法院按照《民事诉讼法》第九十七、九十八条裁定先予执行的，审查协助扣划存款通知书与先予执行裁定书的内容是否相符。 （二）对人民法院按破产程序下达的有关扣划破产企业存款的裁定书，审查协助扣划存款通知书与破产分配裁定书内容是否相符。 （三）对人民法院根据生效的刑事判决书扣划单位存款的，银行只审查协助扣划存款通知书与刑事判决书的内容是否相符。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第四章  协助扣划', '第三十八条', '有权机关对工会款项经费集中户资金、社保资金、粮棉油政策性收购资金、企业存放于再就业服务中心的专项资金、封闭贷款资金等存款不得扣划。遇到法院扣划上述存款，经办行应向执法人员提供最高人民法院相关规定、司法解释或批复等，我行人员应拒绝配合扣划，并在回执联上填写不配合扣划的原因和依据。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第四章  协助扣划', '第三十九条', '各支行（部）遇有权机关冻结扣划除信用证保证金和银行承兑保证金外的保证金和质押存单时，应及时主张对该笔保证金和质押存单的优先权，向执法人员明确说明保证金和质押存单的性质和用途，并出示保证金合同和质押合同。保证金和质押存单被冻结的，相关部门应及时提交相关证据向有权机关申请质押权。保证金和质押存单被扣划的，相关部门应及时向有权机关提交书面异议，并提交债务合同、保证金合同和质押合同等证据。若书面异议被驳回的，应及时向有权机关上级申请复议。需要注意若保证金和质押存单对应的信用业务已经结清，保证金和质押存单已经丧失担保功能，各支行（部）应及时配合有权机关冻结扣划。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第四章  协助扣划', '第四十条', '开户行对扣划的存款应直接划入有关机关指定的账户，有权机关要求提取现金的，不予协助。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第五章  考核与奖惩', '第四十一条', '本行在协助人民检察院、公安机关、国家安全机关查询、冻结工作中有下列行为之一的，并被银行业监督管理机构责令整改的，总行按有关规定对直接负责的主管人员和其他直接责任人员给予处罚；必要时，予以通报批评；构成犯罪的，移交司法部门依法追究刑事责任：（一）向被查询、冻结单位、个人或者第三方通风报信，伪造、隐匿、毁灭相关证据材料，帮助隐匿或者转移财产；（二）擅自转移或解除已冻结的存款；（三）故意推诿、拖延，造成应被冻结的财产被转移的；（四）其他无正当理由拒绝协助配合、造成严重后果的。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第五章  考核与奖惩', '第四十二条', '我行将协助查询、冻结和扣划工作纳入各支行（部）考核，对工作当中受到有权机关表扬并有突出贡献的经办行及其工作人员将给予通报表扬和奖励加分。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第六章  附  则', '第四十三条', '本办法当中特指人民检察院、人民法院、公安机关、国家安全机关查询、冻结、扣划的条款，仅适用于以上有权机关。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第六章  附  则', '第四十四条', '本办法所称的“以上”、“以内”包括本数。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第六章  附  则', '第四十五条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《协助查询、冻结、扣划管理办法》', '第六章  附  则', '第四十六条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第一章  总 则', '第一条', '为规范xxx银行（以下简称“本行”）出具单位存款证明业务行为，确保存款证明书的真实、合法、有效，防范金融风险，依据相关法律法规，根据《中华人民共和国商业银行法》结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第一章  总 则', '第二条', '本办法明确了本行单位存款证明业务的受理、审查、审批和开具等内容和要求。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第一章  总 则', '第三条', '本办法适用于本行单位存款证明业务的管理。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第一章  总 则', '第四条', '本办法所称存款证明书是指证明申请人委托本行出具的证明申请人账户在某一时点或时期的资金情况的书面证明。    时点存款证明是指证明存款人某一日在本行存款情况的书面文书。时段存款证明是指证明存款人某一时段在本行存款情况的书面文书。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第一章  总 则', '第五条', '证明分类存款证明分存款证明书和银行询证函。其中银行询证函是指向单位客户委托的会计师事务所证明存款人某一时点（指截止日）在本行存款账户的余额或验资账户存款缴存情况的书面文书。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第二章  职责与权限', '第六条', '运营管理部负责本行单位存款证明业务管理、检查和辅导。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第二章  职责与权限', '第七条', '基层网点负责单位存款证明业务的受理。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第三章  内容与要求', '第八条', '出具单位存款证明必须以真实的资金情况为依据，各网点不得弄虚作假，提供虚假证明。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第三章  内容与要求', '第九条', '申请办理单位存款证明的客户应当是依法成立的企事业单位法人，并符合下列条件：（一）在基层网点开立人民币结算账户或单位定期、通知存款账户；（二）资信状况良好，在与基层网点合作中无不良记录。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第四章  操作流程', '第十条', '申请业务受理（一）客户申请办理单位存款证明业务，需提供以下基本资料：1、申请书；2、申请人营业执照复印件；3、经法定代表人授权经办人员的有效身份证件及复印件；4、直接由会计师事务所寄来的银行询证函则不需要上述资料。（二）基层网点柜员对客户的申请进行初审，初审客户提交的资料是否齐全，并决定是否受理。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第四章  操作流程', '第十一条', '审核（一）业务受理后，基层网点柜员即时打印对账单，连同客户提供的基本资料交运营主管进行审查；（二）业务审查，审查的主要内容包括：1、申请书是否注明存款金额、证明书的用途；2、申请人提供的资料是否齐全，符合要求；3、申请的存款证明金额是否与实际存款金额相符。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第四章  操作流程', '第十二条', '审批审查通过后，按权限规定报单位负责人审批。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第四章  操作流程', '第十三条', '开具证明审批通过后，由基层网点对外出具单位存款证明书（一式两份），其中证明书正本交客户，副本由经办网点留存。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第四章  操作流程', '第十四条', '解除账户控制（一）《单位存款证明书》所证明的存款在时段有效期内办理账户控制的，时段有效期结束后自动解除账户控制；如不指定解除账户控制日期，则自存款到期后，自动解除账户控制。（二）若客户须提前解除账户控制的，需携带有效证件及交回已开具的存款证明书原件，到开户网点办理解除账户控制手续。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第四章  操作流程', '第十五条', '资料存档存档资料包括申请人营业执照副本、授权经办人员身份证复印件，存款证明申请书、对账单、证明书副本。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第五章  附 则', '第十六条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《单位存款证明业务管理办法》', '第五章  附 则', '第十七条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第一章  总  则', '第一条', '为规范xxx银行（以下简称“本行”）反洗钱工作有效开展，促使本行上下形成共识，高度重视反洗钱工作，切实履行反洗职责，根据《中华人民共和国反洗钱法》、《金融机构反洗钱规定》、《金融机构大额交易和可疑交易报告管理办法》等法律法规及监管部门的其他要求，结合本行员工违规违章行为管理规定，特制定本考核办法。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第一章  总  则', '第二条', '本考核办法采取百分制考核，实行倒扣分和加分的考核方法。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第三条', '本行机关各部室及各分支机构未按规定成立反洗钱领导小组，未明确人员担任反洗钱录入、审核、审批岗位，未正常开展反洗钱工作。扣6分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第四条', '本行机关各部室及各分支机构未组织员工进行反洗钱学习培训，学习记录不完整。扣2分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第五条', '本行反洗钱主管部门及各分支机构未按照规定面向客户及社会各界进行反洗钱法律法规宣传。扣2分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第六条', '柜员为单位或自然人开立银行账户时未按规定进行审查、识别客户身份，未完整采集客户信息。扣6分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第七条', '柜员为客户开立匿名账户、假名账户和非实名账户，为身份不明的客户提供服务或者与其进行交易。扣10分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第八条', '柜员未及时更新客户信息，为证件过期的客户办理业务。扣10。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第九条', '柜员未完整保存客户身份资料、交易记录，在业务关系或交易结束后，未按规定要求和期限保存客户身份资料和交易记录。扣6分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十条', '办理电子银行业务未履行必要的客户身份核对工作和收集齐全有效的证件资料。扣6分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十一条', '未按规定进行客户风险等级划分，定期进行重新审核。扣6分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十二条', '未按照规定结合客户身份、资金来源及去向、交易背景、交易目的、交易性质来详细分析系统抓取的可疑交易，案例分析内容程式化或不同客户的交易案例使用同一分析用语，甄别结果质量差、合格率低。扣5-15分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十三条', '未对系统未抓取但对照办法确属可疑范畴的交易进行人工增加系统上报。扣5-15分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十四条', '未遵守反洗钱工作保密规定，违规泄漏依法履行反洗钱义务时获得的客户身份资料和交易信息，泄漏配合人民银行检查时获得的调查情况及协助司法机关、行政机关打击洗钱活动情况等。扣50分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十五条', '未配合反洗钱各项检查工作。扣6分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十六条', '未按时完成反洗钱工作平台各项任务。扣6分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十七条', '未按时完成总行布置的其他有关反洗钱的各项工作。扣6分'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第二章  考核条款', '第十八条', '反洗钱工作中，经基层网点甄别认定为重点可疑交易，总行确认向人民银行报送重点可疑交易报告的，在当期及年度财务会计规范化验收中加10分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第三章  考核运用', '第十九条', '依照本考核办法所扣分值运用于网点负责人、运营主管岗位职能工资考核，每个考核期折算至相应考核项目。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第三章  考核运用', '第二十条', '依照本考核办法全年汇总，得分1-10名，在年度财务会计规范化验收中，加1-10分。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第四章  附  则', '第二十一条', '记分及经济处罚程序参照本行员工违规违章行为管理规定执行。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第四章  附  则', '第二十二条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱专项考核办法》', '第四章  附  则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第一条', '为预防洗钱活动，维护金融秩序，防止利用xxx银行（以下简称“本行”）进行洗钱活动，规范本行反洗钱信息报送，提高反洗钱信息报送质量，提升本行反洗钱非现场监管水平，实现为内部业务部门、外部监管部门提供有价值数据的目标，根据《金融机构反洗钱监督管理办法（试行）》（银发〔2014〕344 号）及相关规章制度，特制订本办法。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第二条', '本办法适用于各支行、部，机关各部室及中心。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第三条', '本办法所称反洗钱信息包括：年度反洗钱工作计划、反洗钱季度工作报告、反洗钱工作检查报告、反洗钱工作总结、高风险客户电子清单、反洗钱季度报表、反洗钱年度报告和年度报表等。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第四条', '反洗钱工作计划（年度）㈠报送主体：本行；㈡报送对象：省联社运营管理部、当地人民银行；㈢报送形式：行发文；㈣报送周期：按年报送，每年2月28日前报送；㈤报送内容：组织架构、内控建设、反洗钱各项制度落实、宣传培训、检查考核等方面的反洗钱工作计划。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第五条', '反洗钱重大事项和突发事件应实时报送。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第六条', '高风险客户电子清单㈠报送主体：本行；㈡报送对象：省联社运营管理部、当地人民银行；㈢报送形式：通过省联社反洗钱系统报送；㈣报送周期：省联社反洗钱系统自动报送时间；㈤报送内容：本季度高风险客户电子清单。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第七条', '反洗钱工作检查、审计报告㈠报送主体：本行反洗钱主管部门、审计部门；㈡报送对象：省联社运营管理部、当地人民银行；㈢报送形式：行发文；㈣报送周期：按实际检查与审计情况报送；㈤报送内容：反洗钱工作检查、内部审计以及接受当地监管机构检查情况。包括工作方案、存在问题、整改方案、整改结果、总结经验、提出意见或建议等。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第八条', '反洗钱工作总结㈠报送主体：本行反洗钱领导小组；㈡报送对象：省联社运营管理部、当地人民银行；㈢报送方式：行发文；㈣报送周期：每年12月31日前报送；㈤报送内容：全年内控制度建设情况、客户身份识别情况、大额和可疑交易报送情况、反恐怖融资交易情况、宣传和培训情况、监管部门对本机构开展现场、非现场检查情况，各业务部门配合开展反洗钱工作情况、反洗钱工作创新和亮点、对反洗钱工作的意见和建议等。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第九条', '反洗钱季度报表㈠报送主体：本行；㈡报送对象：省联社运营管理部、当地人民银行；㈢报送形式：通过yy市金融邮件系统、信息科技工作平台分别向人民银行、省联社报送；㈣报送周期：按季报送，每季后10个工作日内报送；㈤报送内容：客户身份识别情况、大额和可疑交易报告情况、对高风险业务的管理情况、违反反洗钱法律规章受到行政处罚情况、运用反洗钱机制防范洗钱风险成果情况等。客户身份识别情况    此表填列当季新增客户身份识别情况、洗钱风险等级划分情况、当季存量客户重新识别、持续识别情况、洗钱风险等级调整情况等。2.大额和可疑交易报告情况此表填列当季报送大额交易报告情况、通过反洗钱系统及人工发现的异常交易数、排除洗钱嫌疑的异常交易数、实际上报的可疑交易数、人工甄别不能排除洗钱嫌疑向人民银行或当地公安机关报送的重点可疑交易报告数。3.对高风险业务的管理情况此表填列提出的高风险产品或高风险业务，并采取相应的控制措施情况。4.违反反洗钱法律规章受到行政处罚情况接受人民银行反洗钱现场检查并被行政处罚时，此表逐条填列检查通知书号、检查起止时间、主查单位、被查单位、检查发现的主要问题、对机构处罚金额、处罚人数、对个人处罚金额和整改情况。5.运用反洗钱机制防范洗钱风险成果情况运用反洗钱机制成功防范了洗钱风险，此表逐条填列成功防范的风险事件的名称、事件发现机构、事发起止时间、基本情况、取得成效和是否向当地人行报备。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第十条', '反洗钱年度报告和年度报表㈠报送主体：本行；㈡报送对象：省联社运营管理部、当地人民银行；㈢报送形式：通过yy市金融邮件系统、信息科技工作平台分别向人民银行、省联社报送；㈣报送周期：按年报送，次年首月20个工作日内报送；㈤报送内容：反洗钱年度报告包括反洗钱工作整体情况及机构概况、反洗钱工作机制建立情况、反洗钱法定义务履行情况、反洗钱工作配合与成效情况、其他反洗钱工作情况或问题以及工作改进建议。反洗钱年度报表包括报告机构基本情况表、报告机构现行反洗钱内控制度列表、报告机构反洗钱岗位人员情况表、报告机构客户身份识别及风险等级分类情况表、报告机构大额和可疑交易报告情况表、报告机构反洗钱宣传情况表、报告机构反洗钱培训情况表、报告机构反洗钱内部检查（审计）情况表、报告机构配合反洗钱行政调查情况表、报告机构接受人民银行反洗钱现场检查情况表、报告机构洗钱风险防控成果表和境外机构反洗钱风险工作统计表。1.反洗钱工作整体情况及机构概况本机构（含本级机构及所辖分支机构，下同）反洗钱工作总体情况。2.反洗钱工作机制建立情况（1）内控制度建立和修订情况。（应包括但不限于客户身份识别措施、客户身份资料和交易记录保存措施、可疑交易标准和分析报告程序、反洗钱和反恐怖融资内部审计培训和宣传措施、配合反洗钱和反恐怖融资调查的内部程序、反洗钱和反恐怖融资工作保密措施、涉及恐怖活动资产冻结操作规程、按照《金融机构洗钱和恐怖融资风险评估及客户分类管理指引》修订的内控制度等。）（2）机制设置情况。报告本机构反洗钱组织架构设置情况，指定内设机构负责反洗钱合规管理工作情况，分支机构和业务条线反洗钱资源配置情况，反洗钱工作体系的运作情况，重点反映反洗钱领导小组履行职责情况，可疑交易分析甄别的模式，对新型业务的洗钱风险研判机制（是否建立包括洗钱风险在内的风险评估组织机构，形成洗钱风险评估报告）等。（3）技术保障情况。报告本机构反洗钱业务应用系统建设和反洗钱工作技术保障情况。建立反洗钱监控报送系统和客户洗钱风险等级划分系统模块情况，反洗钱系统从核心系统中获取数据信息情况，相关业务系统为可疑交易甄别分析提供技术支持情况，客户洗钱风险等级结果的运用情况。（4）人员配备与资质情况。报告本机构反洗钱工作岗位的人员配备，以及反洗钱岗位人员业务能力或业务资质情况。报告反洗钱工作人员数量、专岗人员学历，反洗钱主管人员是否可以及时获取各类与反洗钱工作相关的信息系统的查询权限。3.反洗钱法定义务履行情况（1）客户身份识别。报告本机构采取哪些有效的客户身份识别措施，以及如何开展客户分类管理等情况。按照法规及机构内控要求，开展的客户身份识别工作情况（包括初次、持续和重新识别）。执行的客户洗钱风险等级标准及其管理流程，当年开展风险等级管理情况。（2）对高风险客户的特别措施。报告本机构针对高风险客户或者高风险账户持有人采取了哪些加强型识别或控制措施，如何开展对其金融交易活动的监测分析等情况。  （3）客户资料和交易记录保存。报告本机构客户资料和交易记录保存情况。内部处理可疑交易报告记录的保存情况。（4）大额和可疑交易报告。报告本机构可疑交易标准制定、交易监测和分析甄别机制，本年度大额和可疑交易报告情况。异常交易的及时处理情况，对可疑交易的人工甄别分析意见情况。（5）对高风险业务的针对性措施。报告本机构针对所具有的相对洗钱高风险业务或高风险产品采取了何种针对性措施。若有自主开展洗钱风险自评估工作，请简要描述工作开展情况及评估结论等。（6）开展反洗钱宣传情况。报告本年度自主及参与开展反洗钱宣传情况。包括开展宣传活动的内容、形式和效果。（7）组织反洗钱培训情况。报告本年度自主开展及参加的反洗钱培训情况。包括培训对象、内容、形式和效果。（8）自主管理、检查与审计。报告本机构加强反洗钱工作内部监督、落实岗位责任、开展对所辖机构反洗钱专项工作管理的情况。本年度对所辖机构和相关部门开展反洗钱内部检查与审计情况、发现问题类型、整改落实情况。对相关人员履行反洗钱义务的考核情况。4.反洗钱工作配合与成效情况（1）协助行政调查情况。本年度协助人民银行或其分支机构开展反洗钱行政调查的情况。（2）接受现场检查及被处罚情况。本年度接受人民银行或其分支机构反洗钱现场检查的情况及对发现问题的整改情况。（3）工作报告及接受日常监管情况。本机构向人民银行或其分支机构报告反洗钱工作信息的情况，本年度接受人民银行或其分支机构约见谈话、质询、监管走访等日常监管情况，以及对发现问题的整改情况。（4）承担其他重点任务情况。承担人民银行或其分支机构反洗钱有关工作任务或调研任务情况，配合其他工作的情况。（5）洗钱风险防控成果。本机构取得的反洗钱案件、风险防控的积极成果。报告基于反洗钱工作机制，成功防范洗钱等风险案件。（6）有无重大违规事项。包括但不限于反洗钱信息泄密、未严格按照规定履行反洗钱职责导致洗钱案件发生，或内部人员涉嫌洗钱案件等情况。5.其他反洗钱工作情况或问题以及工作改进建议重点报告反映本机构的反洗钱特色工作与积极效果，反洗钱工作受到各级人民银行或外部单位表彰情况，工作中发现的突出矛盾和问题，以及反洗钱工作改进建议等情况。反洗钱年度报表必须为Excel格式，数据模板中的单元格格式都是文本格式，不得改动电子文档的项目位置及数据格式。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第十一条', '本办法由xxx银行负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱信息报送管理办法》', '', '第十二条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第一条', '为规范xxx银行（以下简称“本行”）反洗钱报告工作，及时了解反洗钱工作情况，加强对各营业网点的日常指导和监督，促进反洗钱工作的深入开展，依据《中华人民共和国反洗钱法》和《金融机构反洗钱规定》等法律规章，结合本行实际，制定本制度。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第二条', '各支行应当依照《中民人民共和国反洗钱法》等法律法规及本制度的规定认真履行反洗钱报告义务，审慎识别和报告可疑支付交易，全面、及时地向本行运营管理部报告反洗钱工作情况，确保内容的真实性、完整性和准确性。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第三条', '各支行应分别按季、按年度或按规定随时向本行报送反洗钱工作情况。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第四条', '本行运营管理部收到支行报送的反洗钱工作报告后，应认真进行审核、整理、分析和汇总，发现情况不真实、不准确和不完整的，应责令其重新报送，并在辖内通报。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第五条', '本行对各支行报告的有关信息保密，超过法律规定的，不得向任何其他部门和个人提供。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第六条', '各支行应根据《xxx银行反洗钱管理办法》的要求，上报各项报表及情况说明。反洗钱非现场监管报表情况说明，内容包括：1.各支行执行反洗钱制度办法的总体情况；2.各支行执行反洗钱规定中存在的风险及其他值得关注的问题；3.结合自身情况，改进非现场监管的建议；4.其他需要说明的情况。各支行应于每季度结束后的3个工作日内上报。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第七条', '支行反洗钱工作报告内容包括：（一）反洗钱年度工作计划：1.年度内开展反洗钱工作的总体思路；2.在组织体系、制度建设、人员配备和监督问责等方面的工作安排；3.在履行反洗钱义务方面的措施；4.反洗钱宣传培训年度计划安排；5.工作思路创新。（二）反洗钱工作总结内容包括：1.开展反洗钱工作基本情况。包括内控制度执行情况、客户尽职调查情况、客户交易记录保存情况、大额和可疑交易报告情况和反洗钱宣传培训等情况；2.开展反洗钱工作存在的主要问题；3.宣传培训年度计划执行情况。包括培训次数、人数、内容及方式等；4.开展反洗钱工作存在问题的原因分析；5.对存在问题的整改情况及采取的整改措施；6.对开展反洗钱工作的意见和建议。（三）涉嫌洗钱行为等重大事项报告：1.发现涉嫌洗钱行为的经过和实际情况；2.配合有关部门对涉嫌洗钱行为的查询、冻结等情况。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第八条', '对上述报送内容和要求，严格按人民银行的规定执行。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第九条', '本制度由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱内部报告制度》', '', '第十条', '本制度自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第一章  总则', '第一条', '为规范xxx银行（以下简称“本行”）反洗钱协查流程，防止反洗钱信息泄露，保证反洗钱协查工作有效开展，根据《中华人民共和国反洗钱法》、《金融机构反洗钱规定》、《中国人民银行反洗钱调查实施细则（试行）》、《金融机构反洗钱监督管理办法（试行）》等法律、法规以及相关反洗钱管理办法，特制定本办法。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第一章  总则', '第二条', '反洗钱协查是指根据人民银行和其他有权机关的要求，对有关洗钱犯罪活动等所涉及的客户或交易情况配合进行调查和反馈, 并实施相关资金扣划和冻结工作的有关活动。反洗钱协查工作主要涉及以下四个方面：一是对人民银行要求协查的客户身份信息进行核实和了解；二是对有关客户办理的业务进行核查和分析；三是根据具体的反洗钱调查内容，向人民银行提供其他有关资料；四是协助其他有权机关开展线索查询、相关资金扣划和冻结工作。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第一章  总则', '第三条', '反洗钱协查不仅是本行履行反洗钱法律义务的需要，也是加强反洗钱管理，及时发现风险隐患和洗钱线索，配合人民银行预防和监控洗钱活动，协助其他有权机关打击洗钱犯罪的需要。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第一章  总则', '第四条', '本行开展反洗钱协查应遵循合法、效率、保密的原则，做到统一管理、分工负责、专人保密、保证效率，并指定专人负责反洗钱协查工作。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第一章  总则', '第五条', '本行在配合人民银行及其他有权机关调查时应履行以下义务：（一）配合调查义务。即在人民银行及其他有权机关调查过程中，在合理的范围内提供必要的人员、技术和设备支持。（二）如实提供信息义务。对人民银行调查的可疑交易活动，及其他有权机关调查的涉案线索，如实提供相关的文件和资料。（三）不得拒绝和阻碍义务。不得以明示（拒绝）或默示（阻碍）的方式不履行反洗钱协查义务。（四）保密义务。对依法履行反洗钱义务获得的客户身份资料和交易信息，应当予以保密；非依法律规定，不得向任何单位和个人提供。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第一章  总则', '第六条', '本办法适用于各支行、部，机关各部室及团队。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第二章  职责分工', '第七条', '本行反洗钱领导小组负责全行反洗钱协查事项的协调、组织和管理，主要职责包括：（一）根据工作需要，负责制订、完善反洗钱协查的内控制度；（二）负责全行反洗钱协查事项的协调、组织和管理；（三）负责组织对反洗钱协查制度执行情况的检查监督；（四）负责接洽、处理和反馈中国人民银行反洗钱局、中国反洗钱监测分析中心等监管部门提出的反洗钱调查事项；（五）负责根据反洗钱协查的信息，向反洗钱工作办公室发出风险提示；（六）负责保管全行配合反洗钱调查的档案资料；（七）负责反洗钱协查事项做好保密工作。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第二章  职责分工', '第八条', '本行反洗钱工作办公室负责组织、协调、配合当地人民银行、司法机关发出的反洗钱协查工作，具体职责包括：（一）执行省联社反洗钱协查的内控制度；（二）负责接洽、处理和反馈当地人民银行、省联社提出的反洗钱调查事项；（三）负责根据反洗钱协查的信息，向本机构相关部门、分支机构发出风险提示；（四）负责保管反洗钱调查的档案资料；（五）负责指导相关部门的反洗钱协查工作；（六）负责向本行反洗钱领导小组汇报反洗钱协查工作；（七）负责对本机构反洗钱协查事项做好保密工作。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第二章  职责分工', '第九条', '本行相关职能部门负责反洗钱协查所需业务数据的提取和反馈，主要职责包括：（一）执行反洗钱协查的内控制度；（二）根据反洗钱工作办公室的要求，及时办理查询事项和反馈信息；（三）负责如实提供反洗钱调查所需的相关资料和信息；（四）负责为反洗钱调查工作提供技术支持；（五）及时处理人民银行相关部门发出的反洗钱协查信息的风险提示；（六）负责通过反洗钱协查事项发现本业务领域的隐患和不足，及时采取有效措施防范洗钱风险；（七）负责反洗钱协查事项的保密工作。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第二章  职责分工', '第十条', '本行根据司法协助管理规定，协助各类有权机关开展查询、冻结和扣划工作，涉及洗钱的，按本办法规定履行协查和保密义务。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第二章  职责分工', '第十一条', '本行及各支行（部）均应配置反洗钱人员负责反洗钱协查工作，确保反洗钱调查持续、有效地开展。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第三章  协查程序', '第十二条', '配合书面调查的流程（一）反洗钱人员接到监管部门的反洗钱调查通知书时，应及时与监管部门联络确认收到调查通知书，并就需提供数据信息予以明确后登记具体协查信息。登记好信息后，确认被调查客户是否已在本机构开户。（二）如被调查客户未在本机构开户，则协查流程结束。反洗钱工作办公室与监管部门联络确认协查结果，留存协查相关文件备案。（三）如被调查的客户已在本机构开立账户，反洗钱人员可依照此流程进行协查工作：流程一：反洗钱工作人员经主管领导审批后提交相关职能部门。相关职能部门反洗钱人员在接到审批表后，应在协查需求时限内完成数据提取工作并将调查所需数据反馈给反洗钱工作办公室人员。反洗钱工作办公室人员接到数据后应审核数据是否完整，确认信息完整无误后，应在要求的时间内反馈给监管部门。反洗钱人员应将被监管部门调查的客户信息告知所属机构，相关机构应对该客户开展重新识别、风险等级调整及交易监测分析，涉及可疑交易的，按程序上报可疑交易报告。流程二：若无法获取数据需省联社协助的，经主管领导审批后，提交申请至省联社财务会计部。后续流程依照“流程一”进行。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第三章  协查程序', '第十三条', '配合现场调查的流程（一）准备工作：本行在收到调查通知后，需做好调查前准备工作。包括准备好现场调查需要的场所、设备，保证调查需要的档案资料有效提供，通知调查涉及机构、部门人员届时到位等。（二）现场接洽：本行在接到监管部门反洗钱现场调查任务时，必须按照《中华人民共和国反洗钱法》规定，对调查的合法性进行审查。要求调查人员不得少于二人，并出示合法证件（执法证）和国务院反洗钱行政主管部门或者其省一级派出机构出具的调查通知书，条件符合后依法协助开展反洗钱调查工作。（三）配合开展现场调查：1.本行反洗钱工作办公室负责接待、组织、接洽反洗钱调查事项，其他相关职能部门应在反洗钱工作办公室的统一安排下协助完成反洗钱调查工作。2.本行反洗钱人员必须全力配合，负责与各支行（部）做好材料的调阅联系工作，并认真做好资料的交接登记工作。3.调查人员要求查阅、复制账户信息、交易记录以及其他与被调查对象和可疑交易活动有关的纸质、电子或影像等形式的资料时，反洗钱工作办公室应及时向各支行（部）发出调阅资料通知，各支行（部）应在需求时限内提交资料。（四）协助封存有关资料：遇到调查人员需要封存文件、资料时，反洗钱人员应该核对《反洗钱调查封存清单》，核对无误后，由调查人员和反洗钱人员签名或者盖章后，一份留存备案，一份交监管部门。对于已封存的文件、资料，应加强保管措施，避免受到自然或人为的毁损、破坏，严禁任何人员擅自启用或改动，待接到《解除封存通知书》后，方可正式解除封存。（五）调查工作结束后，反洗钱人员应及时通知各支行（部）协查人员清点调阅文件资料，检查是否有未归还或缺失的情况，并积极做好联系工作。（六）反洗钱现场检查资料交接时，按照书面调查工作相关流程执行。（七）本行在接受中国人民银行的反洗钱现场调查后，应及时通过书面形式就反洗钱检查报告及整改结果向省联社反洗钱办公室报备。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第三章  协查程序', '第十四条', '配合实施临时冻结措施的流程（一）当客户要求将调查所涉及的账户资金转往境外时，各支行（部）反洗钱人员应立即向本行反洗钱工作办公室报告，同时向当地人民银行报告。（二）在接到人民银行的《临时冻结通知书》后，各支行（部）立即将《临时冻结通知书》上报反洗钱工作办公室，各支行（部）配合办理临时冻结措施，同时确保临时冻结手续的时效性。临时冻结期限为48小时，自接到《临时冻结通知书》之时计算。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第三章  协查程序', '第十五条', '侦查机关认为需要继续冻结的，依照《刑事诉讼法》的规定采取冻结措施。应按照协助公、检、法机关执行查询、冻结、扣划的程序办理继续冻结手续。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第三章  协查程序', '第十六条', '有下列情况的之一的，应当立即解除临时冻结：（一）接到中国人民银行的《解除临时冻结通知书》的；（二）在按照中国人民银行的要求采取冻结措施后48小时内未接到侦查机关继续冻结通知的。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第四章  档案管理', '第十七条', '反洗钱协查档案内容：（一）《反洗调查通知书》、《临时冻结通知书》、调查人证件复印件、内部使用的反洗钱工作表格等原件或复印件。（二）反洗钱协查过程中涉及的账户信息和交易记录的清单。（三）封存清单的原件或复印件和其他在反洗钱协查过程中形成的或有关的档案资料。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第四章  档案管理', '第十八条', '反洗钱协查档案保管（一）档案整理要求：1.反洗钱协查档案纸质部分应按年度单独装订，应与其他反洗钱档案分开装订；2.反洗钱协查档案电子文档部分应按年度刻录成光盘，专夹保管；3.所有反洗钱协查档案均应按年制作明晰的目录，以利于规范管理和便于调阅；4.各支行（部）应尽量安排专人保管、专门的场所存放反洗钱协查档案，将其存放在专用档案柜中。（二）保管期限要求：1.自配合反洗钱调查事项办理结束下一年度起，至少保存5年；2.如反洗钱协查档案涉及新的需要反洗钱协查的可疑交易活动或洗钱案件，则应将该档案保管期限顺延，与新的反洗钱协查档案保管期限一致。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第五章  保  密', '第十九条', '本行开展反洗钱协查工作时，必须遵守《xxx银行反洗钱工作保密办法》的相关要求，严格控制相关信息的知晓范围，妥善获取、复制、保管相关信息档案资料。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第五章  保  密', '第二十条', '各支行（部）人员应对协助配合反洗钱监管部门和有权机关的调查、侦查可疑交易活动等有关工作情况予以严格保密，不得违反规定向客户和其他无关人员提供相关情况信息。收集的调查所需要的资料只能移交给涉及本调查的调查人员，不得移交给调查人员以外的其他人员。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第六章  附  则', '第二十一条', '对于不配合反洗钱调查工作或在反洗钱协查过程中发生违规行为的员工，应根据本行员工违规违章行为管理规定进行严肃处罚，情节严重的将追究有关责任人的法律责任。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第六章  附  则', '第二十二条', '本办法由xxx银行负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱协查工作管理办法》', '第六章  附  则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第一章  总 则', '第一条', '反洗钱培训与宣传是提高员工反洗钱能力、树立xxx银行（以下简称“本行”）良好社会形象的重要途径。本行根据《中华人民共和国反洗钱法》、《金融机构反洗钱规定》结合业务特点和地域实际，持续开展各类反洗钱培训和宣传活动，并积极配合有关监管机构落实相关工作。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第一章  总 则', '第二条', '反洗钱培训与宣传情况应纳入各网点、各条线（部门）反洗钱检查内容及反洗钱工作分类管理评价。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第一章  总 则', '第三条', '本办法适用于各支行、部，机关各部室及团队。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第二章  反洗钱培训', '第四条', '本行应当按照反洗钱预防、监控制度的要求，建立反洗钱培训长效机制，确保各类金融从业人员及时了解反洗钱监管政策、反洗钱内控要求、反洗钱新方法、反洗钱新技术、洗钱风险变动情况等方面的反洗钱工作信息。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第二章  反洗钱培训', '第五条', '本行每年应制定年度反洗钱培训计划，并认真组织开展对员工的反洗钱培训，确保员工有效履行反洗钱义务。（一）反洗钱工作办公室负责反洗钱培训计划的制订和组织实施。（二）各业务条线（部门）负责本条线（部门）反洗钱培训，将反洗钱培训纳入本条线业务培训，每年至少组织一次对本条线员工的反洗钱知识培训。（三）对新聘用的从业人员进行必要的反洗钱培训，未经培训并考试合格的员工不得上岗。（四）对从事洗钱风险较高岗位的从业人员，适当提高反洗钱培训的强度和频率。（五）各反洗钱岗位人员更换时应进行反洗钱培训，做好交接工作。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第二章  反洗钱培训', '第六条', '培训内容包括但不限于：反洗钱政策和基本状况、反洗钱法规规章；本单位反洗钱操作流程；反洗钱的基础知识；反洗钱系统操作；客户身份识别、客户风险等级划分、客户尽职调查的内容和要求；可疑交易与恐怖融资的监测、分析与报告；案例分析、风险提示等。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第二章  反洗钱培训', '第七条', '培训方式包括但不限于：集中培训与现场讨论；聘请专业人员集中授课；通过电子化培训平台设置培训课程；布置自学内容并组织考试。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第二章  反洗钱培训', '第八条', '应注意反洗钱培训时效性、有效性和针对性。对反洗钱法律法规、监管要求、反洗钱制度规定、风险提示等要及时培训到相关反洗钱人员，对反洗钱检查中存在的问题进行有针对性的培训。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第二章  反洗钱培训', '第九条', '应采取措施强化培训效果，确保员工掌握有关反洗钱操作程序、可疑资金的甄别和分析等知识，熟悉有关反洗钱工作的规定、反洗钱操作规程，以及发现、处理可疑交易的措施，使员工了解并掌握反洗钱义务及其所在岗位的反洗钱工作要求。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第二章  反洗钱培训', '第十条', '应建立反洗钱培训档案，真实记录反洗钱培训时间、内容、签到、测试等情况，不定期检查反洗钱培训情况，确保反洗钱工作人员接受了反洗钱培训，并妥善保管好反洗钱培训档案。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第三章  反洗钱宣传', '第十一条', '应结合反洗钱形势和当地实际情况，采取在厅堂宣传、广场集中宣传、社区深入宣传等方式，每年至少组织一次反洗钱宣传活动，介绍有关洗钱的特征、危害以及做好反洗钱工作的重要性，提高社会反洗钱的影响力。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第三章  反洗钱宣传', '第十二条', '积极参与和配合中国人民银行和省联社组织的反洗钱宣传工作，妥善保存反洗钱宣传资料。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第四章  附 则', '第十三条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱培训与宣传管理办法》', '第四章  附 则', '第十四条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第一章  总 则', '第一条', '为促进和规范xxx银行（以下简称“本行”）的反洗钱工作，提高反洗钱工作的针对性和有效性，维护国家经济秩序和金融安全，根据《中华人民共和国反洗钱法》、《金融机构反洗钱规定》、《金融机构洗钱和恐怖融资风险评估及客户分类管理指引》等法律法规，以及相关制度规定，特制定本指引。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第一章  总 则', '第二条', '本指引所称反洗钱客户风险等级划分，是指本行各级机构在反洗钱工作中，根据一定的风险等级划分标准，对客户在洗钱方面的风险等级进行划分的活动。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第一章  总 则', '第三条', '本行及各支行（部）对客户进行风险等级划分的目的是根据客户风险等级的不同而采取相应的客户身份识别和风险监控措施，切实防范洗钱风险。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第一章  总 则', '第四条', '本行及各支行（部）在开展反洗钱客户风险等级划分工作中应遵循以下原则：（一）全面性原则。在反洗钱客户风险等级划分中应综合考虑可能涉及洗钱和恐怖融资等各类风险因素，包括但不限于客户身份、地域、行业、业务、交易等风险因素。采取合理方式对所有客户进行风险等级划分。（二）审慎性原则。遵循“了解你的客户”的原则，提高对客户身份的识别能力，审慎进行客户反洗钱风险等级划分。（三）保密性原则。本行及各支行（部）在履行反洗钱工作中对掌握的客户身份信息、交易信息、风险等级信息等予以严格保密，非依法律规定和监管要求，不得向任何单位和个人提供。（四）持续性原则。对客户风险等级应进行持续关注、持续评定，根据实际情况适时调整客户风险等级。（五）分级管理原则。本行及各支行（部）应根据客户的风险等级，定期审核所保存的客户基本信息，对风险等级较高客户的审核应当严于对风险等级较低客户的审核。（六）及时性原则。新开客户必须在10个工作日内完成风险等级的划分。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第二章  客户风险等级划分标准', '第五条', '各支行（部）在为客户开立单位和个人账户时，应当按照反洗钱相关法律法规进行客户身份识别，并在此基础上综合考虑客户身份、地域、行业、业务、交易等风险因素划分风险等级，客户风险等级分高风险、较高风险、一般风险、较低风险、低风险五类，其划分标准如下：（一）高风险等级客户划分标准（1）客户因涉嫌有洗钱、恐怖融资活动行为而被国家有关部门调查的；（2）客户因涉嫌违法违规案件被国家金融监管部门通报的；（3）媒体披露客户存在洗钱行为，或与存在洗钱行为关联的个人或机构；（4）客户属于贩毒、腐败、恐怖活动或其他严重犯罪活动猖獗的国家和地区；（5）客户为外国政要或其家庭成员，以及与之关系密切的人员；（6）客户属于相关政府部门印发的黑名单客户，包括国务院有关部门、机构发布的恐怖组织、恐怖分子名单，联合国安理会相关协议和制裁名单，国家外汇管理局发布的黑名单，司法机关发布的恐怖组织、恐怖分子名单，中国人民银行要求关注的其他恐怖组织、恐怖分子嫌疑人名单中的客户等；（7）怀疑其资金、交易或试图进行的交易与恐怖主义、恐怖活动犯罪相关以及与恐怖组织、恐怖分子、从事恐怖融资活动相关联的客户；（8）使用虚假身份证件办理业务等有意隐瞒身份信息的客户；（9）大量使用现金交易行业，如金属回收行业；（10）大量使用网银、手机银行等非柜面方式进行交易且被监管部门、公安司法部门调查的客户；（11）具有可疑交易特征的交易行为，经认定并作为重点可疑客户报送人行反洗钱监测分析中心和当地监管机构的；（12）符合《金融机构大额交易和可疑交易报告管理办法》规定的可疑交易特征且有合理理由怀疑其存在高度洗钱风险的客户。（二）较高风险等级客户划分标准（1）一个月内对私客户单笔金额为10万以上的现金收付交易累计8次以上 ；（2）高于一般风险低于高风险划分标准的。（三）一般风险等级客户划分标准（1）当月客户重点可疑报告次数和一般可疑报告次数3-4次；（2）客户注册所在国家、地区监管体制存在缺陷的，或所在国家、地区未加入国际反洗钱组织或未被其认可的；（3）按照客户的身份、地域、行业或职业、交易特征，有合理理由怀疑其存在一定洗钱风险的客户；（4）因交易异常接受和配合公安、检察、法院等执法机关调查的客户；（5）所属行业为现金流量高、经营贵重物品、经营娱乐场所的客户；（6）交易偏好为喜好现金交易、跨境交易、非面对面交易的客户；（7）获得的客户身份资料有缺陷，真实性、有效性、完整性存在疑点的；（8）客户影像资料与身份证照片的比对不能认定为“完全相似”，但又不能认定为“不符合”的； 9）其它按照客户的身份、地域、行业或职业、交易特征，有合理理由怀疑其存在一定洗钱风险的客户。（四）较低风险等级客户划分标准高于低风险低于一般风险划分标准的。（五）低风险等级客户划分标准考虑国家或地区、业务、行业、客户和行为因素等，经综合分析不具有任何疑点的客户。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第三章  客户风险监控措施', '第六条', '本行及各支行（部）应按照客户的特点或者账户的属性，考虑地域、业务、行业、客户是否为外国政要等因素，划分风险等级，并在持续关注的基础上适时调整风险等级。在初次确定客户风险等级之后，如发现目前客户风险因素与该客户所属风险等级不对应，核实后要对客户风险等级做出相应调整。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第三章  客户风险监控措施', '第七条', '本行及各支行（部）应当根据客户或者账户的风险等级，划分为高风险的客户每隔0.5年进行一次审查，划分为较高风险的客户每隔1年进行一次审查，划分为一般风险的客户每隔1.5年进行一次审查，划分为较低风险的客户每隔2年进行一次审查，划分为低风险的客户每隔3年进行一次审查，根据审查情况及时、恰当调整客户风险等级。审查的措施包括但不限于分析客户账户开销情况、查阅账户交易流水、分析交易对手与其经营范围是否相符等。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第三章  客户风险监控措施', '第八条', '对于高风险客户或者高风险账户持有人，各支行（部）应当了解其资金来源、资金用途、经济状况或者经营状况等信息，加强对其金融交易活动的监测分析。客户为外国政要的，应采取合理措施了解其资金来源和用途。维护为黑名单的客户一律作为高风险客户，维护为白名单的客户一律作为低风险客户管理。    黑名单主要是指存在洗钱行为的客户及恐怖分子名单，省联社系统管理员及我行反洗钱专职人员按照人行公布的黑名单统一导入系统并及时更新。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第三章  客户风险监控措施', '第九条', '白名单主要包括各级党政机关、国家权力机关、行政机关、司法机关、军事机关、人民政协机关、人民解放军、武警部队等机构（不含其下属的各类企事业单位），由编辑岗在系统中维护并经过审批后生效（除上述情况外不得随意添加白名单）。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第三章  客户风险监控措施', '第十条', '本行及各支行（部）应由专人对反洗钱客户风险分类划分资料按会计档案管理要求妥善保管，期限暂定为永久保管，遇人员变动按规定做好交接手续。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第四章  附 则', '第十一条', '本指引由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱客户风险等级划分标准指引》', '第四章  附 则', '第十二条', '本指引自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第一章  总  则', '第一条', '为尽职履行反洗钱保密义务，规范xxx银行（以下简称“本行”）反洗钱保密工作，明确反洗钱岗位保密职责，根据《中华人民共和国反洗钱法》、《金融机构反洗钱规定》等有关法律、法规以及相关反洗钱管理办法，制定本制度。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第一章  总  则', '第二条', '本办法适用于各支行、部，机关各部室及团队。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第一章  总  则', '第三条', '本行反洗钱领导小组为反洗钱保密工作的管理机构，其主要职能是贯彻执行国家有关反洗钱保密的法律、法规和政策；审批有关涉密事项及奖惩决定。反洗钱领导小组下设办公室，办公室设在运营管理部，由其按领导小组的决定，布置、督促、检查本行的反洗钱保密工作；组织实施追查失密、泄密事件；组织保密宣传、开展保密教育。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第一章  总  则', '第四条', '本行反洗钱岗位人员在职期间应严格履行保密义务，岗位人员离职后应遵守保密义务，反洗钱保密信息和资料，非依法律规定，不得向任何单位和个人提供。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第五条', '反洗钱保密信息和资料包括因依法履行反洗钱职责和义务获得的客户身份资料和交易信息、客户洗钱风险等级分类信息、报告大额和可疑、恐怖融资交易信息、配合中国人民银行调查可疑交易活动的有关反洗钱工作信息、反洗钱非现场监管报送信息等。（一）客户身份资料、交易信息。包括进行客户尽职调查、与客户首次建立业务关系、为客户提供一次性金融服务、持续或重新识别客户时获得的身份、交易信息。（二）客户洗钱风险等级分类信息。包括对客户洗钱风险等级的评定情况及采取的措施。（三）报告大额和可疑、恐怖融资交易信息。包括报送的账户信息、交易信息、可疑交易的种类、反洗钱工作人员的分析判断、报告机构、报告时间等信息。（四）配合人民银行调查可疑交易活动及协助有权机关对反洗钱案件侦查等有关反洗钱工作信息。包括被调查对象的身份资料、账户信息、交易记录及可疑交易数据、调阅的有关文件和资料、调查活动的目的、调查活动的组织和人员安排、调查通知书内容、询问笔录内容、调查进度、调查报告等与调查活动有关的纸质、电子或音像形式的资料信息。（五）反洗钱非现场监管报送信息。包括非现场监管报表、报告以及中国人民银行依法要求报送的其他反洗钱工作信息等资料。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第六条', '本行在依法履行反洗钱职责和义务过程中获得的客户身份资料和交易信息，涉及客户隐私和商业秘密等，可采取的保密措施包括但不限于：（一）指定客户资料档案管理员负责管理档案；（二）设立专门保管客户档案的库房；（三）采取有效控制手段确保涉及客户身份和交易信息凭证及其他业务资料的安全流转及保存；（四）采取有效技术手段确保客户信息及交易电子数据的安全；（五）除在工作中必须接触客户身份资料及交易记录的工作人员外，其他未经授权人员不得取得和使用上述材料；（六）按照工作内容和性质划分工作人员接触客户资料的范围，分类限制客户身份资料及交易记录的查询权限。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第七条', '本行应对通过实地查访、回访客户等各种措施获知的客户财务状况、经营状况、投资目的、投资偏好、风险承受水平等客户信息保密。可采取的保密措施包括但不限于：（一）指定专人进行客户尽职调查工作；（二）限制客户资料查询权限。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第八条', '本行报送大额和可疑、恐怖融资交易报告应对报送的账户信息、交易信息、可疑交易的种类、反洗钱工作人员的分析判断等内容予以保密。可采取的保密措施包括但不限于：（一）指定专设部门、由专人负责报送大额和可疑、恐怖融资交易报告；（二）信息报送人员应对其使用的电脑设备的用户名和密码进行保密，定期更换；（三）设定本行反洗钱监测分析系统访问权限，限制该系统的使用人员范围；（四）通过纸质报送的重点可疑交易应由专人以密件方式报送当地人民银行，严格控制知情范围。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第九条', '本行应建立案件协查办法，明确保密要求。可采取的保密措施包括但不限于：（一）指定专门部门和专职人员负责协查工作；（二）协查过程中应严格控制知情人数；（三）指定专人负责协查工作，确保其他非协查人员不能接触到协查信息；（四）协查材料应由专人以密件方式报送协查机构；（五）建立协查台账，登记有关协查信息；（六）协查工作纸质文档要专人、专夹保管。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第十条', '本行反洗钱非现场监管信息报送等反洗钱工作环节可采取的保密措施包括但不限于：（一）指定专人负责向中国人民银行及其分支机构报送反洗钱统计报表、信息资料、交易数据、工作报告以及内部审计报告中与反洗钱工作有关的内容，如实反映反洗钱工作情况；（二）非现场监管信息和涉密工作报告等反洗钱信息资料应严格使用内部系统，不得使用公共网络传输；（三）严格控制涉密信息资料在内部签报流转过程中的审批路径；（四）选择具备保密条件的会议场所，并根据工作需要限定参加会议人员的范围。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第十一条', '依法履行反洗钱保密义务的工作人员包括但不限于：（一）在客户身份识别过程中，负有保密责任的人员主要包括分支机构经办人员、客户经理、分支机构负责人等获知客户身份资料和交易信息的相关工作人员；（二）在客户洗钱风险等级划分工作中，负有保密责任的人员主要包括负责客户风险等级评定的分支机构工作人员、高风险客户认定小组成员、负责审批高风险客户名单人员等可获知客户洗钱风险等级及其他相关信息的工作人员；（三）在大额和可疑、恐怖融资交易报告过程中，负有保密责任的人员主要包括负责大额和可疑、恐怖融资交易报告的反洗钱监测岗人员、重点可疑交易报告认定小组成员等获知大额和可疑、恐怖融资交易信息的相关工作人员；（四）在案件协查过程中，负有保密责任的人员主要包括各机构中的协查人员、协查部门负责人等获知反洗钱协查信息的相关工作人员；（五）负责人民银行非现场监管报表、报告以及其他反洗钱工作信息资料报送的工作人员；（六）其他应当履行反洗钱保密义务的人员。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第二章  反洗钱保密工作实施', '第十二条', '反洗钱工作档案应指定专人负责管理，保管人员应采取有效措施做好保密工作。可采取措施包括但不限于：（一）不得将反洗钱工作档案与其他文件混合组卷、混合保管；（二）制作、收发、传递、使用、复制、摘抄、保存和销毁工作档案，由专人执行或经有权人员批准后专人执行。在对反洗钱保密信息和资料进行复印或摘抄时，复印件和摘抄件必须采取与原件相同的保密措施。除非确有必要，在业务处理完毕后，复印件和摘抄件应当收回销毁;（三）配备档案库房或专用设施保管反洗钱工作档案；（四）不得带涉密文件到与工作无关的场所；（五）不得在非工作场合或在工作场合与无关人员谈论涉密事项；（六）需要调阅或查询反洗钱工作档案的，应按内部规定办理调阅审批及登记程序，不得在没有得到批准的情况下复印、拍照、拷贝反洗钱工作档案；（七）反洗钱涉密文件、资料、档案不得外借。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第三章  其他管理要求', '第十三条', '本行反洗钱工作办公室应对本行反洗钱工作保密制度的执行情况实施定期或不定期的监督检查，以保证各项保密措施能执行到位，取得实效。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第三章  其他管理要求', '第十四条', '本行工作人员如因未尽职履行反洗钱保密义务，不按规定保守反洗钱工作秘密和保管反洗钱工作信息资料，擅自将反洗钱调查事实告知相关客户和其他人员的，按本行员工违规违章行为管理规定进行处罚，构成犯罪的，依法移交司法机关处理。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第四章  附 则', '第十五条', '本制度由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱工作保密制度 》', '第四章  附 则', '第十六条', '本制度自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第一条', '为进一步加强xxx银行（以下简称“本行”）反洗钱客户识别、分类管理工作，落实“风险为本”原则，提高反洗钱处理工作效率和大额、可疑交易数据报送质量，减少营业机构工作量，现根据《中华人民共和国反洗钱法》、《金融机构大额交易和可疑交易报告管理办法》、《金融机构客户身份识别和客户身份资料及交易记录保存管理办法》和《xxx银行反洗钱客户风险等级划分标准指引》等制定本办法。	第二章  准入条件	    第二条  反洗钱“白名单”客户准入条件：    （一）反洗钱白名单客户均从客户风险等级评定等级为低风险的客户中产生；    （二）各级党的机关、国家权力机关、行政机关、司法机关、军事机关、人民政协机关和人民解放军、武警部队，但不含其下属的各类企事业单位；（三）主权政府客户，包括中央银行、政府部门及政府养老基金客户等。 操作流程'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第三条', '新建流程：（一）由客户账户开立网点根据准入条件初步认定是否符合白名单认定的条件，填写书面申请，提交反洗钱牵头部门负责人初审（核准类账户需经人民银行批准后提出申请）；（二）反洗钱牵头部门负责人同意后提交反洗钱分管行长审批；（三）反洗钱分管行长审批通过后由申请单位反洗钱编辑岗进入反洗钱系统，使用“白名单维护”菜单的“添加”功能予以维护，提交反洗钱系统审核岗；（四）反洗钱系统审核岗根据实际情况予以审核，提交反洗钱系统审批岗；（五）反洗钱系统审批岗根据实际情况予以审批，审批通过后该客户账户白名单维护成功。'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第四条', '退出流程：（一）白名单客户销户或因账户业务、性质发生变更导致原白名单客户已不符合白名单认定的要求时，由客户所在营业网点提出取消白名单客户的书面申请，提交反洗钱牵头部门负责人审批；（二）反洗钱牵头部门负责人审批通过后由申请单位反洗钱编辑岗进入反洗钱系统，使用“白名单维护”菜单的“删除”功能予以维护，提交反洗钱系统审核岗；（三）反洗钱系统审核岗要据实际情况予以审核，提交反洗钱系统审批岗；（四）反洗钱系统审批岗根据实际情况予以审批，审批通过后该客户账户白名单退出成功。  考核机制'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第五条', '本行按月对照各营业网点核心系统开销户登记簿对反洗钱监测系统中的白名单进行监测，确保白名单维护到位。监测中若发现有应入未入白名单、客户已销户白名单未退出和审批手续不齐全情形的，对相关责任人和所在网点进行处罚。  管理要求'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第六条', '坚持审慎原则加强对白名单客户管理。一是严格审批制度，白名单客户只能在风险等级划分为低风险的客户中产生；二是要定期对白名单客户的资金交易进行回溯（暂定为半年），发现异常的必须及时上报可疑交易；三是实施退出机制，一旦发现白名单客户出现风险状况或可疑情形的，及时将其调出白名单；四是加强白名单客户信息保密工作，严禁将白名单信息透露给客户。   附  则'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第十七条', '违规行为按本行员工违规违章行为管理规定执行。'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第十八条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱白名单库管理办法》', '', '第十九条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第一章  总 则', '第一条', '为加强xxx银行(以下简称“本行”)反洗钱工作管理，贯彻落实中国人民银行反洗钱金融规章制度，保障本行资金安全，防止违法分子利用本行从事洗钱活动，根据《中华人民共和国反洗钱法》和中国人民银行《金融机构反洗钱规定》、《金融机构大额交易和可疑交易报告管理办法》、《金融机构客户身份识别和客户身份资料及交易记录保存管理办法》等有关规定，结合本行实际，制定本办法。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第一章  总 则', '第二条', '本办法适用于本行及所辖各支行、部，机关各部室及团队。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第一章  总 则', '第三条', '本办法所称反洗钱，是指为防止违法犯罪分子将毒品犯罪、黑社会性质的组织犯罪、恐怖活动犯罪、走私犯罪或者其他犯罪的违法所得及其产生的收益，通过银行结算掩饰、隐瞒其来源和性质，使其在形式上合法化而制定和实施的防范措施。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第一章  总 则', '第四条', '本办法所称人民币支付交易，是指客户通过票据、银行卡、汇兑、托收承付、委托收款、网上银行支付和现金等方式进行的以人民币收付及其资金清算的交易。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第二章  反洗钱机构和人员', '第五条', '建立反洗钱内部控制制度，设立反洗钱领导小组，由本行行长担任组长，分管运营管理的副行长担任副组长，成员为机关部室负责人，反洗钱领导小组下设反洗钱工作小组办公室，办公室设在运营管理部，负责本行反洗钱日常工作。各支行、部，机关各部室及团队的反洗钱工作在本行反洗钱领导小组的领导下进行，运营管理部设立专门的反洗钱岗位，配备反洗钱专职人员，明确反洗钱审批岗。其他各支行、部，机关各部室及团队设立反洗钱工作岗位，配备反洗钱兼职人员2名，一名为反洗钱编辑岗，另一名为反洗钱审核岗，建立岗位责任制，明确运营主管为反洗钱审核岗，负责对大额交易和可疑交易进行记录、分析和报告，及时、准确完成反洗钱系统的各类报告工作。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第二章  反洗钱机构和人员', '第六条', '反洗钱领导小组工作职责是：（一）根据法律法规及监管要求，负责组织、协调本行的反洗钱管理工作；（二）负责组织制定本行的反洗钱政策和内控制度，将执行情况及时向董事会汇报；（三）负责审议、决策与反洗钱工作有关的管理工作事项；（四）负责监督指导反洗钱内部控制制度有效实施及反洗钱实施细则、操作流程制定工作；（五）负责督导和协调本行反洗钱检查工作的开展，确保反洗钱工作取得实效；（六）负责为反洗钱工作推进组织必要的人力、技术等资源，保证反洗钱工作目标的落实；（七）负责定期向董事会汇报本行反洗钱工作成果、重大事项及决策；（八）当本行发生洗钱风险案件等重大风险事项时，立即召开反洗钱领导小组临时会议并向董事会报告；（九）其他应履行的反洗钱管理职责。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第二章  反洗钱机构和人员', '第七条', '各支行、部，机关各部室及团队反洗钱工作人员应当保守反洗钱工作秘密，不得违反规定将通过依法履行反洗钱义务获得的客户身份资料和交易信息泄露给客户或其他人员，对上报的大额和可疑交易信息、配合人民银行调查可疑交易活动等有关反洗钱工作信息应予以保密，不得违反规定向任何单位和个人提供。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第二章  反洗钱机构和人员', '第八条', '各支行、部，机关各部室及团队应当依法协助、配合司法机关和行政执法机关打击洗钱活动，依照法律、行政法规等有关规定协助司法机关、海关、税务等部门查询、冻结、扣划客户存款等。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第二章  反洗钱机构和人员', '第九条', '各支行、部，机关各部室及团队有合理理由认为客户或者交易行为与洗钱、恐怖主义及其他违法犯罪活动有关的，应在确认为可疑交易后，及时以电子方式提交可疑交易报告，最迟不超过5个工作日。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第三章  客户资料审查、登记', '第十条', '各支行（部）应当按规定实施客户身份识别制度：（一）各支行（部）在以开立账户等方式与客户建立业务关系，为不在本机构开立账户的客户提供现金汇款、现钞兑换、票据兑付等一次性金融服务且交易金额单笔或累计人民币1万元以上或者外币等值1000美元以上的，以及为客户开办保管箱业务时，必须识别客户身份，了解实际控制客户的自然人和交易的实际受益人，自然人客户核对其有效身份证件，公司客户核对其《营业执照》等证明文件，登记客户身份基本信息，并留存有效身份证件或者《营业执照》等证明文件复印件或者影印件（含核查记录电子档）。（二）为自然人客户办理人民币单笔或累计5万元以上或者外币等值1万美元以上现金存取业务的，应当核对客户的有效身份证件。（三）自然人客户由他人代理存取现金业务时，如单笔存取款的金额达到或超过人民币5万元时，应同时核对存款人、代理人的有效身份证件，并登记存款人、代理人的姓名、联系方式以及有效身份证件的种类、号码。（四）办理通存业务时，如果代理人因合理理由无法提供存款人有效身份证件或者其他身份证明文件，且单笔或累计存款金额达到或超过人民币1万元现金时，应比照一次性业务的识别要求对代理人开展客户身份识别工作。（五）利用电话、网络、自动柜员机以及其他方式为客户提供非柜台方式的服务等，应实行严格的身份认证措施，采取相应的技术保障手段，识别客户身份。（六）在与客户的业务关系存续期间，各支行（部）应当采取持续的客户身份识别措施，关注客户及其日常经营活动、金融交易情况，及时提示客户更新资料信息。客户先前提交的有效身份证件和《营业执照》等证件过期后，开户机构应发出公告，要求在公告之日起一个月内到我行营业网点办理更新手续，客户没有在一个月内办理更新手续且没有提出合理理由的，各支行（部）应中止为其办理业务。（七）各支行（部）通过第三方识别客户身份的，应当确保第三方已经采取符合本办法要求的客户识别措施；第三方未采取符合本办法要求的客户身份识别措施的，由支行（部）承担未履行客户识别义务的责任。（八）为客户向境外汇出资金时，应当登记汇款人的姓名或者名称、账号、住所和收款人的姓名、住所等信息，在汇兑凭证或者相关信息系统中留存上述信息，并向接收汇款的境外机构提供汇款人的姓名或者名称、账号、住所等信息。汇款人没有在本支行（部）开户，无法登记汇款人账号的，可登记并向接收汇款的境外机构提供其他相关信息，确保该笔交易的可跟踪稽核。境外收款人住所不明确的，可登记接收汇款的境外机构所在地名称。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第三章  客户资料审查、登记', '第十一条', '出现以下情况时，各支行（部）应当重新识别客户：（一）客户要求变更姓名或者名称、身份证件或者身份证明文件种类、身份证件号码、注册资本、经营范围、法定代表人或者负责人的。（二）客户行为或者交易情况出现异常的。（三）客户姓名或者名称与国务院有关部门、机构和司法机关依法要求本行协查或者关注的犯罪嫌疑人、洗钱和恐怖融资分子的姓名或者名称相同的或被列入黑名单的客户。（四）客户有洗钱、恐怖融资活动嫌疑的或有可疑交易报送的客户。（五）本行获得的客户信息与先前已经掌握的相关信息存在不一致或者相互矛盾的。（六）先前获得的客户身份资料的真实性、有效性、完整性存在疑点的。（七）客户先前提交的身份证件或者《营业执照》等证明文件已过有效期的。（八）客户风险等级为高风险客户。（九）本行认为应重新识别客户身份的其他情形。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第三章  客户资料审查、登记', '第十二条', '各支行（部）除核对有效身份证件或者其他身份证明文件外，可以采取以下的一种或者几种措施，识别或者重新识别客户身份：（一）要求客户补充其他能证明其身份信息的辅助身份资料或者身份证明文件。（二）回访客户。（三）实地查访。（四）向公安、工商行政管理等部门核实。（五）其他可依法采取的措施。各支行、部，机关各部室及团队履行客户身份识别义务时，按照法律、行政法规或部门规章的规定需核对相关自然人的居民身份证的，应通过中国人民银行建立的联网核查公民身份信息系统进行核查。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第三章  客户资料审查、登记', '第十三条', '各支行、部，机关各部室及团队在履行客户身份识别义务时，应当向中国人民银行当地分支机构报告以下可疑行为：（一）客户拒绝提供有效身份证件或者其他身份证明文件的。（二）对向境内汇入资金的境外机构提出要求后，仍无法完整获得汇款人姓名或者名称、汇款人账号和汇款人住所及其他相关替代性信息的。（三）客户无正当理由拒绝更新客户资料信息的。（四）采取必要措施后，仍怀疑先前获得的客户身份资料的真实性、有效性、完整性的。（五）履行客户身份识别义务时发现的其他可疑行为。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第十四条', '各支行、部，机关各部室及团队应当报告下列大额交易：（一）当日单笔或者累计交易人民币5万元以上（含5万元）、外币等值1万美元以上（含1万美元）的现金缴存、现金支取、现金结售汇、现钞兑换、现金汇款、现金票据解付及其他形式的现金收支。（二）非自然人客户银行账户与其他的银行账户发生当日单笔或者累计交易人民币200万元以上（含200万元）、外币等值20万美元以上（含20万美元）的款项划转。（三）自然人客户银行账户与其他的银行账户发生当日单笔或者累计交易人民币50万元以上（含50万元）、外币等值10万美元以上（含10万美元）的境内款项划转。（四）自然人客户银行账户与其他的银行账户发生当日单笔或者累计交易人民币20万元以上（含20万元）、外币等值1万美元以上（含1万美元）的跨境款项划转。累计交易金额以客户为单位，按资金收入或者支出单边累计计算并报告。中国人民银行另有规定的除外。本条第一款规定的大额交易报告标准如遇中国人民银行调整，本行按调整后标准来执行。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第十五条', '大额交易的报告程序。各支行、部，机关各部室及团队反洗钱岗位人员于次日登录反洗钱系统对大额交易有关信息进行填补或补录，最迟不得超过大额交易发生之日起3个工作日。第十六  对符合下列条件之一的大额交易，如未发现该交易可疑的，可以不报告：（一）定期存款到期后，不直接提取或者划转，而是本金或者本金加全部或者部分利息续存入在同一金融机构开立的同一户名下的另一账户。活期存款的本金或者本金加全部或者部分利息转为在同一金融机构开立的同一户名下的另一账户内的定期存款。定期存款的本金或者本金加全部或者部分利息转为在同一金融机构开立的同一户名下的另一账户内的活期存款。（二）自然人实盘外汇买卖交易过程中不同外币币种间的转换。（三）交易一方为各级党的机关、国家权力机关、行政机关、司法机关、军事机关、人民政协机关和人民解放军、武警部队，但不含其下属的各类企事业单位。（四）金融机构同业拆借、在银行间债券市场进行的债券交易。（五）金融机构在黄金交易所进行的黄金交易。（六）金融机构内部调拨资金。（七）国际金融组织和外国政府贷款转贷业务项下的交易。（八）国际金融组织和外国政府贷款项下的债务掉期交易。（九）本行办理的税收、错账冲正、利息支付。（十）中国人民银行确定的其他情形。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第十七条', '定期根据本行情况向省联社提交交易监测标准，并对其有效性负责。可疑与恐怖融资交易监测标准包括并不限于客户的身份、行为，交易的资金来源、金额、频率、流向、性质等存在异常的情形，参考以下因素：（一）中国人民银行及其分支机构发布的反洗钱、反恐怖融资规定及指引、风险提示、洗钱类型分析报告和风险评估报告。（二）公安机关、司法机关发布的犯罪形势分析、风险提示、犯罪类型报告和工作报告。（三）本机构的资产规模、地域分布、业务特点、客户群体、交易特征，洗钱和恐怖融资风险评估结论。（四）中国人民银行及其分支机构出具的反洗钱监管意见。（五）中国人民银行要求关注的其他因素。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第十八条', '可疑交易的报告程序：各支行、部，机关各部室及团队反洗钱岗位人员于次日登录反洗钱系统对通过交易监测标准筛选出的交易进行人工分析、识别，并记录分析过程；不作为可疑交易报告的，应记录分析排除的合理理由；确认为可疑交易的，应在可疑交易报告理由中完整记录对客户身份特征、交易特征或行为特征的分析过程。本行应在本机构可疑交易报告内部操作规程确认为可疑交易后，及时以电子方式提交可疑交易报告，最迟不超过5个工作日。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第十九条', '各支行、部，机关各部室及团队对大额交易报告或者可疑交易报告要素不全或者存在错误，中国人民银行查询或要求补正的，应在接到补正通知的5个工作日内补正，并记录存档。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第二十条', '各支行（部）对上报的可疑交易未有处理结果前，需继续关注该可疑对象。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第二十一条', '对既属于大额交易又属于可疑交易的交易，应当分别提交大额交易报告和可疑交易报告。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第四章  大额、可疑交易报告', '第二十二条', '对于客观上具有可疑交易的异常特征，但却有合理理由排除疑点，或没有合理理由怀疑该交易或客户涉及违法犯罪活动的，无论是否作为可疑交易报告，均应认真做好相应的书面分析甄别工作情况记录，留存可供事后核查的记录和资料。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第五章  客户资料和交易记录的保存', '第二十三条', '各支行、部应当保存的客户身份资料包括：记载客户身份信息、资料以及反映金融机构开展客户身份识别工作情况的各种记录和资料。自然人客户的“身份基本信息”包括客户的姓名、性别、国籍、职业、住所地或者工作单位地址、联系方式，身份证件或者身份证明文件的种类、号码和有效期限。客户的住所地与经常居住地不一致的，登记客户的经常居住地。法人、其他组织和个体工商户客户的基本信息包括客户的名称、住所、经营范围、组织机构代码、税务登记证号码；可证明该客户依法设立或者可依法开展经营、社会活动的执照、证件或者文件的名称、号码和有效期限；控股股东或者实际控制人、法定代表人、负责人和授权办理业务人员的姓名、身份证件或者身份证明文件的种类、号码、有效期限。各支行（部）应当保存的交易记录包括：每笔交易的数据信息、业务凭证、账簿以及有关规定要求的反映交易真实情况的合同、业务凭证、单据、业务函件和其他资料。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第五章  客户资料和交易记录的保存', '第二十四条', '各支行（部）应采取必要管理措施和技术措施，防止客户身份资料和交易记录的缺失、损毁，防止泄漏客户身份信息和交易信息。应采取切实可行的措施保存客户身份资料和交易记录，便于反洗钱调查和监督管理。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第五章  客户资料和交易记录的保存', '第二十五条', '客户资料和交易记录档案的保管及销毁，应按照有关会计档案管理的规定执行。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第五章  客户资料和交易记录的保存', '第二十六条', '客户身份资料和交易记录的保存期限：（一）客户身份资料，自业务关系结束当年或者一次性交易记账次年1月1日起至少保存5 年。（二）交易记录，自交易记账次年1月1日起至少保存5 年。（三）各种报告表、登记簿及有关记录自报告日起不少于5年。同一介质上存有不同保存期限客户身份资料或者交易记录的，应当按最长期限保存。同一客户身份资料或者交易记录采用不同介质保存的，至少应当按照上述期限要求保存一种介质的客户身份资料或者交易记录。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第五章  客户资料和交易记录的保存', '第二十七条', '客户身份资料和交易记录涉及正在被反洗钱调查的可疑交易活动，且反洗钱调查工作在前款规定的最低保存期届满时仍未结束的，各支行（部）应将其保存至反洗钱调查工作结束。法律、行政法规和其他规章对客户身份资料和交易记录有更长保存期限要求的，遵守其规定。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第六章  罚  则', '第二十八条', '各支行（部）工作人员违反本办法，有下列行为之一的，给予警告至开除处分并给予一定的经济处罚：（一）未按照规定对开户资料进行审查、登记，致使单位或个人开立虚假银行结算账户的；（二）未按照规定建立存款人信息数据档案或收集的存款人信息数据不完整的；（三）未按照规定保存客户资料和交易记录的；（四）未按照规定对大额和可疑交易进行审查和报告的；（五）对明知或应知的可疑交易不报告的；（六）对大额、可疑交易不认真进行核实、认定，致使误报、漏报、错报的；（七）泄露因反洗钱知悉的国家秘密、商业秘密或者个人隐私的。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第六章  罚  则', '第二十九条', '本行和各支行（部）负责人有下列行为之一的，给予警告至撤职处分并给予一定的经济处罚：（一）对有关反洗钱法规和制度不按要求及时传达贯彻、贻误工作的；（二）对开户、大额和可疑交易报告行为等规章制度不落实、对客户资料和交易记录保存等管理混乱的；（三）发现问题不及时采取措施进行处理，不按规定向上级报告的；（四）监督检查不力，造成有关反洗钱法规制度、操作流程不落实的；（五）指使、纵容、默认有关违规行为的。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第七章  附 则', '第三十条', '本办法下列用语的含义如下：（一）“以上”包括本数。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第七章  附 则', '第三十一条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱管理办法》', '第七章  附 则', '第三十二条', '本办法自发文之日起施行（大额交易和可疑报告变化的部分自2017年7月1日开始执行）。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第一章  总 则', '第一条', '为加强反洗钱管理，防范和控制洗钱风险，提高xxx银行（以下简称“本行”）反洗钱风控能力和管理水平，根据《中华人民共和国反洗钱法》、《金融机构反洗钱规定》等监管政策及相关反洗钱管理办法，制定本规则。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第一章  总 则', '第二条', '本行反洗钱领导小组在董事会领导下开展工作，负责全行的反洗钱监督管理工作。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第一章  总 则', '第三条', '本办法适用于各支行、部，机关各部室及团队。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第二章  组织机构', '第四条', '反洗钱领导小组是全行反洗钱工作的领导和管理机构。本行反洗钱领导小组组长由董事长担任，副组长由行长以及分管运营和合规副行长担任。成员包括：行政办公室、人力资源部、纪检监察部、安全保卫部、审计部、电子银行部、授信评审部、信贷管理部、合规管理部、风险管理部、计划财务部、运营管理部、信息科技部、金融市场部、发展规划部、资产保全部、统计分析部、三农业务部等相关部室负责人，发生部门及人员调整应及时变更。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第二章  组织机构', '第五条', '反洗钱领导小组下设反洗钱工作小组办公室，办公室设在运营管理部，负责本行反洗钱日常工作。运营管理部负责人兼任反洗钱办公室主任。办公室成员由领导小组指定人员组成，发生成员变更及时向办公室报备。各分支机构负责人、部门负责人是本单位反洗钱工作的第一责任人，对本单位有效实施反洗钱内控制度负责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第六条', '反洗钱领导小组主要职责：（一）根据法律法规及监管要求，负责组织、协调本行的反洗钱管理工作；（二）负责组织制定本行的反洗钱政策和内控制度，将执行情况及时向董事会汇报；（三）负责审议、决策与反洗钱工作有关的管理工作事项；（四）负责监督指导反洗钱内部控制制度有效实施及反洗钱实施细则、操作流程制定工作；（五）负责督导和协调本行反洗钱检查工作的开展，确保反洗钱工作取得实效；（六）负责为反洗钱工作推进组织必要的人力、技术等资源，保证反洗钱工作目标的落实；（七）负责定期向董事会汇报本行反洗钱工作成果、重大事项及决策；（八）当本行发生洗钱风险案件等重大风险事项时，立即召开反洗钱领导小组临时会议并向董事会报告；（九）其他应履行的反洗钱管理职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第七条', '反洗钱领导小组组长工作职责:（一）负责推动反洗钱领导小组各项工作；（二）负责决定反洗钱领导小组会议的召开，审定会议议程,主持召开反洗钱领导小组工作会议；（三）负责对反洗钱领导小组会议内容及其他反洗钱重大事项组织讨论并进行决策；（四）负责审定并签发反洗钱领导小组会议纪要，推动会议决议落实；（五）因故不能召集和主持会议时，负责决定接受委托召集和主持会议的人选。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第八条', '反洗钱领导小组副组长工作职责:（一）负责协助组长推动反洗钱领导小组各项工作开展及落实；（二）负责受组长委托，主持召开反洗钱领导小组会议；（三）负责组织落实反洗钱领导小组会议决议。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第九条', '反洗钱领导小组成员工作职责:（一）负责向反洗钱领导小组提交本业务条线的会议议题；（二）负责对会议议题参与讨论、发表意见和建议；（三）负责反洗钱领导小组决议及其他反洗钱相关工作在本条线（部、室）范围内的协调组织与推动执行。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十条', '反洗钱工作办公室工作职责:（一）负责制订本行反洗钱内控基本制度，推动本行反洗钱内控制度建设；（二）负责制定全行反洗钱工作计划并提交反洗钱领导小组审议，推动本行反洗钱工作计划的贯彻落实；（三）负责组织协调本行反洗钱管理工作，推动反洗钱相关业务部门及分支机构履行反洗钱职责； （四）负责指导各单位运用反洗钱监测分析系统，确保反洗钱数据准确报送，及时与人民银行和中国反洗钱监测分析中心沟通相关情况；（五）负责收集全行反洗钱工作中的重大疑难问题，提出解决方案与对策并提请召开反洗钱领导小组会议；（六）负责组织反洗钱的培训与宣传工作，对全行相关人员进行反洗钱培训，增强反洗钱工作能力；（七）负责组织推动全行各专业条线反洗钱检查工作有效实施；（八）负责研究国内外同业反洗钱工作开展情况，积极参与反洗钱国内外合作；（九）负责向反洗钱领导小组提交年度反洗钱工作报告；（十）负责承办反洗钱领导小组交办的其他工作事项。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十一条', '反洗钱业务条线成员部门主要职责如下（运营管理部、电子银行部、授信管理部、信贷管理部、金融市场部、三农业务部）：（一）负责在本业务条线管理工作中履行反洗钱工作要求，在本业务条线制度制定、流程设计等工作中落实反洗钱大额和可疑交易报告报送、客户身份识别、客户身份资料及交易记录保存等各项内控要求；（二）负责定期对本条线业务制度、操作规程的有效性进行回溯审查，确保将最新反洗钱监管政策要求更新到本条线相关制度当中；（三）负责将本条线反洗钱工作中的重大疑难问题及拟解决措施提交反洗钱工作办公室审议；（四）负责本条线反洗钱会议议题的发起和材料准备；（五）负责将反洗钱培训纳入本专业业务培训内容；（六）负责参与反洗钱监测分析系统的开发，确保本业务条线产品的反洗钱数据能够全面、准确和及时的采集；（七）负责本业务条线反洗钱工作开展情况的检查、评估和考核；（八）负责其他与本部门相关的反洗钱工作管理职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十二条', '总行行政办公室职责:（一）负责对金融行业洗钱或恐怖融资相关舆情（含媒体报道、互联网信息）进行监测，及时发现并通报反洗钱工作办公室；（二）负责协助向公安部门报送有关洗钱犯罪线索的交易；（三）负责其他与本部门相关的反洗钱管理工作职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十三条', '信息科技部职责:（一）负责反洗钱大额和可疑交易报告、客户身份识别、客户身份资料及交易记录保存等工作提供技术支持；（二）负责省联社、反洗钱监管部门与全行反洗钱信息系统的稳定运行及网络的畅通；（三）负责其他与本部门相关的反洗钱管理工作职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十四条', '审计部职责:（一）负责按照监管部门要求定期对全行反洗钱工作进行审计，包括对业务条线（部、团队）和全行反洗钱工作的审计指导，及时发现并监督纠正反洗钱工作中出现的不合规问题；（二）负责其他与本部门相关的反洗钱管理工作职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十五条', '合规管理部门职责（一）负责审核业务制度中反洗钱管理措施的合规性，确保各项反洗钱风险控制措施符合外部法律、法规等监管的要求；（二）负责反洗钱检查工作的统筹管理，组织制定全行反洗钱检查方案并组织实施；（三）当涉及反洗钱相关法律风险时，提供专业法律意见及处理方案；（四）负责其他与本部门相关的反洗钱工作管理工作职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十六条', '风险管理部门职责:（一）负责审核业务、产品洗钱风险识别和采取的风险控制措施是否满足反洗钱风险控制要求；（二）负责与本部门相关的反洗钱工作管理的工作职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第三章  工作职责', '第十七条', '计划财务部、发展规划部部门职责（一）负责审核业务、产品洗钱风险识别和采取的风险控制措施是否满足反洗钱风险控制要求；（二）负责与本部门相关的反洗钱工作管理工作职责。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第十八条', '反洗钱领导小组每年至少召开四次工作会议，组长可以决定召开临时会议。反洗钱领导小组各成员部门可根据本部门（条线）反洗钱工作实际情况，向反洗钱工作办公室提议，并报组长同意后召开临时会议。反洗钱领导小组工作会议由组长主持，组长因故不能主持时，可授权副组长主持。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第十九条', '反洗钱领导小组成员因故不能出席反洗钱小组会议时，经组长批准，可委托本部门其他人员出席会议，被委托人不得再转委托其他人员。被委托人享有与委员相同的权利，委托期限于本次会议结束时终止。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第二十条', '议题确定和会议材料准备反洗钱领导小组会议议题由发起部门在反洗钱领导小组会议召开前七个工作日，将议题和材料以函件形式提交反洗钱工作办公室。反洗钱工作办公室对议题进行汇总整理，报反洗钱领导小组组长审批。议题发起部门对会议材料的合规性、真实性和准确性负责，会议材料由反洗钱工作办公室审核。如存在材料不完整、内容不符合相关制度规定等问题，反洗钱工作办公室负责向材料提交部门反馈材料修改意见。材料提交部门在收到材料修改意见后，于2个工作日内对材料进行补充完善并再次提交审核。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第二十一条', '会议准备（一）反洗钱工作办公室负责起草会议通知，预定会议室，安排人员进行会议记录，并将会议通知、会议材料等在会议召开前提交反洗钱领导小组成员；（二）根据会议议程，反洗钱领导小组组长确定需要其他相关列席部门时，由反洗钱工作办公室在会议召开前发出书面通知。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第二十二条', '会议纪要会议纪要由反洗钱工作办公室负责组织撰写，报反洗钱领导小组组长审批并以会议纪要形式印发。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第二十三条', '会议材料存档会议材料由反洗钱工作办公室负责存档。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第二十四条', '会议决议落实相关责任部门负责落实会议决议，并将会议决议落实情况报反洗钱工作办公室。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第四章  议事规则及流程', '第二十五条', '监督检查反洗钱工作办公室负责对会议决议落实情况进行监督检查，并向反洗钱领导小组组长报告。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第五章  附 则', '第二十六条', '本规则由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《反洗钱领导小组工作规则》', '第五章  附 则', '第二十七条', '本规则自发文之日起施行。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第一章    总   则', '第一条', '为切实加强xxx银行（以下简称“本行”）同城清算系统运行管理，建立良好的工作秩序，保障同城清算系统(以下简称“清算系统”）的正常运行及资金安全，根据《商业银行内部控制指引》、《yy市同城清算往来业务制度》、《yy市同城清算往来业务会计核算手续》等有关规定制定本办法。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第一章    总   则', '第二条', '本办法所称同城往来是指yy市范围内的跨行资金交易。清算团队是本行同城清算系统的清算行。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第一章    总   则', '第三条', '本行应加强对辖内各支行（部）系统运行、操作和维护的管理，以保证同城清算工作的顺利进行。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第一章    总   则', '第四条', '各支行（部）申请参加的手续，对具备参加同城清算系统的各支行（部）由本行向中国人民银行yy市中心支行（以下简称“yy人行”）申请，经yy人行审核同意后，发给交换行号，正式发布入网时间，各支行（部）方可办理资金往来业务。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第一章    总   则', '第五条', '各支行（部）名称变更、撤销，应由本行及时向yy人行提出书面报告，并提供金融监管部门批准的相关资料复印件。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第六条', '各支行（部）办理同城往来业务，必须加强内部控制，确保账务核算准确和资金安全，防范利用计算机犯罪作案。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第七条', '操作人员经培训合格后方能上机操作，并严格按照系统要求的操作流程进行规范操作。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第八条', '根据岗位分离、相互制约和分工协作的原则，合理设置岗位，严禁一人兼任非相容的岗位或独自完成全过程的业务操作。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第九条', '各岗位分工的基本原则⒈根据人员状况，以不违反会计制度为原则，岗位设置可交叉或合并，各岗位之间必须相互牵制。⒉根据操作人员权限管理的要求，加强业务处理的事中复核控制和审核监督。⒊操作人员离岗、调岗或调动工作，应遵循会计制度的规定，办理交接手续。⒋严格执行印、证分管的原则。⒌信息科技部系统管理人员不得进行会计业务操作。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第十条', '级别管理按照岗位职责和操作权限设置不同的操作级别，各级别操作人员在其业务处理权限范围内处理同城往来业务，严格授权操作。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第十一条', '操作员管理1、每个操作人员只有一个操作员号。2、操作人员的密码必须保密，对密码不定期进行更改。3、不操作业务时做到离岗签退，防止盗用。4、各支行（部）须按姓名、代号、操作权限、使用时间、停用时间等项目设置操作人员管理登记簿。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第十二条', '重要空白凭证管理各支行（部）对同城往来业务中所使用的“yy市支付清算综合系统专用凭证”须纳入重要空白凭证管理。“yy市支付清算综合系统专用凭证”由中国人民银行统一印制。各支行（部）对于上述各类重要空白凭证必须严格管理，做到手续严密，账实相符，保证安全。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第十三条', '账户管理必须严格按照《人民币银行结算账户管理办法》、《人民币银行结算账户管理办法实施细则》等有关规定管理、使用账户。各支行（部）运营主管对有变更和撤销的账户及时在同城系统中进行维护。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第二章    内部控制管理', '第十四条', '事后监督团队应加强对同城业务的事后监督，对每天网点上交的凭证与提出、提入业务流水账逐笔勾对、逐笔检查核对，并详细记录情况，发现问题，及时反馈到支行（部）运营主管，严肃处理，对较严重的问题，应上报运营管理部。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第十五条', '同城前置机由信息科技部系统管理员进行日常管理、维护。同城清算网络具备主干网和备份网，当主干网发生故障，信息科技部应由专人立即将网络切换到备份网上，并向yy人行电子结算中心报告。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第十六条', '在办理同城往来业务时，提出行经办人员必须在凭证审核、传递等环节上严格把关，对要素不正确、内容不完整的凭证一律不予受理。提出行对跨行结算票据的真实性、合法性承担完全责任。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第十七条', '各支行（部）需建立严密的同城往来业务凭证的内部传递流程，确保会计凭证按照业务处理流程规范、有序、严密地传递。柜面受理、业务处理、事后监督及票据影像采集、凭证整理归档等各个环节应建立严密的交接手续。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第十八条', '采用计算机打印出来的支付系统凭证、清单，须确保要素完整、规格统一和章戳齐全。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第十九条', '数据输入必须由具有业务处理权限的操作人员在规定的操作权限范围内进行业务处理。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十条', '操作人员必须以合法有效的凭证为记账依据，不得以要素不全或未经审核或批准的凭证进行业务处理，数据输入做到及时、准确。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十一条', '客户提交的电子支付信息必须经过加密、解密后，打印清单，与客户提交的原始凭证或清单勾对一致后，才能进行账务处理。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十二条', '同城清算打印的内容包括专用凭证、差额凭证、提出交换凭证清单、提入交换凭证清单、日志文件、查询查复书等。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十三条', '同城清算系统打印的各类凭证、清单，须严格控制打印份数，打印作废必须作注销或销毁处理。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十四条', '同城清算系统为提出行、提入行提供固定格式报文查询查复和自由格式报文查询查复，涉及到往来业务的查询查复只能使用固定格式报文进行处理。查询查复应坚持“有疑必查，有查必复，复必详尽，切实处理”。查复行最迟不得迟于下个工作日上午进行查复。清算团队口头或书面的业务查复结果不作为查询行记账依据。同城往来业务不得通过查询查复对结算金额进行修改。同城业务差错处理应坚持“有错即纠、双人经办、密切配合、妥善处理”的原则。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十五条', '本行统一在yy人行开设清算账户，用于各支行（部）同城清算业务的资金清算。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十六条', '清算团队负责做好人行头寸的管理监控工作，为本行头寸管理第一责任人；负责每日与人行进行对账，发现异常交易或挂账业务及时查明原因后进行处理。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十七条', '同城清算系统使用虚拟限额控制本行日间轧差净额，当日间轧差净额超过虚拟限额时，各支行（部）的提出贷记业务和提入借记业务需排队等候处理。日终仍排队的业务均设置为失败。yy人行根据本行清算账户平均余额核定本行虚拟限额。若日间轧差净额超过虚拟限额，由本行提出申请，人民银行视清算账户余额和筹措资金能力等情况决定是否提高虚拟限额。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第三章    操作管理', '第二十八条', '清算团队应保证清算账户上有足够余额供同城日终轧差净额的清算，发现资金不足应在规定的时间内及时筹措或由人民银行按有关规定办理。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第四章  系统安全管理', '第二十九条', '机器设备管理。为保证清算系统的正常运行，各支行（部）必须保持良好的设备运行环境，计算机网络系统的供电，通讯设备必须安全可靠，并备有良好的、易切换的替换设备。本行信息科技部应对本系统内各网点的机器设备情况进行定期和不定期检查，保证系统正常运行，遇到系统问题及时通知网点并及时修复。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第四章  系统安全管理', '第三十条', '操作和程序管理。根据系统维护和业务操作分别管理的原则，本行对程序维护和会计操作要严格分别管理，明确岗位职责，做到岗位之间的相互制约，各岗位之间不得相互兼任。严禁维护人员直接操作清算系统，处理会计业务。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第四章  系统安全管理', '第三十一条', '异常情况处理。为安全、准确、及时地处理票据交换业务中出现的问题和差错，操作人员在业务处理过程中，发现异常情况，影响资金往来业务，应及时向本行运营管理部报告。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第五章    权利、义务和责任', '第三十二条', '权利⒈提出行有权拒绝受理要素不完整或不准确的凭证。⒉提出行可以在规定的时限内申请撤销或冲正特定业务。⒊本行可以向中国人民银行结算中心（以下简称“结算中心”）查询提出、提入业务结果。⒋因他行业务差错造成本行资金损失的，有权向对方行索取赔偿。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第五章    权利、义务和责任', '第三十三条', '义务⒈在人民银行保持足够的清算资金，执行同城清算资金透支应急处理有关规定。⒉本行管理各支行（部）的往来业务和资金清算。⒊提出行按规定的格式标准、时间和传送方式办理业务，并及时进行事后监督，按规定传送票据影像信息；提入行按规定的时间接收结算中心转发的业务和票据影像信息。⒋与结算中心核对业务结果，及时反馈对账回执。⒌对查询查复做到“有疑必查、有查必复，复必详尽、切实处理”。⒍对提出行要求更正、撤销或冲正的业务应及时协助处理。⒎提入行对无法处理的业务应主动退还给提出行。⒏对支票借记业务发生透支的，须及时向人民银行举报，配合人民银行做好空头支票的调查取证和处罚。⒐定期缴纳电子同城票据交换汇划费和系统维护费。⒑对同城清算系统在运行中出现的异常情况及时向本行运营管理部门报告。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第五章    权利、义务和责任', '第三十四条', '责任⒈因提出行审核不严或业务操作差错，发生经济责任事故，由提出行负直接责任。⒉因操作人员的责任迟提、漏提、错提业务，由责任方负责补救；造成延误的，按延误天数和规定的利率计付赔偿金；造成资金损失的，负责赔偿经济损失。⒊因管理不善、违章操作、密码泄露等原因造成资金被诈骗、贪污、挪用的，由责任方承担责任。⒋凡违反同城清算有关制度和有管理失职行为的支行（部）由本行责令限期整改，视情节轻重进行相应处罚。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第六章    附    则', '第三十五条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《同城清算系统运行管理办法》', '第六章    附    则', '第三十六条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第一章  总 则', '第一条', '为防止利用xxx银行（以下简称“本行”）进行洗钱活动，规范本行大额交易和可疑交易报告行为，根据《中华人民共和国反洗钱法》、《中华人民共和国反恐怖主义法》、《金融机构大额交易和可疑交易报告管理办法》等有关法律法规制定本办法。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第一章  总 则', '第二条', '本办法适用于各支行、部，机关各部室及团队。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第一章  总 则', '第三条', '各支行、部，机关各部室及团队应当履行大额交易和可疑交易报告义务，按照中国人民银行规定的报告要素要求，提供真实、完整、准确的交易信息，通过省联社反洗钱系统向中国反洗钱监测分析中心报送大额交易和可疑交易报告，接受中国人民银行及其分支机构的监督、检查。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第一章  总 则', '第四条', '本行反洗钱工作办公室设在运营管理部，负责对各支行、部，机关各部室及团队大额交易和可疑交易报告情况进行指导、监督、检查。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第二章  大额交易报告', '第五条', '各支行、部，机关各部室及团队应当报告下列大额交易：（一）当日单笔或者累计交易人民币5万元以上（含5万元）、外币等值1万美元以上（含1万美元）的现金缴存、现金支取、现金结售汇、现钞兑换、现金汇款、现金票据解付及其他形式的现金收支。（二）非自然人客户银行账户与其他的银行账户发生当日单笔或者累计交易人民币200万元以上（含200万元）、外币等值20万美元以上（含20万美元）的款项划转。（三）自然人客户银行账户与其他的银行账户发生当日单笔或者累计交易人民币50万元以上（含50万元）、外币等值10万美元以上（含10万美元）的境内款项划转。（四）自然人客户银行账户与其他的银行账户发生当日单笔或者累计交易人民币20万元以上（含20万元）、外币等值1万美元以上（含1万美元）的跨境款项划转。累计交易金额以客户为单位，按资金收入或者支出单边累计计算并报告。中国人民银行另有规定的除外。本条第一款规定的大额交易报告标准如遇中国人民银行调整，本行按调整后标准来执行。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第二章  大额交易报告', '第六条', '对同时符合两项以上大额交易标准的交易，应当分别提交大额交易报告。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第二章  大额交易报告', '第七条', '对符合下列条件之一的大额交易，如未发现交易或行为可疑的，可以不报告：（一）定期存款到期后，不直接提取或者划转，而是本金或者本金加全部或者部分利息续存入在同一金融机构开立的同一户名下的另一账户。活期存款的本金或者本金加全部或者部分利息转为在同一金融机构开立的同一户名下的另一账户内的定期存款。定期存款的本金或者本金加全部或者部分利息转为在同一金融机构开立的同一户名下的另一账户内的活期存款。（二）自然人实盘外汇买卖交易过程中不同外币币种间的转换。（三）交易一方为各级党的机关、国家权力机关、行政机关、司法机关、军事机关、人民政协机关和人民解放军、武警部队，但不含其下属的各类企事业单位。（四）金融机构同业拆借、在银行间债券市场进行的债券交易。（五）金融机构在黄金交易所进行的黄金交易。（六）金融机构内部调拨资金。（七）国际金融组织和外国政府贷款转贷业务项下的交易。（八）国际金融组织和外国政府贷款项下的债务掉期交易。（九）本行办理的税收、错账冲正、利息支付。（十）中国人民银行确定的其他情形。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第二章  大额交易报告', '第八条', '各支行、部，机关各部室及团队应当在大额交易发生之日起5个工作日内以电子方式提交大额交易报告。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第二章  大额交易报告', '第九条', '下列金融机构与本行客户进行金融交易并通过银行账户划转款项的，应按照本办法规定提交大额交易报告：(一)证券公司、期货公司、基金管理公司。(二)保险公司、保险资产管理公司、保险专业代理公司、保险经纪公司。(三)信托公司、金融资产管理公司、企业集团财务公司、金融租赁公司、汽车金融公司、消费金融公司、货币经纪公司、贷款公司。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第二章  大额交易报告', '第十条', '客户通过本行开立的账户或者境内银行卡发生的大额交易，由开立账户的营业网点或者发卡法人机构报告；客户通过境外银行卡发生的大额交易，由收单机构报告；客户不通过账户或者银行卡发生的大额交易，由办理业务的金融机构报告。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十一条', '本行发现或者有合理理由怀疑客户、客户的资金或者其他资产、客户的交易或者试图进行的交易与洗钱、恐怖融资等犯罪活动相关的，不论所涉资金金额或者资产价值大小，应当提交可疑交易报告。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十二条', '根据本行情况定期向省联社提交交易监测标准，并对其有效性负责。可疑与恐怖融资交易监测标准包括并不限于客户的身份、行为，交易的资金来源、金额、频率、流向、性质等存在异常的情形，参考以下因素：（一）中国人民银行及其分支机构发布的反洗钱、反恐怖融资规定及指引、风险提示、洗钱类型分析报告和风险评估报告。（二）公安机关、司法机关发布的犯罪形势分析、风险提示、犯罪类型报告和工作报告。（三）本机构的资产规模、地域分布、业务特点、客户群体、交易特征，洗钱和恐怖融资风险评估结论。（四）中国人民银行及其分支机构出具的反洗钱监管意见。（五）中国人民银行要求关注的其他因素。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十三条', '省联社定期组织对交易监测标准进行评估，并根据评估结果完善交易监测标准。如发生突发情况或者应当关注的情况，将及时评估和完善交易监测标准。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十四条', '本行对通过交易监测标准筛选出的交易进行人工分析、识别，并记录分析过程；不作为可疑交易报告的，应记录分析排除的合理理由；确认为可疑交易的，应在可疑交易报告理由中完整记录对客户身份特征、交易特征或行为特征的分析过程。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十五条', '本行在可疑交易报告内部操作规程确认为可疑交易后，及时以电子方式提交可疑交易报告，最迟不超过5个工作日。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十六条', '对既属于大额交易又属于可疑交易的交易，应当分别提交大额交易报告和可疑交易报告。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十七条', '可疑交易符合下列情形之一的，应在向中国反洗钱监测分析中心提交可疑交易报告的同时，以电子形式或书面形式向省联社和所在地中国人民银行分支机构报告，并配合反洗钱调查：（一）明显涉嫌洗钱、恐怖融资等犯罪活动的。（二）严重危害国家安全或者影响社会稳定的。（三）其他情节严重或者情况紧急的情形。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第三章  可疑交易报告', '第十八条', '本行对下列恐怖活动组织及恐怖活动人员名单开展实时监测，有合理理由怀疑客户或者其交易对手、资金或者其他资产与名单相关的，应当在立即向中国反洗钱监测分析中心提交可疑交易报告的同时，以电子形式或书面形式向省联社和所在地中国人民银行分支机构报告，并按照相关主管部门的要求依法采取措施。（一）中国政府发布的或者要求执行的恐怖活动组织及恐怖活动人员名单。（二）联合国安理会决议中所列的恐怖活动组织及恐怖活动人员名单。（三）中国人民银行要求关注的其他涉嫌恐怖活动的组织及人员名单。恐怖活动组织及恐怖活动人员名单调整的，应当立即开展回溯性调查，并按前款规定提交可疑交易报告。法律、行政法规、规章对上述名单的监控另有规定的，从其规定。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第四章  内部管理控制', '第十九条', '本行根据本办法制定大额交易和可疑交易报告内部管理制度和操作规程，对本机构的大额交易和可疑交易报告工作做出统一要求。省联社对各机构大额交易和可疑交易报告制度的执行情况进行监督管理。大额交易和可疑交易报告制度应当向所在地的中国人民银行分支机构报备。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第四章  内部管理控制', '第二十条', '运营管理部为反洗钱工作牵头管理部门，设立专职的反洗钱岗位，配备不少于2名专职人员负责大额交易和可疑交易报告工作，并提供必要的资源保障和信息支持。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第四章  内部管理控制', '第二十一条', '省联社建立大额交易和可疑交易监测系统，供本行以客户为基本单位开展资金交易的监测分析，全面、完整、准确地采集各业务系统的客户身份信息和交易信息，保障大额交易和可疑交易监测分析的数据需求。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第四章  内部管理控制', '第二十二条', '本行按照完整准确、安全保密的原则，将大额交易和可疑交易报告、反映交易分析和内部处理情况的工作记录等资料自生成之日起至少保存5年。保存的信息资料涉及正在被反洗钱调查的可疑交易活动，且反洗钱调查工作在前款规定的最低保存期届满时仍未结束的，应将其保存至反洗钱调查工作结束。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第四章  内部管理控制', '第二十三条', '本行及其工作人员应当对依法履行大额交易和可疑交易报告义务获得的客户身份资料和交易信息，对依法监测、分析、报告可疑交易的有关情况予以保密，不得违反规定向任何单位和个人提供。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第五章  附 则', '第二十四条', '本办法所称非自然人，包括法人、其他组织和个体工商户。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第五章  附 则', '第二十五条', '本行应当按照中国人民银行大额交易和可疑交易报告要素要求，提供真实、完整、准确的交易信息，按省联社反洗钱相关系统要求进行报送。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第五章  附 则', '第二十六条', '本行报送的大额交易或者可疑交易报告内容要素不全或者存在错误，收到中国反洗钱监测分析中心发出的补正通知，应在接到补正通知的5个工作日内补正。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第五章  附 则', '第二十七条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《大额交易和可疑交易报告管理办法》', '第五章  附 则', '第二十八条', '本办法自2017年7月1日起执行。原《xxx银行大额、可疑和恐怖融资交易标准及交易报告管理办法》（xxx银行行发〔2014〕139号）同时废止。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第一章   总  则', '第一条', '为规范xxx银行（以下简称“本行”）大额支付系统的正常运行，杜绝差错，防范支付风险，根据中国人民银行《大额支付系统业务处理办法》及《江苏省农村信用社大额支付系统处理暂行办法》等法律法规的规定，结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第一章   总  则', '第二条', '经中国人民银行批准，本行作为间接参与者，通过江苏省农村信用社联合社（以下简称“省联社”）综合业务系统接入大额支付系统办理支付业务，通过省联社清算中心与中国人民银行大额支付系统连接，逐笔实时处理跨行贷记支付业务和查询查复业务。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第一章   总  则', '第三条', '大额支付系统运行方式为信息实时发送，款项实时到账，资金实时清算。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第一章   总  则', '第四条', '大额支付系统处理的支付业务，其信息从发起行发起，经发起清算行、发报中心、国家处理中心、收报中心、接收清算行，至接收行为止。（一）国家处理中心是中国人民银行负责大额支付系统的运行和管理，接收、转发各城市处理中心的支付指令，并对集中开设的清算账户进行资金清算和处理的机构。（二）发（收）报中心是中国人民银行大额支付系统中的城市节点，负责接收和转发支付指令，也称城市处理中心。（三）发起（接收）清算行是拥有中国人民银行批准的行号，在国家处理中心开设清算账户，向发报中心提交、从收报中心接收支付业务并进行账务处理的机构，其账户用于发起（接收）行和自身发起、接收支付业务的资金清算和账务处理，也称直接参与者。（四）发起（接收）行是拥有中国人民银行批准的行号，向发报中心提交、从收报中心接收支付业务并进行账务处理的参与机构，也称间接参与者。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第五条', '本行参加大额支付系统处理下列支付业务：（一）规定金额起点以上的跨行贷记支付业务；（二）规定金额起点以下的紧急跨行贷记支付业务；（三）综合业务系统内需要通过大额支付系统处理的贷记支付业务；（四）中国人民银行规定的其他支付清算业务。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第六条', '办理大额支付系统业务须符合中国人民银行规定的金额起点。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第七条', '加强人员管理，保持人员的相对稳定，对新上岗的人员必须进行岗前培训，试用期内员工一律不得担任大额支付业务的任何岗位。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第八条', '办理大额支付业务，须设置录入员、复核员，相互之间必须明确分工，各司其职，复核员是大额支付业务的主要责任人。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第九条', '大额往账业务实行分级授权管理。授权人按照规定的授权额度对大额往账业务进行授权，不得越级。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第十条', '本行清算团队在省联社的清算账户也须按照省联社规定留足备付金，以保证大额支付系统往来的资金清算。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第十一条', '工作时间大额支付系统运行工作日及工作时间按照中国人民银行规定执行。（一） 各支行（部）在规定的工作时间内应及时处理大额支付业务，不得积压集中处理。（二）各支行（部）的日终、年终业务处理，必须在中国人民银行大额支付系统日终、年终处理完成以后，方可进行。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第二章   基本规定', '第十二条', '结算收费凡通过大额支付系统办理的支付业务，应严格按照《江苏省农村信用社联合社中间业务收费指引》有关规定进行收费，并将收入纳入收费专户核算。对无结算账户的现金汇款，必须另按照现金汇款的标准收取手续费。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第三章   会计科目、凭证及报表', '第十三条', '科目及账户设置（一） 在“社（行）内往来”科目下设立大额支付资金清算账户，核算支付业务往来资金的清算。（二） 在“汇出汇款”科目下设立二级账户，核算因收款人账号或户名不符而挂账的来账。（三）在“其他应付款”科目下设立手续费户，核算在大额支付系统中办理各项业务的收费。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第三章   会计科目、凭证及报表', '第十四条', '大额支付系统凭证包括记账凭证、支付系统专用凭证、查询查复书和自由格式报文。所有来账凭证，都使用“江苏省农村信用社支付系统专用凭证”，并作为重要空白凭证进行管理。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第四章   往账与来账', '第十五条', '大额支付业务严格遵循“谁发出，谁负责”的原则。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第四章   往账与来账', '第十六条', '往账业务处理（一）往账业务采用“先录入后复核、双人操作”的模式。（二）录入员根据原始凭证输入往账信息；复核员根据原始凭证输入复核信息，并执行发出。（三）超过规定限额的往账业务必须授权。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第四章   往账与来账', '第十七条', '来账业务处理（一）对状态为“已挂账”的来账，必须逐笔认真审查来账凭证，检查款项是否已转入“汇出汇款”，并及时作相应的业务处理。（二）对状态为“已入账”的来账，接收行当日无需做账务处理，只须在当日或次日打印来账凭证，第一联作记账凭证，第二联作客户入账通知。（三）来账报单需退汇处理时必须经支行（部）运营主管审批。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第五章   差错及异常处理', '第十八条', '基本规定（一）差错处理必须坚持“更改有据”的原则，对有差错或有疑问的报单应及时处理，不得拖延。（二）对发现差错或有疑问的大额支付业务应通过本系统办理查询查复。（三）对金额、币种、业务种类有误的来账报单，接收行不得代为更正，必须做退汇处理。（四）差错报单应当日处理，最迟不得超过第二个工作日。（五）对往账异常状态的报单必须在当日及时处理。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第五章   差错及异常处理', '第十九条', '往账差错处理（一）未确认的往账业务发现差错时，应由原录入员根据原始凭证进行修改或删除。（二）已清算的往账业务发现差错时，经支行（部）运营主管审批后，及时向接收行发出查询，请其代为更正、退回；或由原复核员通过大额支付系统向接收行发送“退回申请”，如收到接收行“拒绝退回”的答复，应立即与接收行协商解决。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第五章   差错及异常处理', '第二十条', '来账差错处理（一）对有疑问的来账报单，经运营主管审核后，应在当日至迟下一工作日上午10时发出查询，待收到查复后按查复书的要求进行处理。（二）对已自动入客户账的来账报单差错，须由收款人签发支付凭证后，方可办退汇。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第六章   查询查复', '第二十一条', '基本规定（一）严格遵循“有疑必查、有查必复、复必详尽、切实处理”的原则，做到用语文明、内容详尽、有根有据。（二）对金额、币种、业务种类的差错不得通过查询查复进行更正。（三）对有疑问或发生差错的大额支付业务须于当日通过本系统发出查询查复，最迟不得超过下一工作日上午10时。   （四）发出或收到的查询、查复书必须经支行（部）运营主管审核。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第六章   查询查复', '第二十二条', '发出查询收到查复（一）收到有疑问的来账或发现“已清算”状态的差错往账时，应主动、及时发出查询。（二）发出查询收到查复后，与原发出的查询书及原始凭证核对无误后，按查复书要求及时进行相关处理。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第六章   查询查复', '第二十三条', '收到查询发出查复（一）收到往账报单查询时，须与原始凭证认真核对，若属经办员操作差错引起的，按原始凭证的内容及时发出查复；若属客户差错引起的，须由客户提出书面申请后，按客户要求发出查复。（二）收到要求更正来账报单错误信息的查询时，除不得更正的事项外，应按查询书要求予以更正，并将更正情况发出查复。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第七章   档案管理', '第二十四条', '大额支付业务的凭证、业务清单、查询查复书根据以下规定进行收集、装订和保管。（一）记账凭证、来账凭证放入当天传票。（二）发出查询书与收到查复书配对，按发出查询书时间顺序专夹保管；收到查询书与发出查复书配对，按发出查复书时间顺序专夹保管，定期按月（季、年）装订。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第八章   业务核对与监督', '第二十五条', '支行（部）在日终签退前检查“汇出汇款”的余额是否为零，若有余额是否与未处理的来账挂账汇总额相符。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第八章   业务核对与监督', '第二十六条', '大额支付系统采取运营主管负责制。支行（部）运营主管每日对大额支付业务流程进行检查。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第八章   业务核对与监督', '第二十七条', '对大额支付系统执行责任追究制。因违规操作导致资金损失的，全额赔偿。授权范围内复核员负主要责任，授权人、录入员负次要责任。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第九章   附  则', '第二十八条', '本办法由xxx银行运营管理部负责制订、解释和修改。'),
(NULL, '运营管理部', '《大额支付系统业务管理办法》', '第九章   附  则', '第二十九条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第一章   总 则', '第一条', '为加强xxx银行（以下简称“本行）挂失业务管理，有效控制风险，规范挂失业务操作，根据《中华人民共和国票据法》、《储蓄管理条例》等法律法规规定，结合本行实际，特制定本管理办法。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第一章   总 则', '第二条', '存款挂失是指存款人因各种原因遗失存款单（折、卡）、预留印鉴和遗忘存款预留密码，要求银行将有关存款账户止付，并按规定手续补发存单（折、卡）、更换印鉴、密码重置或支取存款。 挂失的方式分为正式挂失、密码挂失、临时挂失。正式挂失是客户在遗失存款单（折、卡）、预留印鉴后，持本人有效身份证，并提供存款人姓名、开户时间、存款种类、金额、账号、住址等相关情况，书面向原开户网点机构正式申请挂失止付，补领新存单(折、卡)、更换印鉴或支取款项。　　密码挂失是客户在遗忘存款凭证预留密码的情况下，持本人有效身份证件，书面向营业机构正式申请密码挂失，重置密码并解除止付。临时挂失，是指在特殊情况下通过电话或函电形式申请挂失。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第一章   总 则', '第四条', '本办法适用于本行辖内各支行（部）办理的挂失业务。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第五条', '客户办理挂失，对公客户应持客户开户证件、单位公函和经办人身份证，个人储蓄的持本人有效身份证件，并提供姓名、存款时间、存款种类、金额、账号及住址等有关情况，代理挂失须同时提供代理人的身份证件。在特殊情况下，客户可以用口头或函电形式申请挂失，但必须在五天内补办书面申请挂失手续。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第六条', '各支行（部）经办人员根据客户提供的资料，确认存款未被支取和未被冻结止付后，方可受理申请。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第七条', '各支行（部）在受理挂失申请前该客户账户内的存款已被他人支取的，各支行（部）不负赔偿责任。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第八条', '不记名式的存单、存折挂失，不予受理。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第九条', '预留印鉴全部或其中印章遗失，单位应出具公函，属个人的提供身份证件向各开户行（部）申请更换印鉴。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第十条', '客户因财产纠纷挂失存款不予受理。存款人死亡，继承人申请挂失时，必须持公证部门或人民法院签发的财产继承权证明书以及本人身份证件，经确认后方可办理挂失手续。挂失申请书及挂失登记簿上必须注明财产继承权证明书的签发单位及号码。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第十一条', '正式挂失须到本行任一营业网点办理，不通兑的账户需到原开户机构办理。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第十二条', '各支行（部）办理印鉴挂失业务期满后，在结清挂失业务前，须经运营主管对挂失资料审核后方可办理。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第二章   基本规定', '第十二条', '各支行（部）对挂失申请书及挂失登记簿等业务档案，作为永久档案保管。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第一条', '存款书面挂失'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第十三条', '客户遗失存单（折、卡），必须立即持本人身份证件及复印件，到我行任一网点办理书面申请挂失，不通兑的账户到原开户机构办理，填写一式三联“挂失/解挂申请书”，提供客户姓名、开户时间、种类、金额、账号及住址等有关情况。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第十四条', '客户本人不能前往办理，可以委托他人代为办理挂失手续，被委托人除持存款人有效身份证件及复印件外，还必须出示其有效身份证件及复印件。但挂失后申请办理补领新存单（折）或支取存款手续时，只能由原存款人办理而不能代理（未成年人或丧失民事能力者，可由其监护人代理）。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第十五条', '各支行（部）经办人员在办理存单（折、卡）正式挂失时，必须通过公民身份联网核查系统查验客户的身份证件，打印核查结果，留存身份证复印件；如委托他人办理，还要同时通过公民身份联网核查系统查验代办人的身份证件，打印核查结果，留存身份证件复印件。核对客户填写的“挂失/解挂申请书”中列明的户名、存款金额、种类、账号、原存款日期及挂失日期等事项，如果挂失存单（折）是凭印鉴支取的，要由客户加盖预留印鉴，并在挂失申请书上注明挂失人所在单位详细地址和身份证件号码。凭印鉴支取者，在挂失申请书上加盖原预留印鉴；凭密码支取者，则由客户输入密码。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第十六条', '各支行（部）经办员人员审核挂失申请书上账号、户名与综合业务系统中的账号、户名是否相符，金额是否一致。审核无误后在综合业务系统中录入相关要素，办理挂失，打印挂失受理结果，交客户核对签章。如是客户本人办理的可即时办理挂失处理，打印处理结果交客户核对签章，在挂失申请书上加盖业务章，登记挂失登记簿，在挂失登记簿上详细登记有关要素和处理结果，并将挂失申请书的留存联、身份证复印件和核查结果打印单等粘贴在登记簿上永久保管；挂失申请书监管部门留存联和客户留存联均作为传票的附件送事后监督审核。如是代理挂失，则必须待客户本人到场后才能办理处理结果，处理结果交客户本人核对后签章连同客户留存联送事后监督审核。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第十七条', '各支行（部）办理存单（折、卡）挂失，收取挂失手续费5元。各支行（部）办理挂失补发IC卡另收取补卡工本费20元，首次领取IC卡的免费。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第二条', '印鉴挂失'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第十八条', '客户遗失印鉴要求挂失时，单位应出示单位公函、开户证件和经办人有效身份证件，个人应出具存款凭证和本人有效身份证件向存款开户行办理，填写挂失申请书，提供账号、户名，开户日期、证件种类，并注明原印鉴形状及字体等情况，各支行（部）经办人核实相关要素无误后方可受理。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第十九条', '各支行（部）经办人员接到申请书后送运营主管审核同意后，在原印鉴卡上用红笔注明，“××年×月×日印鉴挂失”字样，登记挂失登记簿。在挂失申请书、挂失登记簿、存单（折）上注明“原印鉴挂失作废”，“新印鉴自×年×月×日起用”字样，然后重新办理预留印鉴手续。挂失申请书网点留存联和相关证件及核查打印件粘贴在挂失登记簿，挂失申请书监管部门留存联作为传票的附件送事后监督审核。挂失满十天后，抽出旧印鉴卡和申请书客户回单联放入当天传票。印鉴挂失收取手续费5元。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第三条', '票据挂失　　第二十条  已承兑的商业汇票、银行承兑汇票、支票、填明“现金”字样和代理付款人的银行汇票以及填明“现金”字样的银行本票因焚烧、磨损、污损而完全灭失或因遗失、被盗、被抢等原因失去票据占有的情形，可由失票人通知付款人或者代理付款人进行挂失止付。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第二十一条', '各支行（部）收到挂失止付通知书后，查明挂失票据确未付款时，应立即暂停支付。自收到挂失止付通知书之日起12日内没有收到人民法院的止付通知书的，自第13日起，持票人提示付款并依法向持票人付款的，不再承担责任。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第二十二条', '票据具有有价凭证和无因凭证的特点，持票人在发现票据丧失后，应在第一时间通知票据的付款人或代理付款人挂失止付，以防止恶意取得者非法行使票据权利。挂失止付应填写挂失止付通知书并签章。挂失止付仅是失票人向付款人请求暂时停止支付票面款项，失票人还应在挂失止付后3日内，向出票行当地基层人民法院申请公示催告或提起诉讼。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第二十三条', '各支行（部）在收到挂失止付通知书之前，已经向持票人付款的，不再承担责任。但是，以恶意或重大过失付款的除外。票据挂失后不能再办理解除挂失手续，空白支票或未写收款人名称的支票，支票付款行不予受理。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第四条', '密码挂失'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第二十四条', '客户忘记密码要求挂失时，单位应出示单位公函、开户证件和经办人有效身份证件，个人应出具存款凭证和本人有效身份证件到本行任一网点办理。填写挂失申请书。密码挂失不得委托他人代办。　　第二十五条  各支行（部）经办人员接到申请书后，审核客户填写的要素及证件无误后，在综合系统录入相关要素后打印挂失受理结果，交客户核对签章。同时进行客户密码重置，打印处理结果交客户核对签章后，加盖办讫章，登记挂失登记簿。将挂失申请书网点留存联和相关证件及核查打印件粘贴在挂失登记簿，挂失申请书监管部门留存联和客户回单联均作为传票的附件送事后监督审核。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第二十六条', '密码挂失不收手续费。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第五条', '临时挂失　　第二十七条  客户因特殊原因，只能用口头或电话、电报、信函等形式挂失时，可临时挂失。经办人员根据客户提供的情况，在综合业务系统中核对，确认存款尚未被支取后，在综合业务系统中做挂失止付程序办理。　　第二十八条  客户必须在办理口头挂失后的五天之内，到本行任一网点办理正式挂失手续，不通兑的账户需到原开户机构办理，否则挂失失效。　  第二十九条  各支行（部）经办员要做好临时挂失登记记录，并及时做好综合业务系统中口头挂失处理。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第三十条', '口头挂失不收手续费。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第六条', '撤销挂失　　第三十一条  客户办理挂失后，找到了原存单（折）、卡、预留印鉴的印章，可以要求撤销挂失。　　第三十二条  撤销挂失需由客户本人或原代办人持有效身份证件及原挂失申请书的回单联，到原挂失的开户行办理。　　第三十三条  各支行（部）收回客户的挂失申请书，并要求客户在银行挂失申请书的留底联批注“×年×月×日撤销挂失”字样和签名。　　第三十四条  各支行（部）运营主管审核无误后，授权经办人员在综合业务系统中解除挂失冻结，并在挂失登记簿处理栏内注明“撤销挂失”字样。　　第三十五条  如系撤销非正式挂失，应在挂失临时止付记录单上加盖“注销”戳记。　　第三十六条  挂失人用函电要求撤销挂失申请的，各支行（部）不予办理。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第三章  挂失业务的操作规程', '第三十七条', '撤销挂失，已收的挂失手续费不退还客户。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第四章  附  则', '第三十九条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《存款挂失业务管理办法》', '第四章  附  则', '第四十条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第一条', '个人金融信息，是指银行业金融机构在开展业务时，或通过接入中国人民银行征信系统、支付系统以及其他系统获取、加工和保存的以下个人信息：（一）个人身份信息，包括个人姓名、性别、国籍、民族、身份证件种类号码及有效期限、职业、联系方式、婚姻状况、家庭状况、住所或工作单位地址及照片等；（二）个人财产信息，包括个人收入状况、拥有的不动产状况、拥有的车辆状况、纳税额、公积金缴存金额等；（三）个人账户信息，包括账号、账户开立时间、开户行、账户余额、账户交易情况等；（四）个人信用信息，包括信用卡还款情况、贷款偿还情况以及个人在经济活动中形成的，能够反映其信用状况的其他信息；（五）个人金融交易信息，包括银行业金融机构在支付结算、理财、保险箱等中间业务过程中获取、保存、留存的个人信息和客户在通过银行业金融机构与保险公司、证券公司、基金公司、期货公司等第三方机构发生业务关系时产生的个人信息等；（六）衍生信息，包括个人消费习惯、投资意愿等对原始信息进行处理、分析所形成的反映特定个人某些情况的信息；（七）在与个人建立业务关系过程中获取、保存的其他个人信息。'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第二条', '柜员上岗前必须签定客户个人信息保密承诺，严格遵守以下规定：1、严禁出售个人金融信息；2、严禁向本行以外的其他机构和个人提供个人金融信息，但为个人办理相关业务所必须并经个人书面授权或同意的以及法律法规和人民银行另有规定的除外；3、严格人民银行征信系统的用户和密码管理，严禁非制定进入查询；4、指定人员在查询个人金融信息时做好登记并备案；5、严禁以身份核查、核实系统、支付系统以及其他系统获取、加工和保存个人信息。'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第三条', '客户个人信息的存放应安全、有效，符合以下存放要求：客户个人信息应遵循“实时查询、实时处理、用后清理、不留后患”的原则，不得将客户个人信息乱查乱放，不得将客户个人信息存放在以下设备中：1、客户个人信息不得存放在私人存储设备（硬盘、U盘、光盘等介质）中；2、客户个人信息未经有权人同意的，不得存放在公共存储设备中；3、客户个人信息不得存放在网络邮箱、网盘及其他网络设备中。'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第四条', '客户个人信息的查询、使用应设置权限，严格按权限进行：1、客户个人信息应保存在计算机中心的数据库中，计算机维护人员不经有权人员同意不得向外复制；2、各部门查询客户个人信息不得复制留存，如确需下载，必须经分管领导同意；3、支行行长可以查询本支行的客户信息，不得查询他行的客户信息资料；4、普通人员只能查询本人客户经理号下的客户信息；5、有权人员使用各类系统查询客户信息时，不经相关部门同意，不得保存、外传；'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第五条', '客户个人信息各类档案必须列为内部人员调动、变动的移交资料，不得遗失。'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第六条', '客户个人信息资料保管不善的处罚：1、客户个人信息资料随意存放在私人计算机及存储器、公共文件夹内，未产生不良影响的，一经查实，计扣相关责任人2分；2、客户个人信息资料随意存放在私人计算机及存储器、公共文件夹内，产生一定不良影响的，一经查实，计扣相关责任人10分；3、客户个人信息资料随意存放在私人计算机及存储器、公共文件夹内，产生严重不良影响的，一经查实，计扣相关责任人30分；4、私自出售客户个人信息，产生严重不良影响的，将依法移交司法机关处理。'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第七条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《客户个人金融信息管理办法》', '', '第八条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第一章  总  则', '第一条', '为了加强xxx银行(以下简称“本行”)客户印鉴日常管理，防范金融风险发生，确保客户资金安全，根据《人民币银行结算账户管理办法》等法律法规，结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第一章  总  则', '第二条', '本办法明确了客户印鉴的预留、保管、使用、更换、挂失、处置等日常管理的内容和要求。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第一章  总  则', '第三条', '本办法适用于本行客户印鉴的管理。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第一章  总  则', '第四条', '本办法所称预留印鉴，是指单位汇款、（购买）开出支票、开立汇票及办理其他需要使用预留印鉴业务时，单位存款人预留给开户银行的公章或财务专用章加其法定代表人或负责人的签章。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第二章  职责与权限', '第五条', '总行运营管理部负责对基层网点印鉴卡的管理工作进行检查辅导，总行清算团队指定专人负责新开客户印鉴的日常管理和扫描存档工作。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第二章  职责与权限', '第六条', '各支行（部）负责客户印鉴的预留、保管、使用、处置等日常管理工作。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第三章  内容与要求', '第七条', '印鉴的预留要求（一）客户申请开户预留印鉴时，应在《印鉴卡》上加盖印鉴。预留印鉴卡一式三份，一份交清算团队、一份交运营主管保管、一份交客户；（二）印鉴卡片上预留印鉴必须加盖清楚，不能盖在印鉴卡的边框或其它字迹上，印章之间笔画不能相互交叉。同时，在印鉴卡上将客户名称、账号、地址、联系人、电话、账户性质、启用日期等要素填写齐全，并加盖经办人名章；（三）印鉴要求。单位预留印鉴必须使用红色印泥。预留印鉴应该字迹、边框清晰、完整，无断笔或丢笔，印油均匀，无杂质。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第三章  内容与要求', '第八条', '印鉴卡片的管理要求（一）专人保管支行、部必须强化印鉴卡片的管理工作，印鉴卡片的管理人员必须业务过硬、责任心强、思想品德好，负责印鉴卡片的日常保管工作。若遇人员调整，还要办理好印鉴卡片的交接工作，同时登记《交接登记簿》。除运营主管授权外，其他人员不得调阅复印印鉴卡片，任何人不得将印鉴卡片带离营业场所。（二）专夹保管所有印鉴卡片必须按照科目、账号进行分类，放在专夹或专用印鉴册中保管，而且易于查找。（三）入柜保管印鉴管理人员在收到客户的预留印鉴片后，必须入柜保管。临时离岗应加锁保管；中午休息或营业终了，印鉴管理人员必须将经管的印鉴卡片入库上锁保管，严禁印鉴卡片随意丢放，形成风险隐患。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第四章  管理流程', '第九条', '印鉴的使用（一）账户的开户（印鉴的录入）：在账号栏输入账号、开户日期、启用日期、账户类型、通存通兑类型。分六个步骤：确定印鉴属性、放置印鉴卡、调整区域、拍摄印鉴、调整图像、存入数据库；（二）柜员受理客户业务需核对印鉴时，应使用验印系统进行印鉴核对，对验证不通过的，采用手工折角方式核对印鉴，严禁直接人工通过或不核对印鉴；（三）采用手工核对印鉴，核对时要采用目测和折角验印相结合的方法，目测印鉴的形状、文字和字体，折角验印应核对重叠是否一致。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第四章  管理流程', '第十条', '预留印鉴的更换印鉴更换可分为正常变更和遗失变更。显示账户信息按变更资料后可进行账户基本资料的修改操作。（一）正常变更单位存款人申请正常变更时应向本行提供印鉴变更申请书、原预留印鉴卡片、原预留印鉴和新预留印鉴。应提交正式公函或证明，写明更换原因，新印鉴启用日期等（原则上新印鉴启用日期应为印鉴更换日的三个工作日之后），并在新印鉴背面加盖原预留印鉴（右上角加盖公章），正面加盖与原预留印鉴有明显区别的新印鉴。经运营主管审核同意后办理更换手续并录入影像系统内。 1、单位存款人申请变更预留公章或财务专用章，可由法定代表人或负责人直接办理，也可授权他人办理。由法定代表人或单位负责人直接办理的，除出具相应的证明文件外，还应出具法定代表人或单位负责人的身份证件；授权他人办理的，除出具相应的证明文件外，还应出具法定代表人或单位负责人的身份证件及其出具的授权书，以及被授权人的身份证件。 2、单位存款人申请更换预留个人签章，可由法定代表人或单位负责人直接办理，也可授权他人办理。由法定代表人或单位负责人直接办理的，应出具加盖该单位公章的书面申请以及法定代表人或单位负责人的身份证件；授权他人办理的，应出具加盖该单位公章的书面申请、法定代表人或单位负责人的身份证件及其出具的授权书、被授权人的身份证件。（二） 遗失变更（挂失）1、单位存款人申请遗失变更时如无法提供原预留印章，应向本行出具原印鉴卡片、开户许可证、营业执照正本、司法部门的证明等相关证明文件和印鉴挂失申请书；2、单位存款人申请遗失变更时无法提供原预留财务专用章但可以提供公章的，则应向本行出具原印鉴卡片、开户许可证、营业执照正本、加盖单位公章的证明等相关证明文件；3、单位存款人申请遗失变更时无法提供原预留财务专用章且无法提供公章的，则应向本行出具原印鉴卡片、开户许可证、营业执照正本、司法证明（对单位遗失公章的应先登报申明作废）等相关证明文件；4、单位存款人申请变更预留公章或财务专用章，可由法定代表人或单位负责人直接办理，也可授权他人办理。由法定代表人或单位负责人直接办理的，除出具相应的证明文件外，还应出具法定代表人或单位负责人的身份证件；授权他人办理的，除出具相应的证明文件外，还应出具法定代表人或单位负责人的身份证件及其出具的授权书，以及被授权人的身份证件；5、单位存款人申请更换预留个人签章，可由法定代表人或单位负责人直接办理，也可授权他人办理；由法定代表人或单位负责人直接办理的，应出具加盖该单位公章的书面申请以及法定代表人或单位负责人的身份证件； 授权他人办理的，应出具加盖该单位公章的书面申请、法定代表人或单位负责人的身份证件及其出具的授权书、被授权人的身份证件；无法出具法定代表人或单位负责人的身份证件的，应出具加盖该单位公章的书面申请、该单位出具的授权书以及被授权人的身份证件。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第四章  管理流程', '第十一条', '个人银行结算账户印鉴更换（一）个人遗失或更换预留个人印章或更换签字人，应向本行出具经其本人签名确认的书面申请，以及原预留印章或签字人的个人身份证件。个人银行结算账户预留印章变更必须本人办理。（二）柜员收到客户更换印鉴的申请手续后，应要求客户在印鉴卡的正面预留新印鉴，背面预留旧印鉴，印鉴卡一式三份，一份交客户、一份交给运营主管保管、一份在新印鉴启用之日连同客户更换印鉴申请及所有原旧印鉴片一同附于当日该客户办理业务凭证后作为凭证的附件，若新印鉴启用之日该户未发生业务，可附于当日所有业务凭证的最后,交后台人员审核。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第四章  管理流程', '第十二条', '印鉴的遗失变更，开户单位应提交正式公函及证明，写明挂失的原因和理由等，经运营主管审批同意，一周后启用新印鉴。但公函或证明中要注明由此引起的后果要由存款单位自行负责。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第四章  管理流程', '第十三条', '预留印鉴卡片的注销开户单位申请注销账户时，应交还预留印鉴卡片，柜员在注销账户业务处理完毕后，应在所有（包括交还的，银行保管的）旧印鉴卡片正面注明注销日期，随当日做最后一笔支款凭证的附件随传票上报事后监督团队，并在验印系统中注销印鉴。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第五章  附 则', '第十四条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《客户印鉴管理办法》', '第五章  附 则', '第十五条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第一章  总 则', '第一条', '为了预防洗钱和恐怖融资活动，规范xxx银行（以下简称“本行”）客户身份识别、客户身份资料和交易记录保存行为，维护本行反洗钱秩序，根据《中华人民共和国反洗钱法》、《中国人民银行关于改进个人银行账户服务加强账户管理的通知》等法律、行政法规的规定，制定本办法。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第一章  总 则', '第二条', '本办法适用于本行及所辖各支行（部）。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第一章  总 则', '第三条', '各支行（部）应当勤勉尽责，建立健全和执行客户身份识别制度，遵循“了解你的客户”的原则，针对具有不同洗钱或者恐怖融资风险特征的客户、业务关系或者交易，采取相应的措施，了解客户及其交易目的和交易性质，了解实际控制客户的自然人和交易的实际受益人。各支行（部）应当按照安全、准确、完整、保密的原则，妥善保存客户身份资料和交易记录，确保能足以重现每项交易，以提供识别客户身份、监测分析交易情况、调查可疑交易活动和查处洗钱案件所需的信息。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第一章  总 则', '第四条', '本行应当根据人行及省联社制度规定，建立健全客户身份识别、客户身份资料和交易记录保存等方面的内部操作流程，指定专人负责反洗钱和反恐怖融资合规管理工作，合理设计业务流程和操作规范，并定期进行内部审计，评估内部操作规程是否健全、有效，及时修改和完善相关制度。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第一章  总 则', '第五条', '本行对各支行（部）执行客户身份识别制度、客户身份资料和交易记录保存制度的执行情况进行监督管理。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第一章  总 则', '第六条', '与境外金融机构建立代理行或者类似业务关系时，应当充分收集有关境外金融机构业务、声誉、内部控制、接受监管等方面的信息，评估境外金融机构接受反洗钱监管的情况和反洗钱、反恐怖融资措施的健全性和有效性，以书面方式明确本金融机构与境外金融机构在客户身份识别、客户身份资料和交易记录保存方面的职责。本行与境外金融机构建立代理行或者类似业务关系应当经反洗钱领导小组的批准，并报省联社备案。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第七条', '各支行（部）在以开立账户等方式与客户建立业务关系，为不在本机构开立账户的客户提供现金汇款、现钞兑换、票据兑付等一次性金融服务且交易金额单笔或累计人民币1万元以上或者外币等值1000美元以上的，以及为客户开办保管箱业务时，必须识别客户身份，了解实际控制客户的自然人和交易的实际受益人，自然人客户核对其有效身份证件，公司客户核对其《营业执照》等证明文件，登记客户身份基本信息，并留存有效身份证件或者《营业执照》等证明文件复印件或者影印件（含核查记录电子档）。如客户为外国政要，本行为其开立账户应当经高级管理层的批准。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第八条', '个人银行账户存款人有效证件：（一）有效实名身份证件 1.在中华人民共和国境内已登记常住户口的中国公民为居民身份证；不满16周岁的，可以使用居民身份证或户口簿。2.香港、澳门特别行政区居民为港澳居民往来内地通行证。3.台湾地区居民为台湾居民来往大陆通行证。4.定居国外的中国公民为中国护照。5.外国公民为护照或外国人永久居留证（外人边民，按照边贸结算的有关规定办理）。6.法律、行政法规规定的其他身份证明文件。（二）辅助身份证明材料包括不限于1.中国公民为户口簿、护照、机动车驾驶证、居住证、社会保障卡、军人和武装警察身份证件、公安机关出具的户籍证明、工作证。2.香港、澳门特别行政区居民为香港、澳门特别行政区居民身份证。3.台湾地区居民为在台湾居住的有效身份证明。4.定居国外的中国公民为定居国外的证明文件。5.外国公民为外国居民身份证、使领馆人员身份证件或者机动车驾驶证等其他带有照片的身份证件。6.完税证明、水电煤缴费单等税费凭证。军人、武装警察尚未领取居民身份证的，除出具军人和武装警察身份证件外，还应出具军人保障卡或所在单位开具的尚未领取居民身份证的证明材料。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第九条', '为自然人客户办理人民币单笔或累计5万元以上或者外币等值1万美元以上现金存取业务的，必须核对客户的有效身份证件或者其他身份证明文件。业务办理过程中，如发现异常情况，本行要进一步通过联网核查公民身份信息系统对客户身份进行核查。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十条', '提供保管箱服务时，应了解保管箱的实际使用人。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十一条', '为客户向境外汇出资金时，应当登记汇款人的姓名或者名称、账号、住所和收款人的姓名、住所、联系方式等信息，在汇兑凭证或者相关信息系统中留存上述信息，并向接收汇款的境外机构提供汇款人的姓名或者名称、账号、住所和收款人的姓名、住所等信息。汇款人没有在本行开户，本行无法登记汇款人账号的，可登记并向接收汇款的境外机构提供其他相关信息，确保该笔交易的可跟踪稽核。境外收款人住所不明确的，本行可登记接收汇款的境外机构所在地名称。接收境外汇入款的支行，发现汇款人姓名或者名称、汇款人账号和汇款人住所三项信息中任何一项缺失的，应要求境外机构补充。如汇款人没有在办理汇出业务的境外机构开立账户，接收汇款的境内支行无法登记汇款人账号的，可登记其他相关信息，确保该笔交易的可跟踪稽核。境外汇款人住所不明确的，金融机构可登记资金汇出地名称。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十二条', '本行及各支行（部）利用电话、网络、自动柜员机、自助设备以及其他方式为客户提供非柜台方式的服务时，应实行严格的身份认证措施，采取相应的技术保障手段，强化内部管理程序，识别客户身份。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十三条', '本行及各支行（部）应按照客户的特点或者账户的属性，并考虑客户特性、地域、业务（含金融产品、金融服务）、行业（含职业）等因素，划分风险等级，并在持续关注的基础上，适时调整风险等级。本行及各支行（部）应当根据本地实际情况将客户风险等级划分为5级，即：高风险、较高风险、一般风险、较低风险、低风险。定期审核保存的客户基本信息，划分为高风险的客户每隔0.5年进行一次审查，划分为较高风险的客户每隔1年进行一次审查，划分为一般风险的客户每隔1.5年进行一次审查，划分为较低风险的客户每隔2年进行一次审查，划分为低风险的客户每隔3年进行一次审查，根据审查情况及时、恰当调整客户风险等级。对于首次建立业务关系的客户，无论其初评风险等级高低，均应在初次确定其风险等级后的三年内至少进行一次审核。本行及各支行（部）可借助省联社新版反洗钱系统进行客户风险等级的划分。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十四条', '在与客户的业务关系存续期间，各支行（部）应当采取持续的客户身份识别措施，关注客户及其日常经营活动、金融交易情况，及时提示客户更新资料信息。对于高风险客户，各支行（部）应当了解其资金来源、资金用途、经济状况或者经营状况等信息，加强其金融交易活动的监测分析。客户为外国政要的，本行应采取措施了解其资金来源和用途。客户先前提交的身份证件或者《营业执照》等证明文件已过有效期的，开户机构应发出公告，要求在公告之日起一个月内到我行营业网点办理更新手续，客户没有在一个月内办理更新手续且没有提出合理理由的，各支行（部）应中止为其办理业务。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十五条', '采取合理方式确认代理关系的存在，在按照本办法的有关要求对被代理人采取客户身份识别措施时，应当核对代理人的有效身份证件或者身份证明文件，登记代理人的姓名或者名称、联系方式、身份证件或者身份证明文件的种类、号码。当自然人客户由他人代理存取现金时，应按照以下要求开展客户身份识别工作：当单笔或累计存（或取）款的金额达到或超过人民币5万元或者外币等值1万美元时，应同时核对存款人（或取款人）和户主的有效身份证件或者身份证明文件，并登记存款人（或取款人）的姓名、联系方式以及身份证件或者身份证明文件的种类、号码。如果存款人因合理理由无法提供户主有效身份证件或者身份证明文件，且单笔或累计存款金额达到或超过人民币1万元或者外币等值1000美元的现金时，本行参照人民银行《金融机构客户身份识别和客户身份资料及交易记录保存管理办法》第七条有关银行业金融机构在提供一定金额以上一次性金融服务时履行客户身份识别的要求，对存款人开展相关客户身份识别工作。业务办理过程中，如发现异常情况，本行须进一步通过联网核查公民身份信息系统对客户身份进行核查。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十六条', '本行了解或者应当了解客户的资金或者财产属于信托财产的，应当识别信托关系当事人的身份，登记信托人、受益人的姓名或者名称、联系方式。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十七条', '出现以下情况时，各支行（部）应当重新识别客户：（一）客户要求变更姓名或者名称、身份证件或者身份证明文件种类、身份证件号码、注册资本、经营范围、法定代表人或者负责人的。（二）客户行为或者交易情况出现异常的。（三）客户姓名或者名称与国务院有关部门、机构和司法机关依法要求本行协查或者关注的犯罪嫌疑人、洗钱和恐怖融资分子的姓名或者名称相同的或被列入黑名单的客户。（四）客户有洗钱、恐怖融资活动嫌疑的或有可疑交易报送的客户。（五）本行获得的客户信息与先前已经掌握的相关信息存在不一致或者相互矛盾的。（六）先前获得的客户身份资料的真实性、有效性、完整性存在疑点的。（七）客户先前提交的身份证件或者《营业执照》等证明文件已过有效期的。（八）客户风险等级为高风险客户。（九）本行认为应重新识别客户身份的其他情形。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十八条', '各支行（部）除核对有效身份证件或者《营业执照》等证明文件外，可以采取以下的一种或者几种措施，识别或者重新识别客户身份：（一）要求客户补充其他能证明其身份信息的辅助身份资料或者身份证明文件。（二）回访客户。（三）实地查访。（四）向公安、工商行政管理等部门核实。（五）其他可依法采取的措施。各支行（部）履行客户身份识别义务时，按照法律、法规或部门规章的规定核对相关自然人的居民身份证的，应通过中国人民银行建立的联网核查公民身份信息系统进行核查。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第十九条', '本行委托其他金融机构向客户销售金融产品时，应在委托协议中明确双方在识别客户身份方面的职责，相互间提供必要的协助，相应采取有效的客户身份识别措施。符合下列条件时，根据可信赖受托金融机构所提供的客户身份识别结果，不再重复进行已完成的客户身份识别程序，但仍应承担未履行客户身份识别义务的责任：（一）销售金融产品的金融机构采取的客户身份识别措施符合反洗钱法律、行政法规和本办法的要求。（二）金融机构能够有效获得并保存客户身份资料信息。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第二十条', '本行委托本行以外的第三方识别客户身份的，须提前向省联社报备，并符合下列要求：（一）能够证明第三方按反洗钱法律、行政法规和本办法的要求，采取了客户身份识别和身份资料保存的必要措施。（二）第三方为本行提供客户信息，不存在法律制度、技术等方面的障碍。（三）在办理业务时，能立即获得第三方提供的客户信息，还可在必要时从第三方获得客户的有效身份证件、身份证明文件的原件、复印件或者影印件。委托第三方代为识别客户身份的，本行应当承担未履行客户身份识别义务的责任。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第二章  客户身份识别制度', '第二十一条', '本行在履行客户身份识别义务时，应当向中国反洗钱监测分析中心和中国人民银行当地分支机构报告以下可疑行为：（一）客户拒绝提供有效身份证件或者其他身份证明文件的。（二）对向境内汇入资金的境外机构提出要求后，仍无法完整获得汇款人姓名或者名称、汇款人账号和汇款人住所及其他相关替代性信息的。（三）客户无正当理由拒绝更新客户基本信息的。（四）采取必要措施后，仍怀疑先前获得的客户身份资料的真实性、有效性、完整性的。（五）履行客户身份识别义务时发现的其他可疑行为。本行报告上述可疑行为参照《金融机构大额交易和可疑交易报告管理办法》（中国人民银行令[2016]第3号发布）及相关规定执行。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第三章  客户身份资料和交易记录保存', '第二十二条', '各支行（部）应当保存的客户身份资料包括记载客户身份信息、资料以及反映金融机构开展客户身份识别工作情况的各种记录和资料。应当保存的交易记录包括关于每笔交易的数据信息、业务凭证、账簿以及有关规定要求的反映交易真实情况的合同、业务凭证、单据、业务函件和其他资料。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第三章  客户身份资料和交易记录保存', '第二十三条', '各支行（部）应采取必要管理措施和技术措施，防止客户身份资料和交易记录的缺失、损毁，防止泄漏客户身份信息和交易信息。采取切实可行的措施保存客户身份资料和交易记录，便于反洗钱调查和监督管理。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第三章  客户身份资料和交易记录保存', '第二十四条', '各支行（部）应当按照下列期限保存客户身份资料和交易记录：（一）客户身份资料，自业务关系结束当年或者一次性交易记账次年1月1日起至少保存5年。（二）交易记录，自交易记账次年1月1日起至少保存5年。如客户身份资料和交易记录涉及正在被反洗钱调查的可疑交易活动，且反洗钱调查工作在前款规定的最低保存期届满时仍未结束的，金融机构应将其保存至反洗钱调查工作结束。同一介质上存有不同保存期限客户身份资料或者交易记录的，应当按最长期限保存。同一客户身份资料或者交易记录采用不同介质保存的，至少应当按照上述期限要求保存一种介质的客户身份资料或者交易记录。法律、行政法规和其他规章对客户身份资料和交易记录有更长保存期限要求的，遵守其规定。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第四章  附 则', '第二十五条', '本办法由xxx银行负责解释和修订。'),
(NULL, '运营管理部', '《客户身份识别和客户身份资料及交易记录保存管理办法》', '第四章  附 则', '第二十六条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第一章  总 则', '第一条', '为加强xxx银行（以下简称“本行”）对账工作管理，保障本行及客户资金安全，提升对账风险防范水平和对账效率，根据《xxx行对账工作指引》、《江苏省农村信用社长期不动账户管理办法》等相关规定，结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第一章  总 则', '第二条', '本办法适用于本行管理部门和各支行（部）与客户之间的对账工作管理。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第一章  总 则', '第三条', '本办法所称客户，是指在本行开立账户与本行发生业务往来的单位、个人、行内往来机构和其他金融机构。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第一章  总 则', '第四条', '本办法所称对账，是指本行通过各种渠道向客户提示与其账户、交易相关的各类信息，供客户核对、确认并及时掌握其在本行的相关资产、负债情况，以达到供客户核对银行账务的目的。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第一章  总 则', '第五条', '本办法所称对账账户包括单位定期存款、单位通知存款、单位活期存款、单位保证金存款、单位保函、同业存款、本行内部往来的存款账户以及单位应计贷款、个人贷款账户。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第一章  总 则', '第六条', '本行对账工作按照分级组织、集中开展、专业运作、协调配合的方式，统一管理、集中实施。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第一章  总 则', '第七条', '本行对账工作坚持“质量为先，风险为重，效率第一”的指导思想，并遵循以下原则：（一）真实性原则：对账结果真实反映会计核算、业务过程和处理结果的信息。（二）及时性原则：在规定时间内发送对账单，协助和督促客户在规定时间内进行对账，并反馈对账结果。（三）风险重要性原则：利用对账系统数据分析，实现风险账户分类，对余额较大，风险系数较高的重点账户，从对账方式、对账频率及对账后续工作等方面进行重点管理和服务。（四）独立性原则：对账机构设置与业务经办部门、各支行（部）分离。按照岗位不相容要求，不得由业务经办、复核人员进行对账工作，应换人核对。（五）动态性原则：结算账户存续期间对账数据上下衔接，对账工作应连续、周期性进行。（六）合作原则：对账工作应上下联动，各支行（部）及本行相关部门应相互配合、相互监督、共同负责。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第八条', '运营管理部对账管理工作职责包括但不限于以下内容：（一）负责制订本行对账工作管理办法、各岗位责任制及考核办法；（二）负责制订对账工作计划和措施，组织和协调本行的对账工作；（三）对辖内对账工作进行管理、监督，对各支行（部）及对账团队岗位人员的工作业绩进行考核。（四）指定专人负责配合对账团队做好与客户对账的相关解释、宣传等工作，并将此岗位纳入轮岗范围。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第九条', '本行设立对账团队，隶属于运营管理部。其职责包括但不限于以下内容：（一）负责组织实施对账工作正常有序开展，确保本行对账工作质量；（二）负责打印、封装纸质对账单，对收回的对账回执进行扫描、验印、核对、对账单回执识别，对对账协议、对账单据回执等对账资料进行整理、保管归档；进行对账统计，对对账不符的进行重新核对；（三）负责向客户发送电子对账信息和电子对账全过程的跟踪；（四）负责网银对账客户的跟踪服务和电话催复工作；（五）做好客户对账回访，了解客户对账需求；（六）每季度终了后30日内向运营管理部书面上报对账工作情况报告及工作总结，总结内容包括对账工作开展情况、对账单据回执收回率、存在的问题、工作措施与建议等；（七）遇有对账工作中发现的重大问题及异常情况，按内部控制流程向运营管理部报告；（八）揭示对账过程中发现的违规违纪问题，并提出加强经营管理的建议。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第十条', '对账团队岗位设置及职责包括但不限于以下内容：（一）对账团队负责人⒈负责对账团队总体运行、对账系统的日常维护；⒉负责与外包方、各支行（部）的协调与联络；⒊组织实施对账工作正常有序开展，确保本行对账工作质量；⒋负责数据的导入、电子对账数据发送收回、纸质对账单的打印、封装的审核；⒌负责向客户发送电子对账信息和电子对账全过程的跟踪；⒍与对账专员一同进行大客户、财政账户上门面对面账户核对；⒎对全辖对账数据的回收统计管理，做好数据资料的保密工作；⒏负责对账工作开展情况及工作总结的上报。（二）对账团队专职对账人员。1.严格执行对账制度，认真完成客户签约协议、变更协议的准确录入，确保资料的准确性和完整性；2.做好客户信息新增、变更或销户资料核对、整理、保管工作；3.新签约客户发送彩信账单前的信息核对、电话引导工作；4.根据省联社反馈信息，对新签约网银对账客户进行电话回访和对账引导工作，对未及时进行网银对账的客户进行催促；5.纸质账单的打印、封装后移交邮局；6.纸质账单的回收、扫描、验印、核对、整理、保管工作，对账务不符的交负责人对接各支行（部）处理后重新发送账单并收回；7.按照彩信客服电话规范用语进行客户对账的回访，认真做好客服电话的接听和记录工作；8.彩信账单补发、重发、彩信改短信、电话催收客户回复工作；9.与负责人一同进行大客户、财政账户上门面对面账户核对；10.汇总各支行（部）彩信对账明细，对需重新签约的客户返回各支行（部）协助解决并处理；11.认真完成月度彩信对账的各类报表的统计、工作汇报及DVD刻录工作；12.按照本行安全保密制度做好相关信息的保密工作。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第十一条', '对账团队实行对账员分片包干责任制，分片区每年轮换一次。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第十二条', '支行（部）应指定专人配合对账团队做好对账工作，其对账管理职责包括但不限于以下内容：（一）与客户商定对账方式、签订对账协议，留存客户印鉴；（二）按照对账系统的要求，及时、准确、完整地将新增客户信息（邮寄地址、邮编、联系人、联系方式等相关信息）、客户变更信息、对账协议以及预留印鉴及时报对账团队，并按要求填写对账信息登记表，做好交接手续；（三）负责处理辖内对账客户的对账查询，及时打印账户明细对账单（分户账页），处理未达账项的查询、查复；（四）配合对账团队做好面对面对账工作，协助对账团队发送、回收对账单据与回执，协助对账团队对逾期未收回的对账单据回执进行催收；（五）配合对账团队处理对账过程中发现的异常情况；（六）向客户做好电子对账的宣传、推广工作。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第十三条', '审计部职责包括但不限于以下内容：负责定期开展对账情况的检查，检查涵盖对账工作的组织分工、对账过程的制度执行、对账的具体范围、对账频率、不符账项的处理、对账资料的收回与保管等内容。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第十四条', '信息科技部门负责配合做好对账系统运行保障、设备保养等工作。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第二章  职责与权限', '第十五条', '对账团队根据对账需求，及时提取、分发对账数据，包括但不限于：（一）月初5号前提供上月满足本行月度对账条件的账户明细清单，包括对公活期账户、对私贷款账户、行内往来账户、同业存放账户、存放同业账户。（二）季度结束后5日内，提供满足本行季度对账条件的账户明细清单，包括对公活期账户、对私贷款账户、行内往来账户、同业存放账户、存放同业账户、对公贷款账户、单位定期账户、保证金账户、保函账户。（三）在年度对账前，提供满足本行年度对账条件的账户明细清单，包括对公活期账户、对私贷款账户、行内往来账户、同业存放账户、存放同业账户、对公贷款账户、单位定期账户、保证金账户、保函账户。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第三章  对账内容与要求', '第十六条', '对账对象（一）单位、行内往机构及金融机构在本行开立的各类账户以及个人客户在本行开立的贷款账户。（二）新开单位结算账户且发生账务性流水、长期不动户重新启用的，对账团队根据对账系统预警信息，于次月对此账户进行对账。（三）系统内往来对账，是指本行系统内清算中心与各支行（部）之间各种资金往来的对账。（四）同业存放款项对账。由对账团队按月发放对账单，以面对面对账方式与其他金融机构进行对账，对账回执由清算团队、对账团队专夹保管。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第三章  对账内容与要求', '第十七条', '对账内容对账团队根据账户性质、账户余额、发生额、发生频率等数据，对账户进行分类，具体的将各类账户的分类标准及对账规则设置明确如下：'),
(NULL, '运营管理部', '《对账工作管理办法》', '第四章  对账方式及周期', '第十八条', '对账方式本行根据实际情况或管理需要，采取纸质对账和电子对账的方式进行对账，纸质对账包括银行面对面对账、委托网点对账和邮寄对账三种模式；电子对账包括网银对账、彩信对账、短信对账三种模式。（一）纸质对账1.银行面对面对账：是指由本行专职对账人员负责发放对账单和收回账单回执的对账方式，上门面对面对账必须坚持双人办理，由两名对账人员共同负责。2.委托网点对账：是指对账单由对账团队统一打印，（与回函信封一并）封装后下发营业网点，由客户经理或兼职对账员负责送达客户，客户对账后将账单回执用专用回函信封封装并在信封上加盖骑缝印章后再返还。3.邮寄对账：是指对账团队将打印的账单通过邮寄的方式发送给客户，由客户将对账回执寄回或送回本行对账团队的对账方式。（二）电子对账1.彩信、短信对账：是指根据与客户签订的电子对账服务协议，通过对账系统，由对账团队按期发送电子对账信息，客户通过彩信、短信的形式进行确认回复，对未回复和对账结果不符的客户进行再核对的对账方式。2.网上对账：是指在我行开通企业网银功能的客户，在开立时，由柜面人员根据客户提供的对账操作员、手机号码、操作权限等，在核心系统中进行对账签约并录入，由省联社统一提取客户信息，生成网银账单，客户登陆企业网银系统，进行自主对账的对账方式。所有客户，均可根据自身的管理要求，与我行协定对账的方式。但以下账户原则上应采取纸质对账方式：（1）财政性存款账户；（2）单位定期存款账户；（3）同业往来账户；（4）系统内往来账户；（5）对公贷款账户；（6）单位保证金、保函账户。客户新开通网银结算功能，或已经开通网银结算功能的企业客户，我行直接默认为网银对账，如客户需变更对账方式，可与我行商定，选择其他对账方式。未申请开通彩信（短信）对账、网银对账业务的企业客户，对账方式默认为纸质对账；个人客户对账方式默认为彩信对账。开通网银对账功能，当月未进行网银对账的，从次月起自动转为彩信（短信）对账；开通彩信（短信）对账功能，连续两月未进行有效对账的，从最后未回复月的次月起自动转为纸质对账。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第四章  对账方式及周期', '第十九条', '对账周期在账户分类的基础上，针对不同类型的账户采取不同的对账周期。（一）单位结算账户、财政性存款账户和网银账户按月进行对账，当月无发生额或余额低于10万元（不含）的不进行对账；系统内往来账户按月进行对账；同业存放款项账户按月进行对账。（二）单位定期账户、保证金账户、保函账户按季进行对账；公司类应计贷款账户按季进行对账；睡眠账户、非应计贷款账户原则上不再进行对账，若发生，则次月对账。（三）当年无成功对账记录的账户，年末必须进行一次面对面对账。（四）个人贷款账户按月进行对账。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第五章  对账事项要求', '第二十条', '系统内往来账户由清算团队每日对上个工作日的账务进行核对、监督。对账团队应按月打印对账单进行对账。系统内往来账户对账不符的，对账团队要会同各支行（部）运营主管（或负责人）查明原因，及时处理，并在系统内往来余额核对表上注明差错原因及处理结果。系统内往来对账，要严格坚持双人核对、互相监督的原则，对账资料由对账团队及各支行（部）专夹保管，定期装订。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第五章  对账事项要求', '第二十一条', '对账工作主要包括签订对账协议、发送及接收对账信息（纸质对账信息、电子对账信息），处理对账结果，保管对账单及客户回执等。（一）与存款人签订对账协议，明确双方的权利、义务与责任，约定对账周期，明确对账方式。（二）电子对账是指通过对账管理系统对综合业务系统数据进行采集分类，根据对账协议要求，以彩信、短信、网银对账方式与客户传递、收集对账信息。（三）收回的纸质对账单据回执，存款人应加盖全套预留本行印鉴，上门对账人员应加盖“已面对面对账”戳记及对账人员印章。（四）上门对账人员必须保证双人，严禁一人上门与客户对账。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第五章  对账事项要求', '第二十二条', '纸质对账要求（一）发出的对账单，必须由对账团队打印封装，禁止手工填制的对账单。（二）纸质对账单应在每月（季）末后的5个工作日（遇节假日顺延）内制作完成。面对面对账单回执原则上应在当月全部收回，邮寄对账单回执原则上应在当月25日内收回。（三）对账单据丢失的，专职对账员要查明原因，由对账团队负责人补制对账单，审核后重新发送。（四）因账户信息不准确、预留手机号错误等原因造成对账信息无法送达或被退回的，对账团队专职对账员应及时反馈给各支行（部）。各支行（部）运营主管要在2个工作日内查找原因，完成账户信息的核实工作，填写对账信息变更申请。专职对账员应及时按照对账信息变更申请，更改对账登记信息，在5日内重新发起对账。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第五章  对账事项要求', '第二十三条', '对账回执管理。重点账户对账的对账单据回执收回率原则上应达到100%，未能收回回执的，必须逐户说明原因；全部对账账户回收率原则上应达到90%以上；对按月对账、按季对账连续两期未返回对账回执的，对账团队应进行上门对账，查明原因，确保对账率100%。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第五章  对账事项要求', '第二十四条', '本办法所称的重点账户是指以下账户：（一）存款账户1.月末存款余额在500万元（含）以上的单位存款账户；2.当月日均存款余额在500万元（含）以上的单位存款账户；3.当月发生业务笔数达到100笔（含）以上或累计金额达到5000万元（含）以上的单位存款账户；4.三个月（含）以上未发生业务，突然发生金额50万元以上或者连续一年（含）未发生业务突然发生业务的单位存款账户；5.财政性单位存款账户；6.提供上门服务的单位存款账户；7.当月新开立的单位存款账户；8.当月新开通网银业务并有发生额的单位存款账户；9.对账过程中出现可疑情况的存款账户；10.其他迹象表明值得密切、立即关注的账户或监管部门要求即时、上门对账的存款账户。（二）存放款项账户月末有余额或当月有发生额的同业存款账户。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第五章  对账事项要求', '第二十五条', '对账回收率及覆盖率的计算方法。对账回收率、对账覆盖率由对账系统按照考核期自动计算生成。（一）对账回收率是指考核期内已收回对账回执与应对账户发放的余额对账单的比例。（二）对账覆盖率是指年度内已对账户数与应对账户数的比率。（三）按月对账的账户每年应发放12次；按季对账的账户每年应发放4次。年底计算年对账覆盖率，年综合对账覆盖率要求达到100%。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第六章  对账流程', '第二十六条', '对账团队对账流程（一）对账数据的提取及对账单据的输出。对账团队负责人根据对账周期或管理要求，设置对账参数，每月1-3号（节假日顺延）从综合业务系统采集对账数据及客户信息，对电子对账签约账户发送对账彩信，对需提供纸质对账单的客户打印对账单，并进行封装。（二）对账单据的发送。对账团队负责人将打印封装后的对账单集中交邮局发放，按规定出具发出对账单明细，交接双方签字确认。面对面对账单根据片区，由对账团队负责人和专职对账人员共同负责投放。（三）网银对账单由省联社统一生成并发送，对账团队每月15日后，根据省联社反馈的对账数据，对未对账客户进行电话提醒并引导其及时对账。省联社对上月未进行网银对账的客户进行控制，暂停其网上对外支付功能，待其对账后恢复。（四）彩信对账信息发送2日后未收到客户回复，对账团队进行短信提醒及电话跟踪催促服务，两周后对未回复客户打印纸质对账单，由专职对账人员进行纸质对账并查明未回复原因。（五）纸质对账单据信件的收回。面对面对账单回执由各专职对账员负责收回，未签约电子对账客户对账单由邮局集中收回后交专职对账员。收回的纸质对账回执由专职对账员按分工片区进行清分、拆封，对照发出对账单清单进行核对、销账，统计收回、退件、丢失信件等情况，将对账回执按机构整理后连同收回清单交对账团队负责人。（六）对账单据回执的审核。收回的对账单回执于每月底前通过事后监督系统扫描，并进行集中审核，对账单回执的审核应于收回后5个工作日内完成。审核内容包括印鉴的审核和账户余额及发生额是否相符的审核。系统审核未通过的，返还由专职对账人员进行手工核对。（七）对账余额或发生额不符的处理。经未达账项调整后仍对账不符的，专职对账员应和支行（部）运营主管一同与客户进行面对面对账，查找原因，及时处理，直至核对相符，同时详细登记处理情况，形成书面报告，上报对账团队负责人。未达账再处理的对账回执应在5日内完成对账。（八）对印鉴不符的处理。系统审核后与预留印鉴不符的，由专职对账员进行手工核对。手工核对仍然不符的，由专职对账员和支行（部）运营主管一同与客户进行面对面对账，并及时登记处理情况，遇有问题及时以书面形式上报。（九）档案管理。对账团队负责人和专职对账人员按分工片区将对账单回执按机构进行整理、封装，按季进行归档，并按照会计档案管理要求保管。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第七章  对账管理监督', '第二十七条', '各支行（部）要严格履行对账工作职责，认真处理好各项对账工作，积极协助对账团队解决对账过程中发现的问题。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第七章  对账管理监督', '第二十八条', '对账团队要认真组织开展好本行对账工作，确保本行对账工作质量；定期反映和通报对账工作情况，针对工作中发现的问题，及时向管理部门反映；及时发现本行经营和内控管理方面的漏洞，提出有效防范措施。对账人员在对账过程中，发现重大问题或存在风险隐患的，不得与开户支行（部）和经办人员联系，应及时报告对账团队负责人，由负责人按规定程序报告。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第七章  对账管理监督', '第二十九条', '审计部要建立健全对账稽核制度，将客户对账工作列入审计检查的范围，充分揭示对账工作存在的问题、风险隐患以及对账的“盲区”，对对账工作的审计检查每年应不少于一次，并将对账检查结果向行长室报告。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第七章  对账管理监督', '第三十条', '运营管理部作为对账团队的管理部门，要不定期对对账团队和支行（部）对账的情况进行监督和检查，加强对账质量、数量及未达账项的处理情况的核查，对各支行（部）和对账团队反映的问题，应及时向行领导汇报，并提出完善相关管理措施的建议意见；同时要做好指导和协调工作，促进对账工作正常有序开展。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第七章  对账管理监督', '第三十一条', '对账工作定期报告制度。对账团队在对账工作中发现的重大问题及异常情况，应在第一时间报告。在每月后10日、每季后30日内由对账团队负责人向分管行长、运营管理部上报上月和上季度的《对账工作情况报告》。报告内容包括但不限于以下内容：（一）对账工作的基本情况、工作进度、重点账户变动情况、对账单发出收回情况等；（二）发现的差错、事故、风险隐患、案件苗头情况（包括：笔数、金额、案情）；（三）未达账项的处理情况（包括：未达账项的笔数、金额、查清处理情况等）、印鉴核对处理情况和退件处理情况等；（四）对账工作取得的效果以及存在的问题、工作措施以及建议等。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第八章  附  则', '第三十二条', '对账团队的各类对账不代替相关条线、各类检查需要的内外账务核对。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第八章  附  则', '第三十三条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《对账工作管理办法》', '第八章  附  则', '第三十四条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第一条', '为贯彻落实中国人民银行关于加强人民币流通管理的工作要求，加强xxx银行（以下简称“本行”）小面额现金兑付工作的管理，进一步优化人民币流通环境，强化人民币流通满意工程建设，满足广大客户对小面额人民币的需求，根据《现金管理暂行条例》及相关规定，制定本办法。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第二条', '本办法中的小面额现金是指10元及以下券别的人民币。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第三条', '对于金融消费者的小面额现金兑换要求，本行实行主办银行和主办网点制度。主办银行是指单位或个人需要兑换超过500张（枚）的小面额现金时，可向其基本账户或个人账户的开户银行提出兑换要求，该开户银行即为主办银行。主办网点是指单位或个人需要兑换不超过500张（枚）的小面额现金时，可向其附近的银行网点提出兑换要求，该网点即为主办网点。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第四条', '各营业网点应设立并标明小面额现金兑换窗口，满足柜台零散客户兑换要求。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第五条', '各营业网点应保证每日营业前小面额现金备付金额达到最低标准，10元、5元纸币各券别不得低于500张，1元、5角、1角硬币各面额不得低于500枚。总行根据小面额人民币需求淡、旺季特点和人民银行要求，适时调整小面额人民币最低备付金额。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第六条', '各营业网点应根据小面额人民币的需求情况，按规定向总行申报小面额现金计划。总行根据小面额人民币需求情况备足库存量。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第七条', '金融消费者需兑换超过200张（枚）的小面额现金时，应至少提前1个工作日向主办银行或主办网点预约，预约可采用电话预约、当面预约、书面预约等方式。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第八条', '各营业网点应建立预约登记簿，详细记录预约单位或个人、预约兑换时间、券别、金额等，并留存备查。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第九条', '各营业网点应遵循首问负责制，充分满足金融消费者的兑换要求，不得拒绝或推诿。如有金融消费者的合理兑换需求得不到满足，总行将按相关规定进行处罚。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第十条', '有硬币自助兑换机的网点应充分做好便民服务，每日检查设备运行情况并及时配款，保证各面额硬币兑换正常。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第十一条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《小面额现金兑付管理办法》', '', '第十二条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第一条', '为规范xxx银行（以下简称“本行”）小额支付系统的正常运行，杜绝差错，防范支付风险，根据中国人民银行《小额支付系统业务处理办法（试行）》和《江苏省农村信用社小额支付系统业务处理办法》，结合本行实际情况，特制定本办法。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第二条', '小额支付系统批量处理支付业务，轧差净额清算资金。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第三条', '经中国人民银行批准，本行作为间接参与者通过江苏省农村信用社联合社（以下简称“省联社”）综合业务系统接入中国人民银行小额支付系统办理支付业务。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第四条', '本行清算团队在省联社的清算账户须按照省联社规定留足备付金，以保证小额支付系统往来的资金清算。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第五条', '小额支付系统和大额支付系统共享清算账户清算资金。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第六条', '小额支付系统处理同城、异地的借记支付业务以及金额在规定起点以下的贷记支付业务。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第七条', '小额支付系统处理的支付业务一经轧差即具有支付最终性，不可撤销。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第一章  总  则', '第八条', '小额支付系统实行7×24小时不间断运行，系统工作日为自然日，其资金清算时间为大额支付系统的工作时间。小额支付系统的运行时间和资金清算时间由中国人民银行根据管理需要进行调整。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第九条', '本行参加小额支付系统处理下列跨行支付业务：（一）普通贷记业务；（二）定期贷记业务；（三）实时贷记业务；（四）普通借记业务；（五）定期借记业务；（六）实时借记业务；（七）中国人民银行规定的其他支付业务。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第十条', '本行小额支付系统办理的贷记支付业务金额上限，根据中国人民银行规定执行。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第十一条', '各支行（部）应当加强查询查复管理，对有疑问或发生差错的支付业务，应当使用查询查复格式报文，并在当日至迟下一个法定工作日上午发出查询。查复行应当在收到查询信息的当日至迟下一个法定工作日上午予以查复。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第十二条', '通过小额支付系统办理支付业务应当按照规定收取汇划费用。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第十三条', '对发起普通贷记支付业务、定期贷记支付业务、普通借记支付业务回执和定期借记支付业务回执需要撤销的，应当通过小额支付系统发送撤销申请。支付业务未纳入轧差的，立即办理撤销；已纳入轧差的，不能撤销。申请撤销只能撤销批量包，不能撤销批量包中单笔支付业务。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第十四条', '对已发出的普通借记支付业务或定期借记支付业务需要付款行停止付款的，应当使用规定格式报文申请止付。付款行收到止付通知后，应当在当日至迟下一个法定工作日上午10时发出止付应答。付款行对未发出借记回执信息的，立即办理止付；对已经发出借记回执信息的，不予止付。申请止付可以整包止付，也可以只止付批量包中的单笔支付业务。申请止付为整包止付且付款行同意止付的，付款行不再向收款行返回普通借记支付业务回执和定期借记支付业务回执。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第十五条', '对已纳入轧差的普通贷记支付业务、定期贷记支付业务和已确认付款并纳入轧差的借记支付业务回执需要退回的，应当通过小额支付系统发出退回申请。收款行收到退回申请，应当在当日至迟下一个法定工作日上午10时发出退回应答。收款行未贷记收款人账户的，立即办理退回；已贷记收款人账户的，不得办理退回，应当通知付款行由付款人与收款人自行协商解决。付款行可以申请整包退回，也可以申请退回批量包中的单笔支付业务。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第二章  支付业务', '第十六条', '收款行对收到的有差错的支付业务，应当主动通过小额支付系统向付款行查询后办理退回。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第三章   纪律与责任', '第十七条', '各支行（部）应当遵守本办法以及其他相关规定，加强内部控制管理，建立健全应急处理机制，保证系统安全稳定运行和业务的正常处理。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第三章   纪律与责任', '第十八条', '对违反规定延压客户资金，影响客户资金使用的，应依法承担赔偿责任。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第三章   纪律与责任', '第十九条', '办理支付业务查询查复应当使用规定格式报文，并及时查询和查复。对违反规定造成客户和他行资金延误的，应依法承担赔偿责任。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第三章   纪律与责任', '第二十条', '办理支付业务止付应当使用规定格式报文，对收到的止付申请应当及时予以处理。对违反规定造成客户和他行资金损失的，应依法承担赔偿责任。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第三章   纪律与责任', '第二十一条', '收到付款行的支付业务退回申请，未贷记收款人账户的，应当立即办理退回。对收到有差错的贷记支付业务，应当立即向付款行查询后处理退汇。对违反规定造成客户和他行资金损失的，应依法承担赔偿责任。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第三章   纪律与责任', '第二十二条', '因工作差错延误小额支付业务的处理，影响客户和他行资金使用的，应依法承担赔偿责任。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第三章   纪律与责任', '第二十三条', '小额支付业务工作人员在办理小额支付业务中玩忽职守，或出现重大失误，造成资金损失的，应当给予纪律处分，构成犯罪的，移交司法机关依法追究刑事责任；伪造、篡改小额支付系统业务盗用资金的，应当没收非法所得，给予纪律处分，构成犯罪的，移交司法机关依法追究刑事责任。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第四章   附  则', '第二十四条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《小额支付系统业务管理办法》', '第四章   附  则', '第二十五条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第一章   总	则', '第一条', '为正确、及时、高效处置支付系统面临的突发事件，提高支付系统应对突发事件的处置能力，防范支付系统风险，保障支付系统连续处理，依据《突发事件应急预案管理办法》（国办发〔2013〕101号）、《银行业突发事件应急预案》（银监发〔2005〕42号）等法律法规和制度办法，特制定本预案。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第一章   总	则', '第二条', '本预案所称支付清算系统（以下简称支付系统）主要指人民银行支付清算系统和省联社自建支付清算系统，包括以下应用系统：（1）核心账务系统（CBOD）；（2）大额实时支付系统（HVPS）；（3）小额批量支付系统（BEPS）；（4）网上支付跨行清算系统(IBPS)；（5）全国支票影像交换系统(CIS)。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第二章  组织领导', '第三条', '本行成立支付系统应急处置领导小组（以下简称“领导小组”），组长由分管运营管理部的副行长担任，成员由运营管理部、信息科技部、电子银行部、行政办公室负责人组成。支付系统应急处置领导小组办公室设在运营管理部。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第二章  组织领导', '第四条', '领导小组职责（1）执行人民银行地市中心支行及省联社领导小组发布的有关命令。（2）决策和指挥本单位业务系统的应急处置。（3）发生突发事件时，立即报告省联社领导小组。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第二章  组织领导', '第五条', '领导小组办公室职责贯彻执行本行领导小组的统一部署，协调好本部门支付系统应急处置工作，收集有关情况并及时上报本行领导小组。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第二章  组织领导', '第六条', '各部门职责（1）运营管理部负责业务指导。（2）信息科技部负责各类系统的技术支持和定期监控，完善支付系统备份设施，组织开展应急演练工作。（3）电子银行部负责网上支付系统的应急处置工作。（4）行政办公室提供应急处置所需的物资保障。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第三章  突发事件', '第七条', '本预案所称突发事件是指以下三种情况：（1）自然灾害、事故灾难或突发社会安全事件造成系统整体、某一清算系统、接入点崩溃。（2）突发公共卫生事件、社会安全事件造成人力资源的操作限制，严重影响支付系统的操作运行。（3）支付清算系统某一节点出现故障，严重影响支付系统业务的连续处理，在可容忍时间内无法恢复。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第三章  突发事件', '第八条', '根据支付系统受损害的应用系统等级、涉及范围和影响程度不同，突发事件分为特别重大（I级）事件、重大（II级）事件、较大（III级）事件、一般（IV级）事件。当突发事件满足多个级别定级条件时，按最高级别确定事件等级。特别重大（I级）事件：核心账务系统无法正常开展业务达4个小时（含）以上；大额支付系统、小额支付系统、网上支付跨行清算系统和全国支票影像交换系统全部应用系统无法正常开展业务达4个小时（含）以上的突发事件。重大（Ⅱ级）事件：核心账务系统无法正常开展业务达2个小时（含）以上、4个小时以内的突发事件；大额支付系统、小额支付系统、网上支付跨行清算系统和全国支票影像交换系统全部应用系统无法正常开展业务达2个小时（含）以上、4个小时（含）以上的突发事件；大额支付系统、小额支付系统、网上支付跨行清算系统或者全国支票影像交换系统中某一个应用系统无法正常开展业务达4个小时（含）以上的突发事件。较大（III级）事件：核心账务系统无法正常开展业务达30分钟（含）以上、2个小时以内的突发事件；大额支付系统、小额支付系统、网上支付跨行清算系统和全国支票影像交换系统全部应用系统无法正常开展业务达30分钟（含）以上、2个小时（含）以内的突发事件；大额支付系统、小额支付系统、网上支付跨行清算系统或者全国支票影像交换系统中某一个应用系统无法正常开展业务达2个小时（含）以上、4个小时以内的突发事件。一般（IV级）事件：核心账务系统无法正常开展业务在30分钟以内的突发事件；大额支付系统、小额支付系统、网上支付跨行清算系统和全国支票影像交换系统全部应用系统无法正常开展业务在30分钟以内的突发事件；大额支付系统、小额支付系统、网上支付跨行清算系统或者全国支票影像交换系统中某一个应用系统无法正常开展业务达1个小时（含）以上、2个小时以内的突发事件。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第四章  处置原则', '第九条', '处置原则（1）统一指挥，分级响应。各网点必须服从本行领导小组的指挥。建立健全本网点支付清算系统突发事件应急协调机制，明确相关人员在应急响应和协调中的职责，支行行长为第一责任人，运营主管具体负责，确保突发事件发生时响应及时，联系通畅，操作准确，处理高效。（2）业务连续，大额优先。进行突发事件处置时，应采取积极有效的措施，优先恢复对外服务，保障支付业务的连续处理，在多个应用系统受到影响的情况下，坚持核心优先、大额支付系统业务优先恢复的原则。（3）预防为主，持续改进。建立和完善支付系统突发事件风险防范体系，对可能导致突发事件的风险进行有效地识别、分析和控制，并对风险指标动态持续监测，减少重大突发事件发生的可能性。（4）以人为本，安全第一。应急处置要切实加强应急处置人员的安全防护，最大限度地减少突发事件造成的人身危害。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第五章  预防与预警机制', '第十条', '预防机制预防机制是指支付系统日常运行中,为防止因突发事件导致系统出现故障和崩溃，或者在出现故障和崩溃时能最大程度地减少损失所采取的预防措施和行动。信息科技部应根据自身职责对支付系统应用软件、系统、网络、机房等设施实行全方位监控，对于异常事件应及时报告，协同处置。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第五章  预防与预警机制', '第十一条', '信息科技部完善支付系统备份设施（1）完善同城、异地备份系统建设，定期或不定期进行生产环境与备份环境的切换演练，保障备用系统或设备在应急情况下能快速投入使用。（2）建立和完善支付系统应急备份机制，提高应对突发事件的能力。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第五章  预防与预警机制', '第十二条', '信息科技部组织开展应急演练（1）每年定期进行应急演练，应急演练内容应涵盖系统、网络、机房基础设施、主要应用系统等，其中实战演练占演练总数的比例应不低于80%。（2）应急演练以应急预案为基础，严格按照年度应急演练工作计划进行。（3）严格控制应急演练引起的系统变更风险，避免因演练导致正常服务受到影响。（4）应急演练应选择在非主要业务时段进行。对于影响支付系统运行的应急演练，应安排在支付系统维护窗口期间进行。（5）应急演练完成后，应保证实施应急预案所需的各项资源恢复正常。（6）应急演练完成后，应编写总结报告，包括演练中出现的问题，原因分析和应对措施。（7）定期对业务系统应急响应相关人员进行培训。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第五章  预防与预警机制', '第十三条', '系统运行异常的预警信息，由各网点运营主管及时向二级运维反映，二级运维向领导小组办公室报告。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第五章  预防与预警机制', '第十四条', '对突发事件预警信息，由领导小组办公室通知信息科技部、运营管理部相关人员和网点。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第六章  报告与指挥机制', '第十五条', '报告方式突发事件发生后，各网点应立即向本行领导小组报告，领导小组组织有关人员分析突发事件对支付系统的影响程度，严重影响支付系统运行的应立即向省联社领导小组报告。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第六章  报告与指挥机制', '第十六条', '报告要迅速、准确、完整（1）迅速是指网点发现系统突发事件应在第一时间报告。（2）准确是指报告内容要客观真实，不得主观臆断。（3）完整是指报告内容符合本预案的要求，便于本行领导小组研究决策。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第六章  报告与指挥机制', '第十七条', '报告内容（1）突发事件种类。包括：自然灾害、事故灾难、突发公共安全事件、突发公共卫生事件等。（2）影响系统运行情况。包括：突发事件发生后对系统造成的影响程度，影响范围等。（3）影响业务处理情况。包括：系统突发事件发生的时间和地点、系统运行的状态，以及是否造成业务资料丢失等情况。（4）影响人力资源情况。包括：突发事件对人员造成的伤害情况，以及受灾情况、采取的救助和防护措施、岗位接替等情况。（5）现场处置情况。包括：现场组织指挥、所采取的先期处置措施、系统修复等情况。（6）启动应急处置预案的请求。根据突发事件情况，提出启动应急处置预案的请求。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第六章  报告与指挥机制', '第十八条', '领导小组指挥流程，本行领导小组接到省联社命令后，组织各网点实施应急处置方案；及时向省联社领导小组反映执行过程中存在的问题，并提出建议；做好应急处置情况的报告工作。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第七章   应急响应及处置', '第十九条', '支付系统发生突发事件时，应按照突发事件等级及分类响应流程进行应急响应，加强各部门之间的协调配合，快速有效处置突发事件。本行领导小组接到省联社指令后，立即通知各网点，按省联社要求执行。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第七章   应急响应及处置', '第二十条', '支付系统恢复正常服务后，领导小组应立即通知各网点应急结束。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第八章  附	则', '第二十一条', '突发事件发生后，本行领导小组办公室向领导小组总结汇报，本行领导小组按事件上报流程向上级部门总结报告。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第八章  附	则', '第二十二条', '本预案由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《支付清算系统危机处置预案》', '第八章  附	则', '第二十三条', '本预案自发文之日起施行。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第一章　　总　则', '第一条', '为优化人民币流通环境，加强对外支付现金的业务管理，保障以冠字号码查询为手段的现金流通管理系统安全有效运行，切实解决银行涉及假币纠纷的举证和责任认定工作，维护银行业金融机构及消费者合法权益，根据中国人民银行货币金银局印发的《银行业金融机构人民币冠字号码查询解决涉假纠纷工作指引（试行）》、《银行业金融机构冠字号码查询管理检查工作指引（试行）》，结合xxx银行（以下简称“本行”）的实际，特制定本办法。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第一章　　总　则', '第二条', '本办法适用于xxx银行的现金收付营业网点、自助设备、现金管理团队。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第二章  冠字号码信息记录、存储', '第三条', '记录存储设备。本行根据实际情况，对营业网点、现金整点中心统一配备1台以上具有冠字号码记录功能的清分机，对取款机、存取款一体机和柜台渠道现金实现记录、存储冠字号码。（一）现金整点中心配置纸币清分设备。对解缴人民银行和调拨给营业网点的现金进行清分与记录冠字号码。（二）营业网点配置纸币清分设备。对自助设备配款、柜面支付的现金进行清分与记录冠字号码。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第二章  冠字号码信息记录、存储', '第四条', '冠字号码记录。以下来源的现金，均应实现冠字号码记录，且冠字号码信息流与现金实物流应匹配，并将收到现金实物对应批次的冠字号码导入本行现金流通管理系统。如无法导入系统的，应对现金进行重新清分直至能完全记录冠字号码。（一）本网点收入，在网点清分的现金；（二）从他行调入的，由他行清分的现金；（三）从人民银行提取的，由他行清分的现金；（四）从人民银行提取的，由人民银行清分的现金。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第四章  冠字号码查询管理', '第十条', '营业网点、现金整点中心设立《冠字号码查询业务登记簿》（见附表），记录每笔冠字号码检索业务办理情况。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第四章  冠字号码查询管理', '第十一条', '营业网点现金柜员、整点中心操作员为冠字号码检索员，设立系统准入，冠字号码检索员必须通过密码进入系统，如换人管理应及时修改检索员的密码。冠字号码检索员应熟悉掌握各种方式记录、存储冠字号码的检索，对他行调入、人民银行清分完整券调入等多来源的冠字号码记录及检索方法均应掌握。    第十二条  冠字号码查询实现精确检索和模糊检索两项功能。精确检索即输入完整待查冠字号码后显示唯一冠字号码现钞，模糊检索即输入冠字号码部分连续字符，软件显示所有相关现钞，其中应包含待查现钞。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第五章  冠字号码数据信息保存期限管理', '第十三条', '冠字号码文件及图像保存期为3个月。因特殊事项如果认为有必要可以延长保管期限，但不得缩短，对事实尚未查清的，需要及时对未查清冠字号码和图像数据进行拷贝待查。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第五章  冠字号码数据信息保存期限管理', '第十四条', '保管期限从冠字号码记录当日算起，依次向后推3个月。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第六章  冠字号码数据信息丢失预案', '第十五条', '冠字号码数据信息的检查。为防止数据丢失，各整点中心、营业网点可参考以下方式进行检查：一是在清分环节设置校验程序，对清分的每批次纸币张数与冠字号码记录条数进行校验；二是清分机具开机时进行测试，并确保每周不少于一次测试；三是取款机、存取款一体机加钞后取2-3张纸币登录现金流通管理系统查询验证。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第六章  冠字号码数据信息丢失预案', '第十六条', '人民币冠字号码存储采取并机运行模型。本行人民币冠字号码数据上传采用主机与备机同时运行模式，如主机出现问题时，备机自动更新当日上传信息，确保数据完整性。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第六章  冠字号码数据信息丢失预案', '第十七条', '人民币冠字号码上传数据实行定期备份。为防止冠字号信息的丢失，本行现金流通管理系统管理员定期于每月末对上一个月上传冠字号码数据进行磁介质的再备份，同时，信息科技部人员负责提高设备性能，增加设备容量，确保设备适应数据需求。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第六章  冠字号码数据信息丢失预案', '第十八条', '人民币冠字号码上传数据定期检查。为了防止数据丢失，本行信息科技部指定专人对人民币上传冠字号码定期检查，确保冠字号码数据的完整性。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第六章  冠字号码数据信息丢失预案', '第十九条', '营业网点、现金整点中心冠字号码检索员应密切关注数据信息，对因机具或网络问题造成不能上传成功的，先用U盘备份，查明原因，待恢复正常后，及时对现金进行再次复点，确保人民币冠字号码上传成功。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第七章  流通管理系统维护与安全管理', '第二十条', '现金流通管理系统运行维护职责和分工。本行运营管理部负责现金流通管理系统的人员设置和日常业务运行的管理。本行信息科技部负责清分机与现金流通管理系统网络的连接和系统运行的管理。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第七章  流通管理系统维护与安全管理', '第二十一条', '升级管理。遇到系统升级时，本行运营管理部和信息科技部要根据人民银行管理要求做好系统维护和升级更新，确保现金流通管理系统正常运行。遇到故障时，营业网点、现金整点中心在第一时间内进行处理，在无法处理时上报本行运营管理部，便于本行运营管理部汇总故障信息反馈软件供应商解决。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第八章  附　则', '第二十二条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《支付现金记录、存储、备查冠字号码管理办法》', '第八章  附　则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第一章    总	  则', '第一条', '为进一步规范xxx银行（以下简称“本行”）的柜员管理，全面、客观、准确地考评柜员的业务素质、服务水平和岗位职责履行情况，建立科学、合理的柜员考核和分配机制，根据《中华人民共和国商业银行法》、《中国银行业柜面服务规范》结合本行实际，制订本办法。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第一章    总	  则', '第二条', '本办法所指柜员是指与本行签订劳动合同，从事柜面会计出纳业务的所有人员(含劳务派遣工)。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第一章    总	  则', '第三条', '本行成立员工等级考评领导小组，组长由董事长担任，其他领导为成员，领导小组下设办公室，成员为机关各部室负责人；柜员等级考评按照“统一领导、分级负责、客观公正”的原则，由运营管理部牵头，其他相关部门配合，对全行所有柜员进行等级初评、分管行长审核后交人力资源部汇总，报员工等级考评领导小组审定。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第二章    考评项目与评定条件', '第四条', '柜员等级考核评定工作每年组织一次，原则上在每年员工业务技能测试后举行。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第二章    考评项目与评定条件', '第五条', '考评从职业素养、业务素质、业务技能、工作质量、工作业绩、服务效能等六个方面综合评价。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第二章    考评项目与评定条件', '第六条', '参加评级柜员应当具备的条件：1.具有良好的职业道德，较好地履行岗位职责，文明服务，秉公守法，廉洁奉公。2.熟悉和遵守国家有关法律、法规和规章制度，认真贯彻执行行业和本行规章制度。3.熟练掌握商业银行基本业务知识和岗位专业知识，熟练掌握岗位业务技能及办公业务系统操作技能。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第三章   等级评定标准和内容', '第七条', '等级评定标准和内容：柜员等级考评指标采取百分制。㈠职业素养考核（10 分，本项如扣分则扣完为止）1.拥护党的路线、方针、政策，具有较高的政治素质和思想觉悟，积极支持和参与本行中心工作及重点工作，从正当途径反映工作问题或工作建议，得2分；2.熟悉相关法律法规和管理规定，通过总行组织的合规及法律法规知识测试，得1分；3.具有较强的事业心和责任感，作风正派，身心健康，遵纪守法，有良好的职业素养和从业记录，得3分；受到警告、记过、记大过等纪律处分的分别扣1分、2分、5分（多次受到处分的以最高等级计算，受到党纪处分的按对应等级扣分）；4.近三年年度考核均为称职以上，得1分；5.自愿服从组织调配，得1分；不服从部门工作安排的每次扣1分，不服从总行工作调配的每次扣3分；6.自觉遵守本行管理制度，得1分；发生不遵守本行管理制度行为未造成重大影响的每次扣2分，造成重大影响的每次扣5分；7.遵守廉洁从业规定，得1分；发生违背廉洁从业规定行为，经查实，情节较轻，未造成影响的扣3分，情节较重，造成社会影响的，扣5分。㈡业务素质考核（20 分）业务素质考核包括学历、相关职称、职业资格。1.学历考核（5分），取得本科学历的得5分，取得研究生（含双本）及以上学历得8分，大专学历得3分，中专及高中学历得1分，高中以下不得分。2.职称考核（7分），取得中级职称得7分，取得高级职称得10分，取得初级职称得3分。职称必须与本人工作岗位相近，取得多个的按最高级别计算，不重复计算得分。3.职业资格考核（8分），包括银行业专业人员职业资格和从业资格，银行专业人员职业资格占5分，从业资格占3分。其中：银行业专业人员职业初级资格5门通过得5分（每少通过1门扣1分，6门全过的计6分）；银行业专业人员职业中级资格每通过1门得2分。取得与岗位相匹配的从业资格，得3分，另取得一个加0.5分。㈢业务技能考核（20 分）业务技能考核包括6项必考项目和1项选考项目，7项全部通过（即全部达到D级）得12分；通过6项（含）以下每通过1项得1分；每项在D级基础上，每提高1个等级加0.5分。㈣工作质量考核（20 分）工作质量考核项目为业务差错率、授权业务退回率等内容，各占10 分。业务差错率。业务差错率得分标准见下表：授权业务退回率。授权业务退回率得分标准见下表：㈤工作业绩考核（20 分）工作业绩考核柜员业务量和领导布置的其他工作完成情况。⑴柜员业务量10分。按总行划分的等级行标准分别计算人均业务量（计算员工业务量时剔除经人力资源部同意的员工产假、事假、长期病假）。柜员业务量达到人均业务量的得10分，其他按比例得分，得分不超过15分。现金柜员或主出纳按本网点柜员平均得分计算。⑵领导布置的其他工作10分。按本网点领导布置柜员的其他工作完成情况得分，由所在支行行长及运营主管分别打分，加权平均。㈥服务效能考核（10 分）服务效能主要考核服务满意度和客户投诉情况等，按服务满意度考核得分折算分，发生一起客户有效投诉扣2分，扣完为止。㈦业务差错率、授权业务退回率、柜员业务量三个考核指标的特殊规定：1.业务量指标按照柜员当年实际工作时间计算月均业务量，业务权重参照总行当年绩效考核办法业务量权重计算；2.月度请假超过10天（含）的，当月业务量不参加月均业务量计算（剔除月数和业务量）；3.当年转岗或新分配的员工，工作当月业务量不参加月均业务量计算（剔除月数和业务量）；4.当年第四季度转岗或新分配的员工，业务差错率、授权业务退回率、柜员业务量三个项目的得分参照全市柜员平均得分计算；5.主出纳或专职现金柜员业务差错率、授权业务退回率、柜员业务量三个项目的得分参照本网点柜员平均得分计算(包括支行明确的大堂经理)。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第三章   等级评定标准和内容', '第八条', '有下列情形的，在考评时给予加分。1.本人受到本级或xx市属部门表彰的，加计2分；受到xx市级（含）或yy市属部门表彰的加计5分，受到yy市级或省属部门表彰的加计8分，受到省部级表彰的加计20分。2.代表本行参加xx级别（包括行管组）业务技能比赛或知识竞赛获得团体或单项前1－3名（或一、二、三等奖）的，分别加计3分、2分、1分和5分、3分、2分；yy级别业务技能比赛获得团体或单项前1－3名（或一、二、三等奖），分别加计5分、3分、2分和10分、8分、5分；省级农商行系统业务技能比赛获得团体或单项前1－3名，分别加计15分、12分、10分，个人获得前10名的，分别加计20分、18分、17分、15分、14分、13分、12分、11分、10分、9分。3.发生认为可以加分的情形，经本行员工等级考评领导小组认定后予以加分。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第九条', '柜员等级按年评定。由高到低分一、二、三、四、五、六、七等七个等级。综合得分95分（含）以上的为一级，90分（含）－95分的为二级，85分（含）－90分的为三级，80分（含）－85分为四级，75分（含）－80分为五级，60分（含）－75分为六级，60分以下或工作未满一年的为七级。其中：一级为专家级柜员，二级为资深级柜员，三级为高级柜员，四级、五级为中级柜员，六级为初级柜员、七级为见习柜员。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十条', '一级柜员除得分满足条件外，原则上还须同时满足以下条件：取得中级及以上职称或金融理财师（国际金融理财师、注册内审师、注册税务筹划师、风险确认师、理财规划师、注册会计师、律师）等证书；获得银行业专业人员职业资格（目前为初级，待条件成熟后为中级）及相关岗位任职资格；业务技能全部达标（待条件成熟后还要考核等级）；具备培训员工的能力(待条件成熟后考核授课次数)，能够解决业务经营及管理中的重大疑难问题；工作质量、工作业绩优良，在本行工作10年以上。二级柜员除得分满足条件外，原则上还须具有相应的岗位任职资格，具备对新员工“传、帮、带”的能力，能够解决业务经营及管理中的复杂疑难问题；工作质量、工作业绩较好，在本行工作8年以上。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十一条', '经本行员工等级考评领导小组确定，在本行连续工作20年以上，能够完成基本工作任务，无严重违章违纪行为的，原则上可定为五级柜员；在本行连续工作30年以上，能够完成基本工作任务，无严重违章违纪行为的，原则上定为四级柜员。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十二条', '曾经担任行长、经理服从工作安排转岗（违规违纪转岗除外），在岗正常工作的，由本行员工等级考评领导小组根据其任职期间表现综合确定：1.累计主持工作10年以上的，原则上定为二至三级；　2.累计主持工作5－10年（含）的，原则上定为三至四级；3.累计主持工作5年（含）以下的，原则上定为四至五级。原分社负责人任职年限折半计算。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十三条', '曾经担任副职、会计、信贷等主管，现服从工作安排转岗（违规违纪转岗除外），在岗正常工作的，由本行员工等级考评领导小组根据其任职期间表现综合确定：1、累计任职10年以上的，原则上定为三至四级；2、累计任职5－10年（含）的，原则上定为四至五级；3、累计任职5年（含）以下的，原则上定为五级。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十五条', '引进人才、硕士研究生的等级在保护期内（三年，从2016年1月1日起）由本行员工等级考评领导小组确定，原则上定为四至五级。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十四条', '引进人才、硕士研究生的等级在保护期内（三年，从2016年1月1日起）由本行员工等级考评领导小组确定，原则上定为四至五级。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十五条', '员工表现优异，在评定期内作出突出贡献的，经本行员工等级考评领导小组确定，其等级可上调1至3个等级。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十六条', '直接定级与计算定级，以孰高的原则确定等级。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第四章   等级确定', '第十七条', '有下列情形的，取消评级、降级或缓评级。1.受到记大过及以上处分的，在处分期内取消原等级，降为见习级。2.受到服务对象投诉或其他因素引发重大舆情风险，对本行造成声誉影响的，已评级的降为见习级一年，未评级的缓评级一年。3.合规员工考核不达标违规记分一年内累计记分100分以上的，按原评级下降一个等级评定，累计记分150分以上的，已评级的取消等级一年，未评级的缓评级一年。　4.一年内累计请假3个月及以上的（工伤、生育假除外）已评级的降一级，6个月以上的不参加评级。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第五章    等级管理', '第十八条', '岗位变动员工等级的调整因工作需要调整岗位的，在下一个评定期前，保持原等级。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第五章    等级管理', '第十九条', '等级评定结果作为员工职位晋升、岗位安排的重要参考依据，与员工薪酬待遇等挂钩，挂钩方式比例按照相关办法执行。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第五章    等级管理', '第二十条', '实行柜员等级公示、复议制度。柜员对公示的等级评定结果有异议的，可向等级柜员评定领导小组提出书面复议申请。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第五章    等级管理', '第二十一条', '对违反考评工作纪律的，按照本行员工违规违章行为管理规定从重处理。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第五章    等级管理', '第二十二条', '本办法未尽事宜由本行员工等级考评领导小组研究确定。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第六章    附   则', '第二十三条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《柜员等级管理办法》', '第六章    附   则', '第二十四条', '本办法经职工代表大会审议通过后，自2016年1月1日起执行。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第一章  总 则', '第一条', '为规范xxx银行（以下简称“本行”）涉及恐怖活动资产冻结的程序和行为，维护国家安全和社会公共利益，根据《中华人民共和国反洗钱法》、《全国人大常委会关于加强反恐怖工作有关问题的决定》、《涉及恐怖活动资产冻结管理办法》等法律法规，特制定本办法。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第一章  总 则', '第二条', '本办法所称涉及恐怖活动资产是指涉及公安部发布的恐怖活动组织及恐怖活动人员名单中的组织及人员的资金或者其他资产。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第一章  总 则', '第三条', '本行应当严格按照公安部发布的恐怖活动组织及恐怖活动人员名单、冻结资产的决定，依法对相关资产采取冻结措施。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第二章  组织机构设置及职责', '第四条', '本行反洗钱领导小组负责制定涉及恐怖活动资产冻结管理办法并将执行情况及时向董事会报告。本行反洗钱工作办公室负责涉及恐怖活动资产冻结的日常工作，关注并及时掌握恐怖活动组织及恐怖活动人员名单的变动情况，负责指导本行涉及恐怖活动资产冻结的具体工作。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第二章  组织机构设置及职责', '第五条', '本行反洗钱工作办公室负责采取现场和非现场检查等方式，对本行执行本办法的相关情况进行监督管理。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第二章  组织机构设置及职责', '第六条', '本行需不断完善客户身份信息和交易信息管理，加强交易监测，提升识别恐怖分子和恐怖资产的能力。依法协助、配合公安机关和国家安全机关的调查、侦查，提供与恐怖活动组织及恐怖活动人员有关的信息、数据以及相关资产情况。依法协助、配合中国人民银行分支机构的反洗钱调查，提供涉及恐怖活动组织及恐怖活动人员资产的情况。对于采取冻结措施有关的工作信息应当保密，不得违反规定向任何单位及个人提供和透露，不得在采取冻结措施前通知资产的所有人、控制人或者管理人。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第七条', '收到公安部发布的恐怖活动组织及恐怖活动人员名单、冻结资产的决定时，应当立即采取冻结措施。对恐怖活动组织及恐怖活动人员与他人共同拥有或者控制的资产采取冻结措施，但该资产在采取冻结措施时无法分割或者确定份额的，应当一并采取冻结措施。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第八条', '采取冻结措施后，应当立即将资产数额、权属、位置、交易信息等情况以书面形式报告资产所在地县级公安机关和市、县国家安全机关，同时抄报资产所在地中国人民银行分支机构。采取冻结措施后，除中国人民银行及其分支机构、公安机关、国家安全机关另有要求外，应当及时告知客户，并说明采取冻结措施的依据和理由。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第九条', '有理由怀疑客户或者其交易对手、相关资产涉及恐怖活动组织及恐怖活动人员的，应当根据中国人民银行的规定报告可疑交易，并依法向公安机关、国家安全机关报告。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第十条', '本行及各营业网点不得擅自解除冻结措施。但符合下列情形之一的，应当立即解除冻结措施，并按照本办法第八条的规定履行报告程序：（一）公安部公布的恐怖活动组织及恐怖活动人员名单有调整，不再需要采取冻结措施的；（二）公安部或者国家安全部发现金融机构、特定非金融机构采取冻结措施有错误并书面通知的；（三）公安机关或者国家安全机关依法调查、侦查恐怖活动，对有关资产的处理另有要求并书面通知的；（四）人民法院做出的生效裁决对有关资产的处理有明确要求的；（五）法律、行政法规规定的其他情形。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第十一条', '涉及恐怖活动的资产被采取冻结措施期间，符合以下情形之一的，有关账户可以进行款项收取或者资产受让：（一）收取被采取冻结措施的资产产生的孳息以及其他收益；（二）受偿债权；（三）为不影响正常的证券、期货交易秩序，执行恐怖活动组织及恐怖活动人员名单公布前生效的交易指令。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第十二条', '对根据本办法被采取冻结措施的资产的管理及处置，应当按照中国人民银行、中国银行业监督管理委员会、中国证券监督管理委员会、中国保险监督管理委员会的相关规定执行；没有规定的，参照公安机关、国家安全机关、检察机关的相关规定执行。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第十三条', '资产所有人、控制人或者管理人对冻结措施有异议的，可以向资产所在地县级公安机关提出异议。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第三章  恐怖资产冻结流程及控制措施', '第十四条', '境外有关部门以涉及恐怖活动为由，要求冻结相关资产、提供客户身份信息及交易信息的，应当告知对方通过外交途径或者司法协助途径提出请求；不得擅自采取冻结措施，不得擅自提供客户身份信息及交易信息。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第四章  附 则', '第十五条', '本办法所称冻结措施，是指为防止其持有、管理或者控制的有关资产被转移、转换、处置而采取必要措施，包括但不限于：终止交易；拒绝资产的提取、转移、转换；停止账户的开立、变更、撤销和使用。本办法所称资产包括但不限于：银行存款、汇款、旅行支票、银行支票、债券、汇票和信用证等。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第四章  附 则', '第十六条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《涉及恐怖活动资产冻结管理办法》', '第四章  附 则', '第十七条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第一章   总  则', '第一条', '为规范xxx银行（以下简称“本行”）现代化支付系统的运行管理，确保支付系统的安全、稳定、高效运行，依据《现代化支付系统运行管理办法》及有关法律法规的规定，结合本行实际，制定本办法。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第一章   总  则', '第二条', '现代支付系统挂账分类正常挂账和非正常挂账。正常挂账是指系统接受的来账在账号、户名、行号和行名都相符的情况下，系统因某种特殊原因而将客户资金挂在应解付汇入待查款项科目的现象；非正常挂账是指支付系统接受来账时，由于账号、户名、行号和行名等信息中某一项或某几项不符的情况，系统自动将客户资金挂在应解付汇入待查款项科目下的现象。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第一章   总  则', '第三条', '各支行（部）应加强银行结算业务知识的宣传解释工作，要求客户在申请办理结算业务时填写账号、户名的全部信息，而不是部分信息或简化填写，要告知客户准确的支行（部）行名和支付系统行号。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第一章   总  则', '第四条', '各支行（部）每日应加强手工监控查询，发现支付系统挂账及时打印、处理，如有特殊原因，至迟在下一工作日处理完成。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第一章   总  则', '第五条', '本行辖内各支行（部）在处理支付系统挂账时适用本办法。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第二章  挂账处理要求', '第六条', '各支行（部）应及时查明支付系统挂账原因，对正常支付系统挂账应及时按规定通过挂账转汇交易将资金转汇入客户账；对非正常支付系统挂账应及时查明原因，并作挂账转汇或挂账退汇交易处理。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第二章  挂账处理要求', '第七条', '非正常挂账原因主要有：一是发出支付业务的汇款行对汇款人提供的有关汇款信息在系统中录入不正确或不完整导致；二是汇款人人为提供的汇款信息不正确或不完整，导致汇款行在系统中录入信息有误；三是由于银行对结算方面的业务知识宣传解释不够，致使客户对结算业务不甚了解，导致办理结算业务时提供信息不准确。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第二章  挂账处理要求', '第八条', '各支行（部）对非正常挂账必须坚持“有疑必查、有查必复、查必及时、复必详尽”的原则办理查询查复。（一）各支行（部）对本机构接收的支付业务有疑问的，应按查询查复规定的格式、标准向查复行发送查询信息，并打印查询书，登记查询登记簿。（二）各支行（部）收到查询信息，打印查询书，提交相关业务人员确认。相关业务人员依照查询书内容，认真查阅有关账册、凭证和资料，查明情况和原因，报运营主管审核同意，在当日营业时间内最迟不得超过次日上午10：00前（节假日除外），按规定的格式、标准向查询行发送查复信息，并打印查复书，登记查复登记簿。（三）各支行（部）收到查复信息后打印查复书。对所查询问题已得到明确答复的，经运营主管人员审核后，立即按照查复书要求退汇或转汇。（四）各支行（部）打印查复书要附在原查询书后面保存，并登记查询查复登记簿。对挂账转汇处理须以查复书作凭证附件的，则应在原查询书上注明查复情况和本行处理结果以及该笔支付业务的日期、编号等。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第二章  挂账处理要求', '第九条', '各支行（部）在处理非正常挂账时如不通过查询查复，而擅自进行挂账转汇，一是容易导致因转汇有误，入账串户和客户产生资金纠纷，形成操作风险；二是内部人员有意利用转汇操作，乘机盗取或挪用客户资金，形成道德风险。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第三章   附  则', '第十条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《现代支付系统挂账业务管理办法》', '第三章   附  则', '第十一条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第一章  总  则', '第一条', '为加强xxx银行（以下简称“本行”）现金业务库管理，确保资金和财产安全，根据《现金管理暂行条例》及相关规定，特制定本办法。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第一章  总  则', '第二条', '本办法所称现金业务库（以下简称“业务库”），包括营业网点为办理现金业务设置的业务库以及总行对所辖营业网点办理内部现金调拨和集中管理而设置的中心调拨业务库、网点尾款箱集中保管库。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第一章  总  则', '第三条', '业务库管理包括业务库库房建设标准，机构职责与人员管理，业务库钥匙、密码管理，业务库现金、实物管理，业务库安全管理等。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第二章  业务库库房建设标准', '第四条', '业务库库房（以下简称“库房”）必须建在本行楼（院）内，不得直接接触其他单位和居民住宅，不得裸露于公共场所，库体上下、四周环境应安全可靠。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第二章  业务库库房建设标准', '第五条', '库房一律采用钢筋混凝土六面浇筑结构，符合《中华人民共和国金融行业标准—银行业务库》JR/T0003-2000。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第二章  业务库库房建设标准', '第六条', '库房安全设施配备齐全，管理良好。（一）库房内一律安装防爆灯，并保证足够亮度。库房电源开关应设于库房外，并有开关显示。库房应配备应急灯，应急灯不得在库内充电，库内严禁使用明火。（二）库房内电源线不得裸露，并配备消防器材。（三）库房的报警系统，监控系统、通讯联防设施、防卫器材、消防器材、指纹仪、密码锁、库房门、应急按钮等安全防范设施应按规定使用并经常检查测试，保证有效。（四）库房应配备防潮、通风、恒温设备，防止发生火灾、水浸、霉烂、鼠咬等事故。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第二章  业务库库房建设标准', '第七条', '新建、改建库房建造完毕报公安、消防部门验收合格后正式使用。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第三章  机构职责与人员管理', '第八条', '要高度重视业务库管理工作，加强领导，严格管理，做好安全防范工作，做到坚固可靠，管理严密，责任明确，确保账款相符、账实相符、账账相符。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第三章  机构职责与人员管理', '第九条', '业务库保管的范围：人民币和外币现钞、贵金属、重要单证、业务印章、票样、真假币鉴别手册、代保管物品等。凡属业务库保管范围的保管品，均须分类逐项建立保管账簿，建立相应的表内、表外科目账簿，序时记载，及时核对，互相制约。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第三章  机构职责与人员管理', '第十条', '业务库管理人员必须选配作风正派、忠实可靠、工作责任心强的员工担任。可按实际工作需要设置业务库主管岗、管库岗、清分整点岗等基本岗位，并配备符合各岗位要求的人员。业务库各岗位人员应严格履行岗位职责，确保业务库工作正常运营。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第三章  机构职责与人员管理', '第十一条', '管库员必须坚持“五同”规定，即两名管库员同开库、同进库、同在库、同出库、同锁库。对库存物品的安全，两名管库员负同等责任。管库人员调动、休假时必须由指定的管库员平行交接，在主管的监交下办理现金、实物等的交接，并登记交接登记簿。管库员不得从事账务处理和账务监督工作。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第四章  业务库钥匙、密码管理', '第十二条', '业务库要装备两把不同钥匙的锁，并备正、副钥匙各一套。（一）业务库正钥匙的管理。业务库正钥匙由管库员分别掌管，坚持平行交接，不得交叉使用。在工作时间内应随身携带，做到自开、自锁、自管，严禁置于它处或交他人代开、代锁、代管。营业结束关库后，应将库房钥匙放入监控下的专用保险柜内分别保管。任何时间不得将库房钥匙带出行外。（二）业务库副钥匙的管理。业务库副钥匙由业务库主管会同分管行长、运营管理部负责人当面分别密封，加盖业务公章，办理登记签收手续，妥善保管。遇有特殊情况必须动用时，需经分管行长批准，由分管行长、运营管理部负责人共同启封，并认真做好动用原因和时间的详细记录。使用完毕后，应按照要求重新密封保管。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第四章  业务库钥匙、密码管理', '第十三条', '业务库钥匙丢失必须查明原因，追究责任，由安全保卫部负责及时更换库锁。更换业务库锁时，应同时收回副钥匙，重新密封保管新锁的副钥匙，并做好登记工作。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第四章  业务库钥匙、密码管理', '第十四条', '业务库设有密码锁的，应严格管理，做到自开、自锁，严禁泄密。业务库密码、密码副本的管理参照业务库正副钥匙的管理执行。经管密码锁人员变动时，应立即更换密码，不得沿用旧密码。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第四章  业务库钥匙、密码管理', '第十五条', '业务库钥匙、密码换人使用必须严格履行交接手续。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第五章  业务库现金、实物管理', '第十六条', '业务库现金调拨管理（一）营业网点向总行领、解款。由调出机构填写“现金调拨单”，随同现金一并送至调入机构，调出机构、提解员、调入机构分别按规定签字确认。（二）总行向人民银行缴、提款。填写现金解缴单或现金支票，按照缴提款有关程序办理。（三）总行对营业网点上解款项必须及时进行清分整点，不得拖延。未经清分整点款项不得上缴人民银行。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第五章  业务库现金、实物管理', '第十七条', '现金出入库管理凡出入库的现金必须由管库员双人复核，保证账款相符。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第五章  业务库现金、实物管理', '第十八条', '现金实物保管规定（一）业务库内保管的现金、实物应分类逐项建立保管明细账。（二）入库的现金必须达到“五好钱捆”标准，即点准、挑净、墩齐、捆紧、盖章清楚，按券别码放整齐。（三）入库保管的重要单证和贵重物品等经业务库主管批准登记后，按种类入箱入柜保管。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第五章  业务库现金、实物管理', '第十九条', '尾箱入库管理。入库保管的尾箱应标明行名及编号，上双锁并进行登记，管库人员清点款箱数量、与尾箱交接人员当面查验封箱无误后，办理交接手续。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第五章  业务库现金、实物管理', '第二十条', '假币收缴管理。收缴的假币应视同人民币管理入库保管，登记假币收缴登记簿，定期上缴人行，严防已收缴假币流入市场。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第五章  业务库现金、实物管理', '第二十一条', '轧库管理（一）总行业务库严格执行双人轧库制度，坚持每日轧库不少于两次。管库员交接班时必须进行轧库。（二）营业网点坚持“一日三碰库”制度。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第五章  业务库现金、实物管理', '第二十二条', '业务库不得直接对外办理现金收付业务，一切现金收付都必须通过柜面办理。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第六章  业务库安全管理', '第二十三条', '坚持双人管库、双人守库原则。管库员应分工明确，对库房安全负同等责任。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第六章  业务库安全管理', '第二十四条', '非管库人员进出库房需经有权人批准，并进行登记。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第六章  业务库安全管理', '第二十五条', '业务库库房安全管理实行分工负责制。（一）库内安全由管库员负责，随时检查，严防发生火灾、水浸、霉烂、鼠咬等情况。（二）业务库库锁、监控系统、报警系统、通讯防范设备、防卫器材等安装、更换和维修由安全保卫部门负责。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第六章  业务库安全管理', '第二十六条', '库款押运及守库的安全管理由安全保卫部（或外包的第三方保安公司）负责。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第七章 查库管理', '第二十七条', '本办法所称的“查库”是指对业务库及自助设备钞箱的现场检查。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第七章 查库管理', '第二十八条', '查库必须逐一清点，做到全面核对无误，确保账账、账款、账实相符。查库时，应检查下列内容：(一)清点现金、贵金属、重要单证、假币实物、代保管物品等是否账实相符，有无白条抵库或挪用库款情况；自助设备检查当日钞箱是否账实相符。(二)整捆库存现金、凭证是否按规定分类整齐，拆捆、拆把的零散现金、凭证是否入柜保管，有无发生鼠咬、虫蛀、霉烂等现象，库房是否整洁，有无存放私人物品或其他物品现象。(三)现金、凭证出入库是否按规定办理出入库交接手续。(四)入库保管的尾箱是否加双锁保管，寄库保管的现金是否双封保管。(五)库房防火、防盗、防水、防潮以及安全措施是否落实到位。(六)库房钥匙、密码的使用以及保管是否符合规定。(七)出入库房“五同”制度是否执行到位。(八)查库登记是否详实，发现问题是否及时整改到位。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第七章 查库管理', '第二十九条', '查库频率根据不同岗位设置不同查库频率，分为按季查库、按月查库、按周查库。（一）总行业务库1.分管行长按季不定期查库一次。2.运营管理部负责人按月不定期查库一次。3.业务库主管每周不定期查库一次。（二）网点业务库及柜员尾箱（含自助设备钞箱）1.运营管理部不定期组织对全辖网点业务库、尾箱进行抽查，每年至少对所有营业网点循环检查一次。2.营业网点负责人每月至少对本网点业务库、尾箱检查一次，按季增加一次随机突击查库，即“3+1”查库。3.营业网点运营主管对所有柜员尾箱和自助设备钞箱每周至少查库一次。以上查库，管库员或柜员必须始终陪同在场，不得离开。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第七章 查库管理', '第三十条', '查库时，查库人要亲自动手核点库存，不得监而不查，敷衍马虎。严禁以管库员提供的现金登记账代替会计账与库款进行核对。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第七章 查库管理', '第三十一条', '查库结束，应将查库情况和存在问题在查库登记簿上详细记载，由查库人和被查人共同签字确认。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第七章 查库管理', '第三十二条', '对查库中发现的重大问题，应立即查明原因，分别性质，严肃处理。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第七章 查库管理', '第三十三条', '查库权限（一）人民银行、银监部门和省联社等部门组织的查库，应出示查库介绍信和查库人身份证明，并由总行指定专人负责陪同，办理查库手续，接受查库。（二）当地公安部门对业务库及守卫的安全保卫检查，必须出示公安部门的安全检查介绍信和本人身份证件，并由总行相关部门人员陪同，但禁止进入内库检查。对公安部门的夜间安全检查，只查岗不查库。（三）总行组织查库，查库人员应出示介绍信和查库人员身份证明，由被查行运营主管陪同。（四）营业网点内部查库，由网点相关人员负责完成。（五）查库时，被查单位负责人、管库员、柜员应始终陪同在场，自觉接受检查，回答提出的问题。（六）安全保卫部的安保检查，原则上不得进入库内检查。如确需入库检查库内安全设施的，应按照查库手续办理，并由被查行领导、管库员始终陪同在场。检查时，只检查库内安全设施，不得清点现金、重要单证等库存实物。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第八章  罚 则', '第三十四条', '业务库管理工作应定期进行考核，对有章不循、违章操作、玩忽职守，造成重大差错或责任事故的相关人员，按照本行员工违规违章行为管理规定追究相应责任。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第九章  附 则', '第三十五条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《现金业务库管理办法》', '第九章  附 则', '第三十六条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第一章  总  则', '第一条', '为进一步加强xxx银行（以下简称“本行”）人民币现金收付的柜面监督，维护正常的金融秩序以及存款人的合法权益，防范和打击利用大额现金支付进行的经济犯罪活动，根据《现金管理暂行条例》、中国人民银行南京分行《商业银行现金收付柜面监督办法》、反洗钱法律法规及相关规定，结合本行实际，制定本管理办法。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第一章  总  则', '第二条', '现金收付监督管理是指本行对客户大额现金收付实施客户预约、审批、备案等监督管理的一种制度。以下大额现金收付行为属于监督管理范畴：（一）向开户单位一次性支付或在一个工作日内累计支付5万元（含）以上人民币现金的；（二）向个人一次性支付或在一个工作日内累计支付5万元（不含）以上人民币现金的；（三）接受个人单笔金额超过20万元（含）人民币存款的。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第一章  总  则', '第三条', '本办法适用于本行办理人民币现金业务的各支行（部）。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第一章  总  则', '第四条', '凡在各支行（部）开立账户的机关、团体、部队、企业、事业单位和其他单位（简称“开户单位”）以及个人都必须接受本行的现金收付监督管理。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第二章   客户现金收付', '第五条', '加强对开户单位现金支取的柜面监督，严格执行对公账户现金支付管理的各项规定，认真审查开户单位的现金使用范围，不得违反人民银行《现金管理条例》及现金支付相关规定为客户办理现金支付业务。各支行（部）可为开立基本存款账户的客户在下列现金使用范围内办理现金收付：（一）职工工资、各种工资性津贴；（二）个人劳务报酬，包括稿费、讲课费及其他专业工作报酬；（三）支付给个人的各种奖金，包括根据国家规定颁发给个人的科学技术、文化艺术、体育等各种奖金；（四）各种劳保、福利费用以及国家规定的对个人的其他支出；（五）向个人收购农副产品和其他物资的价款；（六）出差人员必须随身携带的差旅费；（七）限额10000元以下的零星支出；（八）因采购地点不确定、交通不便、抢险救灾及其他特殊确需使用现金的。开户单位因前款第五项所列原因办理转账结算不够方便，必须使用现金的应向本行提出书面申请，由开户单位财务部门负责人签字盖章，经支行（部）审查批准后，予以支付现金。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第二章   客户现金收付', '第六条', '各支行（部）对开立一般存款账户的开户单位不予出售现金支票和支付现金业务；专用存款账户和临时存款账户未经人行批准一律不得提取现金；对收款人或付款人为单位的，不得签发和解付现金银行汇票和现金银行本票。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第二章   客户现金收付', '第七条', '因生产经营需要，各支行（部）为个体经营者签发现金银行汇票或银行本票。单张现金银行汇票或银行本票的金额超过30万元（不含）或一日内同一收款人签发两张以上（不含）的现金银行汇票或银行本票，须由本行批准，并报人民银行当地分支机构备案。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第二章   客户现金收付', '第八条', '凡是单位持有的银行卡，在使用过程中需要存入资金的，必须从单位的基本存款账户中转入，不得缴存现金。对存入单位银行卡中的转账支票，必须将收款单位账户名称填写清楚，不得将发卡的开户行作为收款人。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第二章   客户现金收付', '第九条', '严禁各支行（部）为单位持有的银行卡支付现金。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第三章  大额现金收付审批、登记和备案', '第十条', '开户单位提取大额现金（工资性现金、农副产品采购现金支取除外），必须填写《大额现金支取登记审批表》，主要内容包括账户性质、提现单位名称、账号、提现人姓名、证件号码、支取时间、金额、用途等。各支行（部）严格按规定审查客户大额现金的用途和范围。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第三章  大额现金收付审批、登记和备案', '第十一条', '大额现金支付分级审批提取现金额度在5万元（不含）以内的由柜员直接审核支付；提取现金额度在5万元（含）以上50万元（不含）以下的大额现金支取，经柜面经办人员以及支行（部）运营主管二级审核后，由支行（部）负责人审批；提取现金额度在50万元（含）以上100万元（不含）以下的大额现金支取，填写大额现金支取登记审批表，由各支行（部）负责人审批或经本行授权后由运营主管审批；提取现金额度100万元（含）以上，除履行50-100万元的大额现金支取审批手续外，填写大额现金支取登记审批表，由本行授权行长或运营主管审批。现金分级审批需逐级履行手续，严禁越权审批。前款规定的“5万元”指本息合计数。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第三章  大额现金收付审批、登记和备案', '第十二条', '各支行（部）为单位客户办理5万元（含）以上、个人客户20万元（含）以上大额取现业务至少提前一天预约登记。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第三章  大额现金收付审批、登记和备案', '第十三条', '大额现金备案按《金融机构大额交易和可疑交易报告管理办法》执行，按规定向相关部门报备。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第四章   内控制度建设', '第十四条', '各支行（部）必须加强现金管理，提高现金收付管理重要性的认识。严禁现金科目透支，大额取现转存业务必须按照先取后存的流程操作。凡能够通过交易码实现转存及卡卡转账的业务，需统一通过交易码执行。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第四章   内控制度建设', '第十五条', '各支行（部）严格按照本办法和人行有关规定做好现金管理工作，认真执行审批、备案制度，及时上报有关现金管理报表。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第四章   内控制度建设', '第十六条', '加强对现金使用的管理，坚决制止逆程序违规操作，确保现金收支活动的真实性、合法性、合理性，确保金融市场稳定。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第五章   附  则', '第十七条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《现金收付监督管理办法》', '第五章   附  则', '第十八条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第一章  总  则', '第一条', '为规范xxx银行（以下简称“本行”）现金清分管理工作，提升后台现金集中处理能力，提高回笼券清分质量，根据《现金管理暂行条例》及相关制度要求，特制定本办法。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第一章  总  则', '第二条', '本办法所称现金清分是指对回笼的本、外币现金进行的复点、鉴伪、版别分类、挑残、扎把、打捆等集中作业。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第一章  总  则', '第三条', '本办法所称现金管理团队是指总行负责后台现金集中处理的作业中心。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第一章  总  则', '第四条', '营业网点应设立专职或兼职的现金清分岗，从事回笼券的清分整点工作。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第二章  基本原则', '第五条', '现金管理团队隶属于运营管理部，运营管理部对现金管理团队的工作实施管理和监督。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第二章  基本原则', '第六条', '基本原则（一）转移交接原则。清分现金出入库均应登记《现金清分交接登记簿》，做到记载规范、内容完整。（二）确认负责原则。清分人员对经手清分的现金负责。已清分完的成把（捆）现金，经办员在腰条及封签上的复点员处签章确认，复核员在封签上的检查员处签章确认；已清分完的不成把现金由经办员在腰条上注明金额后，与复核员共同签章确认。（三）全面监控原则。现金清分必须二人以上在场，清分工作应实施全面录像监控与入侵报警，清分间、交接区、通道、进出大门等重要部位须纳入录像监控范围，确保安全。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第三章  岗位设置', '第七条', '现金管理团队设立业务主管一人，清分员两人以上，清分员可采用兼职方式，但管库员不得兼职清分员。实施外包的现金管理团队，应配备一名现场业务管理人员，对清分工作实施现场管理、监督。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第三章  岗位设置', '第八条', '清分人员基本要求：（一）身体健康、作风正派，工作责任心强。（二）认真执行《中国人民银行假币收缴、鉴定管理办法》、《不宜流通人民币挑剔标准》、《人民币五好钱捆标准》等相关文件。（三）熟练掌握点钞技能，具备残损人民币挑剔及真假货币鉴别能力。（四）持有《反假货币上岗资格证书》。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第四章  清分要求', '第九条', '现金清分包括机械清分和手工清分。机械清分指通过清分机具进行现金清分的处理过程。手工清分指对不宜采用清分设备清分的现金进行手工清点处理。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第四章  清分要求', '第十条', '全额清分指对外付出（柜台支付、自助设备支付、交存人民银行发行库回笼款、相互取现）的纸币现金全部经过清分。10元及以上面额必须全额机械清分，5元及以下券别如不适合使用机械清分，可以组织手工清分。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第四章  清分要求', '第十一条', '总行业务库集中清分（一）现金出库根据营业网点现金回笼及总行业务库库存情况提出清分需求。管库员根据需求办理现金出库，与清分人员在交接区办理交接，并在现金清分交接登记簿上签章确认。（二）现金清分业务主管将待清分现金分配给清分员，清分员按规定进行清分并记录冠字号码，并标明“已清分”字样。（三）现金入库日终时，清分整点员将当日已清分的现金登记现金清分登记簿，与未清分的现金一并交管库员，管库员核对无误后办理入库手续。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第四章  清分要求', '第十二条', '营业网点对外付出的现金必须全额清分，并按规定做好冠字号码的记录工作。（一）营业网点调入未经清分的和柜面回笼的现金必须交清分人员进行清分。现金交接应手续严密，操作规范，当面点清。（二）现金清分人员按规定进行清分并记录冠字号码，并标明“已清分”字样。（三）当日清分工作结束后，清分人员将当日已清分的现金登记现金清分登记簿，与未清分的现金分别保管。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第四章  清分要求', '第十三条', '未经清分的现金不得交存人民银行发行库，交存人民银行发行库的钱捆上应加盖“已清分”标识。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第四章  清分要求', '第十四条', '从人民银行发行库（包括相互取现）支取其他银行交存的带有“已清分”标识的钱捆和人民银行钞票处理中心清分后的钱捆可不重复清分，直接对外支付。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第五章  基本设施', '第十五条', '清分间应视同业务库，按照相关规定进行设计、建设、审批及启用。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第五章  基本设施', '第十六条', '清分间应设置在业务库库区范围内，并相对独立，面积须适应业务发展需要，清分间与业务库应由相对安全、封闭的交接通道连接。如单独设立清分间的，应在清分间外设立交接区。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第五章  基本设施', '第十七条', '清分间及交接区应合理配备报警系统、监控系统、消防器材及通讯联防设施，按规定使用并定期检查测试，保证运行正常。监控视频资料的调阅，须经运营管理部门负责人和安全保卫部门负责人同意。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第五章  基本设施', '第十八条', '清分间需设置门禁系统或配备锁具，安装防护门窗，并使用不带抽屉的专用清分工作台。清分间的密码钥匙应由专人负责，严密保管。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第五章  基本设施', '第十九条', '清分间应配备防潮、通风、灭火、除尘、恒温、杀菌等设备，防止发生火灾、霉烂、鼠咬等事故。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第五章  基本设施', '第二十条', '现金管理团队的清分机具，应在确保业务正常运作的基础上，以实际需要为原则配置：（一）点钞机。（二）捆钞机。（三）清分机。（四）扎把机。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第六章  清分间管理', '第二十一条', '清分间实行封闭式管理。进入清分区，所有工作人员一律着无袋工作服，佩戴证件，与工作无关的物品一律不得携带入内。非清分人员严禁进入清分场所，除本行主管部门人员外的其他人员因工作需要进入清分间的，需提供总行开具的介绍信及有效身份证件后方可进入。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第六章  清分间管理', '第二十二条', '非正常工作时间任何人不得进入清分间，如需进入须经分管行长批准。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第六章  清分间管理', '第二十三条', '维修人员进入清分间原则上必须进行空库作业，遇有紧急情况须在现金清分期间进入清分间进行设备维护作业的，须经运营管理部门负责人同意后，凭介绍信及有效身份证件方可进入维修，维修时由业务主管监督，确保资金安全。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第六章  清分间管理', '第二十四条', '清分工作人员须保持清分间整洁，工作结束，待与管库员交接款项完毕，清扫现场，关闭电源后，方可离开。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第七章  差错管理', '第二十五条', '清分人员发现错款，需换人复点，经复点确为错款的，应立即报告业务主管并由其复核确认。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第七章  差错管理', '第二十六条', '业务主管确认发生错款的，应留存原封签、腰条，准确记录发现时间，登记差错登记簿，保留监控录像。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第七章  差错管理', '第二十七条', '如发生长短款、假币，应通知差错行，由差错行查明原因并确认后，按规定进行相应账务处理。对于收缴的假币，应及时入库保管不得退回差错行。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第八章  附  则', '第二十八条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《现金清分管理办法》', '第八章  附  则', '第二十九条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第一章  总  则', '第一条', '为加强xxx银行（以下简称“本行”）移动银行（二期项目）规范化管理，确保移动银行（二期项目） 安全稳定运行，防范相关风险，根据《中华人民共和国计算机信息系统安全保护条例》结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第一章  总  则', '第二条', '本办法所指移动银行（二期项目）是指在平板电脑等移动智能设备上安装应用，配合蓝牙连接的密码键盘等外围设备，通过3G、4G网络为客户办理移动开卡等非现金服务的业务。主要业务范围包括：借记卡开卡、借记卡激活，电子银行及代缴费等渠道签约服务。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第一章  总  则', '第三条', '移动开卡（签约）操作人员与移动设备使用人必须为同一人，严格遵守“一机一人”原则，严禁机具、凭证等混用，移动开卡（签约）操作人员必须使用移动银行专用柜员登录并办理相关业务。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第二章 职责与权限', '第四条', '本行信息科技部负责做好软硬件环境搭建、设备管理与调试、远程授权模式设置及有关技术支持等工作。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第二章 职责与权限', '第五条', '本行运营管理部负责确定移动银行（二期项目）业务开办范围、制定制度细则和操作流程、开展业务培训、审批移动银行操作员、配合合规管理部门开展业务检查等工作。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第二章 职责与权限', '第六条', '本行二级运维负责建立操作人员的操作号、绑定指纹。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第二章 职责与权限', '第七条', '本行电子银行部负责做好业务营销活动的推广与指导。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第二章 职责与权限', '第八条', '本行合规管理部负责做好移动银行终端业务的合规性评估，对相关制度流程进行合规性审核，定期对移动银行业务的制度执行情况进行合规性检查。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第二章 职责与权限', '第九条', '本行风险管理部负责做好移动银行（二期项目）各项业务的风险监测。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第二章 职责与权限', '第十条', '各营业机构负责移动银行（二期项目）日常运行管理工作。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第三章 业务流程', '第十一条', '移动银行（二期项目）业务流程如下：（一）由支行根据实际情况填写柜员申请表，向运营管理部申请建立移动银行操作员号。（二）日初，移动银行操作员在核心签到，并领取移动渠道的重要空白凭证。（三）移动银行操作员携带移动终端及外设，现场为客户办理业务，采集客户信息，上传拍照资料，提交授权团队，等待授权团队授权。（四）授权团队审核员登录移动银行后台系统，根据移动银行操作员提交的资料进行审核，检查相关要素是否合规。对于符合业务规范的业务请求审核通过，对于不符合要求的拒绝并退回。业务拒绝后，移动银行操作员需要对客户信息进行补充，检查资料后重新提交授权。（五）审核通过后，移动银行操作员继续为客户办理业务，完成业务流程，并将办理好的凭证交给客户，然后开始下一笔业务的办理。（六）日终，柜员在柜面登录核心业务系统，通过60703交易重新打印相关交易凭证。将未使用的重要空白凭证上缴主出纳，最后柜员签退。传票由经办人员签字，运营主管审核加盖业务章，单独填写传票封面，随当天传票带至事后监督团队扫描归档。（七）事后监督团队按照规定对移动银行业务传票进行扫描和业务审核，并将传票归档保管。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第四章 风险控制', '第十二条', '客户经理开展到户服务，必须坚持“双人到户服务”原则，除操作员外，同时到户服务的另一名员工应在相关凭证上签字，以示已复核，并承担连带责任。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第四章 风险控制', '第十三条', '移动银行操作员必须报经运营管理部批准设立，未经同意，不得由其他在核心系统有柜员号的人员使用移动银行为客户办理业务。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第四章 风险控制', '第十四条', '移动银行操作员领取重要空白凭证须做好事前调研，根据当天业务开展情况，预估使用数量进行领取；领取时，须由运营主管在领用单据上签字确认，并建好电子登记簿进行登记。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第四章 风险控制', '第十五条', '移动银行操作员应做好相关保密工作，外出办理业务时应做好客户信息的保密工作，同时，不得泄露本行相关信息。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第四章 风险控制', '第十六条', '移动银行业务使用的重要空白凭证，应严格按照相关业务制度进行领取、使用和上交。明确重要空白凭证带出后，由移动银行操作员妥善保管，日终凭证入库保管，严防重要空白凭证被盗、遗失或发生其他问题，确保业务安全开展。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第四章 风险控制', '第十七条', '移动银行操作员采集客户信息，应按照个人金融客户信息采集的要求进行信息采集登记，并由客户在信息采集表上签字，确认信息用途，采集照片时应做到真实、准确、完整，确保业务的合规性，保障客户及本行利益。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第四章 风险控制', '第十八条', '授权团队在后台系统授权时，应认真审核客户信息资料等内容，不符合规范的应予拒绝，确保业务的合规性，防范业务风险。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第五章 设备管理', '第十九条', '移动银行（二期项目）的设备包含移动终端和外围设备（主要指密码键盘、读刷卡器、身份证阅读仪一体机）。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第五章 设备管理', '第二十条', '移动银行设备统一由信息科技部管理，负责设备的使用登记，安装调试，软件安装以及设备维护。未经科技部门批准，任何人不得拆卸设备或挪作他用，不得随意增减移动终端上的应用软件。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第五章 设备管理', '第二十一条', '设备在使用期间，由具体领用网点进行管理，各网点应自行明确专人负责管理设备，不得有被盗、损坏或遗失等情况发生。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第五章 设备管理', '第二十二条', '设备使用期间，每日日终设备必须返回网点，不得被任何人私自带回家中，设备不用后，各使用单位应派人将移动终端和外设上缴信息科技部。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第六章 违规处罚', '第二十一条', '各支行（部）在移动银行（二期项目）的业务经办和管理中，不得有下列行为：（一）未经审批或未按业务流程办理移动银行业务。（二）未按规定申请移动银行操作员，随意由核心系统柜员登录办理业务。（三）未按规定妥善保管客户资料，导致客户信息泄漏。（四）未按规定采集客户资料，不能保证客户资料真实性，采集上传虚假客户信息。（五）未按规定妥善保管重要空白凭证，导致发生重要空白凭证被盗、损坏或遗失等情况。（六）未按规定妥善保管移动银行设备，导致发生移动银行设备被盗、损坏或遗失等情况。（七）其他违规行为，导致出现重大差错或造成损失的。各支行（部）如发生以上违规行为，将追究相关人员责任，并严格按照本行相关制度进行处理。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第七章 附 则', '第二十二条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《移动银行（二期项目）业务管理办法》', '第七章 附 则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第一章  总 则', '第一条', '为加强xxx银行（以下简称本行）综合柜员制管理，准确、及时处理柜面业务，保障客户和本行资金安全，提高会计工作核算质量和管理水平，现根据《支付结算办法》、《储蓄管理条例》、本行《会计基本制度》及《出纳基本制度》等有关制度的规定，结合“江苏省农村信用社综合业务系统”（以下简称“综合业务系统”）的实际应用情况和我行实际，特制定本办法。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第一章  总 则', '第二条', '综合柜员制是按照综合业务系统规定的处理权限和操作流程，在严密监控和严格监督下，由单一柜员或多个柜员组合，综合办理储蓄、对公、银行卡、代收代付、中间业务等柜面业务及其会计核算的劳动组合方式。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第一章  总 则', '第三条', '实施综合柜员制应具备以下条件：㈠营业机构会计出纳管理工作规范。㈡营业机构综合柜员熟悉会计、出纳、结算等业务制度规定，熟练掌握综合业务系统操作技能。㈢安全防范措施达标，监控系统完备，对柜员整个营业时间段进行全程录像，能通过影像回放观看柜员及客户业务处理全过程，且能达到看清连续点钞动作及票面清晰的效果。㈣终端、打印机、点钞机、钱箱等其它工作机具配备齐全。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第一章  总 则', '第四条', '实行综合柜员制的支行按照相互制约、业务需求的原则合理设置岗位，支行（部）各设置运营主管1名，根据人员设置综合柜员窗口，有条件的支行可设专职主出纳。 ㈠综合柜员负责处理对公、储蓄、银行卡、代收代付、中间业务等日间柜面业务，并分管相关章印。㈡各综合柜员负责对指定柜员处理的业务进行事中（滞后）交叉复核。㈢对部分个性化业务（如签发汇票、签发本票、发放贷款、收贷、收息、汇兑录入、网内贷记、储蓄存款挂失、止付、冻结、中间业务等）可指定窗口办理。㈣各支行主出纳负责库存现金的调剂和调拨，重要空白凭证、有价单证及抵质押物品的保管。㈤各支行内部管理应保持相对的独立性，岗位设置及柜员休息原则上由运营主管安排。㈥柜员每人一柜员包，当班时直接向主出纳申请调现临柜，日终及休息时将现金上解主出纳库存清零，非正常休息日休假时重要印章上交运营主管进行双人封存，重要空白凭证上交主出纳随包上解。㈦各柜员日间工作时超限额部分的现金必须及时解缴主出纳。㈧运营主管每日认真填写《主管会计坐班工作日志》，如遇休息指定专人填写。㈨运营主管按照有关规定管理内部事务，对需要授权的交易进行授权，负责事前、事中、事后监督和日、月、年终数据核对。各岗位人员必须各司其职，密切配合，严格执行印、证分管等制约制度及综合业务系统的权限管理规定。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第一章  总 则', '第五条', '建立安全有效的监督、复核机制。综合业务系统采取了实时授权前提下的滞后复核与实时复核相结合的方式，以加强对柜员业务操作的事前、事中、事后的安全控制。㈠事前控制。根据本行制定的交易和授权标准，结合业务实际情况和柜员素质，经本行核准后制定各柜员交易、授权的金额标准。详见第三章综合业务系统操作授权管理基本规定。㈡事中复核。柜员间或专职复核员应对接柜柜员处理的业务进行事中复核。详见第四章复核操作制度。㈢事后监督。运营主管必须加强督导，对柜员的业务操作进行事后监督，详见第五章事后监督。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第二章  柜面业务基本规定', '第六条', '柜面业务管理基本规定㈠柜员必须严格按照《支付结算办法》、《储蓄管理条例》、《现金管理办法》、《人民币银行结算账户管理办法》、《会计出纳基本制度》等规章制度以及有关规定，办理对公结算、储蓄、贷款、银行卡、中间业务等各项柜面业务，为客户提供优质高效的服务。㈡柜员受理业务必须认真审查凭证要素，坚持使用印鉴系统进行客户印鉴的核对，对系统不能识别的印鉴卡换人进行手工折角核对，严禁直接人工通过或不核对印鉴。办理现金收付，必须坚持“现金收入先收款后记账，现金付出先记账后付款；转账业务，先借后贷”的原则，受理票据和大额付现须查验取款人身份证，他行票据收妥抵用；定期存款提前支取严格按规定办理，挂失、查询、冻结、扣划等特殊业务必须换人复核办理，以确保业务交易合规合法，真实准确。㈢柜员发放贷款时必须认真审查贷款面谈面签相关资料，要求内容完整、一致、没有涂改，抵质押手续齐备、符合规定；认真审查借款人、担保人、经办人、审批人等签章，要求签章相符、齐全；认真审查借款人等身份证件，确认姓名、身份证号码、签名真实、一致；认真审核信贷员设定的科目、对应账户、计结息方式、利率等内容，确保正确无误；认真审查贷款管理规定应审查的其他内容，切实履行贷时审查工作职责。㈣柜员必须严格按照综合业务系统的操作流程办理各项业务，确保办理及时、核算准确。㈤签发汇票（本票）必须严格执行印、证分管的制度，各支行必须明确专人分别负责汇票、本票的签发、盖章，执行平行交接。㈥柜员应严格按照权限办理业务，严禁越权操作，对综合业务系统约定需要授权的各类交易或交易金额超出柜员权限的交易必须经授权人（运营主管、运行管理部、分管行长等）授权或审批后办理，不得擅自处理。㈦柜员办理业务必须以合法有效的会计凭证作为交易处理的依据，柜员交易的每笔业务必须打印通用凭证等证明交易合法的记账凭证，并与原始凭证的有关要素核对一致，原始凭证作通用凭证的附件。㈧柜员办理现金收付必须根据客户或运营主管编制的合法凭证办理，柜员不得编制其他现金收付凭证（现金缴税、代收水电费等除外）；柜员办理业务应坚持一户一清，不得同时为多个客户办理业务；柜员不得为本人办理业务，也不得代客户保管存折（卡）、支票、章印、电子令牌、身份证等重要物品，不得代理客户办理存取款、挂失、购买支票、更换印鉴等银监规定不得代客办理的业务。㈨柜员办理跨柜交易时，业务发起柜员应及时传递凭证，接收柜员应认真核对、及时处理，不得故意压票或拒绝办理。㈩柜员根据账户管理办法的有关规定，受理结算账户的开立、变更、撤销等业务，经运营主管审核后及时上报人行核准或备案。运营主管要妥善保管开户资料并登记《开销户登记簿》。非经总行批准，支行不得办理内部账户的开设。（十一）柜员发生差错不论金额大小应及时向运营主管汇报，按规定处理；柜员办理错账冲正业务，涉及客户收付款的，必须征得客户（尤其是收款客户）签字同意，同时经运营主管授权并在抹账凭证上签字后方可办理，任何柜员未经批准不得办理冲正业务，涉及损益类账户的冲正，还应上报总行批准；柜员错账冲正必须经运营主管批准，运营主管应查明原因并在《运营主管坐班工作日志》中做好记录；柜员同时在《前台柜员一日常规日志》中进行登记，注明冲正账号、金额及原因，在冲正的原始凭证上注明于第几笔流水进行冲正，在冲正凭证上注明是冲正的哪笔流水；运营主管每天打印机构冲正交易明细表。柜员补打凭条或通用凭证必须填制重新打印申请书，经运营主管签字确认后由授权团队进行授权，运营主管在原凭证或补打凭证上注明补打事由。（十二）支行要明确专柜办理汇兑业务、网内贷记、农信银汇兑业务、同城业务的接收、转汇、复核，及时打印《支付系统专用凭证》，根据支付系统来账凭证及时做好票据存根联、银承托收第二联的抽取归档。柜员重打支付系统来账凭证必须经主管会计在作废和重打的凭证上签字同意，运营主管应查明重打原因并做好记录。（十三）柜员必须按照规定的标准办理残破币兑换业务，收到或兑换的残破币和零币及时上缴主出纳，登记《柜面现金兑换登记簿》；必须严格按照反假币的规定，做好假币的收缴工作并登记《假币收缴登记簿》。（十四）营业中柜员中途临时离岗，必须将经办的印章、现金、重要空白凭证、有价单证、传票等入箱上锁保管，由运营主管或其他柜员对现金进行卡把核大数后，方可离开。午休和日终应换人进行“推磨式”复核后，各柜员的钱箱（包）入柜（包）保管。入柜（包）时运营主管和主出纳应检查柜员钱箱（包）等是否齐全。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第二章  柜面业务基本规定', '第七条', '现金管理基本规定㈠柜员必须严格按照《商业银行现金收付柜面监督办法》的规定进行大额现金登记备案和履行必要的审批手续。㈡柜员营业终了根据当天的现金收付发生额，核对账面库存与实际库存余额一致，柜员日终及休息时必须缴清库存现金。㈢柜员必须坚持每日轧库不少于三次。工作中发生现金差错，要及时如实向运营主管反映，本着“长款归公、短款自赔”的原则处理。㈣主出纳负责现金整点和调拨。根据现金使用情况向本行调拨和上解现金，根据柜员的钱箱限额和现金使用情况调整柜员的备付金。主出纳收到柜员上缴的现金，应及时整点。㈤日终主出纳对各柜员所上缴的现金进行清点，运营主管或指定专人对主出纳进行查库。㈥支行行长和运营主管必须认真执行查库制度，对每个综合柜员和主出纳的库款不定期进行检查，运营主管每周不少于一次，支行行长每月不少于一次，每季不少于四次，并认真记载查库登记簿。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第二章  柜面业务基本规定', '第八条', '通存通兑基本规定㈠通存通兑应遵循的基本原则：⒈办理通存通兑业务必须符合《储蓄管理条例》、相关会计出纳基本制度以及现金管理的有关规定。⒉办理通存通兑业务应该遵循客户自愿的原则。为了确保储蓄存款的安全，凡参加通存通兑业务的储户，均应在原开户单位预留密码，无密码不得办理通存通兑。⒊办理通存通兑必须坚持他行业务与本行业务等同的原则，以维护农村信用社系统的整体形象。各支行（部）柜员不得以任何理由拒绝或拖延办理符合条件的通存通兑业务，并对不符合条件的客户认真解释。⒋办理的通兑存单（折）必须签章齐全，确保凭证的合法性。数据信息输入必须坚持以原始合法凭证为依据，实时输入、实时办理，严禁伪造凭证或无凭证输入数据，避免事故和差错的发生。⒌各支行（部）在办理通存通兑业务时不得使用冲正交易。如发生存（取）操作金额与实际情况不一致、存取颠倒等情况，应在征得客户及收款方允许的情况下通过交易完成。㈡通存通兑的业务范围：⒈储蓄存款通存通兑业务范围按照《江苏省农村信用社储蓄通存通兑业务暂行管理办法》和综合业务系统相关规定执行。⒉对公通存业务范围仅限于本行批准的账户。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第二章  柜面业务基本规定', '第九条', '重要空白凭证管理基本规定㈠柜员要做好重要空白凭证和有价单证的管理，坚持按序销号，对实行手工销号的凭证，营业终了手工登记《重要空白凭证使用销号登记簿》，打印重要空白凭证登记簿，确保实物、手工登记簿、机工登记簿的使用数和结存数核对一致。作废的重要空白凭证，必须剪角（重要空白凭证为单联式的，剪下号码后，将凭证号码手工记载该凭证上以备后查）并加盖作废章，并将凭证正本联的号码剪下粘贴到手工《重要空白凭证使用销号登记簿》上。㈡主出纳负责本支行重要空白凭证和有价单证实物的保管。主出纳根据重要空白凭证的使用情况向本行清算团队领用，及时入库保管，每日打印重要空白凭证登记簿，确保实物与账面核对一致。㈢柜员需领用重要空白凭证时上报主出纳，填列重要空白凭证领用单，经过主出纳确认配号后做支行出库交易，打印重要空白凭证出库单，柜员收到凭证做柜员凭证调入交易并打印交易信息。柜员要根据实际需要领用重要空白凭证，不得一次领用过多。㈣柜员出售支票等凭证，必须凭客户加盖预留印鉴的领用单办理，验证并留存购买人身份证件。柜员必须加强对售出凭证使用的监督，客户销户时必须收回结存的全部凭证，收回的重要空白凭证，放入当日传票中。如由于客户原因无法收回重要空白凭证由存款人作书面自负后果的承诺。㈤支行行长和运营主管必须坚持不定期的核查各柜员的重要空白凭证使用、登记和结存情况（支行行长每月不少于一次，每季不少于四次；运营主管每周不少于一次），并在电子登记登记簿系统中录入检查情况。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第二章  柜面业务基本规定', '第十条', '自助设备的管理规定㈠各支行应指定自助设备管理员，负责本单位自助设备钱箱的维护及日常管理。㈡钱箱实行双人管理的原则，即放入钱箱、取出钱箱、清点钱箱现金等都必须双人办理，可由运营主管和自助设备管理员双人管理，运营主管兼任自助设备管理员也可指定专人管理。㈢自助设备区钥匙、保险箱锁匙、钞箱钥匙由主出纳视同库房钥匙严密保管；自助设备保险箱密码、门禁卡由主管会计严密管理，不得随意放置或托人保管，实行平行交接；自助设备保险箱密码交接时，应进行密码更换或每季更换一次，存在密码泄露风险时，必须立即更换密码。㈣操作规程及管理规定按照《江苏省农村信用社自助设备管理办法》执行。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第三章  综合业务系统操作授权管理基本规定', '第十一条', '业务操作与管理必须坚持“划分权限、分级管理、相互监督”的原则。必须严格控制柜员的操作权限和金额权限，需授权业务由总行授权团队集中授权。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第三章  综合业务系统操作授权管理基本规定', '第十二条', '授权团队应严格执行相关规定，加强操作授权管理，严禁超权限操作，违规授权。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第三章  综合业务系统操作授权管理基本规定', '第十三条', '柜员操作号由柜员操作代码、密码、指纹组成。代码设置应遵循代码专人专用的原则，操作员代码应保持相对稳定，柜员调动或离职须由运营主管报本行授权团队注销原代码。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第三章  综合业务系统操作授权管理基本规定', '第十四条', '柜员操作密码应严格管理：㈠各级柜员密码、指纹仅限本人使用，严禁使用他人指纹代替本人指纹或随意取消指纹登陆。㈡各级柜员必须定期或不定期更换密码，每月更换不得少于三次，密码设置应考虑易于保密，不得使用初始密码或重叠数、顺序数字作为密码。㈢柜员签到或授权，输入密码时，必须要求他人回避。㈣操作员密码解锁和密码重置须由本行授权团队办理，解锁、密码重置由运营主管通过二级运维系统向授权团队申请。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第三章  综合业务系统操作授权管理基本规定', '第十五条', '营业中柜员临时离岗，必须将系统退至初始登录状态。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第三章  综合业务系统操作授权管理基本规定', '第十六条', '综合业务系统中机构签到（签退）和柜员签到（签退）的管理。㈠必须明确办理机构签到和签退的责任人，各级柜员负责各自柜员签到和签退的办理。㈡柜员当日业务处理完毕，办理柜员签退手续。对己办理签退的柜员，若需继续办理有关业务交易，应经运营主管批准，方可重新办理柜员签到。㈢机构当天业务结束平账后，必须及时办理机构签退，若需延迟签退的必须向本行运营管理部门申请。㈣已经办理签退的机构，若需继续办理有关业务交易，应经本行运营管理部批准后，方可重新办理机构签到。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第四章  复核操作制度', '第十七条', '综合柜员业务复核基本要求：㈠复核是保证柜员业务操作规范准确，确保会计出纳工作顺利进行而实行的监督。㈡复核员必须具有较高的业务素质和责任心，严格按照规定办理事中复核操作。㈢复核员必须按规定逐笔认真复审原始凭证的真实性、合法性，并确保交易选择及要素录入的准确性和完整性。㈣对于同城业务、网内贷记和汇兑录入必须全部复核后才能提出票据、发出数据。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第四章  复核操作制度', '第十八条', '复核工作的时间要求。对于网内贷记业务必须实时复核；对于交易金额超100万元的汇划类业务、超过500万元的同城类业务，需提请清算团队及时发送；超过500万的大额汇划类业务，需提前一天向计划财务部报备。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第五章  事后监督及凭证、报表的装订', '第十九条', '事后监督相关规定。㈠运营主管必须坚持每天对柜员业务进行事后监督：⒈查阅柜员冲正业务、查冻扣业务、对公账户的开销户、变更类业务，检查业务办理的合规性。⒉检查主出纳及各柜员是否按规定管理现金、重要空白凭证和有价单证。⒊检查网内贷记和汇兑录入业务是否按规定打印，重打补充凭证是否合规。⒋检查其他需进行会计事后监督的内容，并认真记载主管会计坐班工作日志。㈡本行会计辅导员必须定期对运营主管开展事后监督的情况进行检查考核，通过检查事后监督记录、抽查相关账表凭证、稽核相关业务等办法，从而综合评价与考核运营主管履行事后监督职责的情况。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第五章  事后监督及凭证、报表的装订', '第二十条', '凭证、报表装订相关要求：每日营业终了，柜员应打印重要空白凭证登记簿、账务性流水应按流水号顺序勾对整理后，填写柜员业务监督传票票面，并对每张凭证进行连续编号，交运营主管复核后填写机构传票汇总表，上缴事后监督团队。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第六章  罚 则', '第二十一条', '实施综合柜员制的支行及各级柜员应严格遵守本办法规定，如有违反，本行将视情节轻重，按照本行员工违规违章行为管理规定给予责任人相应的经济处罚或纪律处分。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第七章  附 则', '第二十二条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《综合柜员制管理暂行办法》', '第七章  附 则', '第二十三条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第一章   总  则', '第一条', '为规范xxx银行（以下简称“本行”）对联网核查公民身份信息的(以下简称“联网核查”)业务管理，进一步落实银行账户实名制，促进征信体系建设和反洗钱工作开展，维护正常的经济金融秩序，依据《中华人民共和国反洗钱法》、《个人存款账户实名制规定》、《人民币银行结算账户管理办法》、《银行业金融机构联网核查公民身份信息业务处理规定（试行）》等、和部门规章，特制定本办法。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第一章   总  则', '第二条', '本办法所称联网核查公民身份信息，是指通过联网核查公民身份信息系统(以下简称“联网核查系统”)，核对或查询相关个人的公民身份信息，以验证相关个人的居民身份证所记载的姓名、公民身份号码、照片及签发机关等信息真实性的行为。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第一章   总  则', '第三条', '本办法所称核查行是指通过直接登录方式进行联网核查，通过联网核查系统专用的操作员直接登录联网核查系统进行联网核查的各支行（部）。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第二章   组织职责与权限', '第四条', '运营管理部是联网核查系统的归口管理部门。（一）负责与人民银行联网核查管理部门进行业务衔接；（二）负责日常业务的运营管理；（三）负责联网核查系统管理规章的起草和修改；（四）负责全行联网核查系统培训工作的总体组织；（五）负责对全行联网核查进行检查和考核。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第二章   组织职责与权限', '第五条', '信息科技部负责为联网核查系统提供技术支持，保障网络畅通。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第二章   组织职责与权限', '第六条', '核查行（一）客户办理业务时，核查行应向联网核查系统准确提交待核查人的姓名和公民身份证号码，待收到联网核查系统返回的核查结果后，应将其与待核查人的公民身份证所记载的姓名、公民身份证号码、照片和签发机关进行核对，并在核对无误后按有关规定办理相关业务；（二）核查行对联网核查结果存在疑义时，可通过联网核查系统将疑义信息向公安部门反馈并申请进一步核实。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第二章   组织职责与权限', '第七条', '核查行一级操作员在线上报批量核对请求文件和批量反馈请求文件，单笔核对、单笔精确查询和单笔反馈疑义信息，查询批量核对请求文件登记信息、批量反馈请求文件登记信息、疑义信息核实情况和核查日志，修改本操作员密码。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第二章   组织职责与权限', '第八条', '核查行二级操作员查询批量核对请求文件登记信息、批量反馈请求文件登记信息、疑义信息核实情况和核查日志，监测不合规事项，统计联网核查业务量和疑义信息反馈量，导出和下载核查日志信息。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第三章   原则与要求', '第九条', '在办理下述人民币银行业务时，如法律、法规或部门规章规定需要核对相关个人出示的公民身份证，则应进行联网核查：（一）银行账户业务，具体包括开立、变更个人储蓄账户、个人银行结算账户、单位银行结算账户、个人存款证明、定期存款提前支取、大额存取款、账户挂失等业务。 （二）支付结算业务，包括票据结算业务、银行卡结算业务、汇兑等业务。（三）人民币信贷业务，具体包括个人消费信贷业务、个人经营性信贷业务、个人住房信贷等业务。（四）法律、法规和规章及本行制度规定需要核查公民身份信息的其他银行业务。（五）核查行为加强内部管理，在办理除规定业务之外的其他银行业务时也可进行联网核查。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第三章   原则与要求', '第十条', '下述操作采用直接登录方式：（一）查询核查日志、疑义信息核实情况、批量核对请求文件登记信息和批量反馈请求文件登记信息。（二）监测不合规事项。（三）统计核查业务量、操作员数量和疑义信息反馈量。（四）系统管理。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第三章   原则与要求', '第十一条', '在办理规定业务及其他银行业务时，需当场办理人民币银行账户业务的，应当场联网核查相关个人的公民身份信息；不必办理银行账户业务的，应在办理相关业务前联网核查相关个人的公民身份信息。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第三章   原则与要求', '第十二条', '在办理规定业务及其他银行业务而进行联网核查时，若个人的姓名、公民身份证号码、照片和签发机关中一项或多项核对不一致且能够确切判断客户出示的公民身份证为虚假证件时，应拒绝为该客户办理相关业务。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第三章   原则与要求', '第十三条', '核查时间要求核查行通过联网核查系统进行批量核对的时间为每日7：00至23：00，进行其他操作的时间为每日7：00至19：00。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第三章   原则与要求', '第十四条', '查询方式核查行可采用单笔核对方式、批量核对方式或单笔精确查询方式对相关个人的公民身份信息进行联网核查。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第四章   核查程序', '第十五条', '柜面人员在办理人民币银行账户业务、支付结算业务、人民币信贷业务、个人存款证明、反洗钱相关交易时，必须对公民身份信息进行联网核查，经办柜员为责任人，运营主管负责监督，承担连带责任。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第四章   核查程序', '第十六条', '操作员在进行联网核查时，可根据需要选择单笔核对、批量核对处理方式。（一）选择单笔核对时，应对待核查人的公民身份证所记载的姓名、公民身份证号码、照片、签发机关等信息的真实性进行人工审查；将核对的信息录入联网核查系统，选择“单笔核对”功能，正确选择业务种类，录入相关待核查人的姓名、公民身份证号码后（一次性录入总数≤5个），提交系统核对；联网核查系统将操作员提交的信息核对申请实时提交，并实时返回核对结果。（二）选择批量核对时，操作员将有关个人的公民身份证号码、姓名按照核查请求报文接口规范制作成批量核对请求文件，并通过非接口访问方式提交系统进行处理，请求文件的上报方式可选择反馈照片的批量核对请求文件上报和不反馈照片的批量核对请求文件上报；联网核查系统将对批量核对请求文件与数据库中的数据进行核对处理，并将生成核对结果文件进行反馈；联网核查系统为操作员提供在线查询核对结果文件的功能，并提供打印、下载功能。（三）操作员进行单笔精确查询时，应对待核查人的公民身份证所记载的姓名、公民身份证号码、照片、签发机关等信息的真实性进行人工审查，录入相关待核查人身份证上记载的公民身份证号码和姓名等信息，系统实时转发单笔精确查询请求，并返回结果及其他相关信息；联网核查系统支持对精确查询的结果打印和下载，下载的文件类型为Excel文件。（四）通过联网核查系统对客户身份信息进行核对和查询，如联网核查系统无法查到身份信息或核查结果与身份证不一致，必须通过台式身份证阅读器进行二次识别。运营主管日终通过联网核查系统查询核查日志，检查当日柜员联网核查情况。（五）在联网核查过程中，如出现网络故障等状况，操作员必须及时向运营主管反映，运营主管将具体情况直接向信息科技部反馈，同时向运营管理部反映，信息科技部应予以及时解决。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第五章   疑义信息的反馈和核实', '第十七条', '办理以下业务时，核查结果不一致且无法确切判断客户出示的居民身份证真伪时，应将核查结果明确告知客户，并拒绝办理业务。（一）客户申请办理银行账户业务⒈客户办理个人银行账户提取大额现金5万元以上业务的；⒉客户办理存折（单）、密码挂失解挂业务的；⒊客户办理个人银行账户更换预留银行签章、修改公民身份信息业务的；⒋客户办理个人定期储蓄存款提前支取业务的；⒌客户办理个人存款证明业务的。（二）客户申请办理支付结算业务⒈收款人为个人的持现金支票支取现金的。⒉持票人为个人的现金银行汇票解付、申请人为个人的银行汇票退款、持票人为个人的银行汇票超过付款期限付款；收款人为个人的现金银行本票付款、持票人为个人的银行本票超过付款期限付款。⒊汇款人为个人办理电汇要求退汇的。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第五章   疑义信息的反馈和核实', '第十八条', '办理以下业务时，核查结果不一致且无法确切判断客户出示的居民身份证真伪时，应暂停为该客户办理业务，同时将核查结果明确告知客户，并要求客户提供户口簿、驾驶证等佐证资料对居民身份证的真伪进一步核实，如其居民身份证经核实确属虚假证件，应拒绝办理业务，反之可以继续办理业务。（一）客户申请办理开立、变更单位银行结算账户、更换预留银行印鉴业务的。（二）客户申请办理注册验资、单位存款资金证明业务的。（三）客户申请办理个人银行账户开立、存折（单）、密码挂失、个人银行账户修改密码、账户信息（地址、联系电话）、支取方式变更业务的。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第五章   疑义信息的反馈和核实', '第十九条', '客户办理的业务如不会直接造成重大损失，核查结果不一致且无法确切判断客户出示的居民身份证真伪时，也可以根据实际情况继续办理。如果决定继续为客户办理业务，应详细登记客户准确的联系方式（如通讯地址、电话等），并在办理业务后及时采取其他措施对该相关个人的居民身份证的真伪进一步核实。如其居民身份证经核实确属虚假证件，应及时采取补救措施，立即停办相关个人银行账户的支付业务。对于开户业务的，还应及时通知该客户撤销账户，并将有关情况向中国人民银行当地分支机构报告。对于变更账户业务，还应及时采取恢复原状、通知真实存款人，并将有关情况向中国人民银行当地分支机构报告。（一）核查行可采用单笔反馈方式或批量反馈方式向公安部门反馈疑义信息。核查行采用批量反馈方式的，应按相关接口规范制作批量反馈请求文件，并提交联网核查系统。（二）核查行可以操作员、被核查人姓名、被核查人公民身份证号码、核实状态等为条件，查询和下载公安部门对疑义信息的核实情况。（三）客户对联网核查结果提出疑问的，核查行应向其出具联网核查后相关个人居民身份证信息核对不一致的证明，并告知客户可自行到被核查人常住户口所在地公安机关进行核实。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第五章   疑义信息的反馈和核实', '第二十条', '核查行在进行联网核查时，如对核查结果存在疑义，可向公安部门申请进一步核实。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第六章  核查情况的查询、监测和统计', '第二十一条', '核查行可以操作员、被核查人姓名、被核查公民身份号码等为条件，查询和下载核查日志。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第六章  核查情况的查询、监测和统计', '第二十二条', '核查行可以监测起止日期为条件，对一定时期内进行联网核查操作的不合规核查事项进行监测。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第六章  核查情况的查询、监测和统计', '第二十三条', '各支行（部）可按银行机构代码，统计某一时期的联网核查业务量。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第六章  核查情况的查询、监测和统计', '第二十四条', '各支行（部）可按银行机构代码，统计某一时期的疑义信息反馈量。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第七章   系统操作员管理', '第二十五条', '联网核查系统的操作员分为接口方式操作员、链接方式操作员和直接登录方式操作员三类。本行在联网核查系统上建立的专用操作员为直接登录方式操作员。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第七章   系统操作员管理', '第二十六条', '联网核查系统的直接登录方式操作员分为一级和二级操作员，核查行可设置一个二级操作员和若干个一级操作员。（一）人民银行为本行每个支行（部）设置一个二级操作员。（二）各支行（部）在设置直接登录方式一级操作员时，应向联网核查系统提交银行机构代码、操作员代码、操作员姓名、操作员级别、电话、电子邮件地址、启用日期等信息，并在各级别操作员可选权限中选择该操作员的具体权限。（三）操作员发生变更的，各支行（部）应及时变更相关操作员信息；撤销操作员的，应及时删除相关操作员信息；操作员信息在一定时期暂不操作的，应及时将相关操作员的状态改为停用。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第八章  附  则', '第二十七条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《联网核查身份管理办法》', '第八章  附  则', '第二十八条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第一条', '为加强远程集中授权业务管理，规范远程集中授权业务的运行，增强防范和控制风险的能力，根据《中华人民共和国商业银行法》、《江苏省农村信用社远程集中授权业务管理办法》，结合本行实际，特制定本办法。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二条', '本办法所称的远程集中授权，是指各支行（部）、清算团队综合业务办理中实时将需要授权业务的交易画面、业务凭证影像等要素通过远程集中授权系统传输至本支行（部）、清算团队或远程授权团队的授权员，授权员通过系统对交易画面以及业务凭证影像等要素进行审核并完成授权操作。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第三条', '授权员在办理授权业务时应当认真负责，严格遵守操作程序，做到规范、准确、及时、安全。基本规定'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第四条', '远程集中授权采用指纹加密码的认证方式确认授权员的身份。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第五条', '授权员实行一人，非本岗授权员不得利用授权员操作号办理授权业务。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第六条', '远程集中授权模式包括总行远程集中授权和本支行（部）、清算团队集中授权两种。综合业务系统中需授权的客户业务必须全部通过远程集中授权系统实行总行或本支行（部）、清算团队集中授权。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第七条', '远程集中授权的业务范围（一）授权业务必须采取总行集中授权模式。（二）远程集中授权额度范围。正常工作期间，各支行（部）、清算团队客户业务现金付出50万元以上、现金收入和转账汇兑100万元以上提交至远程集中授权团队授权；客户业务在柜员操作限额以上、远程集中授权团队授权额度以下的，通过远程集中授权系统提交至各支行（部）、清算团队运营主管授权。特殊时期，总行将适时调整授权权限。组织管理'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第八条', '总行设立远程集中授权团队，隶属于运营管理部。运营管理部具体负责远程集中授权工作的管理与日常考核。远程集中授权团队安装监控，对授权员工作情况进行监控录像。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第九条', '信息科技部负责远程授权系统的维护、设备的安装、测试。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十条', '审计部负责远程授权团队的审计。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十一条', '远程集中授权团队应根据授权业务量配备相应专职授权员，并指定一名负责人，负责人须有丰富的基层主管的工作经验。在营业日保证2名以上授权员同时在岗。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十二条', '远程集中授权团队职责（一）对各支行（部）、清算团队发起的需授权业务进行审核授权；（二）发现异常交易时，及时向运营管理部报告；（三）监控远程集中授权业务交易，评估、控制业务操作风险，定期向运营管理部书面报告工作情况；人员管理'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十三条', '远程集中授权系统的操作人员按管理操作权限分为授权团队主管、授权员'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十四条', '远程集中授权团队主管职责（一）负责远程集中授权团队日常管理，安排岗位分工和人员调配，对授权员工作质量进行考核；（二）负责在远程集中授权系统按业务种类、金额、机构、日期等要素设置授权业务参数；（三）负责特殊情况下对所辖授权机构和授权员进行增加、删除、修改等操作维护；（四）负责远程集中授权情况的统计、分析、考核、通报，定期形成运行情况通报；（五）针对远程集中授权发现的问题，及时提出建议和管理措施，防范风险；（六）处理授权员上报的业务风险。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十五条', '远程集中授权（含运营主管）职责（一）负责按规定对授权业务进行审核并作授权确认；（二）负责对柜员输入的交易要素与影像要素的一致性进行核对；（三）负责审核影像资料的合规、完整；（四）负责授权审核时发现存在业务风险的，及时向授权团队主管报告；（五）总行规定的其他职责。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十六条', '各支行（部）、清算团队柜员职责（一）负责审核现金、凭证、身份证件等实物的真实性、有效性、合法性、完整性；（二）负责其拍摄影像资料的真实性；（三）负责确认客户身份的真实性；（四）负责客户凭证的打印、签章；（五）总行规定的其他职责。授权管理'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十七条', '各支行（部）、清算团队必须根据总行确定的业务范围、额度及业务所需审核资料，将所需授权的业务及相关资料拍摄后提交至远程授权员，严禁将应通过远程授权办理的业务直接在本地授权办理，严禁运营主管对权限内业务不进行授权。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十八条', '远程集中授权流程（一）各支行（部）、清算团队柜面操作人员在综合业务系统办理各类需远程授权的业务，根据具体业务所审核的内容依次拍摄，将拍摄的资料与数据一起上传，确保授权员能全面审核每份影像资料内容的合规性、完整性。（二）远程集中授权系统接到柜面发出的授权申请后，根据远程授权参数设置以及授权员忙闲程度自动将所需授权的业务分配至远程授权员（含运营主管）。（三）远程授权员（含运营主管）在接到授权业务提交后，必须根据制度要求对各类业务进行审核，确保所授权的业务无差错、无事故；对审核发现资料提交不全的应退回补充资料，对审核发现要素不符、不符合授权规定的业务应拒绝授权并通过系统返回处理结果。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第十九条', '根据机构和交易风险大小确定授权级别。其中清算团队为最优先级授权机构，各支行（部）、查询、冻结、扣划、挂失等为最优先级授权交易。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十条', '各支行（部）、清算团队由于设备、通信线路或软件等原因导致无法进行远程集中授权时，应及时上报运营管理部。同时，各支行（部）、清算团队运营主管和授权团队主管应及时做好故障情况的详细记录，并通知有关技术人员进行硬件维修、系统维护工作。遇有重大风险的应及时向总行领导和省联社报告。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十一条', '远程集中授权系统管理终端必须设定开机登陆密码，不得安装与业务系统无关的软件，不得与非生产网络连接。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十二条', '远程授权员中途离岗应退出远程集中授权系统。监督与考核'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十三条', '营业期间，各支行（部）、清算团队运营主管、授权团队主管应加大现场监督力度，对柜面经办人员和授权员远程集中授权操作的合规性进行监督。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十四条', '运营管理部将远程授权集中授权情况纳入日常管理内容，定期、不定期进行现场检查或调阅监控录像，检查授权业务合规性、资料完整性、资料与业务的一致性，并检查远程授权系统中的机构和授权员信息与综合业务系统中的信息是否一致。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十五条', '远程集中授权团队对于柜员办理的远程授权业务因拍摄资料不全、不清晰等被退回补充、重拍的，因业务不合规被拒绝授权的，按照综合柜员服务质量百分制考核办法进行扣分，情节严重的将再按照相关制度办法予以处罚。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十六条', '对工作突出、防堵或发现重大差错事故和案件的远程集中授权员，视不同情况给予表彰和奖励。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十七条', '对工作责任心不强、出现重大失误或发现问题隐瞒不报、不积极督促落实整改的远程集中授权员，视情节轻重，按照总行制度予以处理。附  则'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十八条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《远程授权管理办法》', '', '第二十九条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第一章  总  则', '第一条', '为加强xxx银行（以下简称“本行”）重要空白凭证及有价单证管理，严格核算手续，保障资金安全，根据《中华人民共和国票据法》、《商业银行内部控制指引》、《xxx银行会计基本制度》等有关规定，结合本行实际情况，制定本办法。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第一章  总  则', '第二条', '重要空白凭证及有价单证（以下简称“双证”）应严格管理，坚持“定点印制、调用保密、入库保管、保证安全、表外核算、手续严密、使用规范、印证分管、账实相符”的原则。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第二章  重要空白凭证和有价单证管理基本要求', '第三条', '“双证”的印制（一）“双证”的订购必须由本行统一进行，各支行（部）不得私自定购和相互调剂使用。（二）“双证”上应印具号码，使用时应按号码顺序使用和发售，不得跳号。如“双证”在使用中发现整本中有缺号或其他缺陷时，应及时上交本行中心库房，统一保管，以备查考，不得拆本零散使用。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第二章  重要空白凭证和有价单证管理基本要求', '第四条', '“双证”的保管各支行（部）应留存不超过十天“双证”的用量库存，除柜员正常使用的“双证”外，其余部分由主出纳保管。柜员每日打印重要空白凭证登记簿，定期装订、保管，建立保管登记簿，及时如实登记保管、领用、作废、销号等情况。柜员暂时离岗及中午休息，须将使用的凭证锁入柜员现金箱，营业终了，日常使用的“双证”必须全部随包上交总行。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第二章  重要空白凭证和有价单证管理基本要求', '第五条', '“双证”的出入库“双证”的出入库按以下程序进行：（一）本行“双证”管理人员在收到印刷厂订购或上级主管部门下发的“双证”时，应严格履行交接手续。首先应对照印刷厂的发货单（主管部门的下拨单）清点大包，核对大包起讫号码；拆封清点每包本数，核对起讫号码，及时入库（柜）；最后根据发货单或下拨单登记“双证”登记簿。登记簿要详细记载领入日期、发货单位、凭证种类、起讫号码、数量、管理人。（二）各支行（部）领用“双证”时，要填制领用单一式三联，在第一联上领用人签名并加盖业务公章，本行“双证”管理员要根据实际情况予以配发，并在“领用单”上填写实发数量和凭证号码，同时登记“双证”登记簿。“领用单”第一联由本行清算团队编列顺序号，作为表外科目核算传票附件；第二联由本行“双证”管理人员签章后交领用单位，作为领用单位表外科目核算传票附件；第三联备查。“双证”要视同现金随保安公司运钞车配送到各支行（部），不得由支行（部）自行来人领取。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第二章  重要空白凭证和有价单证管理基本要求', '第六条', '“双证”的日常管理（一）柜员领用“双证”时，应办理领用手续。每日营业终了，柜面人员开展推磨复核，依据实物录入系统进行核对，保证账实相符；支行（部）运营主管应每周对“双证”的使用、保管情况进行核对，支行（部）负责人应每月对“双证”的使用、保管情况进行检查；本行运营管理部应定期对各支行（部）“双证”的领用、使用、签发、保管等情况进行检查。（二）现金柜员临时离岗或岗位变动时，应认真办理交接手续（全部凭证一次性移交）。交接时移交人、接受人、监交人需同时在场，仔细清点，核对账实，登记交接登记簿，交接双方、监交人应在电子登记簿中确认，做到手续严密，责任分明。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第三章  重要空白凭证管理的具体要求', '第七条', '重要空白凭证是指无面额的经金融机构或单位填写金额并签章后即具有支付效力（表明债权债务关系）的空白凭证，包括：存单、存折、支票、汇票、开户证实书、信用卡（证）、支付系统凭证、还贷证明单以及其他重要空白凭证等。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第三章  重要空白凭证管理的具体要求', '第八条', '重要空白凭证的核算重要空白凭证一律由现金柜员统一管理，纳入表外“重要空白凭证”科目核算。本行应按凭证种类设立分户账，以一份（存折按一本）一元假定价格记账。领入、收到订购或上级下拨的重要空白凭证，由现金柜员做领入账务处理，记该科目借方（领用单第二联或发货单、下拨单作传票附件）；领用重要空白凭证，由现金柜员（从本行领用）、一般柜员（支行内部领用）做领用账务处理，记该科目贷方（领用单第一联作传票附件）。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第三章  重要空白凭证管理的具体要求', '第九条', '重要空白凭证的领用开户单位领用支票等重要空白凭证时，已购买的重要空白凭证必须已使用完备，购买时应填写领用单（每次一本），加盖全部预留印鉴，柜员根据领用单在系统中做凭证出售。支行内部领用由现金柜员、一般柜员分别作凭证调拨处理。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第三章  重要空白凭证管理的具体要求', '第十条', '重要空白凭证的签发、注销与销毁（一）支行（部）内部签发重要空白凭证，应进行销号控制，严禁由客户签发，并不得预先盖好印章备用；使用打印机打印重要空白凭证时，只能在重要空白凭证上填空打印，不得自行打印凭证模式。填错的重要空白凭证，剪右下角加盖“作废”戳记后作有关科目传票的附件，并将作废凭证的号码剪下粘贴在登记簿上，以备查考。（二）开户单位销户时，应要求客户将剩余的重要空白凭证全部交开户行注销。支行（部）对开户单位交回的重要空白凭证应剪右下角，在凭证上加盖“作废”戳记，造具清单，作废的重要空白凭证作传票附件，清单作开户单位开销户档案保管。（三）对停止使用的重要空白凭证，应剪右下角，并在凭证上加盖“作废”戳记，造具清单，及时上缴本行运营管理部，本行运营管理部比照会计档案销毁程序销毁。对开户单位未用完但停止使用的重要空白凭证，要及时收回。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第四章  有价单证管理的具体要求', '第十一条', '有价单证是指待发行的印有固定面值金额的特定凭证，包括：国库券、金融债券、代理发行的各类债券、定额存单、定额汇票、定额本票、股票以及其他印有固定面额的其他有价单证等。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第四章  有价单证管理的具体要求', '第十二条', '有价单证的核算有价单证纳入表外科目“未发行有价证券”核算，以原面值分类别记账。领入或收到订购、上级下拨的有价单证，记该科目借方（领用单第二联或发货单、下拨单作传票附件）；领用有价单证，记该科目贷方（领用单第一联作传票附件）。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第四章  有价单证管理的具体要求', '第十三条', '有价单证的样本和暗记，比照人民币票样管理办法妥善保管。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第四章  有价单证管理的具体要求', '第十四条', '有价单证的发售有价单证发售或签发时，柜员应先清点现金（或审查支票，记分户账）无误后，才可办理发售或签发业务，同时登记销号单，待日终业务结束时，统一编制传票，销记表外科目。有价单证为多联式的，应将记账联作传票附件，留底联专夹保管，以备查考；如为单联式的，应详细抄列（打印）清单（有价单证号码、账号、名称、金额、期限等），专夹保管，以备查考。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第四章  有价单证管理的具体要求', '第十五条', '有价单证的兑付有价单证兑付时，应先审查该有价单证是否为本行签发，核对账号、名称、金额、期限与留底联（备查清单）是否相符，如准确无误后方可办理兑付业务，兑付时需加盖章印。其利息需填写利息清单，由客户签字后付款。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第四章  有价单证管理的具体要求', '第十六条', '对停止使用、注销作废的有价单证，应加盖明显作废标记，造具清单，及时上缴本行运营管理部，本行运营管理部比照会计档案销毁程序销毁。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第五章  附  则', '第十七条', '各支行（部）要高度重视对“双证”的管理，对“双证”的管理人员要加强思想政治、业务技术能力的教育培训，定期进行轮岗。对“双证”管理混乱，造成严重后果的，要追究有关责任人责任。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第五章  附  则', '第十八条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《重要空白凭证及有价单证管理实施办法》', '第五章  附  则', '第十九条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第一条', '为确保xxx银行（以下简称“本行”）办理金融机构相互取现业务有序、规范开展，根据《中华人民共和国商业银行法》及中国人民银行相关规定，特制定本办法。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第二条', '办理相互取现业务中，提供现金的银行业金融机构称为调出方，领取现金的银行业金融机构称为调入方。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第三条', '相互取现采用固定配对和灵活配对的组合模式。固定配对模式采取一对多方式，灵活配对模式由人民银行中心支行（以下简称“人民银行”）根据当日存取款预约情况确定。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第四条', '相互取现金额纳入现金计划统一管理，应急情况下应服从人民银行的余缺调剂，但原则上采取上门取款方式。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第五条', '所有工作人员不得对外泄露提取现金数量、提取方式、提取时间及调运路线。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第六条', '相互取现业务坚持无偿原则，调剂过程中，银行业金融机构互不收取管理费、手续费、调运费。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第七条', '各相关部门必须切实履行各自岗位职责，相互协调配合，确保金融机构相互取现业务的顺利进行。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第八条', '与调出方原则上每个工作日办理一次取款或送款业务，如需多次办理，可由双方协商解决。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第九条', '从调出方领取的现金，各营业网点清点时如发现长短款，假币等差错情况，按《江苏省银行业金融机构间现金出纳差错管理办法（试行）》的相关规定处理。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第十条', '对相互取现业务中有违约行为并被人民银行处罚的，将根据情节轻重对相关责任人进行处罚。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第十一条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《金融机构相互取现业务管理办法》', '', '第十二条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第一章　 总 则', '第二条', '银行本票是申请人将款项交存本行，由本行签发的、承诺自己或代理付款行在见票时无条件支付确定的金额给收款人或者持票人的票据。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第一章　 总 则', '第三条', '银行本票的出票人即签发银行本票的银行机构为银行本票的付款人。银行本票的代理付款人是代理出票银行审核支付银行本票款项的银行。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第一章　 总 则', '第四条', '银行本票的出票人必须具有支付本票金额的可靠资金来源。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第一章　 总 则', '第五条', '单位和个人在yy市票据交换区域内的各种款项结算，均可以使用银行本票。各支行（部）可依托综合业务系统自行办理本票解讫。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第一章　 总 则', '第六条', '本办法所称的银行本票为不定额银行本票，不设金额起点。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第一章　 总 则', '第七条', '经办银行本票业务的准入条件。本行辖内经银监部门批准办理结算业务，并颁发了《金融许可证》的各支行（部）；具有同城票据交换号或支付系统行号；内控制度完善，分工职责明确，印证管理严格，熟悉并严格执行银行本票的各项制度和办法；配备具备符合岗位条件的人员：要求持有会计证，从事会计工作两年以上。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第一章　 总 则', '第八条', '退出经办银行本票业务的操作程序。正常退出：已被批准经办银行本票业务的各支行（部）如因机构变动等原因需退出的，应由本行以行文形式向中国人民银行yy市中心支行（以下简称“yy人行”）提出申请，yy人行根据实际情况进行研究后，给予批复。勒令退出：对已被批准经办银行本票业务的各支行（部）因内控管理不严、执行制度不力，受到中国人民银行金融监管例会两次通报批评或处罚的，或因岗位职责不清，内部管理混乱，随意压票、退票，签发空头本票产生票据风险和资金案件的，经查实后随时取消办理银行本票业务资格。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第九条', '银行本票可以用于转账，注明“现金”字样的银行本票可以用于支取现金。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十条', '申请人使用银行本票，应向出票银行提交“结算业务申请书”，并向银行足额缴存本票款项。“结算业务申请书”应填明申请日期、申请人名称、收款人名称、金额、经办人身份证件等事项并签章，单位签章为其预留银行印鉴，个人为其本人的签名或盖章。约定使用支付密码的，应在申请书上填写支付密码。申请人和收款人均为个人需要支取现金的，申请书大写金额栏应先填有“现金”字样，后有支付金额。申请人或收款人为单位的，银行不得为其签发现金银行本票。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十一条', '各支行(部)受理结算业务申请书，经审核凭证要素无误、收妥款项，依托综合业务系统签发银行本票。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十二条', '签发银行本票必须记载下列事项，欠缺记载下列事项之一的，银行本票无效。（一）表明“银行本票”的字样；（二）无条件支付的承诺；（三）确定的金额；（四）收款人名称；（五）出票日期；（六）出票人签章；（七）密押。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十三条', '银行本票的出票人在票据上的签章，为yy人行统一刻制的银行本票专用章，加其法定代表人或其授权的经办人的签章。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十四条', '银行本票的出票金额、出票日期和收款人名称，不得更改，更改的银行本票无效。其他记载事项更改的，须由原记载人签章证明。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十五条', '银行本票（填明“现金”字样的银行本票除外）可以背书转让。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十六条', '银行本票不能满足背书人记载事项的需要，可以加附粘单，粘贴于银行本票上。粘单上的第一记载人，应当在银行本票粘单的粘接处签章。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十七条', '背书由背书人签章并记载背书日期。背书未记载日期的，视为在银行本票到期日前背书。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十八条', '银行本票以背书转让或者以背书将一定的银行本票权利授予他人行使时，必须记载被背书人名称。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第十九条', '以背书转让的银行本票，背书应当连续。持票人以背书的连续，证明其票据权利；非经背书转让，而以其他合法方式取得银行本票的，依法举证，证明其票据权利。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第二十条', '背书不得附有条件。背书时附有条件的，所附条件不具有本票上的法定效力。将本票金额部分转让的背书或者将本票金额分别转让给二人以上的背书无效。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第二章   出票与背书', '第二十一条', '出票银行在本票正面记载“不得转让”字样的银行本票，不得背书转让；其直接后手再背书转让的，出票人对其直接后手的被背书人不承担保证责任，对被背书人提示付款或委托收款的银行本票，银行不予受理。背书人在银行本票背面背书人栏记载“不得转让”字样的，其后手再背书转让的，记载“不得转让”字样的背书人对其后手的被背书人不承担保证责任。但出票银行不能以此抗辩持票人。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第三章   付　款', '第二十二条', '银行本票付款形式为见票即付。银行本票的提示付款期限自出票日起最长不超过2个月。超过提示付款期限的银行本票，银行不予受理。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第三章   付　款', '第二十三条', '在本支行(部)开立账户的持票人向本行开户支行（部）提示付款时，应在银行本票背面“提示付款”栏签章，签章须与预留印鉴一致，并将银行本票、进账单送交开户支行，支行（部）审查无误后办理转账。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第三章   付　款', '第二十四条', '未在本支行(部)开立账户的个人持票人，凭注明“现金”字样的银行本票支取现金的，应在银行本票背面签章，记载本人身份证件名称、号码及发证机关，并交验本人身份证件及其复印件。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第三章   付　款', '第二十五条', '各支行(部)受理银行本票时，应审查下列事项：（一）收款人是否为在本支行(部)开立存款账户的单位或个人；（二）银行本票是否在提示付款期限内；（三）必须记载的事项是否齐全，内容是否符合规定；（四）出票日期是否使用中文大写；（五）出票金额、出票日期、收款人名称是否更改，更改的其他记载事项是否由原记载人签章；（六）出票银行签章是否齐全、清晰；（七）记载的密押是否清晰；（八）出票银行的签章是否与预留印模核对一致，出票银行本票专用章中的支付行号是否正确无误。提示付款人为个人的还要通过公民身份核查系统，审查其身份证件。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第三章   付　款', '第二十六条', '各支行(部)受理经背书转让的银行本票，除按照第二十四条规定审查外，还应审查下列事项：（一）银行本票正面是否记载“不得转让”的字样；（二）背书是否连续，背书栏是否记载“不得转让”的字样；（三）背书人签章是否符合规定，背书使用粘单是否按规定签章。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第三章   付　款', '第二十七条', '持票人超过提示付款期限不获付款的，在票据权利时效内向出票银行作出说明，并提供相关有效证件或公函，可持银行本票向出票银行请求付款。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第三章   付　款', '第二十八条', '申请人因银行本票超过提示付款期限或其他原因要求退款时，应将银行本票提交出票银行办理。申请人为单位的，应出具该单位的证明；申请人为个人的，应出具本人的身份证件。出票银行对于在本行开立存款账户的申请人，只能将款项转入原申请人账户；对于申请时未在本行开立存款账户交现金办理本票的申请人，才能退付现金。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第四章   丧失票据权利', '第二十九条', '未填明“现金”字样的银行本票丧失，不得挂失止付。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第四章   丧失票据权利', '第三十条', '允许挂失止付的银行本票丧失，应向出票人申请挂失止付，填写挂失申请书申请挂失止付，并在申请书上注明：(一)本票丧失的时间、地点、原因；(二)本票的号码、金额、出票日期、付款人名称、收款人名称等；(三)挂失止付人的姓名、住址、联系方法。欠缺上述记载事项之一的，各支行(部)不予受理。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第四章   丧失票据权利', '第三十一条', '各支行(部)收到挂失申请书后，查明该本票确未付款时，应立即依托综合系统进行挂失止付处理，暂停支付。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第四章   丧失票据权利', '第三十二条', '各支行(部)自收到挂失申请书之日起12日内未收到人民法院的止付通知书的，自第13日起，持票人提示付款并依法向持票人付款的，不再承担责任。各支行(部)在收到挂失止付通知书之前，已经向持票人付款的，不再承担责任。恶意或重大过失付款的除外。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第四章   丧失票据权利', '第三十三条', '失票人可凭人民法院出具的其享有票据权利的证明，期满后向出票银行请求付款或退款。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第四章   丧失票据权利', '第三十四条', '银行本票的公示催告和提起诉讼按照《中华人民共和国民事诉讼法》、《中华人民共和国票据法》的有关规定办理。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第五章   结算纪律与责任', '第三十五条', '各支行（部）应按规定受理yy市内的银行机构签发的银行本票的兑付业务，应确保银行本票资金的清算，不得无故拒收银行本票，不得无故退票。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第五章   结算纪律与责任', '第三十六条', '各支行（部）因工作差错，延误银行本票解付，影响客户资金使用的，应按中国人民银行规定的同档次流动资金贷款利率计付赔偿金。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第五章   结算纪律与责任', '第三十七条', '各支行（部）违反规定签发空头银行本票，应按规定承担行政责任。因签发空头本票产生票据风险和资金案件的，经查实后取消办理银行本票业务资格。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第六章   附  则', '第三十八条', '银行本票须按照重要空白凭证的管理规定进行保管、使用，并严格执行“印、证”分管制度。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第六章   附  则', '第三十九条', '银行本票的签发手续费、挂失止付手续费，按有关结算收费标准向申请人收取。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第六章   附  则', '第四十条', '本办法未涉及的内容，按《中华人民共和国票据法》、《票据管理实施办法》、《支付结算办法》及中国人民银行颁发的有关规范性文件执行。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第六章   附  则', '第四十一条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《银行本票管理办法》', '第六章   附  则', '第四十二条', '本办法自发文之日起施行。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第一章  总  则', '第一条', '为保障存款人的资金安全，节约银行管理成本，加强账户管理，根据《人民币银行结算账户管理办法》及相关法律法规规定，特制定本办法。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第二章  长期不动账户的确定', '第二条', '本办法所称长期不动账户，是指超过一定期限未发生收付活动（不包括结息等非客户主动发生的业务，下同）且未欠开户银行债务的银行账户。具体包括以下账户：（一）单位不动账户，是指一年内未发生收付活动且未欠开户银行债务、余额在人民币10000元（含）以下的单位银行结算账户。（二）个人不动账户（含个人银行结算账户、个人银行卡、个人储蓄账户等），是指从最后一笔业务或到期日起，三年内未发生收付活动，且余额在人民币50元（含）以下的个人账户。对超过限额标准但确需纳入长期不动账户管理的应事先向总行运营管理部对账团队申请。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第二章  长期不动账户的确定', '第三条', '不包括处于挂失、查询、止付、冻结等非正常状态的银行账户。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第三章  长期不动账户的日常管理', '第四条', '长期不动账户统一在“待处理久悬未取款项”科目核算，该账户不计息。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第三章  长期不动账户的日常管理', '第五条', '长期不动账户按原存款账户分户明细登记“长期不动账户”登记簿，进行明细核算。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第三章  长期不动账户的日常管理', '第六条', '长期不动账户自转入“待处理久悬未取款项”科目之日起，超过五年（含五年）仍未支取的款项可作“营业外收入”入账。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第三章  长期不动账户的日常管理', '第七条', '对已列入长期不动账户的账户，在办理挂失业务时应按有关挂失规定，经有权人授权后正常办理。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第四章  长期不动账户的清理', '第八条', '单位不动账户的清理1.每季度末月15日对符合长期不动账户条件的单位账户进行清理，转入待睡眠状态。2.各营业网点每季度末月16日应根据系统生成的待睡眠户明细表“正常转待睡眠清单”，填写“单位不动账户销户通知书”，采用人工送达或邮寄方式送交客户。3.自通知发出之日起30天内，客户未办理销户手续的，视为该客户自愿销户，将其转入“待处理久悬未取款项”，并登记相应登记簿。4.转入长期不动账户核算后，抽出原账户的客户预留印鉴卡片，由专人集中专夹保管。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第四章  长期不动账户的清理', '第九条', '个人不动账户的清理1.每季度末月15日前发布《个人不动账户管理公告》，公告内容应包括个人不动账户清理截止日期、方法、友情提示等。2.公告发布30天，将符合条件的个人不动账户转入“待处理久悬未取款项”，并登记相应登记簿。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第五章  长期不动账户的销户', '第十条', '单位不动账户的销户1.客户支取长期不动账户款项时只能到其开户网点办理。2.支款凭条应加盖其预留印鉴，经办柜员应调阅“长期不动户登记簿”，并核对其预留印鉴。3.客户应提供其合法拥有该账户支配权的法律文书等证明文件。4.经有权人审核后进行销户处理，将款项转入基本存款账户或支取现金。如客户账户有未使用的凭证，柜员应将凭证收回，如客户有未使用的凭证但无法提供，需要出具相关承诺，并明确陈述相关经济责任由其承担。5.根据长期不动账户的类型分别从“待处理久悬未取款项”科目或“营业外支出”科目列支。6.销户时，在“营业外支出”科目列支按原存款账户种类的计息规定计算的利息。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第五章  长期不动账户的销户', '第十一条', '个人不动账户的销户1.客户必须提供原存单（折、卡）及存款人身份证件到原开户网点办理，严禁无存单（折、卡）取款。凭密码支取的应提供密码。2.由他人代办的，必须提供代理人和被代理人身份证件。3.个人不动账户销户前，必须核对“长期不动户登记簿”并经有权人审核后办理。4.根据长期不动账户的类型分别从“待处理久悬未取款项”科目或“营业外支出”科目列支。5.销户时，在“营业外支出”科目列支按原存款账户种类的计息规定计算的利息。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第六章  长期不动账户的重新启用', '第十二条', '单位不动账户的重新启用已转入“待处理久悬未取款项”或“营业外收入”科目核算的单位不动账户，客户要求继续使用该账户的，需重新提供申请开立账户的资料，填写开户申请书，签定银行结算账户管理协议书，经审核同意后重新启用原账户。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第六章  长期不动账户的重新启用', '第十三条', '个人不动账户的重新启用已转入“待处理久悬未取款项”或“营业外收入”科目核算的个人银行结算账户、个人银行卡、个人储蓄账户，可在客户提供身份证件经审核同意后重新启用。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第七章   附  则', '第十四条', '本办法由xxx银行运营管理部负责解释和修订。'),
(NULL, '运营管理部', '《长期不动户账户管理办法》', '第七章   附  则', '第十五条', '本办法自发文之日起施行。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）债券投资行为，防范债券投资业务风险，规范操作程序，根据《全国银行间债券市场债券交易管理办法》、《银行间债券市场债券登记托管结算管理办法》、《商业银行内部控制指引》、《商业银行市场风险管理指引》以及《关于加强商业银行债券投资风险管理的通知》以等有关规定，结合本行实际，特制定本办法。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第一章  总则', '第二条', '本办法所称债券投资业务是指交易双方通过债券市场进行债券买卖的业务，包括债券分销、债券回购、债券买卖、债券托管等。目前本行仅可通过全国银行间市场进行债券投资业务，不得通过证券机构进行债券投资业务。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第一章  总则', '第三条', '本办法中的有关名词定义如下：（一）债券指经中国人民银行批准可在银行间债券市场或证券交易所市场上进行交易的国债、中央银行融资券、政策性金融债券、国家有关部门批准公开发行的国家重点建设企业AAA级以上债券和人民银行批准的农村商业银行可参与的其他有价证券。（二）债券分销指通过国债、政策性金融债及企业债承销商分销债券的业务。（三）债券回购是指交易双方进行的以债券为权利质押的一种短期资金融通业务，是资金融入方（正回购方）在将债券出质给资金融出方（逆回购方）融入资金的同时，双方约定在将来某一日期由正回购方按约定回购利率计算的资金额向逆回购方返还资金，逆回购方向正回购方返还原出质债券的融资行为。（四）债券买卖是指交易双方以约定的价格转让债券所有权的交易行为。（五）债券托管是指债券投资人基于对债券托管机构的信任，将其所拥有的债券委托托管机构进行债权管理与权益监护的行为。（六）前台交易系统是指全国银行间同业拆借中心提供的交易系统。（七）后台交易系统是指中央国债登记结算有限责任公司提供的中央债券综合业务系统。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第一章  总则', '第四条', '成交日期是债券买卖双方订立买卖交易合同的日期。交割日期是交易双方根据买卖合同进行资金划付、债券交割的日期。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第一章  总则', '第五条', '与本行开展一级市场债券承销业务的单位必须是经中国人民银行批准的相关发债主体的承销团成员。与本行开展二级市场债券买卖业务的单位必须是经中国人民银行批准，进入全国银行间市场的市场成员，双方通过前台交易系统进行交易，通过后台结算系统进行债券交割。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第二章  职责与分工', '第六条', '金融市场部主要负责拟定债券投资业务相关管理办法，根据本行转授权管理办法的规定进行债券业务的操作并进行交易，负责保管相关业务资料。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第二章  职责与分工', '第七条', '运营管理部下属清算中心主要负责债券投资业务的资金清算。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第二章  职责与分工', '第八条', '运营管理部负责拟订债券投资业务会计核算手续。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第二章  职责与分工', '第九条', '合规管理部负责审核债券投资业务管理办法合规性、有效性。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第二章  职责与分工', '第十条', '风险管理部负责对债券投资业务的风险定期识别、计量、监测，做出独立评估并提交风险审查报告。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第二章  职责与分工', '第十一条', '审计部负责定期或不定期对债券投资业务进行审计和监督检查。内容包含但不限于：交易方式、期限、对象等是否符合中国人民银行的有关规定；操作程序、审批程序是否执行本管理办法；成交通知单、成交合同、债券交割单、审批单等是否完备。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第三章  债券投资岗位设置', '第十二条', '金融市场部根据岗位“相互制约、换人复核”的原则，将岗位具体设置为：债券交易岗、债券结算岗、债券复核岗、债券清算岗。其中债券清算岗由本行清算中心人员担任。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第三章  债券投资岗位设置', '第十三条', '各岗位具体职责为：（一）债券交易岗：负责日常的信息收集，在市场上寻找交易对手方，发送每笔交易的对话报价，通过交易系统或书面合同完成债券交易并打印成交单、登记交易台帐。（二）债券结算岗：负责对交易人员提供的成交通知单或债券交易合同通过后台结算系统逐笔录入结算指令，并关注指令交割情况。（三）债券复核岗：负责对结算人员录入的每笔指令进行复核，确认准确无误后发送，对结算人员开立的资金划付凭证进行要素复核，确保资金收付的准确、及时。（四）债券清算岗：负责对债券业务涉及的资金进行核算，划付、签收和记帐。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第三章  债券投资岗位设置', '第十四条', '各岗位必须严格按照货币市场有关管理办法和本办法的有关规定办理业务，严禁越岗、越权操作，如发现资金交易清算中有异常情况发生，必须及时查明原因，逐级报告，采取有效措施妥善解决。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第四章  债券投资操作流程', '第十五条', '申请、审查与审批债券投资业务的申请与审批，由本行计划财务部根据市场情况，本行资产与负债状况以及资金富余情况提出债券投资业务的申请，同时参照本行《转授权管理办法》的通知要求提交本行资产负债管理委员会审议决策。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第四章  债券投资操作流程', '第十六条', '债券业务交易操作（一）债券分销业务的操作流程：本行拟购买新发行的国债、政策性金融债及其他债券，金融市场部应协助本行计划财务部依据资金富余、存券情况及债券发行条款，测算购买金额及相应利率档次，形成交易意见，按照授权规定报经本行有权人审批同意后，参与报价分销。具体交易操作如下：1、交易员根据经领导批准的《债券分销业务审批单》，将经批准的价格和金额报债券承销商，如中标，签订相应债券分销合同，合同原件入档保管，合同复印件交清算中心进行账务处理。2、结算员根据成交单开具资金划付凭证及资金划拨通知单，按程序复核、报批。3、复核员通过中央国债登记结算交易系统，检查债券是否过户，如未过户，及时与债券承销商、中央国债登记结算公司联系，查明原因，将详细情况上报。4、清算中心按照结算员提交经审批的《资金划拨通知单》按分销合同规定日期，将款项汇出。（二）债券现券交易业务即购买或卖出债券的操作流程：金融市场部协助本行计划财务部依据资金富余、存券情况及债券发行条款，测算所购买或出售的金额及相应利率档次，形成交易意见，按照授权规定报经本行有权人审批同意后，参与报价分销。具体交易操作如下：1、金融市场部交易员根据网上报价情况进行测算，选择最优价格，经本行有权人审批同意后，与对手方进行交谈、成交，发送报价并打印《银行间债券买卖成交通知单》一式三份，一份存档、一份交清算中心进行账务处理、一份交结算人员。2、结算人员通过后台结算系统逐笔录入结算指令，复核人员复核并确认无误后发送中央国债登记结算有限公司，同时关注查询债券交割、托管情况。3、清算人员凭《银行间债券买卖成交通知单》所列的金额、品种进行账务处理。（三）债券回购交易业务即拆借资金的操作流程：金融市场部协助本行计划财务部依据资金富余、存券情况及债券发行条款，形成交易意见，按照授权规定报经本行有权人审批同意后，进行业务的交易，具体操作如下：1、金融市场部交易员根据网上报价情况进行测算，选择最优价格，经本行有权人审批同意后，与对手方进行交谈、成交，发送报价并打印《质押式回购成交单》一式三份，一份存档、一份交清算中心进行账务处理、一份交结算人员。2、结算人员通过后台结算系统逐笔录入结算指令，复核人员复核并确认无误后发送中央国债登记结算有限公司，同时关注查询债券交割、托管情况。3、清算人员凭《质押式回购成交单》所列的金额、品种进行账务处理，对融入性债券投资及时查询资金到账情况。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第五章  债券投资会计核算', '第十七条', '债券投资交易的资金必须按交易合同指定的银行账户进行收付，如收款方需变更账户，必须提出书面申请，并加盖业务公章。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第五章  债券投资会计核算', '第十八条', '债券交易资金收付必须以银行转账方式进行，不得以现金或其他方式结算，不得在交易合同约定的价款或利息之外收付其他费用。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第五章  债券投资会计核算', '第十九条', '债券投资交易单位为万元，资金清算单位为元，保留两位小数。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第五章  债券投资会计核算', '第二十条', '债券投资的计价方式。（一）短期债券投资（一年以内），购入时投资性债券实际支付的价格不含债券利息时，按实际成本入账；购入的投资性债券实际支付的价款包含利息时，应按实际支付的价款扣除应计利息入账。（二）长期债券投资（一年以上），购买时无论是按面值、溢价或折价购入，均以实际支付的价款作为债券投资的入账价值，在付款时记账；如果购买债券实际支付价款中含有截至到购买时止的应计利息，则应将这部分应计利息单独记账。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第五章  债券投资会计核算', '第二十一条', '债券投资的具体核算要求。购买债券时，无论是按面值还是按溢价或折价购入，均应以实际支付的价款记账。如果实际支付的价款中含有债券利息，应在“债券投资”科目下设置“应计利息”二级账户核算。要按照权责发生制原则，对购入的债券每年计提利息。如果是溢价购入，其溢价部分应在债券存续期间内提取“应计利息”时摊销。如果是折价购入，其折价部分也应在债券存续期间内提取“应计利息”时摊销。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第五章  债券投资会计核算', '第二十二条', '完善债券投资核算手续。每笔债券买卖交易资金收付必须根据经授权批准的交易合同及审批单办理，并以此作为进行会计核算的依据。划拔资金时，结算员根据成交通知单所列要素出具《资金划拔凭证》，复核员复核，上报部门负责人审批后交清算员，清算员根据交易员提供经审批后的《资金划拔凭证》进行划拔。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第五章  债券投资会计核算', '第二十三条', '定期进行账户核对。包括：与债券托管机构的债券余额核对，与清算银行账户的资金核对，台帐与会计账户核对，做到内外帐一致，发现问题及时核实调整。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第六章  债券投资内控管理', '第二十四条', '债券投资业务严格按照转授权办法办理，在业务上做到资金交易、债券结算、资金清算严格分工协作，做到业务授权制度化、业务过程规范化。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第六章  债券投资内控管理', '第二十五条', '债券承销业务及现券买卖业务由本行计划财务部会同金融市场部负责人以及债券业务人员根据本行资金状况、市场情况和债券品种等拟定承销意向，并提供相关市场情况、债券要素，形成可行性报告上报给本行分管领导，并按照转授权办法规定实行分级审批会办确定。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第六章  债券投资内控管理', '第二十六条', '债券投资业务印章、密钥及中债KEY管理。债券投资业务印章必须入库保管，交易密钥要保持经常性的更换，中债KEY实行专人专用，以确保交易的安全。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第七章 资料归档', '第二十七条', '交易员应根据成交通知单建立相应的明细台账，按月核对其余额，负责向人民银行、省联社和金融监管部门报送各种相关的报表、资料；'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第七章 资料归档', '第二十八条', '结算员负责将有关合同、协议、成交单、审批单、资金划拨通知单收集专夹保管，定期整理装订，确保资料完整。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第七章 资料归档', '第二十九条', '计划财务部按排专人逐笔审核登记的业务管理台账并做好债券业务的测算及考核。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第八章  附则', '第三十条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《债券投资业务管理办法及操作流程》', '第八章  附则', '第三十一条', '本办法自发布之日起执行。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）同业存单业务，根据中国人民银行《同业存单管理暂行办法》、全国银行间同业拆借中心（以下简称“同业拆借中心”）、《银行间市场同业存单发行交易规程》及有关规定，特制定本办法。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第一章  总则', '第二条', '本办法所称同业存单是指由银行业存款类金融机构法人（以下简称“存款类金融机构”）在全国银行间市场上发行的记账式定期存款凭证，是一种货币市场工具。存款类金融机构包括政策性银行、商业银行、农村合作金融机构以及中国人民银行认可的其他金融机构。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第一章  总则', '第三条', '本办法所称的“同业存单业务”是指同业存单发行业务和同业存单交易业务。同业存单发行业务是指本行作为发行人，采取招标发行或报价发行方式，面向全国银行间债券市场参与者发行同业存单的业务。同业存单交易业务是指本行作为投资人，在一级市场认购其他存款类金融机构所发行的同业存单，以及在二级市场进行同业存单交易的业务。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第一章  总则', '第四条', '本行同业存单的发行与交易应遵循“公平、诚信、自律”的原则。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第二章  组织与分工', '第五条', '部门职责（一）金融市场部金融市场部为本行同业存单发行、投资及交易部门。主要负责：1、牵头制定并组织实施同业存单业务管理的相关制度；2、负责联系本行同业存单银行间市场承销发行工作，在银行间市场开展同业存单发行与投资交易；3、做好同业存单客户营销与维护；4、根据监管要求进行发行信息披露工作;5、其他同业存单发行与投资的处理工作。（二）计划财务部1、负责拟定同业存单发行计划、监管联络等工作；2.制定同业存单发行及投资交易的相关会计核算办法，负责同业存单业务相关的会计核算和相关会计科目的开立；3.在规定时间内配合金融市场部门按照中国人民银行要求做好披露本行财务报表等信息工作；4.其他同业存单相关的管理工作。（三）运营管理部负责同业存单业务资金清算相关工作。清算中心根据金融市场部审核确认的交易单据及时完成会计确认、会计核算、同业存单结算和资金划汇清算等工作。（四）授信评审部负责确定本行在同业存单投资交易业务中，在市场上认购其他存款类金融机构所发行的同业存单的额度授信。（五）风险管理部牵头负责发行同业存单风险的管理。包括对同业存单发行交易风险的识别、监测和评价。（六）合规管理部负责同业存单业务合规情况审查;同业存单业务各项制度、办法和流程建设，业务宣传、操作、管理和服务等方面的合规性审查。（七）审计部负责同业存单业务内部审计及后评价工作，并结合本行实际，提出相关的意见或建议。（八）科技信息部负责同业存单发行系统的硬件设备维护、网络维护和网络安全等方面的管理。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第三章  同业存单发行', '第六条', '同业存单发行年度计划及具体方案根据利率市场化推进进程、同业市场利率水平、同业客户投资需求、本行资产负债结构状况及经营管理需要等因素综合制定。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第三章  同业存单发行', '第七条', '同业存单发行对象根据实际发行需要可包含商业银行、政策性银行、邮政储蓄银行、信用社、证券公司、基金公司、保险公司、财务公司、理财产品在内的各类同业客户。具体范围按照中国人民银行有关规定执行。同业存单的投资和交易主体为全国银行间同业拆借市场成员、基金管理公司及基金类产品。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第三章  同业存单发行', '第八条', '同业存单采取电子化发行方式，在全国银行间市场上公开发行或定向发行。由全国银行间同业拆借中心（以下简称同业拆借中心）提供同业存单发行、交易和信息服务。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第三章  同业存单发行', '第九条', '同业存单公开发行分为招标发行和报价发行两种。招标发行分为价格招标和数量招标；报价发行是指本行在发行前确定同业存单的全部发行要素，发行开始后投资人点击该报价即以本行设定的发行要素认购成交。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第三章  同业存单发行', '第十条', '同业存单发行利率、发行价格等以市场化方式确定。其中，固定利率存单期限原则上不超过1年，具体包含1个月、3个月、6个月、9个月和1年，参考同期限上海银行间同业拆借利率定价。浮动利率存单以上海银行间同业拆借利率为浮动利率基准计息，期限原则上在1年以上，包括1年、2年和3年。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第三章  同业存单发行', '第十一条', '同业存单在银行间市场清算所股份有限公司登记、托管、结算。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第三章  同业存单发行', '第十二条', '本行发行同业存单应根据《同业存单管理暂行办法》相关规定进行信息披露。（一）网上披露。本行发行同业存单应当在中国货币网、银行间市场清算所股份有限公司官方网站上披露相关信息。信息披露应遵循诚实信用原则，不得有虚假记载、误导性陈述或重大遗漏。（二）发行计划。每年首只同业存单发行前，向市场披露该年度发行计划。在该年度内若发生重大或实质性变化的，需及时重新披露更新后的发行计划。（三）发行要素公告和发行情况公告。每期同业存单发行前和发行后分别披露该期同业存单的发行要素公告和发行情况公告。（四）重大事件披露/公示。同业存单存续期间，任何影响本行履行债务的重大事件，应当及时进行披露。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第四章  业务流程', '第十三条', '同业存单发行流程：（一）本行计划财务部于每年首期同业存单发行前将当年同业存单发行计划上报中国人民银行备案，至少提前3个工作日向同业拆借中心登记当年发行备案额度，并确保当年发行备案额度与本行向人民银行备案的年度发行计划保持一致。（二）本行每期同业存单计划发行量不得超过本行当年可用额度，当年可用额度=当年备案额度-已发行未到期的同业存单总额-已公告未发行的同业存单总额。单期发行金额不得低于5000万元人民币。发行备案额度实行余额管理，发行人年度内任何时点的同业存单余额均不得超过当年备案额度。（三）本行计划财务部应于每年首期同业存单发行前3个工作日，报领导审定后，通过中国货币网披露该年度的发行计划。在该年度内若发行计划发生重大或实质性变化的，应及时重新披露更新后的发行计划。（四）金融市场部应在发行系统至少创建两个用户，分别负责发行条款的录入和复核。复核通过的发行条款经同业拆借中心确认后生成发行要素公告。（五）金融市场部需及时确认同业存单的中标结果，以确保发行顺利完成。（六）同业存单实行发行日下一日（T+1日）缴款制度（遇节假日顺延）。投资人应在缴款日当日规定时间前足额缴纳款项，金融市场部操作人员于缴款日通过发行系统提交同业存单缴款确认。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第四章  业务流程', '第十四条', '同业存单存续期管理：（一）计划财务部于每期同业存单发行前和发行后分别披露该期同业存单的发行情况，进行要素公告和发行情况公告。（二）同业存单存续期间，对任何影响本行履行还债义务的重大事件，本行需及时进行披露。（三）金融市场部负责及时、准确、完整记录同业存单发行、兑付要素，做好同业存单发行的相关统计报送工作。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第四章  业务流程', '第十五条', '同业存单到期日，金融市场部负责按照《银行间市场清算所股份有限公司同业存单登记托管、清算结算业务操作规程》中资金清算有关规定协同计划财务部做好同业存单还本付息工作。本行必需按照同业存单发行文件，按期兑付存单本息，不得擅自变更兑付日期。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第四章  业务流程', '第十六条', '同业存单投资和交易流程参照债券投资业务操作规程办理。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第五章  同业存单投资和交易', '第十七条', '本行同业存单的交易应通过同业拆借中心交易系统达成。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第五章  同业存单投资和交易', '第十八条', '同业存单投资计划及交易策略根据本行资产负债结构状况及流动性管理需要制定。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第五章  同业存单投资和交易', '第十九条', '公开发行的同业存单可在银行间市场交易流通，可进行的交易品种包括买卖、回购以及人民银行批准的其他交易品种。定向发行的同业存单只能在该只同业存单初始投资人范围内流通转让。同业存单二级市场交易通过同业拆借中心电子交易系统进行。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第五章  同业存单投资和交易', '第二十条', '投资买入同业存单前，需对交易业务对手进行管理，对拟投资同业存单的发行人进行同业授信，确保投资的同业存单在本行足额授信额度之内。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第五章  同业存单投资和交易', '第二十一条', '本行同业存单的交易经营部门不得投资或变相投资本行发行的同业存单。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第六章  同业存单会计核算', '第二十二条', '同业存单发行的会计核算按照省联社相关规定执行。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第六章  同业存单会计核算', '第二十三条', '同业存单投资业务归入本行投资相关科目，根据投资目的不同可计入交易类、可供出售类和持有到期类，买入后进行日常计提和摊销。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第六章  同业存单会计核算', '第二十四条', '同业存单回购业务计入买入返售或卖出回购金融资产科目核算。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第七章  应急管理', '第二十五条', '由于发行系统、客户终端或通讯线路发生故障，导致无法使用发行系统进行相关操作的，或者在复核发行条款设置后仍需进行变更的，本行必需依据《全国银行间同业拆借中心本币交易应急服务规则》（中汇交发〔2010〕第283号）向同业拆借中心申请进行应急服务。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第八章  罚则', '第二十六条', '对违反中国人民银行、同业拆借中心相关规定及本管理办法要求的相关责任人，按照中国人民银行、同业拆借中心和本行相关处罚规定执行。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第九章  附则', '第二十七条', '本办法未尽事宜，参照相关制度规定执行。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第九章  附则', '第二十八条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《同业存单业务管理办法》', '第九章  附则', '第二十九条', '本办法自发文之日起实施。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）对境内金融机构的统一授信，防范和控制同业授信风险，保障资产安全，根据《××××银行股份有限公司同业授信业务管理办法》，制定本操作流程。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第二章  操作细则', '第二条', '同业授信的受理金融市场部客户经理负责收集同业客户申报的授信相关资料，包括：     1、营业执照正副本（三证合一）     2、金融许可证复印件     3、法定代表人（负责人）身份证复印件     4、开户许可证复印件     5、上三年度审计报告     6、上一季度财务报表     7、公司章程     8、最新验资报告     9、总行简介、主要高管人员简历、组织架构以上材料须加盖申请授信行总行公章。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第二章  操作细则', '第三条', '同业授信的调查（一）同业客户经理资料受理完毕，做好整理，出现要件缺失、资料内容不详以及前后各期数据不连续等情况，应要求客户补充资料并作好解释。（二）符合授信要求的同业机构，按要求填写《××××银行股份有限公司同业统一授信额度申请表》。（三）金融市场部受理人员根据同业客户资信状况与本行的业务合作情况，初步确定客户的授信额度。（四）金融市场部业务复核人员将审核无误后的《同业授信额度申请表》及拟授信客户相关资料提交本部门风险经理审核同意后，提交金融市场部负责人审批。（五）金融市场部负责人审批并签署意见后，资料提交本行授信评审部，经授信评审部审查同意后提交本行信贷审查委员会审议决策。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第二章  操作细则', '第四条', '同业授信的审查授信评审部主要采用内部评价方式对拟授信金融机构的经营情况、财务状况、行业风险以及与本行往来情况等方面进行审查，同时结合外部评级进行综合评估。具体评估内容：（一）财务分析包括：总量分析、比率分析、横向同业水平分析和纵向历史水平分析四个方面。其中总量分析可从总资产、总资本、净资产、净利润等方面开展。（二）经营管理状况包括金融机构的业务范围、机构设置、经营管理手段、管理层、风险管理机制等。（三）与本行往来情况，主要通过授信金融机构与本行在账户使用、资金清算、资金拆借、国际结算、授信额度等方面的业务合作情况。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第二章  操作细则', '第五条', '同业授信的审批     （一）同业基本授信额度由本行信贷审查委员会审批决策，本行行长可行使一票否决权。     （二）本行同业临时授信额度申报操作按照基本授信额度申报程序进行。临时授信额度为一次性额度，针对特定业务办理，业务终结自动失效。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第三章  授信业务管理', '第六条', '金融市场部按排专人负责同业授信额度具体使用情况，按交易类别登记具体的交易台账，用信额度不得突破同业统一授信额度。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第三章  授信业务管理', '第七条', '金融市场部业务经办人员要密切关注已用信客户使用和管理过程中的信息，实时掌握和监测交易对手的具体情况。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第三章  授信业务管理', '第八条', '金融市场部应定期将业务使用情况、业务风险状况、授信对象的经营情况和与我行合作的态度反馈给授信评审部，出现特殊情况或重大信息，应立即上报信贷审查委员会。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第三章  授信业务管理', '第九条', '同业基本授信额度每年核定一次，业务经办过程中出现新的同业客户授信、调整授信额度或增加临时授信额度的，金融市场部客户经理可随时提出申请。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第三章  授信业务管理', '第十条', '突发事件处理授信对象出现下列情况，金融市场部可直接调整、暂停或终止对授信对象的授信，报授信评审部备案，遇特殊情况或重大信息情况时，金融市场部负责人应立即报告本行信贷审查委员会。（一）出现重大经营困难或财务危机，包括但不限于：所有者权益折人民币余额小于5000万元；流动性比率小于25%；发生亏损且无明显改善；（二）出现重大违规或违约行为；（三）提供虚假授信资料的；（四）发生兼并、收购、重组等重大事项；（五）授信对象涉及重大法律诉讼；（六）著名评级公司对授信对象的信用等级评定结果有重大调整；（七）其他可能影响本行资金安全的事件或隐患。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第三章  授信业务管理', '第十一条', '授信额度限制(一)单家商业银行对全部法人金融机构的融出资金余额不得超过该银行各项存款的50%；(二)单家商业银行对所有非银行金融机构的融出资金余额不得超过该银行资本净额的25%；(三)单家商业银行对单一法人机构的同业融出资金扣除风险权重为0的资产以及风险缓释后的净额不得超过该银行一级资本的50%；(四)中国银监会根据审慎监管需要适时调整该比例。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第四章  附则', '第十二条', '本操作流程由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《同业授信业务操作流程》', '第四章  附则', '第十三条', '本操作流程自公布之日起实施。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）对境内金融机构的统一授信，防范和控制同业授信风险，保障资产安全，根据《中华人民共和国商业银行法》、《商业银行授权、授信管理暂行办法》等法律、规章及本行相关制度，结合实际情况，制定本办法。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第一章  总则', '第二条', '同业授信管理是指本行对单一同业客户统一确定最高综合授信额度，并加以集中统一风险控制的资产风险管理制度。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第一章  总则', '第三条', '本办法适用于境内同业授信额度的评定。本行同业综合授信额度为内部风险控制额度，不是信用提供的承诺，本行不对授信对象承担任何义务，未经本行批准，任何个人和单位不得与金融机构签订任何形式的外部授信协议。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第二章  组织管理体系', '第四条', '金融市场部是金融机构同业授信的用信部门，负责开展对同业客户授信的受理、尽职调查、申报工作，在确定的授信对象和额度范围内办理授信业务，并负责用信后的管理。在日常业务办理过程中，动态监测授信对象可能存在的风险并及时报告。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第二章  组织管理体系', '第五条', '风险管理部负责对金融机构同业授信风险的监督、识别、评估、控制，并向经营管理层进行风险报告。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第二章  组织管理体系', '第六条', '授信评审部负责对金融同业业务的授信监督管理，负责对金融市场部申报的金融机构同业授信进行审核与提交审批。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第二章  组织管理体系', '第七条', '合规管理部负责审核金融机构同业授信管理办法和流程的合规性和有效性，对金融市场部授信业务进行检查。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第二章  组织管理体系', '第八条', '审计稽核部负责对金融机构同业授信进行审计稽核和监督。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第二章  组织管理体系', '第九条', '信贷审查委员会负责同业授信业务的审议和决策。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第三章  授信对象和范围', '第十条', '本办法所称授信对象是指法人注册地在我国境内的银行及非银行金融机构，银行类金融机构包括股份制商业银行、城市商业银行、农村商业银行（含农村合作银行）、农村信用社等。非银行类金融机构包括保险公司、证券公司、基金公司、信托公司、财务公司、融资租赁公司等。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第三章  授信对象和范围', '第十一条', '对于全国性商业银行、上市公司城商行以及总资产余额达到2000亿元以上的银行金融机构等同业客户免于授信，按本行单户最高授信额度确定。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第三章  授信对象和范围', '第十二条', '授信对象须具备如下条件：(一)具有独立法人资格、获监管当局批准设立，有权经营金融业务的银行和非银行金融机构。(二)成立一年以上，经营及财务状况平稳、无不良记录，重组、分拆、兼并、更名的金融机构如核发新的营业执照和经营金融业务许可证后的期限不到一年的，以其前身单位的存续时间为准；(三)获准办理金融机构间融资业务；(四)银行类金融机构所有者权益折人民币余额不小于5000万元；流动性比率不小于25%；资本充足率不低于8%；总资产不低于50亿元等。非银行类金融机构相关财务指标需符合监管部门要求。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第三章  授信对象和范围', '第十三条', '下列交易需纳入授信管理：（一）存放同业； （二）资金信用拆出； （三）投资同业存单；（四）票据转贴现；（五）其他授信业务。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第四章  授信额度的方式、期限及占用', '第十四条', '同业授信额度（以下简称授信额度）分为基本授信额度和临时授信额度两种方式。基本授信额度可在额度有效期内循环使用，临时授信额度为一次性额度，不得循环使用；基本授信额度的有效期一般为一年，临时授信的有效期单笔确定，期限一般不得超过基本授信期限。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第四章  授信额度的方式、期限及占用', '第十五条', '同业授信业务的集中度管理：(一)单家商业银行对全部法人金融机构的融出资金余额不得超过该银行各项存款的50%；(二)单家商业银行对所有非银行金融机构的融出资金余额不得超过该银行资本净额的25%；(三)单家商业银行对单一法人机构的同业融出资金扣除风险权重为0的资产以及风险缓释后的净额不得超过该银行一级资本的50%；(四)中国银监会根据审慎监管需要适时调整该比例。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第四章  授信额度的方式、期限及占用', '第十六条', '授信额度一般为免担保的额度，也可以是抵、质押或保证下的额度；既可以是包含多个产品单项额度的总额度，也可以是单个产品的单项额度。总额度内各单项额度可以调剂使用。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第四章  授信额度的方式、期限及占用', '第十七条', '按照集中统一控制原则，授信仅针对一级法人机构，不对分支机构授信。法人的各分支机构与我行发生业务纳入其总行同业统一授信管理。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第五章  授信实施', '第十八条', '基本授信额度申报由金融市场部负责发起，按要求收集拟授信对象的相关资料，填制授信申请表，经风险经理审核，部门负责人审批同意后，报授信管理部门审查后出具审查意见提交信贷审查委员会审议决策。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第五章  授信实施', '第十九条', '临时授信额度申报操作按照基本授信额度申报程序进行。临时授信额度为一次性额度，针对特定业务办理，业务终结自动失效。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第五章  授信实施', '第二十条', '在授信决策中，本行行长享有行使一票否决的权利。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第六章  授信工作的组织管理', '第二十一条', '金融市场部业务经办人员建立客户档案，对同业机构的基础资料、授信批准文件、额度使用核准文件、交易资料等进行归档管理，并建立相应交易台账，记录授信执行情况。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第六章  授信工作的组织管理', '第二十二条', '金融市场部负责控制同业授信额度使用情况，并分同业授信对象登记相应交易台账，用信额度不得突破同业授信额度。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第六章  授信工作的组织管理', '第二十三条', '金融市场部业务经办人员要密切关注已用信客户使用和管理过程中的信息，以便在更新额度时综合考虑。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第六章  授信工作的组织管理', '第二十四条', '授信对象出现下列情况时，金融市场部可直接调整、暂停或终止对授信对象的授信：（一）出现重大经营困难或财务危机，包括但不限于：所有者权益折人民币余额小于5000万元；流动性比率小于25%；发生亏损且无明显改善；（二）出现重大违规或违约行为；（三）提供虚假授信资料的；（四）发生兼并、收购、重组等重大事项；（五）授信对象涉及重大法律诉讼；（六）著名评级公司对授信对象的信用等级评定结果有重大调整；（七）其他可能影响本行资金安全的事件或隐患。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第七章  授信工作的监督管理', '第二十五条', '授信评审部负责组织、管理和监督对金融同业的授信工作，并对授信的执行情况进行集中控制和监测。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第七章  授信工作的监督管理', '第二十六条', '合规管理部负责对金融市场部授信工作进行监督和定期检查，发现越权审批授权额度或超额度授信等违规行为，在调查核实的基础上，决定调直或撤销使用权限，对情况严重的或造成较大损失的，应按照相关规定追究当事人及有关负责人的责任。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第七章  授信工作的监督管理', '第二十七条', '金融市场部要加强对授信对象的动态管理，及时了解授信对象的资信和业务变动情况，不得放松日常风险管理。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第七章  授信工作的监督管理', '第二十八条', '金融市场部应定期将业务使用情况、业务风险状况、授信对象的经营作风和与我行合作的态度反馈给授信评审部，出现特殊情况或重大信息，应立即上报信贷审查委员会。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第八章  附则', '第二十九条', '本办法未尽事宜，参照有关规定执行。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第八章  附则', '第三十条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《同业授信业务管理办法》', '第八章  附则', '第三十一条', '本办法自公布之日起实施。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）商业承兑汇票贴现业务运作，防范和控制风险，根据《中华人民共和国票据法》、《支付结算办法》、《贷款通则》等法律法规以及省联社有关规章制度，结合本行实际，制订本办法。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第一章  总则', '第二条', '商业承兑汇票贴现业务是指银行以购买贴现申请人未到期商业承兑汇票方式而发放的贷款。在汇票到期，银行不获付款时，可以对承兑人、背书人、出票人、担保人以及汇票其他债务人行使追索权。持票人可以不按照汇票债务人的先后顺序，对其中任何一人、数人或者全体行使追索权。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第一章  总则', '第三条', '本行办理贴现的商业承兑汇票必须是企业之间以真实的商品交易为基础而签发使用的商业票据，贴现期限一般在三个月以内，最长不超过六个月。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第一章  总则', '第四条', '办理商业承兑汇票贴现，必须严格执行国家的经济及金融法律、法规和信贷政策，遵循商业银行信贷资金安全性、流动性和效益性原则。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第一章  总则', '第五条', '本行对商业承兑汇票贴现业务的担保施行专项管理，并核定相应管理额度，担保方式包括保证、抵押、质押；对担保施行专项管理的商业承兑汇票贴现业务实行总量控制。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第六条', '各支行、公司业务部、小微业务部（以下简称“经办行”）主要职责包括：（一）负责商业承兑汇票业务的营销、推广、客户的准入把关；（二）协助客户对其签发商业承兑汇票备案，商业承兑汇票到期承兑的提示并组织落实；（三）负责对商业承兑汇票贴现业务的真实性、合规性进行调查，对贴现资料进行初审；（四）负责做好贴现相关资料的备查工作。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第七条', '金融市场部主要职责包括：（一）负责拟定商业承兑汇票贴现业务管理制度与流程；（二）受理并办理经办行提交的商业承兑汇票贴现业务；（三）负责商业承兑汇票贴现业务台账建立，做好本行贴现业务的测算和考核，按照外部监管的规定报送相关报表；（四）负责贴现业务资料的整理，归档工作；（五）根据市场利率协助计划财务部确定贴现利率。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第八条', '信贷管理部主要职责：   负责经办行贴现业务的用信管理，做好贴现单位的用信审批。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第九条', '授信评审部主要职责：对申报企业提交资料的合法性和合规性进行审查，对符合贴现要求的，交有权审批人审批，确定授信额度。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第十条', '运营管理部下属清算中心主要职责：（一）指定专门人员负责对商业承兑汇票进行核验，判断票面的真实性，查看票面信息的完整性。（二）负责对已贴现的商业承兑票据实物的保管，建立商业承兑汇票到期台账，做好到期商业承兑汇票的委托收款工作，做好已贴现票据的出入库保管。（三）负责会计核算手续的办理、资金的划拨等工作。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第十一条', '合规管理部主要职责：（一）负责对商业承兑汇票贴现业务管理办法及操作流程的合规性以及有效性进行审查；（二）从法律角度审定有关商业承兑汇票贴现业务服务协议、合同文本。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第十二条', '风险管理部主要负责：对商业承兑汇票贴现业务的风险监测、识别、评估和控制工作，出具评估报告，并按照要求报送。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第二章  职责分工', '第十三条', '审计部主要职责：负责对商业承兑汇票贴现业务不定期进行审计稽核监督。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第三章  基本规定', '第十四条', '申请在本行进行商业承兑汇票贴现，应同时符合下列条件：（一）商业承兑汇票贴现业务纳入本行授信额度管理，必须坚持“先授信、后用信”的原则；向本行申请办理商业承兑汇票贴现的申请人必须具有本行授信额度；拟贴现的商业承兑汇票必须为本行认可具有签发商业承兑汇票资格的企业所签发。（二）商业承兑汇票的出票人、承兑人必须为资信状况良好，具有支付汇票金额的可靠资金来源的企业法人以及其他组织，同时需满足以下条件的客户：1.符合国家产业政策，行业有较好的成长性；2.企业在行业内有较高的市场占有率；3.企业经营稳健，信誉良好，近三年未有工商、税务、银行等方面的不良记录，未涉及重大经济案件；4.企业资产负债率在70%以下；5.需经本行认可的信用评估机构评定的信用等级在A级（含）以上的；6.企业的现金流充沛，具有支付汇票金额的可靠资金来源；7.具有真实的商品交易背景；8.与原材料供应商建立长期稳健的合作关系，其主要上、下游企业在我行开户；9.签发的商业承兑汇票总额原则上不超过其上年度采购总量80%。（三）商业承兑汇票的贴现申请人，需在本行开立结算账户，且结算业务开展情况良好。（四）贴现申请人与出票人或者直接前手之间具有真实的商品交易关系。 （五）了解并熟悉商业承兑汇票的使用、操作流程并愿意遵守本行及人民银行对商业承兑汇票的有关管理规定。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第三章  基本规定', '第十五条', '对商业承兑汇票审核的主要内容包括：（一）应使用中国人民银行规定格式印刷的商业承兑汇票，水印应相符；（二）商业承兑汇票记载内容应真实、完整、合规、字迹清楚，付款期限不能超过6个月；（三）商业承兑汇票的出票人、承兑人、背书人的签章应符合有关规定；（四）背书应连续，背书使用的粘单应按规定在汇票与粘单的粘接处由粘单上第一背书人签章；（五）商业承兑汇票无涂改，大小写金额一致；（六）其他需要审核的内容。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第三章  基本规定', '第十六条', '存在下列情形之一者，不予办理商业承兑汇票贴现：（一）根据《支付结算办法》第三十八条规定内容，票据债务人可以拒绝付款的；（二）转让给两人以上，即两个以上被背书人各获部分转让款的；（三）背书转让不连续的；（四）加附的粘单未盖骑缝章或签章不规范的；（五）背书人签章日期有误、被背书人名称填错或多次转让中有背书不符合要求的；（六）票面审核不符合付款条件的；（七）商业承兑汇票有挂失止付通知或被法院冻结的；（八）商业承兑汇票有汇票疑问或不符合规定以及无法核实真伪的；（九）商业承兑汇票属于“回头背书”的；（十）有其他不符合法律法规情况的。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第三章  基本规定', '第十七条', '贴现金额计算：贴现金额=票面金额-贴现利息贴现利息=票面金额×（贴现天数+托收天数）×(月贴现利率/30天) 贴现天数指贴现银行向贴现申请人支付贴现金额之日起至该贴现商业承兑汇票到期日前一天止的实际天数。承兑人在异地的，贴现期限的计算应另加3天托收天数，节假日顺延。       第十八条    贴现后的商业承兑汇票到期时，清算中心应及时向商业承兑汇票付款人收取票款，若票款未按期收回，清算中心应及时从商业承兑汇票出票人、承兑人、背书人和担保人账户扣收，对尚未扣回的部分转为贴现申请人逾期贷款，并按贴现合同约定计收贷款利息。贴现未收回金额视同流动资金不良贷款管理，客户经理应按照有关法律、法规及本行规章制度积极做好催收工作，及时主张本行权利。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第十九条', '受理申请。经办行客户经理负责受理客户的申请，按照规定收集客户基本资料，出具商业承兑汇票贴现业务调查报告，需要客户提供的资料如下：1、经年审合格的（三证合一）的营业执照;2、企业（法人）基本账户开户许可证；3、企业法人代表证明书或授权委托书；4、董事会（股东会）决议；5、公司章程、企业征信授权书及相关报表等；6、贷款卡；7、本行要求的其他资料。收集全的资料需加盖贴现单位公章，连同调查报告，经本单位负责人签署意见后，加盖本单位业务章印，上报总行授信管理部进行授信审批。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第二十条', '授信审批。（一）授信评信部审查人员对企业申报提交资料的合规性、齐全性进行审核，按照本行贷款审查要求进行风险审查，出具审查意见书，收集整理相关资料提交贷款审查委员会审核决策，总行行长享有行使票一否决权。（二）商业承兑汇票贴现业务审批通过后，由授信评审部经办人员统一操作并入台账统计，相关手续办理由经办行客户经理跟踪办理。（三） 采取抵押担保方式的，由商业承兑汇票贴现签发人及担保人与本行签订最高额抵押借款（信用）合同，在该合同其他约定事项中注明：“乙方所签发（承兑）的商业承兑汇票到甲方办理贴现视同乙方向甲方的借款，受本合同条款及条件的约束”。提交授信审查岗审查后办理抵押登记手续。（四）客户经理将申请客户的相关承诺，担保承诺以及抵押方式下的《最高额抵押借款（信用）合同》（含抵押物清单）、评估报告、内部评估报告单及他项权证（抵押登记书）提交授信审查岗备案。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第二十一条', '贴现申请已授信的商业承兑汇票贴现申请人向经办行提出贴现申请，需提供以下资料：（一）未到期的合法有效的商业承兑汇票原件及商票正反面复印件一份；（二）贴现申请人提供与其直接前手之间购销合同或交易凭证（复印件）；（三）贴现申请人提供与其直接前手之间的增值税发票（原件）；（四）贴现申请人经年检的三证合一营业执照（复印件），公司章程及验资报告（复印件）；（五）贴现凭证、商业汇票贴现合同一式二份；商业承兑汇票贴现审批表原件一式三份；（六）商业承兑汇票查询查复书原件及复印件各一份(须实地查询）； （七）有处分权人的抵(质)押承诺及抵(质)押物清单和所有权、产权证明。（八）银行认为必须担保的保证单位的保证承诺及营业执照、企业代码证、法人代表身份证的复印件。（九）本行规定的其他资料。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第二十二条', '贴现初审经办行客户经理负责贴现业务的初审，具体内容包括：审查客户是否具有相应授信额度；授信条件是否落实；对列入贴现担保施行专项管理额度的必须审核办理贴现商业承兑汇票的签发人（承兑人）是否已办妥相关承诺及保证文书以及抵押登记等手续；申请贴现票据是否已向本行办理票据备案；审查资料的齐全性、合规性，审核贴现申请人提供的合同是否具有真实的贸易背景，鉴别增值税发票真伪。登记商业承兑汇票贴现台账，审查通过后经经办行负责人签署意见后，陪同客户前往本行办理贴现。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第二十三条', '票据的复审及款项划转 （一）金融市场部负责贴现业务资料完整性审核，审核的主要内容有：1、审核《商业承兑汇票贴现申请、审批表》、《商业承兑汇票贴现协议》所记载的内容与票面信息的一致性及签字盖章的完整性；2、 贴现申请人提交的税务发票、商品交易合同复印件是否足以覆盖票面金额；3、 查询查复书是否真实有效及与银票要素是否一致；4、办理贴现业务规定的相关资料是否齐全完整。（二）清算中心对商业承兑汇票真伪性、合规性进行全面审核，核对查复书上承兑人印章是否与商业承兑汇票上承兑人印章一致，并复算贴现利息的正确性，做好贴现票据的实物以及跟单资料的保管工作，办理账务处理。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第二十四条', '资金监督经办行客户经理必须按照本行贷后管理的有关规定和要求，做好贷后检查工作，了解贴现资金使用情况，检查贴现申请人的生产经营和资金运作情况。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第二十五条', '贴现到期或未收回处理商业承兑汇票贴现后到期时，本行清算中心应及时向商业承兑汇票付款人发出托收，如票款未按期收回，应及时通知管户客户经理从商业承兑汇票出票人、承兑人、背书人、贴现人、担保人账户中扣收，对还未扣回的部分转为商业承兑汇票申请人的逾期贷款，并按贴现合同约定计收逾期贷款利息，视同流动资金不良贷款管理，客户经理应按照有关法律、法规及本行规章制度积极做好催收工作，及时主张本行权利，发出风险预警。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第四章  操作流程', '第二十六条', '资料归档各经办行、金融市场部和清算中心办理完贴现业务后，必须按排专人按照本办法档案管理内容规定将有关贴现资料及时统一归档保管。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第五章  风险管理', '第二十七条', '对发现客户办理贴现业务短期内大幅度增加、新开户客户即办理大额贴现申请、贸易流通类客户频繁发生贴现业务、办理的贴现业务资金超过其经营规模等情况的，各经办行客户经理要深入了解客户的真实情况，做好贷前的尽职调查。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第五章  风险管理', '第二十八条', '已办理完贴现业务的，经办行应按排专人跟踪核实贴现资金的具体用途以及使用方向，查看资金有无回流至出票人，有无流入股市，房产等国家禁止的行业。发现苗头，及时采取措施，有效防范风险，必要时暂停其贴现业务资格。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第六章  档案管理', '第二十九条', '经办行应按要求做好以下资料的保管：（一）企业法人资格证明文件及有关法律文件，包括：（1）经年审合格的三证合一营业执照;（2）企业（法人）基本账户开户许可证；（3）企业法人代表证明书或授权委托书；（4）董事会（股东会）决议；（5）其他需要提供的与贴现业务相关的资料。（二）贴现申请人的近期财务报表。上述资料均需加章贴现单位公章，且为A4纸复印件或扫描文件。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第六章  档案管理', '第三十条', '金融市场部负责建立贴现登记台账，做好如贴现申请书、贴现协议书、贴现贷款放款通知书及银票复印件、查询查复书、税务发票和商品交易合同复印件等贴现业务相关资料的装订保管。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第六章  档案管理', '第三十一条', '清算中心必须建立贴现业务相关分户账和到期催收台账，登记代保管有价证券登记簿，严格按照保管有价单证的要求办理银票会计核算和出入库手续，并妥善保管好银票证明等跟单资料。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第七章 附则', '第三十二条', '本办法由××××银行股份有限公司金融市场部负责制定、解释和修改。'),
(NULL, '金融市场部', '《商业承兑汇票贴现业务管理办法与操作流程》', '第七章 附则', '第三十三条', '本办法自印发之日起施行。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第一章  总则', '第一条', '为规范和发展××××银行股份有限公司（以下简称“本行”）再贴现业务，防控票据风险，根据《中华人民共和国票据法》、中国人民银行《商业汇票承兑、贴现与再贴现管理暂行办法》等有关规定，结合本行实际，制定本办法。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第一章  总则', '第二条', '本行开展再贴现业务的目的是解决临时性周转资金需要，增加资金的流动性。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第一章  总则', '第三条', '本办法所称再贴现，是指本行为了取得资金，将未到期的已贴现商业汇票再已贴现的方式向中国人民银行转让的票据行为。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第一章  总则', '第四条', '本办法中的票据再贴现业务包括：票据卖断式业务和票据回购式业务。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第一章  总则', '第五条', '票据回购业务指金融机构将所持有的已贴现票据，以不改变票据权利人的方式暂出售给人民银行，人民银行按票面金额扣除回购利息后，向金融机构支付回购资金，同时以合同的形式约定回购期限，到期后由该金融机构约定赎回同一笔票据的融资行为。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第二章  职责分工', '第六条', '计划财务部按照当地人民银行的要求，在规定时间内，负责向人民银行进行再贴现业务预申报工作。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第二章  职责分工', '第七条', '金融市场部负责商业汇票再贴现业务的日常操作，主要包括提请内部审批程序、提交再贴现申请资料、向人民银行申请赎回等工作。负责对再贴现商业汇票的各要素、背书、签章、查询查复以及合同等要件进行复验。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第二章  职责分工', '第八条', '运营管理部负责商业汇票再贴现业务的会计核算，其下属清算中心，负责商业汇票再贴现业务账务处理、资金汇划等工作'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第二章  职责分工', '第九条', '合规管理部负责对商业汇票再贴现业务管理办法和操作流程的合规性、有效性的审核。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第二章  职责分工', '第十条', '风险管理部负责对商业汇票再贴现业务的风险识别、计量、监测，做出独立评估，定期提交风险审查报告。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第二章  职责分工', '第十一条', '审计稽核部负责定期或不定期对商业汇票再贴现业务进行审计稽核和监督检查。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第三章  基本规定', '第十二条', '申请再贴现的票据必须是符合法定要式且是本行已办理贴现的商业汇票。应具备以下条件：（一）商业汇票必须以真实合法的商品交易为基础；（二）商业汇票的承兑人必须有良好的信誉；（三）已由本行贴现一段时间的且尚未到期的商业汇票；（四）其他经中国人民银行审查认定同意办理再贴现的商业汇票。金融市场部应提供用于已贴现商业汇票的有关单证，并对上述票证的真实性、合法性、合规性负责。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第三章  基本规定', '第十三条', '金融市场部根据商业汇票市场利率、货币市场利率、本行存贷款比例、头寸情况等要素拟申请办理再贴现业务的，需在当地人民银行核定额度范围内，具体审批程序依照本行转授权管理办法执行。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第三章  基本规定', '第十四条', '办理票据回购业务，商业汇票不作背书，票据权利人仍为原贴现金融机构。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第三章  基本规定', '第十五条', '商业汇票再贴现期限从票据再贴现之日起至汇票到期之日止；票据回购期限自中国人民银行批准给付票据回购资金之日起，至票据回购申请人与中国人民银行约定的回购日止（不得定为法定节假日），最长不超过半年。中国人民银行有特殊规定的依照规定办理。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第三章  基本规定', '第十六条', '再贴现利率按同期中国人民银行规定的再贴现利率执行。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第三章  基本规定', '第十七条', '再贴现金额为商业汇票金额扣除贴现（回购）利息后的余额。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第四章  操作程序', '第十八条', '本行申请再贴现业务，应向中国人民银行提交下列资料，并保证资料的真实、合法。（一）《中国人民银行再贴现业务申请书》及《再贴现回购合同》；（二）按规定填写并加盖有效印鉴的《再贴现凭证》；（三）背书连续，办理贴现票据的原件和复印件；（四）贴现凭证第一联复印件；（五）商业汇票查询查复书复印件；（六）申请票据回购业务的电子清单；（七）贴现申请人与出票人或其前手的增值税发票复印件；（八）中国人民银行规定的其他资料。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第四章  操作程序', '第十九条', '首次办理票据回购业务，需向中国人民银行提交以下材料：（一）单位公章以及单位负责人印鉴，或单位负责人授权书或被授权人预留的印鉴；（二）商业汇票承兑、贴现的内控制度和操作规程；（三）中国人民银行办理再贴现业务要求的其他资料。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第四章  操作程序', '第二十条', '本行金融市场部拟向中国人民银行申请办理再贴现业务，需在人民银行规定的授信额度以内开展，业务申请需经部门负责人批准，计划财务部审核，报分管行长审批后方可办理。中国人民银行同意后，本行应按排专人携带票据原件，签订回购合同，办理再贴现业务手续，具体操作流程如下：（一）申请金融市场部根据本行的需求情况向人民银行申请办理再贴现并按照规定提交相关单证。（二）审批接到人民银行同意再贴现意向后，登记再贴现审批登记表，按本行转授权相关规定办理审批手续。（三）资料准备及审查1、资料准备。金融市场部交易人员打印拟出票清单，交清算中心提取票据，清算中心按照要求对票据的相关要素以及所附的相关单证进行初审。2、审查。金融市场部经办人员负责对票据的合法性，真实性以及相关单证完整性进行审查，具体内容如下：  ⑴信贷资金投向审查，资金投向是否符合国家的产业政策和货币政策。  ⑵票据的合规性审查，票据的格式应为中国人民银行统一印制格式，要素齐全，内容完整，真实，汇票的大小写金额相符，签章符合规定，有购销合同号，承兑协议号等。  ⑶票据的真实性审查，交易合同是否真实、合法，要素是否齐全，背书是否连续，是否有查询书，增值税发票是否真实、合规。  ⑷再贴现申请书、再贴现凭证是否与汇票一致，要素是否齐全，是否加盖了贴现行公章。3、清算中心接到再贴现通知后，安排人员会同金融市场部经办人员双人至人民银行办理。（四）合同的签订。人民银行对商业汇票真伪进行辨验，并对材料进行审核，再贴现业务批准后填制一式五联贴现凭证并加盖相应印鉴，双方协商约定再贴现期限，签订再贴现协议。（五）账务的处理本行清算中心负责再贴现业务的款项划转，经办人员根据实际再贴现金额进行账务处理。再贴现利息的计算：1、再贴现利息=汇票金额×再贴现天数×(再贴现月利率/30天)2、实付再贴现金额=汇票金额—再贴现利息（六）票据回购操作1、办理票据回购的申请行，应在票据到期日前在人民银行账户内留足资金，资金不足时，人民银行作逾期贷款处理。2、本行应履行中国人民银行签订的再贴现回购合同，在票据回购到期日，金融市场部经办人员填制人民银行出具的，固定格式的回购业务凭证，按照要求规范填列好相关要素，经复核人员复核后加盖本行预留印鉴，安排专人持身份证、本行介绍信，前往人民银行办理回购业务操作，按票面金额赎回原再贴现商业汇票。（七）资料的归档金融市场部按排专人负责再贴现业务资料的收集整理，按规定做好归档保管，确保资料完整，齐全。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第五章  风险控制', '第二十一条', '再贴现业务必须严格按照本行转授权管理办法办理。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第五章  风险控制', '第二十二条', '金融市场部必须按排专人适时监测票据的到期进度，和人行清算账户余额，及时办理票据的赎回。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第六章  附则', '第二十三条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《商业汇票再贴现业务管理办法与操作流程》', '第六章  附则', '第二十四条', '本办法自发文之日起施行。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第一章  总则', '第一条', '为促进××××银行股份有限公司(以下简称“本行”)商业汇票转贴现业务的健康发展，加强风险管理，完善内控管理制度，规范操作流程，根据《中华人民共和国票据法》、《票据管理实施办法》、《商业汇票承兑、贴现与再贴现管理暂行办法》及有关法律、法规，结合本行实际，特制定本办法。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第一章  总则', '第二条', '本办法所称商业汇票是指出票人签发的，委托付款人在指定日期无条件支付确定的金额给收款人或者持票人的商业票据。分为商业承兑汇票和银行承兑汇票。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第一章  总则', '第三条', '商业承兑汇票由银行以外的付款人承兑，银行承兑汇票由银行承兑,商业汇票的付款人为承兑人。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第一章  总则', '第四条', '商业汇票转贴现系指金融机构为了取得资金，将未到期的已贴现商业汇票再以贴现方式向另一金融机构转让的票据行为，是金融机构间融通资金的一种方式。是本行以商业汇票为工具，因短期资金融通的需求，与其他商业银行或非银行金融机构以协议转让的方式进行的交易行为。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第一章  总则', '第五条', '本行开办的转贴现业务包括买断和回购(暂不办理）两种方式。采用买断方式，票据到期由持票行办理委托收款，采用回购方式的，回购期满后，由持票行返售给转贴现行，票据到期后由转贴现申请行负责委托收款。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第一章  总则', '第六条', '转贴现业务期限最长不超过6个月，电子票据转贴现期限不超过一年，利率由交易双方自主协商确定。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第一章  总则', '第七条', '本行票据转贴现业务由金融市场部负责组织实施，所涉及的资金清算，票据代管，到期办理委托收款等业务由运营管理部下属清算中心组织实施。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第二章  岗位设置', '第八条', '金融市场部设立专门的票据交易岗和复核岗，清算中心设立票据审核岗，结算岗和票据保管岗。各岗位相互制约，换人复核。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第二章  岗位设置', '第九条', '交易岗主要负责市场上信息的收集，票据交易对手的寻找，与对手方达成交易意向后，经复核员复核，依授权规定审批后，与交易对手签订正式转贴现合同。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第二章  岗位设置', '第十条', '复核员负责对交易员所有的交易环节进行复核，对交易的询价议价，审批报告流程以及交易要素进行复核。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第二章  岗位设置', '第十一条', '审票员负责对票据的现场审票，对不符合转贴现要求的不予办理，负责按照清单核对实收、实付票据。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第二章  岗位设置', '第十二条', '保管员负责按照转贴现协议票据清单，保管转贴现票据及跟单资料，并对全部票据进行台账登记。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第二章  岗位设置', '第十三条', '结算员负责按照贴现凭证，划款通知及时准确的划款或收妥资金。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第三章  风险控制', '第十四条', '票据转贴现必须严格设定授权审批权限，具体为：交易员根据交易对手提供的相关资料填列转贴现业务审请书，提交部门风险经理审核，部门负责人审批同意后，报本行授信管理部审核，提交本行信贷审查委员会审议决定，业务办理严格岗位分工协作，做到授权制度化，业务流程规范化。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第三章  风险控制', '第十五条', '金融机构向本行申请办理转贴现业务必须提供以下材料：营业执照（正、副本）复印件组织机构代码证（正、副本）税务登记证（正、副本）金融许可证复印件开户许可证复印件法人代表身份证复印件以及授权委托书以上复印件上需加盖申请人公章。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第三章  风险控制', '第十六条', '本行开办票据转贴现业务应根据本行的资金状况、融资能力、信贷投放节奏和资产负债比例管理要求等因素，谨慎规范进行业务的开展。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第三章  风险控制', '第十七条', '票据交易必须坚持双人复核原则，至少两人参与票据交易的全过程。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第三章  风险控制', '第十八条', '交易的票据为电子商业汇票时，申请金融机构的交易员需通过电子商业汇票系统发起电子商业汇票转贴现申请，交易员需密切关注资金的到账和票据的状态。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第十九条', '交易员通过市场寻找到合适的交易对手后，复核员复核，具体交易权限为：部门风险经理审核，部门负责人审批，报分管行长审批后提交授信管理部审核，报本行信贷审查委员会审议决策。金融市场部在发生转贴现业务时需严格授信范围，逐级审批。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第二十条', '进行转贴现业务，应签订书面合同，合同中应载明交易日期，若为回购还需注明到期交易日期，交易方向，票据份数及金额、交易价格或转贴现利率，实际划款金额，账户与结算方式，转出方的票据责任等要素，合同应包括转贴现协议及票据清单，清单应详细列明票据号码，承兑银行，金额，出票日，到期日，剩余天数、应计贴现利息、实际应划款金额等。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第二十一条', '票据转贴现业务应严格审查转贴现票据，重点审核票据的要素是否齐全（承兑行章印是否齐备）、背书是否清晰、是否涂改、是否连续、转贴现申请人是否签章等。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第二十二条', '转贴现申请人应填制贴现凭证，凭证上应列明汇票金额、贴现利率、贴现利息以及实付贴现金额等要素并加盖转贴现申请人印鉴，转贴现申请行审核后，将实付贴现金额划入申请人指定账户。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第二十三条', '交易员、审票员在外地进行票据交易时，应将票据进行打包、封装后双人在封签上签字。返回后将相关资料交结算员和保管员，并严格交接手续，做好核对工作。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第二十四条', '本行作为转贴现卖出行时，交易员需填列转贴现票据出库审批表，由交易员签字确认，部门负责人同意后方可从保管员处提取票据。未被最终交易的票据，退回保管员处，保管员销记台账。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第二十五条', '交易员、审票员在携票往返途中出现的任何意外事件应及时向领导反映，原则上不得携票过夜，确因路途原因无法当日返回时，应将收妥后的票据封装并交交易对手入库保管或次日交割票据。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第四章  业务的处理', '第二十六条', '转贴现购入的票据到期前，清算中心应及时发出委托收款。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第五章  稽核与监督', '第二十七条', '本行审计稽核部负责对金融市场部的票据交易业务以及清算中心的台账及账务处理进行检查，内容包括：交易方式、期限、对象是否符合人民银行的有关规定。操作程序和审批程序是否严格执行本办法的规定。转贴现协议、票据清单、贴现凭证、授权审批手续是否齐备。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第六章   罚则', '第二十八条', '交易人员应严格按照本办法及商业银行票据法的规定进行票据的交易，如有违规，应按有关办法进行处罚。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第六章   罚则', '第二十九条', '本办法认定的违规行为有：违反业务授权规定，超出自身岗位权限，未经批准办理转贴现业务的行为。违反业务操作流程，逆程序经办的业务行为。因疏于票据查验、保管、操作差错等造成损失的行为。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第七章   附则', '第三十条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《商业汇票转贴现业务管理办法》', '第七章   附则', '第三十一条', '本办法自公布之日起执行。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第一章 总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）市场风险管理,构建市场风险防范体系，保障资金业务、信贷业务健康、快速、持续发展，依据《商业银行内部控制指引》、《商业银行市场风险管理指引》等法律、法规，结合本行实际，制定本制度。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第一章 总则', '第二条', '本制度明确了本行市场风险管理的职责划分、控制管理方法、相应模型、管理制度等内容要求。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第一章 总则', '第三条', '本制度适用于本行市场风险的识别、评估、监测及控制等管理控制。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第一章 总则', '第四条', '本制度中的市场风险是指因市场价格（利率、汇率、股票价格和商品价格）的不利变动而使本行的表内业务和表外业务发生损失的风险。分为利率风险、汇率风险（包括黄金）、股票价格风险和商品价格风险，分别指由于利率、汇率、股票价格和商品价格的不利变动而带来的风险。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第二章 职责与权限', '第五条', '董事会承担对市场风险管理实施监控的最终责任，下设风险管理与关联交易控制委员会履行以下职责：（一）确保本行有效识别、计量、监测和控制各项业务所承担的各类市场风险。（二）负责审核、修订本行市场风险管理的战略、政策和程序，提交董事会审批。（三）确定本行可以承受的市场风险水平。（四）督促行长室采取必要的措施识别、计量、监测和控制市场风险，并定期获得关于市场风险性质和水平的报告，监控和评价市场风险管理的全面性、有效性以及行长室在市场风险管理方面的履职情况。（五）应当对本行与市场风险有关的业务、所承担的各类市场风险以及相应的风险识别、计量和控制方法有足够的了解，定期向董事会提交有关报告。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第二章 职责与权限', '第六条', '行长室职责(一)负责制定、定期审查和监督执行市场风险管理的政策、程序以及具体的操作规程。(二)负责及时了解市场风险水平及其管理状况。(三)确保本行具备足够的人力、物力以及恰当的组织结构、管理信息系统和技术水平来有效地识别、计量、监测和控制各项业务所承担的各类市场风险。(四)行长室应当对本行与市场风险有关的业务、所承担的各类市场风险以及相应的风险识别、计量和控制方法有足够的了解。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第二章 职责与权限', '第七条', '监事会职责负责监督董事会和行长室在市场风险管理方面的履职情况。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第二章 职责与权限', '第八条', '风险管理部职责本行风险管理部为本行市场风险管理牵头部门，主要承担：（一）拟定市场风险管理政策和程序；（二）监测相关业务经营部门和支行对市场风险限额的遵守情况，报告超限额情况；（三）识别、评估新产品、新业务中所包含的市场风险，审核相应的操作和风险管理程序；（四）及时向董事会和行长室提供独立的市场风险报告；（五）其他有关职责。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第二章 职责与权限', '第九条', '本行金融市场部、三农业务部、公司及小微部、电子银行部等承担市场风险的业务管理部门，市场风险管理职责：（一）负责识别、计量和监测市场风险，充分了解并在业务决策中充分考虑所从事业务中包含的本业务的各类市场风险。特别是在开展新产品和开展新业务之前应当充分识别和评估其中包含的市场风险，制定相应风险控制措施，按规定报审并执行，及时报告执行结果信息，以实现经风险调整的收益率的最大化。（二）设计、实施事后检验和压力测试。（三）负责本部门的有关市场风险监测数据的提交。（四）应当为市场风险所带来的损失承担责任。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第二章 职责与权限', '第十条', '各支行负责本支行业务中客户或交易对手相关市场风险监测数据的提交及报告。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第二章 职责与权限', '第十一条', '各支行、各有关部门负责市场风险控制措施的执行与结果信息反馈。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第三章 原则与基本规定', '第十二条', '市场风险管理应遵循量化管理，因素分析，合理定价，及时调整的原则。（一）量化管理，是指定量计算市场风险的大小；（二）因素分析，是指要分析影响利率、汇率等变动的各种因素；（三）合理定价，是指定价要考虑吸收资金成本、管理成本、资本成本、各种风险和预期利润等，坚持收入覆盖风险的原则；（四）及时调整，是指根据利率和汇率变动情况及时调整资产组合和负债组合，或者采取对冲措施转移风险。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第三章 原则与基本规定', '第十三条', '市场风险管理的策略和方法是限额管理和风险对冲。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第三章 原则与基本规定', '第十四条', '为市场风险的计量、监测和控制建立完备、可靠的管理信息系统，并采取相应措施确保数据的准确、可靠、及时和安全。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第三章 原则与基本规定', '第十五条', '审计稽核部应当定期（至少每年一次）对市场风险管理各个组成部分和环节的准确、可靠、充分和有效性进行独立的审查和评价。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第三章 原则与基本规定', '第十六条', '对市场风险有重大影响的情形制定应急处理方案。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第三章 原则与基本规定', '第十七条', '本行在开展新产品和开展新业务之前应当充分识别和评估其中包含的市场风险，建立相应的内部审批、操作和风险管理程序，并获得董事会或其授权的专门委员会/部门的批准。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第三章 原则与基本规定', '第十八条', '本行应当按照银监会关于商业银行资本充足率管理的有关要求划分银行账户和交易账户，并根据银行账户和交易账户的性质和特点，采取相应的市场风险识别、计量、监测和控制方法。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第四章 市场风险管理流程', '第十九条', '市场风险识别（一）利率风险识别1．利率风险影响因素（1）经营成本；（2）通货膨胀预期；（3）央行货币政策；（4）经济周期；（5）国际利率水平；（6）资本市场状况；（7）其他因素。2．利率风险影响范围利率风险主要影响对象是利率敏感型资产及利率敏感型负债。（二）汇率风险识别汇率风险影响因素:1．国际收支；2．通货膨胀率；3．利率政策；4．汇率政策；5．市场预期；6．投机冲击；7．其他国际突发事件或经济危机。（三）商品风险识别1．商品风险影响因素（1）商品合约中约定的交割形式和地点；（2）远期合约定价中运输、储藏和保险；（3）国内商品市场价格波动；（4）国际同类商品市场价格波动；（5）通货膨胀预期；（6）国家进出口政策、相关税制的变动。2．商品风险影响范围商品风险对本行主要影响范围在于由于市场商品价格变动导致本行所持有抵质押资产市场价格可能缩水的部分。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第四章 市场风险管理流程', '第二十条', '市场风险评估计量（一）本行对每项业务和产品中的市场风险因素进行分解和分析，及时、准确地识别所有交易和非交易业务中市场风险的类别和性质。（二）根据本行的业务性质、规模和复杂程度，对银行账户和交易账户中不同类别的市场风险选择适当的、普遍接受的计量方法，基于合理的假设前提和参数，计量承担的所有市场风险。（三）本行尽量对所计量的银行账户和交易账户中的市场风险（特别是利率风险）在全行范围内进行加总，以便董事会和高级管理层了解本行的总体市场风险水平。（四）按照《××××银行股份有限公司资本管理办法》，本行采用标准法计量市场风险。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第四章 市场风险管理流程', '第二十一条', '市场风险监测（一）本行应当对交易账户头寸按市值每日至少重估一次价值。（二）本行应按一定周期（季度、年度）末对全行市场风险进行监测。各有关部门风险管理员负责本部门的风险监测数据进行收集，汇总后提交风险管理部门。风险管理部对收集的数据进行进一步的分析评估，并形成报告意见。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第四章 市场风险管理流程', '第二十二条', '市场风险控制措施（一）本行应当建立全面、严密的压力测试程序，定期对突发的小概率事件，如市场价格发生剧烈变动，或者发生意外的政治、经济事件可能造成的潜在损失进行模拟和估计，以评估本行在极端不利情况下的亏损承受能力。压力测试应当包含定性和定量分析。压力测试应当选择对市场风险有重大影响的情景，包括历史上发生过重大损失的情景和假设情景。假设情景包括模型假设和参数不再适用的情形、市场价格发生剧烈变动的情形、市场流动性严重不足的情形，以及外部环境发生重大变化、可能导致重大损失或风险难以控制的情景。商业银行应当使用银监会规定的压力情景和根据本行业务性质、市场环境设计的压力情景进行压力测试。应当根据压力测试的结果，对市场风险有重大影响的情形制定应急处理方案，并决定是否及如何对限额管理、资本配置及市场风险管理的其他政策和程序进行改进。董事会和高级管理层应当定期对压力测试的设计和结果进行审查，不断完善压力测试程序。（二）对市场风险实施限额管理，根据业务性质、规模、复杂程度和风险承受能力设定、定期审查和更新限额。市场风险限额包括交易限额、风险限额及止损限额等，商业银行总的市场风险限额以及限额的种类、结构应当由董事会批准。在设计限额体系时考虑以下因素：（一）业务性质、规模和复杂程度；（二）商业银行能够承担的市场风险水平；（三）业务经营部门的既往业绩；（四）工作人员的专业水平和经验；（五）定价、估值和市场风险计量系统；（六）压力测试结果；（七）内部控制水平；（八）资本实力；（九）外部市场的发展变化情况。对超限额情况制定监控和处理程序。超限额情况及时向相应级别的管理层报告。该级别管理层根据限额管理的政策和程序决定是否批准以及此超限额情况可以保持多长时间。对未经批准的超限额情况应当按照限额管理的政策和程序进行处理。管理层应当根据超限额发生情况决定是否对限额管理体系进行调整。（三）对市场风险有重大影响的情形制定应急处理方案，包括采取对冲、减少风险暴露等措施降低市场风险水平，以及建立针对自然灾害、银行系统故障和其他突发事件的应急处理或者备用系统、程序和措施，以减少银行可能发生的损失和银行声誉可能受到的损害。商业银行应当将压力测试的结果作为制定市场风险应急处理方案的重要依据，并定期对应急处理方案进行审查和测试，不断更新和完善应急处理方案。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第四章 市场风险管理流程', '第二十三条', '市场风险报告（一）本行定期、及时向董事会、高级管理层和其他管理人员提供市场风险报告。风险管理部根据监测数据分析评估全行市场风险，并与各部门沟通提出控制措施方案，在每周期开始（季度初、年初）提交市场风险报告。报告内容包括：1．按业务、部门和风险类别分别统计的市场风险头寸；2．按业务、部门和风险类别分别计量的市场风险水平；3．总体市场风险头寸和市场风险水平及其结构分析；4．盈亏情况；5．市场风险识别、计量、检测和控制方法及程序的变更情况；6．市场风险管理政策和程序的遵守情况；7．市场风险限额的遵守情况，包括对超限额情况的处理；8．事后检验和压力测试情况；9．内部和外部审计情况；10．市场风险经济资本分配情况；11.对改进市场风险管理政策、程序以及市场风险应急方案的建议；12．市场风险的控制措施；13．市场风险管理的其他情况。（二）市场风险报告提交路径及频度1．市场风险报告应依次提交分管副行长、行长审批，最后报送董事会审核批准通过风险控制措施方案。2．市场风险相关部门的风险管理员在正常市场条件下，需每月向部门经理报告市场风险管理情况，并且由该部门向风险管理部、分管副行长、行长报告；在市场剧烈波动的情况下，需要进行实时报告。(三)市场风险重大事项报告制度本行出现下列重大市场风险事项，各业务条 线应及时向本行董事会、高管层、风险管理部报告；并由风险管理部按照董事会要求统一向银监会报告。重大市场风险事项如下：（1）出现超过本行内部设定的市场风险限额的严重亏损；（2）国内、国际金融市场发生的引起市场较大波动的重大事件将对本行市场风险水平及其管理状况产生的影响；（3）交易业务中的违法行为；（4）其他重大意外情况。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第四章 市场风险管理流程', '第二十四条', '风险控制措施执行风险控制措施由风险管理部门负责监督落实风险控制措施的执行。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第四章 市场风险管理流程', '第二十五条', '资料归档市场风险评估报告、控制措施及控制结果反馈等相关资料由风险管理部最后进行分类、归档及保管。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第五章 内部控制和审计', '第二十六条', '按照银监会关于商业银行内部控制的有关要求，建立完善的市场风险管理内部控制体系，作为银行整体内部控制体系的有机组成部分。市场风险管理的内部控制有利于促进有效的业务运作，提供可靠的财务和监管报告，促使银行严格遵守相关法律、行政法规、部门规章 和内部的制度、程序，确保市场风险管理体系的有效运行。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第五章 内部控制和审计', '第二十七条', '为避免潜在的利益冲突，本行确保各职能部门具有明确的职责分工，以及相关职能适当分离。市场风险管理职能与业务经营职能应当保持相对独立；交易部门前台、后台严格分离，前台交易人员不得参与交易的正式确认、对账、重新估值、交易结算和款项收付；必要时可设置中台监控机制。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第五章 内部控制和审计', '第二十八条', '本行应避免其薪酬制度和激励机制与市场风险管理目标产生利益冲突。董事会和高级管理层应当避免薪酬制度具有鼓励过度冒险投资的负面效应，防止绩效考核过于注重短期投资收益表现，而不考虑长期投资风险。负责市场风险管理工作人员的薪酬不应当与直接投资收益挂钩。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第五章 内部控制和审计', '第二十九条', '本行的内部审计部门应当定期（至少每年一次）对市场风险管理体系各个组成部分和环节的准确、可靠、充分和有效性进行独立的审查和评价。内部审计应当既对业务经营部门，也对负责市场风险管理的部门进行。内部审计报告应当直接提交给董事会。董事会应当督促高级管理层对内部审计所发现的问题提出改进方案并采取改进措施。内部审计部门应当跟踪检查改进措施的实施情况，并向董事会提交有关报告。对市场风险管理体系的内部审计应当至少包括以下内容：（一）市场风险头寸和风险水平；（二）市场风险管理体系文档的完备性；（三）市场风险管理的组织结构，市场风险管理职能的独立性，市场风险管理人员的充足性、专业性和履职情况；（四）市场风险管理所涵盖的风险类别及其范围；（五）市场风险管理信息系统的完备性、可靠性，市场风险头寸数据的准确性、完整性，数据来源的一致性、时效性、可靠性和独立性；（六）市场风险管理系统所用参数和假设前提的合理性、稳定性；（七）市场风险计量方法的恰当性和计量结果的准确性；（八）对市场风险管理政策和程序的遵守情况；（九）市场风险限额管理的有效性；（十）事后检验和压力测试系统的有效性；（十一）市场风险资本的计算和内部配置情况；（十二）对重大超限额交易、未授权交易和账目不匹配情况的调查。本在引入对市场风险水平有重大影响的新产品和新业务、市场风险管理体系出现重大变动或者存在严重缺陷的情况下，应当扩大市场风险内部审计的范围和增加内部审计频率。本行的内部审计人员应当具备相关的专业知识和技能，并经过相应的培训，能够充分理解市场风险识别、计量、监测、控制的方法和程序。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第五章 内部控制和审计', '第三十条', '如本行内部审计力量不足时，应当委托社会中介机构对其市场风险的性质、水平及市场风险管理体系进行审计。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第六章  附则', '第三十一条', '本制度未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第六章  附则', '第三十二条', '本制度由××××银行股份有限公司负责解释和修订。'),
(NULL, '金融市场部', '《市场风险管理制度》', '第六章  附则', '第三十三条', '本制度自发文之日起施行。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第一章  总则', '第一条', '为规范××××银行股份有限公司(以下简称“本行”)代理销售他行理财产品行为，防范业务风险，保障代销业务健康发展，根据《××××银行股份有限公司理财业务代销管理办法(暂行)》,制定本操作流程。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第二章  代销业务的组织管理', '第二条', '本行金融市场部与其他银行联系，选择合作伙伴，拟定合作意向协议，本行分管行长审批，经本行行长办公会审议决策同意后。确定代理销售的理财产品品种、销售期限、销售规模等，与他行签订理财产品代销业务合作协议书。并负责组织人员进行理财产品代销业务培训。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第二章  代销业务的组织管理', '第三条', '本行发展规划部负责理财产品代销目标分解并确定销售进度，按计划、进度进行销售的跟进及考核工作。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第二章  代销业务的组织管理', '第四条', '本行运营管理部负责对本行理财产品代销业务的柜面操作进行管理。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第二章  代销业务的组织管理', '第五条', '营业网点业务人员按《理财业务代销管理办法（暂行）》中的规定向客户营销各类理财产品。其中，个人投资者需持本人有效身份证件至各销售网点办理，机构投资者需持营业执照副本复印件（需加盖公章）、机构代码证复印件（需加盖公章）、有法定代表人本人签名的身份证明文件复印件和法定代表人证明书（需加盖公章），如果经办人不是法定代表人本人，则经办人除须持上述文件外，还须提交有经办人签名的本人身份证明文件复印件和由法定代表人签名并加盖公章的授权委托书原件，签订相关理财协议，在专用资金账户留足申购资金。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第二章  代销业务的组织管理', '第六条', '在产品到期日前，金融市场部与产品行提供数据、平台导出数据核对确认金额及明细。在产品到期日次日，根据对账金额确认收到理财到期的本金和利息，系统进行自动入账，并向客户账户返回资金。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第二章  代销业务的组织管理', '第七条', '手续费结算。金融市场部负责与产品行手续费结算事宜，理财产品到期当天，产品行将代理理财的本金、收益及代销手续费一并转入本行指定账户。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第二章  代销业务的组织管理', '第八条', '营业网点明确专人做好理财代销业务相关档案资料的装订和保管。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第三章  产品的申购', '第九条', '客户通过柜面渠道购买理财产品。具体申购流程：1.客户风险承受能力评估。客户经理通过系统查询客户上次风险评估是否在有效期（一年）内。如评估结果仍有效，则进入联系方式的确认；如未评估或评估结果已过期，则需协助客户进行风险承受能力评估，客户勾选后，经业务人员核对填写正确性并计算评估结果后双方签字确认。2.签署产品说明书。业务人员向客户介绍产品，并对产品说明书所有条款内容进行详细解释、充分说明产品要素、充分提示风险后，客户如认可产品、拟定购买，则由客户对两份产品说明书进行签字确认。3.签订产品申购协议书。业务人员向客户说明产品协议书中有关内容和条款，协助客户填写准确的联系方式、勾选客户风险评估结果、要求客户抄录并确认内容等，业务人员审核完整、准确无误后，在产品协议书客户经理处签字确认。4.对风险评级符合要求并签署完协议的客户，业务人员在系统中进行申购操作。5.系统操作完成后，业务人员打印购买理财产品凭证，交客户审核签字确认，回执交付给投资客户。如客户已完成首次柜面的风险评估，可在本行手机银行客户端进行申购。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第三章  产品的申购', '第十条', '在理财产品销售过程中，各营业网点业务人员须严格按照《理财业务代销管理办法》中的相关规定执行，在双录的情况下指导客户完成风险评估、面签，申购等操作。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第三章  产品的申购', '第十一条', '业务人员需熟悉本行代销理财产品，包括：产品募集期限、产品期限、产品收益率、起点投资金额、投资方向、产品编号、产品额度及管理模式、内部风险评级、产品操作流程、凭证使用要求等。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第四章  代理理财产品业务的撤销', '第十二条', '代理理财业务撤销的处理流程。若客户在理财成立日前反悔，须携带本人有效身份证件、理财签约账户卡（折）、已签署的理财产品销售文件、交易申请回单到本行原购买网点由本行业务人员进行撤销交易处理。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第五章  客户投拆与应急处置', '第十三条', '本行明确办公室下属客户服务中心负责客户对本行代销理财产品业务的咨询和投拆事宜。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第五章  客户投拆与应急处置', '第十四条', '发生理财业务突发事件时，营业网点按照快速报告制度迅速向上级报告，并负责出面协调解决客户的投拆，做好客户的解释和服务工作，金融市场部第一时间向本行行长室汇报突发事件，应急领导小组迅速启动应急预案，避免事件的升级和扩散。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第六章  附则', '第十五条', '本操作流程由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《理财业务代销操作流程（暂行）》', '第六章  附则', '第十六条', '本操作流程自印发之日起执行。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）理财代理业务，保护客户合法权益，保证本行理财代理业务健康发展，根据《商业银行个人理财业务管理暂行办法》、《商业银行个人理财业务风险管理指引》、《商业银行理财产品销售管理办法》等法律、法规及本行的有关规定，特制定本办法。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第一章  总则', '第二条', '本办法所称“理财产品”是指征得银监部门同意公开发行的由他行推出，本行代理的理财产品，包括人民币理财产品和外币理财产品。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第一章  总则', '第三条', '本办法所称“代销”是指本行根据投资者的投资意愿，代理销售他行理财产品，并进行资金划转业务。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第一章  总则', '第四条', '本办法中“投资者”是指拥有合法资金、有投资意愿，并有一定风险承受能力的个人客户和机构投资者。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第一章  总则', '第五条', '本办法中“他行”（以下称合作机构）是指国务院金融监督管理机构依法实施监督管理、持有金融许可证的金融机构。本行与他行签订代理协议，以代理方式向投资者销售他行理财产品。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第一章  总则', '第六条', '本行与他行签订的代理协议应当约定双方的权利和义务，包括但不限于以下内容：1.合作机构提供代销产品和产品宣传资料的合规性承诺，国务院金融监督管理机构另有规定的除外。2.双方在风险承担、信息披露、风险揭示、客户信息传递及信息保密、后续服务安排、投诉和应急处理等方面的责任和义务。3.双方业务管理系统职责边界和运营服务接口。4.他行有义务配合开展对代销业务管理系统的接入、投产变更测试和应急演练等活动。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第一章  总则', '第七条', '本行将按照约定向他行收取代理手续费，收费标准及收费方式将根据相关政策规定与他行协商确定。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第八条', '本行金融市场部的主要职责为：1.与其他银行同业联系，选择合作伙伴，同其他金融机构签订合作协议书，选择确定代理销售的理财产品品种、销售期限、销售规模等。2.负责制定理财产品代销业务管理办法和业务操作规程。3.金融市场部明确专人做好代理账务的核对工作，确保代销结算资金的安全性和双方客户交易明细的一致性。按照代销协议约定，与委托行结算代销费用。4.组织本行代理销售他行理财产品经办人员的营销培训；指导、协调、监督、考核本行代理销售他行理财产品经营。5.负责组织市场调查，研究客户需求，向本行提出引进理财产品的需求，制定理财产品代销业务的营销方案、推广计划并组织实施。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第九条', '本行发展规划部负责组织全行代理理财业务的营销和推广。主要职责为:制定本行理财产品代销业务的发展规划、年度计划并组织实施；按照考核办法对营销人员进行考核。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十条', '本行运营管理部主要职责为：1.确定代销产品后，负责核心系统后台产品数据信息的维护。2.根据产品销售进度进行资金扣划。3.根据产品完成进度，与清算中心和本行客户进行对账，进行本金及分红的资金清算。4.负责对账过程中的差错处理沟通工作。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十一条', '本行计划财务部主要职责为：1.根据本行资金情况，做好代销理财产品资金头寸备付。2.根据代理理财产品交易量和交易额，做好日常流动性监测。3.做好费率和收入统计，为金融市场部提供代销理财业务财务评估数据。4.监督会计科目使用，确保核算合规。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十二条', '本行科技信息部主要职责为：1.负责对平台方交付的系统的测试及系统上线工作。2.与平台方进行技术沟通，及时处理相关技术故障，保障系统运行。3.负责每期代销理财产品核心系统业务数据与第三方平台数据的及时下载、导入。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十三条', '本行风险管理部主要职责为：1.负责对本业务所涉及到的文本、办法、流程以及具体业务操作进行风险评估，并提出风险控制意见。2.制定理财产品代销业务风险监控标准，提出风险控制整改意见。3.定期对理财产品代销业务进行风险评估，并建立风险报告制度。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十四条', '本行合规管理部主要职责为：1.负责对本业务相关协议、合同等法律文本的审核工作。2.负责对理财产品代销业务提供合法性审查建议及相关业务的合规性咨询。3.负责组织人员不定期对本行理财产品代销业务进行检查，内容包括（不限于）理财产品销售程序的合规性，理财人员销售行为的合法性检查。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十五条', '营业网点主要职责为：1.根据客户需要进行理财产品的推广营销工作。2.接受客户咨询、投诉及意见，及时向相关部门反馈沟通。3.根据规定指导客户进行理财产品购买，建立理财产品销售台账。4.进行销售数据统计，协助运营管理部做好对账工作，对理财产品的信息、金额等按照要求及时统计报送。5.负责代销理财产品会计档案的归档和保管工作。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十六条', '审计部对本行理财代销业务的运营情况进行内部监督检查和审计，定期向董事会和高级管理层提供独立评估报告。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第二章  组织管理', '第十七条', '办公室职责为：明确专人受理和处理客户投诉，根据法律、行政法规、国务院金融监督管理机构的相关规定和合同约定妥善处理投诉、突发事件和其他重大风险事件。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第三章  代销理财产品准入管理', '第十八条', '本行应当对拟代销理财产品开展尽职调查，不得仅以他行的产品审批资料作为产品审批依据。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第三章  代销理财产品准入管理', '第十九条', '本行应当根据代销产品的投资范围、投资资产、投资比例和风险状况等因素对代销产品进行风险评级。风险评级结果与他行不一致的，应当采用对应较高风险等级的评级结果。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第三章  代销理财产品准入管理', '第二十条', '本行不得代销未经他行确认合规或者未列入本行合作机构审批名单的机构发行的金融产品。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十一条', '本行应当按照国务院金融监督管理机构的规定确定代销业务的销售渠道。通过营业网点代销产品的，应当按照银监会有关规定在专门区域销售，销售专区应当具有明显标识。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十二条', '本行应当在营业网点或官方网站提供查询代销产品信息的渠道，建立代销产品分类目录，明示代销产品的代销属性、发行机构、合格投资者范围等信息，不得将代销产品与存款或其自身发行的理财产品混淆销售。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十三条', '本行应当使用他行提供的实物或电子形式的代销产品宣传资料和销售合同，全面、客观地揭示代销产品风险。国务院金融监督管理机构另有规定的除外。代销产品宣传资料首页显著位置应当标明合作机构名称，并配备以下文字声明：“本产品由××机构（合作机构）发行与管理，代销机构不承担产品的投资、兑付和风险管理责任”。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十四条', '本行应当对客户风险承受能力进行评估，确定客户风险承受能力评级，并只能向客户销售等于或低于其风险承受能力的代销产品。国务院金融监督管理机构另有规定的除外。风险承受能力评估依据应当包括但不限于客户年龄、财务状况、投资经验、投资目的、收益预期、风险偏好、流动性要求、风险认识和风险损失承受程度等。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十五条', '本行应当告知客户代销业务流程和收费标准，代销产品的发行机构、产品属性、主要风险和风险评级情况，以及本行与合作机构各自的责任和义务等信息。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十六条', '本行应当向客户提供并提示其阅读相关销售文件，包括风险提示文件，以请客户抄写风险提示等方式充分揭示代销产品的风险，销售文件应当由客户签字逐一确认，国务院金融监督管理机构另有规定的除外。通过电子渠道销售的，应由客户通过符合法律、行政法规要求的电子方式逐一确认。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十七条', '本行应当加强员工行为管理，对销售人员及其代销产品范围进行明确授权，并在营业网点公示。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十八条', '销售人员应当具备代销业务相关的法律法规、金融、财务等专业知识、技能和相应的岗位资格，遵守国务院金融监督管理机构、行业协会和商业银行制定的销售人员行为准则和职业道德标准，并充分了解所代销产品的属性和风险特征。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第二十九条', '本行应当会同他行为销售人员持续提供专业培训，确保销售人员每年的培训时间符合国务院金融监督管理机构或其授权机构的要求。代销新产品的，需开展销售前培训；未接受培训或未达到培训要求的销售人员不得销售该类产品。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第三十条', '本行通过营业网点开展代销业务的，应当根据国务院金融监督管理机构的相关规定实施录音录像，完整客观地记录营销推介、风险和关键信息提示、客户确认和反馈等重点销售环节。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第三十一条', '本行应当依法妥善保管与代销业务有关的各种文档（含录音录像文件），如实记载向客户推介、销售产品的情况。文档保存年限应当符合法律、行政法规和国务院金融监督管理机构的相关规定，并在银行内部管理制度中予以明确。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第三十二条', '本行从事代销业务，不得有以下情形：1.未经授权或超越授权范围开展代销业务，假借所属机构名义私自推介、销售未经审批的产品，或在营业区域内存放未经审批的非本行产品销售文件和资料。2.将代销产品作为存款或其自身发行的理财产品进行销售，或者采取夸大宣传、虚假宣传等方式误导客户购买产品。3.违背客户意愿将代销产品与其他产品进行捆绑销售。4.由销售人员违规代替客户签署代销业务相关文件，或者代替客户进行代销产品购买等操作、代替客户持有或安排他人代替客户持有代销产品。5.为代销产品提供直接或间接、显性或隐性担保，包括承诺本金或收益保障。6.给予合作机构及其工作人员，或者向合作机构及其工作人员收取、索要代销协议约定以外的利益。7.国务院金融监督管理机构禁止的其他情形。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第三十三条', '本行应当通过独立的团队或机构对营业网点的代销业务进行抽样回访。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第四章  代销理财产品销售管理', '第三十四条', '本行不得允许非本行人员在营业网点从事产品宣传推介、销售等活动。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第五章  信息披露与保密管理', '第三十五条', '代销产品存续期内，商业银行应当督促合作机构按照规定，及时、准确、完整地向客户披露代销产品投资运作情况、风险状况和对投资者权益或者投资收益有重大影响的风险事件等信息。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第五章  信息披露与保密管理', '第三十六条', '本行应当依法履行客户信息保密义务，防止客户信息被不当使用。与合作机构共享客户信息的，应当事先以醒目方式征得客户书面同意或者通过电子方式确认，并要求合作机构履行客户信息保密义务。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第六章  代销理财业务的监督管理', '第三十七条', '本行应当于每年度结束后２个月内向银监会报送代销业务年度报告，内容包括但不限于：代销业务发展规划和基本情况、主要风险分析和风险管理情况、合规管理和内部控制情况、投诉处理情况以及代销业务管理系统运行情况等。遇有突发情况的，应当及时报告。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第六章  代销理财业务的监督管理', '第三十八条', '本行违反本办法规定开展代销业务的，银监会应当责令其限期改正，并根据《中华人民共和国银行业监督管理法》等法律、行政法规及有关规定，采取相关监管措施或者依法实施行政处罚。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第六章  代销理财业务的监督管理', '第三十九条', '本行代销政府债券和实物贵金属，按照有关规定执行。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第七章  风险控制', '第四十条', '本行严格筛选他行的理财产品，要求口碑好，收益稳定，风险低。理财产品的名称应恰当反映产品属性，避免使用带有诱惑性、误导性和承诺性的称谓。理财产品应设计合理，风险揭示充分、清晰和准确。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第七章  风险控制', '第四十一条', '本行加强对经办人员的培训，严格管理。要求熟悉所销售理财产品的特征和风险情况，向客户充分提示理财产品的风险，明确“理财有风险，投资请慎重”。要求熟悉所办业务的具体流程。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第七章  风险控制', '第四十二条', '本行做好资金划收划付工作，严格按照有关财务规定办理此项业务。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第七章  风险控制', '第四十三条', '本行对代销业务实施绩效考核，不得仅考核销售业绩指标，考核标准应当包括但不限于销售行为和程序的合规性、客户投诉情况和内外部检查结果等。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第七章  风险控制', '第四十四条', '本行的股东、由本行直接或者间接控制的金融机构或者本行所在集团其他金融机构等关联方为代销业务合作机构的，本行对其在合作机构管理和代销产品准入等方面的要求应当不低于其他合作机构。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第七章  风险控制', '第四十五条', '本行应当会同合作机构建立代销业务客户投诉和应急处理机制，明确受理和处理客户投诉的途径、程序和方式，根据法律、行政法规、国务院金融监督管理机构的相关规定和合同约定妥善处理投诉、突发事件和其他重大风险事件。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第八章  法律责任', '第四十六条', '本行开展代理理财业务有下列情形之一的，银行业监督管理机构可依据相关规定对直接负责的董事、高级管理人员和其他直接责任人员进行处理，构成犯罪的，依法追究刑事责任：（一）违规开展代理理财业务造成银行或客户重大经济损失的；（二）未建立相关风险管理制度和管理体系，或虽建立了相关制度但未实际落实风险评估、监测与管控措施，造成银行重大损失的；（三）泄露或不当使用客户个人资料和交易信息记录造成严重后果的。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第八章  法律责任', '第四十七条', '本行开展代理理财业务有下列情形之一的，由银行业监督管理机构依据《中华人民共和国银行业监督管理法》的规定实施处罚：（一）违反规定销售未经批准的理财计划或产品的；（二）将一般储蓄存款产品作为理财计划销售并违反国家利率管理政策，进行变相高息揽储的；（三）提供虚假的成本收益分析报告或风险收益预测数据的；（四）未按规定进行风险揭示和信息披露的；（五）未按规定进行客户评估的。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第八章  法律责任', '第四十八条', '本行违反审慎经营规则开展代理理财业务，或利用代理理财业务进行不公平竞争的，银行业监督管理机构应依据有关法律法规责令其限期改正。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第八章  法律责任', '第四十九条', '本行开展代理理财业务有下列情形之一，并造成客户经济损失的，应按照有关法律规定或者合同的约定承担责任：（一）未保存有关客户评估记录和相关资料，不能证明理财计划或产品的销售是符合客户利益原则的；（二）未按客户指令进行操作，或者未保存相关证明文件的；（三）不具备理财业务人员资格的业务人员向客户提供理财顾问服务、销售理财计划或产品的。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第九章  附则', '第五十条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《理财业务代销管理办法（暂行）》', '第九章  附则', '第五十一条', '本办法自发文之日起开始施行。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第一条', '为规范××××银行股份有限公司（以下简称本行）电子商业汇票业务办理，促进电子商业汇票业务发展，根据《中华人民共和国票据法》、中国人民银行《电子商业汇票业务管理办法》等有关法律法规，特制定本办法。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第二条', '电子商业汇票是指出票人依托电子商业汇票系统，以数据电文形式制作的，委托付款人在指定日期无条件支付确定金额给收款人或者持票人的票据。电子商业汇票分为电子银行承兑汇票和电子商业承兑汇票。电子银行承兑汇票由银行业金融机构、财务公司（以下统称金融机构）承兑；电子商业承兑汇票由金融机构以外的法人或其他组织承兑。电子商业汇票的付款人为承兑人。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第三条', '电子商业汇票系统是经中国人民银行批准建立，依托网络和计算机技术，接收、存储、发送电子商业汇票数据电文，提供与电子商业汇票货币给付、资金清算行为相关服务的业务处理平台。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第四条', '电子商业汇票的出票、承兑、背书转让、贴现、转贴现、再贴现、提示付款和追索等业务必须通过电子商业汇票系统办理。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第五条', '电子商业汇票以人民币为计价单位，付款期限自出票日起至到期日止，最长不得超过1年。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第六条', '电子商业汇票的贴现、转贴现利率统一使用年利率（%）。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第七条', '电子商业汇票信息以中国人民银行电子商业汇票系统的记录为准。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第一章  总 则', '第八条', '办理电子商业汇票的各当事人应本着诚实信用原则，按照本办法的规定作出票据行为。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第九条', '金融市场部负责牵头制定电子商业汇票业务管理办法，负责电子商业汇票直贴、转贴现和再贴现等业务。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十条', '电子银行部负责企业客户端网银系统的运行管理。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十一条', '计划财务部负责电子商业汇票业务的资产负债管理、流动性管理，负责制订、发布全行电子商业汇票贴现业务的利率。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十二条', '风险管理部负责对电子商业汇票相关业务中承兑行和交易对手的名单备案。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十三条', '信贷管理部负责对电子商业汇票业务的放款审查、审批和贷后管理业务。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十四条', '授信管理部负责电子商业汇票业务进行授信和用信审批。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十五条', '运营管理部下属清算中心负责电子商业汇票的管理，托收，贴现业务的办理和资金清算。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十六条', '科技信息部负责票据业务系统以及相关系统的技术支持和运行维护。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第二章  职责与分工', '第十七条', '各营业网点负责客户办理电子商业汇票业务资料的初审以及业务经办的指导。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第三章  电子商业汇票业务申请', '第十八条', '电子商业汇票的业务申请条件及资料：（一）客户向本行申请办理电子商业汇票业务，应具备以下条件：1.依法从事经营活动的企业法人或其他经济组织；2.资信状况良好，无不良信用记录；3.在本行开立人民币结算账户，资金往来正常，信誉良好；4.开通本行对公网上银行；5.办理电子商业汇票业务项下的银行承兑汇票承兑、质押、（转）贴现应取得本行相关授信；6.本行要求的其他条件。（二）客户申请办理电子商业汇票业务，需提供以下资料：1、《电子商业汇票业务申请书》；2、《电子商业汇票业务服务协议》。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第三章  电子商业汇票业务申请', '第十九条', '对符合本办法第十八条规定的客户,经客户申请并通过网点负责人审批后由网点运营主管为其开通电子商业汇票系统客户操作端。各网点应认真审查客户资料，对客户基本信息的真实性负审核责任。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第三章  电子商业汇票业务申请', '第二十条', '客户开展电子商业汇票活动均需具有可靠的电子签名作为电子商业汇票业务办理的依据，其签名所依赖的电子签名制作数据和电子签名认证证书，应向指定的电子认证服务提供者的注册审批机构申请。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第三章  电子商业汇票业务申请', '第二十一条', '票据当事人在电子商业汇票上的数字签名，本行认为是该当事人的真实意图。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第三章  电子商业汇票业务申请', '第二十二条', '本行可以向省农村合作金融机构系统内或跨系统银行提交业务申请，同时实时受理人民银行电子商业汇票系统转发的省农村合作金融机构系统内或系统外银行提交的业务申请。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第三章  电子商业汇票业务申请', '第二十三条', '电子商业汇票责任解除前，电子商业汇票的承兑人不得撤销原办理电子商业汇票业务的账户，本行不得为其办理销户手续。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第二十四条', '电子商业汇票的出票，是指出票人签发电子商业汇票并交付收款人的票据行为。出票人在电子商业汇票交付收款人前，可办理票据的未用退回。出票人不得在提示付款期后将票据交付收款人。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第二十五条', '电子商业汇票的出票人必须为银行业金融机构以外的法人或其他组织。电子银行承兑汇票的出票人应在承兑行开立人民币结算账户。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第二十六条', '出票人可在电子商业汇票上记载自身的评级信息，并对记载信息的真实性负责，但该记载事项不具有票据上的效力。评级信息包括评级机构、信用等级和评级到期日。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第二十七条', '电子商业汇票的承兑，是指付款人承诺在票据到期日支付电子商业汇票金额的票据行为。电子商业汇票交付收款人前，应由付款人承兑。承兑人应在票据到期日前，承兑电子商业汇票。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第二十八条', '电子银行承兑汇票由真实交易关系或债权债务关系中的债务人签发，并交由各行社承兑。电子银行承兑汇票的出票人与收款人不得为同一人。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第二十九条', '电子商业承兑汇票的承兑行为属于企业间行为，承兑人需在本行开立人民币结算账户，其承兑形式有以下几种：（一）真实交易关系或债权债务关系中的债务人签发并承兑；（二）真实交易关系或债权债务关系中的债务人签发，交由第三人承兑;（三）第三人签发，交由真实交易关系或债权债务关系中的债务人承兑;（四）收款人签发，交由真实交易关系或债权债务关系中的债务人承兑。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十条', '承兑人承兑电子商业汇票，必须记载下列事项：（一）表明“承兑”的字样；（二）承兑日期；（三）承兑人签章。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十一条', '承兑人可在电子商业汇票上记载自身的评级信息，并对记载信息的真实性负责，但该记载事项不具有票据上的效力。评级信息包括评级机构、信用等级、评级到期日。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十二条', '出票人向本行申请电子商业汇票承兑，必须符合下列条件：（一）具有企业法人资格或经法人授权的独立核算的经济单位；（二）具有真实合法的交易背景或债权债务关系；（三）具有到期支付票款的能力；（四）本行要求的其他条件。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十三条', '转让背书是指持票人将电子商业汇票权利依法转让给他人的票据行为。票据在提示付款期后或票据上记载了“不得转让”事项的，不得进行转让背书。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十四条', '转让背书应当基于真实、合法的交易关系和债权债务关系，或以税收、继承、捐赠、股利分配等合法行为为基础。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十五条', '转让背书必须记载下列事项：（一）背书人名称；    （二）被背书人名称；（三）背书日期；      （四）背书人签章。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十六条', '贴现是指持票人在票据到期日前，将票据权利背书转让给金融机构，由其扣除一定利息后，将约定金额支付给持票人的票据行为。转贴现是指持有票据的金融机构在票据到期日前，将票据权利背书转让给其他金融机构，由其扣除一定利息后，将约定金额支付给持票人的票据行为。再贴现是指持有票据的金融机构在票据到期日前，将票据权利背书转让给中国人民银行，由其扣除一定利息后，将约定金额支付给持票人的票据行为。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十七条', '转贴现和再贴现按照交易方式，分为买断式和回购式。买断式是指贴出人将票据权利转让给贴入人，不约定日后赎回的交易方式。回购式是指贴出人将票据权利转让给贴入人，约定日后赎回的交易方式。电子商业汇票贴现、转贴现和再贴现业务中转让票据权利的票据当事人为贴出人，受让票据权利的票据当事人为贴入人。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十八条', '电子商业汇票当事人在办理回购式转贴现和回购式再贴现业务时，应明确赎回开放日、赎回截止日。赎回开放日是指办理回购式转贴现赎回和回购式再贴现赎回业务的起始日期。赎回截止日是指办理回购式转贴现赎回和回购式再贴现赎回业务的截止日期，该日期应早于票据到期日。自赎回开放日起至赎回截止日止，为赎回开放期。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第三十九条', '在赎回开放日前，原贴出人、原贴入人不得作出除追索行为外的其他票据行为。回购式转贴现和回购式再贴现业务的原贴出人、原贴入人应按照协议约定，在赎回开放期赎回票据。在赎回开放期未赎回票据的，原贴入人在赎回截止日后只可将票据背书给他人或行使票据权利，除票据关系以外的其他权利义务关系由双方协议约'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十条', '贴现申请人需与贴入行签订业务协议，协议项下的电子商业汇票票据信息和贴现申请信息均以贴现申请人在网银提交、人行电子商业汇票系统登记的信息为准，并且经办人员要在行内电子票据系统查询和打印出票人提交贴现的票据明细，作为贴现协议的组成部分。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十一条', '电子商业汇票贴现、转贴现和再贴现必须记载下列事项：（一）贴出人名称；（二）贴入人名称；（三）贴现、转贴现或再贴现日期；（四）贴现、转贴现或再贴现类型；（五）贴现、转贴现或再贴现利率； （六）实付金额；（七）贴出人签章。实付金额为贴入人实际支付给贴出人的金额。回购式转贴现和回购式再贴现还应记载赎回开放日和赎回截止日。贴现还应记载贴出人贴现资金入账信息。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十二条', '电子商业汇票回购式转贴现和回购式再贴现赎回应作成背书，并记载下列事项：（一）原贴出人名称；（二）原贴入人名称；（三）赎回日期；（四）赎回利率； （五）赎回金额；（六）原贴入人签章。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十三条', '贴现和转贴现利率、期限等由贴出人在与贴入人协商确定后提交申请时填写，贴入行不能修改，仅能接受或拒绝。再贴现利率由中国人民银行规定。计算公式：买断式利息=票据金额*天数*日利率买断式实付金额=票据金额-买断利息天数为票据买断日至票据止息日回购式利息=票据金额*天数*日利率回购式实付金额=票据金额-回购利息天数为票据回购买入日至赎回截止日赎回利息=票据金额*天数*日利率赎回实付金额=票据金额-赎回利息天数为赎回日至赎回截止日。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十四条', '电子商业汇票贴现、转贴现和再贴现可选择线上清算方式或线下清算方式清算资金。线上资金清算是指交易双方通过人行电子商业汇票系统完成业务应答后，人行根据双方成功应答情况，通过大额支付系统对交易双方进行实时资金清算的一种交易方式。线下资金清算是指交易双方通过人行电子商业汇票系统完成业务应答后，交易双方根据约定时间通过开户行完成行内或跨行资金清算的一种交易方式。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十五条', '电子商业汇票的持票人向本行申请办理贴现业务须满足以下条件：（一）办理电子商业汇票贴现业务应坚持“先授信，后贴现”的原则，在授信额度内放款；（二）贴现资金投向符合国家经济政策和本行信贷政策。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十六条', '电子商业汇票贴现业务的付息方式包括卖方付息、买方付息、买卖双方协议付息、他方付息等方式。本条所称卖方是商业汇票交易背景中的商品卖出方或劳务提供方，亦即商业汇票的持票人；买方是商业汇票交易背景中的商品买入方或劳务接受方，亦即商业汇票持票人在票据上的直接前手；他方是买方和卖方之外的第三方。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十七条', '电子商业汇票的持票人向本行申请办理转贴现业务须满足以下条件：本行与交易对手首次办理转贴现业务，需审核交易对手的营业执照、金融许可证、组织机构代码证、法定代表人或负责人身份证明、授权书等资料，核实其交易资格，留取相关资料备查（如系复印件需加盖公章），并签订业务协议，协议项下的电子商业汇票票据信息和转贴现申请信息均以转贴现业务发起人填写的、在人行电子商业汇票系统登记的转贴现申请信息为准，并构成协议的组成部分。每次办理业务须检查上述文件是否已过时效，交易对手名称或有权人是否发生变化，如失效或有变化的应要求交易对手提供新的证明材料。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十八条', '本行在办理回购式转入贴现（买入返售）时，根据交易对手的相关规定可以发起提前赎回申请和约定日赎回申请，交易对手提前赎回票据的，可以根据双方约定的赎回利率、剩余期间、票面金额计算退还对方利息，赎回利率由双方约定，计算期间为赎回日至赎回截止日，在赎回开放期结束交易对手未赎回票据，可按买断处理，但原交易双方的权利义务关系由协议约定。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第四十九条', '本行在办理回购式转出贴现（卖出回购）时，根据交易对手的相关规定可以受理提前赎回申请和约定日赎回申请，但不得受理赎回开放期后的赎回票据申请，提前赎回票据的，可要求交易对手根据约定的赎回利率、剩余期间、票面金额计算退还利息，赎回利率由双方约定，计算期间为赎回日至赎回截止日；在赎回开放期结束各行社未赎回票据的，应按卖断票据业务处理，但原交易双方的权利义务关系由协议约定。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十条', '本行向人民银行办理再贴现业务需根据人民银行相关规定办理。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十一条', '电子商业汇票的质押，是指电子商业汇票持票人为了给债权提供担保，在票据到期日前在人行电子商业汇票系统中进行登记，以该票据为债权人设立质权的票据行为。质权人可以是金融机构，也可以是企业。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十二条', '客户以电子商业汇票质押方式向本行申请融资业务的，融资额度比例遵照有权部门的审批结果，但不得超过质押票据的票面金额。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十三条', '本行作为质权人时，若主债务到期日先于票据到期日，质押申请人将主债务全部归还完毕后，根据质押申请人提交的解除质押申请按约定解除质押。若主债务到期日先于票据到期日，且客户到期未履行债务的，各行社可行使票据权利，但不得继续背书。若票据到期日先于主债务到期日的，本行可在票据到期后行使票据权利，并与质押申请人协议将兑现的票款以客户保证金方式继续为对应的债务担保或用于提前清偿所担保的债权。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十四条', '电子商业汇票质押，必须记载下列事项：（一）出质人名称；（二）质权人名称；（三）质押日期；（四）表明“质押”的字样；（五）出质人签章。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十五条', '电子商业汇票质押解除，必须记载下列事项：（一）表明“质押解除”的字样；（二）质押解除日期。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十六条', '提示付款是指持票人通过电子商业汇票系统向承兑人请求付款的行为。持票人应在提示付款期内向承兑人提示付款。提示付款期自票据到期日起10日，最后一日遇法定休假日、大额支付系统非营业日、电子商业汇票系统非营业日顺延。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十七条', '持票人在票据到期日前提示付款的，承兑人可付款或拒绝付款，或于到期日付款。承兑人拒绝付款或未予应答的，持票人可待票据到期后再次提示付款。持票人超过提示付款期提示付款的，在作出合理说明后，仍可向承兑人提示付款，本行不得拒绝受理，承兑人应在收到提示付款请求的当日至迟次日（遇法定休假日、大额支付系统非营业日、电子商业汇票系统非营业日顺延）付款或拒绝付款。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十八条', '电子银行承兑汇票付款：（一）对于由本行承兑的电子银行承兑汇票，持票人在票据到期日前向承兑行发起提示付款申请的，原则上承兑行应在票据到期日做应答，符合付款条件的，签收提示付款申请，解付票款，不符合付款条件的，拒绝提示付款申请，并注明拒付理由。（二）对于持票人在提示付款期内向承兑行发出提示付款申请，原则上承兑行应在收到提示付款申请当日做应答，符合付款条件的，签收提示付款申请，解付票款，不符合付款条件的，拒绝提示付款申请，并注明拒付理由。（三）若在解付票款时出票人未备足款项，由承兑行垫付款项的，应转入逾期贷款处理。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第五十九条', '电子商业承兑汇票解付：（一）对使用电子商业汇票系统的商业承兑汇票承兑人，本行应实时转发持票人的提示付款请求。（二）承兑人在票面到期日后收到提示付款请求的，且在收到提示付款请求次日起第3日（遇法定休假日、大额支付系统非营业日、电子商业汇票系统非营业日顺延）仍未应答的，本行根据与承兑人签订的《服务协议》条款的约定，进行如下处理：1.承兑人账户余额在该日电子商业汇票系统营业截止时足够支付票款的，则视同承兑人同意付款，应扣划承兑人账户资金支付票款，并在下一日（遇法定休假日、大额支付系统非营业日、电子商业汇票系统非营业日顺延）人行电子商业汇票系统营业开始时，代承兑人作出付款应答，并代理签章；2.承兑人账户余额在该日电子商业汇票系统营业截止时不足以支付票款的，则视同承兑人拒绝付款，应在下一日（遇法定休假日、大额支付系统非营业日、电子商业汇票系统非营业日顺延）人行电子商业汇票系统营业开始时，代承兑人作出拒付应答，并代理签章。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十条', '本行支持提示付款申请选择线上资金清算方式或线下资金清算方式，但若持票人的提示付款账户和承兑人账户同属于本系统的，为线下清算方式。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十一条', '本行向省农村合作金融机构系统内发起提示付款申请的，应在票据到期日当天由系统自动向票据承兑人发起提示付款申请，也可以由操作员在票据到期前手工发起提示付款申请。原则上要求在票据到期日或之前应向票据承兑人发起提示付款申请，提示付款期最后一日遇法定节假日、大额支付系统非营业日、电子商业汇票系统非营业日顺延。若在票据到期日前提示付款的，若遇承兑人拒绝付款或未予应答的，应待票据到期后再次提示付款。若遇承兑人拒绝提示付款申请的，本行在收到拒付通知后应按照具体问题具体对待的原则，及时与承兑人联系，提出解决方案报有权部门或有权人批准后执行。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十二条', '电子商业汇票提示付款，必须记载下列事项：（一）提示付款日期；（二）提示付款人签章。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十三条', '承兑人付款或拒绝付款，必须记载下列事项：（一）承兑人名称；（二）付款日期或拒绝付款日期；（三）承兑人签章。承兑人拒绝付款的，还应注明拒绝付款的理由。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十四条', '追索分为拒付追索和非拒付追索。　拒付追索是指电子商业汇票到期后被拒绝付款，持票人请求前手付款的行为。非拒付追索是指存在下列情形之一，持票人请求前手付款的行为：（一）承兑人被依法宣告破产的；（二）承兑人因违法被责令终止业务活动的。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十五条', '本行在提示付款期内发起提示付款、逾期提示付款申请被拒绝付款经与承兑人联系沟通无法及时收回票款的，应向票据相关前手发起追索通知。若需要发起追索通知的，应及时向相关前手发起，不得延误，以免造成不必要的权利损失。发送追索通知时可以根据实际情况选择向任一前手、多个前手及所有前手发送。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十六条', '发送追索通知时，应提供拒付证明。拒付追索时，拒付证明为票据信息和拒付理由。非拒付追索时，拒付证明为票据信息和相关法律文件。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十七条', '经沟通联系，票据前手发起同意清偿申请的，本行可接受清偿申请。若收到多个票据前手的同意清偿申请，应根据实际情况，选择其中一个票据前手接受其清偿申请。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第四章  业务规定', '第六十八条', '本行收到票据持有人发起追索通知的，应及时与票据持有人、票据直接前手、票据承兑人及其他相关前手联系，确保在清偿后可以及时向相关前手收回票款。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第五章  信息查询', '第六十九条', '本行客户可通过农村商业银行网上银行查询与其相关的电子商业汇票票据信息。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第五章  信息查询', '第七十条', '本行应记录其与电子商业汇票系统之间发送和接收的电子商业汇票票据信息，并按查询人的票据权利将相关票据信息向客户展示。票据信息包括票面信息和行为信息。票面信息是指出票人将票据交付收款人后、其他行为发生前，记载在票据上的所有信息。行为信息是指票据行为的必须记载事项。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第五章  信息查询', '第七十一条', '出票人可查询电子商业汇票票面信息。承兑人在收到提示付款申请前，可查询电子商业汇票票面信息，收到提示付款申请后，可查询该票据的所有票据信息。收款人、被背书人和保证人可查询自身作出的行为信息及之前的票据信息。持票人可查询所有票据信息。在追索阶段，被追索人可查询所有票据信息。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第五章  信息查询', '第七十二条', '票据当事人对票据信息有异议的，本行应以书面形式上报省联社，由省联社向人民银行合肥中心支行提出书面申请查询。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第六章  风险管理', '第七十三条', '协议风险本行在开办业务时，对签署的服务协议及附件等内容要严格审核，防止相关内容被篡改，造成商业纠纷等风险出现。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第六章  风险管理', '第七十四条', '系统风险省联社科技部门及本行科技部门负责本系统电子商业汇票系统安全和稳定，保证系统安全稳定运行。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第六章  风险管理', '第七十五条', '法律责任（一）电子商业汇票发生法律纠纷时，以电子商业汇票系统运营者出具的电子商业汇票系统相关记录作为解决依据；（二）承兑人故意压票、拖延支付，影响持票人资金使用的，按人行规定的同档次流动资金贷款利率计付赔偿金；（三）电子银行承兑汇票的出票人于票据到期日未能足额交存票款时，承兑人除向持票人无条件付款外，对出票人尚未支付的汇票金额转入逾期贷款处理，并按照每天万分之五计收罚息。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第六章  风险管理', '第七十六条', '电子商业汇票当事人应当妥善保管电子签名制作数据，严防泄露。因保管不善造成资金损失的，有关责任方应当依法承担赔偿责任。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第六章  附则', '第七十七条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《电子商业汇票业务管理办法》', '第六章  附则', '第七十八条', '本办法自印发之日起施行。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第一章  总则', '第一条', '为积极开展××××银行股份有限公司（以下简称“本行”）电子商业汇票贴现业务，有效防范票据风险，促进电子商业汇票业务发展，依据《中华人民共和国票据法》、《商业汇票承兑、贴现与再贴现管理暂行办法》、《电子商业汇票业务管理办法》、《关于规范和促进电子商业汇票业务发展的通知》及有关法律制度，结合本行实际，特制定本办法。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第一章  总则', '第二条', '电子商业汇票系统，是指经中国人民银行批准建立，依托网络和计算机技术，接收、存储、发送电子商业汇票数据电文，提供与电子商业汇票货币给付、资金清算行为等相关服务的业务处理平台。本行采用与上线中国人民银行电子商业汇票系统（简称“人行ECDS系统”）的接入行签订《代理电子商业汇票业务服务协议》的方式，间接接入人行ECDS系统。本办法所称电子商业汇票贴现业务是指以人行ECDS系统为核心，提供电子商业汇票的贴现和转贴现等业务。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第一章  总则', '第三条', '电子商业汇票贴现业务适用于对公客户、财务公司、其他银行对电子商业汇票业务的处理需求。办理电子商业汇票贴现业务要以真实、合法的交易关系或债权债务关系为基础，遵循安全性、流动性、效益性原则。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第一章  总则', '第四条', '贴现是指持票人在票据到期日前，将票据权利背书转让给金融机构，由金融机构扣除一定利息后，将约定金额支付给持票人的票据行为。转贴现是指持有票据的金融机构在票据到期日前，将票据权利背书转让给其他金融机构，由其扣除一定利息后，将约定金额支付给持票人的票据行为。电子商业汇票的付款期限自出票日起至到期日止，最长不得超过1年。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第一章  总则', '第五条', '电子商业汇票分为电子银行承兑汇票和电子商业承兑汇票。电子银行承兑汇票由银行业金融机构、财务公司（以下统称金融机构）承兑；电子商业承兑汇票由金融机构以外的法人或其他组织承兑。电子商业承兑汇票按照本行贷款授信程序办理。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第一章  总则', '第六条', '电子商业汇票贴现和转贴现业务中转让票据权利的票据当事人为贴出人，受让票据权利的票据当事人为贴入人。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第二章  部门职责', '第七条', '金融市场部主要职责包括：   （一）负责制订电子商业汇票贴现与再贴现业务的相关管理制度和流程；（二）部门内部设置专门ECDS系统的业务操作岗、复核岗和管理岗；（三）负责电子商业汇票转贴业务客户的市场营销工作；（四）负责根据市场利率确定贴现利率。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第二章  部门职责', '第八条', '其它部门职责：（一）经办行（部）职责：1.根据金融市场部提供的利率定价，组织营销；2.负责对客户提供资料的真实性、完整性、合法性、合规性进行初审；3.负责与客户面签相关协议或合同。（二）授信评审部对经办行（部）提交资料进行审查，对符合贴现要求的，交有权审批人审批，确定授信额度。（三）运营管理部1．指定专门人员复核并审验电子商业汇票贴现业务在人行ECDS系统中操作的准确性；2．处理与电子商业汇票贴现业务交易操作有关的账务、差错和日常资金清算业务处理。（四）计划财务部负责电子商业汇票贴现业务的会计科目核算处理。（五）合规管理部1．审定有关电子商业汇票贴现业务服务协议、合同文本及相关制度的合法、合规性； 2．给予该项业务在开办过程中需要的相关法律支持。（六）科技信息部负责电子商业汇票综合管理系统的运行维护和技术支持，保障系统的平稳运行，并根据业务和管理部门要求，积极拓展系统功能。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第三章  流程管理', '第九条', '申请电子商业汇票贴现的持票人必须具备下列条件：（一）已在市场监督管理部门登记注册，依法从事经营活动的企业法人和其它经济组织；（二）贴现申请人须在本行开立存款账户并开通企业网银。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第三章  流程管理', '第十条', '电子商业汇票贴现业务操作流程:（一）持票人向经办行（部）提出业务申请，并提交下列资料：1．《银行承兑汇票贴现申请、审批表》；2．《银行承兑汇票贴现协议》；3． 初次申请电子商业汇票贴现业务时，要提供经年检的《法人营业执照》复印件、《开户许可证》复印件及法人代表身份证复印件等基础资料，并加盖申请人印章。（二）经办行（部）接到客户的贴现申请后，要对客户提供资料的真实性、合规性进行调查核实，经核对无误后，经办人应签名以示负责，同时，经办行（部）要在金融市场部公布利率基础上与客户协商确定贴现利率。（三）授信评审部对经办行（部）提交的贴现业务进行授信审核，对符合贴现要求的，交有权审批人审批，确定授信额度。（四）信贷管理部对上报的贴现用信业务资料审核无误后，交有权审批人审批。（五）金融市场部对客户经理提交的电子商业汇票贴现资料的完整性进行审查。（六）清算中心进行账务处理，审核金融市场部在人行ECDS系统中的业务操作，核对贴现利率、贴现金额等票据要素，核对无误后将贴现款划至贴现申请人存款账户上。（七）到期处理 1．提示付款是指持票人通过电子商业汇票系统向承兑人请求付款的行为。清算中心应在提示付款期内向承兑人提示付款。提示付款期自票据到期日起10日，最后一日遇法定休假日、大额支付系统非营业日、电子商业汇票系统非营业日顺延。本行发出提示付款请求时，可在人行ECDS系统中对发出托收的电子商业汇票托收和回款情况进行查询。2．票据到期如未收到款项且非本行责任的，清算中心直接从贴现申请人存款账户中扣划。申请贴现的持票人存款账户存款余额不足的，应先将存款划完，不足部分按逾期贷款的规定处理。同时应立即凭承兑银行退回的拒绝付款理由书或付款人未付票款通知书行使追索权。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第三章  流程管理', '第十一条', '电子商业汇票转贴现业务指人行ECDS系统内转贴现业务，交易双方为本行与全国范围内加入中国人民银行电子商业汇票系统内的各金融经营机构。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第三章  流程管理', '第十二条', '电子商业汇票转贴现业务流程：（一）金融市场部负责办理本行的电子商业汇票转贴现业务，根据市场利率水平确定电子商业汇票的转贴现利率，并交有权部门审查、审批，具体审批流程参照本行《商业汇票转贴现管理办法》执行；（二）运营管理部下属清算中心负责电子商业汇票转贴现业务资金的复核、清算及电子商业汇票转贴现业务审批资料保管工作。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第三章  流程管理', '第十三条', '转贴现买入／卖出交易均在人行ECDS系统进行业务操作。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第三章  流程管理', '第十四条', '电子商业汇票转贴现的业务品种包括但不限于：买断式转贴现、回购式转贴现，转贴现的会计账务处理与纸质商业汇票一致。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第四章  系统操作管理', '第十五条', '岗位设置和人员要求金融市场部设置贴现业务操作岗，复核岗，本行科技信息部设置系统管理岗。岗位需遵循本行《电子商业汇票业务管理办法》及中国人民银行商业汇票交易管理系统的设置和管理要求设定，不得相互兼任。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第四章  系统操作管理', '第十六条', '系统操作权限（一）系统管理员（经办/复核）1．负责业务经办人员的增加、密码重置、状态维护和角色分配；2．负责系统的机构维护、角色设置和审核路线设置。（二）操作人员电子票据子系统业务经办人员由金融市场部部门人员担任；业务复核人员由运营管理部下属清算中心指定人员担任；各岗位相互制约。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第五章  检查和监督', '第十七条', '办理电子商业汇票贴现业务的金融市场部应建立贴现台账，并与会计账务核对一致。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第五章  检查和监督', '第十八条', '经办行（部）应定期或不定期对贴现申请人的资金流向、到期支付汇票能力及等情况进行一次跟踪检查。主要内容包括：是否按规定用途使用资金，汇票项下的商品交易是否按约履行，有无套取银行资金等行为。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第五章  检查和监督', '第十九条', '对于电子商业汇票到期未获偿付的垫款部分，视同流动资金不良贷款进行管理，经办行加强催收工作。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第五章  检查和监督', '第二十条', '清算中心在办理票据（含纸票）贴现、转贴现等业务时，通过查询电票系统以及中国法院网、法院公告网、人民法院报网站等方式及时掌握票据是否被挂失止付或公示催告等信息，严格执行支付系统查询查复有关规定，全面如实的向查询行回复票据冻结、司法涉诉等信息，切实防范风险。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第六章  责任与处罚', '第二十一条', '对贴现申请人弄虚作假，提供假合同、假发票套取银行承兑的，应立即采取有效措施，强行扣划保证金或存款，并对不遵守信用的企业，立即停止办理新的电子商业汇票贴现业务，并采取必要的信贷制裁措施，经办行客户经理在初审时应发现而未发现的上述情形的，追究经办行相关人员的责任。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第六章  责任与处罚', '第二十二条', '对贴现申请人无款支付转入垫款科目核算的垫付款项，按照有关贷款责任制管理规定进行处理。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第六章  责任与处罚', '第二十三条', '业务人员在办理电子商业汇票业务中，违反相关规定的，将按照《员工违规行为处理办法》追究相应责任。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第七章  附则', '第二十四条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《电子商业汇票贴现业务管理办法》', '第七章  附则', '第二十五条', '本办法自发文之日起施行。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第一章  总  则', '第一条', '为预防和妥善处置××××银行股份有限公司（以下简称“本行”）因利率、汇率、股票、商品等价格变化而引发的突发市场风险事件，保护本行股东和广大存款人的合法权益，确保本行各项业务的稳健运行，根据银监会《银行业突发事件应急预案》、《商业银行市场风险管理指引》和有关法律、法规，特制定本应急预案。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第一章  总  则', '第二条', '本预案所指的市场风险又称或，它是指未来市场价格(利率、汇率、)的不确定性，给本行带来经济损失的可能性。市场风险可以分为利率风险、、股票价格风险和商品价格风险，这些市场因素可能直接对本行产生影响，也可能是通过对交易对手或者客户间接对本行产生影响。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第二章  组织管理体系', '第三条', '本行成立突发市场风险事件应急预案处置领导小组（以下简称“应急预案处置领导小组”）。由董事长任组长，行长任副组长,成员由总行副行长及各部门负责人组成。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第二章  组织管理体系', '第四条', '应急预案处置领导小组下设办公室，作为日常办事机构，办公室设在计划财务部，负责全行突发市场风险事件的组织、协调和处置工作。其主要职责为：制订本行市场风险事件应急预案；遇突发市场风险事件发生时，启动应急预案，及时处置和化解突发流动性风险，确保突发事件期间和风险处置后业务经营的正常运行。同时，负责及时向银监部门、人民银行、省联社和地方政府等上级部门报告。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第三章  突发市场风险事件的预防和预警', '第五条', '各分支机构和机关各部门应密切关注市场的反应和动态，及时传递信息，最大限度地杜绝决策过程中的信息盲区。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第三章  突发市场风险事件的预防和预警', '第六条', '计划财务部牵头，各业务部门配合，加强全行资产负债比例管理，重视资产、负债的期限匹配和数量匹配，合理安排贷款投放规模与节奏,保持适当的存贷比。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第三章  突发市场风险事件的预防和预警', '第七条', '计划财务部应综合衡量流动性、安全性和效益性之间的关系，对央行可能采取的紧缩流动性调控措施提前做好充分准备；坚持每日对资金头寸进行监测，防止头寸不足，保证一定的超额备付余额。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第三章  突发市场风险事件的预防和预警', '第八条', '风险管理部门应逐步建立和完善流动性风险监测技术，努力运用情景分析等方法，提高应对多种不利情况下流动性问题的能力，做到防患于未然。流动性比例必须达到 25%以上。    第九条 风险管理部门对照流动性风险预警指标，至少每年进行一次风险的定期或不定期监测和评估，建立报告制度。若发生异常情况，必须及时报告，以便及时启动应急预案。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第四章  突发流动性风险事件的应急处置', '第十条', '发生突发市场性风险事件应坚持依法、快速、高效、稳妥、保密的处置原则。（一）依法原则：即在处置突发市场性风险事件过程中，要注意掌握政策、依法办事，不得采取非法、违规手段使问题复杂化。（二）快速原则：即发现问题，立即启动预案及时处置，制止事态进一步发展，将风险损失降低到最小程度。（三）高效原则：即处置突发市场性风险事件，应按照统一指挥、措施得力，统筹协调、部门联动的原则组织实施，相关部门应按照自身的权限和职责，各司其职，服从指挥。（四）稳妥原则：即处置突发市场性风险事件，必须积极主动，快速反应，冷静、有序、果断，有效控制局势，做到指挥统一、宣传解释统一、行动步骤统一、不失控、不失序、不失真。（五）保密原则：即应急处置领导小组各成员要遵守职业道德和组织纪律，保守国家秘密，对于涉及到机密(含)以上的事项，应严格遵守保密法规，不得泄漏。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第四章  突发流动性风险事件的应急处置', '第十一条', '突发市场风险的应急预案处置措施发生突发风险事件后，应急预案处置领导小组应立即启动资金救助应急预案。资金救助原则上遵循自我救助、行业救助、同业救助、人民银行救助等顺序，但情况紧急时，也可从快从速，多法并举，快速、稳妥地处置各种流动性风险。（一）自我救助：主要措施有大力组织存款、调整资产组合、控制贷款投放、提前收回贷款、卖出持有债券、变卖固定资产、要求股东增补股本金等。（二）行业救助：通过省联社系统内资金调剂，并与省联社订立协议，取得流动性互助借款。（三）同业救助：通过银行间市场融资、全国银行间同业拆借市场和债券市场交易系统等方式，及时融入资金，包括同业存款、同业拆借、债券回购、票据回购等。（四）人民银行救助：向人民银行申请短期再贷款、再贴现，订立《常备借贷便利》，提出救助申请。在采取一切必要的手段后，仍存在严重支付困难但又不符合再贷款条件的，可以申请动用法定存款准备金。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第四章  突发流动性风险事件的应急处置', '第十二条', '突发事件发生后，应急预案处置领导小组应在事件发生的第一时间内将相关情况向人民银行、银监分局、省联社及当地政府报告，并通报公安机关。报告的内容包括：事件发生的时间、地点；事件发生的原因、性质、等级、可能涉及的金额和人数、危害程度、影响范围以及事件发生后的社会稳定情况；事态发展趋势、可能造成的损失、已经及拟进一步采取的措施；其他与本事件有关的内容。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第五章  突发市场风险事件的后期处置', '第十三条', '突发流动性风险事件应急处置完毕后，应在各方帮助下，尽快组织恢复正常的营业秩序，协调新闻单位做好正面宣传报道工作，把握正确的舆论宣传导向，最大程度地降低突发市场风险事件带来的不良影响。同时，要密切关注流动性情况，严防突发风险事件出现反复。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第五章  突发市场风险事件的后期处置', '第十四条', '突发风险事件处置完毕后，要及时对事件的原因及全过程进行彻底调查，并追究相关人员的责任。有下列行为之一的，视情节轻重，给予相关责任人员相应的行政或纪律处分：（一）故意隐瞒风险情况造成风险蔓延扩大的；（二）风险事件发生后应急处置不力的；（三）发生风险事件后未及时向有关部门报告的；（四）未经批准向外界泄漏风险情况酿成不良后果的。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第六章  附	  则', '第十五条', '本预案由××××银行股份有限公司负责解释和修订。'),
(NULL, '金融市场部', '《重大市场风险应急处理预案》', '第六章  附	  则', '第十六条', '本预案自印发之日起执行。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第一章　总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）银行承兑汇票管理，促进银行承兑汇票业务健康稳步开展，防范操作风险，根据《中华人民共和国票据法》、《商业汇票承兑、贴现与再贴现管理暂行办法》、《支付结算办法》、《贷款通则》等有关法律、法规及规定，结合本行实际，特制定本办法。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第一章　总则', '第二条', '本办法所称银行承兑汇票（以下简称“银票”），是指符合中国人民银行票据管理规定由承兑申请人签发的，经本行审查同意承兑，并由本行承诺到期无条件支付汇票金额的商业汇票。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第一章　总则', '第三条', '本办法所称承兑，是由本行承诺在汇票到期日对收款人或持票人无条件支付汇票金额的票据行为。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第一章　总则', '第四条', '本办法所称银票敞口，是本行在为客户办理银票时，根据对客户的授信额度，对客户缴存保证金以外部分的承兑金额提供担保，敞口金额实行总额控制。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第五条', '各支行、公司、小微部（以下简称“经办行”）的客户经理负责银票业务的拓展；客户资信的调查；初审申请人提交的申请资料，并根据授信通知书与客户签订银票承兑协议、最高额保证、抵质押合同等合同文本（签发敞口银票适用）；落实并督促客户按规定足额缴存保证金；负责敞口银票资金的到期收回；建立银票签发台账。对发生垫付款项的，积极采取措施，做好催收工作，对经催收和追偿仍不能支付票款的承兑申请人及保证人依法提起诉讼。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第六条', '授信评审部接受客户经理提交的敞口银票的授信申请，并在本行制定的转授权管理办法权限范围内进行授信审核、审批。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第七条', '信贷管理部接受客户经理提交的敞口银票的用信申请，并在本行制定的转授权管理办法权限范围内进行用信审核、审批。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第八条', '金融市场部负责银票业务管理办法的拟订；审查经办行提交的办理银票业务的相关手续和资料；按月做好全辖银票业务的统计、分析和上报工作。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第九条', '清算中心负责银票重要空白凭证、汇票专用章的保管，严格执行印、证分管制度，正确办理承兑及其会计核算，及时处理各类查询、查复工作，妥善做好到期银票的解付工作，将兑付和垫款情况及时通知经办行及金融市场部。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第十条', '运营管理部负责拟订银票会计核算手续，加强对重要空白凭证、印章保管及银票保证金的管理。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第十一条', '计划财务部负责敞口银票敞口部分手续费收取标准的制定及优惠费率的审查、审核。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第十二条', '合规管理部负责审核银票业务管理办法、操作流程的合规性、有效性。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第十三条', '风险管理部负责对银票敞口部分的风险定期识别、计量、监测，做出独立评估并提交风险审查报告'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第二章　组织管理体系', '第十四条', '审计部负责定期或不定期开展银票业务的检查工作。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第三章　银票申请对象及条件', '第十五条', '银票申请对象：申请对象应当是经工商行政管理机关（或主管机关）核准登记的企（事）业法人、其他经济组织。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第三章　银票申请对象及条件', '第十六条', '银票申请条件：1、在本行开立存款账户；2、资信状况良好，具有支付汇票金额的可靠资金来源；3、有真实、合法的商品交易关系；4、近两年在本行无不良贷款、欠息及其他不良信用记录；5、持有人民银行核发且年检有效的贷款卡；6、本行要求提供的其他资料。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第三章　银票申请对象及条件', '第十七条', '敞口银票办理要求：办理银票业务，应按照客户资信和信用等级收取保证金，对银票票面金额剔除保证金金额部分形成的敞口风险部分，必须有足额有效的担保（签订担保合同）。敞口银票的签发必须凭总行批准的敞口授信通知书，方可办理。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第四章　保证金与担保', '第十八条', '根据客户信用等级、对本行的综合贡献度，合理确定承兑金额的保证金比例。敞口银票敞口部分的相关担保措施必须符合《担保法》的规定。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第四章　保证金与担保', '第十九条', '银票保证金由承兑申请人缴存各支行的保证金专用核算账户。经办行客户经理应对保证金资金来源合法性进行审核、保证银票保证金专项用于到期银票的资金承兑，不得挪作他用。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第五章　审查、审批和承兑', '第二十条', '经办行客户经理负责受理客户银票业务申请，对承兑申请人的有关情况进行审查，对提供的相关资料进行初审。经审查同意后，经办行将《银票申请审批书》、《银行承兑协议》、申请人提交的材料以及审查报告等按转授权管理办法上报总行审批。经审批同意后，经办行根据审批意见与承兑申请人及保证人签订《银行承兑协议》，并打印承兑放款通知书交金融市场部审查。签发敞口银票经办行需签订相关的担保合同，对需要办理抵质押登记的，应及时办妥有关手续。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第五章　审查、审批和承兑', '第二十一条', '清算中心收到金融市场部移交的“承兑放款通知书”复核无误后向承兑申请人收取承兑手续费及银票敞口手续费，按照《支付结算办法》规定办理有关承兑手续。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十二条', '受理申请。承兑申请人向开户网点提交书面申请及相关资料：1、银票承兑申请审批表。2、营业执照、税务登记证明、贷款卡（已年检）等基础信贷资料（年度首次办理）。3、上年度和最近一期的资产负债表、损益表、现金流量表等财务报表（无法提供财务报表的小企业除外）。成立不到一年的企业可提供自成立以来的财务资料。4、交易合同、协议等能够证明交易背景资料的原件及复印件。5、按规定需要提供担保的，还应按照贷款担保管理的要求提供相关资料。6、申请银票敞口部分优惠费率的需根据相关制度办法要求提供相关资料。7、本行要求提供的其他资料。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十三条', '调查，网点客户经理接受申请人提供资料后开展调查，内容包括：1、承兑申请事项是否建立在真实合法的商品、劳务交易基础上。2、审查出票人对到期银票的偿债能力。3、调查担保落实情况，调查敞口部分担保措施是否足额、有效。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十四条', '授信，本行授信评审部根据网点提交资料进行授信审批。银票敞口业务授信必须经企业申报、经办行初审、授信评审部在本行转授权管理办法权限范围内对网点上报敞口业务进行授信审核、审批，超授权的由授信评审部提交本行贷款审查委员会审议决策通过。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十五条', '签订协议，申请人拟申请签发银票，网点客户经理审核资料准确无误后与客户进行协议的签订。1、经办行根据经审查的申请资料与承兑申请人签订《银行承兑协议》，加盖承兑申请人公章、法人代表印章，如申请敞口银票还须加盖保证单位公章、法人代表印章；并由经办行填写《××××银行股份有限公司银票申请审批表》，加盖申请单位业务公章，签署调查人、审查人、及支行行长意见并签名。对需要办理抵押登记、质押手续的，及时办妥有关手续。2、经办行根据审查合格的申请资料，由客户经理登录信贷系统进行承兑汇票录入，并严格区分自签或他行代签在系统中选择性录入。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十六条', '用信审请及审批。银票敞口业务由经办行提出用信申请，信贷管理部在本行转授权管理办法权限范围内进行用信审核、审批。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十七条', '优惠费率。银票敞口部分优惠费率必须经企业申请、经办行提交、计划财务部根据相关规定进行审查、审核。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十八条', '审查。金融市场部对承兑申请人提交的《银行承兑协议》、《××××银行股份有限公司银票申请审批表》、承诺函、及商品或劳务交易合同，经审核无误后留存申请人提交的书面资料，并在网点出具的承兑放款通知书上签字确认，交由清算中心据以办理。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第二十九条', '承兑。清算中心收到金融市场部移交的“承兑放款通知书”，经审核无误后，按照《支付结算办法》规定办理承兑手续，并向承兑申请人收取承兑金额万分之五的手续费及敞口手续费，业务处理完毕保管银票第一联。清算中心办理承兑应严格实行印、证分管原则，严禁在缺员情况下操作。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第三十条', '承兑后管理。1、银票签发后，承兑申请人开户行（部）应实行管户客户经理责任制，加强跟踪检查，主要检查可能影响客户到期兑付能力的事项：（1）是否按申请用途使用银票；（2）生产经营和财务状况是否正常；（3）到期前是否按承兑协议足额准备兑付款项；（4）资金流向和销货款是否正常；（5）保证人保证资格和保证能力、抵质押物权属和价值等有无重大变化；（6）其他可能影响承兑申请人到期兑付能力的事项。2、敞口银票到期前10天，客户经理通知承兑申请人提前预留款项，并督促承兑申请人及担保人在银票到期前1天交付足额票款。3、银票到期，清算中心收到持票人开户行的委托收款凭证和银票，经审核无误后，无条件向持票人付款。4、垫付银票资金列入“银票垫款”科目核算，按照有关规定计收逾期贷款利息。同时，承兑申请人开户网点就垫付款项向承兑申请人进行催收，及时处理抵质押物或要求保证人履行担保义务，保全信贷资产。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第六章  操作流程', '第三十一条', '退票。已经签发的银票，因故需要撤销的，应先由客户向开户网点提出书面申请，客户经理对情况调查核实后在申请上签署意见，经办行负责人审批同意并加盖单位业务印章后，必须将申请送交金融市场部审查同意，签字确认后，方可将申请及银票提交清算中心，清算中心审核无误后办理退票，将资金及利息及时退回。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第七章　检查及到、逾期处理', '第三十二条', '银票签发后，经办行应加强跟踪检查，实行管户客户经理责任制，金融市场部应建立银票业务档案，实时监测银票业务及风险敞口，并按月进行统计、分析和通报，清算中心应及时建立银票签发台账，及时核对账卡。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第七章　检查及到、逾期处理', '第三十三条', '在银票到期前10日，客户经理应通知客户将票款足额存入其账户用以付款。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第七章　检查及到、逾期处理', '第三十四条', '银票到期，客户未足额交存票款的，承兑行收到持票人开户行寄来的委托收款凭证和银票，经审核无误后，应无条件向持票人付款，并根据承兑协议的约定，从承兑申请人保证金专户和其他存款账户扣款，不足部分由本行垫付。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第七章　检查及到、逾期处理', '第三十五条', '发生银票垫款后，信贷管理部门必须将其纳入不良贷款考核范围，制定清收计划，落实清收责任。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第八章　档案管理', '第三十六条', '经办行客户经理按照信贷档案管理要求负责对银票相关业务资料的保管，包括：营业执照、税务登记证明、贷款卡（已年检）、资产负债表、损益表、现金流量表等财务报表（无法提供财务报表的小企业除外）、成立不到一年的企业可提供自成立以来的财务报表、交易背景相关的购销合同、敞口部分相关的担保合同及抵、质押手续，银票敞口授信通知书等资料。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第八章　档案管理', '第三十七条', '金融市场部负责保管：承兑放款通知书、银票申请审批表、银行承兑协议、承诺函、交易合同。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第九章　附则', '第三十八条', '银票业务按照《公司类信贷资产风险十级分类管理办法》及有关规定提取风险拨备。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第九章　附则', '第三十九条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《银行承兑汇票业务管理办法及操作流程》', '第九章　附则', '第四十条', '本办法自印发之日起施行。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第一章  总则', '第一条', '为进一步规范××××银行股份有限公司（以下简称“本行”）银行承兑汇票（以下简称“银票”）贴现业务，保障银票贴现业务健康发展，规范操作行为，防控业务风险。根据《中华人民共和国票据法》、《支付结算办法》、《贷款通则》等法律法规以及省联社有关规章制度，结合本行实际，制定本办法。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第一章  总则', '第二条', '贴现业务是指银票的持有人（或收款人）在需要资金时，凭其收取的未到期银票，通过背书方式转让给本行，本行在票面金额中扣除贴现利息将资金贷付给持票人（或收款人），银票到期后，本行按票面金额向银票的承兑人（或付款人）收取票款的行为。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第一章  总则', '第三条', '办理贴现业务必须始终坚持“统一授信、审贷分离、责权分明”的原则。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第四条', '各支行、营业部、公司业务部、小微业务部（以下统称“经办行”）主要职责：（一）负责受理辖区内的银行承兑汇票贴现业务；（二）负责对申请人进行资信调查，按要求搜集资料，做好贴现申请人用信申请；（三）负责对拟办理贴现业务申请人提交资料的真实性、一致性、有效性初审；（四）负责信贷管理系统相关数据资料的录入；（五）负责贴现资料的传递、监督检查贴现资金使用情况等工作；（六）采取复印，扫描等方式做好贴现业务资料的备查工作。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第五条', '授信评审部主要职责：对经办行贴现申报资料的合法性、合规性和完整性进行审核，对符合贴现要求的，根据本行授信流程进行审批，确定授信额度。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第六条', '金融市场部主要职责：（一）负责拟定本行银行承兑汇票贴现管理制度及具体操作流程；（二）负责根据市场利率协助计划财务部确定贴现利率；（三）负责贴现业务资料的审核工作；（四）负责本行贴现业务台账建立并做好本行银票贴现业务的考核和测算工作，按照外部监管的规定报送相关报表；（五）负责已贴现业务资料的收集、保管，装订、归档工作。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第七条', '信贷管理部主要职责：负责贴现业务的用信限额管理。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第八条', '运营管理部下属清算中心主要职责：（一）负责银票真伪（含背书）的查验与识别；（二）负责会计核算手续的办理、资金的划拨等工作；（三）负责已贴现银票实物的保管工作；（四）负责本行已贴现银票台账的建立，负责到期银票委托收款工作，做好已贴现银票的出入库保管。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第九条', '合规管理部主要职责：（一）对本行《银票贴现业务管理办法以及操作流程》合规性、有效性审核；（二）对与贴现业务相关合同、协议的法律、法规的合规性进行审核；（三）结合本行具体实际，对办法与流程提出具体修订建议。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第十条', '风险管理部主要职责：不定期对本行贴现业务开展分析，识别，监测和评估等活动，按要求出具并报送风险评估报告。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第十一条', '计划财务部主要负责本行银票贴现利率的具体确定，做好公布。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第十二条', '审计部负责贴现业务的检查和稽核审计工作。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第二章  职责分工', '第十三条', '资产保全部负责本行票据时效的追诉。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第三章  贴现期限和利率', '第十四条', '贴现期限。贴现期限指从银票贴现之日起至银票到期之日止，到期日是法定休假日的，以休假日的次日为最后一日。承兑行在异地的，贴现期限的计算应另加3天的划款邮程。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第三章  贴现期限和利率', '第十五条', '贴现利率。本行根据利率市场变动情况、资金运用状况、票面金额的大小以及银票签发行的信誉情况等因素，结合人民银行的有关规定，合理确定贴现利率。具体操作中可视申请人信用状况、对本行综合贡献度、贴现业务的金额和期限等情况，在利率上实行区别对待。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第四章  贴现基本规定', '第十六条', '办理贴现业务必须具备下列基本条件：（一）申请人为依法成立的企业法人或其他经济组织；（二）申请人具有良好的经营和资信状况；（三）申请人在本行及其下辖网点开立结算账户；（四）申请人必须与出票人或其直接前手之间具有合法、真实、有效的商品交易关系；（五）银票必须是根据《中华人民共和国票据法》及有关规定签发的要素齐全的有效票据；（六）申请人必须办好银票背书转让手续，并按照《贴现业务管理办法》规定提供相关资料。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第四章  贴现基本规定', '第十七条', '有下列情况之一的，贴现业务不予办理：（一）银票期限超过6个月的；（二）银票载明“不得转让”、“委托收款”字样的；（三）银票要素不齐全、内容涂改的或有关签章不符要求的；（四）银票背书不连续的；（五）粘单使用不规范的；（六）银票权利人已向法院申请公示催告的；（七）融资性的银票或承兑行查复有“有他行查询”但本行未向查询行进行查询的银票；（八）经办行或贴现申请人使用重复或不符要求的发票或购销合同办理贴现业务，则暂停经办行及客户办理贴现业务的资格；（九）不符合贴现业务规定的其他情况。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第四章  贴现基本规定', '第十八条', '办理贴现业务的经办行在贴现业务之前必须向承兑行办理银票的查询，取得查询查复书后方可办理贴现业务。查询书应包括以下因素：银票票面要素录入齐全，是否有他行查询情况，有无挂失止付、冻结、质押或公示催告等。查复书应包括以下内容：查复书上必须注明银票有无冻结、挂失、止付、质押、特别暗记、机打及他行查询等信息。对于有疑问或数额较大的银票，要传真核对或实地查询核对，经实地验票，出票行仍出具“真假自辨”意见的银票，原则上不予受理。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第五章   风险管理', '第十九条', '对发现客户办理贴现业务短期内大幅度增加、新开户客户即办理大额贴现申请、贸易流通类客户频繁发生贴现业务、办理的贴现业务资金超过其经营规模等情况的，各经办行客户经理要深入了解客户的真实情况，做好贷前的尽职调查。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第五章   风险管理', '第二十条', '已办理完贴现业务的，经办行应跟踪核实贴现资金的具体用途以及使用方向，查看资金有无回流至出票人，有无流入股市，房产等国家禁止的行业。发现苗头，及时采取措施，有效防范风险，必要时暂停其贴现业务资格。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第五章   风险管理', '第二十一条', '清算中心要做好银票到期的托收工作，发现到期票款未到账应及时告知信贷管理部以及经办行客户经理，直接从客户账进行款项的划转，对客户账户资金不足，视为逾期贷款，纳入本行不良贷款管理，清算中心要协助好经办行做好款项的跟踪。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第六章  档案管理', '第二十二条', '经办行应按要求做好以下资料的保管：（一）企业法人资格证明文件及有关法律文件，包括：（1）经年审合格的三证合一营业执照;（2）企业（法人）基本账户开户许可证；（3）企业法人代表证明书或授权委托书；（4）董事会（股东会）决议；（5）其他需要提供的与贴现业务相关的资料。（二）贴现申请人的近期财务报表。上述资料均需加章贴现单位公章，且为A4纸复印件或扫描文件。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第六章  档案管理', '第二十三条', '金融市场部负责建立贴现登记台账，做好如：贴现申请书、贴现协议书、贴现贷款放款通知书及银票复印件、查询查复书、税务发票和商品交易合同复印件等贴现业务相关资料的装订保管。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第六章  档案管理', '第二十四条', '清算中心必须建立贴现业务相关分户账和到期催收台账，登记代保管有价证券登记簿，严格按照保管有价单证的要求办理银票会计核算和出入库手续，并妥善保管好银票证明等跟单资料。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第七章  附则', '第二十五条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《银行承兑汇票贴现业务管理办法》', '第七章  附则', '第二十六条', '本办法自印发之日起施行。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第一章  总则', '第一条', '为规范××××银行股份有限公司（以下简称“本行”）在银行间市场的融资行为，完善内控办法，防范金融风险，根据《同业拆借管理办法》、《全国银行间债券市场债券交易管理办法》、《××××省农村信用社系统内资金调剂管理暂行办法》等相关规定，结合本行实际，特制定本办法。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第一章  总则', '第二条', '本办法所称银行间市场融资业务是指本行与全国银行间同业拆借市场的金融机构或与非市场交易成员的银行类机构之间的人民币资金融通业务。包括：同业拆借、系统内资金调剂、回购业务、存放同业、同业存放等。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第一章  总则', '第三条', '本行在银行间市场融资的基本原则是：在保证资金安全性、流动性的前提下，实现资金收益最大化。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第二章  组织职责', '第四条', '金融市场部负责拟定市场融资计划及银行间市场融资业务管理办法与流程，负责同业客户授信资料的收集上报及客户资信的调查和审查，在授权范围内从事银行间市场融资业务，负责相关业务资料的保管。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第二章  组织职责', '第五条', '授信评审部负责对非结算类存放同业业务交易对手的授信管理。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第二章  组织职责', '第六条', '运营管理部负责制定银行间市场融资业务的会计核算程序，其下属清算中心具体负责业务的会计核算和账务处理。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第二章  组织职责', '第七条', '合规管理部负责对银行间市场融资业务管理办法、业务操作流程以及相关合同条款的合规性、有效性审核,对新业务、新产品等制度办法的出台提供法律意见和合规建议。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第二章  组织职责', '第八条', '风险管理部负责对银行间市场融资业务风险定期进行识别、计量、监测，做出独立评估，出具风险审查报告。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第二章  组织职责', '第九条', '审计稽核部负责对银行间市场融资业务定期或不定期进行审计稽核和监督检查。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第三章  同业拆借', '第十条', '同业拆借是指经中国人民银行批准进入全国银行间同业拆借市场的金融机构之间通过全国统一的同业拆借网络进行的无担保资金融通行为。同业拆借包括：同业拆入和同业拆出。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第三章  同业拆借', '第十一条', '同业拆借交易以询价方式进行，自主谈判，逐笔成交。同业拆借利率由交易双方参考当日同期限SHIBOR利率结合市场自行商定。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第三章  同业拆借', '第十二条', '同业拆借交易应逐笔订立交易合同。交易合同的内容应当具体明确，详细约定同业拆借双方的权利和义务。合同应包括以下内容：㈠交易双方的名称、住所以及法定代表人的姓名；㈡成交日期；㈢交易金额；㈣交易期限；㈤同业拆借利率、利率计算规则和利息支付规则；㈥违约责任；㈦中国人民银行要求载明的其他相关事项。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第三章  同业拆借', '第十三条', '交易合同可采用全国银行间同业拆借中心电子交易系统生成的成交单，或者采取合同书、协议等书面形式。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第三章  同业拆借', '第十四条', '同业拆借实行限额管理，最高拆入限额和最高拆出限额均不超过本行各项存款余额的8%。中国人民银行可以根据市场发展和管理的需要调整金融机构的同业拆借资金限额。  同业拆入资金的最长期限为1年，同业拆借的期限最短为1天，拆出资金的最长期限不得超过对手方由中国人民银行规定的拆入资金最长期限。中国人民银行可以根据市场发展和管理的需要调整金融机构的拆借资金最长期限。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第三章  同业拆借', '第十六条', '同业拆借到期后不得展期。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第三章  同业拆借', '第十七条', '拆出对手方仅限于在本行有授信的存款类金融机构，不得向证券公司、基金公司、保险公司、信托公司、财务公司等非银行类金融机构拆出资金。拆入对手方不受本行授信的限制，但必须符合人民银行的规定。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第四章  资金调剂', '第十八条', '本办法所称资金调剂是系统内资金调剂，是指本行与××××省农村信用社联合社（以下简称省联社）之间非交易系统的资金融通，包括：调入调剂和调出调剂。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第四章  资金调剂', '第十九条', '资金调剂以询价方式进行，自主谈判，逐笔成交。资金调剂利率由交易双方参考全国银行间债券市场的（同业拆借）同档次利率自行商定。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第四章  资金调剂', '第二十条', '资金调剂应签订《××××省农村信用社资金调剂业务协议》，协议应明确资金调剂方向、金额、利率、期限、违约责任等内容。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第四章  资金调剂', '第二十一条', '资金调剂期限最短为1天，最长为1年。调出、调入资金必须严格按照约定的期限进行管理。特殊情况需提前收回调出资金，须提前2天通知对方，并经双方协商同意后方可办理资金汇划手续。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第四章  资金调剂', '第二十二条', '调入资金不得用于发放农户贷款以外的其他贷款，不得用于债券投资，严禁用调剂资金发放中长期贷款、固定资产贷款和技术改造贷款。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第四章  资金调剂', '第二十三条', '申请调剂资金的机构必须满足以下条件：㈠及时足额缴足存款准备金。㈡拆入资金余额与本行各项存款余额比例不得高于4%。、'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第五章  回购业务', '第二十四条', '本办法所称回购业务包括债券回购和票据回购。债券回购是指交易双方进行的以债券权利为抵质押品的一种资金融通业务。债券回购分为债券质押式回购和债券买断式回购。票据回购是指本行对金融同业所持已贴现的商业汇票（银行承兑汇票和商业承兑汇票）进行买入返售，或本行所持已贴现商业汇票向金融同业申请卖出回购的融资行为。票据回购分为商业汇票买入返售和商业汇票卖出回购。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第五章  回购业务', '第二十五条', '债券回购业务按照《××××银行股份有限公司债券投资业务管理办法》相关规定办理。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第五章  回购业务', '第二十六条', '商业汇票买入返售业务除按照《××××银行股份有限公司商业汇票转贴现和再贴现业务管理办法》相关规定办理外，还须遵守以下规定：（一）卖出回购银行应提供商业汇票原件以及正反面复印件；商业汇票查询查复书原件或复印件；第一手贴现凭证和最后一手转贴现凭证。（二）买入返售业务的到期日控制在商业汇票到期日前3天。（三）第一次办理票据回购，卖出回购银行应提供营业执照和金融许可证复印件、经办人身份证和授权委托书等资料。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第五章  回购业务', '第二十七条', '票据回购业务的金额、利率、期限及票据种类等要素由交易双方根据票据市场行情协商确定。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第五章  回购业务', '第二十八条', '票据回购应签订《商业汇票回购合同》。合同的内容应当具体明确，详细约定回购双方的权利和义务。合同应包含以下主要内容：回购双方的名称、回购日期、金额、期限、利率、利率计算和利息支付规则、违约责任等内容。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第六章  存放款项', '第二十九条', '本办法所称存放款项业务仅指存放同业和同业存放。存放同业是指本行存放在其他银行或银行类金融机构的存款，分结算类存放同业和非结算类存放同业。结算类存放同业主要用于日常支付结算，非结算类存放同业主要目的是实现固定收益。同业存放是指其他银行或银行类金融机构存放在本行的存款。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第六章  存放款项', '第三十条', '结算类存放同业由业务部门经办人根据日常支付结算资金需求进行头寸划拨。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第六章  存放款项', '第三十一条', '非结算类存放同业由业务部门根据对手方的信用状况、本行对其授信金额以及备付资金余额来确定，金额及利率由交易双方根据资金市场行情来确定，但利率不得低于人民银行超额存款准备金利率。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第六章  存放款项', '第三十二条', '非结算类存放同业应签订存放同业合作协议，协议应包含以下主要内容：双方的名称、起止日期、金额、期限、利率、结息方式、违约责任等内容。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第六章  存放款项', '第三十三条', '存放同业应坚持定期对账，做到内外账户核对相符，业务部门的台账与会计核算账户核对相符。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第六章  存放款项', '第三十四条', '同业存放相关管理规定参照存放同业相关规定执行。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第七章  资金划付与清算', '第三十五条', '金融市场部根据与交易对手达成的书面合同，填写《资金收、划款通知书》，经部门负责人审批后，交清算中心提请划款。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第七章  资金划付与清算', '第三十六条', '清算中心根据金融市场部提交的书面合同、《资金收、划款通知书》，填制付款凭证，经审核无误后，办理划款手续。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第七章  资金划付与清算', '第三十七条', '清算中心及时向金融市场部反馈资金的划拨及到账情况。如资金清算网络发生故障，及时与相关部门联系，查明原因，排除故障。若对资金划拨和到账造成影响，及时将有关情况向运行管理部和金融市场部反馈。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第八章  内部控制', '第三十八条', '金融市场部应当实现逐级授权经营与管理，在权限范围内进行市场间资金融通业务，不得超权限办理。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第八章  内部控制', '第三十九条', '金融市场部应建立内部报告制度，定期向行长室和董事会汇报和分析资金流动性、收益和风险等情况，为管理层提供决策依据。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第九章  附则', '第四十条', '本办法由××××银行股份有限公司金融市场部负责解释和修订。'),
(NULL, '金融市场部', '《银行间市场融资业务管理办法》', '第九章  附则', '第四十一条', '本办法自公布之日起执行。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第一章  总则', '第一条', '为规范xxx银行（以下简称“本行”）信贷行为，加强岗位制约，促进充分履职，防范和化解信贷风险，全面提高信贷资产质量和效益，根据《中华人民共和国商业银行法》、《贷款通则》、《商业银行授信工作尽职指引》等法律法规以及本行信贷管理的相关规章制度，特制订本办法。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第一章  总则', '第二条', '本办法所指的信贷资产是指本行向客户提供的贷款(含贷记卡)、项目融资、贸易融资、贴现、透支、保理、拆借和回购、信贷资产承诺、保函、信用证、票据承兑等表内、外业务所形成的资产。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第一章  总则', '第三条', '本办法所指不良信贷资产包括新发生的五级分类后三类（次级、可疑、损失）、四级不良、违法违规以及本行认为需要追究责任的其他的信贷资产的本息和。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第一章  总则', '第四条', '不良信贷资产责任追究是对不良信贷资产形成的全过程进行深度调查，对各个环节相关人员的操作和管理行为进行责任认定，并对责任人进行处理。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第一章  总则', '第五条', '本办法所指赔偿保证金，系指总行根据本办法为追究责任人未尽职而应承担赔偿责任的保证金，资金来源包括责任人岗位保证（风险）金、延期支付工资、责任人缴纳的现金、责任人工资扣款等，总行为每位员工建立赔偿保证金账户，专门用于赔偿保证金的归集、使用等。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第一章  总则', '第六条', '不良信贷资产责任追究遵循“客观公正、尽职免责、失职必究、追本溯源、违法违规严惩，积极处置从轻、消极对待从重”的原则。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第二章  组织体系及职责分工', '第七条', '风险与内控管理委员会负责不良信贷资产责任追究的组织、协调及疑难问题的集体决策工作。该委员会下设办公室在风险管理部。必要时经风险与内控管理委员会同意，本行可以委托中介机构为本行良信贷资产责任追究工作提供咨询服务。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第二章  组织体系及职责分工', '第八条', '职责分工（一）风险与内控管理委员会职责1.组织实施不良信贷资产责任追究工作；2.审议责任追究实施方案及对责任人的处理方案；3.审议责任人对总行责任认定及处理决定有异议的复议；4.其他需研究的不良信贷资产责任追究事项。（二）风险管理部职责1.牵头实施不良信贷资产的事实调查及确认工作，形成初步不良信贷资产责任追究认定意见。2.每月收集整理需责任追究的新增不良信贷资产数据；3.负责风险与内控管理委员会会办的结论意见，下发《不良信贷资产赔偿通知书》，并负责赔偿保证金的催缴和管理；4.跟踪责任不良信贷资产处置情况及责任确定后的追责到位工作；5.建立不良信贷资产责任追究档案，并定期向风险与内控管理委员会报告责任不良信贷资产的清收处置情况；6明确责任不良信贷资产的清收管理责任人，认定清收管理责任人是否尽职清收；7.对审议通过的问责情况进行通报。8.其他工作事项。（三）总行各部门职责1.合规管理部：对不良信贷资产责任追究中责任人的复议材料提供复议意见。2.审计部：提供审计工作中发现的可能影响不良信贷资产责任追究结果的意见和材料。3.纪检监察部、人力资源部：组织对相关责任人进行处分，提出责任处分方案。4.授信评审部、信贷管理部、电子银行部、资金营运部、运营管理部、计划财务部：配合做好不良信贷资产责任追究的全面调查、事实确认、责任不良信贷资产的管理清收、赔偿保证金的归集等工作。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第三章  不良信贷资产责任类型', '第九条', '本办法所称不良信贷资产责任分为尽职、不完全尽职、轻度失职、失职、渎职。㈠尽职。指信贷业务人员在信贷业务的调查、审查、会办、审批、合同签订、贷款发放、资金支付、贷后检查、催收保全、档案管理等过程中，严格遵守国家法律、法规、金融规章，严格按照信贷业务流程及其相关规定、岗位职责实施规范化操作，有客观尽职履职记录，并满足以下条件的：⒈由于不可控因素或难以预期因素导致的信贷风险；２.前述因素出现后，已采取了果断、有力的补救措施。㈡不完全尽职。指信贷业务人员在信贷业务的调查、审查、会办、审批、合同签订、贷款发放、资金支付、贷后检查、催收保全、档案管理等过程中，遵守国家法律、法规、金融规章，按照信贷业务流程及其相关规定、岗位职责实施规范化操作，有客观尽职履职记录，并满足以下条件的：⒈履行了岗位职责；⒉已从程序上履行了岗位职责，但对出现的资产风险没有足够理由能认定为尽职。㈢轻度失职。指信贷业务人员在信贷业务的调查、审查、会办、审批、合同签订、贷款发放、资金支付、贷后检查、催收保全、档案管理等过程中，未严格遵守国家法律、法规、金融规章，或未严格按照信贷业务流程及其相关规定、岗位职责实施规范化操作，存在以下行为之一的：⒈未严格履行岗位职责；⒉已从程序上履行了岗位职责，但履行职责存在缺陷，致使没有达到程序所应达到的目的，导致风险发生或造成一定影响。㈣失职。指信贷业务人员在信贷业务的调查、审查、会办、审批、合同签订、贷款发放、资金支付、贷后检查、催收保全、档案管理等过程中，未严格遵守国家法律、法规、金融规章，或未严格按照信贷业务流程及其相关规定、岗位职责实施规范化操作，存在以下行为之一的：1.未履行岗位职责；⒉已从程序上履行了岗位职责，但未依照规定充分履行职责，能够做到而没有做到，如应发现风险而未发现或应采取有效措施而未采取，致使没有达到程序所应达到的目的，导致风险发生或造成较大影响。㈤渎职。指信贷业务人员在信贷业务的调查、审查、会办、审批、合同签订、贷款发放、资金支付、贷后检查、催收保全、档案管理等过程中，未遵守国家法律、法规、金融规章，或未按照信贷业务流程及其相关规定、岗位职责实施规范化操作，，造成损失，包括但不限于以下情形:1.冒名贷款。即实际借款人冒他人之名借款。凭证署名借款人存在，但并不知情（或予以否认），本人也未在申请书及借款合同上签字确认，贷款责任人自身或内外勾结，利用所掌握的他人身份证明材料，骗取贷款的行为；2.借名贷款。借款人存在，合同借款人与资金实际使用人不是同一人，但合同借款人知情并认可实际用款人以其名义贷款，清理时，借款人、实际用款人之一无条件、全额承接贷款债务（或两人无条件、分比例、全额承接贷款债务）。具体可分为两种情况：（1）贷款由借款人本人办理，资金为他人（实际用款人）使用；（2）贷款不是借款人本人办理，贷款手续由实际用款人以借款人名义办理，合同借款人未在申请书及借款合上签字，资金为合同借款人认可的他人（实际用款人）使用。3.假名贷款。即凭证署名的借款人不存在，贷款责任人自身或内外勾结，以伪造、编造的虚假借款人身份信息材料，以虚假借款人名义骗取贷款的行为；4.超权限贷款。贷款责任人违反本行授权授信管理制度，发放超过信贷审批权限或客户授信额度及条件的贷款，包括贷款责任人为逃避信贷检查监督而向借款人发放多笔累计超过贷款审批权限的贷款（含由借款人家庭成员或他人承借的贷款）；逆程序或缺程序贷款、未经授权的贷款处置行为，如擅自放弃诉讼、执行借款人或担保人，均视同超权限贷款；5.一户多名贷款。贷款责任人在借款人未归还所借贷款的前提下，故意向借款人配偶、直系亲属、其他家庭成员等多人发放的贷款(同一家庭成员经营不同的项目，资产、负债、经营活动相对独立、单独核算的除外)；6.以贷收本息、以贷收息、未还息而以贷还贷的贷款。贷款责任人对不能按期归还的贷款，采取发放超过原贷款金额的贷款于当日或隔日收回贷款本息的贷款；借款人因归还原贷款利息而发放的贷款；借款人无力归还原贷款利息而为其以贷还贷的贷款（因历史原因盘活的贷款除外）；7.化整为零贷款。贷款责任人为逃避信贷检查监督而向借款人发放多笔累计超过贷款审批权限的贷款（包括由借款人家庭成员或他人承借的贷款）；8.企业有贷款，个人借款企业用的贷款。贷款责任人明知或应当知道企业已在本行有贷款，而继续发放以个人名义用于企业经营的贷款。9.故意编造虚假信息贷款。贷款责任人故意编造虚假调查报告、故意更改或遗漏重要信息发放的贷款；10.合同缺失或合同编号与借据上载明的合同号不一致，造成诉讼或处置困难，造成损失；11其他违反相关法律、法规、规章、制度的贷款。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第三章  不良信贷资产责任类型', '第十条', '已认定的不良信贷资产（包括尽职、不完全尽职、轻度失职、失职、渎职形成的不良信贷资产），在责任人赔偿后，总行风险管理部应明确清收责任人或管理责任人。如因清收管理责任人在后期管理工作中不尽职、造成损失扩大的，同样追究其责任。贷款丧失诉讼时效（包括主债务诉讼时效及担保诉讼时效）的，不按责任追究的一般流程进行责任认定，直接追究导致贷款丧失时效的管理责任人的全额赔偿责任。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十一条', '前移不良信贷资产责任追究时点，对每月新增不良及瑕疵信贷资产（包括贷记卡），由信贷管理部、电子银行部及时落实客户经理在岗清收，会同人力资源部下发在岗清收通知书，对逾期三个月仍未收回的，停发绩效工资。未收回的三个月后进行责任认定。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十二条', '不良信贷资产责任追究方式包括违规记分、经济处罚、纪律处分及其他处罚；涉嫌犯罪的，移交公安机关处理。1.记分处罚：根据违规情节轻重和造成的后果，依据本行《员工违规行为管理办法》的相关规定，给予一定的记分；⒉经济处罚：包括罚款、赔偿损失。⒊纪律处分：包括警告、记过、记大过、降级、撤职、留用察看、开除；4.其他处罚:包括批评教育、组织处理。其中批评教育包括：责令限期改正、责令书面检查、诫勉谈话、通报批评；组织处理包括：转岗清收、调离、停职、解聘专业技术职务、责令辞职、免职、解除劳动合同等。5.刑事责任追究：不良信贷资产责任人违反法律法规涉嫌犯罪的，移交公安机关处理。6.以上处理方式可以并用。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十三条', '纪律处分等责任追究纪律处分及其他处罚按照总行《xxx银行案件责任追究实施细则》、《xxx银行员工违规行为处理办法》等相关规定进行处理。㈠信贷人员违法违规办理信贷业务，给予信贷人员转岗处理或记大过以下处分，情节严重的给予解除劳动合同处分；给予其他责任人待岗至记大过处分。㈡不良信贷资产责任人失职或渎职导致风险发生或形成不良影响的，给予相关责任人转岗处理或记大过以下处分；造成严重后果的或较大影响的，给予解除劳动合同处分。㈢支行行长（经理）、副行长（副经理）等人员在任职期间发生违法违规导致不良信贷资产的，给予警告至撤职处分，情节严重的给予解除劳动合同处分。㈣责任人未按要求及时上缴赔偿保证金的，根据实际情况对有关责任人员给予转岗处理，同时给予记分处罚。清收的最长期限为6个月。清收期间，只发放本地最低生活保障工资。清收期满后，根据具体表现给予其他责任追究。㈤对因违法违规发放信贷资产解除劳动合同的人员及其他原因与本行解除劳动合同的人员，本行保留追索权，并可继续追究其刑事责任。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十四条', '不良信贷资产（含本金、利息）损失，指在信贷资产逾期后三个月内，未能收回的信贷资产本息。对不良信贷资产损失部分，分为五种情况处理（公司贷款按不良信贷资产本息的10%折算成赔偿总额）：一是对已尽职的不良信贷资产，按照尽职免责的原则，对相关责任人不予追责。二是对不完全尽职导致不良信贷资产形成损失的，相关责任人承担不良信贷资产本息的1%-20%的赔偿责任。三是对轻度失职导致不良信贷资产形成损失的，相关责任人承担不良信贷资产本息的21%-40%赔偿责任。四是对失职导致不良信贷资产形成损失的，相关责任人承担不良信贷资产本息的41%-100%（不含）赔偿责任。五是对渎职导致不良信贷资产形成损失的，相关责任人承担不良信贷资产本息全额的赔偿责任。当年每个责任人最高累计赔偿金额为20万元。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十五条', '对所有渎职形成的不良信贷资产（不论金额大小），如在检查发现后一个月内未能收回的，视同已产生信贷资产风险、可能形成信贷资产损失，由相关责任人全额赔偿不良信贷资产本息，并责令相关责任人停岗清收。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十六条', '渎职形成的不良信贷资产的责任比例。根据谁违规谁负责的原则，结合各岗位违规情节合理确定责任比例，原则上主违规责任人至少承担50%的赔偿责任，其他责任人员承担剩余的赔偿责任，具体赔偿比例、金额由风险与内控管理委员会研究确定。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十七条', '不完全尽职、轻度失职、失职导致不良信贷资产形成损失的责任比例。各岗位责任比例原则上按下列标准执行，特殊情况可视各岗位过失性质和程度进行调整：㈠支行（部）权限范围内的发放的：调查岗A角、调查岗B角、审查岗、参与会办同意信贷资产除决策人外的其他成员、审批岗、合同签订岗、提款支付岗（提款经办岗、提款审查岗、提款审批岗责任比例为2：2：1）、柜面支付岗、贷后检查岗、催收保全岗，分别承担40%、15%、5%、5%、5%、5%、5%、5%、5%、5%的责任。信贷人员及其他授权人在权限范围内决策发放有价证券质押信贷资产等，因失职、渎职造成损失的，承担100%的责任。㈡授信评审部、信贷管理部审批的：⒈支行（部）承担80%的责任，承担比例按本条（一）款执行；⒉授信评审部、信贷管理部分别承担10%、10%的责任，由授信评审部、信贷管理部审查岗、审批岗分摊。㈢信贷审查委员会审批的：⒈支行（部）承担70%的责任，承担比例按本条（一）款执行；⒉授信评审部、信贷管理部分别承担10%、10%的责任，由授信评审部、信贷管理部负责人分别承担4%的责任，其余由评审人员分摊；⒊信贷审查委员会承担10%的责任，主任委员承担2%的责任，其余由与会投同意票的成员分摊（不含授信评审部、信贷管理部负责人）。㈣其他：⒈信贷人员直接发放信贷资产，造成损失的由其本人全额赔偿。⒉信贷审查委员会成员或支行会办小组成员在评审中或集体会办提出不同意见的，应在评审记录或会办记录上就不同意的原因作出书面详细说明；如以后信贷资产出现损失，其原因与记录一致或大体符合，则该成员不承担赔偿责任。3.在各个信贷操作流程中，如被认定为尽职的工作人员，则其不承担赔偿责任，其岗位所对应的赔偿责任比例也不计入赔偿总比例。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第四章  责任追究及经济赔偿', '第十八条', '赔偿保证金管理。经认定信贷人员经办的信贷业务负有赔偿责任的，计划财务部根据责任认定部门提供的《不良信贷资产赔偿通知书》在5个工作日内从责任人的延期支付工资等账户划至赔偿保证金账户，不足部分责令责任人现金赔偿，其余不足部分从其工资中按月扣缴（责任人每月只发放最低生活保障工资），直至不良信贷资产处理完毕或赔偿保证金总额达到应赔金额为止。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第五章  责任追究特别规定', '第十九条', '其他规定⒈丧失时效信贷资产。对2014年12月31日（含）以前丧失时效的不良信贷资产，由本行资产保全部逐笔确认建档；对2015年1月1日以后丧失时效的不良信贷资产，责任人一律按不良信贷资产本息承担全额赔偿责任；以虚假材料冒充保全手续或与借款人、担保人串通故意丧失时效的，视同渎职处理，责任人承担全额赔偿责任。⒉职工、职工家属或其担保形成的不良信贷资产。信贷资产形成不良后，视情况给予宽限期，最长不超过一个月。宽限期满后，其仍未履行还款责任的，总行给予其停岗处理，并视情况采取进一步的措施，直至其还款义务履行完毕。⒊对盘活转据、资产重组类不良信贷资产所形成的损失，根据信贷档案资料及调查的具体情况进行责任追究，处罚标准由风险与内控管理委员会研究决定。⒋对同一笔不良信贷资产因不完全尽职、轻度失职、失职、渎职形成的，择其重者进行处理。⒌同一责任人笔数较多或金额较大的，视情节轻重给予纪律处分；不良信贷资产损失超过最高赔偿限额的，从重追究行政责任。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第六章  不良信贷资产责任追究程序', '第二十条', '不良信贷资产责任追究实行“隔季追责”制度。原则上在不良信贷资产产生后三个月内完成相应的尽职调查、责任认定和对责任人的处理工作。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第六章  不良信贷资产责任追究程序', '第二十一条', '不良信贷资产责任认定前，牵头部门及时通知相关责任人，由责任人逐户撰写不良贷款尽职说明书，并保证所提供资料的真实、有效。被调查部门和相关责任人应主动向风险管理部提交尽职说明书，及时提交相关资料。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第六章  不良信贷资产责任追究程序', '第二十二条', '不良信贷资产贷款问责的一般程序包括以下步骤：㈠数据采集。风险管理部每月5日生成需责任认定新增不良信贷资产清单。㈡成立工作组。风险管理部组织相关人员对不良信贷资产逐笔进行尽职调查、责任认定和责任复议。㈢责任认定。风险管理部形成不良信贷资产责任认定意见书（初稿），提出初步处理意见，提交风险与内控管理委员会研究审议。风险管理部根据风险与内控管理委员会的审议意见拟定不良信贷资产责任认定意见书，并将不良信贷资产责任认定意见书送交相关责任人。㈣责任复议。责任人在收到认定意见书后7日内，有权就相关内容向合规管理部申辩，并提交书面的复议申请。合规管理部对责任人申请的复议内容进行核实和确认，并形成复议认定意见。责任人逾期不提交书面复议申请的，视同无异议。㈤综合评定。风险与内控管理委员会根据不良信贷资产责任认定意见、责任人书面申辩意见、合规管理部复议认定意见进行综合评定。㈥责任追究。风险管理部按照风险与内控管理委员会评定的结论意见，下发《不良信贷资产赔偿通知书》，并负责赔偿保证金的催缴和管理工作；计划财务部协助风险管理部归集、使用赔偿保证金；人力资源部组织对责任人停岗、转岗、扣发工资等的处理，需纪律处分的，移交纪检监察部处理。㈦档案管理。风险管理部做好认定资料的归档工作，建立已认定不良信贷资产台账，并定期统计报告责任不良信贷资产的清收处置情况。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第六章  不良信贷资产责任追究程序', '第二十三条', '不良贷款清收处置期限3-6个月，最长不超过2年，具体期限由风险与内控管理委员会研究确定。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第六章  不良信贷资产责任追究程序', '第二十四条', '鼓励加快责任不良信贷资产的清收处置，不良信贷资产界定责任后三个月内全额收回的，全额退还赔偿款；不良信贷资产界定后六个月内全额收回的，退还赔偿款项的90%；不良信贷资产界定后一年内全额收回的，退还赔偿款项的80%；不良信贷资产界定后两年内全额收回的，退还赔偿款项的60%；部分收回的，按收回的比例计算退还赔偿款项；界定后超过两年收回的不再退还责任人赔偿款项。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第六章  不良信贷资产责任追究程序', '第二十五条', '不良贷款追责情况应通过本行办公网络及时在全行通报，其认定结论及责任人处理处罚决定列入人事及信贷档案保管。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第七章  附则', '第二十六条', '本办法未尽事宜按本行《贷款业务尽职管理办法》、《信贷管理制度》、《圆鼎贷记卡业务管理办法》等制度办法规定执行。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第七章  附则', '第二十七条', '本办法由xxx银行行长室负责解释和修订。'),
(NULL, '风险管理部', '《不良信贷资产责任追究管理办法》', '第七章  附则', '第二十八条', '本办法经xxx银行职工代表大会通过后执行；对2017年3月31日之前形成的不良贷款，其责任追究仍按xxx银行行发〔2015〕88号文件执行。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第一章  总则', '第一条', '为规范xxx银行（以下简称“本行”）信息科技系统流程分析，识别可能影响业务连续性的过程，针对影响因素，为保持或及时恢复业务运作，保证业务的连续性，特制定本制度。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第一章  总则', '第二条', '本制度适用于信息科技系统所涉及到的部门、人员及资产。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第一章  总则', '第三条', '本制度所称的业务连续性管理是指本行行为有效应对重要业务运营中断事件，建设应急响应、恢复机制和管理能力框架，保障重要业务持续运营的一整套管理过程，包括策略、组织架构、方法、标准和程序。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第四条', '董事会是本行业务连续性管理的决策机构，对业务连续性管理承担最终责任。主要职责包括：（一）审核和批准业务连续性管理战略、政策和程序；（二）审批经营管理层业务连续性管理职责，定期听取高级管理层关于业务连续性管理的报告，监督、评价其履职情况；（三）审批业务连续性管理年度审计报告。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第五条', '经营管理层负责执行经董事会批准的业务连续性管理政策。主要职责包括：（一）制定并定期审查和监督执行业务连续性管理政策、程序；（二）明确各部门业务连续性管理职责，明确报告路线，审批重要业务恢复目标和恢复策略，督促各部门履行管理职责，确保业务连续性管理体系正常运行；（三）确保配置足够的资源保障业务连续性管理的实施。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第六条', '风险管理部为本行业务连续性管理主管部门，其主要职责是：组织开展全行业务连续性管理工作，指导、评估、监督各部门的业务连续性管理工作；组织制定业务连续性计划，协调业务条线部门，汇总、确定重要业务的恢复目标和恢复策略；组织开展业务连续性计划的演练、评估与改进；开展业务连续性管理培训等。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第七条', '业务连续性管理执行部门，包括业务条线部门与信息科技部。业务条线部门负责风险评估、业务影响分析，确定重要业务恢复目标和恢复策略，负责业务条线重要业务应急响应与恢复；信息科技部门负责信息技术应急响应与恢复。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第八条', '业务连续性管理保障部门，包括行政办公室、人力资源部、计划财务部、合规管理部、安全保卫部等，为业务连续性日常管理提供人力、物力、财力以及安全保障和法律咨询。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第九条', '各部门应当负责本部门业务连续性管理工作，制定相关规章制度，制定和执行本部门业务连续性计划，开展本部门业务连续性计划的演练、评估与改进工作。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第十条', '本行审计部负责并定期开展全行业务连续性管理审计工作。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第二条', '应急处置组织架构'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第十一条', '本行运营中断事件应急处置的组织架构，包括应急决策层、应急指挥层、应急执行层和应急保障层。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第十二条', '应急决策层由本行经营管理层人员组成，负责决定应急处置重大事宜，包括：决定运营中断事件通报、对外报告和公告；批准启动总体应急预案等。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第十三条', '应急指挥层由本行业务连续性管理主管部门、执行部门和保障部门负责人组成，负责运营中断事件处置应急指挥和组织协调，督导应急处置实施。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第十四条', '应急执行层由本行业务连续性管理执行部门组成，负责业务条线与信息技术应急处置工作。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第二章  职责与权限', '第十五条', '应急保障层由本行业务连续性管理保障部门组成，负责应急处置所需人力、物力和财力等资源的保障，应急处置对外报告、宣告、通报和沟通与协调，以及对外媒体公关、秩序维护、安全保障、法律咨询和人员安抚等相关工作。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第三章  业务影响分析', '第十六条', '本行通过业务影响分析识别和评估业务运营中断所造成的影响和损失，明确业务连续性管理重点，根据业务重要程度实现差异化管理，确定各业务恢复优先顺序和恢复指标。本行每三年开展一次全面业务影响分析，并形成业务影响分析报告。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第三章  业务影响分析', '第十七条', '作为业务的支持系统－信息科技系统的连续性需求：（一）正常工作时间内不超过4个小时或者在正常工作时间外不超过8个小时。（二）在中断期间，应保持信息科技业务的最低资源需求（如人员、资产等）。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第三章  业务影响分析', '第十八条', '可能导致本行系统中断的事件包括：（一）内外部资源的故障或缺失（如人员、系统或其他资产）。（二）信息丢失或受损。（三）外部事件（如战争、地震或台风等）。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第三章  业务影响分析', '第十九条', '针对系统中断采取的措施确保系统持续性：（一）风险转嫁，如购买保险或将风险转嫁给供应商。如与供应商签订服务协议，协议中规定在出现中断情况下，供应商应如何进行积极回应。 （二）风险控制，采取一些预防或检测措施，以避免风险的发生或及时检测。如安装自动灭火系统、敏感信息的异地备份等。（三）制定业务连续性计划的原则是，既要节省投资和资源，又要确保业务的连续性目标达成。（四）应采取系统恢复和双机热备处理等措施降低业务中断的可能性，并通过应急方式降低影响。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第一条', '业务连续性计划'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十条', '本行业务连续性计划的主要内容包括：（一）重要业务及关联关系、业务恢复优先次序；（二）重要业务运营所需关键资源；（三）应急指挥和危机通讯程序；（四）各类预案以及预案维护、管理要求；（五）残余风险。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十一条', '本行制定总体应急预案和重要业务专项应急预案。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十二条', '本行重要业务及信息系统的外部供应商应建立业务连续性计划，证明其业务连续性计划的有效性，其业务恢复目标应当满足本行业务要求。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十三条', '本行业务连续性计划应与金融同业单位、外部金融市场、金融服务平台和公共事业部门等业务连续性计划的有效衔接；同时，应当积极采取风险缓释及转移措施，有效控制由于外部机构业务连续性管理不充分可能产生的风险。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二条', '业务连续性资源建设'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十四条', '本行设立统一的运营中断事件指挥中心场所，用于应急决策、指挥与联络，指挥场所应当配置办公与通讯设备以及指挥执行文档、联系资料等。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十五条', '本行建立符合业务连续性管理要求的备用资源，如备用业务和办公场所资源、备用信息系统运行场所资源、备用信息技术资源、备用人力资源等，以及电力、通讯、消防、安保等资源。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十六条', '本行选择备用场地时，应当确保不会同时遭受同类型风险；应当综合分析备用场地所在地的自然环境、地区配套设施、区域经济环境、交通条件、政策环境和成本等各方面因素，以及灾难恢复所需的金融服务、通讯、设备、技术等外部服务供应商资源情况。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十七条', '本行在建立备用业务和办公场所时，应当配备业务操作和办公所需资源，并确保其能够迅速启用。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十八条', '本行建立灾备中心等备用信息技术资源和备用信息系统运行场所资源，并满足银监会关于数据中心相关监管要求。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第四章  业务连续性计划与资源建设', '第二十九条', '本行确保关键岗位备份人员可用，降低关键岗位人员无法及时履职风险。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第一条', '业务连续性计划演练'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十条', '本行定期开展业务连续性计划演练，检验应急预案的完整性、可操作性和有效性，验证业务连续性资源的可用性，提高运营中断事件的综合处置能力。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十一条', '本行每三年对全部重要业务开展一次业务连续性计划演练。在重大业务活动、重大社会活动等关键时点，或在关键资源发生重大变化之前，也应当开展业务连续性计划的专项演练。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十二条', '本行将外部供应商纳入演练范围并定期开展演练；同时，积极参加金融同业单位、外部金融市场、金融服务平台和公共事业部门等组织的业务连续性计划演练，确保应急和协调措施的有效性。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十三条', '本行对业务连续性计划的演练过程进行完整记录，及时总结、评估和改进。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第二条', '业务连续性管理评估与改进'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十四条', '本行建立业务连续性管理体系持续改进机制。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十五条', '本行每年对业务连续性管理体系的完整性、合理性、有效性组织一次自评估，或者委托第三方机构进行评估，并向经营管理层提交评估报告。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十六条', '本行应当每年对业务连续性管理文档进行修订，内容应当包含重要业务调整、制度调整、岗位职责与人员调整等，确保文档的真实性、有效性。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十七条', '本行在开发新业务产品上线前制定业务连续性计划并实施演练。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第五章  业务连续性演练与持续改进', '第三十八条', '本行每年对本行业务连续性管理进行审计，每三年开展一次全面审计，发生大范围业务运营中断事件后应当及时开展专项审计。审计的内容应当包括：业务影响分析、风险评估、恢复策略及恢复目标的合理性和完整性；业务连续性计划的完整性和可操作性；业务连续性计划演练过程及报告的真实性和有效性；业务连续性管理相关部门及人员的履职情况等。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第三十九条', '启动恢复计划（一）计划启动条件：规定在何种条件下启动灾难恢复计划/应急预案。（二）灾难恢复计划的启动条件，可包括（但不限于）：信息科技系统中断超过多长时间；设施受损并且超过多长时间无法使用；科技信息部应根据所制定《xxx银行信息科技应急预案》的灾难恢复计划/应急预案特点，自行确定其它适当的标准。（三）灾难恢复计划必备条件/前提条件：灾难恢复计划的必备条件/前提条件：即确保灾难恢复计划有效开展、达到预期目的必备条件，包括（但不局限于）：1．信息科技系统安全人员得到在其应急响应和恢复角色方面的培训；能够具备应急响应所需的知识、能力和技能。2．预防性控制（如监测系统、灭火系统和消防部门的协助等）在灾难期间可以完全发挥作用。3．计算机中心的设备，包括支持系统的部件连接在不间断电源（UPS）上，该UPS能够在电力故障期间提供较长时间的电力。4．应用软件和数据的最新备份完整并且在离站（离线或冷备）存储设施中可用。5．与信息科技系统有关的硬件、软件和通信供应商签订的服务协议可以支持紧急的业务流程恢复等。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十条', '应急响应小组本行应按照业务、功能及应急级别要求分层建立应急小组，以对信息安全事故做出及时响应。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十一条', '报告程序本行的全体员工均有义务及时报告信息安全事故或隐患。报告的流程为：第一报告人为直接主管领导，第二报告人为所在部门总经理，部门总经理遇到较大事故需要向分管行领导汇报，重大事故由分管行领导需要向行长汇报，特殊事件需要与外部的联系，如当地公安局、消防局、人民银行、银监分局等。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十二条', '初步响应一旦发生信息安全事故，应做出及时响应，以降低事故影响，保障生命财产安全。响应时应遵循“人员安全第一”的原则。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十三条', '分析与检测应急响应小组相关人员应通过多种技术和程序机制来获悉安全突发事件的有关信息。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十四条', '升级程序（一）应急响应小组负责人应根据检测结果、相关的历史数据及经验对安全事件进行分析和评估，以确定是否符合计划的启动条件，如符合计划的启动条件则启动灾难恢复计划。（二）应急计划启动根据灾难所影响的范围和所涉及的部门，需要相应的审批流程。对于涉及多个部门的灾难需要金融创新管理委员会负责人审批，对于涉及某一个部门的灾难恢复启动由科技信息部总经理批准，并上报金融创新管理委员会。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十五条', '取证安排若需取证，则应做出具体安排：内部：相关人员应尽量保留信息安全事故的原始记录，以配合取证。外部：若需要，则应与外部机构及时取得联系，如公安机关或保险公司。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十六条', '公关管理发生安全事件之后本行在公共关系管理方面的安排：总行由有关科技信息部门配合行政办公室进行。公共关系管理方面包括：与顾客、银监局、人民银行、媒体、警察、消防和其他机构等的联系。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十七条', '回退程序发生安全事故之后，为了维持信息科技系统基本需求的安排。如使用其他设备临时顶替或从供应商处获得应急响应所需的设备及服务等。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第六章 信息系统恢复', '第四十八条', '恢复程序（一）恢复程序：即安全事故发生之后采取相应应急预案中的恢复程序进行恢复，以在本行可容忍的中断时间内恢复正常运作，恢复过程中严格按照相应的应急流程进行恢复，以防止在恢复过程中新的安全问题出现。（二）恢复程序的组织原则上按照相应应急预案建立相应的组织结构，一种方式为：在涉及多个部门的情况下，简单地列出所有的恢复目标，根据每一目标进行计划分解，同时明确相应的部门/人员的责任以及任务。另一种方式为：在某部门内根据相应的功能，明确相应的部门/人员的责任以及任务。（三）各系统在制定相应应急程序时，应明确恢复步骤和指令，部门/人员按照这些指令进行恢复操作。各种操作之间的相互关系也必须加以明确说明。所有的指令和说明必须明白无误，以免因可能引起误解或不明了而导致时间损失。（四）恢复程序中还包括报告阶段。这个阶段的目的是回顾并整理安全事件的各种相关信息，尽可能地把所有情况记录在文档中。这些记录的内容，不仅对有关部门的其它工作具有重要意义，而且对将来应急工作的开展也是非常重要的积累。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第七章  系统连续性管理', '第四十九条', '测试、评审与更新（一）测试1．应急预案的测试：为验证应急预案的有效性，应定期或不定期地对其进行测试，检测应急预案的可操作性和有效性，在测试过程中，需要根据可操作性和有效性不断进行评审和更新。2．测试的方法可包括：（1）计划评审。（2）采用中断实例讨论业务恢复的安排。（3）供应商设备和服务测试（确保所提供的外部服务和产品符合合同的承诺）。（4）全面演习（测试组织、成员、设备、设施和过程是否能应付中断）。（二）评审与更新计划的维护与更新：制定相应的维护和更新计划，分配职责，以保持灾难恢复计划持续的有效性。计划的更新一般定期进行，特殊情况下应增加更新的频次。特殊情况包括但不局限于：1．新增重要信息资产。2．系统大规模升级。3．场所、设施和资源的变化。4．适用法律法规的变更。5．信息科技系统流程较大变更。6．组织机构、技术发生重大变更。7．发生重大信息安全事故。8．本行认为有必要的其他情况。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第七章  系统连续性管理', '第五十条', '培训、意识及能力（一）培训内容包括业务连续性程序培训、应急预案培训、业务流程培训。（二）培训必须制度化，培训在应急预案发生变化、业务流程发生变化等条件下，需要进行培训。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第七章  系统连续性管理', '第五十一条', '计划管理对本行建立的各种灾难恢复计划进行管理，该管理至少应满足以下两个要求：（一）在需要启动灾难恢复计划时，可以快速获取的灾难恢复计划；（二）因为灾难恢复计划中包含了本行的一些敏感信息，因此应妥善保管，防止未经授权的泄漏和访问。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第七章 附 则', '第五十二条', '本制度自通过之日起试行。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第七章 附 则', '第五十三条', '本制度未尽事宜，按国家有关法律、法规的规定执行；本制度如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '风险管理部', '《业务连续性管理制度》', '第七章 附 则', '第五十四条', '本制度由xxx银行风险管理部负责解释和修订。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第一章  总则', '第一条', '为规范xxx银行(以下简称“本行”)信贷资产风险分类工作，揭示信贷资产的风险程度，真实、全面、动态地反映信贷资产的质量，树立审慎经营、风险管理理念，提高信贷管理水平，为充分提取损失准备金、增强抗风险能力提供依据，根据银监会《贷款风险分类指引》（银监发[2007]54号）、《农村合作金融机构信贷资产风险分类指引》（银监发[2006]23号）、《农村银行机构公司类信贷资产风险十级分类指引（试行）》（银监办发[2009]284号）、省联社《江苏省农村信用社信贷资产风险分类实施方案》（苏信联发[2013]76号）等规定，特制定本细则。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第一章  总则', '第二条', '本细则适用于本行所属的各支行（包括公司业务部、小微业务部，以下简称“支行”）。适用于各类信贷资产，其中表内信贷资产包括各项贷款、贴现、信用卡透支、信用垫款等，表外信贷资产包括银行承兑汇票、担保、贷款承诺等。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第一章  总则', '第三条', '细则所指的信贷资产分类，是按风险程度将贷款划分为不同档次的过程。通过分类应达到以下目的：㈠促使本行树立审慎经营、风险管理理念；㈡揭示贷款的实际价值和风险程度，真实、全面、动态地反映贷款的质量；㈢发现贷款发放、管理、监控、回收以及不良贷款管理中存在的问题，加强信贷管理；㈣为提取充足的贷款损失准备金提供依据。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第一章  总则', '第四条', '分类原则：㈠风险原则。风险分类应以贷款内在风险为主要依据，逾期情况只作为重要参考因素。内在风险是指潜在的，已经发生但尚未实现的风险。㈡真实原则。本行应当以借款人的财务状况、经营成果、现金流量、信用记录为主要依据，对各类信贷资产准确分类，真实反映其风险价值。㈢审慎原则。本行要按照制度要求，通过对影响债务人偿还债务可能性的诸多因素的定性与定量分析评估，合理划分风险类别。介于相邻类别之间的贷款原则上应归入低级档次。㈣灵活原则。信贷资产应逐笔分类。同一借款人有多笔贷款，且条件基本相同，在不影响总的分类结果的前提下，可将多笔贷款合并分类。同一笔贷款不得进行拆分分类。㈤实时分类及动态管理原则。充分发挥省联社新一代信贷风险管理系统的科技支撑作用，及时、动态地对信贷资产进行形态认定和调整，新发放授信业务次日直接认定为正常，15日后30日内重新认定，当月月底前完成新发放贷款的系统分类认定工作，保证系统电子明细台账与汇总月报相符；对借款人风险状况有明显变化需要进行形态调整的由支行提出申请报总行风险管理部门审核同意后及时调整，但对全部存量贷款至少每季一次进行重新认定，于季末月（即3、6、9、12月份）前10个工作日内完成上报审批及形态调整工作。监管机构、审计部门以及上级管理部门要求进行重新检查的信贷资产，应实时进行重新分类认定。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第二章  贷款风险分类的依据和核心定义', '第五条', '分类依据：本行要通过各种现场查阅和非现场分析手段，获取借款人的财务、现金流量、非财务和担保各方面信息，将影响借款人还款能力的各类因素评估结论，作为判定贷款类别的主要依据，并注重第一还款来源。 ㈠财务状况的评估是指本行在对借款人经营状况和资金实力实地调查了解的基础上，对借款人财务报表中有关数据资料进行确认、比较，重点研究和分析借款人长短期偿债能力、盈利能力、营运能力和现金流量，综合评估借款人的财务状况。㈡现金流量分析是指本行根据借款人现金流量表中现金及现金等价物的信息，评估借款人产生、使用现金和现金等价物的能力、时间和确定性，判断借款人经营活动和投资、筹资活动的净现金流量变化对还款能力的影响。㈢担保分析是指本行对由借款人或第三人提供的债权保障措施进行分析，分为保证、抵押和质押三种方式。主要从法律上的有效性、价值上的充足性、担保续存期间的安全性和执行上的可变现性进行评估，判断担保作为第二还款来源对借款人还款能力的影响。对抵（质）押物的评估，有市场的按市场价格定价；没有市场的按同类抵（质）押物最低价格计算。㈣非财务因素包括借款人的行业风险因素（包括成本结构、行业的成长阶段、行业的经济周期性、行业的盈利性和依赖性、产品的替代性、法律政策、经济和技术环境等）、经营风险因素（包括借款人规模、所处发展阶段、产品多样化程度、经营策略、产品与市场分析、生产与销售环节分析等）、管理风险因素（包括借款人组织形式、管理层素质和经验、管理层的稳定性、员工素质等）、自然社会因素、还款记录（含其他银行偿还记录）、还款意愿、贷款偿还的法律责任以及本行的信贷管理。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第二章  贷款风险分类的依据和核心定义', '第六条', '贷款风险分类考虑的主要因素是：㈠借款人的还款能力；㈡借款人的还款记录；㈢借款人的还款意愿；㈣信贷资金的使用效益或贷款项目的盈利能力。㈤贷款的担保；㈥贷款偿还的法律责任；㈦本行的信贷管理水平。借款人的还款能力是一个综合概念，包括借款人现金流量、财务状况、影响还款能力的非财务因素等。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第二章  贷款风险分类的依据和核心定义', '第七条', '对贷款进行分类时，要以评估借款人的还款能力为核心，把借款人的正常营业收入作为贷款的第一还款来源，贷款的担保作为第二还款来源。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第二章  贷款风险分类的依据和核心定义', '第八条', '核心定义：本行按照风险程度将企事业单位贷款分为十级，分别为：正常1、正常2、正常3，关注1、关注2、关注3，次级1、次级2，可疑，损失。十级分类与原信贷资产风险分类的对应关系：正常1、正常2、正常3对应原信贷资产风险分类的正常类；关注1、关注2、关注3对应原信贷资产风险分类的关注类；次级1、次级2对应原信贷资产风险分类的次级类；可疑、损失分别对应原信贷资产风险分类的可疑、损失类。次级1、次级2、可疑和损失合称为不良信贷资产。㈠正常1：借款人经营状况良好，连续保持良好的信用记录。借款人在行业中享有较高声誉，产品市场份额较高，所在行业前景好。借款人能够履行合同，有充分把握按时足额偿还贷款本息。㈡正常2：借款人经营状况稳定，连续保持良好的信用记录。借款人处于良性发展状态，规模适中，所在行业前景好。借款人能够履行合同，有能力按时足额偿还贷款本息。㈢正常3：借款人经营状况稳定，连续保持良好的信用记录。所在行业发展具有一定不确定性。借款人能够履行合同，没有足够理由怀疑贷款本息不能按时足额偿还。㈣关注1：借款人有能力偿还贷款本息，但借款人经营稳定性一般，对借款人的持续偿债能力需加以关注。㈤关注2：借款人目前有能力偿还贷款本息，但借款人经营稳定性和所在行业一般，存在可能影响借款人偿债能力的不利因素。㈥关注3：借款人目前有能力偿还贷款本息，但借款人经营效益、经营性现金流量连续下降，存在可能影响借款人偿债能力的不利因素。㈦次级1：借款人目前的还款能力不足或抵押物不足值。此类贷款存在影响贷款足额偿还的明显缺陷，如果这些缺陷不能及时纠正，银行贷款遭受损失的可能性较大。㈧次级2：借款人的还款能力出现明显问题，完全依靠其正常营业收入无法足额偿还贷款本息，即使执行担保，也可能造成一定损失。㈨可疑：借款人无法足额偿还贷款本息，即使执行担保，也肯定要造成较大损失。㈩损失：在采取所有可能的措施和一切必要的法律程序后，贷款本息仍然无法收回，或只能收回极少部分。本行按照风险程度将自然人贷款分为正常、关注、次级、可疑和损失五个档次，其中后三类合称为不良贷款。㈠正常贷款：借款人能履行合同，没有足够理由怀疑贷款本息不能按时足额偿还。㈡关注贷款：尽管借款人目前有能力偿还贷款本息，但存在一些可能对偿还产生不利影响的因素。㈢次级贷款：借款人还款能力出现明显问题，完全依靠其正常经营收入无法足额偿还贷款本息，即使执行担保，也可能会造成一定损失。㈣可疑贷款：借款人无法足额偿还贷款本息，即使执行担保也肯定要造成较大损失。㈤损失贷款：在采取所有可能的措施或一切必须的法律程序之后，本息仍然无法收回，或只能收回极少部分。正常类和关注类贷款的预计损失率为0%，次级类贷款的预计损失率在25%（含）以下，可疑类贷款的预计损失率在25%（不含）-90%（含）之间，损失类贷款的预计损失率在90%（不含）以上。实际操作中要紧扣核心定义，严格按相关标准进行分类，预计损失率仅作为分类结果验证参考。预计损失率=1-可受偿金额/贷款本息，可受偿金额=第一还款来源可还贷金额+第二还款来源可还贷金额-实现债权所需的费用。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第三章  贷款风险分类对象的划分', '第九条', '贷款根据借款对象不同，分为企事业单位贷款、自然人贷款两大类型，其中自然人贷款执行五级分类标准，企事业单位贷款执行十级分类标准。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第三章  贷款风险分类对象的划分', '第十条', '自然人贷款包括城镇居民、农户、个体工商户等各类以自然人为借款人申请立据的贷款。自然人贷款按照额度大小分为小额自然人贷款和大额自然人贷款。其单户余额50万元（含）以下的贷款为小额自然人贷款，单户余额在50万元以上的贷款为大额自然人贷款。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第三章  贷款风险分类对象的划分', '第十一条', '企事业单位贷款的借款人包括经工商行政管理机关登记或主管部门核准的企事业法人（含其授权借贷的分支机构），以及不具备法人资格的其他经济组织（包括合伙企业、个人独资企业、经济合作组织等）。企事业单位贷款按照额度大小分为小额企事业单位贷款和大额企事业单位贷款。其单户金额在500万元（含）以下的为小额企事业单位贷款，单户金额在500万元以上的为大额企事业单位贷款。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第四章  贷款风险分类的程序', '第十二条', '贷款风险分类的前期准备：㈠收集、整理信贷档案。信贷档案是对贷款发放、管理、收回等完整过程的真实记录。必须建立和完善信贷档案管理制度，为每一个借款人建立起完整的档案，为贷款风险分类作好准备。客户经理有责任保证客户信贷档案的完整和真实，如有缺漏，应以书面形式说明。1、自然人贷款档案应包括的内容：⑴小额自然人贷款档案的建立，包括以下内容：①借款人姓名、身份证件号码、住址、联系方法等；②从事生产经营活动的主要内容、收入状况、家庭实有资产状况等；③还款的历史记录；④担保情况；⑤本行信贷客户经理意见。⑵大额自然人贷款档案应包括以下内容：①借款人及担保人的基础资料，包括借款人及担保人姓名，相关身份证件、营业执照复印件等，征信系统信用报告；②借款人及担保人的相关财务资料，包括资产证明，各类财务报表，银行对账明细等；③信贷重要文件档案，包括借款申请书、配偶承诺、主从借款合同及借据复印件、相关抵质押权证复印件及价值确认书等；④信贷“三查”档案资料，包括贷款调查报告、当年度贷后检查报告、面谈记录及贷款支付手续、企业相关购销合同及自主支付用途分析资料等；⑤贷款五级分类资料，包括五级分类工作底稿、分类认定表、分类认定报告等。2、企事业单位贷款客户档案应主要包括以下基本内容：⑴信贷企业基础资料档案，包括：营业执照（三证合一）、开户许可证、特种行业经营许可证等企业拥有的各种资质证书复印件，人民银行核发的贷款卡复印件，公司章程及企业验资报告(或注册资本来源的证明材料)复印件，企业法定代表人证明书原件、身份证复印件及授权委托书原件、委托代理人身份证复印件，企业在所有金融机构的开户情况（账户性质、账号、开户行名称），企业内部评级表或外部评级证书等；⑵信贷企业（含担保人）财务资料，包括：借款企业纳税证明，借款企业分户账，借款人固定经济收入或偿还能力的证明文件，借款企业及担保企业各种报表如资产负债表、损益表、现金流量表、纳税申报表、水电费等，企业其他财务信息资料；⑶信贷重要文件档案，包括：企业授信申请书，征信系统查询授权书及查询资料，总行审查、审批（备案）资料，借款申请书，借款企业股东会或董事会同意借款决议，担保企业董事会同意担保决议，股东会或董事会同意抵（质）押决议，借款合同、保证合同、抵（质）押合同、借据，抵（质）押物清单及权利证书(房屋所有权证及他项权证、土地使用权证及他项权证）复印件，资产抵（质）押价值评估报告或抵押值确认表等；⑷信贷企业“三查”资料，包括：贷款调查报告、当年度贷后检查报告、面谈记录及贷款支付手续、企业相关购销合同及自主支付用途分析资料等；⑸贷款风险分类资料，包括：风险分类工作底稿、分类认定表、分类认定报告等。㈡开展现场调查。由客户经理深入贷款户，通过现场调查，了解和掌握借款人生产经营的真实情况，并根据调查情况，调整企业资产负债表、损益表和现金流量表，并按实事求是的原则写出认定报告，作为贷款风险分类的依据。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第四章  贷款风险分类的程序', '第十三条', '贷款风险分类的步骤：㈠阅读信贷档案，填写《分类工作底稿》。㈡审查信贷资产的基本情况。基本情况一般包括的内容有：贷款目的、还款来源、资产转换周期、还款记录等。㈢确定还款可能性。通过财务分析、现金流量分析、非财务因素分析、担保分析，来进行综合判断偿还的可能性。㈣填写《认定表》，初定分类结果。严格按五类（企事业客户为十类）信贷资产核心定义提出初步分类意见。㈤信贷讨论。支行贷款风险分类工作小组或贷款会办小组通过对《认定表》中贷款资料的完整性、真实性和初分结果的准确性进行审核后，由支行负责人签具审核意见。㈥支行上报初步分类结果。㈦本行风险管理部门对支行初分情况进行调查、审查、汇总，报本行风险与内控股管理委员会确认分类结果。㈧对分类争议较大贷款以及一定额度自然人贷款50万元（不含50万元）以上，企事业贷款500万元（不含500万元）以上的大额贷款的损失类贷款，由风险管理部门提出分类意见，本行风险与内控股管理委员会进行逐笔认定。㈨可疑类和损失类贷款不论上调、下调必须由本行风险与内控股管理委员会逐笔确定。单户金额超过200万元（含）的自然人贷款、超过2000万元（含）的企事业单位贷款，如计入损失类，必须上报省联社最终认定。㈩针对分类过程中发现的问题，应及时采取相应措施，降低信贷资产风险，并对责任人实施相应处罚。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第四章  贷款风险分类的程序', '第十四条', '自然人贷款分类尽可能参照上述程序，由支行分片客户经理逐笔填写《小额自然人贷款分类认定表》和《大额自然人贷款分类认定表》进行初分，运营主管进行审查，支行负责人最终认定初分结果。银行卡透支及住房按揭贷款、汽车消费贷款参照小额自然人贷款分类程序操作。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第五章  贷款风险分类各步骤的标准', '第十五条', '阅读信贷档案，填写工作底稿。在此步骤中需重点考察以下项目：㈠信贷档案的健全性；㈡所有档案填写内容或其他文字表述的一致性、合理性和合法性；㈢客户经理根据情况调整档案的及时性。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第五章  贷款风险分类各步骤的标准', '第十六条', '审查信贷资产的基本情况。主要审查以下项目：㈠贷款目的：合同用途与实际用途是否一致；贷款目的是否与业务有关。㈡还款来源：合同还款来源与实际可能还款来源（现金流量、资产转换、资产销售、处置抵质押物、重新筹集资本、保证人代偿等）是否一致；主营业务收入占实际可能还款来源的比率。㈢资产转换周期：分析贷款使用过程中的各种相关信息及其影响。㈣还款记录。通过逾期时间、欠息天数和金额、逾期或欠息原因分析，判断借款人过去和现在以及未来的还款意愿和还款行为。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第十七条', '有下列情况之一的划入正常1级贷款：㈠借款人经营实力和财务实力雄厚，能够抵御和承受重大内外部负面变化，偿债能力和盈利能力很强，经营净现金流稳定且相对债务十分充足。㈡借款人所处行业前景很好，借款人处于行业垄断或主导地位，竞争优势十分明显。㈢借款人资信状况极佳，完全能够满足融资需要，融资成本低，融资渠道广泛，具有较强资本市场融资能力。㈣借款人信用记录良好，还款意愿很好，在本行及其他金融机构均无违约纪录。㈤按照本行规定条件和程序办理的低风险信贷业务，本金利息均未逾期。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第十八条', '有下列情况之一的划入正常2级贷款： ㈠借款人经营实力和财务实力很强，能够抵御和承受较大的内外部负面变化，偿债能力和盈利能力强，经营净现金流稳定且相对债务充足。㈡借款人所处行业前景好，处于行业领先地位，竞争优势明显。㈢借款人资信状况很好，融资能力较强，融资渠道较多，能够满足融资需要。㈣借款人信用记录良好，还款意愿强，近三年内在本行及其他金融机构均无违约记录。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第十九条', '有下列情况之一的划入正常3级贷款㈠借款人经营实力和财务实力强，能抵御和承受一定的内外部负面变化，偿债能力和盈利能力较强，经营净现金流稳定且相对债务较为充足。㈡借款人所处行业前景较好，借款人处于行业上游地位，具有一定竞争优势。㈢借款人资信状况良好，融资渠道和融资能力基本能满足融资需要。㈣借款人在本行信用记录良好，还款意愿良好，近两年内一直能够正常还本付息。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第二十条', '有下列情况之一的一般划入关注1级贷款：㈠宏观政治、经济、市场、行业以及法律、法规等的变化对借款人的经营产生负面影响。㈡借款人从事固有风险很大的行业（如：从事证券投资行业的市场风险高，可能会对企业的现金流造成很大的影响；高科技行业借款人的新技术可能尚处于研发阶段，最终能否形成产品尚不能确定等），最终还款能力容易因市场波动或产品的成败出现较大幅度的负面变动。㈢企业整体盈利能力下降。㈣企业生产或经营环节出现明显问题。㈤企业核心项目未能如期推进或成本控制超标。㈥借款人现金流紧张，经营净现金流明显减少或明显波动，经营净现金流为负并持续减少。㈦本金和利息虽尚未逾期，但借款人有利用兼并、重组、分立等形式恶意逃废银行债务的嫌疑。㈧企业改制、股权或管理层发生重大变化，对本行债权可能产生负面影响。㈨借款人还款意愿较差，不与本行积极合作。㈩银行对贷款管理存在瑕疵，如未能及时了解借款人经营及财务状况等。(十一)难以获得充分的资料对中长期项目的进展情况及其现金流量状况作出定期更新评估，因此很难确定项目是否能够产生明确的现金流在到期时作为还款来源。（十二）保证人的资信状况出现疑问，出现押品价值下降，或开始对押品失去控制的迹象。（十三）借款人的主要股东、关联企业或母子公司等发生了重大的负面变化（发生经济纠纷、诉讼，或退出），并可能影响借款人的偿债能力。（十四）借款人股利分配行为与盈利状况不匹配，可能影响借款人最终的还款能力。（十五）借款人的资产处置、履行担保债务等行为对借款人持续经营产生负面影响，并可能影响借款人最终的还款能力。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第二十一条', '有下列情况之一的一般划入关注2级贷款：㈠宏观政治、经济、市场、行业以及法律、法规等的变化对借款人的经营产生较大负面影响。㈡借款人的借款总额在短期内激增并与其业务发展不成比例，且借款人不能提供合理的解释，以致有理由怀疑借款人的财务状况和偿债能力。㈢企业整体盈利能力明显下降，并影响持续偿债能力。㈣企业生产或经营环节出现严重问题。㈤企业核心项目推进出现问题，或成本控制严重超标。㈥借款人现金流较为紧张，经营净现金流为负并持续减少，或出现现金流不能覆盖到期债务的迹象。㈦借款人提供的财务资料存在明显瑕疵（如: 被出具保留意见的审计报告；存在对借款人财务状况产生负面影响的公开信息；监管机构因一些负面消息或从常规调查中发现问题，进而对借款人进行非常规调查），可能影响本行对借款人还款能力的评价。㈧借款人、担保人信用等级下降，或经营已经开始出现问题；押品价值出现实质性下降。㈨本金或者利息逾期60天(含)以内。㈩企业改制、股权或管理层发生重大变化，可能对本行债权产生负面影响。（十一）难以获得资料对中长期项目的进展情况及其现金流量状况作出定期更新评估，因此很难确定项目是否能够产生明确的现金流在到期时作为还款来源。（十二）借款人的主要股东、关联企业或母子公司等发生了重大的负面变化（发生经济纠纷、诉讼，或退出），影响借款人偿债能力的可能性很大。（十三）借款人的资产处置、履行担保债务等行为对借款人持续经营产生较大负面影响，并可能影响借款人最终的还款能力。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第二十二条', '有下列情况之一的一般划入关注3级贷款：㈠宏观政治、经济、市场、行业以及法律、法规等的变化对借款人的经营产生极大负面影响。㈡借款人的借款总额在短期内激增并与其业务发展不成比例，且借款人不能提供合理的解释，有充足理由怀疑借款人的财务状况和偿债能力。㈢企业亏损，影响借款人偿债能力。㈣企业生产或经营环节出现极为严重的问题。㈤企业核心项目推进出现严重问题，或成本控制极度超标，影响借款人偿债能力。㈥借款人经营净现金流已不能满足债务需要，其通过正常经营偿还贷款本息的能力已出现问题，但通过减少投资、处置非核心资产、对外筹资等可以保证净现金流基本满足偿还债务需要。㈦借款人财务状况不佳，两项或两项以上关键性财务指标（如利息保障倍数、资产负债率、资产或资本利润率、流动比率、资产周转率等）低于行业平均水平或有较大幅度的下降，影响借款人偿债能力。㈧借款人经营管理存在较为严重的问题（如未按规定用途使用贷款，贷款发放严重偏离审批条件），如问题继续存在可能影响贷款的偿还。㈨有证据表明借款人有利用兼并、重组、分立等形式恶意逃废银行债务的可能。㈩项目法人贷款项目出现重大不利于贷款偿还的调整，如政策调整、投资缺口、投资延缓、工期延长、利率汇率等方面的负面重大变动。（十一）担保人信用等级严重下降，或经营已经开始出现较明显的问题；押品价值出现实质性下降，低于本行担保比率要求。（十二）借款人对本行或其他银行贷款出现违约，或涉及金额较大的经济法律纠纷，或高级管理人员涉及法律诉讼，可能对贷款偿还能力产生负面影响。（十三）本金或者利息逾期90天(含)以内或表外业务30天（含）以内的垫款。（十四）企业改制、股权或管理层发生重大变化，可能对本行债权产生重大负面影响。（十五）借款人还款意愿极差，不与本行积极合作,拒绝本行贷后管理的相关工作。（十六）本行对贷款管理存在严重漏洞，如借款人信贷档案不齐全，重要文件或证据遗失等，可能对还款构成实质性负面影响。（十七）无法有效对中长期项目的进展情况及其现金流量状况作出定期更新评估，无法确定项目是否能够产生明确的现金流在到期时作为还款来源。（十八）借款人的主要股东、关联企业或母子公司等发生了极大的不利变化（发生经济纠纷、诉讼，或退出），并可能影响借款人的偿债能力。（十九）借款人股利分配行为与盈利状况严重不匹配，可能影响借款人最终的还款能力。（二十）借款人的资产处置、履行担保债务等行为对借款人持续经营产生严重影响，并影响借款人最终的还款能力。（二十一）借款人还款意愿较差，不与本行积极合作,不配合贷后管理工作。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第二十三条', '有下列情况之一的一般划入次级1级贷款：（一）借款人偿债能力和盈利能力较弱，净现金流已不能满足偿还债务需要，预计在较短时间内难以改善，且难以立即获得新的资金来源。（二）借款人仍能维持生产经营，但贷款的全部偿还除依靠生产经营产生的现金流量之外，还需要通过执行担保或动用其他还款来源。（三）借款人借款本息的逾期时间虽不到90天，但已经出现亏损，净现金流量为负值。（四）借款人在其他金融机构有逾期90天以上的贷款，而且金额较大。（五）借款人未能提供最新的财务数据，且拒绝提供合理解释。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第二十四条', '有下列情况之一的一般划入次级2级贷款：（一）借款人出现持续财务困难，影响到其业务的持续经营，表现为：出现支付困难，并且难以获得新的资金；不能偿还其他债权人的债务。 （二）借款人勉强维持主要生产经营，贷款的偿还除依靠生产经营外，还需要通过执行担保或依赖其他还款来源，但预计会造成的损失较少。（三）借款人陷入重大经济法律纠纷，或借款人未能清偿司法机关已经裁定的债务，或借款人拖欠应缴纳的税款且金额较大。（四）借款人内部管理十分混乱，影响债务的及时足额清偿。（五）借款人在其他金融机构贷款被划为可疑类；（六）本金或利息逾期91天至180天（含）的贷款或表外业务31天至90天（含）的垫款。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第二十五条', '有下列情况之一的一般划入可疑类：（一）借款人处于停产、半停产状态，固定资产贷款项目处于停、缓建状态。（二）借款人实际已资不抵债。（三）借款人进入清算程序。（四）借款人或其法定代表人涉及重大案件，对借款人的正常经营活动造成重大影响。（五）借款人改制后，难以落实本行债务或虽落实债务，但不能正常还本付息。（六）经过多次谈判借款人明显没有还款意愿。（七）已诉诸法律追收贷款。（八）借款人在其他金融机构贷款被划为损失类。（九）本金或利息逾期181天以上的贷款或表外业务91天以上的垫款。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第六章  企事业单位贷款分类标准', '第二十六条', '有下列情况之一的一般划入损失类贷款:(一)符合财政部《金融企业呆账核销管理办法》（财金〔2015〕60号）规定的被认定为呆账条件之一的贷款均划入损失类。本行经采取所有可能的措施和实施必要的程序之后，符合下列条件之一的债权可认定为呆账：1.借款人依法宣告破产、关闭、解散或撤销，相关程序已经终结，本行对借款人财产进行清偿，并对担保人进行追偿后，仍无法收回的债权；法院依法宣告借款人破产后1年以上仍未终结破产程序的，本行对借款人和担保人进行追偿后，经法院或破产管理人出具证明，仍无法收回的债权。2.借款人死亡，或者依照《中华人民共和国民法通则》的规定宣告失踪或者死亡，或者丧失完全民事行为能力或劳动能力，本行依法对其财产或者遗产进行追偿，并对担保人进行追偿后，仍无法收回的债权。3.借款人遭受重大自然灾害或者意外事故，损失巨大且不能获得保险赔偿，或者以保险赔偿后，确实无力偿还部分或者全部债务，本行对其财产进行清偿，并对担保人进行追偿后，仍无法收回的债权。4.借款人已完全停止经营活动，被县级及县级以上工商行政管理部门依法注销、吊销营业执照，本行对借款人和担保人进行追偿后，仍无法收回的债权。5.借款人已完全停止经营活动或下落不明，未进行工商登记或者超过3年未履行企业年度报告公示义务的，本行对借款人和担保人进行追偿后，仍无法收回的债权。6.借款人触犯刑法，依法被判处刑罚，导致其丧失还款能力，其财产不足归还所借债务，又无其他债务承担者，本行经追偿后，仍无法收回的债权。7.由于借款人和担保人不能偿还到期债务，本行诉诸法律，借款人和担保人虽有财产,但对借款人和担保人强制执行超过1年以上仍无法收回的债权；或借款人和担保人虽有财产，但进入强制执行程序后，由于执行困难等原因，经法院裁定终结或终止(中止)执行程序的债权；或者借款人和担保人无财产可执行，法院裁定执行程序终结或终止(中止)的债权。8.本行对债务人诉诸法律后，或债务人按照《破产法》相关规定进入重整或和解程序后，重整协议或和解协议经法院裁定通过，根据和解协议或重整协议，本行无法追偿的剩余债权。9.对借款人和担保人诉诸法律后，因借款人和担保人主体资格不符或消亡等原因，被法院驳回起诉或裁定免除(或部分免除)债务人责任；或因借款合同、担保合同等权利凭证遗失或丧失诉讼时效，本行经追偿后仍无法收回的债权。10.由于上述(一)至(九)项原因借款人不能偿还到期债务，本行依法取得抵债资产，抵债金额小于贷款本息的差额，经追偿后仍无法收回的债权。11.开立信用证、办理承兑汇票、开具保函等发生垫款时，凡开证申请人和保证人由于上述(一)至(十)项原因，无法偿还垫款，本行经追偿后仍无法收回的垫款。12.本行经批准采取打包出售、公开拍卖、转让、债务减免等市场手段处置债权后，根据转让协议或者债务减免协议，其处置回收资金与债权余额的差额。13.对于单户余额在5万元及以下的对公贷款，经追索1年以上，仍无法收回的债权。14.对于单户余额在1万元及以下的个人无抵押(质押)贷款、抵押(质押)无效贷款或抵押(质押)物已处置完毕的贷款，经追索1年以上，仍无法收回的债权。15.因借款人、担保人或者其法定代表人、实际控制人涉嫌违法犯罪，或因本行内部案件，经公安机关或检察机关正式立案侦察2年以上，本行对借款人、担保人或者其他还款义务人进行追偿后，仍无法收回的债权。16.本行对单笔贷款额在500万元及以下的，经追索1年以上，确实无法收回的中小企业和涉农不良贷款，可按照账销案存的原则自主核销；其中，中小企业标准为年销售额和资产总额均不超过2亿元的企业，涉农贷款是按《中国人民银行中国银行业监督管理委员会关于建立＜涉农贷款专项统计制度＞的通知》(银发〔2007〕246号)规定的农户贷款和农村企业及各类组织贷款。17.经国务院专案批准核销的债权。(二)借款人无力偿还贷款,即使处置抵押物(质押物)或向担保人追偿也只能收回很少的部分，预计贷款损失率超过90%。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第七章  大额自然人贷款分类标准', '第二十七条', '大额自然人贷款在对借款人的资信状况进行即时评定的基础上，按照本实施细则确定的矩阵分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第七章  大额自然人贷款分类标准', '第二十八条', '对借款人的资信状况必须在每次清分时按照优秀、较好、一般、不佳、恶化五个等级进行即时评定。其评定依据以下指标：㈠资产负债率小于60%；㈡家庭人均纯收入高于当地水平；㈢近三年固定资产逐年增加（至少不减少）；㈣生产经营正常，销售及货款回笼稳定；㈤借款人有较强的经营、管理能力，信誉好，还款意愿强，无吸毒、嫖娼、赌博、打架斗殴等不良行为；㈥担保状况较好，偿债能力较佳。以上六项指标全部符合要求的为优秀，一项指标不符的为较好，两项不符的为一般，三项不符的为不佳，四项以上不符的为恶化。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第七章  大额自然人贷款分类标准', '第二十九条', '从审慎原则出发，确定如下矩阵分类标准：'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第七章  大额自然人贷款分类标准', '第三十条', '对大额自然人贷款分类标准一定要在矩阵分析时重点考虑定性分析。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第八章  小额自然人贷款分类标准', '第三十一条', '小额自然人贷款主要依据借款人的信用评定等级、担保因素和逾期时间，结合核心定义通过矩阵方式进行分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第八章  小额自然人贷款分类标准', '第三十二条', '小额自然人信用等级依照以下指标评定：A级：本行的正常优质客户。①存贷业务频繁。②与本行发生信贷业务以来按期还本付息，无不良记录。③家庭人均纯收入达到当地平均水平以上。④自有资金占生产生活、经营所需资金60%以上。B级：本行的正常一般客户。①有稳定可靠收入来源。②与本行发生信贷业务以来无不良贷款记录。③家庭人均纯收入达到当地平均水平80%以上。④自有资金占生产生活、经营所需资金50%以上。C级：本行关注的客户。①有一定的收入来源。②发生不良记录但已归还的。③家庭人均纯收入达当地平均水平60%以上。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第八章  小额自然人贷款分类标准', '第三十三条', '根据评定情况，对照以下矩阵分类认定：㈠用评定等级为A级档次的，按照以下矩阵分类。㈡信用评定等级为B级档次的，按照以下矩阵分类。㈢信用评定等级为C级或未参加农户信用等级评定的，按照以下矩阵分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第八章  小额自然人贷款分类标准', '第三十四条', '对小额自然人贷款分类标准一定要在矩阵分析时重点考虑到定性分析。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第三十五条', '违反国家有关法律法规和贷款管理的有关制度规定、未经正常贷款审批而形成的信贷资产分类结果要下调一级，已划分为损失类的不再调整。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第三十六条', '银团、社团贷款的分类等级原则上由主办行或主办社认定，如果合同与协议本身有缺陷，其贷款至少为关注类。转让贷款谁反映谁分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第三十七条', '建设期项目贷款或在建工程贷款，如果各方面情况正常，预期经济效益可实现，可归为正常类；如出现不利于偿还的因素，经济收益受到一定影响的，可归为关注类；偿还受到严重影响的，至少归为次级类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第三十八条', '重组贷款是指本行由于借款人财务状况恶化，或无力还款而对借款合同还款条款作出调整的贷款。该类贷款，至少归为次级类，如果仍然逾期，或借款人仍然无力还贷，则至少归为可疑类。重组贷款分类档次至少在6个月内不得调高，6个月观察期结束后重新分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第三十九条', '借新还旧贷款，符合人民银行4条规定的（1、借款人活动经营正常，能按时支付利息；2、重新办理了贷款手续；3、贷款担保有效；4、属于周转性贷款。），至少归为关注类；仅是基于清收利息、减息还本、保全资产等目的的，至少归为次级类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十条', '恶意逃废债贷款，无论何种形式，至少归为次级类。并应在依法追偿后，按实际偿还能力进行分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十一条', '企业集团贷款分类。如果母子公司关系松散，特别是母公司无法控制子公司，或子公司享有足够的自主权，对子公司的贷款应以子公司的经营情况为主要分类依据；如母子公司关系密切，关联程度高，甚至存在控制与被控制的关系，分类时要兼顾考虑，子公司等级不得高于母公司。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十二条', '有下列情况之一的一般按以下分类：㈠分期还款或订立还款计划的贷款能及时性履行的至少归类为关注，不能及时履行的至少归类为次级或次级以下。㈡垒大户、化整为零,对手续合法完整、贷款主体运转正常、无欠息逾期现象的可归为关注类，对贷款主体和担保措施有缺陷、手续有瑕疵、有欠息逾期现象的一般归为次级类或可疑类。㈢涉案贷款应根据手续的合规性进行认定，至少归为可疑类，对经公安机关确认的涉案贷款应归为损失类。㈣执行终结、中止或领取债权凭证，应归纳为损失类；对虽未执行终结、中止，至少应归纳为可疑类。㈤失去诉讼、执行时效贷款，应归为损失类。㈥借款人、担保人长期外出，无法联系，至少归为可疑类以下。㈦收产、变现差额，存单质押扣款后差额归为损失类。㈧离岗职工贷款、责任人贷款、职工经办不合规贷款至少归为次级或次级以下。㈨借款手续不合规（借款人、担保出质人、抵押人、出质人未到场签字），应归为可疑或损失类。㈩政府及事业单位贷款，对手续合法完整、贷款主体运转正常、无欠息逾期现象的可归入关注类；对贷款主体和担保措施有缺陷、手续有瑕疵、有欠息逾期现象的一般归入次级类或可疑类。（十一）房地产开发企业贷款，可视其自有资金规模、开发业绩、现有项目的进展情况、储备项目情况、当地市场容量等因素分析。对资金紧张、开发历史短、空置房多的企业至少归为关注类或关注类以下；对以其他主体贷款，实际为房地产企业所用的贷款，参照上述要求分类并下调一级。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十三条', '呆账贷款，符合财政部、国家税务总局有关规定的，可简化手续，批量归为损失类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十四条', '以国债、金融债券、本机构存单、100%保证金作为质物的质押贷款，当贷款本金或利息逾期未超过90天，且质物归属不存在任何争议、质押手续完备、质押率低于90%时，一般划为正常贷款。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十五条', '信用卡透支可按照以下标准结合核心定义进行分类：正常类：持卡人能够按照事先约定的还款规则在到期还款日前（含）足额偿还应付款项。关注类：持卡人未按事先约定的还款规则在到期还款日足额偿还应付款项，逾期天数在1-90天（含）。次级类：持卡人未按事先约定的还款规则在到期还款日足额偿还应付款项，逾期天数在91-120天（含）。可疑类：持卡人未按事先约定的还款规则在到期还款日足额偿还应付款项，逾期天数在121-180天（含）。损失类：持卡人未按事先约定的还款规则在到期还款日足额偿还应付款项，逾期天数超过180天。特殊情况应当直接列入相应类别：（1）持卡人因使用诈骗方式申请、使用信用卡造成的风险资产，一经确认，应当直接列入可疑类或损失类。（2）因内部作案或内外勾结作案造成的风险资产应当直接列入可疑类或损失类。（3）因系统故障、操作失误造成的风险资产应当直接列入可疑类或损失类。（4）签订个性化分期还款协议后尚未偿还的风险资产应当直接列入次级类或可疑类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十六条', '住房按揭贷款和汽车消费贷款的分类标准：正常类：借款人在贷款期间能够正常还本付息。关注类：借款人连续违约期数达3次，或贷款本息逾期90天以内。次级类：借款人连续违约期数达4-6次，或贷款本息逾期91-180天以内。可疑类：借款人连续违约期数达7次以上，或贷款本息逾期181天以上。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十七条', '银行承兑汇票及其贴现分类，对照有关管理办法，手续完备有效的视为正常；手续有缺陷的视为关注；手续不完整的，有重大缺陷并足以造成不能顺利收款的划分为次级；手续有重大缺陷并造成承兑人解除付款责任的，可分为可疑。银行承兑汇票敞口部分视同贷款进行分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第九章  特殊贷款及其他信贷资产的分类标准', '第四十八条', '本行在对信用证、承兑、担保等表外信贷资产分类时，要将客户近期的表内业务分类情况作为重要参考依据。原则上，对客户表外信贷资产分类不得高于对其近期表内信贷资产业务分类等级。若客户表内信贷资产未出现不良，或客户尚无信贷资产，一般可直接将表外信贷资产划入正常或关注类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第四十九条', '信贷档案管理。本行必须建立和完善信贷档案管理制度，为每一个借款人建立起完整的档案。客户经理有责任保证客户信贷档案的完整和真实，如有漏缺，应以书面形式说明。档案经办人为各客户经理，管理责任人为信贷业务主管人员或专职信贷内勤。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十条', '信贷资产信息的管理。本行要建立能够及时监测信贷资产质量变化的信息管理制度和组织架构，并推行与之配套的完整、清晰、有效的岗位职责制度。在此基础上，重点加强对企事业单位贷款客户经营信息的管理。在贷前调查时，要把其能否提供及时、真实的财务会计报告作为贷款的必要条件；在贷款合同中，应明确贷款人在信息披露方面的义务；对于借款人会计不规范和管理制度不健全的，本行有权要求其在贷款期内限期改善；对于不采取有效措施改善会计内部控制或提供虚假会计信息的，本行可依据相关法规和贷款合同约定，采取提前收贷、减少授信额度、拒绝提供新贷款等措施进行信贷制裁。本行应当明确专人负责，采取定期现场审查、不定期回访等方式，以了解、分析监测借款人经营情况、财务状况以及其他非财务信息。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十一条', '贷款风险分类的责任管理。信贷资产风险分类管理要按照“落实责任、动态调整、定期分析、科学考核”的要求进行。㈠风险与内控股管理委员会，全面领导信贷资产分类工作的开展，并对分类结果负最终认定责任。㈡本行客户经理要随时监测、分析经办贷款的风险情况，认真收集与贷款有关信息，及时补充信贷档案，并及时向本单位负责人提出调整形态发生变化的贷款类别的建议。本行的信贷资产风险分类工作要与日常信贷管理工作紧密结合，适时组织对借款人的经营情况和偿债能力的分析。㈢信贷资产分类结果认定的权限管理。1、由支行确定所有信贷资产的初分结果。2、由总行认定的:⑴自然人贷款的次级、可疑、损失三类。对单笔超过200万元（含）计入损失类的，必须上报省联社最终认定。⑵企事业单位贷款的次级、可疑、损失三类。对单笔超过2000万元（含）计入损失类的，必须上报省联社最终认定。⑶本细则所列的特殊贷款和其它信贷资产的风险分类。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十二条', '各支行行长（含公司业务部、小微业务部负责人）是本单位贷款风险分类第一责任人。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十三条', '支行（含公司业务部、小微业务部）职责：㈠收集和完善信贷档案；㈡对借款人的调查和核实；㈢负责各项基础资料和工作底稿、报表的填写；㈣对贷款按规定进行初分；㈤按照贷款风险分类的标准建立信贷台账，随时监测、分析经办贷款的风险情况；㈥贷款形态发生变化的，由支行根据认定标准实时、动态地进行调整，其中正常类、关注类贷款下调至不良以及次级类、可疑类和损失类的上调或下调均实行报批制，由风险管理部风险分类岗负责审核，凡由正常贷款调至不良的贷款，在六个月的观察期内其分类形态不得再回调至正常，六个月期满，确因客户自身原因好转，经支行提出申请，风险管理部审核把关，经本行风险管理与关联交易控制委员会确认后可重新调整为正常，同时对全部存量贷款至少每季一次进行重新认定，于季末前10个工作日内完成上报审批及形态调整工作。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十四条', '风险管理部职责:负责信贷资产分类的日常管理、认定的审查、审批、考核和档案建立、管理等。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十五条', '审计部职责：㈠对分类结果进行连续监测、分析、考核。㈡根据辖内贷款风险分类的实际情况，按季写出综合分析报告，提出改进贷款风险分类工作和提高贷款质量的建议。㈢对损失类贷款进行审核。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十六条', '计划财务部职责：根据贷款风险分类的认定结果，按规定做好贷款损失准备金的提取和管理。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十七条', '电子银行部职责：负责做好贷记卡业务的风险分类认定工作。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十八条', '信贷资产分类形态实行动态实时调整原则，对新增的借款人，在贷款发放后可由支行自主认定为正常，并填写《认定表》。在日常的贷后检查中，如发生形态变化按本细则规定进行形态调整，支行应出具《贷款风险分类调整呈批表》，连同分类资料按权限上报审批，并在当月五级分类报表中反映。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第五十九条', '支行要按照信贷资产风险分类的标准建立信贷台账，并及时将分类结果及其结构、报告期间分类结果的异常变化及其成因、分类过程中存在的主要问题、改进措施等情况上报总行。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第六十条', '对贷款风险分类的检查和考核。本行将定期或不定期对贷款风险分类结果进行全面检查或抽样检查，对违反风险分类认定标准和程序，造成贷款质量偏离度超过0.5个百分点的支行和个人，视其情节严重程度，予以1000元至2000元处罚；对违反操作流程规定的，按违反程序进行处罚。同时，本行将根据贷款风险分类的结果制定不良贷款控制和清收盘活计划，并列入年度经营考核指标，对责任人进行严格考核。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十章  贷款风险分类的管理', '第六十一条', '贷款损失准备的计提。在完成信贷资产风险分类的基础上，要按照《财政部关于印发<金融企业准备金计提管理办法>的通知》（财金[2012]20号）文件要求，提取一般准备和专项准备，上级主管部门和监管部门对计提比例另行规定的，按规定执行。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十一章  附则', '第六十二条', '本细则由xxx银行风险管理部负责解释和修订。'),
(NULL, '风险管理部', '《信贷资产风险分类实施细则》', '第十一章  附则', '第六十三条', '本细则自印发之日起执行。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第一章  总则', '第一条', '为进一步加强xxx银行（以下简称“本行”）信贷资产风险分类管理工作，规范操作，真实反映信贷资产质量，保证分类认定结果的及时性和准确性，提高风险分类的质量和效率，根据银监会《农村合作金融机构信贷资产风险分类指引》、江苏省联社《江苏省农村信用社信贷资产分类实施方案》、《江苏省农村信用社信贷资产质量管理评价办法》和《xxx银行信贷资产风险分类实施细则》等有关规定，并结合本行实际，制定本办法。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第一章  总则', '第二条', '本办法所指信贷资产包括表内、表外各类信贷资产。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第二章  分类责任人及工作职责', '第三条', '信贷资产风险分类工作责任制，是指在进行信贷资产风险分类过程中，通过明确初分人、复核人、认定人在分类工作中的责任，确保客观、准确反映贷款真实形态的制度。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第二章  分类责任人及工作职责', '第四条', '本行董事长是全辖信贷资产风险分类工作的第一责任人，各支行、公司部、小微业务部（以下简称“支行”）负责人是本单位信贷资产风险分类工作的第一责任人。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第二章  分类责任人及工作职责', '第五条', '根据信贷资产风险分类操作流程，贷款风险分类工作责任人分类如下：1．初分责任人。各支行（部）管户客户经理为本单位所有信贷资产的初分责任人。2．复核责任人。合规员为本单位贷款风险分类初分结果的复核人和复核责任人。3．认定责任人。各支行（部）负责人既是本单位贷款风险分类工作的第一责任人，同时又是权限内贷款风险分类的认定责任人。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第二章  分类责任人及工作职责', '第六条', '初分责任人职责1．认真学习上级有关部门下发的贷款风险分类文件和资料，熟悉风险分类的基础理论和操作实务，能够熟练、科学并真实地进行贷款风险分类；2．负责对管辖范围内借款人情况的动态监测，熟悉本支行（部）借款人的基本情况和资信状况，及时按要求收集和记录信贷信息资料；3．根据所掌握的资料，对管辖范围内的贷款按照《xxx银行信贷资产风险分类实施细则》的要求按时进行初分；4．确保贷款风险分类过程中形成的各种资料完整齐全，填制内容真实、齐全、准确，符合要求；5．及时、完整、准确地汇总和上报各类贷款风险分类报表。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第二章  分类责任人及工作职责', '第七条', '复核责任人职责1．认真学习上级有关部门下发的贷款风险分类文件和资料，熟悉贷款风险分类的基础理论和操作实务，能够熟练、科学并真实地进行贷款风险分类；2．对信贷资料的形成和构成十分熟悉，并对初分人填制的有关资料进行认真审核，指出信贷资产资料的缺失情况，熟悉个人客户的家庭基本情况和企业客户经营财务状况，对初分理由是否充分和初分结论是否准确作出正确的判定；3．对本人复核的贷款风险分类工作过程和结果负责。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第二章  分类责任人及工作职责', '第八条', '认定责任人职责1．安排部署、组织学习有关信贷资产风险分类的文件和工作要求；2．落实本支行贷款风险分类责任，保证职责分明，分工合理，开展有序，高效及时，奖罚分明；3．督促有关人员对贷款风险分类工作进行规范化管理，对上级检查或在分类过程中发现的问题落实专人及时整改；4．对本支行（部）认定权限内贷款的风险分类过程与结果负全责。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第三章  风险分类管理', '第九条', '制定短期和中长期培训计划。根据信贷资产风险分类有关规定，结合本行信贷资产风险分类实际情况，制定短期和中长期培训计划，分层次、分类别、分批次地组织各级管理人员及客户经理进行培训。培训通过集中培训、分片指导等方式进行，深入了解情况，及时发现风险分类过程中的问题与不足，有针对性地做好现场指导和学习培训。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第三章  风险分类管理', '第十条', '按照省联社的要求，信贷资产客户信息管理要实现电子化，规范分类，要做好贷款风险分类数据的录入工作，提高分类质量和工作效率。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第三章  风险分类管理', '第十一条', '认定程序规范，认定结果真实。按照“收集信息、按季分类、动态调整、分级认定”的原则和填写工作底稿及分类认定表的工作程序对贷款进行分类，确保分类程序规范，分类结果真实。一、收集信息，集中建档按照《xxx银行信贷资产风险分类实施细则》的规定，进行客户信息收集归档。对分类认定为正常、关注、次级类的所有企事业单位贷款和自然人贷款，依据办法要求，应在季前完成相关档案资料的整理与补充完善工作。二、按季分类，动态监测各支行（部）应在每季末20日内对权限内信贷表内外资产进行一次认定，真实反映信贷风险形态，对超过本级认定权限的信贷资产，填写好分类意见后报总行风险管理部进行认定。三、实时调整，定期分析1．依据“随时分类”的原则，在每笔贷款发放后的15至30日内应完成对该笔贷款的第一次认定工作，贷款发放当月内应做好信贷风险管理系统的系统分类工作，对季末发放的贷款，则应于当季末完成认定工作和系统分类工作；2．依据“动态调整”的原则，在贷款管理过程中，对风险状况已发生重大变化、需重新进行分类认定的贷款，应及时重新认定；3．依据“按季分类”的原则，每笔贷款的分类认定间隔期限最长不得超过3个月，即每笔贷款自分类认定之日起的3个月内，至少应对该笔贷款再进行一次分类认定；4．依据“定期分析”的原则，定期对贷款风险分类情况进行汇总分析，并对分类结果及其结构（重点分析偏离度和迁徙率）、报告期间分类结果的异常变化及其成因、分类过程中存在的主要问题、改进措施等情况进行定期上报。四、完善档案，上报总结为确保在当季末完成所有信贷资产的风险分类工作，各支行（部）可提前1个月开展信贷资产的集中分类工作，完善相关贷款分类档案资料，并于季后15个工作日内向总行上报本单位季度信用风险管理报告。五、规范程序，明确权限1．各级认定机构及负责人要在分类认定表相应栏签字确认。认定机构对分类过程中形成的所有集体讨论意见、讨论结果，要建立讨论登记簿形成记录，并经参加讨论的所有人员签字确认；2．对信贷资产类别需要上调或者下调的，必须由各支行完善相关呈批手续，上报总行风险管理部门审查后，报信贷资产风险分类领导小组认定；3．对信贷资产由不良形态调至正常类、关注类的，必须由总行风险管理与关联交易控制委员会审批认；4．单户金额达到省联社规定标准的大额损失类贷款的最终认定，必须报省联社审批。六、准确分类，减少偏离各支行（部）在贷款风险分类过程中应充分运用贷后检查来分析和判断借款人偿还贷款的可能性，确定资产分类形态，努力做到风险分类真实、准确、及时地反映信贷资产质量和风险程度。充分运用贷款偏离度检查、贷款迁徙分析和同质同类比较等手段，分类理由合格率要达到95%以上，分类结果偏离度（即检查验收的偏离度）不超过0.5%。不良贷款内部结构调整，不计入偏离度计算。分类理由合格率公式：分类理由合格率=经抽查分类理由合格的分类贷款笔数/经抽查分类贷款笔数×100%。偏离度计算公式：检查验收偏离度=（检查验收组确认的不良贷款余额-支行（部）认定的不良贷款余额）/检查验收贷款余额×100%。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第三章  风险分类管理', '第十二条', '按照“按月汇总，定期分析，分类管理，按季上报”的原则，建立贷款风险分类与信贷管理的互动机制，加强对贷款的风险控制。一、建立台账，按月汇总，定期分析。每月按照信贷风险管理系统生成的贷款明细清单按标准建立信贷台账，每月的各类报表和风险分类台账要有保存（包括纸质文本及电子台账）；按月统计分析汇总分类结果，定期将分类结果及其结构、报告期间分类结果的异常性变化及其原因、分类工作存在的主要问题、改进建议及措施等情况以信用风险管理报告的形式按季上报总行风险管理部。二、根据信贷资产的风险实际进行分类认定和管理，根据风险状况及特点采取有针对性的管理措施，不放过任何一个可疑的因素。1．正常贷款。对正常类贷款，加强贷后跟踪检查，注意加强风险预警，不能放过任何一个可疑因素。对关注类贷款，在加强贷后跟踪检查的同时，要密切跟踪潜在风险因素的变化情况，分析评价其对贷款安全的影响；对于未采取贷款担保措施或担保不足的，要补办贷款担保或通过追加抵押物和保证人进一步强化担保。对欠息或逾期的正常贷款，要加强催收工作，防止因欠息或逾期超过一定期限致使分类向下迁徙。2．次级类贷款。要加强贷款本息催收，保证贷款诉讼时效，密切关注贷款保证及抵（质）押物变化情况，必要时进行债务重组，并尽可能地压缩贷款。3．可疑类贷款。要利用法律手段催收，依法追究担保人责任和行使抵（质）押权，并加强对借款人资产的监控，密切关注与借款人有关的合并、重组、托管等不确定因素，采取相应的资产保全措施，防止借款人资产的流失。4．损失类贷款。要及时足额申报债权，依法参与破产清算，采取一切必要的手段清收，尽可能地减少贷款损失；对确实已造成的损失，按有关规定予以核销。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第四章  考核措施', '第十三条', '贷款风险分类管理工作纳入各支行（部）年度经营管理目标及相关管理人员管理职能工资中考核。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第四章  考核措施', '第十四条', '贷款风险分类管理工作分定性、定量两个目标考核，具体考核按年度经营管理目标、管理人员管理职能工资及员工违规违章行为积分管理办法执行。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第四章  考核措施', '第十五条', '建立严格的问责制度。各支行（部）应尽职做好贷款风险分类管理工作，对具有以下情节的相关责任人，总行视情节轻重严格依照有关规定追究直接责任和管理责任，并根据具体情况进行通报批评或给予相应的处罚。1.凡在抽查中发现有以四级分类结果直接与五级分类结果套换行为的，或故意隐瞒资产质量，人为掩盖不良贷款弄虚作假行为的。2.不能按规定时间完成贷款风险分类工作，或工作不负责任、工作质量不高的支行（部）。3.进行虚假记载，误导性陈述或有重大遗漏的。4.未对客户资料进行认真全面核实的。5.超越认定权限，违反程序操作的。6.敷衍了事、弄虚作假、故意隐瞒真实情况的。7.不配合检查人员工作或提供虚假信息的。8.其他相关违规行为。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第五章  附则', '第十六条', '本办法由xxx银行风险管理部负责解释和修改。'),
(NULL, '风险管理部', '《信贷资产风险分类管理及考核办法》', '第五章  附则', '第十七条', '本办法自印发之日起执行。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第一章  总则', '第一条', '为有效推广应用客户风险预警管理系统（以下简称“预警系统”），促进信用风险管理转型，提升信用风险管控质量，制定本细则。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第一章  总则', '第二条', '客户风险预警是以预警系统相关的内、外部数据为基础，以各本行信贷政策和规章制度为依据，通过设置预警规则和参数，及时筛选、预警、提示、监测客户预警信号，并规范客户预警信号核查处置情况的全过程管理。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第一章  总则', '第三条', '客户风险预警对象是本行风险监测和管理主体，包括单一客户、集团客户、个人客户和资产组合四类。其中:（一）单一客户是指法人（含其他经济组织、社会团体）授信客户。（二）集团客户是指按控股关系生成或根据实际控制情况认定的多个法人授信客户的集合。（三）个人客户是指个人授信客户、法人授信客户及其关联企业的法定代表人、实际控制人、高管、自然人股东、自然人保证人等。（四）资产组合是涉及行业、客户、地区和产品等维度的一系列风险预警客户的组合。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第一章  总则', '第四条', '客户风险预警管理遵循原则：（一）全面性原则。通过客户风险预警系统自动规则判断以及人工识别两种方法，对客户风险实施预警。客户风险预警管理系统广泛采集行内和行外信息数据，通过规则引擎，揭示客户潜在风险。凡本行员工均可通过系统、信件、电话等途径，署名或匿名提供客户风险预警信息，经总行核实确认后，人工在预警系统中增加相应的风险信号，实施全方位、全员预警。（二）及时性原则。对于系统自动触发的预警信号，相关人员及时调查核实，及时制定处置方案，采取有效措施化解风险，减少损失。对于在授信过程中或通过外部渠道了解的预警信号，及时揭示风险，及时人工录入系统，及时采取核查、处置行动。（三）持续优化原则。本行通过数据管理和数据平台建设完善，不断提升数据质量和扩大数据源。预警规则和参数的设计、调整随着本行的发展战略、风险偏好及政策制度适时进行调整，以适应风险预警需求的变化，客观、真实、准确地反映所发生的风险状况。（四）全流程管理原则。客户风险预警贯穿信贷调查、审查、审议、审批、贷款发放和贷后管理的各个环节，实行全流程管理。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第二章  组织管理体系', '第五条', '风险管理部主要承担以下职责：1.牵头组织本行客户风险预警工作，负责客户风险预警的统一规划、组织管理和业务培训。2.起草和修订客户风险预警相关制度办法，组织推动客户风险预警流程以及贷前调查、贷中审查和贷后管理等流程环节配套制度的建立和完善。3.负责对行业、客户、地区和产品等维度的组合性风险预警信号的收集和发布。4.负责匿名登录客户风险预警信号的审核处理。5.负责预警系统相关内控名单的统一维护管理。6.负责权限内客户风险预警处置方案中加强风险管理的意见和建议以及风险分类等跟踪管理。7.负责客户风险预警总体情况定期跟踪、监测分析以及开展督促检查、考核通报等。8.负责预警系统的业务维护和管理。统筹全行系统功能优化和内部特色数据需求的收集和评估分析，并与省联社系统管理人员及时沟通，共同完善系统功能。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第二章  组织管理体系', '第六条', '授信评审部主要承担以下职责：1.负责协助发现客户或组合性预警信号，通过客户风险预警管理系统发布预警信息。2.负责客户风险预警信号在授信审查、审批环节的应用，完善相关配套制度。3.负责权限内客户风险预警处置方案中加强风险管理的意见和建议以及授信额度等跟踪管理。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第二章  组织管理体系', '第七条', '信贷管理部要承担以下职责：1.负责协助发现客户或组合性预警信号，通过客户风险预警管理系统发布预警信息。2.负责客户风险预警信息在贷后管理环节的应用，完善相关配套制度。3.负责权限内客户风险预警处置方案中加强风险管理的意见和建议以及贷后管理等跟踪管理。4.负责信贷管理系统数据质量的管理和治理工作。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第二章  组织管理体系', '第八条', '科技信息部主要承担以下职责：1.负责预警系统的技术维护和安全保障，提供预警系统所需的日常技术支持和保障。2.负责预警系统内部数据管理和特色数据的技术支持。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第二章  组织管理体系', '第九条', '各支行、公司业务部、小微业务部、金融市场部（以下统称经办行）主要承担以下职责：（一）具体负责录入发现的客户风险预警信号。（二）对已发布风险信号的客户开展风险调查，提出初步评估意见和处置方案。（三）负责具体执行客户预警处置方案，结合客户实际风险状况，采取有效措施化解信贷风险，反馈处置结果，申请客户预警级别调整或解除处理。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十条', '根据客户风险程度的高低以及应用处置不同，将客户风险信号分为预警类信号、提示类信号和监控类信号，并根据本行管理需要，实施动态调整。（一）预警类信号是指针对已经或极有可能出现影响信贷资产安全的信息、事件或行为，根据风险程度从高到低，把预警类信号分别标识为红色、橙色、黄色三个等级，在综合判断客户风险状况基础上认定客户预警级别，及时发布、核查、处置、跟踪直至风险因素消除。其中：1.红色预警信号是指已经或极有可能对信贷资产安全构成严重危害信息、事件或行为。2.橙色预警信号是指已经或极有可能威胁到信贷资产安全的信息、事件或行为。3.黄色预警信号是指已经或极有可能影响信贷资产安全的信息、事件或行为。（二）提示类信号是指针对尚未明显影响信贷资产质量，但任其发展可能会威胁到信贷资产安全的信息、事件或行为，生成风险提示信号由系统对客户发出风险提示信息，提示予以关注与控制。（三）监控类信号是指针对风险偏好和信贷政策制度执行情况，生成风险监控信号，由相关业务或管理部门采取相应的应对措施。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十一条', '客户预警级别是衡量客户所有预警信号严重程度并综合判断客户风险程度的标识，按照客户风险程度从高到低，标识为红色、橙色、黄色。同一客户名下可能有多个不同等级的风险信号。（一）客户预警级别为红色的，预计损失严重，需紧急采取措施防止形成损失或进一步扩大损失。（二）客户预警级别为橙色的，预计损失的可能性较大，需立即采取措施防止风险加剧。（三）客户预警级别为黄色的，进一步发展可能会形成损失，需积极采取措施提高安全保障。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十二条', '客户风险预警管理流程主要包括：风险信号生成认定、客户预警级别认定发布、预警客户风险处置跟踪、客户预警级别调整与解除。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十三条', '客户风险预警按照“准确识别、集中发布、及时处置、动态监控”的原则，实行分级授权管理。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第一条', '风险信号生成认定'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十四条', '风险信号的生成包括预警系统自动识别生成和人工录入生成。（一）系统自动识别是指通过内部数据和外部数据的整合，按照既定的预警规则由系统自动提取、加工、识别而生成的风险信号。（二）人工录入信号分为单个信号录入和组合信号的录入，同时根据是否署名，分为匿名录入和登录系统录入。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十五条', '对于预警系统自动生成的信号，由系统自动按等级认定。对于人工录入生成的单个预警信号，如属于预警系统预设的预警信号，由系统自动认定。对不属于预警系统预设的预警信号，由经办人负责人复核、风险管理部负责人认定。对于组合预警信号，由信贷管理部复核、认定。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十六条', '对提示类信号，经风险管理部复核后批量发布。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十七条', '本行员工均可以参与客户风险预警工作，可以匿名登录系统录入客户风险信息，经由风险管理部审核后人工录入发布。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二条', '客户预警级别认定发布'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十八条', '风险预警信号认定后，遵循客户集中发布的原则。同一客户项下认定的风险预警信号中至少有一条为红色的，系统自动发布客户预警级别为红色。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第十九条', '同一客户项下认定的风险预警信号中未出现红色风险预警信号的，按照孰高原则，由系统自动判断发布。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十条', '对于已发布客户预警级别的客户，再次触发风险预警信号，如原客户预警级别为红色，系统自动按红色客户预警级别发布；如原客户预警级别不为红色，由相关岗位在综合判断客户项下风险预警信号严重程度和风险状况的基础上，对客户预警级别复核、认定并发布生效，该客户预警级别最高可为红色。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十一条', '对于客户预警等级为红色的客户，信贷管理系统各流程环节实现自动拦截，需在预警系统中对红色预警等级进行解除或调低后，方能进行后续信贷流程。对于其他风险等级客户，在信贷管理系统流程环节进行提示。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三条', '预警客户风险处置跟踪'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十二条', '发布生效的预警客户级别由系统自动推送至管户客户经理，进入处置流程。管户客户经理需对客户项下风险预警信号产生原因以及对客户和信贷资产的影响进行综合分析评估，在规定时间内核实情况，拟定风险处置方案。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十三条', '本行对预警处置方案按照客户风险等级、授信额度等维度实行分层管理。（一）经办行管户客户经理拟定初步风险处置方案后，由经办行负责人和总行相关部门进行复核、认定，确定最终意见。（二）总行在审议处置方案过程中，必要时采取由风险管理部牵头，风险管理部、信贷管理部、授信评审部、合规管理部共同会商的方式，共同研究风险状况，明确应提示的风险防控重点，提出切实可行的风险化解措施，制定风险处置方案。（四）各级岗位人员在上报风险处置方案同时，必须及时采取措施防止客户风险进一步扩大或贻误风险处置最佳时机。各级审核、认定、审批人员应根据风险状况变化，进一步完善风险处置方案。风险处置方案形成最终意见后，客户经理、经办行应立即组织实施。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十四条', '预警客户风险处置遵循及时性原则，发布红色、橙色、黄色客户预警级别的，管户客户经理原则上应在客户预警级别发布后三个工作日内完成核查、拟定处置方案工作并提交经办行负责人，经办行负责人原则上应在两个工作内完成各自处置工作。客户预警处置的审核审批部门要及时监控客户预警处置进展，督促相关岗位人员及时完成处置任务，防止因处置不及时影响信贷资产质量等。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十五条', '对任务过期未处理的相关责任人员，由总行相关部门进行绩效考核和违规积分管理。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十六条', '组合客户预警级别发布后，组合预警信号发现识别或录入的相关部门可根据需要，提出有关要求或建议逐级向下传递，并协调、督促预警处置方案制定和执行。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十七条', '客户预警处置流程的特别规定：（一）同一客户名下同一风险预警信号不再重复预警。同一客户某条风险预警信号已经在处置流程中，系统不再触发相同的预警信号，人工也无法添加相同的预警信号。（二）同一客户不同风险预警信号合并处置。1.客户预警级别未发生调整，系统自动将该客户新产生的预警信号推送至原流程中的客户项下，客户继续走后续处置流程。2.客户预警级别处于动态调整之中，调整后的级别低于尚处于流程中的该客户预警级别，系统自动将该客户新产生的预警信号推送至原流程中的客户项下，作为提示内容。3.调整后的级别高于已经处于流程中的该客户预警级别，系统自动终止原有流程中的客户预警任务，原流程中的客户项下待处理的信号由系统自动推送至新发起的流程中该客户项下，并重新进入预警处置流程。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十八条', '客户预警处置方案及措施分为风险控制和内部管理两个方面。（一）风险控制方案及措施。包括但不限于：1.提前收回贷款；2.处置抵质押物；3.资产保全人员介入，实施资产保全；4.增强风险缓释，如更换保证人，或提供更多的抵、质押物等；5.变更授信条件；6.调整“增持减退”授信政策；7.对该客户特别关注，加强监控力度，提高贷后检查频次等。（二）内部管理方案及措施。包括但不限于：1.采取额度控制措施，如冻结未用授信额度；2.冻结借款人在本行开立的存款账户，扣款归还本息；3.暂停新增授信流程；4.根据合同向保证人追索，要求其代偿借款人应付的本息；5.立即开展风险排查和现场检查； 6.调整客户的信用评级；7.对贷款风险分类进行重新评估，调整五级分类（或十级分类）形态等。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第二十九条', '对不同客户预警级别客户的风险严重程度，应选择采取不同的预警处置方案。（一）对于客户预警级别为红色的，采用紧急处置方案，尽快控制住风险进一步扩大。紧急处置方案包括但不限于：1.停止领用未用授信额度、冻结存款账户、扣划账户资金、提前收回贷款等。2.对该客户采取压缩或退出政策，新增授信原则上不得准入，存量授信原则上只收不放，制定压缩退出计划，最大限度压降授信敞口。3.根据实际情况采取处置抵质押品、资产保全提前介入、诉讼等措施。（二）对于客户预警级别为橙色的，一般情况下，应对该客户采取压缩政策，新增授信原则上不得准入，存量授信敞口原则上应逐步下降，风险缓释措施应明显增强。根据实际情况采取增加贷后检查频次、变更授信条件、降低五级分类（或十级分类）、调低客户信用评级等措施。（三）对于客户预警级别为黄色的，一般情况下，对该客户政策不得高于维持类，对该客户采取风险观察政策，风险观察期内原则上不得对其增加授信敞口。根据实际情况增加贷后检查频次、增加风险缓释等措施。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三十条', '对系统提示类信号，管户客户经理应开展针对性贷后检查，在检查后及时制定处置措施和计划并按计划整改落实到位，直至提示类信号完全解除。总行相关部门应加强督查，必要时可进行现场检查。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第四条', '客户预警级别调整与解除'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三十一条', '风险处置方案确定后，经办行负责具体执行风险处置方案，总行相关部门督促、协助经办行按计划处置风险、消除风险，经办行不能按计划处置到位的，应报告具体原因、责任处理情况。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三十二条', '客户预警级别发布后，经核实，客户项下风险预警信号不准确或预警信号特征已消失，可解除预警信号。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三十三条', '同一客户项下发布的风险预警信号部分消除时，如消除后客户项下最高风险预警信号等级低于该客户预警级别，客户经理可提出对该客户预警级别调整申请，否则仍维持原客户预警级别。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三十四条', '客户项下风险预警信号全部消除时，可由管户客户经理申请解除客户预警，通过分层分级审批通过后可解除预警。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三十五条', '客户预警级别在发布直至全部解除过程中，总行依据内外部检查、风险监控、五级分类（或十级分类）、贷后管理等过程中发现的客户风险状况变化，实时发起对客户预警级别调整。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第三章  风险信号和流程管理', '第三十六条', '风险管理部要及时掌握预警工作动态，按月进行总结，按季进行分析通报。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第四章  相关管理要求', '第三十七条', '内控名单管理制度。通过内外部渠道获得客户违反国家法律法规、不良信用记录以及存在影响授信到期偿还的重大不利因素等信息，应据该类客户风险程度的不同，实施名单制管理，根据不同名单类型，实施差异化的授信政策和风险管控措施。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第四章  相关管理要求', '第三十八条', '风险预警督办制度。在风险预警过程中，针对某些需要特别关注或特殊处理的风险预警信号，总行可随时进行督办，提出相应的管理要求，督促加快处置。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第四章  相关管理要求', '第三十九条', '风险预警信息关联制度。当客户预警后，不仅要对该客户加强风险管理，同时要对该客户的集团关联企业、股权关联企业、担保关联企业等密切相关客户加强监管，控制整体风险。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第四章  相关管理要求', '第四十条', '重大授信风险紧急处理制度。授信客户突发重大风险信号的，风险预警及处置不受本细则规定的流程或部门职责限制，必须在第一时间将有关情况及时报告总行，便于总行指导风险处置工作。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第四章  相关管理要求', '第四十一条', '风险预警信息共享和查询制度。预警系统发布的各类预警风险信息全行共享，相关人员在信贷调查、审查、审议、审批、贷款发放和贷后管理的各个环节中都必须充分应用风险预警数据，对相关客户预警信息进行查询。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第四章  相关管理要求', '第四十二条', '保密制度。预警系统中的风险信息属于重要涉密信息，在运用过程中，相关人员均负有保密责任，未经允许，不得对外泄露。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第四章  相关管理要求', '第四十三条', '风险预警正向激励和违规处罚制度。（一）对于授信业务非相关责任人，及时发现并报告重大风险信息，避免或减少重大损失的，或降低负面影响的，总行视情况给予500-2000元经济奖励。（二）对于出现的下列情况的，给予责任人违规积分并通报批评，情节严重的按员工违规行为积分管理办法进行查处。1、以下行为视为一级违章行为：（1）因工作失职，应发现而未发现客户风险预警信号。（2）对发生的客户预警未及时处置或处置不当。（3）未在规定时间内上报处置方案，或制定处置方案敷衍了事不认真，处置时间进度计划明显不合理。2、以下行为视为二级违章行为：（1）未在规定时间内跟踪报告处置方案执行情况，或不据实上报风险处置进度。（2）信贷调查、审查、审批人员未按要求查询客户预警信息，而对预警客户办理信贷业务，造成授信风险扩大。3、以下行为视为三级违章行为：（1）对发现风险预警信号隐瞒不报，或对发现风险预警信号上报不及时导致丧失风险处置最佳时机。（2）未按风险处置方案处置风险造成风险扩大。4、以下行为视为四级违章行为：（1）阻碍风险预警信号上报，或对上报风险信号进行打击报复。（2）相关人员对外泄露风险预警信息，造成一定的经济损失或负面影响。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第五章  附则', '第四十四条', '本细则由xxx银行风险管理部负责解释和修订。'),
(NULL, '风险管理部', '《客户风险预警系统实施细则（试行）》', '第五章  附则', '第四十五条', '本《细则》自发布之日起试行。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第一章  总则', '第一条', '为规范xxx银行（以下简称“本行”） 新产品新业务的风险评估审查，防范新业务开发、宣传、实施中的风险，准确评估其风险等级，制定出针对性控制措施，预防和控制风险的发生，结合本行实际，制定本办法。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第一章  总则', '第二条', '本办法适用于本行各类新产品新业务的风险识别、评估和控制的方法。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第一章  总则', '第三条', '本办法所指新产品新业务是指拟开办而前期本行尚未开办的业务产品，创新的业务操作、产品营销措施，以及新的重大客户合作关系的建立变更等。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第一章  总则', '第四条', '风险指本行在经营管理活动中，由于不确定因素影响使得银行实际收益偏离预期收益，从而导致发生损失或获取额外收益的可能性。或特定的威胁利用脆弱点，进而对本行造成损害的不期望事件。威胁是指可能对银行造成损害的不期望事件的潜在原因。脆弱点是指可能被一个或多个威胁所利用的弱点。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第一章  总则', '第五条', '风险识别对全行经营管理活动中存在的所有风险进行排查和确定其特性的全过程'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第一章  总则', '第六条', '风险评估指测算估计风险大小及确定风险是否可接受的全过程。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第一章  总则', '第七条', '本行侧重风险评估的种类（一）信用风险是指债务人或交易对手未能履行合同所规定的义务或信用质量发生变化，影响金融产品价值，从而给债权人或金融产品持有人造成经济损失的风险。（二）市场风险指因市场价格（利率、汇率、股票价格和商品价格）的不利变化而使本行表内和表外业务发生损失的风险。（三）操作风险指因操作流程不完善、人为过失、系统故障或失误以及外部事件造成损失的风险。（四）流动性风险指本行无力为负债的减少或资产的增加提供融资而造成损失或破产的风险。（五）国家风险指由于他国宏观经济、政治、社会环境的影响导致本行的外国客户或交易对方不能偿还债务的可能性。（六）声誉风险指由于意外事件、本行的政策调整、市场表现或日常经营活动所产生的负面结果，可能对本行的这种无形资产造成损失的风险。（七）法律风险指因无法满足或违反法律要求，导致本行不能履行合同、发生争议、诉讼或其他法律纠纷，而可能给本行造成经济损失的风险。（八）战略风险指本行在追求短期商业目的和长期发展目标的系统化的管理过程中，不适用的未来发展规划和战略决策可能威胁本行未来发展的潜在风险。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第二章  职责与权限', '第八条', '董事会负责审批整体经营战略和重大政策，明确设定可接受的风险程度，确保行长室采取必要措施，识别、计量、检测和控制风险。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第二章  职责与权限', '第九条', '监事会负责监督董事会、行长室风险识别与评估的履行情况。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第二章  职责与权限', '第十条', '行长室负责执行董事会决策，建立识别计量、监测并控制风险的程序和措施。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第二章  职责与权限', '第十一条', '风险管理部为风险识别与评估牵头部门，具体负责风险的识别与评估审查工作。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第三章  基本规定', '第十二条', '新产品业务的风险识别全行每项新产品新业务的推出，要首先进行风险识别和评估，确定风险等级，然后制定出预防和控制措施。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第三章  基本规定', '第十三条', '风险评估的周期（一）一般情况下，本行可每年进行一次风险评估。特殊情况下可增加风险评估的频次，由风险管理部与关联交易控制委员会确定，按本程序实施。（二）特殊情况包括：1. 新产品新业务；2. 经营环境发生重大变化；3. 组织机构、技术发生重大变化；4. 本行认为有必要的其他情况。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第四章  操作流程', '第十四条', '送审1. 开办新产品新业务前，业务主管部门提请风险管理部对新产品新业务进行风险评估审查。2．新产品新业务为支行设计，或仅在部分支行实施的，支行应当提交本行该类业务主管部门，再由主管部门提请风险审查。3.提出申请时，应当填写新产品新业务风险审查审批表，并由部门总经理签字，加盖部门公章。4.提出风险审查需提供以下材料：（1）关于新产品新业务的定义、特点、操作的基本说明、记录表单、宣传材料。（2）已制定的关于该新产品新业务的合同文本、管理制度、操作指导书等。（3）已知与该新产品新业务相关的法律法规、规章以及上级管理部门、监管部门、本行的有关文件规定。（4）与引起本次风险变化的因素相关的背景资料等。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第四章  操作流程', '第十五条', '风险审查（一）受理1.风险管理部收到业务主管部门提交的新产品新业务风险审查时，首先对新产品新业务送审材料的充分性进行审查。2. 对于送审材料不充分的，审查人员有权要求业务主管部门进行补充，补充材料必须一次性告知，待材料齐全后应进行风险评估审查。3. 对于不需要风险评估审查的业务，审查人员及时告知业务主管部门，并在审查申请书上写明不需风险评估审查的理由与意见，并签字确认。4. 业务主管部门对于审查人员不受理风险评估审查的业务，可以直接报业务分管领导批示（二）初审新产品新业务的风险初审原则上自业务主管部门提交齐全审查资料之日起3个工作日内审查完毕，并在风险审查审批表上签署初审意见。（三）复核1.风险管理部总经理对于审查人员出具的风险评估审查意见进行审阅，审查风险意见是否充分、合理，在审查审批表上签署明确的审核意见，签字确认后报送风险分管领导批示。2.风险管理部总经理审核后，认为审查人员审核意见不妥、不足的，可以要求审查人员补充审查。3.风险管理部总经理的审核，原则上应当在收到风险审查意见书起一个工作日内审查完毕。（四）确认风险分管领导接到风险管理部报批的新产品新业务风险审查意见时，对审查意见的全面性、准确性进行确认，认为审查意见不准确、不全面的，退还重审。情况紧急的，风险分管领导可以口头批示，事后补签书面意见。（五）反馈审查意见经风险分管领导批示后，风险管理部须及时向业务主管部门反馈审查意见。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第四章  操作流程', '第十六条', '修订完善业务主管部门接到风险管理部出具的风险评估审查意见后，落实审查意见，进一步修订、完善，保证业务经营的风险性。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第四章  操作流程', '第十七条', '评审（一）评审会议前准备1. 业务主管部门落实风险审查意见后，由业务主管部门总经理组织召集评审会议。2. 在召开评审会议前，业务主管部门应当将拟评议业务的相关材料，至少提前一日送交会议参加人。（二）集体评审1.召开新产品新业务评审会议，通过集体讨论的形式，决定是否实施新产品新业务。2. 业务主管部门应当对新产品新业务基本情况、收益、风险及防范措施等做详细说明。3. 风险管理部总经理或风险审查人员应当对业务风险评估审查情况进行报告。4. 各参会人员可以就新产品新业务风险合规及防范发表意见。5. 会议应当详细记录各参会人员意见，并由参会的风险管理部人员在新产品新业务风险审查审批表记录会议讨论结果。 6. 汇总后，业务主管部门应报实施新产品新业务的有权审批人审批。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第四章  操作流程', '第十八条', '业务开展与监督（一）开展新产品新业务1. 新产品新业务经内部审批同意开办后，由业务主管部门负责必要的报备、报批以及具体宣传策划推广、培训、具体实施管理等工作。2. 在开办新产品新业务过程中，业务主管部门与具体经办人员应当有效识别风险，谨慎风险操作，履行风险报告的相关义务。（二）报告业务运行新产品新业务实施后，业务主管部门跟踪新产品新业务实施情况，对实际操作情况与合规风险情况进行跟踪调查检查，并及时出具业务运行报告。1. 业务运行报告，应当包括新产品新业务运行情况、预期收益实现情况、运行中发现的潜在合规风险点、拟采取的改进措施等内容。2. 业务运行报告一般应当在业务运行后的一个季度内出具，并报送业务分管领导、风险管理部。（三）监测、上报风险管理部根据业务主管部门的运行报告、经营单位的反馈意见以及外部投诉、诉讼、客户意见、监管意见等，对新产品新业务运行风险情况进行评价，并向风险分管领导进行报告。（四）监督风险分管领导有权获知新产品新业务的运行情况，并提出建议。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第四章  操作流程', '第十九条', '检查监督审计部负责新产品新业务风险审查尽职以及防范风险的情况。新产品新业务开办一年内必须检查一次，检查结果上报董事会。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第五章  风险责任', '第二十条', '尽职审查责任审查人员应认真分析新产品新业务送审材料，然后出具审查意见。因过失或故意未能识别应识别合规风险或未予以提示的，审查人员对出具不当的审查意见承担责任。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第五章  风险责任', '第二十一条', '保密责任审查人员及外聘律师出具的审查意见仅限行内有关单位采纳；未经风险管理部同意，任何单位和个人不得向外部单位提供。对于审查过程中接触到的有关国家秘密和商业秘密，审查人员应按规定予以保密'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第五章  风险责任', '第二十二条', '合理采纳责任因未采纳合理的审查意见而造成本行诉讼败诉、监管处罚、经济损失或引发其他重大法律风险事件的，业务主管部门应当承担管理责任，并根据相关规定追究有关人员的责任。	第六章  附  则'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第五章  风险责任', '第二十三条', '本办法未尽事宜，按国家法律法规、规章等规定执行，本办法如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第五章  风险责任', '第二十四条', '本办法由xxx银行风险管理部负责解释和修订。'),
(NULL, '风险管理部', '《新产品新业务风险评估审查办法》', '第五章  风险责任', '第二十五条', '本办法自发文之日起执行。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第一章  总则', '第一条', '为全面反映本行表内外信用风险情况，突出加强集团客户授信风险监测，逐步完善前瞻性的风险识别信息，更好地防范信用风险，根据《中国银监会关于实行新版客户风险统计制度的通知》(银监发〔2012〕39号)文件精神，保证新版客户风险统计制度(以下简称新版制度）的顺利实施，进一步提高数据报送的及时性、准确性和规范性，特制定本细则。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第一章  总则', '第二条', '客户风险统计以法人机构为单位统一向银监会报数，数据报送流程由数据采集、数据审核、数据校验、数据上报、冲突举证、反馈修改等六个环节构成。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第二章  组织管理体系', '第三条', '风险管理部牵头组织实施新版制度相关工作，严格执行信息保密制度，安排专人负责客户风险统计数据的收集、审核和上报。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第二章  组织管理体系', '第四条', '各支行、营业部、公司业务部、小微业务部（以下统称“经办行”）负责客户风险统计的数据核实、补录、修改和举证材料收集工作。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第二章  组织管理体系', '第五条', '授信评审部负责客户风险统计授信数据的审核。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第二章  组织管理体系', '第六条', '信贷管理部负责客户风险统计用信数据的审核。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第二章  组织管理体系', '第七条', '金融市场部负责按要求提供对公及同业客户授信和表内外业相关数据。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第二章  组织管理体系', '第八条', '科技信息部配合完成新版制度相配套外包辅助系统建设工作及基础数据下发、备份工作。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第三章  填报内容', '第九条', '指标数量新版制度包括6张报表，296项填报指标(其中7项指标暂不填报），具体数量指标如下：1.《对公及同业客户授信和表内外业务统计表》共124项指标，其中3项暂不填报。2.《集团客户、供应链融资基本信息统计表》共47项指标，其中3项暂不填报。3.《单一法人客户基本信息统计表》共53项指标，其中1项暂不填报。4.《对公客户担保情况统计表》共19项指标。5.《个人贷款违约情况统计表》共32项指标。6.《个人违约贷款担保情况统计表》共21项指标。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第三章  填报内容', '第十条', '指标内容1.《对公及同业客户授信和表内外业务统计表》填报本行对公及同业客户授信和表内外业务信息，反映本行对各类对公及同业客户承担信用风险的情况。分别从授信情况、集团客户授信拆分情况、贷款明细、持有企业债券明细、持有企业股权明细、表外业务明细、同业客户业务明细等7方面进行填报。2.《集团客户、供应链融资基本信息统计表》填报与表1相关的集团授信客户信息，以及银行开展的供应链融资相关客户信息。分别从集团基本情况、高管及重要关联人信息、成员名单、实际控制人、关联集团信息等5个方面进行填报。3.《单一法人客户基本信息统计表》填报与表1相关的单一法人客户基本信息，分别从单一法人客户基本情况、法定代表人和高管及重要关联人信息、重要股东及主要关联企业等3个方面进行填报。4.《对公客户担保情况统计表》对本行对公客户贷款及表内业务的担保信息，按照贷款或表外业务合同和担保合同的对应关系，将担保台同下的押品或保证人，逐宗押品或逐保证人填报。从客户名称、客户代码、贷款或表外业务合同号、担保合同号等19个方面进行填报。5.《个人贷款违约情况统计表》填报本行个人违约90天及以上的货款信息(不含信用卡违约)。从借款人姓名、有效身份证件类型、证件号码，借据号、贷款品种等32个方面进行填报。6.《个人违约贷款担保情况统计表》填报与表5相关的各笔货款担保信息。从借款人姓名、担保合同类型、押品类型、保证人保证能力上限等21个方面进行填报。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第四章  数据报送流程', '第十一条', '数据采集(一)通过信贷管理系统采集信贷管理系统数据录入规范、准确，有利于新版客户风险报送系统自动提取和采集数据。经办行根据新版制度的要求，重点注意核实以下内容：（1）证件编号与客户名称是否相符；（2）对公客户是否录入法定代表人，申请授信或用信时点法定代表人是否发生变更；（3）有贷款对公客户是否按季更新财务报表；（4）对公客户的基本信息录入是否准确，如注册地址、行业分类、职工人数等；（5）个人违约90天及以上的货款客户的户籍地址、行政区划（省份、直辖市、自治区）、居住地址、押品评估日期等是否准确录入。对存量保函业务应逐步过渡到开立保证金通过信贷管理系统进行开具，新增保函业务业务必须从信贷管理系统发起，否则会出现该项的数据漏报。(二)通过手工台账填报。对公及同业客户授信和表内外业统计报表中需填报的持有企业债券明细、同业客户业务明细，由金融市场部根据设置的EXCEL模板（附件1）如实填入并换人复核，金融市场部应注意收集同业客户证件和非现场监管机构编码的收集和扫描，以便后期举证。(三)其他情况。对新版制度中采集的指标在信贷管理系统没有的字段，如表1表1 PART VI中到期未兑付的银行承兑汇票、表3 PARTⅡ法定代表人、高管及重要关联信息中关系人类型为个人股东的信息等，由相关客户经理依据新版制度的要求，及时收集客户资料，在新版客户风险报送系统中及时进行补录，必要时由总行根据客户经理收集的资料在系统中直接补录。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第四章  数据报送流程', '第十二条', '数据审核（一）授信评审部和信贷管理部预审1.授信评审部利用信贷管理系统进行授信审批时，应注意相关授信数据的审核，包括但不现限于以下内容：（1）对公客户是否录入法定代表人，授信时点法定代表人是否发生变更；（2）对公客户财务报表的时点和数值是否真实更新，是否有最近一期的年度财务报表。授信报告的财务分析指标与信贷管理系统中录入的是否一致；（3）对公客户的基本信息录入是否规范和准确，如注册地址、行业分类、职工人数等。2.信贷管理部利用信贷管理系统进行用信审批时，应注意相关用信数据的审核，包括但不现限于以下内容：（1）对公客户是否录入法定代表人，用信时点法定代表人是否发生变更；（2）用信报告的财务分析指标与信贷管理系统中录入的是否一致；（3）合同和押品录入是否规范准确。（二）风险管理部复审1.数据的规范性（1）客户基本信息是否完整。包括法人代码、股东代码、股东和关联企业、贷款担保情况、上市情况和预警信号是否缺失，确保信息全面完整。（2）有没有无证件客户。境内注册企业一般不应是“无证件”客户，对无“无证件”企业客户应严加审核。（3）客户名称是否规范。特别注意名称中是否为简称以及含有括号等。（4）客户代码是否异常。如是否为相同9位数字组合及其他明显不符合编码规则的情况。（5）押品名称的填报是否规范。主要是商用房地产、商用建设用地使用权、居住用房地产和居住用建设用地使用权押品的填报需符合银监会相应填报规范。（6）其他情况。如对用18位统一社会信用代码注册的企业，应根据银监局的相关要求进行填报。2.数据的合理性（1）数据是否存在异常。对于资产总额、负债总额、授信额度及贷款余额等余额为零或在万元以下以及其他异常数据(如超大数据）等情况要认真核实；（2）对比分析汇总数据。对本行对公客户及同业客户授信逐笔授信业务、授信额度、集团客户授信拆分情况、贷款明细、个人贷款发放金额、贷款余额，违约金额等数据与新版客户风险统计系统生成数据进行比对分析，同时与非现场监管统计报表关键数据进行比对分析，防止出现异常数据和逻辑性差错；（3）本期报送的法人客户数和违约个人客户数与上期相比，增减是否异常。3.其他情况。(三)其他不符合新版制度的错误'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第四章  数据报送流程', '第十三条', '数据校验风险管理部数据审核人员对新版制度系统整表校验出现的包括但不限于下列结果进行处理：(一）组织机构代码（或统一社会信用代码第9-17位）相同，但客户名称不同的，应通知经办行及时查询原始资料进行核实在信贷管理系统内进行修改，必要时提交信贷管理系统管理员进行处理。(二)同一客户法定代表人信息不一致的，应以最新的工商营业执照或通过查询全国企业信用信息公示系统进行核实，若发现经办行在信贷管理系统内的信息更新不及时，应通知其修改。(三）企业的大中小微分类与非现场监管统计S6301报表的手工台账不一致的，经办行应在新版客户风险统计报送系统中进行修改，同时查看信贷管理系统中该企业的机构类型、资产总额、销售额和职工人数是否录入失实，及时进行修改。(四）其他情况。新版客户风险统计系统对个人贷款违约客户，除单个字段的格式外，设定了强制校验，必要时，风险管理部报送人员根据金融机构填报客户端提供的数据导出功能将全量数据导出后进行人工核对，并通知经办行及时进行修改和完善。主要包括：同一身份证下的姓名是否不同、个人身份证件是否符合编码规则、客户住址填报的规范性及与行政区划代码的对应关系、资产负债指标是否及时更新情况等。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第四章  数据报送流程', '第十四条', '数据上报风险管理部报送人员每月后9个工作日内必须完成数据的打包并通过金融机构填报客户端上报，数据上报后5个工作日内必须向yy银监分局邮寄《客户风险统计关键指标数据质量承诺书》(格式见附件2）。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第四章  数据报送流程', '第十五条', '冲突举证根据银监局的反馈，对跨行需要举证的客户，风险管理部及时通知经办行和金融市场部所提供举证资料的扫描件。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第四章  数据报送流程', '第十六条', '反馈修改对举证反馈本行上报数据错误的，风险管理部应督促经办行同时修改信贷管理系统对应字段的数据，防止系统自动跑批后下月出现同样的错误。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第五章  监督与考核', '第十七条', '客户风险统计数据质量考核内容：1.上报、反馈的及时性；2.代码名称相符；3.举证及采纳；4.资产负债指标及时更新；5.差错信息及时更新；6.重大错漏报；7.其他事项。对数据报送过程中发现差错，由风险管理部下达整改通知书，未按要求整改的计分处罚。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第五章  监督与考核', '第十八条', '风险管理部根据银监局对本行客户风险报表报送及数据质量情况考核结果，结合经办行数据差错情况和数据报送的完整性、及时性、准确性、跨行一致性校验结果核实、举证的及时性、准确性等，定期或不定期通报经办行报表报送及数据质量情况，总结存在问题，促进基础数据质量的提升。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第五章  监督与考核', '第十九条', '对于客户风险统计数据报送的完整性、及时性、准确性较差的经办行，总行将采取通报批评、约见谈话、开展统计现场检查及专顶检查等措施，督促其认真整改到位。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第六章  附则', '第二十条', '本细则由xxx银行风险管理部负责解释和修订。'),
(NULL, '风险管理部', '《新版客户风险统计实施细则》', '第六章  附则', '第二十一条', '本细则从下发之日起执行。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第一章  总则', '第一条', '为进一步加强xxx银行（以下简称“本行”）监管统计数据质量管理，强化监管统计数据质量问题追责，规范统计工作行为，确保各类统计工作及时、全面、准确地开展，根据《中国银监会关于进一步提高数据质量做好非现场监管工作的通知》（银监通〔2008〕10号）、《中国银监会江苏监管局办公室关于印发监管统计数据质量问题追责细则的通知》（苏银监办〔2016〕207号）等相关要求，结合《xxx银行员工违规行为处理办法》（xxx银行行发〔2015〕93号），制定本办法。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第一章  总则', '第二条', '监管统计数据质量问题追责遵循客观、公平、公正的原则。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第一章  总则', '第三条', '本办法适用于本行各支行、部，机关各部室及中心（以下简称“单位”）。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第一章  总则', '第四条', '本行监管统计归口管理部门是制定、执行监管统计数据质量问题追责细则的主管部门，各支行、部，机关各部室及中心可参照本办法组织本单位进行监管统计数据质量问题追责工作。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第二章  追责内容', '第五条', '数据质量问题分类数据质量问题按照其形成原因分为迟报、漏报、错报、提供虚假的或隐瞒重要事实数据等情况；按照其影响程度分为一般、重大和严重三类，分别记1分、3分、5分。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第二章  追责内容', '第六条', '数据迟报及程度认定。迟报指各单位在上报总行报表报送截止时仍存在报表未报送的情况。对外报表上报时因未能通过系统校验被自动退回且未能在报送截止前完成重报，或因数据问题被监管人员退回后未能在审核截止前完成重报，均视同迟报。1.以下迟报情况为一般数据质量问题：首次出现迟报但在较短时间内完成补报；对外报送报表中，非现场监管统计报表在报送截止日后1个工作日内完成补报；客户风险统计报表在报送截止时点后3个小时内完成补报。2.以下迟报情况为重大数据质量问题：报送总行报表12个月内发生3次（含）以上迟报；对外报送报表连续12个月内不同批次报表累计出现2次（含）以上迟报；迟报后长时间未完成补报，其中非现场监管统计报表未能在报送截止日后1个工作日内完成补报，客户风险统计报表未能在报送截止时点后3个小时内完成补报。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第二章  追责内容', '第七条', '数据漏报及程度认定。漏报指各单位在报送截止时已完成报表报送，但上报报表中出现全部或部分应填数据项缺失的情况。1.以下漏报情况为一般数据质量问题：上报总行的报表中，漏报3条（含）以下信息点；对于非现场监管统计报表，同一批次报表出现2张（含）以内报表部分数据项缺失；对于客户风险统计报表，同一批次报表出现1张报表数据记录（指按照该张报表填报要求，逐条进行报送的单条完整数据记录，下同）缺失，但缺失记录数未超过应报送记录数的5%。2.以下漏报情况为重大数据质量问题：上报总行的报表中，漏报3条（含）以上信息点；对外报送报表中，连续12个月内不同批次报表累计出现2次（含）以上漏报，同一批次报表出现1张或以上报表全部数据项缺失（即空表）；对于非现场监管统计报表，同一批次报表出现3张（含）以上报表部分数据项缺失；对于客户风险统计报表，同一批次报表出现2张（含）以上报表数据记录缺失，或单张报表缺失记录数超过应报送记录数的5%。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第二章  追责内容', '第八条', '数据错报及程度认定。错报指各单位上报总行的报表数据出现不符合报送要求的错误信息；对外报送报表未能通过监管机构已明确的表内、表间校验关系或因对统计制度填报口径理解错误、人为操作失误、信息系统错误等原因导致的报表数据与实际不符的情况。1.以下错报情况为一般数据质量问题：上报总行的报表中，错报3条（含）以下信息点且数据差错金额较小；对外报送报表数据差错金额较小，不对单家机构风险判断或辖区银行业整体风险判断产生实质性影响，且不包括后两款所列特定情况。数据差错被yy银监分局通报批评。2.以下错报情况为重大数据质量问题：上报总行的报表中，错报3条（含）以上10条（含）以下信息点或数据差错金额对总行数据造成重大影响；对外报送报表数据差错金额较大，改变单家机构对应指标变动趋势方向，同一报表连续12个月内出现2次错报、同一批次出现3张报表错报或连续12个月内累计出现6张报表错报，数据单位错误，数据扩大或缩小相应倍数，将历史批次数据当作当期批次数据报送，报表项目数据串行；yy银监分局监管统计关键指标数据质量承诺制度中所列非现场监管统计和客户风险统计关键指标连续12个月内合计出现1次（含）以上错报。3.以下错报情况为严重数据质量问题：上报总行的报表中，错报10条（含）以上信息点或数据差错金额对总行数据造成严重影响；对外报送报表数据差错金额巨大，改变对应指标辖区汇总数据变动趋势方向，同一报表连续12个月内出现3次（含）以上错报、同一批次中出现6张（含）以上报表错报或连续12个月内累计出现10张（含）报表错报；yy银监分局监管统计关键指标数据质量承诺制度中所列非现场监管统计和客户风险统计关键指标连续12个月内合计出现2次（含）以上错报；数据差错被银监会、江苏银监局通报批评，数据差错被银监会、江苏银监局、yy银监分局责令解锁重报。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第二章  追责内容', '第九条', '提供虚假的或隐瞒重要事实数据及程度认定。提供虚假或隐瞒重要事实数据指各单位弄虚作假，报送的统计信息不真实，不符合客观实际；对外报送的报表与实际数据明显不符的监管统计报表数据；提供虚假的或隐瞒重要事实数据包括但不限于以下行为：监管统计报表数据明显区别于本行信息系统或台账中的原始数据，且不能提供合理解释说明；yy银监分局监管统计关键指标数据质量承诺制度中所列非现场监管统计和客户风险统计关键指标与正确指标偏离度超15%；出于规避监管的目的，违反统计法律、法规和统计制度，以不真实数据报送监管机构。提供虚假的或隐瞒重要事实的报表均为严重数据质量问题，基于主观故意提供虚假的或隐瞒重要事实报表的加重追责。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第二章  追责内容', '第十条', '其他监管统计工作问题追责情况计分规则。1.客户风险数据质量考核情况计分规则。以江苏银监局《客户风险统计数据质量考核实施细则》为依据，根据江苏银监局客户风险数据质量月度通报中省内排名情况进行扣分。江苏省内排名倒数5位以内（含）的，对相关责任人每次扣1分；排名倒数第6至10位（含）的，对相关责任人每次扣0.6分，排名倒数第11至20位的银行（含），对相关责任人每次扣0.3分。2.非现场监管报表异动说明考核计分规则。不按规定报送《yy银监分局监管统计关键指标数据质量承诺书》，迟报1次扣0.5分，漏报1次扣1分；不按规定要求报送监管统计数据异动说明的，迟报1次扣0.5分，漏报1次扣1分，异动说明中错报或漏报1个指标的扣0.2分。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第三章  追责对象及追责方式', '第十一条', '本细则追责对象包括报表填报人员、复核人员、运营主管（或合规员）、单位或部门负责人、归口管理部门相关人员、分管行长、行长。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第三章  追责对象及追责方式', '第十二条', '监管统计数据质量问题追责的处理方式包括：纪律处分、经济处罚和其他处罚。（一）纪律处分，包括警告、记过、记大过、降级（职）、撤职、留用察看、开除。（二）经济处罚，包括积分罚款扣减绩效收入、赔偿经济损失等。（三）其他处罚，包括批评教育、内部下岗、组织处理。其中批评教育包括：责令限期改正、责令书面检查、诫勉谈话、通报批评；组织处理包括：调离、停职、免职、解聘专业技术职务、责令辞职、解除劳动合同等。以上处理方式可以并用。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第三章  追责对象及追责方式', '第十三条', '本追责办法记分值1分、3分、5分，分别对应本行《员工违规行为处理办法》的5分、15分、25分进行记分处罚，其他分值相应折算。原则上，对于一般数据质量问题，给予报表填报人员、复核人员、运营主管（或合规员）、单位或部门负责人不低于500元（含）的经济处罚；对于重大数据质量问题，给予报表填报人员、复核人员、运营主管（或合规员）、单位或部门负责人、归口管理部门相关人员、分管行长不低于1500元（含）的经济处罚；对于严重数据质量问题，给予报表填报人员、复核人员、运营主管（或合规员）、单位或部门负责人、归口管理部门相关人员、分管行长、行长不低于3000元（含）的经济处罚，同时对直接填报人员、复核人员、运营主管（或合规员）、单位或部门负责人进行行政处分；如被监管银监局下发“警示单”，给予报表填报人员、复核人员、运营主管（或合规员）、单位或部门负责人、归口管理部门相关人员、分管行长不低于2000元（含）的经济处罚。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第三章  追责对象及追责方式', '第十四条', '发生数据差错的单位，在报表数据报送后自行发现差错并主动申请重报的，可酌情减轻责任追究力度；对于明知有错，却故意隐瞒且不主动申请重报，被总行或监管机构责令重报的加重追责。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第四章  附则', '第十五条', '本细则由xxx银行风险管理部负责解释和修订。'),
(NULL, '风险管理部', '《监管统计数据质量问题追责细则》', '第四章  附则', '第十六条', '本细则自印发之日起施行。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第一章  总则', '第一条', '为提高xxx银行（以下简称“本行”)经营管理水平，完善本行内部控制体系，树立依法合规经营理念，防范金融风险，保障本行安全稳健运行，特制定本规则。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第一章  总则', '第二条', '风险与内控管理委员会是本行风险管理和内部控制的研究、审议机构，受行长室委托，审议本行风险管理政策，制定风险管理和内部控制目标及工作规划，检查监督风险管理和内部控制执行情况，研究决定有关风险管理和内部控制的重大事项。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第二章  人员组成', '第三条', '风险与内控管理委员会主任委员由行长任，委员由行长室其他副行长、风险管理部、合规管理部、运营管理部、信贷管理部负责人组成。风险与内控管理委员会下设办公室，设在风险管理部，负责办理提交风险与内控管理委员会审议的有关内控制度建立、健全、实施、评价、处罚等日常工作。风险与内控管理委员会应邀请本行董事会、监事会、审计部派员列席会议，但不作为委员参加投票。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第三章  职责权限', '第四条', '风险与内控管理委员会的职责权限：㈠根据董事会制订的风险管理战略，审议本行内控与风险管理政策；㈡制定风险管理和内部控制目标及工作规划，检查监督风险管理和内部控制执行情况；㈢协调本行全面风险管理、内部控制与合规管理、案防工作中的重大事项；㈣负责协调制定有关信息科技风险管理策略，尤其是在涉及信息安全、业务连续性计划和合规性风险等方面，实施持续信息科技风险评估；㈤审议全行不良资产清收、转化、保全管理等工作措施以及不良资产处置方案；㈥审议本行不良资产责任界定、违规问责意见、清收定价；㈦审核认定资产风险分类结果；㈧审议需总行审批的重大疑难信贷业务授信及行长认为有必要提交本委员会审议的事项。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第三章  职责权限', '第五条', '风险与内控管理委员会办公室的职责权限：㈠负责组织委员会会议的有关材料和文件，负责会议的记录，整理会议纪要；㈡负责编制年度风险管理工作规划和风险报告，提交委员会审议，并根据委员会的决议负责委员会闭会期间日常的政策解释、指导等工作；㈢贯彻执行风险与内控管理委员会的各项决定和工作部署，组织、协调、推动全行的内控日常工作；㈣加强内控综合管理调研工作，充分发挥参谋助手作用，为行领导提供有参考价值的内控调研信息，提高内控工作的预见性，为领导决策提供服务；㈤整合各部门的制度检查职能，组织开展风险与内控管理委员会决定的各项检查活动；㈥研究制订内控管理目标评价体系，确立正确的发展导向，引导全行在加快业务发展的同时加强内控管理；㈦加强信息的沟通和反馈，指导各业务部门和分支机构开展内控工作，并组织各风险管理岗形成内控网络；㈧做好全行内控问题的收集，建立内控检查问题库，督促问题库的录入和整改工作。㈨其他授权的事项。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第三章  职责权限', '第六条', '风险与内控管理委员会委员权利：㈠获取与业务发展及风险管理相关的信息；㈡向风险与内控管理委员会提出议案；㈢参加风险与内控管理委员会会议并对会议审议的事项独立发表意见；㈣对风险与内控管理委员会讨论、审议的事项行使表决权等。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第三章  职责权限', '第七条', '风险与内控管理委员会委员义务：㈠按时出席会议；㈡按时提交对书面征询意见的反馈材料；㈢认真审议办公室提交的各项议案和相关材料；㈣在本机构范围内督促落实委员会审议通过的各项规范、办法、标准及其它事项。对于连续两次或累计三次无故缺席会议的委员，视为不适宜担任委员工作，由行长解聘后另行聘任。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第八条', '风险与内控管理委员会会议根据报告事项适时组织召开。会议由主任委员召集和主持。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第九条', '风险与内控管理委员会会议应由三分之二以上（不含三分之二）委员出席方可召开。决议采用投票方式，每名委员有一票表决权。会议通过的决议必须经到会委员三分之二以上（含三分之二）同意，否则为不同意审议事项。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十条', '受理。凡提交风险与内控管理委员会会议审议的事项，各相关部门需将报告加盖部门章印送分管行长签字同意后，提交至风险与内控管理委员会办公室。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十一条', '审查。风险与内控管理委员会办公室对提交的报告进行初审，确保提交材料的完整、合规。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十二条', '会议准备。由风险与内控管理委员会办公室提请会议主持人及时召开风险与内控管理委员会会议，并提前做好会议资料的准备，通知各委员、列席人员按时参加会议。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十三条', '审议。由风险管理部门负责人汇报会办事项，或由申报单位负责人列席会议介绍情况，参加会议的委员就会办事项提出问题及发表意见，并在《表决表》中进行投票表决。会议根据投票情况统计，当场宣布投票结果。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十四条', '审批。风险与内控管理委员会办公室根据投票结果和主任委员审批意见进行审批。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十五条', '备案。风险与内控管理委员会审批通过的事项，超过对行长授权范围的上报董事会审批，其他需上报备案的按规定要求上报备案。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十六条', '批复。风险与内控管理委员会办公室根据备案意见出具审批通知单给申报单位。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十七条', '如申请单位对审议未通过的报告事项有异议，可补充相关资料或说明，经主任委员同意后可以复议，复议仅限一次，提交风险与内控管理委员会重新审议。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第四章  议事程序', '第十八条', '风险与内控管理委员会设立专门的记录本，专人负责对风险与内控管理委员会审议事项进行记录。会办记录由风险与内控管理委员会办公室列入永久保管档案。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第五章  附则', '第十九条', '本议事规则未尽事宜，按国家有关法律、法规的规定执行；本议事规则如与国家日后颁布的法律、法规相抵触时，按国家有关法律、法规的规定执行，并立即修订。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第五章  附则', '第二十条', '本议事规则由xxx银行风险与内控管理委员会办公室负责解释和修订。'),
(NULL, '风险管理部', '《风险与内控管理委员会议事规则》', '第五章  附则', '第二十一条', '本议事规则自下发之日起施行。'),
(NULL, '风险管理部', '《风险管理制度》', '第一章  总则', '第一条', '为推进xxx银行（以下简称“本行”）全面风险管理体系建设，提升风险管理水平，依据中国银行业监督管理委员会《农村中小金融机构风险管理机制建设指引》、《江苏省农村信用社风险管理机制建设规划》、《江苏省农村商业银行全面风险管理指导意见（试行）》（苏信联发[2015]190号）、《银行业金融机构全面风险管理指引》（银监发[2016]44号）以及本行《章程》，并借鉴国际国内银行业风险管理的经验做法，特制定本制度。'),
(NULL, '风险管理部', '《风险管理制度》', '第一章  总则', '第二条', '本制度所称风险，是指对本行实现既定目标可能产生影响的不确定性，这种不确定性既可能带来损失也可能带来收益。本制度主要关注带来损失的不确定性。（一）信用风险。是指因借款人或交易对手未按照约定履行义务从而使本行业务发生损失的风险。（二）市场风险。是指因市场价格（利率、汇率、股票价格和商品价格）的不利变动而使本行表内和表外业务发生损失的风险。（三）操作风险。是指由不完善或有问题的内部程序、员工和信息科技系统，以及外部事件所造成损失的风险，包括法律风险，但不包括策略风险和声誉风险。（四）流动性风险。是指因本行无法以合理的成本及时筹集到客户和交易对手当前和未来所需资金而对本行经营所构成的风险。（五）信息科技风险。是指信息科技在商业银行运用过程中，由于自然因素、人为因素、技术漏洞和管理缺陷产生的操作、法律和声誉等风险。除上述风险外，本行还应关注外部监管部门或本行董事会要求关注的其他风险。'),
(NULL, '风险管理部', '《风险管理制度》', '第一章  总则', '第三条', '本制度所称全面风险管理是指本行董事会、监事会、高级管理层和全行员工各自履行相应职责，对经营管理中面临的以上风险类别进行的管理过程。'),
(NULL, '风险管理部', '《风险管理制度》', '第一章  总则', '第四条', '本行风险管理应遵循以下原则： （一）收益与风险匹配制定风险管理战略和进行风险管理决策，必须考虑承担的风险是在本行的风险容忍度以内，并有预期的收益覆盖风险，经风险调整的资本收益率能够满足股东的最低要求或符合本行的经营目标。（二）内部制衡与效率兼顾本行在风险管理规章制度中明确界定各部门、各分支机构和各层级风险管理人员的具体权责，实行前中后台职能相对分离的管理机制。各部门、各分支机构和全体员工之间要有效沟通与协调，优化管理流程，不断提高管理效率。（三）风险分散本行实现信用风险敞口在国家、地区、行业、产品、期限和币种等维度上的适度分散，防范集中风险。严格遵循监管标准，审慎核定单一客户和关联客户授信额度，有效控制客户信用风险集中度。本行实现市场风险敞口在国家、地区、市场、产品、期限和币种等维度上的适度分散，并采用适当的方式实现市场风险的有效分散。本行统一管理全行的流动性，建立分层次的流动性储备体系，确保融资渠道的多元化。（四）定量与定性结合本行着力提升风险计量水平，开发与本行业务性质、规模和复杂程度相适应的风险计量技术，推广应用国际国内先进银行业成熟的风险管理经验，实现定性分析和定量分析的有机结合。（五）动态适应性调整持续不断地检查和评估内外部经营管理环境和竞争格局的变化及其对本行全面风险管理所产生的实质性影响，及时调整风险管理政策、制度和流程，以确保风险管理与本行业务发展战略等相一致。（六）循序渐进本制度立足于本行风险管理现状，提出了未来风险管理的发展目标和前瞻性要求，是风险管理的指导性文件，本行将逐步实现本制度的要求。独立性原则。全面风险管理的机构、人员和报告路线应单独设置，与业务条线之间形成相互制衡的运作机制。有效性原则。本机构应当将全面风险管理的结果应用于经营管理，根据风险状况、市场和宏观经济情况评估资本和流动性的充足性，有效抵御所承担的总体风险和各类风险。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第五条', '风险管理文化、风险容忍度、风险管理战略和组织架构等构成本行风险管理的内部环境，是风险管理所有构成要素的基础。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第六条', '风险管理文化包含了道德和行为准则以及它们的沟通和强化方式，通过风险容忍度、风险管理战略、管理行为等表现出来，本行致力于风险管理文化的建设和传播。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第七条', '风险容忍度是在经营管理过程中所愿意承担的风险水平。本行采取定性和定量相结合的方式描述风险容忍度。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第八条', '风险管理战略是本行为了实现经营发展目标所制定的一系列风险管理目标、措施等，具有全局性、长远性、纲领性、相对稳定性，并随内外部环境变化实施动态管理。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第九条', '本行应建立分工明确、职责清晰、相互制衡、运行高效的风险管理组织架构。构建并完善董事会及董事会下设风险管理委员会、高级管理层及高级管理层下设风险管理相关委员会、风险管理部门和内部审计部门等风险管理组织架构和职能，强化三道防线体系建设，不断加强风险管理条线独立性和专业性。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十条', '董事会风险管理基本职责。董事会负责建立和保持有效的风险管理体系，对风险管理承担最终责任，按本行《章程》规定履行风险管理的职责。主要包括：（一）决定风险战略、风险管理政策和重大风险管理制度。（二）审议批准年度风险偏好。（三）领导本机构在法律和政策的框架内审慎经营，明确风险偏好并设定可承受的风险水平。（四）批准风险管理组织机构设置方案。（五）确保高级管理层采取必要的措施识别、计量、监测和控制风险，并对高级管理层执行风险管理政策情况实施评价。（六）组织评估风险管理体系充分性与有效性。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十一条', '董事会风险管理与关联交易控制委员会风险管理基本职责。董事会下设风险管理与关联交易控制委员会，根据董事会授权履行风险管理职责，主要职责包括：（一）根据本机构总体战略，审核、修订本机构风险战略、风险偏好和主要风险管理政策，对其实施情况及效果进行监督和评价，向董事会提出建议。（二）评估高级管理层在信用、市场、操作、金融创新和信息科技等方面的风险识别、计量、监测和控制情况，提出完善本机构风险管理的意见。（三）对本机构风险管理政策、风险偏好和全面风险管理状况及风险承受能力进行评估，向董事会提出风险管理工作建议。（四）法律、法规规定以及董事会授权的其他事宜。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十二条', '监事会风险管理基本职责。监事会负责监督风险管理体系的建立和运行，主要职责包括：（一）监督董事会、高级管理层是否履行了建立完善风险管理体系职责。（二）监督董事会、高级管理层是否履行了风险管理职责。（三）对高级管理层执行风险管理政策情况实施检查。（四）要求董事会成员及高级管理人员纠正其损害机构整体利益的行为并监督执行。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十三条', '高级管理层风险管理基本职责。高级管理层作为风险管理的执行主体，对董事会负责，主要职责包括：（一）认真执行董事会制定的风险战略，落实风险管理政策，制定覆盖全部业务和管理环节的风险管理制度和程序。（二）根据董事会风险偏好要求，建立风险偏好管理机制，组织审议年度风险容忍度指标，报董事会审批后执行。（三）推动建立风险识别、计量、监测和控制的程序和机制，采取适当的规避风险、缓释风险、降低风险和分散风险的方法和措施。（四）提出业务部门与风险管理部门的设置方案，保证风险管理的各项职责得到有效履行。（五）对风险管理体系的充分性与有效性进行监测、评估和改进。（六）按照董事会要求定期或不定期向董事会报告风险状况、采取的管理措施以及风险管理规划等情况。高级管理层下设风险管理相关委员会的，可以授权各相关委员会履行部分风险管理职责。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十四条', '经营管理层风险管理基本职责。经营管理层主要负责执行董事会制定的风险管理战略，制订风险管理的程序和规程，管理本行各项业务所承担的风险。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十五条', '风险管理部风险管理基本职责。风险管理部作为全面风险管理的牵头管理部门，应充分发挥全面风险管理的规划、协调、计量管理等职能，并与业务部门保持独立。主要职责包括：（一）组织拟订各类风险管理的政策和制度。（二）组织对风险管理的政策、制度和流程的执行效果进行检查评估。（三）组织制定风险偏好管理制度流程，建立涵盖主要风险的风险限额指标体系，负责风险限额指标分解、监控与考核。（四）研究确定风险识别、评估、计量、监控和缓释方法。（五）对全行风险状况进行监测、分析和报告。（六）对新产品、新业务和重大风险事项提出风险审查意见。（七）创新、引进和实施先进风险管控技术。（八）风险管理人才队伍和风险管理信息系统建设。风险管理部或其他管理部门根据需要可逐步向重点业务部门或分支机构委派风险管理人员，委派的风险管理人员独立实施风险审查和监测，直接对风险管理部门负责。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十六条', '本行各部门、各分支机构均应在全面风险管理中承担相应职责。主要包括：传播本行风险管理理念，树立全面风险管理意识；明确员工在风险管理中承担的职责等。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十七条', '审计部风险管理基本职责。审计部主要负责对全行风险管理体系的全面性、可靠性、充分性和有效性实施评价，对各类风险管理的各个组成部门和环节进行独立审计监督。审计结果应直接向董事会或其下设审计委员会报告，同时通报高级管理层和监事会。'),
(NULL, '风险管理部', '《风险管理制度》', '第二章  内部环境', '第十八条', '根据实际，合理划分风险管理牵头部门、归口管理部门和其他部门之间的职能界线，建立并完善风险管理的信息交流反馈与分工协作的联动管理机制。'),
(NULL, '风险管理部', '《风险管理制度》', '第三章  目标管理', '第十九条', '目标设定是风险管理的前提。战略目标、经营目标、报告目标和合规目标是实施全面风险管理的重要组成部分。本行建立目标管理责任制，实行分工负责制和逐级负责制。'),
(NULL, '风险管理部', '《风险管理制度》', '第三章  目标管理', '第二十条', '战略目标与公司使命、愿景和宗旨相一致，是设定相关目标及其子目标的导向。'),
(NULL, '风险管理部', '《风险管理制度》', '第三章  目标管理', '第二十一条', '经营目标是本行战略目标的具体反映，关注于本行经营活动的有效性和效率，保证本行战略目标的实现。'),
(NULL, '风险管理部', '《风险管理制度》', '第三章  目标管理', '第二十二条', '报告目标应与本行战略目标相一致，保证为董事会和经营管理层、监管机构、投资者和客户提供准确、及时、完整的信息，本行建立包括风险报告制度在内的报告工作制度体系以及信息披露制度等。'),
(NULL, '风险管理部', '《风险管理制度》', '第三章  目标管理', '第二十三条', '合规目标应与本行战略目标相一致，保证本行从事的经营管理活动符合相关法律法规和本行规章制度，并确保支持本行经营目标和报告目标的实现。'),
(NULL, '风险管理部', '《风险管理制度》', '第四章  风险管理流程', '第二十四条', '风险管理流程是指包括风险识别、风险计量、风险控制、风险监测与报告等一系列风险管理活动的全过程，应能贯彻执行既定的战略目标，与本行风险管理文化相匹配。'),
(NULL, '风险管理部', '《风险管理制度》', '第四章  风险管理流程', '第二十五条', '风险识别是指对影响本行各类目标实现的潜在事项或因素予以全面识别，鉴定风险的性质，进行系统分类并查找出风险原因的过程。'),
(NULL, '风险管理部', '《风险管理制度》', '第四章  风险管理流程', '第二十六条', '风险计量是指在通过风险识别确定风险性质的基础上，对影响目标实现的潜在事项出现的可能性和影响程度进行计量的过程。风险计量通常采取定性与定量结合的方法。'),
(NULL, '风险管理部', '《风险管理制度》', '第四章  风险管理流程', '第二十七条', '风险控制是指在风险计量的基础上，综合平衡成本与收益，针对不同风险特性确定相应的风险控制策略，采取措施并有效实施的过程。常见的风险控制策略包括：风险规避、风险分散、风险对冲、风险转移、风险补偿等。'),
(NULL, '风险管理部', '《风险管理制度》', '第四章  风险管理流程', '第二十八条', '风险监测是指监测各种可量化的关键风险指标和不可量化的风险因素的变化和发展趋势，以及风险管理措施的实施质量与效果的过程。'),
(NULL, '风险管理部', '《风险管理制度》', '第四章  风险管理流程', '第二十九条', '风险报告是指在风险监测的基础上，编制不同层次和种类的风险报告，遵循报告的发送范围、程序和频率，以满足不同风险层级和不同职能部门对于风险状况的多样性需求的过程。'),
(NULL, '风险管理部', '《风险管理制度》', '第四章  风险管理流程', '第三十条', '在引进或采取新的产品、业务、程序和系统时，应对其开展风险识别、计量、控制、监测和报告等一系列风险管理活动。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十一条', '本行应建立与业务性质、规模和复杂程度相适应的信用风险管理流程，有效识别、计量、控制、监测和报告信用风险，将信用风险控制在本行可以承受的范围内。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十二条', '本行对产品与业务中的信用风险进行识别，同时关注信用风险与其他风险之间的相关性，防范因其他风险导致信用风险损失事件的发生。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十三条', '引发信用风险的因素包括：国家、地区、行业、客户、交易方式等，本行各级机构应高度重视资产与业务中信用风险在上述维度的集中情况。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十四条', '本行信用风险主要来自于信贷资产、信用担保、贷款承诺、衍生产品交易、结算交易等业务。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十五条', '本行在单一与组合两个层面上对信用风险进行计量与评估。单一信用风险的计量与评估对象包括借款人或交易对象以及特定贷款或交易，组合信用风险的计量与评估对象包括本行各分支机构及地区、行业、客户及产品等。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十六条', '本行应建立和不断完善信用风险计量模型，在目前执行权重法信用风险计量的基础上，逐步实现向内部评级初级法的转变，并力争使用高级法来计量信用风险及其对应的资本要求。本行采用压力测试和其他非统计计量方法进行补充。同时，本行重视定性评价在信用风险计量中所起的重要作用。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十七条', '本行定期对已评定信用风险情况进行复查，当条件改变时及时进行重新评估。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十八条', '本行应建立完整的授信政策、决策机制和统一授信的业务操作程序，明确尽职要求，定期或在有关法律法规发生变化时，对授信业务规章制度进行评审和修订。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第三十九条', '本行对信用风险实施限额管理，制定涵盖地区、行业、客户、产品、以及本行各分支机构等多维度的限额指标。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第四十条', '本行不断完善信贷管理流程，实现信贷审查、信贷审批、贷款发放等职能的分离。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第四十一条', '本行建立信用风险监测程序，对单个债务人或交易对手的合同执行情况进行监测；对投资组合进行整体监测，防止风险在行业、区域、产品及客户等维度上的过度集中。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第四十二条', '本行应建立和完善信用风险报告制度，明确规定信用风险报告应遵循的报送范围、程序和频率，编制不同层次和种类的信用风险报告，以满足不同风险层级和不同职能部门对于信用风险状况的多样性需求。'),
(NULL, '风险管理部', '《风险管理制度》', '第五章  信用风险管理', '第四十三条', '本行按照监管部门关于商业银行资本充足率管理的要求，根据本行信用风险状况和资本实力，为本行所承担的信用风险提取充足的资本。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第四十四条', '本行致力于建立与业务性质、规模和复杂程度相适应的市场风险管理流程，有效识别、计量、控制、监测和报告市场风险，将市场风险控制在本行可以承受的范围内。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第四十五条', '本行明确银行账户和交易账户的划分标准、管理要求和调整程序。根据不同账户的性质和特点，采取不同的市场风险识别、计量、控制和监测方法。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第四十六条', '本行对业务和产品中的市场风险因素进行分解和分析，及时、准确地识别交易和非交易业务中的市场风险的性质和类别。同时，关注市场风险与其他风险之间的相关性。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第四十七条', '引发市场风险的因素主要包括：利率因素、汇率因素（包括黄金）、股票价格因素、商品价格因素等。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第四十八条', '本行应选用适当的方法计量银行账户和交易账户中不同类别的市场风险，对银行账户中的可供出售类资产进行定期估值，逐步实现对交易账户的逐日评估。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第四十九条', '市场风险的常用计量方法包括缺口分析、久期分析、外汇敞口分析、敏感性分析、情景分析和运用内部模型计算风险价值等。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第五十条', '本行逐步开发和使用内部模型来计量风险价值，合理选择、定期审查和调整模型技术，对所承担的市场风险进行量化估计。同时，本行采用压力测试和其他非统计类计量方法进行补充。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第五十一条', '本行对市场风险实施限额管理，制定各类和各级限额的内部审批程序和操作规程，根据业务性质、规模、复杂程度和风险承受能力设定、定期更新限额。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第五十二条', '本行采取市场风险对冲手段，在综合考虑对冲成本和收益情况下，运用金融衍生产品等金融工具，在一定程度上实现对市场风险的控制或对冲。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第五十三条', '本行应对市场风险有重大影响的情形制定应急处理方案，视情况对应急处理方案进行测试和更新。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第五十四条', '本行建立市场风险监测程序，对全行总体市场风险头寸、风险水平、盈亏状况、市场风险限额执行情况等进行持续监测。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第五十五条', '本行应建立和完善市场风险报告制度，明确规定市场风险报告应遵循的报送范围、程序和频率等，编制不同层次和种类的市场风险报告，以满足不同风险层级和不同职能部门对于市场风险状况的多样性需求。'),
(NULL, '风险管理部', '《风险管理制度》', '第六章  市场风险管理', '第五十六条', '本行按照有关监管部门关于商业银行资本充足率管理的要求，根据本行市场风险状况和资本实力，为本行所承担的市场风险提取充足的资本。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第五十七条', '本行应建立与业务性质、规模、复杂程度和风险特征相适应的操作风险管理流程，识别、计量、控制、监测和报告操作风险，将操作风险控制在本行可以承受的范围内。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第五十八条', '本行的操作风险存在于各类业务和管理活动之中，本行对产品、活动、程序和系统中固有的操作风险进行识别。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第五十九条', '操作风险事件包括：内部欺诈，外部欺诈，就业制度和工作场所安全事件，客户、产品和业务活动事件，实物资产的损坏，IT系统事件，执行、交割和流程管理事件等。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十条', '本行统一操作风险损失事件的统计口径，积累各类操作风险的发生频率及损失额等历史数据，不断完善内外部损失事件数据库。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十一条', '本行采用自我评估和第三方独立评估等方式对操作风险的影响程度和控制能力进行持续评估。自我评估可由风险管理部门牵头负责，也可以由业务部门、分支机构及后勤保障部门自行发起组织。第三方独立评估主要由外部监管部门、外部审计部门、本行内部审计部门进行。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十二条', '本行应建立和不断完善操作风险计量模型，在目前实现基本指标法计量的基础上，逐步向标准法转变，并力争使用高级法来计算本行操作风险及其对应的资本要求，同时，采用压力测试和其他非统计类计量方法进行补充。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十三条', '本行应明确业务及管理活动、业务流程及操作环节的关键风险点和控制措施要点，制定和适时修订相关程序和操作指南等。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十四条', '本行应建立和完善各级管理层职责明确的审批和授权制度，并确保有效执行。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十五条', '本行可将部分业务或操作环节外包给外部专业机构处理，但应制定与外包业务有关的风险管理政策，确保业务外包有严谨的合同或服务协议。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十六条', '本行可将购买保险以及与第三方签订合同作为缓释操作风险的方法，但应制定相关的书面政策和程序。本行同时关注运用保险工具将风险转嫁到其他领域所产生的风险。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十七条', '本行应制订控制和缓释重大操作风险的政策、程序和步骤，主要包括：风险控制的策略及方法、内部控制制度等。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十八条', '对关键业务或环节，本行应制订应急和业务连续方案，建立恢复服务和保证业务连续运行的备用机制，并定期检查、测试灾难恢复和业务连续机制有效性和适用性。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第六十九条', '本行建立操作风险监测程序，对关键操作风险指标和操作风险损失事件进行监测分析，及时反映本行操作风险的暴露情况和操作风险资本的变化。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第七十条', '本行应建立和完善操作风险报告制度，明确规定操作风险报告应遵循的报送范围、程序和频率等，编制不同层次和种类的操作风险报告，以满足不同风险层级和不同职能部门对于操作风险状况的多样性需求。对即时发生或接到的重大突发事件，各部门、各分支机构要及时报告。在应急处置过程中，要随时关注事态发展，及时报告后续情况。'),
(NULL, '风险管理部', '《风险管理制度》', '第七章  操作风险管理', '第七十一条', '本行按照有关监管部门关于商业银行资本充足率管理的要求，根据本行操作风险状况和资本实力，为本行所承担的操作风险提取充足的资本。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十二条', '本行应建立与业务性质、规模、复杂程度和风险特征相适应的流动性风险管理流程，不断完善流动性风险管理机制，及时满足全行业务发展对流动性的需求。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十三条', '引发流动性风险的因素包括：存款客户支取存款、贷款客户提款、债务人延期支付、资产负债结构不匹配、资产变现困难、经营损失和衍生品交易风险等。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十四条', '本行根据监管部门相关规定，结合本行实际状况制定流动性风险管理指标体系，包括监管指标和内部控制指标。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十五条', '运用多种计量方法分析和预测当前和未来、以及在不同情景假设下本外币资金来源与运用变化趋势，持续计量本行的净融资需求。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十六条', '流动性风险的计量方法包括但不限于：流动性缺口、期限阶梯、敏感性分析、情景分析等，同时，本行采用压力测试和其他非统计类计量方法进行补充。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十七条', '本行对流动性风险实施限额管理，制定流动性风险限额的内部审批程序和操作规程，根据业务性质、规模、复杂程度和风险承受能力设定、定期审查和更新限额。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十八条', '本行应建立本外币资金集中管理机制和有效的资金调控机制，强化大额资金运动预测预报、大额存款和大额借款、资金拆借和备付金的管理，实现全行流动性的统一调度和流动性风险的统一管理。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第七十九条', '在平衡安全性、流动性和盈利性的基础上，本行应调整和配置全行资产负债规模和期限结构，建立分层次的流动性储备体系，优化流动性储备资产规模和结构，及时通过货币市场和资本市场实现流动性管理组合目标。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第八十条', '积极开拓融资渠道，实施融资分散化和多样化管理战略，优化本行资产负债组合。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第八十一条', '本行建立紧急情况下的流动性风险应急处理机制，制定处理流动性危机的预案及相关部门的职责与分工。定期对应急处理方案进行测试，不断更新和完善应急处理方案。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第八十二条', '本行建立流动性风险的监测程序，实现对潜在重大流动性风险的提前预警。'),
(NULL, '风险管理部', '《风险管理制度》', '第八章  流动性风险管理', '第八十三条', '本行应建立和完善流动性风险报告制度，明确规定流动性风险报告应遵循的报送范围、程序和频率，编制不同层次和种类的流动性风险报告。本行流动性风险报告应能反映本外币流动性风险的管理情况。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第八十四条', '按照《商业银行信息科技风险管理指引》要求，明确董事会、高级管理层、首席信息官、信息科技部门、信息科技风险管理部门、内部审计部门等管理职责，构建职能清晰、防控有力的信息科技风险治理架构。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第八十五条', '制定符合总体业务规划的信息科技战略、信息科技运行计划和信息科技风险评估计划，确保配置足够人力、财力资源，维持稳定、安全的信息科技环境。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第八十六条', '制定全面的信息科技风险管理策略文件，至少包括以下领域：（一）信息分级与保护。（二）信息系统开发、测试和维护。（三）信息科技运行和维护。（四）访问控制。（五）物理安全。（六）人员安全。（七）业务连续性计划与应急处置。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第八十七条', '建立并完善信息科技风险的评估、监测和报告体系，对已有和潜在的信息科技风险因素进行充分评估，实时跟踪监测各类信息科技风险因素的产生和变化，适时调整信息科技风险应对策略和措施，确保信息科技风险管理有效性。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第八十八条', '信息科技部门应负责建立、实施信息分类和保护体系，落实信息安全管理。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第八十九条', '制定相关制度和流程，加强信息系统开发、测试和维护的过程管理。充分认识信息科技项目相关的风险，包括潜在的各种操作风险、财务损失风险和因无效项目规划或不适当的项目管理控制产生的机会成本，采取适当的项目管理方法，控制信息科技项目相关的风险。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第九十条', '根据自身业务性质、规模和复杂程度制定适当的业务连续性规划，确保在出现无法预见的中断时，系统仍能持续运行并提供服务；定期对规划进行更新和演练，保证其有效性。'),
(NULL, '风险管理部', '《风险管理制度》', '第九章   信息科技风险管理', '第九十一条', '加强信息科技相关外包风险管理，对实施重要外包（如数据中心和信息科技基础设施等）应格外谨慎，确保客户资料等敏感信息安全。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十二条', '按照全面风险统筹管理、专业风险分工负责要求，对各类风险管理工作在总行职能部门间作出适当分工，建立起横纵结合、相互协作、多维立体的风险管理执行落实体系，巩固强化执行成效。原则上：（一）风险管理部门作为全面风险管理牵头部门，主要负责上述类别风险的统筹管理工作。同时，还应负责牵头相关职能部门履行本机构市场风险归口管理职责。（二）信贷管理部门在履行本机构信贷管理相关工作职责的同时，风险管理方面还应履行本机构信用风险、集中度风险的归口管理职能。（三）合规管理部门在履行本机构合规管理工作职责的同时，风险管理方面还应履行本机构操作风险归口管理职能。（四）计划财务部门在履行本机构财务管理工作职责的同时，风险管理方面还应履行本机构流动性风险、银行账户利率风险的归口管理职能。（五）信息科技部门在履行本机构信息科技工作职责的同时，风险管理方面还应履行本机构信息科技风险的归口管理职能。（六）办公室在履行本机构行政管理工作职责的同时，风险管理方面还应履行声誉风险的归口管理职能。（七）董事会办公室在履行本机构董事会日常管理的同时，风险管理方面还应履行战略风险的归口管理职能。上述风险的归口管理部门及参与部门在相应风险管理领域的主要职责内容，应由法人单位具体风险管理政策或办法予以规定。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十三条', '建立多层次的风险报告机制。（一）在董事会层面，建立高级管理层向董事会风险管理委员会或董事会风险报告制度及重大风险事项报告制度。同时，在监事会层面，建立高级管理层向监事会风险报备制度及重大事项报备制度，公开接受监事会监督。（二）在经营管理层面，根据不同业务风险特征，制定管理部门向高级管理层差异化的报告制度，明确报告对象、报告种类、报告内容、报告频率、报告路径等内容。（三）日常管理层面，条线职能部门、分支机构应将本管理条线、分支机构的相关风险数据和管理情况等按规定频率要求及时报告相应风险主管部门。（四）重大风险信息和突发事件按照有关规定时效要求及时预警和报告，后续处置措施、管理效果等应在相应风险管理报告中及时总结、评估和反馈。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十四条', '建立并规范风险管理决策推动和执行落实机制。（一）加强董事会和高级管理层对全行全面风险管理的把控力度，建立明晰顺畅的“领导与执行、指导与报告”机制，通过常规例会、条线会议和专题会议等方式议事决策，不断强化决策管理层的统筹领导和推动职能。（二）对于董事会和高级管理层的各项决议事项和工作要求，应通过专题会议纪要、专项工作通报和具体制度办法等形式传导落实并强化督导。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十五条', '建立健全风险管理缺陷评估机制。定期评估风险管理治理架构、职责分工、政策制度、运作流程、管控措施、应急机制等的有效性和合理性，逐步建立起主动识别风险、持续评估监测、及时整改提升的风险管理自我修复机制。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十六条', '建立包含收益和风险在内的风险绩效评价机制。鼓励规模较大机构逐步建立以风险调整后资本收益率（RAROC）和经济增加值（EVA）为基础的绩效考评和资本预算分配体系，推动业务增长向资本节约型转变，提升资本使用效率。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十七条', '建立新产品、新业务风险评估机制。在开办新业务、开发新产品或设立新机构时，应事先充分识别评估潜在风险因素与影响，由业务、风险、合规等相关部门进行会商或会签。对决定开展的业务，要事先制定明确的风险管理政策和制度。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十八条', '建立常态化风险检查机制。根据风险监测和变化情况，制定年度检查计划，灵活采用全面检查、专项检查、飞行检查等手段，对各业务条线和分支机构经营管理的主要领域、层面、环节和关键点的风险情况进行检查，注重检查发现问题的整改和后评价工作。'),
(NULL, '风险管理部', '《风险管理制度》', '第十章  风险管理机制建设', '第九十九条', '建立完善贯穿各类人员、各个业务领域的问责机制。对违规行为及造成风险损失的责任人进行经济处罚和纪律处分，规范经营行为，防范业务风险。'),
(NULL, '风险管理部', '《风险管理制度》', '第十一章  风险管理信息与沟通', '第一百条', '本行明确信息收集的范围、标准、过程，以及各部门、各分支机构和人员在信息收集与加工过程中的职责，不断提高信息质量。第一百零一条  本行建设覆盖各分支机构、业务领域的数据库和管理信息系统，及时、准确地提供风险管理所需要的各种数据，以实现对风险的有效识别、计量、监测等。第一百零二条  风险管理信息的沟通方式可分为以下四类：自上而下的纵向沟通、自下而上的纵向沟通、横向沟通与外部沟通。第一百零三条  各部门和各分支机构应将本行风险管理战略、政策、制度及相关规定等信息传达给员工，以确保员工了解管理层的意图，正确履行所承担的职责。第一百零四条  本行充分重视员工在风险管理中的作用，支持员工将发现的风险及风险管理中存在问题向管理层进行报告，各部门和各分支机构须及时处理员工反映的问题。第一百零五条  各部门和各分支机构之间应顺畅沟通风险管理信息，实现风险管理信息的共享。第一百零六条  本行高度重视外部建议与意见，制定流程规则来保证沟通渠道的畅通，并使之得到及时处理。第一百零七条  本行严格按照相关法律、法规和规章规定的信息披露原则和本行相关的信息披露制度，披露风险管理相关信息。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第一章  总则', '第一条', '为客观公正地评价各支行、部、室专（兼）职风险管理员工作业绩，规范业务条线和基层网点风险管理工作，提升全面风险管理机制建设能力，推动全行各项业务稳健持续发展，根据《中国银监会关于印发〈农村中小金融机构风险管理机制建设指引〉的通知》、《中华人民共和国银行业监督管理法》、《商业银行合规风险管理指引》、《银行业金融机构全面风险管理指引》和《江苏省农村商业银行全面风险管理指导意见（试行）》等省联社风险管理相关文件要求，结合本行实际，特制定本规定。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第一章  总则', '第二条', '本规定适用于全行各分支机构、各业务条线的专（兼）职风险管理员业绩考评工作。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第一章  总则', '第三条', '风险管理员考核评价工作每年进行一次。年度终了后，各支行、部室自我对照本规定自评本单位（部门）风险管理工作情况，自评材料于每年度后5个工作日内上报风险管理部进行初审；风险管理部结合其全年工作实际，对照本规定进行集中审核评价，评价结果报行长室最终审核确认。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第一章  总则', '第四条', '存在下列情形之一的，不参加当年度风险管理工作先进个人评选：1.经检查确认，当年度信贷资产风险分类偏离度大于1%。2.当年发生造成较大损失，或造成较大不良影响等应予问责的风险事件。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第二章  风险管理员任职资格与职责', '第五条', '风险管理员应具备以下条件：（一）思想政治素质和职业道德良好，具有胜任岗位需要的相关法律法规和业务理论知识，坚持原则、客观公正、尽心履职；（二）了解和掌握信用风险、操作风险、流动性风险、声誉风险等与本单位（部门）相关风险的基本控制程序，能客观分析本单位风险管理状况，并按规定要求向总行报告；（三）具有经济、金融、财会、法律等专业大专（含）以上学历；（四）从事本行业务管理或行政管理岗位工作三年（含）以上；（五）获取《风险管理》银行业从业人员资格证书；（六）业务管理能力较强、实际工作经验较为丰富、责任心较强的，条件可适当放宽。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第二章  风险管理员任职资格与职责', '第六条', '风险管理员岗位职责（一）根据总行全面风险管理机制建设总体要求，结合年度风险管理工作计划总体安排，制订上报所在单位（部门）风险管理年度工作方案和实施细则。（二）风险管理员受总行委派，在各分支机构、各业务条线履行风险管理职责，行使风险管理监督、检查和处罚建议，以及对违规违纪问题及时进行报告的权利。风险管理员应当积极配合所在分支机构、业务条线负责人抓好本单位的风险管理工作。（三）根据全面风险管理要求，对所在分支机构、业务条线各类信用风险、市场风险、操作风险、流动性风险、国别风险、声誉风险、战略风险进行识别、评估、监测和报告。（四）加强风险资产分类管理，并按规定做好贷款风险分类管理和贷款质量监测预警报告。 （五）对于出现案件或事故，要及时进行上报，不得迟报、瞒报或不报。（六）对风险管理部下发到所在分支机构、业务条线的风险限改意见进行督促落实，并负责反馈报告。（七）负责搜集所在分支机构、业务条线潜在的风险问题和相关数据，并按规定向风险管理部报告。（八）参与调查所在分支机构、业务条线风险管理薄弱环节和违规问题。（九）参与总行风险管理部组织的各类风险排查、授信业务尽职评价等活动，并按规定进行报告。（十）参加总行风险管理部组织召开的专题工作会议，参加总行风险管理部以及各业务条线组织的各类业务培训、测试、考核。（十一）建立所在分支机构、业务条线风险管理工作档案，并达到“实时搜集、按季整理、全面完善、年度归档”的档案管理要求。（十二）做好所在分支机构、业务条线风险管理其它相关工作，并定期将风险管理工作与所在分支机构、业务条线负责人进行沟通、联系。（十三）完成总行布置的其它工作任务。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第三章  考核评价方式和内容', '第七条', '根据银监会及省联社关于实施全面风险管理机制建设的总体要求，结合经营管理和案件防控治理要求，对各支行、部室风险管理员年度风险管理工作开展情况以及工作成效进行评价考核。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第三章  考核评价方式和内容', '第八条', '风险管理员工作开展和成效评价指标采用定性与定量相结合的形式，通过指标得分汇总考核。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第三章  考核评价方式和内容', '第九条', '风险管理部对风险管理员风险管理工作年度履职情况的考核主要包括：任职资格、履行职责、风险预警、问责管理、风险管理文化建设、工作成效。（一）任职资格（5分）。配备专职或者兼职风险管理人员得2分，风险管理从业人员具有相关规定的资格得2分，及时报告风险管理员人员调整情况的得1分。未按规定设置岗位和报告相关人员变动情况的均不得分。（二）履行职责（30分）。每月至少召开一次有关风险管理的会议得5分，未召开会议的不得分；每年制定风险管理工作计划得5分，当年未制定工作计划的不得分；风险管理员每季总结报告风险管理工作情况，并向风险管理部上报《风险管理综合报告》得10分，每缺一季度扣2.5分；全年未发生操作风险事件的得5分，每发生一次扣2分。重大突发事件及时上报风险管理部门得5分，迟报或少报一次的，除扣完该项分值外，同时对照有关规定进行责任追究。（三）风险预警（10分）。严格按照本行《操作风险控制管理办法》、《信用风险控制管理办法》、《声誉风险控制管理办法》等文件规定，对各类风险进行监测、预警和报告的得5分；风险控制及时、整改到位的得5分，未实时做好监测、预警和报告的本项不得分，对已出现风险苗头性事件未协同单位负责人进行处置化解，致使单位出现较大财产或声誉损失的，除扣完该项分值外，同时对照有关规定进行责任追究。（四）问责管理（10分）。对本单位员工日常违规行为协助主要负责人进行问责处理的得10分，未及时予以问责或处理的，每发现一次扣2分。（五）风险管理文化建设（5分）。对风险管理文化建设进行分析研究，对本单位风险情况进行分析提炼，形成建议文章，向总行风险管理部门全年投稿不少于2篇，得4分，所投稿件被农商行报使用1篇得3分，被上级部门或市外媒体采用，每篇加计5分。（六）工作成效（40分）工作成效考核各支行、部室涉及到的风险管理与控制措施落实情况，主要涉及信用风险、市场风险、操作风险、流动性风险、声誉风险、法律风险等六大类，考核评价分为共性与个性两部分，其中：1、共性部分（20分）。具体包括操作风险、声誉风险、法律风险等。（1）操作风险（10分），主要涉及人力资源管理、内部流程控制、系统完善与科技支撑、法律事务管理、外部风险事件控制等。当年发生应予追究责任的风险事件，根据风险大小和影响程度等因素予以扣分，每检查发现一次，扣2分（包括省联社、银监局、人民银行等上级部门对本行的各类操作风险检查，检查认定属于操作风险事件的，除对问题单位扣分外，同时对总行业务管理条线一并扣分）；涉及金额重大或造成较大损失，或造成较大不良影响的，除扣减全部分值外，同时对照有关规定对问题责任单位和个人予以问责追究。（2）声誉风险（6分），主要考核年度内有无发生影响本行声誉的事件，当年发生应予追究责任的声誉风险事件，根据风险大小和影响程度等因素予以扣分，每检查发现一次，扣3分（包括省联社、银监局、人民银行等上级部门对本行的各类检查，检查认定属于声誉风险事件的，除对问题单位扣分外，同时对总行相关职能管理部室一并扣分）；对本行声誉造成较大不良影响的，除扣减全部分值外，同时对照有关规定对问题责任单位和个人予以问责追究。（3）法律风险（4分），主要考核年度内有无发生法律风险的事件，当年发生应予追究责任的法律风险事件，根据风险大小和影响程度等因素予以扣分，每发现一次，扣2分（包括省联社、银监局、人民银行等上级部门对本行的各类检查，检查认定属于法律风险事件的，除对问题单位扣分外，同时对总行相关职能管理部门一并扣分）； 涉及金额重大或造成较大损失，或造成较大不良影响的，除扣减全部分值外，同时对照有关规定对问题责任单位和个人予以问责追究。2、个性部分（20分）。其中具体到支行、公司业务部、小微业务部和部室分别是：A、基层支行、公司业务部、小微业务部支行、公司业务部、小微业务部个性部分主要涉及信用风险，以年末五级分类不良贷款比率考核评价信用风险管理与控制情况，不良贷款比率（20分），低于2%（含）得基本分，每增加0.1个百分点，扣减2分。不良贷款比率=[（次级类贷款+可疑类贷款+损失类贷款）/各项贷款]×100%B、总行部室（1）行政办公室。行政办公室是本行声誉风险管理职能部门，主要考核日常办公运行与行政文秘、窗口服务与形象建设、对外联络与接待媒体、大宗物品采购、基建装修以及应急事件处理等所涉及的各类风险情况和风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（2）人力资源部。人力资源部是本行机构、员工与岗位等风险管理职能部门，主要考核网点机构设置与准入审批、高级管理人员资格审查与报批、重要岗位人员配置与岗位轮换交流、员工教育培训、员工薪酬与绩效管理等所涉及的各类风险情况和风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（3）运营管理部。运营管理部是本行大额资金流动性、现金和凭证管理等风险管理职能部门，主要考核远程授权与事后监督、资金清算与头寸管理、现金调拨与差错管理、业务库和代理发行库管理、重要空白凭证领用与保管、反洗钱等所涉及的各类风险情况和风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（4）信贷管理部。信贷管理部是征信管理、用信审查、贷后管理、信贷档案管理等风险管理部门，主要考核征信管理、银保业务合作、用信审查、信贷计划执行等所涉及的各类风险情况和风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（5）授信评审部。授信评审部是本行授信业务审查审批等风险管理部门，主要考核授信业务评审所涉及的各类风险情况和风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（6）合规管理部。合规管理部是本行合规风险管理职能部门，包括但不限于本行操作风险管理，规章制度制订、合规性审查，案件防控管理，监管政策落实、问题数据搜集等所涉及的风险隐患和形成事实风险的处理整改情况等。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（7）发展规划部。发展规划部是本行金融产品研发、服务创新等风险管理职能部门，主要考核在产品开发与设计、服务创新等业务操作中涉及的各类风险情况以及风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（8）电子银行部电子银行部是本行电子银行等风险管理职能部室，主要考核在电子银行、网络金融、银行卡管理、POS机管理等涉及的各类风险情况以及风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（9）三农业务部三农业务部是本行农村网点零售业务、阳光信贷和中间业务所涉及的风险管理职能部门，主要考核农村网点零售业务、阳光信贷和中间业务涉及的各类风险情况以及风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（10）计划财务部。计划财务部是本行全行财务计划、财务管理，资产负债、税务、财务分析和产品定价管理等风险管理职能部门，主要考核在全行财务计划、财务管理，资产负债、税务、财务分析和产品定价管理所涉及的各类风险情况、制度执行以及风险隐患的处理、整改情况，同时负责提供流动性比率、日均存贷比、资本充足率、核心资本充足率、资产利润率、流动性覆盖率、净稳定资金比率、贷款拔备覆盖率、贷款拔备率、拔贷比、利息回收率等数据情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。其中：①流动性比率（5分）。流动性比率大于等于25%得基本分，每减少1个百分点，扣减0.5分。流动性比率=（流动性资产/流动性负债）×100%②流动性覆盖率（5分）。流动性覆盖率大于等于100%得基本分，每减少1个百分点，扣减0.5分。流动性覆盖率=（优质流动性资产储备/未来30日的资金净流出量）×100%③净稳定资金比率（3分）。净稳定资金比率大于等于100%得基本分，每减少1个百分点，扣减0.3分。净稳定资金比率=（可用的稳定资金/业务所需的稳定资金）×100%④日均存贷比（2分）。日均存贷比小于等于75%得基本分，每增加1个百分点，扣减0.2分。日均存贷比=（日均贷款余额/日均存款余额）×100%（11）金融市场部。金融市场部是本行资金交易、债券投资、票据经营等业务发展与管理职能部室主要考核在投资和资金营运、资金管理、债券的投融资、同业拆借、票据转贴现、再贴现、信贷资产的转让、同业授信等业务操作中涉及的各类风险情况、制度执行以及风险隐患的处理、整改情况，及时对本行市场风险指标进行分析报告的情况，重点是利率风险敏感度、投资潜在损失率、杠杆率等。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。其中：利率风险敏感度（10分）。利率风险敏感度比例小于等于20%得基本分，每增加0.5个百分点，扣减0.2分。利率风险敏感度=（利率上升200个基点对银行净值影响/资本净额）×100%投资潜在损失率（5分）。投资潜在损失率小于等于5%得基本分，每增加1个百分点，扣减0.5分。投资潜在损失率=[（各项投资市场价值-各项投资账面余额）/资本净额]×100%（12）审计部。审计部是本行风险管理的评价、监督职能部门，主要考核在全行审计、检查中发现的风险隐患，相关规章制度落实和风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（13）信息科技部。信息科技部是本行科技风险管理职能部门，主要考核本行信息科技体系建设、系统开发与维护、网络管理、外包管理、安全管理、机房管理等所涉及的各类风险情况、制度执行以及风险隐患的处理、整改情况。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（14）纪检监察部。纪检监察部是本行员工道德行为等风险管理职能部门，主要考核本行纪检监察、党风廉政建设和反腐败工作的管理与监督检查中涉及的各类风险情况、相关规章制度的落实和风险隐患的处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（15）董事会办公室。董事会办公室是本行信息披露、战略风险管理职能部门，主要考核法人治理、信息披露等所涉及的风险情况处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（16）监事会办公室监事会办公室是本行各类检查监督风险管理职能部门，主要考核各类检查监督及本行股东、员工、客户等对本行的意见和建议等所涉及的风险情况处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（17）党群工作部党群工作部是本行精神文明建设、文明服务风险管理职能部门，主要考核文明创建工作、精神文明建设、文明服务等所涉及的风险情况处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（18）党委办公室党委办公室是本行党的路线、方针、政策执行的风险管理职能部门，主要考核贯彻执行党的路线、方针、政策等所涉及的风险情况处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（19）安全保卫部安全保卫部是本行安全保卫、押运外包、监控中心的风险管理职能部门，主要考核安全保卫、提解中心、离行式自助设备、监控中心等所涉及的风险情况处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（20）资产保全部资产保全部是本行不良贷款清收的风险管理职能部门，主要考核不良贷款清收中诉讼资料、保全措施等所涉及的风险情况处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。（21）统计分析部统计分析部是本行数据统计、分析的风险管理职能部门，主要考核收集、整理的相关经营数据的真实性、准确性、完整性，报表上报的及时性等所涉及的风险情况处理、整改情况。按制度规定及时报告重大风险事件。每发生一次风险事件扣5分；发生案件、严重违章违纪事件或重大风险事故的，除扣减全部分值外，同时对照有关规定对责任人员予以问责追究。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第三章  考核奖惩', '第十条', '风险管理员工作评价得分前5名的单位为风险管理工作先进单位，予以通报和表彰。并对所在单位风险管理员表彰和奖励。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第三章  考核奖惩', '第十一条', '对弄虚作假骗取荣誉的单位，总行一经查实，予以通报批评，并取消已获取的荣誉和奖励。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第三章  考核奖惩', '第十二条', '评价结果作为年度工作业绩或履职的重要内容，纳入支行、部室的年度考评。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第四章  附则', '第十三条', '总行风险管理部负责对各分支机构和业务条线风险管理员风险管理工作进行检查、监督和考核；总行审计部负责对风险管理员考核工作审计、监督。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第四章  附则', '第十四条', '本办法自印发之日起实施。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第四章  附则', '第十五条', '本办法由xxx银行风险部负责解释和修订。'),
(NULL, '风险管理部', '《风险管理员考核评价管理办法》', '第四章  附则', '第十六条', '本办法自发布之日起执行。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第一章  总则', '第一条', '为促进xxx银行（以下简称“本行”）建立健全风险管理体系，切实转换经营管理机制，推行全面风险管理，有效防范各类风险，确保安全稳健运行，根据《中华人民共和国银行业监督管理法》、《中华人民共和国商业银行法》、中国银行业监督管理委员会《农村中小金融机构风险管理机制建设指引》、《江苏省农村信用社风险管理机制建设规划》等法律、法规、制度和审慎监管的要求，制定本方案。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第一章  总则', '第二条', '本行风险管理的目标：（一）确保持续发展，将风险管理纳入本行整体发展战略，通过风险管理促进发展战略的实现；（二）确保审慎合规经营，严格遵循有关法律法规，符合监管要求；（三）确保风险可控，在可承受范围内实现风险、收益与发展的合理匹配。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第一章  总则', '第三条', '本行风险管理遵循的原则：（一）全面性原则。风险管理应当贯穿决策、执行和监督的全过程，覆盖所有业务、所有部门及岗位和所有操作环节；（二）适应性原则。风险管理与机构的经营规模、业务范围和风险水平相适应，并根据发展状况适时调整，以合理的成本实现风险管理目标；（三）独立性原则。风险管理的机构、人员和报告路线应单独设置，对业务职能予以制衡；（四）融合发展原则。风险管理应与业务发展紧密结合，以风险管理推动业务稳健发展，确保机构价值的长期提高。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第四条', '本行的风险管理组织架构应分工明确、职责清晰、相互制衡、运行高效，保证风险管理条线的独立性和专业性。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第五条', '董事会负责建立和保持有效的风险管理体系，对风险管理承担最终责任。主要风险管理职责包括：（一）决定整体风险战略、风险管理政策、风险限额和重大风险管理制度；（二）领导本行在法律和政策的框架内审慎经营，明确风险偏好并设定可承受的风险水平；（三）批准风险管理组织机构设置方案；（四）确保经营管理层采取必要的措施识别、计量、监测和控制风险，并对经营管理层执行风险管理政策情况实施评价；（五）组织评估风险管理体系的充分性与有效性。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第六条', '董事会下设风险管理与关联交易控制委员会，根据董事会授权履行风险管理职责。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第七条', '监事会负责监督风险管理体系的建立和运行，主要风险管理职责包括：（一）监督董事会、经营管理层是否履行了建立完善风险管理体系的职责；（二）监督董事会、经营管理层是否履行了风险管理职责；（三）对经营管理层执行风险管理政策情况实施检查；（四）要求董事会成员及经营管理层人员纠正其损害机构整体利益的行为并监督执行。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第八条', '经营管理层是本行风险管理的执行主体，对董事会负责。主要风险管理职责包括：（一）认真执行董事会制定的风险战略，落实风险管理政策，制定覆盖全部业务和管理环节的风险管理制度和程序；（二）推动建立识别、计量、监测并控制风险的程序和机制，采取适当的规避风险、缓释风险、降低风险和分散风险的方法和措施；（三）提出业务部门与风险管理部门的设置方案，保证风险管理的各项职责得到有效履行；（四）对风险管理体系的充分性与有效性进行监测、评估和改进；（五）按照董事会要求定期或不定期向董事会报告风险状况、采取的管理措施以及风险管理长短期规划等情况。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第九条', '本行分管风险管理条线的副行长，直接对行长负责，不得分管前台业务工作。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第十条', '设立风险管理部，负责组织建立和实施本行风险管理体系，逐步实现对信用风险、市场风险等风险的统一管理。该部门与业务部门保持独立。主要职责包括：（一）拟订或组织拟订各类风险管理的政策和制度；（二）组织对风险管理的政策、制度和流程的执行效果进行检查评估；（三）研究确定风险识别、评估、计量、监控和缓释方法；（四）研究提出本行的重大风险限额；（五）对风险状况进行监测和分析，并根据风险报告制度进行报告；（六）对业务风险进行审查，提出风险审查意见；（七）对客户信用等级评定及资产风险分类进行审查；（八）牵头推动风险管理信息系统的建设；（九）向业务经营部门或分支机构委派风险管理人员，委派的风险管理人员独立实施风险审查。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第十一条', '合理划分风险管理部门与合规、财会、信贷、科技及其他专业风险管理职能之间的界线，建立并完善风险管理的信息交流反馈与分工协作机制。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第二章  风险管理组织体系', '第十二条', '各业务经营部门是本行风险管理的第一道防线，负责本部门和本部门业务条线风险管理的日常工作，对本部门和本业务条线的风险管理负第一责任。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第三章  风险管理政策和程序', '第十三条', '根据风险管理的目标、原则和要求，制定覆盖所有业务和管理环节的政策和程序，建立风险管理制度体系。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第三章  风险管理政策和程序', '第十四条', '根据整体发展战略，确定风险偏好，制定风险管理政策。风险管理政策应与本行的发展规划、资本实力、经营目标和风险管理能力相适应，并符合法律法规和监管要求。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第三章  风险管理政策和程序', '第十五条', '风险管理政策应涵盖风险管理的主要方面，并保证连续性、稳定性和适应性。主要内容包括：（一）风险管理组织、职责和权限安排；（二）可以开展的业务；（三）可以采取的风险管理策略和方法；（四）能够承担的风险水平；（五）适当的风险管理限额，包括信用风险限额、市场风险限额和流动性风险限额等；（六）风险的识别、计量、监测和控制程序；（七）采取压力测试的情形与范围；（八）风险管理信息的报告路径；（九）对重大和突发风险的应急处理方案。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第三章  风险管理政策和程序', '第十六条', '根据风险管理政策，建立覆盖风险管理重要环节的程序和方法。至少包括以下方面：（一）对各类主要风险的识别和评估的程序和方法；（二）对各类主要风险的计量程序和方法；（三）对各类主要风险的缓释或控制的程序和方法；（四）对各类主要风险的监测程序和方法；（五）对各类主要风险的报告程序和方法。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第三章  风险管理政策和程序', '第十七条', '业务发展与风险管理政策应符合资本约束与监管要求。针对业务发展和风险变化，应制定保持资本水平、提取减值准备的规划。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第三章  风险管理政策和程序', '第十八条', '逐步建立并保持压力测试程序和方案，根据业务发展状况、外部环境变化和监管要求，不定期开展压力测试，预防极端事件可能带来的冲击。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第十九条', '保证风险管理体系的有效运行。对已开展和拟开展的业务风险予以充分识别和评估，强化风险管理措施的执行，完善报告机制，有效管理风险。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十条', '根据风险识别程序和方法，对经营管理中面临的各类风险，包括信用风险、市场风险、操作风险、流动性风险、合规风险等，进行主动的识别，分析风险来源，确定风险的影响范围。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十一条', '在开办新业务、开发新产品或设立新机构时，应事先充分识别、评估潜在的风险因素与影响，由业务、风险、合规等相关部门进行会商或会签。对决定开展的业务，应当事先制定明确的风险管理政策和制度。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十二条', '应设定整体风险评估的工作模式和各类风险评估模块的联系机制，优化改进对信用风险、市场风险、操作风险等风险的评估体系。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十三条', '建立适合本行业务特点的信用风险评级方法，完善资产风险分类制度。积极尝试采用违约概率、违约损失率等计量方法管理信用风险。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十四条', '针对自身特点选择灵活的风险缓释策略，创新风险缓释方式，扩大抵押担保范围，逐步建立完善贷款风险评估与定价机制，增加涉农信贷投入，有效平衡支农服务目标与风险管理之间的关系。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十五条', '加强综合风险关联性管理，逐步探索采取资产管理组合、银团贷款等有效方式分散风险，严格控制授信集中度，明确行业风险限额，严格防范过度授信和集中授信风险。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十六条', '建立与本行业务性质相适应的市场风险管理体系。基于市场风险管理能力，决定债券投资业务的品种、交易范围、交易限额、交易方向和方式等。确定总体限额与本行的存款规模、涉农贷款规模和资本水平相挂钩的政策，并每年根据新增存款、涉农贷款增量予以调整。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十七条', '完善资产保护机制，充分关注法律风险，研究完善各种抵押担保方式的法律保障，对抵押担保物的有效性进行审查，对不良贷款积极采取资产保全措施。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十八条', '全面系统地梳理各项业务活动，合理设置前、中、后台，完善业务流程，制定操作手册，清晰划分各流程环节的岗位职责、风险要点、操作规范、合规要求，强化岗位相互约束与制衡，坚持关键岗位人员的轮岗轮调和强制性休假制度。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第二十九条', '建立科学严密的分级授权管理体系。以书面方式确定对董事长、经营管理层、业务部门、分支机构以及各岗位和工作人员的授权，不能存在管理盲点与空白。对于转授权，须结合机构人员配置和工作特点，以书面方式做出有效的制度性安排。严禁越权从事经营管理活动，应通过管理信息系统权限设置、授权检查和规范化管理来防止超授权风险，凡发现被授权人有越权行为的，应立即纠正处理。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第三十条', '确保业务、财务和其他管理信息的完整、真实、准确和及时。完善交叉核对与对账制度，加强与客户、金融机构之间及本行内部业务台账与会计账之间的对账，规定对账频率、对账对象和可参与人员等要求。对现金、有价证券等有形资产定期或不定期进行盘点。加强未达账项和差错处理的环节控制，记账岗位和对账岗位必须严格分开。建立事后监督机制，实施事后集中监督制度。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第三十一条', '建立合规检查制度。应对各业务条线和分支机构经营管理的主要领域、层面、环节和关键点的合规性进行检查，确保经营活动符合外部法律法规、监管要求及内部程序。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第三十二条', '高度重视管理信息系统的建设，逐步实现经营管理的信息化，提高风险管理有效性。建立贯穿各级机构、覆盖各个业务领域的数据库和管理信息系统，满足对各种风险的计量和定量分析，及时、准确地提供经营管理所需的各种数据，实现对各类风险的监控和预警，满足风险管理内部报告和对外信息披露的要求。同时，应当建立和健全信息系统风险防范的制度，确保信息系统设备、数据、系统运行和系统环境的安全。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第四章  风险管理运行机制', '第三十三条', '建立各类风险报告制度。根据不同业务风险特征，制订差异化的报告方式，明确报告对象、报告种类、报告内容、报告频率、报告路径等。保证风险管理条线纵向报告路径的独立性。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第五章  考核问责', '第三十四条', '建立包含收益和风险在内的风险绩效评价体系，合理设置评价指标，逐步开展经济资本管理，提高风险管理绩效，提升经营管理水平。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第五章  考核问责', '第三十五条', '建立风险管理和内部控制工作的考核机制，设定合理的考核范围、内容、标准和方法，将风险管理和内部控制考核纳入经营管理综合考核之中，考核结果与员工奖惩激励挂钩。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第五章  考核问责', '第三十六条', '建立贯穿各个业务领域以及对经营管理层人员与员工日常行为的问责制度。各业务条线要根据内部控制和风险管理的要求，对各项业务进行定期或不定期的检查，对违规行为及造成风险损失的责任人进行经济处罚和纪律处分，规范经营行为，确保各项业务健康可持续发展。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第六章  风险管理文化', '第三十七条', '风险管理应植根于本行的企业文化，并作为董事会战略决策和经营管理层、风险管理部门和其他业务条线的负责人及员工日常工作的核心。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第六章  风险管理文化', '第三十八条', '建立风险为本的企业文化，树立诚实守信、依法合规、稳健审慎的价值观念，制定高标准的员工行为准则与职业操守，规划企业文化渗透方案，并在本行推行实施。董事和经营管理层应率先垂范并引导全体员工参与风险管理文化建设，并通过激励约束、典型案例、警示教育等方式进行传播与渗透。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第六章  风险管理文化', '第三十九条', '根据风险管理的要求配置与管理人力资源，实施主要风险岗位人员准入与退出管理制度，配备足够的、能够达到风险管理岗位资质要求的人员，并随风险管理状况的变化进行相应的调整。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第六章  风险管理文化', '第四十条', '制定并实施中长期培训计划，加强对员工从业知识、风险管理要求及思想道德方面的培训，强化案件警示作用。培训计划应定期评审，充分考虑不同层次员工职责、能力和文化程度以及所面临的风险。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第六章  风险管理文化', '第四十一条', '以健康的风险管理文化为导向，建立以风险管理为基础，风险、发展与效益平衡的激励制度，从源头上遏止为追求自身短期效益最大化而偏离机构长期目标的短期行为。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第七章  监督与评价', '第四十二条', '建立独立、专业的内部审计体系，对风险管理各个组成部分和环节进行独立的审计监督。对风险管理体系的准确性、可靠性、充分性和有效性实施评价。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第七章  监督与评价', '第四十三条', '董事会、监事会、经营管理层可以根据业务发展情况，聘请外部中介机构审计，并应积极创造条件保证外部审计的独立性和有效性。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第七章  监督与评价', '第四十四条', '高度重视监管部门的风险检查评估与监管评级结果，依据监管意见认真改进，并按要求向监管部门报送整改报告。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第七章  监督与评价', '第四十五条', '董事会、经营管理层应定期对风险管理状况组织开展自我评估，评估风险管理的有效性，发现风险管理的缺陷。评估应定期持续进行，当经营管理环境发生重大变化时，应及时重新评估。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第七章  监督与评价', '第四十六条', '董事会、监事会和经营管理层应根据各类评价结果对风险管理体系进行对照研究，分析问题性质，对属于风险管理体系的问题，应建立科学的纠偏和纠错机制持续改进，对因执行不力和违反风险管理制度而发生的问题，应限期整改。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第八章  附则', '第四十七条', '本方案由xxx银行风险管理部负责解释和修订。'),
(NULL, '风险管理部', '《风险管理机制建设实施方案》', '第八章  附则', '第四十八条', '本方案自印发之日起施行。');
